INSERT INTO `v1_e126a47905676890a95b544a2760b0678689327ece67a522e0bb2e05e32cbdb3` VALUES (13, 1001, 1001001, 0, "2020-02-15 15:00:00", "2030-04-01 14:00:00", 190801, "こちらは{0}ルピで\n販売中！\nぜひ購入して\n遊んでくださいね！");
INSERT INTO `v1_e126a47905676890a95b544a2760b0678689327ece67a522e0bb2e05e32cbdb3` VALUES (12, 1002, 1002001, 0, "2020-02-15 15:00:00", "2030-04-01 14:00:00", 190801, "動くハートに合わせてタップ！\nシズルさんとエリコさんの\nチョコレートケーキ作りを\nお手伝いしましょう！");
INSERT INTO `v1_e126a47905676890a95b544a2760b0678689327ece67a522e0bb2e05e32cbdb3` VALUES (11, 1003, 1003001, 0, "2020-02-15 15:00:00", "2030-04-01 14:00:00", 190801, "クウカさんを引っ張ってより遠く\nへ飛ばしてあげましょう！\n色々なハプニングでクウカさんに\nエクスタシーを届けましょう！");
INSERT INTO `v1_e126a47905676890a95b544a2760b0678689327ece67a522e0bb2e05e32cbdb3` VALUES (3, 1004, 1004001, 0, "2020-02-15 15:00:00", "2030-04-01 14:00:00", 190801, "左右をタップしてマジムンを倒し\nましょう！\n放て！\n琉球犬アラウンドシュート！");
INSERT INTO `v1_e126a47905676890a95b544a2760b0678689327ece67a522e0bb2e05e32cbdb3` VALUES (7, 1005, 1005001, 0, "2020-02-15 15:00:00", "2030-04-01 14:00:00", 190801, "パネルをタップしてドラゴンズ\nネストのみなさんと一緒に\nしりとり対決をしませんか？\nおしりに「ん」はご法度です。");
INSERT INTO `v1_e126a47905676890a95b544a2760b0678689327ece67a522e0bb2e05e32cbdb3` VALUES (2, 1006, 1006001, 0, "2020-05-31 12:00:00", "2030-04-01 14:00:00", 190801, "素早い動きのネズミに合わせ、\nタップで撃退！\nやる気のリンさんと一緒に\n大切なチーズを守りましょう！");
INSERT INTO `v1_e126a47905676890a95b544a2760b0678689327ece67a522e0bb2e05e32cbdb3` VALUES (4, 1007, 1007001, 0, "2020-08-15 15:00:00", "2030-04-01 14:00:00", 190801, "ミサトさんや部員のみなさんと\nバッティングの特訓ですよ！\nホームランを目指して、ボールを\nタイミングよく打ち返しましょう");
INSERT INTO `v1_e126a47905676890a95b544a2760b0678689327ece67a522e0bb2e05e32cbdb3` VALUES (6, 1008, 1008001, 0, "2021-08-31 12:00:00", "2030-04-01 14:00:00", 190801, "カルミナメダルを使って、\n応援グッズをゲットしましょう！\nメダルはルピを使って\n交換できますよ。\n");
INSERT INTO `v1_e126a47905676890a95b544a2760b0678689327ece67a522e0bb2e05e32cbdb3` VALUES (13, 1009, 1009001, 0, "2021-12-18 15:00:00", "2030-04-01 14:00:00", 190801, "食べたプリンはプリンノート\nで確認してみましょう！\nプリンの材料はルピを使って\n購入できますよ。");
INSERT INTO `v1_e126a47905676890a95b544a2760b0678689327ece67a522e0bb2e05e32cbdb3` VALUES (4, 1010, 1010001, 0, "2022-02-15 15:00:00", "2030-04-01 14:00:00", 190801, "チームでクイズに挑みましょう！\nソロで自分の実力を試すか、マル\nチで仲間と一緒に遊ぶか、好きな\nモードを選んでくださいね♪");
INSERT INTO `v1_e126a47905676890a95b544a2760b0678689327ece67a522e0bb2e05e32cbdb3` VALUES (6, 1011, 1011001, 0, "2022-07-18 15:00:00", "2030-04-01 14:00:00", 190801, "３つのラウンドを駆け抜け、\n総合１位を目指しましょう！\n参加者の皆さんの特徴や、\nＧＰの特徴にも注目です♪");
INSERT INTO `v1_e126a47905676890a95b544a2760b0678689327ece67a522e0bb2e05e32cbdb3` VALUES (6, 1012, 1012001, 0, "2023-09-18 15:00:00", "2030-04-01 14:00:00", 190801, "キャルさん、スズナさんと一緒に\nクイズでお勉強しましょう！\n放課後の教室で二人きりって…\nなんだかドキドキしますね♪");
INSERT INTO `v1_e126a47905676890a95b544a2760b0678689327ece67a522e0bb2e05e32cbdb3` VALUES (6, 1013, 1013001, 0, "2024-04-18 15:00:00", "2030-04-01 14:00:00", 190801, "魔物の討伐任務にいざ行軍！\nエリコさん、マコトさんの\n頼もしい背中は、\n新兵さんたちの憧れですね♪");
INSERT INTO `v1_e126a47905676890a95b544a2760b0678689327ece67a522e0bb2e05e32cbdb3` VALUES (6, 1014, 1014001, 0, "2024-11-18 15:00:00", "2030-04-01 14:00:00", 190801, "バンディ・シスターズのみなさん\nと戦車バトルに挑戦です！\nパーツやメンバーの組み合わせで\nマシンの特徴が変化しますよ♪");
