INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "カリン", 0, 1, 1, 0, 190804, 1, 1, "新年を迎え、新たに始まる一日を\nおみくじで占ってみませんか？\n良い運勢であるほど、\n沢山のプレゼントが貰えますよ♪", "", 60000);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 1, 1, 2, 0, 0, 0, 2, "今日の運勢は姫吉！　あらゆる運気が最大を示しています！　　　今なら「伝説の吸血鬼」をも従え、世界征服も夢じゃありません！", "", 60000);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 1, 1, 2, 0, 0, 0, 3, "今日の運勢は姫吉！　やること全てが最高潮！　やばいですね☆　ラッキーアイテムはおにぎり！　お腹一杯で幸せが続くでしょう！", "", 60000);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 1, 2, 2, 0, 0, 0, 4, "今日の運勢は姫吉！　その高い運気はまさにショーグンの如く！　その溢れる威光は人々を惹きつけ、皆の人気者間違いなしです！", "", 60000);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 2, 1, 2, 0, 0, 0, 5, "今日の運勢は大吉！　運気がどんどん高まっています！　　　　　何をやっても、最大効率で素晴らしい結果になりますよ♪", "", 60000);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 2, 1, 2, 0, 0, 0, 6, "今日の運勢は大吉！　なにをやっても上手くいって大活躍！　　　そんな素敵なあなたを見て、後ろから誰かが……クスクス♪", "", 60000);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 2, 2, 2, 0, 0, 0, 7, "今日の運勢は大吉！　運気が高く、何かに挑戦すると良い結果に！隠れた才能が引き出されそう！　目指せヒデサ……ではなく秀才！", "", 60000);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 3, 1, 2, 0, 0, 0, 8, "今日の運勢は中吉！　商売運が上がってきています！　　　　　　ラッキーアイテムはたいやき！　和菓子パワーで売上アップです！", "", 60000);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 3, 1, 2, 0, 0, 0, 9, "今日の運勢は中吉！　旅行運がぐんぐんと上昇中♪　　　　　　　もしかしたら、あの「マホマホ王国」にだって行けるかも♪", "", 60000);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 3, 1, 2, 0, 0, 0, 10, "今日の運勢は中吉！　仕事運が急上昇中です！　　　　　　　　　きっと「煉獄に舞う一陣の疾風」の如き活躍ができますよ！", "", 60000);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 3, 1, 2, 0, 0, 0, 11, "今日の運勢は中吉！　チャンスを待つと良い結果に！　　　　　　ラッキーアイテムのプリンを食べて、運を呼び込みましょう！", "", 60000);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 3, 2, 2, 0, 0, 0, 12, "今日の運勢は中吉！　運気が着々と上昇中！　　　　　　　　　　精霊の加護を受けたかの如く、色々な事がうまくいきますよ♪", "", 60000);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 4, 1, 2, 0, 0, 0, 13, "今日の運勢は小吉！　早朝に幸運の兆しあり！　　　　　　　　　もしかしたら、優しいお姉ちゃんが起こしにきてくれますよ♪", "", 60000);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 4, 1, 2, 0, 0, 0, 14, "今日の運勢は小吉！　運気上昇中ですが、上がるまでが要注意！　ラッキーアニマルは熊！　本当の家族のように守ってくれるかも♪", "", 60000);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 4, 1, 2, 0, 0, 0, 15, "今日の運勢は小吉！　外に出てゆっくり過ごすと運気が上昇！　　ラッキースポットは牧場。面白い出会いがありそうです♪", "", 60000);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 4, 1, 2, 0, 0, 0, 16, "今日の運勢は小吉！　運気は高いけども、ハプニングには注意！　出かけた先で、「ふしんしゃ」扱いされてしまうかも……", "", 60000);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 4, 2, 2, 0, 0, 0, 17, "今日の運勢は小吉！　根を詰めずにリラックスすると運気向上！　ラッキーパーソンはアイドル。歌があなたを幸せにしてくれます♪", "", 60000);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "カリン", 0, 0, 3, 0, 190804, 2, 18, "おみくじの結果はどうでしたか？\n新たに始まる一日が、あなたに\nとって良い日になりますように\nまた明日も遊びに来て下さいね♪", "", 60000);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (7, "カリン", 0, 0, 3, 0, 190804, 3, 19, "おみくじは楽しんで頂けましたか？\nあなたの新たなる年が\n素晴らしいものになるよう、\n心からお祈りいたします", "", 60000);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (1, "ハツネ", 0, 0, 0, 0, 101201, 1, 20, "キミと出会ってもう\n1年もたつんだね！\nう～ん、いつも\n寝てばっかりだから、", "", 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (1, "ハツネ", 0, 0, 0, 0, 101201, 2, 21, "少しでも\n寝る時間を減らして\nキミと一緒にいる\n時間増やしたいなぁ♪", "", 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (1, "シオリ", 0, 0, 1, 0, 103801, 1, 22, "あなたと知り合ってから\nもう1年…\n時がたつのは\n早いですね", "", 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (1, "シオリ", 0, 0, 1, 0, 103801, 2, 23, "来年も、\n一緒にいられたら…\nうれしいな", "", 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (2, "キョウカ", 0, 0, 0, 0, 103601, 1, 24, "あなたと一緒にいるようになって、\nもう1年ですか…", "", 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (2, "キョウカ", 0, 0, 0, 0, 103601, 2, 25, "最初の頃よりも、頼もしく\nなりましたね。私はどうですか？\n成長していますか？", "", 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (2, "ミミ", 0, 0, 1, 0, 102001, 1, 26, "おにいちゃんとあってから、\n1年たったよ～！", "", 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (2, "ミミ", 0, 0, 1, 0, 102001, 2, 27, "ミミね、おにいちゃんと\nいっぱい遊べてたのしかったな～♪\nこれからもずっと\nいっしょにいてね～！", "", 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (2, "ミソギ", 0, 0, 2, 0, 100401, 1, 28, "にいちゃんがミソギのイタズラに\n引っかかり続けてもう1年かぁ", "", 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (2, "ミソギ", 0, 0, 2, 0, 100401, 2, 29, "…これからもいっぱいミソギと\n遊んでね、にいちゃん！", "", 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (3, "マホ", 0, 0, 0, 0, 101001, 1, 30, "もう王子はんと会うてから\n1年ぐらいたつそうどすえ\n早いもんやなあ", "", 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (3, "マホ", 0, 0, 0, 0, 101001, 2, 31, "このままやと、\nうちもあっという間に\n干からびてしまいそうどすえ\nなーんてな。うふふ", "", 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (3, "イリヤ", 0, 0, 1, 0, 104401, 1, 32, "お主と会って早1年じゃな\nこれからも存分に尽くすがよい", "", 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (3, "イリヤ", 0, 0, 1, 0, 104401, 2, 33, "わらわもたまには、お主の献身に\n報いてやらぬでもない♪", "", 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (3, "カオリ", 0, 0, 2, 0, 101701, 1, 34, "もう、キミと出会って結構な時間が\nたつさ～。でも、私たちの旅は\nまだまだ始まったばかりさ～！", "", 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (3, "カオリ", 0, 0, 2, 0, 101701, 2, 35, "これからも、どうぞよろしく\nお願いするさ～！", "", 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (4, "サレン", 0, 0, 0, 0, 102801, 1, 36, "あんたと出会ってからもうそんなに\nたつのね～", "", 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (4, "サレン", 0, 0, 0, 0, 102801, 2, 37, "あっという間だったって感じるほど\n楽しかったわ！\nこれからも一緒に頑張りましょ！", "", 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (4, "スズメ", 0, 0, 1, 0, 102501, 1, 38, "出会ってから1年間、ずっと\nご迷惑をかけっぱなしで、\nすみません…", "", 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (4, "スズメ", 0, 0, 1, 0, 102501, 2, 39, "これからはもっと頑張りますから\n見ていてくださいね\nできれば…\n今までよりも、私の近くで♪", "", 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (5, "タマキ", 0, 0, 0, 0, 104601, 1, 40, "振り返れば、今までいろんなことが\nあったにゃあ…", "", 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (5, "タマキ", 0, 0, 0, 0, 104601, 2, 41, "これからも、二人でたくさんの\n思い出をがっぽり作っていく\nにゃー！", "", 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (5, "ミフユ", 0, 0, 1, 0, 104801, 1, 42, "キミとの師弟関係も、\nもう1年なのね…\nでもキミに教えることは、", "", 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (5, "ミフユ", 0, 0, 1, 0, 104801, 2, 43, "まだまだいっぱいあるわ\nこれからも、\nしっかりついて来てね！", "", 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (6, "アキノ", 0, 0, 0, 0, 103201, 1, 44, "あなたさまと出会ってもう1年…", "", 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (6, "アキノ", 0, 0, 0, 0, 103201, 2, 45, "光陰矢のごとし、とはまさに\nこのことですわ。…思い出は決して\nお金では買えませんわね", "", 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (6, "ユカリ", 0, 0, 1, 0, 103401, 1, 46, "ダメな私を1年間も見捨てないで\nくれて、本当にありがとう", "", 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (6, "ユカリ", 0, 0, 1, 0, 103401, 2, 47, "情けないところいっぱいみせるかも\nしれないけど、これからも一緒に\nいてくれると、うれしいな", "", 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (7, "クリスティーナ", 0, 0, 0, 0, 107101, 1, 48, "オマエと出会ってもう1年か\nまさかこのワタシの結婚相手に\nなろうとはな…", "", 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (7, "クリスティーナ", 0, 0, 0, 0, 107101, 2, 49, "ともかくこれからも\n愉しませてくれよ☆", "", 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (7, "ジュン", 0, 0, 1, 0, 104701, 1, 50, "君と出会ってからもう1年か。\n以前と比べて私の表情もいくらか\n柔らかくなった気がするんだが、\nどうだろう？", "", 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (7, "ジュン", 0, 0, 1, 0, 104701, 2, 51, "見えないから分からないって？\nそれもそうだな…", "", 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (8, "トモ", 0, 0, 0, 0, 103701, 1, 52, "もう1年たったみたいだね\nあなたをからかってばかりだった\nけど、楽しかった♪", "", 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (8, "トモ", 0, 0, 0, 0, 103701, 2, 53, "これからもよろしくね！", "", 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (8, "マツリ", 0, 0, 1, 0, 100501, 1, 54, "出会って、もう1年たっちゃった\nんスね。なんだか意外ッス", "", 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (8, "マツリ", 0, 0, 1, 0, 100501, 2, 55, "毎日楽しくて、あっという間に過ぎ\nちゃったみたいなんスよね\nこれからも一緒に悪と戦うッス！", "", 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (9, "ミヤコ", 0, 0, 0, 0, 100701, 1, 56, "オマエと会って1年なの！\n全然成仏もできなかったし、", "", 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (9, "ミヤコ", 0, 0, 0, 0, 100701, 2, 57, "生き返りもしなかったけど、\nすごく楽しかったの～♪\nオマエとプリンがあれば、\nミヤコはハッピーなの～♪", "", 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (9, "シノブ", 0, 0, 1, 0, 103101, 1, 58, "あなたと出会って1年なんですね\nでも、私の占いでは、私とあなたは", "", 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (9, "シノブ", 0, 0, 1, 0, 103101, 2, 59, "来年も再来年も一緒にいるようです\nふふ、よろしくお願いしますね", "", 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (10, "アンナ", 0, 0, 0, 0, 100901, 1, 60, "貴様が閃光のシグルドとして覚醒し\nもう1年か…。これからも我らの\n長き戦いに終わりはないが、", "", 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (10, "アンナ", 0, 0, 0, 0, 100901, 2, 61, "それでも貴様となら…\n添い遂げる覚悟はできているぞ！", "", 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (10, "ナナカ", 0, 0, 1, 0, 101301, 1, 62, "君と出会ってもう1年だって！\n早いねー！", "", 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (10, "ナナカ", 0, 0, 1, 0, 101301, 2, 63, "これからも最強のバディタッグで\n大活躍だー！\n頼りにしてるよ、相棒！", "", 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (11, "ミツキ", 0, 0, 0, 0, 105101, 1, 64, "1年たつのって、あっという間ね\nまだまだ君の知らない部分が\nたくさんあるし…", "", 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (11, "ミツキ", 0, 0, 0, 0, 105101, 2, 65, "これからも、いろんなところを\n調べさせてね？", "", 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (11, "ルカ", 0, 0, 1, 0, 105601, 1, 66, "お前さんと出会ってもう1年か\n助太刀するつもりが", "", 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (11, "ルカ", 0, 0, 1, 0, 105601, 2, 67, "逆に助けられたりもしたね\nこれからもよろしく頼むよ！", "", 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (11, "エリコ", 0, 0, 2, 0, 102701, 1, 68, "早いものですわね、あなた様と\nお会いして、もう1年…", "", 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (11, "エリコ", 0, 0, 2, 0, 102701, 2, 69, "あなた様といると、時があっと\nいう間に過ぎてしまいます\n時間を止める研究をしなくては\nいけませんね…", "", 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (12, "アヤネ", 0, 0, 0, 0, 102301, 1, 70, "お兄ちゃんと会ってから、もう、\n1年なんだね。私、少しは\nお姉ちゃんっぽくなったかな？", "", 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (12, "アヤネ", 0, 0, 0, 0, 102301, 2, 71, "あ、ぷうきちもお兄ちゃんっぽく\nなったよね？", "", 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (12, "チカ", 0, 0, 1, 0, 104201, 1, 72, "あなたと出会って、\nもう1年がたったんですね…\nこれからもそばにいて、", "", 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (12, "チカ", 0, 0, 1, 0, 104201, 2, 73, "私のことを見守ってください\nできることなら、ずっと…", "", 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (12, "クルミ", 0, 0, 2, 0, 102101, 1, 74, "お兄ちゃんと出会って1年かぁ\n私、相変わらずダメダメで…", "", 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (12, "クルミ", 0, 0, 2, 0, 102101, 2, 75, "で、でもこれからもずっと…\n一緒にいたいなぁ…", "", 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (13, "シズル", 0, 0, 0, 0, 104901, 1, 76, "再会してちょうど1年だね\nお祝いにごちそう作ってきたよ～♪\n特製ケーキと、お寿司と、", "", 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (13, "シズル", 0, 0, 0, 0, 104901, 2, 77, "ハンバーグと、カレーと、\nシチューと、あ、隠しきれない隠し\n味は、お姉ちゃんの愛情だよ！", "", 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (13, "リノ", 0, 0, 1, 0, 101101, 1, 78, "お兄ちゃんと再会して、もう1年\nなんですね。\n会えなかった時を思うと、", "", 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (13, "リノ", 0, 0, 1, 0, 101101, 2, 79, "信じられないくらい幸せな1年\nでした！もう絶対、私から離れ\nないでくださいね♪", "", 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (14, "ユイ", 0, 0, 0, 0, 100201, 1, 80, "騎士クンと出会ってから今まで、\nいろんなことがあったよね\n楽しいこと、ドキドキしたこと…", "", 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (14, "ユイ", 0, 0, 0, 0, 100201, 2, 81, "そんな日々をこれからも、\nず、ずっと一緒に…\n過ごしていきたいな…！", "", 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (14, "ヒヨリ", 0, 0, 1, 0, 100101, 1, 82, "騎士くんと出会って、まだ1年しか\nたってないんだ！？", "", 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (14, "ヒヨリ", 0, 0, 1, 0, 100101, 2, 83, "なんかもうずっと一緒にいる気が\nするなぁ…えへへ。これからの\n1年もまたよろしくねっ！\n騎士くん！", "", 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (14, "レイ", 0, 0, 2, 0, 100301, 1, 84, "初めてキミに出会って1年…\n随分見違えたね私もキミも、\n更に強くなっていく…", "", 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (14, "レイ", 0, 0, 2, 0, 100301, 2, 85, "キミに背中を預けられる日が、\n早くきますように", "", 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (15, "ペコリーヌ", 0, 0, 0, 0, 105801, 1, 86, "1年もわたしのことを、忘れない\nでいてくれて、ありがとうござい\nます！", "", 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (15, "ペコリーヌ", 0, 0, 0, 0, 105801, 2, 87, "これからもずっと、覚えていて\nくださいね！", "", 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (15, "コッコロ", 0, 0, 1, 0, 105901, 1, 88, "こうして1年も、主さまのおそばに\n置いていただけて、とても光栄\nです。どうかこれからも、", "", 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (15, "コッコロ", 0, 0, 1, 0, 105901, 2, 89, "末永く主さまをお世話させて\nください。わたくしは、ずっと\n主さまのガイド役でいたいのです", "", 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (15, "キャル", 0, 0, 2, 0, 106001, 1, 90, "1年か、まっさかこんなに長い\n付き合いになるなんてね～\nでも…わりと楽しい1年だったかも", "", 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (15, "キャル", 0, 0, 2, 0, 106001, 2, 91, "だから、もうしばらくあんたに\n付き合ってやるわよ喜びなさい！", "", 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "スズメ", 0, 1, 1, 0, 112101, 1, 92, "新しい年のはじめに、\nおみくじはいかがでしょうか？\nよい運勢であるほど、\n沢山プレゼントが貰えますよ♪", "", 60001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 1, 1, 2, 0, 0, 0, 93, "今日の運勢は姫吉！　あらゆる運気が最大を示しています！　　　今なら「伝説の吸血鬼」をも従え、世界征服も夢じゃありません！", "", 60001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 1, 1, 2, 0, 0, 0, 94, "今日の運勢は姫吉！　やること全てが最高潮！　やばいですね☆　ラッキーアイテムはおにぎり！　お腹一杯で幸せが続くでしょう！", "", 60001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 1, 1, 2, 0, 0, 0, 95, "今日の運勢は姫吉！　最強の運勢でどんなドジも跳ね除けます！　大切な人と一緒に、最高の一日になること間違いなし！", "", 60001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 1, 1, 2, 0, 0, 0, 96, "今日の運勢は姫吉！　どんな挑戦も上手くいく、最高の運勢です！理想の自分に変身できる絶好の機会！　新しい一歩を踏み出そう！", "", 60001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 1, 2, 2, 0, 0, 0, 97, "今日の運勢は姫吉！　その高い運気はまさにショーグンの如く！　その溢れる威光は人々を惹きつけ、皆の人気者間違いなしです！", "", 60001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 2, 1, 2, 0, 0, 0, 98, "今日の運勢は大吉！　運気がどんどん高まっています！　　　　　何をやっても、最大効率で素晴らしい結果になりますよ♪", "", 60001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 2, 1, 2, 0, 0, 0, 99, "今日の運勢は大吉！　なにをやっても上手くいって大活躍！　　　そんな素敵なあなたを見て、後ろから誰かが……クスクス♪", "", 60001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 2, 1, 2, 0, 0, 0, 100, "今日の運勢は大吉！　ラッキーワードは「ちぇる～ん♪」　　　　出会う人みんなに言ってあげると、ハッピーになれますよ！", "", 60001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 2, 1, 2, 0, 0, 0, 101, "今日の運勢は大吉！　運勢はバッチリで無敵状態！　　　　　　　龍眼で全てを見通して、思い通りの一日を過ごしましょう！", "", 60001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 2, 2, 2, 0, 0, 0, 102, "今日の運勢は大吉！　運気が高く、何かに挑戦すると良い結果に！隠れた才能が引き出されそう！　目指せヒデサ……ではなく秀才！", "", 60001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 3, 1, 2, 0, 0, 0, 103, "今日の運勢は中吉！　商売運が上がってきています！　　　　　　ラッキーアイテムはたいやき！　和菓子パワーで売上アップです！", "", 60001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 3, 1, 2, 0, 0, 0, 104, "今日の運勢は中吉！　旅行運がぐんぐんと上昇中♪　　　　　　　もしかしたら、あの「マホマホ王国」にだって行けるかも♪", "", 60001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 3, 1, 2, 0, 0, 0, 105, "今日の運勢は中吉！　仕事運が急上昇中です！　　　　　　　　　きっと「煉獄に舞う一陣の疾風」の如き活躍ができますよ！", "", 60001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 3, 1, 2, 0, 0, 0, 106, "今日の運勢は中吉！　チャンスを待つと良い結果に！　　　　　　ラッキーアイテムのプリンを食べて、運を呼び込みましょう！", "", 60001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 3, 1, 2, 0, 0, 0, 107, "今日の運勢は中吉！　お出かけした先で良縁に恵まれそうです！　ダウナー系あざとい女子が、あま～い飴をくれるかも？", "", 60001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 3, 1, 2, 0, 0, 0, 108, "今日の運勢は中吉！　大抵のことはゴリ押しでなんとかできます！ラッキープレイスはゲーセン。パンチングマシンでストレス発散！", "", 60001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 3, 2, 2, 0, 0, 0, 109, "今日の運勢は中吉！　運気が着々と上昇中！　　　　　　　　　　精霊の加護を受けたかの如く、色々な事がうまくいきますよ♪", "", 60001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 4, 1, 2, 0, 0, 0, 110, "今日の運勢は小吉！　早朝に幸運の兆しあり！　　　　　　　　　もしかしたら、優しいお姉ちゃんが起こしにきてくれますよ♪", "", 60001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 4, 1, 2, 0, 0, 0, 111, "今日の運勢は小吉！　運気上昇中ですが、上がるまでが要注意！　ラッキーアニマルは熊！　本当の家族のように守ってくれるかも♪", "", 60001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 4, 1, 2, 0, 0, 0, 112, "今日の運勢は小吉！　外に出てゆっくり過ごすと運気が上昇！　　ラッキースポットは牧場。面白い出会いがありそうです♪", "", 60001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 4, 1, 2, 0, 0, 0, 113, "今日の運勢は小吉！　運気は高いけども、ハプニングには注意！　出かけた先で、「ふしんしゃ」扱いされてしまうかも……", "", 60001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 4, 1, 2, 0, 0, 0, 114, "今日の運勢は小吉！　同腹一心の友だちが見つかるかも！　　　　キーワードは「ユニちゃんズ」、記憶に刻みましょう！", "", 60001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 4, 1, 2, 0, 0, 0, 115, "今日の運勢は小吉！　思わぬ相手と仲良くなれるかも？　　　　　うっかり毒舌をもらしてしまわないように要注意です！", "", 60001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 4, 2, 2, 0, 0, 0, 116, "今日の運勢は小吉！　根を詰めずにリラックスすると運気向上！　ラッキーパーソンはアイドル。歌があなたを幸せにしてくれます♪", "", 60001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "スズメ", 0, 0, 3, 0, 112101, 2, 117, "おみくじの結果はいかがでしたか？\n新たに始まる一日が、\nあなたにとってよい日になりますように\n明日もお待ちしていますね♪", "", 60001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (7, "スズメ", 0, 0, 3, 0, 112101, 3, 118, "おみくじ、楽しんで頂けましたか？\nあなたの新しい年が\n素晴らしいものになるよう、\n心からお祈りしますね", "", 60001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (1, "ペコリーヌ", 0, 0, 0, 0, 105801, 1, 119, "ヒューヒュー♪\nドンドンパフパフー♪", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (1, "コッコロ", 0, 0, 1, 0, 105901, 1, 120, "ひゅーひゅー\nどんどんぱふぱふー\nでございます", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (1, "コッコロ", 0, 0, 1, 0, 105901, 2, 121, "さぁ、キャルさまもご一緒に\nどんどんぱふぱふいたしましょう", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (1, "キャル", 0, 0, 2, 0, 106001, 1, 122, "え？　ちょっと\nいきなりなにっ？", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (1, "キャル", 0, 0, 2, 0, 106001, 2, 123, "ドンドンパフパフってなんなの？\nあたしにも分かるように\nちゃんと説明しなさいっての！", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (1, "ペコリーヌ", 0, 0, 0, 0, 105801, 2, 124, "実はですねキャルちゃん！\nもうすぐわたしたちにとっての\n大切な節目──", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (1, "ペコリーヌ", 0, 0, 0, 0, 105801, 3, 125, "『プリンセスコネクト！\nRe:Dive』\n2.5周年なんですよ！\nやばいですね☆", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (1, "コッコロ", 0, 0, 1, 0, 105901, 3, 126, "ですからその日に向けて\n今から気分を盛り上げているのです", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (1, "キャル", 0, 0, 2, 0, 106001, 3, 127, "ああ、そういえば\nそんな時期だっけ\nって言っても、もう少し先じゃない\n気が早いんじゃないの？", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (1, "コッコロ", 0, 0, 1, 0, 105901, 4, 128, "いいえ、キャルさま\nこういったお祝いは準備から\n楽しむものでございます\nそうですよね、ペコリーヌさま", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (1, "ペコリーヌ", 0, 0, 0, 0, 105801, 4, 129, "その通りです♪　というわけで！\nカウントダウンの始まりですよ！\nみんなで一緒に2.5周年に向けて\n盛り上がっていきましょー☆", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (1, "キャル", 0, 0, 2, 0, 106001, 4, 130, "ド、ドンドンパフパフ～\nって何言わせんのよ！\nまったくもう、浮かれちゃってさ", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (1, "コッコロ", 0, 0, 1, 0, 105901, 5, 131, "ふふ。キャルさまもワクワクして\nおいでのようですね\nそれでは主さま。大切な記念日まで\nどうぞご期待くださいませ", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (2, "キャル", 0, 0, 0, 0, 106001, 5, 132, "記念日を盛り上げるったって\n一体どうしたらいいのよ？\nあたし、そういうの\n苦手なんだけど…", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (2, "カリン", 0, 0, 1, 0, 190801, 1, 133, "あら、キャルさん\nどうかなさいましたか？\n何かお困りのようですけど", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (2, "キャル", 0, 0, 0, 0, 106001, 6, 134, "げっ…カリンじゃない\nなんでもないわよ！", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (2, "カリン", 0, 0, 1, 0, 190801, 2, 135, "そうなのですか？\nキャルさんのことですからてっきり\n2.5周年をどう盛り上げたら\nいいか悩んでいるのかと…", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (2, "キャル", 0, 0, 0, 0, 106001, 7, 136, "分かってんじゃないのよ！？\nいっ、いや、違うけどね！\nあたしは別にそんなことで\n悩んでないわよっ？", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (2, "カリン", 0, 0, 1, 0, 190801, 3, 137, "ふふっ…\nこういう時は、真心を込めて\n気持ちをそのまま口にしたら\nいいのではないでしょうか？", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (2, "キャル", 0, 0, 0, 0, 106001, 8, 138, "気持ちを、ねぇ\nそれが一番難しいんじゃない…\nそもそも、あたしは記念日とか\n別にどうだっていいし", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (2, "キャル", 0, 0, 0, 0, 106001, 9, 139, "…まあ、ペコリーヌもコロ助も\nやけに楽しそうだったから…\nこういうお祭り騒ぎみたいなのも\nたまにはいいかな、とは思うけど", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (2, "カリン", 0, 0, 1, 0, 190801, 4, 140, "なるほどなるほど～\nキャルさんは【美食殿】で祝う\n2.5周年を心から楽しみに\nしている、ということですね！", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (2, "キャル", 0, 0, 0, 0, 106001, 10, 141, "はぁ！？\nどこ聞いたらそうなるのよ！\nあたしは別に楽しみになんか…！", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (2, "カリン", 0, 0, 1, 0, 190801, 5, 142, "キャルさんの心からの想い\nみなさんにも伝わったでしょうか？\n私も2.5周年が楽しみです！\n以上、カリンがお伝えしました！", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (2, "キャル", 0, 0, 0, 0, 106001, 11, 143, "心からの想いって何なのよ！？\n勝手に人の言葉を\n捻じ曲げんじゃないわよ！\nこのくそ眼鏡ーーーっ！", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (3, "コッコロ", 0, 0, 0, 0, 105901, 6, 144, "ペコリーヌさま、もうすぐ\n記念日でございますね\nこういった節目というのは\n感慨深いものでございます", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (3, "ペコリーヌ", 0, 0, 1, 0, 105801, 5, 145, "色んな思い出が蘇ってきますね！\n楽しい出来事が\nいっぱいありました♪", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (3, "コッコロ", 0, 0, 0, 0, 105901, 7, 146, "はい、そうでございますね\n主さまも【美食殿】の活動の他にも\nいろんなご経験をされたと\n聞いておりますよ", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (3, "コッコロ", 0, 0, 0, 0, 105901, 8, 147, "たしか…ランドソルで話題沸騰と\nなっていた、魔法少女なるものに\nお会いになられたとか", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (3, "ペコリーヌ", 0, 0, 1, 0, 105801, 6, 148, "魔法少女って、あの美味しくって\n栄養たっぷりの大ヒット商品\n『魔法少女ソーセージ』の箱に\n描いてある女の子のことですか！？", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (3, "ペコリーヌ", 0, 0, 1, 0, 105801, 7, 149, "あのソーセージ\n美味しいんですよね～♪\nわたしもたっくさん食べてますよ！", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (3, "コッコロ", 0, 0, 0, 0, 105901, 9, 150, "あとは、牧場に素敵な温泉が\n出来たとか…", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (3, "ペコリーヌ", 0, 0, 1, 0, 105801, 8, 151, "牧場ですか！？　温泉につかって\nチーズもオツですね～♪　考えた\nだけでよだれが出ちゃいそうです！\nやばいですね☆", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (3, "コッコロ", 0, 0, 0, 0, 105901, 10, 152, "ペコリーヌさま\n言ってるそばからよだれが…\n今お拭きしますね。ふき、ふき", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (3, "ペコリーヌ", 0, 0, 1, 0, 105801, 9, 153, "えへへ、ありがとうございます\nコッコロちゃん！", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (3, "コッコロ", 0, 0, 0, 0, 105901, 11, 154, "それにしても、主さまは\n【美食殿】の一員として様々な\n美食と出会われていたのですね\nわたくし、感服いたしました", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (3, "ペコリーヌ", 0, 0, 1, 0, 105801, 10, 155, "これからもたくさん冒険をして\nめいっぱい美食を\n味わい尽くしましょうね！", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (4, "ペコリーヌ", 0, 0, 0, 0, 105801, 11, 156, "2.5周年カウントダウン♪\nキャルちゃんキャルちゃん！\nわくわくしますね☆", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (4, "キャル", 0, 0, 1, 0, 106001, 12, 157, "あぁもう、うるさいわね～\nまだまだ先でしょ？\nちょっとは落ち着きなさいよ", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (4, "ペコリーヌ", 0, 0, 0, 0, 105801, 12, 158, "キャルちゃんだって最近\nソワソワしてるじゃないですか～\nさっきもご機嫌そうに鼻歌とか\n歌ってましたよね？", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (4, "キャル", 0, 0, 1, 0, 106001, 13, 159, "なっ…！　き、聞いてたの！？\nじゃない、歌ってないわよ！？\nあんたいい加減なこと言うと\nぶっ殺すわよ！？", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (4, "ペコリーヌ", 0, 0, 0, 0, 105801, 13, 160, "えっへへ～♪\nわたしは最近のキャルちゃんが\nもっともっと大好きですよ♪\nすごく積極的になりましたよね！", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (4, "キャル", 0, 0, 1, 0, 106001, 14, 161, "…積極的？\nなんの話よ？", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (4, "ペコリーヌ", 0, 0, 0, 0, 105801, 14, 162, "またまた～♪\nアイドルを目指そうとしたこと\n忘れちゃったんですか？", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (4, "ペコリーヌ", 0, 0, 0, 0, 105801, 15, 163, "ムーサの町で\nきゃるきゃる～ん☆　って！", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (4, "キャル", 0, 0, 1, 0, 106001, 15, 164, "あっ…！？　ちちち違うわよっ！\nあれは操られてただけで\nあたしはアイドルなんか\n目指してないっての！！", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (4, "ペコリーヌ", 0, 0, 0, 0, 105801, 16, 165, "本当に本当ですか？\nわたし、キャルちゃんとっても\n乗り気だなって\n思ってたんですけど～", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (4, "キャル", 0, 0, 1, 0, 106001, 16, 166, "まぁ、悪くはなかったけど…って\nだ、だから目指してないって\n言ってるでしょ！！\nなんてこと思い出させんのよ！？", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (4, "キャル", 0, 0, 1, 0, 106001, 17, 167, "ああ…ダメだわ…\nもう殺すしかない…\n2.5周年が来る前に\n記憶ごと消し去ってやるー！", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (5, "キャル", 0, 0, 0, 0, 106001, 18, 168, "2.5周年まであと10日…\nふ～ん？\n案外早いもんね？", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (5, "コッコロ", 0, 0, 1, 0, 105901, 12, 169, "そうでございますね…\nその日のためにやるべきことは\nまだまだございます\n気を引き締めねばなりません…！", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (5, "キャル", 0, 0, 0, 0, 106001, 19, 170, "やる気十分ねコロ助…\nそんな気負ってるとしんどいわよ？\nお気楽にやりましょ？", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (5, "コッコロ", 0, 0, 1, 0, 105901, 13, 171, "キャルさま…\nありがとうございます\nキャルさまは本当に\n優しいおかたです", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (5, "キャル", 0, 0, 0, 0, 106001, 20, 172, "な、なにわけの分かんないこと\n言ってんのよ…\nあたしは優しくなんかないっての", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (5, "コッコロ", 0, 0, 1, 0, 105901, 14, 173, "そんなことはございません\n…あの、実はわたくし\nキャルさまに\nひとつお願いがございまして", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (5, "キャル", 0, 0, 0, 0, 106001, 21, 174, "お願い？　珍しいわね\nコロ助がそんなこと言うなんて\n…まあ、あたしに出来ることなら\n別にいいけど", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (5, "コッコロ", 0, 0, 1, 0, 105901, 15, 175, "ありがとうございます\n実は記念日へ向けて\n『かくし芸』というものを\n用意してみようかと思いまして", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (5, "キャル", 0, 0, 0, 0, 106001, 22, 176, "かくし芸？", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (5, "コッコロ", 0, 0, 1, 0, 105901, 16, 177, "はい。きゃるきゃる～ん☆　を\n参考に、ころころ～ん☆　という\nご挨拶を考えてみたのですが\nポーズがうまくいかなくて…", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (5, "キャル", 0, 0, 0, 0, 106001, 23, 178, "～～！！　ちょっと褒められて\nいい気になって損したわ！！\nふざけんなふざけんな\nふ～ざ～け～ん～な～っ！！", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (6, "ペコリーヌ", 0, 0, 0, 0, 105801, 17, 179, "あと９日！　もうすぐですよ♪\nえへへ、楽しみで楽しみで\nごはんが進みますね☆", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (6, "キャル", 0, 0, 2, 0, 106001, 24, 180, "ごはんが進みますね☆\nじゃなーい！\nいくらなんでも食べ過ぎよ！\n記念日、迎えられなくなるわよ！？", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (6, "コッコロ", 0, 0, 1, 0, 105901, 17, 181, "大丈夫ですよキャルさま\n食事はわたくしたちの\n歩んできた道そのもの\nなのですから", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (6, "ペコリーヌ", 0, 0, 0, 0, 105801, 18, 182, "さっすがコッコロちゃん\nいいことを言いますね！\nそう、わたしたち\n【美食殿】の活動目的は─", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (6, "キャル", 0, 0, 2, 0, 106001, 25, 183, "─みんなで楽しく食事をすること\nでしょ？\nあれだけ食べ歩いてきたんだから\n分かってるわよ", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (6, "コッコロ", 0, 0, 1, 0, 105901, 18, 184, "はい…そうでございますね♪\nそれにしてもわたくしたち\n本当にいろいろなものを\n食べ歩いてきましたね…", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (6, "キャル", 0, 0, 2, 0, 106001, 26, 185, "そうね…ほんっとうにね…\n毒まみれのニセドラゴンとか\n恐竜…は逃げられちゃったっけ\nあとワニゴ…ワニの魔物とかね", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (6, "キャル", 0, 0, 2, 0, 106001, 27, 186, "そして、とうとう本物の\nドラゴンまで相手に\nしちゃったのよね、あたしたちって", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (6, "キャル", 0, 0, 2, 0, 106001, 28, 187, "信じられない強さだったわね\nあのドラゴン…やっぱり\n作戦はしっかり立てていかないと\nダメよね", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (6, "ペコリーヌ", 0, 0, 0, 0, 105801, 19, 188, "えへへ…☆\nキャルちゃんが色んなことを\n覚えていてくれて\nうれしいです♪", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (6, "コッコロ", 0, 0, 1, 0, 105901, 19, 189, "わたくしも鮮明に覚えておりますよ\n主さまもきっと…♪", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (6, "キャル", 0, 0, 2, 0, 106001, 29, 190, "あったり前でしょ！\n忘れないわよ、あんな滅茶苦茶な\nものばかり食べさせられて！\n…忘れられるわけ、ないじゃない", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (7, "キャル", 0, 0, 0, 0, 106001, 30, 191, "ねえちょっと、ちょっと待って！？\nカリンから、2.5周年記念の\n生放送に出演してほしいって\n言われたんだけど！", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (7, "キャル", 0, 0, 0, 0, 106001, 31, 192, "なんでそういう大事なことを\n急に言うわけ！？\nそんなの聞いてないわよ～！？", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (7, "キャル", 0, 0, 0, 0, 106001, 32, 193, "いっぱい人に見られちゃうのよね？\nなんであたしがそんなこと…", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (7, "キャル", 0, 0, 0, 0, 106001, 33, 194, "あー\nあー\n…声、これで大丈夫かしら？", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (7, "キャル", 0, 0, 0, 0, 106001, 34, 195, "聞き取りにくいかもしれないわね…\nもうちょっと明るい方がいいかも\nえっと…こんな感じ？", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (7, "キャル", 0, 0, 0, 0, 106001, 35, 196, "オイッスー☆", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (7, "キャル", 0, 0, 0, 0, 106001, 36, 197, "……\nいや、ないわ\nいつもどおりが一番ね、うん", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (7, "キャル", 0, 0, 0, 0, 106001, 37, 198, "もう、記念の生放送なんて\n超責任重大なやつじゃない…\nもしなにか失敗とかしたら…", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (7, "キャル", 0, 0, 0, 0, 106001, 38, 199, "ど、どど、どうしよう！？\n緊張してきちゃった…！！", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (8, "コッコロ", 0, 0, 0, 0, 105901, 20, 200, "主さま、わたくし\nペコリーヌさまとキャルさまと共に\n2.5周年を祝う生放送に\n出演する大役を仰せつかりました", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (8, "コッコロ", 0, 0, 0, 0, 105901, 21, 201, "多くの方々に\n『プリンセスコネクト！\nRe:Dive』の魅力をお伝えする\n重要な役割でございます", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (8, "コッコロ", 0, 0, 0, 0, 105901, 22, 202, "わたくし、全身全霊をもって\n生放送に取り組みたいと\n思っております", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (8, "コッコロ", 0, 0, 0, 0, 105901, 23, 203, "ですが、もしかしたら\nセリフを噛んでしまったり\n段取りを間違えてしまったり\nするかもしれません…", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (8, "コッコロ", 0, 0, 0, 0, 105901, 24, 204, "そのようなことがないよう\n練習はしっかりしておかなくては\nまずはこちらから…", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (8, "コッコロ", 0, 0, 0, 0, 105901, 25, 205, "オイッスー☆", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (8, "コッコロ", 0, 0, 0, 0, 105901, 26, 206, "先ほどキャルさまが練習されて\nいるのをお見かけしたので\nわたくしもと思いまして…\nいかがでしょうか？", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (8, "コッコロ", 0, 0, 0, 0, 105901, 27, 207, "良い感じ、ですか？\n…ふふ、ありがとうございます\n主さまのあたたかいお言葉があれば\nわたくしは百人力でございます", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (8, "コッコロ", 0, 0, 0, 0, 105901, 28, 208, "この大役を無事に成し遂げ\n主さまに素敵な2.5周年を\n迎えていただけるよう…\nわたくし、尽力いたしますね", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (9, "ペコリーヌ", 0, 0, 0, 0, 105801, 20, 209, "オイッスー☆", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (9, "ペコリーヌ", 0, 0, 0, 0, 105801, 21, 210, "…う～ん、今のはちょっと\n違う感じがしますね\n堅苦しかったかもしれません", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (9, "ペコリーヌ", 0, 0, 0, 0, 105801, 22, 211, "あと６日で2.5周年ですか…", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (9, "ペコリーヌ", 0, 0, 0, 0, 105801, 23, 212, "この年月がオイッスーの\n歴史なんだと思うと\nじ～んとしちゃいます！\nやばいですね☆", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (9, "ペコリーヌ", 0, 0, 0, 0, 105801, 24, 213, "生放送で最高のオイッスー☆　を\nみなさんにお届けするために\n頑張らないと！\nそれじゃあもう一度…", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (9, "ペコリーヌ", 0, 0, 0, 0, 105801, 25, 214, "オイッスー☆", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (9, "ペコリーヌ", 0, 0, 0, 0, 105801, 26, 215, "…うう～ん、どうしてでしょう\nまだ何かが違う気がします…\nもしかしたら\nおなかが空きすぎてるとか？", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (9, "ペコリーヌ", 0, 0, 0, 0, 105801, 27, 216, "今さっき食べたばっかりですけど\nおなかが減っては\n良いオイッスー☆　は\n出せませんよね！", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (9, "ペコリーヌ", 0, 0, 0, 0, 105801, 28, 217, "そうと決まれば、コッコロちゃんと\nキャルちゃんを誘って\nごはんにしましょう！\n善は急げです！", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (9, "ペコリーヌ", 0, 0, 0, 0, 105801, 29, 218, "あ、二人が丁度いいところに！\nオイッス～☆　…ああ！！\n今の、今のですよ～♪", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (9, "ペコリーヌ", 0, 0, 0, 0, 105801, 30, 219, "なるほど、どんな時も\n自然体が一番ってことですね！\nこの調子で、生放送も\n頑張っちゃいます♪", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (10, "ペコリーヌ", 0, 0, 0, 0, 105801, 31, 220, "2.5周年まで残り５日ですが\nその前にもうすぐ生放送ですよ！\nコッコロちゃん\n準備の方はどうですか？", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (10, "コッコロ", 0, 0, 1, 0, 105901, 29, 221, "はい。わたくし、しっかりと\n備えてまいりました\n万事抜かりはございません", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (10, "ペコリーヌ", 0, 0, 0, 0, 105801, 32, 222, "おお～すごい自信を感じます…！\nいい生放送になりそうですね☆\n……あれ？　なんだかこの辺り\n揺れていませんか？", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (10, "キャル", 0, 0, 2, 0, 106001, 39, 223, "…………", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (10, "コッコロ", 0, 0, 1, 0, 105901, 30, 224, "キャルさま？\nお風邪をひかれたんでしょうか\nひどい震えですが…", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (10, "キャル", 0, 0, 2, 0, 106001, 40, 225, "かかかか風邪じゃないわよ！\nここここれはただ\nななな生放送が楽しみ過ぎて\nワワワワクワクが止まらないだけ！", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (10, "ペコリーヌ", 0, 0, 0, 0, 105801, 33, 226, "大丈夫ですかキャルちゃん！？\nそうだ、これを飲んでください！", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (10, "キャル", 0, 0, 2, 0, 106001, 41, 227, "きき気がきくじゃない、ありがとっ\nごくっ、んく…ぶはぁっ！？\nちょっと何飲ませたの！？\n辛っ！　苦ぁ！？　甘ぁっ！？", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (10, "ペコリーヌ", 0, 0, 0, 0, 105801, 34, 228, "これは魔物のエキスに薬草に\nその他色んなものを混ぜた\n特製の栄養ドリンクです！", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (10, "ペコリーヌ", 0, 0, 0, 0, 105801, 35, 229, "これを飲めば体ぽかぽかで\n元気いっぱいですよ♪", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (10, "キャル", 0, 0, 2, 0, 106001, 42, 230, "このアホリーヌ！\n風邪じゃないって言ったでしょ！？\nどさくさに紛れて\nそんな恐ろしいもん飲ませんな～！", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (10, "コッコロ", 0, 0, 1, 0, 105901, 31, 231, "キャルさま\n震えが収まっておりますね\nよかったです…\nわたくし、心配いたしました", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (10, "キャル", 0, 0, 2, 0, 106001, 43, 232, "え？　ああ、ホントね…って\nだから風邪じゃないって\n言ってるのに…\nまあいいわこの際なんでも", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (10, "ペコリーヌ", 0, 0, 0, 0, 105801, 36, 233, "えっへへ～♪　いつも通りの\nキャルちゃんに戻りましたし\n素敵な生放送が出来そうですね！\n今から楽しみです☆", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (11, "カリン", 0, 0, 0, 0, 190801, 6, 234, "ランドソルのみなさま、こんにちは\n生放送の会場設営を担当しています\n【ギルド管理協会】のカリンです", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (11, "カリン", 0, 0, 0, 0, 190801, 7, 235, "【美食殿】のみなさんは\nまもなく始まる生放送に向けて\n最後の調整のため\n席を外しています", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (11, "カリン", 0, 0, 0, 0, 190801, 8, 236, "頑張っている３人の\n邪魔をするわけには\nいきませんので、ご容赦ください", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (11, "カリン", 0, 0, 0, 0, 190801, 9, 237, "ですので、本日は私から\n【美食殿】のみなさんの様子を\nお伝えさせていただこうと思います", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (11, "カリン", 0, 0, 0, 0, 190801, 10, 238, "まずはペコリーヌさんですが…\n何やら大量のおにぎりを\n食べているようです\nえっと…栄養は大事ですよね", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (11, "カリン", 0, 0, 0, 0, 190801, 11, 239, "次にコッコロさんですが\nこちらは発声練習をしています", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (11, "カリン", 0, 0, 0, 0, 190801, 12, 240, "一生懸命、小さなお口を開けて\n声を出す姿が応援したくなりますね", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (11, "カリン", 0, 0, 0, 0, 190801, 13, 241, "そしてキャルさんは…\nこれは…堂々としたものですね\n何もせず、ただ立ち尽くして…", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (11, "カリン", 0, 0, 0, 0, 190801, 14, 242, "あれ？\nもしかして緊張で気を失って…？", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (11, "カリン", 0, 0, 0, 0, 190801, 15, 243, "…ええと\n【美食殿】のみなさんは\nやる気十分なようですね！", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (11, "カリン", 0, 0, 0, 0, 190801, 16, 244, "３人とも生放送を\nいいものにしようと\n全力で頑張ってくれていますので\nどうかご期待ください", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (11, "カリン", 0, 0, 0, 0, 190801, 17, 245, "それでは\nまた明日お会いしましょう♪", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (12, "キャル", 0, 0, 0, 0, 106001, 44, 246, "2.5周年まであと３日！\nそして生放送は明日！\nなんかいよいよって感じね…", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (12, "ペコリーヌ", 0, 0, 2, 0, 105801, 37, 247, "３人でい～っぱい練習しましたし\nきっと大丈夫ですよ☆", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (12, "コッコロ", 0, 0, 1, 0, 105901, 32, 248, "ペコリーヌさまに\n大丈夫と言ってもらえると\nとても心強いですね", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (12, "キャル", 0, 0, 0, 0, 106001, 45, 249, "信じすぎちゃだめよコロ助\nこいつはなーんにも\n考えてないだけなんだから", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (12, "ペコリーヌ", 0, 0, 2, 0, 105801, 38, 250, "ひどいですよぉキャルちゃ～ん！", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (12, "コッコロ", 0, 0, 1, 0, 105901, 33, 251, "ふふ。お二人とも\n普段通りの様子で\n安心いたしました", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (12, "ペコリーヌ", 0, 0, 2, 0, 105801, 39, 252, "はいっ☆\n生放送でもなんでも\n全力全開、わたしらしく\nぶつかっていくだけです！", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (12, "キャル", 0, 0, 0, 0, 106001, 46, 253, "そうね。おめでたいことなんだし\nここまで来たんなら\n楽しめるだけ\n楽しむしかないわよね！", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (12, "コッコロ", 0, 0, 1, 0, 105901, 34, 254, "わたくしも、普段よりも心が\n熱くなっております\nこの胸の気持ちをお伝えできるよう\n精一杯努力いたします…！", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (12, "ペコリーヌ", 0, 0, 2, 0, 105801, 40, 255, "それでは、いきましょう！\n明日の生放送、絶対に\n成功させましょうね☆", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (13, "ペコリーヌ", 0, 0, 0, 0, 105801, 41, 256, "えっと、最初のひと言は…あれ？\nキャルちゃん、わたし\n何て言うんでしたっけ？", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (13, "キャル", 0, 0, 2, 0, 106001, 47, 257, "は！？\nなにいきなりど忘れしてるの！？\nっていうかあんた、台本は！？", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (13, "ペコリーヌ", 0, 0, 0, 0, 105801, 42, 258, "忘れてきちゃいました☆", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (13, "キャル", 0, 0, 2, 0, 106001, 48, 259, "忘れてきちゃいました☆\nで済ませられると思ってんの！？\nほら、あたし二冊持ってきたから\nこれ使いなさい！", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (13, "コッコロ", 0, 0, 1, 0, 105901, 35, 260, "マイクとは、こんな高い位置に\nあるものなのですね…！\n背伸びをすれば、何とか…\nう～ん、うぅ～ん…！", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (13, "キャル", 0, 0, 2, 0, 106001, 49, 261, "あぁもう\n少し下げればいいでしょ！？\nこれくらいでいい？\nまったく、世話が焼けるわね…", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (13, "ペコリーヌ", 0, 0, 0, 0, 105801, 43, 262, "あれ？\nキャルちゃん、後ろ髪が\nはねちゃってますよ？", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (13, "キャル", 0, 0, 2, 0, 106001, 50, 263, "え？　うそっ！？\nちゃんと確かめてきたのに！\nどこどこ！？", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (13, "コッコロ", 0, 0, 1, 0, 105901, 36, 264, "キャルさま、動かないで\nくださいまし。今くしで…\nはい、直りましたよ", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (13, "キャル", 0, 0, 2, 0, 106001, 51, 265, "あ、ありがと…はぁ。生放送\n本番までもうすぐなのに\nこんなことしてる場合じゃ\nないわよね", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (13, "コッコロ", 0, 0, 1, 0, 105901, 37, 266, "はい、時間は残りわずかです\nですが、慌ただしく\n動き回るこの時間も\nなんだか楽しくて…♪", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (13, "キャル", 0, 0, 2, 0, 106001, 52, 267, "コロ助、あんた…って\nそうじゃなくて！\n胸にジンワリきてる\n暇なんかないでしょ…！？", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (13, "ペコリーヌ", 0, 0, 0, 0, 105801, 44, 268, "あはは♪\nキャルちゃんの動きが\nとっても面白いです♪\nやばいですね☆", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (13, "コッコロ", 0, 0, 1, 0, 105901, 38, 269, "ふふ\nきっと面白い生放送になると\nわたくし、確信しております", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (13, "ペコリーヌ", 0, 0, 0, 0, 105801, 45, 270, "絶対絶対、間違いありません！\n見てくれるみなさんが\n笑顔になれる時間を\nお届けしましょう！", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (13, "キャル", 0, 0, 2, 0, 106001, 53, 271, "そんなの当然でしょ！\n誰が見たって面白いって\n言ってもらえるように\nあれだけ準備したんだから！", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (13, "ペコリーヌ", 0, 0, 0, 0, 105801, 46, 272, "それでは\n記念日直前生放送！\n楽しみにしててくださいね☆", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (14, "カリン", 0, 0, 0, 0, 190801, 18, 273, "こんにちは\n【ギルド管理協会】のカリンです", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (14, "カリン", 0, 0, 0, 0, 190801, 19, 274, "昨日の生放送は\nいかがだったでしょうか？", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (14, "カリン", 0, 0, 0, 0, 190801, 20, 275, "たくさんの笑顔と喜びと\nそして驚きを受け取って\nいただけたのではないかと思います", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (14, "カリン", 0, 0, 0, 0, 190801, 21, 276, "そしていよいよ\n明日は2.5周年の当日\n待ちに待った記念日を\n迎えることとなります", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (14, "カリン", 0, 0, 0, 0, 190801, 22, 277, "【美食殿】のみなさんも\n張り切って様々な準備を\nされていますよ", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (14, "カリン", 0, 0, 0, 0, 190801, 23, 278, "【ギルド管理協会】も\n万全のサポート体制で\n協力してまいりたいと思います", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (14, "カリン", 0, 0, 0, 0, 190801, 24, 279, "…ふふ、本当に楽しみですね\n【ギルド管理協会】の職員として\nだけではなく、私個人としても…", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (14, "カリン", 0, 0, 0, 0, 190801, 25, 280, "この記念日を迎えられることが\n心から楽しみですし\n幸せを感じています", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (14, "カリン", 0, 0, 0, 0, 190801, 26, 281, "きっと【美食殿】のみなさんは\nそれ以上に心を弾ませて\nいることでしょう", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (14, "カリン", 0, 0, 0, 0, 190801, 27, 282, "それでは\n明日からの2.5周年を\nどうぞ、お楽しみに…♪", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (15, "ペコリーヌ", 0, 0, 0, 0, 105801, 47, 283, "準備はいいですかっ？\n…いっきますよー！", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (15, "キャル", 0, 0, 2, 0, 106001, 54, 284, "祝！", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (15, "コッコロ", 0, 0, 1, 0, 105901, 39, 285, "2.5周年！", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (15, "ペコリーヌ", 0, 0, 0, 0, 105801, 48, 286, "おめでとうございま～す☆", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (15, "キャル", 0, 0, 2, 0, 106001, 55, 287, "少し前まではまだまだ先じゃない\nとか言ってたけど\n本当にあっという間だったわね", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (15, "コッコロ", 0, 0, 1, 0, 105901, 40, 288, "光陰矢の如し…\nとはこのことでございますね\nでも、今日という日を無事に\n迎えることができて良かったです", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (15, "ペコリーヌ", 0, 0, 0, 0, 105801, 49, 289, "コッコロちゃん！　キャルちゃん！\n落ち着くには\nまだ早いですよ！\n盛り上がるのはこれからです☆", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (15, "キャル", 0, 0, 2, 0, 106001, 56, 290, "ほんとあんたはいつも\n全力全開ねー\nでもそうね、記念日なんだし\nパーっといきましょ！", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (15, "コッコロ", 0, 0, 1, 0, 105901, 41, 291, "ええ、わたくしも全力全開で\nまいります…！\n主さま、さあ\nどうぞ手を取ってくださいまし…♪", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (15, "キャル", 0, 0, 2, 0, 106001, 57, 292, "あんたもコロ助も、浮かれて\n転ぶんじゃないわよ？\n…仕方ないから、あたしも\n手を繋いであげるわよ", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (15, "ペコリーヌ", 0, 0, 0, 0, 105801, 50, 293, "あー！　二人ともずるいです！\nわたしもっ、ギュー♪", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (15, "キャル", 0, 0, 2, 0, 106001, 58, 294, "ちょっ、ペコリーヌ！\nくっつき過ぎよ！", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (15, "ペコリーヌ", 0, 0, 0, 0, 105801, 51, 295, "記念日だから\n平気平気♪\nやばいですね☆", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (15, "キャル", 0, 0, 2, 0, 106001, 59, 296, "意味分かんないわよ！", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (15, "コッコロ", 0, 0, 1, 0, 105901, 42, 297, "ふふ。このめでたき日に\n喜びがあふれておりますね\n主さまは…いかがでしょうか？", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (15, "コッコロ", 0, 0, 1, 0, 105901, 43, 298, "喜んでいただけているならば\nわたくしはとてもとても\n幸せでございます", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (15, "ペコリーヌ", 0, 0, 0, 0, 105801, 52, 299, "さぁ、2.5周年を\n楽しみ尽くしますよ☆\nまだまだ食べたいものもありますし\nやりたいこともたくさんです！", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (15, "キャル", 0, 0, 2, 0, 106001, 60, 300, "分かった、分かったから\n引っ張るんじゃないわよ！\n…そんな掴まなくても\nちゃんとついていくから", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (15, "コッコロ", 0, 0, 1, 0, 105901, 44, 301, "ペコリーヌさま…キャルさま…\n主さま…共にまいりましょう\nどこまでも…！", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (15, "ペコリーヌ", 0, 0, 0, 0, 105801, 53, 302, "はい！　それでは2.5周年\n食べつくしツアーへ！\nそして、その先の未来へ\n向かって出発しましょう！", "", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (15, "ペコリーヌ・コッコロ・キャル", 0, 0, 0, 1, 105801, 54, 303, "2.5周年\nありがとうございます！", "105901,45,106001,61", 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "カリン", 0, 1, 1, 0, 190804, 1, 304, "新年を迎え、新たに始まる一日を\nおみくじで占ってみませんか？\n良い運勢であるほど、\n沢山のプレゼントが貰えますよ♪", "", 60002);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 1, 1, 2, 0, 0, 0, 305, "今日の運勢は姫吉！　どんな不可能も可能にできちゃう！　　　　仲間と食卓を囲めばさらに運気上昇よ！　ただし魔物料理以外！", "", 60002);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 1, 1, 2, 0, 0, 0, 306, "今日の運勢は姫吉！　やること全てが最高潮！　やばいですね☆　ラッキーアイテムはおにぎり！　お腹一杯で幸せが続くでしょう！", "", 60002);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 1, 1, 2, 0, 0, 0, 307, "今日の運勢は姫吉！　最強の運勢でどんなドジも跳ね除けます！　大切な人と一緒に、最高の一日になること間違いなし！", "", 60002);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 1, 1, 2, 0, 0, 0, 308, "今日の運勢は姫吉！　どんな挑戦も上手くいく、最高の運勢です！理想の自分に変身できる絶好の機会！　新しい一歩を踏み出そう！", "", 60002);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 1, 2, 2, 0, 0, 0, 309, "今日の運勢は姫吉！　やること全てが上手くいく最高の一日！　　あなたの活躍に先生もにっこりです。いい子いい子♪", "", 60002);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 2, 1, 2, 0, 0, 0, 310, "今日の運勢は大吉！　お裁縫好きな女の子のお墨付きです！　　　お世話したがりの妹にめいっぱい愛されちゃうかも！？", "", 60002);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 2, 1, 2, 0, 0, 0, 311, "今日の運勢は大吉！　ラッキーカラーは赤！　　　　　　　　　　湧き上がるアイデアの数々に、高笑いが止まりませんわ！", "", 60002);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 2, 1, 2, 0, 0, 0, 312, "今日の運勢は大吉！　ラッキーワードは「ちぇる～ん♪」　　　　出会う人みんなに言ってあげると、ハッピーになれますよ！", "", 60002);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 2, 1, 2, 0, 0, 0, 313, "今日の運勢は大吉！　運勢はバッチリで無敵状態！　　　　　　　龍眼で全てを見通して、思い通りの一日を過ごしましょう！", "", 60002);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 2, 2, 2, 0, 0, 0, 314, "今日の運勢は大吉！　悩んでいたことが解決しそうです！　　　　たまには勉強も仕事も忘れて、心あたたまる家族と過ごしましょ！", "", 60002);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 3, 1, 2, 0, 0, 0, 315, "今日の運勢は中吉！　家の中でゆっくり過ごすのがよさそう！　　ラッキーアイテムはゲームよ！　ベストスコアを更新できるかも？", "", 60002);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 3, 1, 2, 0, 0, 0, 316, "今日の運勢は中吉！　上手くなりたいことに積極的に挑戦してみてサックス好きの妹が、手取り足取りサポートしてくれるかも～？", "", 60002);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 3, 1, 2, 0, 0, 0, 317, "今日の運勢は中吉！　仕事運が急上昇中です！　　　　　　　　　きっと「煉獄に舞う一陣の疾風」の如き活躍ができますよ！", "", 60002);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 3, 1, 2, 0, 0, 0, 318, "今日の運勢は中吉！　日頃の努力が報われます！　　　　　　　　親しい友人がいるなら報告してみて。一緒に喜んでくれるはずだ。", "", 60002);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 3, 1, 2, 0, 0, 0, 319, "今日の運勢は中吉！　商売運が上がってきています！　　　　　　ラッキーアイテムはたい焼き！　和菓子パワーで売上アップです！", "", 60002);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 3, 1, 2, 0, 0, 0, 320, "今日の運勢は中吉！　チャンスを待つと良い結果に！　　　　　　ラッキーアイテムのプリンを食べて、運を呼び込みましょう！", "", 60002);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 3, 2, 2, 0, 0, 0, 321, "今日の運勢は中吉！　運気が着々と上昇中！　　　　　　　　　　精霊の加護を受けたかのごとく、色々な事がうまくいきますよ♪", "", 60002);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 4, 1, 2, 0, 0, 0, 322, "今日の運勢は小吉！　まだ見ぬ危険が隠れているかも！　　　　　でも安心しろ！　提督が貴公を守ってくれるはずだぞ！", "", 60002);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 4, 1, 2, 0, 0, 0, 323, "今日の運勢は小吉！　いろんな事件が起こる一日になりそう！　　状況をよく観察してみようじゃないか！　まさに探偵のごとくね！", "", 60002);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 4, 1, 2, 0, 0, 0, 324, "今日の運勢は小吉！　気の向くままに過ごすのが幸せへの近道！　美味い魚を食べて猫を眺めて、自然体で過ごすのはどうだい？", "", 60002);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 4, 1, 2, 0, 0, 0, 325, "今日の運勢は小吉！　運気は高いけども、ハプニングには注意！　出かけた先で、「ふしんしゃ」扱いされてしまうかも……", "", 60002);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 4, 1, 2, 0, 0, 0, 326, "今日の運勢は小吉！　同腹一心の友だちが見つかるかも！　　　　キーワードは「ユニちゃんズ」、記憶に刻みましょう！", "", 60002);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 4, 1, 2, 0, 0, 0, 327, "今日の運勢は小吉！　周りに流されず、マイペースな一日を！　　ただし寝すぎには注意ぐうたらし過ぎず適度な運動を、だってさ～", "", 60002);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 4, 2, 2, 0, 0, 0, 328, "今日の運勢は小吉！　根を詰めずにリラックスすると運気向上！　ラッキーパーソンはアイドル。歌があなたを幸せにしてくれます♪", "", 60002);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "カリン", 0, 0, 3, 0, 190804, 2, 329, "おみくじの結果はどうでしたか？\n新たに始まる一日が、あなたに\nとって良い日になりますように\nまた明日も遊びに来て下さいね♪", "", 60002);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (7, "カリン", 0, 0, 3, 0, 190804, 3, 330, "おみくじは楽しんで頂けましたか？\nあなたの新たなる年が\n素晴らしいものになるよう、\n心からお祈りいたします", "", 60002);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (1, "コッコロ", 0, 0, 4, 0, 105901, 1, 331, "ささ、シェフィさまはこちらへ…", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (1, "シェフィ", 0, 0, 0, 0, 106401, 1, 332, "コッコロさん、どこへ行くの…？\n…え？", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (1, "ペコリーヌ", 0, 0, 3, 0, 105801, 1, 333, "『おめでとうございます！\nわ～パチパチー！』\n…という感じでどうですかね！？", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (1, "コッコロ", 0, 0, 4, 0, 105901, 2, 334, "ではわたくしも\n後ろからクラッカーを鳴らして\n応戦いたします\nああ、当日が待ち遠しいですね", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (1, "シェフィ", 0, 0, 0, 0, 106401, 2, 335, "ええと…\n二人とも何をしているの？", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (1, "キャル", 0, 0, 1, 0, 106001, 1, 336, "シェフィは初めてだもんね\nあの日が近いからって\n予行練習してんのよ\n気が早いわよね～", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (1, "シェフィ", 0, 0, 0, 0, 106401, 3, 337, "あの日…？", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (1, "ペコリーヌ", 0, 0, 3, 0, 105801, 2, 338, "その通りです…\nいよいよ記念すべきあの日が\n近づいてきましたよ！", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (1, "ペコリーヌ", 0, 0, 3, 0, 105801, 3, 339, "『プリンセスコネクト！\nRe:Dive』3.5周年！", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (1, "カリン", 0, 0, 2, 0, 190801, 1, 340, "やばいですね☆", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (1, "キャル", 0, 0, 1, 0, 106001, 2, 341, "って、なにしれっと\n参加してんのよカリン！", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (1, "カリン", 0, 0, 2, 0, 190801, 2, 342, "ふふ、いいじゃないですか\n【ギルド管理協会】として\n私もみなさんと一緒に\nお祝いをさせてください！", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (1, "カリン", 0, 0, 2, 0, 190801, 3, 343, "それに…記念日が近づけば\nとっておきの情報を\nお届けしますから♪", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (1, "ペコリーヌ", 0, 0, 3, 0, 105801, 4, 344, "というわけで\n今日からシェフィちゃんも一緒に\nカウントダウンしちゃいましょう！\n3.5周年まで…", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (1, "シェフィ", 0, 0, 0, 0, 106401, 4, 345, "えっ！？　ええとええと…！\nあ、あと14日…！", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (2, "ペコリーヌ", 0, 0, 3, 0, 105801, 5, 346, "3.5周年まであと13日\n豪華に、そして盛大に\nお祝いしたいですね☆", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (2, "コッコロ", 0, 0, 1, 0, 105901, 3, 347, "豪華といえば前にとある貴族さまが\n舞踏会を開かれたそうで\n主さまがそのお手伝いを\nしておりました", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (2, "コッコロ", 0, 0, 1, 0, 105901, 4, 348, "そこで美しい所作を学ばれたので\nわたくしも教えていただきました\nしゃなり、しゃなり…\nいかがでしょう？", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (2, "ペコリーヌ", 0, 0, 3, 0, 105801, 6, 349, "すっごく上手ですよ☆\nまるでお姫様みたいです\nせっかくなのでわたしも\nしゃなりしゃなり", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (2, "コッコロ", 0, 0, 1, 0, 105901, 5, 350, "さすがでございます\nペコリーヌさま", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (2, "キャル", 0, 0, 0, 0, 106001, 3, 351, "いや、あんたは\nできなきゃダメでしょ\n本物のお姫様なんだし", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (2, "シェフィ", 0, 0, 2, 0, 106401, 5, 352, "しゃなり、しゃなり…\nふふ、案外できるものね", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (2, "ペコリーヌ", 0, 0, 3, 0, 105801, 7, 353, "わぁ！\nシェフィちゃん\n姿勢がとってもキレイです♪", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (2, "コッコロ", 0, 0, 1, 0, 105901, 6, 354, "さ、次はキャルさまの\n番でございますよ", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (2, "キャル", 0, 0, 0, 0, 106001, 4, 355, "あ、あたしはいいわよ！\nそういうの全然分かんないし…", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (2, "シェフィ", 0, 0, 2, 0, 106401, 6, 356, "大丈夫\nやってみると結構楽しいわよ\nほらほらっ", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (2, "キャル", 0, 0, 0, 0, 106001, 5, 357, "そ、そこまで言うなら…\nしゃなにゅわきゃあっ！", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (2, "キャル", 0, 0, 0, 0, 106001, 6, 358, "いたたた…もぉお～！\nなんであたしがこんな目に～！", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (2, "コッコロ", 0, 0, 1, 0, 105901, 7, 359, "まぁまぁ\n記念日までに練習すれば\nできるようになるかもしれません\nよろしければお教えいたしますよ", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (2, "キャル", 0, 0, 0, 0, 106001, 7, 360, "よし！\nあんたたちができて\nあたしだけできないのはしゃくだし\nやってやろうじゃないの！！", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (3, "コッコロ", 0, 0, 1, 0, 105901, 8, 361, "3.5周年まであと12日\n記念日当日はもちろんですが\nその日を待つ時間というのも\n心躍ります", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (3, "ペコリーヌ", 0, 0, 3, 0, 105801, 8, 362, "分かりますっ！\nどんな特別な料理を食べられるのか\n考えるだけでワクワクしますよね！", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (3, "コッコロ", 0, 0, 1, 0, 105901, 9, 363, "特別な料理といえば、お祭りですね\nそういえば主さまは学生として\n聖学祭に参加されたこと\nがあるとか…", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (3, "シェフィ", 0, 0, 2, 0, 106401, 7, 364, "聖学祭…って、なにかしら？", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (3, "コッコロ", 0, 0, 1, 0, 105901, 10, 365, "聖テレサ女学院の祭典ですよ\n二つのチームが競い合い\n青春を満喫するのが趣旨のようです", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (3, "キャル", 0, 0, 0, 0, 106001, 8, 366, "それってカルミナの\nサプライズライブがあった\nお祭りよね？\nちょっと見たかったかも…", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (3, "キャル", 0, 0, 0, 0, 106001, 9, 367, "カルミナの後の\nライブも好評だったらしいじゃない\n確か、なかよし部\nだったっけ…？", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (3, "ペコリーヌ", 0, 0, 3, 0, 105801, 9, 368, "なかよし部…\n仲が良さそうな名前で\nやばいですね☆", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (3, "キャル", 0, 0, 0, 0, 106001, 10, 369, "いや、そのまんますぎでしょ…", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (3, "シェフィ", 0, 0, 2, 0, 106401, 8, 370, "聖学祭かぁ…なんだか楽しそうね\nそれになかよし部の人たちも\n気になるわ", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (3, "ペコリーヌ", 0, 0, 3, 0, 105801, 10, 371, "それじゃあわたしたちも、\n負けじと仲良くしましょう♪\nほらほらシェフィちゃん、\nぎゅーっ☆　ですよ♪", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (3, "シェフィ", 0, 0, 2, 0, 106401, 9, 372, "えっ？　ええ…\nぎゅーっ…？", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (4, "キャル", 0, 0, 0, 0, 106001, 11, 373, "3.5周年まであと11日！？\nお祝いの準備進んでないんだけど…\nああ～昨日に戻りたいわ…", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (4, "シェフィ", 0, 0, 2, 0, 106401, 10, 374, "そういえばそんな魔道具があるって\n聞いたことがあるわね\n確か、名前はタイムラーベ…", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (4, "キャル", 0, 0, 0, 0, 106001, 12, 375, "なにそれヤバいわね\nでも本当にあるんだったら\n準備よりももっとやり直したいこと\nたくさんあるんだけど…", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (4, "コッコロ", 0, 0, 1, 0, 105901, 11, 376, "もしや正月の件でしょうか…？\nあの時はお金がなくて\n苦労しましたから", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (4, "キャル", 0, 0, 0, 0, 106001, 13, 377, "あれはそうね…\nお金を湯水のように使う\nぺコリーヌを止められたら\nあんな苦労をすることもなかったし", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (4, "ペコリーヌ", 0, 0, 3, 0, 105801, 11, 378, "そんなこともありましたね！\nやり直したいといえば\n『きゃるきゃる～ん♪』\nの方かと思いましたよ！", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (4, "キャル", 0, 0, 0, 0, 106001, 14, 379, "バラすんじゃないわよ\nこのアホリーヌ！\nシェフィはそれ\n知らないんだから！", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (4, "キャル", 0, 0, 0, 0, 106001, 15, 380, "こうなったらもう、なんとか\nタイムラーべを探し出して\n今の会話をなかったことに…\nって、どうしたのよシェフィ", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (4, "シェフィ", 0, 0, 2, 0, 106401, 11, 381, "うん…もし、私だったら…\n私が知らないみんなとの時間を\n見てみたいな、って…", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (4, "ペコリーヌ", 0, 0, 3, 0, 105801, 12, 382, "シェフィちゃん！　タイムなんとか\nなんかなくても大丈夫ですよ！\n一緒の時間はこれから\nどんどん作ればいいんですから！", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (4, "コッコロ", 0, 0, 1, 0, 105901, 12, 383, "はい、今この瞬間も\n未来にはかけがえのない思い出に\nなっていることでしょう", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (4, "キャル", 0, 0, 0, 0, 106001, 16, 384, "だからそんな顔しないの\nほら、お祝いの準備、進めるわよ！", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (4, "シェフィ", 0, 0, 2, 0, 106401, 12, 385, "みんな…そうね\n最高のお祝いにして\n最高の思い出にしましょう！", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (5, "ペコリーヌ", 0, 0, 3, 0, 105801, 13, 386, "3.5周年まで、あと10日！\nもうすぐですよー！\n楽しみすぎて、やばいですね！", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (5, "ペコリーヌ", 0, 0, 3, 0, 105801, 14, 387, "そういえば…たくさんのおもちゃが\n子どもたちを襲うっていう\n騒動もありましたね…\nなんでも勝手に動き出したとか！", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (5, "ペコリーヌ", 0, 0, 3, 0, 105801, 15, 388, "学校や孤児院とかに\nちっちゃなおもちゃが乗り込んで\n暴れたみたいですよ！", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (5, "キャル", 0, 0, 0, 0, 106001, 17, 389, "暴れたって言っても\n所詮はおもちゃじゃない\nどうせちっさいんでしょ？", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (5, "キャル", 0, 0, 0, 0, 106001, 18, 390, "だったら魔法で\n全部ぶっ飛ばせばいいじゃない！\n大したことないわよ！", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (5, "シェフィ", 0, 0, 2, 0, 106401, 13, 391, "キャルってば…\n相手は子どもが\n大事にしてる物なのよ…？", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (5, "コッコロ", 0, 0, 1, 0, 105901, 13, 392, "乱暴な発言は\nめっ、でございますよ？\n子どもの教育に悪影響を\n与えてしまいますから…", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (5, "ペコリーヌ", 0, 0, 3, 0, 105801, 16, 393, "キャルちゃんだって\n意外と猫グッズとか\nたっくさん集めてますよね？\nもしその子たちが暴れたら…？", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (5, "キャル", 0, 0, 0, 0, 106001, 19, 394, "う、うっさいわね！\n意外とか言うな！\n…ま、まあ確かに正面切って\n魔法でぶっ飛ばせないかもね…", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (5, "ペコリーヌ", 0, 0, 3, 0, 105801, 17, 395, "あはは☆\nキャルちゃんのそういうところ\n好きですよ♪", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (5, "ペコリーヌ", 0, 0, 3, 0, 105801, 18, 396, "そんなキャルちゃんに猫グッズを\nプレゼントしちゃいますね！\n一部で流行っていた、たい焼き屋の\nおまけフィギュアですよ♪", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (5, "ペコリーヌ", 0, 0, 3, 0, 105801, 19, 397, "乱暴に扱ったりしないでくださいね\nひとりでに暴れ出しちゃうかも\nしれませんから！", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (6, "カリン", 0, 0, 2, 0, 190801, 4, 398, "3.5周年まであと９日\nここでみなさんに\nビックニュースですよ！", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (6, "カリン", 0, 0, 2, 0, 190801, 5, 399, "3.5周年を祝う生放送に\n去年と同様【美食殿】の\nみなさんにご出演いただくことが\n決定しましたー！", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (6, "キャル", 0, 0, 1, 0, 106001, 20, 400, "…えっ！？", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (6, "ペコリーヌ", 0, 0, 3, 0, 105801, 20, 401, "本当ですか！？\nそれじゃ今から張り切って\n生放送中に食べる魔物料理を\n作らなくちゃですね☆", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (6, "シェフィ", 0, 0, 0, 0, 106401, 14, 402, "いや…がんばるところは\nそこじゃないような…\nというか去年も出演してたのね\nすごいじゃない！", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (6, "コッコロ", 0, 0, 4, 0, 105901, 14, 403, "はい。とても緊張しますが\nやりがいのある大役です\n一緒にがんばりましょうね\nシェフィさま", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (6, "シェフィ", 0, 0, 0, 0, 106401, 15, 404, "わ、私も出るの！？　でも…\n私も【美食殿】の一人だものね\nそれなら今からしっかり準備をして\nコンディションを整えないと…", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (6, "キャル", 0, 0, 1, 0, 106001, 21, 405, "ふふん♪\n生放送直前のカチコチになった\nシェフィの姿が楽しみだわ！", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (6, "ペコリーヌ", 0, 0, 3, 0, 105801, 21, 406, "去年のキャルちゃんは\n緊張しすぎてずっと震えて\nかわいかったですもんね！\n今年もあの姿が見たいです！", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (6, "キャル", 0, 0, 1, 0, 106001, 22, 407, "ぶっ殺すぞ！？", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (6, "カリン", 0, 0, 2, 0, 190801, 6, 408, "というわけで\n『プリンセスコネクト！\nRe:Dive』の魅力を\nお伝えする生放送をお楽しみに♪", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (7, "コッコロ", 0, 0, 1, 0, 105901, 15, 409, "3.5周年まであと８日\nでございますよ\n楽しみですね、主さま", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (7, "キャル", 0, 0, 0, 0, 106001, 23, 410, "そういえばあんた\n夏休みの宿題は\nきちんとやってるの？", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (7, "シェフィ", 0, 0, 2, 0, 106401, 16, 411, "宿題…学校に通うのも\n楽しいことばかりじゃ\nないのね", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (7, "ペコリーヌ", 0, 0, 3, 0, 105801, 22, 412, "あれ？　そういえば\n少し前にルーセント学院で\n何かあったような…", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (7, "キャル", 0, 0, 0, 0, 106001, 24, 413, "ああ、始業式の日に\n大捕り物があったって\n話題になってたわね", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (7, "シェフィ", 0, 0, 2, 0, 106401, 17, 414, "夏休みを終わらせないって\n騒いでたっていう話？\nあれ本当なの？\n都市伝説だと思ってたわ", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (7, "コッコロ", 0, 0, 1, 0, 105901, 16, 415, "その一件ならよく存じ上げて\nおります。ですから主さま\n次の機会にはわたくしと一緒に\nがんばりましょう", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (7, "シェフィ", 0, 0, 2, 0, 106401, 18, 416, "楽しそう…私も一緒にやりたいな\nママ…じゃ、じゃなくて！\nコッコロさん、私もまざっていい？", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (7, "コッコロ", 0, 0, 1, 0, 105901, 17, 417, "もちろんでございます\n仲良くお勉強いたしましょう", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (7, "キャル", 0, 0, 0, 0, 106001, 25, 418, "しれっと教育ママが\n生まれてる…？", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (7, "ペコリーヌ", 0, 0, 3, 0, 105801, 23, 419, "お勉強は大切ですよ！\nキャルちゃんも一緒に\nがんばりましょう！", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (7, "キャル", 0, 0, 0, 0, 106001, 26, 420, "え～～～～！？", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (8, "キャル", 0, 0, 0, 0, 106001, 27, 421, "3.5周年まであと７日〜♪\nふふ～ん♪\nふんふふ～ん♪", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (8, "シェフィ", 0, 0, 2, 0, 106401, 19, 422, "…\nねぇコッコロさん\nキャル、どうしちゃったの？", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (8, "コッコロ", 0, 0, 1, 0, 105901, 18, 423, "とてもご機嫌そうに\n鼻歌を歌ってらっしゃいますね\n耳も尻尾もリズミカルに\n揺れて…ノリノリでございます", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (8, "ペコリーヌ", 0, 0, 3, 0, 105801, 24, 424, "あれはですね！\nカルミナのみなさんの\nサマーライブのチケットが\n手に入ったからなんですよ♪", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (8, "シェフィ", 0, 0, 2, 0, 106401, 20, 425, "ええっ！？\nあのライブのチケットって確か\n入手困難だったんじゃ…", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (8, "キャル", 0, 0, 0, 0, 106001, 28, 426, "ふふふふ～ん♪\nしゃいにんぐふゅ～ちゃ～♪", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (8, "コッコロ", 0, 0, 1, 0, 105901, 19, 427, "なるほど…\nキャルさまがあのように\nなってしまうのも頷けますね", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (8, "シェフィ", 0, 0, 2, 0, 106401, 21, 428, "…カルミナの\n『Shining Future』\nの中に、シャイニングフューチャー\nって歌詞はなかったはずだけど…", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (8, "キャル", 0, 0, 0, 0, 106001, 29, 429, "ふんふ…はっ！？\nあっ、あんたたち！\nなに人の鼻歌を\n勝手に聞いてるのよ！？", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (8, "キャル", 0, 0, 0, 0, 106001, 30, 430, "こっ、これは別に\nカルミナの曲じゃなくて…\nあたしのオリジナルソングよ！\n聞いたらお金取るわよっ！？", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (8, "ペコリーヌ", 0, 0, 3, 0, 105801, 25, 431, "えへへ、キャルちゃん\nカルミナのサマーライブ\n思いっきり楽しみましょうね☆", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (8, "キャル", 0, 0, 0, 0, 106001, 31, 432, "も～っ！\n生暖かい目で見るな～～！\nぶっ殺すわよ～～～！", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (9, "シェフィ", 0, 0, 0, 0, 106401, 22, 433, "3.5周年まであと６日\n３人も言ってたけど\nあっという間だったわね", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (9, "シェフィ", 0, 0, 0, 0, 106401, 23, 434, "…えっ？\n生放送の意気込み！？\n突然なに！？", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (9, "シェフィ", 0, 0, 0, 0, 106401, 24, 435, "ええと…\nとにかく失敗しないように\n台本たくさん読んで…\nそれから…", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (9, "シェフィ", 0, 0, 0, 0, 106401, 25, 436, "えっ！？\nトラブルがあったら\nどうするのかって！？\nそ、それは…！", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (9, "シェフィ", 0, 0, 0, 0, 106401, 26, 437, "とにかく落ち着いて\nスタッフさんの指示を聞いて…", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (9, "シェフィ", 0, 0, 0, 0, 106401, 27, 438, "それから、それから…！\nぅ、ぅぅっ…！", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (9, "シェフィ", 0, 0, 0, 0, 106401, 28, 439, "―びえぇぇ～んっ！！！", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (9, "コッコロ", 0, 0, 1, 0, 105901, 20, 440, "どうされましたか、シェフィさま", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (9, "シェフィ", 0, 0, 0, 0, 106401, 29, 441, "ううっ…ひっく…\nママッ…！", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (9, "コッコロ", 0, 0, 1, 0, 105901, 21, 442, "よしよし、緊張されてるのですね\nママが傍におりますよ\n主さま、少々お待ちくださいませ", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (9, "コッコロ", 0, 0, 1, 0, 105901, 22, 443, "…ふぅ\nシェフィさま\n生放送への意気込み\nお話しできそうですか？", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (9, "シェフィ", 0, 0, 0, 0, 106401, 30, 444, "…ううっ\n恥ずかしすぎて\nもう生放送出られない…", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (10, "コッコロ", 0, 0, 0, 0, 105901, 23, 445, "主さま\n3.5周年まであと５日\nでございます", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (10, "コッコロ", 0, 0, 0, 0, 105901, 24, 446, "ですがその前に\n生放送がありますね", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (10, "コッコロ", 0, 0, 0, 0, 105901, 25, 447, "…生放送に向けた\n意気込みでございますか？", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (10, "コッコロ", 0, 0, 0, 0, 105901, 26, 448, "もちろんわたくしは\n主さまのために\n全身全霊を込めて\n楽しい時間をご提供いたします", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (10, "コッコロ", 0, 0, 0, 0, 105901, 27, 449, "…と、意気込んでみましたが\nうまくできるか少し不安です\n声が小さくなったり\n間違えてしまったらどうしようと…", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (10, "コッコロ", 0, 0, 0, 0, 105901, 28, 450, "ですがわたくしは\n一人ではありません\n【美食殿】のみなさまが\nおります", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (10, "コッコロ", 0, 0, 0, 0, 105901, 29, 451, "どんな困難も\nみなさまと一緒なら\n乗り越えられると\nわたくし、確信しております", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (10, "コッコロ", 0, 0, 0, 0, 105901, 30, 452, "それに今回はシェフィさまも\nご参加されるわけですし\nママとしてお手本に\nならねばなりませんね", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (10, "コッコロ", 0, 0, 0, 0, 105901, 31, 453, "主さま。3.5周年も\n食卓を囲んでいる時のような\n楽しい思い出を\n一緒に作りましょうね", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (11, "ペコリーヌ", 0, 0, 0, 0, 105801, 26, 454, "3.5周年まであと４日！\nやばいですね☆", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (11, "キャル", 0, 0, 1, 0, 106001, 32, 455, "はいはい、やばいわね\nというかもうすぐ生放送だけど\nあんた準備はできてるの？", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (11, "ペコリーヌ", 0, 0, 0, 0, 105801, 27, 456, "ばっちりオイッスー☆　ですよ！\n『王家の装備』も全開です！", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (11, "ペコリーヌ", 0, 0, 0, 0, 105801, 28, 457, "あとは前回を超える\n最高のオイッスー☆　を\nお届けできるように\n練習あるのみですかね！", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (11, "ペコリーヌ", 0, 0, 0, 0, 105801, 29, 458, "キャルちゃんは\nどうなんですか？\nオイッスー☆　を\n毎日ちゃんと練習してますか？", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (11, "キャル", 0, 0, 1, 0, 106001, 33, 459, "はぁ！？　なんであたしまで\nその変なあいさつを練習しないと\nいけないのよ！", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (11, "ペコリーヌ", 0, 0, 0, 0, 105801, 30, 460, "もしかしてやってないんですか？\nダメですよキャルちゃん！\nシェフィちゃんだってちゃんと\n毎日練習してるのに！", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (11, "ペコリーヌ", 0, 0, 0, 0, 105801, 31, 461, "こうなったらわたしがキャルちゃん\nにオイッスー☆　の『オ』から\nしっかり教えてあげないと！", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (11, "ペコリーヌ", 0, 0, 0, 0, 105801, 32, 462, "オイッスー☆　の『オ』は\n口を大きく開いて〜\nドラゴンの骨つき肉に\nかぶりつく直前をイメージして〜", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (11, "キャル", 0, 0, 1, 0, 106001, 34, 463, "だからあたしはやるなんて一言も…", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (11, "ペコリーヌ", 0, 0, 0, 0, 105801, 33, 464, "オイッスー☆", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (11, "キャル", 0, 0, 1, 0, 106001, 35, 465, "聞きなさいっての！！", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (12, "シェフィ", 0, 0, 2, 0, 106401, 31, 466, "3.5周年まであと３日…\nそして生放送は、明日…", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (12, "シェフィ", 0, 0, 2, 0, 106401, 32, 467, "もっと台本を\n読み込んでおかないと…\nあと発声練習も…", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (12, "シェフィ", 0, 0, 2, 0, 106401, 33, 468, "おいっす！\n…こうじゃなかったわね\nええと確か…", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (12, "シェフィ", 0, 0, 2, 0, 106401, 34, 469, "うう…あんなに練習したのに\nこれじゃみんなに迷惑かけちゃう…", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (12, "シェフィ", 0, 0, 2, 0, 106401, 35, 470, "【美食殿】の一員として\n認められるように\n頑張らないといけないのに\nよし、もう一度…", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (12, "ペコリーヌ", 0, 0, 3, 0, 105801, 34, 471, "やってますねシェフィちゃん！", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (12, "キャル", 0, 0, 0, 0, 106001, 36, 472, "なによ、考えることは\nみんな同じってこと？", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (12, "コッコロ", 0, 0, 1, 0, 105901, 32, 473, "ふふ。シェフィさま\nよろしければ\n仲間に入れてください\n一緒にやればきっとうまくできます", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (12, "シェフィ", 0, 0, 2, 0, 106401, 36, 474, "みんな…もちろんよ！", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (12, "ペコリーヌ", 0, 0, 3, 0, 105801, 35, 475, "それじゃ明日の生放送に向けて\n一緒に練習がんばりましょう☆", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (13, "カリン", 0, 0, 2, 0, 190801, 7, 476, "3.5周年まであと２日と\n迫った本日は…\nいよいよ生放送です！", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (13, "カリン", 0, 0, 2, 0, 190801, 8, 477, "【美食殿】のみなさんが\n準備に入られていますので\nそれぞれ繋いでみましょう！\nまずはペコリーヌさんです！", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (13, "ペコリーヌ", 0, 0, 3, 0, 105801, 36, 478, "今日もオイッスー☆\nみんなで一緒に\nオイッスー☆", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (13, "カリン", 0, 0, 2, 0, 190801, 9, 479, "お手本になるような\n発声練習ですね！\nそれでは続いてコッコロさん", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (13, "コッコロ", 0, 0, 4, 0, 105901, 33, 480, "はじめちょろちょろ\nなかぱっぱ～♪\n生放送前にみなさまで\n食卓を囲んで英気を養いましょう", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (13, "カリン", 0, 0, 2, 0, 190801, 10, 481, "さすが【美食殿】のママですね！\nそしてキャルさんは…", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (13, "キャル", 0, 0, 1, 0, 106001, 37, 482, "大丈夫…大丈夫…\nあたしは大丈夫…", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (13, "カリン", 0, 0, 2, 0, 190801, 11, 483, "す、すごく震えてますけど\n大丈夫でしょうか…？\nそして今回初参加となる\n期待の新人シェフィさん！", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (13, "シェフィ", 0, 0, 0, 0, 106401, 37, 484, "あうー？", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (13, "みんな", 0, 0, 4, 1, 105801, 37, 485, "あ…", "105901,34,106001,38,190801,12", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (13, "キャル", 0, 0, 1, 0, 106001, 39, 486, "ちょ、ちょっとシェフィ！？\nなに赤ちゃん返りしてん―\nあ痛ぁっ！\n噛んだわこいつーっ！", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (13, "コッコロ", 0, 0, 4, 0, 105901, 35, 487, "シェフィさま\n緊張しすぎて\nしまったのでしょうね…\nおーよしよし…", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (13, "ペコリーヌ", 0, 0, 3, 0, 105801, 38, 488, "シェフィちゃん！\nそういう時は手のひらに\n好きな文字を書いて\n食べちゃうといいですよ！", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (13, "カリン", 0, 0, 2, 0, 190801, 13, 489, "…ええと、現場からは以上です！\n生放送はもうすぐ！\n特別なお知らせもありますので\nどうぞお楽しみに！", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (14, "シェフィ", 0, 0, 2, 0, 106401, 38, 490, "3.5周年の記念日も\nいよいよ明日ね\n…って、あれ？", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (14, "ペコリーヌ", 0, 0, 3, 0, 105801, 39, 491, "記念日はこの森へ行きませんか！？\nすっごく美味しいお肉が\nとれるんです☆", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (14, "キャル", 0, 0, 0, 0, 106001, 40, 492, "うげ、またあの魔物？\nさすがに今度は\n丸焼きとかはやめてよね…\n見た目がヤバすぎるし", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (14, "コッコロ", 0, 0, 1, 0, 105901, 36, 493, "ふふ、キャルさまもだいぶ\n魔物肉に慣れてきたようですね\n調理ならばわたくしに\nお任せください♪", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (14, "シェフィ", 0, 0, 2, 0, 106401, 39, 494, "みんな、何をしているの？\nなんだか、\n不穏な単語が聞こえたんだけど…", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (14, "ペコリーヌ", 0, 0, 3, 0, 105801, 40, 495, "シェフィちゃん！\n明日の食べ尽くしツアーの\n相談をしてたんですよ！", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (14, "コッコロ", 0, 0, 1, 0, 105901, 37, 496, "はい。明日は【美食殿】の\n一員となったシェフィさまと一緒に\n初めて記念日を過ごすのですから\n特別なコース料理を用意しませんと", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (14, "キャル", 0, 0, 0, 0, 106001, 41, 497, "ふふん♪\n猫の毛も逆立つような\nあれこれを食べさせられるから\n覚悟しておいたほうがいいわよ？", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (14, "シェフィ", 0, 0, 2, 0, 106401, 40, 498, "へ、へぇ…", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (14, "ペコリーヌ", 0, 0, 3, 0, 105801, 41, 499, "期待しててくださいね！\nシェフィちゃんとあなたに\n『おいちー！』って喜んでもらえる\n最高のごちそうを準備しますから☆", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (14, "シェフィ", 0, 0, 2, 0, 106401, 41, 500, "ええと…その…うーんと…\nわ、分かったわ\n私も【美食殿】の一員だもの", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (14, "シェフィ", 0, 0, 2, 0, 106401, 42, 501, "それにあなたも一緒みたいだし\n最後までがんばってみるわ…！", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (15, "ペコリーヌ", 0, 0, 3, 0, 105801, 42, 502, "それではいつものあれを\nやりましょう！\n準備はいいですかっ？\n…いっきますよー！", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (15, "キャル", 0, 0, 1, 0, 106001, 42, 503, "祝！", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (15, "コッコロ", 0, 0, 4, 0, 105901, 38, 504, "3.5周年！", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (15, "シェフィ", 0, 0, 0, 0, 106401, 43, 505, "おめでとうございます！", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (15, "カリン", 0, 0, 2, 0, 190801, 14, 506, "この日を迎えられたのも\nあなたが『プリンセスコネクト！\nRe:Dive』を遊んでくれている\nおかげです！", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (15, "カリン", 0, 0, 2, 0, 190801, 15, 507, "ということで\nみなさんからあなたへ一言ずつ\n感謝の言葉をどうぞ！", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (15, "ペコリーヌ", 0, 0, 3, 0, 105801, 43, 508, "感謝の言葉ですか？　わたしは\n毎日あなたに感謝してますよ\n【美食殿】の仲間になってくれて…\n一緒にごはんを食べてくれて…", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (15, "ペコリーヌ", 0, 0, 3, 0, 105801, 44, 509, "なにより今でもわたしをぺコリーヌ\nって呼んでくれてありがとうござい\nます。これからもわたしはあなたの\nぺコリーヌです！　やばいですね☆", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (15, "キャル", 0, 0, 1, 0, 106001, 43, 510, "えっ！？　今度はあたし！？\n感謝って…まぁ、そうね…\nあんたにはあたしが\n辛くて苦しい時に助けてもらって…", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (15, "キャル", 0, 0, 1, 0, 106001, 44, 511, "でもおかげで色々吹っ切れたわ！\nもしあんたが困ってたら今度は\nあたしが助けてあげるから\nその時はちゃんと言いなさいよ！", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (15, "シェフィ", 0, 0, 0, 0, 106401, 44, 512, "ええと…次は私ね\n実はよく覚えてないのだけど\n空から落ちてきた私を受け止めて\nくれたのはあなたなのでしょう？", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (15, "シェフィ", 0, 0, 0, 0, 106401, 45, 513, "最初に私を見つけてくれたのが\nあなたで良かったって思ってるの\nおかげで私は居場所を見つけたから\nあなたがくれたもの、大切にするわ", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (15, "コッコロ", 0, 0, 4, 0, 105901, 39, 514, "では最後にわたくしから…\nわたくしは主さまの元を離れた日…\nこのように記念日をお祝いする\nことは二度とないと思っていました", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (15, "コッコロ", 0, 0, 4, 0, 105901, 40, 515, "ですが、主さまはわたくしを\n迎えにきてくださり、今もこうして\nおそばにおいてくださっています\nわたくしにとって毎日が記念日です", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (15, "コッコロ", 0, 0, 4, 0, 105901, 41, 516, "わたくしたちの物語は\nまだ続いていきます\nこれからも一緒にページを\n作っていきましょうね", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (15, "ペコリーヌ", 0, 0, 3, 0, 105801, 45, 517, "それじゃ\nいつものあれでしめますよ！\nみんなで一緒に、さんはい☆", "", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (15, "みんな", 0, 0, 4, 1, 105801, 46, 518, "これからも\nよろしくおねがいします！", "105901,42,106001,45,106401,46,190801,16", 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "カリン", 0, 1, 1, 0, 190804, 1, 519, "新年を迎え、新たに始まる一日を\nおみくじで占ってみませんか？\n良い運勢であるほど、\n沢山のプレゼントが貰えますよ♪", "", 60003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 1, 1, 2, 0, 0, 0, 520, "今日の運勢は姫吉！　どんな不可能も可能にできちゃう！　　　　仲間と食卓を囲めばさらに運気上昇よ！　ただし魔物料理以外！", "", 60003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 1, 1, 2, 0, 0, 0, 521, "今日の運勢は姫吉！　やること全てが最高潮！　やばいですね☆　ラッキーアイテムはおにぎり！　お腹一杯で幸せが続くでしょう！", "", 60003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 1, 1, 2, 0, 0, 0, 522, "今日の運勢は姫吉！　羽が生えたように飛躍できる絶好調の運気！新しいことに挑戦するなら今しかないわ！　やってみましょう！", "", 60003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 1, 1, 2, 0, 0, 0, 523, "今日の運勢は姫吉！　精霊も新年の幸福をお祝いしています！　　主さまの笑顔が見られて、わたくしも幸せいっぱいでございます♪", "", 60003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 1, 2, 2, 0, 0, 0, 524, "今日の運勢は姫吉！　やること全てが上手くいく最高の一日！　　あなたの活躍に先生もにっこりです。いい子いい子♪", "", 60003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 2, 1, 2, 0, 0, 0, 525, "今日の運勢は大吉！　お裁縫好きな女の子のお墨付きです！　　　お世話したがりの妹にめいっぱい愛されちゃうかも！？", "", 60003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 2, 1, 2, 0, 0, 0, 526, "今日の運勢は大吉！　ラッキーカラーは赤！　　　　　　　　　　湧き上がるアイデアの数々に、高笑いが止まりませんわ！", "", 60003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 2, 1, 2, 0, 0, 0, 527, "今日の運勢は大吉！　ラッキーカラーは鮮やかな黄色！　　　　　さらにシマシマのアイテムを持つと、運気倍増らしいッスよ♪", "", 60003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 2, 1, 2, 0, 0, 0, 528, "今日の運勢は大吉！　秘められた力が開花するでしょう！　　　　じゃあ、私の龍眼と力比べしてみようか？　ふふ、冗談だよ～", "", 60003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 2, 2, 2, 0, 0, 0, 529, "今日の運勢は大吉！　悩んでいたことが解決しそうです！　　　　たまには勉強も仕事も忘れて、心あたたまる家族と過ごしましょ！", "", 60003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 3, 1, 2, 0, 0, 0, 530, "今日の運勢は中吉！　家の中でゆっくり過ごすのがよさそう！　　ラッキーアイテムはゲームよ！　ベストスコアを更新できるかも？", "", 60003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 3, 1, 2, 0, 0, 0, 531, "今日の運勢は中吉！　上手くなりたいことに積極的に挑戦してみてサックス好きの妹が、手取り足取りサポートしてくれるかも～？", "", 60003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 3, 1, 2, 0, 0, 0, 532, "今日の運勢は中吉！　仕事運が急上昇中です！　　　　　　　　　きっと「煉獄に舞う一陣の疾風」の如き活躍ができますよ！", "", 60003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 3, 1, 2, 0, 0, 0, 533, "今日の運勢は中吉！　日頃の努力が報われます！　　　　　　　　親しい友人がいるなら報告してみて。一緒に喜んでくれるはずだ。", "", 60003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 3, 1, 2, 0, 0, 0, 534, "今日の運勢は中吉！　ラッキーアイテムはかわいいもの！　　　　ア、アタシはかわいいじゃなくてキレイだってば！　も～っ！", "", 60003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 3, 1, 2, 0, 0, 0, 535, "今日の運勢は中吉！　チャンスを待つと良い結果に！　　　　　　ラッキーアイテムのプリンを食べて、運を呼び込みましょう！", "", 60003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 3, 2, 2, 0, 0, 0, 536, "今日の運勢は中吉！　悩まず行動すれば強運に恵まれる！　　　　守るよりも攻めるが吉だ！　勝負事をするにはいい運勢だな！", "", 60003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 4, 1, 2, 0, 0, 0, 537, "今日の運勢は小吉！　まだ見ぬ危険が隠れているかも！　　　　　でも安心しろ！　提督が貴公を守ってくれるはずだぞ！", "", 60003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 4, 1, 2, 0, 0, 0, 538, "今日の運勢は小吉！　いろんな事件が起こる一日になりそう！　　状況をよく観察してみようじゃないか！　まさに探偵のごとくね！", "", 60003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 4, 1, 2, 0, 0, 0, 539, "今日の運勢は小吉！　気の向くままに過ごすのが幸せへの近道！　美味い魚を食べて猫を眺めて、自然体で過ごすのはどうだい？", "", 60003);
