INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (7, "カリン", 0, 0, 3, 0, 190804, 3, 599, "おみくじは楽しんで頂けましたか？\nあなたの新たなる年が\n素晴らしいものになるよう、\n心からお祈りいたします", "", 60005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "カリン", 0, 1, 1, 0, 190804, 1, 600, "新年を迎え、新たに始まる一日を\nおみくじで占ってみませんか？\n良い運勢であるほど、\n沢山のプレゼントが貰えますよ♪", "", 60006);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 1, 2, 2, 0, 0, 0, 601, "今日の運勢は姫吉！　勇気を持って、新しいことに挑戦しよう！\n幸先がいいねえ少年！　今年もいい一年になるよう祈ってるよ。", "", 60006);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 1, 2, 2, 0, 0, 0, 602, "今日の運勢は姫吉！　どんなピンチも簡単に乗り越えられそう！\nよい結果が出たようで何よりです。さて、待人の方は……ふふ。", "", 60006);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 1, 2, 2, 0, 0, 0, 603, "今日の運勢は姫吉！　あの人と仲直り……できちゃうかも！？\nはあ……この私があなたの運勢に興味を抱くわけないでしょう？", "", 60006);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 2, 1, 2, 0, 0, 0, 604, "今日の運勢は大吉！　身近な友達との絆が深まる予感……？\n騎士様は、どなたと友誼を結ぶのでしょう？　気になります……", "", 60006);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 2, 2, 2, 0, 0, 0, 605, "今日の運勢は大吉！　ラッキーアイテムは「蛇の抜け殻」！\nこのアタシにお目にかかれるなんて……アンタ、幸せものね♪", "", 60006);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 3, 2, 2, 0, 0, 0, 606, "今日の運勢は中吉！　気になるあの人に会えちゃうかも！？\n『初夢を正夢にする枕』であなたに会え……な、なんでもない！", "", 60006);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 3, 2, 2, 0, 0, 0, 607, "今日の運勢は中吉！　子供に戻って、一日中遊びつくそう！\nお年玉でけん玉ゲット！　おじちゃん、もしかめ一万回やって！", "", 60006);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 3, 1, 2, 0, 0, 0, 608, "今日の運勢は中吉！　今日はおうちで、ゆーっくりくつろごう。\nお雑煮よし。みかんよし。さ、こたつで寝正月だ。君も入る？", "", 60006);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 4, 2, 2, 0, 0, 0, 609, "今日の運勢は小吉！　突然のドッキリに注意！\nおや、後ろに髪の長い女の霊が──フフ、冗談です。私ですよ。", "", 60006);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 4, 2, 2, 0, 0, 0, 610, "今日の運勢は小吉。慌てなくても、幸運はきっとやってくる。\n運は自分で掴むもの──媛が言ってました。でも、どうやって？", "", 60006);
