INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "カリン", 0, 1, 60000, 1, 1, 0, "新年を迎え、新たに始まる一日を\nおみくじで占ってみませんか？\n良い運勢であるほど、\n沢山のプレゼントが貰えますよ♪", 0, 190804, 1);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "", 0, 2, 60000, 1, 0, 0, "今日の運勢は姫吉！　あらゆる運気が最大を示しています！　　　今なら「伝説の吸血鬼」をも従え、世界征服も夢じゃありません！", 1, 0, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "", 0, 3, 60000, 1, 0, 0, "今日の運勢は姫吉！　やること全てが最高潮！　やばいですね☆　ラッキーアイテムはおにぎり！　お腹一杯で幸せが続くでしょう！", 1, 0, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "", 0, 4, 60000, 2, 0, 0, "今日の運勢は姫吉！　その高い運気はまさにショーグンの如く！　その溢れる威光は人々を惹きつけ、皆の人気者間違いなしです！", 1, 0, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "", 0, 5, 60000, 1, 0, 0, "今日の運勢は大吉！　運気がどんどん高まっています！　　　　　何をやっても、最大効率で素晴らしい結果になりますよ♪", 2, 0, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "", 0, 6, 60000, 1, 0, 0, "今日の運勢は大吉！　なにをやっても上手くいって大活躍！　　　そんな素敵なあなたを見て、後ろから誰かが……クスクス♪", 2, 0, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "", 0, 7, 60000, 2, 0, 0, "今日の運勢は大吉！　運気が高く、何かに挑戦すると良い結果に！隠れた才能が引き出されそう！　目指せヒデサ……ではなく秀才！", 2, 0, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "", 0, 8, 60000, 1, 0, 0, "今日の運勢は中吉！　商売運が上がってきています！　　　　　　ラッキーアイテムはたいやき！　和菓子パワーで売上アップです！", 3, 0, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "", 0, 9, 60000, 1, 0, 0, "今日の運勢は中吉！　旅行運がぐんぐんと上昇中♪　　　　　　　もしかしたら、あの「マホマホ王国」にだって行けるかも♪", 3, 0, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "", 0, 10, 60000, 1, 0, 0, "今日の運勢は中吉！　仕事運が急上昇中です！　　　　　　　　　きっと「煉獄に舞う一陣の疾風」の如き活躍ができますよ！", 3, 0, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "", 0, 11, 60000, 1, 0, 0, "今日の運勢は中吉！　チャンスを待つと良い結果に！　　　　　　ラッキーアイテムのプリンを食べて、運を呼び込みましょう！", 3, 0, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "", 0, 12, 60000, 2, 0, 0, "今日の運勢は中吉！　運気が着々と上昇中！　　　　　　　　　　精霊の加護を受けたかの如く、色々な事がうまくいきますよ♪", 3, 0, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "", 0, 13, 60000, 1, 0, 0, "今日の運勢は小吉！　早朝に幸運の兆しあり！　　　　　　　　　もしかしたら、優しいお姉ちゃんが起こしにきてくれますよ♪", 4, 0, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "", 0, 14, 60000, 1, 0, 0, "今日の運勢は小吉！　運気上昇中ですが、上がるまでが要注意！　ラッキーアニマルは熊！　本当の家族のように守ってくれるかも♪", 4, 0, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "", 0, 15, 60000, 1, 0, 0, "今日の運勢は小吉！　外に出てゆっくり過ごすと運気が上昇！　　ラッキースポットは牧場。面白い出会いがありそうです♪", 4, 0, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "", 0, 16, 60000, 1, 0, 0, "今日の運勢は小吉！　運気は高いけども、ハプニングには注意！　出かけた先で、「ふしんしゃ」扱いされてしまうかも……", 4, 0, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "", 0, 17, 60000, 2, 0, 0, "今日の運勢は小吉！　根を詰めずにリラックスすると運気向上！　ラッキーパーソンはアイドル。歌があなたを幸せにしてくれます♪", 4, 0, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "カリン", 0, 18, 60000, 0, 2, 0, "おみくじの結果はどうでしたか？\n新たに始まる一日が、あなたに\nとって良い日になりますように\nまた明日も遊びに来て下さいね♪", 0, 190804, 3);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "カリン", 0, 19, 60000, 0, 3, 7, "おみくじは楽しんで頂けましたか？\nあなたの新たなる年が\n素晴らしいものになるよう、\n心からお祈りいたします", 0, 190804, 3);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "ハツネ", 0, 20, 80001, 0, 1, 1, "キミと出会ってもう\n1年もたつんだね！\nう～ん、いつも\n寝てばっかりだから、", 0, 101201, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "ハツネ", 0, 21, 80001, 0, 2, 1, "少しでも\n寝る時間を減らして\nキミと一緒にいる\n時間増やしたいなぁ♪", 0, 101201, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "シオリ", 0, 22, 80001, 0, 1, 1, "あなたと知り合ってから\nもう1年…\n時がたつのは\n早いですね", 0, 103801, 1);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "シオリ", 0, 23, 80001, 0, 2, 1, "来年も、\n一緒にいられたら…\nうれしいな", 0, 103801, 1);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "キョウカ", 0, 24, 80001, 0, 1, 2, "あなたと一緒にいるようになって、\nもう1年ですか…", 0, 103601, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "キョウカ", 0, 25, 80001, 0, 2, 2, "最初の頃よりも、頼もしく\nなりましたね。私はどうですか？\n成長していますか？", 0, 103601, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "ミミ", 0, 26, 80001, 0, 1, 2, "おにいちゃんとあってから、\n1年たったよ～！", 0, 102001, 1);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "ミミ", 0, 27, 80001, 0, 2, 2, "ミミね、おにいちゃんと\nいっぱい遊べてたのしかったな～♪\nこれからもずっと\nいっしょにいてね～！", 0, 102001, 1);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "ミソギ", 0, 28, 80001, 0, 1, 2, "にいちゃんがミソギのイタズラに\n引っかかり続けてもう1年かぁ", 0, 100401, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "ミソギ", 0, 29, 80001, 0, 2, 2, "…これからもいっぱいミソギと\n遊んでね、にいちゃん！", 0, 100401, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "マホ", 0, 30, 80001, 0, 1, 3, "もう王子はんと会うてから\n1年ぐらいたつそうどすえ\n早いもんやなあ", 0, 101001, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "マホ", 0, 31, 80001, 0, 2, 3, "このままやと、\nうちもあっという間に\n干からびてしまいそうどすえ\nなーんてな。うふふ", 0, 101001, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "イリヤ", 0, 32, 80001, 0, 1, 3, "お主と会って早1年じゃな\nこれからも存分に尽くすがよい", 0, 104401, 1);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "イリヤ", 0, 33, 80001, 0, 2, 3, "わらわもたまには、お主の献身に\n報いてやらぬでもない♪", 0, 104401, 1);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "カオリ", 0, 34, 80001, 0, 1, 3, "もう、キミと出会って結構な時間が\nたつさ～。でも、私たちの旅は\nまだまだ始まったばかりさ～！", 0, 101701, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "カオリ", 0, 35, 80001, 0, 2, 3, "これからも、どうぞよろしく\nお願いするさ～！", 0, 101701, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "サレン", 0, 36, 80001, 0, 1, 4, "あんたと出会ってからもうそんなに\nたつのね～", 0, 102801, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "サレン", 0, 37, 80001, 0, 2, 4, "あっという間だったって感じるほど\n楽しかったわ！\nこれからも一緒に頑張りましょ！", 0, 102801, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "スズメ", 0, 38, 80001, 0, 1, 4, "出会ってから1年間、ずっと\nご迷惑をかけっぱなしで、\nすみません…", 0, 102501, 1);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "スズメ", 0, 39, 80001, 0, 2, 4, "これからはもっと頑張りますから\n見ていてくださいね\nできれば…\n今までよりも、私の近くで♪", 0, 102501, 1);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "タマキ", 0, 40, 80001, 0, 1, 5, "振り返れば、今までいろんなことが\nあったにゃあ…", 0, 104601, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "タマキ", 0, 41, 80001, 0, 2, 5, "これからも、二人でたくさんの\n思い出をがっぽり作っていく\nにゃー！", 0, 104601, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "ミフユ", 0, 42, 80001, 0, 1, 5, "キミとの師弟関係も、\nもう1年なのね…\nでもキミに教えることは、", 0, 104801, 1);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "ミフユ", 0, 43, 80001, 0, 2, 5, "まだまだいっぱいあるわ\nこれからも、\nしっかりついて来てね！", 0, 104801, 1);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "アキノ", 0, 44, 80001, 0, 1, 6, "あなたさまと出会ってもう1年…", 0, 103201, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "アキノ", 0, 45, 80001, 0, 2, 6, "光陰矢のごとし、とはまさに\nこのことですわ。…思い出は決して\nお金では買えませんわね", 0, 103201, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "ユカリ", 0, 46, 80001, 0, 1, 6, "ダメな私を1年間も見捨てないで\nくれて、本当にありがとう", 0, 103401, 1);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "ユカリ", 0, 47, 80001, 0, 2, 6, "情けないところいっぱいみせるかも\nしれないけど、これからも一緒に\nいてくれると、うれしいな", 0, 103401, 1);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "クリスティーナ", 0, 48, 80001, 0, 1, 7, "オマエと出会ってもう1年か\nまさかこのワタシの結婚相手に\nなろうとはな…", 0, 107101, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "クリスティーナ", 0, 49, 80001, 0, 2, 7, "ともかくこれからも\n愉しませてくれよ☆", 0, 107101, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "ジュン", 0, 50, 80001, 0, 1, 7, "君と出会ってからもう1年か。\n以前と比べて私の表情もいくらか\n柔らかくなった気がするんだが、\nどうだろう？", 0, 104701, 1);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "ジュン", 0, 51, 80001, 0, 2, 7, "見えないから分からないって？\nそれもそうだな…", 0, 104701, 1);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "トモ", 0, 52, 80001, 0, 1, 8, "もう1年たったみたいだね\nあなたをからかってばかりだった\nけど、楽しかった♪", 0, 103701, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "トモ", 0, 53, 80001, 0, 2, 8, "これからもよろしくね！", 0, 103701, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "マツリ", 0, 54, 80001, 0, 1, 8, "出会って、もう1年たっちゃった\nんスね。なんだか意外ッス", 0, 100501, 1);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "マツリ", 0, 55, 80001, 0, 2, 8, "毎日楽しくて、あっという間に過ぎ\nちゃったみたいなんスよね\nこれからも一緒に悪と戦うッス！", 0, 100501, 1);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "ミヤコ", 0, 56, 80001, 0, 1, 9, "オマエと会って1年なの！\n全然成仏もできなかったし、", 0, 100701, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "ミヤコ", 0, 57, 80001, 0, 2, 9, "生き返りもしなかったけど、\nすごく楽しかったの～♪\nオマエとプリンがあれば、\nミヤコはハッピーなの～♪", 0, 100701, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "シノブ", 0, 58, 80001, 0, 1, 9, "あなたと出会って1年なんですね\nでも、私の占いでは、私とあなたは", 0, 103101, 1);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "シノブ", 0, 59, 80001, 0, 2, 9, "来年も再来年も一緒にいるようです\nふふ、よろしくお願いしますね", 0, 103101, 1);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "アンナ", 0, 60, 80001, 0, 1, 10, "貴様が閃光のシグルドとして覚醒し\nもう1年か…。これからも我らの\n長き戦いに終わりはないが、", 0, 100901, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "アンナ", 0, 61, 80001, 0, 2, 10, "それでも貴様となら…\n添い遂げる覚悟はできているぞ！", 0, 100901, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "ナナカ", 0, 62, 80001, 0, 1, 10, "君と出会ってもう1年だって！\n早いねー！", 0, 101301, 1);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "ナナカ", 0, 63, 80001, 0, 2, 10, "これからも最強のバディタッグで\n大活躍だー！\n頼りにしてるよ、相棒！", 0, 101301, 1);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "ミツキ", 0, 64, 80001, 0, 1, 11, "1年たつのって、あっという間ね\nまだまだ君の知らない部分が\nたくさんあるし…", 0, 105101, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "ミツキ", 0, 65, 80001, 0, 2, 11, "これからも、いろんなところを\n調べさせてね？", 0, 105101, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "ルカ", 0, 66, 80001, 0, 1, 11, "お前さんと出会ってもう1年か\n助太刀するつもりが", 0, 105601, 1);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "ルカ", 0, 67, 80001, 0, 2, 11, "逆に助けられたりもしたね\nこれからもよろしく頼むよ！", 0, 105601, 1);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "エリコ", 0, 68, 80001, 0, 1, 11, "早いものですわね、あなた様と\nお会いして、もう1年…", 0, 102701, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "エリコ", 0, 69, 80001, 0, 2, 11, "あなた様といると、時があっと\nいう間に過ぎてしまいます\n時間を止める研究をしなくては\nいけませんね…", 0, 102701, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "アヤネ", 0, 70, 80001, 0, 1, 12, "お兄ちゃんと会ってから、もう、\n1年なんだね。私、少しは\nお姉ちゃんっぽくなったかな？", 0, 102301, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "アヤネ", 0, 71, 80001, 0, 2, 12, "あ、ぷうきちもお兄ちゃんっぽく\nなったよね？", 0, 102301, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "チカ", 0, 72, 80001, 0, 1, 12, "あなたと出会って、\nもう1年がたったんですね…\nこれからもそばにいて、", 0, 104201, 1);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "チカ", 0, 73, 80001, 0, 2, 12, "私のことを見守ってください\nできることなら、ずっと…", 0, 104201, 1);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "クルミ", 0, 74, 80001, 0, 1, 12, "お兄ちゃんと出会って1年かぁ\n私、相変わらずダメダメで…", 0, 102101, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "クルミ", 0, 75, 80001, 0, 2, 12, "で、でもこれからもずっと…\n一緒にいたいなぁ…", 0, 102101, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "シズル", 0, 76, 80001, 0, 1, 13, "再会してちょうど1年だね\nお祝いにごちそう作ってきたよ～♪\n特製ケーキと、お寿司と、", 0, 104901, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "シズル", 0, 77, 80001, 0, 2, 13, "ハンバーグと、カレーと、\nシチューと、あ、隠しきれない隠し\n味は、お姉ちゃんの愛情だよ！", 0, 104901, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "リノ", 0, 78, 80001, 0, 1, 13, "お兄ちゃんと再会して、もう1年\nなんですね。\n会えなかった時を思うと、", 0, 101101, 1);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "リノ", 0, 79, 80001, 0, 2, 13, "信じられないくらい幸せな1年\nでした！もう絶対、私から離れ\nないでくださいね♪", 0, 101101, 1);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "ユイ", 0, 80, 80001, 0, 1, 14, "騎士クンと出会ってから今まで、\nいろんなことがあったよね\n楽しいこと、ドキドキしたこと…", 0, 100201, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "ユイ", 0, 81, 80001, 0, 2, 14, "そんな日々をこれからも、\nず、ずっと一緒に…\n過ごしていきたいな…！", 0, 100201, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "ヒヨリ", 0, 82, 80001, 0, 1, 14, "騎士くんと出会って、まだ1年しか\nたってないんだ！？", 0, 100101, 1);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "ヒヨリ", 0, 83, 80001, 0, 2, 14, "なんかもうずっと一緒にいる気が\nするなぁ…えへへ。これからの\n1年もまたよろしくねっ！\n騎士くん！", 0, 100101, 1);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "レイ", 0, 84, 80001, 0, 1, 14, "初めてキミに出会って1年…\n随分見違えたね私もキミも、\n更に強くなっていく…", 0, 100301, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "レイ", 0, 85, 80001, 0, 2, 14, "キミに背中を預けられる日が、\n早くきますように", 0, 100301, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "ペコリーヌ", 0, 86, 80001, 0, 1, 15, "1年もわたしのことを、忘れない\nでいてくれて、ありがとうござい\nます！", 0, 105801, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "ペコリーヌ", 0, 87, 80001, 0, 2, 15, "これからもずっと、覚えていて\nくださいね！", 0, 105801, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "コッコロ", 0, 88, 80001, 0, 1, 15, "こうして1年も、主さまのおそばに\n置いていただけて、とても光栄\nです。どうかこれからも、", 0, 105901, 1);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "コッコロ", 0, 89, 80001, 0, 2, 15, "末永く主さまをお世話させて\nください。わたくしは、ずっと\n主さまのガイド役でいたいのです", 0, 105901, 1);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "キャル", 0, 90, 80001, 0, 1, 15, "1年か、まっさかこんなに長い\n付き合いになるなんてね～\nでも…わりと楽しい1年だったかも", 0, 106001, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "キャル", 0, 91, 80001, 0, 2, 15, "だから、もうしばらくあんたに\n付き合ってやるわよ喜びなさい！", 0, 106001, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "スズメ", 0, 92, 60001, 1, 1, 0, "新しい年のはじめに、\nおみくじはいかがでしょうか？\nよい運勢であるほど、\n沢山プレゼントが貰えますよ♪", 0, 112101, 1);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "", 0, 93, 60001, 1, 0, 0, "今日の運勢は姫吉！　あらゆる運気が最大を示しています！　　　今なら「伝説の吸血鬼」をも従え、世界征服も夢じゃありません！", 1, 0, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "", 0, 94, 60001, 1, 0, 0, "今日の運勢は姫吉！　やること全てが最高潮！　やばいですね☆　ラッキーアイテムはおにぎり！　お腹一杯で幸せが続くでしょう！", 1, 0, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "", 0, 95, 60001, 1, 0, 0, "今日の運勢は姫吉！　最強の運勢でどんなドジも跳ね除けます！　大切な人と一緒に、最高の一日になること間違いなし！", 1, 0, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "", 0, 96, 60001, 1, 0, 0, "今日の運勢は姫吉！　どんな挑戦も上手くいく、最高の運勢です！理想の自分に変身できる絶好の機会！　新しい一歩を踏み出そう！", 1, 0, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "", 0, 97, 60001, 2, 0, 0, "今日の運勢は姫吉！　その高い運気はまさにショーグンの如く！　その溢れる威光は人々を惹きつけ、皆の人気者間違いなしです！", 1, 0, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "", 0, 98, 60001, 1, 0, 0, "今日の運勢は大吉！　運気がどんどん高まっています！　　　　　何をやっても、最大効率で素晴らしい結果になりますよ♪", 2, 0, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "", 0, 99, 60001, 1, 0, 0, "今日の運勢は大吉！　なにをやっても上手くいって大活躍！　　　そんな素敵なあなたを見て、後ろから誰かが……クスクス♪", 2, 0, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "", 0, 100, 60001, 1, 0, 0, "今日の運勢は大吉！　ラッキーワードは「ちぇる～ん♪」　　　　出会う人みんなに言ってあげると、ハッピーになれますよ！", 2, 0, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "", 0, 101, 60001, 1, 0, 0, "今日の運勢は大吉！　運勢はバッチリで無敵状態！　　　　　　　龍眼で全てを見通して、思い通りの一日を過ごしましょう！", 2, 0, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "", 0, 102, 60001, 2, 0, 0, "今日の運勢は大吉！　運気が高く、何かに挑戦すると良い結果に！隠れた才能が引き出されそう！　目指せヒデサ……ではなく秀才！", 2, 0, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "", 0, 103, 60001, 1, 0, 0, "今日の運勢は中吉！　商売運が上がってきています！　　　　　　ラッキーアイテムはたいやき！　和菓子パワーで売上アップです！", 3, 0, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "", 0, 104, 60001, 1, 0, 0, "今日の運勢は中吉！　旅行運がぐんぐんと上昇中♪　　　　　　　もしかしたら、あの「マホマホ王国」にだって行けるかも♪", 3, 0, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "", 0, 105, 60001, 1, 0, 0, "今日の運勢は中吉！　仕事運が急上昇中です！　　　　　　　　　きっと「煉獄に舞う一陣の疾風」の如き活躍ができますよ！", 3, 0, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "", 0, 106, 60001, 1, 0, 0, "今日の運勢は中吉！　チャンスを待つと良い結果に！　　　　　　ラッキーアイテムのプリンを食べて、運を呼び込みましょう！", 3, 0, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "", 0, 107, 60001, 1, 0, 0, "今日の運勢は中吉！　お出かけした先で良縁に恵まれそうです！　ダウナー系あざとい女子が、あま～い飴をくれるかも？", 3, 0, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "", 0, 108, 60001, 1, 0, 0, "今日の運勢は中吉！　大抵のことはゴリ押しでなんとかできます！ラッキープレイスはゲーセン。パンチングマシンでストレス発散！", 3, 0, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "", 0, 109, 60001, 2, 0, 0, "今日の運勢は中吉！　運気が着々と上昇中！　　　　　　　　　　精霊の加護を受けたかの如く、色々な事がうまくいきますよ♪", 3, 0, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "", 0, 110, 60001, 1, 0, 0, "今日の運勢は小吉！　早朝に幸運の兆しあり！　　　　　　　　　もしかしたら、優しいお姉ちゃんが起こしにきてくれますよ♪", 4, 0, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "", 0, 111, 60001, 1, 0, 0, "今日の運勢は小吉！　運気上昇中ですが、上がるまでが要注意！　ラッキーアニマルは熊！　本当の家族のように守ってくれるかも♪", 4, 0, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "", 0, 112, 60001, 1, 0, 0, "今日の運勢は小吉！　外に出てゆっくり過ごすと運気が上昇！　　ラッキースポットは牧場。面白い出会いがありそうです♪", 4, 0, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "", 0, 113, 60001, 1, 0, 0, "今日の運勢は小吉！　運気は高いけども、ハプニングには注意！　出かけた先で、「ふしんしゃ」扱いされてしまうかも……", 4, 0, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "", 0, 114, 60001, 1, 0, 0, "今日の運勢は小吉！　同腹一心の友だちが見つかるかも！　　　　キーワードは「ユニちゃんズ」、記憶に刻みましょう！", 4, 0, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "", 0, 115, 60001, 1, 0, 0, "今日の運勢は小吉！　思わぬ相手と仲良くなれるかも？　　　　　うっかり毒舌をもらしてしまわないように要注意です！", 4, 0, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "", 0, 116, 60001, 2, 0, 0, "今日の運勢は小吉！　根を詰めずにリラックスすると運気向上！　ラッキーパーソンはアイドル。歌があなたを幸せにしてくれます♪", 4, 0, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "スズメ", 0, 117, 60001, 0, 2, 0, "おみくじの結果はいかがでしたか？\n新たに始まる一日が、\nあなたにとってよい日になりますように\n明日もお待ちしていますね♪", 0, 112101, 3);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "スズメ", 0, 118, 60001, 0, 3, 7, "おみくじ、楽しんで頂けましたか？\nあなたの新しい年が\n素晴らしいものになるよう、\n心からお祈りしますね", 0, 112101, 3);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "ペコリーヌ", 0, 119, 80003, 0, 1, 1, "ヒューヒュー♪\nドンドンパフパフー♪", 0, 105801, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "コッコロ", 0, 120, 80003, 0, 1, 1, "ひゅーひゅー\nどんどんぱふぱふー\nでございます", 0, 105901, 1);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "コッコロ", 0, 121, 80003, 0, 2, 1, "さぁ、キャルさまもご一緒に\nどんどんぱふぱふいたしましょう", 0, 105901, 1);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "キャル", 0, 122, 80003, 0, 1, 1, "え？　ちょっと\nいきなりなにっ？", 0, 106001, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "キャル", 0, 123, 80003, 0, 2, 1, "ドンドンパフパフってなんなの？\nあたしにも分かるように\nちゃんと説明しなさいっての！", 0, 106001, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "ペコリーヌ", 0, 124, 80003, 0, 2, 1, "実はですねキャルちゃん！\nもうすぐわたしたちにとっての\n大切な節目──", 0, 105801, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "ペコリーヌ", 0, 125, 80003, 0, 3, 1, "『プリンセスコネクト！\nRe:Dive』\n2.5周年なんですよ！\nやばいですね☆", 0, 105801, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "コッコロ", 0, 126, 80003, 0, 3, 1, "ですからその日に向けて\n今から気分を盛り上げているのです", 0, 105901, 1);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "キャル", 0, 127, 80003, 0, 3, 1, "ああ、そういえば\nそんな時期だっけ\nって言っても、もう少し先じゃない\n気が早いんじゃないの？", 0, 106001, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "コッコロ", 0, 128, 80003, 0, 4, 1, "いいえ、キャルさま\nこういったお祝いは準備から\n楽しむものでございます\nそうですよね、ペコリーヌさま", 0, 105901, 1);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "ペコリーヌ", 0, 129, 80003, 0, 4, 1, "その通りです♪　というわけで！\nカウントダウンの始まりですよ！\nみんなで一緒に2.5周年に向けて\n盛り上がっていきましょー☆", 0, 105801, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "キャル", 0, 130, 80003, 0, 4, 1, "ド、ドンドンパフパフ～\nって何言わせんのよ！\nまったくもう、浮かれちゃってさ", 0, 106001, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "コッコロ", 0, 131, 80003, 0, 5, 1, "ふふ。キャルさまもワクワクして\nおいでのようですね\nそれでは主さま。大切な記念日まで\nどうぞご期待くださいませ", 0, 105901, 1);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "キャル", 0, 132, 80003, 0, 5, 2, "記念日を盛り上げるったって\n一体どうしたらいいのよ？\nあたし、そういうの\n苦手なんだけど…", 0, 106001, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "カリン", 0, 133, 80003, 0, 1, 2, "あら、キャルさん\nどうかなさいましたか？\n何かお困りのようですけど", 0, 190801, 1);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "キャル", 0, 134, 80003, 0, 6, 2, "げっ…カリンじゃない\nなんでもないわよ！", 0, 106001, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "カリン", 0, 135, 80003, 0, 2, 2, "そうなのですか？\nキャルさんのことですからてっきり\n2.5周年をどう盛り上げたら\nいいか悩んでいるのかと…", 0, 190801, 1);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "キャル", 0, 136, 80003, 0, 7, 2, "分かってんじゃないのよ！？\nいっ、いや、違うけどね！\nあたしは別にそんなことで\n悩んでないわよっ？", 0, 106001, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "カリン", 0, 137, 80003, 0, 3, 2, "ふふっ…\nこういう時は、真心を込めて\n気持ちをそのまま口にしたら\nいいのではないでしょうか？", 0, 190801, 1);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "キャル", 0, 138, 80003, 0, 8, 2, "気持ちを、ねぇ\nそれが一番難しいんじゃない…\nそもそも、あたしは記念日とか\n別にどうだっていいし", 0, 106001, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "キャル", 0, 139, 80003, 0, 9, 2, "…まあ、ペコリーヌもコロ助も\nやけに楽しそうだったから…\nこういうお祭り騒ぎみたいなのも\nたまにはいいかな、とは思うけど", 0, 106001, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "カリン", 0, 140, 80003, 0, 4, 2, "なるほどなるほど～\nキャルさんは【美食殿】で祝う\n2.5周年を心から楽しみに\nしている、ということですね！", 0, 190801, 1);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "キャル", 0, 141, 80003, 0, 10, 2, "はぁ！？\nどこ聞いたらそうなるのよ！\nあたしは別に楽しみになんか…！", 0, 106001, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "カリン", 0, 142, 80003, 0, 5, 2, "キャルさんの心からの想い\nみなさんにも伝わったでしょうか？\n私も2.5周年が楽しみです！\n以上、カリンがお伝えしました！", 0, 190801, 1);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "キャル", 0, 143, 80003, 0, 11, 2, "心からの想いって何なのよ！？\n勝手に人の言葉を\n捻じ曲げんじゃないわよ！\nこのくそ眼鏡ーーーっ！", 0, 106001, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "コッコロ", 0, 144, 80003, 0, 6, 3, "ペコリーヌさま、もうすぐ\n記念日でございますね\nこういった節目というのは\n感慨深いものでございます", 0, 105901, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "ペコリーヌ", 0, 145, 80003, 0, 5, 3, "色んな思い出が蘇ってきますね！\n楽しい出来事が\nいっぱいありました♪", 0, 105801, 1);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "コッコロ", 0, 146, 80003, 0, 7, 3, "はい、そうでございますね\n主さまも【美食殿】の活動の他にも\nいろんなご経験をされたと\n聞いておりますよ", 0, 105901, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "コッコロ", 0, 147, 80003, 0, 8, 3, "たしか…ランドソルで話題沸騰と\nなっていた、魔法少女なるものに\nお会いになられたとか", 0, 105901, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "ペコリーヌ", 0, 148, 80003, 0, 6, 3, "魔法少女って、あの美味しくって\n栄養たっぷりの大ヒット商品\n『魔法少女ソーセージ』の箱に\n描いてある女の子のことですか！？", 0, 105801, 1);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "ペコリーヌ", 0, 149, 80003, 0, 7, 3, "あのソーセージ\n美味しいんですよね～♪\nわたしもたっくさん食べてますよ！", 0, 105801, 1);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "コッコロ", 0, 150, 80003, 0, 9, 3, "あとは、牧場に素敵な温泉が\n出来たとか…", 0, 105901, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "ペコリーヌ", 0, 151, 80003, 0, 8, 3, "牧場ですか！？　温泉につかって\nチーズもオツですね～♪　考えた\nだけでよだれが出ちゃいそうです！\nやばいですね☆", 0, 105801, 1);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "コッコロ", 0, 152, 80003, 0, 10, 3, "ペコリーヌさま\n言ってるそばからよだれが…\n今お拭きしますね。ふき、ふき", 0, 105901, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "ペコリーヌ", 0, 153, 80003, 0, 9, 3, "えへへ、ありがとうございます\nコッコロちゃん！", 0, 105801, 1);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "コッコロ", 0, 154, 80003, 0, 11, 3, "それにしても、主さまは\n【美食殿】の一員として様々な\n美食と出会われていたのですね\nわたくし、感服いたしました", 0, 105901, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "ペコリーヌ", 0, 155, 80003, 0, 10, 3, "これからもたくさん冒険をして\nめいっぱい美食を\n味わい尽くしましょうね！", 0, 105801, 1);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "ペコリーヌ", 0, 156, 80003, 0, 11, 4, "2.5周年カウントダウン♪\nキャルちゃんキャルちゃん！\nわくわくしますね☆", 0, 105801, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "キャル", 0, 157, 80003, 0, 12, 4, "あぁもう、うるさいわね～\nまだまだ先でしょ？\nちょっとは落ち着きなさいよ", 0, 106001, 1);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "ペコリーヌ", 0, 158, 80003, 0, 12, 4, "キャルちゃんだって最近\nソワソワしてるじゃないですか～\nさっきもご機嫌そうに鼻歌とか\n歌ってましたよね？", 0, 105801, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "キャル", 0, 159, 80003, 0, 13, 4, "なっ…！　き、聞いてたの！？\nじゃない、歌ってないわよ！？\nあんたいい加減なこと言うと\nぶっ殺すわよ！？", 0, 106001, 1);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "ペコリーヌ", 0, 160, 80003, 0, 13, 4, "えっへへ～♪\nわたしは最近のキャルちゃんが\nもっともっと大好きですよ♪\nすごく積極的になりましたよね！", 0, 105801, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "キャル", 0, 161, 80003, 0, 14, 4, "…積極的？\nなんの話よ？", 0, 106001, 1);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "ペコリーヌ", 0, 162, 80003, 0, 14, 4, "またまた～♪\nアイドルを目指そうとしたこと\n忘れちゃったんですか？", 0, 105801, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "ペコリーヌ", 0, 163, 80003, 0, 15, 4, "ムーサの町で\nきゃるきゃる～ん☆　って！", 0, 105801, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "キャル", 0, 164, 80003, 0, 15, 4, "あっ…！？　ちちち違うわよっ！\nあれは操られてただけで\nあたしはアイドルなんか\n目指してないっての！！", 0, 106001, 1);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "ペコリーヌ", 0, 165, 80003, 0, 16, 4, "本当に本当ですか？\nわたし、キャルちゃんとっても\n乗り気だなって\n思ってたんですけど～", 0, 105801, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "キャル", 0, 166, 80003, 0, 16, 4, "まぁ、悪くはなかったけど…って\nだ、だから目指してないって\n言ってるでしょ！！\nなんてこと思い出させんのよ！？", 0, 106001, 1);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "キャル", 0, 167, 80003, 0, 17, 4, "ああ…ダメだわ…\nもう殺すしかない…\n2.5周年が来る前に\n記憶ごと消し去ってやるー！", 0, 106001, 1);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "キャル", 0, 168, 80003, 0, 18, 5, "2.5周年まであと10日…\nふ～ん？\n案外早いもんね？", 0, 106001, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "コッコロ", 0, 169, 80003, 0, 12, 5, "そうでございますね…\nその日のためにやるべきことは\nまだまだございます\n気を引き締めねばなりません…！", 0, 105901, 1);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "キャル", 0, 170, 80003, 0, 19, 5, "やる気十分ねコロ助…\nそんな気負ってるとしんどいわよ？\nお気楽にやりましょ？", 0, 106001, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "コッコロ", 0, 171, 80003, 0, 13, 5, "キャルさま…\nありがとうございます\nキャルさまは本当に\n優しいおかたです", 0, 105901, 1);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "キャル", 0, 172, 80003, 0, 20, 5, "な、なにわけの分かんないこと\n言ってんのよ…\nあたしは優しくなんかないっての", 0, 106001, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "コッコロ", 0, 173, 80003, 0, 14, 5, "そんなことはございません\n…あの、実はわたくし\nキャルさまに\nひとつお願いがございまして", 0, 105901, 1);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "キャル", 0, 174, 80003, 0, 21, 5, "お願い？　珍しいわね\nコロ助がそんなこと言うなんて\n…まあ、あたしに出来ることなら\n別にいいけど", 0, 106001, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "コッコロ", 0, 175, 80003, 0, 15, 5, "ありがとうございます\n実は記念日へ向けて\n『かくし芸』というものを\n用意してみようかと思いまして", 0, 105901, 1);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "キャル", 0, 176, 80003, 0, 22, 5, "かくし芸？", 0, 106001, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "コッコロ", 0, 177, 80003, 0, 16, 5, "はい。きゃるきゃる～ん☆　を\n参考に、ころころ～ん☆　という\nご挨拶を考えてみたのですが\nポーズがうまくいかなくて…", 0, 105901, 1);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "キャル", 0, 178, 80003, 0, 23, 5, "～～！！　ちょっと褒められて\nいい気になって損したわ！！\nふざけんなふざけんな\nふ～ざ～け～ん～な～っ！！", 0, 106001, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "ペコリーヌ", 0, 179, 80003, 0, 17, 6, "あと９日！　もうすぐですよ♪\nえへへ、楽しみで楽しみで\nごはんが進みますね☆", 0, 105801, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "キャル", 0, 180, 80003, 0, 24, 6, "ごはんが進みますね☆\nじゃなーい！\nいくらなんでも食べ過ぎよ！\n記念日、迎えられなくなるわよ！？", 0, 106001, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "コッコロ", 0, 181, 80003, 0, 17, 6, "大丈夫ですよキャルさま\n食事はわたくしたちの\n歩んできた道そのもの\nなのですから", 0, 105901, 1);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "ペコリーヌ", 0, 182, 80003, 0, 18, 6, "さっすがコッコロちゃん\nいいことを言いますね！\nそう、わたしたち\n【美食殿】の活動目的は─", 0, 105801, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "キャル", 0, 183, 80003, 0, 25, 6, "─みんなで楽しく食事をすること\nでしょ？\nあれだけ食べ歩いてきたんだから\n分かってるわよ", 0, 106001, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "コッコロ", 0, 184, 80003, 0, 18, 6, "はい…そうでございますね♪\nそれにしてもわたくしたち\n本当にいろいろなものを\n食べ歩いてきましたね…", 0, 105901, 1);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "キャル", 0, 185, 80003, 0, 26, 6, "そうね…ほんっとうにね…\n毒まみれのニセドラゴンとか\n恐竜…は逃げられちゃったっけ\nあとワニゴ…ワニの魔物とかね", 0, 106001, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "キャル", 0, 186, 80003, 0, 27, 6, "そして、とうとう本物の\nドラゴンまで相手に\nしちゃったのよね、あたしたちって", 0, 106001, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "キャル", 0, 187, 80003, 0, 28, 6, "信じられない強さだったわね\nあのドラゴン…やっぱり\n作戦はしっかり立てていかないと\nダメよね", 0, 106001, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "ペコリーヌ", 0, 188, 80003, 0, 19, 6, "えへへ…☆\nキャルちゃんが色んなことを\n覚えていてくれて\nうれしいです♪", 0, 105801, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "コッコロ", 0, 189, 80003, 0, 19, 6, "わたくしも鮮明に覚えておりますよ\n主さまもきっと…♪", 0, 105901, 1);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "キャル", 0, 190, 80003, 0, 29, 6, "あったり前でしょ！\n忘れないわよ、あんな滅茶苦茶な\nものばかり食べさせられて！\n…忘れられるわけ、ないじゃない", 0, 106001, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "キャル", 0, 191, 80003, 0, 30, 7, "ねえちょっと、ちょっと待って！？\nカリンから、2.5周年記念の\n生放送に出演してほしいって\n言われたんだけど！", 0, 106001, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "キャル", 0, 192, 80003, 0, 31, 7, "なんでそういう大事なことを\n急に言うわけ！？\nそんなの聞いてないわよ～！？", 0, 106001, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "キャル", 0, 193, 80003, 0, 32, 7, "いっぱい人に見られちゃうのよね？\nなんであたしがそんなこと…", 0, 106001, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "キャル", 0, 194, 80003, 0, 33, 7, "あー\nあー\n…声、これで大丈夫かしら？", 0, 106001, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "キャル", 0, 195, 80003, 0, 34, 7, "聞き取りにくいかもしれないわね…\nもうちょっと明るい方がいいかも\nえっと…こんな感じ？", 0, 106001, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "キャル", 0, 196, 80003, 0, 35, 7, "オイッスー☆", 0, 106001, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "キャル", 0, 197, 80003, 0, 36, 7, "……\nいや、ないわ\nいつもどおりが一番ね、うん", 0, 106001, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "キャル", 0, 198, 80003, 0, 37, 7, "もう、記念の生放送なんて\n超責任重大なやつじゃない…\nもしなにか失敗とかしたら…", 0, 106001, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "キャル", 0, 199, 80003, 0, 38, 7, "ど、どど、どうしよう！？\n緊張してきちゃった…！！", 0, 106001, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "コッコロ", 0, 200, 80003, 0, 20, 8, "主さま、わたくし\nペコリーヌさまとキャルさまと共に\n2.5周年を祝う生放送に\n出演する大役を仰せつかりました", 0, 105901, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "コッコロ", 0, 201, 80003, 0, 21, 8, "多くの方々に\n『プリンセスコネクト！\nRe:Dive』の魅力をお伝えする\n重要な役割でございます", 0, 105901, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "コッコロ", 0, 202, 80003, 0, 22, 8, "わたくし、全身全霊をもって\n生放送に取り組みたいと\n思っております", 0, 105901, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "コッコロ", 0, 203, 80003, 0, 23, 8, "ですが、もしかしたら\nセリフを噛んでしまったり\n段取りを間違えてしまったり\nするかもしれません…", 0, 105901, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "コッコロ", 0, 204, 80003, 0, 24, 8, "そのようなことがないよう\n練習はしっかりしておかなくては\nまずはこちらから…", 0, 105901, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "コッコロ", 0, 205, 80003, 0, 25, 8, "オイッスー☆", 0, 105901, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "コッコロ", 0, 206, 80003, 0, 26, 8, "先ほどキャルさまが練習されて\nいるのをお見かけしたので\nわたくしもと思いまして…\nいかがでしょうか？", 0, 105901, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "コッコロ", 0, 207, 80003, 0, 27, 8, "良い感じ、ですか？\n…ふふ、ありがとうございます\n主さまのあたたかいお言葉があれば\nわたくしは百人力でございます", 0, 105901, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "コッコロ", 0, 208, 80003, 0, 28, 8, "この大役を無事に成し遂げ\n主さまに素敵な2.5周年を\n迎えていただけるよう…\nわたくし、尽力いたしますね", 0, 105901, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "ペコリーヌ", 0, 209, 80003, 0, 20, 9, "オイッスー☆", 0, 105801, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "ペコリーヌ", 0, 210, 80003, 0, 21, 9, "…う～ん、今のはちょっと\n違う感じがしますね\n堅苦しかったかもしれません", 0, 105801, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "ペコリーヌ", 0, 211, 80003, 0, 22, 9, "あと６日で2.5周年ですか…", 0, 105801, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "ペコリーヌ", 0, 212, 80003, 0, 23, 9, "この年月がオイッスーの\n歴史なんだと思うと\nじ～んとしちゃいます！\nやばいですね☆", 0, 105801, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "ペコリーヌ", 0, 213, 80003, 0, 24, 9, "生放送で最高のオイッスー☆　を\nみなさんにお届けするために\n頑張らないと！\nそれじゃあもう一度…", 0, 105801, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "ペコリーヌ", 0, 214, 80003, 0, 25, 9, "オイッスー☆", 0, 105801, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "ペコリーヌ", 0, 215, 80003, 0, 26, 9, "…うう～ん、どうしてでしょう\nまだ何かが違う気がします…\nもしかしたら\nおなかが空きすぎてるとか？", 0, 105801, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "ペコリーヌ", 0, 216, 80003, 0, 27, 9, "今さっき食べたばっかりですけど\nおなかが減っては\n良いオイッスー☆　は\n出せませんよね！", 0, 105801, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "ペコリーヌ", 0, 217, 80003, 0, 28, 9, "そうと決まれば、コッコロちゃんと\nキャルちゃんを誘って\nごはんにしましょう！\n善は急げです！", 0, 105801, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "ペコリーヌ", 0, 218, 80003, 0, 29, 9, "あ、二人が丁度いいところに！\nオイッス～☆　…ああ！！\n今の、今のですよ～♪", 0, 105801, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "ペコリーヌ", 0, 219, 80003, 0, 30, 9, "なるほど、どんな時も\n自然体が一番ってことですね！\nこの調子で、生放送も\n頑張っちゃいます♪", 0, 105801, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "ペコリーヌ", 0, 220, 80003, 0, 31, 10, "2.5周年まで残り５日ですが\nその前にもうすぐ生放送ですよ！\nコッコロちゃん\n準備の方はどうですか？", 0, 105801, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "コッコロ", 0, 221, 80003, 0, 29, 10, "はい。わたくし、しっかりと\n備えてまいりました\n万事抜かりはございません", 0, 105901, 1);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "ペコリーヌ", 0, 222, 80003, 0, 32, 10, "おお～すごい自信を感じます…！\nいい生放送になりそうですね☆\n……あれ？　なんだかこの辺り\n揺れていませんか？", 0, 105801, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "キャル", 0, 223, 80003, 0, 39, 10, "…………", 0, 106001, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "コッコロ", 0, 224, 80003, 0, 30, 10, "キャルさま？\nお風邪をひかれたんでしょうか\nひどい震えですが…", 0, 105901, 1);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "キャル", 0, 225, 80003, 0, 40, 10, "かかかか風邪じゃないわよ！\nここここれはただ\nななな生放送が楽しみ過ぎて\nワワワワクワクが止まらないだけ！", 0, 106001, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "ペコリーヌ", 0, 226, 80003, 0, 33, 10, "大丈夫ですかキャルちゃん！？\nそうだ、これを飲んでください！", 0, 105801, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "キャル", 0, 227, 80003, 0, 41, 10, "きき気がきくじゃない、ありがとっ\nごくっ、んく…ぶはぁっ！？\nちょっと何飲ませたの！？\n辛っ！　苦ぁ！？　甘ぁっ！？", 0, 106001, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "ペコリーヌ", 0, 228, 80003, 0, 34, 10, "これは魔物のエキスに薬草に\nその他色んなものを混ぜた\n特製の栄養ドリンクです！", 0, 105801, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "ペコリーヌ", 0, 229, 80003, 0, 35, 10, "これを飲めば体ぽかぽかで\n元気いっぱいですよ♪", 0, 105801, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "キャル", 0, 230, 80003, 0, 42, 10, "このアホリーヌ！\n風邪じゃないって言ったでしょ！？\nどさくさに紛れて\nそんな恐ろしいもん飲ませんな～！", 0, 106001, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "コッコロ", 0, 231, 80003, 0, 31, 10, "キャルさま\n震えが収まっておりますね\nよかったです…\nわたくし、心配いたしました", 0, 105901, 1);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "キャル", 0, 232, 80003, 0, 43, 10, "え？　ああ、ホントね…って\nだから風邪じゃないって\n言ってるのに…\nまあいいわこの際なんでも", 0, 106001, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "ペコリーヌ", 0, 233, 80003, 0, 36, 10, "えっへへ～♪　いつも通りの\nキャルちゃんに戻りましたし\n素敵な生放送が出来そうですね！\n今から楽しみです☆", 0, 105801, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "カリン", 0, 234, 80003, 0, 6, 11, "ランドソルのみなさま、こんにちは\n生放送の会場設営を担当しています\n【ギルド管理協会】のカリンです", 0, 190801, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "カリン", 0, 235, 80003, 0, 7, 11, "【美食殿】のみなさんは\nまもなく始まる生放送に向けて\n最後の調整のため\n席を外しています", 0, 190801, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "カリン", 0, 236, 80003, 0, 8, 11, "頑張っている３人の\n邪魔をするわけには\nいきませんので、ご容赦ください", 0, 190801, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "カリン", 0, 237, 80003, 0, 9, 11, "ですので、本日は私から\n【美食殿】のみなさんの様子を\nお伝えさせていただこうと思います", 0, 190801, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "カリン", 0, 238, 80003, 0, 10, 11, "まずはペコリーヌさんですが…\n何やら大量のおにぎりを\n食べているようです\nえっと…栄養は大事ですよね", 0, 190801, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "カリン", 0, 239, 80003, 0, 11, 11, "次にコッコロさんですが\nこちらは発声練習をしています", 0, 190801, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "カリン", 0, 240, 80003, 0, 12, 11, "一生懸命、小さなお口を開けて\n声を出す姿が応援したくなりますね", 0, 190801, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "カリン", 0, 241, 80003, 0, 13, 11, "そしてキャルさんは…\nこれは…堂々としたものですね\n何もせず、ただ立ち尽くして…", 0, 190801, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "カリン", 0, 242, 80003, 0, 14, 11, "あれ？\nもしかして緊張で気を失って…？", 0, 190801, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "カリン", 0, 243, 80003, 0, 15, 11, "…ええと\n【美食殿】のみなさんは\nやる気十分なようですね！", 0, 190801, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "カリン", 0, 244, 80003, 0, 16, 11, "３人とも生放送を\nいいものにしようと\n全力で頑張ってくれていますので\nどうかご期待ください", 0, 190801, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "カリン", 0, 245, 80003, 0, 17, 11, "それでは\nまた明日お会いしましょう♪", 0, 190801, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "キャル", 0, 246, 80003, 0, 44, 12, "2.5周年まであと３日！\nそして生放送は明日！\nなんかいよいよって感じね…", 0, 106001, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "ペコリーヌ", 0, 247, 80003, 0, 37, 12, "３人でい～っぱい練習しましたし\nきっと大丈夫ですよ☆", 0, 105801, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "コッコロ", 0, 248, 80003, 0, 32, 12, "ペコリーヌさまに\n大丈夫と言ってもらえると\nとても心強いですね", 0, 105901, 1);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "キャル", 0, 249, 80003, 0, 45, 12, "信じすぎちゃだめよコロ助\nこいつはなーんにも\n考えてないだけなんだから", 0, 106001, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "ペコリーヌ", 0, 250, 80003, 0, 38, 12, "ひどいですよぉキャルちゃ～ん！", 0, 105801, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "コッコロ", 0, 251, 80003, 0, 33, 12, "ふふ。お二人とも\n普段通りの様子で\n安心いたしました", 0, 105901, 1);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "ペコリーヌ", 0, 252, 80003, 0, 39, 12, "はいっ☆\n生放送でもなんでも\n全力全開、わたしらしく\nぶつかっていくだけです！", 0, 105801, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "キャル", 0, 253, 80003, 0, 46, 12, "そうね。おめでたいことなんだし\nここまで来たんなら\n楽しめるだけ\n楽しむしかないわよね！", 0, 106001, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "コッコロ", 0, 254, 80003, 0, 34, 12, "わたくしも、普段よりも心が\n熱くなっております\nこの胸の気持ちをお伝えできるよう\n精一杯努力いたします…！", 0, 105901, 1);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "ペコリーヌ", 0, 255, 80003, 0, 40, 12, "それでは、いきましょう！\n明日の生放送、絶対に\n成功させましょうね☆", 0, 105801, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "ペコリーヌ", 0, 256, 80003, 0, 41, 13, "えっと、最初のひと言は…あれ？\nキャルちゃん、わたし\n何て言うんでしたっけ？", 0, 105801, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "キャル", 0, 257, 80003, 0, 47, 13, "は！？\nなにいきなりど忘れしてるの！？\nっていうかあんた、台本は！？", 0, 106001, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "ペコリーヌ", 0, 258, 80003, 0, 42, 13, "忘れてきちゃいました☆", 0, 105801, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "キャル", 0, 259, 80003, 0, 48, 13, "忘れてきちゃいました☆\nで済ませられると思ってんの！？\nほら、あたし二冊持ってきたから\nこれ使いなさい！", 0, 106001, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "コッコロ", 0, 260, 80003, 0, 35, 13, "マイクとは、こんな高い位置に\nあるものなのですね…！\n背伸びをすれば、何とか…\nう～ん、うぅ～ん…！", 0, 105901, 1);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "キャル", 0, 261, 80003, 0, 49, 13, "あぁもう\n少し下げればいいでしょ！？\nこれくらいでいい？\nまったく、世話が焼けるわね…", 0, 106001, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "ペコリーヌ", 0, 262, 80003, 0, 43, 13, "あれ？\nキャルちゃん、後ろ髪が\nはねちゃってますよ？", 0, 105801, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "キャル", 0, 263, 80003, 0, 50, 13, "え？　うそっ！？\nちゃんと確かめてきたのに！\nどこどこ！？", 0, 106001, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "コッコロ", 0, 264, 80003, 0, 36, 13, "キャルさま、動かないで\nくださいまし。今くしで…\nはい、直りましたよ", 0, 105901, 1);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "キャル", 0, 265, 80003, 0, 51, 13, "あ、ありがと…はぁ。生放送\n本番までもうすぐなのに\nこんなことしてる場合じゃ\nないわよね", 0, 106001, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "コッコロ", 0, 266, 80003, 0, 37, 13, "はい、時間は残りわずかです\nですが、慌ただしく\n動き回るこの時間も\nなんだか楽しくて…♪", 0, 105901, 1);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "キャル", 0, 267, 80003, 0, 52, 13, "コロ助、あんた…って\nそうじゃなくて！\n胸にジンワリきてる\n暇なんかないでしょ…！？", 0, 106001, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "ペコリーヌ", 0, 268, 80003, 0, 44, 13, "あはは♪\nキャルちゃんの動きが\nとっても面白いです♪\nやばいですね☆", 0, 105801, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "コッコロ", 0, 269, 80003, 0, 38, 13, "ふふ\nきっと面白い生放送になると\nわたくし、確信しております", 0, 105901, 1);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "ペコリーヌ", 0, 270, 80003, 0, 45, 13, "絶対絶対、間違いありません！\n見てくれるみなさんが\n笑顔になれる時間を\nお届けしましょう！", 0, 105801, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "キャル", 0, 271, 80003, 0, 53, 13, "そんなの当然でしょ！\n誰が見たって面白いって\n言ってもらえるように\nあれだけ準備したんだから！", 0, 106001, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "ペコリーヌ", 0, 272, 80003, 0, 46, 13, "それでは\n記念日直前生放送！\n楽しみにしててくださいね☆", 0, 105801, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "カリン", 0, 273, 80003, 0, 18, 14, "こんにちは\n【ギルド管理協会】のカリンです", 0, 190801, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "カリン", 0, 274, 80003, 0, 19, 14, "昨日の生放送は\nいかがだったでしょうか？", 0, 190801, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "カリン", 0, 275, 80003, 0, 20, 14, "たくさんの笑顔と喜びと\nそして驚きを受け取って\nいただけたのではないかと思います", 0, 190801, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "カリン", 0, 276, 80003, 0, 21, 14, "そしていよいよ\n明日は2.5周年の当日\n待ちに待った記念日を\n迎えることとなります", 0, 190801, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "カリン", 0, 277, 80003, 0, 22, 14, "【美食殿】のみなさんも\n張り切って様々な準備を\nされていますよ", 0, 190801, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "カリン", 0, 278, 80003, 0, 23, 14, "【ギルド管理協会】も\n万全のサポート体制で\n協力してまいりたいと思います", 0, 190801, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "カリン", 0, 279, 80003, 0, 24, 14, "…ふふ、本当に楽しみですね\n【ギルド管理協会】の職員として\nだけではなく、私個人としても…", 0, 190801, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "カリン", 0, 280, 80003, 0, 25, 14, "この記念日を迎えられることが\n心から楽しみですし\n幸せを感じています", 0, 190801, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "カリン", 0, 281, 80003, 0, 26, 14, "きっと【美食殿】のみなさんは\nそれ以上に心を弾ませて\nいることでしょう", 0, 190801, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "カリン", 0, 282, 80003, 0, 27, 14, "それでは\n明日からの2.5周年を\nどうぞ、お楽しみに…♪", 0, 190801, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "ペコリーヌ", 0, 283, 80003, 0, 47, 15, "準備はいいですかっ？\n…いっきますよー！", 0, 105801, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "キャル", 0, 284, 80003, 0, 54, 15, "祝！", 0, 106001, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "コッコロ", 0, 285, 80003, 0, 39, 15, "2.5周年！", 0, 105901, 1);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "ペコリーヌ", 0, 286, 80003, 0, 48, 15, "おめでとうございま～す☆", 0, 105801, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "キャル", 0, 287, 80003, 0, 55, 15, "少し前まではまだまだ先じゃない\nとか言ってたけど\n本当にあっという間だったわね", 0, 106001, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "コッコロ", 0, 288, 80003, 0, 40, 15, "光陰矢の如し…\nとはこのことでございますね\nでも、今日という日を無事に\n迎えることができて良かったです", 0, 105901, 1);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "ペコリーヌ", 0, 289, 80003, 0, 49, 15, "コッコロちゃん！　キャルちゃん！\n落ち着くには\nまだ早いですよ！\n盛り上がるのはこれからです☆", 0, 105801, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "キャル", 0, 290, 80003, 0, 56, 15, "ほんとあんたはいつも\n全力全開ねー\nでもそうね、記念日なんだし\nパーっといきましょ！", 0, 106001, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "コッコロ", 0, 291, 80003, 0, 41, 15, "ええ、わたくしも全力全開で\nまいります…！\n主さま、さあ\nどうぞ手を取ってくださいまし…♪", 0, 105901, 1);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "キャル", 0, 292, 80003, 0, 57, 15, "あんたもコロ助も、浮かれて\n転ぶんじゃないわよ？\n…仕方ないから、あたしも\n手を繋いであげるわよ", 0, 106001, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "ペコリーヌ", 0, 293, 80003, 0, 50, 15, "あー！　二人ともずるいです！\nわたしもっ、ギュー♪", 0, 105801, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "キャル", 0, 294, 80003, 0, 58, 15, "ちょっ、ペコリーヌ！\nくっつき過ぎよ！", 0, 106001, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "ペコリーヌ", 0, 295, 80003, 0, 51, 15, "記念日だから\n平気平気♪\nやばいですね☆", 0, 105801, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "キャル", 0, 296, 80003, 0, 59, 15, "意味分かんないわよ！", 0, 106001, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "コッコロ", 0, 297, 80003, 0, 42, 15, "ふふ。このめでたき日に\n喜びがあふれておりますね\n主さまは…いかがでしょうか？", 0, 105901, 1);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "コッコロ", 0, 298, 80003, 0, 43, 15, "喜んでいただけているならば\nわたくしはとてもとても\n幸せでございます", 0, 105901, 1);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "ペコリーヌ", 0, 299, 80003, 0, 52, 15, "さぁ、2.5周年を\n楽しみ尽くしますよ☆\nまだまだ食べたいものもありますし\nやりたいこともたくさんです！", 0, 105801, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "キャル", 0, 300, 80003, 0, 60, 15, "分かった、分かったから\n引っ張るんじゃないわよ！\n…そんな掴まなくても\nちゃんとついていくから", 0, 106001, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "コッコロ", 0, 301, 80003, 0, 44, 15, "ペコリーヌさま…キャルさま…\n主さま…共にまいりましょう\nどこまでも…！", 0, 105901, 1);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "ペコリーヌ", 0, 302, 80003, 0, 53, 15, "はい！　それでは2.5周年\n食べつくしツアーへ！\nそして、その先の未来へ\n向かって出発しましょう！", 0, 105801, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("105901,45,106001,61", "ペコリーヌ・コッコロ・キャル", 1, 303, 80003, 0, 54, 15, "2.5周年\nありがとうございます！", 0, 105801, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "カリン", 0, 304, 60002, 1, 1, 0, "新年を迎え、新たに始まる一日を\nおみくじで占ってみませんか？\n良い運勢であるほど、\n沢山のプレゼントが貰えますよ♪", 0, 190804, 1);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "", 0, 305, 60002, 1, 0, 0, "今日の運勢は姫吉！　どんな不可能も可能にできちゃう！　　　　仲間と食卓を囲めばさらに運気上昇よ！　ただし魔物料理以外！", 1, 0, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "", 0, 306, 60002, 1, 0, 0, "今日の運勢は姫吉！　やること全てが最高潮！　やばいですね☆　ラッキーアイテムはおにぎり！　お腹一杯で幸せが続くでしょう！", 1, 0, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "", 0, 307, 60002, 1, 0, 0, "今日の運勢は姫吉！　最強の運勢でどんなドジも跳ね除けます！　大切な人と一緒に、最高の一日になること間違いなし！", 1, 0, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "", 0, 308, 60002, 1, 0, 0, "今日の運勢は姫吉！　どんな挑戦も上手くいく、最高の運勢です！理想の自分に変身できる絶好の機会！　新しい一歩を踏み出そう！", 1, 0, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "", 0, 309, 60002, 2, 0, 0, "今日の運勢は姫吉！　やること全てが上手くいく最高の一日！　　あなたの活躍に先生もにっこりです。いい子いい子♪", 1, 0, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "", 0, 310, 60002, 1, 0, 0, "今日の運勢は大吉！　お裁縫好きな女の子のお墨付きです！　　　お世話したがりの妹にめいっぱい愛されちゃうかも！？", 2, 0, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "", 0, 311, 60002, 1, 0, 0, "今日の運勢は大吉！　ラッキーカラーは赤！　　　　　　　　　　湧き上がるアイデアの数々に、高笑いが止まりませんわ！", 2, 0, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "", 0, 312, 60002, 1, 0, 0, "今日の運勢は大吉！　ラッキーワードは「ちぇる～ん♪」　　　　出会う人みんなに言ってあげると、ハッピーになれますよ！", 2, 0, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "", 0, 313, 60002, 1, 0, 0, "今日の運勢は大吉！　運勢はバッチリで無敵状態！　　　　　　　龍眼で全てを見通して、思い通りの一日を過ごしましょう！", 2, 0, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "", 0, 314, 60002, 2, 0, 0, "今日の運勢は大吉！　悩んでいたことが解決しそうです！　　　　たまには勉強も仕事も忘れて、心あたたまる家族と過ごしましょ！", 2, 0, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "", 0, 315, 60002, 1, 0, 0, "今日の運勢は中吉！　家の中でゆっくり過ごすのがよさそう！　　ラッキーアイテムはゲームよ！　ベストスコアを更新できるかも？", 3, 0, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "", 0, 316, 60002, 1, 0, 0, "今日の運勢は中吉！　上手くなりたいことに積極的に挑戦してみてサックス好きの妹が、手取り足取りサポートしてくれるかも～？", 3, 0, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "", 0, 317, 60002, 1, 0, 0, "今日の運勢は中吉！　仕事運が急上昇中です！　　　　　　　　　きっと「煉獄に舞う一陣の疾風」の如き活躍ができますよ！", 3, 0, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "", 0, 318, 60002, 1, 0, 0, "今日の運勢は中吉！　日頃の努力が報われます！　　　　　　　　親しい友人がいるなら報告してみて。一緒に喜んでくれるはずだ。", 3, 0, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "", 0, 319, 60002, 1, 0, 0, "今日の運勢は中吉！　商売運が上がってきています！　　　　　　ラッキーアイテムはたい焼き！　和菓子パワーで売上アップです！", 3, 0, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "", 0, 320, 60002, 1, 0, 0, "今日の運勢は中吉！　チャンスを待つと良い結果に！　　　　　　ラッキーアイテムのプリンを食べて、運を呼び込みましょう！", 3, 0, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "", 0, 321, 60002, 2, 0, 0, "今日の運勢は中吉！　運気が着々と上昇中！　　　　　　　　　　精霊の加護を受けたかのごとく、色々な事がうまくいきますよ♪", 3, 0, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "", 0, 322, 60002, 1, 0, 0, "今日の運勢は小吉！　まだ見ぬ危険が隠れているかも！　　　　　でも安心しろ！　提督が貴公を守ってくれるはずだぞ！", 4, 0, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "", 0, 323, 60002, 1, 0, 0, "今日の運勢は小吉！　いろんな事件が起こる一日になりそう！　　状況をよく観察してみようじゃないか！　まさに探偵のごとくね！", 4, 0, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "", 0, 324, 60002, 1, 0, 0, "今日の運勢は小吉！　気の向くままに過ごすのが幸せへの近道！　美味い魚を食べて猫を眺めて、自然体で過ごすのはどうだい？", 4, 0, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "", 0, 325, 60002, 1, 0, 0, "今日の運勢は小吉！　運気は高いけども、ハプニングには注意！　出かけた先で、「ふしんしゃ」扱いされてしまうかも……", 4, 0, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "", 0, 326, 60002, 1, 0, 0, "今日の運勢は小吉！　同腹一心の友だちが見つかるかも！　　　　キーワードは「ユニちゃんズ」、記憶に刻みましょう！", 4, 0, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "", 0, 327, 60002, 1, 0, 0, "今日の運勢は小吉！　周りに流されず、マイペースな一日を！　　ただし寝すぎには注意ぐうたらし過ぎず適度な運動を、だってさ～", 4, 0, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "", 0, 328, 60002, 2, 0, 0, "今日の運勢は小吉！　根を詰めずにリラックスすると運気向上！　ラッキーパーソンはアイドル。歌があなたを幸せにしてくれます♪", 4, 0, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "カリン", 0, 329, 60002, 0, 2, 0, "おみくじの結果はどうでしたか？\n新たに始まる一日が、あなたに\nとって良い日になりますように\nまた明日も遊びに来て下さいね♪", 0, 190804, 3);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "カリン", 0, 330, 60002, 0, 3, 7, "おみくじは楽しんで頂けましたか？\nあなたの新たなる年が\n素晴らしいものになるよう、\n心からお祈りいたします", 0, 190804, 3);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "コッコロ", 0, 331, 80005, 0, 1, 1, "ささ、シェフィさまはこちらへ…", 0, 105901, 4);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "シェフィ", 0, 332, 80005, 0, 1, 1, "コッコロさん、どこへ行くの…？\n…え？", 0, 106401, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "ペコリーヌ", 0, 333, 80005, 0, 1, 1, "『おめでとうございます！\nわ～パチパチー！』\n…という感じでどうですかね！？", 0, 105801, 3);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "コッコロ", 0, 334, 80005, 0, 2, 1, "ではわたくしも\n後ろからクラッカーを鳴らして\n応戦いたします\nああ、当日が待ち遠しいですね", 0, 105901, 4);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "シェフィ", 0, 335, 80005, 0, 2, 1, "ええと…\n二人とも何をしているの？", 0, 106401, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "キャル", 0, 336, 80005, 0, 1, 1, "シェフィは初めてだもんね\nあの日が近いからって\n予行練習してんのよ\n気が早いわよね～", 0, 106001, 1);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "シェフィ", 0, 337, 80005, 0, 3, 1, "あの日…？", 0, 106401, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "ペコリーヌ", 0, 338, 80005, 0, 2, 1, "その通りです…\nいよいよ記念すべきあの日が\n近づいてきましたよ！", 0, 105801, 3);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "ペコリーヌ", 0, 339, 80005, 0, 3, 1, "『プリンセスコネクト！\nRe:Dive』3.5周年！", 0, 105801, 3);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "カリン", 0, 340, 80005, 0, 1, 1, "やばいですね☆", 0, 190801, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "キャル", 0, 341, 80005, 0, 2, 1, "って、なにしれっと\n参加してんのよカリン！", 0, 106001, 1);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "カリン", 0, 342, 80005, 0, 2, 1, "ふふ、いいじゃないですか\n【ギルド管理協会】として\n私もみなさんと一緒に\nお祝いをさせてください！", 0, 190801, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "カリン", 0, 343, 80005, 0, 3, 1, "それに…記念日が近づけば\nとっておきの情報を\nお届けしますから♪", 0, 190801, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "ペコリーヌ", 0, 344, 80005, 0, 4, 1, "というわけで\n今日からシェフィちゃんも一緒に\nカウントダウンしちゃいましょう！\n3.5周年まで…", 0, 105801, 3);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "シェフィ", 0, 345, 80005, 0, 4, 1, "えっ！？　ええとええと…！\nあ、あと14日…！", 0, 106401, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "ペコリーヌ", 0, 346, 80005, 0, 5, 2, "3.5周年まであと13日\n豪華に、そして盛大に\nお祝いしたいですね☆", 0, 105801, 3);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "コッコロ", 0, 347, 80005, 0, 3, 2, "豪華といえば前にとある貴族さまが\n舞踏会を開かれたそうで\n主さまがそのお手伝いを\nしておりました", 0, 105901, 1);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "コッコロ", 0, 348, 80005, 0, 4, 2, "そこで美しい所作を学ばれたので\nわたくしも教えていただきました\nしゃなり、しゃなり…\nいかがでしょう？", 0, 105901, 1);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "ペコリーヌ", 0, 349, 80005, 0, 6, 2, "すっごく上手ですよ☆\nまるでお姫様みたいです\nせっかくなのでわたしも\nしゃなりしゃなり", 0, 105801, 3);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "コッコロ", 0, 350, 80005, 0, 5, 2, "さすがでございます\nペコリーヌさま", 0, 105901, 1);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "キャル", 0, 351, 80005, 0, 3, 2, "いや、あんたは\nできなきゃダメでしょ\n本物のお姫様なんだし", 0, 106001, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "シェフィ", 0, 352, 80005, 0, 5, 2, "しゃなり、しゃなり…\nふふ、案外できるものね", 0, 106401, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "ペコリーヌ", 0, 353, 80005, 0, 7, 2, "わぁ！\nシェフィちゃん\n姿勢がとってもキレイです♪", 0, 105801, 3);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "コッコロ", 0, 354, 80005, 0, 6, 2, "さ、次はキャルさまの\n番でございますよ", 0, 105901, 1);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "キャル", 0, 355, 80005, 0, 4, 2, "あ、あたしはいいわよ！\nそういうの全然分かんないし…", 0, 106001, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "シェフィ", 0, 356, 80005, 0, 6, 2, "大丈夫\nやってみると結構楽しいわよ\nほらほらっ", 0, 106401, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "キャル", 0, 357, 80005, 0, 5, 2, "そ、そこまで言うなら…\nしゃなにゅわきゃあっ！", 0, 106001, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "キャル", 0, 358, 80005, 0, 6, 2, "いたたた…もぉお～！\nなんであたしがこんな目に～！", 0, 106001, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "コッコロ", 0, 359, 80005, 0, 7, 2, "まぁまぁ\n記念日までに練習すれば\nできるようになるかもしれません\nよろしければお教えいたしますよ", 0, 105901, 1);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "キャル", 0, 360, 80005, 0, 7, 2, "よし！\nあんたたちができて\nあたしだけできないのはしゃくだし\nやってやろうじゃないの！！", 0, 106001, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "コッコロ", 0, 361, 80005, 0, 8, 3, "3.5周年まであと12日\n記念日当日はもちろんですが\nその日を待つ時間というのも\n心躍ります", 0, 105901, 1);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "ペコリーヌ", 0, 362, 80005, 0, 8, 3, "分かりますっ！\nどんな特別な料理を食べられるのか\n考えるだけでワクワクしますよね！", 0, 105801, 3);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "コッコロ", 0, 363, 80005, 0, 9, 3, "特別な料理といえば、お祭りですね\nそういえば主さまは学生として\n聖学祭に参加されたこと\nがあるとか…", 0, 105901, 1);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "シェフィ", 0, 364, 80005, 0, 7, 3, "聖学祭…って、なにかしら？", 0, 106401, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "コッコロ", 0, 365, 80005, 0, 10, 3, "聖テレサ女学院の祭典ですよ\n二つのチームが競い合い\n青春を満喫するのが趣旨のようです", 0, 105901, 1);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "キャル", 0, 366, 80005, 0, 8, 3, "それってカルミナの\nサプライズライブがあった\nお祭りよね？\nちょっと見たかったかも…", 0, 106001, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "キャル", 0, 367, 80005, 0, 9, 3, "カルミナの後の\nライブも好評だったらしいじゃない\n確か、なかよし部\nだったっけ…？", 0, 106001, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "ペコリーヌ", 0, 368, 80005, 0, 9, 3, "なかよし部…\n仲が良さそうな名前で\nやばいですね☆", 0, 105801, 3);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "キャル", 0, 369, 80005, 0, 10, 3, "いや、そのまんますぎでしょ…", 0, 106001, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "シェフィ", 0, 370, 80005, 0, 8, 3, "聖学祭かぁ…なんだか楽しそうね\nそれになかよし部の人たちも\n気になるわ", 0, 106401, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "ペコリーヌ", 0, 371, 80005, 0, 10, 3, "それじゃあわたしたちも、\n負けじと仲良くしましょう♪\nほらほらシェフィちゃん、\nぎゅーっ☆　ですよ♪", 0, 105801, 3);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "シェフィ", 0, 372, 80005, 0, 9, 3, "えっ？　ええ…\nぎゅーっ…？", 0, 106401, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "キャル", 0, 373, 80005, 0, 11, 4, "3.5周年まであと11日！？\nお祝いの準備進んでないんだけど…\nああ～昨日に戻りたいわ…", 0, 106001, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "シェフィ", 0, 374, 80005, 0, 10, 4, "そういえばそんな魔道具があるって\n聞いたことがあるわね\n確か、名前はタイムラーベ…", 0, 106401, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "キャル", 0, 375, 80005, 0, 12, 4, "なにそれヤバいわね\nでも本当にあるんだったら\n準備よりももっとやり直したいこと\nたくさんあるんだけど…", 0, 106001, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "コッコロ", 0, 376, 80005, 0, 11, 4, "もしや正月の件でしょうか…？\nあの時はお金がなくて\n苦労しましたから", 0, 105901, 1);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "キャル", 0, 377, 80005, 0, 13, 4, "あれはそうね…\nお金を湯水のように使う\nぺコリーヌを止められたら\nあんな苦労をすることもなかったし", 0, 106001, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "ペコリーヌ", 0, 378, 80005, 0, 11, 4, "そんなこともありましたね！\nやり直したいといえば\n『きゃるきゃる～ん♪』\nの方かと思いましたよ！", 0, 105801, 3);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "キャル", 0, 379, 80005, 0, 14, 4, "バラすんじゃないわよ\nこのアホリーヌ！\nシェフィはそれ\n知らないんだから！", 0, 106001, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "キャル", 0, 380, 80005, 0, 15, 4, "こうなったらもう、なんとか\nタイムラーべを探し出して\n今の会話をなかったことに…\nって、どうしたのよシェフィ", 0, 106001, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "シェフィ", 0, 381, 80005, 0, 11, 4, "うん…もし、私だったら…\n私が知らないみんなとの時間を\n見てみたいな、って…", 0, 106401, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "ペコリーヌ", 0, 382, 80005, 0, 12, 4, "シェフィちゃん！　タイムなんとか\nなんかなくても大丈夫ですよ！\n一緒の時間はこれから\nどんどん作ればいいんですから！", 0, 105801, 3);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "コッコロ", 0, 383, 80005, 0, 12, 4, "はい、今この瞬間も\n未来にはかけがえのない思い出に\nなっていることでしょう", 0, 105901, 1);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "キャル", 0, 384, 80005, 0, 16, 4, "だからそんな顔しないの\nほら、お祝いの準備、進めるわよ！", 0, 106001, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "シェフィ", 0, 385, 80005, 0, 12, 4, "みんな…そうね\n最高のお祝いにして\n最高の思い出にしましょう！", 0, 106401, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "ペコリーヌ", 0, 386, 80005, 0, 13, 5, "3.5周年まで、あと10日！\nもうすぐですよー！\n楽しみすぎて、やばいですね！", 0, 105801, 3);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "ペコリーヌ", 0, 387, 80005, 0, 14, 5, "そういえば…たくさんのおもちゃが\n子どもたちを襲うっていう\n騒動もありましたね…\nなんでも勝手に動き出したとか！", 0, 105801, 3);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "ペコリーヌ", 0, 388, 80005, 0, 15, 5, "学校や孤児院とかに\nちっちゃなおもちゃが乗り込んで\n暴れたみたいですよ！", 0, 105801, 3);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "キャル", 0, 389, 80005, 0, 17, 5, "暴れたって言っても\n所詮はおもちゃじゃない\nどうせちっさいんでしょ？", 0, 106001, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "キャル", 0, 390, 80005, 0, 18, 5, "だったら魔法で\n全部ぶっ飛ばせばいいじゃない！\n大したことないわよ！", 0, 106001, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "シェフィ", 0, 391, 80005, 0, 13, 5, "キャルってば…\n相手は子どもが\n大事にしてる物なのよ…？", 0, 106401, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "コッコロ", 0, 392, 80005, 0, 13, 5, "乱暴な発言は\nめっ、でございますよ？\n子どもの教育に悪影響を\n与えてしまいますから…", 0, 105901, 1);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "ペコリーヌ", 0, 393, 80005, 0, 16, 5, "キャルちゃんだって\n意外と猫グッズとか\nたっくさん集めてますよね？\nもしその子たちが暴れたら…？", 0, 105801, 3);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "キャル", 0, 394, 80005, 0, 19, 5, "う、うっさいわね！\n意外とか言うな！\n…ま、まあ確かに正面切って\n魔法でぶっ飛ばせないかもね…", 0, 106001, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "ペコリーヌ", 0, 395, 80005, 0, 17, 5, "あはは☆\nキャルちゃんのそういうところ\n好きですよ♪", 0, 105801, 3);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "ペコリーヌ", 0, 396, 80005, 0, 18, 5, "そんなキャルちゃんに猫グッズを\nプレゼントしちゃいますね！\n一部で流行っていた、たい焼き屋の\nおまけフィギュアですよ♪", 0, 105801, 3);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "ペコリーヌ", 0, 397, 80005, 0, 19, 5, "乱暴に扱ったりしないでくださいね\nひとりでに暴れ出しちゃうかも\nしれませんから！", 0, 105801, 3);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "カリン", 0, 398, 80005, 0, 4, 6, "3.5周年まであと９日\nここでみなさんに\nビックニュースですよ！", 0, 190801, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "カリン", 0, 399, 80005, 0, 5, 6, "3.5周年を祝う生放送に\n去年と同様【美食殿】の\nみなさんにご出演いただくことが\n決定しましたー！", 0, 190801, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "キャル", 0, 400, 80005, 0, 20, 6, "…えっ！？", 0, 106001, 1);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "ペコリーヌ", 0, 401, 80005, 0, 20, 6, "本当ですか！？\nそれじゃ今から張り切って\n生放送中に食べる魔物料理を\n作らなくちゃですね☆", 0, 105801, 3);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "シェフィ", 0, 402, 80005, 0, 14, 6, "いや…がんばるところは\nそこじゃないような…\nというか去年も出演してたのね\nすごいじゃない！", 0, 106401, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "コッコロ", 0, 403, 80005, 0, 14, 6, "はい。とても緊張しますが\nやりがいのある大役です\n一緒にがんばりましょうね\nシェフィさま", 0, 105901, 4);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "シェフィ", 0, 404, 80005, 0, 15, 6, "わ、私も出るの！？　でも…\n私も【美食殿】の一人だものね\nそれなら今からしっかり準備をして\nコンディションを整えないと…", 0, 106401, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "キャル", 0, 405, 80005, 0, 21, 6, "ふふん♪\n生放送直前のカチコチになった\nシェフィの姿が楽しみだわ！", 0, 106001, 1);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "ペコリーヌ", 0, 406, 80005, 0, 21, 6, "去年のキャルちゃんは\n緊張しすぎてずっと震えて\nかわいかったですもんね！\n今年もあの姿が見たいです！", 0, 105801, 3);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "キャル", 0, 407, 80005, 0, 22, 6, "ぶっ殺すぞ！？", 0, 106001, 1);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "カリン", 0, 408, 80005, 0, 6, 6, "というわけで\n『プリンセスコネクト！\nRe:Dive』の魅力を\nお伝えする生放送をお楽しみに♪", 0, 190801, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "コッコロ", 0, 409, 80005, 0, 15, 7, "3.5周年まであと８日\nでございますよ\n楽しみですね、主さま", 0, 105901, 1);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "キャル", 0, 410, 80005, 0, 23, 7, "そういえばあんた\n夏休みの宿題は\nきちんとやってるの？", 0, 106001, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "シェフィ", 0, 411, 80005, 0, 16, 7, "宿題…学校に通うのも\n楽しいことばかりじゃ\nないのね", 0, 106401, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "ペコリーヌ", 0, 412, 80005, 0, 22, 7, "あれ？　そういえば\n少し前にルーセント学院で\n何かあったような…", 0, 105801, 3);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "キャル", 0, 413, 80005, 0, 24, 7, "ああ、始業式の日に\n大捕り物があったって\n話題になってたわね", 0, 106001, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "シェフィ", 0, 414, 80005, 0, 17, 7, "夏休みを終わらせないって\n騒いでたっていう話？\nあれ本当なの？\n都市伝説だと思ってたわ", 0, 106401, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "コッコロ", 0, 415, 80005, 0, 16, 7, "その一件ならよく存じ上げて\nおります。ですから主さま\n次の機会にはわたくしと一緒に\nがんばりましょう", 0, 105901, 1);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "シェフィ", 0, 416, 80005, 0, 18, 7, "楽しそう…私も一緒にやりたいな\nママ…じゃ、じゃなくて！\nコッコロさん、私もまざっていい？", 0, 106401, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "コッコロ", 0, 417, 80005, 0, 17, 7, "もちろんでございます\n仲良くお勉強いたしましょう", 0, 105901, 1);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "キャル", 0, 418, 80005, 0, 25, 7, "しれっと教育ママが\n生まれてる…？", 0, 106001, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "ペコリーヌ", 0, 419, 80005, 0, 23, 7, "お勉強は大切ですよ！\nキャルちゃんも一緒に\nがんばりましょう！", 0, 105801, 3);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "キャル", 0, 420, 80005, 0, 26, 7, "え～～～～！？", 0, 106001, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "キャル", 0, 421, 80005, 0, 27, 8, "3.5周年まであと７日〜♪\nふふ～ん♪\nふんふふ～ん♪", 0, 106001, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "シェフィ", 0, 422, 80005, 0, 19, 8, "…\nねぇコッコロさん\nキャル、どうしちゃったの？", 0, 106401, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "コッコロ", 0, 423, 80005, 0, 18, 8, "とてもご機嫌そうに\n鼻歌を歌ってらっしゃいますね\n耳も尻尾もリズミカルに\n揺れて…ノリノリでございます", 0, 105901, 1);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "ペコリーヌ", 0, 424, 80005, 0, 24, 8, "あれはですね！\nカルミナのみなさんの\nサマーライブのチケットが\n手に入ったからなんですよ♪", 0, 105801, 3);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "シェフィ", 0, 425, 80005, 0, 20, 8, "ええっ！？\nあのライブのチケットって確か\n入手困難だったんじゃ…", 0, 106401, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "キャル", 0, 426, 80005, 0, 28, 8, "ふふふふ～ん♪\nしゃいにんぐふゅ～ちゃ～♪", 0, 106001, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "コッコロ", 0, 427, 80005, 0, 19, 8, "なるほど…\nキャルさまがあのように\nなってしまうのも頷けますね", 0, 105901, 1);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "シェフィ", 0, 428, 80005, 0, 21, 8, "…カルミナの\n『Shining Future』\nの中に、シャイニングフューチャー\nって歌詞はなかったはずだけど…", 0, 106401, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "キャル", 0, 429, 80005, 0, 29, 8, "ふんふ…はっ！？\nあっ、あんたたち！\nなに人の鼻歌を\n勝手に聞いてるのよ！？", 0, 106001, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "キャル", 0, 430, 80005, 0, 30, 8, "こっ、これは別に\nカルミナの曲じゃなくて…\nあたしのオリジナルソングよ！\n聞いたらお金取るわよっ！？", 0, 106001, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "ペコリーヌ", 0, 431, 80005, 0, 25, 8, "えへへ、キャルちゃん\nカルミナのサマーライブ\n思いっきり楽しみましょうね☆", 0, 105801, 3);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "キャル", 0, 432, 80005, 0, 31, 8, "も～っ！\n生暖かい目で見るな～～！\nぶっ殺すわよ～～～！", 0, 106001, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "シェフィ", 0, 433, 80005, 0, 22, 9, "3.5周年まであと６日\n３人も言ってたけど\nあっという間だったわね", 0, 106401, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "シェフィ", 0, 434, 80005, 0, 23, 9, "…えっ？\n生放送の意気込み！？\n突然なに！？", 0, 106401, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "シェフィ", 0, 435, 80005, 0, 24, 9, "ええと…\nとにかく失敗しないように\n台本たくさん読んで…\nそれから…", 0, 106401, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "シェフィ", 0, 436, 80005, 0, 25, 9, "えっ！？\nトラブルがあったら\nどうするのかって！？\nそ、それは…！", 0, 106401, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "シェフィ", 0, 437, 80005, 0, 26, 9, "とにかく落ち着いて\nスタッフさんの指示を聞いて…", 0, 106401, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "シェフィ", 0, 438, 80005, 0, 27, 9, "それから、それから…！\nぅ、ぅぅっ…！", 0, 106401, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "シェフィ", 0, 439, 80005, 0, 28, 9, "―びえぇぇ～んっ！！！", 0, 106401, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "コッコロ", 0, 440, 80005, 0, 20, 9, "どうされましたか、シェフィさま", 0, 105901, 1);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "シェフィ", 0, 441, 80005, 0, 29, 9, "ううっ…ひっく…\nママッ…！", 0, 106401, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "コッコロ", 0, 442, 80005, 0, 21, 9, "よしよし、緊張されてるのですね\nママが傍におりますよ\n主さま、少々お待ちくださいませ", 0, 105901, 1);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "コッコロ", 0, 443, 80005, 0, 22, 9, "…ふぅ\nシェフィさま\n生放送への意気込み\nお話しできそうですか？", 0, 105901, 1);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "シェフィ", 0, 444, 80005, 0, 30, 9, "…ううっ\n恥ずかしすぎて\nもう生放送出られない…", 0, 106401, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "コッコロ", 0, 445, 80005, 0, 23, 10, "主さま\n3.5周年まであと５日\nでございます", 0, 105901, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "コッコロ", 0, 446, 80005, 0, 24, 10, "ですがその前に\n生放送がありますね", 0, 105901, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "コッコロ", 0, 447, 80005, 0, 25, 10, "…生放送に向けた\n意気込みでございますか？", 0, 105901, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "コッコロ", 0, 448, 80005, 0, 26, 10, "もちろんわたくしは\n主さまのために\n全身全霊を込めて\n楽しい時間をご提供いたします", 0, 105901, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "コッコロ", 0, 449, 80005, 0, 27, 10, "…と、意気込んでみましたが\nうまくできるか少し不安です\n声が小さくなったり\n間違えてしまったらどうしようと…", 0, 105901, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "コッコロ", 0, 450, 80005, 0, 28, 10, "ですがわたくしは\n一人ではありません\n【美食殿】のみなさまが\nおります", 0, 105901, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "コッコロ", 0, 451, 80005, 0, 29, 10, "どんな困難も\nみなさまと一緒なら\n乗り越えられると\nわたくし、確信しております", 0, 105901, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "コッコロ", 0, 452, 80005, 0, 30, 10, "それに今回はシェフィさまも\nご参加されるわけですし\nママとしてお手本に\nならねばなりませんね", 0, 105901, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "コッコロ", 0, 453, 80005, 0, 31, 10, "主さま。3.5周年も\n食卓を囲んでいる時のような\n楽しい思い出を\n一緒に作りましょうね", 0, 105901, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "ペコリーヌ", 0, 454, 80005, 0, 26, 11, "3.5周年まであと４日！\nやばいですね☆", 0, 105801, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "キャル", 0, 455, 80005, 0, 32, 11, "はいはい、やばいわね\nというかもうすぐ生放送だけど\nあんた準備はできてるの？", 0, 106001, 1);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "ペコリーヌ", 0, 456, 80005, 0, 27, 11, "ばっちりオイッスー☆　ですよ！\n『王家の装備』も全開です！", 0, 105801, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "ペコリーヌ", 0, 457, 80005, 0, 28, 11, "あとは前回を超える\n最高のオイッスー☆　を\nお届けできるように\n練習あるのみですかね！", 0, 105801, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "ペコリーヌ", 0, 458, 80005, 0, 29, 11, "キャルちゃんは\nどうなんですか？\nオイッスー☆　を\n毎日ちゃんと練習してますか？", 0, 105801, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "キャル", 0, 459, 80005, 0, 33, 11, "はぁ！？　なんであたしまで\nその変なあいさつを練習しないと\nいけないのよ！", 0, 106001, 1);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "ペコリーヌ", 0, 460, 80005, 0, 30, 11, "もしかしてやってないんですか？\nダメですよキャルちゃん！\nシェフィちゃんだってちゃんと\n毎日練習してるのに！", 0, 105801, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "ペコリーヌ", 0, 461, 80005, 0, 31, 11, "こうなったらわたしがキャルちゃん\nにオイッスー☆　の『オ』から\nしっかり教えてあげないと！", 0, 105801, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "ペコリーヌ", 0, 462, 80005, 0, 32, 11, "オイッスー☆　の『オ』は\n口を大きく開いて〜\nドラゴンの骨つき肉に\nかぶりつく直前をイメージして〜", 0, 105801, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "キャル", 0, 463, 80005, 0, 34, 11, "だからあたしはやるなんて一言も…", 0, 106001, 1);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "ペコリーヌ", 0, 464, 80005, 0, 33, 11, "オイッスー☆", 0, 105801, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "キャル", 0, 465, 80005, 0, 35, 11, "聞きなさいっての！！", 0, 106001, 1);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "シェフィ", 0, 466, 80005, 0, 31, 12, "3.5周年まであと３日…\nそして生放送は、明日…", 0, 106401, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "シェフィ", 0, 467, 80005, 0, 32, 12, "もっと台本を\n読み込んでおかないと…\nあと発声練習も…", 0, 106401, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "シェフィ", 0, 468, 80005, 0, 33, 12, "おいっす！\n…こうじゃなかったわね\nええと確か…", 0, 106401, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "シェフィ", 0, 469, 80005, 0, 34, 12, "うう…あんなに練習したのに\nこれじゃみんなに迷惑かけちゃう…", 0, 106401, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "シェフィ", 0, 470, 80005, 0, 35, 12, "【美食殿】の一員として\n認められるように\n頑張らないといけないのに\nよし、もう一度…", 0, 106401, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "ペコリーヌ", 0, 471, 80005, 0, 34, 12, "やってますねシェフィちゃん！", 0, 105801, 3);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "キャル", 0, 472, 80005, 0, 36, 12, "なによ、考えることは\nみんな同じってこと？", 0, 106001, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "コッコロ", 0, 473, 80005, 0, 32, 12, "ふふ。シェフィさま\nよろしければ\n仲間に入れてください\n一緒にやればきっとうまくできます", 0, 105901, 1);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "シェフィ", 0, 474, 80005, 0, 36, 12, "みんな…もちろんよ！", 0, 106401, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "ペコリーヌ", 0, 475, 80005, 0, 35, 12, "それじゃ明日の生放送に向けて\n一緒に練習がんばりましょう☆", 0, 105801, 3);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "カリン", 0, 476, 80005, 0, 7, 13, "3.5周年まであと２日と\n迫った本日は…\nいよいよ生放送です！", 0, 190801, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "カリン", 0, 477, 80005, 0, 8, 13, "【美食殿】のみなさんが\n準備に入られていますので\nそれぞれ繋いでみましょう！\nまずはペコリーヌさんです！", 0, 190801, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "ペコリーヌ", 0, 478, 80005, 0, 36, 13, "今日もオイッスー☆\nみんなで一緒に\nオイッスー☆", 0, 105801, 3);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "カリン", 0, 479, 80005, 0, 9, 13, "お手本になるような\n発声練習ですね！\nそれでは続いてコッコロさん", 0, 190801, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "コッコロ", 0, 480, 80005, 0, 33, 13, "はじめちょろちょろ\nなかぱっぱ～♪\n生放送前にみなさまで\n食卓を囲んで英気を養いましょう", 0, 105901, 4);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "カリン", 0, 481, 80005, 0, 10, 13, "さすが【美食殿】のママですね！\nそしてキャルさんは…", 0, 190801, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "キャル", 0, 482, 80005, 0, 37, 13, "大丈夫…大丈夫…\nあたしは大丈夫…", 0, 106001, 1);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "カリン", 0, 483, 80005, 0, 11, 13, "す、すごく震えてますけど\n大丈夫でしょうか…？\nそして今回初参加となる\n期待の新人シェフィさん！", 0, 190801, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "シェフィ", 0, 484, 80005, 0, 37, 13, "あうー？", 0, 106401, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("105901,34,106001,38,190801,12", "みんな", 1, 485, 80005, 0, 37, 13, "あ…", 0, 105801, 4);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "キャル", 0, 486, 80005, 0, 39, 13, "ちょ、ちょっとシェフィ！？\nなに赤ちゃん返りしてん―\nあ痛ぁっ！\n噛んだわこいつーっ！", 0, 106001, 1);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "コッコロ", 0, 487, 80005, 0, 35, 13, "シェフィさま\n緊張しすぎて\nしまったのでしょうね…\nおーよしよし…", 0, 105901, 4);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "ペコリーヌ", 0, 488, 80005, 0, 38, 13, "シェフィちゃん！\nそういう時は手のひらに\n好きな文字を書いて\n食べちゃうといいですよ！", 0, 105801, 3);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "カリン", 0, 489, 80005, 0, 13, 13, "…ええと、現場からは以上です！\n生放送はもうすぐ！\n特別なお知らせもありますので\nどうぞお楽しみに！", 0, 190801, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "シェフィ", 0, 490, 80005, 0, 38, 14, "3.5周年の記念日も\nいよいよ明日ね\n…って、あれ？", 0, 106401, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "ペコリーヌ", 0, 491, 80005, 0, 39, 14, "記念日はこの森へ行きませんか！？\nすっごく美味しいお肉が\nとれるんです☆", 0, 105801, 3);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "キャル", 0, 492, 80005, 0, 40, 14, "うげ、またあの魔物？\nさすがに今度は\n丸焼きとかはやめてよね…\n見た目がヤバすぎるし", 0, 106001, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "コッコロ", 0, 493, 80005, 0, 36, 14, "ふふ、キャルさまもだいぶ\n魔物肉に慣れてきたようですね\n調理ならばわたくしに\nお任せください♪", 0, 105901, 1);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "シェフィ", 0, 494, 80005, 0, 39, 14, "みんな、何をしているの？\nなんだか、\n不穏な単語が聞こえたんだけど…", 0, 106401, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "ペコリーヌ", 0, 495, 80005, 0, 40, 14, "シェフィちゃん！\n明日の食べ尽くしツアーの\n相談をしてたんですよ！", 0, 105801, 3);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "コッコロ", 0, 496, 80005, 0, 37, 14, "はい。明日は【美食殿】の\n一員となったシェフィさまと一緒に\n初めて記念日を過ごすのですから\n特別なコース料理を用意しませんと", 0, 105901, 1);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "キャル", 0, 497, 80005, 0, 41, 14, "ふふん♪\n猫の毛も逆立つような\nあれこれを食べさせられるから\n覚悟しておいたほうがいいわよ？", 0, 106001, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "シェフィ", 0, 498, 80005, 0, 40, 14, "へ、へぇ…", 0, 106401, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "ペコリーヌ", 0, 499, 80005, 0, 41, 14, "期待しててくださいね！\nシェフィちゃんとあなたに\n『おいちー！』って喜んでもらえる\n最高のごちそうを準備しますから☆", 0, 105801, 3);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "シェフィ", 0, 500, 80005, 0, 41, 14, "ええと…その…うーんと…\nわ、分かったわ\n私も【美食殿】の一員だもの", 0, 106401, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "シェフィ", 0, 501, 80005, 0, 42, 14, "それにあなたも一緒みたいだし\n最後までがんばってみるわ…！", 0, 106401, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "ペコリーヌ", 0, 502, 80005, 0, 42, 15, "それではいつものあれを\nやりましょう！\n準備はいいですかっ？\n…いっきますよー！", 0, 105801, 3);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "キャル", 0, 503, 80005, 0, 42, 15, "祝！", 0, 106001, 1);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "コッコロ", 0, 504, 80005, 0, 38, 15, "3.5周年！", 0, 105901, 4);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "シェフィ", 0, 505, 80005, 0, 43, 15, "おめでとうございます！", 0, 106401, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "カリン", 0, 506, 80005, 0, 14, 15, "この日を迎えられたのも\nあなたが『プリンセスコネクト！\nRe:Dive』を遊んでくれている\nおかげです！", 0, 190801, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "カリン", 0, 507, 80005, 0, 15, 15, "ということで\nみなさんからあなたへ一言ずつ\n感謝の言葉をどうぞ！", 0, 190801, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "ペコリーヌ", 0, 508, 80005, 0, 43, 15, "感謝の言葉ですか？　わたしは\n毎日あなたに感謝してますよ\n【美食殿】の仲間になってくれて…\n一緒にごはんを食べてくれて…", 0, 105801, 3);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "ペコリーヌ", 0, 509, 80005, 0, 44, 15, "なにより今でもわたしをぺコリーヌ\nって呼んでくれてありがとうござい\nます。これからもわたしはあなたの\nぺコリーヌです！　やばいですね☆", 0, 105801, 3);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "キャル", 0, 510, 80005, 0, 43, 15, "えっ！？　今度はあたし！？\n感謝って…まぁ、そうね…\nあんたにはあたしが\n辛くて苦しい時に助けてもらって…", 0, 106001, 1);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "キャル", 0, 511, 80005, 0, 44, 15, "でもおかげで色々吹っ切れたわ！\nもしあんたが困ってたら今度は\nあたしが助けてあげるから\nその時はちゃんと言いなさいよ！", 0, 106001, 1);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "シェフィ", 0, 512, 80005, 0, 44, 15, "ええと…次は私ね\n実はよく覚えてないのだけど\n空から落ちてきた私を受け止めて\nくれたのはあなたなのでしょう？", 0, 106401, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "シェフィ", 0, 513, 80005, 0, 45, 15, "最初に私を見つけてくれたのが\nあなたで良かったって思ってるの\nおかげで私は居場所を見つけたから\nあなたがくれたもの、大切にするわ", 0, 106401, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "コッコロ", 0, 514, 80005, 0, 39, 15, "では最後にわたくしから…\nわたくしは主さまの元を離れた日…\nこのように記念日をお祝いする\nことは二度とないと思っていました", 0, 105901, 4);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "コッコロ", 0, 515, 80005, 0, 40, 15, "ですが、主さまはわたくしを\n迎えにきてくださり、今もこうして\nおそばにおいてくださっています\nわたくしにとって毎日が記念日です", 0, 105901, 4);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "コッコロ", 0, 516, 80005, 0, 41, 15, "わたくしたちの物語は\nまだ続いていきます\nこれからも一緒にページを\n作っていきましょうね", 0, 105901, 4);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "ペコリーヌ", 0, 517, 80005, 0, 45, 15, "それじゃ\nいつものあれでしめますよ！\nみんなで一緒に、さんはい☆", 0, 105801, 3);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("105901,42,106001,45,106401,46,190801,16", "みんな", 1, 518, 80005, 0, 46, 15, "これからも\nよろしくおねがいします！", 0, 105801, 4);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "カリン", 0, 519, 60003, 1, 1, 0, "新年を迎え、新たに始まる一日を\nおみくじで占ってみませんか？\n良い運勢であるほど、\n沢山のプレゼントが貰えますよ♪", 0, 190804, 1);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "", 0, 520, 60003, 1, 0, 0, "今日の運勢は姫吉！　どんな不可能も可能にできちゃう！　　　　仲間と食卓を囲めばさらに運気上昇よ！　ただし魔物料理以外！", 1, 0, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "", 0, 521, 60003, 1, 0, 0, "今日の運勢は姫吉！　やること全てが最高潮！　やばいですね☆　ラッキーアイテムはおにぎり！　お腹一杯で幸せが続くでしょう！", 1, 0, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "", 0, 522, 60003, 1, 0, 0, "今日の運勢は姫吉！　羽が生えたように飛躍できる絶好調の運気！新しいことに挑戦するなら今しかないわ！　やってみましょう！", 1, 0, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "", 0, 523, 60003, 1, 0, 0, "今日の運勢は姫吉！　精霊も新年の幸福をお祝いしています！　　主さまの笑顔が見られて、わたくしも幸せいっぱいでございます♪", 1, 0, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "", 0, 524, 60003, 2, 0, 0, "今日の運勢は姫吉！　やること全てが上手くいく最高の一日！　　あなたの活躍に先生もにっこりです。いい子いい子♪", 1, 0, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "", 0, 525, 60003, 1, 0, 0, "今日の運勢は大吉！　お裁縫好きな女の子のお墨付きです！　　　お世話したがりの妹にめいっぱい愛されちゃうかも！？", 2, 0, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "", 0, 526, 60003, 1, 0, 0, "今日の運勢は大吉！　ラッキーカラーは赤！　　　　　　　　　　湧き上がるアイデアの数々に、高笑いが止まりませんわ！", 2, 0, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "", 0, 527, 60003, 1, 0, 0, "今日の運勢は大吉！　ラッキーカラーは鮮やかな黄色！　　　　　さらにシマシマのアイテムを持つと、運気倍増らしいッスよ♪", 2, 0, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "", 0, 528, 60003, 1, 0, 0, "今日の運勢は大吉！　秘められた力が開花するでしょう！　　　　じゃあ、私の龍眼と力比べしてみようか？　ふふ、冗談だよ～", 2, 0, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "", 0, 529, 60003, 2, 0, 0, "今日の運勢は大吉！　悩んでいたことが解決しそうです！　　　　たまには勉強も仕事も忘れて、心あたたまる家族と過ごしましょ！", 2, 0, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "", 0, 530, 60003, 1, 0, 0, "今日の運勢は中吉！　家の中でゆっくり過ごすのがよさそう！　　ラッキーアイテムはゲームよ！　ベストスコアを更新できるかも？", 3, 0, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "", 0, 531, 60003, 1, 0, 0, "今日の運勢は中吉！　上手くなりたいことに積極的に挑戦してみてサックス好きの妹が、手取り足取りサポートしてくれるかも～？", 3, 0, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "", 0, 532, 60003, 1, 0, 0, "今日の運勢は中吉！　仕事運が急上昇中です！　　　　　　　　　きっと「煉獄に舞う一陣の疾風」の如き活躍ができますよ！", 3, 0, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "", 0, 533, 60003, 1, 0, 0, "今日の運勢は中吉！　日頃の努力が報われます！　　　　　　　　親しい友人がいるなら報告してみて。一緒に喜んでくれるはずだ。", 3, 0, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "", 0, 534, 60003, 1, 0, 0, "今日の運勢は中吉！　ラッキーアイテムはかわいいもの！　　　　ア、アタシはかわいいじゃなくてキレイだってば！　も～っ！", 3, 0, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "", 0, 535, 60003, 1, 0, 0, "今日の運勢は中吉！　チャンスを待つと良い結果に！　　　　　　ラッキーアイテムのプリンを食べて、運を呼び込みましょう！", 3, 0, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "", 0, 536, 60003, 2, 0, 0, "今日の運勢は中吉！　悩まず行動すれば強運に恵まれる！　　　　守るよりも攻めるが吉だ！　勝負事をするにはいい運勢だな！", 3, 0, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "", 0, 537, 60003, 1, 0, 0, "今日の運勢は小吉！　まだ見ぬ危険が隠れているかも！　　　　　でも安心しろ！　提督が貴公を守ってくれるはずだぞ！", 4, 0, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "", 0, 538, 60003, 1, 0, 0, "今日の運勢は小吉！　いろんな事件が起こる一日になりそう！　　状況をよく観察してみようじゃないか！　まさに探偵のごとくね！", 4, 0, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "", 0, 539, 60003, 1, 0, 0, "今日の運勢は小吉！　気の向くままに過ごすのが幸せへの近道！　美味い魚を食べて猫を眺めて、自然体で過ごすのはどうだい？", 4, 0, 2);
