INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "カリン", 0, 599, 60005, 0, 3, 7, "おみくじは楽しんで頂けましたか？\nあなたの新たなる年が\n素晴らしいものになるよう、\n心からお祈りいたします", 0, 190804, 3);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "カリン", 0, 600, 60006, 1, 1, 0, "新年を迎え、新たに始まる一日を\nおみくじで占ってみませんか？\n良い運勢であるほど、\n沢山のプレゼントが貰えますよ♪", 0, 190804, 1);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "", 0, 601, 60006, 2, 0, 0, "今日の運勢は姫吉！　勇気を持って、新しいことに挑戦しよう！\n幸先がいいねえ少年！　今年もいい一年になるよう祈ってるよ。", 1, 0, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "", 0, 602, 60006, 2, 0, 0, "今日の運勢は姫吉！　どんなピンチも簡単に乗り越えられそう！\nよい結果が出たようで何よりです。さて、待人の方は……ふふ。", 1, 0, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "", 0, 603, 60006, 2, 0, 0, "今日の運勢は姫吉！　あの人と仲直り……できちゃうかも！？\nはあ……この私があなたの運勢に興味を抱くわけないでしょう？", 1, 0, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "", 0, 604, 60006, 1, 0, 0, "今日の運勢は大吉！　身近な友達との絆が深まる予感……？\n騎士様は、どなたと友誼を結ぶのでしょう？　気になります……", 2, 0, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "", 0, 605, 60006, 2, 0, 0, "今日の運勢は大吉！　ラッキーアイテムは「蛇の抜け殻」！\nこのアタシにお目にかかれるなんて……アンタ、幸せものね♪", 2, 0, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "", 0, 606, 60006, 2, 0, 0, "今日の運勢は中吉！　気になるあの人に会えちゃうかも！？\n『初夢を正夢にする枕』であなたに会え……な、なんでもない！", 3, 0, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "", 0, 607, 60006, 2, 0, 0, "今日の運勢は中吉！　子供に戻って、一日中遊びつくそう！\nお年玉でけん玉ゲット！　おじちゃん、もしかめ一万回やって！", 3, 0, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "", 0, 608, 60006, 1, 0, 0, "今日の運勢は中吉！　今日はおうちで、ゆーっくりくつろごう。\nお雑煮よし。みかんよし。さ、こたつで寝正月だ。君も入る？", 3, 0, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "", 0, 609, 60006, 2, 0, 0, "今日の運勢は小吉！　突然のドッキリに注意！\nおや、後ろに髪の長い女の霊が──フフ、冗談です。私ですよ。", 4, 0, 2);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", "", 0, 610, 60006, 2, 0, 0, "今日の運勢は小吉。慌てなくても、幸運はきっとやってくる。\n運は自分で掴むもの──媛が言ってました。でも、どうやって？", 4, 0, 2);
