INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 0, 190804, 1, 0, "新年を迎え、新たに始まる一日を\nおみくじで占ってみませんか？\n良い運勢であるほど、\n沢山のプレゼントが貰えますよ♪", 1, "カリン", 1, 1, 60000);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (1, "", 0, 0, 1, 0, "今日の運勢は姫吉！　あらゆる運気が最大を示しています！　　　今なら「伝説の吸血鬼」をも従え、世界征服も夢じゃありません！", 0, "", 2, 2, 60000);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (1, "", 0, 0, 1, 0, "今日の運勢は姫吉！　やること全てが最高潮！　やばいですね☆　ラッキーアイテムはおにぎり！　お腹一杯で幸せが続くでしょう！", 0, "", 2, 3, 60000);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (1, "", 0, 0, 2, 0, "今日の運勢は姫吉！　その高い運気はまさにショーグンの如く！　その溢れる威光は人々を惹きつけ、皆の人気者間違いなしです！", 0, "", 2, 4, 60000);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (2, "", 0, 0, 1, 0, "今日の運勢は大吉！　運気がどんどん高まっています！　　　　　何をやっても、最大効率で素晴らしい結果になりますよ♪", 0, "", 2, 5, 60000);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (2, "", 0, 0, 1, 0, "今日の運勢は大吉！　なにをやっても上手くいって大活躍！　　　そんな素敵なあなたを見て、後ろから誰かが……クスクス♪", 0, "", 2, 6, 60000);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (2, "", 0, 0, 2, 0, "今日の運勢は大吉！　運気が高く、何かに挑戦すると良い結果に！隠れた才能が引き出されそう！　目指せヒデサ……ではなく秀才！", 0, "", 2, 7, 60000);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (3, "", 0, 0, 1, 0, "今日の運勢は中吉！　商売運が上がってきています！　　　　　　ラッキーアイテムはたいやき！　和菓子パワーで売上アップです！", 0, "", 2, 8, 60000);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (3, "", 0, 0, 1, 0, "今日の運勢は中吉！　旅行運がぐんぐんと上昇中♪　　　　　　　もしかしたら、あの「マホマホ王国」にだって行けるかも♪", 0, "", 2, 9, 60000);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (3, "", 0, 0, 1, 0, "今日の運勢は中吉！　仕事運が急上昇中です！　　　　　　　　　きっと「煉獄に舞う一陣の疾風」の如き活躍ができますよ！", 0, "", 2, 10, 60000);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (3, "", 0, 0, 1, 0, "今日の運勢は中吉！　チャンスを待つと良い結果に！　　　　　　ラッキーアイテムのプリンを食べて、運を呼び込みましょう！", 0, "", 2, 11, 60000);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (3, "", 0, 0, 2, 0, "今日の運勢は中吉！　運気が着々と上昇中！　　　　　　　　　　精霊の加護を受けたかの如く、色々な事がうまくいきますよ♪", 0, "", 2, 12, 60000);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (4, "", 0, 0, 1, 0, "今日の運勢は小吉！　早朝に幸運の兆しあり！　　　　　　　　　もしかしたら、優しいお姉ちゃんが起こしにきてくれますよ♪", 0, "", 2, 13, 60000);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (4, "", 0, 0, 1, 0, "今日の運勢は小吉！　運気上昇中ですが、上がるまでが要注意！　ラッキーアニマルは熊！　本当の家族のように守ってくれるかも♪", 0, "", 2, 14, 60000);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (4, "", 0, 0, 1, 0, "今日の運勢は小吉！　外に出てゆっくり過ごすと運気が上昇！　　ラッキースポットは牧場。面白い出会いがありそうです♪", 0, "", 2, 15, 60000);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (4, "", 0, 0, 1, 0, "今日の運勢は小吉！　運気は高いけども、ハプニングには注意！　出かけた先で、「ふしんしゃ」扱いされてしまうかも……", 0, "", 2, 16, 60000);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (4, "", 0, 0, 2, 0, "今日の運勢は小吉！　根を詰めずにリラックスすると運気向上！　ラッキーパーソンはアイドル。歌があなたを幸せにしてくれます♪", 0, "", 2, 17, 60000);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 0, 190804, 0, 0, "おみくじの結果はどうでしたか？\n新たに始まる一日が、あなたに\nとって良い日になりますように\nまた明日も遊びに来て下さいね♪", 2, "カリン", 3, 18, 60000);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 7, 190804, 0, 0, "おみくじは楽しんで頂けましたか？\nあなたの新たなる年が\n素晴らしいものになるよう、\n心からお祈りいたします", 3, "カリン", 3, 19, 60000);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 1, 101201, 0, 0, "キミと出会ってもう\n1年もたつんだね！\nう～ん、いつも\n寝てばっかりだから、", 1, "ハツネ", 0, 20, 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 1, 101201, 0, 0, "少しでも\n寝る時間を減らして\nキミと一緒にいる\n時間増やしたいなぁ♪", 2, "ハツネ", 0, 21, 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 1, 103801, 0, 0, "あなたと知り合ってから\nもう1年…\n時がたつのは\n早いですね", 1, "シオリ", 1, 22, 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 1, 103801, 0, 0, "来年も、\n一緒にいられたら…\nうれしいな", 2, "シオリ", 1, 23, 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 2, 103601, 0, 0, "あなたと一緒にいるようになって、\nもう1年ですか…", 1, "キョウカ", 0, 24, 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 2, 103601, 0, 0, "最初の頃よりも、頼もしく\nなりましたね。私はどうですか？\n成長していますか？", 2, "キョウカ", 0, 25, 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 2, 102001, 0, 0, "おにいちゃんとあってから、\n1年たったよ～！", 1, "ミミ", 1, 26, 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 2, 102001, 0, 0, "ミミね、おにいちゃんと\nいっぱい遊べてたのしかったな～♪\nこれからもずっと\nいっしょにいてね～！", 2, "ミミ", 1, 27, 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 2, 100401, 0, 0, "にいちゃんがミソギのイタズラに\n引っかかり続けてもう1年かぁ", 1, "ミソギ", 2, 28, 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 2, 100401, 0, 0, "…これからもいっぱいミソギと\n遊んでね、にいちゃん！", 2, "ミソギ", 2, 29, 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 3, 101001, 0, 0, "もう王子はんと会うてから\n1年ぐらいたつそうどすえ\n早いもんやなあ", 1, "マホ", 0, 30, 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 3, 101001, 0, 0, "このままやと、\nうちもあっという間に\n干からびてしまいそうどすえ\nなーんてな。うふふ", 2, "マホ", 0, 31, 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 3, 104401, 0, 0, "お主と会って早1年じゃな\nこれからも存分に尽くすがよい", 1, "イリヤ", 1, 32, 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 3, 104401, 0, 0, "わらわもたまには、お主の献身に\n報いてやらぬでもない♪", 2, "イリヤ", 1, 33, 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 3, 101701, 0, 0, "もう、キミと出会って結構な時間が\nたつさ～。でも、私たちの旅は\nまだまだ始まったばかりさ～！", 1, "カオリ", 2, 34, 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 3, 101701, 0, 0, "これからも、どうぞよろしく\nお願いするさ～！", 2, "カオリ", 2, 35, 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 4, 102801, 0, 0, "あんたと出会ってからもうそんなに\nたつのね～", 1, "サレン", 0, 36, 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 4, 102801, 0, 0, "あっという間だったって感じるほど\n楽しかったわ！\nこれからも一緒に頑張りましょ！", 2, "サレン", 0, 37, 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 4, 102501, 0, 0, "出会ってから1年間、ずっと\nご迷惑をかけっぱなしで、\nすみません…", 1, "スズメ", 1, 38, 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 4, 102501, 0, 0, "これからはもっと頑張りますから\n見ていてくださいね\nできれば…\n今までよりも、私の近くで♪", 2, "スズメ", 1, 39, 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 5, 104601, 0, 0, "振り返れば、今までいろんなことが\nあったにゃあ…", 1, "タマキ", 0, 40, 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 5, 104601, 0, 0, "これからも、二人でたくさんの\n思い出をがっぽり作っていく\nにゃー！", 2, "タマキ", 0, 41, 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 5, 104801, 0, 0, "キミとの師弟関係も、\nもう1年なのね…\nでもキミに教えることは、", 1, "ミフユ", 1, 42, 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 5, 104801, 0, 0, "まだまだいっぱいあるわ\nこれからも、\nしっかりついて来てね！", 2, "ミフユ", 1, 43, 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 6, 103201, 0, 0, "あなたさまと出会ってもう1年…", 1, "アキノ", 0, 44, 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 6, 103201, 0, 0, "光陰矢のごとし、とはまさに\nこのことですわ。…思い出は決して\nお金では買えませんわね", 2, "アキノ", 0, 45, 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 6, 103401, 0, 0, "ダメな私を1年間も見捨てないで\nくれて、本当にありがとう", 1, "ユカリ", 1, 46, 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 6, 103401, 0, 0, "情けないところいっぱいみせるかも\nしれないけど、これからも一緒に\nいてくれると、うれしいな", 2, "ユカリ", 1, 47, 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 7, 107101, 0, 0, "オマエと出会ってもう1年か\nまさかこのワタシの結婚相手に\nなろうとはな…", 1, "クリスティーナ", 0, 48, 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 7, 107101, 0, 0, "ともかくこれからも\n愉しませてくれよ☆", 2, "クリスティーナ", 0, 49, 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 7, 104701, 0, 0, "君と出会ってからもう1年か。\n以前と比べて私の表情もいくらか\n柔らかくなった気がするんだが、\nどうだろう？", 1, "ジュン", 1, 50, 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 7, 104701, 0, 0, "見えないから分からないって？\nそれもそうだな…", 2, "ジュン", 1, 51, 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 8, 103701, 0, 0, "もう1年たったみたいだね\nあなたをからかってばかりだった\nけど、楽しかった♪", 1, "トモ", 0, 52, 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 8, 103701, 0, 0, "これからもよろしくね！", 2, "トモ", 0, 53, 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 8, 100501, 0, 0, "出会って、もう1年たっちゃった\nんスね。なんだか意外ッス", 1, "マツリ", 1, 54, 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 8, 100501, 0, 0, "毎日楽しくて、あっという間に過ぎ\nちゃったみたいなんスよね\nこれからも一緒に悪と戦うッス！", 2, "マツリ", 1, 55, 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 9, 100701, 0, 0, "オマエと会って1年なの！\n全然成仏もできなかったし、", 1, "ミヤコ", 0, 56, 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 9, 100701, 0, 0, "生き返りもしなかったけど、\nすごく楽しかったの～♪\nオマエとプリンがあれば、\nミヤコはハッピーなの～♪", 2, "ミヤコ", 0, 57, 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 9, 103101, 0, 0, "あなたと出会って1年なんですね\nでも、私の占いでは、私とあなたは", 1, "シノブ", 1, 58, 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 9, 103101, 0, 0, "来年も再来年も一緒にいるようです\nふふ、よろしくお願いしますね", 2, "シノブ", 1, 59, 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 10, 100901, 0, 0, "貴様が閃光のシグルドとして覚醒し\nもう1年か…。これからも我らの\n長き戦いに終わりはないが、", 1, "アンナ", 0, 60, 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 10, 100901, 0, 0, "それでも貴様となら…\n添い遂げる覚悟はできているぞ！", 2, "アンナ", 0, 61, 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 10, 101301, 0, 0, "君と出会ってもう1年だって！\n早いねー！", 1, "ナナカ", 1, 62, 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 10, 101301, 0, 0, "これからも最強のバディタッグで\n大活躍だー！\n頼りにしてるよ、相棒！", 2, "ナナカ", 1, 63, 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 11, 105101, 0, 0, "1年たつのって、あっという間ね\nまだまだ君の知らない部分が\nたくさんあるし…", 1, "ミツキ", 0, 64, 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 11, 105101, 0, 0, "これからも、いろんなところを\n調べさせてね？", 2, "ミツキ", 0, 65, 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 11, 105601, 0, 0, "お前さんと出会ってもう1年か\n助太刀するつもりが", 1, "ルカ", 1, 66, 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 11, 105601, 0, 0, "逆に助けられたりもしたね\nこれからもよろしく頼むよ！", 2, "ルカ", 1, 67, 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 11, 102701, 0, 0, "早いものですわね、あなた様と\nお会いして、もう1年…", 1, "エリコ", 2, 68, 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 11, 102701, 0, 0, "あなた様といると、時があっと\nいう間に過ぎてしまいます\n時間を止める研究をしなくては\nいけませんね…", 2, "エリコ", 2, 69, 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 12, 102301, 0, 0, "お兄ちゃんと会ってから、もう、\n1年なんだね。私、少しは\nお姉ちゃんっぽくなったかな？", 1, "アヤネ", 0, 70, 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 12, 102301, 0, 0, "あ、ぷうきちもお兄ちゃんっぽく\nなったよね？", 2, "アヤネ", 0, 71, 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 12, 104201, 0, 0, "あなたと出会って、\nもう1年がたったんですね…\nこれからもそばにいて、", 1, "チカ", 1, 72, 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 12, 104201, 0, 0, "私のことを見守ってください\nできることなら、ずっと…", 2, "チカ", 1, 73, 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 12, 102101, 0, 0, "お兄ちゃんと出会って1年かぁ\n私、相変わらずダメダメで…", 1, "クルミ", 2, 74, 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 12, 102101, 0, 0, "で、でもこれからもずっと…\n一緒にいたいなぁ…", 2, "クルミ", 2, 75, 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 13, 104901, 0, 0, "再会してちょうど1年だね\nお祝いにごちそう作ってきたよ～♪\n特製ケーキと、お寿司と、", 1, "シズル", 0, 76, 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 13, 104901, 0, 0, "ハンバーグと、カレーと、\nシチューと、あ、隠しきれない隠し\n味は、お姉ちゃんの愛情だよ！", 2, "シズル", 0, 77, 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 13, 101101, 0, 0, "お兄ちゃんと再会して、もう1年\nなんですね。\n会えなかった時を思うと、", 1, "リノ", 1, 78, 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 13, 101101, 0, 0, "信じられないくらい幸せな1年\nでした！もう絶対、私から離れ\nないでくださいね♪", 2, "リノ", 1, 79, 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 14, 100201, 0, 0, "騎士クンと出会ってから今まで、\nいろんなことがあったよね\n楽しいこと、ドキドキしたこと…", 1, "ユイ", 0, 80, 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 14, 100201, 0, 0, "そんな日々をこれからも、\nず、ずっと一緒に…\n過ごしていきたいな…！", 2, "ユイ", 0, 81, 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 14, 100101, 0, 0, "騎士くんと出会って、まだ1年しか\nたってないんだ！？", 1, "ヒヨリ", 1, 82, 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 14, 100101, 0, 0, "なんかもうずっと一緒にいる気が\nするなぁ…えへへ。これからの\n1年もまたよろしくねっ！\n騎士くん！", 2, "ヒヨリ", 1, 83, 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 14, 100301, 0, 0, "初めてキミに出会って1年…\n随分見違えたね私もキミも、\n更に強くなっていく…", 1, "レイ", 2, 84, 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 14, 100301, 0, 0, "キミに背中を預けられる日が、\n早くきますように", 2, "レイ", 2, 85, 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 15, 105801, 0, 0, "1年もわたしのことを、忘れない\nでいてくれて、ありがとうござい\nます！", 1, "ペコリーヌ", 0, 86, 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 15, 105801, 0, 0, "これからもずっと、覚えていて\nくださいね！", 2, "ペコリーヌ", 0, 87, 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 15, 105901, 0, 0, "こうして1年も、主さまのおそばに\n置いていただけて、とても光栄\nです。どうかこれからも、", 1, "コッコロ", 1, 88, 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 15, 105901, 0, 0, "末永く主さまをお世話させて\nください。わたくしは、ずっと\n主さまのガイド役でいたいのです", 2, "コッコロ", 1, 89, 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 15, 106001, 0, 0, "1年か、まっさかこんなに長い\n付き合いになるなんてね～\nでも…わりと楽しい1年だったかも", 1, "キャル", 2, 90, 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 15, 106001, 0, 0, "だから、もうしばらくあんたに\n付き合ってやるわよ喜びなさい！", 2, "キャル", 2, 91, 80001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 0, 112101, 1, 0, "新しい年のはじめに、\nおみくじはいかがでしょうか？\nよい運勢であるほど、\n沢山プレゼントが貰えますよ♪", 1, "スズメ", 1, 92, 60001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (1, "", 0, 0, 1, 0, "今日の運勢は姫吉！　あらゆる運気が最大を示しています！　　　今なら「伝説の吸血鬼」をも従え、世界征服も夢じゃありません！", 0, "", 2, 93, 60001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (1, "", 0, 0, 1, 0, "今日の運勢は姫吉！　やること全てが最高潮！　やばいですね☆　ラッキーアイテムはおにぎり！　お腹一杯で幸せが続くでしょう！", 0, "", 2, 94, 60001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (1, "", 0, 0, 1, 0, "今日の運勢は姫吉！　最強の運勢でどんなドジも跳ね除けます！　大切な人と一緒に、最高の一日になること間違いなし！", 0, "", 2, 95, 60001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (1, "", 0, 0, 1, 0, "今日の運勢は姫吉！　どんな挑戦も上手くいく、最高の運勢です！理想の自分に変身できる絶好の機会！　新しい一歩を踏み出そう！", 0, "", 2, 96, 60001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (1, "", 0, 0, 2, 0, "今日の運勢は姫吉！　その高い運気はまさにショーグンの如く！　その溢れる威光は人々を惹きつけ、皆の人気者間違いなしです！", 0, "", 2, 97, 60001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (2, "", 0, 0, 1, 0, "今日の運勢は大吉！　運気がどんどん高まっています！　　　　　何をやっても、最大効率で素晴らしい結果になりますよ♪", 0, "", 2, 98, 60001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (2, "", 0, 0, 1, 0, "今日の運勢は大吉！　なにをやっても上手くいって大活躍！　　　そんな素敵なあなたを見て、後ろから誰かが……クスクス♪", 0, "", 2, 99, 60001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (2, "", 0, 0, 1, 0, "今日の運勢は大吉！　ラッキーワードは「ちぇる～ん♪」　　　　出会う人みんなに言ってあげると、ハッピーになれますよ！", 0, "", 2, 100, 60001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (2, "", 0, 0, 1, 0, "今日の運勢は大吉！　運勢はバッチリで無敵状態！　　　　　　　龍眼で全てを見通して、思い通りの一日を過ごしましょう！", 0, "", 2, 101, 60001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (2, "", 0, 0, 2, 0, "今日の運勢は大吉！　運気が高く、何かに挑戦すると良い結果に！隠れた才能が引き出されそう！　目指せヒデサ……ではなく秀才！", 0, "", 2, 102, 60001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (3, "", 0, 0, 1, 0, "今日の運勢は中吉！　商売運が上がってきています！　　　　　　ラッキーアイテムはたいやき！　和菓子パワーで売上アップです！", 0, "", 2, 103, 60001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (3, "", 0, 0, 1, 0, "今日の運勢は中吉！　旅行運がぐんぐんと上昇中♪　　　　　　　もしかしたら、あの「マホマホ王国」にだって行けるかも♪", 0, "", 2, 104, 60001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (3, "", 0, 0, 1, 0, "今日の運勢は中吉！　仕事運が急上昇中です！　　　　　　　　　きっと「煉獄に舞う一陣の疾風」の如き活躍ができますよ！", 0, "", 2, 105, 60001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (3, "", 0, 0, 1, 0, "今日の運勢は中吉！　チャンスを待つと良い結果に！　　　　　　ラッキーアイテムのプリンを食べて、運を呼び込みましょう！", 0, "", 2, 106, 60001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (3, "", 0, 0, 1, 0, "今日の運勢は中吉！　お出かけした先で良縁に恵まれそうです！　ダウナー系あざとい女子が、あま～い飴をくれるかも？", 0, "", 2, 107, 60001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (3, "", 0, 0, 1, 0, "今日の運勢は中吉！　大抵のことはゴリ押しでなんとかできます！ラッキープレイスはゲーセン。パンチングマシンでストレス発散！", 0, "", 2, 108, 60001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (3, "", 0, 0, 2, 0, "今日の運勢は中吉！　運気が着々と上昇中！　　　　　　　　　　精霊の加護を受けたかの如く、色々な事がうまくいきますよ♪", 0, "", 2, 109, 60001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (4, "", 0, 0, 1, 0, "今日の運勢は小吉！　早朝に幸運の兆しあり！　　　　　　　　　もしかしたら、優しいお姉ちゃんが起こしにきてくれますよ♪", 0, "", 2, 110, 60001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (4, "", 0, 0, 1, 0, "今日の運勢は小吉！　運気上昇中ですが、上がるまでが要注意！　ラッキーアニマルは熊！　本当の家族のように守ってくれるかも♪", 0, "", 2, 111, 60001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (4, "", 0, 0, 1, 0, "今日の運勢は小吉！　外に出てゆっくり過ごすと運気が上昇！　　ラッキースポットは牧場。面白い出会いがありそうです♪", 0, "", 2, 112, 60001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (4, "", 0, 0, 1, 0, "今日の運勢は小吉！　運気は高いけども、ハプニングには注意！　出かけた先で、「ふしんしゃ」扱いされてしまうかも……", 0, "", 2, 113, 60001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (4, "", 0, 0, 1, 0, "今日の運勢は小吉！　同腹一心の友だちが見つかるかも！　　　　キーワードは「ユニちゃんズ」、記憶に刻みましょう！", 0, "", 2, 114, 60001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (4, "", 0, 0, 1, 0, "今日の運勢は小吉！　思わぬ相手と仲良くなれるかも？　　　　　うっかり毒舌をもらしてしまわないように要注意です！", 0, "", 2, 115, 60001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (4, "", 0, 0, 2, 0, "今日の運勢は小吉！　根を詰めずにリラックスすると運気向上！　ラッキーパーソンはアイドル。歌があなたを幸せにしてくれます♪", 0, "", 2, 116, 60001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 0, 112101, 0, 0, "おみくじの結果はいかがでしたか？\n新たに始まる一日が、\nあなたにとってよい日になりますように\n明日もお待ちしていますね♪", 2, "スズメ", 3, 117, 60001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 7, 112101, 0, 0, "おみくじ、楽しんで頂けましたか？\nあなたの新しい年が\n素晴らしいものになるよう、\n心からお祈りしますね", 3, "スズメ", 3, 118, 60001);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 1, 105801, 0, 0, "ヒューヒュー♪\nドンドンパフパフー♪", 1, "ペコリーヌ", 0, 119, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 1, 105901, 0, 0, "ひゅーひゅー\nどんどんぱふぱふー\nでございます", 1, "コッコロ", 1, 120, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 1, 105901, 0, 0, "さぁ、キャルさまもご一緒に\nどんどんぱふぱふいたしましょう", 2, "コッコロ", 1, 121, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 1, 106001, 0, 0, "え？　ちょっと\nいきなりなにっ？", 1, "キャル", 2, 122, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 1, 106001, 0, 0, "ドンドンパフパフってなんなの？\nあたしにも分かるように\nちゃんと説明しなさいっての！", 2, "キャル", 2, 123, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 1, 105801, 0, 0, "実はですねキャルちゃん！\nもうすぐわたしたちにとっての\n大切な節目──", 2, "ペコリーヌ", 0, 124, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 1, 105801, 0, 0, "『プリンセスコネクト！\nRe:Dive』\n2.5周年なんですよ！\nやばいですね☆", 3, "ペコリーヌ", 0, 125, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 1, 105901, 0, 0, "ですからその日に向けて\n今から気分を盛り上げているのです", 3, "コッコロ", 1, 126, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 1, 106001, 0, 0, "ああ、そういえば\nそんな時期だっけ\nって言っても、もう少し先じゃない\n気が早いんじゃないの？", 3, "キャル", 2, 127, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 1, 105901, 0, 0, "いいえ、キャルさま\nこういったお祝いは準備から\n楽しむものでございます\nそうですよね、ペコリーヌさま", 4, "コッコロ", 1, 128, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 1, 105801, 0, 0, "その通りです♪　というわけで！\nカウントダウンの始まりですよ！\nみんなで一緒に2.5周年に向けて\n盛り上がっていきましょー☆", 4, "ペコリーヌ", 0, 129, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 1, 106001, 0, 0, "ド、ドンドンパフパフ～\nって何言わせんのよ！\nまったくもう、浮かれちゃってさ", 4, "キャル", 2, 130, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 1, 105901, 0, 0, "ふふ。キャルさまもワクワクして\nおいでのようですね\nそれでは主さま。大切な記念日まで\nどうぞご期待くださいませ", 5, "コッコロ", 1, 131, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 2, 106001, 0, 0, "記念日を盛り上げるったって\n一体どうしたらいいのよ？\nあたし、そういうの\n苦手なんだけど…", 5, "キャル", 0, 132, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 2, 190801, 0, 0, "あら、キャルさん\nどうかなさいましたか？\n何かお困りのようですけど", 1, "カリン", 1, 133, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 2, 106001, 0, 0, "げっ…カリンじゃない\nなんでもないわよ！", 6, "キャル", 0, 134, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 2, 190801, 0, 0, "そうなのですか？\nキャルさんのことですからてっきり\n2.5周年をどう盛り上げたら\nいいか悩んでいるのかと…", 2, "カリン", 1, 135, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 2, 106001, 0, 0, "分かってんじゃないのよ！？\nいっ、いや、違うけどね！\nあたしは別にそんなことで\n悩んでないわよっ？", 7, "キャル", 0, 136, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 2, 190801, 0, 0, "ふふっ…\nこういう時は、真心を込めて\n気持ちをそのまま口にしたら\nいいのではないでしょうか？", 3, "カリン", 1, 137, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 2, 106001, 0, 0, "気持ちを、ねぇ\nそれが一番難しいんじゃない…\nそもそも、あたしは記念日とか\n別にどうだっていいし", 8, "キャル", 0, 138, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 2, 106001, 0, 0, "…まあ、ペコリーヌもコロ助も\nやけに楽しそうだったから…\nこういうお祭り騒ぎみたいなのも\nたまにはいいかな、とは思うけど", 9, "キャル", 0, 139, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 2, 190801, 0, 0, "なるほどなるほど～\nキャルさんは【美食殿】で祝う\n2.5周年を心から楽しみに\nしている、ということですね！", 4, "カリン", 1, 140, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 2, 106001, 0, 0, "はぁ！？\nどこ聞いたらそうなるのよ！\nあたしは別に楽しみになんか…！", 10, "キャル", 0, 141, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 2, 190801, 0, 0, "キャルさんの心からの想い\nみなさんにも伝わったでしょうか？\n私も2.5周年が楽しみです！\n以上、カリンがお伝えしました！", 5, "カリン", 1, 142, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 2, 106001, 0, 0, "心からの想いって何なのよ！？\n勝手に人の言葉を\n捻じ曲げんじゃないわよ！\nこのくそ眼鏡ーーーっ！", 11, "キャル", 0, 143, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 3, 105901, 0, 0, "ペコリーヌさま、もうすぐ\n記念日でございますね\nこういった節目というのは\n感慨深いものでございます", 6, "コッコロ", 0, 144, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 3, 105801, 0, 0, "色んな思い出が蘇ってきますね！\n楽しい出来事が\nいっぱいありました♪", 5, "ペコリーヌ", 1, 145, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 3, 105901, 0, 0, "はい、そうでございますね\n主さまも【美食殿】の活動の他にも\nいろんなご経験をされたと\n聞いておりますよ", 7, "コッコロ", 0, 146, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 3, 105901, 0, 0, "たしか…ランドソルで話題沸騰と\nなっていた、魔法少女なるものに\nお会いになられたとか", 8, "コッコロ", 0, 147, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 3, 105801, 0, 0, "魔法少女って、あの美味しくって\n栄養たっぷりの大ヒット商品\n『魔法少女ソーセージ』の箱に\n描いてある女の子のことですか！？", 6, "ペコリーヌ", 1, 148, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 3, 105801, 0, 0, "あのソーセージ\n美味しいんですよね～♪\nわたしもたっくさん食べてますよ！", 7, "ペコリーヌ", 1, 149, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 3, 105901, 0, 0, "あとは、牧場に素敵な温泉が\n出来たとか…", 9, "コッコロ", 0, 150, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 3, 105801, 0, 0, "牧場ですか！？　温泉につかって\nチーズもオツですね～♪　考えた\nだけでよだれが出ちゃいそうです！\nやばいですね☆", 8, "ペコリーヌ", 1, 151, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 3, 105901, 0, 0, "ペコリーヌさま\n言ってるそばからよだれが…\n今お拭きしますね。ふき、ふき", 10, "コッコロ", 0, 152, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 3, 105801, 0, 0, "えへへ、ありがとうございます\nコッコロちゃん！", 9, "ペコリーヌ", 1, 153, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 3, 105901, 0, 0, "それにしても、主さまは\n【美食殿】の一員として様々な\n美食と出会われていたのですね\nわたくし、感服いたしました", 11, "コッコロ", 0, 154, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 3, 105801, 0, 0, "これからもたくさん冒険をして\nめいっぱい美食を\n味わい尽くしましょうね！", 10, "ペコリーヌ", 1, 155, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 4, 105801, 0, 0, "2.5周年カウントダウン♪\nキャルちゃんキャルちゃん！\nわくわくしますね☆", 11, "ペコリーヌ", 0, 156, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 4, 106001, 0, 0, "あぁもう、うるさいわね～\nまだまだ先でしょ？\nちょっとは落ち着きなさいよ", 12, "キャル", 1, 157, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 4, 105801, 0, 0, "キャルちゃんだって最近\nソワソワしてるじゃないですか～\nさっきもご機嫌そうに鼻歌とか\n歌ってましたよね？", 12, "ペコリーヌ", 0, 158, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 4, 106001, 0, 0, "なっ…！　き、聞いてたの！？\nじゃない、歌ってないわよ！？\nあんたいい加減なこと言うと\nぶっ殺すわよ！？", 13, "キャル", 1, 159, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 4, 105801, 0, 0, "えっへへ～♪\nわたしは最近のキャルちゃんが\nもっともっと大好きですよ♪\nすごく積極的になりましたよね！", 13, "ペコリーヌ", 0, 160, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 4, 106001, 0, 0, "…積極的？\nなんの話よ？", 14, "キャル", 1, 161, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 4, 105801, 0, 0, "またまた～♪\nアイドルを目指そうとしたこと\n忘れちゃったんですか？", 14, "ペコリーヌ", 0, 162, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 4, 105801, 0, 0, "ムーサの町で\nきゃるきゃる～ん☆　って！", 15, "ペコリーヌ", 0, 163, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 4, 106001, 0, 0, "あっ…！？　ちちち違うわよっ！\nあれは操られてただけで\nあたしはアイドルなんか\n目指してないっての！！", 15, "キャル", 1, 164, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 4, 105801, 0, 0, "本当に本当ですか？\nわたし、キャルちゃんとっても\n乗り気だなって\n思ってたんですけど～", 16, "ペコリーヌ", 0, 165, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 4, 106001, 0, 0, "まぁ、悪くはなかったけど…って\nだ、だから目指してないって\n言ってるでしょ！！\nなんてこと思い出させんのよ！？", 16, "キャル", 1, 166, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 4, 106001, 0, 0, "ああ…ダメだわ…\nもう殺すしかない…\n2.5周年が来る前に\n記憶ごと消し去ってやるー！", 17, "キャル", 1, 167, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 5, 106001, 0, 0, "2.5周年まであと10日…\nふ～ん？\n案外早いもんね？", 18, "キャル", 0, 168, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 5, 105901, 0, 0, "そうでございますね…\nその日のためにやるべきことは\nまだまだございます\n気を引き締めねばなりません…！", 12, "コッコロ", 1, 169, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 5, 106001, 0, 0, "やる気十分ねコロ助…\nそんな気負ってるとしんどいわよ？\nお気楽にやりましょ？", 19, "キャル", 0, 170, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 5, 105901, 0, 0, "キャルさま…\nありがとうございます\nキャルさまは本当に\n優しいおかたです", 13, "コッコロ", 1, 171, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 5, 106001, 0, 0, "な、なにわけの分かんないこと\n言ってんのよ…\nあたしは優しくなんかないっての", 20, "キャル", 0, 172, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 5, 105901, 0, 0, "そんなことはございません\n…あの、実はわたくし\nキャルさまに\nひとつお願いがございまして", 14, "コッコロ", 1, 173, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 5, 106001, 0, 0, "お願い？　珍しいわね\nコロ助がそんなこと言うなんて\n…まあ、あたしに出来ることなら\n別にいいけど", 21, "キャル", 0, 174, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 5, 105901, 0, 0, "ありがとうございます\n実は記念日へ向けて\n『かくし芸』というものを\n用意してみようかと思いまして", 15, "コッコロ", 1, 175, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 5, 106001, 0, 0, "かくし芸？", 22, "キャル", 0, 176, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 5, 105901, 0, 0, "はい。きゃるきゃる～ん☆　を\n参考に、ころころ～ん☆　という\nご挨拶を考えてみたのですが\nポーズがうまくいかなくて…", 16, "コッコロ", 1, 177, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 5, 106001, 0, 0, "～～！！　ちょっと褒められて\nいい気になって損したわ！！\nふざけんなふざけんな\nふ～ざ～け～ん～な～っ！！", 23, "キャル", 0, 178, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 6, 105801, 0, 0, "あと９日！　もうすぐですよ♪\nえへへ、楽しみで楽しみで\nごはんが進みますね☆", 17, "ペコリーヌ", 0, 179, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 6, 106001, 0, 0, "ごはんが進みますね☆\nじゃなーい！\nいくらなんでも食べ過ぎよ！\n記念日、迎えられなくなるわよ！？", 24, "キャル", 2, 180, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 6, 105901, 0, 0, "大丈夫ですよキャルさま\n食事はわたくしたちの\n歩んできた道そのもの\nなのですから", 17, "コッコロ", 1, 181, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 6, 105801, 0, 0, "さっすがコッコロちゃん\nいいことを言いますね！\nそう、わたしたち\n【美食殿】の活動目的は─", 18, "ペコリーヌ", 0, 182, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 6, 106001, 0, 0, "─みんなで楽しく食事をすること\nでしょ？\nあれだけ食べ歩いてきたんだから\n分かってるわよ", 25, "キャル", 2, 183, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 6, 105901, 0, 0, "はい…そうでございますね♪\nそれにしてもわたくしたち\n本当にいろいろなものを\n食べ歩いてきましたね…", 18, "コッコロ", 1, 184, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 6, 106001, 0, 0, "そうね…ほんっとうにね…\n毒まみれのニセドラゴンとか\n恐竜…は逃げられちゃったっけ\nあとワニゴ…ワニの魔物とかね", 26, "キャル", 2, 185, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 6, 106001, 0, 0, "そして、とうとう本物の\nドラゴンまで相手に\nしちゃったのよね、あたしたちって", 27, "キャル", 2, 186, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 6, 106001, 0, 0, "信じられない強さだったわね\nあのドラゴン…やっぱり\n作戦はしっかり立てていかないと\nダメよね", 28, "キャル", 2, 187, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 6, 105801, 0, 0, "えへへ…☆\nキャルちゃんが色んなことを\n覚えていてくれて\nうれしいです♪", 19, "ペコリーヌ", 0, 188, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 6, 105901, 0, 0, "わたくしも鮮明に覚えておりますよ\n主さまもきっと…♪", 19, "コッコロ", 1, 189, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 6, 106001, 0, 0, "あったり前でしょ！\n忘れないわよ、あんな滅茶苦茶な\nものばかり食べさせられて！\n…忘れられるわけ、ないじゃない", 29, "キャル", 2, 190, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 7, 106001, 0, 0, "ねえちょっと、ちょっと待って！？\nカリンから、2.5周年記念の\n生放送に出演してほしいって\n言われたんだけど！", 30, "キャル", 0, 191, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 7, 106001, 0, 0, "なんでそういう大事なことを\n急に言うわけ！？\nそんなの聞いてないわよ～！？", 31, "キャル", 0, 192, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 7, 106001, 0, 0, "いっぱい人に見られちゃうのよね？\nなんであたしがそんなこと…", 32, "キャル", 0, 193, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 7, 106001, 0, 0, "あー\nあー\n…声、これで大丈夫かしら？", 33, "キャル", 0, 194, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 7, 106001, 0, 0, "聞き取りにくいかもしれないわね…\nもうちょっと明るい方がいいかも\nえっと…こんな感じ？", 34, "キャル", 0, 195, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 7, 106001, 0, 0, "オイッスー☆", 35, "キャル", 0, 196, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 7, 106001, 0, 0, "……\nいや、ないわ\nいつもどおりが一番ね、うん", 36, "キャル", 0, 197, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 7, 106001, 0, 0, "もう、記念の生放送なんて\n超責任重大なやつじゃない…\nもしなにか失敗とかしたら…", 37, "キャル", 0, 198, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 7, 106001, 0, 0, "ど、どど、どうしよう！？\n緊張してきちゃった…！！", 38, "キャル", 0, 199, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 8, 105901, 0, 0, "主さま、わたくし\nペコリーヌさまとキャルさまと共に\n2.5周年を祝う生放送に\n出演する大役を仰せつかりました", 20, "コッコロ", 0, 200, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 8, 105901, 0, 0, "多くの方々に\n『プリンセスコネクト！\nRe:Dive』の魅力をお伝えする\n重要な役割でございます", 21, "コッコロ", 0, 201, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 8, 105901, 0, 0, "わたくし、全身全霊をもって\n生放送に取り組みたいと\n思っております", 22, "コッコロ", 0, 202, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 8, 105901, 0, 0, "ですが、もしかしたら\nセリフを噛んでしまったり\n段取りを間違えてしまったり\nするかもしれません…", 23, "コッコロ", 0, 203, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 8, 105901, 0, 0, "そのようなことがないよう\n練習はしっかりしておかなくては\nまずはこちらから…", 24, "コッコロ", 0, 204, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 8, 105901, 0, 0, "オイッスー☆", 25, "コッコロ", 0, 205, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 8, 105901, 0, 0, "先ほどキャルさまが練習されて\nいるのをお見かけしたので\nわたくしもと思いまして…\nいかがでしょうか？", 26, "コッコロ", 0, 206, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 8, 105901, 0, 0, "良い感じ、ですか？\n…ふふ、ありがとうございます\n主さまのあたたかいお言葉があれば\nわたくしは百人力でございます", 27, "コッコロ", 0, 207, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 8, 105901, 0, 0, "この大役を無事に成し遂げ\n主さまに素敵な2.5周年を\n迎えていただけるよう…\nわたくし、尽力いたしますね", 28, "コッコロ", 0, 208, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 9, 105801, 0, 0, "オイッスー☆", 20, "ペコリーヌ", 0, 209, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 9, 105801, 0, 0, "…う～ん、今のはちょっと\n違う感じがしますね\n堅苦しかったかもしれません", 21, "ペコリーヌ", 0, 210, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 9, 105801, 0, 0, "あと６日で2.5周年ですか…", 22, "ペコリーヌ", 0, 211, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 9, 105801, 0, 0, "この年月がオイッスーの\n歴史なんだと思うと\nじ～んとしちゃいます！\nやばいですね☆", 23, "ペコリーヌ", 0, 212, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 9, 105801, 0, 0, "生放送で最高のオイッスー☆　を\nみなさんにお届けするために\n頑張らないと！\nそれじゃあもう一度…", 24, "ペコリーヌ", 0, 213, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 9, 105801, 0, 0, "オイッスー☆", 25, "ペコリーヌ", 0, 214, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 9, 105801, 0, 0, "…うう～ん、どうしてでしょう\nまだ何かが違う気がします…\nもしかしたら\nおなかが空きすぎてるとか？", 26, "ペコリーヌ", 0, 215, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 9, 105801, 0, 0, "今さっき食べたばっかりですけど\nおなかが減っては\n良いオイッスー☆　は\n出せませんよね！", 27, "ペコリーヌ", 0, 216, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 9, 105801, 0, 0, "そうと決まれば、コッコロちゃんと\nキャルちゃんを誘って\nごはんにしましょう！\n善は急げです！", 28, "ペコリーヌ", 0, 217, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 9, 105801, 0, 0, "あ、二人が丁度いいところに！\nオイッス～☆　…ああ！！\n今の、今のですよ～♪", 29, "ペコリーヌ", 0, 218, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 9, 105801, 0, 0, "なるほど、どんな時も\n自然体が一番ってことですね！\nこの調子で、生放送も\n頑張っちゃいます♪", 30, "ペコリーヌ", 0, 219, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 10, 105801, 0, 0, "2.5周年まで残り５日ですが\nその前にもうすぐ生放送ですよ！\nコッコロちゃん\n準備の方はどうですか？", 31, "ペコリーヌ", 0, 220, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 10, 105901, 0, 0, "はい。わたくし、しっかりと\n備えてまいりました\n万事抜かりはございません", 29, "コッコロ", 1, 221, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 10, 105801, 0, 0, "おお～すごい自信を感じます…！\nいい生放送になりそうですね☆\n……あれ？　なんだかこの辺り\n揺れていませんか？", 32, "ペコリーヌ", 0, 222, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 10, 106001, 0, 0, "…………", 39, "キャル", 2, 223, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 10, 105901, 0, 0, "キャルさま？\nお風邪をひかれたんでしょうか\nひどい震えですが…", 30, "コッコロ", 1, 224, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 10, 106001, 0, 0, "かかかか風邪じゃないわよ！\nここここれはただ\nななな生放送が楽しみ過ぎて\nワワワワクワクが止まらないだけ！", 40, "キャル", 2, 225, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 10, 105801, 0, 0, "大丈夫ですかキャルちゃん！？\nそうだ、これを飲んでください！", 33, "ペコリーヌ", 0, 226, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 10, 106001, 0, 0, "きき気がきくじゃない、ありがとっ\nごくっ、んく…ぶはぁっ！？\nちょっと何飲ませたの！？\n辛っ！　苦ぁ！？　甘ぁっ！？", 41, "キャル", 2, 227, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 10, 105801, 0, 0, "これは魔物のエキスに薬草に\nその他色んなものを混ぜた\n特製の栄養ドリンクです！", 34, "ペコリーヌ", 0, 228, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 10, 105801, 0, 0, "これを飲めば体ぽかぽかで\n元気いっぱいですよ♪", 35, "ペコリーヌ", 0, 229, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 10, 106001, 0, 0, "このアホリーヌ！\n風邪じゃないって言ったでしょ！？\nどさくさに紛れて\nそんな恐ろしいもん飲ませんな～！", 42, "キャル", 2, 230, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 10, 105901, 0, 0, "キャルさま\n震えが収まっておりますね\nよかったです…\nわたくし、心配いたしました", 31, "コッコロ", 1, 231, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 10, 106001, 0, 0, "え？　ああ、ホントね…って\nだから風邪じゃないって\n言ってるのに…\nまあいいわこの際なんでも", 43, "キャル", 2, 232, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 10, 105801, 0, 0, "えっへへ～♪　いつも通りの\nキャルちゃんに戻りましたし\n素敵な生放送が出来そうですね！\n今から楽しみです☆", 36, "ペコリーヌ", 0, 233, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 11, 190801, 0, 0, "ランドソルのみなさま、こんにちは\n生放送の会場設営を担当しています\n【ギルド管理協会】のカリンです", 6, "カリン", 0, 234, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 11, 190801, 0, 0, "【美食殿】のみなさんは\nまもなく始まる生放送に向けて\n最後の調整のため\n席を外しています", 7, "カリン", 0, 235, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 11, 190801, 0, 0, "頑張っている３人の\n邪魔をするわけには\nいきませんので、ご容赦ください", 8, "カリン", 0, 236, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 11, 190801, 0, 0, "ですので、本日は私から\n【美食殿】のみなさんの様子を\nお伝えさせていただこうと思います", 9, "カリン", 0, 237, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 11, 190801, 0, 0, "まずはペコリーヌさんですが…\n何やら大量のおにぎりを\n食べているようです\nえっと…栄養は大事ですよね", 10, "カリン", 0, 238, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 11, 190801, 0, 0, "次にコッコロさんですが\nこちらは発声練習をしています", 11, "カリン", 0, 239, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 11, 190801, 0, 0, "一生懸命、小さなお口を開けて\n声を出す姿が応援したくなりますね", 12, "カリン", 0, 240, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 11, 190801, 0, 0, "そしてキャルさんは…\nこれは…堂々としたものですね\n何もせず、ただ立ち尽くして…", 13, "カリン", 0, 241, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 11, 190801, 0, 0, "あれ？\nもしかして緊張で気を失って…？", 14, "カリン", 0, 242, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 11, 190801, 0, 0, "…ええと\n【美食殿】のみなさんは\nやる気十分なようですね！", 15, "カリン", 0, 243, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 11, 190801, 0, 0, "３人とも生放送を\nいいものにしようと\n全力で頑張ってくれていますので\nどうかご期待ください", 16, "カリン", 0, 244, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 11, 190801, 0, 0, "それでは\nまた明日お会いしましょう♪", 17, "カリン", 0, 245, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 12, 106001, 0, 0, "2.5周年まであと３日！\nそして生放送は明日！\nなんかいよいよって感じね…", 44, "キャル", 0, 246, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 12, 105801, 0, 0, "３人でい～っぱい練習しましたし\nきっと大丈夫ですよ☆", 37, "ペコリーヌ", 2, 247, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 12, 105901, 0, 0, "ペコリーヌさまに\n大丈夫と言ってもらえると\nとても心強いですね", 32, "コッコロ", 1, 248, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 12, 106001, 0, 0, "信じすぎちゃだめよコロ助\nこいつはなーんにも\n考えてないだけなんだから", 45, "キャル", 0, 249, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 12, 105801, 0, 0, "ひどいですよぉキャルちゃ～ん！", 38, "ペコリーヌ", 2, 250, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 12, 105901, 0, 0, "ふふ。お二人とも\n普段通りの様子で\n安心いたしました", 33, "コッコロ", 1, 251, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 12, 105801, 0, 0, "はいっ☆\n生放送でもなんでも\n全力全開、わたしらしく\nぶつかっていくだけです！", 39, "ペコリーヌ", 2, 252, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 12, 106001, 0, 0, "そうね。おめでたいことなんだし\nここまで来たんなら\n楽しめるだけ\n楽しむしかないわよね！", 46, "キャル", 0, 253, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 12, 105901, 0, 0, "わたくしも、普段よりも心が\n熱くなっております\nこの胸の気持ちをお伝えできるよう\n精一杯努力いたします…！", 34, "コッコロ", 1, 254, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 12, 105801, 0, 0, "それでは、いきましょう！\n明日の生放送、絶対に\n成功させましょうね☆", 40, "ペコリーヌ", 2, 255, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 13, 105801, 0, 0, "えっと、最初のひと言は…あれ？\nキャルちゃん、わたし\n何て言うんでしたっけ？", 41, "ペコリーヌ", 0, 256, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 13, 106001, 0, 0, "は！？\nなにいきなりど忘れしてるの！？\nっていうかあんた、台本は！？", 47, "キャル", 2, 257, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 13, 105801, 0, 0, "忘れてきちゃいました☆", 42, "ペコリーヌ", 0, 258, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 13, 106001, 0, 0, "忘れてきちゃいました☆\nで済ませられると思ってんの！？\nほら、あたし二冊持ってきたから\nこれ使いなさい！", 48, "キャル", 2, 259, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 13, 105901, 0, 0, "マイクとは、こんな高い位置に\nあるものなのですね…！\n背伸びをすれば、何とか…\nう～ん、うぅ～ん…！", 35, "コッコロ", 1, 260, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 13, 106001, 0, 0, "あぁもう\n少し下げればいいでしょ！？\nこれくらいでいい？\nまったく、世話が焼けるわね…", 49, "キャル", 2, 261, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 13, 105801, 0, 0, "あれ？\nキャルちゃん、後ろ髪が\nはねちゃってますよ？", 43, "ペコリーヌ", 0, 262, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 13, 106001, 0, 0, "え？　うそっ！？\nちゃんと確かめてきたのに！\nどこどこ！？", 50, "キャル", 2, 263, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 13, 105901, 0, 0, "キャルさま、動かないで\nくださいまし。今くしで…\nはい、直りましたよ", 36, "コッコロ", 1, 264, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 13, 106001, 0, 0, "あ、ありがと…はぁ。生放送\n本番までもうすぐなのに\nこんなことしてる場合じゃ\nないわよね", 51, "キャル", 2, 265, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 13, 105901, 0, 0, "はい、時間は残りわずかです\nですが、慌ただしく\n動き回るこの時間も\nなんだか楽しくて…♪", 37, "コッコロ", 1, 266, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 13, 106001, 0, 0, "コロ助、あんた…って\nそうじゃなくて！\n胸にジンワリきてる\n暇なんかないでしょ…！？", 52, "キャル", 2, 267, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 13, 105801, 0, 0, "あはは♪\nキャルちゃんの動きが\nとっても面白いです♪\nやばいですね☆", 44, "ペコリーヌ", 0, 268, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 13, 105901, 0, 0, "ふふ\nきっと面白い生放送になると\nわたくし、確信しております", 38, "コッコロ", 1, 269, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 13, 105801, 0, 0, "絶対絶対、間違いありません！\n見てくれるみなさんが\n笑顔になれる時間を\nお届けしましょう！", 45, "ペコリーヌ", 0, 270, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 13, 106001, 0, 0, "そんなの当然でしょ！\n誰が見たって面白いって\n言ってもらえるように\nあれだけ準備したんだから！", 53, "キャル", 2, 271, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 13, 105801, 0, 0, "それでは\n記念日直前生放送！\n楽しみにしててくださいね☆", 46, "ペコリーヌ", 0, 272, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 14, 190801, 0, 0, "こんにちは\n【ギルド管理協会】のカリンです", 18, "カリン", 0, 273, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 14, 190801, 0, 0, "昨日の生放送は\nいかがだったでしょうか？", 19, "カリン", 0, 274, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 14, 190801, 0, 0, "たくさんの笑顔と喜びと\nそして驚きを受け取って\nいただけたのではないかと思います", 20, "カリン", 0, 275, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 14, 190801, 0, 0, "そしていよいよ\n明日は2.5周年の当日\n待ちに待った記念日を\n迎えることとなります", 21, "カリン", 0, 276, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 14, 190801, 0, 0, "【美食殿】のみなさんも\n張り切って様々な準備を\nされていますよ", 22, "カリン", 0, 277, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 14, 190801, 0, 0, "【ギルド管理協会】も\n万全のサポート体制で\n協力してまいりたいと思います", 23, "カリン", 0, 278, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 14, 190801, 0, 0, "…ふふ、本当に楽しみですね\n【ギルド管理協会】の職員として\nだけではなく、私個人としても…", 24, "カリン", 0, 279, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 14, 190801, 0, 0, "この記念日を迎えられることが\n心から楽しみですし\n幸せを感じています", 25, "カリン", 0, 280, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 14, 190801, 0, 0, "きっと【美食殿】のみなさんは\nそれ以上に心を弾ませて\nいることでしょう", 26, "カリン", 0, 281, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 14, 190801, 0, 0, "それでは\n明日からの2.5周年を\nどうぞ、お楽しみに…♪", 27, "カリン", 0, 282, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 15, 105801, 0, 0, "準備はいいですかっ？\n…いっきますよー！", 47, "ペコリーヌ", 0, 283, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 15, 106001, 0, 0, "祝！", 54, "キャル", 2, 284, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 15, 105901, 0, 0, "2.5周年！", 39, "コッコロ", 1, 285, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 15, 105801, 0, 0, "おめでとうございま～す☆", 48, "ペコリーヌ", 0, 286, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 15, 106001, 0, 0, "少し前まではまだまだ先じゃない\nとか言ってたけど\n本当にあっという間だったわね", 55, "キャル", 2, 287, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 15, 105901, 0, 0, "光陰矢の如し…\nとはこのことでございますね\nでも、今日という日を無事に\n迎えることができて良かったです", 40, "コッコロ", 1, 288, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 15, 105801, 0, 0, "コッコロちゃん！　キャルちゃん！\n落ち着くには\nまだ早いですよ！\n盛り上がるのはこれからです☆", 49, "ペコリーヌ", 0, 289, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 15, 106001, 0, 0, "ほんとあんたはいつも\n全力全開ねー\nでもそうね、記念日なんだし\nパーっといきましょ！", 56, "キャル", 2, 290, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 15, 105901, 0, 0, "ええ、わたくしも全力全開で\nまいります…！\n主さま、さあ\nどうぞ手を取ってくださいまし…♪", 41, "コッコロ", 1, 291, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 15, 106001, 0, 0, "あんたもコロ助も、浮かれて\n転ぶんじゃないわよ？\n…仕方ないから、あたしも\n手を繋いであげるわよ", 57, "キャル", 2, 292, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 15, 105801, 0, 0, "あー！　二人ともずるいです！\nわたしもっ、ギュー♪", 50, "ペコリーヌ", 0, 293, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 15, 106001, 0, 0, "ちょっ、ペコリーヌ！\nくっつき過ぎよ！", 58, "キャル", 2, 294, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 15, 105801, 0, 0, "記念日だから\n平気平気♪\nやばいですね☆", 51, "ペコリーヌ", 0, 295, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 15, 106001, 0, 0, "意味分かんないわよ！", 59, "キャル", 2, 296, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 15, 105901, 0, 0, "ふふ。このめでたき日に\n喜びがあふれておりますね\n主さまは…いかがでしょうか？", 42, "コッコロ", 1, 297, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 15, 105901, 0, 0, "喜んでいただけているならば\nわたくしはとてもとても\n幸せでございます", 43, "コッコロ", 1, 298, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 15, 105801, 0, 0, "さぁ、2.5周年を\n楽しみ尽くしますよ☆\nまだまだ食べたいものもありますし\nやりたいこともたくさんです！", 52, "ペコリーヌ", 0, 299, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 15, 106001, 0, 0, "分かった、分かったから\n引っ張るんじゃないわよ！\n…そんな掴まなくても\nちゃんとついていくから", 60, "キャル", 2, 300, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 15, 105901, 0, 0, "ペコリーヌさま…キャルさま…\n主さま…共にまいりましょう\nどこまでも…！", 44, "コッコロ", 1, 301, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 15, 105801, 0, 0, "はい！　それでは2.5周年\n食べつくしツアーへ！\nそして、その先の未来へ\n向かって出発しましょう！", 53, "ペコリーヌ", 0, 302, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "105901,45,106001,61", 15, 105801, 0, 1, "2.5周年\nありがとうございます！", 54, "ペコリーヌ・コッコロ・キャル", 0, 303, 80003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 0, 190804, 1, 0, "新年を迎え、新たに始まる一日を\nおみくじで占ってみませんか？\n良い運勢であるほど、\n沢山のプレゼントが貰えますよ♪", 1, "カリン", 1, 304, 60002);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (1, "", 0, 0, 1, 0, "今日の運勢は姫吉！　どんな不可能も可能にできちゃう！　　　　仲間と食卓を囲めばさらに運気上昇よ！　ただし魔物料理以外！", 0, "", 2, 305, 60002);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (1, "", 0, 0, 1, 0, "今日の運勢は姫吉！　やること全てが最高潮！　やばいですね☆　ラッキーアイテムはおにぎり！　お腹一杯で幸せが続くでしょう！", 0, "", 2, 306, 60002);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (1, "", 0, 0, 1, 0, "今日の運勢は姫吉！　最強の運勢でどんなドジも跳ね除けます！　大切な人と一緒に、最高の一日になること間違いなし！", 0, "", 2, 307, 60002);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (1, "", 0, 0, 1, 0, "今日の運勢は姫吉！　どんな挑戦も上手くいく、最高の運勢です！理想の自分に変身できる絶好の機会！　新しい一歩を踏み出そう！", 0, "", 2, 308, 60002);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (1, "", 0, 0, 2, 0, "今日の運勢は姫吉！　やること全てが上手くいく最高の一日！　　あなたの活躍に先生もにっこりです。いい子いい子♪", 0, "", 2, 309, 60002);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (2, "", 0, 0, 1, 0, "今日の運勢は大吉！　お裁縫好きな女の子のお墨付きです！　　　お世話したがりの妹にめいっぱい愛されちゃうかも！？", 0, "", 2, 310, 60002);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (2, "", 0, 0, 1, 0, "今日の運勢は大吉！　ラッキーカラーは赤！　　　　　　　　　　湧き上がるアイデアの数々に、高笑いが止まりませんわ！", 0, "", 2, 311, 60002);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (2, "", 0, 0, 1, 0, "今日の運勢は大吉！　ラッキーワードは「ちぇる～ん♪」　　　　出会う人みんなに言ってあげると、ハッピーになれますよ！", 0, "", 2, 312, 60002);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (2, "", 0, 0, 1, 0, "今日の運勢は大吉！　運勢はバッチリで無敵状態！　　　　　　　龍眼で全てを見通して、思い通りの一日を過ごしましょう！", 0, "", 2, 313, 60002);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (2, "", 0, 0, 2, 0, "今日の運勢は大吉！　悩んでいたことが解決しそうです！　　　　たまには勉強も仕事も忘れて、心あたたまる家族と過ごしましょ！", 0, "", 2, 314, 60002);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (3, "", 0, 0, 1, 0, "今日の運勢は中吉！　家の中でゆっくり過ごすのがよさそう！　　ラッキーアイテムはゲームよ！　ベストスコアを更新できるかも？", 0, "", 2, 315, 60002);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (3, "", 0, 0, 1, 0, "今日の運勢は中吉！　上手くなりたいことに積極的に挑戦してみてサックス好きの妹が、手取り足取りサポートしてくれるかも～？", 0, "", 2, 316, 60002);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (3, "", 0, 0, 1, 0, "今日の運勢は中吉！　仕事運が急上昇中です！　　　　　　　　　きっと「煉獄に舞う一陣の疾風」の如き活躍ができますよ！", 0, "", 2, 317, 60002);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (3, "", 0, 0, 1, 0, "今日の運勢は中吉！　日頃の努力が報われます！　　　　　　　　親しい友人がいるなら報告してみて。一緒に喜んでくれるはずだ。", 0, "", 2, 318, 60002);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (3, "", 0, 0, 1, 0, "今日の運勢は中吉！　商売運が上がってきています！　　　　　　ラッキーアイテムはたい焼き！　和菓子パワーで売上アップです！", 0, "", 2, 319, 60002);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (3, "", 0, 0, 1, 0, "今日の運勢は中吉！　チャンスを待つと良い結果に！　　　　　　ラッキーアイテムのプリンを食べて、運を呼び込みましょう！", 0, "", 2, 320, 60002);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (3, "", 0, 0, 2, 0, "今日の運勢は中吉！　運気が着々と上昇中！　　　　　　　　　　精霊の加護を受けたかのごとく、色々な事がうまくいきますよ♪", 0, "", 2, 321, 60002);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (4, "", 0, 0, 1, 0, "今日の運勢は小吉！　まだ見ぬ危険が隠れているかも！　　　　　でも安心しろ！　提督が貴公を守ってくれるはずだぞ！", 0, "", 2, 322, 60002);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (4, "", 0, 0, 1, 0, "今日の運勢は小吉！　いろんな事件が起こる一日になりそう！　　状況をよく観察してみようじゃないか！　まさに探偵のごとくね！", 0, "", 2, 323, 60002);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (4, "", 0, 0, 1, 0, "今日の運勢は小吉！　気の向くままに過ごすのが幸せへの近道！　美味い魚を食べて猫を眺めて、自然体で過ごすのはどうだい？", 0, "", 2, 324, 60002);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (4, "", 0, 0, 1, 0, "今日の運勢は小吉！　運気は高いけども、ハプニングには注意！　出かけた先で、「ふしんしゃ」扱いされてしまうかも……", 0, "", 2, 325, 60002);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (4, "", 0, 0, 1, 0, "今日の運勢は小吉！　同腹一心の友だちが見つかるかも！　　　　キーワードは「ユニちゃんズ」、記憶に刻みましょう！", 0, "", 2, 326, 60002);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (4, "", 0, 0, 1, 0, "今日の運勢は小吉！　周りに流されず、マイペースな一日を！　　ただし寝すぎには注意ぐうたらし過ぎず適度な運動を、だってさ～", 0, "", 2, 327, 60002);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (4, "", 0, 0, 2, 0, "今日の運勢は小吉！　根を詰めずにリラックスすると運気向上！　ラッキーパーソンはアイドル。歌があなたを幸せにしてくれます♪", 0, "", 2, 328, 60002);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 0, 190804, 0, 0, "おみくじの結果はどうでしたか？\n新たに始まる一日が、あなたに\nとって良い日になりますように\nまた明日も遊びに来て下さいね♪", 2, "カリン", 3, 329, 60002);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 7, 190804, 0, 0, "おみくじは楽しんで頂けましたか？\nあなたの新たなる年が\n素晴らしいものになるよう、\n心からお祈りいたします", 3, "カリン", 3, 330, 60002);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 1, 105901, 0, 0, "ささ、シェフィさまはこちらへ…", 1, "コッコロ", 4, 331, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 1, 106401, 0, 0, "コッコロさん、どこへ行くの…？\n…え？", 1, "シェフィ", 0, 332, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 1, 105801, 0, 0, "『おめでとうございます！\nわ～パチパチー！』\n…という感じでどうですかね！？", 1, "ペコリーヌ", 3, 333, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 1, 105901, 0, 0, "ではわたくしも\n後ろからクラッカーを鳴らして\n応戦いたします\nああ、当日が待ち遠しいですね", 2, "コッコロ", 4, 334, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 1, 106401, 0, 0, "ええと…\n二人とも何をしているの？", 2, "シェフィ", 0, 335, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 1, 106001, 0, 0, "シェフィは初めてだもんね\nあの日が近いからって\n予行練習してんのよ\n気が早いわよね～", 1, "キャル", 1, 336, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 1, 106401, 0, 0, "あの日…？", 3, "シェフィ", 0, 337, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 1, 105801, 0, 0, "その通りです…\nいよいよ記念すべきあの日が\n近づいてきましたよ！", 2, "ペコリーヌ", 3, 338, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 1, 105801, 0, 0, "『プリンセスコネクト！\nRe:Dive』3.5周年！", 3, "ペコリーヌ", 3, 339, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 1, 190801, 0, 0, "やばいですね☆", 1, "カリン", 2, 340, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 1, 106001, 0, 0, "って、なにしれっと\n参加してんのよカリン！", 2, "キャル", 1, 341, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 1, 190801, 0, 0, "ふふ、いいじゃないですか\n【ギルド管理協会】として\n私もみなさんと一緒に\nお祝いをさせてください！", 2, "カリン", 2, 342, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 1, 190801, 0, 0, "それに…記念日が近づけば\nとっておきの情報を\nお届けしますから♪", 3, "カリン", 2, 343, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 1, 105801, 0, 0, "というわけで\n今日からシェフィちゃんも一緒に\nカウントダウンしちゃいましょう！\n3.5周年まで…", 4, "ペコリーヌ", 3, 344, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 1, 106401, 0, 0, "えっ！？　ええとええと…！\nあ、あと14日…！", 4, "シェフィ", 0, 345, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 2, 105801, 0, 0, "3.5周年まであと13日\n豪華に、そして盛大に\nお祝いしたいですね☆", 5, "ペコリーヌ", 3, 346, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 2, 105901, 0, 0, "豪華といえば前にとある貴族さまが\n舞踏会を開かれたそうで\n主さまがそのお手伝いを\nしておりました", 3, "コッコロ", 1, 347, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 2, 105901, 0, 0, "そこで美しい所作を学ばれたので\nわたくしも教えていただきました\nしゃなり、しゃなり…\nいかがでしょう？", 4, "コッコロ", 1, 348, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 2, 105801, 0, 0, "すっごく上手ですよ☆\nまるでお姫様みたいです\nせっかくなのでわたしも\nしゃなりしゃなり", 6, "ペコリーヌ", 3, 349, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 2, 105901, 0, 0, "さすがでございます\nペコリーヌさま", 5, "コッコロ", 1, 350, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 2, 106001, 0, 0, "いや、あんたは\nできなきゃダメでしょ\n本物のお姫様なんだし", 3, "キャル", 0, 351, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 2, 106401, 0, 0, "しゃなり、しゃなり…\nふふ、案外できるものね", 5, "シェフィ", 2, 352, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 2, 105801, 0, 0, "わぁ！\nシェフィちゃん\n姿勢がとってもキレイです♪", 7, "ペコリーヌ", 3, 353, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 2, 105901, 0, 0, "さ、次はキャルさまの\n番でございますよ", 6, "コッコロ", 1, 354, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 2, 106001, 0, 0, "あ、あたしはいいわよ！\nそういうの全然分かんないし…", 4, "キャル", 0, 355, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 2, 106401, 0, 0, "大丈夫\nやってみると結構楽しいわよ\nほらほらっ", 6, "シェフィ", 2, 356, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 2, 106001, 0, 0, "そ、そこまで言うなら…\nしゃなにゅわきゃあっ！", 5, "キャル", 0, 357, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 2, 106001, 0, 0, "いたたた…もぉお～！\nなんであたしがこんな目に～！", 6, "キャル", 0, 358, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 2, 105901, 0, 0, "まぁまぁ\n記念日までに練習すれば\nできるようになるかもしれません\nよろしければお教えいたしますよ", 7, "コッコロ", 1, 359, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 2, 106001, 0, 0, "よし！\nあんたたちができて\nあたしだけできないのはしゃくだし\nやってやろうじゃないの！！", 7, "キャル", 0, 360, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 3, 105901, 0, 0, "3.5周年まであと12日\n記念日当日はもちろんですが\nその日を待つ時間というのも\n心躍ります", 8, "コッコロ", 1, 361, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 3, 105801, 0, 0, "分かりますっ！\nどんな特別な料理を食べられるのか\n考えるだけでワクワクしますよね！", 8, "ペコリーヌ", 3, 362, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 3, 105901, 0, 0, "特別な料理といえば、お祭りですね\nそういえば主さまは学生として\n聖学祭に参加されたこと\nがあるとか…", 9, "コッコロ", 1, 363, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 3, 106401, 0, 0, "聖学祭…って、なにかしら？", 7, "シェフィ", 2, 364, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 3, 105901, 0, 0, "聖テレサ女学院の祭典ですよ\n二つのチームが競い合い\n青春を満喫するのが趣旨のようです", 10, "コッコロ", 1, 365, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 3, 106001, 0, 0, "それってカルミナの\nサプライズライブがあった\nお祭りよね？\nちょっと見たかったかも…", 8, "キャル", 0, 366, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 3, 106001, 0, 0, "カルミナの後の\nライブも好評だったらしいじゃない\n確か、なかよし部\nだったっけ…？", 9, "キャル", 0, 367, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 3, 105801, 0, 0, "なかよし部…\n仲が良さそうな名前で\nやばいですね☆", 9, "ペコリーヌ", 3, 368, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 3, 106001, 0, 0, "いや、そのまんますぎでしょ…", 10, "キャル", 0, 369, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 3, 106401, 0, 0, "聖学祭かぁ…なんだか楽しそうね\nそれになかよし部の人たちも\n気になるわ", 8, "シェフィ", 2, 370, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 3, 105801, 0, 0, "それじゃあわたしたちも、\n負けじと仲良くしましょう♪\nほらほらシェフィちゃん、\nぎゅーっ☆　ですよ♪", 10, "ペコリーヌ", 3, 371, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 3, 106401, 0, 0, "えっ？　ええ…\nぎゅーっ…？", 9, "シェフィ", 2, 372, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 4, 106001, 0, 0, "3.5周年まであと11日！？\nお祝いの準備進んでないんだけど…\nああ～昨日に戻りたいわ…", 11, "キャル", 0, 373, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 4, 106401, 0, 0, "そういえばそんな魔道具があるって\n聞いたことがあるわね\n確か、名前はタイムラーベ…", 10, "シェフィ", 2, 374, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 4, 106001, 0, 0, "なにそれヤバいわね\nでも本当にあるんだったら\n準備よりももっとやり直したいこと\nたくさんあるんだけど…", 12, "キャル", 0, 375, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 4, 105901, 0, 0, "もしや正月の件でしょうか…？\nあの時はお金がなくて\n苦労しましたから", 11, "コッコロ", 1, 376, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 4, 106001, 0, 0, "あれはそうね…\nお金を湯水のように使う\nぺコリーヌを止められたら\nあんな苦労をすることもなかったし", 13, "キャル", 0, 377, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 4, 105801, 0, 0, "そんなこともありましたね！\nやり直したいといえば\n『きゃるきゃる～ん♪』\nの方かと思いましたよ！", 11, "ペコリーヌ", 3, 378, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 4, 106001, 0, 0, "バラすんじゃないわよ\nこのアホリーヌ！\nシェフィはそれ\n知らないんだから！", 14, "キャル", 0, 379, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 4, 106001, 0, 0, "こうなったらもう、なんとか\nタイムラーべを探し出して\n今の会話をなかったことに…\nって、どうしたのよシェフィ", 15, "キャル", 0, 380, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 4, 106401, 0, 0, "うん…もし、私だったら…\n私が知らないみんなとの時間を\n見てみたいな、って…", 11, "シェフィ", 2, 381, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 4, 105801, 0, 0, "シェフィちゃん！　タイムなんとか\nなんかなくても大丈夫ですよ！\n一緒の時間はこれから\nどんどん作ればいいんですから！", 12, "ペコリーヌ", 3, 382, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 4, 105901, 0, 0, "はい、今この瞬間も\n未来にはかけがえのない思い出に\nなっていることでしょう", 12, "コッコロ", 1, 383, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 4, 106001, 0, 0, "だからそんな顔しないの\nほら、お祝いの準備、進めるわよ！", 16, "キャル", 0, 384, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 4, 106401, 0, 0, "みんな…そうね\n最高のお祝いにして\n最高の思い出にしましょう！", 12, "シェフィ", 2, 385, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 5, 105801, 0, 0, "3.5周年まで、あと10日！\nもうすぐですよー！\n楽しみすぎて、やばいですね！", 13, "ペコリーヌ", 3, 386, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 5, 105801, 0, 0, "そういえば…たくさんのおもちゃが\n子どもたちを襲うっていう\n騒動もありましたね…\nなんでも勝手に動き出したとか！", 14, "ペコリーヌ", 3, 387, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 5, 105801, 0, 0, "学校や孤児院とかに\nちっちゃなおもちゃが乗り込んで\n暴れたみたいですよ！", 15, "ペコリーヌ", 3, 388, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 5, 106001, 0, 0, "暴れたって言っても\n所詮はおもちゃじゃない\nどうせちっさいんでしょ？", 17, "キャル", 0, 389, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 5, 106001, 0, 0, "だったら魔法で\n全部ぶっ飛ばせばいいじゃない！\n大したことないわよ！", 18, "キャル", 0, 390, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 5, 106401, 0, 0, "キャルってば…\n相手は子どもが\n大事にしてる物なのよ…？", 13, "シェフィ", 2, 391, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 5, 105901, 0, 0, "乱暴な発言は\nめっ、でございますよ？\n子どもの教育に悪影響を\n与えてしまいますから…", 13, "コッコロ", 1, 392, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 5, 105801, 0, 0, "キャルちゃんだって\n意外と猫グッズとか\nたっくさん集めてますよね？\nもしその子たちが暴れたら…？", 16, "ペコリーヌ", 3, 393, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 5, 106001, 0, 0, "う、うっさいわね！\n意外とか言うな！\n…ま、まあ確かに正面切って\n魔法でぶっ飛ばせないかもね…", 19, "キャル", 0, 394, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 5, 105801, 0, 0, "あはは☆\nキャルちゃんのそういうところ\n好きですよ♪", 17, "ペコリーヌ", 3, 395, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 5, 105801, 0, 0, "そんなキャルちゃんに猫グッズを\nプレゼントしちゃいますね！\n一部で流行っていた、たい焼き屋の\nおまけフィギュアですよ♪", 18, "ペコリーヌ", 3, 396, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 5, 105801, 0, 0, "乱暴に扱ったりしないでくださいね\nひとりでに暴れ出しちゃうかも\nしれませんから！", 19, "ペコリーヌ", 3, 397, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 6, 190801, 0, 0, "3.5周年まであと９日\nここでみなさんに\nビックニュースですよ！", 4, "カリン", 2, 398, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 6, 190801, 0, 0, "3.5周年を祝う生放送に\n去年と同様【美食殿】の\nみなさんにご出演いただくことが\n決定しましたー！", 5, "カリン", 2, 399, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 6, 106001, 0, 0, "…えっ！？", 20, "キャル", 1, 400, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 6, 105801, 0, 0, "本当ですか！？\nそれじゃ今から張り切って\n生放送中に食べる魔物料理を\n作らなくちゃですね☆", 20, "ペコリーヌ", 3, 401, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 6, 106401, 0, 0, "いや…がんばるところは\nそこじゃないような…\nというか去年も出演してたのね\nすごいじゃない！", 14, "シェフィ", 0, 402, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 6, 105901, 0, 0, "はい。とても緊張しますが\nやりがいのある大役です\n一緒にがんばりましょうね\nシェフィさま", 14, "コッコロ", 4, 403, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 6, 106401, 0, 0, "わ、私も出るの！？　でも…\n私も【美食殿】の一人だものね\nそれなら今からしっかり準備をして\nコンディションを整えないと…", 15, "シェフィ", 0, 404, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 6, 106001, 0, 0, "ふふん♪\n生放送直前のカチコチになった\nシェフィの姿が楽しみだわ！", 21, "キャル", 1, 405, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 6, 105801, 0, 0, "去年のキャルちゃんは\n緊張しすぎてずっと震えて\nかわいかったですもんね！\n今年もあの姿が見たいです！", 21, "ペコリーヌ", 3, 406, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 6, 106001, 0, 0, "ぶっ殺すぞ！？", 22, "キャル", 1, 407, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 6, 190801, 0, 0, "というわけで\n『プリンセスコネクト！\nRe:Dive』の魅力を\nお伝えする生放送をお楽しみに♪", 6, "カリン", 2, 408, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 7, 105901, 0, 0, "3.5周年まであと８日\nでございますよ\n楽しみですね、主さま", 15, "コッコロ", 1, 409, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 7, 106001, 0, 0, "そういえばあんた\n夏休みの宿題は\nきちんとやってるの？", 23, "キャル", 0, 410, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 7, 106401, 0, 0, "宿題…学校に通うのも\n楽しいことばかりじゃ\nないのね", 16, "シェフィ", 2, 411, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 7, 105801, 0, 0, "あれ？　そういえば\n少し前にルーセント学院で\n何かあったような…", 22, "ペコリーヌ", 3, 412, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 7, 106001, 0, 0, "ああ、始業式の日に\n大捕り物があったって\n話題になってたわね", 24, "キャル", 0, 413, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 7, 106401, 0, 0, "夏休みを終わらせないって\n騒いでたっていう話？\nあれ本当なの？\n都市伝説だと思ってたわ", 17, "シェフィ", 2, 414, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 7, 105901, 0, 0, "その一件ならよく存じ上げて\nおります。ですから主さま\n次の機会にはわたくしと一緒に\nがんばりましょう", 16, "コッコロ", 1, 415, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 7, 106401, 0, 0, "楽しそう…私も一緒にやりたいな\nママ…じゃ、じゃなくて！\nコッコロさん、私もまざっていい？", 18, "シェフィ", 2, 416, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 7, 105901, 0, 0, "もちろんでございます\n仲良くお勉強いたしましょう", 17, "コッコロ", 1, 417, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 7, 106001, 0, 0, "しれっと教育ママが\n生まれてる…？", 25, "キャル", 0, 418, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 7, 105801, 0, 0, "お勉強は大切ですよ！\nキャルちゃんも一緒に\nがんばりましょう！", 23, "ペコリーヌ", 3, 419, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 7, 106001, 0, 0, "え～～～～！？", 26, "キャル", 0, 420, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 8, 106001, 0, 0, "3.5周年まであと７日〜♪\nふふ～ん♪\nふんふふ～ん♪", 27, "キャル", 0, 421, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 8, 106401, 0, 0, "…\nねぇコッコロさん\nキャル、どうしちゃったの？", 19, "シェフィ", 2, 422, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 8, 105901, 0, 0, "とてもご機嫌そうに\n鼻歌を歌ってらっしゃいますね\n耳も尻尾もリズミカルに\n揺れて…ノリノリでございます", 18, "コッコロ", 1, 423, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 8, 105801, 0, 0, "あれはですね！\nカルミナのみなさんの\nサマーライブのチケットが\n手に入ったからなんですよ♪", 24, "ペコリーヌ", 3, 424, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 8, 106401, 0, 0, "ええっ！？\nあのライブのチケットって確か\n入手困難だったんじゃ…", 20, "シェフィ", 2, 425, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 8, 106001, 0, 0, "ふふふふ～ん♪\nしゃいにんぐふゅ～ちゃ～♪", 28, "キャル", 0, 426, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 8, 105901, 0, 0, "なるほど…\nキャルさまがあのように\nなってしまうのも頷けますね", 19, "コッコロ", 1, 427, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 8, 106401, 0, 0, "…カルミナの\n『Shining Future』\nの中に、シャイニングフューチャー\nって歌詞はなかったはずだけど…", 21, "シェフィ", 2, 428, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 8, 106001, 0, 0, "ふんふ…はっ！？\nあっ、あんたたち！\nなに人の鼻歌を\n勝手に聞いてるのよ！？", 29, "キャル", 0, 429, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 8, 106001, 0, 0, "こっ、これは別に\nカルミナの曲じゃなくて…\nあたしのオリジナルソングよ！\n聞いたらお金取るわよっ！？", 30, "キャル", 0, 430, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 8, 105801, 0, 0, "えへへ、キャルちゃん\nカルミナのサマーライブ\n思いっきり楽しみましょうね☆", 25, "ペコリーヌ", 3, 431, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 8, 106001, 0, 0, "も～っ！\n生暖かい目で見るな～～！\nぶっ殺すわよ～～～！", 31, "キャル", 0, 432, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 9, 106401, 0, 0, "3.5周年まであと６日\n３人も言ってたけど\nあっという間だったわね", 22, "シェフィ", 0, 433, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 9, 106401, 0, 0, "…えっ？\n生放送の意気込み！？\n突然なに！？", 23, "シェフィ", 0, 434, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 9, 106401, 0, 0, "ええと…\nとにかく失敗しないように\n台本たくさん読んで…\nそれから…", 24, "シェフィ", 0, 435, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 9, 106401, 0, 0, "えっ！？\nトラブルがあったら\nどうするのかって！？\nそ、それは…！", 25, "シェフィ", 0, 436, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 9, 106401, 0, 0, "とにかく落ち着いて\nスタッフさんの指示を聞いて…", 26, "シェフィ", 0, 437, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 9, 106401, 0, 0, "それから、それから…！\nぅ、ぅぅっ…！", 27, "シェフィ", 0, 438, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 9, 106401, 0, 0, "―びえぇぇ～んっ！！！", 28, "シェフィ", 0, 439, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 9, 105901, 0, 0, "どうされましたか、シェフィさま", 20, "コッコロ", 1, 440, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 9, 106401, 0, 0, "ううっ…ひっく…\nママッ…！", 29, "シェフィ", 0, 441, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 9, 105901, 0, 0, "よしよし、緊張されてるのですね\nママが傍におりますよ\n主さま、少々お待ちくださいませ", 21, "コッコロ", 1, 442, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 9, 105901, 0, 0, "…ふぅ\nシェフィさま\n生放送への意気込み\nお話しできそうですか？", 22, "コッコロ", 1, 443, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 9, 106401, 0, 0, "…ううっ\n恥ずかしすぎて\nもう生放送出られない…", 30, "シェフィ", 0, 444, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 10, 105901, 0, 0, "主さま\n3.5周年まであと５日\nでございます", 23, "コッコロ", 0, 445, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 10, 105901, 0, 0, "ですがその前に\n生放送がありますね", 24, "コッコロ", 0, 446, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 10, 105901, 0, 0, "…生放送に向けた\n意気込みでございますか？", 25, "コッコロ", 0, 447, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 10, 105901, 0, 0, "もちろんわたくしは\n主さまのために\n全身全霊を込めて\n楽しい時間をご提供いたします", 26, "コッコロ", 0, 448, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 10, 105901, 0, 0, "…と、意気込んでみましたが\nうまくできるか少し不安です\n声が小さくなったり\n間違えてしまったらどうしようと…", 27, "コッコロ", 0, 449, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 10, 105901, 0, 0, "ですがわたくしは\n一人ではありません\n【美食殿】のみなさまが\nおります", 28, "コッコロ", 0, 450, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 10, 105901, 0, 0, "どんな困難も\nみなさまと一緒なら\n乗り越えられると\nわたくし、確信しております", 29, "コッコロ", 0, 451, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 10, 105901, 0, 0, "それに今回はシェフィさまも\nご参加されるわけですし\nママとしてお手本に\nならねばなりませんね", 30, "コッコロ", 0, 452, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 10, 105901, 0, 0, "主さま。3.5周年も\n食卓を囲んでいる時のような\n楽しい思い出を\n一緒に作りましょうね", 31, "コッコロ", 0, 453, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 11, 105801, 0, 0, "3.5周年まであと４日！\nやばいですね☆", 26, "ペコリーヌ", 0, 454, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 11, 106001, 0, 0, "はいはい、やばいわね\nというかもうすぐ生放送だけど\nあんた準備はできてるの？", 32, "キャル", 1, 455, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 11, 105801, 0, 0, "ばっちりオイッスー☆　ですよ！\n『王家の装備』も全開です！", 27, "ペコリーヌ", 0, 456, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 11, 105801, 0, 0, "あとは前回を超える\n最高のオイッスー☆　を\nお届けできるように\n練習あるのみですかね！", 28, "ペコリーヌ", 0, 457, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 11, 105801, 0, 0, "キャルちゃんは\nどうなんですか？\nオイッスー☆　を\n毎日ちゃんと練習してますか？", 29, "ペコリーヌ", 0, 458, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 11, 106001, 0, 0, "はぁ！？　なんであたしまで\nその変なあいさつを練習しないと\nいけないのよ！", 33, "キャル", 1, 459, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 11, 105801, 0, 0, "もしかしてやってないんですか？\nダメですよキャルちゃん！\nシェフィちゃんだってちゃんと\n毎日練習してるのに！", 30, "ペコリーヌ", 0, 460, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 11, 105801, 0, 0, "こうなったらわたしがキャルちゃん\nにオイッスー☆　の『オ』から\nしっかり教えてあげないと！", 31, "ペコリーヌ", 0, 461, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 11, 105801, 0, 0, "オイッスー☆　の『オ』は\n口を大きく開いて〜\nドラゴンの骨つき肉に\nかぶりつく直前をイメージして〜", 32, "ペコリーヌ", 0, 462, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 11, 106001, 0, 0, "だからあたしはやるなんて一言も…", 34, "キャル", 1, 463, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 11, 105801, 0, 0, "オイッスー☆", 33, "ペコリーヌ", 0, 464, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 11, 106001, 0, 0, "聞きなさいっての！！", 35, "キャル", 1, 465, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 12, 106401, 0, 0, "3.5周年まであと３日…\nそして生放送は、明日…", 31, "シェフィ", 2, 466, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 12, 106401, 0, 0, "もっと台本を\n読み込んでおかないと…\nあと発声練習も…", 32, "シェフィ", 2, 467, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 12, 106401, 0, 0, "おいっす！\n…こうじゃなかったわね\nええと確か…", 33, "シェフィ", 2, 468, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 12, 106401, 0, 0, "うう…あんなに練習したのに\nこれじゃみんなに迷惑かけちゃう…", 34, "シェフィ", 2, 469, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 12, 106401, 0, 0, "【美食殿】の一員として\n認められるように\n頑張らないといけないのに\nよし、もう一度…", 35, "シェフィ", 2, 470, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 12, 105801, 0, 0, "やってますねシェフィちゃん！", 34, "ペコリーヌ", 3, 471, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 12, 106001, 0, 0, "なによ、考えることは\nみんな同じってこと？", 36, "キャル", 0, 472, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 12, 105901, 0, 0, "ふふ。シェフィさま\nよろしければ\n仲間に入れてください\n一緒にやればきっとうまくできます", 32, "コッコロ", 1, 473, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 12, 106401, 0, 0, "みんな…もちろんよ！", 36, "シェフィ", 2, 474, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 12, 105801, 0, 0, "それじゃ明日の生放送に向けて\n一緒に練習がんばりましょう☆", 35, "ペコリーヌ", 3, 475, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 13, 190801, 0, 0, "3.5周年まであと２日と\n迫った本日は…\nいよいよ生放送です！", 7, "カリン", 2, 476, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 13, 190801, 0, 0, "【美食殿】のみなさんが\n準備に入られていますので\nそれぞれ繋いでみましょう！\nまずはペコリーヌさんです！", 8, "カリン", 2, 477, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 13, 105801, 0, 0, "今日もオイッスー☆\nみんなで一緒に\nオイッスー☆", 36, "ペコリーヌ", 3, 478, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 13, 190801, 0, 0, "お手本になるような\n発声練習ですね！\nそれでは続いてコッコロさん", 9, "カリン", 2, 479, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 13, 105901, 0, 0, "はじめちょろちょろ\nなかぱっぱ～♪\n生放送前にみなさまで\n食卓を囲んで英気を養いましょう", 33, "コッコロ", 4, 480, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 13, 190801, 0, 0, "さすが【美食殿】のママですね！\nそしてキャルさんは…", 10, "カリン", 2, 481, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 13, 106001, 0, 0, "大丈夫…大丈夫…\nあたしは大丈夫…", 37, "キャル", 1, 482, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 13, 190801, 0, 0, "す、すごく震えてますけど\n大丈夫でしょうか…？\nそして今回初参加となる\n期待の新人シェフィさん！", 11, "カリン", 2, 483, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 13, 106401, 0, 0, "あうー？", 37, "シェフィ", 0, 484, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "105901,34,106001,38,190801,12", 13, 105801, 0, 1, "あ…", 37, "みんな", 4, 485, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 13, 106001, 0, 0, "ちょ、ちょっとシェフィ！？\nなに赤ちゃん返りしてん―\nあ痛ぁっ！\n噛んだわこいつーっ！", 39, "キャル", 1, 486, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 13, 105901, 0, 0, "シェフィさま\n緊張しすぎて\nしまったのでしょうね…\nおーよしよし…", 35, "コッコロ", 4, 487, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 13, 105801, 0, 0, "シェフィちゃん！\nそういう時は手のひらに\n好きな文字を書いて\n食べちゃうといいですよ！", 38, "ペコリーヌ", 3, 488, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 13, 190801, 0, 0, "…ええと、現場からは以上です！\n生放送はもうすぐ！\n特別なお知らせもありますので\nどうぞお楽しみに！", 13, "カリン", 2, 489, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 14, 106401, 0, 0, "3.5周年の記念日も\nいよいよ明日ね\n…って、あれ？", 38, "シェフィ", 2, 490, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 14, 105801, 0, 0, "記念日はこの森へ行きませんか！？\nすっごく美味しいお肉が\nとれるんです☆", 39, "ペコリーヌ", 3, 491, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 14, 106001, 0, 0, "うげ、またあの魔物？\nさすがに今度は\n丸焼きとかはやめてよね…\n見た目がヤバすぎるし", 40, "キャル", 0, 492, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 14, 105901, 0, 0, "ふふ、キャルさまもだいぶ\n魔物肉に慣れてきたようですね\n調理ならばわたくしに\nお任せください♪", 36, "コッコロ", 1, 493, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 14, 106401, 0, 0, "みんな、何をしているの？\nなんだか、\n不穏な単語が聞こえたんだけど…", 39, "シェフィ", 2, 494, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 14, 105801, 0, 0, "シェフィちゃん！\n明日の食べ尽くしツアーの\n相談をしてたんですよ！", 40, "ペコリーヌ", 3, 495, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 14, 105901, 0, 0, "はい。明日は【美食殿】の\n一員となったシェフィさまと一緒に\n初めて記念日を過ごすのですから\n特別なコース料理を用意しませんと", 37, "コッコロ", 1, 496, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 14, 106001, 0, 0, "ふふん♪\n猫の毛も逆立つような\nあれこれを食べさせられるから\n覚悟しておいたほうがいいわよ？", 41, "キャル", 0, 497, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 14, 106401, 0, 0, "へ、へぇ…", 40, "シェフィ", 2, 498, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 14, 105801, 0, 0, "期待しててくださいね！\nシェフィちゃんとあなたに\n『おいちー！』って喜んでもらえる\n最高のごちそうを準備しますから☆", 41, "ペコリーヌ", 3, 499, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 14, 106401, 0, 0, "ええと…その…うーんと…\nわ、分かったわ\n私も【美食殿】の一員だもの", 41, "シェフィ", 2, 500, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 14, 106401, 0, 0, "それにあなたも一緒みたいだし\n最後までがんばってみるわ…！", 42, "シェフィ", 2, 501, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 15, 105801, 0, 0, "それではいつものあれを\nやりましょう！\n準備はいいですかっ？\n…いっきますよー！", 42, "ペコリーヌ", 3, 502, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 15, 106001, 0, 0, "祝！", 42, "キャル", 1, 503, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 15, 105901, 0, 0, "3.5周年！", 38, "コッコロ", 4, 504, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 15, 106401, 0, 0, "おめでとうございます！", 43, "シェフィ", 0, 505, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 15, 190801, 0, 0, "この日を迎えられたのも\nあなたが『プリンセスコネクト！\nRe:Dive』を遊んでくれている\nおかげです！", 14, "カリン", 2, 506, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 15, 190801, 0, 0, "ということで\nみなさんからあなたへ一言ずつ\n感謝の言葉をどうぞ！", 15, "カリン", 2, 507, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 15, 105801, 0, 0, "感謝の言葉ですか？　わたしは\n毎日あなたに感謝してますよ\n【美食殿】の仲間になってくれて…\n一緒にごはんを食べてくれて…", 43, "ペコリーヌ", 3, 508, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 15, 105801, 0, 0, "なにより今でもわたしをぺコリーヌ\nって呼んでくれてありがとうござい\nます。これからもわたしはあなたの\nぺコリーヌです！　やばいですね☆", 44, "ペコリーヌ", 3, 509, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 15, 106001, 0, 0, "えっ！？　今度はあたし！？\n感謝って…まぁ、そうね…\nあんたにはあたしが\n辛くて苦しい時に助けてもらって…", 43, "キャル", 1, 510, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 15, 106001, 0, 0, "でもおかげで色々吹っ切れたわ！\nもしあんたが困ってたら今度は\nあたしが助けてあげるから\nその時はちゃんと言いなさいよ！", 44, "キャル", 1, 511, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 15, 106401, 0, 0, "ええと…次は私ね\n実はよく覚えてないのだけど\n空から落ちてきた私を受け止めて\nくれたのはあなたなのでしょう？", 44, "シェフィ", 0, 512, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 15, 106401, 0, 0, "最初に私を見つけてくれたのが\nあなたで良かったって思ってるの\nおかげで私は居場所を見つけたから\nあなたがくれたもの、大切にするわ", 45, "シェフィ", 0, 513, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 15, 105901, 0, 0, "では最後にわたくしから…\nわたくしは主さまの元を離れた日…\nこのように記念日をお祝いする\nことは二度とないと思っていました", 39, "コッコロ", 4, 514, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 15, 105901, 0, 0, "ですが、主さまはわたくしを\n迎えにきてくださり、今もこうして\nおそばにおいてくださっています\nわたくしにとって毎日が記念日です", 40, "コッコロ", 4, 515, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 15, 105901, 0, 0, "わたくしたちの物語は\nまだ続いていきます\nこれからも一緒にページを\n作っていきましょうね", 41, "コッコロ", 4, 516, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 15, 105801, 0, 0, "それじゃ\nいつものあれでしめますよ！\nみんなで一緒に、さんはい☆", 45, "ペコリーヌ", 3, 517, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "105901,42,106001,45,106401,46,190801,16", 15, 105801, 0, 1, "これからも\nよろしくおねがいします！", 46, "みんな", 4, 518, 80005);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (0, "", 0, 190804, 1, 0, "新年を迎え、新たに始まる一日を\nおみくじで占ってみませんか？\n良い運勢であるほど、\n沢山のプレゼントが貰えますよ♪", 1, "カリン", 1, 519, 60003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (1, "", 0, 0, 1, 0, "今日の運勢は姫吉！　どんな不可能も可能にできちゃう！　　　　仲間と食卓を囲めばさらに運気上昇よ！　ただし魔物料理以外！", 0, "", 2, 520, 60003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (1, "", 0, 0, 1, 0, "今日の運勢は姫吉！　やること全てが最高潮！　やばいですね☆　ラッキーアイテムはおにぎり！　お腹一杯で幸せが続くでしょう！", 0, "", 2, 521, 60003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (1, "", 0, 0, 1, 0, "今日の運勢は姫吉！　羽が生えたように飛躍できる絶好調の運気！新しいことに挑戦するなら今しかないわ！　やってみましょう！", 0, "", 2, 522, 60003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (1, "", 0, 0, 1, 0, "今日の運勢は姫吉！　精霊も新年の幸福をお祝いしています！　　主さまの笑顔が見られて、わたくしも幸せいっぱいでございます♪", 0, "", 2, 523, 60003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (1, "", 0, 0, 2, 0, "今日の運勢は姫吉！　やること全てが上手くいく最高の一日！　　あなたの活躍に先生もにっこりです。いい子いい子♪", 0, "", 2, 524, 60003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (2, "", 0, 0, 1, 0, "今日の運勢は大吉！　お裁縫好きな女の子のお墨付きです！　　　お世話したがりの妹にめいっぱい愛されちゃうかも！？", 0, "", 2, 525, 60003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (2, "", 0, 0, 1, 0, "今日の運勢は大吉！　ラッキーカラーは赤！　　　　　　　　　　湧き上がるアイデアの数々に、高笑いが止まりませんわ！", 0, "", 2, 526, 60003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (2, "", 0, 0, 1, 0, "今日の運勢は大吉！　ラッキーカラーは鮮やかな黄色！　　　　　さらにシマシマのアイテムを持つと、運気倍増らしいッスよ♪", 0, "", 2, 527, 60003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (2, "", 0, 0, 1, 0, "今日の運勢は大吉！　秘められた力が開花するでしょう！　　　　じゃあ、私の龍眼と力比べしてみようか？　ふふ、冗談だよ～", 0, "", 2, 528, 60003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (2, "", 0, 0, 2, 0, "今日の運勢は大吉！　悩んでいたことが解決しそうです！　　　　たまには勉強も仕事も忘れて、心あたたまる家族と過ごしましょ！", 0, "", 2, 529, 60003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (3, "", 0, 0, 1, 0, "今日の運勢は中吉！　家の中でゆっくり過ごすのがよさそう！　　ラッキーアイテムはゲームよ！　ベストスコアを更新できるかも？", 0, "", 2, 530, 60003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (3, "", 0, 0, 1, 0, "今日の運勢は中吉！　上手くなりたいことに積極的に挑戦してみてサックス好きの妹が、手取り足取りサポートしてくれるかも～？", 0, "", 2, 531, 60003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (3, "", 0, 0, 1, 0, "今日の運勢は中吉！　仕事運が急上昇中です！　　　　　　　　　きっと「煉獄に舞う一陣の疾風」の如き活躍ができますよ！", 0, "", 2, 532, 60003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (3, "", 0, 0, 1, 0, "今日の運勢は中吉！　日頃の努力が報われます！　　　　　　　　親しい友人がいるなら報告してみて。一緒に喜んでくれるはずだ。", 0, "", 2, 533, 60003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (3, "", 0, 0, 1, 0, "今日の運勢は中吉！　ラッキーアイテムはかわいいもの！　　　　ア、アタシはかわいいじゃなくてキレイだってば！　も～っ！", 0, "", 2, 534, 60003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (3, "", 0, 0, 1, 0, "今日の運勢は中吉！　チャンスを待つと良い結果に！　　　　　　ラッキーアイテムのプリンを食べて、運を呼び込みましょう！", 0, "", 2, 535, 60003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (3, "", 0, 0, 2, 0, "今日の運勢は中吉！　悩まず行動すれば強運に恵まれる！　　　　守るよりも攻めるが吉だ！　勝負事をするにはいい運勢だな！", 0, "", 2, 536, 60003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (4, "", 0, 0, 1, 0, "今日の運勢は小吉！　まだ見ぬ危険が隠れているかも！　　　　　でも安心しろ！　提督が貴公を守ってくれるはずだぞ！", 0, "", 2, 537, 60003);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (4, "", 0, 0, 1, 0, "今日の運勢は小吉！　いろんな事件が起こる一日になりそう！　　状況をよく観察してみようじゃないか！　まさに探偵のごとくね！", 0, "", 2, 538, 60003);
