INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (1, "カリン", 0, 1, 1, 1, 0, 60000, 190804, 0, "新年を迎え、新たに始まる一日を\nおみくじで占ってみませんか？\n良い運勢であるほど、\n沢山のプレゼントが貰えますよ♪", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (2, "", 1, 0, 2, 1, 0, 60000, 0, 0, "今日の運勢は姫吉！　あらゆる運気が最大を示しています！　　　今なら「伝説の吸血鬼」をも従え、世界征服も夢じゃありません！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (3, "", 1, 0, 2, 1, 0, 60000, 0, 0, "今日の運勢は姫吉！　やること全てが最高潮！　やばいですね☆　ラッキーアイテムはおにぎり！　お腹一杯で幸せが続くでしょう！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (4, "", 1, 0, 2, 2, 0, 60000, 0, 0, "今日の運勢は姫吉！　その高い運気はまさにショーグンの如く！　その溢れる威光は人々を惹きつけ、皆の人気者間違いなしです！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (5, "", 2, 0, 2, 1, 0, 60000, 0, 0, "今日の運勢は大吉！　運気がどんどん高まっています！　　　　　何をやっても、最大効率で素晴らしい結果になりますよ♪", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (6, "", 2, 0, 2, 1, 0, 60000, 0, 0, "今日の運勢は大吉！　なにをやっても上手くいって大活躍！　　　そんな素敵なあなたを見て、後ろから誰かが……クスクス♪", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (7, "", 2, 0, 2, 2, 0, 60000, 0, 0, "今日の運勢は大吉！　運気が高く、何かに挑戦すると良い結果に！隠れた才能が引き出されそう！　目指せヒデサ……ではなく秀才！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (8, "", 3, 0, 2, 1, 0, 60000, 0, 0, "今日の運勢は中吉！　商売運が上がってきています！　　　　　　ラッキーアイテムはたいやき！　和菓子パワーで売上アップです！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (9, "", 3, 0, 2, 1, 0, 60000, 0, 0, "今日の運勢は中吉！　旅行運がぐんぐんと上昇中♪　　　　　　　もしかしたら、あの「マホマホ王国」にだって行けるかも♪", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (10, "", 3, 0, 2, 1, 0, 60000, 0, 0, "今日の運勢は中吉！　仕事運が急上昇中です！　　　　　　　　　きっと「煉獄に舞う一陣の疾風」の如き活躍ができますよ！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (11, "", 3, 0, 2, 1, 0, 60000, 0, 0, "今日の運勢は中吉！　チャンスを待つと良い結果に！　　　　　　ラッキーアイテムのプリンを食べて、運を呼び込みましょう！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (12, "", 3, 0, 2, 2, 0, 60000, 0, 0, "今日の運勢は中吉！　運気が着々と上昇中！　　　　　　　　　　精霊の加護を受けたかの如く、色々な事がうまくいきますよ♪", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (13, "", 4, 0, 2, 1, 0, 60000, 0, 0, "今日の運勢は小吉！　早朝に幸運の兆しあり！　　　　　　　　　もしかしたら、優しいお姉ちゃんが起こしにきてくれますよ♪", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (14, "", 4, 0, 2, 1, 0, 60000, 0, 0, "今日の運勢は小吉！　運気上昇中ですが、上がるまでが要注意！　ラッキーアニマルは熊！　本当の家族のように守ってくれるかも♪", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (15, "", 4, 0, 2, 1, 0, 60000, 0, 0, "今日の運勢は小吉！　外に出てゆっくり過ごすと運気が上昇！　　ラッキースポットは牧場。面白い出会いがありそうです♪", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (16, "", 4, 0, 2, 1, 0, 60000, 0, 0, "今日の運勢は小吉！　運気は高いけども、ハプニングには注意！　出かけた先で、「ふしんしゃ」扱いされてしまうかも……", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (17, "", 4, 0, 2, 2, 0, 60000, 0, 0, "今日の運勢は小吉！　根を詰めずにリラックスすると運気向上！　ラッキーパーソンはアイドル。歌があなたを幸せにしてくれます♪", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (18, "カリン", 0, 2, 3, 0, 0, 60000, 190804, 0, "おみくじの結果はどうでしたか？\n新たに始まる一日が、あなたに\nとって良い日になりますように\nまた明日も遊びに来て下さいね♪", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (19, "カリン", 0, 3, 3, 0, 0, 60000, 190804, 7, "おみくじは楽しんで頂けましたか？\nあなたの新たなる年が\n素晴らしいものになるよう、\n心からお祈りいたします", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (20, "ハツネ", 0, 1, 0, 0, 0, 80001, 101201, 1, "キミと出会ってもう\n1年もたつんだね！\nう～ん、いつも\n寝てばっかりだから、", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (21, "ハツネ", 0, 2, 0, 0, 0, 80001, 101201, 1, "少しでも\n寝る時間を減らして\nキミと一緒にいる\n時間増やしたいなぁ♪", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (22, "シオリ", 0, 1, 1, 0, 0, 80001, 103801, 1, "あなたと知り合ってから\nもう1年…\n時がたつのは\n早いですね", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (23, "シオリ", 0, 2, 1, 0, 0, 80001, 103801, 1, "来年も、\n一緒にいられたら…\nうれしいな", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (24, "キョウカ", 0, 1, 0, 0, 0, 80001, 103601, 2, "あなたと一緒にいるようになって、\nもう1年ですか…", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (25, "キョウカ", 0, 2, 0, 0, 0, 80001, 103601, 2, "最初の頃よりも、頼もしく\nなりましたね。私はどうですか？\n成長していますか？", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (26, "ミミ", 0, 1, 1, 0, 0, 80001, 102001, 2, "おにいちゃんとあってから、\n1年たったよ～！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (27, "ミミ", 0, 2, 1, 0, 0, 80001, 102001, 2, "ミミね、おにいちゃんと\nいっぱい遊べてたのしかったな～♪\nこれからもずっと\nいっしょにいてね～！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (28, "ミソギ", 0, 1, 2, 0, 0, 80001, 100401, 2, "にいちゃんがミソギのイタズラに\n引っかかり続けてもう1年かぁ", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (29, "ミソギ", 0, 2, 2, 0, 0, 80001, 100401, 2, "…これからもいっぱいミソギと\n遊んでね、にいちゃん！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (30, "マホ", 0, 1, 0, 0, 0, 80001, 101001, 3, "もう王子はんと会うてから\n1年ぐらいたつそうどすえ\n早いもんやなあ", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (31, "マホ", 0, 2, 0, 0, 0, 80001, 101001, 3, "このままやと、\nうちもあっという間に\n干からびてしまいそうどすえ\nなーんてな。うふふ", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (32, "イリヤ", 0, 1, 1, 0, 0, 80001, 104401, 3, "お主と会って早1年じゃな\nこれからも存分に尽くすがよい", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (33, "イリヤ", 0, 2, 1, 0, 0, 80001, 104401, 3, "わらわもたまには、お主の献身に\n報いてやらぬでもない♪", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (34, "カオリ", 0, 1, 2, 0, 0, 80001, 101701, 3, "もう、キミと出会って結構な時間が\nたつさ～。でも、私たちの旅は\nまだまだ始まったばかりさ～！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (35, "カオリ", 0, 2, 2, 0, 0, 80001, 101701, 3, "これからも、どうぞよろしく\nお願いするさ～！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (36, "サレン", 0, 1, 0, 0, 0, 80001, 102801, 4, "あんたと出会ってからもうそんなに\nたつのね～", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (37, "サレン", 0, 2, 0, 0, 0, 80001, 102801, 4, "あっという間だったって感じるほど\n楽しかったわ！\nこれからも一緒に頑張りましょ！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (38, "スズメ", 0, 1, 1, 0, 0, 80001, 102501, 4, "出会ってから1年間、ずっと\nご迷惑をかけっぱなしで、\nすみません…", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (39, "スズメ", 0, 2, 1, 0, 0, 80001, 102501, 4, "これからはもっと頑張りますから\n見ていてくださいね\nできれば…\n今までよりも、私の近くで♪", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (40, "タマキ", 0, 1, 0, 0, 0, 80001, 104601, 5, "振り返れば、今までいろんなことが\nあったにゃあ…", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (41, "タマキ", 0, 2, 0, 0, 0, 80001, 104601, 5, "これからも、二人でたくさんの\n思い出をがっぽり作っていく\nにゃー！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (42, "ミフユ", 0, 1, 1, 0, 0, 80001, 104801, 5, "キミとの師弟関係も、\nもう1年なのね…\nでもキミに教えることは、", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (43, "ミフユ", 0, 2, 1, 0, 0, 80001, 104801, 5, "まだまだいっぱいあるわ\nこれからも、\nしっかりついて来てね！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (44, "アキノ", 0, 1, 0, 0, 0, 80001, 103201, 6, "あなたさまと出会ってもう1年…", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (45, "アキノ", 0, 2, 0, 0, 0, 80001, 103201, 6, "光陰矢のごとし、とはまさに\nこのことですわ。…思い出は決して\nお金では買えませんわね", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (46, "ユカリ", 0, 1, 1, 0, 0, 80001, 103401, 6, "ダメな私を1年間も見捨てないで\nくれて、本当にありがとう", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (47, "ユカリ", 0, 2, 1, 0, 0, 80001, 103401, 6, "情けないところいっぱいみせるかも\nしれないけど、これからも一緒に\nいてくれると、うれしいな", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (48, "クリスティーナ", 0, 1, 0, 0, 0, 80001, 107101, 7, "オマエと出会ってもう1年か\nまさかこのワタシの結婚相手に\nなろうとはな…", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (49, "クリスティーナ", 0, 2, 0, 0, 0, 80001, 107101, 7, "ともかくこれからも\n愉しませてくれよ☆", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (50, "ジュン", 0, 1, 1, 0, 0, 80001, 104701, 7, "君と出会ってからもう1年か。\n以前と比べて私の表情もいくらか\n柔らかくなった気がするんだが、\nどうだろう？", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (51, "ジュン", 0, 2, 1, 0, 0, 80001, 104701, 7, "見えないから分からないって？\nそれもそうだな…", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (52, "トモ", 0, 1, 0, 0, 0, 80001, 103701, 8, "もう1年たったみたいだね\nあなたをからかってばかりだった\nけど、楽しかった♪", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (53, "トモ", 0, 2, 0, 0, 0, 80001, 103701, 8, "これからもよろしくね！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (54, "マツリ", 0, 1, 1, 0, 0, 80001, 100501, 8, "出会って、もう1年たっちゃった\nんスね。なんだか意外ッス", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (55, "マツリ", 0, 2, 1, 0, 0, 80001, 100501, 8, "毎日楽しくて、あっという間に過ぎ\nちゃったみたいなんスよね\nこれからも一緒に悪と戦うッス！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (56, "ミヤコ", 0, 1, 0, 0, 0, 80001, 100701, 9, "オマエと会って1年なの！\n全然成仏もできなかったし、", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (57, "ミヤコ", 0, 2, 0, 0, 0, 80001, 100701, 9, "生き返りもしなかったけど、\nすごく楽しかったの～♪\nオマエとプリンがあれば、\nミヤコはハッピーなの～♪", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (58, "シノブ", 0, 1, 1, 0, 0, 80001, 103101, 9, "あなたと出会って1年なんですね\nでも、私の占いでは、私とあなたは", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (59, "シノブ", 0, 2, 1, 0, 0, 80001, 103101, 9, "来年も再来年も一緒にいるようです\nふふ、よろしくお願いしますね", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (60, "アンナ", 0, 1, 0, 0, 0, 80001, 100901, 10, "貴様が閃光のシグルドとして覚醒し\nもう1年か…。これからも我らの\n長き戦いに終わりはないが、", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (61, "アンナ", 0, 2, 0, 0, 0, 80001, 100901, 10, "それでも貴様となら…\n添い遂げる覚悟はできているぞ！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (62, "ナナカ", 0, 1, 1, 0, 0, 80001, 101301, 10, "君と出会ってもう1年だって！\n早いねー！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (63, "ナナカ", 0, 2, 1, 0, 0, 80001, 101301, 10, "これからも最強のバディタッグで\n大活躍だー！\n頼りにしてるよ、相棒！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (64, "ミツキ", 0, 1, 0, 0, 0, 80001, 105101, 11, "1年たつのって、あっという間ね\nまだまだ君の知らない部分が\nたくさんあるし…", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (65, "ミツキ", 0, 2, 0, 0, 0, 80001, 105101, 11, "これからも、いろんなところを\n調べさせてね？", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (66, "ルカ", 0, 1, 1, 0, 0, 80001, 105601, 11, "お前さんと出会ってもう1年か\n助太刀するつもりが", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (67, "ルカ", 0, 2, 1, 0, 0, 80001, 105601, 11, "逆に助けられたりもしたね\nこれからもよろしく頼むよ！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (68, "エリコ", 0, 1, 2, 0, 0, 80001, 102701, 11, "早いものですわね、あなた様と\nお会いして、もう1年…", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (69, "エリコ", 0, 2, 2, 0, 0, 80001, 102701, 11, "あなた様といると、時があっと\nいう間に過ぎてしまいます\n時間を止める研究をしなくては\nいけませんね…", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (70, "アヤネ", 0, 1, 0, 0, 0, 80001, 102301, 12, "お兄ちゃんと会ってから、もう、\n1年なんだね。私、少しは\nお姉ちゃんっぽくなったかな？", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (71, "アヤネ", 0, 2, 0, 0, 0, 80001, 102301, 12, "あ、ぷうきちもお兄ちゃんっぽく\nなったよね？", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (72, "チカ", 0, 1, 1, 0, 0, 80001, 104201, 12, "あなたと出会って、\nもう1年がたったんですね…\nこれからもそばにいて、", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (73, "チカ", 0, 2, 1, 0, 0, 80001, 104201, 12, "私のことを見守ってください\nできることなら、ずっと…", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (74, "クルミ", 0, 1, 2, 0, 0, 80001, 102101, 12, "お兄ちゃんと出会って1年かぁ\n私、相変わらずダメダメで…", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (75, "クルミ", 0, 2, 2, 0, 0, 80001, 102101, 12, "で、でもこれからもずっと…\n一緒にいたいなぁ…", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (76, "シズル", 0, 1, 0, 0, 0, 80001, 104901, 13, "再会してちょうど1年だね\nお祝いにごちそう作ってきたよ～♪\n特製ケーキと、お寿司と、", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (77, "シズル", 0, 2, 0, 0, 0, 80001, 104901, 13, "ハンバーグと、カレーと、\nシチューと、あ、隠しきれない隠し\n味は、お姉ちゃんの愛情だよ！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (78, "リノ", 0, 1, 1, 0, 0, 80001, 101101, 13, "お兄ちゃんと再会して、もう1年\nなんですね。\n会えなかった時を思うと、", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (79, "リノ", 0, 2, 1, 0, 0, 80001, 101101, 13, "信じられないくらい幸せな1年\nでした！もう絶対、私から離れ\nないでくださいね♪", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (80, "ユイ", 0, 1, 0, 0, 0, 80001, 100201, 14, "騎士クンと出会ってから今まで、\nいろんなことがあったよね\n楽しいこと、ドキドキしたこと…", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (81, "ユイ", 0, 2, 0, 0, 0, 80001, 100201, 14, "そんな日々をこれからも、\nず、ずっと一緒に…\n過ごしていきたいな…！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (82, "ヒヨリ", 0, 1, 1, 0, 0, 80001, 100101, 14, "騎士くんと出会って、まだ1年しか\nたってないんだ！？", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (83, "ヒヨリ", 0, 2, 1, 0, 0, 80001, 100101, 14, "なんかもうずっと一緒にいる気が\nするなぁ…えへへ。これからの\n1年もまたよろしくねっ！\n騎士くん！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (84, "レイ", 0, 1, 2, 0, 0, 80001, 100301, 14, "初めてキミに出会って1年…\n随分見違えたね私もキミも、\n更に強くなっていく…", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (85, "レイ", 0, 2, 2, 0, 0, 80001, 100301, 14, "キミに背中を預けられる日が、\n早くきますように", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (86, "ペコリーヌ", 0, 1, 0, 0, 0, 80001, 105801, 15, "1年もわたしのことを、忘れない\nでいてくれて、ありがとうござい\nます！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (87, "ペコリーヌ", 0, 2, 0, 0, 0, 80001, 105801, 15, "これからもずっと、覚えていて\nくださいね！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (88, "コッコロ", 0, 1, 1, 0, 0, 80001, 105901, 15, "こうして1年も、主さまのおそばに\n置いていただけて、とても光栄\nです。どうかこれからも、", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (89, "コッコロ", 0, 2, 1, 0, 0, 80001, 105901, 15, "末永く主さまをお世話させて\nください。わたくしは、ずっと\n主さまのガイド役でいたいのです", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (90, "キャル", 0, 1, 2, 0, 0, 80001, 106001, 15, "1年か、まっさかこんなに長い\n付き合いになるなんてね～\nでも…わりと楽しい1年だったかも", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (91, "キャル", 0, 2, 2, 0, 0, 80001, 106001, 15, "だから、もうしばらくあんたに\n付き合ってやるわよ喜びなさい！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (92, "スズメ", 0, 1, 1, 1, 0, 60001, 112101, 0, "新しい年のはじめに、\nおみくじはいかがでしょうか？\nよい運勢であるほど、\n沢山プレゼントが貰えますよ♪", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (93, "", 1, 0, 2, 1, 0, 60001, 0, 0, "今日の運勢は姫吉！　あらゆる運気が最大を示しています！　　　今なら「伝説の吸血鬼」をも従え、世界征服も夢じゃありません！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (94, "", 1, 0, 2, 1, 0, 60001, 0, 0, "今日の運勢は姫吉！　やること全てが最高潮！　やばいですね☆　ラッキーアイテムはおにぎり！　お腹一杯で幸せが続くでしょう！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (95, "", 1, 0, 2, 1, 0, 60001, 0, 0, "今日の運勢は姫吉！　最強の運勢でどんなドジも跳ね除けます！　大切な人と一緒に、最高の一日になること間違いなし！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (96, "", 1, 0, 2, 1, 0, 60001, 0, 0, "今日の運勢は姫吉！　どんな挑戦も上手くいく、最高の運勢です！理想の自分に変身できる絶好の機会！　新しい一歩を踏み出そう！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (97, "", 1, 0, 2, 2, 0, 60001, 0, 0, "今日の運勢は姫吉！　その高い運気はまさにショーグンの如く！　その溢れる威光は人々を惹きつけ、皆の人気者間違いなしです！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (98, "", 2, 0, 2, 1, 0, 60001, 0, 0, "今日の運勢は大吉！　運気がどんどん高まっています！　　　　　何をやっても、最大効率で素晴らしい結果になりますよ♪", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (99, "", 2, 0, 2, 1, 0, 60001, 0, 0, "今日の運勢は大吉！　なにをやっても上手くいって大活躍！　　　そんな素敵なあなたを見て、後ろから誰かが……クスクス♪", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (100, "", 2, 0, 2, 1, 0, 60001, 0, 0, "今日の運勢は大吉！　ラッキーワードは「ちぇる～ん♪」　　　　出会う人みんなに言ってあげると、ハッピーになれますよ！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (101, "", 2, 0, 2, 1, 0, 60001, 0, 0, "今日の運勢は大吉！　運勢はバッチリで無敵状態！　　　　　　　龍眼で全てを見通して、思い通りの一日を過ごしましょう！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (102, "", 2, 0, 2, 2, 0, 60001, 0, 0, "今日の運勢は大吉！　運気が高く、何かに挑戦すると良い結果に！隠れた才能が引き出されそう！　目指せヒデサ……ではなく秀才！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (103, "", 3, 0, 2, 1, 0, 60001, 0, 0, "今日の運勢は中吉！　商売運が上がってきています！　　　　　　ラッキーアイテムはたいやき！　和菓子パワーで売上アップです！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (104, "", 3, 0, 2, 1, 0, 60001, 0, 0, "今日の運勢は中吉！　旅行運がぐんぐんと上昇中♪　　　　　　　もしかしたら、あの「マホマホ王国」にだって行けるかも♪", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (105, "", 3, 0, 2, 1, 0, 60001, 0, 0, "今日の運勢は中吉！　仕事運が急上昇中です！　　　　　　　　　きっと「煉獄に舞う一陣の疾風」の如き活躍ができますよ！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (106, "", 3, 0, 2, 1, 0, 60001, 0, 0, "今日の運勢は中吉！　チャンスを待つと良い結果に！　　　　　　ラッキーアイテムのプリンを食べて、運を呼び込みましょう！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (107, "", 3, 0, 2, 1, 0, 60001, 0, 0, "今日の運勢は中吉！　お出かけした先で良縁に恵まれそうです！　ダウナー系あざとい女子が、あま～い飴をくれるかも？", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (108, "", 3, 0, 2, 1, 0, 60001, 0, 0, "今日の運勢は中吉！　大抵のことはゴリ押しでなんとかできます！ラッキープレイスはゲーセン。パンチングマシンでストレス発散！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (109, "", 3, 0, 2, 2, 0, 60001, 0, 0, "今日の運勢は中吉！　運気が着々と上昇中！　　　　　　　　　　精霊の加護を受けたかの如く、色々な事がうまくいきますよ♪", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (110, "", 4, 0, 2, 1, 0, 60001, 0, 0, "今日の運勢は小吉！　早朝に幸運の兆しあり！　　　　　　　　　もしかしたら、優しいお姉ちゃんが起こしにきてくれますよ♪", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (111, "", 4, 0, 2, 1, 0, 60001, 0, 0, "今日の運勢は小吉！　運気上昇中ですが、上がるまでが要注意！　ラッキーアニマルは熊！　本当の家族のように守ってくれるかも♪", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (112, "", 4, 0, 2, 1, 0, 60001, 0, 0, "今日の運勢は小吉！　外に出てゆっくり過ごすと運気が上昇！　　ラッキースポットは牧場。面白い出会いがありそうです♪", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (113, "", 4, 0, 2, 1, 0, 60001, 0, 0, "今日の運勢は小吉！　運気は高いけども、ハプニングには注意！　出かけた先で、「ふしんしゃ」扱いされてしまうかも……", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (114, "", 4, 0, 2, 1, 0, 60001, 0, 0, "今日の運勢は小吉！　同腹一心の友だちが見つかるかも！　　　　キーワードは「ユニちゃんズ」、記憶に刻みましょう！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (115, "", 4, 0, 2, 1, 0, 60001, 0, 0, "今日の運勢は小吉！　思わぬ相手と仲良くなれるかも？　　　　　うっかり毒舌をもらしてしまわないように要注意です！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (116, "", 4, 0, 2, 2, 0, 60001, 0, 0, "今日の運勢は小吉！　根を詰めずにリラックスすると運気向上！　ラッキーパーソンはアイドル。歌があなたを幸せにしてくれます♪", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (117, "スズメ", 0, 2, 3, 0, 0, 60001, 112101, 0, "おみくじの結果はいかがでしたか？\n新たに始まる一日が、\nあなたにとってよい日になりますように\n明日もお待ちしていますね♪", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (118, "スズメ", 0, 3, 3, 0, 0, 60001, 112101, 7, "おみくじ、楽しんで頂けましたか？\nあなたの新しい年が\n素晴らしいものになるよう、\n心からお祈りしますね", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (119, "ペコリーヌ", 0, 1, 0, 0, 0, 80003, 105801, 1, "ヒューヒュー♪\nドンドンパフパフー♪", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (120, "コッコロ", 0, 1, 1, 0, 0, 80003, 105901, 1, "ひゅーひゅー\nどんどんぱふぱふー\nでございます", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (121, "コッコロ", 0, 2, 1, 0, 0, 80003, 105901, 1, "さぁ、キャルさまもご一緒に\nどんどんぱふぱふいたしましょう", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (122, "キャル", 0, 1, 2, 0, 0, 80003, 106001, 1, "え？　ちょっと\nいきなりなにっ？", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (123, "キャル", 0, 2, 2, 0, 0, 80003, 106001, 1, "ドンドンパフパフってなんなの？\nあたしにも分かるように\nちゃんと説明しなさいっての！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (124, "ペコリーヌ", 0, 2, 0, 0, 0, 80003, 105801, 1, "実はですねキャルちゃん！\nもうすぐわたしたちにとっての\n大切な節目──", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (125, "ペコリーヌ", 0, 3, 0, 0, 0, 80003, 105801, 1, "『プリンセスコネクト！\nRe:Dive』\n2.5周年なんですよ！\nやばいですね☆", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (126, "コッコロ", 0, 3, 1, 0, 0, 80003, 105901, 1, "ですからその日に向けて\n今から気分を盛り上げているのです", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (127, "キャル", 0, 3, 2, 0, 0, 80003, 106001, 1, "ああ、そういえば\nそんな時期だっけ\nって言っても、もう少し先じゃない\n気が早いんじゃないの？", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (128, "コッコロ", 0, 4, 1, 0, 0, 80003, 105901, 1, "いいえ、キャルさま\nこういったお祝いは準備から\n楽しむものでございます\nそうですよね、ペコリーヌさま", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (129, "ペコリーヌ", 0, 4, 0, 0, 0, 80003, 105801, 1, "その通りです♪　というわけで！\nカウントダウンの始まりですよ！\nみんなで一緒に2.5周年に向けて\n盛り上がっていきましょー☆", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (130, "キャル", 0, 4, 2, 0, 0, 80003, 106001, 1, "ド、ドンドンパフパフ～\nって何言わせんのよ！\nまったくもう、浮かれちゃってさ", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (131, "コッコロ", 0, 5, 1, 0, 0, 80003, 105901, 1, "ふふ。キャルさまもワクワクして\nおいでのようですね\nそれでは主さま。大切な記念日まで\nどうぞご期待くださいませ", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (132, "キャル", 0, 5, 0, 0, 0, 80003, 106001, 2, "記念日を盛り上げるったって\n一体どうしたらいいのよ？\nあたし、そういうの\n苦手なんだけど…", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (133, "カリン", 0, 1, 1, 0, 0, 80003, 190801, 2, "あら、キャルさん\nどうかなさいましたか？\n何かお困りのようですけど", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (134, "キャル", 0, 6, 0, 0, 0, 80003, 106001, 2, "げっ…カリンじゃない\nなんでもないわよ！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (135, "カリン", 0, 2, 1, 0, 0, 80003, 190801, 2, "そうなのですか？\nキャルさんのことですからてっきり\n2.5周年をどう盛り上げたら\nいいか悩んでいるのかと…", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (136, "キャル", 0, 7, 0, 0, 0, 80003, 106001, 2, "分かってんじゃないのよ！？\nいっ、いや、違うけどね！\nあたしは別にそんなことで\n悩んでないわよっ？", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (137, "カリン", 0, 3, 1, 0, 0, 80003, 190801, 2, "ふふっ…\nこういう時は、真心を込めて\n気持ちをそのまま口にしたら\nいいのではないでしょうか？", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (138, "キャル", 0, 8, 0, 0, 0, 80003, 106001, 2, "気持ちを、ねぇ\nそれが一番難しいんじゃない…\nそもそも、あたしは記念日とか\n別にどうだっていいし", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (139, "キャル", 0, 9, 0, 0, 0, 80003, 106001, 2, "…まあ、ペコリーヌもコロ助も\nやけに楽しそうだったから…\nこういうお祭り騒ぎみたいなのも\nたまにはいいかな、とは思うけど", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (140, "カリン", 0, 4, 1, 0, 0, 80003, 190801, 2, "なるほどなるほど～\nキャルさんは【美食殿】で祝う\n2.5周年を心から楽しみに\nしている、ということですね！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (141, "キャル", 0, 10, 0, 0, 0, 80003, 106001, 2, "はぁ！？\nどこ聞いたらそうなるのよ！\nあたしは別に楽しみになんか…！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (142, "カリン", 0, 5, 1, 0, 0, 80003, 190801, 2, "キャルさんの心からの想い\nみなさんにも伝わったでしょうか？\n私も2.5周年が楽しみです！\n以上、カリンがお伝えしました！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (143, "キャル", 0, 11, 0, 0, 0, 80003, 106001, 2, "心からの想いって何なのよ！？\n勝手に人の言葉を\n捻じ曲げんじゃないわよ！\nこのくそ眼鏡ーーーっ！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (144, "コッコロ", 0, 6, 0, 0, 0, 80003, 105901, 3, "ペコリーヌさま、もうすぐ\n記念日でございますね\nこういった節目というのは\n感慨深いものでございます", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (145, "ペコリーヌ", 0, 5, 1, 0, 0, 80003, 105801, 3, "色んな思い出が蘇ってきますね！\n楽しい出来事が\nいっぱいありました♪", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (146, "コッコロ", 0, 7, 0, 0, 0, 80003, 105901, 3, "はい、そうでございますね\n主さまも【美食殿】の活動の他にも\nいろんなご経験をされたと\n聞いておりますよ", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (147, "コッコロ", 0, 8, 0, 0, 0, 80003, 105901, 3, "たしか…ランドソルで話題沸騰と\nなっていた、魔法少女なるものに\nお会いになられたとか", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (148, "ペコリーヌ", 0, 6, 1, 0, 0, 80003, 105801, 3, "魔法少女って、あの美味しくって\n栄養たっぷりの大ヒット商品\n『魔法少女ソーセージ』の箱に\n描いてある女の子のことですか！？", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (149, "ペコリーヌ", 0, 7, 1, 0, 0, 80003, 105801, 3, "あのソーセージ\n美味しいんですよね～♪\nわたしもたっくさん食べてますよ！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (150, "コッコロ", 0, 9, 0, 0, 0, 80003, 105901, 3, "あとは、牧場に素敵な温泉が\n出来たとか…", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (151, "ペコリーヌ", 0, 8, 1, 0, 0, 80003, 105801, 3, "牧場ですか！？　温泉につかって\nチーズもオツですね～♪　考えた\nだけでよだれが出ちゃいそうです！\nやばいですね☆", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (152, "コッコロ", 0, 10, 0, 0, 0, 80003, 105901, 3, "ペコリーヌさま\n言ってるそばからよだれが…\n今お拭きしますね。ふき、ふき", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (153, "ペコリーヌ", 0, 9, 1, 0, 0, 80003, 105801, 3, "えへへ、ありがとうございます\nコッコロちゃん！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (154, "コッコロ", 0, 11, 0, 0, 0, 80003, 105901, 3, "それにしても、主さまは\n【美食殿】の一員として様々な\n美食と出会われていたのですね\nわたくし、感服いたしました", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (155, "ペコリーヌ", 0, 10, 1, 0, 0, 80003, 105801, 3, "これからもたくさん冒険をして\nめいっぱい美食を\n味わい尽くしましょうね！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (156, "ペコリーヌ", 0, 11, 0, 0, 0, 80003, 105801, 4, "2.5周年カウントダウン♪\nキャルちゃんキャルちゃん！\nわくわくしますね☆", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (157, "キャル", 0, 12, 1, 0, 0, 80003, 106001, 4, "あぁもう、うるさいわね～\nまだまだ先でしょ？\nちょっとは落ち着きなさいよ", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (158, "ペコリーヌ", 0, 12, 0, 0, 0, 80003, 105801, 4, "キャルちゃんだって最近\nソワソワしてるじゃないですか～\nさっきもご機嫌そうに鼻歌とか\n歌ってましたよね？", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (159, "キャル", 0, 13, 1, 0, 0, 80003, 106001, 4, "なっ…！　き、聞いてたの！？\nじゃない、歌ってないわよ！？\nあんたいい加減なこと言うと\nぶっ殺すわよ！？", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (160, "ペコリーヌ", 0, 13, 0, 0, 0, 80003, 105801, 4, "えっへへ～♪\nわたしは最近のキャルちゃんが\nもっともっと大好きですよ♪\nすごく積極的になりましたよね！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (161, "キャル", 0, 14, 1, 0, 0, 80003, 106001, 4, "…積極的？\nなんの話よ？", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (162, "ペコリーヌ", 0, 14, 0, 0, 0, 80003, 105801, 4, "またまた～♪\nアイドルを目指そうとしたこと\n忘れちゃったんですか？", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (163, "ペコリーヌ", 0, 15, 0, 0, 0, 80003, 105801, 4, "ムーサの町で\nきゃるきゃる～ん☆　って！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (164, "キャル", 0, 15, 1, 0, 0, 80003, 106001, 4, "あっ…！？　ちちち違うわよっ！\nあれは操られてただけで\nあたしはアイドルなんか\n目指してないっての！！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (165, "ペコリーヌ", 0, 16, 0, 0, 0, 80003, 105801, 4, "本当に本当ですか？\nわたし、キャルちゃんとっても\n乗り気だなって\n思ってたんですけど～", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (166, "キャル", 0, 16, 1, 0, 0, 80003, 106001, 4, "まぁ、悪くはなかったけど…って\nだ、だから目指してないって\n言ってるでしょ！！\nなんてこと思い出させんのよ！？", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (167, "キャル", 0, 17, 1, 0, 0, 80003, 106001, 4, "ああ…ダメだわ…\nもう殺すしかない…\n2.5周年が来る前に\n記憶ごと消し去ってやるー！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (168, "キャル", 0, 18, 0, 0, 0, 80003, 106001, 5, "2.5周年まであと10日…\nふ～ん？\n案外早いもんね？", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (169, "コッコロ", 0, 12, 1, 0, 0, 80003, 105901, 5, "そうでございますね…\nその日のためにやるべきことは\nまだまだございます\n気を引き締めねばなりません…！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (170, "キャル", 0, 19, 0, 0, 0, 80003, 106001, 5, "やる気十分ねコロ助…\nそんな気負ってるとしんどいわよ？\nお気楽にやりましょ？", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (171, "コッコロ", 0, 13, 1, 0, 0, 80003, 105901, 5, "キャルさま…\nありがとうございます\nキャルさまは本当に\n優しいおかたです", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (172, "キャル", 0, 20, 0, 0, 0, 80003, 106001, 5, "な、なにわけの分かんないこと\n言ってんのよ…\nあたしは優しくなんかないっての", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (173, "コッコロ", 0, 14, 1, 0, 0, 80003, 105901, 5, "そんなことはございません\n…あの、実はわたくし\nキャルさまに\nひとつお願いがございまして", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (174, "キャル", 0, 21, 0, 0, 0, 80003, 106001, 5, "お願い？　珍しいわね\nコロ助がそんなこと言うなんて\n…まあ、あたしに出来ることなら\n別にいいけど", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (175, "コッコロ", 0, 15, 1, 0, 0, 80003, 105901, 5, "ありがとうございます\n実は記念日へ向けて\n『かくし芸』というものを\n用意してみようかと思いまして", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (176, "キャル", 0, 22, 0, 0, 0, 80003, 106001, 5, "かくし芸？", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (177, "コッコロ", 0, 16, 1, 0, 0, 80003, 105901, 5, "はい。きゃるきゃる～ん☆　を\n参考に、ころころ～ん☆　という\nご挨拶を考えてみたのですが\nポーズがうまくいかなくて…", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (178, "キャル", 0, 23, 0, 0, 0, 80003, 106001, 5, "～～！！　ちょっと褒められて\nいい気になって損したわ！！\nふざけんなふざけんな\nふ～ざ～け～ん～な～っ！！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (179, "ペコリーヌ", 0, 17, 0, 0, 0, 80003, 105801, 6, "あと９日！　もうすぐですよ♪\nえへへ、楽しみで楽しみで\nごはんが進みますね☆", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (180, "キャル", 0, 24, 2, 0, 0, 80003, 106001, 6, "ごはんが進みますね☆\nじゃなーい！\nいくらなんでも食べ過ぎよ！\n記念日、迎えられなくなるわよ！？", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (181, "コッコロ", 0, 17, 1, 0, 0, 80003, 105901, 6, "大丈夫ですよキャルさま\n食事はわたくしたちの\n歩んできた道そのもの\nなのですから", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (182, "ペコリーヌ", 0, 18, 0, 0, 0, 80003, 105801, 6, "さっすがコッコロちゃん\nいいことを言いますね！\nそう、わたしたち\n【美食殿】の活動目的は─", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (183, "キャル", 0, 25, 2, 0, 0, 80003, 106001, 6, "─みんなで楽しく食事をすること\nでしょ？\nあれだけ食べ歩いてきたんだから\n分かってるわよ", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (184, "コッコロ", 0, 18, 1, 0, 0, 80003, 105901, 6, "はい…そうでございますね♪\nそれにしてもわたくしたち\n本当にいろいろなものを\n食べ歩いてきましたね…", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (185, "キャル", 0, 26, 2, 0, 0, 80003, 106001, 6, "そうね…ほんっとうにね…\n毒まみれのニセドラゴンとか\n恐竜…は逃げられちゃったっけ\nあとワニゴ…ワニの魔物とかね", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (186, "キャル", 0, 27, 2, 0, 0, 80003, 106001, 6, "そして、とうとう本物の\nドラゴンまで相手に\nしちゃったのよね、あたしたちって", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (187, "キャル", 0, 28, 2, 0, 0, 80003, 106001, 6, "信じられない強さだったわね\nあのドラゴン…やっぱり\n作戦はしっかり立てていかないと\nダメよね", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (188, "ペコリーヌ", 0, 19, 0, 0, 0, 80003, 105801, 6, "えへへ…☆\nキャルちゃんが色んなことを\n覚えていてくれて\nうれしいです♪", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (189, "コッコロ", 0, 19, 1, 0, 0, 80003, 105901, 6, "わたくしも鮮明に覚えておりますよ\n主さまもきっと…♪", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (190, "キャル", 0, 29, 2, 0, 0, 80003, 106001, 6, "あったり前でしょ！\n忘れないわよ、あんな滅茶苦茶な\nものばかり食べさせられて！\n…忘れられるわけ、ないじゃない", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (191, "キャル", 0, 30, 0, 0, 0, 80003, 106001, 7, "ねえちょっと、ちょっと待って！？\nカリンから、2.5周年記念の\n生放送に出演してほしいって\n言われたんだけど！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (192, "キャル", 0, 31, 0, 0, 0, 80003, 106001, 7, "なんでそういう大事なことを\n急に言うわけ！？\nそんなの聞いてないわよ～！？", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (193, "キャル", 0, 32, 0, 0, 0, 80003, 106001, 7, "いっぱい人に見られちゃうのよね？\nなんであたしがそんなこと…", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (194, "キャル", 0, 33, 0, 0, 0, 80003, 106001, 7, "あー\nあー\n…声、これで大丈夫かしら？", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (195, "キャル", 0, 34, 0, 0, 0, 80003, 106001, 7, "聞き取りにくいかもしれないわね…\nもうちょっと明るい方がいいかも\nえっと…こんな感じ？", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (196, "キャル", 0, 35, 0, 0, 0, 80003, 106001, 7, "オイッスー☆", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (197, "キャル", 0, 36, 0, 0, 0, 80003, 106001, 7, "……\nいや、ないわ\nいつもどおりが一番ね、うん", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (198, "キャル", 0, 37, 0, 0, 0, 80003, 106001, 7, "もう、記念の生放送なんて\n超責任重大なやつじゃない…\nもしなにか失敗とかしたら…", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (199, "キャル", 0, 38, 0, 0, 0, 80003, 106001, 7, "ど、どど、どうしよう！？\n緊張してきちゃった…！！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (200, "コッコロ", 0, 20, 0, 0, 0, 80003, 105901, 8, "主さま、わたくし\nペコリーヌさまとキャルさまと共に\n2.5周年を祝う生放送に\n出演する大役を仰せつかりました", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (201, "コッコロ", 0, 21, 0, 0, 0, 80003, 105901, 8, "多くの方々に\n『プリンセスコネクト！\nRe:Dive』の魅力をお伝えする\n重要な役割でございます", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (202, "コッコロ", 0, 22, 0, 0, 0, 80003, 105901, 8, "わたくし、全身全霊をもって\n生放送に取り組みたいと\n思っております", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (203, "コッコロ", 0, 23, 0, 0, 0, 80003, 105901, 8, "ですが、もしかしたら\nセリフを噛んでしまったり\n段取りを間違えてしまったり\nするかもしれません…", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (204, "コッコロ", 0, 24, 0, 0, 0, 80003, 105901, 8, "そのようなことがないよう\n練習はしっかりしておかなくては\nまずはこちらから…", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (205, "コッコロ", 0, 25, 0, 0, 0, 80003, 105901, 8, "オイッスー☆", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (206, "コッコロ", 0, 26, 0, 0, 0, 80003, 105901, 8, "先ほどキャルさまが練習されて\nいるのをお見かけしたので\nわたくしもと思いまして…\nいかがでしょうか？", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (207, "コッコロ", 0, 27, 0, 0, 0, 80003, 105901, 8, "良い感じ、ですか？\n…ふふ、ありがとうございます\n主さまのあたたかいお言葉があれば\nわたくしは百人力でございます", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (208, "コッコロ", 0, 28, 0, 0, 0, 80003, 105901, 8, "この大役を無事に成し遂げ\n主さまに素敵な2.5周年を\n迎えていただけるよう…\nわたくし、尽力いたしますね", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (209, "ペコリーヌ", 0, 20, 0, 0, 0, 80003, 105801, 9, "オイッスー☆", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (210, "ペコリーヌ", 0, 21, 0, 0, 0, 80003, 105801, 9, "…う～ん、今のはちょっと\n違う感じがしますね\n堅苦しかったかもしれません", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (211, "ペコリーヌ", 0, 22, 0, 0, 0, 80003, 105801, 9, "あと６日で2.5周年ですか…", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (212, "ペコリーヌ", 0, 23, 0, 0, 0, 80003, 105801, 9, "この年月がオイッスーの\n歴史なんだと思うと\nじ～んとしちゃいます！\nやばいですね☆", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (213, "ペコリーヌ", 0, 24, 0, 0, 0, 80003, 105801, 9, "生放送で最高のオイッスー☆　を\nみなさんにお届けするために\n頑張らないと！\nそれじゃあもう一度…", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (214, "ペコリーヌ", 0, 25, 0, 0, 0, 80003, 105801, 9, "オイッスー☆", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (215, "ペコリーヌ", 0, 26, 0, 0, 0, 80003, 105801, 9, "…うう～ん、どうしてでしょう\nまだ何かが違う気がします…\nもしかしたら\nおなかが空きすぎてるとか？", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (216, "ペコリーヌ", 0, 27, 0, 0, 0, 80003, 105801, 9, "今さっき食べたばっかりですけど\nおなかが減っては\n良いオイッスー☆　は\n出せませんよね！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (217, "ペコリーヌ", 0, 28, 0, 0, 0, 80003, 105801, 9, "そうと決まれば、コッコロちゃんと\nキャルちゃんを誘って\nごはんにしましょう！\n善は急げです！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (218, "ペコリーヌ", 0, 29, 0, 0, 0, 80003, 105801, 9, "あ、二人が丁度いいところに！\nオイッス～☆　…ああ！！\n今の、今のですよ～♪", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (219, "ペコリーヌ", 0, 30, 0, 0, 0, 80003, 105801, 9, "なるほど、どんな時も\n自然体が一番ってことですね！\nこの調子で、生放送も\n頑張っちゃいます♪", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (220, "ペコリーヌ", 0, 31, 0, 0, 0, 80003, 105801, 10, "2.5周年まで残り５日ですが\nその前にもうすぐ生放送ですよ！\nコッコロちゃん\n準備の方はどうですか？", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (221, "コッコロ", 0, 29, 1, 0, 0, 80003, 105901, 10, "はい。わたくし、しっかりと\n備えてまいりました\n万事抜かりはございません", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (222, "ペコリーヌ", 0, 32, 0, 0, 0, 80003, 105801, 10, "おお～すごい自信を感じます…！\nいい生放送になりそうですね☆\n……あれ？　なんだかこの辺り\n揺れていませんか？", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (223, "キャル", 0, 39, 2, 0, 0, 80003, 106001, 10, "…………", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (224, "コッコロ", 0, 30, 1, 0, 0, 80003, 105901, 10, "キャルさま？\nお風邪をひかれたんでしょうか\nひどい震えですが…", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (225, "キャル", 0, 40, 2, 0, 0, 80003, 106001, 10, "かかかか風邪じゃないわよ！\nここここれはただ\nななな生放送が楽しみ過ぎて\nワワワワクワクが止まらないだけ！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (226, "ペコリーヌ", 0, 33, 0, 0, 0, 80003, 105801, 10, "大丈夫ですかキャルちゃん！？\nそうだ、これを飲んでください！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (227, "キャル", 0, 41, 2, 0, 0, 80003, 106001, 10, "きき気がきくじゃない、ありがとっ\nごくっ、んく…ぶはぁっ！？\nちょっと何飲ませたの！？\n辛っ！　苦ぁ！？　甘ぁっ！？", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (228, "ペコリーヌ", 0, 34, 0, 0, 0, 80003, 105801, 10, "これは魔物のエキスに薬草に\nその他色んなものを混ぜた\n特製の栄養ドリンクです！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (229, "ペコリーヌ", 0, 35, 0, 0, 0, 80003, 105801, 10, "これを飲めば体ぽかぽかで\n元気いっぱいですよ♪", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (230, "キャル", 0, 42, 2, 0, 0, 80003, 106001, 10, "このアホリーヌ！\n風邪じゃないって言ったでしょ！？\nどさくさに紛れて\nそんな恐ろしいもん飲ませんな～！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (231, "コッコロ", 0, 31, 1, 0, 0, 80003, 105901, 10, "キャルさま\n震えが収まっておりますね\nよかったです…\nわたくし、心配いたしました", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (232, "キャル", 0, 43, 2, 0, 0, 80003, 106001, 10, "え？　ああ、ホントね…って\nだから風邪じゃないって\n言ってるのに…\nまあいいわこの際なんでも", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (233, "ペコリーヌ", 0, 36, 0, 0, 0, 80003, 105801, 10, "えっへへ～♪　いつも通りの\nキャルちゃんに戻りましたし\n素敵な生放送が出来そうですね！\n今から楽しみです☆", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (234, "カリン", 0, 6, 0, 0, 0, 80003, 190801, 11, "ランドソルのみなさま、こんにちは\n生放送の会場設営を担当しています\n【ギルド管理協会】のカリンです", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (235, "カリン", 0, 7, 0, 0, 0, 80003, 190801, 11, "【美食殿】のみなさんは\nまもなく始まる生放送に向けて\n最後の調整のため\n席を外しています", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (236, "カリン", 0, 8, 0, 0, 0, 80003, 190801, 11, "頑張っている３人の\n邪魔をするわけには\nいきませんので、ご容赦ください", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (237, "カリン", 0, 9, 0, 0, 0, 80003, 190801, 11, "ですので、本日は私から\n【美食殿】のみなさんの様子を\nお伝えさせていただこうと思います", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (238, "カリン", 0, 10, 0, 0, 0, 80003, 190801, 11, "まずはペコリーヌさんですが…\n何やら大量のおにぎりを\n食べているようです\nえっと…栄養は大事ですよね", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (239, "カリン", 0, 11, 0, 0, 0, 80003, 190801, 11, "次にコッコロさんですが\nこちらは発声練習をしています", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (240, "カリン", 0, 12, 0, 0, 0, 80003, 190801, 11, "一生懸命、小さなお口を開けて\n声を出す姿が応援したくなりますね", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (241, "カリン", 0, 13, 0, 0, 0, 80003, 190801, 11, "そしてキャルさんは…\nこれは…堂々としたものですね\n何もせず、ただ立ち尽くして…", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (242, "カリン", 0, 14, 0, 0, 0, 80003, 190801, 11, "あれ？\nもしかして緊張で気を失って…？", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (243, "カリン", 0, 15, 0, 0, 0, 80003, 190801, 11, "…ええと\n【美食殿】のみなさんは\nやる気十分なようですね！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (244, "カリン", 0, 16, 0, 0, 0, 80003, 190801, 11, "３人とも生放送を\nいいものにしようと\n全力で頑張ってくれていますので\nどうかご期待ください", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (245, "カリン", 0, 17, 0, 0, 0, 80003, 190801, 11, "それでは\nまた明日お会いしましょう♪", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (246, "キャル", 0, 44, 0, 0, 0, 80003, 106001, 12, "2.5周年まであと３日！\nそして生放送は明日！\nなんかいよいよって感じね…", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (247, "ペコリーヌ", 0, 37, 2, 0, 0, 80003, 105801, 12, "３人でい～っぱい練習しましたし\nきっと大丈夫ですよ☆", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (248, "コッコロ", 0, 32, 1, 0, 0, 80003, 105901, 12, "ペコリーヌさまに\n大丈夫と言ってもらえると\nとても心強いですね", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (249, "キャル", 0, 45, 0, 0, 0, 80003, 106001, 12, "信じすぎちゃだめよコロ助\nこいつはなーんにも\n考えてないだけなんだから", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (250, "ペコリーヌ", 0, 38, 2, 0, 0, 80003, 105801, 12, "ひどいですよぉキャルちゃ～ん！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (251, "コッコロ", 0, 33, 1, 0, 0, 80003, 105901, 12, "ふふ。お二人とも\n普段通りの様子で\n安心いたしました", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (252, "ペコリーヌ", 0, 39, 2, 0, 0, 80003, 105801, 12, "はいっ☆\n生放送でもなんでも\n全力全開、わたしらしく\nぶつかっていくだけです！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (253, "キャル", 0, 46, 0, 0, 0, 80003, 106001, 12, "そうね。おめでたいことなんだし\nここまで来たんなら\n楽しめるだけ\n楽しむしかないわよね！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (254, "コッコロ", 0, 34, 1, 0, 0, 80003, 105901, 12, "わたくしも、普段よりも心が\n熱くなっております\nこの胸の気持ちをお伝えできるよう\n精一杯努力いたします…！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (255, "ペコリーヌ", 0, 40, 2, 0, 0, 80003, 105801, 12, "それでは、いきましょう！\n明日の生放送、絶対に\n成功させましょうね☆", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (256, "ペコリーヌ", 0, 41, 0, 0, 0, 80003, 105801, 13, "えっと、最初のひと言は…あれ？\nキャルちゃん、わたし\n何て言うんでしたっけ？", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (257, "キャル", 0, 47, 2, 0, 0, 80003, 106001, 13, "は！？\nなにいきなりど忘れしてるの！？\nっていうかあんた、台本は！？", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (258, "ペコリーヌ", 0, 42, 0, 0, 0, 80003, 105801, 13, "忘れてきちゃいました☆", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (259, "キャル", 0, 48, 2, 0, 0, 80003, 106001, 13, "忘れてきちゃいました☆\nで済ませられると思ってんの！？\nほら、あたし二冊持ってきたから\nこれ使いなさい！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (260, "コッコロ", 0, 35, 1, 0, 0, 80003, 105901, 13, "マイクとは、こんな高い位置に\nあるものなのですね…！\n背伸びをすれば、何とか…\nう～ん、うぅ～ん…！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (261, "キャル", 0, 49, 2, 0, 0, 80003, 106001, 13, "あぁもう\n少し下げればいいでしょ！？\nこれくらいでいい？\nまったく、世話が焼けるわね…", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (262, "ペコリーヌ", 0, 43, 0, 0, 0, 80003, 105801, 13, "あれ？\nキャルちゃん、後ろ髪が\nはねちゃってますよ？", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (263, "キャル", 0, 50, 2, 0, 0, 80003, 106001, 13, "え？　うそっ！？\nちゃんと確かめてきたのに！\nどこどこ！？", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (264, "コッコロ", 0, 36, 1, 0, 0, 80003, 105901, 13, "キャルさま、動かないで\nくださいまし。今くしで…\nはい、直りましたよ", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (265, "キャル", 0, 51, 2, 0, 0, 80003, 106001, 13, "あ、ありがと…はぁ。生放送\n本番までもうすぐなのに\nこんなことしてる場合じゃ\nないわよね", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (266, "コッコロ", 0, 37, 1, 0, 0, 80003, 105901, 13, "はい、時間は残りわずかです\nですが、慌ただしく\n動き回るこの時間も\nなんだか楽しくて…♪", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (267, "キャル", 0, 52, 2, 0, 0, 80003, 106001, 13, "コロ助、あんた…って\nそうじゃなくて！\n胸にジンワリきてる\n暇なんかないでしょ…！？", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (268, "ペコリーヌ", 0, 44, 0, 0, 0, 80003, 105801, 13, "あはは♪\nキャルちゃんの動きが\nとっても面白いです♪\nやばいですね☆", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (269, "コッコロ", 0, 38, 1, 0, 0, 80003, 105901, 13, "ふふ\nきっと面白い生放送になると\nわたくし、確信しております", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (270, "ペコリーヌ", 0, 45, 0, 0, 0, 80003, 105801, 13, "絶対絶対、間違いありません！\n見てくれるみなさんが\n笑顔になれる時間を\nお届けしましょう！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (271, "キャル", 0, 53, 2, 0, 0, 80003, 106001, 13, "そんなの当然でしょ！\n誰が見たって面白いって\n言ってもらえるように\nあれだけ準備したんだから！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (272, "ペコリーヌ", 0, 46, 0, 0, 0, 80003, 105801, 13, "それでは\n記念日直前生放送！\n楽しみにしててくださいね☆", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (273, "カリン", 0, 18, 0, 0, 0, 80003, 190801, 14, "こんにちは\n【ギルド管理協会】のカリンです", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (274, "カリン", 0, 19, 0, 0, 0, 80003, 190801, 14, "昨日の生放送は\nいかがだったでしょうか？", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (275, "カリン", 0, 20, 0, 0, 0, 80003, 190801, 14, "たくさんの笑顔と喜びと\nそして驚きを受け取って\nいただけたのではないかと思います", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (276, "カリン", 0, 21, 0, 0, 0, 80003, 190801, 14, "そしていよいよ\n明日は2.5周年の当日\n待ちに待った記念日を\n迎えることとなります", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (277, "カリン", 0, 22, 0, 0, 0, 80003, 190801, 14, "【美食殿】のみなさんも\n張り切って様々な準備を\nされていますよ", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (278, "カリン", 0, 23, 0, 0, 0, 80003, 190801, 14, "【ギルド管理協会】も\n万全のサポート体制で\n協力してまいりたいと思います", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (279, "カリン", 0, 24, 0, 0, 0, 80003, 190801, 14, "…ふふ、本当に楽しみですね\n【ギルド管理協会】の職員として\nだけではなく、私個人としても…", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (280, "カリン", 0, 25, 0, 0, 0, 80003, 190801, 14, "この記念日を迎えられることが\n心から楽しみですし\n幸せを感じています", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (281, "カリン", 0, 26, 0, 0, 0, 80003, 190801, 14, "きっと【美食殿】のみなさんは\nそれ以上に心を弾ませて\nいることでしょう", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (282, "カリン", 0, 27, 0, 0, 0, 80003, 190801, 14, "それでは\n明日からの2.5周年を\nどうぞ、お楽しみに…♪", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (283, "ペコリーヌ", 0, 47, 0, 0, 0, 80003, 105801, 15, "準備はいいですかっ？\n…いっきますよー！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (284, "キャル", 0, 54, 2, 0, 0, 80003, 106001, 15, "祝！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (285, "コッコロ", 0, 39, 1, 0, 0, 80003, 105901, 15, "2.5周年！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (286, "ペコリーヌ", 0, 48, 0, 0, 0, 80003, 105801, 15, "おめでとうございま～す☆", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (287, "キャル", 0, 55, 2, 0, 0, 80003, 106001, 15, "少し前まではまだまだ先じゃない\nとか言ってたけど\n本当にあっという間だったわね", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (288, "コッコロ", 0, 40, 1, 0, 0, 80003, 105901, 15, "光陰矢の如し…\nとはこのことでございますね\nでも、今日という日を無事に\n迎えることができて良かったです", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (289, "ペコリーヌ", 0, 49, 0, 0, 0, 80003, 105801, 15, "コッコロちゃん！　キャルちゃん！\n落ち着くには\nまだ早いですよ！\n盛り上がるのはこれからです☆", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (290, "キャル", 0, 56, 2, 0, 0, 80003, 106001, 15, "ほんとあんたはいつも\n全力全開ねー\nでもそうね、記念日なんだし\nパーっといきましょ！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (291, "コッコロ", 0, 41, 1, 0, 0, 80003, 105901, 15, "ええ、わたくしも全力全開で\nまいります…！\n主さま、さあ\nどうぞ手を取ってくださいまし…♪", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (292, "キャル", 0, 57, 2, 0, 0, 80003, 106001, 15, "あんたもコロ助も、浮かれて\n転ぶんじゃないわよ？\n…仕方ないから、あたしも\n手を繋いであげるわよ", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (293, "ペコリーヌ", 0, 50, 0, 0, 0, 80003, 105801, 15, "あー！　二人ともずるいです！\nわたしもっ、ギュー♪", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (294, "キャル", 0, 58, 2, 0, 0, 80003, 106001, 15, "ちょっ、ペコリーヌ！\nくっつき過ぎよ！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (295, "ペコリーヌ", 0, 51, 0, 0, 0, 80003, 105801, 15, "記念日だから\n平気平気♪\nやばいですね☆", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (296, "キャル", 0, 59, 2, 0, 0, 80003, 106001, 15, "意味分かんないわよ！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (297, "コッコロ", 0, 42, 1, 0, 0, 80003, 105901, 15, "ふふ。このめでたき日に\n喜びがあふれておりますね\n主さまは…いかがでしょうか？", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (298, "コッコロ", 0, 43, 1, 0, 0, 80003, 105901, 15, "喜んでいただけているならば\nわたくしはとてもとても\n幸せでございます", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (299, "ペコリーヌ", 0, 52, 0, 0, 0, 80003, 105801, 15, "さぁ、2.5周年を\n楽しみ尽くしますよ☆\nまだまだ食べたいものもありますし\nやりたいこともたくさんです！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (300, "キャル", 0, 60, 2, 0, 0, 80003, 106001, 15, "分かった、分かったから\n引っ張るんじゃないわよ！\n…そんな掴まなくても\nちゃんとついていくから", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (301, "コッコロ", 0, 44, 1, 0, 0, 80003, 105901, 15, "ペコリーヌさま…キャルさま…\n主さま…共にまいりましょう\nどこまでも…！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (302, "ペコリーヌ", 0, 53, 0, 0, 0, 80003, 105801, 15, "はい！　それでは2.5周年\n食べつくしツアーへ！\nそして、その先の未来へ\n向かって出発しましょう！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (303, "ペコリーヌ・コッコロ・キャル", 0, 54, 0, 0, 1, 80003, 105801, 15, "2.5周年\nありがとうございます！", "105901,45,106001,61");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (304, "カリン", 0, 1, 1, 1, 0, 60002, 190804, 0, "新年を迎え、新たに始まる一日を\nおみくじで占ってみませんか？\n良い運勢であるほど、\n沢山のプレゼントが貰えますよ♪", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (305, "", 1, 0, 2, 1, 0, 60002, 0, 0, "今日の運勢は姫吉！　どんな不可能も可能にできちゃう！　　　　仲間と食卓を囲めばさらに運気上昇よ！　ただし魔物料理以外！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (306, "", 1, 0, 2, 1, 0, 60002, 0, 0, "今日の運勢は姫吉！　やること全てが最高潮！　やばいですね☆　ラッキーアイテムはおにぎり！　お腹一杯で幸せが続くでしょう！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (307, "", 1, 0, 2, 1, 0, 60002, 0, 0, "今日の運勢は姫吉！　最強の運勢でどんなドジも跳ね除けます！　大切な人と一緒に、最高の一日になること間違いなし！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (308, "", 1, 0, 2, 1, 0, 60002, 0, 0, "今日の運勢は姫吉！　どんな挑戦も上手くいく、最高の運勢です！理想の自分に変身できる絶好の機会！　新しい一歩を踏み出そう！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (309, "", 1, 0, 2, 2, 0, 60002, 0, 0, "今日の運勢は姫吉！　やること全てが上手くいく最高の一日！　　あなたの活躍に先生もにっこりです。いい子いい子♪", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (310, "", 2, 0, 2, 1, 0, 60002, 0, 0, "今日の運勢は大吉！　お裁縫好きな女の子のお墨付きです！　　　お世話したがりの妹にめいっぱい愛されちゃうかも！？", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (311, "", 2, 0, 2, 1, 0, 60002, 0, 0, "今日の運勢は大吉！　ラッキーカラーは赤！　　　　　　　　　　湧き上がるアイデアの数々に、高笑いが止まりませんわ！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (312, "", 2, 0, 2, 1, 0, 60002, 0, 0, "今日の運勢は大吉！　ラッキーワードは「ちぇる～ん♪」　　　　出会う人みんなに言ってあげると、ハッピーになれますよ！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (313, "", 2, 0, 2, 1, 0, 60002, 0, 0, "今日の運勢は大吉！　運勢はバッチリで無敵状態！　　　　　　　龍眼で全てを見通して、思い通りの一日を過ごしましょう！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (314, "", 2, 0, 2, 2, 0, 60002, 0, 0, "今日の運勢は大吉！　悩んでいたことが解決しそうです！　　　　たまには勉強も仕事も忘れて、心あたたまる家族と過ごしましょ！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (315, "", 3, 0, 2, 1, 0, 60002, 0, 0, "今日の運勢は中吉！　家の中でゆっくり過ごすのがよさそう！　　ラッキーアイテムはゲームよ！　ベストスコアを更新できるかも？", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (316, "", 3, 0, 2, 1, 0, 60002, 0, 0, "今日の運勢は中吉！　上手くなりたいことに積極的に挑戦してみてサックス好きの妹が、手取り足取りサポートしてくれるかも～？", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (317, "", 3, 0, 2, 1, 0, 60002, 0, 0, "今日の運勢は中吉！　仕事運が急上昇中です！　　　　　　　　　きっと「煉獄に舞う一陣の疾風」の如き活躍ができますよ！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (318, "", 3, 0, 2, 1, 0, 60002, 0, 0, "今日の運勢は中吉！　日頃の努力が報われます！　　　　　　　　親しい友人がいるなら報告してみて。一緒に喜んでくれるはずだ。", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (319, "", 3, 0, 2, 1, 0, 60002, 0, 0, "今日の運勢は中吉！　商売運が上がってきています！　　　　　　ラッキーアイテムはたい焼き！　和菓子パワーで売上アップです！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (320, "", 3, 0, 2, 1, 0, 60002, 0, 0, "今日の運勢は中吉！　チャンスを待つと良い結果に！　　　　　　ラッキーアイテムのプリンを食べて、運を呼び込みましょう！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (321, "", 3, 0, 2, 2, 0, 60002, 0, 0, "今日の運勢は中吉！　運気が着々と上昇中！　　　　　　　　　　精霊の加護を受けたかのごとく、色々な事がうまくいきますよ♪", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (322, "", 4, 0, 2, 1, 0, 60002, 0, 0, "今日の運勢は小吉！　まだ見ぬ危険が隠れているかも！　　　　　でも安心しろ！　提督が貴公を守ってくれるはずだぞ！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (323, "", 4, 0, 2, 1, 0, 60002, 0, 0, "今日の運勢は小吉！　いろんな事件が起こる一日になりそう！　　状況をよく観察してみようじゃないか！　まさに探偵のごとくね！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (324, "", 4, 0, 2, 1, 0, 60002, 0, 0, "今日の運勢は小吉！　気の向くままに過ごすのが幸せへの近道！　美味い魚を食べて猫を眺めて、自然体で過ごすのはどうだい？", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (325, "", 4, 0, 2, 1, 0, 60002, 0, 0, "今日の運勢は小吉！　運気は高いけども、ハプニングには注意！　出かけた先で、「ふしんしゃ」扱いされてしまうかも……", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (326, "", 4, 0, 2, 1, 0, 60002, 0, 0, "今日の運勢は小吉！　同腹一心の友だちが見つかるかも！　　　　キーワードは「ユニちゃんズ」、記憶に刻みましょう！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (327, "", 4, 0, 2, 1, 0, 60002, 0, 0, "今日の運勢は小吉！　周りに流されず、マイペースな一日を！　　ただし寝すぎには注意ぐうたらし過ぎず適度な運動を、だってさ～", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (328, "", 4, 0, 2, 2, 0, 60002, 0, 0, "今日の運勢は小吉！　根を詰めずにリラックスすると運気向上！　ラッキーパーソンはアイドル。歌があなたを幸せにしてくれます♪", "");
