INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (332, "シェフィ", 0, 1, 0, 0, 0, 80005, 106401, 1, "コッコロさん、どこへ行くの…？\n…え？", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (333, "ペコリーヌ", 0, 1, 3, 0, 0, 80005, 105801, 1, "『おめでとうございます！\nわ～パチパチー！』\n…という感じでどうですかね！？", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (334, "コッコロ", 0, 2, 4, 0, 0, 80005, 105901, 1, "ではわたくしも\n後ろからクラッカーを鳴らして\n応戦いたします\nああ、当日が待ち遠しいですね", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (335, "シェフィ", 0, 2, 0, 0, 0, 80005, 106401, 1, "ええと…\n二人とも何をしているの？", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (336, "キャル", 0, 1, 1, 0, 0, 80005, 106001, 1, "シェフィは初めてだもんね\nあの日が近いからって\n予行練習してんのよ\n気が早いわよね～", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (337, "シェフィ", 0, 3, 0, 0, 0, 80005, 106401, 1, "あの日…？", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (338, "ペコリーヌ", 0, 2, 3, 0, 0, 80005, 105801, 1, "その通りです…\nいよいよ記念すべきあの日が\n近づいてきましたよ！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (339, "ペコリーヌ", 0, 3, 3, 0, 0, 80005, 105801, 1, "『プリンセスコネクト！\nRe:Dive』3.5周年！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (340, "カリン", 0, 1, 2, 0, 0, 80005, 190801, 1, "やばいですね☆", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (341, "キャル", 0, 2, 1, 0, 0, 80005, 106001, 1, "って、なにしれっと\n参加してんのよカリン！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (342, "カリン", 0, 2, 2, 0, 0, 80005, 190801, 1, "ふふ、いいじゃないですか\n【ギルド管理協会】として\n私もみなさんと一緒に\nお祝いをさせてください！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (343, "カリン", 0, 3, 2, 0, 0, 80005, 190801, 1, "それに…記念日が近づけば\nとっておきの情報を\nお届けしますから♪", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (344, "ペコリーヌ", 0, 4, 3, 0, 0, 80005, 105801, 1, "というわけで\n今日からシェフィちゃんも一緒に\nカウントダウンしちゃいましょう！\n3.5周年まで…", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (345, "シェフィ", 0, 4, 0, 0, 0, 80005, 106401, 1, "えっ！？　ええとええと…！\nあ、あと14日…！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (346, "ペコリーヌ", 0, 5, 3, 0, 0, 80005, 105801, 2, "3.5周年まであと13日\n豪華に、そして盛大に\nお祝いしたいですね☆", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (347, "コッコロ", 0, 3, 1, 0, 0, 80005, 105901, 2, "豪華といえば前にとある貴族さまが\n舞踏会を開かれたそうで\n主さまがそのお手伝いを\nしておりました", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (348, "コッコロ", 0, 4, 1, 0, 0, 80005, 105901, 2, "そこで美しい所作を学ばれたので\nわたくしも教えていただきました\nしゃなり、しゃなり…\nいかがでしょう？", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (349, "ペコリーヌ", 0, 6, 3, 0, 0, 80005, 105801, 2, "すっごく上手ですよ☆\nまるでお姫様みたいです\nせっかくなのでわたしも\nしゃなりしゃなり", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (350, "コッコロ", 0, 5, 1, 0, 0, 80005, 105901, 2, "さすがでございます\nペコリーヌさま", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (351, "キャル", 0, 3, 0, 0, 0, 80005, 106001, 2, "いや、あんたは\nできなきゃダメでしょ\n本物のお姫様なんだし", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (352, "シェフィ", 0, 5, 2, 0, 0, 80005, 106401, 2, "しゃなり、しゃなり…\nふふ、案外できるものね", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (353, "ペコリーヌ", 0, 7, 3, 0, 0, 80005, 105801, 2, "わぁ！\nシェフィちゃん\n姿勢がとってもキレイです♪", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (354, "コッコロ", 0, 6, 1, 0, 0, 80005, 105901, 2, "さ、次はキャルさまの\n番でございますよ", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (355, "キャル", 0, 4, 0, 0, 0, 80005, 106001, 2, "あ、あたしはいいわよ！\nそういうの全然分かんないし…", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (356, "シェフィ", 0, 6, 2, 0, 0, 80005, 106401, 2, "大丈夫\nやってみると結構楽しいわよ\nほらほらっ", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (357, "キャル", 0, 5, 0, 0, 0, 80005, 106001, 2, "そ、そこまで言うなら…\nしゃなにゅわきゃあっ！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (358, "キャル", 0, 6, 0, 0, 0, 80005, 106001, 2, "いたたた…もぉお～！\nなんであたしがこんな目に～！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (359, "コッコロ", 0, 7, 1, 0, 0, 80005, 105901, 2, "まぁまぁ\n記念日までに練習すれば\nできるようになるかもしれません\nよろしければお教えいたしますよ", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (360, "キャル", 0, 7, 0, 0, 0, 80005, 106001, 2, "よし！\nあんたたちができて\nあたしだけできないのはしゃくだし\nやってやろうじゃないの！！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (361, "コッコロ", 0, 8, 1, 0, 0, 80005, 105901, 3, "3.5周年まであと12日\n記念日当日はもちろんですが\nその日を待つ時間というのも\n心躍ります", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (362, "ペコリーヌ", 0, 8, 3, 0, 0, 80005, 105801, 3, "分かりますっ！\nどんな特別な料理を食べられるのか\n考えるだけでワクワクしますよね！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (363, "コッコロ", 0, 9, 1, 0, 0, 80005, 105901, 3, "特別な料理といえば、お祭りですね\nそういえば主さまは学生として\n聖学祭に参加されたこと\nがあるとか…", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (364, "シェフィ", 0, 7, 2, 0, 0, 80005, 106401, 3, "聖学祭…って、なにかしら？", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (365, "コッコロ", 0, 10, 1, 0, 0, 80005, 105901, 3, "聖テレサ女学院の祭典ですよ\n二つのチームが競い合い\n青春を満喫するのが趣旨のようです", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (366, "キャル", 0, 8, 0, 0, 0, 80005, 106001, 3, "それってカルミナの\nサプライズライブがあった\nお祭りよね？\nちょっと見たかったかも…", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (367, "キャル", 0, 9, 0, 0, 0, 80005, 106001, 3, "カルミナの後の\nライブも好評だったらしいじゃない\n確か、なかよし部\nだったっけ…？", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (368, "ペコリーヌ", 0, 9, 3, 0, 0, 80005, 105801, 3, "なかよし部…\n仲が良さそうな名前で\nやばいですね☆", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (369, "キャル", 0, 10, 0, 0, 0, 80005, 106001, 3, "いや、そのまんますぎでしょ…", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (370, "シェフィ", 0, 8, 2, 0, 0, 80005, 106401, 3, "聖学祭かぁ…なんだか楽しそうね\nそれになかよし部の人たちも\n気になるわ", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (371, "ペコリーヌ", 0, 10, 3, 0, 0, 80005, 105801, 3, "それじゃあわたしたちも、\n負けじと仲良くしましょう♪\nほらほらシェフィちゃん、\nぎゅーっ☆　ですよ♪", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (372, "シェフィ", 0, 9, 2, 0, 0, 80005, 106401, 3, "えっ？　ええ…\nぎゅーっ…？", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (373, "キャル", 0, 11, 0, 0, 0, 80005, 106001, 4, "3.5周年まであと11日！？\nお祝いの準備進んでないんだけど…\nああ～昨日に戻りたいわ…", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (374, "シェフィ", 0, 10, 2, 0, 0, 80005, 106401, 4, "そういえばそんな魔道具があるって\n聞いたことがあるわね\n確か、名前はタイムラーベ…", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (375, "キャル", 0, 12, 0, 0, 0, 80005, 106001, 4, "なにそれヤバいわね\nでも本当にあるんだったら\n準備よりももっとやり直したいこと\nたくさんあるんだけど…", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (376, "コッコロ", 0, 11, 1, 0, 0, 80005, 105901, 4, "もしや正月の件でしょうか…？\nあの時はお金がなくて\n苦労しましたから", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (377, "キャル", 0, 13, 0, 0, 0, 80005, 106001, 4, "あれはそうね…\nお金を湯水のように使う\nぺコリーヌを止められたら\nあんな苦労をすることもなかったし", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (378, "ペコリーヌ", 0, 11, 3, 0, 0, 80005, 105801, 4, "そんなこともありましたね！\nやり直したいといえば\n『きゃるきゃる～ん♪』\nの方かと思いましたよ！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (379, "キャル", 0, 14, 0, 0, 0, 80005, 106001, 4, "バラすんじゃないわよ\nこのアホリーヌ！\nシェフィはそれ\n知らないんだから！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (380, "キャル", 0, 15, 0, 0, 0, 80005, 106001, 4, "こうなったらもう、なんとか\nタイムラーべを探し出して\n今の会話をなかったことに…\nって、どうしたのよシェフィ", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (381, "シェフィ", 0, 11, 2, 0, 0, 80005, 106401, 4, "うん…もし、私だったら…\n私が知らないみんなとの時間を\n見てみたいな、って…", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (382, "ペコリーヌ", 0, 12, 3, 0, 0, 80005, 105801, 4, "シェフィちゃん！　タイムなんとか\nなんかなくても大丈夫ですよ！\n一緒の時間はこれから\nどんどん作ればいいんですから！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (383, "コッコロ", 0, 12, 1, 0, 0, 80005, 105901, 4, "はい、今この瞬間も\n未来にはかけがえのない思い出に\nなっていることでしょう", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (384, "キャル", 0, 16, 0, 0, 0, 80005, 106001, 4, "だからそんな顔しないの\nほら、お祝いの準備、進めるわよ！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (385, "シェフィ", 0, 12, 2, 0, 0, 80005, 106401, 4, "みんな…そうね\n最高のお祝いにして\n最高の思い出にしましょう！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (386, "ペコリーヌ", 0, 13, 3, 0, 0, 80005, 105801, 5, "3.5周年まで、あと10日！\nもうすぐですよー！\n楽しみすぎて、やばいですね！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (387, "ペコリーヌ", 0, 14, 3, 0, 0, 80005, 105801, 5, "そういえば…たくさんのおもちゃが\n子どもたちを襲うっていう\n騒動もありましたね…\nなんでも勝手に動き出したとか！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (388, "ペコリーヌ", 0, 15, 3, 0, 0, 80005, 105801, 5, "学校や孤児院とかに\nちっちゃなおもちゃが乗り込んで\n暴れたみたいですよ！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (389, "キャル", 0, 17, 0, 0, 0, 80005, 106001, 5, "暴れたって言っても\n所詮はおもちゃじゃない\nどうせちっさいんでしょ？", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (390, "キャル", 0, 18, 0, 0, 0, 80005, 106001, 5, "だったら魔法で\n全部ぶっ飛ばせばいいじゃない！\n大したことないわよ！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (391, "シェフィ", 0, 13, 2, 0, 0, 80005, 106401, 5, "キャルってば…\n相手は子どもが\n大事にしてる物なのよ…？", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (392, "コッコロ", 0, 13, 1, 0, 0, 80005, 105901, 5, "乱暴な発言は\nめっ、でございますよ？\n子どもの教育に悪影響を\n与えてしまいますから…", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (393, "ペコリーヌ", 0, 16, 3, 0, 0, 80005, 105801, 5, "キャルちゃんだって\n意外と猫グッズとか\nたっくさん集めてますよね？\nもしその子たちが暴れたら…？", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (394, "キャル", 0, 19, 0, 0, 0, 80005, 106001, 5, "う、うっさいわね！\n意外とか言うな！\n…ま、まあ確かに正面切って\n魔法でぶっ飛ばせないかもね…", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (395, "ペコリーヌ", 0, 17, 3, 0, 0, 80005, 105801, 5, "あはは☆\nキャルちゃんのそういうところ\n好きですよ♪", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (396, "ペコリーヌ", 0, 18, 3, 0, 0, 80005, 105801, 5, "そんなキャルちゃんに猫グッズを\nプレゼントしちゃいますね！\n一部で流行っていた、たい焼き屋の\nおまけフィギュアですよ♪", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (397, "ペコリーヌ", 0, 19, 3, 0, 0, 80005, 105801, 5, "乱暴に扱ったりしないでくださいね\nひとりでに暴れ出しちゃうかも\nしれませんから！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (398, "カリン", 0, 4, 2, 0, 0, 80005, 190801, 6, "3.5周年まであと９日\nここでみなさんに\nビックニュースですよ！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (399, "カリン", 0, 5, 2, 0, 0, 80005, 190801, 6, "3.5周年を祝う生放送に\n去年と同様【美食殿】の\nみなさんにご出演いただくことが\n決定しましたー！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (400, "キャル", 0, 20, 1, 0, 0, 80005, 106001, 6, "…えっ！？", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (401, "ペコリーヌ", 0, 20, 3, 0, 0, 80005, 105801, 6, "本当ですか！？\nそれじゃ今から張り切って\n生放送中に食べる魔物料理を\n作らなくちゃですね☆", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (402, "シェフィ", 0, 14, 0, 0, 0, 80005, 106401, 6, "いや…がんばるところは\nそこじゃないような…\nというか去年も出演してたのね\nすごいじゃない！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (403, "コッコロ", 0, 14, 4, 0, 0, 80005, 105901, 6, "はい。とても緊張しますが\nやりがいのある大役です\n一緒にがんばりましょうね\nシェフィさま", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (404, "シェフィ", 0, 15, 0, 0, 0, 80005, 106401, 6, "わ、私も出るの！？　でも…\n私も【美食殿】の一人だものね\nそれなら今からしっかり準備をして\nコンディションを整えないと…", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (405, "キャル", 0, 21, 1, 0, 0, 80005, 106001, 6, "ふふん♪\n生放送直前のカチコチになった\nシェフィの姿が楽しみだわ！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (406, "ペコリーヌ", 0, 21, 3, 0, 0, 80005, 105801, 6, "去年のキャルちゃんは\n緊張しすぎてずっと震えて\nかわいかったですもんね！\n今年もあの姿が見たいです！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (407, "キャル", 0, 22, 1, 0, 0, 80005, 106001, 6, "ぶっ殺すぞ！？", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (408, "カリン", 0, 6, 2, 0, 0, 80005, 190801, 6, "というわけで\n『プリンセスコネクト！\nRe:Dive』の魅力を\nお伝えする生放送をお楽しみに♪", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (409, "コッコロ", 0, 15, 1, 0, 0, 80005, 105901, 7, "3.5周年まであと８日\nでございますよ\n楽しみですね、主さま", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (410, "キャル", 0, 23, 0, 0, 0, 80005, 106001, 7, "そういえばあんた\n夏休みの宿題は\nきちんとやってるの？", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (411, "シェフィ", 0, 16, 2, 0, 0, 80005, 106401, 7, "宿題…学校に通うのも\n楽しいことばかりじゃ\nないのね", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (412, "ペコリーヌ", 0, 22, 3, 0, 0, 80005, 105801, 7, "あれ？　そういえば\n少し前にルーセント学院で\n何かあったような…", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (413, "キャル", 0, 24, 0, 0, 0, 80005, 106001, 7, "ああ、始業式の日に\n大捕り物があったって\n話題になってたわね", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (414, "シェフィ", 0, 17, 2, 0, 0, 80005, 106401, 7, "夏休みを終わらせないって\n騒いでたっていう話？\nあれ本当なの？\n都市伝説だと思ってたわ", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (415, "コッコロ", 0, 16, 1, 0, 0, 80005, 105901, 7, "その一件ならよく存じ上げて\nおります。ですから主さま\n次の機会にはわたくしと一緒に\nがんばりましょう", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (416, "シェフィ", 0, 18, 2, 0, 0, 80005, 106401, 7, "楽しそう…私も一緒にやりたいな\nママ…じゃ、じゃなくて！\nコッコロさん、私もまざっていい？", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (417, "コッコロ", 0, 17, 1, 0, 0, 80005, 105901, 7, "もちろんでございます\n仲良くお勉強いたしましょう", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (418, "キャル", 0, 25, 0, 0, 0, 80005, 106001, 7, "しれっと教育ママが\n生まれてる…？", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (419, "ペコリーヌ", 0, 23, 3, 0, 0, 80005, 105801, 7, "お勉強は大切ですよ！\nキャルちゃんも一緒に\nがんばりましょう！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (420, "キャル", 0, 26, 0, 0, 0, 80005, 106001, 7, "え～～～～！？", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (421, "キャル", 0, 27, 0, 0, 0, 80005, 106001, 8, "3.5周年まであと７日〜♪\nふふ～ん♪\nふんふふ～ん♪", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (422, "シェフィ", 0, 19, 2, 0, 0, 80005, 106401, 8, "…\nねぇコッコロさん\nキャル、どうしちゃったの？", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (423, "コッコロ", 0, 18, 1, 0, 0, 80005, 105901, 8, "とてもご機嫌そうに\n鼻歌を歌ってらっしゃいますね\n耳も尻尾もリズミカルに\n揺れて…ノリノリでございます", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (424, "ペコリーヌ", 0, 24, 3, 0, 0, 80005, 105801, 8, "あれはですね！\nカルミナのみなさんの\nサマーライブのチケットが\n手に入ったからなんですよ♪", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (425, "シェフィ", 0, 20, 2, 0, 0, 80005, 106401, 8, "ええっ！？\nあのライブのチケットって確か\n入手困難だったんじゃ…", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (426, "キャル", 0, 28, 0, 0, 0, 80005, 106001, 8, "ふふふふ～ん♪\nしゃいにんぐふゅ～ちゃ～♪", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (427, "コッコロ", 0, 19, 1, 0, 0, 80005, 105901, 8, "なるほど…\nキャルさまがあのように\nなってしまうのも頷けますね", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (428, "シェフィ", 0, 21, 2, 0, 0, 80005, 106401, 8, "…カルミナの\n『Shining Future』\nの中に、シャイニングフューチャー\nって歌詞はなかったはずだけど…", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (429, "キャル", 0, 29, 0, 0, 0, 80005, 106001, 8, "ふんふ…はっ！？\nあっ、あんたたち！\nなに人の鼻歌を\n勝手に聞いてるのよ！？", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (430, "キャル", 0, 30, 0, 0, 0, 80005, 106001, 8, "こっ、これは別に\nカルミナの曲じゃなくて…\nあたしのオリジナルソングよ！\n聞いたらお金取るわよっ！？", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (431, "ペコリーヌ", 0, 25, 3, 0, 0, 80005, 105801, 8, "えへへ、キャルちゃん\nカルミナのサマーライブ\n思いっきり楽しみましょうね☆", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (432, "キャル", 0, 31, 0, 0, 0, 80005, 106001, 8, "も～っ！\n生暖かい目で見るな～～！\nぶっ殺すわよ～～～！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (433, "シェフィ", 0, 22, 0, 0, 0, 80005, 106401, 9, "3.5周年まであと６日\n３人も言ってたけど\nあっという間だったわね", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (434, "シェフィ", 0, 23, 0, 0, 0, 80005, 106401, 9, "…えっ？\n生放送の意気込み！？\n突然なに！？", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (435, "シェフィ", 0, 24, 0, 0, 0, 80005, 106401, 9, "ええと…\nとにかく失敗しないように\n台本たくさん読んで…\nそれから…", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (436, "シェフィ", 0, 25, 0, 0, 0, 80005, 106401, 9, "えっ！？\nトラブルがあったら\nどうするのかって！？\nそ、それは…！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (437, "シェフィ", 0, 26, 0, 0, 0, 80005, 106401, 9, "とにかく落ち着いて\nスタッフさんの指示を聞いて…", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (438, "シェフィ", 0, 27, 0, 0, 0, 80005, 106401, 9, "それから、それから…！\nぅ、ぅぅっ…！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (439, "シェフィ", 0, 28, 0, 0, 0, 80005, 106401, 9, "―びえぇぇ～んっ！！！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (440, "コッコロ", 0, 20, 1, 0, 0, 80005, 105901, 9, "どうされましたか、シェフィさま", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (441, "シェフィ", 0, 29, 0, 0, 0, 80005, 106401, 9, "ううっ…ひっく…\nママッ…！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (442, "コッコロ", 0, 21, 1, 0, 0, 80005, 105901, 9, "よしよし、緊張されてるのですね\nママが傍におりますよ\n主さま、少々お待ちくださいませ", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (443, "コッコロ", 0, 22, 1, 0, 0, 80005, 105901, 9, "…ふぅ\nシェフィさま\n生放送への意気込み\nお話しできそうですか？", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (444, "シェフィ", 0, 30, 0, 0, 0, 80005, 106401, 9, "…ううっ\n恥ずかしすぎて\nもう生放送出られない…", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (445, "コッコロ", 0, 23, 0, 0, 0, 80005, 105901, 10, "主さま\n3.5周年まであと５日\nでございます", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (446, "コッコロ", 0, 24, 0, 0, 0, 80005, 105901, 10, "ですがその前に\n生放送がありますね", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (447, "コッコロ", 0, 25, 0, 0, 0, 80005, 105901, 10, "…生放送に向けた\n意気込みでございますか？", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (448, "コッコロ", 0, 26, 0, 0, 0, 80005, 105901, 10, "もちろんわたくしは\n主さまのために\n全身全霊を込めて\n楽しい時間をご提供いたします", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (449, "コッコロ", 0, 27, 0, 0, 0, 80005, 105901, 10, "…と、意気込んでみましたが\nうまくできるか少し不安です\n声が小さくなったり\n間違えてしまったらどうしようと…", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (450, "コッコロ", 0, 28, 0, 0, 0, 80005, 105901, 10, "ですがわたくしは\n一人ではありません\n【美食殿】のみなさまが\nおります", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (451, "コッコロ", 0, 29, 0, 0, 0, 80005, 105901, 10, "どんな困難も\nみなさまと一緒なら\n乗り越えられると\nわたくし、確信しております", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (452, "コッコロ", 0, 30, 0, 0, 0, 80005, 105901, 10, "それに今回はシェフィさまも\nご参加されるわけですし\nママとしてお手本に\nならねばなりませんね", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (453, "コッコロ", 0, 31, 0, 0, 0, 80005, 105901, 10, "主さま。3.5周年も\n食卓を囲んでいる時のような\n楽しい思い出を\n一緒に作りましょうね", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (454, "ペコリーヌ", 0, 26, 0, 0, 0, 80005, 105801, 11, "3.5周年まであと４日！\nやばいですね☆", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (455, "キャル", 0, 32, 1, 0, 0, 80005, 106001, 11, "はいはい、やばいわね\nというかもうすぐ生放送だけど\nあんた準備はできてるの？", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (456, "ペコリーヌ", 0, 27, 0, 0, 0, 80005, 105801, 11, "ばっちりオイッスー☆　ですよ！\n『王家の装備』も全開です！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (457, "ペコリーヌ", 0, 28, 0, 0, 0, 80005, 105801, 11, "あとは前回を超える\n最高のオイッスー☆　を\nお届けできるように\n練習あるのみですかね！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (458, "ペコリーヌ", 0, 29, 0, 0, 0, 80005, 105801, 11, "キャルちゃんは\nどうなんですか？\nオイッスー☆　を\n毎日ちゃんと練習してますか？", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (459, "キャル", 0, 33, 1, 0, 0, 80005, 106001, 11, "はぁ！？　なんであたしまで\nその変なあいさつを練習しないと\nいけないのよ！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (460, "ペコリーヌ", 0, 30, 0, 0, 0, 80005, 105801, 11, "もしかしてやってないんですか？\nダメですよキャルちゃん！\nシェフィちゃんだってちゃんと\n毎日練習してるのに！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (461, "ペコリーヌ", 0, 31, 0, 0, 0, 80005, 105801, 11, "こうなったらわたしがキャルちゃん\nにオイッスー☆　の『オ』から\nしっかり教えてあげないと！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (462, "ペコリーヌ", 0, 32, 0, 0, 0, 80005, 105801, 11, "オイッスー☆　の『オ』は\n口を大きく開いて〜\nドラゴンの骨つき肉に\nかぶりつく直前をイメージして〜", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (463, "キャル", 0, 34, 1, 0, 0, 80005, 106001, 11, "だからあたしはやるなんて一言も…", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (464, "ペコリーヌ", 0, 33, 0, 0, 0, 80005, 105801, 11, "オイッスー☆", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (465, "キャル", 0, 35, 1, 0, 0, 80005, 106001, 11, "聞きなさいっての！！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (466, "シェフィ", 0, 31, 2, 0, 0, 80005, 106401, 12, "3.5周年まであと３日…\nそして生放送は、明日…", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (467, "シェフィ", 0, 32, 2, 0, 0, 80005, 106401, 12, "もっと台本を\n読み込んでおかないと…\nあと発声練習も…", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (468, "シェフィ", 0, 33, 2, 0, 0, 80005, 106401, 12, "おいっす！\n…こうじゃなかったわね\nええと確か…", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (469, "シェフィ", 0, 34, 2, 0, 0, 80005, 106401, 12, "うう…あんなに練習したのに\nこれじゃみんなに迷惑かけちゃう…", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (470, "シェフィ", 0, 35, 2, 0, 0, 80005, 106401, 12, "【美食殿】の一員として\n認められるように\n頑張らないといけないのに\nよし、もう一度…", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (471, "ペコリーヌ", 0, 34, 3, 0, 0, 80005, 105801, 12, "やってますねシェフィちゃん！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (472, "キャル", 0, 36, 0, 0, 0, 80005, 106001, 12, "なによ、考えることは\nみんな同じってこと？", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (473, "コッコロ", 0, 32, 1, 0, 0, 80005, 105901, 12, "ふふ。シェフィさま\nよろしければ\n仲間に入れてください\n一緒にやればきっとうまくできます", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (474, "シェフィ", 0, 36, 2, 0, 0, 80005, 106401, 12, "みんな…もちろんよ！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (475, "ペコリーヌ", 0, 35, 3, 0, 0, 80005, 105801, 12, "それじゃ明日の生放送に向けて\n一緒に練習がんばりましょう☆", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (476, "カリン", 0, 7, 2, 0, 0, 80005, 190801, 13, "3.5周年まであと２日と\n迫った本日は…\nいよいよ生放送です！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (477, "カリン", 0, 8, 2, 0, 0, 80005, 190801, 13, "【美食殿】のみなさんが\n準備に入られていますので\nそれぞれ繋いでみましょう！\nまずはペコリーヌさんです！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (478, "ペコリーヌ", 0, 36, 3, 0, 0, 80005, 105801, 13, "今日もオイッスー☆\nみんなで一緒に\nオイッスー☆", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (479, "カリン", 0, 9, 2, 0, 0, 80005, 190801, 13, "お手本になるような\n発声練習ですね！\nそれでは続いてコッコロさん", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (480, "コッコロ", 0, 33, 4, 0, 0, 80005, 105901, 13, "はじめちょろちょろ\nなかぱっぱ～♪\n生放送前にみなさまで\n食卓を囲んで英気を養いましょう", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (481, "カリン", 0, 10, 2, 0, 0, 80005, 190801, 13, "さすが【美食殿】のママですね！\nそしてキャルさんは…", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (482, "キャル", 0, 37, 1, 0, 0, 80005, 106001, 13, "大丈夫…大丈夫…\nあたしは大丈夫…", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (483, "カリン", 0, 11, 2, 0, 0, 80005, 190801, 13, "す、すごく震えてますけど\n大丈夫でしょうか…？\nそして今回初参加となる\n期待の新人シェフィさん！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (484, "シェフィ", 0, 37, 0, 0, 0, 80005, 106401, 13, "あうー？", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (485, "みんな", 0, 37, 4, 0, 1, 80005, 105801, 13, "あ…", "105901,34,106001,38,190801,12");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (486, "キャル", 0, 39, 1, 0, 0, 80005, 106001, 13, "ちょ、ちょっとシェフィ！？\nなに赤ちゃん返りしてん―\nあ痛ぁっ！\n噛んだわこいつーっ！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (487, "コッコロ", 0, 35, 4, 0, 0, 80005, 105901, 13, "シェフィさま\n緊張しすぎて\nしまったのでしょうね…\nおーよしよし…", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (488, "ペコリーヌ", 0, 38, 3, 0, 0, 80005, 105801, 13, "シェフィちゃん！\nそういう時は手のひらに\n好きな文字を書いて\n食べちゃうといいですよ！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (489, "カリン", 0, 13, 2, 0, 0, 80005, 190801, 13, "…ええと、現場からは以上です！\n生放送はもうすぐ！\n特別なお知らせもありますので\nどうぞお楽しみに！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (490, "シェフィ", 0, 38, 2, 0, 0, 80005, 106401, 14, "3.5周年の記念日も\nいよいよ明日ね\n…って、あれ？", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (491, "ペコリーヌ", 0, 39, 3, 0, 0, 80005, 105801, 14, "記念日はこの森へ行きませんか！？\nすっごく美味しいお肉が\nとれるんです☆", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (492, "キャル", 0, 40, 0, 0, 0, 80005, 106001, 14, "うげ、またあの魔物？\nさすがに今度は\n丸焼きとかはやめてよね…\n見た目がヤバすぎるし", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (493, "コッコロ", 0, 36, 1, 0, 0, 80005, 105901, 14, "ふふ、キャルさまもだいぶ\n魔物肉に慣れてきたようですね\n調理ならばわたくしに\nお任せください♪", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (494, "シェフィ", 0, 39, 2, 0, 0, 80005, 106401, 14, "みんな、何をしているの？\nなんだか、\n不穏な単語が聞こえたんだけど…", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (495, "ペコリーヌ", 0, 40, 3, 0, 0, 80005, 105801, 14, "シェフィちゃん！\n明日の食べ尽くしツアーの\n相談をしてたんですよ！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (496, "コッコロ", 0, 37, 1, 0, 0, 80005, 105901, 14, "はい。明日は【美食殿】の\n一員となったシェフィさまと一緒に\n初めて記念日を過ごすのですから\n特別なコース料理を用意しませんと", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (497, "キャル", 0, 41, 0, 0, 0, 80005, 106001, 14, "ふふん♪\n猫の毛も逆立つような\nあれこれを食べさせられるから\n覚悟しておいたほうがいいわよ？", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (498, "シェフィ", 0, 40, 2, 0, 0, 80005, 106401, 14, "へ、へぇ…", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (499, "ペコリーヌ", 0, 41, 3, 0, 0, 80005, 105801, 14, "期待しててくださいね！\nシェフィちゃんとあなたに\n『おいちー！』って喜んでもらえる\n最高のごちそうを準備しますから☆", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (500, "シェフィ", 0, 41, 2, 0, 0, 80005, 106401, 14, "ええと…その…うーんと…\nわ、分かったわ\n私も【美食殿】の一員だもの", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (501, "シェフィ", 0, 42, 2, 0, 0, 80005, 106401, 14, "それにあなたも一緒みたいだし\n最後までがんばってみるわ…！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (502, "ペコリーヌ", 0, 42, 3, 0, 0, 80005, 105801, 15, "それではいつものあれを\nやりましょう！\n準備はいいですかっ？\n…いっきますよー！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (503, "キャル", 0, 42, 1, 0, 0, 80005, 106001, 15, "祝！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (504, "コッコロ", 0, 38, 4, 0, 0, 80005, 105901, 15, "3.5周年！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (505, "シェフィ", 0, 43, 0, 0, 0, 80005, 106401, 15, "おめでとうございます！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (506, "カリン", 0, 14, 2, 0, 0, 80005, 190801, 15, "この日を迎えられたのも\nあなたが『プリンセスコネクト！\nRe:Dive』を遊んでくれている\nおかげです！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (507, "カリン", 0, 15, 2, 0, 0, 80005, 190801, 15, "ということで\nみなさんからあなたへ一言ずつ\n感謝の言葉をどうぞ！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (508, "ペコリーヌ", 0, 43, 3, 0, 0, 80005, 105801, 15, "感謝の言葉ですか？　わたしは\n毎日あなたに感謝してますよ\n【美食殿】の仲間になってくれて…\n一緒にごはんを食べてくれて…", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (509, "ペコリーヌ", 0, 44, 3, 0, 0, 80005, 105801, 15, "なにより今でもわたしをぺコリーヌ\nって呼んでくれてありがとうござい\nます。これからもわたしはあなたの\nぺコリーヌです！　やばいですね☆", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (510, "キャル", 0, 43, 1, 0, 0, 80005, 106001, 15, "えっ！？　今度はあたし！？\n感謝って…まぁ、そうね…\nあんたにはあたしが\n辛くて苦しい時に助けてもらって…", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (511, "キャル", 0, 44, 1, 0, 0, 80005, 106001, 15, "でもおかげで色々吹っ切れたわ！\nもしあんたが困ってたら今度は\nあたしが助けてあげるから\nその時はちゃんと言いなさいよ！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (512, "シェフィ", 0, 44, 0, 0, 0, 80005, 106401, 15, "ええと…次は私ね\n実はよく覚えてないのだけど\n空から落ちてきた私を受け止めて\nくれたのはあなたなのでしょう？", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (513, "シェフィ", 0, 45, 0, 0, 0, 80005, 106401, 15, "最初に私を見つけてくれたのが\nあなたで良かったって思ってるの\nおかげで私は居場所を見つけたから\nあなたがくれたもの、大切にするわ", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (514, "コッコロ", 0, 39, 4, 0, 0, 80005, 105901, 15, "では最後にわたくしから…\nわたくしは主さまの元を離れた日…\nこのように記念日をお祝いする\nことは二度とないと思っていました", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (515, "コッコロ", 0, 40, 4, 0, 0, 80005, 105901, 15, "ですが、主さまはわたくしを\n迎えにきてくださり、今もこうして\nおそばにおいてくださっています\nわたくしにとって毎日が記念日です", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (516, "コッコロ", 0, 41, 4, 0, 0, 80005, 105901, 15, "わたくしたちの物語は\nまだ続いていきます\nこれからも一緒にページを\n作っていきましょうね", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (517, "ペコリーヌ", 0, 45, 3, 0, 0, 80005, 105801, 15, "それじゃ\nいつものあれでしめますよ！\nみんなで一緒に、さんはい☆", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (518, "みんな", 0, 46, 4, 0, 1, 80005, 105801, 15, "これからも\nよろしくおねがいします！", "105901,42,106001,45,106401,46,190801,16");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (519, "カリン", 0, 1, 1, 1, 0, 60003, 190804, 0, "新年を迎え、新たに始まる一日を\nおみくじで占ってみませんか？\n良い運勢であるほど、\n沢山のプレゼントが貰えますよ♪", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (520, "", 1, 0, 2, 1, 0, 60003, 0, 0, "今日の運勢は姫吉！　どんな不可能も可能にできちゃう！　　　　仲間と食卓を囲めばさらに運気上昇よ！　ただし魔物料理以外！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (521, "", 1, 0, 2, 1, 0, 60003, 0, 0, "今日の運勢は姫吉！　やること全てが最高潮！　やばいですね☆　ラッキーアイテムはおにぎり！　お腹一杯で幸せが続くでしょう！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (522, "", 1, 0, 2, 1, 0, 60003, 0, 0, "今日の運勢は姫吉！　羽が生えたように飛躍できる絶好調の運気！新しいことに挑戦するなら今しかないわ！　やってみましょう！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (523, "", 1, 0, 2, 1, 0, 60003, 0, 0, "今日の運勢は姫吉！　精霊も新年の幸福をお祝いしています！　　主さまの笑顔が見られて、わたくしも幸せいっぱいでございます♪", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (524, "", 1, 0, 2, 2, 0, 60003, 0, 0, "今日の運勢は姫吉！　やること全てが上手くいく最高の一日！　　あなたの活躍に先生もにっこりです。いい子いい子♪", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (525, "", 2, 0, 2, 1, 0, 60003, 0, 0, "今日の運勢は大吉！　お裁縫好きな女の子のお墨付きです！　　　お世話したがりの妹にめいっぱい愛されちゃうかも！？", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (526, "", 2, 0, 2, 1, 0, 60003, 0, 0, "今日の運勢は大吉！　ラッキーカラーは赤！　　　　　　　　　　湧き上がるアイデアの数々に、高笑いが止まりませんわ！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (527, "", 2, 0, 2, 1, 0, 60003, 0, 0, "今日の運勢は大吉！　ラッキーカラーは鮮やかな黄色！　　　　　さらにシマシマのアイテムを持つと、運気倍増らしいッスよ♪", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (528, "", 2, 0, 2, 1, 0, 60003, 0, 0, "今日の運勢は大吉！　秘められた力が開花するでしょう！　　　　じゃあ、私の龍眼と力比べしてみようか？　ふふ、冗談だよ～", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (529, "", 2, 0, 2, 2, 0, 60003, 0, 0, "今日の運勢は大吉！　悩んでいたことが解決しそうです！　　　　たまには勉強も仕事も忘れて、心あたたまる家族と過ごしましょ！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (530, "", 3, 0, 2, 1, 0, 60003, 0, 0, "今日の運勢は中吉！　家の中でゆっくり過ごすのがよさそう！　　ラッキーアイテムはゲームよ！　ベストスコアを更新できるかも？", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (531, "", 3, 0, 2, 1, 0, 60003, 0, 0, "今日の運勢は中吉！　上手くなりたいことに積極的に挑戦してみてサックス好きの妹が、手取り足取りサポートしてくれるかも～？", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (532, "", 3, 0, 2, 1, 0, 60003, 0, 0, "今日の運勢は中吉！　仕事運が急上昇中です！　　　　　　　　　きっと「煉獄に舞う一陣の疾風」の如き活躍ができますよ！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (533, "", 3, 0, 2, 1, 0, 60003, 0, 0, "今日の運勢は中吉！　日頃の努力が報われます！　　　　　　　　親しい友人がいるなら報告してみて。一緒に喜んでくれるはずだ。", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (534, "", 3, 0, 2, 1, 0, 60003, 0, 0, "今日の運勢は中吉！　ラッキーアイテムはかわいいもの！　　　　ア、アタシはかわいいじゃなくてキレイだってば！　も～っ！", "");
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES (535, "", 3, 0, 2, 1, 0, 60003, 0, 0, "今日の運勢は中吉！　チャンスを待つと良い結果に！　　　　　　ラッキーアイテムのプリンを食べて、運を呼び込みましょう！", "");
