INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("カリン", 60000, 0, 1, "", 0, 1, "新年を迎え、新たに始まる一日を\nおみくじで占ってみませんか？\n良い運勢であるほど、\n沢山のプレゼントが貰えますよ♪", 1, 190804, 1, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", 60000, 1, 2, "", 0, 0, "今日の運勢は姫吉！　あらゆる運気が最大を示しています！　　　今なら「伝説の吸血鬼」をも従え、世界征服も夢じゃありません！", 1, 0, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", 60000, 1, 3, "", 0, 0, "今日の運勢は姫吉！　やること全てが最高潮！　やばいですね☆　ラッキーアイテムはおにぎり！　お腹一杯で幸せが続くでしょう！", 1, 0, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", 60000, 1, 4, "", 0, 0, "今日の運勢は姫吉！　その高い運気はまさにショーグンの如く！　その溢れる威光は人々を惹きつけ、皆の人気者間違いなしです！", 2, 0, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", 60000, 2, 5, "", 0, 0, "今日の運勢は大吉！　運気がどんどん高まっています！　　　　　何をやっても、最大効率で素晴らしい結果になりますよ♪", 1, 0, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", 60000, 2, 6, "", 0, 0, "今日の運勢は大吉！　なにをやっても上手くいって大活躍！　　　そんな素敵なあなたを見て、後ろから誰かが……クスクス♪", 1, 0, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", 60000, 2, 7, "", 0, 0, "今日の運勢は大吉！　運気が高く、何かに挑戦すると良い結果に！隠れた才能が引き出されそう！　目指せヒデサ……ではなく秀才！", 2, 0, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", 60000, 3, 8, "", 0, 0, "今日の運勢は中吉！　商売運が上がってきています！　　　　　　ラッキーアイテムはたいやき！　和菓子パワーで売上アップです！", 1, 0, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", 60000, 3, 9, "", 0, 0, "今日の運勢は中吉！　旅行運がぐんぐんと上昇中♪　　　　　　　もしかしたら、あの「マホマホ王国」にだって行けるかも♪", 1, 0, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", 60000, 3, 10, "", 0, 0, "今日の運勢は中吉！　仕事運が急上昇中です！　　　　　　　　　きっと「煉獄に舞う一陣の疾風」の如き活躍ができますよ！", 1, 0, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", 60000, 3, 11, "", 0, 0, "今日の運勢は中吉！　チャンスを待つと良い結果に！　　　　　　ラッキーアイテムのプリンを食べて、運を呼び込みましょう！", 1, 0, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", 60000, 3, 12, "", 0, 0, "今日の運勢は中吉！　運気が着々と上昇中！　　　　　　　　　　精霊の加護を受けたかの如く、色々な事がうまくいきますよ♪", 2, 0, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", 60000, 4, 13, "", 0, 0, "今日の運勢は小吉！　早朝に幸運の兆しあり！　　　　　　　　　もしかしたら、優しいお姉ちゃんが起こしにきてくれますよ♪", 1, 0, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", 60000, 4, 14, "", 0, 0, "今日の運勢は小吉！　運気上昇中ですが、上がるまでが要注意！　ラッキーアニマルは熊！　本当の家族のように守ってくれるかも♪", 1, 0, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", 60000, 4, 15, "", 0, 0, "今日の運勢は小吉！　外に出てゆっくり過ごすと運気が上昇！　　ラッキースポットは牧場。面白い出会いがありそうです♪", 1, 0, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", 60000, 4, 16, "", 0, 0, "今日の運勢は小吉！　運気は高いけども、ハプニングには注意！　出かけた先で、「ふしんしゃ」扱いされてしまうかも……", 1, 0, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", 60000, 4, 17, "", 0, 0, "今日の運勢は小吉！　根を詰めずにリラックスすると運気向上！　ラッキーパーソンはアイドル。歌があなたを幸せにしてくれます♪", 2, 0, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("カリン", 60000, 0, 18, "", 0, 2, "おみくじの結果はどうでしたか？\n新たに始まる一日が、あなたに\nとって良い日になりますように\nまた明日も遊びに来て下さいね♪", 0, 190804, 3, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("カリン", 60000, 0, 19, "", 7, 3, "おみくじは楽しんで頂けましたか？\nあなたの新たなる年が\n素晴らしいものになるよう、\n心からお祈りいたします", 0, 190804, 3, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("ハツネ", 80001, 0, 20, "", 1, 1, "キミと出会ってもう\n1年もたつんだね！\nう～ん、いつも\n寝てばっかりだから、", 0, 101201, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("ハツネ", 80001, 0, 21, "", 1, 2, "少しでも\n寝る時間を減らして\nキミと一緒にいる\n時間増やしたいなぁ♪", 0, 101201, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("シオリ", 80001, 0, 22, "", 1, 1, "あなたと知り合ってから\nもう1年…\n時がたつのは\n早いですね", 0, 103801, 1, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("シオリ", 80001, 0, 23, "", 1, 2, "来年も、\n一緒にいられたら…\nうれしいな", 0, 103801, 1, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("キョウカ", 80001, 0, 24, "", 2, 1, "あなたと一緒にいるようになって、\nもう1年ですか…", 0, 103601, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("キョウカ", 80001, 0, 25, "", 2, 2, "最初の頃よりも、頼もしく\nなりましたね。私はどうですか？\n成長していますか？", 0, 103601, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("ミミ", 80001, 0, 26, "", 2, 1, "おにいちゃんとあってから、\n1年たったよ～！", 0, 102001, 1, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("ミミ", 80001, 0, 27, "", 2, 2, "ミミね、おにいちゃんと\nいっぱい遊べてたのしかったな～♪\nこれからもずっと\nいっしょにいてね～！", 0, 102001, 1, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("ミソギ", 80001, 0, 28, "", 2, 1, "にいちゃんがミソギのイタズラに\n引っかかり続けてもう1年かぁ", 0, 100401, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("ミソギ", 80001, 0, 29, "", 2, 2, "…これからもいっぱいミソギと\n遊んでね、にいちゃん！", 0, 100401, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("マホ", 80001, 0, 30, "", 3, 1, "もう王子はんと会うてから\n1年ぐらいたつそうどすえ\n早いもんやなあ", 0, 101001, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("マホ", 80001, 0, 31, "", 3, 2, "このままやと、\nうちもあっという間に\n干からびてしまいそうどすえ\nなーんてな。うふふ", 0, 101001, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("イリヤ", 80001, 0, 32, "", 3, 1, "お主と会って早1年じゃな\nこれからも存分に尽くすがよい", 0, 104401, 1, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("イリヤ", 80001, 0, 33, "", 3, 2, "わらわもたまには、お主の献身に\n報いてやらぬでもない♪", 0, 104401, 1, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("カオリ", 80001, 0, 34, "", 3, 1, "もう、キミと出会って結構な時間が\nたつさ～。でも、私たちの旅は\nまだまだ始まったばかりさ～！", 0, 101701, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("カオリ", 80001, 0, 35, "", 3, 2, "これからも、どうぞよろしく\nお願いするさ～！", 0, 101701, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("サレン", 80001, 0, 36, "", 4, 1, "あんたと出会ってからもうそんなに\nたつのね～", 0, 102801, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("サレン", 80001, 0, 37, "", 4, 2, "あっという間だったって感じるほど\n楽しかったわ！\nこれからも一緒に頑張りましょ！", 0, 102801, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("スズメ", 80001, 0, 38, "", 4, 1, "出会ってから1年間、ずっと\nご迷惑をかけっぱなしで、\nすみません…", 0, 102501, 1, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("スズメ", 80001, 0, 39, "", 4, 2, "これからはもっと頑張りますから\n見ていてくださいね\nできれば…\n今までよりも、私の近くで♪", 0, 102501, 1, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("タマキ", 80001, 0, 40, "", 5, 1, "振り返れば、今までいろんなことが\nあったにゃあ…", 0, 104601, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("タマキ", 80001, 0, 41, "", 5, 2, "これからも、二人でたくさんの\n思い出をがっぽり作っていく\nにゃー！", 0, 104601, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("ミフユ", 80001, 0, 42, "", 5, 1, "キミとの師弟関係も、\nもう1年なのね…\nでもキミに教えることは、", 0, 104801, 1, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("ミフユ", 80001, 0, 43, "", 5, 2, "まだまだいっぱいあるわ\nこれからも、\nしっかりついて来てね！", 0, 104801, 1, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("アキノ", 80001, 0, 44, "", 6, 1, "あなたさまと出会ってもう1年…", 0, 103201, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("アキノ", 80001, 0, 45, "", 6, 2, "光陰矢のごとし、とはまさに\nこのことですわ。…思い出は決して\nお金では買えませんわね", 0, 103201, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("ユカリ", 80001, 0, 46, "", 6, 1, "ダメな私を1年間も見捨てないで\nくれて、本当にありがとう", 0, 103401, 1, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("ユカリ", 80001, 0, 47, "", 6, 2, "情けないところいっぱいみせるかも\nしれないけど、これからも一緒に\nいてくれると、うれしいな", 0, 103401, 1, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("クリスティーナ", 80001, 0, 48, "", 7, 1, "オマエと出会ってもう1年か\nまさかこのワタシの結婚相手に\nなろうとはな…", 0, 107101, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("クリスティーナ", 80001, 0, 49, "", 7, 2, "ともかくこれからも\n愉しませてくれよ☆", 0, 107101, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("ジュン", 80001, 0, 50, "", 7, 1, "君と出会ってからもう1年か。\n以前と比べて私の表情もいくらか\n柔らかくなった気がするんだが、\nどうだろう？", 0, 104701, 1, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("ジュン", 80001, 0, 51, "", 7, 2, "見えないから分からないって？\nそれもそうだな…", 0, 104701, 1, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("トモ", 80001, 0, 52, "", 8, 1, "もう1年たったみたいだね\nあなたをからかってばかりだった\nけど、楽しかった♪", 0, 103701, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("トモ", 80001, 0, 53, "", 8, 2, "これからもよろしくね！", 0, 103701, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("マツリ", 80001, 0, 54, "", 8, 1, "出会って、もう1年たっちゃった\nんスね。なんだか意外ッス", 0, 100501, 1, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("マツリ", 80001, 0, 55, "", 8, 2, "毎日楽しくて、あっという間に過ぎ\nちゃったみたいなんスよね\nこれからも一緒に悪と戦うッス！", 0, 100501, 1, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("ミヤコ", 80001, 0, 56, "", 9, 1, "オマエと会って1年なの！\n全然成仏もできなかったし、", 0, 100701, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("ミヤコ", 80001, 0, 57, "", 9, 2, "生き返りもしなかったけど、\nすごく楽しかったの～♪\nオマエとプリンがあれば、\nミヤコはハッピーなの～♪", 0, 100701, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("シノブ", 80001, 0, 58, "", 9, 1, "あなたと出会って1年なんですね\nでも、私の占いでは、私とあなたは", 0, 103101, 1, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("シノブ", 80001, 0, 59, "", 9, 2, "来年も再来年も一緒にいるようです\nふふ、よろしくお願いしますね", 0, 103101, 1, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("アンナ", 80001, 0, 60, "", 10, 1, "貴様が閃光のシグルドとして覚醒し\nもう1年か…。これからも我らの\n長き戦いに終わりはないが、", 0, 100901, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("アンナ", 80001, 0, 61, "", 10, 2, "それでも貴様となら…\n添い遂げる覚悟はできているぞ！", 0, 100901, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("ナナカ", 80001, 0, 62, "", 10, 1, "君と出会ってもう1年だって！\n早いねー！", 0, 101301, 1, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("ナナカ", 80001, 0, 63, "", 10, 2, "これからも最強のバディタッグで\n大活躍だー！\n頼りにしてるよ、相棒！", 0, 101301, 1, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("ミツキ", 80001, 0, 64, "", 11, 1, "1年たつのって、あっという間ね\nまだまだ君の知らない部分が\nたくさんあるし…", 0, 105101, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("ミツキ", 80001, 0, 65, "", 11, 2, "これからも、いろんなところを\n調べさせてね？", 0, 105101, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("ルカ", 80001, 0, 66, "", 11, 1, "お前さんと出会ってもう1年か\n助太刀するつもりが", 0, 105601, 1, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("ルカ", 80001, 0, 67, "", 11, 2, "逆に助けられたりもしたね\nこれからもよろしく頼むよ！", 0, 105601, 1, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("エリコ", 80001, 0, 68, "", 11, 1, "早いものですわね、あなた様と\nお会いして、もう1年…", 0, 102701, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("エリコ", 80001, 0, 69, "", 11, 2, "あなた様といると、時があっと\nいう間に過ぎてしまいます\n時間を止める研究をしなくては\nいけませんね…", 0, 102701, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("アヤネ", 80001, 0, 70, "", 12, 1, "お兄ちゃんと会ってから、もう、\n1年なんだね。私、少しは\nお姉ちゃんっぽくなったかな？", 0, 102301, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("アヤネ", 80001, 0, 71, "", 12, 2, "あ、ぷうきちもお兄ちゃんっぽく\nなったよね？", 0, 102301, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("チカ", 80001, 0, 72, "", 12, 1, "あなたと出会って、\nもう1年がたったんですね…\nこれからもそばにいて、", 0, 104201, 1, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("チカ", 80001, 0, 73, "", 12, 2, "私のことを見守ってください\nできることなら、ずっと…", 0, 104201, 1, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("クルミ", 80001, 0, 74, "", 12, 1, "お兄ちゃんと出会って1年かぁ\n私、相変わらずダメダメで…", 0, 102101, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("クルミ", 80001, 0, 75, "", 12, 2, "で、でもこれからもずっと…\n一緒にいたいなぁ…", 0, 102101, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("シズル", 80001, 0, 76, "", 13, 1, "再会してちょうど1年だね\nお祝いにごちそう作ってきたよ～♪\n特製ケーキと、お寿司と、", 0, 104901, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("シズル", 80001, 0, 77, "", 13, 2, "ハンバーグと、カレーと、\nシチューと、あ、隠しきれない隠し\n味は、お姉ちゃんの愛情だよ！", 0, 104901, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("リノ", 80001, 0, 78, "", 13, 1, "お兄ちゃんと再会して、もう1年\nなんですね。\n会えなかった時を思うと、", 0, 101101, 1, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("リノ", 80001, 0, 79, "", 13, 2, "信じられないくらい幸せな1年\nでした！もう絶対、私から離れ\nないでくださいね♪", 0, 101101, 1, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("ユイ", 80001, 0, 80, "", 14, 1, "騎士クンと出会ってから今まで、\nいろんなことがあったよね\n楽しいこと、ドキドキしたこと…", 0, 100201, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("ユイ", 80001, 0, 81, "", 14, 2, "そんな日々をこれからも、\nず、ずっと一緒に…\n過ごしていきたいな…！", 0, 100201, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("ヒヨリ", 80001, 0, 82, "", 14, 1, "騎士くんと出会って、まだ1年しか\nたってないんだ！？", 0, 100101, 1, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("ヒヨリ", 80001, 0, 83, "", 14, 2, "なんかもうずっと一緒にいる気が\nするなぁ…えへへ。これからの\n1年もまたよろしくねっ！\n騎士くん！", 0, 100101, 1, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("レイ", 80001, 0, 84, "", 14, 1, "初めてキミに出会って1年…\n随分見違えたね私もキミも、\n更に強くなっていく…", 0, 100301, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("レイ", 80001, 0, 85, "", 14, 2, "キミに背中を預けられる日が、\n早くきますように", 0, 100301, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("ペコリーヌ", 80001, 0, 86, "", 15, 1, "1年もわたしのことを、忘れない\nでいてくれて、ありがとうござい\nます！", 0, 105801, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("ペコリーヌ", 80001, 0, 87, "", 15, 2, "これからもずっと、覚えていて\nくださいね！", 0, 105801, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("コッコロ", 80001, 0, 88, "", 15, 1, "こうして1年も、主さまのおそばに\n置いていただけて、とても光栄\nです。どうかこれからも、", 0, 105901, 1, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("コッコロ", 80001, 0, 89, "", 15, 2, "末永く主さまをお世話させて\nください。わたくしは、ずっと\n主さまのガイド役でいたいのです", 0, 105901, 1, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("キャル", 80001, 0, 90, "", 15, 1, "1年か、まっさかこんなに長い\n付き合いになるなんてね～\nでも…わりと楽しい1年だったかも", 0, 106001, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("キャル", 80001, 0, 91, "", 15, 2, "だから、もうしばらくあんたに\n付き合ってやるわよ喜びなさい！", 0, 106001, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("スズメ", 60001, 0, 92, "", 0, 1, "新しい年のはじめに、\nおみくじはいかがでしょうか？\nよい運勢であるほど、\n沢山プレゼントが貰えますよ♪", 1, 112101, 1, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", 60001, 1, 93, "", 0, 0, "今日の運勢は姫吉！　あらゆる運気が最大を示しています！　　　今なら「伝説の吸血鬼」をも従え、世界征服も夢じゃありません！", 1, 0, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", 60001, 1, 94, "", 0, 0, "今日の運勢は姫吉！　やること全てが最高潮！　やばいですね☆　ラッキーアイテムはおにぎり！　お腹一杯で幸せが続くでしょう！", 1, 0, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", 60001, 1, 95, "", 0, 0, "今日の運勢は姫吉！　最強の運勢でどんなドジも跳ね除けます！　大切な人と一緒に、最高の一日になること間違いなし！", 1, 0, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", 60001, 1, 96, "", 0, 0, "今日の運勢は姫吉！　どんな挑戦も上手くいく、最高の運勢です！理想の自分に変身できる絶好の機会！　新しい一歩を踏み出そう！", 1, 0, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", 60001, 1, 97, "", 0, 0, "今日の運勢は姫吉！　その高い運気はまさにショーグンの如く！　その溢れる威光は人々を惹きつけ、皆の人気者間違いなしです！", 2, 0, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", 60001, 2, 98, "", 0, 0, "今日の運勢は大吉！　運気がどんどん高まっています！　　　　　何をやっても、最大効率で素晴らしい結果になりますよ♪", 1, 0, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", 60001, 2, 99, "", 0, 0, "今日の運勢は大吉！　なにをやっても上手くいって大活躍！　　　そんな素敵なあなたを見て、後ろから誰かが……クスクス♪", 1, 0, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", 60001, 2, 100, "", 0, 0, "今日の運勢は大吉！　ラッキーワードは「ちぇる～ん♪」　　　　出会う人みんなに言ってあげると、ハッピーになれますよ！", 1, 0, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", 60001, 2, 101, "", 0, 0, "今日の運勢は大吉！　運勢はバッチリで無敵状態！　　　　　　　龍眼で全てを見通して、思い通りの一日を過ごしましょう！", 1, 0, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", 60001, 2, 102, "", 0, 0, "今日の運勢は大吉！　運気が高く、何かに挑戦すると良い結果に！隠れた才能が引き出されそう！　目指せヒデサ……ではなく秀才！", 2, 0, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", 60001, 3, 103, "", 0, 0, "今日の運勢は中吉！　商売運が上がってきています！　　　　　　ラッキーアイテムはたいやき！　和菓子パワーで売上アップです！", 1, 0, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", 60001, 3, 104, "", 0, 0, "今日の運勢は中吉！　旅行運がぐんぐんと上昇中♪　　　　　　　もしかしたら、あの「マホマホ王国」にだって行けるかも♪", 1, 0, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", 60001, 3, 105, "", 0, 0, "今日の運勢は中吉！　仕事運が急上昇中です！　　　　　　　　　きっと「煉獄に舞う一陣の疾風」の如き活躍ができますよ！", 1, 0, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", 60001, 3, 106, "", 0, 0, "今日の運勢は中吉！　チャンスを待つと良い結果に！　　　　　　ラッキーアイテムのプリンを食べて、運を呼び込みましょう！", 1, 0, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", 60001, 3, 107, "", 0, 0, "今日の運勢は中吉！　お出かけした先で良縁に恵まれそうです！　ダウナー系あざとい女子が、あま～い飴をくれるかも？", 1, 0, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", 60001, 3, 108, "", 0, 0, "今日の運勢は中吉！　大抵のことはゴリ押しでなんとかできます！ラッキープレイスはゲーセン。パンチングマシンでストレス発散！", 1, 0, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", 60001, 3, 109, "", 0, 0, "今日の運勢は中吉！　運気が着々と上昇中！　　　　　　　　　　精霊の加護を受けたかの如く、色々な事がうまくいきますよ♪", 2, 0, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", 60001, 4, 110, "", 0, 0, "今日の運勢は小吉！　早朝に幸運の兆しあり！　　　　　　　　　もしかしたら、優しいお姉ちゃんが起こしにきてくれますよ♪", 1, 0, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", 60001, 4, 111, "", 0, 0, "今日の運勢は小吉！　運気上昇中ですが、上がるまでが要注意！　ラッキーアニマルは熊！　本当の家族のように守ってくれるかも♪", 1, 0, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", 60001, 4, 112, "", 0, 0, "今日の運勢は小吉！　外に出てゆっくり過ごすと運気が上昇！　　ラッキースポットは牧場。面白い出会いがありそうです♪", 1, 0, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", 60001, 4, 113, "", 0, 0, "今日の運勢は小吉！　運気は高いけども、ハプニングには注意！　出かけた先で、「ふしんしゃ」扱いされてしまうかも……", 1, 0, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", 60001, 4, 114, "", 0, 0, "今日の運勢は小吉！　同腹一心の友だちが見つかるかも！　　　　キーワードは「ユニちゃんズ」、記憶に刻みましょう！", 1, 0, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", 60001, 4, 115, "", 0, 0, "今日の運勢は小吉！　思わぬ相手と仲良くなれるかも？　　　　　うっかり毒舌をもらしてしまわないように要注意です！", 1, 0, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", 60001, 4, 116, "", 0, 0, "今日の運勢は小吉！　根を詰めずにリラックスすると運気向上！　ラッキーパーソンはアイドル。歌があなたを幸せにしてくれます♪", 2, 0, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("スズメ", 60001, 0, 117, "", 0, 2, "おみくじの結果はいかがでしたか？\n新たに始まる一日が、\nあなたにとってよい日になりますように\n明日もお待ちしていますね♪", 0, 112101, 3, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("スズメ", 60001, 0, 118, "", 7, 3, "おみくじ、楽しんで頂けましたか？\nあなたの新しい年が\n素晴らしいものになるよう、\n心からお祈りしますね", 0, 112101, 3, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("ペコリーヌ", 80003, 0, 119, "", 1, 1, "ヒューヒュー♪\nドンドンパフパフー♪", 0, 105801, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("コッコロ", 80003, 0, 120, "", 1, 1, "ひゅーひゅー\nどんどんぱふぱふー\nでございます", 0, 105901, 1, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("コッコロ", 80003, 0, 121, "", 1, 2, "さぁ、キャルさまもご一緒に\nどんどんぱふぱふいたしましょう", 0, 105901, 1, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("キャル", 80003, 0, 122, "", 1, 1, "え？　ちょっと\nいきなりなにっ？", 0, 106001, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("キャル", 80003, 0, 123, "", 1, 2, "ドンドンパフパフってなんなの？\nあたしにも分かるように\nちゃんと説明しなさいっての！", 0, 106001, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("ペコリーヌ", 80003, 0, 124, "", 1, 2, "実はですねキャルちゃん！\nもうすぐわたしたちにとっての\n大切な節目──", 0, 105801, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("ペコリーヌ", 80003, 0, 125, "", 1, 3, "『プリンセスコネクト！\nRe:Dive』\n2.5周年なんですよ！\nやばいですね☆", 0, 105801, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("コッコロ", 80003, 0, 126, "", 1, 3, "ですからその日に向けて\n今から気分を盛り上げているのです", 0, 105901, 1, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("キャル", 80003, 0, 127, "", 1, 3, "ああ、そういえば\nそんな時期だっけ\nって言っても、もう少し先じゃない\n気が早いんじゃないの？", 0, 106001, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("コッコロ", 80003, 0, 128, "", 1, 4, "いいえ、キャルさま\nこういったお祝いは準備から\n楽しむものでございます\nそうですよね、ペコリーヌさま", 0, 105901, 1, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("ペコリーヌ", 80003, 0, 129, "", 1, 4, "その通りです♪　というわけで！\nカウントダウンの始まりですよ！\nみんなで一緒に2.5周年に向けて\n盛り上がっていきましょー☆", 0, 105801, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("キャル", 80003, 0, 130, "", 1, 4, "ド、ドンドンパフパフ～\nって何言わせんのよ！\nまったくもう、浮かれちゃってさ", 0, 106001, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("コッコロ", 80003, 0, 131, "", 1, 5, "ふふ。キャルさまもワクワクして\nおいでのようですね\nそれでは主さま。大切な記念日まで\nどうぞご期待くださいませ", 0, 105901, 1, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("キャル", 80003, 0, 132, "", 2, 5, "記念日を盛り上げるったって\n一体どうしたらいいのよ？\nあたし、そういうの\n苦手なんだけど…", 0, 106001, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("カリン", 80003, 0, 133, "", 2, 1, "あら、キャルさん\nどうかなさいましたか？\n何かお困りのようですけど", 0, 190801, 1, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("キャル", 80003, 0, 134, "", 2, 6, "げっ…カリンじゃない\nなんでもないわよ！", 0, 106001, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("カリン", 80003, 0, 135, "", 2, 2, "そうなのですか？\nキャルさんのことですからてっきり\n2.5周年をどう盛り上げたら\nいいか悩んでいるのかと…", 0, 190801, 1, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("キャル", 80003, 0, 136, "", 2, 7, "分かってんじゃないのよ！？\nいっ、いや、違うけどね！\nあたしは別にそんなことで\n悩んでないわよっ？", 0, 106001, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("カリン", 80003, 0, 137, "", 2, 3, "ふふっ…\nこういう時は、真心を込めて\n気持ちをそのまま口にしたら\nいいのではないでしょうか？", 0, 190801, 1, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("キャル", 80003, 0, 138, "", 2, 8, "気持ちを、ねぇ\nそれが一番難しいんじゃない…\nそもそも、あたしは記念日とか\n別にどうだっていいし", 0, 106001, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("キャル", 80003, 0, 139, "", 2, 9, "…まあ、ペコリーヌもコロ助も\nやけに楽しそうだったから…\nこういうお祭り騒ぎみたいなのも\nたまにはいいかな、とは思うけど", 0, 106001, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("カリン", 80003, 0, 140, "", 2, 4, "なるほどなるほど～\nキャルさんは【美食殿】で祝う\n2.5周年を心から楽しみに\nしている、ということですね！", 0, 190801, 1, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("キャル", 80003, 0, 141, "", 2, 10, "はぁ！？\nどこ聞いたらそうなるのよ！\nあたしは別に楽しみになんか…！", 0, 106001, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("カリン", 80003, 0, 142, "", 2, 5, "キャルさんの心からの想い\nみなさんにも伝わったでしょうか？\n私も2.5周年が楽しみです！\n以上、カリンがお伝えしました！", 0, 190801, 1, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("キャル", 80003, 0, 143, "", 2, 11, "心からの想いって何なのよ！？\n勝手に人の言葉を\n捻じ曲げんじゃないわよ！\nこのくそ眼鏡ーーーっ！", 0, 106001, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("コッコロ", 80003, 0, 144, "", 3, 6, "ペコリーヌさま、もうすぐ\n記念日でございますね\nこういった節目というのは\n感慨深いものでございます", 0, 105901, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("ペコリーヌ", 80003, 0, 145, "", 3, 5, "色んな思い出が蘇ってきますね！\n楽しい出来事が\nいっぱいありました♪", 0, 105801, 1, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("コッコロ", 80003, 0, 146, "", 3, 7, "はい、そうでございますね\n主さまも【美食殿】の活動の他にも\nいろんなご経験をされたと\n聞いておりますよ", 0, 105901, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("コッコロ", 80003, 0, 147, "", 3, 8, "たしか…ランドソルで話題沸騰と\nなっていた、魔法少女なるものに\nお会いになられたとか", 0, 105901, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("ペコリーヌ", 80003, 0, 148, "", 3, 6, "魔法少女って、あの美味しくって\n栄養たっぷりの大ヒット商品\n『魔法少女ソーセージ』の箱に\n描いてある女の子のことですか！？", 0, 105801, 1, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("ペコリーヌ", 80003, 0, 149, "", 3, 7, "あのソーセージ\n美味しいんですよね～♪\nわたしもたっくさん食べてますよ！", 0, 105801, 1, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("コッコロ", 80003, 0, 150, "", 3, 9, "あとは、牧場に素敵な温泉が\n出来たとか…", 0, 105901, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("ペコリーヌ", 80003, 0, 151, "", 3, 8, "牧場ですか！？　温泉につかって\nチーズもオツですね～♪　考えた\nだけでよだれが出ちゃいそうです！\nやばいですね☆", 0, 105801, 1, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("コッコロ", 80003, 0, 152, "", 3, 10, "ペコリーヌさま\n言ってるそばからよだれが…\n今お拭きしますね。ふき、ふき", 0, 105901, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("ペコリーヌ", 80003, 0, 153, "", 3, 9, "えへへ、ありがとうございます\nコッコロちゃん！", 0, 105801, 1, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("コッコロ", 80003, 0, 154, "", 3, 11, "それにしても、主さまは\n【美食殿】の一員として様々な\n美食と出会われていたのですね\nわたくし、感服いたしました", 0, 105901, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("ペコリーヌ", 80003, 0, 155, "", 3, 10, "これからもたくさん冒険をして\nめいっぱい美食を\n味わい尽くしましょうね！", 0, 105801, 1, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("ペコリーヌ", 80003, 0, 156, "", 4, 11, "2.5周年カウントダウン♪\nキャルちゃんキャルちゃん！\nわくわくしますね☆", 0, 105801, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("キャル", 80003, 0, 157, "", 4, 12, "あぁもう、うるさいわね～\nまだまだ先でしょ？\nちょっとは落ち着きなさいよ", 0, 106001, 1, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("ペコリーヌ", 80003, 0, 158, "", 4, 12, "キャルちゃんだって最近\nソワソワしてるじゃないですか～\nさっきもご機嫌そうに鼻歌とか\n歌ってましたよね？", 0, 105801, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("キャル", 80003, 0, 159, "", 4, 13, "なっ…！　き、聞いてたの！？\nじゃない、歌ってないわよ！？\nあんたいい加減なこと言うと\nぶっ殺すわよ！？", 0, 106001, 1, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("ペコリーヌ", 80003, 0, 160, "", 4, 13, "えっへへ～♪\nわたしは最近のキャルちゃんが\nもっともっと大好きですよ♪\nすごく積極的になりましたよね！", 0, 105801, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("キャル", 80003, 0, 161, "", 4, 14, "…積極的？\nなんの話よ？", 0, 106001, 1, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("ペコリーヌ", 80003, 0, 162, "", 4, 14, "またまた～♪\nアイドルを目指そうとしたこと\n忘れちゃったんですか？", 0, 105801, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("ペコリーヌ", 80003, 0, 163, "", 4, 15, "ムーサの町で\nきゃるきゃる～ん☆　って！", 0, 105801, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("キャル", 80003, 0, 164, "", 4, 15, "あっ…！？　ちちち違うわよっ！\nあれは操られてただけで\nあたしはアイドルなんか\n目指してないっての！！", 0, 106001, 1, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("ペコリーヌ", 80003, 0, 165, "", 4, 16, "本当に本当ですか？\nわたし、キャルちゃんとっても\n乗り気だなって\n思ってたんですけど～", 0, 105801, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("キャル", 80003, 0, 166, "", 4, 16, "まぁ、悪くはなかったけど…って\nだ、だから目指してないって\n言ってるでしょ！！\nなんてこと思い出させんのよ！？", 0, 106001, 1, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("キャル", 80003, 0, 167, "", 4, 17, "ああ…ダメだわ…\nもう殺すしかない…\n2.5周年が来る前に\n記憶ごと消し去ってやるー！", 0, 106001, 1, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("キャル", 80003, 0, 168, "", 5, 18, "2.5周年まであと10日…\nふ～ん？\n案外早いもんね？", 0, 106001, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("コッコロ", 80003, 0, 169, "", 5, 12, "そうでございますね…\nその日のためにやるべきことは\nまだまだございます\n気を引き締めねばなりません…！", 0, 105901, 1, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("キャル", 80003, 0, 170, "", 5, 19, "やる気十分ねコロ助…\nそんな気負ってるとしんどいわよ？\nお気楽にやりましょ？", 0, 106001, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("コッコロ", 80003, 0, 171, "", 5, 13, "キャルさま…\nありがとうございます\nキャルさまは本当に\n優しいおかたです", 0, 105901, 1, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("キャル", 80003, 0, 172, "", 5, 20, "な、なにわけの分かんないこと\n言ってんのよ…\nあたしは優しくなんかないっての", 0, 106001, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("コッコロ", 80003, 0, 173, "", 5, 14, "そんなことはございません\n…あの、実はわたくし\nキャルさまに\nひとつお願いがございまして", 0, 105901, 1, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("キャル", 80003, 0, 174, "", 5, 21, "お願い？　珍しいわね\nコロ助がそんなこと言うなんて\n…まあ、あたしに出来ることなら\n別にいいけど", 0, 106001, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("コッコロ", 80003, 0, 175, "", 5, 15, "ありがとうございます\n実は記念日へ向けて\n『かくし芸』というものを\n用意してみようかと思いまして", 0, 105901, 1, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("キャル", 80003, 0, 176, "", 5, 22, "かくし芸？", 0, 106001, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("コッコロ", 80003, 0, 177, "", 5, 16, "はい。きゃるきゃる～ん☆　を\n参考に、ころころ～ん☆　という\nご挨拶を考えてみたのですが\nポーズがうまくいかなくて…", 0, 105901, 1, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("キャル", 80003, 0, 178, "", 5, 23, "～～！！　ちょっと褒められて\nいい気になって損したわ！！\nふざけんなふざけんな\nふ～ざ～け～ん～な～っ！！", 0, 106001, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("ペコリーヌ", 80003, 0, 179, "", 6, 17, "あと９日！　もうすぐですよ♪\nえへへ、楽しみで楽しみで\nごはんが進みますね☆", 0, 105801, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("キャル", 80003, 0, 180, "", 6, 24, "ごはんが進みますね☆\nじゃなーい！\nいくらなんでも食べ過ぎよ！\n記念日、迎えられなくなるわよ！？", 0, 106001, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("コッコロ", 80003, 0, 181, "", 6, 17, "大丈夫ですよキャルさま\n食事はわたくしたちの\n歩んできた道そのもの\nなのですから", 0, 105901, 1, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("ペコリーヌ", 80003, 0, 182, "", 6, 18, "さっすがコッコロちゃん\nいいことを言いますね！\nそう、わたしたち\n【美食殿】の活動目的は─", 0, 105801, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("キャル", 80003, 0, 183, "", 6, 25, "─みんなで楽しく食事をすること\nでしょ？\nあれだけ食べ歩いてきたんだから\n分かってるわよ", 0, 106001, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("コッコロ", 80003, 0, 184, "", 6, 18, "はい…そうでございますね♪\nそれにしてもわたくしたち\n本当にいろいろなものを\n食べ歩いてきましたね…", 0, 105901, 1, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("キャル", 80003, 0, 185, "", 6, 26, "そうね…ほんっとうにね…\n毒まみれのニセドラゴンとか\n恐竜…は逃げられちゃったっけ\nあとワニゴ…ワニの魔物とかね", 0, 106001, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("キャル", 80003, 0, 186, "", 6, 27, "そして、とうとう本物の\nドラゴンまで相手に\nしちゃったのよね、あたしたちって", 0, 106001, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("キャル", 80003, 0, 187, "", 6, 28, "信じられない強さだったわね\nあのドラゴン…やっぱり\n作戦はしっかり立てていかないと\nダメよね", 0, 106001, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("ペコリーヌ", 80003, 0, 188, "", 6, 19, "えへへ…☆\nキャルちゃんが色んなことを\n覚えていてくれて\nうれしいです♪", 0, 105801, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("コッコロ", 80003, 0, 189, "", 6, 19, "わたくしも鮮明に覚えておりますよ\n主さまもきっと…♪", 0, 105901, 1, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("キャル", 80003, 0, 190, "", 6, 29, "あったり前でしょ！\n忘れないわよ、あんな滅茶苦茶な\nものばかり食べさせられて！\n…忘れられるわけ、ないじゃない", 0, 106001, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("キャル", 80003, 0, 191, "", 7, 30, "ねえちょっと、ちょっと待って！？\nカリンから、2.5周年記念の\n生放送に出演してほしいって\n言われたんだけど！", 0, 106001, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("キャル", 80003, 0, 192, "", 7, 31, "なんでそういう大事なことを\n急に言うわけ！？\nそんなの聞いてないわよ～！？", 0, 106001, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("キャル", 80003, 0, 193, "", 7, 32, "いっぱい人に見られちゃうのよね？\nなんであたしがそんなこと…", 0, 106001, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("キャル", 80003, 0, 194, "", 7, 33, "あー\nあー\n…声、これで大丈夫かしら？", 0, 106001, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("キャル", 80003, 0, 195, "", 7, 34, "聞き取りにくいかもしれないわね…\nもうちょっと明るい方がいいかも\nえっと…こんな感じ？", 0, 106001, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("キャル", 80003, 0, 196, "", 7, 35, "オイッスー☆", 0, 106001, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("キャル", 80003, 0, 197, "", 7, 36, "……\nいや、ないわ\nいつもどおりが一番ね、うん", 0, 106001, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("キャル", 80003, 0, 198, "", 7, 37, "もう、記念の生放送なんて\n超責任重大なやつじゃない…\nもしなにか失敗とかしたら…", 0, 106001, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("キャル", 80003, 0, 199, "", 7, 38, "ど、どど、どうしよう！？\n緊張してきちゃった…！！", 0, 106001, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("コッコロ", 80003, 0, 200, "", 8, 20, "主さま、わたくし\nペコリーヌさまとキャルさまと共に\n2.5周年を祝う生放送に\n出演する大役を仰せつかりました", 0, 105901, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("コッコロ", 80003, 0, 201, "", 8, 21, "多くの方々に\n『プリンセスコネクト！\nRe:Dive』の魅力をお伝えする\n重要な役割でございます", 0, 105901, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("コッコロ", 80003, 0, 202, "", 8, 22, "わたくし、全身全霊をもって\n生放送に取り組みたいと\n思っております", 0, 105901, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("コッコロ", 80003, 0, 203, "", 8, 23, "ですが、もしかしたら\nセリフを噛んでしまったり\n段取りを間違えてしまったり\nするかもしれません…", 0, 105901, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("コッコロ", 80003, 0, 204, "", 8, 24, "そのようなことがないよう\n練習はしっかりしておかなくては\nまずはこちらから…", 0, 105901, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("コッコロ", 80003, 0, 205, "", 8, 25, "オイッスー☆", 0, 105901, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("コッコロ", 80003, 0, 206, "", 8, 26, "先ほどキャルさまが練習されて\nいるのをお見かけしたので\nわたくしもと思いまして…\nいかがでしょうか？", 0, 105901, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("コッコロ", 80003, 0, 207, "", 8, 27, "良い感じ、ですか？\n…ふふ、ありがとうございます\n主さまのあたたかいお言葉があれば\nわたくしは百人力でございます", 0, 105901, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("コッコロ", 80003, 0, 208, "", 8, 28, "この大役を無事に成し遂げ\n主さまに素敵な2.5周年を\n迎えていただけるよう…\nわたくし、尽力いたしますね", 0, 105901, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("ペコリーヌ", 80003, 0, 209, "", 9, 20, "オイッスー☆", 0, 105801, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("ペコリーヌ", 80003, 0, 210, "", 9, 21, "…う～ん、今のはちょっと\n違う感じがしますね\n堅苦しかったかもしれません", 0, 105801, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("ペコリーヌ", 80003, 0, 211, "", 9, 22, "あと６日で2.5周年ですか…", 0, 105801, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("ペコリーヌ", 80003, 0, 212, "", 9, 23, "この年月がオイッスーの\n歴史なんだと思うと\nじ～んとしちゃいます！\nやばいですね☆", 0, 105801, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("ペコリーヌ", 80003, 0, 213, "", 9, 24, "生放送で最高のオイッスー☆　を\nみなさんにお届けするために\n頑張らないと！\nそれじゃあもう一度…", 0, 105801, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("ペコリーヌ", 80003, 0, 214, "", 9, 25, "オイッスー☆", 0, 105801, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("ペコリーヌ", 80003, 0, 215, "", 9, 26, "…うう～ん、どうしてでしょう\nまだ何かが違う気がします…\nもしかしたら\nおなかが空きすぎてるとか？", 0, 105801, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("ペコリーヌ", 80003, 0, 216, "", 9, 27, "今さっき食べたばっかりですけど\nおなかが減っては\n良いオイッスー☆　は\n出せませんよね！", 0, 105801, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("ペコリーヌ", 80003, 0, 217, "", 9, 28, "そうと決まれば、コッコロちゃんと\nキャルちゃんを誘って\nごはんにしましょう！\n善は急げです！", 0, 105801, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("ペコリーヌ", 80003, 0, 218, "", 9, 29, "あ、二人が丁度いいところに！\nオイッス～☆　…ああ！！\n今の、今のですよ～♪", 0, 105801, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("ペコリーヌ", 80003, 0, 219, "", 9, 30, "なるほど、どんな時も\n自然体が一番ってことですね！\nこの調子で、生放送も\n頑張っちゃいます♪", 0, 105801, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("ペコリーヌ", 80003, 0, 220, "", 10, 31, "2.5周年まで残り５日ですが\nその前にもうすぐ生放送ですよ！\nコッコロちゃん\n準備の方はどうですか？", 0, 105801, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("コッコロ", 80003, 0, 221, "", 10, 29, "はい。わたくし、しっかりと\n備えてまいりました\n万事抜かりはございません", 0, 105901, 1, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("ペコリーヌ", 80003, 0, 222, "", 10, 32, "おお～すごい自信を感じます…！\nいい生放送になりそうですね☆\n……あれ？　なんだかこの辺り\n揺れていませんか？", 0, 105801, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("キャル", 80003, 0, 223, "", 10, 39, "…………", 0, 106001, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("コッコロ", 80003, 0, 224, "", 10, 30, "キャルさま？\nお風邪をひかれたんでしょうか\nひどい震えですが…", 0, 105901, 1, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("キャル", 80003, 0, 225, "", 10, 40, "かかかか風邪じゃないわよ！\nここここれはただ\nななな生放送が楽しみ過ぎて\nワワワワクワクが止まらないだけ！", 0, 106001, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("ペコリーヌ", 80003, 0, 226, "", 10, 33, "大丈夫ですかキャルちゃん！？\nそうだ、これを飲んでください！", 0, 105801, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("キャル", 80003, 0, 227, "", 10, 41, "きき気がきくじゃない、ありがとっ\nごくっ、んく…ぶはぁっ！？\nちょっと何飲ませたの！？\n辛っ！　苦ぁ！？　甘ぁっ！？", 0, 106001, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("ペコリーヌ", 80003, 0, 228, "", 10, 34, "これは魔物のエキスに薬草に\nその他色んなものを混ぜた\n特製の栄養ドリンクです！", 0, 105801, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("ペコリーヌ", 80003, 0, 229, "", 10, 35, "これを飲めば体ぽかぽかで\n元気いっぱいですよ♪", 0, 105801, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("キャル", 80003, 0, 230, "", 10, 42, "このアホリーヌ！\n風邪じゃないって言ったでしょ！？\nどさくさに紛れて\nそんな恐ろしいもん飲ませんな～！", 0, 106001, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("コッコロ", 80003, 0, 231, "", 10, 31, "キャルさま\n震えが収まっておりますね\nよかったです…\nわたくし、心配いたしました", 0, 105901, 1, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("キャル", 80003, 0, 232, "", 10, 43, "え？　ああ、ホントね…って\nだから風邪じゃないって\n言ってるのに…\nまあいいわこの際なんでも", 0, 106001, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("ペコリーヌ", 80003, 0, 233, "", 10, 36, "えっへへ～♪　いつも通りの\nキャルちゃんに戻りましたし\n素敵な生放送が出来そうですね！\n今から楽しみです☆", 0, 105801, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("カリン", 80003, 0, 234, "", 11, 6, "ランドソルのみなさま、こんにちは\n生放送の会場設営を担当しています\n【ギルド管理協会】のカリンです", 0, 190801, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("カリン", 80003, 0, 235, "", 11, 7, "【美食殿】のみなさんは\nまもなく始まる生放送に向けて\n最後の調整のため\n席を外しています", 0, 190801, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("カリン", 80003, 0, 236, "", 11, 8, "頑張っている３人の\n邪魔をするわけには\nいきませんので、ご容赦ください", 0, 190801, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("カリン", 80003, 0, 237, "", 11, 9, "ですので、本日は私から\n【美食殿】のみなさんの様子を\nお伝えさせていただこうと思います", 0, 190801, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("カリン", 80003, 0, 238, "", 11, 10, "まずはペコリーヌさんですが…\n何やら大量のおにぎりを\n食べているようです\nえっと…栄養は大事ですよね", 0, 190801, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("カリン", 80003, 0, 239, "", 11, 11, "次にコッコロさんですが\nこちらは発声練習をしています", 0, 190801, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("カリン", 80003, 0, 240, "", 11, 12, "一生懸命、小さなお口を開けて\n声を出す姿が応援したくなりますね", 0, 190801, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("カリン", 80003, 0, 241, "", 11, 13, "そしてキャルさんは…\nこれは…堂々としたものですね\n何もせず、ただ立ち尽くして…", 0, 190801, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("カリン", 80003, 0, 242, "", 11, 14, "あれ？\nもしかして緊張で気を失って…？", 0, 190801, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("カリン", 80003, 0, 243, "", 11, 15, "…ええと\n【美食殿】のみなさんは\nやる気十分なようですね！", 0, 190801, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("カリン", 80003, 0, 244, "", 11, 16, "３人とも生放送を\nいいものにしようと\n全力で頑張ってくれていますので\nどうかご期待ください", 0, 190801, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("カリン", 80003, 0, 245, "", 11, 17, "それでは\nまた明日お会いしましょう♪", 0, 190801, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("キャル", 80003, 0, 246, "", 12, 44, "2.5周年まであと３日！\nそして生放送は明日！\nなんかいよいよって感じね…", 0, 106001, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("ペコリーヌ", 80003, 0, 247, "", 12, 37, "３人でい～っぱい練習しましたし\nきっと大丈夫ですよ☆", 0, 105801, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("コッコロ", 80003, 0, 248, "", 12, 32, "ペコリーヌさまに\n大丈夫と言ってもらえると\nとても心強いですね", 0, 105901, 1, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("キャル", 80003, 0, 249, "", 12, 45, "信じすぎちゃだめよコロ助\nこいつはなーんにも\n考えてないだけなんだから", 0, 106001, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("ペコリーヌ", 80003, 0, 250, "", 12, 38, "ひどいですよぉキャルちゃ～ん！", 0, 105801, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("コッコロ", 80003, 0, 251, "", 12, 33, "ふふ。お二人とも\n普段通りの様子で\n安心いたしました", 0, 105901, 1, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("ペコリーヌ", 80003, 0, 252, "", 12, 39, "はいっ☆\n生放送でもなんでも\n全力全開、わたしらしく\nぶつかっていくだけです！", 0, 105801, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("キャル", 80003, 0, 253, "", 12, 46, "そうね。おめでたいことなんだし\nここまで来たんなら\n楽しめるだけ\n楽しむしかないわよね！", 0, 106001, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("コッコロ", 80003, 0, 254, "", 12, 34, "わたくしも、普段よりも心が\n熱くなっております\nこの胸の気持ちをお伝えできるよう\n精一杯努力いたします…！", 0, 105901, 1, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("ペコリーヌ", 80003, 0, 255, "", 12, 40, "それでは、いきましょう！\n明日の生放送、絶対に\n成功させましょうね☆", 0, 105801, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("ペコリーヌ", 80003, 0, 256, "", 13, 41, "えっと、最初のひと言は…あれ？\nキャルちゃん、わたし\n何て言うんでしたっけ？", 0, 105801, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("キャル", 80003, 0, 257, "", 13, 47, "は！？\nなにいきなりど忘れしてるの！？\nっていうかあんた、台本は！？", 0, 106001, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("ペコリーヌ", 80003, 0, 258, "", 13, 42, "忘れてきちゃいました☆", 0, 105801, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("キャル", 80003, 0, 259, "", 13, 48, "忘れてきちゃいました☆\nで済ませられると思ってんの！？\nほら、あたし二冊持ってきたから\nこれ使いなさい！", 0, 106001, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("コッコロ", 80003, 0, 260, "", 13, 35, "マイクとは、こんな高い位置に\nあるものなのですね…！\n背伸びをすれば、何とか…\nう～ん、うぅ～ん…！", 0, 105901, 1, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("キャル", 80003, 0, 261, "", 13, 49, "あぁもう\n少し下げればいいでしょ！？\nこれくらいでいい？\nまったく、世話が焼けるわね…", 0, 106001, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("ペコリーヌ", 80003, 0, 262, "", 13, 43, "あれ？\nキャルちゃん、後ろ髪が\nはねちゃってますよ？", 0, 105801, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("キャル", 80003, 0, 263, "", 13, 50, "え？　うそっ！？\nちゃんと確かめてきたのに！\nどこどこ！？", 0, 106001, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("コッコロ", 80003, 0, 264, "", 13, 36, "キャルさま、動かないで\nくださいまし。今くしで…\nはい、直りましたよ", 0, 105901, 1, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("キャル", 80003, 0, 265, "", 13, 51, "あ、ありがと…はぁ。生放送\n本番までもうすぐなのに\nこんなことしてる場合じゃ\nないわよね", 0, 106001, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("コッコロ", 80003, 0, 266, "", 13, 37, "はい、時間は残りわずかです\nですが、慌ただしく\n動き回るこの時間も\nなんだか楽しくて…♪", 0, 105901, 1, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("キャル", 80003, 0, 267, "", 13, 52, "コロ助、あんた…って\nそうじゃなくて！\n胸にジンワリきてる\n暇なんかないでしょ…！？", 0, 106001, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("ペコリーヌ", 80003, 0, 268, "", 13, 44, "あはは♪\nキャルちゃんの動きが\nとっても面白いです♪\nやばいですね☆", 0, 105801, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("コッコロ", 80003, 0, 269, "", 13, 38, "ふふ\nきっと面白い生放送になると\nわたくし、確信しております", 0, 105901, 1, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("ペコリーヌ", 80003, 0, 270, "", 13, 45, "絶対絶対、間違いありません！\n見てくれるみなさんが\n笑顔になれる時間を\nお届けしましょう！", 0, 105801, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("キャル", 80003, 0, 271, "", 13, 53, "そんなの当然でしょ！\n誰が見たって面白いって\n言ってもらえるように\nあれだけ準備したんだから！", 0, 106001, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("ペコリーヌ", 80003, 0, 272, "", 13, 46, "それでは\n記念日直前生放送！\n楽しみにしててくださいね☆", 0, 105801, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("カリン", 80003, 0, 273, "", 14, 18, "こんにちは\n【ギルド管理協会】のカリンです", 0, 190801, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("カリン", 80003, 0, 274, "", 14, 19, "昨日の生放送は\nいかがだったでしょうか？", 0, 190801, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("カリン", 80003, 0, 275, "", 14, 20, "たくさんの笑顔と喜びと\nそして驚きを受け取って\nいただけたのではないかと思います", 0, 190801, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("カリン", 80003, 0, 276, "", 14, 21, "そしていよいよ\n明日は2.5周年の当日\n待ちに待った記念日を\n迎えることとなります", 0, 190801, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("カリン", 80003, 0, 277, "", 14, 22, "【美食殿】のみなさんも\n張り切って様々な準備を\nされていますよ", 0, 190801, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("カリン", 80003, 0, 278, "", 14, 23, "【ギルド管理協会】も\n万全のサポート体制で\n協力してまいりたいと思います", 0, 190801, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("カリン", 80003, 0, 279, "", 14, 24, "…ふふ、本当に楽しみですね\n【ギルド管理協会】の職員として\nだけではなく、私個人としても…", 0, 190801, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("カリン", 80003, 0, 280, "", 14, 25, "この記念日を迎えられることが\n心から楽しみですし\n幸せを感じています", 0, 190801, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("カリン", 80003, 0, 281, "", 14, 26, "きっと【美食殿】のみなさんは\nそれ以上に心を弾ませて\nいることでしょう", 0, 190801, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("カリン", 80003, 0, 282, "", 14, 27, "それでは\n明日からの2.5周年を\nどうぞ、お楽しみに…♪", 0, 190801, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("ペコリーヌ", 80003, 0, 283, "", 15, 47, "準備はいいですかっ？\n…いっきますよー！", 0, 105801, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("キャル", 80003, 0, 284, "", 15, 54, "祝！", 0, 106001, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("コッコロ", 80003, 0, 285, "", 15, 39, "2.5周年！", 0, 105901, 1, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("ペコリーヌ", 80003, 0, 286, "", 15, 48, "おめでとうございま～す☆", 0, 105801, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("キャル", 80003, 0, 287, "", 15, 55, "少し前まではまだまだ先じゃない\nとか言ってたけど\n本当にあっという間だったわね", 0, 106001, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("コッコロ", 80003, 0, 288, "", 15, 40, "光陰矢の如し…\nとはこのことでございますね\nでも、今日という日を無事に\n迎えることができて良かったです", 0, 105901, 1, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("ペコリーヌ", 80003, 0, 289, "", 15, 49, "コッコロちゃん！　キャルちゃん！\n落ち着くには\nまだ早いですよ！\n盛り上がるのはこれからです☆", 0, 105801, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("キャル", 80003, 0, 290, "", 15, 56, "ほんとあんたはいつも\n全力全開ねー\nでもそうね、記念日なんだし\nパーっといきましょ！", 0, 106001, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("コッコロ", 80003, 0, 291, "", 15, 41, "ええ、わたくしも全力全開で\nまいります…！\n主さま、さあ\nどうぞ手を取ってくださいまし…♪", 0, 105901, 1, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("キャル", 80003, 0, 292, "", 15, 57, "あんたもコロ助も、浮かれて\n転ぶんじゃないわよ？\n…仕方ないから、あたしも\n手を繋いであげるわよ", 0, 106001, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("ペコリーヌ", 80003, 0, 293, "", 15, 50, "あー！　二人ともずるいです！\nわたしもっ、ギュー♪", 0, 105801, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("キャル", 80003, 0, 294, "", 15, 58, "ちょっ、ペコリーヌ！\nくっつき過ぎよ！", 0, 106001, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("ペコリーヌ", 80003, 0, 295, "", 15, 51, "記念日だから\n平気平気♪\nやばいですね☆", 0, 105801, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("キャル", 80003, 0, 296, "", 15, 59, "意味分かんないわよ！", 0, 106001, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("コッコロ", 80003, 0, 297, "", 15, 42, "ふふ。このめでたき日に\n喜びがあふれておりますね\n主さまは…いかがでしょうか？", 0, 105901, 1, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("コッコロ", 80003, 0, 298, "", 15, 43, "喜んでいただけているならば\nわたくしはとてもとても\n幸せでございます", 0, 105901, 1, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("ペコリーヌ", 80003, 0, 299, "", 15, 52, "さぁ、2.5周年を\n楽しみ尽くしますよ☆\nまだまだ食べたいものもありますし\nやりたいこともたくさんです！", 0, 105801, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("キャル", 80003, 0, 300, "", 15, 60, "分かった、分かったから\n引っ張るんじゃないわよ！\n…そんな掴まなくても\nちゃんとついていくから", 0, 106001, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("コッコロ", 80003, 0, 301, "", 15, 44, "ペコリーヌさま…キャルさま…\n主さま…共にまいりましょう\nどこまでも…！", 0, 105901, 1, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("ペコリーヌ", 80003, 0, 302, "", 15, 53, "はい！　それでは2.5周年\n食べつくしツアーへ！\nそして、その先の未来へ\n向かって出発しましょう！", 0, 105801, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("ペコリーヌ・コッコロ・キャル", 80003, 0, 303, "105901,45,106001,61", 15, 54, "2.5周年\nありがとうございます！", 0, 105801, 0, 1);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("カリン", 60002, 0, 304, "", 0, 1, "新年を迎え、新たに始まる一日を\nおみくじで占ってみませんか？\n良い運勢であるほど、\n沢山のプレゼントが貰えますよ♪", 1, 190804, 1, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", 60002, 1, 305, "", 0, 0, "今日の運勢は姫吉！　どんな不可能も可能にできちゃう！　　　　仲間と食卓を囲めばさらに運気上昇よ！　ただし魔物料理以外！", 1, 0, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", 60002, 1, 306, "", 0, 0, "今日の運勢は姫吉！　やること全てが最高潮！　やばいですね☆　ラッキーアイテムはおにぎり！　お腹一杯で幸せが続くでしょう！", 1, 0, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", 60002, 1, 307, "", 0, 0, "今日の運勢は姫吉！　最強の運勢でどんなドジも跳ね除けます！　大切な人と一緒に、最高の一日になること間違いなし！", 1, 0, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", 60002, 1, 308, "", 0, 0, "今日の運勢は姫吉！　どんな挑戦も上手くいく、最高の運勢です！理想の自分に変身できる絶好の機会！　新しい一歩を踏み出そう！", 1, 0, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", 60002, 1, 309, "", 0, 0, "今日の運勢は姫吉！　やること全てが上手くいく最高の一日！　　あなたの活躍に先生もにっこりです。いい子いい子♪", 2, 0, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", 60002, 2, 310, "", 0, 0, "今日の運勢は大吉！　お裁縫好きな女の子のお墨付きです！　　　お世話したがりの妹にめいっぱい愛されちゃうかも！？", 1, 0, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", 60002, 2, 311, "", 0, 0, "今日の運勢は大吉！　ラッキーカラーは赤！　　　　　　　　　　湧き上がるアイデアの数々に、高笑いが止まりませんわ！", 1, 0, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", 60002, 2, 312, "", 0, 0, "今日の運勢は大吉！　ラッキーワードは「ちぇる～ん♪」　　　　出会う人みんなに言ってあげると、ハッピーになれますよ！", 1, 0, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", 60002, 2, 313, "", 0, 0, "今日の運勢は大吉！　運勢はバッチリで無敵状態！　　　　　　　龍眼で全てを見通して、思い通りの一日を過ごしましょう！", 1, 0, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", 60002, 2, 314, "", 0, 0, "今日の運勢は大吉！　悩んでいたことが解決しそうです！　　　　たまには勉強も仕事も忘れて、心あたたまる家族と過ごしましょ！", 2, 0, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", 60002, 3, 315, "", 0, 0, "今日の運勢は中吉！　家の中でゆっくり過ごすのがよさそう！　　ラッキーアイテムはゲームよ！　ベストスコアを更新できるかも？", 1, 0, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", 60002, 3, 316, "", 0, 0, "今日の運勢は中吉！　上手くなりたいことに積極的に挑戦してみてサックス好きの妹が、手取り足取りサポートしてくれるかも～？", 1, 0, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", 60002, 3, 317, "", 0, 0, "今日の運勢は中吉！　仕事運が急上昇中です！　　　　　　　　　きっと「煉獄に舞う一陣の疾風」の如き活躍ができますよ！", 1, 0, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", 60002, 3, 318, "", 0, 0, "今日の運勢は中吉！　日頃の努力が報われます！　　　　　　　　親しい友人がいるなら報告してみて。一緒に喜んでくれるはずだ。", 1, 0, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", 60002, 3, 319, "", 0, 0, "今日の運勢は中吉！　商売運が上がってきています！　　　　　　ラッキーアイテムはたい焼き！　和菓子パワーで売上アップです！", 1, 0, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", 60002, 3, 320, "", 0, 0, "今日の運勢は中吉！　チャンスを待つと良い結果に！　　　　　　ラッキーアイテムのプリンを食べて、運を呼び込みましょう！", 1, 0, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", 60002, 3, 321, "", 0, 0, "今日の運勢は中吉！　運気が着々と上昇中！　　　　　　　　　　精霊の加護を受けたかのごとく、色々な事がうまくいきますよ♪", 2, 0, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", 60002, 4, 322, "", 0, 0, "今日の運勢は小吉！　まだ見ぬ危険が隠れているかも！　　　　　でも安心しろ！　提督が貴公を守ってくれるはずだぞ！", 1, 0, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", 60002, 4, 323, "", 0, 0, "今日の運勢は小吉！　いろんな事件が起こる一日になりそう！　　状況をよく観察してみようじゃないか！　まさに探偵のごとくね！", 1, 0, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", 60002, 4, 324, "", 0, 0, "今日の運勢は小吉！　気の向くままに過ごすのが幸せへの近道！　美味い魚を食べて猫を眺めて、自然体で過ごすのはどうだい？", 1, 0, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", 60002, 4, 325, "", 0, 0, "今日の運勢は小吉！　運気は高いけども、ハプニングには注意！　出かけた先で、「ふしんしゃ」扱いされてしまうかも……", 1, 0, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", 60002, 4, 326, "", 0, 0, "今日の運勢は小吉！　同腹一心の友だちが見つかるかも！　　　　キーワードは「ユニちゃんズ」、記憶に刻みましょう！", 1, 0, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", 60002, 4, 327, "", 0, 0, "今日の運勢は小吉！　周りに流されず、マイペースな一日を！　　ただし寝すぎには注意ぐうたらし過ぎず適度な運動を、だってさ～", 1, 0, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", 60002, 4, 328, "", 0, 0, "今日の運勢は小吉！　根を詰めずにリラックスすると運気向上！　ラッキーパーソンはアイドル。歌があなたを幸せにしてくれます♪", 2, 0, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("カリン", 60002, 0, 329, "", 0, 2, "おみくじの結果はどうでしたか？\n新たに始まる一日が、あなたに\nとって良い日になりますように\nまた明日も遊びに来て下さいね♪", 0, 190804, 3, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("カリン", 60002, 0, 330, "", 7, 3, "おみくじは楽しんで頂けましたか？\nあなたの新たなる年が\n素晴らしいものになるよう、\n心からお祈りいたします", 0, 190804, 3, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("コッコロ", 80005, 0, 331, "", 1, 1, "ささ、シェフィさまはこちらへ…", 0, 105901, 4, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("シェフィ", 80005, 0, 332, "", 1, 1, "コッコロさん、どこへ行くの…？\n…え？", 0, 106401, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("ペコリーヌ", 80005, 0, 333, "", 1, 1, "『おめでとうございます！\nわ～パチパチー！』\n…という感じでどうですかね！？", 0, 105801, 3, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("コッコロ", 80005, 0, 334, "", 1, 2, "ではわたくしも\n後ろからクラッカーを鳴らして\n応戦いたします\nああ、当日が待ち遠しいですね", 0, 105901, 4, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("シェフィ", 80005, 0, 335, "", 1, 2, "ええと…\n二人とも何をしているの？", 0, 106401, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("キャル", 80005, 0, 336, "", 1, 1, "シェフィは初めてだもんね\nあの日が近いからって\n予行練習してんのよ\n気が早いわよね～", 0, 106001, 1, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("シェフィ", 80005, 0, 337, "", 1, 3, "あの日…？", 0, 106401, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("ペコリーヌ", 80005, 0, 338, "", 1, 2, "その通りです…\nいよいよ記念すべきあの日が\n近づいてきましたよ！", 0, 105801, 3, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("ペコリーヌ", 80005, 0, 339, "", 1, 3, "『プリンセスコネクト！\nRe:Dive』3.5周年！", 0, 105801, 3, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("カリン", 80005, 0, 340, "", 1, 1, "やばいですね☆", 0, 190801, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("キャル", 80005, 0, 341, "", 1, 2, "って、なにしれっと\n参加してんのよカリン！", 0, 106001, 1, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("カリン", 80005, 0, 342, "", 1, 2, "ふふ、いいじゃないですか\n【ギルド管理協会】として\n私もみなさんと一緒に\nお祝いをさせてください！", 0, 190801, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("カリン", 80005, 0, 343, "", 1, 3, "それに…記念日が近づけば\nとっておきの情報を\nお届けしますから♪", 0, 190801, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("ペコリーヌ", 80005, 0, 344, "", 1, 4, "というわけで\n今日からシェフィちゃんも一緒に\nカウントダウンしちゃいましょう！\n3.5周年まで…", 0, 105801, 3, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("シェフィ", 80005, 0, 345, "", 1, 4, "えっ！？　ええとええと…！\nあ、あと14日…！", 0, 106401, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("ペコリーヌ", 80005, 0, 346, "", 2, 5, "3.5周年まであと13日\n豪華に、そして盛大に\nお祝いしたいですね☆", 0, 105801, 3, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("コッコロ", 80005, 0, 347, "", 2, 3, "豪華といえば前にとある貴族さまが\n舞踏会を開かれたそうで\n主さまがそのお手伝いを\nしておりました", 0, 105901, 1, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("コッコロ", 80005, 0, 348, "", 2, 4, "そこで美しい所作を学ばれたので\nわたくしも教えていただきました\nしゃなり、しゃなり…\nいかがでしょう？", 0, 105901, 1, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("ペコリーヌ", 80005, 0, 349, "", 2, 6, "すっごく上手ですよ☆\nまるでお姫様みたいです\nせっかくなのでわたしも\nしゃなりしゃなり", 0, 105801, 3, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("コッコロ", 80005, 0, 350, "", 2, 5, "さすがでございます\nペコリーヌさま", 0, 105901, 1, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("キャル", 80005, 0, 351, "", 2, 3, "いや、あんたは\nできなきゃダメでしょ\n本物のお姫様なんだし", 0, 106001, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("シェフィ", 80005, 0, 352, "", 2, 5, "しゃなり、しゃなり…\nふふ、案外できるものね", 0, 106401, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("ペコリーヌ", 80005, 0, 353, "", 2, 7, "わぁ！\nシェフィちゃん\n姿勢がとってもキレイです♪", 0, 105801, 3, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("コッコロ", 80005, 0, 354, "", 2, 6, "さ、次はキャルさまの\n番でございますよ", 0, 105901, 1, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("キャル", 80005, 0, 355, "", 2, 4, "あ、あたしはいいわよ！\nそういうの全然分かんないし…", 0, 106001, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("シェフィ", 80005, 0, 356, "", 2, 6, "大丈夫\nやってみると結構楽しいわよ\nほらほらっ", 0, 106401, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("キャル", 80005, 0, 357, "", 2, 5, "そ、そこまで言うなら…\nしゃなにゅわきゃあっ！", 0, 106001, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("キャル", 80005, 0, 358, "", 2, 6, "いたたた…もぉお～！\nなんであたしがこんな目に～！", 0, 106001, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("コッコロ", 80005, 0, 359, "", 2, 7, "まぁまぁ\n記念日までに練習すれば\nできるようになるかもしれません\nよろしければお教えいたしますよ", 0, 105901, 1, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("キャル", 80005, 0, 360, "", 2, 7, "よし！\nあんたたちができて\nあたしだけできないのはしゃくだし\nやってやろうじゃないの！！", 0, 106001, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("コッコロ", 80005, 0, 361, "", 3, 8, "3.5周年まであと12日\n記念日当日はもちろんですが\nその日を待つ時間というのも\n心躍ります", 0, 105901, 1, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("ペコリーヌ", 80005, 0, 362, "", 3, 8, "分かりますっ！\nどんな特別な料理を食べられるのか\n考えるだけでワクワクしますよね！", 0, 105801, 3, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("コッコロ", 80005, 0, 363, "", 3, 9, "特別な料理といえば、お祭りですね\nそういえば主さまは学生として\n聖学祭に参加されたこと\nがあるとか…", 0, 105901, 1, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("シェフィ", 80005, 0, 364, "", 3, 7, "聖学祭…って、なにかしら？", 0, 106401, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("コッコロ", 80005, 0, 365, "", 3, 10, "聖テレサ女学院の祭典ですよ\n二つのチームが競い合い\n青春を満喫するのが趣旨のようです", 0, 105901, 1, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("キャル", 80005, 0, 366, "", 3, 8, "それってカルミナの\nサプライズライブがあった\nお祭りよね？\nちょっと見たかったかも…", 0, 106001, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("キャル", 80005, 0, 367, "", 3, 9, "カルミナの後の\nライブも好評だったらしいじゃない\n確か、なかよし部\nだったっけ…？", 0, 106001, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("ペコリーヌ", 80005, 0, 368, "", 3, 9, "なかよし部…\n仲が良さそうな名前で\nやばいですね☆", 0, 105801, 3, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("キャル", 80005, 0, 369, "", 3, 10, "いや、そのまんますぎでしょ…", 0, 106001, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("シェフィ", 80005, 0, 370, "", 3, 8, "聖学祭かぁ…なんだか楽しそうね\nそれになかよし部の人たちも\n気になるわ", 0, 106401, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("ペコリーヌ", 80005, 0, 371, "", 3, 10, "それじゃあわたしたちも、\n負けじと仲良くしましょう♪\nほらほらシェフィちゃん、\nぎゅーっ☆　ですよ♪", 0, 105801, 3, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("シェフィ", 80005, 0, 372, "", 3, 9, "えっ？　ええ…\nぎゅーっ…？", 0, 106401, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("キャル", 80005, 0, 373, "", 4, 11, "3.5周年まであと11日！？\nお祝いの準備進んでないんだけど…\nああ～昨日に戻りたいわ…", 0, 106001, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("シェフィ", 80005, 0, 374, "", 4, 10, "そういえばそんな魔道具があるって\n聞いたことがあるわね\n確か、名前はタイムラーベ…", 0, 106401, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("キャル", 80005, 0, 375, "", 4, 12, "なにそれヤバいわね\nでも本当にあるんだったら\n準備よりももっとやり直したいこと\nたくさんあるんだけど…", 0, 106001, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("コッコロ", 80005, 0, 376, "", 4, 11, "もしや正月の件でしょうか…？\nあの時はお金がなくて\n苦労しましたから", 0, 105901, 1, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("キャル", 80005, 0, 377, "", 4, 13, "あれはそうね…\nお金を湯水のように使う\nぺコリーヌを止められたら\nあんな苦労をすることもなかったし", 0, 106001, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("ペコリーヌ", 80005, 0, 378, "", 4, 11, "そんなこともありましたね！\nやり直したいといえば\n『きゃるきゃる～ん♪』\nの方かと思いましたよ！", 0, 105801, 3, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("キャル", 80005, 0, 379, "", 4, 14, "バラすんじゃないわよ\nこのアホリーヌ！\nシェフィはそれ\n知らないんだから！", 0, 106001, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("キャル", 80005, 0, 380, "", 4, 15, "こうなったらもう、なんとか\nタイムラーべを探し出して\n今の会話をなかったことに…\nって、どうしたのよシェフィ", 0, 106001, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("シェフィ", 80005, 0, 381, "", 4, 11, "うん…もし、私だったら…\n私が知らないみんなとの時間を\n見てみたいな、って…", 0, 106401, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("ペコリーヌ", 80005, 0, 382, "", 4, 12, "シェフィちゃん！　タイムなんとか\nなんかなくても大丈夫ですよ！\n一緒の時間はこれから\nどんどん作ればいいんですから！", 0, 105801, 3, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("コッコロ", 80005, 0, 383, "", 4, 12, "はい、今この瞬間も\n未来にはかけがえのない思い出に\nなっていることでしょう", 0, 105901, 1, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("キャル", 80005, 0, 384, "", 4, 16, "だからそんな顔しないの\nほら、お祝いの準備、進めるわよ！", 0, 106001, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("シェフィ", 80005, 0, 385, "", 4, 12, "みんな…そうね\n最高のお祝いにして\n最高の思い出にしましょう！", 0, 106401, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("ペコリーヌ", 80005, 0, 386, "", 5, 13, "3.5周年まで、あと10日！\nもうすぐですよー！\n楽しみすぎて、やばいですね！", 0, 105801, 3, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("ペコリーヌ", 80005, 0, 387, "", 5, 14, "そういえば…たくさんのおもちゃが\n子どもたちを襲うっていう\n騒動もありましたね…\nなんでも勝手に動き出したとか！", 0, 105801, 3, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("ペコリーヌ", 80005, 0, 388, "", 5, 15, "学校や孤児院とかに\nちっちゃなおもちゃが乗り込んで\n暴れたみたいですよ！", 0, 105801, 3, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("キャル", 80005, 0, 389, "", 5, 17, "暴れたって言っても\n所詮はおもちゃじゃない\nどうせちっさいんでしょ？", 0, 106001, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("キャル", 80005, 0, 390, "", 5, 18, "だったら魔法で\n全部ぶっ飛ばせばいいじゃない！\n大したことないわよ！", 0, 106001, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("シェフィ", 80005, 0, 391, "", 5, 13, "キャルってば…\n相手は子どもが\n大事にしてる物なのよ…？", 0, 106401, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("コッコロ", 80005, 0, 392, "", 5, 13, "乱暴な発言は\nめっ、でございますよ？\n子どもの教育に悪影響を\n与えてしまいますから…", 0, 105901, 1, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("ペコリーヌ", 80005, 0, 393, "", 5, 16, "キャルちゃんだって\n意外と猫グッズとか\nたっくさん集めてますよね？\nもしその子たちが暴れたら…？", 0, 105801, 3, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("キャル", 80005, 0, 394, "", 5, 19, "う、うっさいわね！\n意外とか言うな！\n…ま、まあ確かに正面切って\n魔法でぶっ飛ばせないかもね…", 0, 106001, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("ペコリーヌ", 80005, 0, 395, "", 5, 17, "あはは☆\nキャルちゃんのそういうところ\n好きですよ♪", 0, 105801, 3, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("ペコリーヌ", 80005, 0, 396, "", 5, 18, "そんなキャルちゃんに猫グッズを\nプレゼントしちゃいますね！\n一部で流行っていた、たい焼き屋の\nおまけフィギュアですよ♪", 0, 105801, 3, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("ペコリーヌ", 80005, 0, 397, "", 5, 19, "乱暴に扱ったりしないでくださいね\nひとりでに暴れ出しちゃうかも\nしれませんから！", 0, 105801, 3, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("カリン", 80005, 0, 398, "", 6, 4, "3.5周年まであと９日\nここでみなさんに\nビックニュースですよ！", 0, 190801, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("カリン", 80005, 0, 399, "", 6, 5, "3.5周年を祝う生放送に\n去年と同様【美食殿】の\nみなさんにご出演いただくことが\n決定しましたー！", 0, 190801, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("キャル", 80005, 0, 400, "", 6, 20, "…えっ！？", 0, 106001, 1, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("ペコリーヌ", 80005, 0, 401, "", 6, 20, "本当ですか！？\nそれじゃ今から張り切って\n生放送中に食べる魔物料理を\n作らなくちゃですね☆", 0, 105801, 3, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("シェフィ", 80005, 0, 402, "", 6, 14, "いや…がんばるところは\nそこじゃないような…\nというか去年も出演してたのね\nすごいじゃない！", 0, 106401, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("コッコロ", 80005, 0, 403, "", 6, 14, "はい。とても緊張しますが\nやりがいのある大役です\n一緒にがんばりましょうね\nシェフィさま", 0, 105901, 4, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("シェフィ", 80005, 0, 404, "", 6, 15, "わ、私も出るの！？　でも…\n私も【美食殿】の一人だものね\nそれなら今からしっかり準備をして\nコンディションを整えないと…", 0, 106401, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("キャル", 80005, 0, 405, "", 6, 21, "ふふん♪\n生放送直前のカチコチになった\nシェフィの姿が楽しみだわ！", 0, 106001, 1, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("ペコリーヌ", 80005, 0, 406, "", 6, 21, "去年のキャルちゃんは\n緊張しすぎてずっと震えて\nかわいかったですもんね！\n今年もあの姿が見たいです！", 0, 105801, 3, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("キャル", 80005, 0, 407, "", 6, 22, "ぶっ殺すぞ！？", 0, 106001, 1, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("カリン", 80005, 0, 408, "", 6, 6, "というわけで\n『プリンセスコネクト！\nRe:Dive』の魅力を\nお伝えする生放送をお楽しみに♪", 0, 190801, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("コッコロ", 80005, 0, 409, "", 7, 15, "3.5周年まであと８日\nでございますよ\n楽しみですね、主さま", 0, 105901, 1, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("キャル", 80005, 0, 410, "", 7, 23, "そういえばあんた\n夏休みの宿題は\nきちんとやってるの？", 0, 106001, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("シェフィ", 80005, 0, 411, "", 7, 16, "宿題…学校に通うのも\n楽しいことばかりじゃ\nないのね", 0, 106401, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("ペコリーヌ", 80005, 0, 412, "", 7, 22, "あれ？　そういえば\n少し前にルーセント学院で\n何かあったような…", 0, 105801, 3, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("キャル", 80005, 0, 413, "", 7, 24, "ああ、始業式の日に\n大捕り物があったって\n話題になってたわね", 0, 106001, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("シェフィ", 80005, 0, 414, "", 7, 17, "夏休みを終わらせないって\n騒いでたっていう話？\nあれ本当なの？\n都市伝説だと思ってたわ", 0, 106401, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("コッコロ", 80005, 0, 415, "", 7, 16, "その一件ならよく存じ上げて\nおります。ですから主さま\n次の機会にはわたくしと一緒に\nがんばりましょう", 0, 105901, 1, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("シェフィ", 80005, 0, 416, "", 7, 18, "楽しそう…私も一緒にやりたいな\nママ…じゃ、じゃなくて！\nコッコロさん、私もまざっていい？", 0, 106401, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("コッコロ", 80005, 0, 417, "", 7, 17, "もちろんでございます\n仲良くお勉強いたしましょう", 0, 105901, 1, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("キャル", 80005, 0, 418, "", 7, 25, "しれっと教育ママが\n生まれてる…？", 0, 106001, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("ペコリーヌ", 80005, 0, 419, "", 7, 23, "お勉強は大切ですよ！\nキャルちゃんも一緒に\nがんばりましょう！", 0, 105801, 3, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("キャル", 80005, 0, 420, "", 7, 26, "え～～～～！？", 0, 106001, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("キャル", 80005, 0, 421, "", 8, 27, "3.5周年まであと７日〜♪\nふふ～ん♪\nふんふふ～ん♪", 0, 106001, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("シェフィ", 80005, 0, 422, "", 8, 19, "…\nねぇコッコロさん\nキャル、どうしちゃったの？", 0, 106401, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("コッコロ", 80005, 0, 423, "", 8, 18, "とてもご機嫌そうに\n鼻歌を歌ってらっしゃいますね\n耳も尻尾もリズミカルに\n揺れて…ノリノリでございます", 0, 105901, 1, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("ペコリーヌ", 80005, 0, 424, "", 8, 24, "あれはですね！\nカルミナのみなさんの\nサマーライブのチケットが\n手に入ったからなんですよ♪", 0, 105801, 3, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("シェフィ", 80005, 0, 425, "", 8, 20, "ええっ！？\nあのライブのチケットって確か\n入手困難だったんじゃ…", 0, 106401, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("キャル", 80005, 0, 426, "", 8, 28, "ふふふふ～ん♪\nしゃいにんぐふゅ～ちゃ～♪", 0, 106001, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("コッコロ", 80005, 0, 427, "", 8, 19, "なるほど…\nキャルさまがあのように\nなってしまうのも頷けますね", 0, 105901, 1, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("シェフィ", 80005, 0, 428, "", 8, 21, "…カルミナの\n『Shining Future』\nの中に、シャイニングフューチャー\nって歌詞はなかったはずだけど…", 0, 106401, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("キャル", 80005, 0, 429, "", 8, 29, "ふんふ…はっ！？\nあっ、あんたたち！\nなに人の鼻歌を\n勝手に聞いてるのよ！？", 0, 106001, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("キャル", 80005, 0, 430, "", 8, 30, "こっ、これは別に\nカルミナの曲じゃなくて…\nあたしのオリジナルソングよ！\n聞いたらお金取るわよっ！？", 0, 106001, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("ペコリーヌ", 80005, 0, 431, "", 8, 25, "えへへ、キャルちゃん\nカルミナのサマーライブ\n思いっきり楽しみましょうね☆", 0, 105801, 3, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("キャル", 80005, 0, 432, "", 8, 31, "も～っ！\n生暖かい目で見るな～～！\nぶっ殺すわよ～～～！", 0, 106001, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("シェフィ", 80005, 0, 433, "", 9, 22, "3.5周年まであと６日\n３人も言ってたけど\nあっという間だったわね", 0, 106401, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("シェフィ", 80005, 0, 434, "", 9, 23, "…えっ？\n生放送の意気込み！？\n突然なに！？", 0, 106401, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("シェフィ", 80005, 0, 435, "", 9, 24, "ええと…\nとにかく失敗しないように\n台本たくさん読んで…\nそれから…", 0, 106401, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("シェフィ", 80005, 0, 436, "", 9, 25, "えっ！？\nトラブルがあったら\nどうするのかって！？\nそ、それは…！", 0, 106401, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("シェフィ", 80005, 0, 437, "", 9, 26, "とにかく落ち着いて\nスタッフさんの指示を聞いて…", 0, 106401, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("シェフィ", 80005, 0, 438, "", 9, 27, "それから、それから…！\nぅ、ぅぅっ…！", 0, 106401, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("シェフィ", 80005, 0, 439, "", 9, 28, "―びえぇぇ～んっ！！！", 0, 106401, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("コッコロ", 80005, 0, 440, "", 9, 20, "どうされましたか、シェフィさま", 0, 105901, 1, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("シェフィ", 80005, 0, 441, "", 9, 29, "ううっ…ひっく…\nママッ…！", 0, 106401, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("コッコロ", 80005, 0, 442, "", 9, 21, "よしよし、緊張されてるのですね\nママが傍におりますよ\n主さま、少々お待ちくださいませ", 0, 105901, 1, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("コッコロ", 80005, 0, 443, "", 9, 22, "…ふぅ\nシェフィさま\n生放送への意気込み\nお話しできそうですか？", 0, 105901, 1, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("シェフィ", 80005, 0, 444, "", 9, 30, "…ううっ\n恥ずかしすぎて\nもう生放送出られない…", 0, 106401, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("コッコロ", 80005, 0, 445, "", 10, 23, "主さま\n3.5周年まであと５日\nでございます", 0, 105901, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("コッコロ", 80005, 0, 446, "", 10, 24, "ですがその前に\n生放送がありますね", 0, 105901, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("コッコロ", 80005, 0, 447, "", 10, 25, "…生放送に向けた\n意気込みでございますか？", 0, 105901, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("コッコロ", 80005, 0, 448, "", 10, 26, "もちろんわたくしは\n主さまのために\n全身全霊を込めて\n楽しい時間をご提供いたします", 0, 105901, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("コッコロ", 80005, 0, 449, "", 10, 27, "…と、意気込んでみましたが\nうまくできるか少し不安です\n声が小さくなったり\n間違えてしまったらどうしようと…", 0, 105901, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("コッコロ", 80005, 0, 450, "", 10, 28, "ですがわたくしは\n一人ではありません\n【美食殿】のみなさまが\nおります", 0, 105901, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("コッコロ", 80005, 0, 451, "", 10, 29, "どんな困難も\nみなさまと一緒なら\n乗り越えられると\nわたくし、確信しております", 0, 105901, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("コッコロ", 80005, 0, 452, "", 10, 30, "それに今回はシェフィさまも\nご参加されるわけですし\nママとしてお手本に\nならねばなりませんね", 0, 105901, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("コッコロ", 80005, 0, 453, "", 10, 31, "主さま。3.5周年も\n食卓を囲んでいる時のような\n楽しい思い出を\n一緒に作りましょうね", 0, 105901, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("ペコリーヌ", 80005, 0, 454, "", 11, 26, "3.5周年まであと４日！\nやばいですね☆", 0, 105801, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("キャル", 80005, 0, 455, "", 11, 32, "はいはい、やばいわね\nというかもうすぐ生放送だけど\nあんた準備はできてるの？", 0, 106001, 1, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("ペコリーヌ", 80005, 0, 456, "", 11, 27, "ばっちりオイッスー☆　ですよ！\n『王家の装備』も全開です！", 0, 105801, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("ペコリーヌ", 80005, 0, 457, "", 11, 28, "あとは前回を超える\n最高のオイッスー☆　を\nお届けできるように\n練習あるのみですかね！", 0, 105801, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("ペコリーヌ", 80005, 0, 458, "", 11, 29, "キャルちゃんは\nどうなんですか？\nオイッスー☆　を\n毎日ちゃんと練習してますか？", 0, 105801, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("キャル", 80005, 0, 459, "", 11, 33, "はぁ！？　なんであたしまで\nその変なあいさつを練習しないと\nいけないのよ！", 0, 106001, 1, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("ペコリーヌ", 80005, 0, 460, "", 11, 30, "もしかしてやってないんですか？\nダメですよキャルちゃん！\nシェフィちゃんだってちゃんと\n毎日練習してるのに！", 0, 105801, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("ペコリーヌ", 80005, 0, 461, "", 11, 31, "こうなったらわたしがキャルちゃん\nにオイッスー☆　の『オ』から\nしっかり教えてあげないと！", 0, 105801, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("ペコリーヌ", 80005, 0, 462, "", 11, 32, "オイッスー☆　の『オ』は\n口を大きく開いて〜\nドラゴンの骨つき肉に\nかぶりつく直前をイメージして〜", 0, 105801, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("キャル", 80005, 0, 463, "", 11, 34, "だからあたしはやるなんて一言も…", 0, 106001, 1, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("ペコリーヌ", 80005, 0, 464, "", 11, 33, "オイッスー☆", 0, 105801, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("キャル", 80005, 0, 465, "", 11, 35, "聞きなさいっての！！", 0, 106001, 1, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("シェフィ", 80005, 0, 466, "", 12, 31, "3.5周年まであと３日…\nそして生放送は、明日…", 0, 106401, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("シェフィ", 80005, 0, 467, "", 12, 32, "もっと台本を\n読み込んでおかないと…\nあと発声練習も…", 0, 106401, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("シェフィ", 80005, 0, 468, "", 12, 33, "おいっす！\n…こうじゃなかったわね\nええと確か…", 0, 106401, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("シェフィ", 80005, 0, 469, "", 12, 34, "うう…あんなに練習したのに\nこれじゃみんなに迷惑かけちゃう…", 0, 106401, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("シェフィ", 80005, 0, 470, "", 12, 35, "【美食殿】の一員として\n認められるように\n頑張らないといけないのに\nよし、もう一度…", 0, 106401, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("ペコリーヌ", 80005, 0, 471, "", 12, 34, "やってますねシェフィちゃん！", 0, 105801, 3, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("キャル", 80005, 0, 472, "", 12, 36, "なによ、考えることは\nみんな同じってこと？", 0, 106001, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("コッコロ", 80005, 0, 473, "", 12, 32, "ふふ。シェフィさま\nよろしければ\n仲間に入れてください\n一緒にやればきっとうまくできます", 0, 105901, 1, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("シェフィ", 80005, 0, 474, "", 12, 36, "みんな…もちろんよ！", 0, 106401, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("ペコリーヌ", 80005, 0, 475, "", 12, 35, "それじゃ明日の生放送に向けて\n一緒に練習がんばりましょう☆", 0, 105801, 3, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("カリン", 80005, 0, 476, "", 13, 7, "3.5周年まであと２日と\n迫った本日は…\nいよいよ生放送です！", 0, 190801, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("カリン", 80005, 0, 477, "", 13, 8, "【美食殿】のみなさんが\n準備に入られていますので\nそれぞれ繋いでみましょう！\nまずはペコリーヌさんです！", 0, 190801, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("ペコリーヌ", 80005, 0, 478, "", 13, 36, "今日もオイッスー☆\nみんなで一緒に\nオイッスー☆", 0, 105801, 3, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("カリン", 80005, 0, 479, "", 13, 9, "お手本になるような\n発声練習ですね！\nそれでは続いてコッコロさん", 0, 190801, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("コッコロ", 80005, 0, 480, "", 13, 33, "はじめちょろちょろ\nなかぱっぱ～♪\n生放送前にみなさまで\n食卓を囲んで英気を養いましょう", 0, 105901, 4, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("カリン", 80005, 0, 481, "", 13, 10, "さすが【美食殿】のママですね！\nそしてキャルさんは…", 0, 190801, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("キャル", 80005, 0, 482, "", 13, 37, "大丈夫…大丈夫…\nあたしは大丈夫…", 0, 106001, 1, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("カリン", 80005, 0, 483, "", 13, 11, "す、すごく震えてますけど\n大丈夫でしょうか…？\nそして今回初参加となる\n期待の新人シェフィさん！", 0, 190801, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("シェフィ", 80005, 0, 484, "", 13, 37, "あうー？", 0, 106401, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("みんな", 80005, 0, 485, "105901,34,106001,38,190801,12", 13, 37, "あ…", 0, 105801, 4, 1);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("キャル", 80005, 0, 486, "", 13, 39, "ちょ、ちょっとシェフィ！？\nなに赤ちゃん返りしてん―\nあ痛ぁっ！\n噛んだわこいつーっ！", 0, 106001, 1, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("コッコロ", 80005, 0, 487, "", 13, 35, "シェフィさま\n緊張しすぎて\nしまったのでしょうね…\nおーよしよし…", 0, 105901, 4, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("ペコリーヌ", 80005, 0, 488, "", 13, 38, "シェフィちゃん！\nそういう時は手のひらに\n好きな文字を書いて\n食べちゃうといいですよ！", 0, 105801, 3, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("カリン", 80005, 0, 489, "", 13, 13, "…ええと、現場からは以上です！\n生放送はもうすぐ！\n特別なお知らせもありますので\nどうぞお楽しみに！", 0, 190801, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("シェフィ", 80005, 0, 490, "", 14, 38, "3.5周年の記念日も\nいよいよ明日ね\n…って、あれ？", 0, 106401, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("ペコリーヌ", 80005, 0, 491, "", 14, 39, "記念日はこの森へ行きませんか！？\nすっごく美味しいお肉が\nとれるんです☆", 0, 105801, 3, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("キャル", 80005, 0, 492, "", 14, 40, "うげ、またあの魔物？\nさすがに今度は\n丸焼きとかはやめてよね…\n見た目がヤバすぎるし", 0, 106001, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("コッコロ", 80005, 0, 493, "", 14, 36, "ふふ、キャルさまもだいぶ\n魔物肉に慣れてきたようですね\n調理ならばわたくしに\nお任せください♪", 0, 105901, 1, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("シェフィ", 80005, 0, 494, "", 14, 39, "みんな、何をしているの？\nなんだか、\n不穏な単語が聞こえたんだけど…", 0, 106401, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("ペコリーヌ", 80005, 0, 495, "", 14, 40, "シェフィちゃん！\n明日の食べ尽くしツアーの\n相談をしてたんですよ！", 0, 105801, 3, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("コッコロ", 80005, 0, 496, "", 14, 37, "はい。明日は【美食殿】の\n一員となったシェフィさまと一緒に\n初めて記念日を過ごすのですから\n特別なコース料理を用意しませんと", 0, 105901, 1, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("キャル", 80005, 0, 497, "", 14, 41, "ふふん♪\n猫の毛も逆立つような\nあれこれを食べさせられるから\n覚悟しておいたほうがいいわよ？", 0, 106001, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("シェフィ", 80005, 0, 498, "", 14, 40, "へ、へぇ…", 0, 106401, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("ペコリーヌ", 80005, 0, 499, "", 14, 41, "期待しててくださいね！\nシェフィちゃんとあなたに\n『おいちー！』って喜んでもらえる\n最高のごちそうを準備しますから☆", 0, 105801, 3, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("シェフィ", 80005, 0, 500, "", 14, 41, "ええと…その…うーんと…\nわ、分かったわ\n私も【美食殿】の一員だもの", 0, 106401, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("シェフィ", 80005, 0, 501, "", 14, 42, "それにあなたも一緒みたいだし\n最後までがんばってみるわ…！", 0, 106401, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("ペコリーヌ", 80005, 0, 502, "", 15, 42, "それではいつものあれを\nやりましょう！\n準備はいいですかっ？\n…いっきますよー！", 0, 105801, 3, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("キャル", 80005, 0, 503, "", 15, 42, "祝！", 0, 106001, 1, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("コッコロ", 80005, 0, 504, "", 15, 38, "3.5周年！", 0, 105901, 4, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("シェフィ", 80005, 0, 505, "", 15, 43, "おめでとうございます！", 0, 106401, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("カリン", 80005, 0, 506, "", 15, 14, "この日を迎えられたのも\nあなたが『プリンセスコネクト！\nRe:Dive』を遊んでくれている\nおかげです！", 0, 190801, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("カリン", 80005, 0, 507, "", 15, 15, "ということで\nみなさんからあなたへ一言ずつ\n感謝の言葉をどうぞ！", 0, 190801, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("ペコリーヌ", 80005, 0, 508, "", 15, 43, "感謝の言葉ですか？　わたしは\n毎日あなたに感謝してますよ\n【美食殿】の仲間になってくれて…\n一緒にごはんを食べてくれて…", 0, 105801, 3, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("ペコリーヌ", 80005, 0, 509, "", 15, 44, "なにより今でもわたしをぺコリーヌ\nって呼んでくれてありがとうござい\nます。これからもわたしはあなたの\nぺコリーヌです！　やばいですね☆", 0, 105801, 3, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("キャル", 80005, 0, 510, "", 15, 43, "えっ！？　今度はあたし！？\n感謝って…まぁ、そうね…\nあんたにはあたしが\n辛くて苦しい時に助けてもらって…", 0, 106001, 1, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("キャル", 80005, 0, 511, "", 15, 44, "でもおかげで色々吹っ切れたわ！\nもしあんたが困ってたら今度は\nあたしが助けてあげるから\nその時はちゃんと言いなさいよ！", 0, 106001, 1, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("シェフィ", 80005, 0, 512, "", 15, 44, "ええと…次は私ね\n実はよく覚えてないのだけど\n空から落ちてきた私を受け止めて\nくれたのはあなたなのでしょう？", 0, 106401, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("シェフィ", 80005, 0, 513, "", 15, 45, "最初に私を見つけてくれたのが\nあなたで良かったって思ってるの\nおかげで私は居場所を見つけたから\nあなたがくれたもの、大切にするわ", 0, 106401, 0, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("コッコロ", 80005, 0, 514, "", 15, 39, "では最後にわたくしから…\nわたくしは主さまの元を離れた日…\nこのように記念日をお祝いする\nことは二度とないと思っていました", 0, 105901, 4, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("コッコロ", 80005, 0, 515, "", 15, 40, "ですが、主さまはわたくしを\n迎えにきてくださり、今もこうして\nおそばにおいてくださっています\nわたくしにとって毎日が記念日です", 0, 105901, 4, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("コッコロ", 80005, 0, 516, "", 15, 41, "わたくしたちの物語は\nまだ続いていきます\nこれからも一緒にページを\n作っていきましょうね", 0, 105901, 4, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("ペコリーヌ", 80005, 0, 517, "", 15, 45, "それじゃ\nいつものあれでしめますよ！\nみんなで一緒に、さんはい☆", 0, 105801, 3, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("みんな", 80005, 0, 518, "105901,42,106001,45,106401,46,190801,16", 15, 46, "これからも\nよろしくおねがいします！", 0, 105801, 4, 1);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("カリン", 60003, 0, 519, "", 0, 1, "新年を迎え、新たに始まる一日を\nおみくじで占ってみませんか？\n良い運勢であるほど、\n沢山のプレゼントが貰えますよ♪", 1, 190804, 1, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", 60003, 1, 520, "", 0, 0, "今日の運勢は姫吉！　どんな不可能も可能にできちゃう！　　　　仲間と食卓を囲めばさらに運気上昇よ！　ただし魔物料理以外！", 1, 0, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", 60003, 1, 521, "", 0, 0, "今日の運勢は姫吉！　やること全てが最高潮！　やばいですね☆　ラッキーアイテムはおにぎり！　お腹一杯で幸せが続くでしょう！", 1, 0, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", 60003, 1, 522, "", 0, 0, "今日の運勢は姫吉！　羽が生えたように飛躍できる絶好調の運気！新しいことに挑戦するなら今しかないわ！　やってみましょう！", 1, 0, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", 60003, 1, 523, "", 0, 0, "今日の運勢は姫吉！　精霊も新年の幸福をお祝いしています！　　主さまの笑顔が見られて、わたくしも幸せいっぱいでございます♪", 1, 0, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", 60003, 1, 524, "", 0, 0, "今日の運勢は姫吉！　やること全てが上手くいく最高の一日！　　あなたの活躍に先生もにっこりです。いい子いい子♪", 2, 0, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", 60003, 2, 525, "", 0, 0, "今日の運勢は大吉！　お裁縫好きな女の子のお墨付きです！　　　お世話したがりの妹にめいっぱい愛されちゃうかも！？", 1, 0, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", 60003, 2, 526, "", 0, 0, "今日の運勢は大吉！　ラッキーカラーは赤！　　　　　　　　　　湧き上がるアイデアの数々に、高笑いが止まりませんわ！", 1, 0, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", 60003, 2, 527, "", 0, 0, "今日の運勢は大吉！　ラッキーカラーは鮮やかな黄色！　　　　　さらにシマシマのアイテムを持つと、運気倍増らしいッスよ♪", 1, 0, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", 60003, 2, 528, "", 0, 0, "今日の運勢は大吉！　秘められた力が開花するでしょう！　　　　じゃあ、私の龍眼と力比べしてみようか？　ふふ、冗談だよ～", 1, 0, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", 60003, 2, 529, "", 0, 0, "今日の運勢は大吉！　悩んでいたことが解決しそうです！　　　　たまには勉強も仕事も忘れて、心あたたまる家族と過ごしましょ！", 2, 0, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", 60003, 3, 530, "", 0, 0, "今日の運勢は中吉！　家の中でゆっくり過ごすのがよさそう！　　ラッキーアイテムはゲームよ！　ベストスコアを更新できるかも？", 1, 0, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", 60003, 3, 531, "", 0, 0, "今日の運勢は中吉！　上手くなりたいことに積極的に挑戦してみてサックス好きの妹が、手取り足取りサポートしてくれるかも～？", 1, 0, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", 60003, 3, 532, "", 0, 0, "今日の運勢は中吉！　仕事運が急上昇中です！　　　　　　　　　きっと「煉獄に舞う一陣の疾風」の如き活躍ができますよ！", 1, 0, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", 60003, 3, 533, "", 0, 0, "今日の運勢は中吉！　日頃の努力が報われます！　　　　　　　　親しい友人がいるなら報告してみて。一緒に喜んでくれるはずだ。", 1, 0, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", 60003, 3, 534, "", 0, 0, "今日の運勢は中吉！　ラッキーアイテムはかわいいもの！　　　　ア、アタシはかわいいじゃなくてキレイだってば！　も～っ！", 1, 0, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", 60003, 3, 535, "", 0, 0, "今日の運勢は中吉！　チャンスを待つと良い結果に！　　　　　　ラッキーアイテムのプリンを食べて、運を呼び込みましょう！", 1, 0, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", 60003, 3, 536, "", 0, 0, "今日の運勢は中吉！　悩まず行動すれば強運に恵まれる！　　　　守るよりも攻めるが吉だ！　勝負事をするにはいい運勢だな！", 2, 0, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", 60003, 4, 537, "", 0, 0, "今日の運勢は小吉！　まだ見ぬ危険が隠れているかも！　　　　　でも安心しろ！　提督が貴公を守ってくれるはずだぞ！", 1, 0, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", 60003, 4, 538, "", 0, 0, "今日の運勢は小吉！　いろんな事件が起こる一日になりそう！　　状況をよく観察してみようじゃないか！　まさに探偵のごとくね！", 1, 0, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", 60003, 4, 539, "", 0, 0, "今日の運勢は小吉！　気の向くままに過ごすのが幸せへの近道！　美味い魚を食べて猫を眺めて、自然体で過ごすのはどうだい？", 1, 0, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", 60003, 4, 540, "", 0, 0, "今日の運勢は小吉！　難しい頼み事をされるかもしれません！　　うまくやれればきっと成長につながります、挑戦してみましょう！", 1, 0, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", 60003, 4, 541, "", 0, 0, "今日の運勢は小吉！　同腹一心の友だちが見つかるかも！　　　　キーワードは「ユニちゃんズ」、記憶に刻みましょう！", 1, 0, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", 60003, 4, 542, "", 0, 0, "今日の運勢は小吉！　周りに流されず、マイペースな一日を！　　ただし寝すぎには注意ぐうたらし過ぎず適度な運動を、だってさ～", 1, 0, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("", 60003, 4, 543, "", 0, 0, "今日の運勢は小吉！　無理は禁物、お家で静かにしていましょう　プロぼっちの私が、お家での過ごし方……教えてあげましょうか？", 2, 0, 2, 0);
INSERT INTO `v1_e4490a29ec461af957058cb1092059d9ac57d6c99ed2c998a4a304cd796c6988` VALUES ("カリン", 60003, 0, 544, "", 0, 2, "おみくじの結果はどうでしたか？\n新たに始まる一日が、あなたに\nとって良い日になりますように\nまた明日も遊びに来て下さいね♪", 0, 190804, 3, 0);
