INSERT INTO `v1_e818ad3bb05a23da13b9fc8e810d616baef67f9c624489509d0f1f2420832269` VALUES (100, 100, "エリコ", "2024/03/31 12:00:00", 1, "2030/12/17 14:59:59", "訓練成功、おめでとうございます\n褒めるべき時にはしっかり褒めるのも\n教官の務めですものね。それとも、\n罵られる方が好みですか？　クスクス", 10142, 0.0, 0.0, 129901, 10.85, 24, 7);
INSERT INTO `v1_e818ad3bb05a23da13b9fc8e810d616baef67f9c624489509d0f1f2420832269` VALUES (101, 101, "エリコ", "2024/03/31 12:00:00", 1, "2030/12/17 14:59:59", "厳しい訓練も、終わってみれば\n短く感じるものですわね。\n正直、私は暴れ足りないくらい…\nいえ、何でもありませんわ。クスクス", 10142, 0.0, 0.0, 129901, 11.15, 24, 2);
INSERT INTO `v1_e818ad3bb05a23da13b9fc8e810d616baef67f9c624489509d0f1f2420832269` VALUES (102, 102, "エリコ", "2024/03/31 12:00:00", 7, "2030/12/17 14:59:59", "行く手を阻む忌々しい邪魔者を\n蹴散らして、見事ゴールイン…！\nまるで私とあのおかたの人生を\n象徴するような道のりでした…", 10142, 0.0, 0.0, 129901, 6.4, 24, 5);
INSERT INTO `v1_e818ad3bb05a23da13b9fc8e810d616baef67f9c624489509d0f1f2420832269` VALUES (103, 103, "エリコ", "2024/03/31 12:00:00", 3, "2030/12/17 14:59:59", "まったくもって不甲斐ない結果です\nしかし、挽回の機会はまだありますわ\n次はゴミ虫の地位から脱却できるよう\n死力を尽くしなさい。いいですね？", 10142, 0.0, 0.0, 129901, 0.0, 24, 3);
INSERT INTO `v1_e818ad3bb05a23da13b9fc8e810d616baef67f9c624489509d0f1f2420832269` VALUES (104, 104, "エリコ", "2024/03/31 12:00:00", 2, "2030/12/17 14:59:59", "クスクス…ああご心配なさらず\nなんとも情けない成績を残す輩には\nどんな罰が相応しいか、考えている\nだけですので…クスクスクス", 10142, 0.0, 0.0, 129901, 2.43, 24, 8);
INSERT INTO `v1_e818ad3bb05a23da13b9fc8e810d616baef67f9c624489509d0f1f2420832269` VALUES (105, 105, "エリコ", "2024/03/31 12:00:00", 4, "2030/12/17 14:59:59", "私が教官として認められなければ、\nあのおかたを失望させてしまう…\nそれだけはあってはなりません\nあなた方も、わかっていますわね？", 10142, 0.0, 0.0, 129901, 9.08, 24, 3);
INSERT INTO `v1_e818ad3bb05a23da13b9fc8e810d616baef67f9c624489509d0f1f2420832269` VALUES (106, 106, "エリコ", "2024/03/31 12:00:00", 6, "2030/12/17 14:59:59", "まずまずの結果ではありますが、\nこの程度で満足してもらっては\n困りますわ。エリコ班の一員として、\nより高みを目指していただきますので", 10142, 0.0, 0.0, 129901, 0.0, 24, 6);
INSERT INTO `v1_e818ad3bb05a23da13b9fc8e810d616baef67f9c624489509d0f1f2420832269` VALUES (107, 107, "エリコ", "2024/03/31 12:00:00", 3, "2030/12/17 14:59:59", "私の指導を受けながら、この結果は…\nもっと真剣に考えねばなりませんね\nあなた方が怠けずにいるには、\nどんなしつけが必要かを…クスクス", 10142, 0.0, 0.0, 129901, 10.6, 24, 8);
INSERT INTO `v1_e818ad3bb05a23da13b9fc8e810d616baef67f9c624489509d0f1f2420832269` VALUES (108, 108, "エリコ", "2024/03/31 12:00:00", 6, "2030/12/17 14:59:59", "ひとまず訓練をこなしたご褒美か、\n実力を出しきれたとは言えない罰か…\nみなさんにどちらを与えるべきか\n少し悩む結果ですわね", 10142, 0.0, 0.0, 129901, 7.54, 24, 4);
INSERT INTO `v1_e818ad3bb05a23da13b9fc8e810d616baef67f9c624489509d0f1f2420832269` VALUES (109, 109, "エリコ", "2024/03/31 12:00:00", 2, "2030/12/17 14:59:59", "なかなかいい戦いぶりでしたわ\nこれもあなた方の頑張りと、何より\n優秀な教官である私の教育の賜物\nそうですわね？　クスクスクス…", 10142, 0.0, 0.0, 129901, 11.5, 24, 7);
INSERT INTO `v1_e818ad3bb05a23da13b9fc8e810d616baef67f9c624489509d0f1f2420832269` VALUES (110, 110, "エリコ", "2024/03/31 12:00:00", 2, "2030/12/17 14:59:59", "これだけ進めたのならば合格点ですわ\nでも、もっと高みを目指したいと\n思っているのなら…さらなる訓練にも\n付き合って差し上げましょう", 10142, 0.0, 0.0, 129901, 8.83, 24, 1);
INSERT INTO `v1_e818ad3bb05a23da13b9fc8e810d616baef67f9c624489509d0f1f2420832269` VALUES (111, 111, "エリコ", "2024/03/31 12:00:00", 1, "2030/12/17 14:59:59", "未熟で頼りなかったあなた方が、\n見違えるように成長しました\nなるほど…これこそが、教官冥利に\n尽きるという気持ちなのですね", 10142, 0.0, 0.0, 129901, 6.68, 24, 2);
INSERT INTO `v1_e818ad3bb05a23da13b9fc8e810d616baef67f9c624489509d0f1f2420832269` VALUES (112, 112, "エリコ", "2024/03/31 12:00:00", 6, "2030/12/17 14:59:59", "まさかあなた方が、私の予想すら\n超える力を発揮するとは…驚きました\nもはやゴミ虫などではありません\n一人前の騎士として胸を張りなさい", 10142, 0.0, 0.0, 129901, 8.8, 24, 2);
INSERT INTO `v1_e818ad3bb05a23da13b9fc8e810d616baef67f9c624489509d0f1f2420832269` VALUES (113, 113, "エリコ", "2024/03/31 12:00:00", 2, "2030/12/17 14:59:59", "クスクス、見事な働きです\nみなさんならできると信じていました\n他でもない私の指導を受けた、\nエリコ班の優秀な新兵なのですから", 10142, 0.0, 0.0, 129901, 8.9, 24, 1);
INSERT INTO `v1_e818ad3bb05a23da13b9fc8e810d616baef67f9c624489509d0f1f2420832269` VALUES (114, 114, "エリコ", "2024/03/31 12:00:00", 2, "2030/12/17 14:59:59", "なんてすばらしい成績…！\n今日という日は『あのおかたを想い\nエリコが限界を超えた記念日』として\n歴史に刻まれることでしょう…！", 10142, 0.0, 0.0, 129901, 3.7, 24, 5);
INSERT INTO `v1_e818ad3bb05a23da13b9fc8e810d616baef67f9c624489509d0f1f2420832269` VALUES (200, 200, "マコト", "2024/03/31 12:00:00", 1, "2030/12/17 14:59:59", "よし、いい訓練になったな\nおまえたちもよく動けてたし、\nちゃんと成長できてると思うぜ\nその調子で頑張れよっ", 10142, 0.0, 0.0, 129801, 7.6, 24, 2);
INSERT INTO `v1_e818ad3bb05a23da13b9fc8e810d616baef67f9c624489509d0f1f2420832269` VALUES (201, 201, "マコト", "2024/03/31 12:00:00", 2, "2030/12/17 14:59:59", "ひとまずゴールおめでとう！\nでも、努力は継続してこそだからな\nおまえたちにやる気があるなら、\n何度だって付き合ってやるよ", 10142, 0.0, 0.0, 129801, 2.25, 24, 1);
INSERT INTO `v1_e818ad3bb05a23da13b9fc8e810d616baef67f9c624489509d0f1f2420832269` VALUES (202, 202, "マコト", "2024/03/31 12:00:00", 2, "2030/12/17 14:59:59", "行軍訓練はこれにて完了！\n…なーんて思ってないよな？\n学んだことが体に染みつくまで\n地道に繰り返すのが練習ってもんさ", 10142, 0.0, 0.0, 129801, 3.28, 24, 1);
INSERT INTO `v1_e818ad3bb05a23da13b9fc8e810d616baef67f9c624489509d0f1f2420832269` VALUES (203, 203, "マコト", "2024/03/31 12:00:00", 4, "2030/12/17 14:59:59", "みんなに無理をさせちまったか…\nでもこれで諦めることはないぜ\n次はきっとうまくいく。そう信じて\n鍛え続ければ、道は開けるはずだ！", 10142, 0.0, 0.0, 129801, 2.98, 24, 1);
INSERT INTO `v1_e818ad3bb05a23da13b9fc8e810d616baef67f9c624489509d0f1f2420832269` VALUES (204, 204, "マコト", "2024/03/31 12:00:00", 4, "2030/12/17 14:59:59", "こいつは悔しい結果だな…\nま、反省点はいろいろあったし\nひとつずつ乗り越えていこう\nあたしたちで力を合わせて、なっ！", 10142, 0.0, 0.0, 129801, 3.0, 24, 1);
INSERT INTO `v1_e818ad3bb05a23da13b9fc8e810d616baef67f9c624489509d0f1f2420832269` VALUES (205, 205, "マコト", "2024/03/31 12:00:00", 1, "2030/12/17 14:59:59", "おまえたちはまだ未熟な訓練兵、\n失敗を必要以上に悔やむことはねえよ\nでも実戦じゃこうはいかないからな\n次は気合を入れ直していけよっ！", 10142, 0.0, 0.0, 129801, 10.3, 24, 2);
INSERT INTO `v1_e818ad3bb05a23da13b9fc8e810d616baef67f9c624489509d0f1f2420832269` VALUES (206, 206, "マコト", "2024/03/31 12:00:00", 6, "2030/12/17 14:59:59", "これがおまえたちの全力？\nいやいや、そんなはずはないよなっ\nおまえらならもっと強くなれる！\nあたしが言うんだ、間違いないぜ！", 10142, 0.0, 0.0, 129801, 2.93, 24, 1);
INSERT INTO `v1_e818ad3bb05a23da13b9fc8e810d616baef67f9c624489509d0f1f2420832269` VALUES (207, 207, "マコト", "2024/03/31 12:00:00", 3, "2030/12/17 14:59:59", "おまえたちの実力を考えりゃ、\nベストを尽くせたとは言えない成績だ\nなら、次やるべきことも決まってる\n気持ちを切り替えて、鍛え直すんだ！", 10142, 0.0, 0.0, 129801, 9.3, 24, 1);
INSERT INTO `v1_e818ad3bb05a23da13b9fc8e810d616baef67f9c624489509d0f1f2420832269` VALUES (208, 208, "マコト", "2024/03/31 12:00:00", 4, "2030/12/17 14:59:59", "まだまだ課題がある内容だったな～\n無駄な動き、判断の遅れ…\nそういうのをしっかり直していけば、\n記録ももっと伸びるはずだっ", 10142, 0.0, 0.0, 129801, 6.9, 24, 1);
INSERT INTO `v1_e818ad3bb05a23da13b9fc8e810d616baef67f9c624489509d0f1f2420832269` VALUES (209, 209, "マコト", "2024/03/31 12:00:00", 2, "2030/12/17 14:59:59", "ここまでいけりゃ、合格点！\nむしろ、最初の頼りなさを思えば\nすげえ成長してるんじゃねえか？\n胸を張れる結果だと思うぜっ！", 10142, 0.0, 0.0, 129801, 0.0, 24, 2);
INSERT INTO `v1_e818ad3bb05a23da13b9fc8e810d616baef67f9c624489509d0f1f2420832269` VALUES (210, 210, "マコト", "2024/03/31 12:00:00", 1, "2030/12/17 14:59:59", "なかなかハードな訓練だったけど、\nみんなしっかりついてこられたな\n地道に続けた基礎トレの成果が\n形になってる、その証だぜっ", 10142, 0.0, 0.0, 129801, 5.95, 24, 2);
INSERT INTO `v1_e818ad3bb05a23da13b9fc8e810d616baef67f9c624489509d0f1f2420832269` VALUES (211, 211, "マコト", "2024/03/31 12:00:00", 2, "2030/12/17 14:59:59", "今回の訓練もよく頑張ったな！\nしっかり休んで次に備えろよ！\nなんつーか、教え子が成長してくのを\n見るのは楽しいもんだな…", 10142, 0.0, 0.0, 129801, 6.73, 24, 1);
INSERT INTO `v1_e818ad3bb05a23da13b9fc8e810d616baef67f9c624489509d0f1f2420832269` VALUES (212, 212, "マコト", "2024/03/31 12:00:00", 2, "2030/12/17 14:59:59", "おまえら、すげえじゃねえか！\nこれならもう、ひよっこ新兵なんて\n言えねえな。きっと騎士団でも\n大活躍間違いなしだぜ！", 10142, 0.0, 0.0, 129801, 0.0, 24, 2);
INSERT INTO `v1_e818ad3bb05a23da13b9fc8e810d616baef67f9c624489509d0f1f2420832269` VALUES (213, 213, "マコト", "2024/03/31 12:00:00", 1, "2030/12/17 14:59:59", "敵との戦い方、状況判断の正確さ\n何より根性…どれも最高だったぞ！\nうかうかしてたら、教官のあたしまで\n追い抜かれちまうかもな…へへっ", 10142, 0.0, 0.0, 129801, 6.65, 24, 2);
INSERT INTO `v1_e818ad3bb05a23da13b9fc8e810d616baef67f9c624489509d0f1f2420832269` VALUES (214, 214, "マコト", "2024/03/31 12:00:00", 6, "2030/12/17 14:59:59", "あたしの指導が良かった？\nいやいや、ここまで強くなれたのは、\n他でもないおまえたち自身が努力した\nおかげだよ。改めて…おめでとうっ！", 10142, 0.0, 0.0, 129801, 3.05, 24, 1);
