INSERT INTO `v1_ea086c4647ca338e98020f5882a82d6275ec9c93c6b8f8456f635c5a2ac0f9a6` VALUES (0, 0, 335, 0, 3.0, 1, 270, 250200, 250200, "【物理】\n堅牢な鉱石の表皮を持つ岩トカゲ。\n\n・近接攻撃\n・与えるダメージによるＴＰ回復量80％ダウン", 14, "マインリザード", 0);
INSERT INTO `v1_ea086c4647ca338e98020f5882a82d6275ec9c93c6b8f8456f635c5a2ac0f9a6` VALUES (0, 0, 95, 0, 0.5, 1, 270, 250300, 250300, "【物理】【物理防御】\n幼くも、勇猛な見習いゴブリン。\n非力を補う動きで、前線を駆ける。\n\n・近接攻撃\n・与えるダメージによるＴＰ回復量80％ダウン", 0, "ゴブリンリトル", 0);
INSERT INTO `v1_ea086c4647ca338e98020f5882a82d6275ec9c93c6b8f8456f635c5a2ac0f9a6` VALUES (0, 0, 55, 0, 4.0, 1, 270, 250400, 250400, "【物理】【防御】\n身体も態度も巨漢な北海のドン。\n纏う厚い脂肪が敵からの攻撃を押止める。\n\n・近接攻撃\n・与えるダメージによるＴＰ回復量80％ダウン", 1, "トドドン", 0);
INSERT INTO `v1_ea086c4647ca338e98020f5882a82d6275ec9c93c6b8f8456f635c5a2ac0f9a6` VALUES (0, 0, 410, 0, 1.3, 1, 270, 250500, 250500, "【物理】【遠距離】\n砂地の地中で獲物を待ち、\n一気に大顎で仕留めるムカデの魔物\n\n・後ろから１番目に移動して近接攻撃\n・与えるダメージによるＴＰ回復量80％ダウン", 11, "サンドワーム", 0);
INSERT INTO `v1_ea086c4647ca338e98020f5882a82d6275ec9c93c6b8f8456f635c5a2ac0f9a6` VALUES (0, 0, 120, 0, 0.5, 2, 100, 250600, 250600, "【魔法】【中距離】【防御】\n幼いながらも巧みな変化で\n攻守を必死にこなす豆狸の魔物。\n\n・後ろから３番目攻撃\n・一定時間無敵\n・与えるダメージによるＴＰ回復量80％ダウン", 0, "ワッパダヌキ", 0);
INSERT INTO `v1_ea086c4647ca338e98020f5882a82d6275ec9c93c6b8f8456f635c5a2ac0f9a6` VALUES (0, 0, 20, 0, 5.0, 1, 270, 250700, 250700, "【物理】【物理防御】\n頑丈な身体で、攻守を備えた岩の魔物。\n\n・近接攻撃\n・与えるダメージによるＴＰ回復量80％ダウン", 16, "ゴーレム", 0);
INSERT INTO `v1_ea086c4647ca338e98020f5882a82d6275ec9c93c6b8f8456f635c5a2ac0f9a6` VALUES (0, 0, 25, 0, 5.0, 1, 270, 250800, 250800, "【物理】【魔法防御】\n魔力を秘めた岩から生まれた、\n魔法耐性のあるゴーレム。\n\n・近接攻撃\n・与えるダメージによるＴＰ回復量80％ダウン", 16, "ブルーゴーレム", 0);
INSERT INTO `v1_ea086c4647ca338e98020f5882a82d6275ec9c93c6b8f8456f635c5a2ac0f9a6` VALUES (0, 0, 100, 0, 4.0, 1, 270, 250900, 250900, "【物理】【防御】\nあらゆる攻撃を防ぐ盾をもった、命ある石像。\n\n・近接攻撃\n・与えるダメージによるＴＰ回復量80％ダウン", 16, "動く戦士像", 0);
INSERT INTO `v1_ea086c4647ca338e98020f5882a82d6275ec9c93c6b8f8456f635c5a2ac0f9a6` VALUES (0, 0, 105, 0, 4.0, 1, 270, 251000, 251000, "【物理】【防御】\nあらゆる攻撃を剣ではじき返す、命ある石像。\n\n・近接攻撃\n・与えるダメージによるＴＰ回復量80％ダウン", 5, "動く剣士像", 0);
INSERT INTO `v1_ea086c4647ca338e98020f5882a82d6275ec9c93c6b8f8456f635c5a2ac0f9a6` VALUES (0, 0, 10, 0, 5.5, 1, 270, 251100, 251100, "【物理】【防御】\nあらゆる攻撃を甲羅ではじく、\n巨大ガメの魔物。\n\n・近接攻撃\n・与えるダメージによるＴＰ回復量80％ダウン", 16, "タートルタイラント", 0);
INSERT INTO `v1_ea086c4647ca338e98020f5882a82d6275ec9c93c6b8f8456f635c5a2ac0f9a6` VALUES (0, 0, 85, 0, 4.0, 1, 270, 251200, 251200, "【物理】【物理防御】\n身の丈ほどもある、巨大な盾で\n仲間を護る見習いゴブリン兵。\n\n・近接攻撃\n・与えるダメージによるＴＰ回復量80％ダウン", 15, "ゴブリンガード", 0);
INSERT INTO `v1_ea086c4647ca338e98020f5882a82d6275ec9c93c6b8f8456f635c5a2ac0f9a6` VALUES (0, 0, 90, 0, 4.0, 1, 270, 251300, 251300, "【物理】【魔法防御】\n顔にも見える大きな盾で敵を威嚇し\n侵入者を阻む、ジャングルの護り人。\n\n・近接攻撃\n・与えるダメージによるＴＰ回復量80％ダウン", 15, "ジャングルガード", 0);
INSERT INTO `v1_ea086c4647ca338e98020f5882a82d6275ec9c93c6b8f8456f635c5a2ac0f9a6` VALUES (0, 0, 50, 0, 4.0, 1, 270, 251400, 251400, "【物理】【防御】【妨害】\n熱地に生きる、原始の大イノシシ。\n数多のマグマ岩を突き続けた先に、\n鎧をも砕く牙の一撃を体得した。\n\n・近接攻撃、物防ダウン\n・与えるダメージによるＴＰ回復量80％ダウン", 0, "ワイルドボア", 0);
