INSERT INTO `v1_ea086c4647ca338e98020f5882a82d6275ec9c93c6b8f8456f635c5a2ac0f9a6` VALUES (0, 0, 1000, 0, 1.8, 1, 270, 301600, 301600, "【物理】猛り狂う暴風を纏いし大怪鳥。\n魔物図鑑を抜け出し、破壊の限りを尽くす。\n\n・敵全体にキャラの最大ＨＰに応じた\n　大ダメージを与える。\n・前方範囲内の敵に物理中ダメージを与え、\n　さらにスタンさせる。", 3, "ジズ", 0);
INSERT INTO `v1_ea086c4647ca338e98020f5882a82d6275ec9c93c6b8f8456f635c5a2ac0f9a6` VALUES (0, 0, 1000, 0, 1.8, 1, 270, 301601, 301600, "【物理】猛り狂う暴風を纏いし大怪鳥。\n魔物図鑑を抜け出し、破壊の限りを尽くす。\n\n・敵全体にキャラの最大ＨＰに応じた\n　大ダメージを与える。\n・前方範囲内の敵に物理中ダメージを与え、\n　さらにスタンさせる。\n・前方範囲内の敵にキャラの最大ＨＰに応じた\n　中ダメージを与え、物理防御力を下げる。\n・ジズ自身のＨＰが50％を切ると、さらに\n　攻撃が激しく凶暴になる。", 3, "ジズ", 0);
INSERT INTO `v1_ea086c4647ca338e98020f5882a82d6275ec9c93c6b8f8456f635c5a2ac0f9a6` VALUES (0, 0, 1000, 0, 1.8, 1, 270, 301602, 301602, "【物理】砂島にのさばる獰猛な大\n怪鳥。紫電を纏い嵐を巻き起こす\nさまは、天災と恐れられる。\n\n・ユニオンバーストは、敵全体に\n　最大ＨＰに応じた物理割合\n　ダメージを与える。\n\n・前から１番目の敵を中心にした\n　小範囲の最大２キャラに物理中\n　ダメージを与え、さらにスタン\n　させる。", 3, "ストーム・ジズ", 0);
INSERT INTO `v1_ea086c4647ca338e98020f5882a82d6275ec9c93c6b8f8456f635c5a2ac0f9a6` VALUES (0, 0, 1000, 0, 2.6, 1, 270, 301700, 301700, "【物理】毒瘴の闇稜の頂に棲まう、醜悪なる狂獣。\n猛毒の瘴気で生を奪い、屍を食み力の糧にする。\n\n・ユニオンバーストは敵全体に魔法大ダメージと\n　毒状態を与え、さらにノックバックさせる。\n・前方2キャラに物理ダメージを与え、さらに物\n　理攻撃力とTPをダウンさせる。\n・ジャバウォック自身の残りHPが40%を切る\n　とさらに攻撃が激しく強暴になり、さらに3体\n　のモンスターを呼び寄せる。\n・残り時間が20秒を切ると、敵全体に強力な毒\n　状態を付与し、自身のTPを特大ダウン。", 3, "ジャバウォック", 0);
INSERT INTO `v1_ea086c4647ca338e98020f5882a82d6275ec9c93c6b8f8456f635c5a2ac0f9a6` VALUES (0, 0, 1000, 0, 2.0, 1, 270, 301800, 301800, "天を駆け数多の標的を射止める射手座の魔星獣。\n雷光纏いし矢は、刹那にすべてを穿つ至高の技。\n\n・ユニオンバーストは敵全体に最大ＨＰに応じ\n　て魔法ダメージを与える。\n　暗闇状態の敵はダメージが3倍になる。\n・敵全体に魔法中ダメージを与え、さらに残り\n　ＨＰ割合が最も少ない敵1キャラに強力な暗\n　闇状態を付与。\n・前方の敵3キャラに物理ダメージを与える。", 3, "サジタリウス", 0);
INSERT INTO `v1_ea086c4647ca338e98020f5882a82d6275ec9c93c6b8f8456f635c5a2ac0f9a6` VALUES (0, 0, 1000, 0, 2.0, 1, 270, 301801, 301801, "【物理】星穹の神殿を守護する射手座の魔星獣。\n万象を射抜く一閃は、紫電煌めく神域の絶技。\n\n・バトル開始時、自身の物理防御力と魔法防御力\n　を小アップさせる。この防御力アップ効果で上\n　昇した防御力は、バトル中に受ける防御力ダウ\n　ンスキルの影響を受けない。\n・サジタリウスから受けるすべてのダメージで発\n　生する、パーティキャラのＴＰ回復量を中ダウ\n　ンさせる。\n・ユニオンバーストは、敵全体に魔法中ダメージ\n　を与え、さらに【黒星】の数に応じて、追加の\n　魔法ダメージを与える。\n・敵全体に魔法小ダメージを与え、自身の矢に\n　【黒星】を１追加する。さらに前方広範囲内の\n　敵に魔法大ダメージを与え、範囲内に敵が存在\n　する場合、自身の矢に【黒星】を１追加する。\n・最も近い敵２キャラに物理中ダメージを与え、\n　物理防御力を中ダウンさせる。このスキルの状\n　態異常はダメージを無効化しても付与される。", 3, "サジタリウス", 0);
INSERT INTO `v1_ea086c4647ca338e98020f5882a82d6275ec9c93c6b8f8456f635c5a2ac0f9a6` VALUES (0, 0, 1000, 0, 2.0, 1, 270, 301802, 301802, "【物理】星穹の神殿を守護する射手座の魔星獣。\n万象を射抜く一閃は、紫電煌めく神域の絶技。\n\n・バトル開始時、自身の物理防御力と魔法防御力\n　を小アップさせる。この防御力アップ効果で上\n　昇した防御力は、バトル中に受ける防御力ダウ\n　ンスキルの影響を受けない。\n・サジタリウスから受けるすべてのダメージで発\n　生する、パーティキャラのＴＰ回復量を中ダウ\n　ンさせる。\n・ユニオンバーストは、敵全体に魔法中ダメージ\n　を与え、さらに【黒星】の数に応じて、追加の\n　魔法ダメージを与える。このスキルによるダ\n　メージでは、ＴＰ回復量が大ダウンする。\n・敵全体に魔法小ダメージを与え、自身の矢に\n　【黒星】を１追加する。さらに前方広範囲内の\n　敵に魔法大ダメージを与え、範囲内に敵が存在\n　する場合、自身の矢に【黒星】を２追加する。\n　このスキルによるダメージでは、ＴＰ回復量が\n　大ダウンする。\n・最も近い敵２キャラに物理中ダメージを与え、\n　物理防御力を中ダウンさせる。このスキルの状\n　態異常はダメージを無効化しても付与される。\n・敵全体のバリアを解除し、魔法小ダメージを与\n　え、前方広範囲内の敵に魔法大ダメージを与え\n　る。このスキルによるダメージでは、ＴＰ回復\n　量が大ダウンする。", 3, "サジタリウス", 0);
INSERT INTO `v1_ea086c4647ca338e98020f5882a82d6275ec9c93c6b8f8456f635c5a2ac0f9a6` VALUES (0, 0, 1000, 0, 2.0, 1, 270, 301803, 301803, "【物理】星穹の神殿を守護する射手座の魔星獣。\n万象を射抜く一閃は、紫電煌めく神域の絶技。\n\n・バトル開始時、自身の物理防御力と魔法防御力\n　を中アップさせる。この防御力アップ効果で上\n　昇した防御力は、バトル中に受ける防御力ダウ\n　ンスキルの影響を受けない。\n・バトル開始時、敵全体のＴＰ上昇を中ダウンさ\n　せるフィールドを展開する。\n・バトル開始時、自身にＴＰダウン100％カッ\n　ト効果を付与する。\n・サジタリウスから受けるすべてのダメージで発\n　生する、パーティキャラのＴＰ回復量を中ダウ\n　ンさせる。\n・敵から受けるすべてのダメージで発生する、サ\n　ジタリウスのＴＰ回復量が大ダウンする。\n・ユニオンバーストは、敵全体に魔法中ダメージ\n　を与え、さらに【黒星】の数に応じて、追加の\n　魔法割合ダメージを与える。このスキルによる\n　ダメージでは、ＴＰが回復しない。\n・敵全体に魔法小ダメージを与え、自身の矢に\n　【黒星】を１追加する。さらに前方広範囲内の\n　敵に魔法大ダメージを与え、範囲内に敵が存在\n　する場合、自身の矢に【黒星】を２追加する。\n　このスキルによるダメージでは、ＴＰが回復し\n　ない。\n・最も近い敵２キャラに物理中ダメージを与え、\n　物理防御力を大ダウンさせる。このスキルの状\n　態異常はダメージを無効化しても付与される。\n・敵全体のバリアを解除し、魔法小ダメージを与\n　え、前方広範囲内の敵に魔法大ダメージを与え\n　る。さらに【黒星】の数に応じて、自身のＴＰ\n　を回復する。このスキルによるダメージでは、\n　ＴＰが回復しない。", 3, "サジタリウス", 0);
INSERT INTO `v1_ea086c4647ca338e98020f5882a82d6275ec9c93c6b8f8456f635c5a2ac0f9a6` VALUES (0, 0, 1000, 0, 1.7, 1, 270, 301900, 301900, "【物理】鋭い眼つきで、獲物\nと波を探し求める海のハンター。\n魔力で無限に生み出す潮水は\n敵の刃と気力を鈍らせる。\n\n・前方4キャラに物理中ダメージを\n　与え、物理攻撃力を下げる。\n\n・前方2キャラに魔法小ダメージを\n　与え、ＴＰを下げる。", 11, "ウェアオルカ", 0);
INSERT INTO `v1_ea086c4647ca338e98020f5882a82d6275ec9c93c6b8f8456f635c5a2ac0f9a6` VALUES (0, 0, 1000, 0, 1.5, 1, 270, 301901, 301901, "【物理】ルナの魔力により凶悪化した大海の狩人。\n鍛え抜いた躰で、刃と化した水流の一撃を放つ。\n\n・前方４キャラに物理大ダメージを与え、\n　物理攻撃力を中ダウンさせる。さらに、\n　味方全員のＴＰ上昇を小アップする。\n・前方小範囲に魔法中ダメージを与え、\n　ＴＰを大ダウン。\n・後ろから２番目のキャラを中心にした\n　小範囲に物理中ダメージを与え、\n　魔法攻撃力を大ダウンさせる。", 11, "キリングオルカ", 0);
INSERT INTO `v1_ea086c4647ca338e98020f5882a82d6275ec9c93c6b8f8456f635c5a2ac0f9a6` VALUES (0, 0, 1000, 0, 1.6, 1, 270, 302000, 302000, "【物理】過酷な環境で進化したプチグリフォン。\n竜巻で獲物を巻き上げ、空で狩りをする。\n\n・バトル開始時、自身の物理防御力と魔法防御力\n　を小アップさせる。この防御力アップ効果で上\n　昇した防御力は、バトル中に受ける防御力ダウ\n　ンスキルの影響を受けない。\n・ワイルドグリフォンから受けるすべてのダメー\n　ジで発生する、パーティキャラのＴＰ回復量を\n　中ダウンさせる。\n・ユニオンバーストは、召喚体、分身を除いた最\n　も近い敵３キャラに魔法大ダメージを与える。", 11, "ワイルドグリフォン", 0);
