INSERT INTO `v1_ea086c4647ca338e98020f5882a82d6275ec9c93c6b8f8456f635c5a2ac0f9a6` VALUES (0, 0, 1000, 0, 1.7, 1, 270, 303606, 303606, "【物理】強靭な脚と鋭利なツメを操る獰猛な恐竜。\n滅びた大地でマグマを喰らい生きる孤高の存在。\n\n・バトル開始時、自身の物理防御力と魔法防御力\n　を小アップさせる。この防御力アップ効果で上\n　昇した防御力は、バトル中に受ける防御力ダウ\n　ンスキルの影響を受けない。\n・バーンサウルスから受けるすべてのダメージで\n　発生する、パーティキャラのＴＰ回復量を中ダ\n　ウンさせる。\n・ユニオンバーストは、最も近い敵１キャラを中\n　心にした中範囲内の敵すべてに物理中ダメージ\n　を与え、敵全体の行動速度を中ダウンさせ、さ\n　らに自身の行動速度を大アップさせる。範囲内\n　の敵の数に応じて、このスキルのダメージがダ\n　ウンする。このスキルによるダメージではＴＰ\n　が回復しない。このスキルの効果はダメージを\n　無効化しても付与される。\n・最も近い敵１キャラを中心にした中範囲内の敵\n　すべてに物理小ダメージを与え、物理防御力を\n　特大ダウンさせる。範囲内の敵の数に応じて、\n　防御力の減少量がダウンする。このスキルの効\n　果はダメージを無効化しても付与される。", 13, "バーンサウルス", 0);
INSERT INTO `v1_ea086c4647ca338e98020f5882a82d6275ec9c93c6b8f8456f635c5a2ac0f9a6` VALUES (0, 0, 1000, 0, 1.7, 1, 270, 303607, 303607, "【物理】強靭な脚と鋭利なツメを操る獰猛な恐竜。\n滅びた大地でマグマを喰らい生きる孤高の存在。\n\n・バトル開始時、自身の物理防御力と魔法防御力\n　を中アップさせる。この防御力アップ効果で上\n　昇した防御力は、バトル中に受ける防御力ダウ\n　ンスキルの影響を受けない。\n・バトル開始時、敵全体のＨＰ吸収を特大ダウン\n　させ、ＴＰ上昇を小ダウンさせる。\n・バトル開始時、自身にＴＰダウン100％カッ\n　ト効果を付与する。\n・バーンサウルスから受けるすべてのダメージで\n　は、パーティキャラのＴＰが回復しない。\n・ユニオンバーストは、最も近い敵１キャラを中\n　心にした中範囲内の敵すべてに物理中ダメージ\n　を与え、敵全体の行動速度を大ダウンさせ、さ\n　らに自身の行動速度を大アップさせる。範囲内\n　の敵の数に応じて、このスキルのダメージがダ\n　ウンする。このスキルによるダメージではＴＰ\n　が回復しない。このスキルの効果はダメージを\n　無効化しても付与される。\n・最も近い敵１キャラを中心にした中範囲内の敵\n　すべてに物理小ダメージを与え、物理防御力を\n　特大ダウンさせる。範囲内の敵の数に応じて、\n　防御力の減少量がダウンする。このスキルの効\n　果はダメージを無効化しても付与される。", 13, "バーンサウルス", 0);
INSERT INTO `v1_ea086c4647ca338e98020f5882a82d6275ec9c93c6b8f8456f635c5a2ac0f9a6` VALUES (0, 0, 1000, 0, 1.7, 1, 270, 303608, 303608, "【物理】強靭な脚と鋭利なツメを操る獰猛な恐竜。\n滅びた大地でマグマを喰らい生きる孤高の存在。\n\n・バーンサウルスから受けるすべてのダメージで\n　発生する、パーティキャラのＴＰ回復量を大ダ\n　ウンさせる。\n・ユニオンバーストは、最も近い敵１キャラを中\n　心とした範囲に物理特大ダメージを与える。さ\n　らに自身の行動速度を特大アップさせる。範囲\n　内の敵の数に応じて、このスキルのダメージが\n　ダウンする。このスキルによるダメージでは、\n　敵のＴＰが回復しない。\n・最も近い敵１キャラを中心にした範囲に物理中\n　ダメージを与え、さらに物理防御力を大ダウン\n　させる。範囲内の敵の数に応じて、防御力の減\n　少量がダウンする。このスキルの状態異常はダ\n　メージを無効化しても付与される。", 13, "バーンサウルス", 0);
INSERT INTO `v1_ea086c4647ca338e98020f5882a82d6275ec9c93c6b8f8456f635c5a2ac0f9a6` VALUES (0, 0, 1000, 0, 0.7, 1, 270, 303700, 303700, "【物理】火山に身を落としてなお\n地獄の炎を纏い蘇った双頭の魔犬。\n愛犬ボニーと共に縄張りを駆け、\n侵入者には灼熱の洗礼を浴びせる。\n\n・敵全体に物理中ダメージを与え、\n　火傷状態にする。さらに、召喚\n　モンスターのＨＰを回復する。\n\n・ボニーを召喚する。", 14, "ヘルハウンド", 0);
INSERT INTO `v1_ea086c4647ca338e98020f5882a82d6275ec9c93c6b8f8456f635c5a2ac0f9a6` VALUES (0, 0, 1000, 0, 0.7, 1, 270, 303701, 303701, "【物理】塔の魔力で、魔界の炎を顕現させる魔狼。\n灼熱の地で、相棒のボニーとともに待ち受ける。\n\n・敵全体に物理大ダメージを与え火傷状態にする。\n　さらに、ボニーのHPを回復する。\n・ボニーを召喚する。\n・敵全体に物理中ダメージを与え、行動速度をダ\n　ウンさせる。さらに、味方全体の物理攻撃力を\n　大アップさせる。", 14, "バーンウルフ", 0);
INSERT INTO `v1_ea086c4647ca338e98020f5882a82d6275ec9c93c6b8f8456f635c5a2ac0f9a6` VALUES (0, 0, 1000, 0, 2.0, 1, 270, 303800, 303800, "【物理】はるか古より迷宮を守護する防衛兵器。すべての罠を超えた最奥で侵入者を静かに待つ。\n・敵全体にキャラの最大ＨＰに応じた\n　魔法中ダメージを与える。\n・前方範囲内の敵に物理中ダメージを与える。", 16, "アルマ", 0);
INSERT INTO `v1_ea086c4647ca338e98020f5882a82d6275ec9c93c6b8f8456f635c5a2ac0f9a6` VALUES (0, 0, 1000, 0, 2.0, 1, 270, 303801, 303800, "【物理】はるか古より迷宮を守護する防衛兵器。すべての罠を超えた最奥で侵入者を静かに待つ。\n・敵全体にキャラの最大ＨＰに応じた\n　魔法特大ダメージを与える。\n・前方範囲内の敵に物理中ダメージを与える。\n・残りＨＰの高い敵に対して最大ＨＰに応じた\n　魔法中ダメージを与えさらにスタンさせる。\n・アルマ自身のＨＰが50％を切ると、さらに\n　攻撃が激しく凶暴になる。", 16, "アルマ", 0);
INSERT INTO `v1_ea086c4647ca338e98020f5882a82d6275ec9c93c6b8f8456f635c5a2ac0f9a6` VALUES (0, 0, 1000, 0, 2.0, 1, 270, 303900, 303900, "【物理】怪力が自慢の、凶暴な一つ目巨人。\n瞳に魔力をみなぎらせ、強力な怪光線を放つ。\n\n・バトル開始時、自身の物理防御力と魔法防御力\n　を小アップさせる。この防御力アップ効果で上\n　昇した防御力は、バトル中に受ける防御力ダウ\n　ンスキルの影響を受けない。\n・サイクロプスから受けるすべてのダメージで発\n　生する、パーティキャラのＴＰ回復量を中ダウ\n　ンさせる。\n・ユニオンバーストは、飛行状態のキャラを含む\n　敵全体にキャラの最大ＨＰに応じた魔法割合ダ\n　メージを与え、ＴＰを小ダウンさせ、ステータ\n　スアップ効果を解除する。さらに、バトル終了\n　まで物理攻撃力を大ダウン、魔法攻撃力を中ダ\n　ウンさせる。このスキルの状態異常はダメージ\n　を無効化しても付与される。このスキルはダ\n　メージを無効化してもＴＰをダウンさせる。\n・物理攻撃力もしくは魔法攻撃力が最も高い敵１\n　キャラを中心にした小範囲に物理特大ダメージ\n　を与え、スタン状態を付与する。このスキルに\n　よるダメージでは、ＴＰが回復しない。このス\n　キルの状態異常はダメージを無効化しても付与\n　される。", 13, "サイクロプス", 0);
INSERT INTO `v1_ea086c4647ca338e98020f5882a82d6275ec9c93c6b8f8456f635c5a2ac0f9a6` VALUES (0, 0, 1000, 0, 2.0, 1, 270, 303901, 303901, "【物理】怪力が自慢の、凶暴な一つ目巨人。\n瞳に魔力をみなぎらせ、強力な怪光線を放つ。\n\n・敵全体に魔法大ダメージ。\n　残りＨＰが１００％のキャラにはダメージが\n　アップし、さらに行動速度をダウンさせる。\n・最も物理攻撃力が高い１キャラに、最大ＨＰ\n　に応じた物理中ダメージを与え、ＴＰダウン。", 13, "サイクロプス", 0);
INSERT INTO `v1_ea086c4647ca338e98020f5882a82d6275ec9c93c6b8f8456f635c5a2ac0f9a6` VALUES (0, 0, 1000, 0, 2.0, 1, 270, 303902, 303902, "【物理】怪力が自慢の、凶暴な一つ目巨人。\n瞳に魔力をみなぎらせ、強力な怪光線を放つ。\n\n・バトル開始時、自身の物理防御力と魔法防御力\n　を中アップさせる。この防御力アップ効果で上\n　昇した防御力は、バトル中に受ける防御力ダウ\n　ンスキルの影響を受けない。\n・バトル開始時、敵全体のＨＰ吸収を特大ダウン\n　させる。 \n・バトル開始時、敵全体のＴＰ上昇を小ダウンさ\n　せるフィールドを展開する。\n・バトル開始時、自身にＴＰダウン25％カット\n　効果を付与する。\n・サイクロプスから受けるすべてのダメージで発\n　生する、パーティキャラのＴＰ回復量を中ダウ\n　ンさせる。\n・ユニオンバーストは、飛行状態のキャラを含む\n　敵全体にキャラの最大ＨＰに応じた魔法割合ダ\n　メージを与え、ＴＰを中ダウンさせ、ステータ\n　スアップ効果を解除する。さらに、バトル終了\n　まで物理攻撃力を大ダウン、魔法攻撃力を中ダ\n　ウンさせる。このスキルの状態異常はダメージ\n　を無効化しても付与される。このスキルはダ\n　メージを無効化してもＴＰをダウンさせる。\n・物理攻撃力もしくは魔法攻撃力が最も高い敵１\n　キャラを中心にした小範囲に物理特大ダメージ\n　を与え、スタン状態を付与する。このスキルに\n　よるダメージでは、ＴＰが回復しない。このス\n　キルの状態異常はダメージを無効化しても付与\n　される。\n・通常攻撃によるダメージでは、ＴＰ回復量が特\n　大ダウンする。", 13, "サイクロプス", 0);
INSERT INTO `v1_ea086c4647ca338e98020f5882a82d6275ec9c93c6b8f8456f635c5a2ac0f9a6` VALUES (0, 0, 1000, 0, 2.0, 1, 270, 303903, 303903, "【物理】砂塵の中、山のように佇\nむ一つ目の巨人。単眼から超強力\nな光線を発し冒険者を薙ぎ払う。\n\n・ユニオンバーストは、敵全体に\n　魔法大ダメージを与え、さらに\n　行動速度を中ダウンさせる。\n　残りＨＰが100％のキャラ\n　には、ダメージがアップする。\n\n・最も物理攻撃力が高い１キャラ\n　に、最大ＨＰに応じた物理割合\n　ダメージを与え、さらにＴＰを\n　大ダウンさせる。", 13, "ヒトツメ巨人", 0);
INSERT INTO `v1_ea086c4647ca338e98020f5882a82d6275ec9c93c6b8f8456f635c5a2ac0f9a6` VALUES (0, 0, 3000, 0, 2.0, 1, 270, 303904, 303904, "【物理】怪力が自慢の、凶暴な一つ目巨人。\n瞳に魔力をみなぎらせ、強力な怪光線を放つ。\n\n・サイクロプスから受けるすべてのダメージで発\n　生する、パーティキャラのＴＰ回復量を中ダウ\n　ンさせる。\n・ユニオンバーストは、敵全体にキャラの最大Ｈ\n　Ｐに応じた魔法割合ダメージを与え、物理攻撃\n　力を大ダウン、魔法攻撃力を中ダウン、ＴＰを\n　大ダウンさせ、ステータスアップ効果を解除す\n　る。このスキルの状態異常はダメージを無効化\n　しても付与される。\n・挑発を無視して、物理攻撃力もしくは魔法攻撃\n　力が最も高い敵１キャラを中心とした小範囲に\n　物理大ダメージを与え、スタン状態を付与し、\n　行動速度を中ダウンさせる。このスキルによる\n　ダメージでは、キャラのＴＰが回復しない。こ\n　のスキルの状態異常はダメージを無効化しても\n　付与される。", 13, "サイクロプス", 0);
INSERT INTO `v1_ea086c4647ca338e98020f5882a82d6275ec9c93c6b8f8456f635c5a2ac0f9a6` VALUES (0, 0, 1000, 0, 2.0, 1, 270, 304000, 304000, "【物理】夢幻より生まれ、闇を彷徨う魔獣。\n悪夢の牢に獲物を縛り、旨そうに生気を喰う。\n\n・挑発効果の影響を受けやすい。\n・バトル開始時、自身の物理防御力と魔法防御力\n　を小アップさせる。この防御力アップ効果で上\n　昇した防御力は、バトル中に受ける防御力ダウ\n　ンスキルの影響を受けない。\n・ムーバから受けるすべてのダメージで発生する、\n　パーティキャラのＴＰ回復量を中ダウンさせる。\n・ユニオンバーストは、物理攻撃力もしくは魔法\n　攻撃力が最も高い敵１キャラに魔法特大ダメー\n　ジを与え、ＴＰを特大ダウンさせる。このスキ\n　ルはダメージを無効化してもＴＰをダウンさせ\n　る。\n・敵の挑発を無視して、最も遠い敵１キャラを中\n　心にした小範囲内の敵すべてに呪い状態を付与\n　する。このスキルにより付与された呪いのダ\n　メージでは、ＴＰが回復しない。", 13, "ムーバ", 0);
INSERT INTO `v1_ea086c4647ca338e98020f5882a82d6275ec9c93c6b8f8456f635c5a2ac0f9a6` VALUES (0, 0, 1000, 0, 2.0, 1, 270, 304001, 304001, "【物理】夢幻より生まれ、闇を彷徨う魔獣。\n悪夢の牢に獲物を縛り、旨そうに生気を喰う。\n\n・挑発効果の影響を受けやすい。\n・バトル開始時、自身の物理防御力を大アップさ\n　せ、魔法防御力を中アップさせる。この防御力\n　アップ効果で上昇した防御力は、バトル中に受\n　ける防御力ダウンスキルの影響を受けない。\n・バトル開始時、敵全体のＨＰ吸収を特大ダウン\n　させる。\n・バトル開始時、敵全体のＴＰ上昇を小ダウンさ\n　せるフィールドを展開する。\n・バトル開始時、自身にＴＰダウン100％カッ\n　ト効果を付与する。\n・ムーバから受けるすべてのダメージで発生する、\n　パーティキャラのＴＰ回復量を中ダウンさせる。\n・ユニオンバーストは、物理攻撃力もしくは魔法\n　攻撃力が最も高い敵１キャラのステータスアッ\n　プ効果を解除した後、魔法特大ダメージを与え、\n　ＴＰを特大ダウンさせる。このスキルはダメー\n　ジを無効化してもＴＰをダウンさせる。\n・敵の挑発を無視して、最も遠い敵１キャラを中\n　心にした小範囲内の敵すべてのＴＰを小ダウン\n　させ、呪い状態を付与する。このスキルにより\n　付与された呪いのダメージでは、ＴＰが回復し\n　ない。", 13, "ムーバ", 0);
