INSERT INTO `v1_ea086c4647ca338e98020f5882a82d6275ec9c93c6b8f8456f635c5a2ac0f9a6` VALUES (0, 0, 1000, 0, 1.8, 1, 270, 305301, 305301, "【物理】雪山に棲む獰猛な魔狼。\n強靭な歯牙は歴戦の冒険者さえ\n容易く氷塊の墓標へ変える。\n\n・ユニオンバーストは敵全体に物\n　理大ダメージを与える。\n\n・一番後ろの敵キャラを中心とし\n　て、範囲内の敵すべてに物理小\n　ダメージを与え、スタンさせる。\n　さらに敵全体の物理防御力を中\n　ダウンさせる。\n\n・前方範囲内の敵すべてに物理小\n　ダメージを与え、毒状態にする。", 16, "アイスガルグ", 0);
