INSERT INTO `v1_ea086c4647ca338e98020f5882a82d6275ec9c93c6b8f8456f635c5a2ac0f9a6` VALUES (0, 0, 1000, 0, 3.0, 2, 270, 305508, 305508, "【魔法】塔の魔力を帯びた霧が実体化した魔物。\n怨嗟を振りまき、分身とともに冒険者を付け狙う。\n\n・敵全体に魔法大ダメージを与える。\n　敵が呪い状態の場合、ダメージがアップし、呪\n　詛状態の場合、さらにダメージがアップする。\n・敵全体に魔法中ダメージを与える。\n　敵が呪い状態の場合、呪詛状態にする。さらに、\n　ミストシーカー自身のＨＰが80％未満の場合、\n　物理攻撃力と魔法攻撃力を短時間大ダウンさせ\n　る。\n・分身体を召喚する。\n　召喚する際は敵全体をノックバックさせる。\n・分身体は、敵全体に物理中ダメージを\n　与え、さらに呪い状態にする。\n・分身体のＨＰが30％以下になった時、最も魔\n　法攻撃力が高い味方１キャラのＴＰを1000\n　回復させる。\n・ミストシーカー自身のＨＰが80％を切ると、\n　さらに攻撃が激しく狂暴になる。", 16, "ミストシーカー", 0);
INSERT INTO `v1_ea086c4647ca338e98020f5882a82d6275ec9c93c6b8f8456f635c5a2ac0f9a6` VALUES (0, 0, 1000, 0, 1.8, 1, 270, 305600, 305600, "【物理】ルナの魔力欲しさに現れた異界の魔物。\n魔眼と従えた兵で、ルナの塔の征服を目論む。\n\n・敵全体に魔法大ダメージを与え、\n　さらに敵全体の魔法防御力を中ダウンさせる。\n・6回、ランダムで1キャラに魔法小ダメージ\n　を与える。\n・後ろから3番目のキャラに魔法中ダメージと、\n　長時間の拘束を与える。\n　さらに全体に魔法小ダメージを与える。", 16, "ロード・イビリィ", 0);
INSERT INTO `v1_ea086c4647ca338e98020f5882a82d6275ec9c93c6b8f8456f635c5a2ac0f9a6` VALUES (0, 0, 1000, 0, 1.8, 1, 270, 305700, 305700, "【物理】豪快に戦斧を振るう熟練のゴブリン戦士。\n幼き戦士を脇に抱え、魂の技を見せ付ける。\n\n・バトル開始時、自身の物理防御力と魔法防御力\n　を小アップさせる。この防御力アップ効果で上\n　昇した防御力は、バトル中に受ける防御力ダウ\n　ンスキルの影響を受けない。\n・ゴブリングレートから受けるすべてのダメージ\n　で発生する、パーティキャラのＴＰ回復量を中\n　ダウンさせる。\n・ユニオンバーストは、最も近い敵１キャラに物\n　理特大ダメージを与え、自身の物理攻撃力と物\n　理防御力をバトル終了まで小アップさせる。", 16, "ゴブリングレート", 0);
INSERT INTO `v1_ea086c4647ca338e98020f5882a82d6275ec9c93c6b8f8456f635c5a2ac0f9a6` VALUES (0, 0, 1000, 0, 1.8, 1, 270, 305701, 305701, "豪快に戦斧を振り回す、熟練のゴブリン戦士。\n幼き戦士を脇に抱え、魂の技を見せ付ける。\n\n・ユニオンバーストは、自身の物理攻撃力を中\n　アップ、物理防御力を小アップさせ、さらに\n　目の前の１キャラに物理特大ダメージ。", 16, "ゴブリングレート", 0);
INSERT INTO `v1_ea086c4647ca338e98020f5882a82d6275ec9c93c6b8f8456f635c5a2ac0f9a6` VALUES (0, 0, 1000, 0, 1.8, 1, 270, 305702, 305702, "【物理】豪快に戦斧を振るう熟練のゴブリン戦士。\n幼き戦士を脇に抱え、魂の技を見せ付ける。\n\n・バトル開始時、自身の物理防御力と魔法防御力\n　を中アップさせる。この防御力アップ効果で上\n　昇した防御力は、バトル中に受ける防御力ダウ\n　ンスキルの影響を受けない。\n・バトル開始時、敵全体のＨＰ吸収を特大ダウン\n　させ、ＴＰ上昇を小ダウンさせる。\n・バトル開始時、自身にＴＰダウン100％カッ\n　ト効果を付与する。\n・ゴブリングレートから受けるすべてのダメージ\n　で発生する、パーティキャラのＴＰ回復量を中\n　ダウンさせる。\n・ユニオンバーストは、最も近い敵１キャラに物\n　理特大ダメージを与え、スタン状態を付与し、\n　さらに自身の物理攻撃力と物理防御力をバトル\n　終了まで小アップさせる。このスキルの状態異\n　常はダメージを無効化しても付与される。\n・最も近い敵１キャラに物理大ダメージを与え、\n　スタン状態を付与する。このスキルの状態異常\n　はダメージを無効化しても付与される。", 16, "ゴブリングレート", 0);
INSERT INTO `v1_ea086c4647ca338e98020f5882a82d6275ec9c93c6b8f8456f635c5a2ac0f9a6` VALUES (0, 0, 1000, 0, 1.8, 1, 270, 305703, 305703, "【物理】屈強な体と心で砂漠を\n生き抜く蛮族の戦士。我が幼子を\n守るため、戦斧を振るい続ける。\n\n・ユニオンバーストは、自身の物理\n　攻撃力を中アップ、物理防御力を\n　小アップさせる。\n　さらに目の前の１キャラに物理特\n　大ダメージを与える。", 16, "蛮族のリーダー", 0);
INSERT INTO `v1_ea086c4647ca338e98020f5882a82d6275ec9c93c6b8f8456f635c5a2ac0f9a6` VALUES (0, 0, 1000, 0, 1.8, 1, 270, 305704, 305704, "【物理】豪快に戦斧を振るう熟練のゴブリン戦士。\n幼き戦士を脇に抱え、魂の技を見せ付ける。\n\n・バトル開始時、自身の物理防御力と魔法防御力\n　を中アップさせる。この防御力アップ効果で上\n　昇した防御力は、バトル中に受ける防御力ダウ\n　ンスキルの影響を受けない。\n・バトル開始時、自身にＴＰダウン50％カット\n　効果を付与する。\n・ゴブリングレートから受けるすべてのダメージ\n　で発生する、パーティキャラのＴＰ回復量を中\n　ダウンさせる。\n・ユニオンバーストは、自身の物理攻撃力と物理\n　防御力を小アップさせ、最も近い敵１キャラに\n　物理特大ダメージを与え、さらにスタン状態を\n　付与する。このスキルの状態異常はダメージを\n　無効化しても付与される。\n・最も近い敵１キャラに物理大ダメージを与え、\n　スタン状態を付与する。このスキルの状態異常\n　はダメージを無効化しても付与される。", 16, "ゴブリングレート", 0);
INSERT INTO `v1_ea086c4647ca338e98020f5882a82d6275ec9c93c6b8f8456f635c5a2ac0f9a6` VALUES (0, 0, 1000, 0, 1.8, 1, 270, 305705, 305705, "【物理】豪快に戦斧を振るう熟練のゴブリン戦士。\n幼き戦士を脇に抱え、魂の技を見せ付ける。\n\n・ゴブリングレートから受けるすべてのダメージ\n　で発生する、パーティキャラのＴＰ回復量を中\n　ダウンさせる。\n・ユニオンバーストは、最も近い敵１キャラに物\n　理特大ダメージを与え、さらにスタン状態を付\n　与する。その後自身の物理攻撃力と物理防御力\n　をバトル終了まで小アップさせる。このスキル\n　の状態異常はダメージを無効化しても付与され\n　る。\n・最も近い敵１キャラに物理大ダメージを与え、\n　スタン状態を付与する。このスキルの状態異常\n　はダメージを無効化しても付与される。", 16, "ゴブリングレート", 0);
INSERT INTO `v1_ea086c4647ca338e98020f5882a82d6275ec9c93c6b8f8456f635c5a2ac0f9a6` VALUES (0, 0, 1000, 0, 1.8, 1, 270, 305707, 305707, "【物理】ロードの左腕にしてゴブリンの大戦士。\n巨大な戦斧を豪快に振るい、眼前の敵を打ち砕く。\n\n・バトル開始時、自身の物理防御力と魔法防御\n　力を小アップさせる。この防御力アップ効果\n　で上昇した防御力は、バトル中に受ける防御\n　力ダウンスキルの影響を受けない。\n・バトル開始時、ダメージ上限スキルを使用し\n　て、一度の行動で与えるダメージの値が一定\n　を超えた場合、与えるダメージの値に応じて\n　ダメージ量を減少させる。\n・ゴブリングレートから受けるすべてのダメー\n　ジで発生する、パーティキャラのＴＰ回復量\n　を中ダウンさせる。\n・ユニオンバーストは、前方１キャラに物理特\n　大ダメージを与え、自身の物理攻撃力と物理\n　防御力を小アップさせる。\n・前方１キャラにキャラの最大ＨＰに応じた\n　物理割合ダメージを与える。", 16, "ゴブリングレート", 0);
INSERT INTO `v1_ea086c4647ca338e98020f5882a82d6275ec9c93c6b8f8456f635c5a2ac0f9a6` VALUES (0, 0, 1000, 0, 1.8, 2, 270, 305800, 305800, "【魔法】海神の使者で、不思議な軟体の魔物。\n身体のほとんどは魔力を帯びた海水だとか。\n\n・バトル開始時、自身の物理防御力と魔法防御力\n　を小アップさせる。この防御力アップ効果で上\n　昇した防御力は、バトル中に受ける防御力ダウ\n　ンスキルの影響を受けない。\n・ネプテリオンから受けるすべてのダメージで発\n　生する、パーティキャラのＴＰ回復量を小ダウ\n　ンさせる。\n・ユニオンバーストは、敵全体に魔法大ダメージ\n　を与え、さらに物理攻撃力と魔法攻撃力を大ダ\n　ウンさせる。このスキルの状態異常はダメージ\n　を無効化しても付与される。このスキルによる\n　ダメージでは、敵のＴＰが回復しない。\n・敵の挑発を無視して、前から４番目のキャラを\n　中心とした小範囲に継続魔法小ダメージを与え\n　る。このスキルによるダメージでは、敵のＴＰ\n　が回復しない。\n・通常攻撃は敵の挑発を無視して、前から４番目\n　のキャラに魔法特大ダメージを与え、そのキャ\n　ラを中心とした小範囲に行動速度を大ダウンさ\n　せる。このスキルの状態異常はダメージを無効\n　化しても付与される。", 16, "ネプテリオン", 0);
INSERT INTO `v1_ea086c4647ca338e98020f5882a82d6275ec9c93c6b8f8456f635c5a2ac0f9a6` VALUES (0, 0, 1000, 0, 2.6, 2, 270, 305801, 305801, "【魔法】ルナの塔で暴れ回る海獣。太古の海神に\n仕えるが、今や大義なき破壊者となり果てる。\n\n・敵全体に魔法ダメージを与える。\n・一定範囲に、物理攻撃力と魔法防御力を\n　ダウンさせ、継続魔法ダメージを与える\n　フィールドを展開する。\n・前から２番目と３番目のキャラに\n　魔法大ダメージを与える。", 16, "カムツカイ", 0);
