INSERT INTO `v1_ea086c4647ca338e98020f5882a82d6275ec9c93c6b8f8456f635c5a2ac0f9a6` VALUES (0, 0, 3000, 0, 1.8, 1, 270, 307003, 307002, "【物理】異界の魔石から生まれた、弩級の巨人。\n強靭な肉体を武器に、苛烈な猛攻を繰り出す。\n\n・物理攻撃力アップ・ダウン効果の影響を受け\n　やすい。\n・ユニオンバーストは、敵全体に物理大ダメージ。\n・前方３キャラに物理中ダメージ。\n・一定時間防御の構えをとったあと、その間に受\n　けたダメージ量に応じた物理ダメージを敵全体\n　に与え、前方２キャラを麻痺させる。\n　さらに、自身の物理攻撃力をアップ。\n・敵全体に物理ダメージを与え、大きくノック\n　バックさせる。\n・コア・ギガース自身のＨＰが40％を切ると\n　さらに攻撃が激しく凶暴になる。", 16, "コア・ギガース", 0);
