INSERT INTO `v1_ea086c4647ca338e98020f5882a82d6275ec9c93c6b8f8456f635c5a2ac0f9a6` VALUES (0, 0, 1000, 0, 1.8, 1, 270, 308606, 308606, "【ＭＯＤＥ３】竜への憧れが石竜子を覚醒\nさせる。空を統べる覇者の息吹は島を絶やす\n劫火となる。\n\n・バトルの開始時、自身に【火の実】を70追加。\n・ユニオンバーストは、敵全体に\n　ドラゴンもどきが保持している【火の実】の\n　数に応じた物理割合ダメージを与える。\n　その後【火の実】が60減少する。\n・前方の３キャラに、キャラの最大ＨＰに応じた\n　物理割合ダメージを与え、スタンさせる。\n・しばらくの間、物理防御力と魔法防御力が大幅\n　に下がり、食事モーションをとっている間、\n　最大で60、【火の実】を継続して追加する。\n　モーション中にドラゴンもどきが250000\n　以上のダメージを受けるとスキルが中断され\n　【火の実】が30減少する。\n・バトル開始直後に、さらに攻撃が激しく\n　凶暴になる。", 16, "ドラゴンもどき", 0);
INSERT INTO `v1_ea086c4647ca338e98020f5882a82d6275ec9c93c6b8f8456f635c5a2ac0f9a6` VALUES (0, 0, 1000, 0, 1.8, 1, 270, 309000, 309000, "【物理】夢うつつの狭間で惰眠を貪るナマケモノ。\n眠りを妨げると怒り狂い、超能力で暴れまわる。\n\n・バトル開始時、自身の物理防御力と魔法防御力\n　を小アップさせる。この防御力アップ効果で上\n　昇した防御力は、バトル中に受ける防御力ダウ\n　ンスキルの影響を受けない。\n・ランドスロースから受けるすべてのダメージで\n　発生する、パーティキャラのＴＰ回復量を中ダ\n　ウンさせる。\n・ユニオンバーストは、敵全体にキャラの最大Ｈ\n　Ｐに応じた魔法割合ダメージを与え、物理攻撃\n　力を特大ダウン、魔法攻撃力を中ダウンさせる。\n　このスキルの効果はダメージを無効化しても付\n　与される。\n・敵全体に魔法中ダメージを与える。", 16, "ランドスロース", 0);
INSERT INTO `v1_ea086c4647ca338e98020f5882a82d6275ec9c93c6b8f8456f635c5a2ac0f9a6` VALUES (0, 0, 1000, 0, 1.8, 1, 270, 309001, 309001, "【物理】夢うつつの狭間で惰眠を貪るナマケモノ。\n眠りを妨げると怒り狂い、超能力で暴れまわる。\n\n・バトル開始時、自身の物理防御力と魔法防御力\n　を中アップさせる。この防御力アップ効果で上\n　昇した防御力は、バトル中に受ける防御力ダウ\n　ンスキルの影響を受けない。\n・バトル開始時、敵全体のＨＰ吸収を特大ダウン\n　させ、ＴＰ上昇を小ダウンさせる。\n・バトル開始時、自身にＴＰダウン50％カット\n　効果を付与する。\n・ランドスロースから受けるすべてのダメージで\n　発生する、パーティキャラのＴＰ回復量を中ダ\n　ウンさせる。\n・ユニオンバーストは、敵全体にキャラの最大Ｈ\n　Ｐに応じた魔法割合ダメージを与え、物理攻撃\n　力を特大ダウン、魔法攻撃力を中ダウンさせ、\n　スタン状態を付与する。このスキルの状態異常\n　はダメージを無効化しても付与される。\n・敵全体に魔法中ダメージを与え、スタン状態を\n　付与する。このスキルの状態異常はダメージを\n　無効化しても付与される。", 16, "ランドスロース", 0);
INSERT INTO `v1_ea086c4647ca338e98020f5882a82d6275ec9c93c6b8f8456f635c5a2ac0f9a6` VALUES (0, 0, 1000, 0, 1.8, 1, 270, 309002, 309002, "【物理】広大な密林に棲む巨大な\n獣。身の危険が迫ると防衛反応が\n働き、大いなる野生を解き放つ。\n\n・ユニオンバーストは、敵全体に\n　キャラの最大ＨＰに応じた魔法\n　割合ダメージを与え、さらに物\n　理攻撃力と魔法攻撃力を大ダウ\n　ン。このスキルの状態異常はダ\n　メージを無効化しても付与され\n　る。\n\n・敵全体に魔法小ダメージを与え\n　スタンさせる。このスキルの状\n　態異常はダメージを無効化して\n　も付与される。", 16, "レイジーママル", 0);
