INSERT INTO `v1_ea086c4647ca338e98020f5882a82d6275ec9c93c6b8f8456f635c5a2ac0f9a6` VALUES (0, 0, 3000, 0, 1.4, 1, 270, 310701, 310700, "【物理】規格外のアイデアと歪んだ情熱から爆誕\nした鋼鉄の巨人。礎となった者の特性を、異常な\n解釈で再構築し敵の言語中枢と精神を破壊する。\n\n・自身が毒、猛毒、呪い、呪詛、火傷の効果を\n　受けると、自身の物理防御力と魔法防御力と\n　行動速度が大ダウンする。\n・ユニオンバーストは、敵全体にキャラの\n　最大ＨＰに応じた魔法割合ダメージを与える。\n・自身の物理攻撃力をアップさせたあと、\n　敵全体に物理ダメージを与える。\n・前方３キャラに最大ＨＰに応じた魔法割合\n　ダメージを与え、行動速度をダウンさせる。", 16, "なかよしＸ", 0);
