INSERT INTO `v1_ea086c4647ca338e98020f5882a82d6275ec9c93c6b8f8456f635c5a2ac0f9a6` VALUES (0, 0, 3000, 0, 2.7, 1, 270, 312105, 312100, "【ＭＯＤＥ２】魔法少女たちの猛攻をも取り込み\nさらに膨れ上がるアンチビーストの渇望。更には\n人々を守るための勇気までも溶蝕されてゆく。\n\n・ユニオンバーストは、敵全体のバリアを解除し、\n　魔法大ダメージを与える。さらに、物理防御力\n　と魔法防御力を大ダウンさせ、一定時間ＨＰ回\n　復量を70％ダウンさせる。\n・敵全体のバリアを解除し、最大ＨＰに応じた\n　魔法割合ダメージを与え、毒状態にする。", 16, "アンチビースト de M", 0);
INSERT INTO `v1_ea086c4647ca338e98020f5882a82d6275ec9c93c6b8f8456f635c5a2ac0f9a6` VALUES (0, 0, 3000, 0, 2.7, 1, 270, 312106, 312100, "【ＭＯＤＥ３】負の情念は最高潮に達し、歪んだ\n渇望が一帯を包み込む。魔法少女たちの信念を\n挫き、守護者なき街を我が一部とせんとする。\n\n・ユニオンバーストは、敵全体のバリアを解除し、\n　魔法大ダメージを与える。さらに、物理防御力\n　と魔法防御力を大ダウンさせ、一定時間ＨＰ回\n　復量を70％ダウンさせる。\n・前方２キャラに物理大ダメージを与える。\n・アンチビースト de M自身のＨＰが12％\n　を切ると、さらに攻撃が激しく凶暴になる。", 16, "アンチビースト de M", 0);
INSERT INTO `v1_ea086c4647ca338e98020f5882a82d6275ec9c93c6b8f8456f635c5a2ac0f9a6` VALUES (0, 0, 3000, 0, 2.7, 1, 270, 312200, 312200, "【物理】でっぷりとした眼光鋭い最恐雪ダルマ。\n狂気をその身に纏いながら聖夜の街中を縷々転々。\n年に一度の特別な夜がブラッククリスマスと化す。", 16, "ドミネイテッドスノーマン", 0);
INSERT INTO `v1_ea086c4647ca338e98020f5882a82d6275ec9c93c6b8f8456f635c5a2ac0f9a6` VALUES (0, 0, 3000, 0, 4.0, 1, 270, 312201, 312200, "【物理】でっぷりとした眼光鋭い最恐雪ダルマ。\n狂気をその身に纏いながら聖夜の街中を縷々転々。\n年に一度の特別な夜がブラッククリスマスと化す。\n\n・ユニオンバーストは、敵全体にキャラの最大\n　ＨＰに応じた物理割合ダメージを与え、大きく\n　吹き飛ばす。さらに、自身のターゲットがブレ\n　イクしていない場合、物理防御力を中アップ、\n　魔法防御力を小アップ。このスキルの効果は\n　ダメージを無効化しても付与される。\n・敵全体にキャラの最大ＨＰに応じた物理割合ダ\n　メージを与える。自身のターゲットがブレイク\n　していない場合、追加でスタン状態を付与。\n　このスキルの状態異常はダメージを無効化して\n　も付与される。\n・残り時間が20秒を切ると、プレゼント型爆弾\n　を取り出し、その後一定時間が経過で、敵全体\n　にキャラの最大ＨＰに応じた物理割合ダメージ\n　を与える。\n　さらに、ドミネイテッドスノーマンにも自身の\n　残りＨＰに応じた物理割合ダメージを与え、\n　ＴＰを特大ダウンさせる。\n・頭部のブレイク時、物理防御力と魔法防御力\n　が小ダウン。\n・胴部のブレイク時、物理防御力と魔法防御力\n　が小ダウン。", 16, "ドミネイテッドスノーマン", 0);
