INSERT INTO `v1_ea086c4647ca338e98020f5882a82d6275ec9c93c6b8f8456f635c5a2ac0f9a6` VALUES (0, 0, 3000, 0, 0.0, 1, 270, 312304, 312302, "【ＭＯＤＥ１】数多の漁師を波間に沈めた海の王。\n黄金に輝くヒレで大漁船の竜骨をも圧し折る。\nその様はさながら大海原の解体ショー\n\n・ユニオンバーストは、敵全体にキャラの最大\n　ＨＰに応じた物理割合ダメージを与える。\n　さらにノックバックさせる。\n・前方範囲内の敵すべてに物理大ダメージを与え、\n　さらに後ろから２キャラに物理中ダメージを\n　与える。また、自身の物理攻撃力を小アップ\n　させる。", 16, "グレートトゥンヌス", 0);
INSERT INTO `v1_ea086c4647ca338e98020f5882a82d6275ec9c93c6b8f8456f635c5a2ac0f9a6` VALUES (0, 0, 3000, 0, 1.0, 1, 270, 312305, 312305, "【ＭＯＤＥ２】海を駆ける怪魚はさらに力を増す。\nひと泳ぎで自身を鼓舞するかのように大渦をおこ\nし大海を分かつ巨大な砲弾となって敵に激突する。\n\n・ユニオンバーストは、前から３キャラに\n　物理特大ダメージを与え\n　さらにノックバックさせる。\n・自身の物理攻撃力を大アップ、行動速度を\n　中アップ、ＴＰを小アップさせる。", 16, "グレートトゥンヌス", 0);
INSERT INTO `v1_ea086c4647ca338e98020f5882a82d6275ec9c93c6b8f8456f635c5a2ac0f9a6` VALUES (0, 0, 3000, 0, 1.0, 1, 270, 312306, 312302, "【ＭＯＤＥ３】幾度もその黒い巨体に銛を受けよ\nうとも、伝説の巨魚の猛進は止められない。\n永きにわたる海の漢たちとの勝負の決着の時。\n\n・ユニオンバーストは、敵全体にキャラの最大\n　ＨＰに応じた物理割合ダメージを与える。\n　さらにノックバックさせる。\n・自身の物理攻撃力を大アップ、行動速度を\n　中アップ、ＴＰを大アップさせる。\n・残り時間が35秒を切ると、さらに攻撃が激し\n　く凶暴になる。", 16, "グレートトゥンヌス", 0);
INSERT INTO `v1_ea086c4647ca338e98020f5882a82d6275ec9c93c6b8f8456f635c5a2ac0f9a6` VALUES (0, 0, 1000, 0, 1.6, 1, 270, 312400, 312400, "【物理】氷翼を広げ、冒険者を威嚇\nする大鷲。縄張りに踏み込んだ者\nを、容赦なく氷漬けにし子どもたち\nの贄とする。\n\n・ユニオンバーストは、前方小範囲\n　に物理大ダメージを与え、さらに\n　大きく吹き飛ばし、氷結状態に\n　する。", 11, "アイスグリフォン", 0);
INSERT INTO `v1_ea086c4647ca338e98020f5882a82d6275ec9c93c6b8f8456f635c5a2ac0f9a6` VALUES (0, 0, 1000, 0, 1.6, 1, 270, 312401, 312400, "【物理】氷翼を広げ、冒険者を威嚇\nする大鷲。縄張りに踏み込んだ者\nを、容赦なく氷漬けにし子どもたち\nの贄とする。\n\n・ユニオンバーストは、前方小範囲\n　に物理大ダメージを与え、さらに\n　大きく吹き飛ばし、氷結状態に\n　する。", 11, "アイスグリフォン", 0);
INSERT INTO `v1_ea086c4647ca338e98020f5882a82d6275ec9c93c6b8f8456f635c5a2ac0f9a6` VALUES (0, 0, 1000, 0, 1.6, 2, 270, 312500, 312500, "【魔法】塔の魔力を蓄え雷撃として放電する怪鳥。\n雷雲と共に顕現し、天災の如き雷で敵を葬り去る。\n\n・敵全体に魔法大ダメージを与え、自分の魔法\n　攻撃力と、行動速度を小アップさせる。\n　物理攻撃をする敵にはさらに追加ダメージ。\n・前方範囲内の敵すべてに魔法中ダメージを与え\n　物理防御力と魔法防御力を中ダウンさせる。\n・物理攻撃力が最も高い敵１キャラに魔法大\n　ダメージを与え、長時間麻痺状態にする。\n　自分のＨＰが85％を切ると、魔法攻撃力が\n　最も高い敵にもこの攻撃を行う。", 6, "マダムエレクトラ", 0);
INSERT INTO `v1_ea086c4647ca338e98020f5882a82d6275ec9c93c6b8f8456f635c5a2ac0f9a6` VALUES (0, 0, 1000, 0, 1.6, 2, 270, 312501, 312501, "【魔法】魔力を蓄え雷撃として放電する怪鳥。\n雷雲と共に顕現し、天災の如き雷で敵を葬り去る。\n\n・バトル開始時、自身の物理防御力と魔法防御力\n　を小アップさせる。この防御力アップ効果で上\n　昇した防御力は、バトル中に受ける防御力ダウ\n　ンスキルの影響を受けない。\n・マダムエレクトラから受けるすべてのダメージ\n　で発生する、パーティキャラのＴＰ回復量を中\n　ダウンさせる。\n・ユニオンバーストは、自身の魔法攻撃力と魔法\n　防御力をバトル終了まで小アップさせ、最も近\n　い敵１キャラに魔法特大ダメージを与える。", 6, "マダムエレクトラ", 0);
INSERT INTO `v1_ea086c4647ca338e98020f5882a82d6275ec9c93c6b8f8456f635c5a2ac0f9a6` VALUES (0, 0, 1000, 0, 1.6, 2, 270, 312502, 312502, "【魔法】魔力を蓄え雷撃として放電する怪鳥。\n雷雲と共に顕現し、天災の如き雷で敵を葬り去る。\n\n・バトル開始時、自身の物理防御力と魔法防御力\n　を中アップさせる。この防御力アップ効果で上\n　昇した防御力は、バトル中に受ける防御力ダウ\n　ンスキルの影響を受けない。\n・バトル開始時、自身にＴＰダウン100％カッ\n　ト効果を付与する。\n・バトル開始時、敵全体のＨＰ吸収を特大ダウン\n　させ、ＴＰ上昇を小ダウンさせる。\n・マダムエレクトラから受けるすべてのダメージ\n　で発生する、パーティキャラのＴＰ回復量を中\n　ダウンさせる。\n・ユニオンバーストは、バトル終了まで自身の魔\n　法攻撃力を小アップ、魔法防御力を中アップさ\n　せる。その後、最も近い敵１キャラに魔法特大\n　ダメージを与え、麻痺状態を付与する。このス\n　キルの状態異常はダメージを無効化しても付与\n　される。\n・最も近い敵１キャラに魔法大ダメージを与え、\n　麻痺状態を付与する。このスキルの状態異常は\n　ダメージを無効化しても付与される。", 6, "マダムエレクトラ", 0);
