INSERT INTO `v1_ea086c4647ca338e98020f5882a82d6275ec9c93c6b8f8456f635c5a2ac0f9a6` VALUES (0, 0, 3000, 0, 2.7, 2, 270, 312701, 312700, "【魔法】使役者の真の力を試す黒き風の精霊。\nそこには悪意はなく、純粋に己の内に秘めた想い\nと絆が試される。\n\n・ユニオンバーストは、敵全体に黒風の精霊\n　の物理攻撃力とキャラの最大ＨＰに応じた\n　魔法割合ダメージを与える。さらに、\n　残りＨＰが最も低い１キャラにカウントダウン\n　を付与し、５秒後に最大ＨＰの80%の魔法\n　割合ダメージを与える。\n　黒風の精霊がユニオンバーストを使用してから\n　一定時間が経過するまでは再度ユニオン\n　バーストを使用しない。", 16, "黒風の精霊", 0);
