INSERT INTO `v1_ea086c4647ca338e98020f5882a82d6275ec9c93c6b8f8456f635c5a2ac0f9a6` VALUES (0, 0, 3000, 0, 1.0, 1, 270, 314002, 314002, "誰にでもある苦手科目の宿題。ついつい後回しに\nしがちで、溜まってしまうとなかなか大変。\nここで立ち止まるわけにはいかないぞ！\n\n・バトル開始後、大量の宿題が降り注ぎ、\n　敵全体に物理中ダメージ。このスキル\n　によるダメージでは、キャラのＴＰが\n　回復しない。\n・バトル中、一定時間が経過するごとに\n　モンスターが押し寄せてくる。", 16, "ちょっと苦手な宿題", 0);
