INSERT INTO `v1_ea086c4647ca338e98020f5882a82d6275ec9c93c6b8f8456f635c5a2ac0f9a6` VALUES (0, 0, 3000, 0, 1.6, 2, 270, 316401, 316400, "【魔法】月の満ち欠けに応じて現れる、異なる二\nつの世界を繋ぐゲート。みゅ～ちゃんをゲートま\nで導いて、元の世界に戻してあげよう！\n\n・ユニオンバーストは、敵全体にムーンレイゲー\n　トの【月の光印】の数に応じた魔法割合ダメー\n　ジを与える。\n・敵全体をノックバックさせ行動速度を中ダウン\n　させる。さらに、敵全体にムーンレイゲートの\n　【月の光印】の数に応じた魔法割合ダメージを\n　与える。\n　その後ムーンレイゲートに【月の光印】を10\n　個付与する。\n　このスキルの状態異常はダメージを無効化して\n　も付与される。\n・敵全体にムーンレイゲートの【月の光印】の数\n　に応じた魔法小ダメージを与える。\n　さらにムーンレイゲートを中心とした範囲内の\n　敵全てにムーンレイゲートの【月の光印】の数\n　に応じた魔法小ダメージを与える。\n・【月の光印】は最大50個まで付与される。", 16, "ムーンレイゲート", 0);
