INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES (5, 1, "やばいですね☆なの！", "おいしすぎて食べ始めたら\n止まらない最高のプリンなの\n今のミヤコを止められるヤツは\n誰もいないの", 1, 3, "ペコリーヌのプリン", "プリンセスなプリンなの\nお店にあったいろんな食べもの\nを入れられるだけ入れてみたの\nどんな味なのか楽しみなの", 100);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES (5, 2, "主さま、なの！", "なんだかすごくやさしい気持ち\nになる味なの…\nおはようからおやすみまで\nずっと一緒な味なの…", 1, 3, "コッコロのプリン", "いつもオマエのそばに寄りそう\nヤツのぷりんなの。ミヤコの\nワガママもやさしく受け入れて\nくれそうなの", 101);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES (5, 3, "ぶっ殺すぞ！なの！\n…デレデレはどこなの？", "いろんな味が一度で楽しめる\nプリンなの。ツンデレはプリン\nにするとおいしいの。ミヤコも\nツンデレを目指してみるの", 0, 3, "キャルのプリン", "ツンツンデレデレなヤツの\nプリンなの。砂糖とスパイスを\n混ぜてみたの。どんな味になる\nか楽しみなの", 102);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES (5, 4, "おにーたん♪なの！", "あったかいところで食べる\nプリンアイスは最高なの～♪\n安心しすぎて昔の記憶のない\nミヤコも心が子どもになるの～", 1, 3, "シェフィのプリン", "オマエと同じで、一度赤ちゃん\nみたいになったヤツのプリン\nなの。ヒンヤリ、プリンアイス\nにしてみたの", 103);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES (5, 5, "ハーハッハッハ！\n世界征服なのじゃなのー！", "トマトで栄養補給も完璧なの\nどんどん力がみなぎってきて\n復活も夢じゃないの\nミヤコが復活したら…", 1, 3, "イリヤのプリン", "イリヤを見てたら思いついた\nトマトジュースが隠し味の\nプリンなの。栄養満点なの", 104);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES (5, 6, "お兄ちゃんのエッチ！　なの！", "見た目がかわいくてもミヤコは\n味にも厳しいの。あむっ…\nこ、これは…！なんだかすごく\n変な気分になってきたの…", 1, 3, "アカリのプリン", "アカリのプリンなの。ヨリと\n同じくらいかわいく作って\nやったの。正直ミヤコは\nおいしければなんでもいいの", 105);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES (5, 7, "えっと…その…あんたのこと…\nだ、大っ嫌いなのーー！", "んむぅ…これはなかなか感想が\n難しいプリンなの…率直に言い\nたいような…心のうちに秘めて\nおきたいような…", 1, 3, "ヨリのプリン", "かわいくつくらないとアカリが\n怒りそうだからヨリのプリンは\nかわいく作ったの。自信作なの", 106);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES (5, 8, "お父さん…\nいいかげんにしてなの！\nん？プリンの未来は…\n見えなかったの…", "このプリンを食べるとシノブが\nミヤコの中にいるみたいなの！\nシノブの力があれば未来の\nプリンを見放題なの！", 0, 3, "シノブのプリン", "シノブはいつもミヤコに\nやさしくしてくれるから\n丁寧に作ったの。ドクロ親父は\nいらないから作ってないの", 107);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES (5, 9, "インフェルノシールド！なの！", "固すぎてかじれないから丸吞み\nにしたの。なんだか体が硬く\nなった気がするの。みんなを\n守れる気がするの！", 1, 3, "ジュンのプリン", "黒い鎧を脱がないヤツのプリン\nなの。固めのプリンを目指したら\nカチカチになりすぎたの", 108);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES (5, 10, "あなたといると調子が狂うな\n…なの\n翻弄してたのはオマエなの？", "口の中でスパイスがプチプチ\nはじけて新食感なの！\nプチプチがミヤコを\n翻弄するの！", 1, 3, "トモのプリン", "オマエをいっつもからかってる\nヤツのプリンなの\nちょっぴり変わったスパイスで\nからかいを表現したの", 109);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES (5, 11, "タイガーヒーローボンバー！\nなの！", "ん～見かけによらず濃厚な\nパンチのある味わいが\n口いっぱいに広がるの！", 1, 3, "マツリのプリン", "王宮騎士団にいるちっこいヤツ\nなの。ちっこいけどやるヤツ\nらしいから、濃いめの味付けに\nしてみたの", 110);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES (5, 13, "ま、いいんじゃん？\nしらんけどなの", "クールな見た目なのにとっても\n優しい味なの！なんだか\n懐かしさを感じるの…", 1, 3, "クロエのプリン", "なんかすごくクールなプリンなの\nなんかテンションも少し\n低そうなの。これ\n食べちゃっても大丈夫なの？", 111);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES (5, 14, "ぷりーん★なの！", "なんか変わった食感なの…\nちゃる…ちょる…ちぇる？\nちぇるってしてるの\nでもちぇるってなんなの？", 1, 3, "チエルのプリン", "きれいにデコれたの！\nカラフルでおいしそうなの\nこれは期待できそうなの", 112);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES (5, 15, "端的に換言すれば\n青春の味なの？", "味はなんか妙に\n甘酸っぱいの…\n食べたことない味なの\nこの味ってもしかして…", 1, 3, "ユニのプリン", "見た目は完ぺきなの！\nこれをつくったやつは\nきっと天才なの！\n早く食べたいの！", 113);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES (5, 16, "幼なじみはあんた…なの？", "ミヤコも幽霊になる前の記憶\nがないのに、なんだか懐かしい\n気持ちになってくるの…", 1, 3, "サレンのプリン", "オマエから聞いた懐かしい\n味ってヤツを全部入れてみたの\nオマエ記憶がないのに懐かしい\nってどういうことなの？", 114);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES (5, 17, "ひゃああんなの～！", "おかしいの。このプリンを\n食べてから、３回も転んだの\nミヤコは幽霊だから転んだり\nしないの。なんでなの…？", 1, 3, "スズメのプリン", "たまたま会ったメイドに教えて\nもらった作り方なの。メイドは\nプリンも作れるはずなの。\nなのにこの不安は何なの…？", 115);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES (5, 18, "サルキデバレヤ！なの！", "甘さのなかに少しのしょっぱさ\nクセになる味なの\nこのプリンはがんばればきっと\nいろんな美味しいになれるの", 0, 3, "クルミのプリン", "怖がりな子どもが教えてくれた\n小さくてかわいいプリンなの　\n触ってないのにぷるぷる震える\n驚きのやわらかさなの", 116);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES (5, 19, "プリンフルスイングなの～！", "このプリン、食べるとなんだか\nお姉ちゃんになった気分に\nなるの。ピンチのプリンは\nミヤコが守ってやるの！", 1, 3, "アヤネのプリン", "くまのぬいぐるみを見ていたら\nひらめいたの。小さいけど\nしっかりした甘さの元気に\nなれるプリンなの！", 117);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES (5, 20, "みらくるみやりん\nくるりんぱ～☆なの！", "メルヘンパワーがあふれてきて\nいまならミヤコにも魔法が\n使える気がするの！\nほな、いきまひょ～なの！", 1, 3, "マホのプリン", "マホマホ王国って国にいる\nお姫様のプリンなの\n魔物も虜にしちゃう\nすてきなプリンなの", 118);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES (5, 21, "ウルフェンバイトなの！", "うおーん！ミヤコなんだか\n強くなった気がするの！\n困ったことがあればミヤコが\nまとめて面倒見てやるの！", 1, 3, "マコトのプリン", "カッコいいプリンを目指して\nワイルドなものをガンガン\n入れてみたの。きっと熱い\nプリンができるはずなの", 119);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES (5, 22, "なんくるないさ～！なの！", "一口食べたら元気が\nあふれて止まらないの！\n気分がいいから、一曲\n踊りでも披露してやるの！", 1, 3, "カオリのプリン", "どっかの国でよくとれる\nゴーヤーに似てるやつを\nいれてみたの！ちょっと\n苦いらしいけど…", 120);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES (5, 23, "ふふん。簡単な推理なの", "むむむ…なるほどなの\n上質な糖分をふんだんに\n使うことで、脳をより活性化\nできるからなの！", 1, 3, "カスミのプリン", "勘の鋭いやつが好きな食べ物を\nたくさん入れてみたの\n難しいこと考えるのにぴったり\nらしいけど、なんでなの？", 121);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES (5, 24, "エリザベス～！なの～！", "ミルクがとっても濃厚で\nほっぺたが落ちちゃうの～♪\nこのプリンからは牧場の…\n自然の息吹を感じるの！", 1, 3, "マヒルのプリン", "搾りたての牛乳を\nたっぷり使ったプリンなの～\nマヒルに手伝ってもらって\nミヤコ直々に搾ってきたの！", 122);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES (5, 25, "私の好きな本…読んで\nもらえたらうれしいです…\nなの", "ほのかな甘さでありながら\nしっかりとした主張も\n感じさせる\n味わい深いプリンなの", 1, 3, "シオリのプリン", "マヒルからやさしいヤツだって\n聞いたの。やさしさを表現\nするために奥ゆかしい甘さに\n仕上げてみたの", 123);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES (5, 26, "もふもふにしてあげるなの！", "こ、これは…！口の中に入れた\n瞬間にふんわり溶けて\nなくなっちゃうの！\nその食感がやみつきなの！", 1, 3, "リマのプリン", "牧場にいるヤツらしいの\n牛とは違うらしいの\nもふもふ気持ちいいらしいから\n食感にこだわって作ったの", 124);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES (5, 27, "めんどくさいな～なの…", "んむぅ…評価が難しい味なの…\nおいしいのはおいしいの…\nん～考えてたら…\n眠くなってきたの…", 0, 3, "リンのプリン", "いっつも寝てるやつらしいの\nぐっすり眠れる薬草をたくさん\n入れてみたの。プリンとして\nおいしくなるかはわからないの", 125);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES (5, 28, "プリンの権能を解放するの～！", "プ…プリンなのにクレープの味\nがするの。味もすごく普通なの\nでも…なんだかプリンの新しい\n扉を開けそうな気がするの…！", 1, 3, "ラビリスタのプリン", "クレープ屋の真っ赤な人に\n教わったプリンなの\n作り方を丁寧に教えてくれて\nとっても優しい人だったの！", 126);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES (5, 29, "お姉ちゃんパワー全開！なの！", "とろける食感が絶品なの～♪\n食べるとなんだか幸せな\n気持ちになれるの…これが\n愛情ってやつなの？", 0, 3, "シズルのプリン", "お店に落ちてた謎のレシピの\nプリンなの。レシピの表紙には\n『お姉ちゃんの愛情たっぷり』\nって書いてあったの", 127);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES (5, 30, "お兄ちゃん、プリンをよこすの！", "これで妹力？があがるらしいの。\n妹になればお兄ちゃんに\nプリンをたかり放題なの！\n最高なの～！", 1, 3, "リノのプリン", "リノから作り方を教わったの！\nさすがクレープ屋で働いてる\nだけあって美味しそうなの！", 128);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES (5, 31, "困った時はお互いさまさま！\nなの！", "食べたら元気が湧いてくるの\nだからプリンのレシピのお礼に\nミヤコも人助けを手伝って\nやったの。ミヤコはえらいの！", 1, 3, "ヒヨリのプリン", "プリンのレシピで悩んでたら\n通りすがりのおねーさんが\n一緒に考えてくれたプリンなの\nいいヤツもたまにはいるの～♪", 129);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES (5, 32, "フラワリングヒールなの～♪", "はっ…思わずお花畑が見えたの\n身も心も包まれるような\nとっても優しい味がするの\nこれは…まさに癒しなの～♪", 1, 3, "ユイのプリン", "ミヤコががんばって作った\nこだわりのプリンなの～\nプリンは癒しなの。だから優しい\n気持ちで丁寧に作ったの", 130);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES (5, 33, "スラッシュテンペスト！なの。", "確かに完璧なプリンなの\nでも究極のプリンと呼ぶには\nまだまだ修行が必要なの…\nプリン作りは奥が深いの…！", 1, 3, "レイのプリン", "究極のプリンを作るため\nミヤコは厳しい修行をしたの…\nその結果あって完成した\nクールで完璧なプリンなの！", 131);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES (5, 34, "おーほっほほほ！なの！", "味にも素材にもこだわりが\nあるの。一流は妥協しないの\nもっと豪華に、もっとおいしく\nできるはず…", 1, 3, "アキノのプリン", "豪華で高級な\nお嬢様プリンなの\n中には宝石みたいな\n大きいイチゴを入れるの！", 132);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES (5, 35, "プリしゅわ最高～！なの！", "プリンは飲み物とも言うの\nぐいっていってみるの\n…ふわしゅわでとろけるの～！\n体に染みわたるの～", 1, 3, "ユカリのプリン", "外はしっかり、中はふわとろの\nプリンにするの\n泡みたいなしゅわしゅわの\n生クリームも入れるの♪", 133);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES (5, 36, "にゃにゃにゃ！なの！", "このプリンは焼きたてのうちに\n食べるのが一番なの…！\n熱いけどおいしくて\nうずうずしてきたの…！", 1, 3, "タマキのプリン", "餡子を入れて\nオーブンでこんがり焼くの\nたい…焼きプリンなの\n斬新なの！", 134);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES (5, 37, "効率全開！なの！", "プリンはいつでも手軽に\n食べられるのも魅力なの\nこれは食べ応えもあるから一つ\n食べればいっぱい動けるの", 0, 3, "ミフユのプリン", "栄養バランスを考えて\n木の実をたくさん入れるの\nあれこれ忙しいときでも\n頼りになるプリンなの！", 135);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES (5, 38, "あらあら～なの～♪", "このプリンを食べてから\nいい子になった気がするの\n大好きなプリンを泣いてる子に\nあげたの。いい気分なの", 1, 3, "ミサトのプリン", "エルフの森で子どもにもらった\n薬草を使って作ったの\nこんな優しい味のプリンは\n初めてなの…", 136);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES (5, 39, "スヤスヤ～…なの…", "あまいプリンが口の中で\nぽんぽん弾ける未知の味なの！\n今なら不思議な力で世界も\n救えそうなの！でも眠くなるの…", 1, 3, "ハツネのプリン", "お昼寝でみた夢の中に\nでてきた不思議なプリンを\n作ってみたの。見てたらまた\n眠くなってきたの…", 137);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES (5, 40, "ぼっちは卒業なの～！", "知らないうちにはしっこに\n隠れちゃってたの。食べたら\nミヤコ、シノブたちに会いに\nいきたくなってきたの", 1, 3, "アオイのプリン", "お店のはしっこに一つだけ\n残ったおもちゃを見て思い\nついたの。他のプリンと一緒に\n置いておくと震えだすの", 138);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES (5, 41, "リズムあげるのー！", "口の中に華やかな甘さが\n広がる…これがアイドルなの？\n急に歌って踊りたく\nなってきたの！", 1, 3, "ノゾミのプリン", "トップオブアイドルの\nプリンなの。キラキラした\nものを入れて、あつ～い\nオーブンで焼いてみたの", 139);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES (5, 42, "風の加護を！なの！", "プリンの生地がとっても\nなめらかで最高の舌触りなの！\n精霊、ミヤコのプリンのために…", 1, 3, "チカのプリン", "ミヤコにプリンをくれるすごく\nいいヤツのプリンなの\n精霊に呼びかけながら\n一生懸命かき混ぜたの", 140);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES (5, 43, "騎士さんのために\nしたんじゃありません！なの！", "包装は食べるまでの手間が\n増えてめんどくさいの。でも\nそのめんどくささもプリンの\n味を引き立てるの", 1, 3, "ツムギのプリン", "歌って踊って衣装も作れるヤツ\nのプリンなの。このプリンは\nきれいに包装するの。プリンを\n最高の形で着飾らせるの", 141);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES (5, 12, "宴の始まりなの！", "んむっ！？\nく、口のなかでプリンの\nおいしさが大暴れなの！\n止まらないの！", 0, 3, "クリスティーナのプリン", "プリンをくれないならどうでも\nいいヤツなの。でも横で\nいろいろ言ってきたから\nがんばって作ったの", 142);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES (5, 44, "一刀両断、覚悟しな！なの！", "大人の味なの。今やミヤコは\nみんなの頼れる姐御なの！\n悪さするやつはミヤコが\n許さないの！", 1, 3, "ルカのプリン", "大人な甘さ控えめのプリンを\n作るの。試しにお魚を入れて\nみるの。プリンの可能性は\n無限大なの！", 143);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES (5, 45, "きゅぴーん！キタコレ！なの！", "一口食べる度に味が変わって\n魔法みたいなの！\nもしかして全部食べたら\nミヤコも魔法少女になれるの？", 1, 3, "ナナカのプリン", "サイカワサイツヨな\n魔法のプリンなの\n中身をカラフルにするの～\n甘いプリンは頭が冴えるの！", 144);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES (5, 46, "人呼んで、疾風のミヤコなの！", "一口食べたら謎の組織に\n狙われたり、前世の記憶が蘇る\n気がするの～設定盛りすぎなの\nミヤコは、ミヤコは…", 1, 3, "アンナのプリン", "小難しい言葉をささやきながら\n作ったら魔力あふれるプリンが\n爆誕したの！でも、不思議と\nイタイタしいのはなぜなの～？", 145);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES (5, 47, "クスクス…クスクスクス…なの", "なんか普通のプリンよりも\nいい匂いがするの～…\nプリンのことがもっともっと\n好きになっちゃうの…", 1, 3, "エリコのプリン", "甘酸っぱい赤い木の実を\n切り刻んで入れるの\n甘くてちょっぴり刺激的な\nヤンデレのプリンなの", 146);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES (5, 48, "どんなプリンも\nミヤコの隻眼からは\n逃れられないの～！", "鼻に抜ける薬のにおいがアレ\nだけど大人っぽくて頭が\nよくなった気がするの。無性に\n実験したくてしかたないの", 1, 3, "ミツキのプリン", "健康に気を遣うミヤコは\nプリンにお薬を入れてみたの～\nでもなんだか毒々しいから\n彩りにバラを添えてみたの～", 147);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES (5, 49, "私は子どもではない！なの！", "ん～王道なプリンは\n甘くて安定したおいしさなの～\n思わず笑顔になっちゃうの\nにこにこなの～♪…はっ！", 1, 3, "モニカのプリン", "小さいけどしっかりした\n軍人のプリンなの\nちょっと固めにして\n砂糖はいっぱい入れるの", 148);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES (5, 50, "ショーグン！　ミヤコと一緒に\n天下統一するデースなの！", "このプリンを食べてから\n誰かにお仕えしなきゃ\nいけない気がするデースなの\nそれはショ、ショー…", 1, 3, "ニノンのプリン", "オーエド町で手に入れた素材と\nトーゴクに伝わる術をかけて\nプリンを作るの。シュババ！\n忍法隠し味の術なの！", 149);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES (5, 51, "…ぐふふ、じゅるり～なの", "カチカチに固めたせいで食感が\nプリンのようで\nプリンじゃないの…でもこの\nもどかしさが癖になるの…", 0, 3, "クウカのプリン", "オマエが持ってきた手錠を\nヒントに作ったプリンなの\nプリンが動けないように\nカチカチに固めたの", 150);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES (5, 52, "あぁ、ミヤコはなんて美しいの～", "味も美しい以外の感想が\n出てこないの。ていうかこれを\n食べたミヤコも美しいの！\n鏡、鏡はどこなの！", 1, 3, "ユキのプリン", "とっても美しいプリンなの\n美しいものを手当たり次第\n入れたらすごいことになったの", 151);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES (5, 53, "ジー…ってミヤコなにしてるの\nミヤコはストーカーじゃないの！", "うん、やっぱりおいしく…\nん？あそこにいるのは\nアイツなの。あれ？\n体が勝手にアイツの方に…", 0, 3, "アユミのプリン", "知らないうちにお菓子が売れた\n時に思いついたプリンなの。\n作り方もよく覚えてない…\nけど、きっとおいしいの！", 152);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES (5, 54, "にひひ…イタズラの時間なの！", "見た目通り甘々で優しい味なの\nでも、レシピを見たら裏面に\n別の作り方が書いてあるの。\nこれだと激辛プリンに…", 0, 3, "ミソギのプリン", "イタズラ好きの手下と一緒に\n作ったプリンなの。見た目は\nかわいいけど、作り方は\n意外と複雑なの～", 153);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES (5, 55, "ぴょんぴょこぴょーん、なの♪", "ん～、ふわふわの甘々なの！\nなんだか楽しくなって\n心も体もるんたったしてくるの", 0, 3, "ミミのプリン", "素直でやさしい手下と一緒に\n動物のぬいぐるみをいっぱい\n並べて作ったの", 154);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES (5, 56, "へんたいふしんしゃさんなの！", "ひんやりしてて暑い日でも\nおいしく食べられそうなの！\nん？これを買いたい大人が\nいる？それって…", 0, 3, "キョウカのプリン", "幽霊を怖がっている手下と\n一緒に作ったプリンなの。\n小さいクセにきっちりしてて\nとってもかわいいプリンなの", 155);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES (5, 57, "ハート狙い撃ち！なの！", "見て食べて楽しめる新機軸の\nプリンなの。ミヤコは幽霊\nだけど自分の足でランウェイを\n歩きたくなってきたの！", 1, 3, "スズナのプリン", "カリスマモデルなプリンなの\nモリモリにデコレーションして\nカリスマ感を演出したの", 156);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES (5, 58, "授業を始めます♪なの！", "プ、プリンは全部ミヤコのもの\nなのに、なんだかこのプリンは\nいろんな人に教えたく\nなっちゃう不思議な味なの！", 0, 3, "イオのプリン", "学校の先生をしてるヤツの\nプリンなの。お堅くしようと\nしたら、いつの間にか色っぽく\nなっちゃったの。なんでなの？", 157);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES (5, 59, "立派なレディ、なの！", "ミヤコはプリンならどんなもの\nでも食べるの。でもカラメルを\nちょっとだけ足すの。\nちょっとだけなら…", 1, 3, "ミサキのプリン", "少し背伸びしたおませな子を\n表現したプリンなの。あえて\n甘さ控えめなの。カラメルは\n後乗せできるから安心するの", 158);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES (5, 60, "プリンを持ってこないと\nお仕置きだよ～☆なの！", "味はすごいゴージャスなの！\nボスの風格があるの！\nビッグなミヤコに\nプリンを捧げるの！", 1, 3, "ホマレのプリン", "謎に包まれたプリンができたの\n何か隠してるようなの…\nこれは危険なプリンなの！", 159);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES (5, 61, "たぎってきたの！", "最初の一口目が\nすごいインパクトなの！\n無性にケンカしたくなるの！\nプリンをよこせなの！", 1, 3, "カヤのプリン", "ワイルドな見た目なの！\nこれは期待できそうなの。\nきっと躍動感あふれる味なの！", 160);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES (5, 62, "下剋上、やってやるですなの！", "見た目はアレだけど味はおいし\nいの…あと主張もはっきりして\nるの！やる気と自信だけは\n出てきたのうおおおーなの！", 0, 3, "イノリのプリン", "なんか見た目がパッとしないの\nこれほんとに大丈夫なの？\nなんか頼りないの…", 161);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES (5, 63, "どけどけ～！なの～！", "むむむ…食べたら\nやる気が湧いてきたの！\nミヤコを邪魔するやつはみんな\nまとめてぶっ飛ばしてやるの～", 1, 3, "ムイミのプリン", "天下の大悪党のプリンなの\nこれまでのジョーシキに\n捉われないプリンを\n作ってみたの～", 162);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES (5, 64, "ミラーミラー！なの！", "いつのまにか作ったの全部\n食べちゃったの。なんで\nプリンは分身しないの？\n無限に増えろなの！", 1, 3, "ネネカのプリン", "いろんなものに変身したり\n分身したりできるヤツの\nプリンなの。型で同じプリンを\nたくさんたくさん作ったの", 163);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES (5, 65, "プリンに忠誠を誓いますわ！\nなの！", "むぅ…認めたくないけど\nおいしいの…これを何個も\n作れるほどのお金を稼ぐのは\n大変なの…でもおいしいから…", 1, 3, "クレジッタのプリン", "金の亡者のプリンなの\n金にものを言わせて作って\nみたの。おばあちゃんの\nプリンと対極なの", 164);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES (5, 66, "ギルド管理協会へようこそ！\nなの！", "手堅い…！手堅いの…！\n隙のないプリンなの！\n今のミヤコなら完璧な\n書類作成ができそうなの！", 1, 3, "カリンのプリン", "みんなお世話になるギルド\n管理協会職員のプリンなの\nお役所感を出すために手堅く\n王道に作ったの", 165);
