INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("ペコリーヌのプリン", 5, 100, 3, 1, "おいしすぎて食べ始めたら\n止まらない最高のプリンなの\n今のミヤコを止められるヤツは\n誰もいないの", "プリンセスなプリンなの\nお店にあったいろんな食べもの\nを入れられるだけ入れてみたの\nどんな味なのか楽しみなの", "やばいですね☆なの！", 1);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("コッコロのプリン", 5, 101, 3, 1, "なんだかすごくやさしい気持ち\nになる味なの…\nおはようからおやすみまで\nずっと一緒な味なの…", "いつもオマエのそばに寄りそう\nヤツのぷりんなの。ミヤコの\nワガママもやさしく受け入れて\nくれそうなの", "主さま、なの！", 2);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("キャルのプリン", 5, 102, 3, 0, "いろんな味が一度で楽しめる\nプリンなの。ツンデレはプリン\nにするとおいしいの。ミヤコも\nツンデレを目指してみるの", "ツンツンデレデレなヤツの\nプリンなの。砂糖とスパイスを\n混ぜてみたの。どんな味になる\nか楽しみなの", "ぶっ殺すぞ！なの！\n…デレデレはどこなの？", 3);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("シェフィのプリン", 5, 103, 3, 1, "あったかいところで食べる\nプリンアイスは最高なの～♪\n安心しすぎて昔の記憶のない\nミヤコも心が子どもになるの～", "オマエと同じで、一度赤ちゃん\nみたいになったヤツのプリン\nなの。ヒンヤリ、プリンアイス\nにしてみたの", "おにーたん♪なの！", 4);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("イリヤのプリン", 5, 104, 3, 1, "トマトで栄養補給も完璧なの\nどんどん力がみなぎってきて\n復活も夢じゃないの\nミヤコが復活したら…", "イリヤを見てたら思いついた\nトマトジュースが隠し味の\nプリンなの。栄養満点なの", "ハーハッハッハ！\n世界征服なのじゃなのー！", 5);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("アカリのプリン", 5, 105, 3, 1, "見た目がかわいくてもミヤコは\n味にも厳しいの。あむっ…\nこ、これは…！なんだかすごく\n変な気分になってきたの…", "アカリのプリンなの。ヨリと\n同じくらいかわいく作って\nやったの。正直ミヤコは\nおいしければなんでもいいの", "お兄ちゃんのエッチ！　なの！", 6);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("ヨリのプリン", 5, 106, 3, 1, "んむぅ…これはなかなか感想が\n難しいプリンなの…率直に言い\nたいような…心のうちに秘めて\nおきたいような…", "かわいくつくらないとアカリが\n怒りそうだからヨリのプリンは\nかわいく作ったの。自信作なの", "えっと…その…あんたのこと…\nだ、大っ嫌いなのーー！", 7);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("シノブのプリン", 5, 107, 3, 0, "このプリンを食べるとシノブが\nミヤコの中にいるみたいなの！\nシノブの力があれば未来の\nプリンを見放題なの！", "シノブはいつもミヤコに\nやさしくしてくれるから\n丁寧に作ったの。ドクロ親父は\nいらないから作ってないの", "お父さん…\nいいかげんにしてなの！\nん？プリンの未来は…\n見えなかったの…", 8);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("ジュンのプリン", 5, 108, 3, 1, "固すぎてかじれないから丸吞み\nにしたの。なんだか体が硬く\nなった気がするの。みんなを\n守れる気がするの！", "黒い鎧を脱がないヤツのプリン\nなの。固めのプリンを目指したら\nカチカチになりすぎたの", "インフェルノシールド！なの！", 9);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("トモのプリン", 5, 109, 3, 1, "口の中でスパイスがプチプチ\nはじけて新食感なの！\nプチプチがミヤコを\n翻弄するの！", "オマエをいっつもからかってる\nヤツのプリンなの\nちょっぴり変わったスパイスで\nからかいを表現したの", "あなたといると調子が狂うな\n…なの\n翻弄してたのはオマエなの？", 10);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("マツリのプリン", 5, 110, 3, 1, "ん～見かけによらず濃厚な\nパンチのある味わいが\n口いっぱいに広がるの！", "王宮騎士団にいるちっこいヤツ\nなの。ちっこいけどやるヤツ\nらしいから、濃いめの味付けに\nしてみたの", "タイガーヒーローボンバー！\nなの！", 11);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("クロエのプリン", 5, 111, 3, 1, "クールな見た目なのにとっても\n優しい味なの！なんだか\n懐かしさを感じるの…", "なんかすごくクールなプリンなの\nなんかテンションも少し\n低そうなの。これ\n食べちゃっても大丈夫なの？", "ま、いいんじゃん？\nしらんけどなの", 13);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("チエルのプリン", 5, 112, 3, 1, "なんか変わった食感なの…\nちゃる…ちょる…ちぇる？\nちぇるってしてるの\nでもちぇるってなんなの？", "きれいにデコれたの！\nカラフルでおいしそうなの\nこれは期待できそうなの", "ぷりーん★なの！", 14);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("ユニのプリン", 5, 113, 3, 1, "味はなんか妙に\n甘酸っぱいの…\n食べたことない味なの\nこの味ってもしかして…", "見た目は完ぺきなの！\nこれをつくったやつは\nきっと天才なの！\n早く食べたいの！", "端的に換言すれば\n青春の味なの？", 15);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("サレンのプリン", 5, 114, 3, 1, "ミヤコも幽霊になる前の記憶\nがないのに、なんだか懐かしい\n気持ちになってくるの…", "オマエから聞いた懐かしい\n味ってヤツを全部入れてみたの\nオマエ記憶がないのに懐かしい\nってどういうことなの？", "幼なじみはあんた…なの？", 16);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("スズメのプリン", 5, 115, 3, 1, "おかしいの。このプリンを\n食べてから、３回も転んだの\nミヤコは幽霊だから転んだり\nしないの。なんでなの…？", "たまたま会ったメイドに教えて\nもらった作り方なの。メイドは\nプリンも作れるはずなの。\nなのにこの不安は何なの…？", "ひゃああんなの～！", 17);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("クルミのプリン", 5, 116, 3, 0, "甘さのなかに少しのしょっぱさ\nクセになる味なの\nこのプリンはがんばればきっと\nいろんな美味しいになれるの", "怖がりな子どもが教えてくれた\n小さくてかわいいプリンなの　\n触ってないのにぷるぷる震える\n驚きのやわらかさなの", "サルキデバレヤ！なの！", 18);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("アヤネのプリン", 5, 117, 3, 1, "このプリン、食べるとなんだか\nお姉ちゃんになった気分に\nなるの。ピンチのプリンは\nミヤコが守ってやるの！", "くまのぬいぐるみを見ていたら\nひらめいたの。小さいけど\nしっかりした甘さの元気に\nなれるプリンなの！", "プリンフルスイングなの～！", 19);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("マホのプリン", 5, 118, 3, 1, "メルヘンパワーがあふれてきて\nいまならミヤコにも魔法が\n使える気がするの！\nほな、いきまひょ～なの！", "マホマホ王国って国にいる\nお姫様のプリンなの\n魔物も虜にしちゃう\nすてきなプリンなの", "みらくるみやりん\nくるりんぱ～☆なの！", 20);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("マコトのプリン", 5, 119, 3, 1, "うおーん！ミヤコなんだか\n強くなった気がするの！\n困ったことがあればミヤコが\nまとめて面倒見てやるの！", "カッコいいプリンを目指して\nワイルドなものをガンガン\n入れてみたの。きっと熱い\nプリンができるはずなの", "ウルフェンバイトなの！", 21);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("カオリのプリン", 5, 120, 3, 1, "一口食べたら元気が\nあふれて止まらないの！\n気分がいいから、一曲\n踊りでも披露してやるの！", "どっかの国でよくとれる\nゴーヤーに似てるやつを\nいれてみたの！ちょっと\n苦いらしいけど…", "なんくるないさ～！なの！", 22);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("カスミのプリン", 5, 121, 3, 1, "むむむ…なるほどなの\n上質な糖分をふんだんに\n使うことで、脳をより活性化\nできるからなの！", "勘の鋭いやつが好きな食べ物を\nたくさん入れてみたの\n難しいこと考えるのにぴったり\nらしいけど、なんでなの？", "ふふん。簡単な推理なの", 23);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("マヒルのプリン", 5, 122, 3, 1, "ミルクがとっても濃厚で\nほっぺたが落ちちゃうの～♪\nこのプリンからは牧場の…\n自然の息吹を感じるの！", "搾りたての牛乳を\nたっぷり使ったプリンなの～\nマヒルに手伝ってもらって\nミヤコ直々に搾ってきたの！", "エリザベス～！なの～！", 24);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("シオリのプリン", 5, 123, 3, 1, "ほのかな甘さでありながら\nしっかりとした主張も\n感じさせる\n味わい深いプリンなの", "マヒルからやさしいヤツだって\n聞いたの。やさしさを表現\nするために奥ゆかしい甘さに\n仕上げてみたの", "私の好きな本…読んで\nもらえたらうれしいです…\nなの", 25);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("リマのプリン", 5, 124, 3, 1, "こ、これは…！口の中に入れた\n瞬間にふんわり溶けて\nなくなっちゃうの！\nその食感がやみつきなの！", "牧場にいるヤツらしいの\n牛とは違うらしいの\nもふもふ気持ちいいらしいから\n食感にこだわって作ったの", "もふもふにしてあげるなの！", 26);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("リンのプリン", 5, 125, 3, 0, "んむぅ…評価が難しい味なの…\nおいしいのはおいしいの…\nん～考えてたら…\n眠くなってきたの…", "いっつも寝てるやつらしいの\nぐっすり眠れる薬草をたくさん\n入れてみたの。プリンとして\nおいしくなるかはわからないの", "めんどくさいな～なの…", 27);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("ラビリスタのプリン", 5, 126, 3, 1, "プ…プリンなのにクレープの味\nがするの。味もすごく普通なの\nでも…なんだかプリンの新しい\n扉を開けそうな気がするの…！", "クレープ屋の真っ赤な人に\n教わったプリンなの\n作り方を丁寧に教えてくれて\nとっても優しい人だったの！", "プリンの権能を解放するの～！", 28);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("シズルのプリン", 5, 127, 3, 0, "とろける食感が絶品なの～♪\n食べるとなんだか幸せな\n気持ちになれるの…これが\n愛情ってやつなの？", "お店に落ちてた謎のレシピの\nプリンなの。レシピの表紙には\n『お姉ちゃんの愛情たっぷり』\nって書いてあったの", "お姉ちゃんパワー全開！なの！", 29);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("リノのプリン", 5, 128, 3, 1, "これで妹力？があがるらしいの。\n妹になればお兄ちゃんに\nプリンをたかり放題なの！\n最高なの～！", "リノから作り方を教わったの！\nさすがクレープ屋で働いてる\nだけあって美味しそうなの！", "お兄ちゃん、プリンをよこすの！", 30);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("ヒヨリのプリン", 5, 129, 3, 1, "食べたら元気が湧いてくるの\nだからプリンのレシピのお礼に\nミヤコも人助けを手伝って\nやったの。ミヤコはえらいの！", "プリンのレシピで悩んでたら\n通りすがりのおねーさんが\n一緒に考えてくれたプリンなの\nいいヤツもたまにはいるの～♪", "困った時はお互いさまさま！\nなの！", 31);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("ユイのプリン", 5, 130, 3, 1, "はっ…思わずお花畑が見えたの\n身も心も包まれるような\nとっても優しい味がするの\nこれは…まさに癒しなの～♪", "ミヤコががんばって作った\nこだわりのプリンなの～\nプリンは癒しなの。だから優しい\n気持ちで丁寧に作ったの", "フラワリングヒールなの～♪", 32);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("レイのプリン", 5, 131, 3, 1, "確かに完璧なプリンなの\nでも究極のプリンと呼ぶには\nまだまだ修行が必要なの…\nプリン作りは奥が深いの…！", "究極のプリンを作るため\nミヤコは厳しい修行をしたの…\nその結果あって完成した\nクールで完璧なプリンなの！", "スラッシュテンペスト！なの。", 33);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("アキノのプリン", 5, 132, 3, 1, "味にも素材にもこだわりが\nあるの。一流は妥協しないの\nもっと豪華に、もっとおいしく\nできるはず…", "豪華で高級な\nお嬢様プリンなの\n中には宝石みたいな\n大きいイチゴを入れるの！", "おーほっほほほ！なの！", 34);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("ユカリのプリン", 5, 133, 3, 1, "プリンは飲み物とも言うの\nぐいっていってみるの\n…ふわしゅわでとろけるの～！\n体に染みわたるの～", "外はしっかり、中はふわとろの\nプリンにするの\n泡みたいなしゅわしゅわの\n生クリームも入れるの♪", "プリしゅわ最高～！なの！", 35);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("タマキのプリン", 5, 134, 3, 1, "このプリンは焼きたてのうちに\n食べるのが一番なの…！\n熱いけどおいしくて\nうずうずしてきたの…！", "餡子を入れて\nオーブンでこんがり焼くの\nたい…焼きプリンなの\n斬新なの！", "にゃにゃにゃ！なの！", 36);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("ミフユのプリン", 5, 135, 3, 0, "プリンはいつでも手軽に\n食べられるのも魅力なの\nこれは食べ応えもあるから一つ\n食べればいっぱい動けるの", "栄養バランスを考えて\n木の実をたくさん入れるの\nあれこれ忙しいときでも\n頼りになるプリンなの！", "効率全開！なの！", 37);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("ミサトのプリン", 5, 136, 3, 1, "このプリンを食べてから\nいい子になった気がするの\n大好きなプリンを泣いてる子に\nあげたの。いい気分なの", "エルフの森で子どもにもらった\n薬草を使って作ったの\nこんな優しい味のプリンは\n初めてなの…", "あらあら～なの～♪", 38);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("ハツネのプリン", 5, 137, 3, 1, "あまいプリンが口の中で\nぽんぽん弾ける未知の味なの！\n今なら不思議な力で世界も\n救えそうなの！でも眠くなるの…", "お昼寝でみた夢の中に\nでてきた不思議なプリンを\n作ってみたの。見てたらまた\n眠くなってきたの…", "スヤスヤ～…なの…", 39);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("アオイのプリン", 5, 138, 3, 1, "知らないうちにはしっこに\n隠れちゃってたの。食べたら\nミヤコ、シノブたちに会いに\nいきたくなってきたの", "お店のはしっこに一つだけ\n残ったおもちゃを見て思い\nついたの。他のプリンと一緒に\n置いておくと震えだすの", "ぼっちは卒業なの～！", 40);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("ノゾミのプリン", 5, 139, 3, 1, "口の中に華やかな甘さが\n広がる…これがアイドルなの？\n急に歌って踊りたく\nなってきたの！", "トップオブアイドルの\nプリンなの。キラキラした\nものを入れて、あつ～い\nオーブンで焼いてみたの", "リズムあげるのー！", 41);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("チカのプリン", 5, 140, 3, 1, "プリンの生地がとっても\nなめらかで最高の舌触りなの！\n精霊、ミヤコのプリンのために…", "ミヤコにプリンをくれるすごく\nいいヤツのプリンなの\n精霊に呼びかけながら\n一生懸命かき混ぜたの", "風の加護を！なの！", 42);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("ツムギのプリン", 5, 141, 3, 1, "包装は食べるまでの手間が\n増えてめんどくさいの。でも\nそのめんどくささもプリンの\n味を引き立てるの", "歌って踊って衣装も作れるヤツ\nのプリンなの。このプリンは\nきれいに包装するの。プリンを\n最高の形で着飾らせるの", "騎士さんのために\nしたんじゃありません！なの！", 43);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("クリスティーナのプリン", 5, 142, 3, 0, "んむっ！？\nく、口のなかでプリンの\nおいしさが大暴れなの！\n止まらないの！", "プリンをくれないならどうでも\nいいヤツなの。でも横で\nいろいろ言ってきたから\nがんばって作ったの", "宴の始まりなの！", 12);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("ルカのプリン", 5, 143, 3, 1, "大人の味なの。今やミヤコは\nみんなの頼れる姐御なの！\n悪さするやつはミヤコが\n許さないの！", "大人な甘さ控えめのプリンを\n作るの。試しにお魚を入れて\nみるの。プリンの可能性は\n無限大なの！", "一刀両断、覚悟しな！なの！", 44);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("ナナカのプリン", 5, 144, 3, 1, "一口食べる度に味が変わって\n魔法みたいなの！\nもしかして全部食べたら\nミヤコも魔法少女になれるの？", "サイカワサイツヨな\n魔法のプリンなの\n中身をカラフルにするの～\n甘いプリンは頭が冴えるの！", "きゅぴーん！キタコレ！なの！", 45);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("アンナのプリン", 5, 145, 3, 1, "一口食べたら謎の組織に\n狙われたり、前世の記憶が蘇る\n気がするの～設定盛りすぎなの\nミヤコは、ミヤコは…", "小難しい言葉をささやきながら\n作ったら魔力あふれるプリンが\n爆誕したの！でも、不思議と\nイタイタしいのはなぜなの～？", "人呼んで、疾風のミヤコなの！", 46);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("エリコのプリン", 5, 146, 3, 1, "なんか普通のプリンよりも\nいい匂いがするの～…\nプリンのことがもっともっと\n好きになっちゃうの…", "甘酸っぱい赤い木の実を\n切り刻んで入れるの\n甘くてちょっぴり刺激的な\nヤンデレのプリンなの", "クスクス…クスクスクス…なの", 47);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("ミツキのプリン", 5, 147, 3, 1, "鼻に抜ける薬のにおいがアレ\nだけど大人っぽくて頭が\nよくなった気がするの。無性に\n実験したくてしかたないの", "健康に気を遣うミヤコは\nプリンにお薬を入れてみたの～\nでもなんだか毒々しいから\n彩りにバラを添えてみたの～", "どんなプリンも\nミヤコの隻眼からは\n逃れられないの～！", 48);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("モニカのプリン", 5, 148, 3, 1, "ん～王道なプリンは\n甘くて安定したおいしさなの～\n思わず笑顔になっちゃうの\nにこにこなの～♪…はっ！", "小さいけどしっかりした\n軍人のプリンなの\nちょっと固めにして\n砂糖はいっぱい入れるの", "私は子どもではない！なの！", 49);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("ニノンのプリン", 5, 149, 3, 1, "このプリンを食べてから\n誰かにお仕えしなきゃ\nいけない気がするデースなの\nそれはショ、ショー…", "オーエド町で手に入れた素材と\nトーゴクに伝わる術をかけて\nプリンを作るの。シュババ！\n忍法隠し味の術なの！", "ショーグン！　ミヤコと一緒に\n天下統一するデースなの！", 50);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("クウカのプリン", 5, 150, 3, 0, "カチカチに固めたせいで食感が\nプリンのようで\nプリンじゃないの…でもこの\nもどかしさが癖になるの…", "オマエが持ってきた手錠を\nヒントに作ったプリンなの\nプリンが動けないように\nカチカチに固めたの", "…ぐふふ、じゅるり～なの", 51);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("ユキのプリン", 5, 151, 3, 1, "味も美しい以外の感想が\n出てこないの。ていうかこれを\n食べたミヤコも美しいの！\n鏡、鏡はどこなの！", "とっても美しいプリンなの\n美しいものを手当たり次第\n入れたらすごいことになったの", "あぁ、ミヤコはなんて美しいの～", 52);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("アユミのプリン", 5, 152, 3, 0, "うん、やっぱりおいしく…\nん？あそこにいるのは\nアイツなの。あれ？\n体が勝手にアイツの方に…", "知らないうちにお菓子が売れた\n時に思いついたプリンなの。\n作り方もよく覚えてない…\nけど、きっとおいしいの！", "ジー…ってミヤコなにしてるの\nミヤコはストーカーじゃないの！", 53);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("ミソギのプリン", 5, 153, 3, 0, "見た目通り甘々で優しい味なの\nでも、レシピを見たら裏面に\n別の作り方が書いてあるの。\nこれだと激辛プリンに…", "イタズラ好きの手下と一緒に\n作ったプリンなの。見た目は\nかわいいけど、作り方は\n意外と複雑なの～", "にひひ…イタズラの時間なの！", 54);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("ミミのプリン", 5, 154, 3, 0, "ん～、ふわふわの甘々なの！\nなんだか楽しくなって\n心も体もるんたったしてくるの", "素直でやさしい手下と一緒に\n動物のぬいぐるみをいっぱい\n並べて作ったの", "ぴょんぴょこぴょーん、なの♪", 55);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("キョウカのプリン", 5, 155, 3, 0, "ひんやりしてて暑い日でも\nおいしく食べられそうなの！\nん？これを買いたい大人が\nいる？それって…", "幽霊を怖がっている手下と\n一緒に作ったプリンなの。\n小さいクセにきっちりしてて\nとってもかわいいプリンなの", "へんたいふしんしゃさんなの！", 56);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("スズナのプリン", 5, 156, 3, 1, "見て食べて楽しめる新機軸の\nプリンなの。ミヤコは幽霊\nだけど自分の足でランウェイを\n歩きたくなってきたの！", "カリスマモデルなプリンなの\nモリモリにデコレーションして\nカリスマ感を演出したの", "ハート狙い撃ち！なの！", 57);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("イオのプリン", 5, 157, 3, 0, "プ、プリンは全部ミヤコのもの\nなのに、なんだかこのプリンは\nいろんな人に教えたく\nなっちゃう不思議な味なの！", "学校の先生をしてるヤツの\nプリンなの。お堅くしようと\nしたら、いつの間にか色っぽく\nなっちゃったの。なんでなの？", "授業を始めます♪なの！", 58);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("ミサキのプリン", 5, 158, 3, 1, "ミヤコはプリンならどんなもの\nでも食べるの。でもカラメルを\nちょっとだけ足すの。\nちょっとだけなら…", "少し背伸びしたおませな子を\n表現したプリンなの。あえて\n甘さ控えめなの。カラメルは\n後乗せできるから安心するの", "立派なレディ、なの！", 59);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("ホマレのプリン", 5, 159, 3, 1, "味はすごいゴージャスなの！\nボスの風格があるの！\nビッグなミヤコに\nプリンを捧げるの！", "謎に包まれたプリンができたの\n何か隠してるようなの…\nこれは危険なプリンなの！", "プリンを持ってこないと\nお仕置きだよ～☆なの！", 60);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("カヤのプリン", 5, 160, 3, 1, "最初の一口目が\nすごいインパクトなの！\n無性にケンカしたくなるの！\nプリンをよこせなの！", "ワイルドな見た目なの！\nこれは期待できそうなの。\nきっと躍動感あふれる味なの！", "たぎってきたの！", 61);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("イノリのプリン", 5, 161, 3, 0, "見た目はアレだけど味はおいし\nいの…あと主張もはっきりして\nるの！やる気と自信だけは\n出てきたのうおおおーなの！", "なんか見た目がパッとしないの\nこれほんとに大丈夫なの？\nなんか頼りないの…", "下剋上、やってやるですなの！", 62);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("ムイミのプリン", 5, 162, 3, 1, "むむむ…食べたら\nやる気が湧いてきたの！\nミヤコを邪魔するやつはみんな\nまとめてぶっ飛ばしてやるの～", "天下の大悪党のプリンなの\nこれまでのジョーシキに\n捉われないプリンを\n作ってみたの～", "どけどけ～！なの～！", 63);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("ネネカのプリン", 5, 163, 3, 1, "いつのまにか作ったの全部\n食べちゃったの。なんで\nプリンは分身しないの？\n無限に増えろなの！", "いろんなものに変身したり\n分身したりできるヤツの\nプリンなの。型で同じプリンを\nたくさんたくさん作ったの", "ミラーミラー！なの！", 64);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("クレジッタのプリン", 5, 164, 3, 1, "むぅ…認めたくないけど\nおいしいの…これを何個も\n作れるほどのお金を稼ぐのは\n大変なの…でもおいしいから…", "金の亡者のプリンなの\n金にものを言わせて作って\nみたの。おばあちゃんの\nプリンと対極なの", "プリンに忠誠を誓いますわ！\nなの！", 65);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("カリンのプリン", 5, 165, 3, 1, "手堅い…！手堅いの…！\n隙のないプリンなの！\n今のミヤコなら完璧な\n書類作成ができそうなの！", "みんなお世話になるギルド\n管理協会職員のプリンなの\nお役所感を出すために手堅く\n王道に作ったの", "ギルド管理協会へようこそ！\nなの！", 66);
