INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("ペコリーヌのプリン", 1, 3, "やばいですね☆なの！", 100, 5, "プリンセスなプリンなの\nお店にあったいろんな食べもの\nを入れられるだけ入れてみたの\nどんな味なのか楽しみなの", "おいしすぎて食べ始めたら\n止まらない最高のプリンなの\n今のミヤコを止められるヤツは\n誰もいないの", 1);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("コッコロのプリン", 2, 3, "主さま、なの！", 101, 5, "いつもオマエのそばに寄りそう\nヤツのぷりんなの。ミヤコの\nワガママもやさしく受け入れて\nくれそうなの", "なんだかすごくやさしい気持ち\nになる味なの…\nおはようからおやすみまで\nずっと一緒な味なの…", 1);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("キャルのプリン", 3, 3, "ぶっ殺すぞ！なの！\n…デレデレはどこなの？", 102, 5, "ツンツンデレデレなヤツの\nプリンなの。砂糖とスパイスを\n混ぜてみたの。どんな味になる\nか楽しみなの", "いろんな味が一度で楽しめる\nプリンなの。ツンデレはプリン\nにするとおいしいの。ミヤコも\nツンデレを目指してみるの", 0);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("シェフィのプリン", 4, 3, "おにーたん♪なの！", 103, 5, "オマエと同じで、一度赤ちゃん\nみたいになったヤツのプリン\nなの。ヒンヤリ、プリンアイス\nにしてみたの", "あったかいところで食べる\nプリンアイスは最高なの～♪\n安心しすぎて昔の記憶のない\nミヤコも心が子どもになるの～", 1);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("イリヤのプリン", 5, 3, "ハーハッハッハ！\n世界征服なのじゃなのー！", 104, 5, "イリヤを見てたら思いついた\nトマトジュースが隠し味の\nプリンなの。栄養満点なの", "トマトで栄養補給も完璧なの\nどんどん力がみなぎってきて\n復活も夢じゃないの\nミヤコが復活したら…", 1);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("アカリのプリン", 6, 3, "お兄ちゃんのエッチ！　なの！", 105, 5, "アカリのプリンなの。ヨリと\n同じくらいかわいく作って\nやったの。正直ミヤコは\nおいしければなんでもいいの", "見た目がかわいくてもミヤコは\n味にも厳しいの。あむっ…\nこ、これは…！なんだかすごく\n変な気分になってきたの…", 1);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("ヨリのプリン", 7, 3, "えっと…その…あんたのこと…\nだ、大っ嫌いなのーー！", 106, 5, "かわいくつくらないとアカリが\n怒りそうだからヨリのプリンは\nかわいく作ったの。自信作なの", "んむぅ…これはなかなか感想が\n難しいプリンなの…率直に言い\nたいような…心のうちに秘めて\nおきたいような…", 1);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("シノブのプリン", 8, 3, "お父さん…\nいいかげんにしてなの！\nん？プリンの未来は…\n見えなかったの…", 107, 5, "シノブはいつもミヤコに\nやさしくしてくれるから\n丁寧に作ったの。ドクロ親父は\nいらないから作ってないの", "このプリンを食べるとシノブが\nミヤコの中にいるみたいなの！\nシノブの力があれば未来の\nプリンを見放題なの！", 0);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("ジュンのプリン", 9, 3, "インフェルノシールド！なの！", 108, 5, "黒い鎧を脱がないヤツのプリン\nなの。固めのプリンを目指したら\nカチカチになりすぎたの", "固すぎてかじれないから丸吞み\nにしたの。なんだか体が硬く\nなった気がするの。みんなを\n守れる気がするの！", 1);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("トモのプリン", 10, 3, "あなたといると調子が狂うな\n…なの\n翻弄してたのはオマエなの？", 109, 5, "オマエをいっつもからかってる\nヤツのプリンなの\nちょっぴり変わったスパイスで\nからかいを表現したの", "口の中でスパイスがプチプチ\nはじけて新食感なの！\nプチプチがミヤコを\n翻弄するの！", 1);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("マツリのプリン", 11, 3, "タイガーヒーローボンバー！\nなの！", 110, 5, "王宮騎士団にいるちっこいヤツ\nなの。ちっこいけどやるヤツ\nらしいから、濃いめの味付けに\nしてみたの", "ん～見かけによらず濃厚な\nパンチのある味わいが\n口いっぱいに広がるの！", 1);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("クロエのプリン", 13, 3, "ま、いいんじゃん？\nしらんけどなの", 111, 5, "なんかすごくクールなプリンなの\nなんかテンションも少し\n低そうなの。これ\n食べちゃっても大丈夫なの？", "クールな見た目なのにとっても\n優しい味なの！なんだか\n懐かしさを感じるの…", 1);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("チエルのプリン", 14, 3, "ぷりーん★なの！", 112, 5, "きれいにデコれたの！\nカラフルでおいしそうなの\nこれは期待できそうなの", "なんか変わった食感なの…\nちゃる…ちょる…ちぇる？\nちぇるってしてるの\nでもちぇるってなんなの？", 1);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("ユニのプリン", 15, 3, "端的に換言すれば\n青春の味なの？", 113, 5, "見た目は完ぺきなの！\nこれをつくったやつは\nきっと天才なの！\n早く食べたいの！", "味はなんか妙に\n甘酸っぱいの…\n食べたことない味なの\nこの味ってもしかして…", 1);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("サレンのプリン", 16, 3, "幼なじみはあんた…なの？", 114, 5, "オマエから聞いた懐かしい\n味ってヤツを全部入れてみたの\nオマエ記憶がないのに懐かしい\nってどういうことなの？", "ミヤコも幽霊になる前の記憶\nがないのに、なんだか懐かしい\n気持ちになってくるの…", 1);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("スズメのプリン", 17, 3, "ひゃああんなの～！", 115, 5, "たまたま会ったメイドに教えて\nもらった作り方なの。メイドは\nプリンも作れるはずなの。\nなのにこの不安は何なの…？", "おかしいの。このプリンを\n食べてから、３回も転んだの\nミヤコは幽霊だから転んだり\nしないの。なんでなの…？", 1);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("クルミのプリン", 18, 3, "サルキデバレヤ！なの！", 116, 5, "怖がりな子どもが教えてくれた\n小さくてかわいいプリンなの　\n触ってないのにぷるぷる震える\n驚きのやわらかさなの", "甘さのなかに少しのしょっぱさ\nクセになる味なの\nこのプリンはがんばればきっと\nいろんな美味しいになれるの", 0);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("アヤネのプリン", 19, 3, "プリンフルスイングなの～！", 117, 5, "くまのぬいぐるみを見ていたら\nひらめいたの。小さいけど\nしっかりした甘さの元気に\nなれるプリンなの！", "このプリン、食べるとなんだか\nお姉ちゃんになった気分に\nなるの。ピンチのプリンは\nミヤコが守ってやるの！", 1);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("マホのプリン", 20, 3, "みらくるみやりん\nくるりんぱ～☆なの！", 118, 5, "マホマホ王国って国にいる\nお姫様のプリンなの\n魔物も虜にしちゃう\nすてきなプリンなの", "メルヘンパワーがあふれてきて\nいまならミヤコにも魔法が\n使える気がするの！\nほな、いきまひょ～なの！", 1);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("マコトのプリン", 21, 3, "ウルフェンバイトなの！", 119, 5, "カッコいいプリンを目指して\nワイルドなものをガンガン\n入れてみたの。きっと熱い\nプリンができるはずなの", "うおーん！ミヤコなんだか\n強くなった気がするの！\n困ったことがあればミヤコが\nまとめて面倒見てやるの！", 1);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("カオリのプリン", 22, 3, "なんくるないさ～！なの！", 120, 5, "どっかの国でよくとれる\nゴーヤーに似てるやつを\nいれてみたの！ちょっと\n苦いらしいけど…", "一口食べたら元気が\nあふれて止まらないの！\n気分がいいから、一曲\n踊りでも披露してやるの！", 1);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("カスミのプリン", 23, 3, "ふふん。簡単な推理なの", 121, 5, "勘の鋭いやつが好きな食べ物を\nたくさん入れてみたの\n難しいこと考えるのにぴったり\nらしいけど、なんでなの？", "むむむ…なるほどなの\n上質な糖分をふんだんに\n使うことで、脳をより活性化\nできるからなの！", 1);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("マヒルのプリン", 24, 3, "エリザベス～！なの～！", 122, 5, "搾りたての牛乳を\nたっぷり使ったプリンなの～\nマヒルに手伝ってもらって\nミヤコ直々に搾ってきたの！", "ミルクがとっても濃厚で\nほっぺたが落ちちゃうの～♪\nこのプリンからは牧場の…\n自然の息吹を感じるの！", 1);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("シオリのプリン", 25, 3, "私の好きな本…読んで\nもらえたらうれしいです…\nなの", 123, 5, "マヒルからやさしいヤツだって\n聞いたの。やさしさを表現\nするために奥ゆかしい甘さに\n仕上げてみたの", "ほのかな甘さでありながら\nしっかりとした主張も\n感じさせる\n味わい深いプリンなの", 1);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("リマのプリン", 26, 3, "もふもふにしてあげるなの！", 124, 5, "牧場にいるヤツらしいの\n牛とは違うらしいの\nもふもふ気持ちいいらしいから\n食感にこだわって作ったの", "こ、これは…！口の中に入れた\n瞬間にふんわり溶けて\nなくなっちゃうの！\nその食感がやみつきなの！", 1);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("リンのプリン", 27, 3, "めんどくさいな～なの…", 125, 5, "いっつも寝てるやつらしいの\nぐっすり眠れる薬草をたくさん\n入れてみたの。プリンとして\nおいしくなるかはわからないの", "んむぅ…評価が難しい味なの…\nおいしいのはおいしいの…\nん～考えてたら…\n眠くなってきたの…", 0);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("ラビリスタのプリン", 28, 3, "プリンの権能を解放するの～！", 126, 5, "クレープ屋の真っ赤な人に\n教わったプリンなの\n作り方を丁寧に教えてくれて\nとっても優しい人だったの！", "プ…プリンなのにクレープの味\nがするの。味もすごく普通なの\nでも…なんだかプリンの新しい\n扉を開けそうな気がするの…！", 1);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("シズルのプリン", 29, 3, "お姉ちゃんパワー全開！なの！", 127, 5, "お店に落ちてた謎のレシピの\nプリンなの。レシピの表紙には\n『お姉ちゃんの愛情たっぷり』\nって書いてあったの", "とろける食感が絶品なの～♪\n食べるとなんだか幸せな\n気持ちになれるの…これが\n愛情ってやつなの？", 0);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("リノのプリン", 30, 3, "お兄ちゃん、プリンをよこすの！", 128, 5, "リノから作り方を教わったの！\nさすがクレープ屋で働いてる\nだけあって美味しそうなの！", "これで妹力？があがるらしいの。\n妹になればお兄ちゃんに\nプリンをたかり放題なの！\n最高なの～！", 1);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("ヒヨリのプリン", 31, 3, "困った時はお互いさまさま！\nなの！", 129, 5, "プリンのレシピで悩んでたら\n通りすがりのおねーさんが\n一緒に考えてくれたプリンなの\nいいヤツもたまにはいるの～♪", "食べたら元気が湧いてくるの\nだからプリンのレシピのお礼に\nミヤコも人助けを手伝って\nやったの。ミヤコはえらいの！", 1);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("ユイのプリン", 32, 3, "フラワリングヒールなの～♪", 130, 5, "ミヤコががんばって作った\nこだわりのプリンなの～\nプリンは癒しなの。だから優しい\n気持ちで丁寧に作ったの", "はっ…思わずお花畑が見えたの\n身も心も包まれるような\nとっても優しい味がするの\nこれは…まさに癒しなの～♪", 1);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("レイのプリン", 33, 3, "スラッシュテンペスト！なの。", 131, 5, "究極のプリンを作るため\nミヤコは厳しい修行をしたの…\nその結果あって完成した\nクールで完璧なプリンなの！", "確かに完璧なプリンなの\nでも究極のプリンと呼ぶには\nまだまだ修行が必要なの…\nプリン作りは奥が深いの…！", 1);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("アキノのプリン", 34, 3, "おーほっほほほ！なの！", 132, 5, "豪華で高級な\nお嬢様プリンなの\n中には宝石みたいな\n大きいイチゴを入れるの！", "味にも素材にもこだわりが\nあるの。一流は妥協しないの\nもっと豪華に、もっとおいしく\nできるはず…", 1);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("ユカリのプリン", 35, 3, "プリしゅわ最高～！なの！", 133, 5, "外はしっかり、中はふわとろの\nプリンにするの\n泡みたいなしゅわしゅわの\n生クリームも入れるの♪", "プリンは飲み物とも言うの\nぐいっていってみるの\n…ふわしゅわでとろけるの～！\n体に染みわたるの～", 1);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("タマキのプリン", 36, 3, "にゃにゃにゃ！なの！", 134, 5, "餡子を入れて\nオーブンでこんがり焼くの\nたい…焼きプリンなの\n斬新なの！", "このプリンは焼きたてのうちに\n食べるのが一番なの…！\n熱いけどおいしくて\nうずうずしてきたの…！", 1);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("ミフユのプリン", 37, 3, "効率全開！なの！", 135, 5, "栄養バランスを考えて\n木の実をたくさん入れるの\nあれこれ忙しいときでも\n頼りになるプリンなの！", "プリンはいつでも手軽に\n食べられるのも魅力なの\nこれは食べ応えもあるから一つ\n食べればいっぱい動けるの", 0);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("ミサトのプリン", 38, 3, "あらあら～なの～♪", 136, 5, "エルフの森で子どもにもらった\n薬草を使って作ったの\nこんな優しい味のプリンは\n初めてなの…", "このプリンを食べてから\nいい子になった気がするの\n大好きなプリンを泣いてる子に\nあげたの。いい気分なの", 1);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("ハツネのプリン", 39, 3, "スヤスヤ～…なの…", 137, 5, "お昼寝でみた夢の中に\nでてきた不思議なプリンを\n作ってみたの。見てたらまた\n眠くなってきたの…", "あまいプリンが口の中で\nぽんぽん弾ける未知の味なの！\n今なら不思議な力で世界も\n救えそうなの！でも眠くなるの…", 1);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("アオイのプリン", 40, 3, "ぼっちは卒業なの～！", 138, 5, "お店のはしっこに一つだけ\n残ったおもちゃを見て思い\nついたの。他のプリンと一緒に\n置いておくと震えだすの", "知らないうちにはしっこに\n隠れちゃってたの。食べたら\nミヤコ、シノブたちに会いに\nいきたくなってきたの", 1);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("ノゾミのプリン", 41, 3, "リズムあげるのー！", 139, 5, "トップオブアイドルの\nプリンなの。キラキラした\nものを入れて、あつ～い\nオーブンで焼いてみたの", "口の中に華やかな甘さが\n広がる…これがアイドルなの？\n急に歌って踊りたく\nなってきたの！", 1);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("チカのプリン", 42, 3, "風の加護を！なの！", 140, 5, "ミヤコにプリンをくれるすごく\nいいヤツのプリンなの\n精霊に呼びかけながら\n一生懸命かき混ぜたの", "プリンの生地がとっても\nなめらかで最高の舌触りなの！\n精霊、ミヤコのプリンのために…", 1);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("ツムギのプリン", 43, 3, "騎士さんのために\nしたんじゃありません！なの！", 141, 5, "歌って踊って衣装も作れるヤツ\nのプリンなの。このプリンは\nきれいに包装するの。プリンを\n最高の形で着飾らせるの", "包装は食べるまでの手間が\n増えてめんどくさいの。でも\nそのめんどくささもプリンの\n味を引き立てるの", 1);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("クリスティーナのプリン", 12, 3, "宴の始まりなの！", 142, 5, "プリンをくれないならどうでも\nいいヤツなの。でも横で\nいろいろ言ってきたから\nがんばって作ったの", "んむっ！？\nく、口のなかでプリンの\nおいしさが大暴れなの！\n止まらないの！", 0);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("ルカのプリン", 44, 3, "一刀両断、覚悟しな！なの！", 143, 5, "大人な甘さ控えめのプリンを\n作るの。試しにお魚を入れて\nみるの。プリンの可能性は\n無限大なの！", "大人の味なの。今やミヤコは\nみんなの頼れる姐御なの！\n悪さするやつはミヤコが\n許さないの！", 1);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("ナナカのプリン", 45, 3, "きゅぴーん！キタコレ！なの！", 144, 5, "サイカワサイツヨな\n魔法のプリンなの\n中身をカラフルにするの～\n甘いプリンは頭が冴えるの！", "一口食べる度に味が変わって\n魔法みたいなの！\nもしかして全部食べたら\nミヤコも魔法少女になれるの？", 1);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("アンナのプリン", 46, 3, "人呼んで、疾風のミヤコなの！", 145, 5, "小難しい言葉をささやきながら\n作ったら魔力あふれるプリンが\n爆誕したの！でも、不思議と\nイタイタしいのはなぜなの～？", "一口食べたら謎の組織に\n狙われたり、前世の記憶が蘇る\n気がするの～設定盛りすぎなの\nミヤコは、ミヤコは…", 1);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("エリコのプリン", 47, 3, "クスクス…クスクスクス…なの", 146, 5, "甘酸っぱい赤い木の実を\n切り刻んで入れるの\n甘くてちょっぴり刺激的な\nヤンデレのプリンなの", "なんか普通のプリンよりも\nいい匂いがするの～…\nプリンのことがもっともっと\n好きになっちゃうの…", 1);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("ミツキのプリン", 48, 3, "どんなプリンも\nミヤコの隻眼からは\n逃れられないの～！", 147, 5, "健康に気を遣うミヤコは\nプリンにお薬を入れてみたの～\nでもなんだか毒々しいから\n彩りにバラを添えてみたの～", "鼻に抜ける薬のにおいがアレ\nだけど大人っぽくて頭が\nよくなった気がするの。無性に\n実験したくてしかたないの", 1);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("モニカのプリン", 49, 3, "私は子どもではない！なの！", 148, 5, "小さいけどしっかりした\n軍人のプリンなの\nちょっと固めにして\n砂糖はいっぱい入れるの", "ん～王道なプリンは\n甘くて安定したおいしさなの～\n思わず笑顔になっちゃうの\nにこにこなの～♪…はっ！", 1);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("ニノンのプリン", 50, 3, "ショーグン！　ミヤコと一緒に\n天下統一するデースなの！", 149, 5, "オーエド町で手に入れた素材と\nトーゴクに伝わる術をかけて\nプリンを作るの。シュババ！\n忍法隠し味の術なの！", "このプリンを食べてから\n誰かにお仕えしなきゃ\nいけない気がするデースなの\nそれはショ、ショー…", 1);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("クウカのプリン", 51, 3, "…ぐふふ、じゅるり～なの", 150, 5, "オマエが持ってきた手錠を\nヒントに作ったプリンなの\nプリンが動けないように\nカチカチに固めたの", "カチカチに固めたせいで食感が\nプリンのようで\nプリンじゃないの…でもこの\nもどかしさが癖になるの…", 0);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("ユキのプリン", 52, 3, "あぁ、ミヤコはなんて美しいの～", 151, 5, "とっても美しいプリンなの\n美しいものを手当たり次第\n入れたらすごいことになったの", "味も美しい以外の感想が\n出てこないの。ていうかこれを\n食べたミヤコも美しいの！\n鏡、鏡はどこなの！", 1);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("アユミのプリン", 53, 3, "ジー…ってミヤコなにしてるの\nミヤコはストーカーじゃないの！", 152, 5, "知らないうちにお菓子が売れた\n時に思いついたプリンなの。\n作り方もよく覚えてない…\nけど、きっとおいしいの！", "うん、やっぱりおいしく…\nん？あそこにいるのは\nアイツなの。あれ？\n体が勝手にアイツの方に…", 0);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("ミソギのプリン", 54, 3, "にひひ…イタズラの時間なの！", 153, 5, "イタズラ好きの手下と一緒に\n作ったプリンなの。見た目は\nかわいいけど、作り方は\n意外と複雑なの～", "見た目通り甘々で優しい味なの\nでも、レシピを見たら裏面に\n別の作り方が書いてあるの。\nこれだと激辛プリンに…", 0);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("ミミのプリン", 55, 3, "ぴょんぴょこぴょーん、なの♪", 154, 5, "素直でやさしい手下と一緒に\n動物のぬいぐるみをいっぱい\n並べて作ったの", "ん～、ふわふわの甘々なの！\nなんだか楽しくなって\n心も体もるんたったしてくるの", 0);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("キョウカのプリン", 56, 3, "へんたいふしんしゃさんなの！", 155, 5, "幽霊を怖がっている手下と\n一緒に作ったプリンなの。\n小さいクセにきっちりしてて\nとってもかわいいプリンなの", "ひんやりしてて暑い日でも\nおいしく食べられそうなの！\nん？これを買いたい大人が\nいる？それって…", 0);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("スズナのプリン", 57, 3, "ハート狙い撃ち！なの！", 156, 5, "カリスマモデルなプリンなの\nモリモリにデコレーションして\nカリスマ感を演出したの", "見て食べて楽しめる新機軸の\nプリンなの。ミヤコは幽霊\nだけど自分の足でランウェイを\n歩きたくなってきたの！", 1);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("イオのプリン", 58, 3, "授業を始めます♪なの！", 157, 5, "学校の先生をしてるヤツの\nプリンなの。お堅くしようと\nしたら、いつの間にか色っぽく\nなっちゃったの。なんでなの？", "プ、プリンは全部ミヤコのもの\nなのに、なんだかこのプリンは\nいろんな人に教えたく\nなっちゃう不思議な味なの！", 0);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("ミサキのプリン", 59, 3, "立派なレディ、なの！", 158, 5, "少し背伸びしたおませな子を\n表現したプリンなの。あえて\n甘さ控えめなの。カラメルは\n後乗せできるから安心するの", "ミヤコはプリンならどんなもの\nでも食べるの。でもカラメルを\nちょっとだけ足すの。\nちょっとだけなら…", 1);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("ホマレのプリン", 60, 3, "プリンを持ってこないと\nお仕置きだよ～☆なの！", 159, 5, "謎に包まれたプリンができたの\n何か隠してるようなの…\nこれは危険なプリンなの！", "味はすごいゴージャスなの！\nボスの風格があるの！\nビッグなミヤコに\nプリンを捧げるの！", 1);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("カヤのプリン", 61, 3, "たぎってきたの！", 160, 5, "ワイルドな見た目なの！\nこれは期待できそうなの。\nきっと躍動感あふれる味なの！", "最初の一口目が\nすごいインパクトなの！\n無性にケンカしたくなるの！\nプリンをよこせなの！", 1);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("イノリのプリン", 62, 3, "下剋上、やってやるですなの！", 161, 5, "なんか見た目がパッとしないの\nこれほんとに大丈夫なの？\nなんか頼りないの…", "見た目はアレだけど味はおいし\nいの…あと主張もはっきりして\nるの！やる気と自信だけは\n出てきたのうおおおーなの！", 0);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("ムイミのプリン", 63, 3, "どけどけ～！なの～！", 162, 5, "天下の大悪党のプリンなの\nこれまでのジョーシキに\n捉われないプリンを\n作ってみたの～", "むむむ…食べたら\nやる気が湧いてきたの！\nミヤコを邪魔するやつはみんな\nまとめてぶっ飛ばしてやるの～", 1);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("ネネカのプリン", 64, 3, "ミラーミラー！なの！", 163, 5, "いろんなものに変身したり\n分身したりできるヤツの\nプリンなの。型で同じプリンを\nたくさんたくさん作ったの", "いつのまにか作ったの全部\n食べちゃったの。なんで\nプリンは分身しないの？\n無限に増えろなの！", 1);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("クレジッタのプリン", 65, 3, "プリンに忠誠を誓いますわ！\nなの！", 164, 5, "金の亡者のプリンなの\n金にものを言わせて作って\nみたの。おばあちゃんの\nプリンと対極なの", "むぅ…認めたくないけど\nおいしいの…これを何個も\n作れるほどのお金を稼ぐのは\n大変なの…でもおいしいから…", 1);
INSERT INTO `v1_ea57cd74bb2774fc4ad1d0583933413263f66475a371cb6d09322ab92d794abc` VALUES ("カリンのプリン", 66, 3, "ギルド管理協会へようこそ！\nなの！", 165, 5, "みんなお世話になるギルド\n管理協会職員のプリンなの\nお役所感を出すために手堅く\n王道に作ったの", "手堅い…！手堅いの…！\n隙のないプリンなの！\n今のミヤコなら完璧な\n書類作成ができそうなの！", 1);
