INSERT INTO `v1_ecad5942cd1c62918de38109dc15b8b8e861fab5270269ced46f38b6925b9a44` VALUES (0.0, "2030/12/17 14:59:59", "アヤネ", 24, 0.0, "2020/07/31 12:00:00", 10046, 2, 2, 102301, 0.0, 401, "やったね、ぷうきち！\nこれならお兄ちゃんたちの\nお手本になれたよね！", 24001);
INSERT INTO `v1_ecad5942cd1c62918de38109dc15b8b8e861fab5270269ced46f38b6925b9a44` VALUES (0.0, "2030/12/17 14:59:59", "アヤネ", 24, 2.21, "2020/07/31 12:00:00", 10046, 1, 2, 102301, 0.0, 402, "私たちの勝ちだね！\nぷうきちと一緒なら\nどんなボールでも\nホームランを打ってみせるよ！", 24002);
INSERT INTO `v1_ecad5942cd1c62918de38109dc15b8b8e861fab5270269ced46f38b6925b9a44` VALUES (0.0, "2030/12/17 14:59:59", "アヤネ", 24, 0.0, "2020/07/31 12:00:00", 10046, 2, 2, 102301, 0.0, 403, "やったやったぁ！\nお兄ちゃん、見ててくれた？\n私、すっごくがんばったよ！", 24003);
INSERT INTO `v1_ecad5942cd1c62918de38109dc15b8b8e861fab5270269ced46f38b6925b9a44` VALUES (0.0, "2030/12/17 14:59:59", "アサヒ", 24, 1.99, "2020/07/31 12:00:00", 10046, 2, 1, 192201, 0.0, 701, "特訓の成果が出たみたいだ！\nもっと練習を重ねて\nこの調子で上達していこう！", 24004);
INSERT INTO `v1_ecad5942cd1c62918de38109dc15b8b8e861fab5270269ced46f38b6925b9a44` VALUES (0.0, "2030/12/17 14:59:59", "アサヒ", 24, 2.21, "2020/07/31 12:00:00", 10046, 1, 2, 192201, 0.0, 702, "よし、いい感じだ！\nキャプテンとしての仕事は\nきっちり果たせそうだよ", 24005);
INSERT INTO `v1_ecad5942cd1c62918de38109dc15b8b8e861fab5270269ced46f38b6925b9a44` VALUES (0.0, "2030/12/17 14:59:59", "アサヒ", 24, 3.33, "2020/07/31 12:00:00", 10046, 2, 1, 192201, 0.0, 703, "試合に向けて\nいい感覚を掴めた気がするよ！\nこのまま一気に全国大会だ！", 24006);
INSERT INTO `v1_ecad5942cd1c62918de38109dc15b8b8e861fab5270269ced46f38b6925b9a44` VALUES (0.0, "2030/12/17 14:59:59", "部員３", 24, 0.0, "2020/07/31 12:00:00", 10046, 2, 2, 192301, 0.0, 801, "このくらい余裕だ\nオレたちは全国大会を\n目指してるんだからな", 24007);
INSERT INTO `v1_ecad5942cd1c62918de38109dc15b8b8e861fab5270269ced46f38b6925b9a44` VALUES (0.0, "2030/12/17 14:59:59", "部員３", 24, 2.68, "2020/07/31 12:00:00", 10046, 1, 2, 192301, 0.0, 802, "オレだって\nビーチベースボール選手だ\nどんな球だろうが\n全部スタンドまで飛ばしてやる", 24008);
INSERT INTO `v1_ecad5942cd1c62918de38109dc15b8b8e861fab5270269ced46f38b6925b9a44` VALUES (0.0, "2030/12/17 14:59:59", "部員３", 24, 0.0, "2020/07/31 12:00:00", 10046, 2, 2, 192301, 0.0, 803, "いい感じだ\nこれなら試合でも活躍できそうだ\n大暴れしてやる！", 24009);
INSERT INTO `v1_ecad5942cd1c62918de38109dc15b8b8e861fab5270269ced46f38b6925b9a44` VALUES (0.0, "2030/12/17 14:59:59", "", 24, 0.0, "2020/07/31 12:00:00", 10046, 1, 1, 192501, 0.0, 501, "やった！", 24010);
INSERT INTO `v1_ecad5942cd1c62918de38109dc15b8b8e861fab5270269ced46f38b6925b9a44` VALUES (0.0, "2030/12/17 14:59:59", "", 24, 0.0, "2020/07/31 12:00:00", 10046, 1, 1, 192501, 0.0, 502, "勝ったよ！", 24011);
INSERT INTO `v1_ecad5942cd1c62918de38109dc15b8b8e861fab5270269ced46f38b6925b9a44` VALUES (0.0, "2030/12/17 14:59:59", "", 24, 0.0, "2020/07/31 12:00:00", 10046, 1, 1, 192501, 0.0, 503, "いい調子！", 24012);
INSERT INTO `v1_ecad5942cd1c62918de38109dc15b8b8e861fab5270269ced46f38b6925b9a44` VALUES (0.0, "2030/12/17 14:59:59", "ミサト", 24, 5.33, "2020/07/31 12:00:00", 10046, 6, 2, 113501, 0.0, 111, "あら～…もしかして\n勝っちゃったのかしら？\nみんなの練習を見ていたから\nかもしれないわね", 24013);
INSERT INTO `v1_ecad5942cd1c62918de38109dc15b8b8e861fab5270269ced46f38b6925b9a44` VALUES (0.0, "2030/12/17 14:59:59", "ミサト", 24, 6.85, "2020/07/31 12:00:00", 10046, 1, 2, 113501, 0.0, 112, "みんなの力になりたくて\n先生も頑張ってみました～！\nうまく投げられたみたいで\nよかったわ～！", 24014);
INSERT INTO `v1_ecad5942cd1c62918de38109dc15b8b8e861fab5270269ced46f38b6925b9a44` VALUES (0.0, "2030/12/17 14:59:59", "ミサト", 24, 3.0, "2020/07/31 12:00:00", 10046, 1, 2, 113501, 0.0, 113, "練習お疲れさま～！\n先生も協力できて\nとってもうれしいわ～♪\n次も頑張りましょうね～！", 24015);
INSERT INTO `v1_ecad5942cd1c62918de38109dc15b8b8e861fab5270269ced46f38b6925b9a44` VALUES (0.0, "2030/12/17 14:59:59", "ダイチ", 24, 6.11, "2020/07/31 12:00:00", 10046, 1, 2, 192401, 0.0, 611, "まだ練習が足りねえみてえだな\nこれっぽっちも本気で\n投げてねえっつーの\nもう一回やんぞ！", 24016);
INSERT INTO `v1_ecad5942cd1c62918de38109dc15b8b8e861fab5270269ced46f38b6925b9a44` VALUES (0.0, "2030/12/17 14:59:59", "ダイチ", 24, 6.75, "2020/07/31 12:00:00", 10046, 1, 2, 192401, 0.0, 612, "ふん、当然の結果だな\n俺の球は、誰にも打てねえよ！\nまたやりてえっつーなら\nとことん付き合ってやるぜ！", 24017);
INSERT INTO `v1_ecad5942cd1c62918de38109dc15b8b8e861fab5270269ced46f38b6925b9a44` VALUES (0.0, "2030/12/17 14:59:59", "ダイチ", 24, 0.0, "2020/07/31 12:00:00", 10046, 2, 2, 192401, 0.0, 613, "おっしゃあ！\nこの勢いで全国に行ってやるぜ！\n全員三振で仕留めてやる！", 24018);
INSERT INTO `v1_ecad5942cd1c62918de38109dc15b8b8e861fab5270269ced46f38b6925b9a44` VALUES (0.0, "2030/12/17 14:59:59", "ハツネ", 24, 4.52, "2020/07/31 12:00:00", 10046, 2, 1, 113401, 0.0, 211, "きらーん☆\nハツネちゃんの大勝利なのだ～！\nみんながもっと上達できるように\n私もまだまだ頑張るからね！", 24019);
INSERT INTO `v1_ecad5942cd1c62918de38109dc15b8b8e861fab5270269ced46f38b6925b9a44` VALUES (0.0, "2030/12/17 14:59:59", "ハツネ", 24, 3.89, "2020/07/31 12:00:00", 10046, 2, 1, 113401, 0.0, 212, "やったあ！　私の勝ちだね！\nチアリーダーもピッチャーも\nハツネちゃんに任せてよ！", 24020);
INSERT INTO `v1_ecad5942cd1c62918de38109dc15b8b8e861fab5270269ced46f38b6925b9a44` VALUES (0.0, "2030/12/17 14:59:59", "ハツネ", 24, 13.49, "2020/07/31 12:00:00", 10046, 1, 2, 113401, 0.0, 213, "よお～し、この調子でもっと\n頑張ろう…と思ったけど\n超能力を使ったから…\n眠くなってきちゃった…すやぁ…", 24021);
INSERT INTO `v1_ecad5942cd1c62918de38109dc15b8b8e861fab5270269ced46f38b6925b9a44` VALUES (0.0, "2030/12/17 14:59:59", "カヤ", 24, 3.17, "2020/07/31 12:00:00", 10046, 1, 2, 106501, 0.0, 311, "いい勝負だったぜ！\nたぎっちまって\nつい本気で投げちまったよ", 24022);
INSERT INTO `v1_ecad5942cd1c62918de38109dc15b8b8e861fab5270269ced46f38b6925b9a44` VALUES (0.0, "2030/12/17 14:59:59", "カヤ", 24, 4.42, "2020/07/31 12:00:00", 10046, 1, 2, 106501, 0.0, 312, "どうだ？\nオレ様のボールは熱かっただろ！\nま、でもおまえらの根性も\nなかなかのもんだったぜ", 24023);
INSERT INTO `v1_ecad5942cd1c62918de38109dc15b8b8e861fab5270269ced46f38b6925b9a44` VALUES (0.0, "2030/12/17 14:59:59", "カヤ", 24, 2.89, "2020/07/31 12:00:00", 10046, 6, 1, 106501, 0.0, 313, "なんだよ、そんなもんか？\nこっちはまだまだ投げたりねえ\nもっと勝負しようぜ！", 24024);
INSERT INTO `v1_ecad5942cd1c62918de38109dc15b8b8e861fab5270269ced46f38b6925b9a44` VALUES (0.0, "2030/12/17 14:59:59", "アヤネ", 24, 3.71, "2020/07/31 12:00:00", 10046, 4, 3, 102301, 0.0, 421, "あとちょっとだったのに～！\n次こそぷうきちと一緒に\n勝ってみせるからね！", 24025);
INSERT INTO `v1_ecad5942cd1c62918de38109dc15b8b8e861fab5270269ced46f38b6925b9a44` VALUES (0.0, "2030/12/17 14:59:59", "アサヒ", 24, 5.1, "2020/07/31 12:00:00", 10046, 2, 1, 192201, 0.0, 721, "いい勝負だったね！\nでも、やっぱり悔しいな…\nもっと練習して\n今度は絶対に俺が勝つから！", 24026);
INSERT INTO `v1_ecad5942cd1c62918de38109dc15b8b8e861fab5270269ced46f38b6925b9a44` VALUES (0.0, "2030/12/17 14:59:59", "部員３", 24, 4.34, "2020/07/31 12:00:00", 10046, 1, 3, 192301, 0.0, 821, "ちっ…あと一歩足りなかったか\n次こそは絶対に勝つから\nそのつもりでいろ", 24027);
INSERT INTO `v1_ecad5942cd1c62918de38109dc15b8b8e861fab5270269ced46f38b6925b9a44` VALUES (0.0, "2030/12/17 14:59:59", "", 24, 0.0, "2020/07/31 12:00:00", 10046, 1, 1, 192501, 0.0, 521, "いい勝負だったよ\nまたやろうね", 24028);
INSERT INTO `v1_ecad5942cd1c62918de38109dc15b8b8e861fab5270269ced46f38b6925b9a44` VALUES (-360.0, "2030/12/17 14:59:59", "ミサト", 24, 5.62, "2020/07/31 12:00:00", 10046, 1, 2, 113501, 0.0, 101, "とってもよくできました～♪\n頑張ってくれて先生うれしいわ\nこれからも試合に向けて\n練習していきましょうね～", 24029);
INSERT INTO `v1_ecad5942cd1c62918de38109dc15b8b8e861fab5270269ced46f38b6925b9a44` VALUES (-360.0, "2030/12/17 14:59:59", "ミサト", 24, 9.58, "2020/07/31 12:00:00", 10046, 1, 2, 113501, 0.0, 102, "頑張った成果ね～！\nこのまま練習を続ければ\nもっとすごい成績を残せそうよ\n期待してますからね～！", 24030);
INSERT INTO `v1_ecad5942cd1c62918de38109dc15b8b8e861fab5270269ced46f38b6925b9a44` VALUES (-360.0, "2030/12/17 14:59:59", "ミサト", 24, 0.0, "2020/07/31 12:00:00", 10046, 2, 2, 113501, 0.0, 103, "いい子、いい子～♪\n頑張り屋さんのあなたを\nたっくさん褒めてあげますからね", 24031);
INSERT INTO `v1_ecad5942cd1c62918de38109dc15b8b8e861fab5270269ced46f38b6925b9a44` VALUES (-360.0, "2030/12/17 14:59:59", "ミサト", 24, 9.13, "2020/07/31 12:00:00", 10046, 2, 1, 113501, 0.0, 104, "夢に向けて、一歩前進できたわね\nこの調子で、夢を叶えましょう\n目指せ、全国大会～！", 24032);
INSERT INTO `v1_ecad5942cd1c62918de38109dc15b8b8e861fab5270269ced46f38b6925b9a44` VALUES (-360.0, "2030/12/17 14:59:59", "ミサト", 24, 8.27, "2020/07/31 12:00:00", 10046, 1, 2, 113501, 0.0, 105, "絶好調ね～♪\nきっと試合でも大活躍\n間違いなしだわ\n楽しみにしていますからね～！", 24033);
