INSERT INTO `v1_ecad5942cd1c62918de38109dc15b8b8e861fab5270269ced46f38b6925b9a44` VALUES (0.0, "2030/12/17 14:59:59", 2, 24, "やったね、ぷうきち！\nこれならお兄ちゃんたちの\nお手本になれたよね！", 102301, 24001, "アヤネ", "2020/07/31 12:00:00", 0.0, 401, 0.0, 2, 10046);
INSERT INTO `v1_ecad5942cd1c62918de38109dc15b8b8e861fab5270269ced46f38b6925b9a44` VALUES (2.21, "2030/12/17 14:59:59", 2, 24, "私たちの勝ちだね！\nぷうきちと一緒なら\nどんなボールでも\nホームランを打ってみせるよ！", 102301, 24002, "アヤネ", "2020/07/31 12:00:00", 0.0, 402, 0.0, 1, 10046);
