INSERT INTO `v1_ecad5942cd1c62918de38109dc15b8b8e861fab5270269ced46f38b6925b9a44` VALUES (3.33, "2030/12/17 14:59:59", 1, 24, "試合に向けて\nいい感覚を掴めた気がするよ！\nこのまま一気に全国大会だ！", 192201, 24006, "アサヒ", "2020/07/31 12:00:00", 0.0, 703, 0.0, 2, 10046);
