INSERT INTO `v1_ecad5942cd1c62918de38109dc15b8b8e861fab5270269ced46f38b6925b9a44` VALUES (6.85, "2030/12/17 14:59:59", 2, 24, "みんなの力になりたくて\n先生も頑張ってみました～！\nうまく投げられたみたいで\nよかったわ～！", 113501, 24014, "ミサト", "2020/07/31 12:00:00", 0.0, 112, 0.0, 1, 10046);
