INSERT INTO `v1_ecad5942cd1c62918de38109dc15b8b8e861fab5270269ced46f38b6925b9a44` VALUES (0.0, "2030/12/17 14:59:59", 2, 24, "おっしゃあ！\nこの勢いで全国に行ってやるぜ！\n全員三振で仕留めてやる！", 192401, 24018, "ダイチ", "2020/07/31 12:00:00", 0.0, 613, 0.0, 2, 10046);
