INSERT INTO `v1_ecad5942cd1c62918de38109dc15b8b8e861fab5270269ced46f38b6925b9a44` VALUES (13.49, "2030/12/17 14:59:59", 2, 24, "よお～し、この調子でもっと\n頑張ろう…と思ったけど\n超能力を使ったから…\n眠くなってきちゃった…すやぁ…", 113401, 24021, "ハツネ", "2020/07/31 12:00:00", 0.0, 213, 0.0, 1, 10046);
