INSERT INTO `v1_ecad5942cd1c62918de38109dc15b8b8e861fab5270269ced46f38b6925b9a44` VALUES (401, "2020/07/31 12:00:00", 0.0, 0.0, 102301, "2030/12/17 14:59:59", 24, 2, 24001, 0.0, 2, "アヤネ", "やったね、ぷうきち！\nこれならお兄ちゃんたちの\nお手本になれたよね！", 10046);
INSERT INTO `v1_ecad5942cd1c62918de38109dc15b8b8e861fab5270269ced46f38b6925b9a44` VALUES (402, "2020/07/31 12:00:00", 0.0, 0.0, 102301, "2030/12/17 14:59:59", 24, 1, 24002, 2.21, 2, "アヤネ", "私たちの勝ちだね！\nぷうきちと一緒なら\nどんなボールでも\nホームランを打ってみせるよ！", 10046);
INSERT INTO `v1_ecad5942cd1c62918de38109dc15b8b8e861fab5270269ced46f38b6925b9a44` VALUES (403, "2020/07/31 12:00:00", 0.0, 0.0, 102301, "2030/12/17 14:59:59", 24, 2, 24003, 0.0, 2, "アヤネ", "やったやったぁ！\nお兄ちゃん、見ててくれた？\n私、すっごくがんばったよ！", 10046);
INSERT INTO `v1_ecad5942cd1c62918de38109dc15b8b8e861fab5270269ced46f38b6925b9a44` VALUES (701, "2020/07/31 12:00:00", 0.0, 0.0, 192201, "2030/12/17 14:59:59", 24, 2, 24004, 1.99, 1, "アサヒ", "特訓の成果が出たみたいだ！\nもっと練習を重ねて\nこの調子で上達していこう！", 10046);
INSERT INTO `v1_ecad5942cd1c62918de38109dc15b8b8e861fab5270269ced46f38b6925b9a44` VALUES (702, "2020/07/31 12:00:00", 0.0, 0.0, 192201, "2030/12/17 14:59:59", 24, 1, 24005, 2.21, 2, "アサヒ", "よし、いい感じだ！\nキャプテンとしての仕事は\nきっちり果たせそうだよ", 10046);
INSERT INTO `v1_ecad5942cd1c62918de38109dc15b8b8e861fab5270269ced46f38b6925b9a44` VALUES (703, "2020/07/31 12:00:00", 0.0, 0.0, 192201, "2030/12/17 14:59:59", 24, 2, 24006, 3.33, 1, "アサヒ", "試合に向けて\nいい感覚を掴めた気がするよ！\nこのまま一気に全国大会だ！", 10046);
INSERT INTO `v1_ecad5942cd1c62918de38109dc15b8b8e861fab5270269ced46f38b6925b9a44` VALUES (801, "2020/07/31 12:00:00", 0.0, 0.0, 192301, "2030/12/17 14:59:59", 24, 2, 24007, 0.0, 2, "部員３", "このくらい余裕だ\nオレたちは全国大会を\n目指してるんだからな", 10046);
INSERT INTO `v1_ecad5942cd1c62918de38109dc15b8b8e861fab5270269ced46f38b6925b9a44` VALUES (802, "2020/07/31 12:00:00", 0.0, 0.0, 192301, "2030/12/17 14:59:59", 24, 1, 24008, 2.68, 2, "部員３", "オレだって\nビーチベースボール選手だ\nどんな球だろうが\n全部スタンドまで飛ばしてやる", 10046);
INSERT INTO `v1_ecad5942cd1c62918de38109dc15b8b8e861fab5270269ced46f38b6925b9a44` VALUES (803, "2020/07/31 12:00:00", 0.0, 0.0, 192301, "2030/12/17 14:59:59", 24, 2, 24009, 0.0, 2, "部員３", "いい感じだ\nこれなら試合でも活躍できそうだ\n大暴れしてやる！", 10046);
INSERT INTO `v1_ecad5942cd1c62918de38109dc15b8b8e861fab5270269ced46f38b6925b9a44` VALUES (501, "2020/07/31 12:00:00", 0.0, 0.0, 192501, "2030/12/17 14:59:59", 24, 1, 24010, 0.0, 1, "", "やった！", 10046);
INSERT INTO `v1_ecad5942cd1c62918de38109dc15b8b8e861fab5270269ced46f38b6925b9a44` VALUES (502, "2020/07/31 12:00:00", 0.0, 0.0, 192501, "2030/12/17 14:59:59", 24, 1, 24011, 0.0, 1, "", "勝ったよ！", 10046);
INSERT INTO `v1_ecad5942cd1c62918de38109dc15b8b8e861fab5270269ced46f38b6925b9a44` VALUES (503, "2020/07/31 12:00:00", 0.0, 0.0, 192501, "2030/12/17 14:59:59", 24, 1, 24012, 0.0, 1, "", "いい調子！", 10046);
INSERT INTO `v1_ecad5942cd1c62918de38109dc15b8b8e861fab5270269ced46f38b6925b9a44` VALUES (111, "2020/07/31 12:00:00", 0.0, 0.0, 113501, "2030/12/17 14:59:59", 24, 6, 24013, 5.33, 2, "ミサト", "あら～…もしかして\n勝っちゃったのかしら？\nみんなの練習を見ていたから\nかもしれないわね", 10046);
INSERT INTO `v1_ecad5942cd1c62918de38109dc15b8b8e861fab5270269ced46f38b6925b9a44` VALUES (112, "2020/07/31 12:00:00", 0.0, 0.0, 113501, "2030/12/17 14:59:59", 24, 1, 24014, 6.85, 2, "ミサト", "みんなの力になりたくて\n先生も頑張ってみました～！\nうまく投げられたみたいで\nよかったわ～！", 10046);
INSERT INTO `v1_ecad5942cd1c62918de38109dc15b8b8e861fab5270269ced46f38b6925b9a44` VALUES (113, "2020/07/31 12:00:00", 0.0, 0.0, 113501, "2030/12/17 14:59:59", 24, 1, 24015, 3.0, 2, "ミサト", "練習お疲れさま～！\n先生も協力できて\nとってもうれしいわ～♪\n次も頑張りましょうね～！", 10046);
INSERT INTO `v1_ecad5942cd1c62918de38109dc15b8b8e861fab5270269ced46f38b6925b9a44` VALUES (611, "2020/07/31 12:00:00", 0.0, 0.0, 192401, "2030/12/17 14:59:59", 24, 1, 24016, 6.11, 2, "ダイチ", "まだ練習が足りねえみてえだな\nこれっぽっちも本気で\n投げてねえっつーの\nもう一回やんぞ！", 10046);
INSERT INTO `v1_ecad5942cd1c62918de38109dc15b8b8e861fab5270269ced46f38b6925b9a44` VALUES (612, "2020/07/31 12:00:00", 0.0, 0.0, 192401, "2030/12/17 14:59:59", 24, 1, 24017, 6.75, 2, "ダイチ", "ふん、当然の結果だな\n俺の球は、誰にも打てねえよ！\nまたやりてえっつーなら\nとことん付き合ってやるぜ！", 10046);
INSERT INTO `v1_ecad5942cd1c62918de38109dc15b8b8e861fab5270269ced46f38b6925b9a44` VALUES (613, "2020/07/31 12:00:00", 0.0, 0.0, 192401, "2030/12/17 14:59:59", 24, 2, 24018, 0.0, 2, "ダイチ", "おっしゃあ！\nこの勢いで全国に行ってやるぜ！\n全員三振で仕留めてやる！", 10046);
INSERT INTO `v1_ecad5942cd1c62918de38109dc15b8b8e861fab5270269ced46f38b6925b9a44` VALUES (211, "2020/07/31 12:00:00", 0.0, 0.0, 113401, "2030/12/17 14:59:59", 24, 2, 24019, 4.52, 1, "ハツネ", "きらーん☆\nハツネちゃんの大勝利なのだ～！\nみんながもっと上達できるように\n私もまだまだ頑張るからね！", 10046);
INSERT INTO `v1_ecad5942cd1c62918de38109dc15b8b8e861fab5270269ced46f38b6925b9a44` VALUES (212, "2020/07/31 12:00:00", 0.0, 0.0, 113401, "2030/12/17 14:59:59", 24, 2, 24020, 3.89, 1, "ハツネ", "やったあ！　私の勝ちだね！\nチアリーダーもピッチャーも\nハツネちゃんに任せてよ！", 10046);
INSERT INTO `v1_ecad5942cd1c62918de38109dc15b8b8e861fab5270269ced46f38b6925b9a44` VALUES (213, "2020/07/31 12:00:00", 0.0, 0.0, 113401, "2030/12/17 14:59:59", 24, 1, 24021, 13.49, 2, "ハツネ", "よお～し、この調子でもっと\n頑張ろう…と思ったけど\n超能力を使ったから…\n眠くなってきちゃった…すやぁ…", 10046);
INSERT INTO `v1_ecad5942cd1c62918de38109dc15b8b8e861fab5270269ced46f38b6925b9a44` VALUES (311, "2020/07/31 12:00:00", 0.0, 0.0, 106501, "2030/12/17 14:59:59", 24, 1, 24022, 3.17, 2, "カヤ", "いい勝負だったぜ！\nたぎっちまって\nつい本気で投げちまったよ", 10046);
INSERT INTO `v1_ecad5942cd1c62918de38109dc15b8b8e861fab5270269ced46f38b6925b9a44` VALUES (312, "2020/07/31 12:00:00", 0.0, 0.0, 106501, "2030/12/17 14:59:59", 24, 1, 24023, 4.42, 2, "カヤ", "どうだ？\nオレ様のボールは熱かっただろ！\nま、でもおまえらの根性も\nなかなかのもんだったぜ", 10046);
INSERT INTO `v1_ecad5942cd1c62918de38109dc15b8b8e861fab5270269ced46f38b6925b9a44` VALUES (313, "2020/07/31 12:00:00", 0.0, 0.0, 106501, "2030/12/17 14:59:59", 24, 6, 24024, 2.89, 1, "カヤ", "なんだよ、そんなもんか？\nこっちはまだまだ投げたりねえ\nもっと勝負しようぜ！", 10046);
INSERT INTO `v1_ecad5942cd1c62918de38109dc15b8b8e861fab5270269ced46f38b6925b9a44` VALUES (421, "2020/07/31 12:00:00", 0.0, 0.0, 102301, "2030/12/17 14:59:59", 24, 4, 24025, 3.71, 3, "アヤネ", "あとちょっとだったのに～！\n次こそぷうきちと一緒に\n勝ってみせるからね！", 10046);
INSERT INTO `v1_ecad5942cd1c62918de38109dc15b8b8e861fab5270269ced46f38b6925b9a44` VALUES (721, "2020/07/31 12:00:00", 0.0, 0.0, 192201, "2030/12/17 14:59:59", 24, 2, 24026, 5.1, 1, "アサヒ", "いい勝負だったね！\nでも、やっぱり悔しいな…\nもっと練習して\n今度は絶対に俺が勝つから！", 10046);
INSERT INTO `v1_ecad5942cd1c62918de38109dc15b8b8e861fab5270269ced46f38b6925b9a44` VALUES (821, "2020/07/31 12:00:00", 0.0, 0.0, 192301, "2030/12/17 14:59:59", 24, 1, 24027, 4.34, 3, "部員３", "ちっ…あと一歩足りなかったか\n次こそは絶対に勝つから\nそのつもりでいろ", 10046);
INSERT INTO `v1_ecad5942cd1c62918de38109dc15b8b8e861fab5270269ced46f38b6925b9a44` VALUES (521, "2020/07/31 12:00:00", 0.0, 0.0, 192501, "2030/12/17 14:59:59", 24, 1, 24028, 0.0, 1, "", "いい勝負だったよ\nまたやろうね", 10046);
INSERT INTO `v1_ecad5942cd1c62918de38109dc15b8b8e861fab5270269ced46f38b6925b9a44` VALUES (101, "2020/07/31 12:00:00", -360.0, 0.0, 113501, "2030/12/17 14:59:59", 24, 1, 24029, 5.62, 2, "ミサト", "とってもよくできました～♪\n頑張ってくれて先生うれしいわ\nこれからも試合に向けて\n練習していきましょうね～", 10046);
