INSERT INTO `v1_ecad5942cd1c62918de38109dc15b8b8e861fab5270269ced46f38b6925b9a44` VALUES (2, 401, 2, 102301, 24001, 10046, "アヤネ", "やったね、ぷうきち！\nこれならお兄ちゃんたちの\nお手本になれたよね！", 24, 0.0, 0.0, "2030/12/17 14:59:59", 0.0, "2020/07/31 12:00:00");
INSERT INTO `v1_ecad5942cd1c62918de38109dc15b8b8e861fab5270269ced46f38b6925b9a44` VALUES (2, 402, 1, 102301, 24002, 10046, "アヤネ", "私たちの勝ちだね！\nぷうきちと一緒なら\nどんなボールでも\nホームランを打ってみせるよ！", 24, 2.21, 0.0, "2030/12/17 14:59:59", 0.0, "2020/07/31 12:00:00");
INSERT INTO `v1_ecad5942cd1c62918de38109dc15b8b8e861fab5270269ced46f38b6925b9a44` VALUES (2, 403, 2, 102301, 24003, 10046, "アヤネ", "やったやったぁ！\nお兄ちゃん、見ててくれた？\n私、すっごくがんばったよ！", 24, 0.0, 0.0, "2030/12/17 14:59:59", 0.0, "2020/07/31 12:00:00");
INSERT INTO `v1_ecad5942cd1c62918de38109dc15b8b8e861fab5270269ced46f38b6925b9a44` VALUES (1, 701, 2, 192201, 24004, 10046, "アサヒ", "特訓の成果が出たみたいだ！\nもっと練習を重ねて\nこの調子で上達していこう！", 24, 1.99, 0.0, "2030/12/17 14:59:59", 0.0, "2020/07/31 12:00:00");
INSERT INTO `v1_ecad5942cd1c62918de38109dc15b8b8e861fab5270269ced46f38b6925b9a44` VALUES (2, 702, 1, 192201, 24005, 10046, "アサヒ", "よし、いい感じだ！\nキャプテンとしての仕事は\nきっちり果たせそうだよ", 24, 2.21, 0.0, "2030/12/17 14:59:59", 0.0, "2020/07/31 12:00:00");
INSERT INTO `v1_ecad5942cd1c62918de38109dc15b8b8e861fab5270269ced46f38b6925b9a44` VALUES (1, 703, 2, 192201, 24006, 10046, "アサヒ", "試合に向けて\nいい感覚を掴めた気がするよ！\nこのまま一気に全国大会だ！", 24, 3.33, 0.0, "2030/12/17 14:59:59", 0.0, "2020/07/31 12:00:00");
INSERT INTO `v1_ecad5942cd1c62918de38109dc15b8b8e861fab5270269ced46f38b6925b9a44` VALUES (2, 801, 2, 192301, 24007, 10046, "部員３", "このくらい余裕だ\nオレたちは全国大会を\n目指してるんだからな", 24, 0.0, 0.0, "2030/12/17 14:59:59", 0.0, "2020/07/31 12:00:00");
INSERT INTO `v1_ecad5942cd1c62918de38109dc15b8b8e861fab5270269ced46f38b6925b9a44` VALUES (2, 802, 1, 192301, 24008, 10046, "部員３", "オレだって\nビーチベースボール選手だ\nどんな球だろうが\n全部スタンドまで飛ばしてやる", 24, 2.68, 0.0, "2030/12/17 14:59:59", 0.0, "2020/07/31 12:00:00");
INSERT INTO `v1_ecad5942cd1c62918de38109dc15b8b8e861fab5270269ced46f38b6925b9a44` VALUES (2, 803, 2, 192301, 24009, 10046, "部員３", "いい感じだ\nこれなら試合でも活躍できそうだ\n大暴れしてやる！", 24, 0.0, 0.0, "2030/12/17 14:59:59", 0.0, "2020/07/31 12:00:00");
INSERT INTO `v1_ecad5942cd1c62918de38109dc15b8b8e861fab5270269ced46f38b6925b9a44` VALUES (1, 501, 1, 192501, 24010, 10046, "", "やった！", 24, 0.0, 0.0, "2030/12/17 14:59:59", 0.0, "2020/07/31 12:00:00");
INSERT INTO `v1_ecad5942cd1c62918de38109dc15b8b8e861fab5270269ced46f38b6925b9a44` VALUES (1, 502, 1, 192501, 24011, 10046, "", "勝ったよ！", 24, 0.0, 0.0, "2030/12/17 14:59:59", 0.0, "2020/07/31 12:00:00");
INSERT INTO `v1_ecad5942cd1c62918de38109dc15b8b8e861fab5270269ced46f38b6925b9a44` VALUES (1, 503, 1, 192501, 24012, 10046, "", "いい調子！", 24, 0.0, 0.0, "2030/12/17 14:59:59", 0.0, "2020/07/31 12:00:00");
INSERT INTO `v1_ecad5942cd1c62918de38109dc15b8b8e861fab5270269ced46f38b6925b9a44` VALUES (2, 111, 6, 113501, 24013, 10046, "ミサト", "あら～…もしかして\n勝っちゃったのかしら？\nみんなの練習を見ていたから\nかもしれないわね", 24, 5.33, 0.0, "2030/12/17 14:59:59", 0.0, "2020/07/31 12:00:00");
INSERT INTO `v1_ecad5942cd1c62918de38109dc15b8b8e861fab5270269ced46f38b6925b9a44` VALUES (2, 112, 1, 113501, 24014, 10046, "ミサト", "みんなの力になりたくて\n先生も頑張ってみました～！\nうまく投げられたみたいで\nよかったわ～！", 24, 6.85, 0.0, "2030/12/17 14:59:59", 0.0, "2020/07/31 12:00:00");
INSERT INTO `v1_ecad5942cd1c62918de38109dc15b8b8e861fab5270269ced46f38b6925b9a44` VALUES (2, 113, 1, 113501, 24015, 10046, "ミサト", "練習お疲れさま～！\n先生も協力できて\nとってもうれしいわ～♪\n次も頑張りましょうね～！", 24, 3.0, 0.0, "2030/12/17 14:59:59", 0.0, "2020/07/31 12:00:00");
INSERT INTO `v1_ecad5942cd1c62918de38109dc15b8b8e861fab5270269ced46f38b6925b9a44` VALUES (2, 611, 1, 192401, 24016, 10046, "ダイチ", "まだ練習が足りねえみてえだな\nこれっぽっちも本気で\n投げてねえっつーの\nもう一回やんぞ！", 24, 6.11, 0.0, "2030/12/17 14:59:59", 0.0, "2020/07/31 12:00:00");
INSERT INTO `v1_ecad5942cd1c62918de38109dc15b8b8e861fab5270269ced46f38b6925b9a44` VALUES (2, 612, 1, 192401, 24017, 10046, "ダイチ", "ふん、当然の結果だな\n俺の球は、誰にも打てねえよ！\nまたやりてえっつーなら\nとことん付き合ってやるぜ！", 24, 6.75, 0.0, "2030/12/17 14:59:59", 0.0, "2020/07/31 12:00:00");
INSERT INTO `v1_ecad5942cd1c62918de38109dc15b8b8e861fab5270269ced46f38b6925b9a44` VALUES (2, 613, 2, 192401, 24018, 10046, "ダイチ", "おっしゃあ！\nこの勢いで全国に行ってやるぜ！\n全員三振で仕留めてやる！", 24, 0.0, 0.0, "2030/12/17 14:59:59", 0.0, "2020/07/31 12:00:00");
INSERT INTO `v1_ecad5942cd1c62918de38109dc15b8b8e861fab5270269ced46f38b6925b9a44` VALUES (1, 211, 2, 113401, 24019, 10046, "ハツネ", "きらーん☆\nハツネちゃんの大勝利なのだ～！\nみんながもっと上達できるように\n私もまだまだ頑張るからね！", 24, 4.52, 0.0, "2030/12/17 14:59:59", 0.0, "2020/07/31 12:00:00");
INSERT INTO `v1_ecad5942cd1c62918de38109dc15b8b8e861fab5270269ced46f38b6925b9a44` VALUES (1, 212, 2, 113401, 24020, 10046, "ハツネ", "やったあ！　私の勝ちだね！\nチアリーダーもピッチャーも\nハツネちゃんに任せてよ！", 24, 3.89, 0.0, "2030/12/17 14:59:59", 0.0, "2020/07/31 12:00:00");
INSERT INTO `v1_ecad5942cd1c62918de38109dc15b8b8e861fab5270269ced46f38b6925b9a44` VALUES (2, 213, 1, 113401, 24021, 10046, "ハツネ", "よお～し、この調子でもっと\n頑張ろう…と思ったけど\n超能力を使ったから…\n眠くなってきちゃった…すやぁ…", 24, 13.49, 0.0, "2030/12/17 14:59:59", 0.0, "2020/07/31 12:00:00");
INSERT INTO `v1_ecad5942cd1c62918de38109dc15b8b8e861fab5270269ced46f38b6925b9a44` VALUES (2, 311, 1, 106501, 24022, 10046, "カヤ", "いい勝負だったぜ！\nたぎっちまって\nつい本気で投げちまったよ", 24, 3.17, 0.0, "2030/12/17 14:59:59", 0.0, "2020/07/31 12:00:00");
INSERT INTO `v1_ecad5942cd1c62918de38109dc15b8b8e861fab5270269ced46f38b6925b9a44` VALUES (2, 312, 1, 106501, 24023, 10046, "カヤ", "どうだ？\nオレ様のボールは熱かっただろ！\nま、でもおまえらの根性も\nなかなかのもんだったぜ", 24, 4.42, 0.0, "2030/12/17 14:59:59", 0.0, "2020/07/31 12:00:00");
INSERT INTO `v1_ecad5942cd1c62918de38109dc15b8b8e861fab5270269ced46f38b6925b9a44` VALUES (1, 313, 6, 106501, 24024, 10046, "カヤ", "なんだよ、そんなもんか？\nこっちはまだまだ投げたりねえ\nもっと勝負しようぜ！", 24, 2.89, 0.0, "2030/12/17 14:59:59", 0.0, "2020/07/31 12:00:00");
INSERT INTO `v1_ecad5942cd1c62918de38109dc15b8b8e861fab5270269ced46f38b6925b9a44` VALUES (3, 421, 4, 102301, 24025, 10046, "アヤネ", "あとちょっとだったのに～！\n次こそぷうきちと一緒に\n勝ってみせるからね！", 24, 3.71, 0.0, "2030/12/17 14:59:59", 0.0, "2020/07/31 12:00:00");
INSERT INTO `v1_ecad5942cd1c62918de38109dc15b8b8e861fab5270269ced46f38b6925b9a44` VALUES (1, 721, 2, 192201, 24026, 10046, "アサヒ", "いい勝負だったね！\nでも、やっぱり悔しいな…\nもっと練習して\n今度は絶対に俺が勝つから！", 24, 5.1, 0.0, "2030/12/17 14:59:59", 0.0, "2020/07/31 12:00:00");
INSERT INTO `v1_ecad5942cd1c62918de38109dc15b8b8e861fab5270269ced46f38b6925b9a44` VALUES (3, 821, 1, 192301, 24027, 10046, "部員３", "ちっ…あと一歩足りなかったか\n次こそは絶対に勝つから\nそのつもりでいろ", 24, 4.34, 0.0, "2030/12/17 14:59:59", 0.0, "2020/07/31 12:00:00");
INSERT INTO `v1_ecad5942cd1c62918de38109dc15b8b8e861fab5270269ced46f38b6925b9a44` VALUES (1, 521, 1, 192501, 24028, 10046, "", "いい勝負だったよ\nまたやろうね", 24, 0.0, 0.0, "2030/12/17 14:59:59", 0.0, "2020/07/31 12:00:00");
INSERT INTO `v1_ecad5942cd1c62918de38109dc15b8b8e861fab5270269ced46f38b6925b9a44` VALUES (2, 101, 1, 113501, 24029, 10046, "ミサト", "とってもよくできました～♪\n頑張ってくれて先生うれしいわ\nこれからも試合に向けて\n練習していきましょうね～", 24, 5.62, -360.0, "2030/12/17 14:59:59", 0.0, "2020/07/31 12:00:00");
INSERT INTO `v1_ecad5942cd1c62918de38109dc15b8b8e861fab5270269ced46f38b6925b9a44` VALUES (2, 102, 1, 113501, 24030, 10046, "ミサト", "頑張った成果ね～！\nこのまま練習を続ければ\nもっとすごい成績を残せそうよ\n期待してますからね～！", 24, 9.58, -360.0, "2030/12/17 14:59:59", 0.0, "2020/07/31 12:00:00");
INSERT INTO `v1_ecad5942cd1c62918de38109dc15b8b8e861fab5270269ced46f38b6925b9a44` VALUES (2, 103, 2, 113501, 24031, 10046, "ミサト", "いい子、いい子～♪\n頑張り屋さんのあなたを\nたっくさん褒めてあげますからね", 24, 0.0, -360.0, "2030/12/17 14:59:59", 0.0, "2020/07/31 12:00:00");
INSERT INTO `v1_ecad5942cd1c62918de38109dc15b8b8e861fab5270269ced46f38b6925b9a44` VALUES (1, 104, 2, 113501, 24032, 10046, "ミサト", "夢に向けて、一歩前進できたわね\nこの調子で、夢を叶えましょう\n目指せ、全国大会～！", 24, 9.13, -360.0, "2030/12/17 14:59:59", 0.0, "2020/07/31 12:00:00");
INSERT INTO `v1_ecad5942cd1c62918de38109dc15b8b8e861fab5270269ced46f38b6925b9a44` VALUES (2, 105, 1, 113501, 24033, 10046, "ミサト", "絶好調ね～♪\nきっと試合でも大活躍\n間違いなしだわ\n楽しみにしていますからね～！", 24, 8.27, -360.0, "2030/12/17 14:59:59", 0.0, "2020/07/31 12:00:00");
