INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1, 1, 1, 100101, 1, 1, 0, "ここに困ってる人は\nいないよね…？\nよかった♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2, 1, 1, 100101, 2, 1, 0, "冒険の準備準備…\nうん、忘れ物はなし！\n…あっ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3, 1, 1, 100101, 3, 1, 0, "リズムを意識して…\nわんつーわんつー！\nうん、調子はバッチリ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4, 1, 1, 100101, 4, 1, 0, "ここで特訓していい？\n物は壊さないように\n気をつけるね！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5, 1, 1, 100101, 5, 1, 0, "なんか落ち着かないや\nもしかして、\n困ってる人が\n近くにいるのかな…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6, 1, 4, 100101, 101, 1, 0, "えへへ…いつも\n一緒にいてくれて\nありがとねっ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7, 1, 8, 100101, 102, 1, 0, "ちゃんと覚えてるよ\n騎士くんの声…\nあの時はありがとね！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8, 1, 1, 100101, 1, 2, 0, "あははっ！\nやったなー！\nお返しだー！\nこちょこちょこちょ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9, 1, 1, 100101, 2, 2, 0, "どうしたの？\nもしかして困ってる？\nあたしにまかせて！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (10, 1, 1, 100101, 3, 2, 0, "えっとね…しっぽは\n触っちゃダメだよ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (11, 1, 1, 100101, 4, 2, 0, "反応しない特訓…\nだったけど無理～！\n悔しいよぉ～！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (12, 1, 4, 100101, 101, 2, 0, "ふぁふぃ？\nやられた～！　ほっぺ\nぷにぷにしないでぇ～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (13, 1, 8, 100101, 102, 2, 0, "えへへ…\n騎士くんの手だ…\nあったかいな…♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (14, 1, 1, 100101, 1, 2, 0, "おはよー！\n今日も元気に\nいっちゃうよー！", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (15, 1, 1, 100101, 2, 2, 0, "ユイちゃんに\nおやつ作って\nもらおうかな～♪", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (16, 1, 1, 100101, 3, 2, 0, "寝るのも修行だって\nこないだレイさんが\n言ってたんだ…", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (17, 1, 1, 100101, 1, 3, 0, "ちゃんとケアして\n傷が残らないように\nしないと…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (18, 1, 1, 100101, 2, 3, 0, "一回休むと\nこのままずるずる\nいきそう…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (19, 1, 1, 100101, 3, 3, 0, "新しい練習相手に…\nって冗談だよ冗談！\n本気じゃないから！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (20, 1, 1, 100101, 4, 3, 0, "落ち着くなぁ～\n心だけじゃなくて\n体もほぐれそう♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (21, 1, 1, 100101, 5, 3, 0, "あったかい所に\nいると体が丸く\nなっちゃう…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (22, 1, 1, 100101, 6, 3, 0, "いつ何が起きるか\n分からないし、\nもっと力をつけないと", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (23, 1, 1, 100101, 7, 3, 0, "美味しいけど、\n食べ過ぎないように\nしないと…！！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (24, 1, 1, 100101, 8, 3, 0, "文字が多いと\n眠くなっちゃう…\n危ない危ない！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (26, 1, 1, 100102, 1, 1, 0, "リズムを意識して…\nわんつーわんつー！\nうん、調子はバッチリ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (27, 1, 1, 100102, 2, 1, 0, "のんびりするのって\n退屈かも…\n何かして遊ばない？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (28, 1, 1, 100102, 3, 1, 0, "留守番はちょっと\n苦手かも…\nなんか寂しいんだもん", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (29, 1, 1, 100102, 4, 1, 0, "あたしのお友達も\n呼んできていい？\nきっと楽しいよ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (30, 1, 1, 100102, 5, 1, 0, "誰も困ってないよね？\n…何をすれば\nいいのか分かんないや", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (31, 1, 8, 100102, 101, 1, 0, "ちゃんと覚えてるよ\n騎士くんの声…\nあの時はありがとね！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (32, 1, 8, 100102, 102, 1, 0, "…なんであたし\n騎士くんばっかり\n見てるんだろ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (33, 1, 1, 100102, 1, 2, 0, "反応しない特訓…\nだったけど無理～！\n悔しいよぉ～！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (34, 1, 1, 100102, 2, 2, 0, "触るのって楽しい？\n…そっか\n少しだけだよ…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (35, 1, 1, 100102, 3, 2, 0, "あたしの元気を\nわけてあげるね！\nフレー！　フレー！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (36, 1, 1, 100102, 4, 2, 0, "何かムズムズする！？\nって騎士くんかぁー\nどしたの？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (37, 1, 8, 100102, 101, 2, 0, "ひゃうっ！　もう…\nしっぽはだめだってば\n次やったらデコピン！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (38, 1, 8, 100102, 102, 2, 0, "えへへ…\n騎士くんの手だ…\nあったかいな…♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (39, 1, 1, 100102, 1, 2, 0, "朝ごはんと運動！\nこれがあたしの\n元気の素だよ！", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (40, 1, 1, 100102, 2, 2, 0, "ユイちゃんに\nおやつ作って\nもらおうかな～♪", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (41, 1, 1, 100102, 3, 2, 0, "寝るのも修行だって\nこないだレイさんが\n言ってたんだ…", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (42, 1, 1, 100102, 1, 3, 0, "これでよし♪\n騎士くん\n気づくかな…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (43, 1, 1, 100102, 2, 3, 0, "一回休むと\nこのままずるずる\nいきそう…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (44, 1, 1, 100102, 3, 3, 0, "新しい練習相手に…\nって冗談だよ冗談！\n本気じゃないから！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (45, 1, 1, 100102, 4, 3, 0, "こういうの\nなんか気になって\nじーって見ちゃうんだ", 0);
