INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (48, 1, 1, 100102, 7, 3, 0, "お腹が減ると\nなんか力が\n出ないんだよね…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (49, 1, 1, 100102, 8, 3, 0, "色んなことを\n知っておくのは\n大事だよね！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (51, 1, 1, 100201, 1, 1, 0, "お片付けも済んだし\n今度は洗濯ものを\nたたもうかな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (52, 1, 1, 100201, 2, 1, 0, "この気持ち\nもっとちゃんと\n伝えなくっちゃ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (53, 1, 1, 100201, 3, 1, 0, "お茶をいれようと\n思うんだけど\n他に飲みたい人は\nいるかな？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (54, 1, 1, 100201, 4, 1, 0, "戦いは\n得意じゃないけど\nお料理なら…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (55, 1, 1, 100201, 5, 1, 0, "こういう平和な\n時間が、もっと\n続くといいな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (56, 1, 4, 100201, 101, 1, 0, "わたし…\nもうちょっとだけ勇気\n出してみようかな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (57, 1, 8, 100201, 102, 1, 0, "みんなのことが大好き\nも、もちろん、\n騎士クンのことも…\nな、仲間として！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (58, 1, 1, 100201, 1, 2, 0, "ひゃうっ！\nな、なに？\nびっくりしたぁ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (59, 1, 1, 100201, 2, 2, 0, "男の子って、\nやっぱりおっきいね。\nその…手とか\n他にもいろいろ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (60, 1, 1, 100201, 3, 2, 0, "困ったことが\nあったら\nいつでも話してね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (61, 1, 1, 100201, 4, 2, 0, "え！　む、虫っ！？\n取って取って！\nはやく～！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (62, 1, 4, 100201, 101, 2, 0, "あのね…\n一緒におでかけ\nしたいんだけど…\nダメかな？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (63, 1, 8, 100201, 102, 2, 0, "騎士クンに\n伝えたいことがあるの\nとっても大切な…\nこ、今度、話すね！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (64, 1, 1, 100201, 1, 2, 0, "お、おはよう！\nあの…朝ごはんは\nもう食べちゃった？", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (65, 1, 1, 100201, 2, 2, 0, "お昼休みだね\nあの…良かったら\nお弁当…食べない？", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (66, 1, 1, 100201, 3, 2, 0, "遅くまでお疲れ様\n明日も頑張ろうね\nふわぁ…", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (67, 1, 1, 100201, 1, 3, 0, "よし…\nこれなら\n褒めてもらえるかな？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (68, 1, 1, 100201, 2, 3, 0, "がんばることも\n必要だけど、\nお休みも大切だよね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (69, 1, 1, 100201, 3, 3, 0, "かわいいなぁ…\nわたしも今度\n作ってみよう。\nえっと、材料は…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (70, 1, 1, 100201, 4, 3, 0, "戦う前にもこうやって\n落ち着けたら\nいいのに", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (71, 1, 1, 100201, 5, 3, 0, "…はへっ？\nも、もしかして今\n寝ちゃってた…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (72, 1, 1, 100201, 6, 3, 0, "騎士クンと一緒に\n冒険するなら\n体力がないと…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (73, 1, 1, 100201, 7, 3, 0, "おいしいものって\n食べ過ぎちゃうけど…\nしょうがない…よね？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (74, 1, 1, 100201, 8, 3, 0, "…そっか\nこうすれば魔法が\nもっと強くなるんだ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (76, 1, 1, 100202, 1, 1, 0, "洗濯と掃除\nどっちから\nはじめよう？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (77, 1, 1, 100202, 2, 1, 0, "新しい魔道書を\n買いに行かなくちゃ\nもっとたくさん\n魔法を覚えたいな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (78, 1, 1, 100202, 3, 1, 0, "お茶をいれようと\n思うんだけど\n他に飲みたい人は\nいるかな？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (79, 1, 1, 100202, 4, 1, 0, "今日はどんな料理に\n挑戦しよう…♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (80, 1, 1, 100202, 5, 1, 0, "この気持ち\nもっとちゃんと\n伝えなくっちゃ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (81, 1, 8, 100202, 101, 1, 0, "みんなのことが大好き\nも、もちろん\n騎士クンのことも…\nな、仲間として！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (82, 1, 8, 100202, 102, 1, 0, "騎士クンのそばに\nいられるだけで\n幸せだよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (83, 1, 1, 100202, 1, 2, 0, "えっと、その…\nこんな所で\nこういうことされると\n恥ずかしいよ…", 0);
