INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (85, 1, 1, 100202, 3, 2, 0, "あ、あの…実は\n騎士クンのこと…\nううん、やっぱり\nなんでもない", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (86, 1, 1, 100202, 4, 2, 0, "男の子って\nやっぱりおっきいね\nその…手とか\n他にもいろいろ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (87, 1, 8, 100202, 101, 2, 0, "騎士クンに\n伝えたいことがあるの\nとっても大切な…\nこ、今度、話すね！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (88, 1, 8, 100202, 102, 2, 0, "ふふふっ…そんなとこ\nツンツンしないで\nくすぐったいよ～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (89, 1, 1, 100202, 1, 2, 0, "おはよう\nよく眠れた？　今日も\n一日、頑張ろうね♪", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (90, 1, 1, 100202, 2, 2, 0, "お腹空いちゃった…\nそうだ。騎士クンも\n一緒にお昼食べない？", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (91, 1, 1, 100202, 3, 2, 0, "もうこんな時間…\nそろそろ寝なくちゃ\nおやすみなさい", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (92, 1, 1, 100202, 1, 3, 0, "よし…\nこれなら\n褒めてもらえるかな？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (93, 1, 1, 100202, 2, 3, 0, "がんばることも\n必要だけど\nお休みも大切だよね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (94, 1, 1, 100202, 3, 3, 0, "かわいい～♪\n騎士クンは\nこういうのって好き？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (95, 1, 1, 100202, 4, 3, 0, "戦う前にもこうやって\n落ち着けたら\nいいのに", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (96, 1, 1, 100202, 5, 3, 0, "あの…！\nよ、良かったら\n一緒に…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (97, 1, 1, 100202, 6, 3, 0, "騎士クンと一緒に\n冒険するなら\n体力がないと…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (98, 1, 1, 100202, 7, 3, 0, "最近服のサイズが\n合わないんだけど…\n食べ過ぎなのかな？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (99, 1, 1, 100202, 8, 3, 0, "…やっぱり\n月刊恋愛指南は\nためになる…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (101, 1, 1, 100301, 1, 1, 0, "装備の手入れでも\nしようかな…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (102, 1, 1, 100301, 2, 1, 0, "次に鍛えるべきは\n肉体面か精神面か\nどちらにしよう…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (103, 1, 1, 100301, 3, 1, 0, "ツムギが作ってくれた\nこの服…一体\nいつサイズを…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (104, 1, 1, 100301, 4, 1, 0, "仲間なんていらない\nそう思ってた頃も\nあったな…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (105, 1, 1, 100301, 5, 1, 0, "考えごとをしていると\nつい部屋中を\n歩いてしまうな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (106, 1, 4, 100301, 101, 1, 0, "彼は\nどこまで強くなるのか\n私も負けられないな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (107, 1, 8, 100301, 102, 1, 0, "私が一本を取られる\nなんてね…願い事は\nもう決まったのかな？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (108, 1, 1, 100301, 1, 2, 0, "ああ、キミだったのか\n人の気配がしたから\n誰かと思ったよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (109, 1, 1, 100301, 2, 2, 0, "斬りかかるつもりなら\nちゃんとかかってきて\n…呼んだだけ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (110, 1, 1, 100301, 3, 2, 0, "なに？　私に相談？\n戦いについてなら\n喜んで", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (111, 1, 1, 100301, 4, 2, 0, "今のが突き？\n軽い！　もっと鋭く！\nさあ、もう一回！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (112, 1, 4, 100301, 101, 2, 0, "ひゃっ！　ど、どこを\n触ってるの！？\n…油断した", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (113, 1, 8, 100301, 102, 2, 0, "キミにはこれからも\n私のそばに…\nいや、なんでもない", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (114, 1, 1, 100301, 1, 2, 0, "おはよう\n今日も充実した\n一日にしていこう", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (115, 1, 1, 100301, 2, 2, 0, "そろそろ\nお茶の時間か\nキミも一緒にどう？", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (116, 1, 1, 100301, 3, 2, 0, "遅くまで大変だね\nまだ仕事があるなら\n手伝おうか？", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (117, 1, 1, 100301, 1, 3, 0, "戦いを経てもなお\n美しくあるように…\nこれでよし♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (118, 1, 1, 100301, 2, 3, 0, "戦ってばかりで\n休まないと、また\nユイに怒られるな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (119, 1, 1, 100301, 3, 3, 0, "ヒヨリみたいに\n練習相手に…\nいや、やめておこう", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (120, 1, 1, 100301, 4, 3, 0, "これはいい…\n戦いの前の精神統一に\nもってこいじゃないか", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (121, 1, 1, 100301, 5, 3, 0, "時々は落ち着いた\n時間を過ごすのも\n悪くない…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (122, 1, 1, 100301, 6, 3, 0, "彼に負けないよう\n私も更に強くなろう\n共に戦うために！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (123, 1, 1, 100301, 7, 3, 0, "食事は大切だけど\n食べ過ぎには\n注意しておこう…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (124, 1, 1, 100301, 8, 3, 0, "ユイ…！\nこ、こんな本を\n読んでいたのか…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (126, 1, 1, 100302, 1, 1, 0, "海だけじゃなくて\n川でも釣りがしたいな\n道具も新調しよう", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (127, 1, 1, 100302, 2, 1, 0, "ツムギが作ってくれた\nこの服…一体\nいつサイズを…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (128, 1, 1, 100302, 3, 1, 0, "彼は\nどこまで強くなるのか\n私も負けられないな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (129, 1, 1, 100302, 4, 1, 0, "そろそろ修行の\n内容を新しくしよう\nより厳しいものにね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (130, 1, 1, 100302, 5, 1, 0, "強くなって…誰にも\n妨げられない自由な\n生き方がしたい", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (131, 1, 8, 100302, 101, 1, 0, "まさか風邪を引くとは\n彼の看病のおかげで\nすっかり良く\nなったけど", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (132, 1, 8, 100302, 102, 1, 0, "私が一本を取られる\nなんてね…願い事は\nもう決まったのかな？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (133, 1, 1, 100302, 1, 2, 0, "どうしたの？\nなんでもない？\nそれなら良かった", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (134, 1, 1, 100302, 2, 2, 0, "あんまり突っつくと\nお返しするよ？\n覚悟は出来てる？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (135, 1, 1, 100302, 3, 2, 0, "ねぇ、今度また\n釣りに行かない？\n大きいのを狙おう", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (136, 1, 1, 100302, 4, 2, 0, "修行がしたいの？\nそれを早く言ってよ\nすぐに出発しよう！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (137, 1, 8, 100302, 101, 2, 0, "ひょっとして\n今のも一本だなんて\n言わないよね？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (138, 1, 8, 100302, 102, 2, 0, "キミにはこれからも\n私のそばに…\nいや、なんでもない", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (139, 1, 1, 100302, 1, 2, 0, "今日は仕事？\nそれとも修行？\n良ければ付き合うよ", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (140, 1, 1, 100302, 2, 2, 0, "休息も時には\n必要だと、仲間から\n教わったよ", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (141, 1, 1, 100302, 3, 2, 0, "遅くまで大変だね\nまだ仕事があるなら\n手伝おうか？", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (142, 1, 1, 100302, 1, 3, 0, "髪の長さを少し\n変えてみようかな…\nわ、私は何を…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (143, 1, 1, 100302, 2, 3, 0, "休息があってこそ\n万全な状態で\n戦えるんだ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (144, 1, 1, 100302, 3, 3, 0, "ヒヨリみたいに\n練習相手に…\nいや、やめておこう", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (145, 1, 1, 100302, 4, 3, 0, "体と同様に\n心もほぐれるね…\nはぁ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (146, 1, 1, 100302, 5, 3, 0, "休む時は休む\nヒヨリもそろそろ\n覚えて欲しいな…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (147, 1, 1, 100302, 6, 3, 0, "彼に負けないよう\n私も更に強くなろう\n共に戦うために！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (148, 1, 1, 100302, 7, 3, 0, "修行のせいか\n最近食べる量が\n増えてきたような…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (149, 1, 1, 100302, 8, 3, 0, "ユイ…！\nこ、こんな本を\n読んでいたのか…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (151, 1, 1, 100401, 1, 1, 0, "お宝の予感！\n部屋中を\n探検しちゃうぞ～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (152, 1, 1, 100401, 2, 1, 0, "落とし穴…どこに\n作ったんだっけ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (153, 1, 1, 100401, 3, 1, 0, "うえ～ん！\n…嘘泣きしたら\n相手は油断するかな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (154, 1, 1, 100401, 4, 1, 0, "ハニートラップって\nすごく甘いお菓子で\nイタズラするのかな？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (155, 1, 1, 100401, 5, 1, 0, "かくれんぼする人\nこの指、と～まれ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (156, 1, 4, 100401, 101, 1, 0, "ミソギ以外の人に\nイタズラされちゃ\nダメだよ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (157, 1, 8, 100401, 102, 1, 0, "ミソギと遊ぶ？\nミソギに遊ばれる？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (158, 1, 1, 100401, 1, 2, 0, "いいこと思いついた！\nい、イタズラとかじゃ\nないよ～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (159, 1, 1, 100401, 2, 2, 0, "うわっ！？\n　にいちゃんが、\nミソギにイタズラ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (160, 1, 1, 100401, 3, 2, 0, "にいちゃんの\n通りそうなところに\nバナナの皮を…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (161, 1, 1, 100401, 4, 2, 0, "にいちゃんのオデコと\nホッペと鼻と目と耳に\n落書きを…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (162, 1, 4, 100401, 101, 2, 0, "ここ、ミソギ探検隊の\n基地にしようよ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (163, 1, 8, 100401, 102, 2, 0, "だ～れだ…って？\nにいちゃんの声くらい\nわかるよ！\n大好きだもん♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (164, 1, 1, 100401, 1, 2, 0, "にいちゃん、おはよ！\n目覚まし時計を\n止めておいたのに、\nよく起きられたね～♪", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (165, 1, 1, 100401, 2, 2, 0, "にいちゃんが\n昼寝したら、\nイタズラし放題♪", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (166, 1, 1, 100401, 3, 2, 0, "夜の探検も\nおもしろそう！\nねぇ出かけちゃダメ？", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (167, 1, 1, 100401, 1, 3, 0, "イタズラには\nお色気も必要…\nうん、完璧！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (168, 1, 1, 100401, 2, 3, 0, "座ったら、ブーッって\nいう音が出たら、\nおもしろいな～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (169, 1, 1, 100401, 3, 3, 0, "かわいい！\nミソギ探検隊の\nマスコットにしよう♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (170, 1, 1, 100401, 4, 3, 0, "吸うとくしゃみが\n止まらなくなるお香を\nここに…くしゅっ！", 0);
