INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (258, 1, 1, 100701, 1, 2, 0, "姿を消して、オマエを\n見張ってやるの～\nむふふ～♪", 0);
