INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (279, 1, 1, 100702, 4, 1, 0, "買ってきたプリンには\n全部ミヤコの名前を\n書いておくの～", 0);
