INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (302, 1, 1, 100801, 2, 1, 0, "う～ん、この部屋には\nボクの肖像画や\n彫像が必要だね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (303, 1, 1, 100801, 3, 1, 0, "美の女神と愛の女神が\nボクを作る時、本気\n出しちゃったんだね♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (304, 1, 1, 100801, 4, 1, 0, "ボクの誕生とともに\n全ての美しさは過去の\n遺物になったんだよ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (305, 1, 1, 100801, 5, 1, 0, "うわっ昨日より可愛く\nなってる。明日は\nどうなっちゃう\nのかな？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (306, 1, 4, 100801, 101, 1, 0, "このボクがいるだけで\nあの人への\nおもてなしだよね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (307, 1, 8, 100801, 102, 1, 0, "ボクの可愛さは、\nふふ…あの人の\nためのものさ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (308, 1, 1, 100801, 1, 2, 0, "ねぇ～、ボクって\n可愛い？\nふふ、知ってる☆", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (309, 1, 1, 100801, 2, 2, 0, "呼んだ？\n可愛いボクと話したい\n気持ちはわかるよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (310, 1, 1, 100801, 3, 2, 0, "ふふ、ボクに触ると\nキミも美しく\nなっちゃうかもよ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (311, 1, 1, 100801, 4, 2, 0, "どうかな、\n今日のボク。\n美しい？　可愛い？\nねえ、どっち？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (312, 1, 4, 100801, 101, 2, 0, "あの人も今、\n可愛いボクのこと\n考えてるかな…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (313, 1, 8, 100801, 102, 2, 0, "美しい…\nキミの瞳に写るボクは\nより一層きれいだ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (314, 1, 1, 100801, 1, 2, 0, "美しいボクの\n美しい一日が\n始まるよ！", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (315, 1, 1, 100801, 2, 2, 0, "さーて、午後からも\n可愛く美しく\n華麗に優雅に♪", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (316, 1, 1, 100801, 3, 2, 0, "バラの花びら\nいっぱい浮かべた\nお風呂に入ってから\n寝ようっと♪", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (317, 1, 1, 100801, 1, 3, 0, "鏡よ、鏡。この世で\n一番可愛いボクに\nお似合いの人はだれ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (318, 1, 1, 100801, 2, 3, 0, "ボクが座ったソファは\n「美しいソファ」に\nランクアップした！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (319, 1, 1, 100801, 3, 3, 0, "わあ、可愛い！\n…まあ、ボクほど\nじゃないけどさ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (320, 1, 1, 100801, 4, 3, 0, "服を羽織るように\n香りをまとうボク…\n美しい、美しすぎる！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (321, 1, 1, 100801, 5, 3, 0, "気安く触ったら\n火傷する…\n炎はボクに似ているね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (322, 1, 1, 100801, 6, 3, 0, "運動は苦手なのに\n運動するボクの姿は\n可愛いなんて…罪♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (323, 1, 1, 100801, 7, 3, 0, "ボクが食べると、\n食べ物まで\n可愛いなあ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (324, 1, 1, 100801, 8, 3, 0, "ボクが触った瞬間に、\nこの本は美を\n携えてしまうんだ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (326, 1, 1, 100802, 1, 1, 0, "ボクの美しさは\nみんなのために\nあるんだよ！", 0);
