INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (329, 1, 1, 100802, 4, 1, 0, "ボクの誕生とともに\n全ての美しさは過去の\n遺物になったんだよ！", 0);
