INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (337, 1, 8, 100802, 101, 2, 0, "呼んだ？\n可愛いボクと話したい\n気持ちはわかるよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (338, 1, 8, 100802, 102, 2, 0, "美しい…\nキミの瞳に写るボクは\nより一層綺麗だ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (339, 1, 1, 100802, 1, 2, 0, "朝日と共に\nボクの美しさも\n周りを照らすよ", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (340, 1, 1, 100802, 2, 2, 0, "ボクの美しさを\n維持するために\nお昼ご飯食べなくちゃ", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (341, 1, 1, 100802, 3, 2, 0, "バラの花びらいっぱい\n浮かべたお風呂に\n入ってから\n寝ようっと♪", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (342, 1, 1, 100802, 1, 3, 0, "鏡よ、鏡。この世で\n一番可愛いボクに\nお似合いの人はだれ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (343, 1, 1, 100802, 2, 3, 0, "ボクが座ったソファは\n「美しいソファ」に\nランクアップした！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (344, 1, 1, 100802, 3, 3, 0, "可愛いものを\n可愛いって言える\nボクって可愛いよね！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (345, 1, 1, 100802, 4, 3, 0, "服を羽織るように\n香りをまとうボク…\n美しい、美しすぎる！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (346, 1, 1, 100802, 5, 3, 0, "冷えは美容の大敵\nなんだから！\nここ、ボクの場所ね！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (347, 1, 1, 100802, 6, 3, 0, "運動は苦手なのに\n運動するボクの姿は\n可愛いなんて…罪♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (348, 1, 1, 100802, 7, 3, 0, "ボクが食べると\n食べ物まで\n可愛いなあ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (349, 1, 1, 100802, 8, 3, 0, "ボクが触った瞬間に\nこの本は美を\n携えてしまうんだ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (351, 1, 1, 100901, 1, 1, 0, "「第１０部２０章\n終焉戦争、開幕」\n…これでよし！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (352, 1, 1, 100901, 2, 1, 0, "今度シグルドに\n私の魔法を一つ\n伝授してやるか", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (353, 1, 1, 100901, 3, 1, 0, "ここでは風が\n吹いてこないな…\n換気したい！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (354, 1, 1, 100901, 4, 1, 0, "そうだ、ナナカに\nこの間借りた本を\n返さないと…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (355, 1, 1, 100901, 5, 1, 0, "まずは包帯をとって\n手から龍の形の\n炎を出して…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (356, 1, 4, 100901, 101, 1, 0, "シグルド…\n今頃何処で何を\nしているのだろう…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (357, 1, 8, 100901, 102, 1, 0, "最近、彼の姿が\nまぶしく見える…\nどうして…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (358, 1, 1, 100901, 1, 2, 0, "辞書を知らないか？\n…探している\n理由は聞くな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (359, 1, 1, 100901, 2, 2, 0, "そうだ！\n貴様の技にも\n名前をつけてやろう！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (360, 1, 1, 100901, 3, 2, 0, "私に触れても力を\n奪われないのは\n貴様くらいだぞ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (361, 1, 1, 100901, 4, 2, 0, "何事だ？\nまさか我が瞳に\n魅入られたのか？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (362, 1, 4, 100901, 101, 2, 0, "貴様がそばにいると\nとても安心できる…\n…不思議だ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (363, 1, 8, 100901, 102, 2, 0, "い、いきなり目の前に\n現れるな！　ドキドキ\nするだろうがぁ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (364, 1, 1, 100901, 1, 2, 0, "「覚醒」めたか…\n行くぞ、悪を討つ\n戦いに出発だ！", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (365, 1, 1, 100901, 2, 2, 0, "昼間は「ヤツら」も\n襲ってはこない…\n束の間の休息だ", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (366, 1, 1, 100901, 3, 2, 0, "どうした？　夜はまだ\nこれから…ふぁ…\nね、眠くなどないぞ！", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (367, 1, 1, 100901, 1, 3, 0, "何か足りないな…\n暗黒騎士の紋章を\n作ろうかな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (368, 1, 1, 100901, 2, 3, 0, "くっ…！\n私を眠りの国へ\n誘うつもりか！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (369, 1, 1, 100901, 3, 3, 0, "このようなものに\n懐柔されるものか！\n…されるものか！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (370, 1, 1, 100901, 4, 3, 0, "これを使えば\nもっとそれっぽい\n雰囲気が出そうだ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (371, 1, 1, 100901, 5, 3, 0, "これが我らの求めた\n安寧か…\n悪くない…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (372, 1, 1, 100901, 6, 3, 0, "この程度\n前世での激闘に\n比べれば…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (373, 1, 1, 100901, 7, 3, 0, "最近金欠でな…\nいやぁありがたい…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (374, 1, 1, 100901, 8, 3, 0, "ここにも\n前世の記憶が\n眠っていたとは…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (376, 1, 1, 100902, 1, 1, 0, "「第１３部１章\n閃光のシグルド」\n…ついに登場だな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (377, 1, 1, 100902, 2, 1, 0, "何故だ…何故\nこうもあっさり金欠に\n逆戻りするのだ！？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (378, 1, 1, 100902, 3, 1, 0, "やはり転生者という\n設定はカッコイイ…\n天才だな、私！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (379, 1, 1, 100902, 4, 1, 0, "そうだ、ナナカに\nこの間借りた本を\n返さないと…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (380, 1, 1, 100902, 5, 1, 0, "そろそろ今日の分の\n魔力を溜めないと\nまた魔力切れが…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (381, 1, 1, 100902, 101, 1, 0, "最近奴がそばに\nいるだけで、鼓動が\n早くなるのは何故だ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (382, 1, 8, 100902, 102, 1, 0, "シ、シグルド…！\nだめだ…妄想でも\n上手く言えない…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (383, 1, 8, 100902, 1, 2, 0, "なんだ？　新たな\n賞金の情報を\n持ってきたのか？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (384, 1, 1, 100902, 2, 2, 0, "名乗りやポーズは\nその強度で相手を\n圧倒する技術だ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (385, 1, 1, 100902, 3, 2, 0, "いいか、魔法の\n詠唱中は絶対に\n声をかけるなよ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (386, 1, 1, 100902, 4, 2, 0, "おお、ちょうど今\n貴様専用の設定集を\n作成中だぞ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (387, 1, 1, 100902, 101, 2, 0, "貴様がそばにいると\nとても安心できる…\n…不思議だ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (388, 1, 8, 100902, 102, 2, 0, "い、いきなり目の前に\n現れるな！　ドキドキ\nするだろうがぁ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (389, 1, 8, 100902, 1, 2, 0, "夜更かしのせいか\nまだ眠気が…いや！\nこれは組織の罠だ！", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (390, 1, 1, 100902, 2, 2, 0, "平穏な時に\n出来るだけ魔力を\n溜めておかねば…", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (391, 1, 1, 100902, 3, 2, 0, "夜になると、力が\n増した気分になるな\n…なりませんか？", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (392, 1, 1, 100902, 1, 3, 0, "何か足りないな…\n暗黒騎士の紋章を\n作ろうかな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (393, 1, 1, 100902, 2, 3, 0, "お、おのれ\nヒュプノス…\n私は負けないぞ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (394, 1, 1, 100902, 3, 3, 0, "くっくっくっ…\n貴様は特別団員に\nしてやろう", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (395, 1, 1, 100902, 4, 3, 0, "これを使えば\nもっとそれっぽい\n雰囲気が出そうだ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (396, 1, 1, 100902, 5, 3, 0, "これが我らの求めた\n安寧か…\n悪くない…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (397, 1, 1, 100902, 6, 3, 0, "新たな戦いに備えて\n魔力だけでなく\n体力も鍛えねば！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (398, 1, 1, 100902, 7, 3, 0, "食事は魔力に\n変換する体力の\n元になるのだ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (399, 1, 1, 100902, 8, 3, 0, "フフフ…\nこれを覚えれば\nもっと強く…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (401, 1, 1, 101001, 1, 1, 0, "王子はんほんまに\nはんなりしてて、\n王子はんって感じやわ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (402, 1, 1, 101001, 2, 1, 0, "この絵本の王子はん、\n王子はんみたいで\nええなぁ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (403, 1, 1, 101001, 3, 1, 0, "うちがガラスの靴を\n落としたら、王子はん\n見つけてくれるやろか", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (404, 1, 1, 101001, 4, 1, 0, "マホマホ王国\n次期国王の王子はん\n…見てみたいわ～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (405, 1, 1, 101001, 5, 1, 0, "王子はんのために\n糸を紡いで、\n編み物でもしまひょか", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (406, 1, 4, 101001, 101, 1, 0, "マホ姫と王子はん、\n絵本の世界より\nベストカップルや", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (407, 1, 8, 101001, 102, 1, 0, "王子はんと\n一緒にいると\n胸がせわしのー\nうごいてかなん", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (408, 1, 1, 101001, 1, 2, 0, "うちら並んでたら\nお雛様みたいに\n見えると思いまへん？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (409, 1, 1, 101001, 2, 2, 0, "王子はんを\n国に連れて帰ったら\nみんな大歓迎して\nくれるやろなぁ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (410, 1, 1, 101001, 3, 2, 0, "王子はんは、\n絵本から出てきた人\nみたいやね～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (411, 1, 1, 101001, 4, 2, 0, "うちの話し方\nのんびりしてます\nやろか？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (412, 1, 4, 101001, 101, 2, 0, "マホ姫の手は\nつべたいから、\n王子はんが温めて？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (413, 1, 8, 101001, 102, 2, 0, "うちがもし悪い魔法に\nかかったら、王子はん\n口づけしてくれはる？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (414, 1, 1, 101001, 1, 2, 0, "朝は小鳥はんも\nおなかがすいてるから\nよう飛び回りはるねえ", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (415, 1, 1, 101001, 2, 2, 0, "お茶請けはお大福が\nよろしおすけど\nクッキーもええなあ", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (416, 1, 1, 101001, 3, 2, 0, "うち、普段から\n夢見がちやけど…\nほんに眠い時間やわぁ", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (417, 1, 1, 101001, 1, 3, 0, "王子はんに\n会いに行くなら、\nおめかしせな\nあかんなぁ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (418, 1, 1, 101001, 2, 3, 0, "はんなりした\nええお椅子やわぁ\nほっこりしますなあ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (419, 1, 1, 101001, 3, 3, 0, "かいらしもんが\nいっぱいで、\nマホマホ王国みたい\nやわぁ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (420, 1, 1, 101001, 4, 3, 0, "心地ええどす。まるで\nお花畑にいるようやね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (421, 1, 1, 101001, 5, 3, 0, "ぽかぽかどす。\n心まであったかく\nなりますわぁ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (422, 1, 1, 101001, 6, 3, 0, "はぁはぁ、お姫はんは\n激しい運動は\nしぃひんもんどす～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (423, 1, 1, 101001, 7, 3, 0, "もぐもぐ…あんまり\n速う食べられへんけど\n美味しいわぁ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (424, 1, 1, 101001, 8, 3, 0, "絵本、絵本～\n幸せやわぁ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (426, 1, 1, 101002, 1, 1, 0, "王子はんにとって\n自慢の姫になるえ～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (427, 1, 1, 101002, 2, 1, 0, "カボチャの馬車に\nガラスの靴…\n欲しいなぁ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (428, 1, 1, 101002, 3, 1, 0, "うちがガラスの靴を\n落としたら、王子はん\n見つけてくれるやろか", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (429, 1, 1, 101002, 4, 1, 0, "マホマホ王国\n次期国王の王子はん\n…見てみたいわ～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (430, 1, 1, 101002, 5, 1, 0, "ほんまなぁ\nこの絵本が面白いんよ\n王子はんに\n読んであげよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (431, 1, 8, 101002, 101, 1, 0, "マホ姫と王子はん\n絵本の世界より\nベストカップルや", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (432, 1, 8, 101002, 102, 1, 0, "王子はんと\n一緒にいると\n胸がせわしのー\nうごいてかなん", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (433, 1, 1, 101002, 1, 2, 0, "たまには、王子はんの\n好きな絵本も\n知りたいな～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (434, 1, 1, 101002, 2, 2, 0, "王子はんを\n国に連れて帰ったら\nみんな大歓迎して\nくれるやろなぁ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (435, 1, 1, 101002, 3, 2, 0, "そのうち、王子はんの\nお国にも連れてって\nもらえますやろか？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (436, 1, 1, 101002, 4, 2, 0, "絵本読むんも\nええなぁ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (437, 1, 8, 101002, 101, 2, 0, "うちがもし悪い魔法に\nかかったら、王子はん\n口づけしてくれはる？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (438, 1, 8, 101002, 102, 2, 0, "うちら並んでたら\nお雛様みたいに\n見えると思いまへん？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (439, 1, 1, 101002, 1, 2, 0, "朝は小鳥はんも\nおなかがすいてるから\nよう飛び回りはるねえ", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (440, 1, 1, 101002, 2, 2, 0, "マホマホ王国では\nこの時分は\nみんなお昼寝\nしてたんよ", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (441, 1, 1, 101002, 3, 2, 0, "夜に飛んだり鳴いたり\nする鳥はんもおるんよ\n昼は寝てはるんかねぇ", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (442, 1, 1, 101002, 1, 3, 0, "王子はんに\n会いに行くなら\nおめかしせな\nあかんなぁ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (443, 1, 1, 101002, 2, 3, 0, "王子はんも一緒に\nゆっくりしていっても\nええんよ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (444, 1, 1, 101002, 3, 3, 0, "かいらしい子やねぇ\nうちとお友だちに\nなってくれるん？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (445, 1, 1, 101002, 4, 3, 0, "癒しアイテムって\nほんま、好っきゃわぁ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (446, 1, 1, 101002, 5, 3, 0, "うちのお城にも\nほしいわぁ。帰ったら\nさっそく準備せな\nあかんなぁ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (447, 1, 1, 101002, 6, 3, 0, "部屋の中で運動\nできるのも、なかなか\n面白いもんやわぁ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (448, 1, 1, 101002, 7, 3, 0, "おいひいわ～\nマホマホ王国には\nない味やわぁ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (449, 1, 1, 101002, 8, 3, 0, "しっかり知識を\n身につけんと\n魔法も使えへん\nからなぁ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (451, 1, 1, 101101, 1, 1, 0, "１、２、３、４…\nあれ？　いま、\n何段目まで\n編んだっけ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (452, 1, 1, 101101, 2, 1, 0, "おやつのケーキは\n苺かチョコ…\nどっちも食べよう！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (453, 1, 1, 101101, 3, 1, 0, "い、いけない！\n縫い針落としちゃった", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (454, 1, 1, 101101, 4, 1, 0, "ケーキをクレープで\n巻いたら…\n大ヒット商品の予感！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (455, 1, 1, 101101, 5, 1, 0, "「どうだ！妹暮らし」\nってことわざ\nなかったっけ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (456, 1, 4, 101101, 101, 1, 0, "お兄ちゃんに、\nお洋服でも\n作ろうかな？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (457, 1, 8, 101101, 102, 1, 0, "ここがお兄ちゃんの\n部屋…\nドキドキしちゃう～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (458, 1, 1, 101101, 1, 2, 0, "ねぇ、おにいちゃん\n一緒に、スイーツでも\n食べにいかない？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (459, 1, 1, 101101, 2, 2, 0, "クリームついてた！？\nさっき食べた、\nケーキかも…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (460, 1, 1, 101101, 3, 2, 0, "勉強、見てくれるん\nですか？　やった～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (461, 1, 1, 101101, 4, 2, 0, "服がほつれてますよ？\n直してあげます！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (462, 1, 4, 101101, 101, 2, 0, "私をお嫁さんに\nしたいなら、予約は\nお早目に、ですよ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (463, 1, 8, 101101, 102, 2, 0, "ねぇねぇお兄ちゃん！\n…えへへ、\n呼んでみただけ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (464, 1, 1, 101101, 1, 2, 0, "今日も１日\n勉強がんばります！\n…少しは遊ぼうかな", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (465, 1, 1, 101101, 2, 2, 0, "お昼休憩に\n甘いものが\n食べたいです～", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (466, 1, 1, 101101, 3, 2, 0, "ねむいです～\nでももうちょっと\n勉強しないと～…", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (467, 1, 1, 101101, 1, 3, 0, "この服、かわいい♪\n今度作って\nみよっと～！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (468, 1, 1, 101101, 2, 3, 0, "勉強で疲れたから\n休憩しよ～っと", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (469, 1, 1, 101101, 3, 3, 0, "かわいい♪\n私が服を作って\nあげよっか？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (470, 1, 1, 101101, 4, 3, 0, "リラックスできます～\nお兄ちゃんが隣に\nいるみたい！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (471, 1, 1, 101101, 5, 3, 0, "のんびりしたら、\nまた勉強しよっと！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (472, 1, 1, 101101, 6, 3, 0, "運動すると勉強も\nはかどるよね！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (473, 1, 1, 101101, 7, 3, 0, "おいし～！\nお兄ちゃんにも\nおすそ上げしますね！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (474, 1, 1, 101101, 8, 3, 0, "お兄ちゃんの記憶を\n戻す方法、どこかに\n書いてないかな？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (476, 1, 1, 101102, 1, 1, 0, "ケーキの食べ過ぎで\nお腹が…うぅ、\n因果横暴だよー…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (477, 1, 1, 101102, 2, 1, 0, "１、２、３、４…\nあれ？　いま、\n何段目まで\n編んだっけ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (478, 1, 1, 101102, 3, 1, 0, "い、いけない！\n縫い針落としちゃった", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (479, 1, 1, 101102, 4, 1, 0, "笑う時には服着ます！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (480, 1, 1, 101102, 5, 1, 0, "「どうだ！妹暮らし」\nってことわざ\nなかったっけ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (481, 1, 8, 101102, 101, 1, 0, "お兄ちゃんに、\nお洋服でも\n作ろうかな？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (482, 1, 8, 101102, 102, 1, 0, "お兄ちゃんが帰って\nきたら、何して遊ぼう\nかな～？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (483, 1, 1, 101102, 1, 2, 0, "勉強、見てくれるん\nですか？　やった～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (484, 1, 1, 101102, 2, 2, 0, "クリームついてた！？\nさっき食べた、\nケーキかも…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (485, 1, 1, 101102, 3, 2, 0, "おにいちゃん！\nそろそろ、おやつに\nしませんか？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (486, 1, 1, 101102, 4, 2, 0, "勉強は…\n明日にしようっと", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (487, 1, 8, 101102, 101, 2, 0, "妹だけど、大人な\nレディですからね！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (488, 1, 8, 101102, 102, 2, 0, "な、ナデナデとか\n恥ずかしいな～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (489, 1, 1, 101102, 1, 2, 0, "今日も１日\n勉強がんばります！\n…少しは遊ぼうかな", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (490, 1, 1, 101102, 2, 2, 0, "お昼休憩に\n甘いものが\n食べたいです～", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (491, 1, 1, 101102, 3, 2, 0, "お兄ちゃんは\n夜強いんですね\n私は眠くて…", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (492, 1, 1, 101102, 1, 3, 0, "服装や髪形が変わると\n気分も変わるよね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (493, 1, 1, 101102, 2, 3, 0, "ここで寝ちゃったら\n受験勉強が～…\nぐーー", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (494, 1, 1, 101102, 3, 3, 0, "かわいい♪\n私が服を作って\nあげよっか？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (495, 1, 1, 101102, 4, 3, 0, "いまならどんな試験も\n合格できそうです！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (496, 1, 1, 101102, 5, 3, 0, "のんびりしたら\nまた勉強しよっと！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (497, 1, 1, 101102, 6, 3, 0, "お兄ちゃんを\n守るためには\n毎日、鍛えなくちゃ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (498, 1, 1, 101102, 7, 3, 0, "おいし～！\nお兄ちゃんにも\nおすそ上げしますね！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (499, 1, 1, 101102, 8, 3, 0, "この本の服\nかわいいなぁ♪\n私に作れるかな？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (501, 1, 1, 101201, 1, 1, 0, "う～ん、今なら\nどこでだって\n眠れちゃいそう…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (502, 1, 1, 101201, 2, 1, 0, "たくさん寝すぎて、\n夢の中か現実か\nわかんなく\nなっちゃうんだよね～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (503, 1, 1, 101201, 3, 1, 0, "…はっ！？\nね、寝てないよ？\nホントだよっ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (504, 1, 1, 101201, 4, 1, 0, "ぐっすり眠って\nパワーを\nフルチャージっ☆", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (505, 1, 1, 101201, 5, 1, 0, "もし、超能力のことが\nバレたら…ブルブル", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (506, 1, 4, 101201, 101, 1, 0, "テレパシーで\nキミの心を覗けたら\n…なんてね！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (507, 1, 8, 101201, 102, 1, 0, "キミを抱き枕に…\nじょ、冗談冗談！", 0);
