INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (591, 1, 1, 101602, 3, 2, 0, "お腹すいたー\nでもこの時間に\n食べたら絶対に\n太るやつだよねー", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (592, 1, 1, 101602, 1, 3, 0, "この服ね～\n今年のトレンドなんだ♪\nかわたんでしょ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (593, 1, 1, 101602, 2, 3, 0, "ちょっち疲れたかも？\nガン寝しちゃおっかな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (594, 1, 1, 101602, 3, 3, 0, "うわー\nこのぬいぐるみ\nもきゅすぎでしょ～！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (595, 1, 1, 101602, 4, 3, 0, "いいにおーい♪\nファンのみんなにも\n教えてあげよー♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (596, 1, 1, 101602, 5, 3, 0, "あったまるー\nこれ、サイツヨに\n癒しじゃない？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (597, 1, 1, 101602, 6, 3, 0, "食べた分は\n消費しないとね～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (598, 1, 1, 101602, 7, 3, 0, "体形維持しないと\nいけないけど\nついつい食べちゃう！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (599, 1, 1, 101602, 8, 3, 0, "全然読めない～\nそだ、ヒデサイに\n読んでもらおっと！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (601, 1, 1, 101701, 1, 1, 0, "やっさー\n踊ってると、どんどん\n楽しくなるさー！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (602, 1, 1, 101701, 2, 1, 0, "このお菓子、でーじ\nおいしいねー！\nキミも食べる？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (603, 1, 1, 101701, 3, 1, 0, "ハイターイ！　元気な\nあいさつが一番さ～！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (604, 1, 1, 101701, 4, 1, 0, "あまった食材があれば\nチャンプルーにして\n食べたいさー！ ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (605, 1, 1, 101701, 5, 1, 0, "さぁ、みんなで歌って\n踊るさ～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (606, 1, 4, 101701, 101, 1, 0, "毎日、最高に幸せさ～\nキミのおかげだねー", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (607, 1, 8, 101701, 102, 1, 0, "キミへの気持ちを\n踊りで表現\nしてみるさ～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (608, 1, 1, 101701, 1, 2, 0, "海でひと泳ぎ\nしたいさ～\nキミもどう？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (609, 1, 1, 101701, 2, 2, 0, "あぎひゃっ！\n急に声かけたら、\nビックリするさ～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (610, 1, 1, 101701, 3, 2, 0, "踊りなら、\n私が手とり足とり\n教えてあげるさ～！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (611, 1, 1, 101701, 4, 2, 0, "空手の型の\n練習するさー\nキミもどう？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (612, 1, 4, 101701, 101, 2, 0, "よ～し、手をつないで\n踊るさ～！\n恥ずかしがること\nないさ～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (613, 1, 8, 101701, 102, 2, 0, "キミと一緒だから、\n毎日楽しいこと\nばっかりさー", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (614, 1, 1, 101701, 1, 2, 0, "ハイターイ！\n今日も一日、\nがんばるさー！", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (615, 1, 1, 101701, 2, 2, 0, "午後も頑張るために\nお昼ご飯は\nてびちにするさー！", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (616, 1, 1, 101701, 3, 2, 0, "故郷では見えない星が\n見られるんだねえ。\nなんか不思議さぁ", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (617, 1, 1, 101701, 1, 3, 0, "夢中で踊ってたら、\n髪の毛ぼさぼさに\nなってるさ～！？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (618, 1, 1, 101701, 2, 3, 0, "よく食べてよく踊って\nよく休む。\nそれが元気の秘訣さ～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (619, 1, 1, 101701, 3, 3, 0, "ちゅらかーぎーねー！\nキミはどこから\n来たのー？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (620, 1, 1, 101701, 4, 3, 0, "いい香り～、って\n私もカオリだったさ！\nなんだか照れるね☆", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (621, 1, 1, 101701, 5, 3, 0, "故郷はあんまり暖房を\n使わなかったから、\n新鮮さー！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (622, 1, 1, 101701, 6, 3, 0, "汗をかくのは\n気持ちいいさー！\nどんどん行くよー！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (623, 1, 1, 101701, 7, 3, 0, "うーん、おいしいさ～\nゴーヤっぽいものを\n混ぜたいさ～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (624, 1, 1, 101701, 8, 3, 0, "おおー、故郷の民話！\nこんなとこで見るとは\n懐かしいさぁ～！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (626, 1, 1, 101702, 1, 1, 0, "焦らなくても\nなんくるないさ～\nなるようになるさ～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (627, 1, 1, 101702, 2, 1, 0, "さぁ、みんなで歌って\n踊るさ～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (628, 1, 1, 101702, 3, 1, 0, "元気がないときは\n私のところに来るさー", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (629, 1, 1, 101702, 4, 1, 0, "あまった食材があれば\nチャンプルーにして\n食べたいさー！ ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (630, 1, 1, 101702, 5, 1, 0, "ノリノリな音楽と\n踊りで、疲れを\n吹き飛ばすさ～！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (631, 1, 8, 101702, 101, 1, 0, "毎日、最高に幸せさ～\nキミのおかげだねー", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (632, 1, 8, 101702, 102, 1, 0, "幸せすぎて踊らずには\nいられないさ～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (633, 1, 1, 101702, 1, 2, 0, "海でひと泳ぎ\nしたいさ～\nキミもどう？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (634, 1, 1, 101702, 2, 2, 0, "ん？　どうしたの～？\nヒマなら一緒に\n踊ろうさ～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (635, 1, 1, 101702, 3, 2, 0, "キミと一緒だから\n毎日楽しいこと\nばっかりさー", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (636, 1, 1, 101702, 4, 2, 0, "ハイターイ！　元気な\nあいさつが一番さ～！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (637, 1, 8, 101702, 101, 2, 0, "キミと踊ってる時が\n一番幸せさ～\nキミもそう思ってたら\nうれしいさ～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (638, 1, 8, 101702, 102, 2, 0, "海に沈む夕日は\nとっても綺麗さ～！\n今度一緒に見ようね～ ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (639, 1, 1, 101702, 1, 2, 0, "気持ちのいい朝さー！\nひとっ走り\nしてくるさー！", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (640, 1, 1, 101702, 2, 2, 0, "午後も頑張るために\nお昼ご飯は\nてびちにするさー！ ", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (641, 1, 1, 101702, 3, 2, 0, "故郷では見えない星が\n見られるんだねえ\nなんか不思議さぁ ", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (642, 1, 1, 101702, 1, 3, 0, "夢中で踊ってたら\n髪の毛ぼさぼさに\nなってるさ～！？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (643, 1, 1, 101702, 2, 3, 0, "よく食べてよく踊って\nよく休む\nそれが元気の秘訣さ～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (644, 1, 1, 101702, 3, 3, 0, "ちゅらかーぎーねー！\nキミはどこから\n来たのー？ ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (645, 1, 1, 101702, 4, 3, 0, "いい香り～、って\n私もカオリだったさ！\nなんだか照れるね☆ ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (646, 1, 1, 101702, 5, 3, 0, "故郷はあんまり暖房を\n使わなかったから\n新鮮さー！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (647, 1, 1, 101702, 6, 3, 0, "徹夜で踊り続ける\nためにも、まずは\n体を鍛えるさ～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (648, 1, 1, 101702, 7, 3, 0, "もぐもぐ…もっと\nおかわりがほしいさー", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (649, 1, 1, 101702, 8, 3, 0, "おおー、故郷の民話！\nこんなとこで見るとは\n懐かしいさぁ～！ ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (651, 1, 1, 101801, 1, 1, 0, "はあ…\nすてきな小説だった～\nキュンキュンしちゃう", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (652, 1, 1, 101801, 2, 1, 0, "このお部屋\n女の子ばかりね。\n魔族の女子校を\n思い出しちゃう♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (653, 1, 1, 101801, 3, 1, 0, "次のテスト\n恋愛小説から\n出題したら\nダメかしら…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (654, 1, 1, 101801, 4, 1, 0, "キンコンカンコ～ン♪\n授業をはじめま～す", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (655, 1, 1, 101801, 5, 1, 0, "はー…\nちょっと肩が\nこっちゃった…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (656, 1, 4, 101801, 101, 1, 0, "私、あの子のこと\n無意識に特別扱い\nしちゃってるかな…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (657, 1, 8, 101801, 102, 1, 0, "物語だと生徒が先生を\n好きになるのにな…\n逆の場合は…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (658, 1, 1, 101801, 1, 2, 0, "あん！　見ちゃダメよ\n明日のテストの問題を\n作ってるとこなの！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (659, 1, 1, 101801, 2, 2, 0, "勉強勉強で\n疲れたよね？\n膝枕してあげる！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (660, 1, 1, 101801, 3, 2, 0, "テストがんばったね～\nご褒美に先生が\n特別な授業\nしてあげるね？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (661, 1, 1, 101801, 4, 2, 0, "学院の外でも\n私は君の先生よ\nなんでも聞いてね♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (662, 1, 4, 101801, 101, 2, 0, "今読んでるのは\nヒロインが年下の子と\n恋に落ちる話…あ、\n深い意味はないのよ～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (663, 1, 8, 101801, 102, 2, 0, "生徒は全員可愛いけど\n君は少し特別…\nみんなには内緒よ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (664, 1, 1, 101801, 1, 2, 0, "学校に\n遅刻しないように\n早く準備しないとね", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (665, 1, 1, 101801, 2, 2, 0, "男の子といっしょに\nお弁当を食べるの、\n夢なんだ～", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (666, 1, 1, 101801, 3, 2, 0, "明日の予習はやった？\n先生も手伝うから\n寝る前にやりましょ♪", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (667, 1, 1, 101801, 1, 3, 0, "もしかして…\n少し露出多いかしら？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (668, 1, 1, 101801, 2, 3, 0, "リラックスすると\nα波が出て、勉強が\nはかどるんですって", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (669, 1, 1, 101801, 3, 3, 0, "かわいい♪　あなたも\n私の生徒になる？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (670, 1, 1, 101801, 4, 3, 0, "学院にもこういう\n安らぐ設備が\n必要かしらね？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (671, 1, 1, 101801, 5, 3, 0, "まだ寒いわぁ\n誰か隣に\n来てくれたら…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (672, 1, 1, 101801, 6, 3, 0, "運動部の顧問も\nできるかしら？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (673, 1, 1, 101801, 7, 3, 0, "学校の給食でも\n食べたいわぁ～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (674, 1, 1, 101801, 8, 3, 0, "いい参考書を\n見つけちゃった♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (676, 1, 1, 101802, 1, 1, 0, "私の生徒に\n不良なんていないわ\nみーんないい子♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (677, 1, 1, 101802, 2, 1, 0, "このお部屋\n女の子ばかりね\n魔族の女子校を\n思い出しちゃう♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (678, 1, 1, 101802, 3, 1, 0, "あの子、まだかな…\n宿題も見てあげたいし\n早く来ないかしら？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (679, 1, 1, 101802, 4, 1, 0, "はあ…\nすてきな小説だった～\nキュンキュンしちゃう", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (680, 1, 1, 101802, 5, 1, 0, "学校の図書室に\n恋愛小説を\n置いて欲しいなあ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (681, 1, 8, 101802, 101, 1, 0, "私、あの子のこと\n無意識に特別扱い\nしちゃってるかな…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (682, 1, 8, 101802, 102, 1, 0, "物語だと生徒が先生を\n好きになるのにな…\n逆の場合は…　", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (683, 1, 1, 101802, 1, 2, 0, "あん！　見ちゃダメよ\n明日のテストの問題を\n作ってるとこなの！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (684, 1, 1, 101802, 2, 2, 0, "勉強勉強で\n疲れたよね？\n膝枕してあげる！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (685, 1, 1, 101802, 3, 2, 0, "宿題はもうやった？\n苦手なところは\n先生が見てあげる♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (686, 1, 1, 101802, 4, 2, 0, "んん？　なんだか\n元気がないみたい\n先生がぎゅぅ～って\nしてあげる…　", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (687, 1, 8, 101802, 101, 2, 0, "君のお勉強も\nだいぶ進んできたね～\n補習卒業も近いかな？\n少し寂しいけど…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (688, 1, 8, 101802, 102, 2, 0, "テストがんばったね～\nご褒美に先生が\n特別な授業\nしてあげるね？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (689, 1, 1, 101802, 1, 2, 0, "朝の特別授業\nしてあげましょうか？", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (690, 1, 1, 101802, 2, 2, 0, "午後の授業って\n眠いでしょう？\n気持ちは分かるけど\nがんばってね♪", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (691, 1, 1, 101802, 3, 2, 0, "明日の予習はやった？\n先生も手伝うから\n寝る前にやりましょ♪", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (692, 1, 1, 101802, 1, 3, 0, "もしかして…\n少し露出多いかしら？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (693, 1, 1, 101802, 2, 3, 0, "私の膝枕と、どっちが\nフカフカかしら？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (694, 1, 1, 101802, 3, 3, 0, "可愛いわね～\n教室に置いたら\n生徒が喜ぶかしら？　", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (695, 1, 1, 101802, 4, 3, 0, "は～落ち着くなぁ～\n教師には\nこういうゆとりも\n必要よね♪　", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (696, 1, 1, 101802, 5, 3, 0, "まだ寒いわぁ\n誰か隣に\n来てくれたら…　", 0);
