INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (762, 1, 4, 102101, 101, 2, 0, "リボンがかわいい？\nほ、ほぇぇぇ～～\nは、恥ずかしいですぅ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (763, 1, 8, 102101, 102, 2, 0, "お兄ちゃん…♪\nえへへ、呼んでみた\nだけです…♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (764, 1, 1, 102101, 1, 2, 0, "今日は一日、\n恥ずかしがらずに\n過ごせるかなあ…？", 1);
