INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (781, 1, 8, 102102, 101, 1, 0, "お、お腹が空いた時は\n言ってくださいね\nが、頑張って\n作ります！", 0);
