INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (841, 1, 1, 102202, 3, 2, 0, "夜はこれからよ！\n朝まで\nゲームするからね！", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (842, 1, 1, 102202, 1, 3, 0, "もっと女の子らしく\nなったらアイツも\n嬉しいかな…って、\n何考えてるの！？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (843, 1, 1, 102202, 2, 3, 0, "ふああ…\n徹夜明けには\n辛い誘惑だな…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (844, 1, 1, 102202, 3, 3, 0, "可愛い…けど、\n私には似合わないかな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (845, 1, 1, 102202, 4, 3, 0, "こういうのに詳しいと\n女の子っぽく\n見えたりするのかな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (846, 1, 1, 102202, 5, 3, 0, "寒い日は\nここでゲームするのが\n最高のぜいたくよね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (847, 1, 1, 102202, 6, 3, 0, "最近ゲームばかりだし\nたまにはいいかな？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (848, 1, 1, 102202, 7, 3, 0, "ゲームは頭使うから\nそのぶん\n栄養がいるのよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (849, 1, 1, 102202, 8, 3, 0, "ふむふむ、なるほど\n友だちを作るには\n笑顔が大事っと…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (851, 1, 1, 102301, 1, 1, 0, "じゃんけんぽん！\nあっち向いてほい！\nあ～ん、負けたぁ\n　『勝ったぜ！』", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (852, 1, 1, 102301, 2, 1, 0, "ぷうきちがいれば、\nなんでも出来ちゃうよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (853, 1, 1, 102301, 3, 1, 0, "発声練習しよっと\nぷうきち\nフルスイ～ング！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (854, 1, 1, 102301, 4, 1, 0, "ここで、ぷうきち\nフルスイングは、\nまずいかな～？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (855, 1, 1, 102301, 5, 1, 0, "この部屋、ぷうきちを\n置く場所はないのかな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (856, 1, 4, 102301, 101, 1, 0, "お兄ちゃんって…\nちょっとぷうきちに\n似てるかも♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (857, 1, 8, 102301, 102, 1, 0, "お兄ちゃんにも、\nぷうきちのこと\n好きになって\nもらいたいな～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (858, 1, 1, 102301, 1, 2, 0, "おやつは、\n私とぷうきちの\n二人分用意してね♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (859, 1, 1, 102301, 2, 2, 0, "ぷうきちが、お腹\nペコペコだって～\n何かちょうだい！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (860, 1, 1, 102301, 3, 2, 0, "『アヤネに手出したら\n…坊ちゃんでも、\n分かってんな？』", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (861, 1, 1, 102301, 4, 2, 0, "ぷうきちとの\n連携を磨いてるの！\nもっと仲良くなるよ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (862, 1, 4, 102301, 101, 2, 0, "特別だからね？\nもっと触っていいよ\n…ぷうきちのこと", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (863, 1, 8, 102301, 102, 2, 0, "お兄ちゃんのこと、\n「ぷう兄」って\n呼ぼうかな？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (864, 1, 1, 102301, 1, 2, 0, "お兄ちゃんって\n朝早いんだね～\n私はまだ眠いよ～", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (865, 1, 1, 102301, 2, 2, 0, "ふぁ～…ぷうきちと\nお昼寝してくるね…", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (866, 1, 1, 102301, 3, 2, 0, "ぷうきちと\nおしゃべりしてたら\nもうこんな時間…", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (868, 1, 1, 102301, 2, 3, 0, "『座り心地がいいな』\nぷうきちは座れない\nでしょ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (869, 1, 1, 102301, 3, 3, 0, "かわいい～！\nぷうきちのお友達に\nしちゃおうかな？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (870, 1, 1, 102301, 4, 3, 0, "いいニオイ～♪\nぷうきちも\n気に入ったみたい！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (871, 1, 1, 102301, 5, 3, 0, "ぽわぽわする～\nぷうきちも\n気持ちいい？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (873, 1, 1, 102301, 7, 3, 0, "おいしい～\nぷうきちも\n食べてみる？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (874, 1, 1, 102301, 8, 3, 0, "ぷうきち！\nこの本、読んで～！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (901, 1, 1, 102501, 1, 1, 0, "どうして失敗ばっかり\nこうなったら\n腹を切るしか～！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (902, 1, 1, 102501, 2, 1, 0, "あれ？　あれ？\nお部屋の中で迷子に\nなっちゃいました～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (903, 1, 1, 102501, 3, 1, 0, "どうしたら転ばなく\nなるんでしょうか？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (904, 1, 1, 102501, 4, 1, 0, "わわわわ～～っ\nふう…\nきゃ、きゃあっ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (905, 1, 1, 102501, 5, 1, 0, "私のドジで、\nこのお部屋を\n壊しちゃったら\nどうしましょう～～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (906, 1, 4, 102501, 101, 1, 0, "おいしい料理で\nお出迎えしなくちゃ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (907, 1, 8, 102501, 102, 1, 0, "一生懸命\nご奉仕しますよ～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (908, 1, 1, 102501, 1, 2, 0, "耳かきしましょうか？\nえ？　こわい？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (909, 1, 1, 102501, 2, 2, 0, "お疲れでしたら、\nお昼寝しませんか？\n膝枕もしますよ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (910, 1, 1, 102501, 3, 2, 0, "おかえりなさいませ\nご主人さま♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (911, 1, 1, 102501, 4, 2, 0, "すみません\nすみません\nすみません～～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (912, 1, 4, 102501, 101, 2, 0, "メイドの私が、\nこんな気持ちを持って\nいいんでしょうか…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (913, 1, 8, 102501, 102, 2, 0, "私のこと、\n専属メイドと思って\n頼ってくださいね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (914, 1, 1, 102501, 1, 2, 0, "おはようございます♪\n今日も一日がんば…\nきゃ、きゃあ！", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (915, 1, 1, 102501, 2, 2, 0, "お昼ごはん…\n失敗しちゃいましたぁ\nお、おやつでリベンジします！", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (916, 1, 1, 102501, 3, 2, 0, "明日の目標は\nドジをしないです！\nき、昨日の目標も\nドジをしないでした", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (917, 1, 1, 102501, 1, 3, 0, "あ、メイド服が\nほつれてる\n後でお裁縫しなくちゃ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (918, 1, 1, 102501, 2, 3, 0, "休憩が終わったら\nお洗濯にお料理に\nお掃除もしないと！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (919, 1, 1, 102501, 3, 3, 0, "かわいい♪\nあとでお洗濯\nしてあげますね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (920, 1, 1, 102501, 4, 3, 0, "いい香り…\nお嬢様のお部屋にも\n置いてあげたいです", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (921, 1, 1, 102501, 5, 3, 0, "ここでお洗濯ものを\n乾かしましょうか", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (922, 1, 1, 102501, 6, 3, 0, "あわわわっ…\nこ、転びそうです～！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (923, 1, 1, 102501, 7, 3, 0, "これくらい\nおいしいものを\n作ってみたいです～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (924, 1, 1, 102501, 8, 3, 0, "物語のメイドさんって\n御主人さまを好きに\nなるんですよね…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (926, 1, 1, 102502, 1, 1, 0, "失敗したら、この\nお部屋追い出され\nちゃうかも～？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (927, 1, 1, 102502, 2, 1, 0, "あれ？　あれ？\nお部屋の中で迷子に\nなっちゃいました～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (928, 1, 1, 102502, 3, 1, 0, "どうやったら、立派な\nメイドになれるのかな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (929, 1, 1, 102502, 4, 1, 0, "塩にはＳマーク\n砂糖にはＳマーク\n…あ、あれ！？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (930, 1, 1, 102502, 5, 1, 0, "ご用があれば\nなんでも言って\nくださいね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (931, 1, 8, 102502, 101, 1, 0, "迷惑をかけすぎて…\n嫌われちゃったら\nどうしましょう", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (932, 1, 8, 102502, 102, 1, 0, "メイドの私が\nこんな気持ちを持って\nいいんでしょうか…　", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (933, 1, 1, 102502, 1, 2, 0, "おでかけですか？\nいってらっしゃいませ\nご主人さま♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (934, 1, 1, 102502, 2, 2, 0, "どうして失敗ばっかり\nこうなったら\n腹を切るしか～！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (935, 1, 1, 102502, 3, 2, 0, "私、またなにか\nやっちゃいましたか？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (936, 1, 1, 102502, 4, 2, 0, "おいしいお茶っ葉が\n手に入りました～♪\n一緒に飲みませんか？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (937, 1, 8, 102502, 101, 2, 0, "転んだふりであの人に\n…って！\nなんでもないですよ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (938, 1, 8, 102502, 102, 2, 0, "私、できることなら\nなんでもやりますよ\nあなたのためですから", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (939, 1, 1, 102502, 1, 2, 0, "おはようございます♪\n今日も一日がんば…\nう、うわあ！", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (940, 1, 1, 102502, 2, 2, 0, "お昼ごはん…\n失敗しちゃいましたぁ\nお、おやつでリベンジします！", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (941, 1, 1, 102502, 3, 2, 0, "明日の目標は\nドジをしないです！\nき、昨日の目標も\nドジをしないでした", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (942, 1, 1, 102502, 1, 3, 0, "あ、メイド服が\nほつれてる\n後でお裁縫しなくちゃ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (943, 1, 1, 102502, 2, 3, 0, "いい座り心地です～\nこれならお嬢さまも\nゆったりできますね♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (944, 1, 1, 102502, 3, 3, 0, "かわいい♪\nあとでお洗濯\nしてあげますね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (945, 1, 1, 102502, 4, 3, 0, "いい香り…\nお嬢様のお部屋にも\n置いてあげたいです", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (946, 1, 1, 102502, 5, 3, 0, "暖かいですけど\n…私が火に近づいて\n大丈夫でしょうか！？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (947, 1, 1, 102502, 6, 3, 0, "運動しようとすると\nいつも怪我するん\nですよね…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (948, 1, 1, 102502, 7, 3, 0, "こんなに美味しいもの\nメイドの私には\nもったいないです～！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (949, 1, 1, 102502, 8, 3, 0, "物語のメイドさんって\n御主人さまを好きに\nなるんですよね…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (951, 1, 1, 102701, 1, 1, 0, "あの方と私だけの\n部屋にしたいですわ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (952, 1, 1, 102701, 2, 1, 0, "あの方は私のもの…\n眼も耳も鼻も口も髪も\n首も胸も腰も手も脚も\nぬくもりも心も…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (953, 1, 1, 102701, 3, 1, 0, "運命とは決して\n抗えないもの…\nクスクス…クスクス", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (954, 1, 1, 102701, 4, 1, 0, "部屋からあの方の\n香りが…\nああ、至福ですわ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (955, 1, 1, 102701, 5, 1, 0, "毒にも薬にもならない\n存在なんて嫌ですわ\nどうせならとびきりの\n毒でありたい…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (956, 1, 4, 102701, 101, 1, 0, "クスクス…\nあの方とデートする\nイメトレ中ですわ\nクスクスクスクス…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (957, 1, 8, 102701, 102, 1, 0, "記憶を戻す研究より\n記憶をねつ造する研究\nのほうが良いかしら？\n伴侶という設定で…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (958, 1, 1, 102701, 1, 2, 0, "運命を信じますか？\nもちろん絶対当然\n信じますわよね？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (959, 1, 1, 102701, 2, 2, 0, "この薬は起床時就寝前\n食前食後食間に、毎日\n服用してください。\n惚れ薬ですわ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (960, 1, 1, 102701, 3, 2, 0, "お願いがあるのです。\n新しい研究の実験台に\n…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (961, 1, 1, 102701, 4, 2, 0, "こっそり栽培している\n毒きのこが毒々しく\n育ちましたわ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (962, 1, 4, 102701, 101, 2, 0, "あなた様の前では\n普通の女の子…\n少し毒薬づくりが\n得意なだけの…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (963, 1, 8, 102701, 102, 2, 0, "私の身も心も、全てが\nあなた様のもの。\nあなた様の全ても…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (964, 1, 1, 102701, 1, 2, 0, "おはようございます\n一晩中、あなた様の\n寝顔を見ていましたわ", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (965, 1, 1, 102701, 2, 2, 0, "お昼を用意しましたの\n何も入れていませんわ\nクスクス…", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (966, 1, 1, 102701, 3, 2, 0, "あなた様が眠るまで\nお傍で見守りますわ。\n…あの、どうして\n眠らないのですか？", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (967, 1, 1, 102701, 1, 3, 0, "いっそ鏡の中に\n閉じ込めてしまえたら\n私だけのものに…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (968, 1, 1, 102701, 2, 3, 0, "はっ！\nリラックスしたら\n閃きましたわ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (969, 1, 1, 102701, 3, 3, 0, "あら、キメラの素体に\nぴったりですわ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (970, 1, 1, 102701, 4, 3, 0, "このお部屋は\n良い香りがしますわ。\nはっ、あなた様の匂い\nなのでは…！？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (971, 1, 1, 102701, 5, 3, 0, "あたたかい…\n加熱調合にも\n使えそうですわね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (972, 1, 1, 102701, 6, 3, 0, "この運動エネルギー\n何かにうまく\n利用できないかしら？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (973, 1, 1, 102701, 7, 3, 0, "美味しい。あの方にも\n差し上げましょう…\n薬を入れて…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (974, 1, 1, 102701, 8, 3, 0, "あの方を虜にする\n方法がどこかに\n載ってるはずですわ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (976, 1, 1, 102702, 1, 1, 0, "あの方と私だけの\n部屋にしたいですわ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (977, 1, 1, 102702, 2, 1, 0, "こっそり栽培している\n毒きのこが毒々しく\n育ちましたわ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (978, 1, 1, 102702, 3, 1, 0, "新しい薬\nどうやってあの方に\n飲ませようかしら", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (979, 1, 1, 102702, 4, 1, 0, "部屋からあの方の\n香りが…\nああ、至福ですわ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (980, 1, 1, 102702, 5, 1, 0, "記憶を戻す研究より\n記憶をねつ造する研究\nのほうが良いかしら？\n伴侶という設定で…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (981, 1, 8, 102702, 101, 1, 0, "クスクス…\nあの方とデートする\nイメトレ中ですわ\nクスクスクスクス…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (982, 1, 8, 102702, 102, 1, 0, "魔法も科学も錬金術も\n全て駆使して\nあの方のために…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (983, 1, 1, 102702, 1, 2, 0, "この部屋には雌犬…\n失礼、女の方がとても\n沢山いますのね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (984, 1, 1, 102702, 2, 2, 0, "あなた様には私の伴侶\nとして高い教養を…\nあ、逃げられてしまい\nましたわ…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (985, 1, 1, 102702, 3, 2, 0, "お願いがあるのです\n新しい研究の\n実験台に…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (986, 1, 1, 102702, 4, 2, 0, "あなた様が寝ている時\n私は…クスクス\nこれ以上は言えません", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (987, 1, 8, 102702, 101, 2, 0, "想像の中のあなた様は\nすっかり私の虜なのに\n現実は…\nもどかしいですわね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (988, 1, 8, 102702, 102, 2, 0, "あなた様の前では\n普通の女の子…\n少し毒薬づくりが\n得意なだけの…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (989, 1, 1, 102702, 1, 2, 0, "おはようございます\n一晩中、あなた様の\n寝顔を見ていましたわ", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (990, 1, 1, 102702, 2, 2, 0, "さあ、お食事ですよ\n味は保証しますわ\n特製ソースで少しだけ\n体が痺れますけど…", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (991, 1, 1, 102702, 3, 2, 0, "あなた様が眠るまで\nお傍で見守りますわ\n…あの、どうして\n眠らないのですか？", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (992, 1, 1, 102702, 1, 3, 0, "私ももう少し\n外見に気を配る\nべきでしょうか…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (993, 1, 1, 102702, 2, 3, 0, "はっ！\nリラックスしたら\n閃きましたわ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (994, 1, 1, 102702, 3, 3, 0, "この私を差し置いて\nあなた様に媚びるなど\n言語道断…っ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (995, 1, 1, 102702, 4, 3, 0, "このお部屋は\n良い香りがしますわ\nはっ、あなた様の匂い\nなのでは…！？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (996, 1, 1, 102702, 5, 3, 0, "素敵なぬくもり…\nまるであの方に\n抱きしめられたような", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (997, 1, 1, 102702, 6, 3, 0, "この運動エネルギー\n何かにうまく\n利用できないかしら？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (998, 1, 1, 102702, 7, 3, 0, "美味しい。あの方にも\n差し上げましょう…\n薬を入れて…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (999, 1, 1, 102702, 8, 3, 0, "あの方を虜にする\n方法がどこかに\n載ってるはずですわ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1001, 1, 1, 102801, 1, 1, 0, "子供たちの遊びって\nあたしが子供の頃と\nあまり変わらないのね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1002, 1, 1, 102801, 2, 1, 0, "使えるものは\n再利用しないと\n…あ、これとか\nまだ使えるわね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1003, 1, 1, 102801, 3, 1, 0, "贅沢は敵！　とはいえ\n気持ちに余裕のない\n暮らしはダメよね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1004, 1, 1, 102801, 4, 1, 0, "お腹空いたわね、\n残り物で\n何か作ろうかしら", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1005, 1, 1, 102801, 5, 1, 0, "商売はノンゼロサムが\n理想だなぁ。誰も損を\nしないってことだもの", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1006, 1, 4, 102801, 101, 1, 0, "あんたが手伝って\nくれるから、仕事も\n前より楽しいのよね♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1007, 1, 8, 102801, 102, 1, 0, "焼きそばパンが\n食べたいな～\nあいつと…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1008, 1, 1, 102801, 1, 2, 0, "ねぇあんた、どこか\n安いお店知らない？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1009, 1, 1, 102801, 2, 2, 0, "あたし、エレガントに\n振る舞えてる？\n…そう？　嬉しい♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1010, 1, 1, 102801, 3, 2, 0, "いっぱい働いて遊んで\nよく休んでまた働く。\nそれが健全な生活よね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1011, 1, 1, 102801, 4, 2, 0, "一緒に部屋の\n整頓でもする？\nハマると楽しいわよ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1012, 1, 4, 102801, 101, 2, 0, "あんたがいると\n子供たちも喜ぶわ♪\nもちろんあたしもね！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1013, 1, 8, 102801, 102, 2, 0, "ね、あんた今、幸せ？\n何びっくりしてるのよ\n大事なことじゃないの", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1014, 1, 1, 102801, 1, 2, 0, "面倒な用事は午前中に\n片づけてしまうのが、\n時間の使い方のコツよ", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1015, 1, 1, 102801, 2, 2, 0, "午後のティータイム\nとかどう？\n良いカフェがあるの♪", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1016, 1, 1, 102801, 3, 2, 0, "さ、エレガントな夜を\n楽しみましょ。\nモダンな音楽でも\nかけましょうか？", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1017, 1, 1, 102801, 1, 3, 0, "レディたるもの\n身嗜みはしっかりとね\n見本にならないと♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1018, 1, 1, 102801, 2, 3, 0, "これで仕事は一段落ね\nちょっと休憩…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1019, 1, 1, 102801, 3, 3, 0, "うふふ、\nかわいいものを\n見るのは幸せね～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1020, 1, 1, 102801, 4, 3, 0, "部屋中がいい香り…\nなんだかすごい贅沢\nしてる気分♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1021, 1, 1, 102801, 5, 3, 0, "風邪を引かないように\n身体を温めなくちゃ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1022, 1, 1, 102801, 6, 3, 0, "さっ！　家の中でも\n効率的に\nトレーニングよ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1023, 1, 1, 102801, 7, 3, 0, "もぐもぐ…はっ、\n口の周り、\n汚れてないわよね？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1024, 1, 1, 102801, 8, 3, 0, "知識から過去を学んで\n未来に活かすわよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1026, 1, 1, 102802, 1, 1, 0, "安売りのお店の情報\n整理しないと…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1027, 1, 1, 102802, 2, 1, 0, "どんなに苦労してても\nそう見せないのが真の\nエレガントなレディよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1028, 1, 1, 102802, 3, 1, 0, "安く済んであいつが\n喜ぶ料理とか…\nないかしら", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1029, 1, 1, 102802, 4, 1, 0, "子供たちの遊びって\nあたしが子供の頃と\nあまり変わらないのね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1030, 1, 1, 102802, 5, 1, 0, "商売はノンゼロサムが\n理想だなぁ。誰も損を\nしないってことだもの", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1031, 1, 8, 102802, 101, 1, 0, "焼きそばパンが\n食べたいな～\nあいつと…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1032, 1, 8, 102802, 102, 1, 0, "あいつと一緒に…\n色々なところに\nいきたいな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1033, 1, 1, 102802, 1, 2, 0, "今度買い物に\n付き合いなさい\nセールがあるのよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1034, 1, 1, 102802, 2, 2, 0, "あんたがいると子供\nたちも喜ぶわ♪\nもちろんあたしもね♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1035, 1, 1, 102802, 3, 2, 0, "いっぱい働いて遊んで\nよく休んでまた働く\nそれが健全な生活よね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1036, 1, 1, 102802, 4, 2, 0, "あんたとこうして\nのんびりするのも\nいいものね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1037, 1, 8, 102802, 101, 2, 0, "ねえ、今度遊びに\n行きたいな\nもちろん一緒に、よ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1038, 1, 8, 102802, 102, 2, 0, "あんたが手伝って\nくれるから、仕事も\n前より楽しいのよね♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1039, 1, 1, 102802, 1, 2, 0, "面倒な用事は午前中に\n片づけてしまうのが\n時間の使い方のコツよ", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1040, 1, 1, 102802, 2, 2, 0, "ランチにして午後の\n仕事に備えましょ！\nやることは一杯よ♪", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1041, 1, 1, 102802, 3, 2, 0, "ふわあ～\nもうこんな時間\n子供たちは\nもう寝たかしら", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1042, 1, 1, 102802, 1, 3, 0, "忙しい時こそ\n身だしなみは隙なく\nぴしっとね！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1043, 1, 1, 102802, 2, 3, 0, "これで仕事は一段落ね\nちょっと休憩…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1044, 1, 1, 102802, 3, 3, 0, "ふふふ\nずっと眺めてたく\nなっちゃうわね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1045, 1, 1, 102802, 4, 3, 0, "部屋中がいい香り…\nなんだかすごい贅沢\nしてる気分♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1046, 1, 1, 102802, 5, 3, 0, "風邪を引かないように\n身体を温めなくちゃ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1047, 1, 1, 102802, 6, 3, 0, "さっ！　家の中でも\n効率的に\nトレーニングよ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1048, 1, 1, 102802, 7, 3, 0, "しっかり食べて\n栄養補給、栄養補給！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1049, 1, 1, 102802, 8, 3, 0, "知識から過去を学んで\n未来に活かすわよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1051, 1, 1, 102901, 1, 1, 0, "可愛い子が\nいっぱいいるなぁ…\n誰か私とユニット\n組んでくれないかな？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1052, 1, 1, 102901, 2, 1, 0, "今度のライブ会場、\nいつもより大きいから\n楽しみなんだ～！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1053, 1, 1, 102901, 3, 1, 0, "新曲の衣装、もうすぐ\n出来あがるんだって！\n楽しみだな～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1054, 1, 1, 102901, 4, 1, 0, "彼とデュエット\nしてみたいな～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1055, 1, 1, 102901, 5, 1, 0, "キラキラ無病息災、\nドキドキ一日一善…\n作詞って難しい～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1056, 1, 4, 102901, 101, 1, 0, "ファンはもちろん\n大事だけど…\n彼のことは、もっと\n大切にしたいな…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1057, 1, 8, 102901, 102, 1, 0, "写真集見なくても、\n本物がここに\nいるのにな～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1058, 1, 1, 102901, 1, 2, 0, "握手会の練習、\n付き合ってくれない？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1059, 1, 1, 102901, 2, 2, 0, "写真集のポーズ\n練習中～！\nど、どうかな？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1060, 1, 1, 102901, 3, 2, 0, "何て書いてあるか\n読めないって？\nもー！\nこれは私のサイン！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1061, 1, 1, 102901, 4, 2, 0, "ね、次のライブ\n観に来てくれる？\nいい席とっとくから！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1062, 1, 4, 102901, 101, 2, 0, "ねぇ、台本読むの\nつきあってくれる？\n恋人役に\nなってほしいんだ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1063, 1, 8, 102901, 102, 2, 0, "新曲、聞いてくれた？\nキミとのことを\nテーマにしたんだよ。\nどこかは秘密だけど♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1064, 1, 1, 102901, 1, 2, 0, "朝の発声練習に\n行くけど、キミも\n一緒に来ない？", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1065, 1, 1, 102901, 2, 2, 0, "この時間は\nお腹すくけど…\n水着撮影があるから\nお昼抜いてるんだ", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1066, 1, 1, 102901, 3, 2, 0, "夜の帳が二人を包み\n…こういう方向性の\n歌詞も悪くないなぁ。", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1067, 1, 1, 102901, 1, 3, 0, "身だしなみオッケー！\nアイドルはいつでも\n完璧でいなくちゃ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1068, 1, 1, 102901, 2, 3, 0, "ふぅ…\nこんな姿、ファンには\n見せられないな～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1069, 1, 1, 102901, 3, 3, 0, "かわいい～♪\nこれは強力な\nライバル登場かな！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1070, 1, 1, 102901, 4, 3, 0, "ステキな香り～♪\n歌詞のイメージが\n浮かんできそう！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1071, 1, 1, 102901, 5, 3, 0, "体も暖まったし！\nダンスレッスンに\n行こうかな♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1072, 1, 1, 102901, 6, 3, 0, "ベストタイム更新！\nライブに向けて\n体力つけないと…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1073, 1, 1, 102901, 7, 3, 0, "太っちゃったら\nどうしよう…？\nでも手が止まらない～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1074, 1, 1, 102901, 8, 3, 0, "ステキなお話…\n新曲の作詞の\n参考になりそう♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1076, 1, 1, 102902, 1, 1, 0, "このお部屋すてき！\nファンに追われた時の\n隠れ家にピッタリね！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1077, 1, 1, 102902, 2, 1, 0, "そろそろイメチェン\nしてみようかな…\nそれともこの路線で\nもう少し…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1078, 1, 1, 102902, 3, 1, 0, "みんながくれた\nファンレター…\n私の大事な宝物…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1079, 1, 1, 102902, 4, 1, 0, "彼とデュエット\nしてみたいな～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1080, 1, 1, 102902, 5, 1, 0, "疾風怒濤、威風堂々\n獅子奮迅、唯我独尊…\nラブソングの作詞って\n難しいな～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1081, 1, 8, 102902, 101, 1, 0, "ファンはもちろん\n大事だけど…\n彼のことは、もっと\n大切にしたいな…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1082, 1, 8, 102902, 102, 1, 0, "トップアイドルの\nさらに先…\n彼とならその景色も\n見られるかな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1083, 1, 1, 102902, 1, 2, 0, "新曲の衣装、もうすぐ\n出来あがるんだって！\n楽しみだな～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1084, 1, 1, 102902, 2, 2, 0, "アイドルノゾミだけ\nじゃなくて、私の事も\nちゃんと見てね？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1085, 1, 1, 102902, 3, 2, 0, "キミに響かないなら\nファンにも響くわけ\nないよ！　練習練習！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1086, 1, 1, 102902, 4, 2, 0, "衣装のスカートは\n鉄壁なんだよ！\n残念でした～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1087, 1, 8, 102902, 101, 2, 0, "新曲、聞いてくれた？\nキミとのことを\nテーマにしたんだよ\nどこかは秘密だけど♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1088, 1, 8, 102902, 102, 2, 0, "ねぇ、私がトップ\nアイドルになっても\nちゃんと隣にいてね\n遠くに行かないでね？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1089, 1, 1, 102902, 1, 2, 0, "朝の発声練習に\n行くけど、キミも\n一緒に来ない？", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1090, 1, 1, 102902, 2, 2, 0, "お昼ごはん行かない？\n誰にも知られてない\nお店があるんだけど！", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1091, 1, 1, 102902, 3, 2, 0, "夜の帳が二人を包み\n…こういう方向性の\n歌詞も悪くないなぁ", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1092, 1, 1, 102902, 1, 3, 0, "身だしなみオッケー！\nアイドルはいつでも\n完璧でいなくちゃ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1093, 1, 1, 102902, 2, 3, 0, "ふぅ…\nこんな姿、ファンには\n見せられないな～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1094, 1, 1, 102902, 3, 3, 0, "かわいい～♪\nこれは強力な\nライバル登場かな！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1095, 1, 1, 102902, 4, 3, 0, "みんなのイメージする\nアイドルの香りは\nこんな感じかな～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1096, 1, 1, 102902, 5, 3, 0, "体も暖まったし！\nダンスレッスンに\n行こうかな♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1097, 1, 1, 102902, 6, 3, 0, "ベストタイム更新！\nライブに向けて\n体力つけないと…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1098, 1, 1, 102902, 7, 3, 0, "太っちゃったら\nどうしよう…？\nでも手が止まらない～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1099, 1, 1, 102902, 8, 3, 0, "この恋愛小説ステキ！\n私もアイドルじゃ\nなかったら…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1101, 1, 1, 103001, 1, 1, 0, "手裏剣の数が\n合わないデス！\nどこかに落とした\nデスか…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1102, 1, 1, 103001, 2, 1, 0, "クセモノ、隠れても\nむだデス！\n…気のせいデシタ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1103, 1, 1, 103001, 3, 1, 0, "金は天下の\nマワリモノ！\nところで、\nどういう意味デス？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1104, 1, 1, 103001, 4, 1, 0, "むむ、アクダイカンの\n気配がするデース", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1105, 1, 1, 103001, 5, 1, 0, "クニトリ合戦、\n楽しみデース！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1106, 1, 4, 103001, 101, 1, 0, "ショーグンと\n出会えて、\nワタシは果報者デス！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1107, 1, 8, 103001, 102, 1, 0, "最近、ショーグンの\nそばにいると\nドキドキするデス…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1108, 1, 1, 103001, 1, 2, 0, "なんでも\n命令するデス！\nショーグンの命は\n絶対デス！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1109, 1, 1, 103001, 2, 2, 0, "あっ、ショーグン、\n出陣はいつで\nゴザルデス！？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1110, 1, 1, 103001, 3, 2, 0, "さ、さすが\nショーグンデス…\nいつの間に背後を！？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1111, 1, 1, 103001, 4, 2, 0, "出陣の合図に\n備えるデス！\nほら貝は\nどこデスか！？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1112, 1, 4, 103001, 101, 2, 0, "見つめられると\nドキドキするデス…\n新しい忍法デスか？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1113, 1, 8, 103001, 102, 2, 0, "ショーグン！\nえへへ…これからも\nおそばに置いて\nクダサイね？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1114, 1, 1, 103001, 1, 2, 0, "おはようデス！\n今日も一日\n忍法日和デス！", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1115, 1, 1, 103001, 2, 2, 0, "そろそろお腹\n空いてきたデス。\n昼ごはんにするデス！", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1116, 1, 1, 103001, 3, 2, 0, "明日もきっと\nいい日デスよ、\nショーグン！", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1117, 1, 1, 103001, 1, 3, 0, "ちょんまげに\nしてみるのも\n面白いかもデス！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1118, 1, 1, 103001, 2, 3, 0, "心地よくて\n眠くなってきマス…\nもしやテキコクの\n罠デスか！？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1119, 1, 1, 103001, 3, 3, 0, "これを変わり身の\n術に…かわいそう\nだから止めるデス", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1120, 1, 1, 103001, 4, 3, 0, "ワビサビというもの\nデスね！\n素晴らしいデス！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1121, 1, 1, 103001, 5, 3, 0, "身体を暖めて\nイクサに向かうデス！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1122, 1, 1, 103001, 6, 3, 0, "アクロバットな忍法を\n使うには、体を\n鍛えないと危険デス！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1123, 1, 1, 103001, 7, 3, 0, "うーん、\n美味しいデス…\n幸せデス～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1124, 1, 1, 103001, 8, 3, 0, "これは…\nＢＵＳＨＩＤＯ…\nデスか？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1126, 1, 1, 103002, 1, 1, 0, "ふんふんふ～ん\n武器の手入れは\n念入りにしマスよ～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1127, 1, 1, 103002, 2, 1, 0, "クセモノ、隠れても\nむだデス！\n…気のせいデシタ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1128, 1, 1, 103002, 3, 1, 0, "金は天下の\nマワリモノ！\nところで\nどういう意味デス？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1129, 1, 1, 103002, 4, 1, 0, "むむ、アクダイカンの\n気配がするデース", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1130, 1, 1, 103002, 5, 1, 0, "クニトリ合戦\n楽しみデース！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1131, 1, 8, 103002, 101, 1, 0, "最近、ショーグンの\nそばにいると\nドキドキするデス…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1132, 1, 8, 103002, 102, 1, 0, "ショーグンの身は\n何があってもワタシが\n守るデス！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1133, 1, 1, 103002, 1, 2, 0, "あっ、ショーグン\n出陣はいつで\nゴザルデス！？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1134, 1, 1, 103002, 2, 2, 0, "イクサの前に\nホネ休めも大事デスよ\nゆっくりするデース！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1135, 1, 1, 103002, 3, 2, 0, "さ、さすがショーグン\nデス…いつの間に\n背後を！？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1136, 1, 1, 103002, 4, 2, 0, "一緒に忍法考える\nデス！\nショーグン用の\n忍法も必要デスね！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1137, 1, 8, 103002, 101, 2, 0, "見つめられると\nドキドキするデス…\n新しい忍法デスか？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1138, 1, 8, 103002, 102, 2, 0, "ショーグン！\nえへへ…これからも\nおそばに置いて\nクダサイね？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1139, 1, 1, 103002, 1, 2, 0, "いい朝デス！\nトーゴク風の朝ゴハン\n作るデス！", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1140, 1, 1, 103002, 2, 2, 0, "もうお昼デス\nお昼寝するデスか？", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1141, 1, 1, 103002, 3, 2, 0, "そろそろおネム\nデース…\n一緒に寝るデス？", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1142, 1, 1, 103002, 1, 3, 0, "服の乱れは心の乱れ\nデス！　きっちり\nしないといけないデス", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1143, 1, 1, 103002, 2, 3, 0, "心地よくて眠く\nなってきマス…\nもしやテキコクの\n罠デスか！？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1144, 1, 1, 103002, 3, 3, 0, "これを変わり身の\n術に…かわいそう\nだから止めるデス", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1145, 1, 1, 103002, 4, 3, 0, "ワビサビというもの\nデスね！\n素晴らしいデス！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1146, 1, 1, 103002, 5, 3, 0, "身体を暖めてイクサに\n向かうデス！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1147, 1, 1, 103002, 6, 3, 0, "ショーグンのお役に\n立つため、修行\nあるのみデス！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1148, 1, 1, 103002, 7, 3, 0, "美味しいデス…\n酢昆布を和えて\nみたいデス", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1149, 1, 1, 103002, 8, 3, 0, "物事を知って\n来たるべきイクサに\n活かすデス", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1151, 1, 1, 103101, 1, 1, 0, "お父さん、そろそろ\n子離れを…しない？\nそうですか…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1152, 1, 1, 103101, 2, 1, 0, "父は、いつも男性に\nきびしいですね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1153, 1, 1, 103101, 3, 1, 0, "占ってほしい人は、\nいつでも言って\nください", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1154, 1, 1, 103101, 4, 1, 0, "あなたの手相は…\nて、手を握るのって\n緊張しますね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1155, 1, 1, 103101, 5, 1, 0, "この部屋、風水的にも\n運気が上がりそうです", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1156, 1, 4, 103101, 101, 1, 0, "あの人とのこと占って\nみようかな…\n好き…嫌い…\n好き♪…き、嫌い", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1157, 1, 8, 103101, 102, 1, 0, "私との相性ですか？\nそれなら占う必要は\nありませんよ…\nふふっ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1158, 1, 1, 103101, 1, 2, 0, "女難の相が\n出ていますね…\n気をつけてください", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1159, 1, 1, 103101, 2, 2, 0, "今日のあなたの運勢は\n大きょ…\n大吉です", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1160, 1, 1, 103101, 3, 2, 0, "父と散歩中です。\n毎日決まったコースを\n散歩させないと、\nストレスが溜まるので", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1161, 1, 1, 103101, 4, 2, 0, "小僧！\nシノブに触るとは\nいい度胸じゃねぇか！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1162, 1, 4, 103101, 101, 2, 0, "あなたに触られると、\n動悸が早くなります\nもしかして心霊現象？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1163, 1, 8, 103101, 102, 2, 0, "今日もたくさんの幽霊\nに慕われていますね。\n素敵です", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1164, 1, 1, 103101, 1, 2, 0, "おはようございます\n今日の運勢を\n占いましょうか？", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1165, 1, 1, 103101, 2, 2, 0, "今日のラッキー\nアイテムはドクロです\n良ければ父を\nお貸ししましょうか？", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1166, 1, 1, 103101, 3, 2, 0, "今日のあなたの運気は\n最高潮でした。\nもうすぐ今日は\n終わりますが", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1167, 1, 1, 103101, 1, 3, 0, "お父さん。\nいつも、身だしなみに\n気を使ってるけど、\nあまり変わってないよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1168, 1, 1, 103101, 2, 3, 0, "一休みしましょう。\n憑かれて疲れました", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1169, 1, 1, 103101, 3, 3, 0, "かわいい…\n付喪神が宿らないかな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1170, 1, 1, 103101, 4, 3, 0, "お父さん、\nリラックスし過ぎると\n成仏しちゃうよ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1171, 1, 1, 103101, 5, 3, 0, "気持ちいいです…\n極楽浄土みたい…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1172, 1, 1, 103101, 6, 3, 0, "はぁはぁ…\nお、お父さん、\n代わりに運動して…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1173, 1, 1, 103101, 7, 3, 0, "お父さんも\n食べたいの？\n死んでるのに？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1174, 1, 1, 103101, 8, 3, 0, "この本…\nもしかして予言書？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1176, 1, 1, 103102, 1, 1, 0, "ここにいる幽霊は\nみんな幸せそうです", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1177, 1, 1, 103102, 2, 1, 0, "お父さんが\n女の子ばかりに\n声をかけるので\n困っています", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1178, 1, 1, 103102, 3, 1, 0, "成仏はうれしいこと\nです…でも友達が\n減るのは寂しいですね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1179, 1, 1, 103102, 4, 1, 0, "幽霊に好かれる人は\n素敵な方です\nもちろん、あなたも", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1180, 1, 1, 103102, 5, 1, 0, "お父さん、そろそろ\n子離れを…しない？\nそうですか…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1181, 1, 8, 103102, 101, 1, 0, "生きている人に\n好かれるには、\nどうすればいい\nのでしょう？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1182, 1, 8, 103102, 102, 1, 0, "お父さん、どうしたら\nあの人のことを\n認めてくれますか？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1183, 1, 1, 103102, 1, 2, 0, "この部屋、風水的にも\n運気が上がりそうです", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1184, 1, 1, 103102, 2, 2, 0, "シノブには指一本\n触れさせん！\nガブッ！！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1185, 1, 1, 103102, 3, 2, 0, "あなたの手相は…\nて、手を握るのって\n緊張しますね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1186, 1, 1, 103102, 4, 2, 0, "ラップ音？\nいえ、父のいびきです", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1187, 1, 8, 103102, 101, 2, 0, "私との相性ですか？\nそれなら占う必要は\nありませんよ…\nふふっ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1188, 1, 8, 103102, 102, 2, 0, "前世って信じますか？\n私とあなたは\nもしかすると…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1189, 1, 1, 103102, 1, 2, 0, "今日のラッキー\nアイテムはドクロです\n良ければ父を\nお貸ししましょうか？", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1190, 1, 1, 103102, 2, 2, 0, "明るい時間は\nあまり得意では\nないんです…", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1191, 1, 1, 103102, 3, 2, 0, "夜のお散歩に\n行きませんか？\n知り合いの幽霊に\n挨拶して回るんです", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1192, 1, 1, 103102, 1, 3, 0, "お父さん\nいつも、身だしなみに\n気を使ってるけど\nあまり変わってないよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1193, 1, 1, 103102, 2, 3, 0, "うとうと…\nあれ…\n走馬灯が見える…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1194, 1, 1, 103102, 3, 3, 0, "かわいい…\nお父さんみたい…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1195, 1, 1, 103102, 4, 3, 0, "お父さん\nリラックスし過ぎると\n成仏しちゃうよ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1196, 1, 1, 103102, 5, 3, 0, "快適です…\n幽霊もどんどん\n集まってきますね…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1197, 1, 1, 103102, 6, 3, 0, "はぁはぁ…\nお、お父さん、\n代わりに運動して…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1198, 1, 1, 103102, 7, 3, 0, "お父さんの分も、\n私が食べるね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1199, 1, 1, 103102, 8, 3, 0, "この本の占い\n私の占いと違う…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1201, 1, 1, 103201, 1, 1, 0, "私だって、一人で\n買い物ぐらい\nできますわ…きっと", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1202, 1, 1, 103201, 2, 1, 0, "そろそろ新しい\n案件を始めないと\nいけませんわね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1203, 1, 1, 103201, 3, 1, 0, "一日も早く、\n一人前の商人に\nなりたいものです", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1204, 1, 1, 103201, 4, 1, 0, "平和を買うのには\nどれほどのお金が\n必要なのかしら？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1205, 1, 1, 103201, 5, 1, 0, "ここももう少し広いと\n落ち着くのですけど。\n改築しませんこと？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1206, 1, 4, 103201, 101, 1, 0, "あの方はお金で雇った\nわけではないのに\n私といてくださる…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1207, 1, 8, 103201, 102, 1, 0, "たとえ私が間違っても\nきっとあの方が\n正してくれますわ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1208, 1, 1, 103201, 1, 2, 0, "私と、庶民向けの\nお店へ一緒に\n参りませんか？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1209, 1, 1, 103201, 2, 2, 0, "資産運用？\nお幾らほど\n増やしたいのです？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1210, 1, 1, 103201, 3, 2, 0, "どんぶり勘定だとよく\n言われるのですが…\n例えが小さすぎます！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1211, 1, 1, 103201, 4, 2, 0, "お金で解決するのは\n悪いことでは\nありませんわよね？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1212, 1, 4, 103201, 101, 2, 0, "その、よろしければ\n私とお散歩など\nいかがです？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1213, 1, 8, 103201, 102, 2, 0, "もしあなたさまが\n道に迷われるなら、\n私もお供いたします！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1214, 1, 1, 103201, 1, 2, 0, "早起きは三ルピの徳と\n申しますが…\nもっとお得ですわね", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1215, 1, 1, 103201, 2, 2, 0, "昼食は町の屋台で\n立ったまま食べて\nみたいですわ！", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1216, 1, 1, 103201, 3, 2, 0, "よろしければ\nお休み前のミルクを\n御一緒にいかが？", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1217, 1, 1, 103201, 1, 3, 0, "だらしない身なりでは\n取引相手に信用して\nいただけませんものね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1218, 1, 1, 103201, 2, 3, 0, "この感触と安らぎ…\n素晴らしい\n費用対効果ですわ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1219, 1, 1, 103201, 3, 3, 0, "何の見返りも求めず\nただひたすら愛でて\nいたいのです！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1220, 1, 1, 103201, 4, 3, 0, "目に見えないものに\nお金をかけるのが\n真の贅沢ですわ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1221, 1, 1, 103201, 5, 3, 0, "誰ひとり寒い思いを\nしない町にするために\n私にできる事は…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1222, 1, 1, 103201, 6, 3, 0, "健康はお金では\n買えませんが\n運動器具は別ですわ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1223, 1, 1, 103201, 7, 3, 0, "なかなか\n美味ですわ\nシェフはどちらに？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1224, 1, 1, 103201, 8, 3, 0, "経済の仕組みを\nしっかりと\n学ばなくては…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1226, 1, 1, 103202, 1, 1, 0, "そろそろ新しい\n案件を始めないと\nいけませんわね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1227, 1, 1, 103202, 2, 1, 0, "ええと、これは\nおいくらで買ったん\nでしたっけ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1228, 1, 1, 103202, 3, 1, 0, "お金を見せたら\n態度が変わる人は\n信用できませんわ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1229, 1, 1, 103202, 4, 1, 0, "地図が読めずとも\n道に迷わない方って\nいらっしゃるのですね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1230, 1, 1, 103202, 5, 1, 0, "この狭さも\n慣れれば\n快適ですわ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1231, 1, 8, 103202, 101, 1, 0, "あの方はお金で雇った\nわけではないのに\n私といてくださる…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1232, 1, 8, 103202, 102, 1, 0, "たとえ私が間違っても\nきっとあの方が\n正してくれますわ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1233, 1, 1, 103202, 1, 2, 0, "一人前の商人に\n相応しい居振る舞いを\nしたいものですわ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1234, 1, 1, 103202, 2, 2, 0, "幼い頃\nお店屋さんごっこを\nなさいましたか？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1235, 1, 1, 103202, 3, 2, 0, "資産運用？\nお幾らほど\n増やしたいのです？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1236, 1, 1, 103202, 4, 2, 0, "今度一緒に…\nパーティにでも\n行きませんこと？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1237, 1, 8, 103202, 101, 2, 0, "その、よろしければ\n私とお散歩など\nいかがです？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1238, 1, 8, 103202, 102, 2, 0, "あなたさまの優しさは\nお金には\n代えられませんわね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1239, 1, 1, 103202, 1, 2, 0, "なぜだか\n眠り足りないような\n感じがしますわ…", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1240, 1, 1, 103202, 2, 2, 0, "昼食は町の屋台で\n立ったまま食べて\nみたいですわ！", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1241, 1, 1, 103202, 3, 2, 0, "よろしければ\nお休み前のミルクを\n御一緒にいかが？", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1242, 1, 1, 103202, 1, 3, 0, "ドレスは汚れていない\nようですが…やだ\n髪がほつれてますわ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1243, 1, 1, 103202, 2, 3, 0, "こういったものに\n腰を沈めると…\nき、気が緩んで…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1244, 1, 1, 103202, 3, 3, 0, "何の見返りも求めず\nただひたすら愛でて\nいたいのです！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1245, 1, 1, 103202, 4, 3, 0, "香りは質のいいものと\nそうでないものが\nはっきり分かりますわ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1246, 1, 1, 103202, 5, 3, 0, "誰ひとり寒い思いを\nしない町にするために\n私にできる事は…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1247, 1, 1, 103202, 6, 3, 0, "目標は遠くに\n置いた方が励みに\nなりますわ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1248, 1, 1, 103202, 7, 3, 0, "なかなか\n美味ですわ\nシェフはどちらに？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1249, 1, 1, 103202, 8, 3, 0, "商人って、物語では\n悪役にされやすいの\nですね…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1251, 1, 1, 103301, 1, 1, 0, "家の中のものを使って\n物ボケ、考えるべ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1252, 1, 1, 103301, 2, 1, 0, "毎日の日課…それは\n炊事、洗濯、大喜利\nだべ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1253, 1, 1, 103301, 3, 1, 0, "うちに塀ができたら、\nへー、って言いたい！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1254, 1, 1, 103301, 4, 1, 0, "オラが作る料理は\nこの後スタッフで\n美味しくいただくべよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1255, 1, 1, 103301, 5, 1, 0, "エリザベスも\n連れてきてぇ～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1256, 1, 4, 103301, 101, 1, 0, "あんちゃんも、\nエリザベスと同じ位\nめんこいべ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1257, 1, 8, 103301, 102, 1, 0, "オラとあんちゃん、\n息がぴったりに\nなってきたべ…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1258, 1, 1, 103301, 1, 2, 0, "おぉ！　そんな急に\nツッコまれたら、\nびっくらこくべやー！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1259, 1, 1, 103301, 2, 2, 0, "ネタはオラが書くけど\nあんちゃんが\n書きたかったら\n書いてもいいんだぞ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1260, 1, 1, 103301, 3, 2, 0, "あんちゃん、オラには\n容赦なんかせずに\nツッコんでくれて\nいいから！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1261, 1, 1, 103301, 4, 2, 0, "押すなよ、\n絶対押すなよ\n…なんて\n言ってないべさ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1262, 1, 4, 103301, 101, 2, 0, "うう…あんちゃんに\nツッコまれたくて\nうずうずしてきたべ～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1263, 1, 8, 103301, 102, 2, 0, "将来的には夫婦漫才\nってのも…あ、ひ、\nひとりごとだべさ～！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1264, 1, 1, 103301, 1, 2, 0, "おはよう！　一緒に\n牛の乳しぼりいくべ？", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1265, 1, 1, 103301, 2, 2, 0, "お昼だ～腹が減っては\n笑いはできぬ～！", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1266, 1, 1, 103301, 3, 2, 0, "今日もよく働いたべ！\nさ、今からは\nお笑いの練習だ～！", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1267, 1, 1, 103301, 1, 3, 0, "舞台に上がる前に\n身なりは整えねぇと\nだめだべ～！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1268, 1, 1, 103301, 2, 3, 0, "ナイスなイスに\n座りたい、\nなんちゃって！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1269, 1, 1, 103301, 3, 3, 0, "めんこいのは、\nスベっても許される\nからずるいべな～？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1270, 1, 1, 103301, 4, 3, 0, "必要以上に\n癒されちまうべや～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1271, 1, 1, 103301, 5, 3, 0, "ぽかぽかだ…\nウケた会場みてぇだ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1272, 1, 1, 103301, 6, 3, 0, "お笑い芸人は、\n体が資本だべさ～！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1273, 1, 1, 103301, 7, 3, 0, "もぐもぐ…\nボケることも\n忘れちまう美味さだ～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1274, 1, 1, 103301, 8, 3, 0, "ネタ作りのヒントが\n載ってねぇか探すべ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1276, 1, 1, 103302, 1, 1, 0, "さぁ今日も張り切って\n漫才の練習するべさ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1277, 1, 1, 103302, 2, 1, 0, "この部屋でも、商品\n売っていいんだべか？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1278, 1, 1, 103302, 3, 1, 0, "眼鏡を頭の上に乗せて\n「メガネメガネ～」\nって言う練習した方が\nいいべか？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1279, 1, 1, 103302, 4, 1, 0, "漫才を持ち時間に\n収めるには、何度も\n練習するしかねぇべ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1280, 1, 1, 103302, 5, 1, 0, "エリザベスも\n連れてきてぇ～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1281, 1, 8, 103302, 101, 1, 0, "うう…あんちゃんに\nツッコまれたくて\nうずうずしてきたべ～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1282, 1, 8, 103302, 102, 1, 0, "あんちゃんに\n採れたての食材を\n食わしてやりてぇ～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1283, 1, 1, 103302, 1, 2, 0, "押すなよ\n絶対押すなよ\n…なんて\n言ってないべさ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1284, 1, 1, 103302, 2, 2, 0, "あんちゃん、百連\nツッコミ特訓だ～！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1285, 1, 1, 103302, 3, 2, 0, "あんちゃん、オラには\n容赦なんかせずに\nツッコんでくれて\nいいから！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1286, 1, 1, 103302, 4, 2, 0, "「なんでだべ～！」\nって言いながら\nツッコむといいべ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1287, 1, 8, 103302, 101, 2, 0, "あはは～あんちゃんの\nツッコミはやっぱり\n気持ちいいなぁ～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1288, 1, 8, 103302, 102, 2, 0, "将来的には夫婦漫才\nってのも…あ、ひ\nひとりごとだべさ～！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1289, 1, 1, 103302, 1, 2, 0, "おはよう！\n今日もいい\nネタ作り日和だ！", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1290, 1, 1, 103302, 2, 2, 0, "お昼だな\nご飯を食べたら、\nネタの練習するべ！", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1291, 1, 1, 103302, 3, 2, 0, "もうこんな時間だべ\nネタ作りは\n明日にするべ…", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1292, 1, 1, 103302, 1, 3, 0, "うーん…衣装、\nずれてねぇべか？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1293, 1, 1, 103302, 2, 3, 0, "ナイスなイスに\n座りたい\nなんちゃって！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1294, 1, 1, 103302, 3, 3, 0, "はう～\nなまらめんこいべや～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1295, 1, 1, 103302, 4, 3, 0, "必要以上に\n癒されちまうべや～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1296, 1, 1, 103302, 5, 3, 0, "笑いをとることを\n忘れるほど\nあったけぇべ～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1297, 1, 1, 103302, 6, 3, 0, "たくさん運動して\n動きでも笑いがとれる\nように頑張るべ～！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1298, 1, 1, 103302, 7, 3, 0, "もぐもぐ…\nボケることも\n忘れちまう美味さだ～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1299, 1, 1, 103302, 8, 3, 0, "幅広く教養を\n身につけて\nいいネタ作るべ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1301, 1, 1, 103401, 1, 1, 0, "通りにいる\nカップル…爆発\nすればいいのに…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1302, 1, 1, 103401, 2, 1, 0, "う～ん、仕事なんて\nしないで、\n飲んでたいな～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1303, 1, 1, 103401, 3, 1, 0, "今日は一日、\n外に出ない！\nもう決めたわ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1304, 1, 1, 103401, 4, 1, 0, "お腹空いた…\nけど、外に出るのは\n面倒だなぁ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1305, 1, 1, 103401, 5, 1, 0, "うぅ…頭がガンガン\nする…昨日、\n飲み過ぎたかな…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1306, 1, 4, 103401, 101, 1, 0, "デートとか…\n誘っても嫌がられ\nないかな…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1307, 1, 8, 103401, 102, 1, 0, "もしかして運命の\n相手だったり…\nするのかな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1308, 1, 1, 103401, 1, 2, 0, "やっぱり一杯目が\n一番おいしいわ～！\n…え、見てた！？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1309, 1, 1, 103401, 2, 2, 0, "頼れるお姉さんだって\nところ、もっと見せて\nあげるからね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1310, 1, 1, 103401, 3, 2, 0, "ねえ、一緒に買い物に\n行かない？　その…\nの、飲み物とか…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1311, 1, 1, 103401, 4, 2, 0, "ひっく、うふふ…\nもっとお姉さんと\nお話しようよ～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1312, 1, 4, 103401, 101, 2, 0, "極力人付き合いは\nしない主義なんだけど\nキミは例外かも…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1313, 1, 8, 103401, 102, 2, 0, "いつか…\nキミと一緒に飲めたら\n楽しいだろうな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1314, 1, 1, 103401, 1, 2, 0, "おはよう…朝は\nちょっとつらいな…\n低血圧なのよ…", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1315, 1, 1, 103401, 2, 2, 0, "おっひるー♪\nなに食べよっかな～♪", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1316, 1, 1, 103401, 3, 2, 0, "ふわあ…\n眠る前にちょっと\n一杯…冗談よ", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1317, 1, 1, 103401, 1, 3, 0, "うう…\n髪がボサボサ…\nちゃんとしなきゃ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1318, 1, 1, 103401, 2, 3, 0, "自然体でゴロゴロ\nするのって\n幸せだなぁ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1319, 1, 1, 103401, 3, 3, 0, "こういうの男の子に\nプレゼントして\nもらいたいな…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1320, 1, 1, 103401, 4, 3, 0, "うわあ、\nこれ、私の好きな\n香りだー！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1321, 1, 1, 103401, 5, 3, 0, "ぽっかぽか…\nあとは熱い一杯が\nあれば…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1322, 1, 1, 103401, 6, 3, 0, "家でゴロゴロして\n付いちゃったお肉を\n落とさないと…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1323, 1, 1, 103401, 7, 3, 0, "もぐもぐ…\nおーいしー！\n…太らないよね？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1324, 1, 1, 103401, 8, 3, 0, "大人の女らしく\n色んなことを学んで\nおかなくっちゃ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1326, 1, 1, 103402, 1, 1, 0, "仕事終わりの一杯の\nために、今日も\n頑張らないと", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1327, 1, 1, 103402, 2, 1, 0, "今日は\nのんびり\n過ごそうかな～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1328, 1, 1, 103402, 3, 1, 0, "あ…\nお皿、洗って\nなかった…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1329, 1, 1, 103402, 4, 1, 0, "もう少し、大人の\n魅力が出せるように\nなりたいな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1330, 1, 1, 103402, 5, 1, 0, "素敵な彼氏…\n欲しいなぁ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1331, 1, 8, 103402, 101, 1, 0, "彼と私\n釣り合うかな…\n大丈夫よね？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1332, 1, 8, 103402, 102, 1, 0, "今後のために\nちょっとは花嫁修業\nしてみようかな…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1333, 1, 1, 103402, 1, 2, 0, "何事も\nお姉さんと一緒に\n楽しく頑張りましょ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1334, 1, 1, 103402, 2, 2, 0, "ね、ねえ！\nキミは年上と年下\nどっちが好きかな？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1335, 1, 1, 103402, 3, 2, 0, "もう…\n構ってくれないと\n泣いちゃうぞ～？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1336, 1, 1, 103402, 4, 2, 0, "お姉さんが～\n可愛がって\nあげようか～？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1337, 1, 8, 103402, 101, 2, 0, "極力人付き合いは\nしない主義なんだけど\nキミは例外かも…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1338, 1, 8, 103402, 102, 2, 0, "お姉さんとチュー\nする？　ふふふ～\n冗談で～す！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1339, 1, 1, 103402, 1, 2, 0, "さーて、今日も\nいっちょやるぞー！", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1340, 1, 1, 103402, 2, 2, 0, "後でのんびり\nストレッチでも\nしようっと♪", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1341, 1, 1, 103402, 3, 2, 0, "一日よく働いた～！\n自分にごほうび\nあげたいなー♪", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1342, 1, 1, 103402, 1, 3, 0, "仕事の前に、髪とか\n整えないと…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1343, 1, 1, 103402, 2, 3, 0, "自然体でゴロゴロ\nするのって\n幸せだなぁ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1344, 1, 1, 103402, 3, 3, 0, "もう大人だし…\nあんまり可愛いのは\n似合わないかな…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1345, 1, 1, 103402, 4, 3, 0, "うわあ\nこれ、私の好きな\n香りだー！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1346, 1, 1, 103402, 5, 3, 0, "あったかーい…\nだ、だらけ\nちゃいそう…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1347, 1, 1, 103402, 6, 3, 0, "地道な努力が\nリア充への\n第一歩！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1348, 1, 1, 103402, 7, 3, 0, "おーいしっ♪\nこのために\n生きてるわねえ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1349, 1, 1, 103402, 8, 3, 0, "大人の女らしく\n色んなことを学んで\nおかなくっちゃ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1351, 1, 1, 103801, 1, 1, 0, "大切な人に\n本をすすめるのって\nけっこう難しいなぁ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1352, 1, 1, 103801, 2, 1, 0, "き、きらーん☆\nお姉ちゃんに\n似てるかな…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1353, 1, 1, 103801, 3, 1, 0, "そろそろ好きな\n作家さんの新刊が\n出るから楽しみ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1354, 1, 1, 103801, 4, 1, 0, "ちょっと、休憩…\n本でも読もうかな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1355, 1, 1, 103801, 5, 1, 0, "もっとたくさんの本に\n囲まれて生活したいな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1356, 1, 4, 103801, 101, 1, 0, "むかしむかしある所に\nシオリと\nあの人が…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1357, 1, 8, 103801, 102, 1, 0, "この本の主人公、\nあの人に\n似ているかも…\nかっこいいな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1358, 1, 1, 103801, 1, 2, 0, "ケホケホ…\nコンコン…\nゴホッゴホッ…\nへ、平気ですよ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1359, 1, 1, 103801, 2, 2, 0, "あ…お姉ちゃんと\n約束があったんだ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1360, 1, 1, 103801, 3, 2, 0, "しおりを探している？\nあ、本に挟む方ですね\nドキドキしました…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1361, 1, 1, 103801, 4, 2, 0, "最近調子がいいのは\n牧場の牛乳やチーズの\nおかげでしょうか？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1362, 1, 4, 103801, 101, 2, 0, "読みたい本も\nあなたと行きたい所も\n増えるばっかりです", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1363, 1, 8, 103801, 102, 2, 0, "私の病気、\n悪化してるのかも！？\nすごく胸がドキドキ\nするんです…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1364, 1, 1, 103801, 1, 2, 0, "えっと、朝食の前には\nこの薬を飲んで…\n食後にはこの薬を…", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1365, 1, 1, 103801, 2, 2, 0, "ふぅ…午前中\n動いたので体力が…\nすこし休みますね", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1366, 1, 1, 103801, 3, 2, 0, "早く寝ないと\n体に良くないですよね\nでも気になっている\n本があって…", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1367, 1, 1, 103801, 1, 3, 0, "寝てばかりなので、\n髪が乱れてないかな\n…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1368, 1, 1, 103801, 2, 3, 0, "少し休もう\n…やっぱり\nたくさん休もう", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1369, 1, 1, 103801, 3, 3, 0, "かわいいものが\n枕元にあると\n安心できるんですよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1370, 1, 1, 103801, 4, 3, 0, "いい香り…\n病は気からと言うので\n雰囲気も大事ですね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1371, 1, 1, 103801, 5, 3, 0, "こうして温まって\nいると、病気も\n治る気がします", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1372, 1, 1, 103801, 6, 3, 0, "運動…イメージ\nトレーニングだけじゃ\nだめでしょうか…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1373, 1, 1, 103801, 7, 3, 0, "少食ですけど\nこれならたくさん\n食べられそうです", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1374, 1, 1, 103801, 8, 3, 0, "続きが気になって\n読むのを止める\nタイミングが…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1376, 1, 1, 103802, 1, 1, 0, "しっかり休まないと\n早く回復するように", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1377, 1, 1, 103802, 2, 1, 0, "き、きらーん☆\nお姉ちゃんに\n似てるかな…？　", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1378, 1, 1, 103802, 3, 1, 0, "病室よりも…\nずっとこの部屋に\nいたいですね…　", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1379, 1, 1, 103802, 4, 1, 0, "そろそろ好きな\n作家さんの新刊が\n出るから楽しみ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1380, 1, 1, 103802, 5, 1, 0, "大切な人に\n本をすすめるのって\nけっこう難しいなぁ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1381, 1, 8, 103802, 101, 1, 0, "むかしむかしある所に\nシオリと\nあの人が…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1382, 1, 8, 103802, 102, 1, 0, "あの人の\nためにも早く\n病気を治さないと…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1383, 1, 1, 103802, 1, 2, 0, "ケホケホ…\nコンコン…\nゴホッゴホッ…\nへ、平気ですよ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1384, 1, 1, 103802, 2, 2, 0, "新記録です！\n一週間連続、\n平熱を保ちました！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1385, 1, 1, 103802, 3, 2, 0, "あ…お薬の時間…\n苦いけど我慢しなきゃ　", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1386, 1, 1, 103802, 4, 2, 0, "最近調子がいいのは\n牧場の牛乳やチーズの\nおかげでしょうか？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1387, 1, 8, 103802, 101, 2, 0, "しおりを探している？\nあ、本に挟む方ですね\nドキドキしました…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1388, 1, 8, 103802, 102, 2, 0, "私の病気\n悪化してるのかも！？\nすごく胸がドキドキ\nするんです…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1389, 1, 1, 103802, 1, 2, 0, "えっと、朝食の前には\nこの薬を飲んで…\n食後にはこの薬を…", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1390, 1, 1, 103802, 2, 2, 0, "…お昼の薬？\nあ、飲み忘れてました\nありがとうございます", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1391, 1, 1, 103802, 3, 2, 0, "この薬を飲むと\n眠くなっちゃうんです\nでもまだ、あなたと\nお話したいし～", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1392, 1, 1, 103802, 1, 3, 0, "寝てばかりなので、\n髪が乱れてないかな\n…？　", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1393, 1, 1, 103802, 2, 3, 0, "少し休もう\n…やっぱり\nたくさん休もう", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1394, 1, 1, 103802, 3, 3, 0, "この子がいれば\n病室に一人でも\n寂しくないですね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1395, 1, 1, 103802, 4, 3, 0, "いろんな効能が？\n健康になる香りも\nあるのでしょうか？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1396, 1, 1, 103802, 5, 3, 0, "こうして温まって\nいると、病気も\n治る気がします", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1397, 1, 1, 103802, 6, 3, 0, "私も、少しは\n体力をつけないと\nいけませんよね　", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1398, 1, 1, 103802, 7, 3, 0, "いっぱい食べて\n健康な体にならないと", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1399, 1, 1, 103802, 8, 3, 0, "続きが気になって\n読むのを止める\nタイミングが…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1401, 1, 1, 104001, 1, 1, 0, "友達ができたら、\n山の上でお弁当…\n夢が広がります…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1402, 1, 1, 104001, 2, 1, 0, "あ、忘れずに\nお花に水を\nあげなきゃ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1403, 1, 1, 104001, 3, 1, 0, "お菓子を作って、\nみなさんに振る舞って\n友達作戦！　とか…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1404, 1, 1, 104001, 4, 1, 0, "そろそろ新しい\n草人形を作って\n友達作りの特訓を…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1405, 1, 1, 104001, 5, 1, 0, "植物のお世話って、\nぼっちにとっては、\n救い…なんです…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1406, 1, 4, 104001, 101, 1, 0, "ＢＢ団の活動日…\nまだかな…\nうふふふっ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1407, 1, 8, 104001, 102, 1, 0, "もっともっとあの人の\nそばで、一緒に友達を\n作っていきたいな…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1408, 1, 1, 104001, 1, 2, 0, "あ、こんにちは！\n今日もＢＢ団で\nお世話になります！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1409, 1, 1, 104001, 2, 2, 0, "あの…私の友達作りの\n練習方法、間違って\nませんよね…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1410, 1, 1, 104001, 3, 2, 0, "そうだ、今度、\n森に一緒に木の苗を\n植えに行きませんか？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1411, 1, 1, 104001, 4, 2, 0, "人とお話するのって、\n私、大好きです！\n友達ふやすぞ～！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1412, 1, 4, 104001, 101, 2, 0, "あなたと話してると、\nまるで私がぼっちじゃ\nないみたいです！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1413, 1, 8, 104001, 102, 2, 0, "友達もほしいけど…\nあなたに会えたことが\n私の一番の幸せです", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1414, 1, 1, 104001, 1, 2, 0, "おはようございます！\n今日もＢＢ団、\nがんばるぞー！", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1415, 1, 1, 104001, 2, 2, 0, "なんだか、お腹が\n空いてきました。\nお昼ごはんー♪", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1416, 1, 1, 104001, 3, 2, 0, "もう眠いかもです…\n明日は友達、\nできるといいなあ…", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1417, 1, 1, 104001, 1, 3, 0, "私がもっと\n可愛ければ、\n友達できるかなあ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1418, 1, 1, 104001, 2, 3, 0, "ふわあ…のんびり\nしすぎないように\n気を付けなきゃです", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1419, 1, 1, 104001, 3, 3, 0, "かわいいなあ。\nこの子とも\n友達になりたい…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1420, 1, 1, 104001, 4, 3, 0, "いい香りです…\n精神統一して\n友達のイメトレ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1421, 1, 1, 104001, 5, 3, 0, "灯りを見てると、\n心がなごみます～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1422, 1, 1, 104001, 6, 3, 0, "もぐもぐ…\nおいしいです…！\nみなさんもぜひ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1423, 1, 1, 104001, 7, 3, 0, "おいしいです…\n私に友達がいたら、\n分けてあげたいな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1424, 1, 1, 104001, 8, 3, 0, "友達作りの\n参考になりそうなこと\n書いてないかな～？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1426, 1, 1, 104002, 1, 1, 0, "友達作りは、焦らず\n慌てず、じっくりと…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1427, 1, 1, 104002, 2, 1, 0, "ひとつ屋根の下\nって感じ…\nいいですねえ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1428, 1, 1, 104002, 3, 1, 0, "煮汁、煮汁が\n噴きこぼれて\nいませんか～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1429, 1, 1, 104002, 4, 1, 0, "今度、みなさんと\nピクニックとか…\nいってみたいな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1430, 1, 1, 104002, 5, 1, 0, "ぼっちでもいい\nたくましく育てば\n…なんて", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1431, 1, 8, 104002, 101, 1, 0, "最近、毎日あの人の\nこと考えちゃいます\nどうしてだろう…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1432, 1, 8, 104002, 102, 1, 0, "もっともっとあの人の\nそばで、一緒に友達を\n作っていきたいな…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1433, 1, 1, 104002, 1, 2, 0, "あ！　ＢＢ団の活動\nですか？　嬉しくて\n飛び跳ねちゃいます！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1434, 1, 1, 104002, 2, 2, 0, "あなたとのお話で友達\n作りの練習になるなら\n一石二鳥です！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1435, 1, 1, 104002, 3, 2, 0, "私少しは人と話しても\nテンパらなくなって\nきましたかね…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1436, 1, 1, 104002, 4, 2, 0, "あの、友達作りの\n新しい作戦\n考えてみたんですっ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1437, 1, 8, 104002, 101, 2, 0, "あなたとお話する時の\nこの気持ち、私の宝物\nなんです…えへへ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1438, 1, 8, 104002, 102, 2, 0, "友達もほしいけど…\nあなたに会えたことが\n私の一番の幸せです", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1439, 1, 1, 104002, 1, 2, 0, "朝ですね！\nこんないい朝は\n友達作りのチャンス！", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1440, 1, 1, 104002, 2, 2, 0, "あの…そろそろお腹\nすきませんか？\nお昼ごはんは…", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1441, 1, 1, 104002, 3, 2, 0, "もう眠いかもです…\n明日は友達\nできるといいなあ…", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1442, 1, 1, 104002, 1, 3, 0, "私…見た目\nそこまで変じゃ\nないですよね…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1443, 1, 1, 104002, 2, 3, 0, "ほわあ～…\nふかふかって\nいいものですね～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1444, 1, 1, 104002, 3, 3, 0, "この子みたいな\nあいくるしさを\n目指したいです！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1445, 1, 1, 104002, 4, 3, 0, "灯りを見てると\n心がなごみます～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1446, 1, 1, 104002, 5, 3, 0, "ぬくぬくで、なんだか\n眠くなっちゃいますね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1447, 1, 1, 104002, 6, 3, 0, "身体を鍛えて\nみなさんを\n守ります！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1448, 1, 1, 104002, 7, 3, 0, "もぐもぐ…\nおいしいです…！\nみなさんもぜひ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1449, 1, 1, 104002, 8, 3, 0, "友達作りの\n参考になりそうなこと\n書いてないかな～？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1451, 1, 1, 104201, 1, 1, 0, "唱喚の歌は、みだりに\n歌っていいものでは\nないんです", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1452, 1, 1, 104201, 2, 1, 0, "ここでアイドルの\n練習は、さすがに\n迷惑ですよね？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1453, 1, 1, 104201, 3, 1, 0, "練習してもあんまり\n疲れなくなった…\nアイドルのおかげかな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1454, 1, 1, 104201, 4, 1, 0, "人々の平和を守る旅に\n同行してくれる人を\n探していて…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1455, 1, 1, 104201, 5, 1, 0, "あとで曲を復習して\nおかないと…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1456, 1, 4, 104201, 101, 1, 0, "踊りを見られるのは\nまだ恥ずかしいけど\nあの人になら…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1457, 1, 8, 104201, 102, 1, 0, "浜辺で曲を書こうと\n思うんですけど…\n一緒に行きませんか？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1458, 1, 1, 104201, 1, 2, 0, "いつも応援ありがとう\nございます♪　これは\nあ、握手の練習です！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1459, 1, 1, 104201, 2, 2, 0, "え、もう練習の時間？\nごめんなさい\nすぐ準備します！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1460, 1, 1, 104201, 3, 2, 0, "守るべき人たちに\n笑顔でいてもらうのも\n大切なんですね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1461, 1, 1, 104201, 4, 2, 0, "歌える曲を増やそうと\n思って。どんな曲が\n聞きたいですか？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1462, 1, 4, 104201, 101, 2, 0, "～～♪　あれ？\n今私、歌ってました？\n気づきませんでした", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1463, 1, 8, 104201, 102, 2, 0, "…歌って欲しい？\n喜んで♪\nどの歌にしましょう？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1464, 1, 1, 104201, 1, 2, 0, "おはようございます♪\n少し歌の練習を\nしてきますね", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1465, 1, 1, 104201, 2, 2, 0, "もうお昼なんですか？\n練習に夢中で\n気づきませんでした", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1466, 1, 1, 104201, 3, 2, 0, "明日の予定は…\nアイドルはやることが\n多いみたいで…", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1467, 1, 1, 104201, 1, 3, 0, "見た目については\n前よりも気をつけてと\nノゾミに言われてます", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1468, 1, 1, 104201, 2, 3, 0, "歌の調子をよくする\nためには休息も\n必要ですよね…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1469, 1, 1, 104201, 3, 3, 0, "この子たちを\n相手に練習して\nみようかな…♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1470, 1, 1, 104201, 4, 3, 0, "…疲れてるのかな\n落ち着けているのが\n自分でもわかります", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1471, 1, 1, 104201, 5, 3, 0, "ここにいると\n穏やかな旋律が\n浮かんできます…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1472, 1, 1, 104201, 6, 3, 0, "歌うためには\n体力づくりは\n欠かせません", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1473, 1, 1, 104201, 7, 3, 0, "歌った後は\nお腹が空いて\nしまうんです", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1474, 1, 1, 104201, 8, 3, 0, "音楽について\n理論も勉強\nしないと…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1476, 1, 1, 104202, 1, 1, 0, "歌うことの楽しさ\n分かってきました\nもっと歌いたいな…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1477, 1, 1, 104202, 2, 1, 0, "練習してもあんまり\n疲れなくなった…\nアイドルのおかげかな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1478, 1, 1, 104202, 3, 1, 0, "精霊も強くなったし\nもっとたくさんの人を\n守りたいです", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1479, 1, 1, 104202, 4, 1, 0, "最近、よく頭の中に\n歌が浮かんできます\n不思議な感覚…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1480, 1, 1, 104202, 5, 1, 0, "楽譜を買いました\n思いついた歌を\n書き留めておくために", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1481, 1, 8, 104202, 101, 1, 0, "浜辺で曲を書こうと\n思うんですけど…\n一緒に行きませんか？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1482, 1, 8, 104202, 102, 1, 0, "私の中にある\n私だけの歌…\n作ってみようかな？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1483, 1, 1, 104202, 1, 2, 0, "守るべき人たちに\n笑顔でいてもらうのも\n大切なんですね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1484, 1, 1, 104202, 2, 2, 0, "歌える曲を増やそうと\n思って。どんな曲が\n聞きたいですか？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1485, 1, 1, 104202, 3, 2, 0, "魔物退治ですか！？\n…違います？\n良かった…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1486, 1, 1, 104202, 4, 2, 0, "いつも応援ありがとう\nございます♪　これは\nあ、握手の練習です！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1487, 1, 8, 104202, 101, 2, 0, "…歌って欲しい？\n喜んで♪\nどの歌にしましょう？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1488, 1, 8, 104202, 102, 2, 0, "～～♪　あれ？\n今私、歌ってました？\n気づきませんでした", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1489, 1, 1, 104202, 1, 2, 0, "呼吸を深く、大きく…\nこれでちゃんと\n目が覚めますよ", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1490, 1, 1, 104202, 2, 2, 0, "少し休んだら\n午後の練習に\n行ってきますね", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1491, 1, 1, 104202, 3, 2, 0, "曲を作っていたら\nもうこんな時間…\nおやすみなさい", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1492, 1, 1, 104202, 1, 3, 0, "見た目については\n前よりも気をつけてと\nノゾミに言われてます", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1493, 1, 1, 104202, 2, 3, 0, "あ～…♪\nよく体がほぐれます…\nいい歌が歌えそうです", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1494, 1, 1, 104202, 3, 3, 0, "かわいいと思う\n気持ちを、歌にしても\nいいかも知れませんね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1495, 1, 1, 104202, 4, 3, 0, "…疲れてるのかな\n落ち着けているのが\n自分でもわかります", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1496, 1, 1, 104202, 5, 3, 0, "ここにいると\n穏やかな旋律が\n浮かんできます…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1497, 1, 1, 104202, 6, 3, 0, "もっと体力をつけて\n最高のライブを\nファンに届けます！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1498, 1, 1, 104202, 7, 3, 0, "これでも一応\n体形には気をつけて\n食べてるんですよ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1499, 1, 1, 104202, 8, 3, 0, "読書は歌に\n新しい風を\n吹き込んでくれます", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1501, 1, 1, 104301, 1, 1, 0, "店の新メニューでも\n考えるかな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1502, 1, 1, 104301, 2, 1, 0, "あたしの店から、\n出前を届けようか？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1503, 1, 1, 104301, 3, 1, 0, "パトロール、\nパトロール…\nここは異常なしかな？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1504, 1, 1, 104301, 4, 1, 0, "腹へったら食う！\n悩んだら寝る！\nそれが一番だろ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1505, 1, 1, 104301, 5, 1, 0, "のんびりし過ぎるのも\n…性に合わねぇな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1506, 1, 4, 104301, 101, 1, 0, "そろそろあいつ、\n腹すかしてるん\nじゃないかな？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1507, 1, 8, 104301, 102, 1, 0, "今日の献立は…\nあいつの好きなものに\nしてやるか", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1508, 1, 1, 104301, 1, 2, 0, "強くなりたいなら、\nあたしがビシバシ\n鍛えてやるよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1509, 1, 1, 104301, 2, 2, 0, "天気がいい日は\n外で運動したく\nならねぇか？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1510, 1, 1, 104301, 3, 2, 0, "腹減ったか？　なんか\n作ってやろうか？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1511, 1, 1, 104301, 4, 2, 0, "ん？　なんだよ、\n相談事か？\nなんでも言ってくれ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1512, 1, 4, 104301, 101, 2, 0, "お前が好きな飯って\nなんだ？\n今度、作ってやるよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1513, 1, 8, 104301, 102, 2, 0, "お前って不思議だよな\n一緒にいるとなんだか\n…なんでもねーよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1514, 1, 1, 104301, 1, 2, 0, "気持ちいい朝だな！\n体を動かしたい気分だ\n付き合ってくれるか", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1515, 1, 1, 104301, 2, 2, 0, "昼飯もう食ったか？\nまだなら、あたしが\nなんか作ってやるよ", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1516, 1, 1, 104301, 3, 2, 0, "日中たっぷり動いたら\nそのぶん夜はしっかり\n休んどけよな", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1517, 1, 1, 104301, 1, 3, 0, "た、たまにはその…\nかわいい格好とか\nしても…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1518, 1, 1, 104301, 2, 3, 0, "ふわぁ、\n一休みすっかなぁ～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1519, 1, 1, 104301, 3, 3, 0, "うっ！　か、かわいい\nじゃねぇか", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1520, 1, 1, 104301, 4, 3, 0, "へー　いい匂いだな\nあいつもこういうの\n好きなのかな…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1521, 1, 1, 104301, 5, 3, 0, "ふわぁ〜！\n温かいと、つい\nウトウトしちまうな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1522, 1, 1, 104301, 6, 3, 0, "オラオラオラーーー！\n燃えるぜーーー！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1523, 1, 1, 104301, 7, 3, 0, "いい味してるぜ！\nうちの店でも\n出してみたいな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1524, 1, 1, 104301, 8, 3, 0, "文字を読んでると\nどうしても眠く\nなっちまうんだよなぁ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1526, 1, 1, 104302, 1, 1, 0, "パトロール終了～\n本日も異常なしっと！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1527, 1, 1, 104302, 2, 1, 0, "あたしの店から\n出前を届けようか？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1528, 1, 1, 104302, 3, 1, 0, "あいつ、早く\n帰ってこないかな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1529, 1, 1, 104302, 4, 1, 0, "あいつ\n腹すかせてないと\nいいんだけど…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1530, 1, 1, 104302, 5, 1, 0, "のんびりし過ぎるのも\n…性に合わねぇな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1531, 1, 8, 104302, 101, 1, 0, "あいつの事を思うと\n胸があったかくなる？\nなんだコレ…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1532, 1, 8, 104302, 102, 1, 0, "今日の献立は…\nあいつの好きなものに\nしてやるか", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1533, 1, 1, 104302, 1, 2, 0, "ん？　なんだよ\n相談事か？\nなんでも言ってくれ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1534, 1, 1, 104302, 2, 2, 0, "天気がいい日は\n外で運動したく\nならねぇか？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1535, 1, 1, 104302, 3, 2, 0, "腹減ったか？　なんか\n作ってやろうか？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1536, 1, 1, 104302, 4, 2, 0, "腹へったら食う！\n悩んだら寝る！\nそれが一番だろ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1537, 1, 8, 104302, 101, 2, 0, "あのさ…できれば\nもっとそばに…いや\nなんでもねぇ忘れろ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1538, 1, 8, 104302, 102, 2, 0, "お前って不思議だよな\n一緒にいるとなんだか\n…なんでもねーよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1539, 1, 1, 104302, 1, 2, 0, "気持ちいい朝だな！\n体を動かしたい気分だ\n付き合ってくれるか？", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1540, 1, 1, 104302, 2, 2, 0, "昼飯もう食ったか？\nまだなら、あたしが\nなんか作ってやるよ", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1541, 1, 1, 104302, 3, 2, 0, "あんま夜更しすんなよ\n明日に響いたら\n大変だろ？", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1542, 1, 1, 104302, 1, 3, 0, "た、たまにはその…\nかわいい格好とか\nしても…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1543, 1, 1, 104302, 2, 3, 0, "ふぅ～♪\n疲れがとれるな～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1544, 1, 1, 104302, 3, 3, 0, "うっ！　か、かわいい\nじゃねぇか", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1545, 1, 1, 104302, 4, 3, 0, "へー　いい匂いだな\nあいつもこういうの\n好きなのかな…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1546, 1, 1, 104302, 5, 3, 0, "ふわぁ～！\n温かいと、つい\nウトウトしちまうな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1547, 1, 1, 104302, 6, 3, 0, "オラオラオラーーー！\n燃えるぜーーー！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1548, 1, 1, 104302, 7, 3, 0, "いい味してるぜ！\nうちの店でも\n出してみたいな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1549, 1, 1, 104302, 8, 3, 0, "料理だけじゃなく\n経営術とかも\n学んどかねぇと…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1551, 1, 1, 104501, 1, 1, 0, "最近なにも\nしてくれません…\n焦らしてるんですね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1552, 1, 1, 104501, 2, 1, 0, "ぐふふ、じゅるり…\nい、いけない、また\nいつの間にかよだれが", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1553, 1, 1, 104501, 3, 1, 0, "好きな武器といえば\n剣より槍より\nやっぱり鞭…ぐふふ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1554, 1, 1, 104501, 4, 1, 0, "「無数の触手が\nクウカの柔肌に\n食い込み」…ぐふふ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1555, 1, 1, 104501, 5, 1, 0, "もしドＳさんが\nＭに転じたらと思うと\n身の毛がよだちます！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1556, 1, 4, 104501, 101, 1, 0, "ドＳさんを登場\nさせるだけで、妄想に\n無限の広がりが！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1557, 1, 8, 104501, 102, 1, 0, "あっ…\nジロジロ見てたの\n気づいちゃいました？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1558, 1, 1, 104501, 1, 2, 0, "視線を感じると\n思ったら…やはり\nドＳさんでしたか", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1559, 1, 1, 104501, 2, 2, 0, "今日は\nどんなヒドイことを\nしてくれるんですか？\nぐふふふ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1560, 1, 1, 104501, 3, 2, 0, "いつの間にそこに！？\nまさかクウカを監視\nしていたのですか！？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1561, 1, 1, 104501, 4, 2, 0, "クウカに触るつもり\nですか？　触るつもり\nなのですねっ！？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1562, 1, 4, 104501, 101, 2, 0, "クウカにナニをする\nつもりですか…\n期待してしまいます！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1563, 1, 8, 104501, 102, 2, 0, "ドＳさんの視界に入る\nだけでドキドキが…\nこれが恋！？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1564, 1, 1, 104501, 1, 2, 0, "おはようございます。\n朝からお盛んですね？\nぐふふ…", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1565, 1, 1, 104501, 2, 2, 0, "こんなにも日の高い\nうちからドＳさんは…\nじゅるり", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1566, 1, 1, 104501, 3, 2, 0, "夜はこれから、\nですよ…\nぐふ、ぐふふふ…", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1567, 1, 1, 104501, 1, 3, 0, "キレイにおめかし\nしたのに力ずくで…\nぐふふふ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1568, 1, 1, 104501, 2, 3, 0, "ソファの背にクウカを\n押し付けて…ぐふふ\nいいですね\nソファドン", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1569, 1, 1, 104501, 3, 3, 0, "「見た目とは裏腹に\nそれは激しく攻め\nたてて」…ぐふふふ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1570, 1, 1, 104501, 4, 3, 0, "香りを吸ったクウカの\n頬は紅潮し目はうるみ\nもはや抗う力も…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1571, 1, 1, 104501, 5, 3, 0, "ああ、身体が\n火照って…\nぐふ、ぐふふ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1572, 1, 1, 104501, 6, 3, 0, "はぁ、はぁ、こうして\nクウカの体力を\n奪ってから…ぐふふ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1573, 1, 1, 104501, 7, 3, 0, "食欲が満たされたら\nつ、次はやはり…！\nそうなのですね！？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1574, 1, 1, 104501, 8, 3, 0, "この本の下に、\nそういう本を\nカムフラージュ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1576, 1, 1, 104502, 1, 1, 0, "「ドＳさんと踊り子\n乱舞の一夜」…\n次の日記はこれで…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1577, 1, 1, 104502, 2, 1, 0, "最近なにも\nしてくれません…\n焦らしてるんですね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1578, 1, 1, 104502, 3, 1, 0, "も、妄想が\n足りません…\n禁断症状がっ…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1579, 1, 1, 104502, 4, 1, 0, "もしドＳさんが\nＭに転じたらと思うと\n身の毛がよだちます！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1580, 1, 1, 104502, 5, 1, 0, "次はどんなドＳを\nされるのか、想像する\nだけで…ぐふふふ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1581, 1, 8, 104502, 101, 1, 0, "じっとしてると\nドＳさんのことばかり\n考えてしまいますね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1582, 1, 8, 104502, 102, 1, 0, "妄想が止まらなくて\nクウカどうにかなって\nしまいそうです！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1583, 1, 1, 104502, 1, 2, 0, "ドＳさんといると体が\n火照って…クウカは\nいやらしい子です！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1584, 1, 1, 104502, 2, 2, 0, "あっ…\nジロジロ見てたの\n気づいちゃいました？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1585, 1, 1, 104502, 3, 2, 0, "ドＳさんのおかげで\n新しい扉が開けそう\nです…じゅるり", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1586, 1, 1, 104502, 4, 2, 0, "いやー！　ドＳさんに\n襲われ…襲って\nくれないんですか？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1587, 1, 8, 104502, 101, 2, 0, "クウカはドＳさんに\n身も心も堕とされて\nしまったのですね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1588, 1, 8, 104502, 102, 2, 0, "ドＳさんがお望みなら\nクウカは\nどんなことだって！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1589, 1, 1, 104502, 1, 2, 0, "朝だからこそ\nできる妄想が\nあります…ぐふふ", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1590, 1, 1, 104502, 2, 2, 0, "こんな日の高いうち\nからドＳさんは…\nじゅるり", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1591, 1, 1, 104502, 3, 2, 0, "今夜は一晩中…\nなんですね？\nぐふふ…", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1592, 1, 1, 104502, 1, 3, 0, "意外と難しいんですよ\n正しい亀甲縛りと\nいうものは…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1593, 1, 1, 104502, 2, 3, 0, "くつろいで油断\nしているスキに何を\nされるのでしょう！？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1594, 1, 1, 104502, 3, 3, 0, "「見た目とは裏腹に\nそれは激しく攻め\nたてて」…ぐふふふ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1595, 1, 1, 104502, 4, 3, 0, "これは禁断の香り…\nああっ一段階上の\n妄想が開花するうぅ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1596, 1, 1, 104502, 5, 3, 0, "ああっ、暖気がまるで\nロウソクのように\nクウカを攻めますっ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1597, 1, 1, 104502, 6, 3, 0, "激しい攻めを受けきる\nためには、鍛えないと\nいけないのですね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1598, 1, 1, 104502, 7, 3, 0, "こんなにいっぱい\n入りません…\nお腹にっ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1599, 1, 1, 104502, 8, 3, 0, "どんな本も\nよく見ると\nいやらしいです…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1601, 1, 1, 104601, 1, 1, 0, "この前遊んだ猫、\nモッフモフ\nだったにゃあ…♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1602, 1, 1, 104601, 2, 1, 0, "ほっぺやお腹が、\n肉球ぐらいぷにぷに…\nこれはまずいにゃ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1603, 1, 1, 104601, 3, 1, 0, "大きな仕事が\n決まったにゃ！\n腕が鳴るにゃ～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1604, 1, 1, 104601, 4, 1, 0, "おやつのたい焼き、\nつぶあんとこしあん、\nどっちに\nしようかにゃ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1605, 1, 1, 104601, 5, 1, 0, "うにゃー…\nもうちょっとお昼寝\nしたかったにゃ…\nむにゃむにゃ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1606, 1, 4, 104601, 101, 1, 0, "キミに呼ばれると、\n猫まっしぐらな気分に\nなっちゃうにゃ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1607, 1, 8, 104601, 102, 1, 0, "キミのおかげで\nギルドの仕事も\n大成功だにゃ♪\nありがとにゃ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1608, 1, 1, 104601, 1, 2, 0, "うずうず…その指、\nじゃれついても\nいいかにゃ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1609, 1, 1, 104601, 2, 2, 0, "にゃ？　あたしに何か\nご入り用かにゃ？\nお安くしとくにゃ～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1610, 1, 1, 104601, 3, 2, 0, "うーん、なんだか\n退屈にゃ～\n遊んで、遊んで～？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1611, 1, 1, 104601, 4, 2, 0, "素早い動きのコツ？\nタダじゃ教えて\nあげられないにゃ～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1612, 1, 4, 104601, 101, 2, 0, "そんなにあたしを\nなでたいにゃ？\nキミならなでさせて\nあげてもいいかにゃー", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1613, 1, 8, 104601, 102, 2, 0, "ふにゃあ、ねえねえ、\n一緒にゴロゴロ\nしようにゃあ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1614, 1, 1, 104601, 1, 2, 0, "いい朝にゃ！\n顔を洗ってくるにゃ！", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1615, 1, 1, 104601, 2, 2, 0, "むーん、そろそろ\nお昼…猫たちにも\n猫缶を…", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1616, 1, 1, 104601, 3, 2, 0, "ふわあ…眠いにゃ…\n猫は布団で\n丸くなるにゃ", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1617, 1, 1, 104601, 1, 3, 0, "客商売では、\n外見のチェックも\n欠かせないのにゃ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1618, 1, 1, 104601, 2, 3, 0, "うにゃ～！\nお昼寝に\nぴったりにゃ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1619, 1, 1, 104601, 3, 3, 0, "これ…量産したら\n子どもにバカ売れ\nしそうだにゃ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1620, 1, 1, 104601, 4, 3, 0, "この灯り、まるで\n猫みたいな\n癒し効果にゃ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1621, 1, 1, 104601, 5, 3, 0, "ふにゃあ、\nあったかいと猫は丸く\nなっちゃうのにゃあ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1622, 1, 1, 104601, 6, 3, 0, "本当は外で\n運動したいん\nだけどにゃ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1623, 1, 1, 104601, 7, 3, 0, "むーん、\n美味しいにゃー！\nたい焼きに\n匹敵するにゃ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1624, 1, 1, 104601, 8, 3, 0, "ふむふむ…猫の\nしつけ方は、と…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1626, 1, 1, 104602, 1, 1, 0, "かわいいネコの置物を\nゲットしたにゃ！\nどこに飾ろうか\nにゃ～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1627, 1, 1, 104602, 2, 1, 0, "仕事の交渉がうまく\n行ったにゃ～♪\nいい気分だにゃ～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1628, 1, 1, 104602, 3, 1, 0, "ねこじゃらしには\n食いついちゃうにゃ\nびしっ！　びしっ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1629, 1, 1, 104602, 4, 1, 0, "ほっぺやお腹が、肉球\nぐらいぷにぷに…\nこれはまずいにゃ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1630, 1, 1, 104602, 5, 1, 0, "さっきのたいやき\nおいしかったにゃ♪\nごろごろごろ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1631, 1, 8, 104602, 101, 1, 0, "手作りのたい焼き\nあいつは食べて\nくれるかにゃ…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1632, 1, 8, 104602, 102, 1, 0, "あいつには、いつか\nたっぷり恩返しが\nしたいにゃ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1633, 1, 1, 104602, 1, 2, 0, "にゃーに？　あたしの\nことがそんなに気に\nなるのかにゃ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1634, 1, 1, 104602, 2, 2, 0, "あたしのこと…\nしっかり捕まえ\nといてにゃ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1635, 1, 1, 104602, 3, 2, 0, "あたしの力が\n必要かにゃ？　猫の手\n貸してあげるにゃ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1636, 1, 1, 104602, 4, 2, 0, "たいやきとクレープを\n合わせれば…\n新しい商売のニオイが\nするにゃ…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1637, 1, 8, 104602, 101, 2, 0, "ふにゃあ、ねえねえ\n一緒にゴロゴロ\nしようにゃあ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1638, 1, 8, 104602, 102, 2, 0, "キミと一緒にいると\n楽しくなってつい\nじゃれちゃうにゃ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1639, 1, 1, 104602, 1, 2, 0, "いい朝にゃ！\n顔を洗ってくるにゃ！", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1640, 1, 1, 104602, 2, 2, 0, "そろそろおっひる～\nにゃーんか\n食べたい…", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1641, 1, 1, 104602, 3, 2, 0, "うーん、眠くなって\nきたにゃ。あたしは\n夜行性じゃないのにゃ", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1642, 1, 1, 104602, 1, 3, 0, "にゃにゃ…\n髪が乱れてるにゃ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1643, 1, 1, 104602, 2, 3, 0, "ふっかふかにゃ～\n猫がねころん\nじゃうにゃ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1644, 1, 1, 104602, 3, 3, 0, "これ、猫と匹敵する\nぐらいかわいいにゃ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1645, 1, 1, 104602, 4, 3, 0, "猫でもこわくない\n優しい灯りにゃ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1646, 1, 1, 104602, 5, 3, 0, "ぽっかぽか\nしあわせな気分にゃ～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1647, 1, 1, 104602, 6, 3, 0, "雨の日でも運動\nできるのは、濡れ\nなくて便利だにゃ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1648, 1, 1, 104602, 7, 3, 0, "美味しいにゃ～！\nこれ、朝昼晩\n食べたいにゃ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1649, 1, 1, 104602, 8, 3, 0, "本を読んで\n猫の気持ちを\n勉強するのにゃ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1651, 1, 1, 104801, 1, 1, 0, "内職手伝える人\nいないかしら？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1652, 1, 1, 104801, 2, 1, 0, "次の依頼は、\nこういう計画で…\nうん、完璧ね！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1653, 1, 1, 104801, 3, 1, 0, "次の特売セールに\n向けて、しっかり\n準備しないと！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1654, 1, 1, 104801, 4, 1, 0, "配達の後に売り子\n次に採取のバイト…\nうん、効率的ね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1655, 1, 1, 104801, 5, 1, 0, "一日に受ける\nバイトの数、もう少し\n増やしたいわね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1656, 1, 4, 104801, 101, 1, 0, "師匠として\n頼りにされたいし\nもっと頑張らなきゃ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1657, 1, 8, 104801, 102, 1, 0, "男の子と効率的に\n仲良くなる方法…\nこればっかりは難題ね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1658, 1, 1, 104801, 1, 2, 0, "あら、\nちょうどよかった\n今から一緒に\nバイトでもどう？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1659, 1, 1, 104801, 2, 2, 0, "相談事かしら？\nそれくらいなら\n無料でいいわよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1660, 1, 1, 104801, 3, 2, 0, "背後の気配なら\nすぐ分かるわ\n試してみる？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1661, 1, 1, 104801, 4, 2, 0, "道具やお金の管理は\n大丈夫？　手伝うから\nいつでも言って", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1662, 1, 4, 104801, 101, 2, 0, "暇ができたから\n働きたいけど…\nキミとの時間も大切ね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1663, 1, 8, 104801, 102, 2, 0, "ねえ、あの…\n私はキミにとって…\nいや、何でもないわ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1664, 1, 1, 104801, 1, 2, 0, "おはよう！\n今日も朝から\nバイトよ！", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1665, 1, 1, 104801, 2, 2, 0, "お昼時ね。\n少し休憩したら、\nまた働くわよ！", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1666, 1, 1, 104801, 3, 2, 0, "もうこんな時間！\n明日もバイトで\n朝早いのよ…", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1667, 1, 1, 104801, 1, 3, 0, "接客業の依頼が\nある時は、外見も\nキレイにしないとね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1668, 1, 1, 104801, 2, 3, 0, "はぁ～バイトで\n疲れたから癒される～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1669, 1, 1, 104801, 3, 3, 0, "これを内職で\n作ったら\n儲かるかしら？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1670, 1, 1, 104801, 4, 3, 0, "リフレッシュできたわ\nこれでまた、バリバリ\n働けるわね！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1671, 1, 1, 104801, 5, 3, 0, "風邪を引いたら効率が\n落ちちゃうから、\n暖めないと！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1672, 1, 1, 104801, 6, 3, 0, "働き続けるには\n健康第一！　それには\n運動が一番よ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1673, 1, 1, 104801, 7, 3, 0, "もぐもぐ…\n腹が減っては\n戦ができぬ、よ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1674, 1, 1, 104801, 8, 3, 0, "日々絶えず学んで、\n生活に活かすのよ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1676, 1, 1, 104802, 1, 1, 0, "家計簿をつけなきゃ！\n覚えている時に\nやっておかないとね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1677, 1, 1, 104802, 2, 1, 0, "次の依頼は\nこういう計画で…\nうん、完璧ね！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1678, 1, 1, 104802, 3, 1, 0, "ギルドの経費\nあとちょっとだけ\n削れそうね…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1679, 1, 1, 104802, 4, 1, 0, "少しでも時給のいい\nバイトがしたいけど\nなかなか\n出会えないのよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1680, 1, 1, 104802, 5, 1, 0, "今度のバイトも\nなかなか大変そう\nね…腕が鳴るわ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1681, 1, 8, 104802, 101, 1, 0, "男の子と効率的に\n仲良くなる方法…\nこればっかりは難題ね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1682, 1, 8, 104802, 102, 1, 0, "私、どうしたんだろ？\n仕事中もずっと\n彼のこと考えてる…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1683, 1, 1, 104802, 1, 2, 0, "この間はヘルプに\n来てくれてありがとう\n頼んで正解だったわ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1684, 1, 1, 104802, 2, 2, 0, "見つけた！　ちょうど\n頼みたいことが\nあるんだけど、いい？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1685, 1, 1, 104802, 3, 2, 0, "今から\n休憩するところなの\n一緒にどう？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1686, 1, 1, 104802, 4, 2, 0, "ん？　どうしたの？\n私のことが\n気になるの？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1687, 1, 8, 104802, 101, 2, 0, "…たまには甘えても\nいい？　私だって\n弱る時はあるのよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1688, 1, 8, 104802, 102, 2, 0, "暇ができたから\n働きたいけど…\nキミとの時間も大切ね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1689, 1, 1, 104802, 1, 2, 0, "おはよう！\nいい朝ね。一緒に\nランニングでもいく？", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1690, 1, 1, 104802, 2, 2, 0, "お昼時ね\n少し休憩したら\nまた働くわよ！", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1691, 1, 1, 104802, 3, 2, 0, "もうこんな時間！\n明日もバイトで\n朝早いのよ…", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1692, 1, 1, 104802, 1, 3, 0, "女の子らしく…\nどうも忘れちゃう\nのよね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1693, 1, 1, 104802, 2, 3, 0, "ここで少し\n休んだら\nまたバイトよ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1694, 1, 1, 104802, 3, 3, 0, "これを内職で\n作ったら\n儲かるかしら？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1695, 1, 1, 104802, 4, 3, 0, "仕事から帰ってきて\nこういうのがあると\n素敵ね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1696, 1, 1, 104802, 5, 3, 0, "しっかり身体を\n温めて、血流を\nよくしなくっちゃ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1697, 1, 1, 104802, 6, 3, 0, "これで休憩中も\n無駄なく\n鍛えられるわ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1698, 1, 1, 104802, 7, 3, 0, "もぐもぐ…よく\n食べて、よく働く！\nそれが私のモットーよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1699, 1, 1, 104802, 8, 3, 0, "日々絶えず学んで\n生活に活かすのよ！", 0);
