INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1782, 1, 8, 105002, 102, 1, 0, "アタシが待ってる\nなんて、アイツは\n幸せものよねー ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1783, 1, 1, 105002, 1, 2, 0, "なーに？\nアタシに甘えたいの？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1784, 1, 1, 105002, 2, 2, 0, "アタシに合う服\n雑誌で探そうっと！\nサイズじゃないわ\nデザインとかの話よ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1785, 1, 1, 105002, 3, 2, 0, "頭撫でないで！\nお子様みたいに\nあつかわないでって\n言ってるのに～！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1786, 1, 1, 105002, 4, 2, 0, "壁ドンの練習…\nあれ？　壁ドンって\n男がするんだっけ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1787, 1, 8, 105002, 101, 2, 0, "ねーねー！\nアタシ、かわいい？\nかわいいわよね？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1788, 1, 8, 105002, 102, 2, 0, "これから出かけるの？\nしっかりアタシを\nエスコートしてね！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1789, 1, 1, 105002, 1, 2, 0, "レディの朝は\n忙しいのよ", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1790, 1, 1, 105002, 2, 2, 0, "お腹すいたー\nダイエット\nやめちゃおうかなー", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1791, 1, 1, 105002, 3, 2, 0, "エイジングケアを\nしないとねー", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1792, 1, 1, 105002, 1, 3, 0, "身だしなみチェック\nオッケー！　これで\nいつアイツが来ても\n大丈夫ね！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1793, 1, 1, 105002, 2, 3, 0, "あ！　これ雑誌で\n見たことあるわ～\nだからきっと\nオシャレなのよ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1794, 1, 1, 105002, 3, 3, 0, "あたしがもっと\nオシャレにかわいく\nしてあげようか？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1795, 1, 1, 105002, 4, 3, 0, "オシャレ～♪\n大人なレディって\n感じよね♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1796, 1, 1, 105002, 5, 3, 0, "あったかーい♪\nここで宿題\nしようかしら？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1797, 1, 1, 105002, 6, 3, 0, "運動したら成長\nしないかしら？\n背とか胸とか…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1798, 1, 1, 105002, 7, 3, 0, "レディは、食べ物にも\n気を使うのよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1799, 1, 1, 105002, 8, 3, 0, "おもしろーい！\n…でも絵本って\n子供っぽいかしら？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1801, 1, 1, 105101, 1, 1, 0, "魔法と科学\nどっちも極めて\nみせるわ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1802, 1, 1, 105101, 2, 1, 0, "仕事も片付いたし\n部屋で\n実験実験…♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1803, 1, 1, 105101, 3, 1, 0, "たまった治療代の\n請求書を\nまとめないと…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1804, 1, 1, 105101, 4, 1, 0, "新薬が出来たし\nすぐに試したいけど…\n実験台がいないわ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1805, 1, 1, 105101, 5, 1, 0, "部屋にこもりすぎると\n曜日の感覚が\nなくなるわね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1806, 1, 4, 105101, 101, 1, 0, "あの子の記憶を\n取り戻せる薬…\n作れないものかしら", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1807, 1, 8, 105101, 102, 1, 0, "好意に気づいて貰える\n薬って、どうすれば\n作れるのかしら", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1808, 1, 1, 105101, 1, 2, 0, "あら、ヒマなら\n薬草の整理を\n手伝って", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1809, 1, 1, 105101, 2, 2, 0, "もしかして実験に\n付き合ってくれるの？\nえ？　違う？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1810, 1, 1, 105101, 3, 2, 0, "具合が悪い？\nなら診てあげる\nまずは触診から…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1811, 1, 1, 105101, 4, 2, 0, "植物に興味あるの？\nなら教えてあげるわ\n色々とね…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1812, 1, 4, 105101, 101, 2, 0, "キミの傍にいるだけで\n高揚しちゃうのは\n何故かしら…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1813, 1, 8, 105101, 102, 2, 0, "私はおしおきする\n側のことが多いけど…\nキミにはされたいかも", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1814, 1, 1, 105101, 1, 2, 0, "お昼までに調合用の\n花を集めたいの\n手伝ってくれる？", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1815, 1, 1, 105101, 2, 2, 0, "あら、もうお昼？\nそろそろ一息\n入れましょうか", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1816, 1, 1, 105101, 3, 2, 0, "夜も更けて来たわね\n私は朝まで実験だけど\n見学していく？", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1817, 1, 1, 105101, 1, 3, 0, "医者たるもの\nいつだって\n清潔に…♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1818, 1, 1, 105101, 2, 3, 0, "いいわねこれ\n仮眠の時に\n使わせてもらうわ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1819, 1, 1, 105101, 3, 3, 0, "ぬいぐるみなら\n動物も…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1820, 1, 1, 105101, 4, 3, 0, "いい香り…\n仕事場にも\n置いてみようかしら", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1821, 1, 1, 105101, 5, 3, 0, "ここにいると\nついウトウト\nしちゃうわ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1822, 1, 1, 105101, 6, 3, 0, "座ってる時間が多いと\n運動不足になるし\nたまにはいいわね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1823, 1, 1, 105101, 7, 3, 0, "研究していると\nつい食べるのを\n忘れちゃうのよね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1824, 1, 1, 105101, 8, 3, 0, "実験もいいけど\nいつか成果を\n本にしないと…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1826, 1, 1, 105102, 1, 1, 0, "徹夜はツラいけど\n集中すると時間の\n感覚がなくなって…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1827, 1, 1, 105102, 2, 1, 0, "痛みに悶える顔って\nどうしてあんなに\n興奮するのかしら？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1828, 1, 1, 105102, 3, 1, 0, "彼に罵られたら\nどんな気分かしら\n意外と快感かも？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1829, 1, 1, 105102, 4, 1, 0, "毒を薄めると\n薬になるのって\n今でも不思議だわ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1830, 1, 1, 105102, 5, 1, 0, "綺麗なバラには\nトゲがあるけど\n魅力の一つよね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1831, 1, 8, 105102, 101, 1, 0, "カレをさりげなく\n惚れさせる方法を\n研究するわよ…♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1832, 1, 8, 105102, 102, 1, 0, "好意に気づいて貰える\n薬って、どうすれば\n作れるのかしら", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1833, 1, 1, 105102, 1, 2, 0, "構って欲しいの？\nじゃあ、君の体を\n調べさせて？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1834, 1, 1, 105102, 2, 2, 0, "実験に付き合って\n大丈夫よ、試すのは\nただの栄養剤だから", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1835, 1, 1, 105102, 3, 2, 0, "欲しい薬が\nあったら言ってね\n調合してみるから", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1836, 1, 1, 105102, 4, 2, 0, "おしおきされたいの？\nキミにだったら\nいつでもしてあげる", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1837, 1, 8, 105102, 101, 2, 0, "キミの傍にいるだけで\n高揚しちゃうのは\n何故かしら…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1838, 1, 8, 105102, 102, 2, 0, "私はおしおきする\n側のことが多いけど…\nキミにはされたいかも", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1839, 1, 1, 105102, 1, 2, 0, "朝しか取れない\n植物があるの\n採取しに行くわよ", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1840, 1, 1, 105102, 2, 2, 0, "お昼時ってやけに\n眠たくなるわよね\n…調べたくなるわ", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1841, 1, 1, 105102, 3, 2, 0, "今日くらいは\nゆっくり眠りたいわ\n…キミと一緒にね", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1842, 1, 1, 105102, 1, 3, 0, "身なりについて\n研究するのも\n楽しそうね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1843, 1, 1, 105102, 2, 3, 0, "休息に必要な道具は\n妥協せずに選ぶ！\nこれだけは譲れないわ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1844, 1, 1, 105102, 3, 3, 0, "たまにはこういう\n可愛らしいものに\n触れないとね♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1845, 1, 1, 105102, 4, 3, 0, "いい香り…\n仕事場にも\n置いてみようかしら", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1846, 1, 1, 105102, 5, 3, 0, "仕事がなかったら\nこのまますーっと\n眠りたいのに…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1847, 1, 1, 105102, 6, 3, 0, "運動しないと\n体型の維持が…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1848, 1, 1, 105102, 7, 3, 0, "適度な食事も\n大切よね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1849, 1, 1, 105102, 8, 3, 0, "まさか私が\n恋愛読本を読む\n日が来るなんて…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1851, 1, 1, 105201, 1, 1, 0, "ぺっぺっ！\n…い、いけないっ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1852, 1, 1, 105201, 2, 1, 0, "いっけな～い！\nひづめで床に傷が！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1853, 1, 1, 105201, 3, 1, 0, "太っちゃったかも～！\n伐採でダイエット\nしなくっちゃ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1854, 1, 1, 105201, 4, 1, 0, "きちんと生活すると\n毛づやが良くなる\nみたいなのよね～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1855, 1, 1, 105201, 5, 1, 0, "みんな、体重\nどれくらいなのかな？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1856, 1, 4, 105201, 101, 1, 0, "体力なら自信\nあるんだけど…\n恋にはちょっぴり\n臆病になっちゃう", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1857, 1, 8, 105201, 102, 1, 0, "いつか人間の女の子\nみたいなデート、\nしてみたいな～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1858, 1, 1, 105201, 1, 2, 0, "私ね「どこにでもいる\n普通の女の子」って\n言葉が憧れなんだ～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1859, 1, 1, 105201, 2, 2, 0, "チャームポイント？\n自分では分からない\nけど…まつげかなぁ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1860, 1, 1, 105201, 3, 2, 0, "リマリマ～♪\nはっ！？\nわ、私ったら今何を？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1861, 1, 1, 105201, 4, 2, 0, "力仕事ならまかせて！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1862, 1, 4, 105201, 101, 2, 0, "人間になりたいな～\nそれか、あなたが\n私みたいな姿に…♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1863, 1, 8, 105201, 102, 2, 0, "しょうがないなあ、\nこんなにモフらせるの\nあなただけだよ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1864, 1, 1, 105201, 1, 2, 0, "今朝は寝癖がひどいわ\nしっぽの辺りとか～", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1865, 1, 1, 105201, 2, 2, 0, "ランチは\nおしゃれなカフェで\nしてみたいな～♪", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1866, 1, 1, 105201, 3, 2, 0, "お肌のケアに\nパックしながら\n寝ようかな～", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1867, 1, 1, 105201, 1, 3, 0, "やだっ、\nまた寝ぐせついてる！\n直さないと！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1868, 1, 1, 105201, 2, 3, 0, "ミシッっていった！\n私、重すぎるかも！？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1869, 1, 1, 105201, 3, 3, 0, "あ！　あなたも\n美人さんね～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1870, 1, 1, 105201, 4, 3, 0, "はぁ、まったり…\n四足歩行しちゃい\nそうだわ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1871, 1, 1, 105201, 5, 3, 0, "あったか～い♪\n…私、毛皮だから\n暑すぎるかも", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1872, 1, 1, 105201, 6, 3, 0, "な、なんだか野生に\n帰っちゃいそう！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1873, 1, 1, 105201, 7, 3, 0, "ぱくぱく、もぐもぐ、\nぺっぺっ…\nやだ、はしたない！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1874, 1, 1, 105201, 8, 3, 0, "きゃーーー！\nこの恋愛小説\nとっても\nドキドキしちゃう～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1876, 1, 1, 105202, 1, 1, 0, "素敵ね～、居るだけで\n女子力があがりそうな\nお部屋だわ～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1877, 1, 1, 105202, 2, 1, 0, "私、大丈夫かしら？\n他の子に怖がられたり\nしないかな…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1878, 1, 1, 105202, 3, 1, 0, "動物苑…って\n嫌なギルド名ね…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1879, 1, 1, 105202, 4, 1, 0, "好き、嫌い、好き…\nあっ！　うっかりして\n花占いのお花\n食べちゃった！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1880, 1, 1, 105202, 5, 1, 0, "ここ、女の子らしい\nものでいっぱいね～！\nときめいちゃうわ～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1881, 1, 8, 105202, 101, 1, 0, "体力なら自信\nあるんだけど…\n恋にはちょっぴり\n臆病になっちゃう", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1882, 1, 8, 105202, 102, 1, 0, "あの人が\n来てくれるまで薪割り\nでもしてよっかな？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1883, 1, 1, 105202, 1, 2, 0, "一緒にお出かけ？\nうれしいけど…\n心の準備が！\nはわわわ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1884, 1, 1, 105202, 2, 2, 0, "どうしたの？\n急に抱きついてきて…\n癒されたいの？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1885, 1, 1, 105202, 3, 2, 0, "ぺっぺっ！\n…い、いけないっ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1886, 1, 1, 105202, 4, 2, 0, "メタモルアップルを\nここで食べたら、\nみんな驚くわよね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1887, 1, 8, 105202, 101, 2, 0, "しょうがないなあ、\nこんなにモフらせるの\nあなただけだよ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1888, 1, 8, 105202, 102, 2, 0, "人間になりたいな～\nそれか、あなたが\n私みたいな姿に…♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1889, 1, 1, 105202, 1, 2, 0, "今朝は寝癖がひどいわ\nしっぽの辺りとか～", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1890, 1, 1, 105202, 2, 2, 0, "ダイエット中だから\nお昼は抜いてるの～", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1891, 1, 1, 105202, 3, 2, 0, "お肌のケアに\nパックしながら\n寝ようかな～", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1892, 1, 1, 105202, 1, 3, 0, "ちゃんとかわいく\nしてないと刈られ…\n狩られちゃうもんね！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1893, 1, 1, 105202, 2, 3, 0, "ミシッっていった！\n私、重すぎるかも！？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1894, 1, 1, 105202, 3, 3, 0, "あ！　あなたも\n美人さんね～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1895, 1, 1, 105202, 4, 3, 0, "いい香り～\n森の中にいるみたいに\nとっても落ち着く～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1896, 1, 1, 105202, 5, 3, 0, "素敵～♪　…\nきゃっ！　私の毛が\nたいへんなことに～！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1897, 1, 1, 105202, 6, 3, 0, "な、なんだか野生に\n帰っちゃいそう！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1898, 1, 1, 105202, 7, 3, 0, "何を食べるかより\n誰と食べるかが\n大事なのよね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1899, 1, 1, 105202, 8, 3, 0, "きゃーーー！\nこの恋愛小説\nとっても\nドキドキしちゃう～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1901, 1, 1, 105301, 1, 1, 0, "ヴァイスフリューゲル\nを盛り上げる方法、\n共に語らおう！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1902, 1, 1, 105301, 2, 1, 0, "私は子供ではない…！\nが、お菓子は\nいただこう！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1903, 1, 1, 105301, 3, 1, 0, "部屋はきちんと\n片づけておかなくては\n部屋の汚れは\n心の汚れだ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1904, 1, 1, 105301, 4, 1, 0, "むむむ…明日\n目が覚めたら、身長が\n伸びていないものか…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1905, 1, 1, 105301, 5, 1, 0, "お菓子が常備されて\nいる部屋。そんな\nものが、私の好みだ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1906, 1, 4, 105301, 101, 1, 0, "あいつとの新たな\n鍛錬が\n待ち遠しいな…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1907, 1, 8, 105301, 102, 1, 0, "最近、あいつと\n過ごしていて、毎日が\n充実しているな…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1908, 1, 1, 105301, 1, 2, 0, "軍人の本分は日々の\n研鑽にあるからな。\n今日も鍛錬に\n行かないか？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1909, 1, 1, 105301, 2, 2, 0, "毎日牛乳を飲んでいる\nのだが、なかなか背が\n伸びないものだな…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1910, 1, 1, 105301, 3, 2, 0, "貴公にも、飴をくれて\nやろうか？\nあまいぞ～！\n美味しいぞ～！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1911, 1, 1, 105301, 4, 2, 0, "私ともっと高く、\n共に飛ぼう！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1912, 1, 4, 105301, 101, 2, 0, "貴公は、本当に頼りに\nなる男だな…いや、\nふとそう思っただけだ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1913, 1, 8, 105301, 102, 2, 0, "貴公と共にいられる\nことは私の喜びだ。\nこれからも\nよろしく頼むぞ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1914, 1, 1, 105301, 1, 2, 0, "うむ、いい朝だ。\n本国の夜明けを\n思い出してしまうな！", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1915, 1, 1, 105301, 2, 2, 0, "そろそろ昼食に\nするか。腹が減っては\n戦はできないからな", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1916, 1, 1, 105301, 3, 2, 0, "ふわああ…私は子供\nではないが…眠い時は\n眠いのだ…", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1917, 1, 1, 105301, 1, 3, 0, "身だしなみは、心の\n在りようを映すから\n気をつけなくてはな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1918, 1, 1, 105301, 2, 3, 0, "ふう…軍人たるもの\n常日頃、油断は\nできないのだが…\nお、落ち着くな…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1919, 1, 1, 105301, 3, 3, 0, "うむ…このかわいさ…\n本国に持って\n帰りたい…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1920, 1, 1, 105301, 4, 3, 0, "このともし火、戦いに\n明け暮れる我らの心を\n癒すものだな…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1921, 1, 1, 105301, 5, 3, 0, "はあ～、ぽかぽか…\nい、いや！\n気を緩めないぞっ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1922, 1, 1, 105301, 6, 3, 0, "うおおおおおっ！\n鍛錬、鍛錬、\n鍛錬っ！！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1923, 1, 1, 105301, 7, 3, 0, "いつ戦いになっても\n困らぬように、\n腹八分目に\nしておくのだ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1924, 1, 1, 105301, 8, 3, 0, "これを読んで、新たな\n戦術を編み出すぞっ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1926, 1, 1, 105302, 1, 1, 0, "たまには掃除でも\nするか。部屋が\n綺麗になるのは\n喜ばしいことだ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1927, 1, 1, 105302, 2, 1, 0, "部屋の中では\n剣を振り回せないのが\n玉に瑕だな…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1928, 1, 1, 105302, 3, 1, 0, "お菓子が常備されて\nいる部屋。そんな\nものが、私の好みだ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1929, 1, 1, 105302, 4, 1, 0, "毎日牛乳を飲んでいる\nのだが、なかなか背が\n伸びないものだな…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1930, 1, 1, 105302, 5, 1, 0, "家庭を守れない者が\n国を守れるはずが\nない。この部屋も\n私が守るぞ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1931, 1, 8, 105302, 101, 1, 0, "あいつは今頃、何を\nしているのだろうな…\nふふっ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1932, 1, 8, 105302, 102, 1, 0, "あいつと会える日が\nこんなにも待ち遠しく\nなるとはな…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1933, 1, 1, 105302, 1, 2, 0, "軍人の本分は日々の\n研鑽にあるからな\n今日も鍛錬に\n行かないか？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1934, 1, 1, 105302, 2, 2, 0, "おお、今日も鍛錬の\n時間だったか！\nすまないな\nすぐ行く！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1935, 1, 1, 105302, 3, 2, 0, "私は子供ではない！\nが…たまには童心に\n帰りたい時もある…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1936, 1, 1, 105302, 4, 2, 0, "貴公にも、飴を\nくれてやろうか？\nあまいぞ～！\n美味しいぞ～！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1937, 1, 8, 105302, 101, 2, 0, "私と歩きたいのか…？\nそうだな。二人で\n散歩でも行こうか", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1938, 1, 8, 105302, 102, 2, 0, "貴公と共にいられる\nことは私の喜びだ\nこれからも\nよろしく頼むぞ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1939, 1, 1, 105302, 1, 2, 0, "うむ、いい朝だ\n本国の夜明けを\n思い出してしまうな！", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1940, 1, 1, 105302, 2, 2, 0, "そろそろ昼時だな\n鍛錬の手を緩めて\n昼食にするか", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1941, 1, 1, 105302, 3, 2, 0, "寝る前に…あと少し\nだけ、鍛錬を…", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1942, 1, 1, 105302, 1, 3, 0, "ヴァイス\nフリューゲルの一員と\nして恥ずかしくないよう\n身なりを整えなくては", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1943, 1, 1, 105302, 2, 3, 0, "ふう…軍人たるもの\n常日頃、油断は\nできないのだが…\nお、落ち着くな…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1944, 1, 1, 105302, 3, 3, 0, "うむ…このかわいさ…\n本国に持って\n帰りたい…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1945, 1, 1, 105302, 4, 3, 0, "このともし火\n戦いに明け暮れる\n我らの心を\n癒すものだな…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1946, 1, 1, 105302, 5, 3, 0, "はあ～、ぽかぽか…\nい、いや！\n気を緩めないぞっ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1947, 1, 1, 105302, 6, 3, 0, "うおおおおおっ！\n鍛錬、鍛錬\n鍛錬っ！！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1948, 1, 1, 105302, 7, 3, 0, "いつ戦いになっても\n困らぬように\n腹八分目に\nしておくのだ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1949, 1, 1, 105302, 8, 3, 0, "うむ…脳細胞が\n活性化してきたぞ\n読書は大事だな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1951, 1, 1, 105701, 1, 1, 0, "私たちの騎空艇\nどんなデザインに\nしよう…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1952, 1, 1, 105701, 2, 1, 0, "団員募集の\nポスター\n作ろうっと♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1953, 1, 1, 105701, 3, 1, 0, "父さん…\n今頃どこで\n何してるんだろ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1954, 1, 1, 105701, 4, 1, 0, "騎空艇に乗って、\n誰もいったことがない\n場所を見つけたいな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1955, 1, 1, 105701, 5, 1, 0, "父さん以上の\n世界一の\n騎空士になりたい！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1956, 1, 4, 105701, 101, 1, 0, "仲間と行く空の旅…\n何度想像しても\nドキドキするなぁ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1957, 1, 8, 105701, 102, 1, 0, "父さん…\n私にもね、大切な\n仲間ができたよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1958, 1, 1, 105701, 1, 2, 0, "仕事の依頼？\nすぐに支度\nしてくるね！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1959, 1, 1, 105701, 2, 2, 0, "ねえねえ！\n団員募集のポスター\n一緒に作ろうよ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1960, 1, 1, 105701, 3, 2, 0, "なーにー？\nどうしたの？\n今呼んだよね？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1961, 1, 1, 105701, 4, 2, 0, "今は休憩中だよ？\nだからあなたも\nゆっくりしてて", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1962, 1, 4, 105701, 101, 2, 0, "私にとってあなたは\nとっても頼りになる、\n自慢の団員だよ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1963, 1, 8, 105701, 102, 2, 0, "お父さんに会えたら、\n大切な仲間として\nあなたを紹介するね！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1964, 1, 1, 105701, 1, 2, 0, "おはよう！\n体の調子はどう？\n今日は頑張れそう？", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1965, 1, 1, 105701, 2, 2, 0, "ねえねえ、お昼\n食べに行こうよ！\nお腹空いちゃった", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1966, 1, 1, 105701, 3, 2, 0, "…あれ？\nおしゃべりしてたら\nもうこんな時間？", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1967, 1, 1, 105701, 1, 3, 0, "どうせならオシャレな\n騎空士って\n呼ばれたいな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1968, 1, 1, 105701, 2, 3, 0, "ふぅ、疲れた～…\nちょっとだけ\n休んじゃおっと", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1969, 1, 1, 105701, 3, 3, 0, "よし！　キミは\n今から団長代理だ！\n…なんてね♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1970, 1, 1, 105701, 4, 3, 0, "いい香り～♪\n気持ちが\n落ち着く～…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1971, 1, 1, 105701, 5, 3, 0, "これって\n騎空艇に\n置けるかな…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1972, 1, 1, 105701, 6, 3, 0, "どんな仕事でも\nこなせるような\n体力がないとね…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1973, 1, 1, 105701, 7, 3, 0, "あむっ…うん！\n美味しい～！\n今度作ってみよう♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1974, 1, 1, 105701, 8, 3, 0, "空を冒険するには\n色んなことを\n知っておかないと…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1976, 1, 1, 105702, 1, 1, 0, "…戻らなくなったよ\nどうすれば元に\n戻る…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1977, 1, 1, 105702, 2, 1, 0, "暇だよ…\nどこかに強い人は\nいないか？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1978, 1, 1, 105702, 3, 1, 0, "ジョブチェンジ！\n…叫んでも\n戻らないよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1979, 1, 1, 105702, 4, 1, 0, "今のうちに、\n魔物退治の\n仕事に行くね！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1980, 1, 1, 105702, 5, 1, 0, "ハァ～…\nうん、気の巡りは\n問題ないね！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1981, 1, 8, 105702, 101, 1, 0, "私の力の源は\n仲間との\n絆だよ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1982, 1, 8, 105702, 102, 1, 0, "いつかこの拳を\n空を越えた、星まで\n届かせたいね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1983, 1, 1, 105702, 1, 2, 0, "功夫が足りない人は\nどうすればいいか？\nもちろん修行だよ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1984, 1, 1, 105702, 2, 2, 0, "私はいつも通りに\n喋ってるつもり\nだよ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1985, 1, 1, 105702, 3, 2, 0, "どうした？\nもしかして手合せ\nしたいか？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1986, 1, 1, 105702, 4, 2, 0, "騎空士としても\n拳法家としても\n目指すは世界一ね！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1987, 1, 8, 105702, 101, 2, 0, "背中はあなたに\n預けたよ！\nよろしくね！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1988, 1, 8, 105702, 102, 2, 0, "あなたと一緒なら\nきっと、今よりもっと\n強くなれるよ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1989, 1, 1, 105702, 1, 2, 0, "ちゃんと起きてるか？\n寝ぼけてたら\nダメよ？", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1990, 1, 1, 105702, 2, 2, 0, "腹減ったね…\nなにか食べに\n行くよ！", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1991, 1, 1, 105702, 3, 2, 0, "修行のしすぎは\n良くないよ？\n気をつけるね", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1992, 1, 1, 105702, 1, 3, 0, "美しい技は\n美しい体が\nあってこそ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1993, 1, 1, 105702, 2, 3, 0, "よいしょっと…\nちょっとのんびり\nさせてもらうよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1994, 1, 1, 105702, 3, 3, 0, "…練習に\n使うのはさすがに\n可哀そうね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1995, 1, 1, 105702, 4, 3, 0, "これは…\n瞑想の時にも\n使ってみたいね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1996, 1, 1, 105702, 5, 3, 0, "出来ればもっと\n熱いほうが\n嬉しいよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1997, 1, 1, 105702, 6, 3, 0, "ここでも\n修行出来るなんて\n最高だよ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1998, 1, 1, 105702, 7, 3, 0, "気力の源は体力！\n体力の源は\n健康とご飯！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (1999, 1, 1, 105702, 8, 3, 0, "んー…\n難しいことは\nわかんないよ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2001, 1, 1, 105801, 1, 1, 0, "わたしたちの\nギルドハウスへ\nようこそ～！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2002, 1, 1, 105801, 2, 1, 0, "ぐるるる～…\nあ、お腹が\n鳴っちゃいました…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2003, 1, 1, 105801, 3, 1, 0, "ん？\n食べ物のにおい？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2004, 1, 1, 105801, 4, 1, 0, "みんなと仲良く食卓を\n囲みたいですね～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2005, 1, 1, 105801, 5, 1, 0, "この部屋が、わたしの\n帰る場所です♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2006, 1, 4, 105801, 101, 1, 0, "あの人を見ると\n胸が満たされます～\nお腹は空いてますが！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2007, 1, 8, 105801, 102, 1, 0, "あの人、\n大食いの子って\n嫌いかな？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2008, 1, 1, 105801, 1, 2, 0, "食べもの、くれるん\nですか？　わーい！\n恋しちゃいそう♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2009, 1, 1, 105801, 2, 2, 0, "つ、つまみ食いなんて\nしてませんよ？\nこれは味見です！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2010, 1, 1, 105801, 3, 2, 0, "おやつは別腹、\nごはんも別腹、\n魔物も別腹～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2011, 1, 1, 105801, 4, 2, 0, "好きなものを、\n好きな人達と食べる！\n最高の贅沢ですね☆", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2012, 1, 4, 105801, 101, 2, 0, "あなたは、\nごはん王子ですね～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2013, 1, 8, 105801, 102, 2, 0, "大好きです～\n愛してます～\nえへへ～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2014, 1, 1, 105801, 1, 2, 0, "朝ごはんも終わったし\n早速ブランチを\n食べませんか？", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2015, 1, 1, 105801, 2, 2, 0, "お昼ご飯、\nおいしかったですね～\nそれじゃあ、そろそろ\nおやつにしましょう☆", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2016, 1, 1, 105801, 3, 2, 0, "お夕飯も、晩ごはんも\n食べたし…さぁ！\nお夜食の時間ですよ☆", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2017, 1, 1, 105801, 1, 3, 0, "身だしなみ、\n身だしなみ…\nきちんとしないとね♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2018, 1, 1, 105801, 2, 3, 0, "ふい～\n消化中です～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2019, 1, 1, 105801, 3, 3, 0, "おいしそ…\nか、かわいいです～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2020, 1, 1, 105801, 4, 3, 0, "いい香りですね～\n空気までおいしそう～\nじゅるり♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2021, 1, 1, 105801, 5, 3, 0, "ほっかほか～♪\n魔物の丸焼きが\n作れちゃいますね！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2022, 1, 1, 105801, 6, 3, 0, "走って食べて！\n走って食べて！\n\この無限ループです！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2023, 1, 1, 105801, 7, 3, 0, "いくつでも\n食べられますよ！\n幸せ～☆", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2024, 1, 1, 105801, 8, 3, 0, "料理の絵を\n見ているだけで、\nよ、よだれが～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2026, 1, 1, 105802, 1, 1, 0, "この部屋が、わたしの\n帰る場所です♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2027, 1, 1, 105802, 2, 1, 0, "ギルドハウス\nどんどん素敵に\nなっていくな～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2028, 1, 1, 105802, 3, 1, 0, "「やばい」とか\n「おいっす」とか…\n民の言葉って難しい", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2029, 1, 1, 105802, 4, 1, 0, "お菓子の家に\n住みたいな～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2030, 1, 1, 105802, 5, 1, 0, "あ、頼んでた出前が\n届いたかな？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2031, 1, 8, 105802, 101, 1, 0, "大切な人がいて\n大切な場所がある\nすごく幸せです♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2032, 1, 8, 105802, 102, 1, 0, "あの人\n大食いの子って\n嫌いかな？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2033, 1, 1, 105802, 1, 2, 0, "ごはんに行きますか？\n作りましょうか？\n店屋物もいいですね～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2034, 1, 1, 105802, 2, 2, 0, "モグモグ…\nあげませんよ～", 0);
