INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2117, 1, 1, 105901, 1, 3, 0, "もう少し…\nおっきくなりたい\nです", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2118, 1, 1, 105901, 2, 3, 0, "このふかふかの優しさ\n主さまのようです", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2119, 1, 1, 105901, 3, 3, 0, "かわいい♪\n主さまにそっくり♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2120, 1, 1, 105901, 4, 3, 0, "故郷の香りとは\n違いますが、これも\n心地よいものです…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2121, 1, 1, 105901, 5, 3, 0, "ここで洗濯物を\n乾かせるでしょうか", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2122, 1, 1, 105901, 6, 3, 0, "都会の人々は\nこんなふうに\n運動を…！？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2123, 1, 1, 105901, 7, 3, 0, "これは…主さまにも\n食べさせて\n差し上げたい…\nあーんって♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2124, 1, 1, 105901, 8, 3, 0, "よく学んで、\n主さまのお役に\n立たなければ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2126, 1, 1, 105902, 1, 1, 0, "主さまがいつ戻って\n来ても平気なよう\n部屋を綺麗に保たねば", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2127, 1, 1, 105902, 2, 1, 0, "節約のためにも\n自家菜園などするのは\nどうでしょう…　", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2128, 1, 1, 105902, 3, 1, 0, "アメスさまへの\nお祈りができそうな\n場所は…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2129, 1, 1, 105902, 4, 1, 0, "瞑想をすると\n心が安らぎます…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2130, 1, 1, 105902, 5, 1, 0, "都会の方の身なりを\n勉強した方が\n良いのでしょうか？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2131, 1, 8, 105902, 101, 1, 0, "わたくしの居場所は\n主さまのお傍です\nずっと、ずっと…♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2132, 1, 8, 105902, 102, 1, 0, "主さまの隣には\nわたくしが…\nわたくしの隣には\n主さまが…幸せです", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2133, 1, 1, 105902, 1, 2, 0, "主さま、なにか\nしてほしいことは\nありませんか？\n欲しいものは？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2134, 1, 1, 105902, 2, 2, 0, "わたくし、背が伸び\nましたでしょうか？\n…そうですか", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2135, 1, 1, 105902, 3, 2, 0, "お出かけですか？\nわたくしもお供\nいたします", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2136, 1, 1, 105902, 4, 2, 0, "ひゃん！\n主さま…\nいじわるです", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2137, 1, 8, 105902, 101, 2, 0, "主さま、なにか御用は\nありませんか？\nわたくし、主さまの\nお役にたちたいのです", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2138, 1, 8, 105902, 102, 2, 0, "主さまの優しさに\nわたくしはいつも\n癒されています…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2139, 1, 1, 105902, 1, 2, 0, "おはようございます♪\n主さまの可愛らしい\n寝顔、堪能致しました", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2140, 1, 1, 105902, 2, 2, 0, "のんびりした時間帯\nですね…ふぁ～…\nし、失礼致しました", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2141, 1, 1, 105902, 3, 2, 0, "ふむ、都会は夜でも\nこんなにも、にぎやか\nなのですね", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2142, 1, 1, 105902, 1, 3, 0, "主さまに会う前に\n身なりを整えなくては\nいけませんね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2143, 1, 1, 105902, 2, 3, 0, "このふかふかの優しさ\n主さまのようです", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2144, 1, 1, 105902, 3, 3, 0, "かわいい♪\n主さまにそっくり♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2145, 1, 1, 105902, 4, 3, 0, "この心地よさ…\n主さまのようです♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2146, 1, 1, 105902, 5, 3, 0, "ここで洗濯物を\n乾かせるでしょうか　", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2147, 1, 1, 105902, 6, 3, 0, "はぁっはぁっはぁっ\n…すべては\n主さまのため…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2148, 1, 1, 105902, 7, 3, 0, "都会の味…\nとてもおいしいです\n…もぐもぐもぐ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2149, 1, 1, 105902, 8, 3, 0, "都会のことを\n学ばないと…\nふむふむ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2151, 1, 1, 190801, 1, 2, 0, "ふぁ～…あっ\n見られちゃい\nましたか？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2152, 1, 1, 190801, 2, 2, 0, "ギルドハウスの\n使い心地は\nどうですか？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2153, 1, 1, 190801, 3, 2, 0, "ご用があれば\nなんでも言って\nくださいね～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2154, 1, 1, 190801, 4, 2, 0, "ときどきみなさんと\nクエストに行きたいな\nって思っちゃいます", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2155, 1, 1, 190801, 1, 2, 0, "おはようございます！\nお掃除の方、\nはじめていきますね", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2156, 1, 1, 190801, 2, 2, 0, "皆さん、お昼ごはんの\nご要望がありましたら\n用意致しますよ♪", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2157, 1, 1, 190801, 3, 2, 0, "ここは夜でも\nにぎやかですね", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2158, 1, 1, 104701, 1, 1, 0, "…\n少しうとうと\nしてしまった", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2159, 1, 1, 104701, 2, 1, 0, "室内だし兜を脱ぐか\n…いや\nやはりやめておこう", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2160, 1, 1, 104701, 3, 1, 0, "部屋にいると\n鎧が蒸し暑くなるが\n我慢せねば…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2161, 1, 1, 104701, 4, 1, 0, "仕事が門番だからか\n部屋にいても\nじっとしてしまう…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2162, 1, 1, 104701, 5, 1, 0, "少年と会話したことを\n思いだすと\n自然と楽しくなるな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2163, 1, 4, 104701, 101, 1, 0, "少年のことを考えると\n顔がにやけてしまう", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2164, 1, 8, 104701, 102, 1, 0, "少年は今頃\n平和に過ごせて\nいるかな…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2165, 1, 1, 104701, 1, 2, 0, "ん？　悪いが\n兜は脱がないぞ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2166, 1, 1, 104701, 2, 2, 0, "私と遊びたいのか？\nそう言われても\n何をしていいやら…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2167, 1, 1, 104701, 3, 2, 0, "鎧が重くないかって？\n大丈夫。けっこう\n鍛えているからな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2168, 1, 1, 104701, 4, 2, 0, "少年、寒くはないか？\nいや自分がこうなので\n人が薄着に見えてね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2169, 1, 4, 104701, 101, 2, 0, "君といると、兜の中が\nいつもより暑いんだ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2170, 1, 8, 104701, 102, 2, 0, "私としゃべりたい？\n物好きだな\nでも、嬉しいよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2171, 1, 1, 104701, 1, 2, 0, "早いな少年\nえ？　私の仕事に\n合わせてくれたのか", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2172, 1, 1, 104701, 2, 2, 0, "昼食の誘い？\n嬉しいけど\n人前では食べないんだ", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2173, 1, 1, 104701, 3, 2, 0, "王宮騎士団の\n日報を書かなければ…\n『何事もなし』、と", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2175, 1, 1, 104701, 2, 3, 0, "鎧を着ているから\n感触が分かりづらい\nのが残念だ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2176, 1, 1, 104701, 3, 3, 0, "か、かわいいな…\n私には似合わないが\nぎゅ…ってしよう", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2177, 1, 1, 104701, 4, 3, 0, "良い香りだ…\n普段はどうしても\n鉄くさくなるからな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2178, 1, 1, 104701, 5, 3, 0, "わかっていたけど\n鎧を着たまま暖炉に\nあたると暑い…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2180, 1, 1, 104701, 7, 3, 0, "兜を脱がずに食べる\nうん、段々コツが\n掴めてきた…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2181, 1, 1, 104701, 8, 3, 0, "兜があっても\n意外と本くらいは\n難なく読めるんだ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2183, 1, 1, 102601, 1, 1, 0, "居心地よくて\nゴロゴロしちゃうよ～\n…いつも通りだね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2184, 1, 1, 102601, 2, 1, 0, "ドングリの木\n近くに生えてない？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2185, 1, 1, 102601, 3, 1, 0, "快適な寝床があれば\nそれだけで幸せだねぇ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2186, 1, 1, 102601, 4, 1, 0, "あたしと一緒に\nだらけてくれる人が\nいそうだねぇ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2187, 1, 1, 102601, 5, 1, 0, "ここなら牧場よりも\n速く配達が\n届きそうだね～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2188, 1, 4, 102601, 101, 1, 0, "あんパン買ったし\nあいつにも\n分けてあげよう", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2189, 1, 8, 102601, 102, 1, 0, "あいつと一緒に\nだらけられたら\n最高に幸せだね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2190, 1, 1, 102601, 1, 2, 0, "ドングリでも\nつまみながら\n一緒にゲームしようよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2191, 1, 1, 102601, 2, 2, 0, "横になりたいから\n枕になっておくれよ～\nあんたあったかいしさ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2192, 1, 1, 102601, 3, 2, 0, "ドングリあげるから\nあんパン買ってきて～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2193, 1, 1, 102601, 4, 2, 0, "う～\n疲れちゃったよ～\nおんぶして運んで～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2194, 1, 4, 102601, 101, 2, 0, "あんたといると\nリア充って\n感じがするよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2195, 1, 8, 102601, 102, 2, 0, "あんたが一生\n世話してくれたら\n人生安泰なんだけどね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2196, 1, 1, 102601, 1, 2, 0, "ふわ～っ…\nおはよう…あたしは\nまだ眠いよ", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2197, 1, 1, 102601, 2, 2, 0, "そろそろお昼寝でも\nしたい気分だねぇ", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2198, 1, 1, 102601, 3, 2, 0, "徹夜でゲームするけど\nあんたもどうだい？\nあたしは強いよ", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2200, 1, 1, 102601, 2, 3, 0, "あ…だめだこれ。\n気持ち良くて\n落ちそう…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2201, 1, 1, 102601, 3, 3, 0, "かわいいものに\n囲まれて寝るのも\nよさそうだねぇ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2202, 1, 1, 102601, 4, 3, 0, "心身ともに\nリラックスした状態で\nぐうたら出来そうだね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2203, 1, 1, 102601, 5, 3, 0, "ふい～…\nごくらくごくらく…\n暖かいねぇ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2205, 1, 1, 102601, 7, 3, 0, "食べてすぐ寝ると\n牛になるって\n本当なのかな？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2206, 1, 1, 102601, 8, 3, 0, "おー！　この本なら\nあたしも居眠りせずに\n読めそうだよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2208, 1, 1, 106301, 1, 1, 0, "ランドソル…\nおっきな街だなぁ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2209, 1, 1, 106301, 2, 1, 0, "ロザリア…\nきっと、いるよね？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2210, 1, 1, 106301, 3, 1, 0, "最近、弓の練習\nサボっちゃってるな…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2211, 1, 1, 106301, 4, 1, 0, "今日の私は\n森番じゃなくて\n家番だよ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2212, 1, 1, 106301, 5, 1, 0, "ちょっとだけ\n慣れてきたかも？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2213, 1, 4, 106301, 101, 1, 0, "いつか、\nあの人と一緒に…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2214, 1, 8, 106301, 102, 1, 0, "…なんか最近、\nあの人のこと\nばっかり…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2215, 1, 1, 106301, 1, 2, 0, "もしよかったら\n街に出かけない？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2216, 1, 1, 106301, 2, 2, 0, "この部屋、\n妖精さんも\n気に入るかも！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2217, 1, 1, 106301, 3, 2, 0, "今日は\nなににしよっか？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2218, 1, 1, 106301, 4, 2, 0, "今日はどこ行く？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2219, 1, 4, 106301, 101, 2, 0, "たまには二人で…\nな、なんちゃって！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2220, 1, 8, 106301, 102, 2, 0, "最近、\nずっと楽しいんだ！\nあなたのおかげかな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2221, 1, 1, 106301, 1, 2, 0, "おはよう！\n今日も一緒に\n頑張ろうね！", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2222, 1, 1, 106301, 2, 2, 0, "お昼はどうする？\nあなたのオススメに\n連れて行って欲しいな", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2223, 1, 1, 106301, 3, 2, 0, "ランドソルは\n夜でも賑やかだね！", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2225, 1, 1, 106301, 2, 3, 0, "わぁっ！\nすっごく\nふわふわだよ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2226, 1, 1, 106301, 3, 3, 0, "かわい～！\nぎゅってしたく\nなっちゃうかも！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2227, 1, 1, 106301, 4, 3, 0, "くんくん\nなんか…いい匂い？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2228, 1, 1, 106301, 5, 3, 0, "すっごいなー\nあったかいなー！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2230, 1, 1, 106301, 7, 3, 0, "わぁっ、おいしい！\nもっと食べたい\nかも…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2231, 1, 1, 106301, 8, 3, 0, "も、文字は苦手…\nでも、頑張らないと\nだよね…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2233, 1, 1, 103601, 1, 1, 0, "にんじんとピーマンを\n食べないでいい方法\nないかな…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2234, 1, 1, 103601, 2, 1, 0, "カップケーキが\n食べたいな…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2235, 1, 1, 103601, 3, 1, 0, "予習が終わりました！\nさて、次は復習を\nしないと…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2236, 1, 1, 103601, 4, 1, 0, "あの人の部屋にいる\n私って…もしかして\nふしんしゃなんじゃ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2237, 1, 1, 103601, 5, 1, 0, "男の人は、みんな\nへんたいだと\n聞きました…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2238, 1, 4, 103601, 101, 1, 0, "あの人に、一緒に\n遊んでほしいけど…\nな、なんていえば？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2239, 1, 8, 103601, 102, 1, 0, "早く大人になりたいな\n…あの人と同じくらい", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2240, 1, 1, 103601, 1, 2, 0, "ぼ、防犯魔石を\n鳴らしますよ！？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2241, 1, 1, 103601, 2, 2, 0, "へんたい！？　…\nあ、あなたでしたか～\nやっぱりへんたーい！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2242, 1, 1, 103601, 3, 2, 0, "これ宿題です\nやっておいて\nくださいね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2243, 1, 1, 103601, 4, 2, 0, "あ、遅刻ですよ\n待っていたんですから", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2244, 1, 4, 103601, 101, 2, 0, "にらめっこですか？\nあ、あなたの前で変顔\nするのはちょっと…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2245, 1, 8, 103601, 102, 2, 0, "あの…何歳まで\n子供だと思いますか？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2246, 1, 1, 103601, 1, 2, 0, "顔を洗って、着替えて\n朝ごはんを食べて、\n歯を磨いて…", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2247, 1, 1, 103601, 2, 2, 0, "て、鉄棒の練習！？　\nほ、他のことを\nしませんか？", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2248, 1, 1, 103601, 3, 2, 0, "ね、眠くないです\n子供ではありません\nから…ふわぁ", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2250, 1, 1, 103601, 2, 3, 0, "ひと休みしよっと\n………うん、\nひと休みおしまい♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2251, 1, 1, 103601, 3, 3, 0, "つ、つぶらな瞳には\n弱いんです", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2252, 1, 1, 103601, 4, 3, 0, "リラックスできますね\n集中力も\n上がりそうです", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2253, 1, 1, 103601, 5, 3, 0, "あったかいと\n眠くなっちゃうな…\n勉強しなくちゃなのに", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2255, 1, 1, 103601, 7, 3, 0, "ピーマンもこれくらい\nおいしければ\nいいのに…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2256, 1, 1, 103601, 8, 3, 0, "この本、むずかしい\n…もっと勉強\nがんばらなくちゃ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2258, 1, 1, 101501, 1, 1, 0, "今日はみんなに\nなにを読んで\nあげようかしら", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2259, 1, 1, 101501, 2, 1, 0, "子どもたちと同じ\n目線で考えるのが\n大事よね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2260, 1, 1, 101501, 3, 1, 0, "お天気が良い日は\nみんなでお散歩に\n行きたいわね～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2261, 1, 1, 101501, 4, 1, 0, "鬼ごっこにかくれんぼ\n…みんなは\nどっちが好きかしら", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2262, 1, 1, 101501, 5, 1, 0, "お遊戯会の配役、\n考え出すと\n大変だわ～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2263, 1, 4, 101501, 101, 1, 0, "彼は優しいから、\nきっと良い\nお父さんになるわね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2264, 1, 8, 101501, 102, 1, 0, "彼には私がいないと\n…っていうのは少し\n過保護すぎるかしら？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2265, 1, 1, 101501, 1, 2, 0, "保育園の行事を\n考えてるんだけど…\n良い案ないかしら？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2266, 1, 1, 101501, 2, 2, 0, "今度、子どもたちと\nおやつを作るから、\n食べに来てね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2267, 1, 1, 101501, 3, 2, 0, "よかったら一緒に\n絵本を作らない？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2268, 1, 1, 101501, 4, 2, 0, "あなたはいつも元気ね\n先生も見習わないと♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2269, 1, 4, 101501, 101, 2, 0, "先生は\nずーっと、ずーっと、\nあなたの味方だからね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2270, 1, 8, 101501, 102, 2, 0, "あなたみたいな\nいい子をお世話できて\n先生は幸せものだわ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2271, 1, 1, 101501, 1, 2, 0, "おはよう～\nちゃんと朝ごはんは\n食べた？", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2272, 1, 1, 101501, 2, 2, 0, "お昼寝の時間ね♪\nあなたも一緒に\n寝ましょうか", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2273, 1, 1, 101501, 3, 2, 0, "すっかり真っ暗ね。\n今日も一日、\nよくできました♪", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2275, 1, 1, 101501, 2, 3, 0, "ふかふかねぇ♪\n年甲斐もなく、\n飛び跳ねちゃいそう", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2276, 1, 1, 101501, 3, 3, 0, "あらあら、かわいい！\n保育園のみんなに\n見せてあげたいわ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2277, 1, 1, 101501, 4, 3, 0, "いい香りね～\n保育園にも\n置こうかしら", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2278, 1, 1, 101501, 5, 3, 0, "寒い日は、ここで\n子どもたちに絵本を\n読んであげましょ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2280, 1, 1, 101501, 7, 3, 0, "美味しい～♪\n遠足に持っていったら\nみんな喜びそう", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2281, 1, 1, 101501, 8, 3, 0, "みんなが\n楽しめる読み聞かせの\n方法特集♪　素敵ねぇ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2283, 1, 1, 104401, 1, 1, 0, "世界の前に、\nこの部屋を征服\nしてやろう♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2284, 1, 1, 104401, 2, 1, 0, "拠点とはやはり\n斯くあるべきじゃの", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2285, 1, 1, 104401, 3, 1, 0, "眷属の物はわらわの物\n故に、この部屋は\nわらわの部屋じゃな！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2286, 1, 1, 104401, 4, 1, 0, "永く眠っている間に\n随分と世界は変わった\nようじゃな…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2287, 1, 1, 104401, 5, 1, 0, "うるさいのう～\nまったく、近頃の\n若いもんは…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2288, 1, 4, 104401, 101, 1, 0, "あやつが来てから\n動悸が治まらぬ…\n如何ような病で\nあろうかの…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2289, 1, 8, 104401, 102, 1, 0, "眷属よ、どこに行った\nのじゃ？\nわらわの世話を\n許可するのじゃ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2290, 1, 1, 104401, 1, 2, 0, "いずれ世界は\n我が手中に収まる\nふふふ、\n楽しみじゃのう", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2291, 1, 1, 104401, 2, 2, 0, "眷属よ、わらわから\n離れてはならんぞ！\n子供に戻ってしまう\nではないか！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2292, 1, 1, 104401, 3, 2, 0, "トマトジュースが\n欲しいのじゃ！\n野菜ジュースでは\nダメだぞ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2293, 1, 1, 104401, 4, 2, 0, "蚊にくわれたぞ！\nわらわの血を吸う\nとは～！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2294, 1, 4, 104401, 101, 2, 0, "こら、頭をなでるな\nお主はこの姿でも\n子供扱いするのか？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2295, 1, 8, 104401, 102, 2, 0, "お主が望むなら世界の\n半分をくれてやっても\n良いのじゃぞ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2296, 1, 1, 104401, 1, 2, 0, "朝早くから感心じゃの\nさすがわらわの\n眷属じゃ", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2297, 1, 1, 104401, 2, 2, 0, "太陽は苦手じゃ…\nこの部屋の中で\n過ごそうぞ", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2298, 1, 1, 104401, 3, 2, 0, "夜も更けてきた！\nわらわ達の時間は\nこれからじゃ～♪", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2300, 1, 1, 104401, 2, 3, 0, "よい座り心地じゃ\nわらわの玉座としよう", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2301, 1, 1, 104401, 3, 3, 0, "可愛いではないか\nわらわの眷属に\nしてやろうかのう", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2302, 1, 1, 104401, 4, 3, 0, "うむ、良い気持ちじゃ\nこれを作った者、\n褒めてつかわすぞ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2303, 1, 1, 104401, 5, 3, 0, "あたたかい…\n身も心も\n浄化されるようじゃ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2305, 1, 1, 104401, 7, 3, 0, "吸血鬼のわらわが、\nこのようなもの…\nうまい♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2306, 1, 1, 104401, 8, 3, 0, "このまま大人の姿を\n保つ方法…どこかに\n書いてないかのう", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2308, 1, 1, 105401, 1, 1, 0, "いろんな人がいて\nいろんな服を着てて…\n目の保養になります♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2309, 1, 1, 105401, 2, 1, 0, "レイ様への新作\nどんな服にしよう…\n方向性が絞れない～！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2310, 1, 1, 105401, 3, 1, 0, "忘れないうちに\n隠し糸のメンテナンス\nやっておこうっと！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2311, 1, 1, 105401, 4, 1, 0, "むっ！　あの人の\nスリーサイズは…\nほほう…なるほど♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2312, 1, 1, 105401, 5, 1, 0, "ノゾミさんの\n衣装はああして\nチカさんのは…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2313, 1, 4, 105401, 101, 1, 0, "私がアイドル…\n…実感ないけど\n精一杯やってみよう", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2314, 1, 8, 105401, 102, 1, 0, "まさか私が\nアイドルになるなんて\n思ってなかったです", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2315, 1, 1, 105401, 1, 2, 0, "なんですかぁ？\n衣装作りで疲れて\n眠いんですぅ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2316, 1, 1, 105401, 2, 2, 0, "あーっ！　また\n服がほつれてる…\nしょうがないですね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2317, 1, 1, 105401, 3, 2, 0, "はっ！　ちょうど今\n新しいデザインが\n思い浮かびました！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2318, 1, 1, 105401, 4, 2, 0, "暇そうですね？\n時間あるなら仕事\n手伝ってください！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2319, 1, 4, 105401, 101, 2, 0, "どうしました？\n新しい服が\n欲しくなりましたか？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2320, 1, 8, 105401, 102, 2, 0, "騎士さんのおかげで\nカルミナでも\n頑張れてますよ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2321, 1, 1, 105401, 1, 2, 0, "おはようございます…\nなんで朝って毎日\nやってくるんでしょう", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2322, 1, 1, 105401, 2, 2, 0, "お腹空きましたね\n騎士さんはちゃんと\n御飯食べましたか？", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2323, 1, 1, 105401, 3, 2, 0, "夜の方が\n作業が捗るのは\n私だけですかね？", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2325, 1, 1, 105401, 2, 3, 0, "このふかふか具合\nいい素材を\n使ってますね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2326, 1, 1, 105401, 3, 3, 0, "小さくて可愛い♪\nそうだ！　レイ様の\nぬいぐるみを…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2327, 1, 1, 105401, 4, 3, 0, "こういう時くらい\n何も考えずに\nリラックスしないと♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2328, 1, 1, 105401, 5, 3, 0, "炎を使った服って\nカッコ良いかも…\nでも材料がな～…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2330, 1, 1, 105401, 7, 3, 0, "何をするにも\n食事は大切！\n体力が資本です！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2331, 1, 1, 105401, 8, 3, 0, "私もいつか\nデザインを\nまとめて本に…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2333, 1, 1, 107501, 1, 1, 0, "またお魚を\n捕りにいこうかな♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2334, 1, 1, 107501, 2, 1, 0, "そろそろみんなも\nお腹が空くころ\nじゃないですか～？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2335, 1, 1, 107501, 3, 1, 0, "ここなら、\n日焼けの心配は\nありませんね！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2336, 1, 1, 107501, 4, 1, 0, "お刺身に焼き貝…\nお腹が魚介類を\n求めてますっ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2337, 1, 1, 107501, 5, 1, 0, "泳ぐのも食べるのも\n大得意です！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2338, 1, 4, 107501, 101, 1, 0, "あの人と二人で\n浜辺を散歩…\n素敵ですねぇ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2339, 1, 8, 107501, 102, 1, 0, "あの人に水着を\n褒めてもらえたら、\nすごく嬉しいなぁ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2340, 1, 1, 107501, 1, 2, 0, "たまには釣りじゃなく\n一緒に素潜りでも\n行きませんか？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2341, 1, 1, 107501, 2, 2, 0, "男の子に水着を\n見られるのって\n少し緊張しちゃいます", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2342, 1, 1, 107501, 3, 2, 0, "ギルドハウスの\nみんなとも、\n海に行きたいですね！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2343, 1, 1, 107501, 4, 2, 0, "あなたの水着、\n私が選んであげます！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2344, 1, 4, 107501, 101, 2, 0, "泳ぎの練習なら\n付き合いますよ！\n手取り足取り♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2345, 1, 8, 107501, 102, 2, 0, "水着姿が好きなら\nいくらでも\n着てあげますよぉ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2346, 1, 1, 107501, 1, 2, 0, "朝の海も\n幻想的で\nいいですよねぇ～！", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2347, 1, 1, 107501, 2, 2, 0, "お昼はみんなで、\n海鮮バーベキューとか\nどうでしょう！", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2348, 1, 1, 107501, 3, 2, 0, "夜の海は星が\nきれいですよ！\n見に行きましょう！", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2350, 1, 1, 107501, 2, 3, 0, "泳ぎ疲れたので\nちょっと休憩です～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2351, 1, 1, 107501, 3, 3, 0, "あなたにも、\n可愛い水着を\n着せてあげたいです", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2352, 1, 1, 107501, 4, 3, 0, "なんだか、\nリゾート気分です…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2353, 1, 1, 107501, 5, 3, 0, "ふい～、泳ぎで冷えた\n身体を暖めましょう～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2355, 1, 1, 107501, 7, 3, 0, "おいひ～です～\nこれ、海水浴にも\n持っていきたいですね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2356, 1, 1, 107501, 8, 3, 0, "南国のフルーツ図鑑\n…じゅるり…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2358, 1, 1, 107501, 10, 3, 0, "わ～♪　おいしそ…\nか、かわいいですね～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2359, 1, 1, 107501, 11, 3, 0, "これくらい大きな貝…\n食べたいな～\n食べたいな～～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2360, 1, 1, 107601, 1, 1, 0, "この格好で\n部屋をうろつくのは\n不思議な感じがします", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2361, 1, 1, 107601, 2, 1, 0, "南国の果実を使って\n主さまにデザートを\nお作りしましょう", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2362, 1, 1, 107601, 3, 1, 0, "せっかくの水着ですし\nこれから定期的に\n海水浴に行きたいです", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2363, 1, 1, 107601, 4, 1, 0, "海の生き物も、\n森の生き物に負けず\n可愛かったです…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2364, 1, 1, 107601, 5, 1, 0, "クロールの練習なら、\n部屋でもできそうです", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2365, 1, 4, 107601, 101, 1, 0, "主さまに、水着姿を\n褒めていただけた…\nえへへ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2366, 1, 8, 107601, 102, 1, 0, "水着で主さまの\nお世話をしたら、\n喜びますでしょうか…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2367, 1, 1, 107601, 1, 2, 0, "お疲れでしたら\nわたくしの膝を\nお使いくださいね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2368, 1, 1, 107601, 2, 2, 0, "主さま、海の家で\nつかうおこづかいを\n渡しておきますね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2369, 1, 1, 107601, 3, 2, 0, "海の美しさは、\n森とはちがう\n魅力がありました", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2370, 1, 1, 107601, 4, 2, 0, "この髪飾りですか？\n造花ですから\n水の中も平気です", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2371, 1, 4, 107601, 101, 2, 0, "水着でくっついて\n眠ると、いつもより\n密着して暖かいです", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2372, 1, 8, 107601, 102, 2, 0, "はじめての海が、\n主さまと一緒で\n幸せです♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2373, 1, 1, 107601, 1, 2, 0, "おはようございます。\n泳ぎに行く準備は\n万端です", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2374, 1, 1, 107601, 2, 2, 0, "午後は泳ぐのをやめて\nスイカ割りなど\nいかがでしょう？", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2375, 1, 1, 107601, 3, 2, 0, "夜も更けてきました…\n波の音を聞きながら\n少しお話しませんか？", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2377, 1, 1, 107601, 2, 3, 0, "あぅ…この家具には、\n人をダメにする魔法が\nかけられています…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2378, 1, 1, 107601, 3, 3, 0, "ふふっ♪\nあなたも、海水浴に\n行きますか？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2379, 1, 1, 107601, 4, 3, 0, "この香り…\nきっと主さまも\n気に入ります", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2380, 1, 1, 107601, 5, 3, 0, "これは暖かいです…\n後ほど主さまと一緒に\nあたりましょう", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2382, 1, 1, 107601, 7, 3, 0, "泳いだ後の食事は\n一段と美味しく\n感じます", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2383, 1, 1, 107601, 8, 3, 0, "深海魚の生態…\nこれは主さまにも\nお伝えしなくては…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2385, 1, 1, 107601, 10, 3, 0, "顔立ちが、主さまに\n似ております…\nかわいらしい♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2386, 1, 1, 107601, 11, 3, 0, "ふかふかですね\n主さまのようです♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2387, 1, 1, 107701, 1, 1, 0, "お掃除、お掃除\nふんふふ～ん☆\nあ、聞かれてました？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2388, 1, 1, 107701, 2, 1, 0, "日焼けには回復魔法が\n効きますよ。どうして\n逃げるんですか～！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2389, 1, 1, 107701, 3, 1, 0, "今日はドジを\nしませんように！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2390, 1, 1, 107701, 4, 1, 0, "接客業を極めれば\nメイドとして\n成長できるかも？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2391, 1, 1, 107701, 5, 1, 0, "水着って\n動きやすいんですけど\n人の目が気になります", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2392, 1, 4, 107701, 101, 1, 0, "あの人の気持ちを知る\n魔法とかあれば…\nな、なんでもないです", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2393, 1, 8, 107701, 102, 1, 0, "水着でご奉仕したら、\nあの人は喜ぶかな？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2394, 1, 1, 107701, 1, 2, 0, "暑いのでしたら、\n私が魔法で涼しく…\nえ？　いらない？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2395, 1, 1, 107701, 2, 2, 0, "何か御用ですか？\n特にないのでしたら、\nお話ししましょう♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2396, 1, 1, 107701, 3, 2, 0, "水着姿でも\nメイドはメイドです\nご奉仕しますよ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2397, 1, 1, 107701, 4, 2, 0, "ひゃっ！　あ、危うく\n転ぶところでした…\n…きゃうっ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2398, 1, 4, 107701, 101, 2, 0, "どうかしましたか？\nあなたの御用なら、\n喜んでお受けしますよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2399, 1, 8, 107701, 102, 2, 0, "水着姿をあなたに\n見られると、\nドキドキしちゃいます", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2400, 1, 1, 107701, 1, 2, 0, "朝は暑くてやる気が\n出ない？　でしたら、\nえぇい、元気になぁれ", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2401, 1, 1, 107701, 2, 2, 0, "お腹がぺこぺこなら、\nお昼は海の家で\n食べましょう♪", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2402, 1, 1, 107701, 3, 2, 0, "夜の浜辺でお散歩も\nいいですね。たくさん\n転びそうですけど…", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2404, 1, 1, 107701, 2, 3, 0, "休憩しながら、\nお掃除すれば\n一石二鳥です！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2405, 1, 1, 107701, 3, 3, 0, "きれいにすれば\nかわいさも倍増\nですよ～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2406, 1, 1, 107701, 4, 3, 0, "素敵な香りです～\n救護院に置けば、\nみんな喜ぶかも～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2407, 1, 1, 107701, 5, 3, 0, "あったかいですね…\nはっ！　いけません！\nメイドがサボるなんて", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2409, 1, 1, 107701, 7, 3, 0, "料理のできない\nメイドなんて…\nはぐはぐ、おいしい♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2410, 1, 1, 107701, 8, 3, 0, "水着姿で接客をすると\n売り上げが倍になる\n…本当ですか、これ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2412, 1, 1, 107701, 10, 3, 0, "えへへ♪\n南の島まで\n乗せてください！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2413, 1, 1, 107701, 11, 3, 0, "なんだか、\n人魚姫に\nなった気分です♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2414, 1, 1, 107801, 1, 1, 0, "みんなで過ごす\n休日っていいわよね…\nずっと続かないかな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2415, 1, 1, 107801, 2, 1, 0, "海に浮輪でぷかぷか\n浮かぶのって\n癖になりそう", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2416, 1, 1, 107801, 3, 1, 0, "ビーチにいると\n日焼けが心配だわ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2417, 1, 1, 107801, 4, 1, 0, "水着姿って楽よね。\n室内だとちょっと\n落ち着かないけど…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2418, 1, 1, 107801, 5, 1, 0, "お、泳ぎの練習なんて\nしないからね！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2419, 1, 4, 107801, 101, 1, 0, "楽しすぎて立場を\n忘れそう…あんたの\nせいなんだから！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2420, 1, 8, 107801, 102, 1, 0, "みんなで遊んで\n美味しいもの食べて\nほんっとに楽しいっ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2421, 1, 1, 107801, 1, 2, 0, "マジであっついわね…\nよっし！　海に入って\n涼みましょ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2422, 1, 1, 107801, 2, 2, 0, "シャワー浴びたばかり\nなのに…また、遊び\nたくなってきたわ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2423, 1, 1, 107801, 3, 2, 0, "遊び足りないって？\nしょうがないわね…\n付き合ってあげるわ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2424, 1, 1, 107801, 4, 2, 0, "一応伝えておくけど\n猫って水が苦手なの！\nわかった！？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2425, 1, 4, 107801, 101, 2, 0, "あたしと話したいの？\nふふ。\nまぁ、いいわよ。\nほら隣に座りなさい", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2426, 1, 8, 107801, 102, 2, 0, "こんなに楽しいの\n初めてかも…\nずっと続けばいいのに", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2427, 1, 1, 107801, 1, 2, 0, "は、早起きしたのは、\n楽しみで眠れなかった\nからじゃないわよ！", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2428, 1, 1, 107801, 2, 2, 0, "昼食後のストレッチ\n終わり！　さあ、\nビーチが待ってるわ！", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2429, 1, 1, 107801, 3, 2, 0, "夜の海に散歩に\n行こうかな～\nあんたも来なさいよ☆", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2431, 1, 1, 107801, 2, 3, 0, "海ではしゃぎすぎて\n疲れたわ…ちょっと\n休もうかしら？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2432, 1, 1, 107801, 3, 3, 0, "こんな魔物がいたら\n操るのも楽しいかも", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2433, 1, 1, 107801, 4, 3, 0, "にゃう～♪\nまるで南国にいる\n気分ね♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2434, 1, 1, 107801, 5, 3, 0, "あったかぁい♪\n身体が冷えてきたから\n丁度いいわ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2436, 1, 1, 107801, 7, 3, 0, "運動後のご飯って\nなんでこんなに\n美味しいんだろ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2437, 1, 1, 107801, 8, 3, 0, "どんなカナヅチでも、\n絶対必ず泳げる方法、\n書いてないかしら？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2439, 1, 1, 107801, 10, 3, 0, "あんたもあたしが\n操ってあげる！\nなんてね～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2440, 1, 1, 107801, 11, 3, 0, "リゾートリゾートっ\nのんびりしましょ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2441, 1, 1, 108001, 1, 1, 0, "ここなら思う存分\nデスクワークに\n励めるわね！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2442, 1, 1, 108001, 2, 1, 0, "武器のメンテナンスも\nしっかりしないと\n最高効率は望めないわ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2443, 1, 1, 108001, 3, 1, 0, "準備をしっかり整えて\nもっともっと\n稼がなくちゃね！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2444, 1, 1, 108001, 4, 1, 0, "少し休んだら\n次のバイトに\n行ってくるわね！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2445, 1, 1, 108001, 5, 1, 0, "この格好でここに\nいるのって\n気恥ずかしいわね…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2446, 1, 4, 108001, 101, 1, 0, "キミが望むなら\n今日のバイト\n休んでもいいわよ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2447, 1, 8, 108001, 102, 1, 0, "キミだけね…\n私のことをここまで\n理解してくれるのは", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2448, 1, 1, 108001, 1, 2, 0, "普段と違う格好\nだからって\nそんなに見ないでよ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2449, 1, 1, 108001, 2, 2, 0, "キミを立派な\nバイト戦士にするのが\n師匠としての役目よね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2450, 1, 1, 108001, 3, 2, 0, "そろそろ次のバイトに\n行かないと…\nキミも一緒に来る？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2451, 1, 1, 108001, 4, 2, 0, "効率は大事だけど\nゆっくりするのも\n悪くないわね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2452, 1, 4, 108001, 101, 2, 0, "一緒の時間が楽しくて\nバイトのことを\n忘れちゃいそうね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2453, 1, 8, 108001, 102, 2, 0, "キミと一緒なら\nどんなミッションも\n楽勝ね！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2454, 1, 1, 108001, 1, 2, 0, "おはよう、いい朝ね！\n今日もめいっぱい\n稼ぐわよ！", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2455, 1, 1, 108001, 2, 2, 0, "もうお昼だったのね…\n内職のバイトに\n集中しすぎちゃったわ", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2456, 1, 1, 108001, 3, 2, 0, "スケジュールは、と…\nこういう管理の徹底が\n効率を生み出すのよ♪", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2458, 1, 1, 108001, 2, 3, 0, "しっかり働いて休む…\n最高効率にまた一歩\n近づいたわね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2459, 1, 1, 108001, 3, 3, 0, "なごんじゃうわね…\nよぉし、次のバイトも\n頑張らないと！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2460, 1, 1, 108001, 4, 3, 0, "いい香りね…\n働く意欲が\n湧いてくるわ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2461, 1, 1, 108001, 5, 3, 0, "身体が冷えたから\n助かるわ…冷えは\n女の天敵だもの", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2463, 1, 1, 108001, 7, 3, 0, "海のバイトは\n体力勝負よ\nエネルギーはしっかり\n摂取しないとね！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2464, 1, 1, 108001, 8, 3, 0, "座学も大切ね\nもっと知識を\n身につけないと…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2466, 1, 1, 108001, 10, 3, 0, "海の楽園みたいね…\n今だけは効率を\n忘れちゃいそう…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2467, 1, 1, 108001, 11, 3, 0, "落ち着くわ…\n海とひとつになった\n気分ね！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2468, 1, 1, 107901, 1, 1, 0, "ちょっと休憩にゃ～\n潮風は体に\n良くないにゃ～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2469, 1, 1, 107901, 2, 1, 0, "猫グッズで\n海の家を飾りたいにゃ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2470, 1, 1, 107901, 3, 1, 0, "海の幸は美味しいけど\n水はイヤだにゃ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2471, 1, 1, 107901, 4, 1, 0, "開店にゃ！\nこのたい焼きなら\nきっと大繁盛にゃ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2472, 1, 1, 107901, 5, 1, 0, "海と言えば水着にゃ\n似合ってるかにゃ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2473, 1, 4, 107901, 101, 1, 0, "キミが頼むなら\n手伝ってあげても\nいいにゃ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2474, 1, 8, 107901, 102, 1, 0, "あたしの宝は\nここにあるにゃ～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2475, 1, 1, 107901, 1, 2, 0, "にゃ？\nそんなに見つめても\n何も出ないにゃ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2476, 1, 1, 107901, 2, 2, 0, "今からキミを\nあたしの相棒に\n任命するにゃ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2477, 1, 1, 107901, 3, 2, 0, "これから\n開店の準備にゃ\nキミも手伝って\nくれるのかにゃ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2478, 1, 1, 107901, 4, 2, 0, "海の家で出す\n新しいメニューを\n考え中なのにゃ～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2479, 1, 4, 107901, 101, 2, 0, "キミと一緒なら\n売上げナンバーワンに\nなれるにゃ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2480, 1, 8, 107901, 102, 2, 0, "キミと一緒にいると\n楽しくて、もっと\nいたくなるにゃ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2481, 1, 1, 107901, 1, 2, 0, "おはようにゃ！\n今日も頑張って\nいくにゃー！", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2482, 1, 1, 107901, 2, 2, 0, "お腹すいたにゃ～…\nお魚、ちょっとだけ、\nつまみ食いするにゃ", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2483, 1, 1, 107901, 3, 2, 0, "明日の準備は万端にゃ\nいつでも\n出発できるにゃ", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2485, 1, 1, 107901, 2, 3, 0, "このふかふか具合\nまるで子猫の\n毛並みのようにゃ～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2486, 1, 1, 107901, 3, 3, 0, "にゃにゃ～\nなんてかわいさにゃ～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2487, 1, 1, 107901, 4, 3, 0, "この匂い…\nもう少し香ばしいと\nいいかもしれないにゃ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2488, 1, 1, 107901, 5, 3, 0, "にゃ～…\nあったかくて\nぬくぬくだにゃ～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2490, 1, 1, 107901, 7, 3, 0, "働くにはまず体力にゃ\nしっかり食べて\nスタミナアップ\nだにゃ!", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2491, 1, 1, 107901, 8, 3, 0, "ふむふむ…\nここにお宝が\n眠ってるんだにゃ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2493, 1, 1, 107901, 10, 3, 0, "シードレイクにゃ！\n本物は怖かったけど、\nぬいぐるみなら\nかわいいにゃ～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2494, 1, 1, 107901, 11, 3, 0, "貝なのにふかふかの\nクッションにゃ！\n屋台の前にも\n置いてみたいにゃあ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2495, 1, 1, 103701, 1, 1, 0, "人の家って、道場とは\n違った香りがするね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2496, 1, 1, 103701, 2, 1, 0, "ちょっと退屈だね…\nあの人をからかいに\n行こうかな♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2497, 1, 1, 103701, 3, 1, 0, "普通の女の子みたいに\n遊びたいって思う\nこともあるんだよね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2498, 1, 1, 103701, 4, 1, 0, "剣の手入れを怠らない\nようにしないと。\nいざという時、困るし", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2499, 1, 1, 103701, 5, 1, 0, "もっと\n上手く立ち回って\n出世したいな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2500, 1, 4, 103701, 101, 1, 0, "おかえりなさい！\nふふ、なんだか奥さん\nになった気分だよ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2501, 1, 8, 103701, 102, 1, 0, "恥ずかしくて、あなた\nを見られないよ…\n私らしくないなぁ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2502, 1, 1, 103701, 1, 2, 0, "私、お話好きなんだ。\nどうせなら楽しい話を\nしたいな♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2503, 1, 1, 103701, 2, 2, 0, "女の子っぽい子と、\n男勝りな子、あなたは\nどっちが好き？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2504, 1, 1, 103701, 3, 2, 0, "そろそろ見回りの\n時間かな。あなたも\n一緒に行く？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2505, 1, 1, 103701, 4, 2, 0, "構って欲しいのかな？\nそれじゃ一緒に\nランニングに行こうか", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2506, 1, 4, 103701, 101, 2, 0, "二人で出かけるなら、\n手を繋がないとね。\nあはは、冗談冗談♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2507, 1, 8, 103701, 102, 2, 0, "ねえ、あなた♪\nおや、顔が真っ赤だ！\n…ふふ、私も、かな？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2508, 1, 1, 103701, 1, 2, 0, "私は朝から鍛錬だよ。\n慣れてるけど大変だ。\nお互いに頑張ろうね", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2509, 1, 1, 103701, 2, 2, 0, "鍛錬でお腹ぺこぺこ。\nお昼ごはんが\n楽しみだね", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2510, 1, 1, 103701, 3, 2, 0, "今日も一日見回りと\n鍛錬を頑張ったよ。\nあなたもお疲れさま", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2512, 1, 1, 103701, 2, 3, 0, "いざという時のため\nにも、休憩は大事だね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2513, 1, 1, 103701, 3, 3, 0, "こういうものには\n今まで\n縁がなかったけど。\nうん、可愛いね♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2514, 1, 1, 103701, 4, 3, 0, "精神統一に通じる\nものがあるね。\nふぅ、癒されるなぁ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2515, 1, 1, 103701, 5, 3, 0, "あったかいなぁ。\nたまにはのんびり\nしようか", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2517, 1, 1, 103701, 7, 3, 0, "鍛錬後はすごくお腹が\n空くんだよね。\nう～ん、おいしい♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2518, 1, 1, 103701, 8, 3, 0, "剣術書だけで強くなる\nには飛びぬけた才能と\n努力が必要だよね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2520, 1, 1, 100501, 1, 1, 0, "正義の刃で\n悪を挫く！\n…キマったッス♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2521, 1, 1, 100501, 2, 1, 0, "ヒーローの\n秘密特訓を\n始めるッスよ～！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2522, 1, 1, 100501, 3, 1, 0, "正義の味方目指して\n今日も明日も\n全力ッスよ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2523, 1, 1, 100501, 4, 1, 0, "一日一善！\nヒーローだったら\n当たり前ッス♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2524, 1, 1, 100501, 5, 1, 0, "どうッスか\nこのポーズ！\nイイ感じッスか？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2525, 1, 4, 100501, 101, 1, 0, "た、たまには\nよく頑張ったねって\n褒めてほしいッス…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2526, 1, 8, 100501, 102, 1, 0, "自分を信じてくれる\nあんたと一緒なら\n何でもやれるッス！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2527, 1, 1, 100501, 1, 2, 0, "おつかれッス♪\n冒険の準備は\nばっちりッスよ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2528, 1, 1, 100501, 2, 2, 0, "どうしたんッスか？\n…はっ！　もしや敵が\n迫ってるんスかっ！？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2529, 1, 1, 100501, 3, 2, 0, "べ、別に\n頭を撫でてほしいとか\n思ってないッスよ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2530, 1, 1, 100501, 4, 2, 0, "スーパーヒーロー\nトラタイガーは\n絶対無敵ッス！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2531, 1, 4, 100501, 101, 2, 0, "あんたと自分で\n正義の心を燃やして\n悪を一網打尽ッス！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2532, 1, 8, 100501, 102, 2, 0, "あんたと一緒だと\n幸せな気持ちに\nなるんッス…♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2533, 1, 1, 100501, 1, 2, 0, "お、早いッスね～♪\n今日も一日一善\nよろしくッス！", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2534, 1, 1, 100501, 2, 2, 0, "一休みしながら\nヒーローについて\n語り合うッスよ～！", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2535, 1, 1, 100501, 3, 2, 0, "夜のパトロールで\n一緒に平和を\n守るッス！", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2537, 1, 1, 100501, 2, 3, 0, "ふぅぅ…\n落ち着くッスね\n戦士の休息ッス♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2538, 1, 1, 100501, 3, 3, 0, "くぅぅ、可愛いッス…\nヒーローが守るべき\n愛らしさッスね！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2539, 1, 1, 100501, 4, 3, 0, "なんスかこの香り？\nすぅぅ…ふぅぅ…\n力がみなぎるッス！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2540, 1, 1, 100501, 5, 3, 0, "優しい光ッスね…\nヒーローの優しさ\nそのものッス！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2542, 1, 1, 100501, 7, 3, 0, "あむあむあむ…\nんぅう、元気が溢れて\n止まらないッス！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2543, 1, 1, 100501, 8, 3, 0, "トラタイガーのお話は\n笑いあり涙ありで\n超おすすめッス！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2545, 1, 1, 108101, 1, 1, 0, "ハッピーハロウィン\nお父さん、お菓子は…\n食べられないね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2546, 1, 1, 108101, 2, 1, 0, "トリックオアトリート\nお菓子をくれないと\n不吉な占いをします", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2547, 1, 1, 108101, 3, 1, 0, "ふふ、あなたの後ろで\n霊のみなさんが\n踊り始めました", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2548, 1, 1, 108101, 4, 1, 0, "霊感がいつも以上に\n冴えているので\n占いの調子も良好です", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2549, 1, 1, 108101, 5, 1, 0, "父はハロウィンが\n大好きだそうです\n大胆な格好をする子が\n多いので…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2550, 1, 4, 108101, 101, 1, 0, "あなたは霊たちにも\n大人気ですよね\n私も見てほしいな…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2551, 1, 8, 108101, 102, 1, 0, "ハロウィンの賑やかさ\n今は好きです…\nあなたといられるので", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2552, 1, 1, 108101, 1, 2, 0, "霊のみなさんとの\nダンスパーティー…\n楽しみです", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2553, 1, 1, 108101, 2, 2, 0, "お父さん、元気だね\nえ？　ここは色んな\n女の子に会えて幸せ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2554, 1, 1, 108101, 3, 2, 0, "ハロウィンのお菓子\n食べすぎちゃったな…\nでも、美味しかった…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2555, 1, 1, 108101, 4, 2, 0, "あなたの女難の相…\n悪化していますね\nお祓いが必要かも", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2556, 1, 4, 108101, 101, 2, 0, "あの、霊のみなさんが\nあなたと踊りたいと…\nえっと、私も…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2557, 1, 8, 108101, 102, 2, 0, "あなたと手を繋ぐと\n胸が苦しくなります\nでも、繋ぎたいな…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2558, 1, 1, 108101, 1, 2, 0, "ハッピーハロウィン…\n父が元気なので\n今日の運気は良好です", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2559, 1, 1, 108101, 2, 2, 0, "父はお昼寝中です\n本当に寝ているかは\n私にも分かりませんが", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2560, 1, 1, 108101, 3, 2, 0, "お父さん、\nもう眠っちゃったの？\n…眠れるんだったっけ", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2562, 1, 1, 108101, 2, 3, 0, "落ち着きますね…\n霊のみなさんも\n喜んでいます", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2563, 1, 1, 108101, 3, 3, 0, "つん、つん。ふふっ♪\n癒やされます…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2564, 1, 1, 108101, 4, 3, 0, "精神が研ぎ澄まされる\n香りですね…\n占いが捗ります", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2565, 1, 1, 108101, 5, 3, 0, "あったかいね\nお父さん…\nえ？　分からない？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2567, 1, 1, 108101, 7, 3, 0, "霊のみなさんは\n食べられないので…\n私がいただきます", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2568, 1, 1, 108101, 8, 3, 0, "お父さんを元に戻す\n手掛かり…\n書かれていないかな？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2570, 1, 1, 108101, 12, 3, 0, "わぁ、お菓子が沢山…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2572, 1, 1, 108201, 1, 1, 0, "無料でプリンが\nもらえるなんて、\nハロウィンはすごいの", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2573, 1, 1, 108201, 2, 1, 0, "生きてるときも、\nミヤコはハロウィンが\n好きだった気がするの", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2574, 1, 1, 108201, 3, 1, 0, "みんなに仮装を\n見せつけて、\nプリンをもらうの～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2575, 1, 1, 108201, 4, 1, 0, "この仮装で、\nみんなに恐怖を\n教えてやるの！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2576, 1, 1, 108201, 5, 1, 0, "アオーン、アオーン！\nミヤコ、狼女に\nなりきってるの～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2577, 1, 4, 108201, 101, 1, 0, "アイツに秘密で、\nパーティーの\n飾り付けをするの♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2578, 1, 8, 108201, 102, 1, 0, "アイツが狼男の\n仮装をすれば、\nおそろいなの～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2579, 1, 1, 108201, 1, 2, 0, "プリンをくれなきゃ、\nオマエをカボチャに\nしてやるの～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2580, 1, 1, 108201, 2, 2, 0, "ハロウィンは幽霊でも\n参加できるから\n楽しいの～！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2581, 1, 1, 108201, 3, 2, 0, "プリンがないなら\nカボチャプリンを\n食べればいいの！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2582, 1, 1, 108201, 4, 2, 0, "プリンの可能性は\n無限大なの～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2583, 1, 4, 108201, 101, 2, 0, "オマエの隣で\nプリンを食べると、\nいつもより美味しいの", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2584, 1, 8, 108201, 102, 2, 0, "オマエが死んでも、\nミヤコはずーっと\n一緒にいてやるの～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2585, 1, 1, 108201, 1, 2, 0, "朝ごはんは\nカボチャプリンなの！\nハロウィンっぽいの～", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2586, 1, 1, 108201, 2, 2, 0, "ランチは豪華に\nプリンアラモードなの", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2587, 1, 1, 108201, 3, 2, 0, "夜は幽霊たちと\nハロウィンするの！\nオマエも来るの！", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2589, 1, 1, 108201, 2, 3, 0, "幽霊にも休息は\n必要なの！\nプリンもってこいなの", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2590, 1, 1, 108201, 3, 3, 0, "オマエもミヤコの\nパーティーに\n招待してあげるの～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2591, 1, 1, 108201, 4, 3, 0, "癒されるの～\nまるで天国なの～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2592, 1, 1, 108201, 5, 3, 0, "ここでなら、\n焼きプリンが\n作れるの！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2594, 1, 1, 108201, 7, 3, 0, "腹が減っては\nハロウィンは\nできぬなの！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2595, 1, 1, 108201, 8, 3, 0, "丸ごとカボチャ\nプリン特集！？\n美味しそうなの～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2597, 1, 1, 108201, 12, 3, 0, "お菓子がたくさんで、\nミヤコはとっても\nハッピーなの♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2599, 1, 1, 108301, 1, 1, 0, "魔女以外にも、\n色々な仮装を\nしてみたいわね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2600, 1, 1, 108301, 2, 1, 0, "チョコやアメよりも、\nアタシに相応しい\nスイーツがあるはず…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2601, 1, 1, 108301, 3, 1, 0, "お菓子の食べ過ぎは、\nお母さんに\n怒られるかも…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2602, 1, 1, 108301, 4, 1, 0, "この格好で学院に\n行ったら、\nみんな驚くかしら", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2603, 1, 1, 108301, 5, 1, 0, "大人のレディは、\nどういうハロウィンを\n過ごすべきかしら…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2604, 1, 4, 108301, 101, 1, 0, "お菓子、アイツの分も\nとっといてあげよ！\n大人の余裕ってやつね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2605, 1, 8, 108301, 102, 1, 0, "アイツを魅了する\n魔法とかないかしら？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2606, 1, 1, 108301, 1, 2, 0, "おしゃれスイーツを\nくれなきゃ\nイタズラするわよ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2607, 1, 1, 108301, 2, 2, 0, "アタシの大人センスで\nアンタに似合う仮装を\n選んであげるわ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2608, 1, 1, 108301, 3, 2, 0, "不気味よねぇ…\nジャックオーランタン\nこ、怖くないわよ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2609, 1, 1, 108301, 4, 2, 0, "ハロウィンが楽しくて\n宿題やり忘れてたわ…\nて、手伝って～！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2610, 1, 4, 108301, 101, 2, 0, "お菓子をもらうのも\nアンタと一緒なら\n恥ずかしくないわ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2611, 1, 8, 108301, 102, 2, 0, "アタシのエスコートを\n任せられるのは、\nアンタだけなんだから", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2612, 1, 1, 108301, 1, 2, 0, "朝食はオシャレに\nグリーンスムージー！\n…に、にがぁ", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2613, 1, 1, 108301, 2, 2, 0, "昼下がりはカフェで\n優雅なティータイム\n…憧れるわ～", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2614, 1, 1, 108301, 3, 2, 0, "ハロウィンナイトの\nはじまりよ！\n街に繰り出しましょ！", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2616, 1, 1, 108301, 2, 3, 0, "あう～、お菓子の\n食べすぎで\n動けな～い", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2617, 1, 1, 108301, 3, 3, 0, "アンタも仮装する？\nいまよりもーっと\nかわいくなるわよ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2618, 1, 1, 108301, 4, 3, 0, "大人な香りに\n包まれてる～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2619, 1, 1, 108301, 5, 3, 0, "ポカポカね～♪\n…ああー！\nチョコが溶けてる！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2621, 1, 1, 108301, 7, 3, 0, "おいしい～♪\n…じゃなくて！\nび、美味ですわ～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2622, 1, 1, 108301, 8, 3, 0, "この服かわい～♪\nって、なにこの値段！\nお小遣い何年分よ！？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2624, 1, 1, 108301, 12, 3, 0, "お、お菓子なんかで\n喜ぶほど、子供じゃ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2626, 1, 1, 107101, 1, 1, 0, "次はどんな策で団長を\nからかってみようか…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2627, 1, 1, 107101, 2, 1, 0, "む？\n血の臭いがするな\n祭りの時間か☆", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2628, 1, 1, 107101, 3, 1, 0, "平和に飼い殺される\n前に、混沌の愛を\n育みたいね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2629, 1, 1, 107101, 4, 1, 0, "本気を出した団長と\nいつか手加減なしに\n殺り合いたいな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2630, 1, 1, 107101, 5, 1, 0, "おばさん呼ばわりする\n新米騎士の小娘を\n懲らしめてやろうか", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2631, 1, 4, 107101, 101, 1, 0, "胸を触られるまで\n誓約のことなど\nすっかり忘れていたな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2632, 1, 8, 107101, 102, 1, 0, "まさかこのワタシが\nプロポーズするとはな\n…笑えるじゃないか", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2633, 1, 1, 107101, 1, 2, 0, "面白い暇つぶしを\n思いついたんだ\n戯れに付き合え☆", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2634, 1, 1, 107101, 2, 2, 0, "退屈で死にそうだ\n愉しい話題を\nワタシに提供してくれ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2635, 1, 1, 107101, 3, 2, 0, "なんだ、暇なのか？\n心躍ることでも探しに\n出かけようではないか", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2636, 1, 1, 107101, 4, 2, 0, "このワタシを愉快に\nさせてくれる事でも\nあったのか？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2637, 1, 4, 107101, 101, 2, 0, "ワタシに求婚した男の\n数だと？\nいちいち覚えてないな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2638, 1, 8, 107101, 102, 2, 0, "最強無敵の女と\n結婚できるんだ\n誇りに思えよ☆", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2639, 1, 1, 107101, 1, 2, 0, "なにか愉快なことが\n起こりそうな朝だ☆\nワクワクするね", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2640, 1, 1, 107101, 2, 2, 0, "うーむ、腹がすいたな\n愉快なことで\nこの空腹を満たしたい", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2641, 1, 1, 107101, 3, 2, 0, "これからは大人の\n時間だ。愉しみ方を\n知りたいのかい？", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2643, 1, 1, 107101, 2, 3, 0, "このワタシとした事が\n穏やかな気分に\nなってしまうとは…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2644, 1, 1, 107101, 3, 3, 0, "可愛い姿をしたものが\n惨たらしい\n醜態を晒すのを\n想像してしまう", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2645, 1, 1, 107101, 4, 3, 0, "悪くはない香りだ\nだが、ワタシは血の\n臭いを嗅ぎたいぞ☆", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2646, 1, 1, 107101, 5, 3, 0, "ぬるい、ぬるすぎる！\n地獄の業火には\nほど遠い熱さだ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2648, 1, 1, 107101, 7, 3, 0, "ワタシの腕前に\n勝るとも劣らない\nなかなかの味だ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2649, 1, 1, 107101, 8, 3, 0, "意中の男性を絶対に\n落とす方法…？\nく、下らんっ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2651, 1, 1, 105601, 1, 1, 0, "明日はあの釣り場に\n行くとしようかな\nあの子も誘ってみるか", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2652, 1, 1, 105601, 2, 1, 0, "ぷはーっ！\n体を動かしたあとの\n一杯は格別だね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2653, 1, 1, 105601, 3, 1, 0, "天気が良いと\n昼寝か釣りかで\n迷っちまうな…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2654, 1, 1, 105601, 4, 1, 0, "大物が釣れたときの\n快感は、他じゃ\n味わえないね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2655, 1, 1, 105601, 5, 1, 0, "ふあぁ～…\n昼寝でもするか", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2656, 1, 4, 105601, 101, 1, 0, "ふふっ…\nあの子、見た目より\n根性あるんだね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2657, 1, 8, 105601, 102, 1, 0, "あの子が強くなるには\nどんな魚を食べさせて\nあげればいいのやら…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2658, 1, 1, 105601, 1, 2, 0, "姉御って呼ぶんじゃ\nないよ。からかうと\nタダじゃおかないよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2659, 1, 1, 105601, 2, 2, 0, "釣りのコツ？\n何事も経験が大事だ\n今から行くかい？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2660, 1, 1, 105601, 3, 2, 0, "どうしたのさ？\n助太刀が必要かい？\nアタシにまかせときな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2661, 1, 1, 105601, 4, 2, 0, "困ってる人がいたら\nまた力を貸しておくれ\n頼りにしてるよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2662, 1, 4, 105601, 101, 2, 0, "なにか困りごとかい？\nどうやら昼寝は\nまた今度だね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2663, 1, 8, 105601, 102, 2, 0, "よしよし！\nお人好しのお前さんを\n労ってあげるよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2664, 1, 1, 105601, 1, 2, 0, "おはよう、お前さん\n魚屋は早起きでなきゃ\nつとまらないよ？", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2665, 1, 1, 105601, 2, 2, 0, "昼飯かい？　今朝\n仕入れた魚があるんだ\n食べてみるかい？", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2666, 1, 1, 105601, 3, 2, 0, "夜釣りにぴったりの\n月が出てるよ\nいい釣りが出来そうだ", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2668, 1, 1, 105601, 2, 3, 0, "コイツに座って\nのんびり釣りが\nできりゃいいのにな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2669, 1, 1, 105601, 3, 3, 0, "よしよし…♪\nおっと、かわいい物も\nつい抱きしめちまうな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2670, 1, 1, 105601, 4, 3, 0, "これで魚の臭いを\n消せれば、猫も\n寄らなくなるのかね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2671, 1, 1, 105601, 5, 3, 0, "冬場の釣りも\nこれくらい暖かけりゃ\nいいんだけどね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2673, 1, 1, 105601, 7, 3, 0, "腹が減っては\n釣りもできぬ、ってな\nまずは腹ごしらえだ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2674, 1, 1, 105601, 8, 3, 0, "へぇ…こんな魚が\nいたとは…いつか\n釣りたいもんだね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2676, 1, 1, 101301, 1, 1, 0, "流石に室内で\n魔法ぶっぱしたら\n怒られるよねぇ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2677, 1, 1, 101301, 2, 1, 0, "可愛い女の子が\nたくさんいる！\nここは楽園か！？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2678, 1, 1, 101301, 3, 1, 0, "みんなに漫画とか\n勧めてみようかな！\n布教用のがあるし！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2679, 1, 1, 101301, 4, 1, 0, "わ、私一人の\n部屋じゃないから\n色々自重せねば！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2680, 1, 1, 101301, 5, 1, 0, "「ぬるぽ」\n…くっ、\n誰も反応なしか！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2681, 1, 4, 101301, 101, 1, 0, "次はどの漫画を\n貸そうかなー♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2682, 1, 8, 101301, 102, 1, 0, "次の定期巡回で\nとっておきの店に\n連れてってあげよう！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2683, 1, 1, 101301, 1, 2, 0, "君に似合いそうな\n衣装を買ったよ！\n君もいざ魔法少女に！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2684, 1, 1, 101301, 2, 2, 0, "さあ！\n暇なら定期巡回に\nレッツラゴー！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2685, 1, 1, 101301, 3, 2, 0, "戦闘だけじゃなくて、\n日常にも役立つ\n魔法を習得したいね！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2686, 1, 1, 101301, 4, 2, 0, "新魔法思いついた！\n私って天才！？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2687, 1, 4, 101301, 101, 2, 0, "決め台詞は超大事！\n君の決め台詞も\n私が考えてあげよう！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2688, 1, 8, 101301, 102, 2, 0, "私ってもしやリア充？\n…そんなバカな！\n非リア代表のはずが！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2689, 1, 1, 101301, 1, 2, 0, "おはよー…\n徹夜で本読んでたから\nまだ眠い～", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2690, 1, 1, 101301, 2, 2, 0, "昼飯食べて、\n午後の定期巡回も\n頑張りましょー！", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2691, 1, 1, 101301, 3, 2, 0, "夜からが、\nオタ活本番でしょ！", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2693, 1, 1, 101301, 2, 3, 0, "ふいー！\nくつろいで本を読む！\n最高だねー！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2694, 1, 1, 101301, 3, 3, 0, "君、可愛いね～♪\n魔法少女の\n使い魔みたい！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2695, 1, 1, 101301, 4, 3, 0, "良い匂いに\n釣られてしまった…\nまさか、孔明の罠！？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2696, 1, 1, 101301, 5, 3, 0, "暖かいけど、\nここで本を読むのは\nちょっち危ないかな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2698, 1, 1, 101301, 7, 3, 0, "ウマ―！\nこれの詳細きぼんぬ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2699, 1, 1, 101301, 8, 3, 0, "こ、古代語の本！？\n現代語でおk…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2701, 1, 1, 190901, 1, 1, 0, "ふふふっ♪\n次はどんな悪戯を\nしてやろうかしら♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2702, 1, 1, 190901, 2, 1, 0, "幻影湖の霧を吸った\n人間たちの\nリアクションって、\nサイコーよねぇ～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2703, 1, 1, 190901, 3, 1, 0, "室内だけとはいえ…\n自由に動けるって、\nやっぱりいいわ～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2704, 1, 1, 190901, 4, 1, 0, "改めて見ると良い\n部屋ね…悪戯とか\nしやすそうだしぃ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2705, 1, 1, 190901, 5, 1, 0, "私を封じていた部屋\nなのに…急に居心地が\n良くなったわね♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2706, 1, 1, 190901, 1, 2, 0, "あんたの頭、座り心地\n良いわね…私専用の\n椅子にしてあげる♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2707, 1, 1, 190901, 2, 2, 0, "外は大変みたいねぇ…\nまあ、私は好き勝手に\nやらせてもらうわ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2708, 1, 1, 190901, 3, 2, 0, "顔に落書された？\n誰か知らないけど、\n悪いことする奴も\nいるものねぇ～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2709, 1, 1, 190901, 4, 2, 0, "フィオのやつ…\nどこをウロチョロ\nしてるのかしら？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2710, 1, 1, 190901, 1, 2, 0, "人間の朝は大変ねぇ…\n私はその様を見ながら\nゆっくりするわね♪", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2711, 1, 1, 190901, 2, 2, 0, "う～私も、お日様を\n浴びながら、お外を\nお散歩したーい！", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2712, 1, 1, 190901, 3, 2, 0, "この暗さ…閉じ込め\nられていた時を\n思い出すわね…", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2714, 1, 1, 190901, 2, 3, 0, "悪戯しすぎて\n疲れたから、ちょっと\n休憩しよっと♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2715, 1, 1, 190901, 3, 3, 0, "ガイド妖精たる私が、\nこんな造形物に\n惹かれる…わけが……\nかわいい♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2716, 1, 1, 190901, 4, 3, 0, "この香りを囮にして、\n周りに悪戯を仕込む…\n完璧ね♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2717, 1, 1, 190901, 5, 3, 0, "不思議ねぇ…私は\n温度とか関係ないのに\n離れられないわ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2719, 1, 1, 190901, 7, 3, 0, "食事の必要がない体…\nつまり、食べたければ\n食べていいのよね～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2720, 1, 1, 190901, 8, 3, 0, "へぇ…最近はこんな\n悪戯もあるのねぇ…\nあいつで試さないと♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2722, 1, 1, 108401, 1, 1, 0, "メリークリスマス！\n歌で幸せを\n届けます…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2723, 1, 1, 108401, 2, 1, 0, "あなたと出会えたのは\n偶然ではなくて…\nクリスマスの奇跡だと\n思うんです", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2724, 1, 1, 108401, 3, 1, 0, "クリスマスは華やかで\nつい子供のように\nはしゃいでしまいます", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2725, 1, 1, 108401, 4, 1, 0, "曲が書けるのは\nあなたが側に\nいてくれるからです…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2726, 1, 1, 108401, 5, 1, 0, "感動を与えられる歌…\nあなたと一緒になら\n生み出せるはず…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2727, 1, 4, 108401, 101, 1, 0, "クリスマスの幸せを\nあなたと\n感じ合えたらな…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2728, 1, 8, 108401, 102, 1, 0, "あなたの幸せを願った\nクリスマスソング…\nいつか披露しますね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2729, 1, 1, 108401, 1, 2, 0, "みなさんの笑顔を\n守りたいから…\n私は歌い続けます", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2730, 1, 1, 108401, 2, 2, 0, "歌を届ける\nサンタさんに\nなりたいな…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2731, 1, 1, 108401, 3, 2, 0, "お暇でしたら\nクリスマスのお祝いを\nしませんか？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2732, 1, 1, 108401, 4, 2, 0, "ステージに立つ前に\n喉を整えないと…\n発声練習は念入りに…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2733, 1, 4, 108401, 101, 2, 0, "まわりはかわいい子\nばっかりだけど…\nこの服なら\nアピールになるかな？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2734, 1, 8, 108401, 102, 2, 0, "歌でならいくらでも\nあの人への気持ちを\n込められるのに…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2735, 1, 1, 108401, 1, 2, 0, "今日も一日\n幸せな歌で世界が\n包まれますように…", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2736, 1, 1, 108401, 2, 2, 0, "お茶を飲みながら\n好きな音楽を\n語り合いませんか？", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2737, 1, 1, 108401, 3, 2, 0, "ふふ、寝かしつけの\n子守唄を\nお望みですか？", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2739, 1, 1, 108401, 2, 3, 0, "ふかふかですね\n心地良すぎて\n眠ってしまいそう…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2740, 1, 1, 108401, 3, 3, 0, "素敵…\n優しい歌みたいに\n気持ちが落ち着く…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2741, 1, 1, 108401, 4, 3, 0, "インスピレーションが\n溢れてくる…\nいい曲が書けそう…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2742, 1, 1, 108401, 5, 3, 0, "この光みたいに\n灯火になるような\n歌を歌いたい…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2744, 1, 1, 108401, 7, 3, 0, "喉の健康のためにも\n食事はしっかり\nとるようにしています", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2745, 1, 1, 108401, 8, 3, 0, "物語には\n曲作りのヒントが\n詰まっているんです", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2747, 1, 1, 108401, 14, 3, 0, "絵が飛び出してくる\nなんて…精霊が宿って\nいるのでしょうか？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2748, 1, 1, 108401, 15, 3, 0, "か、可愛い…♪\nちょっとだけ触っても\nいいですよね…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2749, 1, 1, 108501, 1, 1, 0, "クリスマスでも、\n演劇の練習は\nしっかりやらないと！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2750, 1, 1, 108501, 2, 1, 0, "あうぅ…\nクリスマスの準備が\n終わらないよぅ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2751, 1, 1, 108501, 3, 1, 0, "アヤネちゃんが\n摘み食いしないように\n見ておかないと…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2752, 1, 1, 108501, 4, 1, 0, "サンタさんみたいに、\n皆に笑顔で接する…\nうぅ…私には無理ぃ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2753, 1, 1, 108501, 5, 1, 0, "新しいお洋服～♪\nスズメお姉ちゃんに\nお礼をいわないと♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2754, 1, 4, 108501, 101, 1, 0, "お兄ちゃんの為に、\nプレゼントを\n用意しないと♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2755, 1, 8, 108501, 102, 1, 0, "クリスマス…\n恋人の日…ひゃわっ！\nわ、私ってば何を…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2756, 1, 1, 108501, 1, 2, 0, "メリー\nクリスマスです、\nお兄ちゃん♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2757, 1, 1, 108501, 2, 2, 0, "そ、そんなに\n見られると…\n恥ずかしいよぉ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2758, 1, 1, 108501, 3, 2, 0, "ひゃわぁっ！？\n急に触られると、\n驚くからやめてぇ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2759, 1, 1, 108501, 4, 2, 0, "あ…クリスマスの\n準備を手伝って\nくれるんですか？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2760, 1, 4, 108501, 101, 2, 0, "お兄ちゃんと\n過ごすクリスマス、\n楽しみですぅ…♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2761, 1, 8, 108501, 102, 2, 0, "クリスマスはその…\n二人で…あうぅ…\nなんでもないですぅ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2762, 1, 1, 108501, 1, 2, 0, "うう…パーティーが\n楽しみで、あんまり\n眠れなかったよぅ…", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2763, 1, 1, 108501, 2, 2, 0, "ケーキを食べられなく\nなっちゃうから、\nお昼は軽めにしないと", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2764, 1, 1, 108501, 3, 2, 0, "ちょっと眠いけど…\n今日は少しだけ\n夜更かししたいです♪", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2766, 1, 1, 108501, 2, 3, 0, "ふかふかぁ…\nクリスマスの準備中に\n眠くなっちゃいますぅ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2767, 1, 1, 108501, 3, 3, 0, "この子と一緒に\nおままごとしたいな♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2768, 1, 1, 108501, 4, 3, 0, "いい匂い…♪\nみんなで\nアロマパーティー\nしたいなぁ…♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2769, 1, 1, 108501, 5, 3, 0, "ぬっくぬく…♪\nもう、ここから\n離れたくないよぉ…♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2771, 1, 1, 108501, 7, 3, 0, "はむはむ…♪\nクリスマスのお料理\nより美味しいかも～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2772, 1, 1, 108501, 8, 3, 0, "すごい…\nクリスマスの\n演劇って、\nこんなにあるんだ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2774, 1, 1, 108501, 14, 3, 0, "すごい…まるで、\nクリスマスの演劇を\n観てるようですぅ…♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2775, 1, 1, 108501, 15, 3, 0, "とっても可愛いです♪\nお芝居の相手役に\nしたいなぁ…♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2776, 1, 1, 108601, 1, 1, 0, "甘ぁいケーキも、\nデッカイ七面鳥も、\nぜーんぶ私のだー♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2777, 1, 1, 108601, 2, 1, 0, "毎日が\nクリスマスだったら\nいいのにな～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2778, 1, 1, 108601, 3, 1, 0, "プレゼントはあるし、\n料理は美味しいし、\nクリスマス最高ー♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2779, 1, 1, 108601, 4, 1, 0, "ここでぷうきちの\nクリスマス一発芸♪\n『無茶ぶりするな！』", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2780, 1, 1, 108601, 5, 1, 0, "ぷうきちがいつもより\n重いかも…太ったの？\n『衣装の重さだよ！』", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2781, 1, 4, 108601, 101, 1, 0, "お兄ちゃんが\n一緒にいてくれると\nクリスマス楽しいね♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2782, 1, 8, 108601, 102, 1, 0, "お兄ちゃんに抱きつく\nと、なんだかドキドキ\nする…なんでかな？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2783, 1, 1, 108601, 1, 2, 0, "メリークリスマス♪\nプレゼントは、\nいつでも受付中だよ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2784, 1, 1, 108601, 2, 2, 0, "このクマさん耳\n可愛いでしょ♪\nぷうきちと\nお揃いなんだー♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2785, 1, 1, 108601, 3, 2, 0, "お兄ちゃん…私、\n甘ぁいケーキが\n食べたいなぁ…♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2786, 1, 1, 108601, 4, 2, 0, "クリスマスぷうきちは\nぬくぬく感が凄いよ！\nお兄ちゃんもさわる？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2787, 1, 4, 108601, 101, 2, 0, "えへへ…お兄ちゃんの\n手は、大きくて、\n暖かくて大好き～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2788, 1, 8, 108601, 102, 2, 0, "来年のクリスマスも、\nこうやって\nお兄ちゃんに\n甘えていたいな～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2789, 1, 1, 108601, 1, 2, 0, "あれぇ…\n私のケーキはぁ…？\n『ちゃんと起きろ』", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2790, 1, 1, 108601, 2, 2, 0, "お昼はデッカイ\n七面鳥が食べたーい♪\n『夜まで我慢しろ』", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2791, 1, 1, 108601, 3, 2, 0, "『もう寝る時間だぞ』\nサンタさんが来る\nまで起きてるぅ…", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2793, 1, 1, 108601, 2, 3, 0, "このふかふか具合…\nぷうきち！\n負けてられないよ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2794, 1, 1, 108601, 3, 3, 0, "この子、\nすっごくかわいい～♪\n『ライバル登場か！』", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2795, 1, 1, 108601, 4, 3, 0, "良い匂い…あっ！\nぷうきちにこの\n匂いを移せば…！\n『匂い袋にするな』", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2796, 1, 1, 108601, 5, 3, 0, "あったか～い♪\nえへへ、ぷうきちも\nあっためてあげる♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2798, 1, 1, 108601, 7, 3, 0, "ぷうきち！　これ、\nとってもおいしいよ♪\n『食べすぎるなよ』", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2799, 1, 1, 108601, 8, 3, 0, "お、大人の私なら、\nこれぐらい…読め…\n『無理するな…』", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2801, 1, 1, 108601, 14, 3, 0, "絵が飛び出す…\nぷうきちに絵本を\n入れれば、イタズラに\n使えるかも…♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2802, 1, 1, 108601, 15, 3, 0, "か、可愛いけど…\nぷうきちサンタの方が\n可愛いんだからぁ…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2803, 1, 1, 108901, 1, 1, 0, "着物も慣れると\n動きやすいものさ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2804, 1, 1, 108901, 2, 1, 0, "鼻緒が緩んでしまった\n下駄で修業は\n無理があったかな…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2805, 1, 1, 108901, 3, 1, 0, "小腹が空いた…\nユイが作った餅、\nまだ食べられるかな？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2806, 1, 1, 108901, 4, 1, 0, "服装も変わったし\nいつもと違う戦い方を\n想定しないと…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2807, 1, 1, 108901, 5, 1, 0, "…ヒヨリは動きすぎて\n着崩れしてないか\n心配だ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2808, 1, 4, 108901, 101, 1, 0, "これからもずっと\n仲間と、彼と\n一緒に…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2809, 1, 8, 108901, 102, 1, 0, "願いを叶えるために\nもっと自分を\n鍛えていかないと！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2810, 1, 1, 108901, 1, 2, 0, "頭の飾りがずれてた？\nそうか、直してくれて\nありがとう", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2811, 1, 1, 108901, 2, 2, 0, "着物がそんなに\n珍しい？\nキミも着てみる？\n…冗談だよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2812, 1, 1, 108901, 3, 2, 0, "肩掛けの触り心地が\n気に入った？\nならもっと触る？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2813, 1, 1, 108901, 4, 2, 0, "ああ、この刀？\n釣り仲間に見繕って\nもらったんだ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2814, 1, 4, 108901, 101, 2, 0, "キミといると\n気持ちが段々と\n安らいでいくよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2815, 1, 8, 108901, 102, 2, 0, "キミは私の行く先を\n照らしてくれる\n光のような存在だよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2816, 1, 1, 108901, 1, 2, 0, "おはよう\n朝の修行を始めるけど\nキミもどう？", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2817, 1, 1, 108901, 2, 2, 0, "この時間に軽く\n休憩をとるといい\n能率がよくなるよ", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2818, 1, 1, 108901, 3, 2, 0, "もう夜か…\n明日もまた頑張ろう\nそれじゃ、おやすみ", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2820, 1, 1, 108901, 2, 3, 0, "…疲れていたのかな\nもう少しで眠る\nところだった…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2821, 1, 1, 108901, 3, 3, 0, "ふふっ、正月衣装を\n着せてやるのも\nよさそうだ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2822, 1, 1, 108901, 4, 3, 0, "ふぅ…いい香りだ\n慌ただしい日々から\n心が解放される", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2823, 1, 1, 108901, 5, 3, 0, "この心地よさ…\n寝てしまわないよう\n気をつけないと", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2825, 1, 1, 108901, 7, 3, 0, "おいしいけど\n帯がきつくなる前に\n手を止めよう…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2826, 1, 1, 108901, 8, 3, 0, "外を出歩くのも\nいいけど、中で\n読書もありだな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2828, 1, 1, 108801, 1, 1, 0, "…騎士クンって\nおせち料理で\n何が好きなんだろう？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2829, 1, 1, 108801, 2, 1, 0, "着物でお掃除や\nお洗濯するの\n慣れてきた♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2830, 1, 1, 108801, 3, 1, 0, "今年は\nどんな福袋を\n買おうかな…♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2831, 1, 1, 108801, 4, 1, 0, "お餅に使う調味料\nみんなで違うから\n色々用意しなきゃ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2832, 1, 1, 108801, 5, 1, 0, "ほっぺがお餅みたいに\nぷにぷに…\n食べ過ぎちゃった…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2833, 1, 4, 108801, 101, 1, 0, "来年も、再来年も\nみんなで一緒に\n新年を迎えたいな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2834, 1, 8, 108801, 102, 1, 0, "お願いです…\nいつかこの気持ちが\n伝わりますように…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2835, 1, 1, 108801, 1, 2, 0, "髪飾りが落ちそう？\nじゃあ…騎士クン\nつけ直してくれる？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2836, 1, 1, 108801, 2, 2, 0, "着物が着たいの？\nじゃあ今度一緒に\n買いに…行く？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2837, 1, 1, 108801, 3, 2, 0, "あっ、騎士クン\nお雑煮のお餅\nいくつ入れたい？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2838, 1, 1, 108801, 4, 2, 0, "外、寒かったよね？\nはいこれ。熱い甘粥\n飲むと温まるよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2839, 1, 4, 108801, 101, 2, 0, "今年は騎士クンと…\nえっ？　騎士クン！？\nな、なんでもないよ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2840, 1, 8, 108801, 102, 2, 0, "騎士クンの願い事\nわたしが\n叶えてあげたいな…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2841, 1, 1, 108801, 1, 2, 0, "おはよう\n太陽の光に当たると\n元気が出るよ", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2842, 1, 1, 108801, 2, 2, 0, "お昼ごはん\n作らなきゃ…\n騎士クンも食べる？", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2843, 1, 1, 108801, 3, 2, 0, "一日が終わるのは\nあっという間だよね\n一年も同じなのかな？", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2845, 1, 1, 108801, 2, 3, 0, "ふぅ…\n帯も少し\nゆるめちゃおう…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2846, 1, 1, 108801, 3, 3, 0, "お正月用に\n着物を縫って\nあげたいな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2847, 1, 1, 108801, 4, 3, 0, "これでお肌が\n保湿出来たら\nいいのになぁ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2848, 1, 1, 108801, 5, 3, 0, "ん～…\nぬくぬくしてて\n動きたくないよ～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2850, 1, 1, 108801, 7, 3, 0, "…冬眠しないのに\n熊さんみたいに\n食べちゃいそう", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2851, 1, 1, 108801, 8, 3, 0, "年末年始は\n告白のチャンス…\nむ、無理無理！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2853, 1, 1, 108701, 1, 1, 0, "みんな忙しそう…\n待っててね、\nすぐ助けるから！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2854, 1, 1, 108701, 2, 1, 0, "ユイちゃんと\nレイさん…よし！\nどっちも手伝おう！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2855, 1, 1, 108701, 3, 1, 0, "あっ、着崩れてる！\nわわっ、どうしよ！\n帯が結べないよー！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2856, 1, 1, 108701, 4, 1, 0, "初詣楽しみー！\n出店はどこから\n行こうかな？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2857, 1, 1, 108701, 5, 1, 0, "お正月だし\nみんなで楽しく\n遊んじゃおー！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2858, 1, 4, 108701, 101, 1, 0, "うーん…\n騎士くんはお正月\n何してるんだろ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2859, 1, 8, 108701, 102, 1, 0, "ううぅー…\n騎士くんを見てると\nドキってなっちゃう…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2860, 1, 1, 108701, 1, 2, 0, "あたしから\nおいしそうな匂い？\nくんくん…ソースだ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2861, 1, 1, 108701, 2, 2, 0, "ヘアピンが…\n拾ってくれたんだ！\nありがとう騎士くん！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2862, 1, 1, 108701, 3, 2, 0, "髪飾りがかわいい？\n耳の近く触りたい？\n…優しくね？　", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2863, 1, 1, 108701, 4, 2, 0, "グローブで食べるの\n大変そう？　特訓して\n今はお箸も使えるよ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2864, 1, 4, 108701, 101, 2, 0, "えっ、ほっぺが赤い？\nそうかな…？\n気のせい気のせい！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2865, 1, 8, 108701, 102, 2, 0, "騎士くんといるとね…\nなんでもない！\nまだ内緒っ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2866, 1, 1, 108701, 1, 2, 0, "おはよー！\n今日も明るく元気に\nがんばろー！", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2867, 1, 1, 108701, 2, 2, 0, "お腹すいてない？\n一緒にご飯食べよ！\n何にしようかなー♪", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2868, 1, 1, 108701, 3, 2, 0, "今日もいっぱい\nがんばったー！\nいつも？　…そう？", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2870, 1, 1, 108701, 2, 3, 0, "休憩休憩♪\nちょっと休んで\nガンガン動くぞー！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2871, 1, 1, 108701, 3, 3, 0, "かわいい♪\nでも、壊しそうで\nちょっと怖い…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2872, 1, 1, 108701, 4, 3, 0, "はぁー♪\nいい香りー…\n気分スッキリ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2873, 1, 1, 108701, 5, 3, 0, "ほっこりしてて\nぬくぬく…\n丸まりたい…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2875, 1, 1, 108701, 7, 3, 0, "ど、どうしよう…！\n手が、止まんないよ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2876, 1, 1, 108701, 8, 3, 0, "へー…お年玉って\n昔はお餅だったんだ\n…今のほうがいいね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2878, 1, 1, 109001, 1, 1, 0, "甘いチョコの香りを\n漂わせて…ふふっ\nこれで準備万端ですわ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2879, 1, 1, 109001, 2, 1, 0, "ここを私のチョコで\n埋め尽くしますわ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2880, 1, 1, 109001, 3, 1, 0, "エプロンを着て\nあの方を待つ私…\nまるで…クスクス", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2881, 1, 1, 109001, 4, 1, 0, "キッチンはどこかしら\n今のうちに作って\nおきたいものが…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2882, 1, 1, 109001, 5, 1, 0, "あのお方を私のチョコ\nなしでは生きられない\n体に…ふふっ\n冗談ですわ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2883, 1, 4, 109001, 101, 1, 0, "このチョコは運命の味\n一口食べれば私の虜…\nそれも運命ですわ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2884, 1, 8, 109001, 102, 1, 0, "私ともあろうものが\nなんだかドキドキして\nきましたわ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2885, 1, 1, 109001, 1, 2, 0, "あなた様。私に\n隠しているものなど\nありませんよね…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2886, 1, 1, 109001, 2, 2, 0, "私の手作りチョコです\n味の方にも自信\nありますのよ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2887, 1, 1, 109001, 3, 2, 0, "少し苦いのは\nチョコがビターなだけ\nお気になさらず…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2888, 1, 1, 109001, 4, 2, 0, "解毒薬はどこに\nいれたかしら…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2889, 1, 4, 109001, 101, 2, 0, "運命で結ばれている\nあなた様と私…\nチョコも特別でないと\nいけませんわ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2890, 1, 8, 109001, 102, 2, 0, "チョコを食べた後は\nメインディッシュ…\nさぁ、私を召し上がれ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2891, 1, 1, 109001, 1, 2, 0, "目覚めのチョコですわ\nすっきり目が覚めます\nしばらく眠れなく\nなりますけど…", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2892, 1, 1, 109001, 2, 2, 0, "おやつのチョコですわ\nデザートにどうぞ\n空腹を感じなく\nなりますわ…クスクス", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2893, 1, 1, 109001, 3, 2, 0, "お夜食のチョコですわ\nとてもよく眠れます\nさ、溶けてしまう前に", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2895, 1, 1, 109001, 2, 3, 0, "この心地良さ…\n参考になりますわ\n次の薬は…クスクス", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2896, 1, 1, 109001, 3, 3, 0, "この中、何か\n入りそうですわね\n…チョコレート、とか", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2897, 1, 1, 109001, 4, 3, 0, "いい香り…この香りと\n同じものを調合して\n私も持っていたい", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2898, 1, 1, 109001, 5, 3, 0, "この暖かさ…\nチョコが溶けないよう\n気を付けなくては", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2900, 1, 1, 109001, 7, 3, 0, "いい味をしていますわ\n私も腕を磨きましょう\n料理と、調合の腕を…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2901, 1, 1, 109001, 8, 3, 0, "勉強になりますわ\n知識はいくらあっても\n困りませんもの\n特に、人体の知識は…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2903, 1, 1, 109101, 1, 1, 0, "弟くんのことを\n考えると\nあっという間に時間が\n過ぎちゃう…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2904, 1, 1, 109101, 2, 1, 0, "お姉ちゃんの\n元気の源は、弟くんの\n喜ぶ顔なんだから♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2905, 1, 1, 109101, 3, 1, 0, "弟くんと会ったら\nまずは抱きしめて\nそれからそれから…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2906, 1, 1, 109101, 4, 1, 0, "今日のお姉ちゃんは\n昨日のお姉ちゃんより\nいいお姉ちゃんだよ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2907, 1, 1, 109101, 5, 1, 0, "弟くん、もっと\nお姉ちゃんに\n甘えてもいいのに♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2908, 1, 4, 109101, 101, 1, 0, "お姉ちゃんのチョコ\n食べさせてあげたい…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2909, 1, 8, 109101, 102, 1, 0, "ドキドキしてきた…\nお姉ちゃんなのに\nどうしてかなぁ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2910, 1, 1, 109101, 1, 2, 0, "お姉ちゃんを\n好きなだけ\n触っていいよ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2911, 1, 1, 109101, 2, 2, 0, "そんなにお姉ちゃんの\nチョコが欲しいの？\n食いしん坊さん♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2912, 1, 1, 109101, 3, 2, 0, "ナデナデするのは\nお姉ちゃんの仕事！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2913, 1, 1, 109101, 4, 2, 0, "ありがとうなんて…\nお姉ちゃんとして\n当たり前だよ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2914, 1, 4, 109101, 101, 2, 0, "どうしたのかなっ？\nお姉ちゃんはいつでも\n弟くんのそばにいるよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2915, 1, 8, 109101, 102, 2, 0, "チョコを作ったよ\n隠し味は、たくさんの\n弟くんへの愛情♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2916, 1, 1, 109101, 1, 2, 0, "おはよ。お姉ちゃんが\n顔を洗うの\n手伝ってあげるね♪", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2917, 1, 1, 109101, 2, 2, 0, "ご飯を食べたら\n眠たくなるよね…\nお姉ちゃんと寝る？", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2918, 1, 1, 109101, 3, 2, 0, "そろそろ眠くなった？\nお姉ちゃんが添い寝\nしてあげるね♪", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2920, 1, 1, 109101, 2, 3, 0, "この気持ちよさは\nお姉ちゃんも太鼓判♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2921, 1, 1, 109101, 3, 3, 0, "このかわいさは\nお姉ちゃんも大満足♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2922, 1, 1, 109101, 4, 3, 0, "この香り、チョコに\n使えるかな…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2923, 1, 1, 109101, 5, 3, 0, "弟くんと一緒に\nぬくぬくしたいな…♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2925, 1, 1, 109101, 7, 3, 0, "すごくおいしいねっ♪\nあとで運動しなきゃ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2926, 1, 1, 109101, 8, 3, 0, "本を読んでる\n知的なお姉ちゃん\n素敵でしょ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2928, 1, 1, 106101, 1, 1, 0, "アタシって…\nここにいても\nいいのか…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2929, 1, 1, 106101, 2, 1, 0, "ちゃんとした建物に\nいるのが久しぶりで\nそわそわする…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2930, 1, 1, 106101, 3, 1, 0, "ここで天楼覇断剣\n出す練習したら\nアイツら怒るかな？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2931, 1, 1, 106101, 4, 1, 0, "ギルドハウスか…\nこういう隠れ家\n欲しかったんだよな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2932, 1, 1, 106101, 5, 1, 0, "めちゃくちゃ暇だ…\n…なんかやること\nないのか？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2933, 1, 4, 106101, 101, 1, 0, "みんなが早く\nアタシのこと\n思い出さないかな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2934, 1, 8, 106101, 102, 1, 0, "アイツはいいヤツだし\n戦う時はアタシも\n協力してやるか！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2935, 1, 1, 106101, 1, 2, 0, "ちゃんはいらん！\nノウェムでいい！\n分かったか？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2936, 1, 1, 106101, 2, 2, 0, "どうした？\n戦うのか！？\n…なんだ、違うのか", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2937, 1, 1, 106101, 3, 2, 0, "身長のことは\nあんまり言うな！\n気にしてるからな！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2938, 1, 1, 106101, 4, 2, 0, "アタシの戦い方は\nなんでもアリだ！\nだから見習うなよ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2939, 1, 4, 106101, 101, 2, 0, "オマエとの思い出か…\n敵として戦ってばっか\nだったな！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2940, 1, 8, 106101, 102, 2, 0, "オマエやオクトーと\n一緒に、あちこち\nいってみたいんだ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2941, 1, 1, 106101, 1, 2, 0, "よっ！　おはよう！\n朝だぞー！\n起きてるかー？", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2942, 1, 1, 106101, 2, 2, 0, "腹減ったー！\n飯食うぞ、飯！\nオマエも行くよな？", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2943, 1, 1, 106101, 3, 2, 0, "ふわぁ…\nまだ起きてんのか？\n元気だなオマエ…", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2945, 1, 1, 106101, 2, 3, 0, "ふぃ～…\n極楽極楽…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2946, 1, 1, 106101, 3, 3, 0, "おいオマエ！\n寝技の練習に\n付き合ってもらうぞ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2947, 1, 1, 106101, 4, 3, 0, "くんくん…\nたまにはこういうのも\n悪くないかもな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2948, 1, 1, 106101, 5, 3, 0, "あー…\nもっと前にコイツと\n出会いたかった…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2950, 1, 1, 106101, 7, 3, 0, "VRでも飯はうまい！\n腹が減るのはちゃんと\n生きてる気がするな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2951, 1, 1, 106101, 8, 3, 0, "勉強は大事だな！\nうん！…だめだ！\n全然わからん！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2953, 1, 1, 101401, 1, 1, 0, "探偵というのは\n謎を追い求める\n生き物なのさっ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2954, 1, 1, 101401, 2, 1, 0, "事件のオチは\n往々にして\n取るに足らないものさ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2955, 1, 1, 101401, 3, 1, 0, "読書で得る知識が\n探偵の\n血肉になるんだよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2956, 1, 1, 101401, 4, 1, 0, "この前の\n捜査資料…\nどこにあったかな？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2957, 1, 1, 101401, 5, 1, 0, "どんな小さな謎も\n私にとっては\n宝石みたいなものさ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2958, 1, 4, 101401, 101, 1, 0, "助手くんと一緒だと\nよい発想が浮かぶ…\nどうしてだろうね？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2959, 1, 8, 101401, 102, 1, 0, "助手くんの好きな\nタイプって…\nす、推理してみるっ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2960, 1, 1, 101401, 1, 2, 0, "珈琲、淹れたてだよ\n一杯どうだい？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2961, 1, 1, 101401, 2, 2, 0, "事件の考察を\nまとめてみたんだけど\n聞いてくれるかな？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2962, 1, 1, 101401, 3, 2, 0, "小さな違和感を\n見逃さない…\n探偵の心得だよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2963, 1, 1, 101401, 4, 2, 0, "機能的な手帳とペン…\nこの仕事には\n欠かせないね！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2964, 1, 4, 101401, 101, 2, 0, "助手くんに\n珈琲…\n淹れてほしいなぁ～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2965, 1, 8, 101401, 102, 2, 0, "助手くんは\n私にとって\n最高の相棒だよ☆", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2966, 1, 1, 101401, 1, 2, 0, "朝はいいひらめきが\n得られるんだ…\nさあ捜査を始めよう！", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2967, 1, 1, 101401, 2, 2, 0, "一休みしたら\n推理の答え合わせを\nしてみようか？", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2968, 1, 1, 101401, 3, 2, 0, "夜の張りこみかい？\nなら私も\n同行させてほしいな！", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2970, 1, 1, 101401, 2, 3, 0, "脳を酷使した後は\nゆっくり休む…\nこれも仕事だよ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2971, 1, 1, 101401, 3, 3, 0, "この愛らしさ…\n要捜査対象だね！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2972, 1, 1, 101401, 4, 3, 0, "思考が活性化される\n素敵な香りだね…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2973, 1, 1, 101401, 5, 3, 0, "くぅ、心地よすぎる…\n今だけは捜査も\n一休みだね♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2975, 1, 1, 101401, 7, 3, 0, "いっぱい食べて\nいっぱい考える\n探偵の本分だね！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2976, 1, 1, 101401, 8, 3, 0, "探偵だからね\nミステリには\n一家言あるんだ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2978, 1, 1, 105501, 1, 1, 0, "先輩、素敵だなぁ…\nもっと近づければ\nいいのに…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2979, 1, 1, 105501, 2, 1, 0, "せ、先輩がいた部屋！\n私には刺激が強い…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2980, 1, 1, 105501, 3, 1, 0, "先輩を観察しやすい\n場所はあるかな…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2981, 1, 1, 105501, 4, 1, 0, "バ、バイト代出たし、\nせ、先輩をご飯とかに\n誘いたい…な", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2982, 1, 1, 105501, 5, 1, 0, "今頃先輩は\nあの辺かな…？\n見に行ってみよう！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2983, 1, 4, 105501, 101, 1, 0, "私と先輩の出会いって\n思い返すと少女漫画\nみたい…えへへ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2984, 1, 8, 105501, 102, 1, 0, "せ、先輩になら\n観察されてもいいなぁ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2985, 1, 1, 105501, 1, 2, 0, "バイト先に新しい子が\n入って来たんです…\nわ、私、先輩です♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2986, 1, 1, 105501, 2, 2, 0, "せ、先輩…？\nえっと、あの、その、\nあわわわわ…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2987, 1, 1, 105501, 3, 2, 0, "えへへ…えへへ～\nせ、先輩から\n話しかけてくれました", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2988, 1, 1, 105501, 4, 2, 0, "ま、また…\n先輩と一緒に\nバイトしたい…です", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2989, 1, 4, 105501, 101, 2, 0, "私と先輩が同じ空間に\n…ゆ、夢みたいです", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2990, 1, 8, 105501, 102, 2, 0, "せ、先輩のそばに\n居られて、私\n今、とっても\n幸せです…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2991, 1, 1, 105501, 1, 2, 0, "せ、先輩…！\nおひゃっ！\nおはようございます！", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2992, 1, 1, 105501, 2, 2, 0, "先輩ランチに\n行きましょう！\nよし、練習はバッチリ", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2993, 1, 1, 105501, 3, 2, 0, "あ、せ、先輩。\nえと、そ、外…\nもう真っ暗ですねぇ", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2995, 1, 1, 105501, 2, 3, 0, "こ、ここで先輩も\n休んだり…はっ！\nわ、私ったらなにを！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2996, 1, 1, 105501, 3, 3, 0, "かわいいですねー\n君も一緒に、\n先輩を観察する？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2997, 1, 1, 105501, 4, 3, 0, "ここから先輩の\n香りがすればなあ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (2998, 1, 1, 105501, 5, 3, 0, "先輩と一緒に\nぬくぬくできたら\n最高なんだけどなぁ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3000, 1, 1, 105501, 7, 3, 0, "おいし～\nこれは\n先輩も好きな味かな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3001, 1, 1, 105501, 8, 3, 0, "す、好きな人に\n近づく方法…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3003, 1, 1, 109201, 1, 1, 0, "私の力は、大切な\n人たちを護るために\n役立てたいんだ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3004, 1, 1, 109201, 2, 1, 0, "きれいな景色と\n美味しいご飯！\nこの世界も素敵だね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3005, 1, 1, 109201, 3, 1, 0, "この世界のみんなとも\nもっと仲良く\nなりたいな！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3006, 1, 1, 109201, 4, 1, 0, "わー！\nあれもこれも\n気になっちゃうな！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3007, 1, 1, 109201, 5, 1, 0, "しっかりしなきゃって\n思ってても、\nグレアといるとつい\n甘えちゃうんだよね～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3008, 1, 4, 109201, 101, 1, 0, "違う世界でも\n友だちができて、\nすごくうれしいよ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3009, 1, 8, 109201, 102, 1, 0, "みんなとも一緒に\n授業とか受けたいな～\n絶対楽しそうだもん！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3010, 1, 1, 109201, 1, 2, 0, "歩き方がきれい？\nえへへ、そうかな…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3011, 1, 1, 109201, 2, 2, 0, "勉強は好きだよ！\n知識が増えていくのは\nやっぱり楽しいから", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3012, 1, 1, 109201, 3, 2, 0, "私たちの国マナリアは\n人々の間に魔法が\n伝えられた、その\n始まりの地なんだ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3013, 1, 1, 109201, 4, 2, 0, "この部屋にも、\nもっとたくさん本棚が\n増えないかな～…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3014, 1, 4, 109201, 101, 2, 0, "あなたも魔法、\n覚えてみる？\n私、教えるよ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3015, 1, 8, 109201, 102, 2, 0, "お茶の時間に\nしたいな～…\n一緒にどう？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3016, 1, 1, 109201, 1, 2, 0, "おはようっ！\n今日も素敵な\n一日にしようね！", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3017, 1, 1, 109201, 2, 2, 0, "お昼ご飯は\nちゃんと食べた？\n午後も頑張ろうね！", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3018, 1, 1, 109201, 3, 2, 0, "つい本の続きが\n気になって、夜更かし\nしちゃうんだよね～…", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3020, 1, 1, 109201, 2, 3, 0, "はぁ、気持ちいい～…\nちょっとだけ\n眠っちゃおうかな…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3021, 1, 1, 109201, 3, 3, 0, "わあ、これ可愛い！\nグレアにも見せて\nあげたいな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3022, 1, 1, 109201, 4, 3, 0, "いい香り～！\nなんだか気持ちが\n落ち着くね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3023, 1, 1, 109201, 5, 3, 0, "あぁ、あったかくて\n幸せ～♪　身体が\nポカポカしてきたよ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3025, 1, 1, 109201, 7, 3, 0, "こ、これ美味し～！\n止められないよー！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3026, 1, 1, 109201, 8, 3, 0, "ふんふん…\n…はっ！　む、夢中に\nなっちゃってた…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3028, 1, 1, 109301, 1, 1, 0, "新しい建物に入ると\n必ず一度は迷子に\nなっちゃうですよ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3029, 1, 1, 109301, 2, 1, 0, "おやつはどこですか？\nお腹が空いて\nきちゃったですぅ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3030, 1, 1, 109301, 3, 1, 0, "宿題、７つぐらい\n出しそびれたですけど\nまあ大丈夫ですぅ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3031, 1, 1, 109301, 4, 1, 0, "不思議なこと\nばかりですけど…\nまぁ大丈夫です！\nこれもせいしゅんです", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3032, 1, 1, 109301, 5, 1, 0, "なにか面白そうなもの\nないですかね～？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3033, 1, 4, 109301, 101, 1, 0, "ここのみんなとも\nもうお友だちですぅ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3034, 1, 8, 109301, 102, 1, 0, "おなかが減ったです…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3035, 1, 1, 109301, 1, 2, 0, "ごはんも美味しいし\nここはいいところです\nルゥは幸せですぅ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3036, 1, 1, 109301, 2, 2, 0, "魔法の話ですか？\nそれはアンせんぱいに\n聞いてほしいですぅ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3037, 1, 1, 109301, 3, 2, 0, "あなたもお勉強\n苦手ですか？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3038, 1, 1, 109301, 4, 2, 0, "ルゥの帽子、\nかわいいです？\nお気に入りですぅ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3039, 1, 4, 109301, 101, 2, 0, "お友だちいっぱいで、\nルゥはうれしいです♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3040, 1, 8, 109301, 102, 2, 0, "一緒にお勉強…！？\nぐえぇ、イヤですぅ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3041, 1, 1, 109301, 1, 2, 0, "朝ですねぇ…ふわわ…\nルゥはまだ夢の中に\nいたいですぅ…", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3042, 1, 1, 109301, 2, 2, 0, "午後もいっぱい\n遊ぶですよ～！", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3043, 1, 1, 109301, 3, 2, 0, "夜ふかしって\nドキドキするです！\nせいしゅんですぅ！", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3045, 1, 1, 109301, 2, 3, 0, "ふかふかですぅ～♪\nおひるねが\nしたくなるですぅ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3046, 1, 1, 109301, 3, 3, 0, "これふかふかですぅ！\n机でうとうとする時の\n枕によさそうですぅ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3047, 1, 1, 109301, 4, 3, 0, "ふぁぁ、いいにおい…\nなんだか気持ちが\nよくなってきたですぅ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3048, 1, 1, 109301, 5, 3, 0, "ぽっかぽかですぅ～！\nあったまったら\nまた遊びにいくですぅ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3050, 1, 1, 109301, 7, 3, 0, "お、おいしいですぅ！\n全部ルゥに\nくださいですぅ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3051, 1, 1, 109301, 8, 3, 0, "本は…読むと、\nすぐ眠気がぁ…\n…ぐぅ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3053, 1, 1, 109401, 1, 1, 0, "ピアノ…\nどこかにないかな？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3054, 1, 1, 109401, 2, 1, 0, "ふぅ…\n落ち着くな…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3055, 1, 1, 109401, 3, 1, 0, "マナリアはいつも\n賑やかなんだ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3056, 1, 1, 109401, 4, 1, 0, "みんな…\n今頃何してるかな？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3057, 1, 1, 109401, 5, 1, 0, "まさか違う世界に\n来ちゃうなんて…\nびっくりだよね…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3058, 1, 4, 109401, 101, 1, 0, "人と話すのは\n得意じゃないな…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3059, 1, 8, 109401, 102, 1, 0, "ここのみんなと一緒に\n学院に通えたら…\nちょっと面白いかも", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3060, 1, 1, 109401, 1, 2, 0, "やっぱり、私の翼や\n尻尾…気になる？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3061, 1, 1, 109401, 2, 2, 0, "一番得意なのは、\nやっぱり炎魔法かな…\n加減は難しいけど…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3062, 1, 1, 109401, 3, 2, 0, "私の故郷は竜の国…\n険しい山脈を\n越えた先にあるんだ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3063, 1, 1, 109401, 4, 2, 0, "アン…？\nあっ、ご、ごめんね！\n間違えちゃった…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3064, 1, 4, 109401, 101, 2, 0, "ふん…ふん…♪\n…わっ！\n今の、聞いてた…？\nは、恥ずかしい…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3065, 1, 8, 109401, 102, 2, 0, "私に手伝えること、\nなにかある…？\nなんでも言ってね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3066, 1, 1, 109401, 1, 2, 0, "朝の空気って\nすごく\n静かで透き通ってて…\nなんだか落ち着くよね", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3067, 1, 1, 109401, 2, 2, 0, "学院食堂のランチも\n美味しいけど…\nここのご飯も好きかな", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3068, 1, 1, 109401, 3, 2, 0, "そろそろベッドに\n行かないとだね\n…寝相、今夜こそ\n気を付けよう…！", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3070, 1, 1, 109401, 2, 3, 0, "羽を伸ばしたく\nなっちゃうね…ふふ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3071, 1, 1, 109401, 3, 3, 0, "わ、私が持つには\nかわいすぎるかな…\nでも、素敵だね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3072, 1, 1, 109401, 4, 3, 0, "いい香り…\nこれ、\nアンも好きそう…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3073, 1, 1, 109401, 5, 3, 0, "火を見ていると\n少し落ち着くんだ…\nへ、変かな？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3075, 1, 1, 109401, 7, 3, 0, "わ、美味しい…！\nこれ、どうやって\n作ったのかな…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3076, 1, 1, 109401, 8, 3, 0, "この本…アンにも\n見せてあげたら\n喜ぶかな、ふふっ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3078, 1, 1, 109501, 1, 1, 0, "着物はきれいですが\n歩きにくいです…\nいっそ脱いで…ぐふふ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3079, 1, 1, 109501, 2, 1, 0, "あっ、いけません\n着物がはだけて…はっ\nもしやドＳさんが…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3080, 1, 1, 109501, 3, 1, 0, "この部屋は妄想する\nのにうってつけです…\nぐふふ…じゅるり", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3081, 1, 1, 109501, 4, 1, 0, "扇をゆっくりと開く\nこの感触…なぜだか\n艶めかしさを感じます", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3082, 1, 1, 109501, 5, 1, 0, "「マッサージをすると\n言いながらドＳさんの\n手は次第に」…ぐふふ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3083, 1, 4, 109501, 101, 1, 0, "…ドＳさんの視線を\n思い出しながら踊りの\n練習です…イイ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3084, 1, 8, 109501, 102, 1, 0, "よいではないか\nよいではないか～\nいけませんご堪忍を～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3085, 1, 1, 109501, 1, 2, 0, "あ～れ～～～！\nドＳさんもお好きな人\nですねぇ…ぐふふ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3086, 1, 1, 109501, 2, 2, 0, "どこにいてもドＳさん\nの気配を感じ取れる\nようになりました…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3087, 1, 1, 109501, 3, 2, 0, "こんな場所で\nなんというプレイを！\n…妄想でした", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3088, 1, 1, 109501, 4, 2, 0, "クウカを押し倒して\nそのまま…じゅるり\nああっ、着物が…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3089, 1, 4, 109501, 101, 2, 0, "ドＳさんは優しいです\n優しいのにドＳとは\nさすが人格破綻者…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3090, 1, 8, 109501, 102, 2, 0, "ドＳさんの考えている\nことが伝わってきます\nはい…クウカ脱ぎます", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3091, 1, 1, 109501, 1, 2, 0, "眩しい朝日がクウカの\n体を焦がします\n…それもイイ！", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3092, 1, 1, 109501, 2, 2, 0, "お昼のランチは\nクウカがいいだなんて\n大胆過ぎです…じゅる", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3093, 1, 1, 109501, 3, 2, 0, "…今夜のドＳさんは\n大胆です…ぐふふ…\nはっ、視線が…！？", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3095, 1, 1, 109501, 2, 3, 0, "この心地良さ、\n心が休まりすぎて\n体が疼いてきます…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3096, 1, 1, 109501, 3, 3, 0, "すごく愛らしいです…\n妄想も出てこない\nくらい…じゅるり", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3097, 1, 1, 109501, 4, 3, 0, "この香りに包まれて\nいると、いい気持ちに\nなって…いきますぅ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3098, 1, 1, 109501, 5, 3, 0, "「熱を帯びた体は\nどうしようもなく\n誰かを求め…」ぐふふ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3100, 1, 1, 109501, 7, 3, 0, "お腹が膨らんで\nいきます…それは\nまるで…ぐふふ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3101, 1, 1, 109501, 8, 3, 0, "クウカの探している\nジャンルはどこに\nありますかね？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3103, 1, 1, 109601, 1, 1, 0, "カチョーフーゲツは\nトーゴクの心を\n表した言葉デスね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3104, 1, 1, 109601, 2, 1, 0, "オー！　新しい忍法を\n思いついたデス！\nさっそく試すデス！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3105, 1, 1, 109601, 3, 1, 0, "トーゴクの天気には\n槍が降る日も\nあるそうデス…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3106, 1, 1, 109601, 4, 1, 0, "我がショーグンは\n永遠にフメツデース\nいざ出陣デース！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3107, 1, 1, 109601, 5, 1, 0, "壁に耳あり\n障子に…ん？\nメアリーって誰デス？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3108, 1, 4, 109601, 101, 1, 0, "ショーグンがいれば\nワタシはどんな\n忍法も使いこなせマス", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3109, 1, 8, 109601, 102, 1, 0, "不届き者はどこで\nゴザルデス？　忍法で\n退治してくれヨウ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3110, 1, 1, 109601, 1, 2, 0, "ショーグン！　新しい\n忍法を覚えたデス！\n見てほしいデース！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3111, 1, 1, 109601, 2, 2, 0, "レッツ手裏剣道デス！\n投げてソウロウで\nゴザルデース！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3112, 1, 1, 109601, 3, 2, 0, "ふりかかる火の粉は\nワタシが払うデス！\nバンバン！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3113, 1, 1, 109601, 4, 2, 0, "とっておきの忍法が\n吹き荒れるデス！\nよそ見はダメデスヨ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3114, 1, 4, 109601, 101, 2, 0, "ショーグンは\n何階建てのお城を\n建てたいデスか？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3115, 1, 8, 109601, 102, 2, 0, "テンシュカクから\n平和な城下町を\n眺めてみたいデス…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3116, 1, 1, 109601, 1, 2, 0, "おはようございマス！\n本日はオヒガラモヨク\n良い日になりそうデス", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3117, 1, 1, 109601, 2, 2, 0, "お腹すいたデース…\nショーグンもご飯に\nしまショウ！", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3118, 1, 1, 109601, 3, 2, 0, "闇討ちに遭わぬよう\n夜は気を付けて\n歩くデスよー？", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3120, 1, 1, 109601, 2, 3, 0, "天下分け目の\n戦いに備えて…\nマッタリデース", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3121, 1, 1, 109601, 3, 3, 0, "口寄せの術で\n召喚したいくらい\nかわいいデース", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3122, 1, 1, 109601, 4, 3, 0, "いい匂いデ～ス\n香り玉の開発の\n参考になりそうデスね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3123, 1, 1, 109601, 5, 3, 0, "ワタシの懐よりも\nゾーリが暖まりマス…\nライバル出現デス…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3125, 1, 1, 109601, 7, 3, 0, "腹が減っては\n戦はできぬデース！\n天下統一に向けて\n腹ごしらえデス！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3126, 1, 1, 109601, 8, 3, 0, "ムム、暗号文デス！？\n解読には時間が\nかかりそうデース…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3128, 1, 1, 109701, 1, 1, 0, "掃除も終わりましたし\nそろそろ、食事の\n準備をしなくては", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3129, 1, 1, 109701, 2, 1, 0, "異世界の演劇…\n許されるならば、\n観てみたいです", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3130, 1, 1, 109701, 3, 1, 0, "こちらの世界には、\nマヨネーズが普通に\n存在するのですね…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3131, 1, 1, 109701, 4, 1, 0, "こちらの世界は食材も\n面白い物が多いですね\nなにかレシピを\n考えてみましょうか", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3132, 1, 1, 109701, 5, 1, 0, "みょんみょんみょん…\n魔法の練習は\nこれぐらいですね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3133, 1, 4, 109701, 101, 1, 0, "倒木の撤去で思わぬ\n収入となりました\nサレン様に\nお渡ししておかないと", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3134, 1, 8, 109701, 102, 1, 0, "料理を美味しく\n召し上がって頂けると\nメイド冥利につきます", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3135, 1, 1, 109701, 1, 2, 0, "お出かけでしたら、\nレムも同行\nさせてくださいね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3136, 1, 1, 109701, 2, 2, 0, "レムにできること\nでしたら、なんでも\nお申し付けください", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3137, 1, 1, 109701, 3, 2, 0, "じーー。いえ、\nその髪の長さが少し、\nとても少し、かなり\n少し気になったもので", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3138, 1, 1, 109701, 4, 2, 0, "鉄球ですか？\nあれは護身用ですので\n問題はありませんよ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3139, 1, 4, 109701, 101, 2, 0, "計算の勉強は\n進んでおりますか？\nレムでよろしければ、\nいつでもお教えします", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3140, 1, 8, 109701, 102, 2, 0, "次はもっと、大きな\n魔物料理に挑戦します\nワイバーンの姿焼き\nなんてどうでしょうか", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3141, 1, 1, 109701, 1, 2, 0, "おはようございます\n朝食の準備が\nできておりますので、\n食卓におこしください", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3142, 1, 1, 109701, 2, 2, 0, "昼食の時間ですね\n何か食べたいものは\nありますか？", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3143, 1, 1, 109701, 3, 2, 0, "寝ないのか、ですか？\nメイドの就寝は、\n屋敷で最後と\n決まっておりますので", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3145, 1, 1, 109701, 2, 3, 0, "ふんわり…\nこのふんわりさは、\n鬼をも黙らせます…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3146, 1, 1, 109701, 3, 3, 0, "ふふっ…異世界でも\nかわいいものは\n共通なのですね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3147, 1, 1, 109701, 4, 3, 0, "よい香りですね\n魔女教の悪臭とは\n大違いです", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3148, 1, 1, 109701, 5, 3, 0, "ぬくぬくです…はっ！\nこれはいけません！\n人を…いえ、\n鬼をだめにします！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3150, 1, 1, 109701, 7, 3, 0, "はむはむ…美味しい！\nこの味を再現できたら\n褒めてもらえる\nかもしれません…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3151, 1, 1, 109701, 8, 3, 0, "絵がいっぱい…\n異世界の本は\nとても面白いですね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3153, 1, 1, 109801, 1, 1, 0, "異世界であろうと、\nラムのやるべきことは\n変わらないわ\nまずは休憩するわよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3154, 1, 1, 109801, 2, 1, 0, "マヨネーズがある\nということは、\nバルスの故郷が近い\nのかもしれないわね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3155, 1, 1, 109801, 3, 1, 0, "この世界では、バルス\nの言う言葉が使われる\nときがあるのね\n忌々しいかぎりだわ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3156, 1, 1, 109801, 4, 1, 0, "ラムたち以外にも\nメイドが居るのね\nラムが…いえ、\n仕事を押しつけるわ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3157, 1, 1, 109801, 5, 1, 0, "…千里眼で視るかぎり\n周囲に異変はないわね\n危険がないなら、\nあとはレムに任せるわ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3158, 1, 4, 109801, 101, 1, 0, "…ラムもまた、\n「お姉ちゃん」って\n呼ばれる日が\nくるのかしらね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3159, 1, 8, 109801, 102, 1, 0, "ハッ！　ラムが作る\n蒸かし芋は完璧ね\n…気が向いたら、また\n作ってやろうかしら", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3160, 1, 1, 109801, 1, 2, 0, "ラムに構う暇が\nあるなら働きなさい\nそうすれば、\nラムが楽をできるわ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3161, 1, 1, 109801, 2, 2, 0, "ラムに何かを\n期待しても無駄よ\nラムに命令できるのは\nロズワール様だけだわ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3162, 1, 1, 109801, 3, 2, 0, "暇なら買い物に\n付き合いなさい\n荷物を\n持たせてあげるわ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3163, 1, 1, 109801, 4, 2, 0, "ラムになんの用？\nふざけた内容なら\nねじ切るわよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3164, 1, 4, 109801, 101, 2, 0, "そういえば、あなたは\n記憶喪失だったわね…\n思い出す努力をなさい\n…家族が悲しむわよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3165, 1, 8, 109801, 102, 2, 0, "蒸かし芋が欲しい？\n随分と厚かましいわね\n…まだ残りがあるから\n好きに食べなさい", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3166, 1, 1, 109801, 1, 2, 0, "だらしない顔ね\n顔を洗って\nしゃんとなさい", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3167, 1, 1, 109801, 2, 2, 0, "お腹が空く時間ね\nラムの昼食を\n用意させてあげるわ", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3168, 1, 1, 109801, 3, 2, 0, "メイドが寝るのは\n屋敷で最後…\nラムが寝るために、\nさっさと寝なさい", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3170, 1, 1, 109801, 2, 3, 0, "…ここから動かない\nという、誓いを\n立てたくなるわね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3171, 1, 1, 109801, 3, 3, 0, "これは…かわいいわね\nもちろん、\nラムほどではないけど", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3172, 1, 1, 109801, 4, 3, 0, "…香炉かしら？\nこちらの世界にも\n質のよい物があるのね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3173, 1, 1, 109801, 5, 3, 0, "見つけてしまったの\nかもしれないわ…\nラムの楽園を！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3175, 1, 1, 109801, 7, 3, 0, "…はむっ\nラムの蒸かし芋ほど\nではないけど、\nなかなかいけるわね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3176, 1, 1, 109801, 8, 3, 0, "異世界ならではの\n面白い内容もあるわね\nとても興味深いわ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3178, 1, 1, 109901, 1, 1, 0, "ねえパック、\nにゃんにゃか\nにゃかにゃんしよっか", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3179, 1, 1, 109901, 2, 1, 0, "時間もあるし、\nこっちの世界の石で\n石積みしようかな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3180, 1, 1, 109901, 3, 1, 0, "私もお姉さんだし、\nサレンさんを\n見習わないと…うん！\n私、すごーく頑張る！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3181, 1, 1, 109901, 4, 1, 0, "この世界は、銀髪で\nハーフエルフでも受け\n入れてくれるのね…\nなんだか嬉しいな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3182, 1, 1, 109901, 5, 1, 0, "今日のご飯…\nピーマルは\n入ってないわよね…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3183, 1, 4, 109901, 101, 1, 0, "また、みんなで\n一緒に買い物に\n行きたいな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3184, 1, 8, 109901, 102, 1, 0, "パック以外から\n家族って呼ばれたの\n初めて…ふふっ♪\nちょっと嬉しいかも！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3185, 1, 1, 109901, 1, 2, 0, "今からパックの\n毛繕いをするけど…\n一緒にする？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3186, 1, 1, 109901, 2, 2, 0, "私も何かお手伝いを…\nあ、そうだ！\n料理とかどうかな！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3187, 1, 1, 109901, 3, 2, 0, "ねえ、ここで\n歌の練習って、\nしてもいいかな？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3188, 1, 1, 109901, 4, 2, 0, "…あ、ごめんなさい！\n話しかけられるのって\nあまり慣れてなくて…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3189, 1, 4, 109901, 101, 2, 0, "すごーく頑張るから、\nこれからも勉強を\n手伝ってね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3190, 1, 8, 109901, 102, 2, 0, "せーの、びくとりー！\nふふっ…また、一緒に\nラジーオタイソー\nしようね♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3191, 1, 1, 109901, 1, 2, 0, "ちゃんと起きてる？\nふふっ…よかった。\nあ、べ、別に心配した\nわけじゃないからね？", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3192, 1, 1, 109901, 2, 2, 0, "お昼ご飯が楽しみね♪\nあ、こっちの世界って\nまよねーずはあるの？", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3193, 1, 1, 109901, 3, 2, 0, "そろそろ寝る時間よ。\n夜更かししたら、\nめっ！　だからね！", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3195, 1, 1, 109901, 2, 3, 0, "勉強も終わったし…\nちょっ、ちょっとだけ\n休んでもいいよね…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3196, 1, 1, 109901, 3, 3, 0, "か、かわいい…！\nパックのかわいさに\n匹敵するかも…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3197, 1, 1, 109901, 4, 3, 0, "良い香り…\nロズワールの屋敷にも\nこういうのって\nあるのかな？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3198, 1, 1, 109901, 5, 3, 0, "あったかい…\nずっとここに…はっ！\nダメダメ！　王選の\nために勉強しないと！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3200, 1, 1, 109901, 7, 3, 0, "あ…これ美味しい！\nこっちの世界は色んな\n食べ物があるのね。", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3201, 1, 1, 109901, 8, 3, 0, "知らないことが\nいっぱい書いてある…\n異世界の本って、\n面白いのね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3203, 1, 1, 110001, 1, 1, 0, "海ってマジ最高！\nビーチの撮影も\n超楽しいしー♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3204, 1, 1, 110001, 2, 1, 0, "20パーって何パ？\n今度ヒデサイに\n聞いてみよっ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3205, 1, 1, 110001, 3, 1, 0, "夏の思い出を\nヒデサイと一緒に\n作りたいな～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3206, 1, 1, 110001, 4, 1, 0, "テンションあがる\n水着ゲットして\n超ラッキーだよ～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3207, 1, 1, 110001, 5, 1, 0, "休憩が終わったら\n撮影にもどらないと！\n気合入れてこ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3208, 1, 4, 110001, 101, 1, 0, "ヒデサイとビーチで\nパーティーしたいな～\n超楽しそうだよね！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3209, 1, 8, 110001, 102, 1, 0, "ヒデサイといると\n楽しくてうれしくて…\nカレシってこんな感じ\nなのかなぁ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3210, 1, 1, 110001, 1, 2, 0, "見て見て～！\n夏らしいネイルに\nしてみたんだ～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3211, 1, 1, 110001, 2, 2, 0, "ヒデサイ～\nこの水着どうかな？\nイケてるっしょ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3212, 1, 1, 110001, 3, 2, 0, "水着姿の写真？\nもちいいよ！\nかわいく撮ってね！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3213, 1, 1, 110001, 4, 2, 0, "うちだって\n夏の海みたいに\nキラキラしちゃうよ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3214, 1, 4, 110001, 101, 2, 0, "ヒデサイとうちの\nフィーリングって\nめっちゃエモいよね♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3215, 1, 8, 110001, 102, 2, 0, "ねーねーヒデサイ！\n一緒に泳ぎにいこっ！\nほら、手繋いで♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3216, 1, 1, 110001, 1, 2, 0, "水着オッケー！\nネイルもばっちり！\n朝から遊んじゃおー♪", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3217, 1, 1, 110001, 2, 2, 0, "お昼食べたら\n昼寝して\nリフレッシュしよー♪", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3218, 1, 1, 110001, 3, 2, 0, "水着ってマジで\n最強かも…このまま\nお風呂にも入れるし♪", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3220, 1, 1, 110001, 2, 3, 0, "マジ気持ちいいー♪\nおに癒やされるん\nですけど～！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3221, 1, 1, 110001, 3, 3, 0, "やばばばばー！\nかわいすぎだってば！\nマジらぶだよ～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3222, 1, 1, 110001, 4, 3, 0, "あ、この香り超好き！\nモデル仲間に\n教えたげよ～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3223, 1, 1, 110001, 5, 3, 0, "あったか～い♪\n夏の太陽みたい！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3225, 1, 1, 110001, 7, 3, 0, "マジ美味しいねこれ♪\nビーチで\n流行っちゃうかも！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3226, 1, 1, 110001, 8, 3, 0, "夏のために\n最新ファッションを\n勉強ちう！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3228, 1, 1, 110001, 10, 3, 0, "なにコレもにカワ～♪\nなんて生き物？\nヒデサイ知ってる？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3229, 1, 1, 110001, 11, 3, 0, "うわっ超フカフカ～\nなんかうち眠く\nなってきちゃった…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3230, 1, 1, 110101, 1, 1, 0, "海の家でのんびり\n過ごしましょう\n熱中症には注意してね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3231, 1, 1, 110101, 2, 1, 0, "はぁ、どうやったら\n泳げるように\nなれるのかしら～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3232, 1, 1, 110101, 3, 1, 0, "海で日焼け止めを\n塗らないと、あとで\n大変なことになるわよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3233, 1, 1, 110101, 4, 1, 0, "久しぶりに水着を\n着たんだけど\n変じゃなかったかな…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3234, 1, 1, 110101, 5, 1, 0, "波の音って落ち着くわ\n授業中に聞いたら\nみんな寝ちゃうかも？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3235, 1, 4, 110101, 101, 1, 0, "海が楽しいって\n思えたのも\nあの子のおかげね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3236, 1, 8, 110101, 102, 1, 0, "あの子と二人で海に\n来られてよかったわ\n一日があっという間ね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3237, 1, 1, 110101, 1, 2, 0, "男の子と海だなんて\n少女漫画みたいで\n憧れてたの～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3238, 1, 1, 110101, 2, 2, 0, "夏休みの宿題は\nちゃんとやった？\n遊んでばかりではダメ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3239, 1, 1, 110101, 3, 2, 0, "昼の海と夜の海\nどっちが好き？　私は\n君といる海が一番よ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3240, 1, 1, 110101, 4, 2, 0, "ふう、疲れたわ～\n冷たいジュースで\n元気回復ね！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3241, 1, 4, 110101, 101, 2, 0, "海で見た君の背中\nすごく大きく見えたわ\nやっぱり男の子なのね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3242, 1, 8, 110101, 102, 2, 0, "君と一緒に見た\n夜の海…\n忘れられそうにないわ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3243, 1, 1, 110101, 1, 2, 0, "準備ができたら\n海に出かけようよ\n今日も楽しみましょう", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3244, 1, 1, 110101, 2, 2, 0, "あら、もうお昼？\n海の家でランチに\nしましょ♪", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3245, 1, 1, 110101, 3, 2, 0, "夜の海岸についてきて\nくれる？　暗がりは\n一人じゃ怖くて…", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3247, 1, 1, 110101, 2, 3, 0, "泳ぐ練習をしたら\n疲れちゃったわ～\nちょっと一休み…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3248, 1, 1, 110101, 3, 3, 0, "かわいいわね～\n浮き輪に乗せて\n海に浮かべたいわ～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3249, 1, 1, 110101, 4, 3, 0, "この匂い…\nなんだか夏の海を\n思い出すわ～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3250, 1, 1, 110101, 5, 3, 0, "夜の海風で冷えない\nように、しっかりと\n身体を暖めましょうね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3252, 1, 1, 110101, 7, 3, 0, "海で遊ぶ前に\nしっかり食べて\n体力つけましょう", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3253, 1, 1, 110101, 8, 3, 0, "夏休み中でも\nしっかりお勉強\nしないと！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3255, 1, 1, 110101, 10, 3, 0, "この子に乗れば\n海の上でも\nすいすい進めるかしら", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3256, 1, 1, 110101, 11, 3, 0, "ビーナスの誕生！\n…なんちゃって\nはしゃぎすぎかしら？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3257, 1, 1, 110301, 1, 1, 0, "夏の海には商機が\nあるはず…\nのがす手はないわね♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3258, 1, 1, 110301, 2, 1, 0, "あたしが作った\n日焼け止め…\nこれは売れそうね！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3259, 1, 1, 110301, 3, 1, 0, "子どもたちのために\n浮き輪を用意して\nおかないと…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3260, 1, 1, 110301, 4, 1, 0, "海っていいわよね！\nみんなで羽を伸ばしに\n行きたいわ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3261, 1, 1, 110301, 5, 1, 0, "波の音を聞いて\nのんびりするのも\n悪くないわね…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3262, 1, 4, 110301, 101, 1, 0, "ただの砂遊びも\nあんたと一緒なら\n楽しいから不思議ね…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3263, 1, 8, 110301, 102, 1, 0, "あんたがあたしの\nなんであれ、大切な\n存在ではあるのよね…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3264, 1, 1, 110301, 1, 2, 0, "夏の海って\nすごい人よね…\n迷子にならないでよ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3265, 1, 1, 110301, 2, 2, 0, "ぼんやり海に浮かんで\n波に揺られるのも\n悪くないわよ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3266, 1, 1, 110301, 3, 2, 0, "ビーチで子どもたちと\n一緒に砂のお城を\n作りたいわ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3267, 1, 1, 110301, 4, 2, 0, "海で食べる\n焼きそばパン…\nいいかもしれないわね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3268, 1, 4, 110301, 101, 2, 0, "あたしの水着\n似合ってる？\nそう、ありがとう♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3269, 1, 8, 110301, 102, 2, 0, "あんたが居ると\nうれしい…って、\n子どもたちが\n言ってたわ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3270, 1, 1, 110301, 1, 2, 0, "朝の浜辺って\nいいわよね\n散歩しに行きたいわ", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3271, 1, 1, 110301, 2, 2, 0, "海の家のご飯って\nなんであんなに\nおいしいのかしら♪", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3272, 1, 1, 110301, 3, 2, 0, "夜の海って、\nロマンチックよね。\nずっと眺めてたいわ…", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3274, 1, 1, 110301, 2, 3, 0, "やわらかくて\n気持ちいい…\nこのまま寝ちゃいそう", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3275, 1, 1, 110301, 3, 3, 0, "海に一緒に入れる\nぬいぐるみとか\nないかしら？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3276, 1, 1, 110301, 4, 3, 0, "夏らしい\nいい香りね…\nなんの香りかしら？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3277, 1, 1, 110301, 5, 3, 0, "あったかい…\n濡れた体を乾かすには\nちょうどいいわね♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3279, 1, 1, 110301, 7, 3, 0, "泳いだ後の食事は\n普段の何倍も\nおいしく感じるわね！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3280, 1, 1, 110301, 8, 3, 0, "海に行く前にいろいろ\n調べなきゃね。備え\nあれば憂いなしよ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3282, 1, 1, 110301, 10, 3, 0, "海の守り神？\nずいぶんかわいらしい\n神様ね♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3283, 1, 1, 110301, 11, 3, 0, "このぐらい\n大きな貝があったら\n食費も浮くかしら？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3284, 1, 1, 110401, 1, 1, 0, "浜辺で食うものって\nなんであんなに\nうまいのかな～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3285, 1, 1, 110401, 2, 1, 0, "いてて…\nちょっと日焼け\nしすぎたかな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3286, 1, 1, 110401, 3, 1, 0, "砂浜で走ると\nいいトレーニングに\nなるんだぜ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3287, 1, 1, 110401, 4, 1, 0, "夏ならではの\n料理…か\n何がいいかな…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3288, 1, 1, 110401, 5, 1, 0, "やっぱりここは\n涼しくて快適だな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3289, 1, 4, 110401, 101, 1, 0, "泳ぎの練習だったら\nいつでも付き合って\nやるからな！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3290, 1, 8, 110401, 102, 1, 0, "水着で部屋の中に\nいるのって\n変な感じだな…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3291, 1, 1, 110401, 1, 2, 0, "うおっ！\nきゅ、急に\n触るなって…\nびっくりした", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3292, 1, 1, 110401, 2, 2, 0, "え、砂がついてた？\n払ってくれたのか\nありがとよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3293, 1, 1, 110401, 3, 2, 0, "夏バテしてないか？\nここで休んでけよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3294, 1, 1, 110401, 4, 2, 0, "…そう見られると\n照れるっていうか…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3295, 1, 4, 110401, 101, 2, 0, "カキ氷とか\n食いたくないか？\n氷、削ってやるよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3296, 1, 8, 110401, 102, 2, 0, "おまえと\n夏の思い出ができて\nよかったよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3297, 1, 1, 110401, 1, 2, 0, "ふう…\n朝一番で泳ぐの\nやっぱ気持ちいいな", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3298, 1, 1, 110401, 2, 2, 0, "一番あちぃ時間だ\n水分補給は\nこまめにな！", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3299, 1, 1, 110401, 3, 2, 0, "今日もお疲れ\n明日のパトロールも\nよろしくな", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3301, 1, 1, 110401, 2, 3, 0, "泳ぎ疲れたからな\n少し休憩だ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3302, 1, 1, 110401, 3, 3, 0, "か、かわいい…\nいや、なんでもねぇ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3303, 1, 1, 110401, 4, 3, 0, "この香り落ち着くな\nこういうのも\n悪くねーかも…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3304, 1, 1, 110401, 5, 3, 0, "あったけえ…！\n泳ぎ終わった後って\nけっこう体冷えてよ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3306, 1, 1, 110401, 7, 3, 0, "海で食べると\nまた一味違うよな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3307, 1, 1, 110401, 8, 3, 0, "夏のサバイバル特集…\nちょっと読んでみるか", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3309, 1, 1, 110401, 10, 3, 0, "へー！　こいつ、\nよくできてんな！\nいいなあ、実物も\n乗れたらいいのに", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3310, 1, 1, 110401, 11, 3, 0, "か、かわ…！\nいや、じゃなくて\nあ、あたしがここに\n寝ていいのか…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3311, 1, 1, 110501, 1, 1, 0, "故郷の花を飾っても\nいいかな～？\n部屋が楽しくなるよ～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3312, 1, 1, 110501, 2, 1, 0, "水着で歩いてると\n泳ぎたくなるさ～\n我慢できないさ～！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3313, 1, 1, 110501, 3, 1, 0, "夏が眩しいのは\nてぃーだがにこにこ\n笑ってるからさ～！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3314, 1, 1, 110501, 4, 1, 0, "泳ぎ疲れたら\n涼しい木陰で\nお昼寝するさ～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3315, 1, 1, 110501, 5, 1, 0, "クロールも\n犬かきも\n大得意さ～！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3316, 1, 4, 110501, 101, 1, 0, "なんだか歌いたい\n気分さ～\nキミもどう～？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3317, 1, 8, 110501, 102, 1, 0, "海の近くに住んで\nキミと歌って踊って\nそれが理想さ～！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3318, 1, 1, 110501, 1, 2, 0, "私とキミ\nどっちがたくさん\n泳げるか\n競争するさ～！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3319, 1, 1, 110501, 2, 2, 0, "キミも三線に興味が\nあるのかな～？\n私が教えるさ～！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3320, 1, 1, 110501, 3, 2, 0, "嫌なことがあっても\n私と歌って踊れば\nなんくるないさー！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3321, 1, 1, 110501, 4, 2, 0, "たまには時間を忘れて\n私とのーんびり\n海を眺めるさ～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3322, 1, 4, 110501, 101, 2, 0, "ぶるぶるぶるっ！\n毛が濡れちゃっても\nへっちゃらさ～！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3323, 1, 8, 110501, 102, 2, 0, "泳ぐ前は準備体操～！\n背中押してあげるから\nしっかりやるさ～！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3324, 1, 1, 110501, 1, 2, 0, "朝の浜辺は\nちゅらさんよ～\n一緒に歩こうよー！", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3325, 1, 1, 110501, 2, 2, 0, "お昼ご飯は\n決まったかな～？\n私はタコライスさ～！", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3326, 1, 1, 110501, 3, 2, 0, "星と波音の中で\n三線鳴らすのも\nオツなものさ～", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3328, 1, 1, 110501, 2, 3, 0, "ちょうどよかったさー\nゆっくり休んだら\nまた泳ぎに行くさ～！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3329, 1, 1, 110501, 3, 3, 0, "砂浜に飾って\nおきたいさ～\nきっとかわいいさ～！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3330, 1, 1, 110501, 4, 3, 0, "くんくん…はあ\n癒されるさ…\n故郷の夏の匂いさ～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3331, 1, 1, 110501, 5, 3, 0, "おおー…！\n濡れた髪が\nみるみる乾いてくさ～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3333, 1, 1, 110501, 7, 3, 0, "ん～！　これ、\n海で食べたら\nもっとおいしいさ～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3334, 1, 1, 110501, 8, 3, 0, "海の生物図鑑ね～\nこの前みかけた魚も\n載ってるかな～？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3336, 1, 1, 110501, 10, 3, 0, "わぁ～！　みてみて！\n背中に乗れるさー！\nキミと一緒に\n乗りたいさ～！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3337, 1, 1, 110501, 11, 3, 0, "ふかふかさ～\n海の底の\n人魚になった気分さ～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3338, 1, 12, 105801, 103, 1, 0, "あの人が\nわたしの騎士だったら\nいいのにな…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3339, 1, 12, 105801, 103, 2, 0, "あなたのおかげで\nわたしの心はいつも\nお腹いっぱいです♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3340, 1, 12, 105901, 103, 1, 0, "許されるのならば、\n今すぐにでも、\n主さまのおそばに\n行きたいです…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3341, 1, 12, 105901, 103, 2, 0, "主さま…\nお慕いしております…\nいつまでも、おそばに\n居させてください…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3342, 1, 12, 106001, 103, 1, 0, "なんか最近、\nあいつのことしか\n考えてない…\nあたし…どうしよう…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3343, 1, 12, 106001, 103, 2, 0, "そ、外に行くなら、\n連れていきなさいよね\nその…あたしが\n守ってあげるから…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3344, 1, 1, 110601, 1, 1, 0, "部屋の中は涼しくて\n気持ちええどすなぁ\n天国やわぁ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3345, 1, 1, 110601, 2, 1, 0, "クラゲはんって\nフリルいっぱいの\nドレスみたいやねぇ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3346, 1, 1, 110601, 3, 1, 0, "外でセミはんが\n元気に鳴いてはるねぇ\n今日もええ天気やわぁ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3347, 1, 1, 110601, 4, 1, 0, "こないに暑いと\n海に入って\n涼みたくなるわぁ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3348, 1, 1, 110601, 5, 1, 0, "いつか\nマホマホ王国にも\n海の家作りまひょ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3349, 1, 4, 110601, 101, 1, 0, "うちも\n付き合いますえ\n泳ぎの練習しまひょ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3350, 1, 8, 110601, 102, 1, 0, "うちの里帰り\n王子はんも一緒に\n来まへんか？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3351, 1, 1, 110601, 1, 2, 0, "王子はんは日焼けより\n色白美人の方が\n好きでっしゃろか？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3352, 1, 1, 110601, 2, 2, 0, "うちと一緒に\n浮き輪で海、\nプカプカしまへん？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3353, 1, 1, 110601, 3, 2, 0, "日が落ちたら\n線香花火\nやりたいわぁ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3354, 1, 1, 110601, 4, 2, 0, "木陰でのんびり\nお昼寝…\n最高やね～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3355, 1, 4, 110601, 101, 2, 0, "お祭ではぐれても\n王子はんなら見つけて\nくれはります？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3356, 1, 8, 110601, 102, 2, 0, "楽しい夏やわぁ\nずっとこんな日が\nつづくとええね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3357, 1, 1, 110601, 1, 2, 0, "うちにしては早起き？\n暑くて目が早う\n覚めてもうたんどす", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3358, 1, 1, 110601, 2, 2, 0, "お昼は冷たい\nおそうめんとか\nどうでっしゃろ", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3359, 1, 1, 110601, 3, 2, 0, "夜は星がきれいやね\n二人で浜辺でも\n行きまへんか？", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3361, 1, 1, 110601, 2, 3, 0, "うーん、遊び疲れた\n身体にちょうどええ\nほっこり具合やわぁ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3362, 1, 1, 110601, 3, 3, 0, "かいらしい子は\nみーんな\nマホマホ王国の\n住人どすえ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3363, 1, 1, 110601, 4, 3, 0, "この香り…\n南国にいる気分やねぇ\n癒されるわぁ～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3364, 1, 1, 110601, 5, 3, 0, "これで\n冷えた身体も\nポカポカやねぇ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3366, 1, 1, 110601, 7, 3, 0, "外はまだ暑いやろうし\nバテんよう\nしっかり食べんとね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3367, 1, 1, 110601, 8, 3, 0, "海の中にもお城が\nあるんやねぇ\n行ってみたいわぁ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3369, 1, 1, 110601, 10, 3, 0, "この子、ふかふかやね\nこのままマホマホ王国\n乗せてってくれはる？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3370, 1, 1, 110601, 11, 3, 0, "人魚姫になっても\n最後は王子はんと\nハッピーエンド\nどすえ～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3371, 1, 1, 107001, 1, 1, 0, "真似したくなる\n人がここには\n大勢いますね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3372, 1, 1, 107001, 2, 1, 0, "偽物を一人\n紛れこませました\n…冗談です", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3373, 1, 1, 107001, 3, 1, 0, "動物に変身して\nその辺りを\n散歩してきます", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3374, 1, 1, 107001, 4, 1, 0, "失った記憶は\nどの程度回復するの\nでしょうね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3375, 1, 1, 107001, 5, 1, 0, "人や物だけでなく\nいずれは世界すら\n真似てみせます", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3376, 1, 4, 107001, 101, 1, 0, "次は彼に\nどんなことを\n教えましょうか…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3377, 1, 8, 107001, 102, 1, 0, "知識はともかく\n私の力はまだ\n必要なようですね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3378, 1, 1, 107001, 1, 2, 0, "どうしました？\n…何でもない？\n…そうですか", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3379, 1, 1, 107001, 2, 2, 0, "退屈しています\n芸でもして\n楽しませなさい", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3380, 1, 1, 107001, 3, 2, 0, "研究のためです\n少し話し相手に\nなりなさい", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3381, 1, 1, 107001, 4, 2, 0, "何か真似して欲しい？\n私の能力を何だと\n思っているのですか", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3382, 1, 4, 107001, 101, 2, 0, "貴方にはまだ色々と\n教えるべきことが\nありますね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3383, 1, 8, 107001, 102, 2, 0, "随分多くの女性と\n知り合いなのですね\n驚きました", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3384, 1, 1, 107001, 1, 2, 0, "…\nおはようございます\n…まだ朝ですよ", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3385, 1, 1, 107001, 2, 2, 0, "食事にします\n…どうしました？\n早く持ってきなさい", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3386, 1, 1, 107001, 3, 2, 0, "早く寝なさい\n夜更かしすると\n背が伸びませんよ？", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3388, 1, 1, 107001, 2, 3, 0, "マサキより\n座り心地は\nいいですね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3389, 1, 1, 107001, 3, 3, 0, "意外に複雑な\n構造ですね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3390, 1, 1, 107001, 4, 3, 0, "真似るなら\n香りまで\n再現しなくては", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3391, 1, 1, 107001, 5, 3, 0, "発火現象の\n模倣…なかなか\n難しいものです", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3393, 1, 1, 107001, 7, 3, 0, "偽物や分身を作るには\nエネルギーが\n必要不可欠なのです", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3394, 1, 1, 107001, 8, 3, 0, "暇つぶしには\nちょうどよさそうです\n…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3396, 1, 1, 110701, 1, 1, 0, "学院かぁ…\n友達の作り方の\n授業はないかな？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3397, 1, 1, 110701, 2, 1, 0, "学院でぼっちに\nなったときのために、\n避難場所を\n探しておかないと…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3398, 1, 1, 110701, 3, 1, 0, "ぼっちの私が学院…\nな、なんだか\n目まいがしてきました", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3399, 1, 1, 110701, 4, 1, 0, "クラスメイトに\n話しかける練習…\nむ、難しすぎます…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3400, 1, 1, 110701, 5, 1, 0, "目指せ友達100人…\n無理かな…10人…\nや、やっぱり一人！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3401, 1, 4, 110701, 101, 1, 0, "あなたと学院生活が\n送れるなんて、\nとっても\nうれしいです！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3402, 1, 8, 110701, 102, 1, 0, "えへへ…\n学院でも、\nあなたと一緒に\n居られてうれしいな♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3403, 1, 1, 110701, 1, 2, 0, "が、学院ってあんなに\n人が居るんですね…\n友達作りのイメトレを\nやり直さないと！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3404, 1, 1, 110701, 2, 2, 0, "私にとって\n部活動とはＢＢ団！\n今日もたくさん\n活動しましょうね！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3405, 1, 1, 110701, 3, 2, 0, "休み時間は寝たふり…\nご飯はおトイレ…\nぼっち対策は完璧です", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3406, 1, 1, 110701, 4, 2, 0, "学院で友達を作る\n勉強は完璧ですよ！\n購買部でパンを買って\n渡すんですよね！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3407, 1, 4, 110701, 101, 2, 0, "学院でも、\nＢＢ団の活動を\nしていきましょうね♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3408, 1, 8, 110701, 102, 2, 0, "ぼっちに学院生活は\n厳しいですね…でも、\nあなたと一緒なら\n乗りこえられそうです", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3409, 1, 1, 110701, 1, 2, 0, "もうこんな時間…\nあの…一緒に学院に\n行ってくれませんか？", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3410, 1, 1, 110701, 2, 2, 0, "お昼ですね！\n言ってくれれば、\nパンでもお握りでも\n買ってきますよ！", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3411, 1, 1, 110701, 3, 2, 0, "今日もぼっち…\nいつか友達と一緒に、\nパジャマパーティー\nしたいなぁ…", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3413, 1, 1, 110701, 2, 3, 0, "森とは違う\n心地良さが…このまま\n眠っちゃいそうです…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3414, 1, 1, 110701, 3, 3, 0, "かわいい…この子を、\n話す練習の相手役に\nしてみようかな？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3415, 1, 1, 110701, 4, 3, 0, "いい香り…\nいつ友達ができても\nいいように、私の\n部屋にも置こうかな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3416, 1, 1, 110701, 5, 3, 0, "こ、これは…\nぼっちで\n日陰者の私には\n暖かすぎます！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3418, 1, 1, 110701, 7, 3, 0, "おいしい…！\nお弁当にして、学院に\n持っていけないかな？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3419, 1, 1, 110701, 8, 3, 0, "学院の授業に\nついていくためにも、\n勉強しておこうかな…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3421, 1, 1, 110801, 1, 1, 0, "あんた、何してんの。\nいいけど、なんでも", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3422, 1, 1, 110801, 2, 1, 0, "邪魔。どいて。\nいや怒ってないし。\nコレがフツーだから", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3423, 1, 1, 110801, 3, 1, 0, "いいけど、\n散らかしめの部屋だと\n片付けたくなるよね…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3424, 1, 1, 110801, 4, 1, 0, "暇なときは明日の\nコーデ考えてるよ。\n考えるのはタダだし", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3425, 1, 1, 110801, 5, 1, 0, "昔よくシオリン\n構いすぎてハツネに\n文句言われたなァ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3426, 1, 4, 110801, 101, 1, 0, "なんかさ。あんたって\n謎に女子トモ多いよね\n…いいけど、べつに", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3427, 1, 8, 110801, 102, 1, 0, "ねえ。あのさあ。\nあんた弁当で好きな\n具とかってあんの", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3428, 1, 1, 110801, 1, 2, 0, "あ？　何。なんか用。\nや、ないならないで\nいいけど、べつに", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3429, 1, 1, 110801, 2, 2, 0, "ごめ、ちょっと\n黙っててくんない。\n明日の試験ヤバだから", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3430, 1, 1, 110801, 3, 2, 0, "歩き方が静か？\n癖になってるんだよね\n足音を殺すの", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3431, 1, 1, 110801, 4, 2, 0, "うちの血液型？\n大雑把のОだけど。\nなんか文句あんの", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3432, 1, 4, 110801, 101, 2, 0, "ひゃんっ！\nや、だからさ…急に\n話しかけんなっての", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3433, 1, 8, 110801, 102, 2, 0, "な、何…あんまジッと\n見ないでくんない…\nなんか、その、ハズい", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3434, 1, 1, 110801, 1, 2, 0, "早起きは得意な方だと\n思うけど。昔から\nそれがデフォだったし", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3435, 1, 1, 110801, 2, 2, 0, "こんな時間か…\n昼ごはん\nサクッと作っちゃお", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3436, 1, 1, 110801, 3, 2, 0, "あ？　夜遊び？\nはッ…そんなん\n５歳で卒業したっての", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3438, 1, 1, 110801, 2, 3, 0, "あーヤバじゃんコレ…\nふかついてる場合\nじゃないっつーのに…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3439, 1, 1, 110801, 3, 3, 0, "なんだこれ\nかわいいかよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3440, 1, 1, 110801, 4, 3, 0, "ふーん。\nこの匂い…\nなんかいいじゃん", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3441, 1, 1, 110801, 5, 3, 0, "暖炉はアドじゃん。\n煮物、焼き物、蒸し物\nいろいろ作れそう", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3443, 1, 1, 110801, 7, 3, 0, "何コレ\n食べ物困んないじゃん\nうちにも一台くんない", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3444, 1, 1, 110801, 8, 3, 0, "本かァー…んー\n学校の勉強だけで\nいいかなァ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3446, 1, 1, 111101, 1, 1, 0, "イタズラってなにを\nしたらいいのかな？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3447, 1, 1, 111101, 2, 1, 0, "カップケーキ\n上手に作れました\n食べてくれるかな…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3448, 1, 1, 111101, 3, 1, 0, "あの人、\nお菓子くれるかな？\nくれなかったら…ふふ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3449, 1, 1, 111101, 4, 1, 0, "オバケ…いないよね？\n怖くなんてないけど！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3450, 1, 1, 111101, 5, 1, 0, "ハロウィンは\n楽しいけどオバケの\nお祭りなんだよね…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3451, 1, 4, 111101, 101, 1, 0, "にゃ～、にゃお～！\nちゃんと怖いかな？\n早く帰ってこないかな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3452, 1, 8, 111101, 102, 1, 0, "あの人のことだから\nお菓子欲しがるよね…\n準備はバッチリです♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3453, 1, 1, 111101, 1, 2, 0, "えへへ。猫さんのお耳\nよく出来てるでしょ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3454, 1, 1, 111101, 2, 2, 0, "触りましたね？\nイタズラしましたね？\nへんた～い！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3455, 1, 1, 111101, 3, 2, 0, "フー！　頭を撫でたら\n怒りますよ！　あれ？\n猫みたいでした？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3456, 1, 1, 111101, 4, 2, 0, "きゃっ！　も、もう、\n脅かさないでください\n怖くありませんけど！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3457, 1, 4, 111101, 101, 2, 0, "お菓子ですか？\nありがとうございます\nハッピーハロウィン♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3458, 1, 8, 111101, 102, 2, 0, "ゴロゴロ…\nね、猫さんの真似です\n…似てましたか？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3459, 1, 1, 111101, 1, 2, 0, "さぁ、お菓子作りを\n始めますよ。下準備は\n大事ですからね！", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3460, 1, 1, 111101, 2, 2, 0, "今、つまみ食い\nしましたね！？　あっ\nおやつの時間でしたか", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3461, 1, 1, 111101, 3, 2, 0, "さぁ、ハロウィンの\n始まりです…ふぁ…\nね、眠くありません！", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3463, 1, 1, 111101, 2, 3, 0, "ちょっと疲れたかも…\nパーティの時間まで\nひと休みしなきゃ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3464, 1, 1, 111101, 3, 3, 0, "私も今、こんな感じ\nなのかな？　こんなに\nかわいいといいなぁ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3465, 1, 1, 111101, 4, 3, 0, "甘い匂いがいっぱい…\nお菓子の香りかな？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3466, 1, 1, 111101, 5, 3, 0, "温かくなると、\n丸まりたくなって…\n…猫になっちゃう！？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3468, 1, 1, 111101, 7, 3, 0, "ニンジンもこれくらい\nおいしければ\nいいのに…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3469, 1, 1, 111101, 8, 3, 0, "ハロウィンの本、\nオバケがたくさん\n載ってて怖いかも…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3471, 1, 1, 111101, 12, 3, 0, "美味しそうなお菓子が\nいっぱい…これなら\nイタズラされませんね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3473, 1, 1, 111201, 1, 1, 0, "たくさんいたずら\nできるから\nハロウィン大好き！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3474, 1, 1, 111201, 2, 1, 0, "ハロウィンだから\nいつもよりすごい\nいたずらしたいな～！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3475, 1, 1, 111201, 3, 1, 0, "カボチャを綺麗に\nくり抜いてすごく怖い\nランタン作っちゃお！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3476, 1, 1, 111201, 4, 1, 0, "お菓子がたくさんで\nどれから食べるか\n迷っちゃうな～！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3477, 1, 1, 111201, 5, 1, 0, "どうすればみんなを\nびっくりさせられる\nかな～？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3478, 1, 4, 111201, 101, 1, 0, "カボチャ見てたら\nお腹空いて来たな～\nお菓子食べちゃお♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3479, 1, 8, 111201, 102, 1, 0, "にいちゃんには\n特別ないたずらを\n用意してあげるね！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3480, 1, 1, 111201, 1, 2, 0, "カボチャにクラッカー\nを仕掛けて…わっ！？\nみ、見ちゃダメだよ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3481, 1, 1, 111201, 2, 2, 0, "ん？　なぁに？\nお菓子ほしいの？\nもう、しかたないな～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3482, 1, 1, 111201, 3, 2, 0, "にいちゃんの\n面白い仮装が\n見てみたいな～！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3483, 1, 1, 111201, 4, 2, 0, "お菓子くれたお礼に\nとびっきりの\nいたずらしちゃうね！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3484, 1, 4, 111201, 101, 2, 0, "にいちゃんは\nハロウィンでも最高の\nいたずら相手だよ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3485, 1, 8, 111201, 102, 2, 0, "にいちゃんと一緒に\nハロウィンパレード\n歩きたいな～！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3486, 1, 1, 111201, 1, 2, 0, "おはようにいちゃん！\n仮装して出かける\n準備しなきゃ！", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3487, 1, 1, 111201, 2, 2, 0, "お腹すいたな～\nもらったお菓子\n食べちゃだめかな？", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3488, 1, 1, 111201, 3, 2, 0, "たくさんいたずらして\nもうクタクタ～\nミソギもう寝るね…", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3490, 1, 1, 111201, 2, 3, 0, "やわらか～い…\nここでひと休みして\nまたいたずらしよう！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3491, 1, 1, 111201, 3, 3, 0, "かわいいもので\n油断させてわっ！\nってするのどうかな？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3492, 1, 1, 111201, 4, 3, 0, "いいにお～い！\nいたずらのアイデアが\nわいてくるよ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3493, 1, 1, 111201, 5, 3, 0, "もらったお菓子\nあったかくしたら\nおいしいかな～？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3495, 1, 1, 111201, 7, 3, 0, "お菓子ばっかりじゃ\n飽きちゃうもんね！\nご飯も食べよう！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3496, 1, 1, 111201, 8, 3, 0, "本のなかに\nいたずらのヒント\nないかな～？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3498, 1, 1, 111201, 12, 3, 0, "お菓子がたくさん！\n一人で食べていいの？\nやっぱりだめか～！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3500, 1, 12, 101101, 103, 1, 0, "お勉強は順調ですよ\nやっぱりお兄ちゃんの\nおかげなんでしょうか", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3501, 1, 12, 101101, 103, 2, 0, "お兄ちゃんとの\n思い出の場所\nまた行きましょうね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3502, 1, 1, 111301, 1, 1, 0, "えへへ～♪\nハロウィンの\nミイラさんうさぎさん\nぴょんぴょ～ん！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3503, 1, 1, 111301, 2, 1, 0, "るんたった～\nハロウィンハロウィン\n楽しいな～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3504, 1, 1, 111301, 3, 1, 0, "ハロウィンって\nどんなお菓子を\nもらえるのかな～？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3505, 1, 1, 111301, 4, 1, 0, "お菓子をもらう\n練習をしなくちゃ！\nトリックオア\nトリート～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3506, 1, 1, 111301, 5, 1, 0, "おにいちゃんと\nハロウィンするの\nミミ楽しみだな～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3507, 1, 4, 111301, 101, 1, 0, "おにいちゃんと\nいっしょにお菓子を\n食べたいな～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3508, 1, 8, 111301, 102, 1, 0, "おにいちゃんと\n毎日ハロウィン\nできたらいいな～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3509, 1, 1, 111301, 1, 2, 0, "えへへ～\nおにいちゃん！\nハッピーハロウィン♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3510, 1, 1, 111301, 2, 2, 0, "おにいちゃんも\nいっしょに\nハロウィンパレード\nしようね～！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3511, 1, 1, 111301, 3, 2, 0, "ミイラさんうさぎさん\nだぞ～…えへへ\nミミこわかったかな？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3512, 1, 1, 111301, 4, 2, 0, "おにいちゃん！\nハロウィンのお菓子\nおいしいね～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3513, 1, 4, 111301, 101, 2, 0, "おにいちゃんと\nいっしょなら\nおばけさんも\nこわくないよ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3514, 1, 8, 111301, 102, 2, 0, "ミミね\nハロウィンも\nおにいちゃんも\nだ～いすき♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3515, 1, 1, 111301, 1, 2, 0, "おひさまさんも\nハロウィンの\nおようふく\n着たいかな～？", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3516, 1, 1, 111301, 2, 2, 0, "ねえねえおにいちゃん\nお昼ごはんはもらった\nお菓子でもい～い？", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3517, 1, 1, 111301, 3, 2, 0, "まだハロウィン…？\nうぅん…眠くないよぅ\nでもちょっと…ふぁ～", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3519, 1, 1, 111301, 2, 3, 0, "ふかふか～♪\nおばけさんが\n中にいるのかな～？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3520, 1, 1, 111301, 3, 3, 0, "わぁ、かわいい～♪\nミミといっしょに\nあそぼ～！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3521, 1, 1, 111301, 4, 3, 0, "ふわぁ～\nミミ、このにおい\nだ～いすき♪", 0);
