INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3534, 1, 4, 111401, 101, 1, 0, "おにいさん！\n今日は何して遊ぶの？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3535, 1, 8, 111401, 102, 1, 0, "あのね、\nお料理教えてほしい！", 0);
