INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3549, 1, 1, 111401, 5, 3, 0, "あったかいねー", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3551, 1, 1, 111401, 7, 3, 0, "ふふっ\nおいしいね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3552, 1, 1, 111401, 8, 3, 0, "ルナ、\n眠くなっちゃう…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3554, 1, 12, 105201, 103, 1, 0, "えへへ。あの人と\nランドソルでデート\nできたらうれしいな♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3555, 1, 12, 105201, 103, 2, 0, "あなたとなら\nどんなことだって\nできる気がするわ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3556, 1, 1, 106501, 1, 1, 0, "あーあ、退屈だな\nカレでも探しに\nいこうかな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3557, 1, 1, 106501, 2, 1, 0, "この前のケンカは\n最高に燃えたなぁ\n…拳が疼いてきたぜ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3558, 1, 1, 106501, 3, 1, 0, "一体、この世界は\nどうなっちまったって\nいうんだ…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3559, 1, 1, 106501, 4, 1, 0, "もう仕事の時間か\nボスも人使いが荒いぜ\n…ま、いいけどさ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3560, 1, 1, 106501, 5, 1, 0, "オレたち以外に\nドラゴン族はあと何人\nいるんだろうな…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3561, 1, 4, 106501, 101, 1, 0, "カレの真剣な顔\n面白かったな\nまた遊びたいぜ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3562, 1, 8, 106501, 102, 1, 0, "どうしてカレがそばに\nいると、腹の底から\n力が湧いてくるんだ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3563, 1, 1, 106501, 1, 2, 0, "よっす！\nなんかオイシイ話でも\nあったりしない？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3564, 1, 1, 106501, 2, 2, 0, "なあ、オレと遊ぼうぜ\n心配すんなよ。危ない\nことはよしとくからさ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3565, 1, 1, 106501, 3, 2, 0, "どうした。オレに\nケンカ売ろうってか？\n…ははは、冗談だよ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3566, 1, 1, 106501, 4, 2, 0, "ちょうど退屈してた\nところだ、特訓に\n付き合ってくれよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3567, 1, 4, 106501, 101, 2, 0, "ったくよぉ…\nオレがかわいいなんて\n冗談はもうよせよ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3568, 1, 8, 106501, 102, 2, 0, "次の仕事も一緒に\n来るだろ？　断っても\n連れて行くけどな！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3569, 1, 1, 106501, 1, 2, 0, "おはよう！　今日の\n予定はまずケンカして\nで、その後もケンカだ", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3570, 1, 1, 106501, 2, 2, 0, "よっす！\n腹ごしらえが済んだら\nオレと遊ぼうぜ！", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3571, 1, 1, 106501, 3, 2, 0, "夜は危ないから\nあんま出歩くな？\n…だから面白えんだろ", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3573, 1, 1, 106501, 2, 3, 0, "次のケンカに備えて\n今は休んでおくか…\n全力で暴れたいからな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3574, 1, 1, 106501, 3, 3, 0, "ボスの飼ってる\nラーゴが、中に\n入ってるんじゃ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3575, 1, 1, 106501, 4, 3, 0, "ああ、たぎる匂いが\nする…ひと暴れ\nしたくなってきた！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3576, 1, 1, 106501, 5, 3, 0, "おおぉ…\nオレの拳もなんだか\n熱くなってきたぜ…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3578, 1, 1, 106501, 7, 3, 0, "いつでも全力で\nケンカするために\n力を蓄えないとな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3579, 1, 1, 106501, 8, 3, 0, "やっぱりな…オレが\n知ってる地図とは\nまるで違ってやがる", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3581, 1, 1, 111501, 1, 1, 0, "さぁプレゼントで\n埋め尽くしてやろう！\n大いに喜ぶがいいよ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3582, 1, 1, 111501, 2, 1, 0, "メリークリスマス☆\n聖夜の宴だ\n派手に楽しむとしよう", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3583, 1, 1, 111501, 3, 1, 0, "煙突から入る？\nいいや、ワタシは\nいつでも正面からさ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3584, 1, 1, 111501, 4, 1, 0, "サンタクロースの\nお出ましだ！\n歓迎するがいいよ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3585, 1, 1, 111501, 5, 1, 0, "飾り付けが足りん！\nワタシが演出して\nやろうじゃないか！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3586, 1, 4, 111501, 101, 1, 0, "人の喜ぶ顔を見るのも\n悪くはないな…おっと\nワタシらしくないな☆", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3587, 1, 8, 111501, 102, 1, 0, "プレゼントはまだか？\nワタシを喜ばせたら\nお返しは弾むぞ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3588, 1, 1, 111501, 1, 2, 0, "ワタシを呼んだか？\n退屈な要件だったら\n覚悟しておくことだ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3589, 1, 1, 111501, 2, 2, 0, "なんだ？\nワタシへの挑戦か？\nよし、受けた♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3590, 1, 1, 111501, 3, 2, 0, "おっと、当たらんよ\nワタシの乱数聖域は\n絶対だからな…♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3591, 1, 1, 111501, 4, 2, 0, "なんだ、オマエも\n暇なのか？\n奇遇だな、ワタシもさ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3592, 1, 4, 111501, 101, 2, 0, "なにか愉しいことでも\nしようじゃないか\nさぁ、何をする？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3593, 1, 8, 111501, 102, 2, 0, "こうもワタシに\n気安く触れるのは\nオマエくらいだよ…♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3594, 1, 1, 111501, 1, 2, 0, "朝だろうと関係ない！\nさぁ愉しく苛烈に\n騒ごうじゃないか♪", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3595, 1, 1, 111501, 2, 2, 0, "調子の出てくる時間だ\nしっかりワタシを\n愉しませてくれよ♪", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3596, 1, 1, 111501, 3, 2, 0, "眠いなどと言わせんぞ\nまだまだ一日は\n終わらんからな！", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3598, 1, 1, 111501, 2, 3, 0, "いい具合じゃないか\nこのまま持って帰って\nしまおうか…♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3599, 1, 1, 111501, 3, 3, 0, "なかなか愛らしい\n造形をしているな？\nワタシには負けるがな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3600, 1, 1, 111501, 4, 3, 0, "ほう、悪くない香りだ\n戦いの匂いをうまく\n隠してくれそうだ…♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3601, 1, 1, 111501, 5, 3, 0, "体が温まってくると\nひと暴れしたくなるな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3603, 1, 1, 111501, 7, 3, 0, "ワタシの舌は厳しいぞ\n…ふーむ、なるほど\n認めざるをえんな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3604, 1, 1, 111501, 8, 3, 0, "クリスティーナさんは\n知識の収集も\n欠かさないんだよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3606, 1, 1, 111501, 14, 3, 0, "これは驚いたな\nだがもっと、もっとだ\nワタシを退屈させるな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3607, 1, 1, 111501, 15, 3, 0, "こんな戯れだって\n軽くこなしてしまう\nクリスティーナさんさ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3608, 1, 1, 111601, 1, 1, 0, "メリークリスマス！\nさあ、パーティーを\n盛り上げるよ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3609, 1, 1, 111601, 2, 1, 0, "衣装一つで全然\n気分が違うね！\n最高のクリスマスに\nなりそうだよ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3610, 1, 1, 111601, 3, 1, 0, "ツリーのてっぺんの\n星って、なんだか\n欲しくなるんだよね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3611, 1, 1, 111601, 4, 1, 0, "めいっぱい\nクリスマスを楽しむよ\n遊びだって本気\nなんだからね！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3612, 1, 1, 111601, 5, 1, 0, "赤、白、緑、金\nクリスマスカラー\nってお洒落だよね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3613, 1, 4, 111601, 101, 1, 0, "雪、降らないかな？\nホワイトクリスマス\nってちょっと\n憧れるなぁ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3614, 1, 8, 111601, 102, 1, 0, "キミと過ごすこの\n楽しい時間がずーっと\n続けばいいのにな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3615, 1, 1, 111601, 1, 2, 0, "クリスマスライブ\nチケット用意するから\n絶対来てよね！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3616, 1, 1, 111601, 2, 2, 0, "カンパーイ！\nジュースだけど\nちょっと大人の\n気分だね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3617, 1, 1, 111601, 3, 2, 0, "クリスマス衣装どう？\nかわいいでしょ\nあっ、どこ見てるの！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3618, 1, 1, 111601, 4, 2, 0, "ねえ、後で街に\n行こうよ\n大きなツリーを\n一緒に見たいんだ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3619, 1, 4, 111601, 101, 2, 0, "ねえ、ダンスの練習に\n付き合ってくれない？\n大丈夫、手取り足取り\n教えてあげるから♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3620, 1, 8, 111601, 102, 2, 0, "来年のクリスマスも\n隣にキミが\nいてくれたら\nうれしいのにな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3621, 1, 1, 111601, 1, 2, 0, "クリスマスって朝から\nドキドキしちゃうね\nなんだか子どもみたい", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3622, 1, 1, 111601, 2, 2, 0, "パーティーの準備も\n大分進んだね\n一息ついて\nランチにしよっか", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3623, 1, 1, 111601, 3, 2, 0, "静かな夜だね\n心臓の音まで\n聞こえちゃいそう…", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3625, 1, 1, 111601, 2, 3, 0, "あ～、この絶妙な\n柔らかさ…\nいい仕事してるなぁ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3626, 1, 1, 111601, 3, 3, 0, "アイドル顔負けの\nかわいさ、ズルい！\nでもかわいい！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3627, 1, 1, 111601, 4, 3, 0, "ん～、落ち着くなぁ\n今日はゆっくり\n眠れそうだよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3628, 1, 1, 111601, 5, 3, 0, "ぬくぬく～\nどうしよう、ここから\n動きたくないなぁ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3630, 1, 1, 111601, 7, 3, 0, "ダンスの練習もしたし\nちょっとくらいなら\nいいよね！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3631, 1, 1, 111601, 8, 3, 0, "アイドルといえど\nかわいいだけじゃダメ\n知識も必要だよね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3633, 1, 1, 111601, 14, 3, 0, "面白ーい！\nこの演出、ライブでも\nできないかな？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3634, 1, 1, 111601, 15, 3, 0, "もふもふ～♪\n寒い日には最高の\n抱き心地だね！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3635, 1, 12, 101801, 103, 1, 0, "彼ともっとお話し\nしたいな…もう少し\n補習を増やそうかしら", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3636, 1, 12, 101801, 103, 2, 0, "君のこともっともっと\n教えて欲しいな。私に\n補習授業してくれる？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3637, 1, 1, 111701, 1, 1, 0, "この姿ならあやつも\n子ども扱いできまい！\n…じゃがなぜか、少し\n物足りないような…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3638, 1, 1, 111701, 2, 1, 0, "時間もあるし、\n聖夜の宴の\n準備をするかのう♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3639, 1, 1, 111701, 3, 1, 0, "…わらわの知る聖夜と\n随分違うが、宴は宴\n楽しませて\nもらおうかのう♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3640, 1, 1, 111701, 4, 1, 0, "大人の姿で\nできること…ふむ\n少しあやつを\nからかってやるか♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3641, 1, 1, 111701, 5, 1, 0, "…クリスマスは\n恋人たちの夜！？\nち、近頃の若いもんは\n破廉恥なのじゃ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3642, 1, 4, 111701, 101, 1, 0, "…あやつがおらんと、\n宴も物足りぬな\n…馬鹿者が\n早く帰ってこい…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3643, 1, 8, 111701, 102, 1, 0, "誰も見ておらぬな…？\n…よし、あやつへの\nプレゼントを\n準備するとしようか！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3644, 1, 1, 111701, 1, 2, 0, "クリスマスの\nチキンにケーキ…\nべ、別に楽しみになど\nしておらぬぞ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3645, 1, 1, 111701, 2, 2, 0, "この姿では、わらわの\n頭をなでるのは\n難しかろう…べ、\n別に残念ではないわ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3646, 1, 1, 111701, 3, 2, 0, "メリー\nクリスマスじゃ！\nさあ、わらわのために\n宴の準備をするのじゃ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3647, 1, 1, 111701, 4, 2, 0, "そんなにこの衣装が\n気になるのか？\n…だったら、直に\n触ってみるがよい♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3648, 1, 4, 111701, 101, 2, 0, "いつもと逆に、お主を\n子ども扱いしてやるか\nほれ、膝枕をしてやる\nからそばに来い♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3649, 1, 8, 111701, 102, 2, 0, "…せ、聖夜の予定は\n空けておくのじゃぞ？\nなんでって…\nな、なんでもじゃ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3650, 1, 1, 111701, 1, 2, 0, "もう朝か…聖夜の宴が\n楽しみで…ではなく！\n準備のせいで、\nあまり眠れんかったぞ", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3651, 1, 1, 111701, 2, 2, 0, "…ずっと雪が\n降っておれば、\n陽光が強いこの時間も\n怯えずにすむのにのう", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3652, 1, 1, 111701, 3, 2, 0, "時は来た…\nさあ眷属よ！\nわらわと共に、\n聖夜の宴を楽しもうぞ", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3654, 1, 1, 111701, 2, 3, 0, "良い椅子ではないか\nここから聖夜の宴を\n眺めるのもよいかもな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3655, 1, 1, 111701, 3, 3, 0, "か、かわい…こほん\nなかなか良い\nデザインをしておるな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3656, 1, 1, 111701, 4, 3, 0, "ふむ、良い香りじゃ\n聖夜の宴でも\nこれを使うとしよう", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3657, 1, 1, 111701, 5, 3, 0, "これが癒し…はっ！\nあやうく浄化される\nところであった…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3659, 1, 1, 111701, 7, 3, 0, "ニ、ニンニクは\n入っておらぬよな？\n…うむ、美味じゃ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3660, 1, 1, 111701, 8, 3, 0, "聖夜は恋人たちの夜…\n…き、気になるわけ\nではないが、\n少し調べてみるか", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3662, 1, 1, 111701, 14, 3, 0, "ほう…これは面白い\n魔法の使い方じゃな\nまるで本物の\n演劇のようじゃ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3663, 1, 1, 111701, 15, 3, 0, "こ、この程度の\nかわいさに誘惑される\nわらわでは…\nふわもこなのじゃ～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3664, 1, 1, 112001, 1, 1, 0, "おせち料理も\n魔物尽くしだったり\nしないわよね！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3665, 1, 1, 112001, 2, 1, 0, "初詣で何をお祈り\nしようかしら…\n定番でいいわよね？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3666, 1, 1, 112001, 3, 1, 0, "初日の出って\n特別な感じがして\nいいわよね…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3667, 1, 1, 112001, 4, 1, 0, "寒い中並んで飲む\n甘酒って不思議なほど\nおいしいわよね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3668, 1, 1, 112001, 5, 1, 0, "羽根つきで勝負…\nあいつにだったら\n勝てるかしら？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3669, 1, 4, 112001, 101, 1, 0, "年賀状になんて書こう\n…いろいろと\n恥ずかしくなるのよね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3670, 1, 8, 112001, 102, 1, 0, "今年もあいつと\n居られること…\n結構うれしいわ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3671, 1, 1, 112001, 1, 2, 0, "何じっと\n見てんのよ？\nお年玉はあげないわよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3672, 1, 1, 112001, 2, 2, 0, "おせち料理一緒に\n食べる？\n結構おいしいわよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3673, 1, 1, 112001, 3, 2, 0, "羽根つきで負けたら\n顔に墨塗られるのよ\n覚悟しなさい！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3674, 1, 1, 112001, 4, 2, 0, "門松飾るから\nちょっと手伝って\nくれる？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3675, 1, 4, 112001, 101, 2, 0, "あんたと一緒に\n初詣行くのも悪くない\n…かも", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3676, 1, 8, 112001, 102, 2, 0, "初詣で何をお祈り\nしたか？　うっさい！\n内緒よ！内緒！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3677, 1, 1, 112001, 1, 2, 0, "もう朝なの…\n正月くらいゆっくり\n寝ていたいわよね…", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3678, 1, 1, 112001, 2, 2, 0, "おせちに飽きたら\n次は何を食べよう\nかしら…", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3679, 1, 1, 112001, 3, 2, 0, "そろそろ寝ましょ\nいい初夢が見られると\nいいわね", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3681, 1, 1, 112001, 2, 3, 0, "この格好でいるのも\nなかなか疲れるわね\n休憩～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3682, 1, 1, 112001, 3, 3, 0, "か、かわいい…う～ん\n鏡餅も顔を描けば\nかわいくなるかしら？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3683, 1, 1, 112001, 4, 3, 0, "いい匂いね\n落ち着いて\n眠れそう…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3684, 1, 1, 112001, 5, 3, 0, "ここでお餅焼いたら\n一段とおいしく\nなりそうね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3686, 1, 1, 112001, 7, 3, 0, "正月に食べるものは\nなんでもおいしく\n感じちゃうわね！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3687, 1, 1, 112001, 8, 3, 0, "魔物を使わない\nおせちを\n調べておかなきゃ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3689, 1, 1, 112101, 1, 1, 0, "新年を気持ちよく\n過ごせるように\nお掃除がんばります！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3690, 1, 1, 112101, 2, 1, 0, "神殿に通っていたら\nいつの間にか足腰が\n鍛えられたような…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3691, 1, 1, 112101, 3, 1, 0, "神殿付近は屋台も\nあるので、良い香りが\n漂ってくるんですよ～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3692, 1, 1, 112101, 4, 1, 0, "おせちを作れるように\nなれば、子どもたちも\n喜んでくれるかな…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3693, 1, 1, 112101, 5, 1, 0, "コホォォォォォ…！\nふぅ、深呼吸すると\n落ち着きますね♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3694, 1, 4, 112101, 101, 1, 0, "お仕事以外の時も\nあなたと…い、いえ！\nなんでもないです！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3695, 1, 8, 112101, 102, 1, 0, "あなたと一緒だと\nなんだかポカポカ\nしてくるんです…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3696, 1, 1, 112101, 1, 2, 0, "この格好をしていると\nなんだか神殿にいる\n気分になりますね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3697, 1, 1, 112101, 2, 2, 0, "そういえば神殿で\n甘酒をもらったんです\nあなたも飲みますか？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3698, 1, 1, 112101, 3, 2, 0, "あっ、髪飾りが\nズレちゃってました！\nいけないいけない…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3699, 1, 1, 112101, 4, 2, 0, "ひゃわっ！？\nく、くすぐったり\nしないでください～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3700, 1, 4, 112101, 101, 2, 0, "そ、そんなに\n見つめられると\nて、照れちゃいます…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3701, 1, 8, 112101, 102, 2, 0, "今度、私が考えた\n運気上昇のおまじない\nかけてあげますね♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3702, 1, 1, 112101, 1, 2, 0, "おはようございます\n神殿のお手伝いに行く\n準備もしなきゃですね", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3703, 1, 1, 112101, 2, 2, 0, "お昼になりましたし\nご飯は私が作…\nえっ、ダメですか！？", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3704, 1, 1, 112101, 3, 2, 0, "明日も早いですし\nそろそろ寝る支度を…\nふわぁ…", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3706, 1, 1, 112101, 2, 3, 0, "お掃除も終わったので\n少し休憩を…ふわぁ…\n急に眠気が…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3707, 1, 1, 112101, 3, 3, 0, "新年を気持ちよく\n過ごせるようこの子も\nきれいにしましょう♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3708, 1, 1, 112101, 4, 3, 0, "神殿のお香のような\n香りだと落ち着いて\n過ごせそうですよね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3709, 1, 1, 112101, 5, 3, 0, "ぽかぽかですね～…\n甘酒も飲んで体の\n芯から温めましょう", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3711, 1, 1, 112101, 7, 3, 0, "今年こそ\n料理も上手に…\nもぐもぐもぐもぐ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3712, 1, 1, 112101, 8, 3, 0, "失敗続きだと\n逆に運が良くなる…\nほ、本当ですかぁ…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3714, 1, 12, 103401, 103, 1, 0, "キミにならもう\nどんな私を見られても\n大丈夫かな…♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3715, 1, 12, 103401, 103, 2, 0, "なになにどしたのぉ？\nお姉さんが撫でたげる\nこっちにきなさぁい♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3716, 1, 1, 111901, 1, 1, 0, "わたくしの舞を\n主さまは喜んで\nくださるでしょうか…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3717, 1, 1, 111901, 2, 1, 0, "年を越したあとに\n食べるおそばも\nおいしいです", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3718, 1, 1, 111901, 3, 1, 0, "甘酒を飲むと\n心まで温まる\n気がいたします", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3719, 1, 1, 111901, 4, 1, 0, "主さまのために\n真心を込めたおせちを\nご用意しなくては", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3720, 1, 1, 111901, 5, 1, 0, "おみくじを引くのは\nドキドキしつつも\n楽しいものです", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3721, 1, 4, 111901, 101, 1, 0, "主さまの温もりは\n心まで温めてくれる\nようです", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3722, 1, 8, 111901, 102, 1, 0, "うまくできなくとも\n心を込めて精いっぱい\n舞いましょう", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3723, 1, 1, 111901, 1, 2, 0, "主さま、この晴れ着は\n似合っている\nでしょうか？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3724, 1, 1, 111901, 2, 2, 0, "お餅はゆっくりと\n召し上がって\nくださいまし", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3725, 1, 1, 111901, 3, 2, 0, "主さまのお顔の\n福笑いなら、完璧に\nできる気がいたします", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3726, 1, 1, 111901, 4, 2, 0, "主さまはどのような\n初夢をご覧になった\nのでしょう？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3727, 1, 4, 111901, 101, 2, 0, "甘酒の飲み過ぎは\nいけませんよ？\nめっ、でございます", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3728, 1, 8, 111901, 102, 2, 0, "二人羽織とは\nあんなにも暖かい\nものなのですね…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3729, 1, 1, 111901, 1, 2, 0, "おはようございます\n寒い季節の朝日は\nとても美しいですね", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3730, 1, 1, 111901, 2, 2, 0, "おいしくとも\n食べ過ぎは正月太りの\nもとでございます", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3731, 1, 1, 111901, 3, 2, 0, "いい夢が見られるよう\n暖かくして\n眠るといたしましょう", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3733, 1, 1, 111901, 2, 3, 0, "ふかふか…\nこのまま\n寝てしまいそうです", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3734, 1, 1, 111901, 3, 3, 0, "あなたと一緒に\nお正月遊びを\n楽しんでみたいです", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3735, 1, 1, 111901, 4, 3, 0, "神殿の独特な香りを\nおうちに持ち込むのは\nどうでしょうか", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3736, 1, 1, 111901, 5, 3, 0, "風邪をひきませんよう\n暖かくいたしましょう", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3738, 1, 1, 111901, 7, 3, 0, "食べ過ぎは\n正月太りの\nもとだというのに…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3739, 1, 1, 111901, 8, 3, 0, "主さまのために\n参拝の作法を\n予習しておきましょう", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3741, 1, 1, 112201, 1, 1, 0, "…この杖、変身前には\n無いパーツがあるけど\nこれはどこから…うん\n考えないようにしよう", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3742, 1, 1, 112201, 2, 1, 0, "フ、フリフリの\nかわいい衣装…はっ！\n素に戻ってはダメだ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3743, 1, 1, 112201, 3, 1, 0, "魔法探偵\nミスティ★カスミ！\n魔法の力で捜査開始！\n…練習終わりっと", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3744, 1, 1, 112201, 4, 1, 0, "怪しい視線を感じる…\nまさか、近くに\n闇の力を持つ人が！？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3745, 1, 1, 112201, 5, 1, 0, "時間もあるし、\n魔法少女の修行を…\nじゃ、じゃなくて！\n探偵の仕事をしよう！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3746, 1, 4, 112201, 101, 1, 0, "助手くんとずっと\n一緒に居られるし、\n魔法少女も\n案外悪くないかも♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3747, 1, 8, 112201, 102, 1, 0, "魔法少女の訓練のため\n助手くんの所に行こう\nかな…あ、あくまで\n訓練のためにね！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3748, 1, 1, 112201, 1, 2, 0, "…髪型が急に\n変わって驚いた？\nき、気になるなら…\n少し触ってみるかい？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3749, 1, 1, 112201, 2, 2, 0, "助手くんは魔法少女に\nなりたいのかい？\nその場合は魔法騎士…\nいや、魔法助手とか？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3750, 1, 1, 112201, 3, 2, 0, "頭に浮かぶ呪文…\n強制変身…助手くん\n魔法少女とは\n呪いか何かなのかな？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3751, 1, 1, 112201, 4, 2, 0, "あ、あまり触らないで\nくれ…魔法少女になる\nと感覚が鋭くなって、\n耳も敏感に…ひゃう！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3752, 1, 4, 112201, 101, 2, 0, "や、やっぱりこの\n格好は変かな…え、\nかわいい？　じゃあ…\nいや、でも、う～ん", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3753, 1, 8, 112201, 102, 2, 0, "…この変身は助手くん\nのせいでもあるんだよ\nだから…せ、責任を\n取ってもらうからね！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3754, 1, 1, 112201, 1, 2, 0, "おはよう助手くん\nさあ、そろそろ\n街のパトロールに\n行こうじゃないか！", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3755, 1, 1, 112201, 2, 2, 0, "もうお昼…助手くん、\nそろそろ本業に戻ろう\n…その顔は何かな？\n私の本業は探偵だぞ！", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3756, 1, 1, 112201, 3, 2, 0, "夜だけど、私は\nもう少し仕事をするよ\n魔法少女の強い体は\nこんなときに便利だね", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3758, 1, 1, 112201, 2, 3, 0, "気持ちいい…\nこの癒しを、魔法と\n論理の二つの観点から\n解明したいね…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3759, 1, 1, 112201, 3, 3, 0, "ダ、ダメ！！\nここで感情のままに\n飛びついたら、私の\nイメージがぁ～！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3760, 1, 1, 112201, 4, 3, 0, "心が安らぐ香りだね…\n…今なら、魔法少女も\nすんなりと受け入れ…\nてはダメだっ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3761, 1, 1, 112201, 5, 3, 0, "あったかい…\n魔法少女だろうと\n休息は必要だし、少し\n休んでいこうかな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3763, 1, 1, 112201, 7, 3, 0, "魔法少女も探偵も、\nやっぱり体が資本！\n食べられるときに\n食べておかないとね♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3764, 1, 1, 112201, 8, 3, 0, "…魔法少女\nセリイちゃんの本は\nどこにあるのかな？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3766, 1, 1, 112301, 1, 1, 0, "魔法少女かぁ…\nせっかくだし思う存分\n体を動かしてみたいな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3767, 1, 1, 112301, 2, 1, 0, "ニャットちゃん…\nこの格好を見て\n私って気づくかな？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3768, 1, 1, 112301, 3, 1, 0, "ふふ…\n今日はなんだか\n調子がいいかも", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3769, 1, 1, 112301, 4, 1, 0, "魔法は便利だけど\nあまり頼りすぎちゃ\nダメだよね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3770, 1, 1, 112301, 5, 1, 0, "さっき街で小さな子に\n声かけられちゃった\n照れるけどうれしいな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3771, 1, 4, 112301, 101, 1, 0, "あの人がいると\nなんだかホッとする\n不思議…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3772, 1, 8, 112301, 102, 1, 0, "魔法少女になっても\nあの人への気持ちは\nずっと同じ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3773, 1, 1, 112301, 1, 2, 0, "今日の私…ですか？\nはいとっても元気です\n…あなたのおかげかも", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3774, 1, 1, 112301, 2, 2, 0, "よかったら魔法の\n練習に付き合って\nくれませんか？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3775, 1, 1, 112301, 3, 2, 0, "あの…\nこの格好どうですか？\n似合ってると…いいな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3776, 1, 1, 112301, 4, 2, 0, "…な、なんですか？\nあまり見られると\n恥ずかしいです…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3777, 1, 4, 112301, 101, 2, 0, "私、あなたのそばに\nいられたら、それで…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3778, 1, 8, 112301, 102, 2, 0, "想い人と結ばれる\n魔法は…ひゃっ！？\n今の聞いてましたか？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3779, 1, 1, 112301, 1, 2, 0, "おはようございます\n今日も街の平和の\nために頑張らなきゃ", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3780, 1, 1, 112301, 2, 2, 0, "お腹すきましたね。\nあの…一緒にランチは\nいかがですか？", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3781, 1, 1, 112301, 3, 2, 0, "あ、もうこんな時間…\nふふ、まだこんなに\n体力が余ってるのに", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3783, 1, 1, 112301, 2, 3, 0, "気持ちいい…\nこの気持ち良さは\nどんな魔法も勝てない", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3784, 1, 1, 112301, 3, 3, 0, "何これ、かわいい…\nお姉ちゃんにも\n見せてあげたいな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3785, 1, 1, 112301, 4, 3, 0, "ん～、いい香り\n…これ、魔法で\n出せたりしないかな？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3786, 1, 1, 112301, 5, 3, 0, "あったか～い\nえへへ…なんだか\nお姉ちゃんみたい", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3788, 1, 1, 112301, 7, 3, 0, "おいしいなぁ、これ\n魔法少女になってから\n食欲旺盛かも…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3789, 1, 1, 112301, 8, 3, 0, "ふふ…\n魔法少女になっても\n読書はやめられないな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3791, 1, 12, 101001, 103, 1, 0, "みらくるまほりん\nくるりんぱ～♪\n王子はんのために\n花嫁修業や～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3792, 1, 12, 101001, 103, 2, 0, "あ、あのな王子はん\nできたらその…\nお姫さまだっこを\nお願いしてもええ…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3793, 1, 1, 180401, 1, 1, 0, "やっぱりわたしたちの\nギルドハウスの\n居心地は最高ですね☆", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3794, 1, 1, 180401, 2, 1, 0, "王宮じゃできないこと\nやっちゃいましょう♪\n例えば、えっと～…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3795, 1, 1, 180401, 3, 1, 0, "そうだ、隠しておいた\nおやつがありました！\nまだ食べられますね☆", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3796, 1, 1, 180401, 4, 1, 0, "ごはんはみんなで\n楽しく食べましょう！\nそれが一番の\n調味料です♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3797, 1, 1, 180401, 5, 1, 0, "やっぱりここが、\nわたしの帰る場所です\n…ただいま☆", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3798, 1, 4, 180401, 101, 1, 0, "なんだか物足りません\nきっと、あの人が隣に\nいないからですね…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3799, 1, 8, 180401, 102, 1, 0, "どこを見ても、大切な\n思い出がたくさん\n詰まっていますね♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3800, 1, 1, 180401, 1, 2, 0, "無礼者～、なんて\n冗談です♪　どんどん\n触っちゃってください", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3801, 1, 1, 180401, 2, 2, 0, "ぷにぷにでしょう～？\nい～っぱい食べるのが\nぷにぷにの秘訣です！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3802, 1, 1, 180401, 3, 2, 0, "握手したいんですか？\nはい、にぎにぎ～！\nもう離しませんよ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3803, 1, 1, 180401, 4, 2, 0, "どうかしましたか？\n退屈してるんでしたら\nお散歩に行きましょう", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3804, 1, 4, 180401, 101, 2, 0, "ごはん王子♪\nわたしは王女ですから\nこれでピッタリですね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3805, 1, 8, 180401, 102, 2, 0, "大好きです～\n愛してます～\n…ほんと、ですよ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3806, 1, 1, 180401, 1, 2, 0, "朝ごはんは大事です！\n山盛りごはんを食べて\n元気いっぱいですよ☆", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3807, 1, 1, 180401, 2, 2, 0, "お腹ぺこぺこになる\n時間ですね～！\nごはん特盛、一つ！", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3808, 1, 1, 180401, 3, 2, 0, "お腹いっぱい、今日は\nよく眠れそうですね♪\n明日もおいしい一日を", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3810, 1, 1, 180401, 2, 3, 0, "休憩休憩～♪\nお腹を休めて、次の\nごはんに挑みます…☆", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3811, 1, 1, 180401, 3, 3, 0, "かわいらしい\nぬいぐるみですね～\nキャルちゃんみたい☆", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3812, 1, 1, 180401, 4, 3, 0, "すんすん…この匂い\n食欲増進の効果が\nありそうですね…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3813, 1, 1, 180401, 5, 3, 0, "体温が上がってくると\n眠くなっちゃいます…\nほわぁ～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3815, 1, 1, 180401, 7, 3, 0, "もぐもぐもぐもぐもぐ\nむぐむぐむぐむぐむぐ\nはぐはぐはぐはぐはぐ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3816, 1, 1, 180401, 8, 3, 0, "教養を身に着けるには\n読書が一番です！\nえっと次のレシピは～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3818, 1, 1, 112401, 1, 1, 0, "アイドルらしくなる\nコツって、何か\nあるんでしょうか？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3819, 1, 1, 112401, 2, 1, 0, "色んな人と話すのが\n好きなんです。つい\n長くなっちゃって…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3820, 1, 1, 112401, 3, 1, 0, "アイドルを目指すって\n決めてから、毎日が\nワクワクなんです♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3821, 1, 1, 112401, 4, 1, 0, "ちゃんとストレッチ\nしておかなくっちゃ\nよいしょ、よいしょ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3822, 1, 1, 112401, 5, 1, 0, "リンちゃんミオちゃん\n二人に負けないように\n私、頑張ります！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3823, 1, 4, 112401, 101, 1, 0, "私の笑顔は素敵だって\n誰かに褒められた…\nそんな気がするんです", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3824, 1, 8, 112401, 102, 1, 0, "私たちを支えてくれる\n誰かが、ずっと一緒に\nいてくれたような…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3825, 1, 1, 112401, 1, 2, 0, "どうかしましたか？\n何か困ってるのなら\nお手伝いしますよ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3826, 1, 1, 112401, 2, 2, 0, "みなさん個性的で\n普通じゃない人ばかり\nいるような気が…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3827, 1, 1, 112401, 3, 2, 0, "今日はポカポカした\nいい天気ですね～\nお散歩日和です！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3828, 1, 1, 112401, 4, 2, 0, "今、ミルクティーを\n淹れてたんです♪\nあなたも飲みますか？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3829, 1, 4, 112401, 101, 2, 0, "私の得意なこと？\nうーんなんでしょう…\nあっ、肩たたきです！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3830, 1, 8, 112401, 102, 2, 0, "生・ハム・メローン！\nあ、ライブの掛け声を\n練習してたんですよ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3831, 1, 1, 112401, 1, 2, 0, "おはようございます！\n今日も一日\n頑張りましょうね！", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3832, 1, 1, 112401, 2, 2, 0, "しっかりお昼を食べて\n午後からも張り切って\nいきましょうね♪", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3833, 1, 1, 112401, 3, 2, 0, "ぐっすり眠れるよう\n早めに用事を済ませて\nお休みしましょうね", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3835, 1, 1, 112401, 2, 3, 0, "ちょっと休んだら\nレッスンの続きも\n頑張ります！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3836, 1, 1, 112401, 3, 3, 0, "えへへ～\nふわふわもこもこで\nかわいいです～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3837, 1, 1, 112401, 4, 3, 0, "リラックスできる\nいい香りですね～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3838, 1, 1, 112401, 5, 3, 0, "わぁ、ポカポカです♪\n温かい飲み物も\nほしくなりますね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3840, 1, 1, 112401, 7, 3, 0, "レッスンしたら\nお腹が空いちゃって…\nぱくぱくもぐもぐ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3841, 1, 1, 112401, 8, 3, 0, "レッスンに役立つ\n運動の情報とか\n載ってるでしょうか…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3843, 1, 1, 112601, 1, 1, 0, "きらきらーん☆\nよし、次のステージは\nこれで行けるかも！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3844, 1, 1, 112601, 2, 1, 0, "私の元気でみんなも\n元気になってくれたら\nうれしいな～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3845, 1, 1, 112601, 3, 1, 0, "ちょっとレッスンで\n疲れちゃったかも…\nでも頑張るぞっ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3846, 1, 1, 112601, 4, 1, 0, "晩ごはん何にしよう？\nそーだ、しぶりんにも\n聞いてみよっと！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3847, 1, 1, 112601, 5, 1, 0, "元気だから笑顔になる\nじゃなくて\n笑顔だから\n元気になるんだよね～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3848, 1, 4, 112601, 101, 1, 0, "声援を聞いてる時ね\nすごく懐かしいって\n思うことがあるんだ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3849, 1, 8, 112601, 102, 1, 0, "私を応援してくれる\n一番大事な人が\n隣にいた気がする…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3850, 1, 1, 112601, 1, 2, 0, "なになにー？\nミオちゃんに\n何か用かな～？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3851, 1, 1, 112601, 2, 2, 0, "おっ、やっほー！\n元気そうだね～、キミ\n私？\nもちろん元気～！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3852, 1, 1, 112601, 3, 2, 0, "ねぇねぇ、ちょっと\n遊びに行かない？\nたまには息抜きで！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3853, 1, 1, 112601, 4, 2, 0, "ね、この魔物料理さ\nプリンって言い張れば\n食べてもらえそうかな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3854, 1, 4, 112601, 101, 2, 0, "えへへ\nいつもありがとねっ！\nパワーもらってるよ～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3855, 1, 8, 112601, 102, 2, 0, "どうしたどうした～\nミオちゃんの魅力に\nメロメロなのかい？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3856, 1, 1, 112601, 1, 2, 0, "おっはよ～！\n一日の始まりだし\nテンション上げてこ！", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3857, 1, 1, 112601, 2, 2, 0, "お腹空いた～…\nねぇねぇ、一緒に\nお昼食べようよ～", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3858, 1, 1, 112601, 3, 2, 0, "ふわぁ～…\n眠くなってきちゃった\nそろそろ休もっかなぁ", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3860, 1, 1, 112601, 2, 3, 0, "わぁ、ふっかふか～！\nレッスンの疲れも\n吹っ飛んじゃうね☆", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3861, 1, 1, 112601, 3, 3, 0, "何これ…かわいーっ！\n早くしまむーに\n教えてあげなきゃ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3862, 1, 1, 112601, 4, 3, 0, "すぅ～…\nうんっ、これ私の\n好きな香りだな～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3863, 1, 1, 112601, 5, 3, 0, "ポッカポカだね～♪\n…あ～ん、ここから\n動きたくないーっ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3865, 1, 1, 112601, 7, 3, 0, "わっ、おーいしー！\nこれはテンション\n上がっちゃうね♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3866, 1, 1, 112601, 8, 3, 0, "ふむふむ…\nふむふむふむ…！\n勉強になった、ブイ☆", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3868, 1, 12, 100101, 103, 1, 0, "なんでドキドキが\n止まらないの…？\n騎士くんのせいだー！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3869, 1, 12, 100101, 103, 2, 0, "ドキドキしてるの\nあたしだけ、かな…\n騎士くんはどう…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3870, 1, 12, 100201, 103, 1, 0, "騎士クンの前で\n好きって言えるように\n練習しないと…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3871, 1, 12, 100201, 103, 2, 0, "騎士クン…大好き\n…き、騎士クン！？\nなんでここに！？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3872, 1, 12, 100301, 103, 1, 0, "困ったな…\n彼が居ないだけで、\nここまで\n寂しいと思うなんて…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3873, 1, 12, 100301, 103, 2, 0, "えっと…\n今日は訓練じゃなくて\nその…二人でゆっくり\n過ごしたいな…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3874, 1, 1, 112501, 1, 1, 0, "アイドルの心構え…\nどういう風に考えると\nしっくりくるのかな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3875, 1, 1, 112501, 2, 1, 0, "何か始めるのって\nドキドキするよね\nだから最近楽しいんだ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3876, 1, 1, 112501, 3, 1, 0, "冒険者の時って毎日\nバタバタしてたけど…\n目標ができると違うね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3877, 1, 1, 112501, 4, 1, 0, "少しお腹が空いた…\nチョコレート一粒だけ\n食べようかな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3878, 1, 1, 112501, 5, 1, 0, "ウヅキもミオも\n頑張ってるし\n私も負けられないね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3879, 1, 4, 112501, 101, 1, 0, "レッスンしてる時さ\n何か、誰かが足りない\nそう思うことがあるの", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3880, 1, 8, 112501, 102, 1, 0, "大事なのは笑顔…\n誰か、大切な人から\n言われた気がするんだ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3881, 1, 1, 112501, 1, 2, 0, "どうしたの、何か用？\n手伝えることがあれば\nすぐに言ってね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3882, 1, 1, 112501, 2, 2, 0, "姿勢がいい？\n普段から鍛えてるし\n自然とそうなるのかも", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3883, 1, 1, 112501, 3, 2, 0, "さっき散歩してたら\n犬を見かけたんだ\n人懐っこい子だったよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3884, 1, 1, 112501, 4, 2, 0, "今から冒険？\nなら私も一緒に行くよ\n前衛は任せておいて", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3885, 1, 4, 112501, 101, 2, 0, "今からレッスンに\n行くつもりだったんだ\n少し手伝ってくれる？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3886, 1, 8, 112501, 102, 2, 0, "蒼の剣を受けよ！\nえ、何してるのって？\nただの発声練習だけど", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3887, 1, 1, 112501, 1, 2, 0, "おはよう、早いね\n今日は依頼もないし\nゆっくりしようかな", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3888, 1, 1, 112501, 2, 2, 0, "そろそろお昼だね\nうーん、今日は\n何を食べよう…", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3889, 1, 1, 112501, 3, 2, 0, "もうこんな時間…\n明日も早いし\nそろそろ寝なくちゃ", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3891, 1, 1, 112501, 2, 3, 0, "レッスンで疲れたし\nちょっとだけ\n休んでおこうかな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3892, 1, 1, 112501, 3, 3, 0, "ふふっ、かわいい\nふわふわしたものって\n撫でたくなるんだよね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3893, 1, 1, 112501, 4, 3, 0, "うん、いい香り…\nこういうものがあると\n落ち着くからいいよね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3894, 1, 1, 112501, 5, 3, 0, "温かい場所って\n居心地がいいから\nのんびりしちゃうよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3896, 1, 1, 112501, 7, 3, 0, "レッスン頑張ったら\nすぐお腹が空くし\n食べておかないと…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3897, 1, 1, 112501, 8, 3, 0, "アイドルのことを\nもっと勉強して\n成長しなくちゃ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3899, 1, 1, 111001, 1, 1, 0, "ああ、知りたい…\nなんでもかんでも\nとにかく知りたい…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3900, 1, 1, 111001, 2, 1, 0, "ああ、眠い…\nいつでもどこでも\nとにかく眠い…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3901, 1, 1, 111001, 3, 1, 0, "君、気を付けたまえ\nぼーっと歩いていると\nほわっ！　転んだ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3902, 1, 1, 111001, 4, 1, 0, "ミツキ師にいただいた\n新作サプリ…丁度よい\n被験体はいないものか", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3903, 1, 1, 111001, 5, 1, 0, "サレンディア救護院に\nユニちゃん８歳として\n潜入済みだが何か？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3904, 1, 4, 111001, 101, 1, 0, "君がユニちゃんズの\n同志であること、\nぼくは誇りに思うぞ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3905, 1, 8, 111001, 102, 1, 0, "ぼくは青春がしたい\nおい、君\nぼくと青春しないか", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3906, 1, 1, 111001, 1, 2, 0, "ああ、そうだな。\nぼくもそう思う。\n……えっ、何が？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3907, 1, 1, 111001, 2, 2, 0, "待て、話しかけるな。\n今の閃きをメモに…\n…ああっ、忘れた！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3908, 1, 1, 111001, 3, 2, 0, "歩き方が綺麗？\n何を今更なこと\n言っとるんだ君は", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3909, 1, 1, 111001, 4, 2, 0, "ぼくの血液型？\n大らかのОだ。\n何か異論あるかね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3910, 1, 4, 111001, 101, 2, 0, "君の声は精神に\n高揚をもたらすな…\n波形を分析したいぞ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3911, 1, 8, 111001, 102, 2, 0, "妙だな…最近\n君に話しかけられると\n心拍に乱れを覚える", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3912, 1, 1, 111001, 1, 2, 0, "おっと、朝だな。\nそろそろ寝るか、\n朝更かしは体に悪い", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3913, 1, 1, 111001, 2, 2, 0, "ふむ、昼餉時か…\n今日のランチは\nサプリ定食にしよう", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3914, 1, 1, 111001, 3, 2, 0, "もう夜だな、\nそろそろ起きるか。\n最高の一日にするぞ！", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3916, 1, 1, 111001, 2, 3, 0, "ほわぁ～\n副交感神経が…\n優位に…ふふっひ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3917, 1, 1, 111001, 3, 3, 0, "かわいすぎるな…\nセクシーユニには\nミスマッチか…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3918, 1, 1, 111001, 4, 3, 0, "ほぁあアあァぁー…\nいい香りー…\nあアァあ…イイ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3919, 1, 1, 111001, 5, 3, 0, "炎はいいぞ\nそこには人類発展の\nドラマがある", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3921, 1, 1, 111001, 7, 3, 0, "フオオー！\n次から次へと固形食物\n原理気になるー！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3922, 1, 1, 111001, 8, 3, 0, "良書は時間を忘れる\n…ふむ、今度\n布団を持ってこよう", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3924, 1, 12, 101201, 103, 1, 0, "きらーん☆\nハツネちゃんに\n不可能はないのだ☆", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3925, 1, 12, 101201, 103, 2, 0, "キミと一緒だと\n寝ちゃうのが\nもったいないよ～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3933, 1, 1, 192101, 1, 2, 0, "ちぇるーん☆", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3951, 1, 1, 110901, 1, 1, 0, "ちぇる～ん☆\n今日もチエルは\nかわいいですよね～？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3952, 1, 1, 110901, 2, 1, 0, "どこかに楽しいもの\n落ちてないかにゃー", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3953, 1, 1, 110901, 3, 1, 0, "チエルのオキニは\n甘々カフェラテ！\nシクヨロでぇ～す☆", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3954, 1, 1, 110901, 4, 1, 0, "先輩、ひまですよね？\nチエル遊んであげても\nいいですけど？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3955, 1, 1, 110901, 5, 1, 0, "はぁー…しゅき…\nカルミナ尊い…\n一生すこれる…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3956, 1, 4, 110901, 101, 1, 0, "先輩、ひまー\nちょっとは構えよーう\nデュクシ！デュクシ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3957, 1, 8, 110901, 102, 1, 0, "先輩なんか\nおごって～！\nてか…おごらなくても\nいいから遊んでよ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3958, 1, 1, 110901, 1, 2, 0, "え、なんですか？\n告白だったら\n間に合ってますけど", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3959, 1, 1, 110901, 2, 2, 0, "ちょっと静かに！\n今ヤバかわポーズの\nイメトレ中なんです！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3960, 1, 1, 110901, 3, 2, 0, "足取りが軽やか？\nあー…ダンスとか\n習ってるからですかね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3961, 1, 1, 110901, 4, 2, 0, "チエルの血液型？\n大型新人のОです\n何か問題でも？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3962, 1, 4, 110901, 101, 2, 0, "先輩って何気に\nいい声してますよね\n言うことは変だけど", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3963, 1, 8, 110901, 102, 2, 0, "え、なんですか？\nたまにだったら\n告白してもいいですよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3964, 1, 1, 110901, 1, 2, 0, "おはちぇる～ん☆\nハイ、ご一緒に～？", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3965, 1, 1, 110901, 2, 2, 0, "今日のランチは\nどこで\n映えよっかにゃー", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3966, 1, 1, 110901, 3, 2, 0, "こんばんちぇるるん☆\nハイ、ご一緒に～？", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3968, 1, 1, 110901, 2, 3, 0, "ふかふかチエルン\n略して、ふかルン♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3969, 1, 1, 110901, 3, 3, 0, "かわいいこの子と\nかわいいチエル！\n映ぁ～え～る～！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3970, 1, 1, 110901, 4, 3, 0, "かわいい香り～！\n持って帰ろっと！\nすぅーっ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3971, 1, 1, 110901, 5, 3, 0, "マシュマロは\n焼かない方のが\nかわいくないです？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3973, 1, 1, 110901, 7, 3, 0, "チエル食べても\n太らない体質\nなんでぇ～！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3974, 1, 1, 110901, 8, 3, 0, "読書は好きですよ\nシンプルに楽しいし\nギャップ萌えでしょ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3976, 1, 1, 112701, 1, 1, 0, "みんなのあたしを\n見る目が違うね\nま、気にしないけどね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3977, 1, 1, 112701, 2, 1, 0, "今日も疲れた～\nやる気ゼロだし\nもう寝ちゃおうかな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3978, 1, 1, 112701, 3, 1, 0, "あたしの代わりに誰か\n働いてくれないかね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3979, 1, 1, 112701, 4, 1, 0, "労働の後に食べる\nあんパン最高～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3980, 1, 1, 112701, 5, 1, 0, "仕事の合間に\nちょっと一息いれよ\nあ～ドングリ美味し～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3981, 1, 4, 112701, 101, 1, 0, "報酬も入ったことだし\nあいつに何か\n買ってあげようかな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3982, 1, 8, 112701, 102, 1, 0, "次の休みはあいつと\n一日中だらだら\nしたいな～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3983, 1, 1, 112701, 1, 2, 0, "あたしは褒められて\n伸びるタイプなんだ\nだから褒めて褒めて", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3984, 1, 1, 112701, 2, 2, 0, "お、差し入れ？\n気が利くねぇ\nあんがと", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3985, 1, 1, 112701, 3, 2, 0, "新しい依頼？\n今日はもう無理～\n閉店で～す", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3986, 1, 1, 112701, 4, 2, 0, "サ、サボってないよ！\nちょっと休憩してた\nだけだから！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3987, 1, 4, 112701, 101, 2, 0, "ねえ、一緒に\nサボろうよ？\n働き過ぎはよくないよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3988, 1, 8, 112701, 102, 2, 0, "あんたが近くにいると\n仕事の疲れも\n和らぐんだよね～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3989, 1, 1, 112701, 1, 2, 0, "はぁ…もう起きなきゃ\nつらい労働の\nはじまりだ～", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3990, 1, 1, 112701, 2, 2, 0, "うぅ、お昼に\nあんパン食べ過ぎた…\n苦しい…眠い…", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3991, 1, 1, 112701, 3, 2, 0, "明日は仕事ないし\nガッツリ夜ふかし\nしちゃうよ～", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3993, 1, 1, 112701, 2, 3, 0, "猫みたいに\n丸くなっちゃうよ～\nあたしリスだけどね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3994, 1, 1, 112701, 3, 3, 0, "ふかふか～\nあたしの尻尾と\n互角とはやるね～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3995, 1, 1, 112701, 4, 3, 0, "あんパンの香りの\nアロマとか\nあればいいのにな～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3996, 1, 1, 112701, 5, 3, 0, "これは危険な\nあたたかさだね～\n労働意欲が\nなくなるよ～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3998, 1, 1, 112701, 7, 3, 0, "いっぱい働いたから\nいっぱい食べても\nカロリー０だよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (3999, 1, 1, 112701, 8, 3, 0, "あたしだってたまには\n本くらい読むって\n全然似合わないけどね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4001, 1, 12, 104601, 103, 1, 0, "あたしの作った\nオリジナルたい焼き…\nあいつ、\n喜んでくれるかにゃ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4002, 1, 12, 104601, 103, 2, 0, "あ、お帰りにゃー♪\nえへへ…キミに\nお帰りって言えるのが\nなんかうれしいにゃ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4003, 1, 1, 112801, 1, 1, 0, "フロンティア\nスピリッツを\nお見せするべ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4004, 1, 1, 112801, 2, 1, 0, "この鎧を着ると\n気が引き締まる\n感じがするべ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4005, 1, 1, 112801, 3, 1, 0, "搾りたての牛乳飲んで\n今日も一日\nがんばるべ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4006, 1, 1, 112801, 4, 1, 0, "みんなに牧場の\n乳製品を\n味わってもらいてぇべ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4007, 1, 1, 112801, 5, 1, 0, "ここはいいとこだべ\nうちの動物たちも\n連れてきてやりてぇな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4008, 1, 4, 112801, 101, 1, 0, "みんなオラより\n若ぇのに働いてて\n立派だべな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4009, 1, 8, 112801, 102, 1, 0, "牧場もギルドの\nみんなもオラが\nまとめて面倒見るべ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4010, 1, 1, 112801, 1, 2, 0, "さあ、今日も\n忙しくなるべよ\n気合入れてくべ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4011, 1, 1, 112801, 2, 2, 0, "お、あんちゃん\n丁度いいとこに！\n一緒に街に\n行商しに行くべ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4012, 1, 1, 112801, 3, 2, 0, "牧場仕事で培った力が\n戦いでも役立つなら\n一石二鳥だべさ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4013, 1, 1, 112801, 4, 2, 0, "疲れた時は牧場産の\nミルク菓子で\n糖分補給だべ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4014, 1, 4, 112801, 101, 2, 0, "これ、牧場の\n新製品だべ\n一番最初に味見\nしてほしいべさ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4015, 1, 8, 112801, 102, 2, 0, "働きづめはよくねぇベ\n膝枕してやっから\nこっち来るべさ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4016, 1, 1, 112801, 1, 2, 0, "ふぁあ…\n酪農家の朝は\n早ぇんだべ…", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4017, 1, 1, 112801, 2, 2, 0, "ごはんの時間だべ！\n腹が減っては酪農も\n戦いもできねえべ！", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4018, 1, 1, 112801, 3, 2, 0, "そろそろ寝るべ！\n健全なお笑いは\n健全な肉体に宿るべ！", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4020, 1, 1, 112801, 2, 3, 0, "牧草みたいに\nふっかふかだべ～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4021, 1, 1, 112801, 3, 3, 0, "この子、目の感じが\nエリザベスに似てるべ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4022, 1, 1, 112801, 4, 3, 0, "いい匂いだべ～\n疲れた体に\nしみるべな～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4023, 1, 1, 112801, 5, 3, 0, "あったけぇな～\n牧場にも\n置いてみっかな～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4025, 1, 1, 112801, 7, 3, 0, "うまいべ！\n生産者のこだわりを\n感じるべさ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4026, 1, 1, 112801, 8, 3, 0, "酪農も戦いも\nまずは知識だべ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4028, 1, 1, 113001, 1, 1, 0, "ぴょん、ぴょん♪\nこの服のおかげで、\n体が軽いなぁ…♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4029, 1, 1, 113001, 2, 1, 0, "今の内に部屋の\n掃除をしておこっと…\nみんな喜んで\nくれるといいなぁ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4030, 1, 1, 113001, 3, 1, 0, "耳と尻尾…この服、\n本当にウサギさん\nみたいだなぁ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4031, 1, 1, 113001, 4, 1, 0, "なんだか先輩の\n気配がするような…\n気のせいかな？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4032, 1, 1, 113001, 5, 1, 0, "ウサギさんの真似を\nしたら、存在感が薄い\nとか言われないかな？\nア、アユミだぴょん♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4033, 1, 4, 113001, 101, 1, 0, "えへへ…\nいつもとは違う先輩が\nたくさん見られて、\nうれしいな♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4034, 1, 8, 113001, 102, 1, 0, "本の中の世界で先輩と\n旅をするなんて…\n幸せすぎて、罰が\n当たりそうだよぉ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4035, 1, 1, 113001, 1, 2, 0, "じ～…はっ！？\nす、すみません！\n話しかけてもらえる\nなんて思ってなくて！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4036, 1, 1, 113001, 2, 2, 0, "先輩がすぐそばに…\nし、幸せすぎて\n倒れそうですぅ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4037, 1, 1, 113001, 3, 2, 0, "え、えっと…あの…！\nうう…いざ先輩と\n向き合うと\n緊張しちゃうよぉ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4038, 1, 1, 113001, 4, 2, 0, "はうっ！？\nしょ、正面から見る\n先輩は、私にとって\n神々しすぎますぅ…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4039, 1, 4, 113001, 101, 2, 0, "先輩…その…\nい、一緒にご飯でも\nどうでひゅか！\nうう…またかんだぁ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4040, 1, 8, 113001, 102, 2, 0, "せ、先輩…\nハーブティーを\n入れたんですが、\n一緒にどうですか…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4041, 1, 1, 113001, 1, 2, 0, "せ、先輩！\nおはようございます！\nえへへ、練習通りに\nバッチリ言えたぁ♪", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4042, 1, 1, 113001, 2, 2, 0, "あ、もうお昼ですね\n時計を持ってて\n良かったです", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4043, 1, 1, 113001, 3, 2, 0, "もうこんな時間…\nお休みする\n準備をしちゃいますね", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4045, 1, 1, 113001, 2, 3, 0, "あ…ちょっとだけ\n先輩の匂いがする…\nえへへ、幸せだなぁ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4046, 1, 1, 113001, 3, 3, 0, "…私もウサギさんの\nぬいぐるみとして、\n先輩のそばに…はっ！\n私ったら何を…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4047, 1, 1, 113001, 4, 3, 0, "あ、いい香り…\n先輩もこういうのが\n好きなのかな？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4048, 1, 1, 113001, 5, 3, 0, "先輩が隣に\n居たら、もっと\n暖かいのになぁ…♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4050, 1, 1, 113001, 7, 3, 0, "おいしい…でも、\n先輩と一緒に\n食べたら、もっと\nおいしいだろうな…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4051, 1, 1, 113001, 8, 3, 0, "ウサギさんって、穴を\n掘ったりするんだ…\n私も、隠れたいときに\n掘ってみようかな？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4053, 1, 1, 180501, 1, 1, 0, "ネビアさまのように\n普段からパタパタと\n飛んでみるのも…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4054, 1, 1, 180501, 2, 1, 0, "ご立派になられた\n主さまと並べるように\nもっと精進しなくては", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4055, 1, 1, 180501, 3, 1, 0, "月々の食費が\n増えてしまいました\n申し訳ございません…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4056, 1, 1, 180501, 4, 1, 0, "ワンワンのほうの\n主さまは、この姿に\n驚くでしょうか…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4057, 1, 1, 180501, 5, 1, 0, "主さまと離れていても\n主さまとの絆は\n胸に感じております…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4058, 1, 4, 180501, 101, 1, 0, "このギルドハウスは\nみなさまの帰る場所…\nわたくしが守ります", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4059, 1, 8, 180501, 102, 1, 0, "どんな未来が\n待ち受けようとも\n主さまと添い遂げます", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4060, 1, 1, 180501, 1, 2, 0, "おでかけですか？\nではわたくしも\nお供いたします", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4061, 1, 1, 180501, 2, 2, 0, "もう二度と主さまと\n離れたくはありません\nいつ何時も…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4062, 1, 1, 180501, 3, 2, 0, "なにかお困りごと\nでございますか？\nわたくしに何なりと…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4063, 1, 1, 180501, 4, 2, 0, "どうぞゆっくりと\n心ゆくまでくつろいで\nくださいまし、主さま", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4064, 1, 4, 180501, 101, 2, 0, "この羽で主さまを\nどこまでも遠く…\n高くへと導きます", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4065, 1, 8, 180501, 102, 2, 0, "主さま、主さま…♪\nおそばにいられるのが\nうれしくて、つい…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4066, 1, 1, 180501, 1, 2, 0, "おはようございます\n今日も一日、主さまの\nサポートをいたします", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4067, 1, 1, 180501, 2, 2, 0, "今日も頑張っている\n主さまに、スタンプを\nもう一つ…ポン！", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4068, 1, 1, 180501, 3, 2, 0, "寝付けないのでしたら\nわたくしが子守唄を\n歌ってさしあげます", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4070, 1, 1, 180501, 2, 3, 0, "ふかふか…\nまるで主さまのような\n柔らかさでございます", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4071, 1, 1, 180501, 3, 3, 0, "あなたも一緒に\n主さまと遊びますか？\n至福の時間ですよ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4072, 1, 1, 180501, 4, 3, 0, "主さまの優しくて\nくすぐったい匂いが\nたまらなく恋しいです", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4073, 1, 1, 180501, 5, 3, 0, "主さまの心と\n繋がったときのような\n温もりを感じます", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4075, 1, 1, 180501, 7, 3, 0, "いけませんっ…！\n元の姿に戻ると、また\n食べてしまうのに…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4076, 1, 1, 180501, 8, 3, 0, "主さまに読み聞かせる\n絵本は…妖精が主役の\n物語にいたしましょう", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4078, 1, 12, 104801, 103, 1, 0, "…彼のこと弟みたいに\n思ってきたのに…私、\nどうしたらいいの…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4079, 1, 12, 104801, 103, 2, 0, "よ、予定がなかったら\nでいいんだけど…\nその、ふ、二人で\n訓練とか…どうかな？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4080, 1, 1, 112901, 1, 1, 0, "おめかしもしたし\nあとはお兄ちゃんを\n待つだけですね～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4081, 1, 1, 112901, 2, 1, 0, "お兄ちゃんの身の回り\nをお片付け～、これぞ\nデキる妹アピール！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4082, 1, 1, 112901, 3, 1, 0, "あ、衣装がほつれてる\nこれくらいなら自分で\n繕えますね…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4083, 1, 1, 112901, 4, 1, 0, "この格好で歩いてると\n見慣れた景色も\n違うものに見えますね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4084, 1, 1, 112901, 5, 1, 0, "勉強もちゃんとします\n…でも、一人だと\n集中力がもちません～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4085, 1, 4, 112901, 101, 1, 0, "お兄ちゃんを大好きな\n妹が待ってますよ～\n…まだですかね～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4086, 1, 8, 112901, 102, 1, 0, "お兄ちゃんのために\n掃除洗濯をする私って\nお嫁さんみたい…\n…えへへ～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4087, 1, 1, 112901, 1, 2, 0, "おに～いちゃん！\n縫い物、一緒に\nやりましょうよ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4088, 1, 1, 112901, 2, 2, 0, "かわいい妹を\n愛でたくなりました？\n思う存分、どうぞ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4089, 1, 1, 112901, 3, 2, 0, "わっ、ビックリした～\nもう、お兄ちゃんてば\nイタズラっ子ですね～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4090, 1, 1, 112901, 4, 2, 0, "一緒にお買い物\n行きませんか？\n妹とデートですよっ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4091, 1, 4, 112901, 101, 2, 0, "お兄ちゃん…頭を\n撫でてくれませんか？\nえへへ、これが一番♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4092, 1, 8, 112901, 102, 2, 0, "お兄ちゃん大好き♪\n何度でも言いますよ！\nお兄ちゃん大好きっ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4093, 1, 1, 112901, 1, 2, 0, "おはよ～お兄ちゃん！\n妹モーニングコールの\n時間ですよっ♪", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4094, 1, 1, 112901, 2, 2, 0, "そろそろ休憩ですね！\n甘いお菓子で\n元気回復しましょ～！", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4095, 1, 1, 112901, 3, 2, 0, "夜更かし厳禁！\nでも、もう少しだけ\nお話ししたいなぁ～", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4097, 1, 1, 112901, 2, 3, 0, "ふっかふかですよ！\nお兄ちゃんと一緒に\n座りたいなぁ…♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4098, 1, 1, 112901, 3, 3, 0, "このぬいぐるみの\nかわいさ…う～ん\n負けられません…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4099, 1, 1, 112901, 4, 3, 0, "香りにも気をつかって\nパーフェクトな妹に\nなっちゃいますから！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4100, 1, 1, 112901, 5, 3, 0, "はぁ～ぬくぬくです～\nお兄ちゃんと一緒に\n温まりたいなぁ…♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4102, 1, 1, 112901, 7, 3, 0, "これは止まりませんね\nもぐもぐ…いくらでも\n食べられちゃいます", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4103, 1, 1, 112901, 8, 3, 0, "これ、デザインの\n参考になりそう！\n私にも作れるかなぁ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4105, 1, 1, 106601, 1, 1, 0, "退屈です…\nなにか楽しいことは\nないですか？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4106, 1, 1, 106601, 2, 1, 0, "ギャングの仕事は\n今日はお休みです\nのんびりするですよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4107, 1, 1, 106601, 3, 1, 0, "ひうっ！　あたしの\n尻尾なんて食べても\nおいしくないですよ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4108, 1, 1, 106601, 4, 1, 0, "ふう…どうやら\n例の件はまだボスに\nバレてないですね…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4109, 1, 1, 106601, 5, 1, 0, "下克上…いい言葉です\nって、ボスに聞かれて\nないですよね…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4110, 1, 4, 106601, 101, 1, 0, "いつか絶対ビッグに\nなってやるです！\nあたしを信じるです！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4111, 1, 8, 106601, 102, 1, 0, "あたしが覚醒したら\nきっと誰よりも\n強くなるはずです！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4112, 1, 1, 106601, 1, 2, 0, "なんですか？\n貢ぎ物なら\nいつでも歓迎ですよ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4113, 1, 1, 106601, 2, 2, 0, "あたしだって\nやる時はやるですよ！\nその時がこないだけ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4114, 1, 1, 106601, 3, 2, 0, "しりとり勝負ですか？\nいい度胸ですね、\nかかってくるです！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4115, 1, 1, 106601, 4, 2, 0, "お兄さんは\nヒマそうですね…\nうらやましいです", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4116, 1, 4, 106601, 101, 2, 0, "かまってほしいです？\nまったく…いいですよ\nしょうがないですね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4117, 1, 8, 106601, 102, 2, 0, "お兄さんのことは\nこのあたしが\n守ってやるですよ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4118, 1, 1, 106601, 1, 2, 0, "デキるギャングの\n朝は早いのです…\nふぁあ…", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4119, 1, 1, 106601, 2, 2, 0, "お腹がすいたですね\nなにか食べにいくです\nお兄さんのおごりで！", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4120, 1, 1, 106601, 3, 2, 0, "ふぁあ…むにゅう…\nまだ起きてるですよぉ", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4122, 1, 1, 106601, 2, 3, 0, "はぁ～…\nもうここから一歩も\n動きたくないです", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4123, 1, 1, 106601, 3, 3, 0, "なかなかいいモフりを\nしてやがるです…\n舎弟にしてやるですよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4124, 1, 1, 106601, 4, 3, 0, "なんかおしゃれな\n匂いがするですね…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4125, 1, 1, 106601, 5, 3, 0, "ふふーん！\nあたしのブレスの方が\n火力は上ですね！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4127, 1, 1, 106601, 7, 3, 0, "あたしに貢ぎ物とは\nよく分かってるですね\n食べてやるですよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4128, 1, 1, 106601, 8, 3, 0, "ふーむ…\nもっと楽しい本は\nないですかね？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4130, 1, 1, 113201, 1, 1, 0, "海に眠る秘宝…\n心躍る響きだ！\n早く見つけたい…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4131, 1, 1, 113201, 2, 1, 0, "うう～、暑いぃ…\nかき氷を食べて\nすっきりしたいぃ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4132, 1, 1, 113201, 3, 1, 0, "アターック！\nいや、もっとこう…\nアタァーック！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4133, 1, 1, 113201, 4, 1, 0, "スイカ割りで\n目を回さない\nコツはなんだろうか…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4134, 1, 1, 113201, 5, 1, 0, "せっかく海に来たのだ\n泳ぎも上手に\nなりたいものだな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4135, 1, 4, 113201, 101, 1, 0, "ふっふっふ…\n疼く…疼くぞ！\n日焼けの痕が…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4136, 1, 8, 113201, 102, 1, 0, "シグルドと私は\n来世でも共にある…\nつまり我らはずっと…\nはわわ…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4137, 1, 1, 113201, 1, 2, 0, "シグルド、私と共に\n日光浴に行かないか？\n光の力を集めよう！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4138, 1, 1, 113201, 2, 2, 0, "この格好だからできる\n必殺技を考えている\n何か案はないか？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4139, 1, 1, 113201, 3, 2, 0, "シグルド～！\nスイカを貰ったから\n一緒に食べよう！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4140, 1, 1, 113201, 4, 2, 0, "こう日差しが強いと\n日焼けしそうだな\n跡になってないか…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4141, 1, 4, 113201, 101, 2, 0, "我らの夏の黙示録\nしっかりと記録\nしておかねばな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4142, 1, 8, 113201, 102, 2, 0, "ちゃんと日焼けどめを\n塗ってもらうべき\nだったかな…？\nシ、シグルドに…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4143, 1, 1, 113201, 1, 2, 0, "朝だぞシグルド！\nさぁ、日光を浴びて\n光の加護を得るのだ！", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4144, 1, 1, 113201, 2, 2, 0, "もう昼食の時間か…\n私の魔力も減ったし\n早く補給しなければ", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4145, 1, 1, 113201, 3, 2, 0, "夜でも我らの活動は\n終わることはない！\nさぁ準備を…ふわぁ…", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4147, 1, 1, 113201, 2, 3, 0, "海で遊んでいると\n疲れてしまうからな\n少し休まるとしよう", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4148, 1, 1, 113201, 3, 3, 0, "えへへ～、君の名前は\n何にしまちょうかね♪\n…はっ、見たのか！？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4149, 1, 1, 113201, 4, 3, 0, "心地よい香りで\n我が精神も安らぐ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4150, 1, 1, 113201, 5, 3, 0, "別に寒くなどないぞ！\n寒くないが、ちょっと\n暖まりたくなってな！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4152, 1, 1, 113201, 7, 3, 0, "もぐ…食べ過ぎた分は\n魔力に変換されるのだ\n…たぶん", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4153, 1, 1, 113201, 8, 3, 0, "…なな、なんだ！？\nシグルドはこういう\n趣味なのか…！？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4155, 1, 1, 113201, 10, 3, 0, "むむ、この乗り心地…\nいざゆかん、\nシードラゴンよ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4156, 1, 1, 113201, 11, 3, 0, "疾風の冥姫の名に\nふさわしき\n深海の玉座よ…\nふああ…柔らかい～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4157, 1, 1, 180201, 1, 1, 0, "この姿でいると\nわたしもお姫様に\nなれた気がする…♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4158, 1, 1, 180201, 2, 1, 0, "お腹空いてきちゃった\nまずは買い出しに…\n…変身解かなくちゃ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4159, 1, 1, 180201, 3, 1, 0, "胸のところが空いてて\nスースーするの\nまだ慣れないな…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4160, 1, 1, 180201, 4, 1, 0, "治癒魔法に使ってた\n魔力を攻撃魔法に…\n頭の中でも練習練習", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4161, 1, 1, 180201, 5, 1, 0, "何もできなかった\nわたしから、少しは\n変われたのかな…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4162, 1, 4, 180201, 101, 1, 0, "騎士クン…\n今どこにいるの…？\n…会いたいな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4163, 1, 8, 180201, 102, 1, 0, "離れてても分かるよ…\n温かい力で騎士クンに\n守られてるの", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4164, 1, 1, 180201, 1, 2, 0, "わたしが戦えてるのは\n騎士クンのおかげだよ\nいつもありがとう！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4165, 1, 1, 180201, 2, 2, 0, "お腹空いてそう…？\n…内緒で作ったおやつ\n騎士クンも食べる？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4166, 1, 1, 180201, 3, 2, 0, "ひゃうっ！　え！？\nせ、背中はダメ～！\n言ってから触って～！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4167, 1, 1, 180201, 4, 2, 0, "この格好、どうかな？\nえ？　似合ってる…？\n本当？　…うれしい！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4168, 1, 4, 180201, 101, 2, 0, "かわいい？　そうだね\nひらひらが…わたし？\nええっ、そ、そんな！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4169, 1, 8, 180201, 102, 2, 0, "騎士クン、これからも\nずっと…わたしの\nそばにいてください", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4170, 1, 1, 180201, 1, 2, 0, "朝からこんなに\n食べる日が来るなんて\n思ってなかったよ…", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4171, 1, 1, 180201, 2, 2, 0, "お昼を作らなくちゃ\n騎士クン、一緒に\nお料理してみる？", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4172, 1, 1, 180201, 3, 2, 0, "騎士クン、もう眠い？\n眠るまで、わたしが\nずっとそばにいるね", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4174, 1, 1, 180201, 2, 3, 0, "攻撃参加するからこそ\n適度に体を休めるのは\n大切だよね！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4175, 1, 1, 180201, 3, 3, 0, "かわいい…騎士クンと\nシェフィちゃんにも\nプレゼントしたいな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4176, 1, 1, 180201, 4, 3, 0, "いい香り～…\nあれ？　お腹が空いて\nきちゃった…！？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4177, 1, 1, 180201, 5, 3, 0, "温かい…\n騎士クンがくれる\n力みたい…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4179, 1, 1, 180201, 7, 3, 0, "ぱくっ、\nあむ、はむっ…\nううぅ～…やっぱり\n手が止まらないよぉ～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4180, 1, 1, 180201, 8, 3, 0, "新しく覚えたいな…\nみんなで食べられる\n大皿向けの料理♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4182, 1, 12, 104901, 103, 1, 0, "お姉ちゃんの愛\nこのままじゃ\nあふれちゃうよ…♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4183, 1, 12, 104901, 103, 2, 0, "弟くん♪\n弟くんっ♪\nおっとうっとくんっ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4184, 1, 1, 113101, 1, 1, 0, "泳ぐのもいいけど\n今日は砂浜で\nのんびりしようかね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4185, 1, 1, 113101, 2, 1, 0, "さてと\nちょっくらひと泳ぎ\n行ってくるかね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4186, 1, 1, 113101, 3, 1, 0, "あっついねぇ…\nうちわがないと\nしんどいったらないよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4187, 1, 1, 113101, 4, 1, 0, "冷たすぎる飲み物で\n体を冷やさないよう\n気をつけないと…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4188, 1, 1, 113101, 5, 1, 0, "夏はあっという間だし\n悔いがないように\n楽しまないとね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4189, 1, 4, 113101, 101, 1, 0, "きっとこの夏は\nアタシにとって大事な\n想い出になるね…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4190, 1, 8, 113101, 102, 1, 0, "…前に比べたら\nアタシたちの距離も\n近づいたみたいだね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4191, 1, 1, 113101, 1, 2, 0, "もし暇なら\n今日も笹舟釣り\n一緒にどうだい？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4192, 1, 1, 113101, 2, 2, 0, "お前さん、釣りに\n興味が湧いたなら今度\n道具を見に行こうよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4193, 1, 1, 113101, 3, 2, 0, "今から砂浜に行くんだ\nお前さんも一緒に\n散歩するかい？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4194, 1, 1, 113101, 4, 2, 0, "え、日焼け跡？\nおや…本当だ\nすぐ焼けちまうねえ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4195, 1, 4, 113101, 101, 2, 0, "ひゃっ！？\nちょっと！\n日焼け跡をいきなり\n触らないでおくれ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4196, 1, 8, 113101, 102, 2, 0, "お前さんは、その…\nアタシと一緒にいて\n楽しかったかい？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4197, 1, 1, 113101, 1, 2, 0, "おはようさん\n今日も砂浜に行って\nゆっくりしようか", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4198, 1, 1, 113101, 2, 2, 0, "お昼か…\nそうだ、よかったら\n刺身定食、作ろうか？", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4199, 1, 1, 113101, 3, 2, 0, "夜の空を見てるとさ\nなつかしい気持ちに\nなるんだよね", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4201, 1, 1, 113101, 2, 3, 0, "適度に休憩して\n夏バテしないよう\n気をつけないとね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4202, 1, 1, 113101, 3, 3, 0, "あんたたちも\n海を楽しみたいって\n思っているのかね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4203, 1, 1, 113101, 4, 3, 0, "ん～…香りでも\nバカンスらしさを\n楽しめるもんだね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4204, 1, 1, 113101, 5, 3, 0, "こういう格好だと\n少し冷えちまうんだよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4206, 1, 1, 113101, 7, 3, 0, "もぐもぐもぐ…\n遊んだ分、お腹が\n空いちまってねぇ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4207, 1, 1, 113101, 8, 3, 0, "…ん、なんだいこの本\n催眠…うーん読めない\nナナカに聞いてみるか", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4209, 1, 1, 113101, 10, 3, 0, "へえ…\nなかなかいい\n乗り心地じゃないか", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4210, 1, 1, 113101, 11, 3, 0, "そうそうハマグリって\n竿でも釣れるんだよ\n知ってたかい？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4211, 1, 1, 113301, 1, 1, 0, "海を満喫しなきゃね\nちょっとひと泳ぎ\n行ってこようかな？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4212, 1, 1, 113301, 2, 1, 0, "う～ん…♪\nいつもと違った\n開放感～\nなんちゃって！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4213, 1, 1, 113301, 3, 1, 0, "今のナナカちゃんは\nたぶん水属性！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4214, 1, 1, 113301, 4, 1, 0, "水着もバッチリ！\nサイカワサイツヨ\nナナカちゃ～ん☆", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4215, 1, 1, 113301, 5, 1, 0, "んっふっふ\nドキ！　水着だらけの\nギルドハウス！\nとかどうかな？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4216, 1, 4, 113301, 101, 1, 0, "そんなに見られると\nちょ～っと\n恥ずかしいかも…\nなんてねなんてね！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4217, 1, 8, 113301, 102, 1, 0, "ちょっとくらい\nドキッとしてくれても\nいいのにな～、もう…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4218, 1, 1, 113301, 1, 2, 0, "水着魔法少女の\n相方がほしいなぁ～…\nね、君やってみない？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4219, 1, 1, 113301, 2, 2, 0, "いつもの格好と同じ？\nも～、わかってない！\nセクスィー度が違う！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4220, 1, 1, 113301, 3, 2, 0, "どうかなどうかな？\nこのひらひらのとこが\nかわいーかなー\nとか思うんだけど！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4221, 1, 1, 113301, 4, 2, 0, "せっかくだから\nギャルゲーイベント\nごっことか\nやってみる？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4222, 1, 4, 113301, 101, 2, 0, "ナナカちゃんの\n好感度を上げたい？\nならおでかけだー！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4223, 1, 8, 113301, 102, 2, 0, "おうちで水着って\nなんだかコスプレ\nみたいで楽しいかも♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4224, 1, 1, 113301, 1, 2, 0, "おっはよーう！\n今日はバカンス日和！\n海へゴーですぞ～♪", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4225, 1, 1, 113301, 2, 2, 0, "お昼ご飯どうしよ～…\nあ、ナナカちゃんと\nどっか食べに行く？", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4226, 1, 1, 113301, 3, 2, 0, "夜は本を読むのが\n捗りまくりですな～！\n今日はなに読もっかな", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4228, 1, 1, 113301, 2, 3, 0, "次のイベントもあるし\nちょっと休んで体力を\n回復しなきゃね～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4229, 1, 1, 113301, 3, 3, 0, "こういうマスコット\n私にもいたらなぁ\nいっそ作っちゃう…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4230, 1, 1, 113301, 4, 3, 0, "水着魔法少女に\n似合う香りってのも\n考えてみよっかな♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4231, 1, 1, 113301, 5, 3, 0, "夜になると急に冷えて\n肌寒いんだよね～…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4233, 1, 1, 113301, 7, 3, 0, "あんまり食べると\nお腹が出て…でも…\nうう、止まらない～！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4234, 1, 1, 113301, 8, 3, 0, "この催眠魔法の本\n誰にもばれないように\n保管しとかなきゃね…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4236, 1, 1, 113301, 10, 3, 0, "ゴーゴーゴーゴーゴー\nナナカちゃーん♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4237, 1, 1, 113301, 11, 3, 0, "ナナカちゃんの\n誕生～\nなんちゃって", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4238, 1, 1, 113401, 1, 1, 0, "ファイトファイト☆\n一生懸命\n応援するね！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4239, 1, 1, 113401, 2, 1, 0, "みんながんばってる！\n私も応援でみんなを\n盛り上げなきゃ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4240, 1, 1, 113401, 3, 1, 0, "もし元気のない人が\nいたら…応援で少し\nでも元気にしたいな！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4241, 1, 1, 113401, 4, 1, 0, "がんばれ～☆\nよし、発声練習OK！\n応援は、声も大事！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4242, 1, 1, 113401, 5, 1, 0, "せっかく水着なんだし\n泳いだり潜ったりして\n遊びたいな～！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4243, 1, 4, 113401, 101, 1, 0, "チアリーダーとして\nキミを精一杯\n応援したいな…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4244, 1, 8, 113401, 102, 1, 0, "キミへの応援\nちゃんと\n届いてるかな…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4245, 1, 1, 113401, 1, 2, 0, "えへへ…今ね、\nキミへの応援歌を\n考えてたのだ～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4246, 1, 1, 113401, 2, 2, 0, "どうしたの？\n元気がないなら\n私が応援するよ☆", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4247, 1, 1, 113401, 3, 2, 0, "がんばったあとは休憩\nしなきゃね！　私と\n一緒にお昼寝する？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4248, 1, 1, 113401, 4, 2, 0, "シオリンとおそろいの\n水着で応援…\nしてみたいなぁ～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4249, 1, 4, 113401, 101, 2, 0, "次はなにして遊ぶ？\nキミがしたいことを\n教えてよ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4250, 1, 8, 113401, 102, 2, 0, "キミを応援\nすることができて…\n私、すごく幸せ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4251, 1, 1, 113401, 1, 2, 0, "キミって早起き\nなんだね！　私も\n見習わなきゃ！", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4252, 1, 1, 113401, 2, 2, 0, "ちょっとお疲れかな？\nそれじゃあ\n応援してあげるね♪", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4253, 1, 1, 113401, 3, 2, 0, "遅い時間まで\nがんばってえらいね！\nでも無理はダメだよ♪", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4255, 1, 1, 113401, 2, 3, 0, "ちょっとだけ休憩…\n…あう、座り心地が\n良すぎて…眠気が…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4256, 1, 1, 113401, 3, 3, 0, "ここにいるみんなを\n私の応援でもっと\n元気にするのだ～！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4257, 1, 1, 113401, 4, 3, 0, "元気の出る香り～♪\nこれもうまく応援に\n活かせないかな～？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4258, 1, 1, 113401, 5, 3, 0, "ぽかぽかあったまって\n元気をチャージ\nするのだ～☆", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4260, 1, 1, 113401, 7, 3, 0, "いっぱい食べて、\nみんなを応援する\nパワーをつけなきゃ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4261, 1, 1, 113401, 8, 3, 0, "みんなを元気にする\n方法は…どこかに\n書いてないかなぁ…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4263, 1, 1, 113401, 10, 3, 0, "わぁ、この子\n怪獣さんみたいで\nかわいいね～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4264, 1, 1, 113401, 11, 3, 0, "水着でこれに\n乗ってると海にいる\n気分になれるね！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4265, 1, 1, 113501, 1, 1, 0, "すごく忙しいけど\nとても充実した気分\nこれが青春なのかしら", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4266, 1, 1, 113501, 2, 1, 0, "森の静かな雰囲気も\n海の開放的な空気も\nどちらも素敵ね～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4267, 1, 1, 113501, 3, 1, 0, "汚れたお洗濯物は\nみんなが頑張った証\nなんだか愛おしいわ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4268, 1, 1, 113501, 4, 1, 0, "みんなを見てると\n何だか私も\n走りたくなっちゃうわ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4269, 1, 1, 113501, 5, 1, 0, "せっかくの水着だし\n泳ぐ練習でも\nしてみようかしら", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4270, 1, 4, 113501, 101, 1, 0, "みんな～ファイト～！\n応援するのも大事よね\n練習しておかなきゃ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4271, 1, 8, 113501, 102, 1, 0, "先生に教えられること\nまだまだい～っぱいね\n次は何がいいかしら♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4272, 1, 1, 113501, 1, 2, 0, "今日も暑くなりそうね\nこまめな水分補給を\n忘れないでね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4273, 1, 1, 113501, 2, 2, 0, "手伝ってくれるの？\nありがとう♪\n先生、頼りにしてるわ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4274, 1, 1, 113501, 3, 2, 0, "休憩はこまめに\nとりましょうね\n無理は厳禁よ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4275, 1, 1, 113501, 4, 2, 0, "海の遊びって\nどんなのがあるの？\n先生に教えてね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4276, 1, 4, 113501, 101, 2, 0, "水着は初めてだけど\nどう？　変じゃない？\nふふ、ありがとう", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4277, 1, 8, 113501, 102, 2, 0, "あなたと過ごした\nこの夏は、きっと…\n忘れられない思い出ね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4278, 1, 1, 113501, 1, 2, 0, "おはよう～\n朝練前にちゃんと\nごはん食べましょうね", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4279, 1, 1, 113501, 2, 2, 0, "お弁当作ってきたの\n栄養いっぱい摂って\n午後も頑張ろうね", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4280, 1, 1, 113501, 3, 2, 0, "もうお休みしなきゃね\n先生が子守唄を歌って\nあげましょうか？", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4282, 1, 1, 113501, 2, 3, 0, "ふふ、いい気持ちねぇ\n先生、少しだけ\n休憩しちゃおうかしら", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4283, 1, 1, 113501, 3, 3, 0, "あらかわいい♪\nチームにもこういう\nマスコットが欲しいわ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4284, 1, 1, 113501, 4, 3, 0, "あら、海の香り？\n波の音まで聞こえて\nきちゃいそうだわ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4285, 1, 1, 113501, 5, 3, 0, "うふふ\nこれなら濡れた水着を\n乾かすのも簡単ね♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4287, 1, 1, 113501, 7, 3, 0, "これ、美味しいわね♪\nでも、食べ過ぎは\n禁物よ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4288, 1, 1, 113501, 8, 3, 0, "先生だって日々勉強よ\n新しいことを知るのは\nとても楽しいわ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4290, 1, 1, 113501, 10, 3, 0, "あらあら、楽しいわね\nみんなもきっと\nおおはしゃぎね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4291, 1, 1, 113501, 11, 3, 0, "ふふ、これで\n夏らしい気分を\n満喫するわよ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4292, 1, 12, 102301, 103, 1, 0, "サンドイッチも\n作ったし、\nお兄ちゃんのお出迎え\n準備は完璧だね♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4293, 1, 12, 102301, 103, 2, 0, "えへへ、お兄ちゃんと\nぷうきちが居てくれる\nだけで、なんだか\nうれしくなっちゃう♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4294, 1, 1, 113601, 1, 1, 0, "少し赤くなっているな\n日焼けなんてしたのは\nいつ以来だろう", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4295, 1, 1, 113601, 2, 1, 0, "この格好で\n過ごすのは\n少し勇気がいるな…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4296, 1, 1, 113601, 3, 1, 0, "暑いのは平気だが\n肌が外気に触れるの\nにはまだ慣れないな…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4297, 1, 1, 113601, 4, 1, 0, "この格好でも\n堂々としていれば\n恥ずかしくはない…\nのかな？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4298, 1, 1, 113601, 5, 1, 0, "おっと…鎧姿の時と\n重心が違うせいか\nちょっと不安定だな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4299, 1, 4, 113601, 101, 1, 0, "この水着…\n私に似合って\nいるのだろうか…？\n少年には聞きづらいな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4300, 1, 8, 113601, 102, 1, 0, "少年との\n海のバカンス…\nまた行きたい\nものだな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4301, 1, 1, 113601, 1, 2, 0, "あまり見るのは\nよしてくれ…\n恥ずかしいじゃ\nないか…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4302, 1, 1, 113601, 2, 2, 0, "どうかしたのかい？\n私でよければ\n話し相手くらいには\nなろうじゃないか", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4303, 1, 1, 113601, 3, 2, 0, "ひゃっ！\nす、すまない\nちょっと過敏に\nなっているようだ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4304, 1, 1, 113601, 4, 2, 0, "…っくしゅん！\nすまない\n薄着には慣れて\nないものでね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4305, 1, 4, 113601, 101, 2, 0, "まあ、少年になら\n見られても…\nい、いや！\nなんでもない！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4306, 1, 8, 113601, 102, 2, 0, "私がこうして\nゆっくりできるのは\n少年がそばにいて\nくれるからだよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4307, 1, 1, 113601, 1, 2, 0, "この時間なら\nビーチにも人は\n少ないだろうね…\n遊びに行くかい？", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4308, 1, 1, 113601, 2, 2, 0, "昼食のお誘いかい？\nいいよ、付き合おう", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4309, 1, 1, 113601, 3, 2, 0, "もう夜か…\n一日が経つのは\nはやいものだね", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4311, 1, 1, 113601, 2, 3, 0, "これは楽でいいな…\nいつもは鎧姿で\n家具を傷つけないか\n気を使っているからね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4312, 1, 1, 113601, 3, 3, 0, "うん…ふわふわの\n感触が\nよくわかるな…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4313, 1, 1, 113601, 4, 3, 0, "いつもは\n鎧に匂いが\n移ってしまうのが\n悩みなんだよ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4314, 1, 1, 113601, 5, 3, 0, "うーむ…\n涼しいのに暖かい\nなんだか奇妙な気分だ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4316, 1, 1, 113601, 7, 3, 0, "兜を気にせず\n食べられるのは\n楽でいいな…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4317, 1, 1, 113601, 8, 3, 0, "ふむ…鎧がないと\nページがめくりやすい", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4319, 1, 1, 113601, 10, 3, 0, "身軽でいいな…\n鎧を着ていると\n騎乗訓練の時\n馬に申し訳がなくてね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4320, 1, 1, 113601, 11, 3, 0, "貝をうまく使えば\n水中でも\n守りを固められそうだ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4321, 1, 1, 106801, 1, 1, 0, "どこをアジトと\nつなげようかな～…\nこのへんにしよっと♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4322, 1, 1, 106801, 2, 1, 0, "ここってちょっと\n変わった建物だね\n妖精がいるでしょ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4323, 1, 1, 106801, 3, 1, 0, "色んな人を観察できて\n眼福眼福…♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4324, 1, 1, 106801, 4, 1, 0, "家具の配置変更なら\nアタシにおまかせあれ\n…家の形は変えない？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4325, 1, 1, 106801, 5, 1, 0, "新メニューどうしよう\n思いついた人がいたら\nアタシに教えてね！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4326, 1, 4, 106801, 101, 1, 0, "少年、早く帰って\nこないかな…\nよし、迎えに行こう！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4327, 1, 8, 106801, 102, 1, 0, "結局またあちこち\n移動する日々だし…\n少年に会いたいな～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4328, 1, 1, 106801, 1, 2, 0, "どしたの少年？\nお姉さんに何でも\n相談してみな～？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4329, 1, 1, 106801, 2, 2, 0, "あれをああして…\nおや少年！　ごめんね\nすぐに気づけなくて！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4330, 1, 1, 106801, 3, 2, 0, "何？　クリスが\nアタシを探してる？\n…いないって言って！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4331, 1, 1, 106801, 4, 2, 0, "よければリノちゃんと\nシズルちゃんにも\n顔を見せてあげて", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4332, 1, 4, 106801, 101, 2, 0, "少年～！\nもうヘトヘトだよ～！\nお願い癒して～！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4333, 1, 8, 106801, 102, 2, 0, "…アタシが素直に\n助けてって言えるのは\n少年だけだよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4334, 1, 1, 106801, 1, 2, 0, "朝だよ少年！\n爽やかな朝の\nクレープはいかが？", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4335, 1, 1, 106801, 2, 2, 0, "お腹空いたな～\nお昼のクレープ\n少年は食べる？", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4336, 1, 1, 106801, 3, 2, 0, "夜までクレープは\nやめとけ？　ごめん\nそれだけは無理…！", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4338, 1, 1, 106801, 2, 3, 0, "アタシこんなに\nゆっくりしちゃって\nいいんだっけ～？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4339, 1, 1, 106801, 3, 3, 0, "…これ\nネネカが変身\nしてないよね？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4340, 1, 1, 106801, 4, 3, 0, "いい香りだ…\n解析完了\n今度生成しようっと♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4341, 1, 1, 106801, 5, 3, 0, "安心するな～…\nアジトに置けないから\n羨ましいよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4343, 1, 1, 106801, 7, 3, 0, "食べ物を消化するって\n仕組み、どんなものか\n気になる…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4344, 1, 1, 106801, 8, 3, 0, "速読できるけど\n本を読む時くらい\n文字にじっくり\n浸かりたいんだよね…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4346, 1, 1, 113701, 1, 1, 0, "エンジェルとして\nみんなを元気に…！\nアカリ、頑張ります♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4347, 1, 1, 113701, 2, 1, 0, "お姉ちゃんと一緒に\nお兄ちゃんを…えへへ\nまだかな、お兄ちゃん", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4348, 1, 1, 113701, 3, 1, 0, "エンジェルの音色を\n目指して、今日も\nサックスの練習です♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4349, 1, 1, 113701, 4, 1, 0, "お兄ちゃんとの特訓も\n頑張らないと…\n厳しくしてくださいね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4350, 1, 1, 113701, 5, 1, 0, "かわいいエンジェルの\n格好、お兄ちゃんなら\n喜んでくれますよね♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4351, 1, 4, 113701, 101, 1, 0, "エンジェルの力で\nお兄ちゃんのハートも\nイチコロですよ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4352, 1, 8, 113701, 102, 1, 0, "アカリが一番\n元気にしたいのは\nお兄ちゃんですよ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4353, 1, 1, 113701, 1, 2, 0, "お兄ちゃん？\n触りたいなら…\nこことかどうですか♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4354, 1, 1, 113701, 2, 2, 0, "特訓の時間ですか？\nアカリ、楽しみに\nしてたんですよ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4355, 1, 1, 113701, 3, 2, 0, "あ、お兄ちゃん！\nそろそろアカリと\n休憩…しませんか♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4356, 1, 1, 113701, 4, 2, 0, "この羽、触り心地\nいいですか？　あんっ\nくすぐったいですよぉ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4357, 1, 4, 113701, 101, 2, 0, "お兄ちゃんも元気に\nなりたいんですか？\nアカリにお任せです♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4358, 1, 8, 113701, 102, 2, 0, "やんっ♪\nお兄ちゃんったら\nダイタンですね♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4359, 1, 1, 113701, 1, 2, 0, "お兄ちゃん、朝ですよ\nすっごく気持ちのいい\nお天気ですよ…♪", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4360, 1, 1, 113701, 2, 2, 0, "お昼寝しませんか\nお兄ちゃん？\nアカリの膝枕で…♪", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4361, 1, 1, 113701, 3, 2, 0, "夜ですね、お兄ちゃん\nアカリ、なんだか胸が\nドキドキしちゃいます", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4363, 1, 1, 113701, 2, 3, 0, "フカフカですね…\n隣にお兄ちゃんが\nいてくれたらなぁ…♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4364, 1, 1, 113701, 3, 3, 0, "えへへ、アカリも\nこんな風にかわいく\nなりたいな～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4365, 1, 1, 113701, 4, 3, 0, "お兄ちゃんが好きな\n匂いなのかな？\n参考にしますね！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4366, 1, 1, 113701, 5, 3, 0, "体が火照ってきて\nなんだかアカリ…\nクラクラしちゃう…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4368, 1, 1, 113701, 7, 3, 0, "はむ、んぐ…あっ\nほっぺについちゃった\nベトベトですよぉ～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4369, 1, 1, 113701, 8, 3, 0, "エンジェルって色んな\n物語に出てきますよね\nもっと調べなくちゃ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4371, 1, 12, 103201, 103, 1, 0, "あの方の前では\n常に気高く\nありたいですわね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4372, 1, 12, 103201, 103, 2, 0, "どんなに道に迷っても\nあなたさまの元には\n絶対に辿り着く\n自信がありますわ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4373, 1, 12, 102801, 103, 1, 0, "あんたと一緒に\nいるときが一番楽しい\n一時かもしれないわ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4374, 1, 12, 102801, 103, 2, 0, "いつかあんたと一緒に\nサレンディア救護院を\n切り盛り…なんてね♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4375, 1, 1, 113801, 1, 1, 0, "今日も\nエンジェルとして、\nみんなの幸せの\nために頑張らないと", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4376, 1, 1, 113801, 2, 1, 0, "せっかく羽が\nついてるのに\n飛べないのが残念だわ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4377, 1, 1, 113801, 3, 1, 0, "この格好、子供たちに\n妙にウケがいいわね\nま、悪くないけど", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4378, 1, 1, 113801, 4, 1, 0, "や、やっぱりみんな\n注目してくるわね…\nエンジェルって珍しい\nからしかたないか", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4379, 1, 1, 113801, 5, 1, 0, "エンジェルになると\n人見知りがなおる！\n…わけないか", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4380, 1, 4, 113801, 101, 1, 0, "エンジェルの活動は\n大変だけど、自分が\n成長してるって\n実感がうれしいわね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4381, 1, 8, 113801, 102, 1, 0, "もし誰かの願いを\nかなえられるなら\n私はアイツの…\nって何考えてるのよ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4382, 1, 1, 113801, 1, 2, 0, "何ボサっとしてるの！\nさあ、街に出て\n人助けするわよ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4383, 1, 1, 113801, 2, 2, 0, "エンジェリック\nアロー！\nなんてね…え、アンタ\nいつから見てたのよ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4384, 1, 1, 113801, 3, 2, 0, "コラ！\n羽は触らないで！\n壊れたらどうするのよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4385, 1, 1, 113801, 4, 2, 0, "エンジェルじゃなく\nても、アンタはいつも\n人助けしてるわよね\n…ちょっと尊敬するわ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4386, 1, 4, 113801, 101, 2, 0, "この服なら他の子より\n私を見てくれるかも…\nって、な、何\n見てるのよ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4387, 1, 8, 113801, 102, 2, 0, "ね、ねえ、もし私が\n困ってたら、一番に\n駆けつけて…やっぱり\n何でもないわ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4388, 1, 1, 113801, 1, 2, 0, "おはよう…あ、あの、\nエンジェルとして\n町に行くんだけど…\n一緒に行かない？", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4389, 1, 1, 113801, 2, 2, 0, "ふぅ、いいことした\n後のランチは\nおいしいわね", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4390, 1, 1, 113801, 3, 2, 0, "もうこんな時間…\n夜更かししてないで\n早く寝なさいよね", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4392, 1, 1, 113801, 2, 3, 0, "いい座り心地ね～\nまさに天にも昇る\n心地よさだわ～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4393, 1, 1, 113801, 3, 3, 0, "ふふ、かわいい…\nあ、羽付けたら\nもっとかわいく\nなるんじゃないかな？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4394, 1, 1, 113801, 4, 3, 0, "はぁ～落ち着くわ～\n回復アイテムみたいで\n癒やされちゃうわ～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4395, 1, 1, 113801, 5, 3, 0, "わっ、とと…\n危ない、羽が\nこげちゃうところ\nだったわ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4397, 1, 1, 113801, 7, 3, 0, "人の役に立つのって\nすごく大変だから、\nこれでライフ回復よ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4398, 1, 1, 113801, 8, 3, 0, "エンジェルに関する\n本はあるかしら…あ！\nこれ探してた攻略本！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4400, 1, 1, 113901, 1, 1, 0, "ふっふっふ\n吸血鬼らしく\n驚かせてあげましょう", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4401, 1, 1, 113901, 2, 1, 0, "獲物はどこですか～！\nう～ん、もうちょっと\n迫力が欲しいですね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4402, 1, 1, 113901, 3, 1, 0, "トリックオアトリート\n…返事がないので\nイタズラしておきます", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4403, 1, 1, 113901, 4, 1, 0, "ハロウィンですし\nたまには騎士さんでも\n誘ってあげますかね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4404, 1, 1, 113901, 5, 1, 0, "マントは動きに\nメリハリがついて\nいいですね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4405, 1, 4, 113901, 101, 1, 0, "色んな仮装が見られて\nやっぱりハロウィンは\n最高ですね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4406, 1, 8, 113901, 102, 1, 0, "吸血鬼らしい行動って\n噛みついたり…いや\nそれは大胆過ぎです！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4407, 1, 1, 113901, 1, 2, 0, "呼びましたか？\n手短にお願いしますよ\n忙しいんですから！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4408, 1, 1, 113901, 2, 2, 0, "え？　衣装に穴が？\nありがとうございます\n…よく見てるんですね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4409, 1, 1, 113901, 3, 2, 0, "ふわあ…吸血鬼の\n姿でも、眠い時は\n眠いんですよね…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4410, 1, 1, 113901, 4, 2, 0, "ハロウィンってライブ\nとは違った賑やかさが\nあってワクワクします", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4411, 1, 4, 113901, 101, 2, 0, "私を驚かそうとしても\n無駄ですよ！\nすぐ分かるんですから", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4412, 1, 8, 113901, 102, 2, 0, "騎士さん、一緒に\nハロウィンを…いいえ\nなんでもないですっ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4413, 1, 1, 113901, 1, 2, 0, "仕事で寝不足だから\n太陽がまぶしいです…\n私、吸血鬼っぽいかも", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4414, 1, 1, 113901, 2, 2, 0, "お昼ご飯は真っ赤な…\nトマト尽くしのお弁当\nにしてみました", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4415, 1, 1, 113901, 3, 2, 0, "夜はハロウィンが\n盛り上がる時間…\n行きますよ騎士さん！", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4417, 1, 1, 113901, 2, 3, 0, "このフィット感\nどうにか服作りにも\n活かせれば…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4418, 1, 1, 113901, 3, 3, 0, "よく出来てますね…！\nぬいぐるみの服を作る\nのも楽しそうですね！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4419, 1, 1, 113901, 4, 3, 0, "いい感じですね…\n服に合わせた香りって\nいうのも、いいかも…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4420, 1, 1, 113901, 5, 3, 0, "はぁ、温まりますね…\nおっと、マントに火が\n点いちゃいそうです！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4422, 1, 1, 113901, 7, 3, 0, "あぁ、美味しい～\n食べた分は、働いて\n燃焼しますからね！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4423, 1, 1, 113901, 8, 3, 0, "伝説の吸血鬼…\n夜を統べる者…\nほうほう…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4425, 1, 1, 113901, 12, 3, 0, "やっぱりハロウィンと\nいえばお菓子です！\nさぁ配りましょうか！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4427, 1, 1, 114101, 1, 1, 0, "この格好…\nカッコイイのに\n動きやすくて\n着心地も最高ッス！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4428, 1, 1, 114101, 2, 1, 0, "キメポーズはこう…\nいやこっちのほうが…\nなやましいッス…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4429, 1, 1, 114101, 3, 1, 0, "トリックオア\nジャスティス！\nって言ったら何か\nもらえるッスかね？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4430, 1, 1, 114101, 4, 1, 0, "新しい必殺技の\n特訓もしなくちゃ\nいけないッスね…\n忙しいッス！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4431, 1, 1, 114101, 5, 1, 0, "自分のこの姿\nトモね～ちゃんにも\nみせてあげるッス！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4432, 1, 4, 114101, 101, 1, 0, "ホワイトタイガー\n参上ッス！\n今のポーズは\nキマったッス！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4433, 1, 8, 114101, 102, 1, 0, "あの人と一緒だと\nほんとにヒーローに\nなれる気がするッス！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4434, 1, 1, 114101, 1, 2, 0, "どうッスか？\nこの色、自分に\n似合ってるッスかね？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4435, 1, 1, 114101, 2, 2, 0, "ヒマだったら\n必殺技の特訓に\n付き合ってもらって\nいいッスか？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4436, 1, 1, 114101, 3, 2, 0, "ちょうどいいところに\n来たッスね！\nこのキメポーズ\nどうッスか？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4437, 1, 1, 114101, 4, 2, 0, "お！　この格好、\n気になるッスか？\nじっくり見ても\nいいッスよ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4438, 1, 4, 114101, 101, 2, 0, "あんたも一緒に\n新しい技の練習\nどうッスか？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4439, 1, 8, 114101, 102, 2, 0, "今日もあんたと一緒に\nランドソルの平和を\n守るッスよ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4440, 1, 1, 114101, 1, 2, 0, "朝ッス！\n一緒に早朝特訓\nやるッスよ！", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4441, 1, 1, 114101, 2, 2, 0, "お昼休憩ッス！\nヒーローにも\n休息は必要ッス！", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4442, 1, 1, 114101, 3, 2, 0, "闇夜に映える\n白い衣装で\n平和を守るッスよ！", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4444, 1, 1, 114101, 2, 3, 0, "トラも狩りの時以外は\nごろごろしてるって\n本で読んだッスよー", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4445, 1, 1, 114101, 3, 3, 0, "もこもこッスね♪\nこのかわいらしさを\n守ってあげるのも\nヒーローの役目ッス！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4446, 1, 1, 114101, 4, 3, 0, "いい匂いッスね…\nリラックスして\n瞑想するッス！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4447, 1, 1, 114101, 5, 3, 0, "燃える炎は\nヒーローの心意気ッス\nいつまでも\n見てられるッスよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4449, 1, 1, 114101, 7, 3, 0, "もぐもぐ…\nいっぱい食べて\n強いヒーローに\nなるッスよ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4450, 1, 1, 114101, 8, 3, 0, "ちゃんと勉強の本も\n読んでるッスよ…！\nヒーローには賢さも\n必要らしいッス！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4452, 1, 1, 114101, 12, 3, 0, "わわっ、お菓子が\nいっぱいッス！\nみんなで\n食べるッスよ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4454, 1, 12, 103001, 103, 1, 0, "ワタシがチューシンと\nなった今、天下統一は\n目の前デス！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4455, 1, 12, 103001, 103, 2, 0, "もうショーグンってば\nそれはセイシツに\nすることデ～ス…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4456, 1, 1, 114001, 1, 1, 0, "女幽霊…うらめしや…\nうーん、これは\nちょっと違う気が\nするな…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4457, 1, 1, 114001, 2, 1, 0, "何だか寒気がする…\nま、まさか\nこの部屋にも幽霊が？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4458, 1, 1, 114001, 3, 1, 0, "ハロウィンは楽しいね\n童心に帰ったような\n気分だよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4459, 1, 1, 114001, 4, 1, 0, "今度のハロウィンは\nユイとヒヨリを\n誘ってみようかな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4460, 1, 1, 114001, 5, 1, 0, "ハロウィンのお菓子は\nどれもかわいいね\n…一つくらいなら\nいいかな？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4461, 1, 4, 114001, 101, 1, 0, "次は彼と\nどんなハロウィンを\n過ごそうかな…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4462, 1, 8, 114001, 102, 1, 0, "こんな賑やかさも\nいいものだね\n彼といられるのなら\nなおさら…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4463, 1, 1, 114001, 1, 2, 0, "…いたずらしようと\nしてない、キミ？\nふふ、お見通しだよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4464, 1, 1, 114001, 2, 2, 0, "え、私にもお菓子を？\nありがとう…ふふ\nうれしいものだね\nハッピーハロウィン！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4465, 1, 1, 114001, 3, 2, 0, "どうしたの？\n出かけるなら\n私も同行しようか", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4466, 1, 1, 114001, 4, 2, 0, "この格好、舞踏会にも\n出られそうだね\n一曲、踊ってみる？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4467, 1, 4, 114001, 101, 2, 0, "やるからには本気で\nやらせてもらおう\nトリックオア\nトリート！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4468, 1, 8, 114001, 102, 2, 0, "他の子に目移りする\nのも分かるが、その…\nせっかくの特別な衣装\n見てほしいな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4469, 1, 1, 114001, 1, 2, 0, "寝起きの体を\n慣らしておこうか\nほら、剣を取って", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4470, 1, 1, 114001, 2, 2, 0, "お菓子もいいけど\nちゃんと昼食も\nとるんだよ", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4471, 1, 1, 114001, 3, 2, 0, "さあ、行こうか！\nハロウィンは\nこれからが本番だよ", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4473, 1, 1, 114001, 2, 3, 0, "ハロウィンはまだ\nこれからだ\n一休みしようか", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4474, 1, 1, 114001, 3, 3, 0, "キミたちも\n仮装に興味は\nあるかな？　…ふふ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4475, 1, 1, 114001, 4, 3, 0, "いい香りだけど\n幽霊が集まったり\nしないだろうか…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4476, 1, 1, 114001, 5, 3, 0, "暖かい…一息ついたら\nハロウィンの\nパレードを\n見に行こうかな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4478, 1, 1, 114001, 7, 3, 0, "ハロウィンを本気で\n楽しむなら\n食事もしっかり\nとっておかないとね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4479, 1, 1, 114001, 8, 3, 0, "ハロウィンの\nホラー小説か…\nけ、結構\n怖いものだね…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4481, 1, 1, 114001, 12, 3, 0, "お菓子がいっぱいだ…\nこういうのって\n見ているだけで心が\n躍ってしまうね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4483, 1, 1, 114201, 1, 1, 0, "こんなにかわいい\n武器で戦えるとは…\nすごいものだな\n魔法少女は", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4484, 1, 1, 114201, 2, 1, 0, "決めポーズの訓練も\nしっかりやっておくか\n…誰も見ていないな？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4485, 1, 1, 114201, 3, 1, 0, "こういう格好は\nユキあたりがすごく\n似合いそうだな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4486, 1, 1, 114201, 4, 1, 0, "子どもたちにサインを\n求められてしまった…\nこれもまた私の使命！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4487, 1, 1, 114201, 5, 1, 0, "この格好は目立つな…\nいや、提督とは人々の\n視線を集めるものだ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4488, 1, 4, 114201, 101, 1, 0, "あいつと作戦会議を\nしておくか。あれで\nなかなか頼れるからな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4489, 1, 8, 114201, 102, 1, 0, "あいつには私の勇姿を\nちゃんと見ていて\nほしいものだな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4490, 1, 1, 114201, 1, 2, 0, "一仕事終えたことだし\n一緒に甘いものでも\n食べに行くか？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4491, 1, 1, 114201, 2, 2, 0, "怪しげな気配がするな\n貴公も警戒を\n怠るなよ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4492, 1, 1, 114201, 3, 2, 0, "シオリに魔法少女の\n本を借りたんだ\n一緒に読まないか？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4493, 1, 1, 114201, 4, 2, 0, "巡回の時間まで\nお茶にでもするか\n貴公も付き合うがいい", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4494, 1, 4, 114201, 101, 2, 0, "貴公がサポートして\nくれるのなら心強いな\n頼りにしているぞ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4495, 1, 8, 114201, 102, 2, 0, "頭を撫でるな～！\n…まったく、貴公だけ\nだぞ、こんな風に私を\n撫でてくるのは…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4496, 1, 1, 114201, 1, 2, 0, "起きろ貴公！\n魔法少女の朝は\n早いぞ！", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4497, 1, 1, 114201, 2, 2, 0, "ほら、食後の\nデザートだ\nて、適度な糖分は\n体に必要なんだ！", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4498, 1, 1, 114201, 3, 2, 0, "ふわぁ…魔法少女も…\n眠気、には…い、いや\n寝てないぞ！？", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4500, 1, 1, 114201, 2, 3, 0, "任務や訓練で\n疲れた体に\nこれは効くなぁ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4501, 1, 1, 114201, 3, 3, 0, "うむ、かわいいな\nモーラのやつを隣に\n並べようか", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4502, 1, 1, 114201, 4, 3, 0, "ああ、身も心も\n安らぐ…\nまるで魔法だな…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4503, 1, 1, 114201, 5, 3, 0, "この温かさ…\n愛と希望に\n通じるものがある…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4505, 1, 1, 114201, 7, 3, 0, "食事の大切さは\n魔法少女も軍人も\n変わりない\nしっかり食べるんだ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4506, 1, 1, 114201, 8, 3, 0, "魔法少女の本を\n読んでるのだが\nわ、私もこんななのか", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4508, 1, 12, 103301, 103, 1, 0, "オラとあんちゃんは\nボケもツッコミも\nあうんの呼吸だべ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4509, 1, 12, 103301, 103, 2, 0, "あんちゃんと二人で\nネタ合わせ…初めての\n共同作業だべ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4510, 1, 1, 192501, 1, 1, 0, "ぴっぷる！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4511, 1, 1, 192501, 2, 1, 0, "ぴっぷるぴっぷる♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4512, 1, 1, 192501, 3, 1, 0, "ぴっ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4513, 1, 1, 192501, 4, 1, 0, "ぷっぷー", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4514, 1, 1, 192501, 5, 1, 0, "ぴぃ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4515, 1, 1, 192501, 1, 2, 0, "ぴっぷるんるん♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4516, 1, 1, 192501, 2, 2, 0, "ぷぅ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4517, 1, 1, 192501, 3, 2, 0, "ぴぴっ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4518, 1, 1, 192501, 4, 2, 0, "ぷぅー…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4519, 1, 1, 192501, 1, 2, 0, "ちゅ！", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4520, 1, 1, 192501, 2, 2, 0, "ぷはー", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4521, 1, 1, 192501, 3, 2, 0, "ぴぇー", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4531, 1, 1, 114301, 1, 1, 0, "魔法少女になったら\n髪型も変わるんだね\n色々試してみようかな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4532, 1, 1, 114301, 2, 1, 0, "…煌く剣技で、\n悪を断つ！\nうん、今日も\n完璧なポーズだね♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4533, 1, 1, 114301, 3, 1, 0, "ミクマ流剣術で、\n魔法少女の技に使え\nそうなものは…よし、\n早速試してみよっと♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4534, 1, 1, 114301, 4, 1, 0, "魔法少女になったから\nには、サインの練習も\nしないと…\nよし、頑張るぞ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4535, 1, 1, 114301, 5, 1, 0, "魔法少女の姿はすごく\nかわいいけど…この\n姿はジュンさんたちに\n見せられないなぁ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4536, 1, 4, 114301, 101, 1, 0, "あの人は魔法少女\nのこと好きなのかな？\n…好きだったら\nうれしいな♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4537, 1, 8, 114301, 102, 1, 0, "…やっぱり、\nあの人がいないと\n特訓に身が入らない…\n早く帰ってこないかな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4538, 1, 1, 114301, 1, 2, 0, "もし時間があるなら、\n私と街の見回りに\n行かない？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4539, 1, 1, 114301, 2, 2, 0, "あ、いいところに！\n新しい魔法剣術を\n編み出したから、\n見てくれないかな？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4540, 1, 1, 114301, 3, 2, 0, "魔法少女の姿が気に\nなるのは分かるけど…\nず、ずっと見られる\nのは照れるからやめて", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4541, 1, 1, 114301, 4, 2, 0, "変身ポーズ中の動き、\n少し変えた方が\nいいかな…\nあなたはどう思う？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4542, 1, 4, 114301, 101, 2, 0, "よければ、\n魔法少女の特訓に\n付き合ってくれない？\nその…二人きりでさ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4543, 1, 8, 114301, 102, 2, 0, "私…あなただけの\n魔法少女になっても…\nふふっ、な～んてね♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4544, 1, 1, 114301, 1, 2, 0, "今日もいい朝だね\nさあ、一緒に\n街の見回りに行こうか", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4545, 1, 1, 114301, 2, 2, 0, "もう昼か…なにか\n食べたいものある？\n簡単なのでいいなら、\n私が作ってあげるよ", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4546, 1, 1, 114301, 3, 2, 0, "そろそろ休もうか\n…なんなら、添い寝\nしてあげようか？\nふふっ、冗談だよ♪", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4548, 1, 1, 114301, 2, 3, 0, "これは…\n心身ともに安らぐね\nなんだか\n眠くなっちゃいそう", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4549, 1, 1, 114301, 3, 3, 0, "か、かわいい…これ、\n魔法で動かせたり\nしないかな？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4550, 1, 1, 114301, 4, 3, 0, "いい香りだね\nなんだか頭が冴えて\nきたし、新しい技でも\n考えようかな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4551, 1, 1, 114301, 5, 3, 0, "あったかいな…\nこの温もりは、\n魔法少女の魔法でも\n再現不可能だね…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4553, 1, 1, 114301, 7, 3, 0, "こ、これは…\nおいしすぎて手が\n止まらないかも", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4554, 1, 1, 114301, 8, 3, 0, "この本の\nキャラの動き、\n変身ポーズの\n参考にできるかも！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4556, 1, 1, 114401, 1, 1, 0, "メリークリスマス！\nさあ、素敵な\nパーティーを始めると\nいたしましょう！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4557, 1, 1, 114401, 2, 1, 0, "ふふっ、\nいくつになっても、\nやはりクリスマスは、\nワクワクしますわね♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4558, 1, 1, 114401, 3, 1, 0, "自分で料理をしてみて\nシェフたちのすごさが\n改めて分かりましたわ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4559, 1, 1, 114401, 4, 1, 0, "何事も自分で\n体験してみると\n新しい発見が\nあるものですわね♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4560, 1, 1, 114401, 5, 1, 0, "やはり、クリスマスは\n街が活発になり\nますわね…これは、\n絶好の商機ですわ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4561, 1, 4, 114401, 101, 1, 0, "他のクリスマス料理も\n作れるようになったら\nあのお方は食べて\nくださるかしら…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4562, 1, 8, 114401, 102, 1, 0, "あのお方に贈る\nプレゼント…\nこれはかなりの\n難題ですわね…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4563, 1, 1, 114401, 1, 2, 0, "メリークリスマス！\nよろしければ、一緒に\nクリスマスの準備を\nいたしませんか？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4564, 1, 1, 114401, 2, 2, 0, "まあ、クリスマスの\n飾りつけを手伝って\nいただけるのですか？\nありがとうございます", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4565, 1, 1, 114401, 3, 2, 0, "クリスマスに合わせて\nあつらえた自慢の衣装\nなのですが…その、\nいかがでしょうか？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4566, 1, 1, 114401, 4, 2, 0, "幼い頃はサンタさんが\nいらっしゃるのを\n待ちわびたものです\n今は…内緒ですわ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4567, 1, 4, 114401, 101, 2, 0, "も、もしお時間が\nあるようでしたら、\nクリスマスの街を\n一緒に歩きませんか？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4568, 1, 8, 114401, 102, 2, 0, "来年のクリスマスも\nあのお方と一緒に\n迎えられたら…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4569, 1, 1, 114401, 1, 2, 0, "時は金なり…\nせっかくの朝ですもの\n時間を有効活用\nいたしましょう！", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4570, 1, 1, 114401, 2, 2, 0, "お昼になりましたわね\nさあ、\nパーティーの準備を\n進めますわよ！", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4571, 1, 1, 114401, 3, 2, 0, "静かな夜ですわね…\nもうちょっとだけ、\nあなたさまとお話を\nしていたい気分です", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4573, 1, 1, 114401, 2, 3, 0, "適度な休息は\n成功の秘訣ですわ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4574, 1, 1, 114401, 3, 3, 0, "このかわいらしさ…\nいつまでも見て\nいられそうですわ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4575, 1, 1, 114401, 4, 3, 0, "人をもてなすには\nこうしたところにも\n気を配らないと\nいけませんわね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4576, 1, 1, 114401, 5, 3, 0, "暖かい…\nこういう憩いの\nひと時こそ、大事に\nしたいものですわね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4578, 1, 1, 114401, 7, 3, 0, "この奥深い味わい…\n何か隠し味など\n使われていまして？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4579, 1, 1, 114401, 8, 3, 0, "クリスマス料理の\nレシピも、\nこれだけあると\n迷ってしまいますわね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4581, 1, 1, 114401, 14, 3, 0, "この絵本、とっても\n面白いつくりですわね\nうちの商品に\nしたいぐらいですわ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4582, 1, 1, 114401, 15, 3, 0, "これだけ大きな\n着ぐるみ…\n職人の技術が\n光りますわね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4583, 1, 1, 114601, 1, 1, 0, "クリスマスといえば\nパーティーよね！\nいっぱい飲むわよ～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4584, 1, 1, 114601, 2, 1, 0, "私を麦しゅわだけの\n女と思ったら\n大間違いなんだから♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4585, 1, 1, 114601, 3, 1, 0, "クリスマスらしい\n飲み物も結構いいわね\n私も今度\n作ってみようかしら", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4586, 1, 1, 114601, 4, 1, 0, "暖かい部屋で飲む\n麦しゅわも\nまた格別なのよね…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4587, 1, 1, 114601, 5, 1, 0, "今日はオフの日だし\n朝から飲んでも\n怒られないわよね？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4588, 1, 4, 114601, 101, 1, 0, "クリスマスの\nデートスポットで\n彼と一緒に…ってなに\n考えてるのユカリ！？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4589, 1, 8, 114601, 102, 1, 0, "背伸びしなくても\nありのままの自分を\n受け入れてくれるって\nすごく…幸せ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4590, 1, 1, 114601, 1, 2, 0, "大丈夫よ～\n飲んでない\n飲んでないひゃら～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4591, 1, 1, 114601, 2, 2, 0, "クリスマスは\n特別だもの\n特別な一杯を\n飲まないとね♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4592, 1, 1, 114601, 3, 2, 0, "メリークリスマース！\nほらほらぁ\n乾杯しましょ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4593, 1, 1, 114601, 4, 2, 0, "お姉さんの\n頼れるところ\n見せてあげるわね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4594, 1, 4, 114601, 101, 2, 0, "何かあったら\n頼ってくれても\nいいんだからね？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4595, 1, 8, 114601, 102, 2, 0, "ふふ…これからも\nキミのこと…\n頼りにしちゃうからね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4596, 1, 1, 114601, 1, 2, 0, "さわやかな朝ね\n…二日酔いがないと\n朝ってこんなに\n気持ちいいのね", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4597, 1, 1, 114601, 2, 2, 0, "昼間から飲むのって\nまた格別よね…って\nガマン、ガマンよ…", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4598, 1, 1, 114601, 3, 2, 0, "もうこんな時間かあ…\nあとは寝るだけだし\nちょっとくらいは…", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4600, 1, 1, 114601, 2, 3, 0, "ゴロゴロするのって\n最高ね～\nあとは麦しゅわが\nあればなぁ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4601, 1, 1, 114601, 3, 3, 0, "私だって…\nかわいいものは\n好きでいいわよね？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4602, 1, 1, 114601, 4, 3, 0, "んっ～…いい香りね\n日頃の疲れが\n取れる気がするわ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4603, 1, 1, 114601, 5, 3, 0, "ポカポカする～\n…なんだかのどが\nかわいてきたわね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4605, 1, 1, 114601, 7, 3, 0, "もぐもぐ…労働の\n後のご飯はやっぱり\nおいしいわぁ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4606, 1, 1, 114601, 8, 3, 0, "クリスマスデート\n完全攻略マニュアル？\n…読んでおかなくちゃ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4608, 1, 1, 114601, 14, 3, 0, "あら、絵が飛び出して\n見えるけど…\n酔っ払ってるせいじゃ\nないわよね…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4609, 1, 1, 114601, 15, 3, 0, "いい手触りね～\nウチにもほしいけど\n麦しゅわをこぼしたら\n大変そうだわ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4610, 1, 12, 101601, 103, 1, 0, "ヒデサイの視線\nずっと独り占め\nしてたいな～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4611, 1, 12, 101601, 103, 2, 0, "ヒデサイといるだけで\nうち、おに楽しいし！\nヒデサイもっしょ～？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4612, 1, 1, 114501, 1, 1, 0, "メリークリスマス！\nふふ、口にするだけで\n何だか楽しくなるわね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4613, 1, 1, 114501, 2, 1, 0, "いつもは子どもたちを\n見守る方だけど\n今ははしゃいでも\nいいわよね？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4614, 1, 1, 114501, 3, 1, 0, "ツリーや料理\nそしてプレゼント！\nいくつになっても\nやっぱり心躍るわね♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4615, 1, 1, 114501, 4, 1, 0, "飾り付けも料理も\nいいセンスしてるわね\n救護院のパーティーの\n参考にさせてもらうわ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4616, 1, 1, 114501, 5, 1, 0, "焼きそばパンを\nクリスマスの定番に\nするにはどうすれば…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4617, 1, 4, 114501, 101, 1, 0, "これ、あいつの\n好きそうな料理だわ\n取っといて食べさせて\nあげようかしら♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4618, 1, 8, 114501, 102, 1, 0, "あいつのプレゼント\nちょっとだけ奮発\nしたのよね…\n喜んでくれるかしら", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4619, 1, 1, 114501, 1, 2, 0, "このドレス\n似合ってるかしら？\n今だけは見とれても\n許してあげるわ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4620, 1, 1, 114501, 2, 2, 0, "見て、このケーキ！\nおいしそうでしょ\nたまにはこんな贅沢も\nいいわよね♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4621, 1, 1, 114501, 3, 2, 0, "料理も飲み物も\nおいしいわね！\nいっぱい食べて\n元を取らないと♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4622, 1, 1, 114501, 4, 2, 0, "子どもたちと一緒に\n雪遊び、楽しそうね\nあんたもどう？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4623, 1, 4, 114501, 101, 2, 0, "せっかくの\nクリスマスなんだもの\nエスコート\nよろしくね♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4624, 1, 8, 114501, 102, 2, 0, "寒い？　そう…\nなら、もっとこっち\n来ていいわよ…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4625, 1, 1, 114501, 1, 2, 0, "ほら、早く起きて？\nパーティー用の\n買い出しに\n付き合ってちょうだい", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4626, 1, 1, 114501, 2, 2, 0, "パーティーの料理の\n準備をしながらお昼も\n作っておいたわ\n我ながらいい手際ね", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4627, 1, 1, 114501, 3, 2, 0, "にぎやかなパーティー\nもいいけど、聖夜の\n余韻を感じるのも\n結構素敵ね", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4629, 1, 1, 114501, 2, 3, 0, "いい座り心地だわ～\n腕のいい職人の仕事ね\nうちでも扱いたいわ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4630, 1, 1, 114501, 3, 3, 0, "かわいい！\n子どもたちに\nプレゼントしたら\n喜んでくれるかも", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4631, 1, 1, 114501, 4, 3, 0, "華やかな香りね\nクリスマスに\nピッタリだわ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4632, 1, 1, 114501, 5, 3, 0, "寒い日にはやっぱり\nこれよね…\nはぁ、極楽極楽♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4634, 1, 1, 114501, 7, 3, 0, "んー、おいしいわ！\n焼きそばパン味の\nものも、作れない\nかしら？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4635, 1, 1, 114501, 8, 3, 0, "たまにはのんびり\n読書でも…なんだか\nラブロマンスばかりね\nクリスマスだから？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4637, 1, 1, 114501, 14, 3, 0, "これ、面白いわね！\n子どもたちの\nプレゼントに\nピッタリだわ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4638, 1, 1, 114501, 15, 3, 0, "こんないい毛並み\n初めて見るわ…\n一体どこで手に\n入るのかしら？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4639, 1, 1, 114701, 1, 1, 0, "アタシもみんなと\n一緒に正月を\n楽しんでいいのか？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4640, 1, 1, 114701, 2, 1, 0, "今年こそ、もう少し\n身長が伸びます\nように…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4641, 1, 1, 114701, 3, 1, 0, "げっ！　お年玉が\n全然残ってないぞ！？\nそんな、嘘だろ…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4642, 1, 1, 114701, 4, 1, 0, "大勢で過ごす\n正月か…初めてだけど\n悪くないもんだな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4643, 1, 1, 114701, 5, 1, 0, "んー…ここじゃ\n天楼覇断剣の\n出番はなさそうだな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4644, 1, 4, 114701, 101, 1, 0, "アイツらと作る\n戦い以外の思い出も\nもっと増やしたいな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4645, 1, 8, 114701, 102, 1, 0, "こんなに賑やかな\n正月は初めてだ\n…温かくて、いいな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4646, 1, 1, 114701, 1, 2, 0, "記憶がなくったって\n楽しい思い出は\n新しく作れるよな？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4647, 1, 1, 114701, 2, 2, 0, "早くでかけるぞ！\nアタシはもっと\n正月を楽しみたい！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4648, 1, 1, 114701, 3, 2, 0, "なあ、オマエはもう\nノウェムって名前\n覚えてくれたよな？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4649, 1, 1, 114701, 4, 2, 0, "この服、アタシに\n似合ってるのか？\n…そっか。\nありがとな！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4650, 1, 4, 114701, 101, 2, 0, "オマエらと見た朝日は\nきっと忘れないぞ！\n思い出ができたな！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4651, 1, 8, 114701, 102, 2, 0, "いつかオクトーも\n一緒に、みんなで\n正月を過ごせたらな…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4652, 1, 1, 114701, 1, 2, 0, "朝から元気だな！\nアタシもいっちょ\n見習ってみるか！", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4653, 1, 1, 114701, 2, 2, 0, "昼飯は何にする？\n正月はうまいものが\n多くて迷うよな！", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4654, 1, 1, 114701, 3, 2, 0, "いい夜だな！\nアタシと一緒に\n夜更かしを楽しむか？", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4656, 1, 1, 114701, 2, 3, 0, "正月らしく、たまには\nゆっくりするのも\nいいかもな…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4657, 1, 1, 114701, 3, 3, 0, "なんだ？\nオマエも初詣に\n行きたいのか？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4658, 1, 1, 114701, 4, 3, 0, "すーはー…\n神殿のニオイがするな\nいい事ありそうだ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4659, 1, 1, 114701, 5, 3, 0, "コイツがあれば\nうまい魚が\n焼けそうだな…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4661, 1, 1, 114701, 7, 3, 0, "ううー\n食べ過ぎて帯が\nきつくなってきたぞ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4662, 1, 1, 114701, 8, 3, 0, "おみくじって\n書いてあることが\nむずかしいんだよな…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4664, 1, 1, 180101, 1, 1, 0, "困ってる人は\nいないみたいだし…\nちょっとここで、\n特訓しようかな♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4665, 1, 1, 180101, 2, 1, 0, "空が飛べるんだから\nもっとたくさんの人を\n助けにいけちゃう！\n…ここじゃ飛べないや", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4666, 1, 1, 180101, 3, 1, 0, "炎の翼\nかっこいいなー！\nこれ、もっと大きく\nできないのかな？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4667, 1, 1, 180101, 4, 1, 0, "やっぱり変身すると\nお腹すくんだね…\n…今ならペコさんの\n食欲の理由が分かるよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4668, 1, 1, 180101, 5, 1, 0, "色なんかは違うけど…\nマントがあるから\n騎士くんとお揃いに\nなったみたい♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4669, 1, 4, 180101, 101, 1, 0, "…騎士くんがいないと\nなんか調子でない…\n早く会いたいよぉ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4670, 1, 8, 180101, 102, 1, 0, "この姿になると、\n騎士くんを近くに\n感じる…なんだろ\nドキドキしちゃう…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4671, 1, 1, 180101, 1, 2, 0, "騎士くん騎士くん！\n今大丈夫？\nトレーニングメニュー\n考えるの手伝って！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4672, 1, 1, 180101, 2, 2, 0, "…うにゃっ！？\nこ、この姿になっても\n尻尾は触っちゃ\nダメー！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4673, 1, 1, 180101, 3, 2, 0, "もしかして、この服が\n気になってるの？\nだったら、もっと\n近くで見ていいよ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4674, 1, 1, 180101, 4, 2, 0, "あ、騎士くん！\n困ってる人がいないか\n見回りに行くんだけど\nついてきて欲しいな！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4675, 1, 4, 180101, 101, 2, 0, "耳に触りたいの？\nだったら頭もなでて！\nそしたら、好きなだけ\n触っていいよ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4676, 1, 8, 180101, 102, 2, 0, "…あたし、強くなって\n騎士くんを守るよ！\nだから…これからも\n一緒にいていい？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4677, 1, 1, 180101, 1, 2, 0, "おっはよー！\nさあさあ騎士くん！\n今日も一緒に\nはりきっていこー♪", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4678, 1, 1, 180101, 2, 2, 0, "お昼、お昼～♪\n今日のご飯は何かな～\nあ、騎士くん！\n早くお昼食べ行こ！", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4679, 1, 1, 180101, 3, 2, 0, "今日もお疲れ様ー！\n騎士くん、あたしが\nマッサージしよっか？\n結構評判なんだよ！", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4681, 1, 1, 180101, 2, 3, 0, "うにゃぁ…\nこの気持ちよさ…\n溶けちゃいそう…\nにゃぁ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4682, 1, 1, 180101, 3, 3, 0, "か、かわいい～\nこれ、なでなで\nしていいんだよね…？\nよしよし…♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4683, 1, 1, 180101, 4, 3, 0, "とってもいい香り！\n気分も落ち着くし、\n特訓の後なんかに\nいいかもー！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4684, 1, 1, 180101, 5, 3, 0, "ぬくぬくだぁ…\nなんだか、\nふにゃふにゃに\nなっちゃうよぉ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4686, 1, 1, 180101, 7, 3, 0, "お、おいひぃ…！\nこの姿になると\nお腹すくし、すっごく\n助かっちゃった！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4687, 1, 1, 180101, 8, 3, 0, "この本で勉強して、\nぶんぶりょーどーの\n超ウルトラヒヨリに\nなっちゃうぞー！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4689, 1, 1, 111801, 1, 1, 0, "今年もみんなで\nおいしいものを\nたくさん食べたいです", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4690, 1, 1, 111801, 2, 1, 0, "挑戦は大切ですよね☆\nというわけで\nおせちに魔物を…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4691, 1, 1, 111801, 3, 1, 0, "おせちは縁起物です！\n食べれば食べるほど\n幸せになるはずです！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4692, 1, 1, 111801, 4, 1, 0, "食べ物がたくさん\n入った福袋は\nないんでしょうか…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4693, 1, 1, 111801, 5, 1, 0, "初詣で何を\nお願いしましょう…\n五穀豊穣？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4694, 1, 4, 111801, 101, 1, 0, "ずっとこうして\n年の始まりを\n過ごしたいですね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4695, 1, 8, 111801, 102, 1, 0, "いつか二人きりで\n初日の出を\n見たいです", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4696, 1, 1, 111801, 1, 2, 0, "お餅おかわりです！\nいくらでも\n食べられますよ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4697, 1, 1, 111801, 2, 2, 0, "おせちの食材を\n取りに行きませんか？\nいい運動です☆", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4698, 1, 1, 111801, 3, 2, 0, "凧揚げって\n難しいですね～\n糸が絡まっちゃいます", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4699, 1, 1, 111801, 4, 2, 0, "一年の計は元旦にあり\n今年一年分の\n献立を作りましょう！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4700, 1, 4, 111801, 101, 2, 0, "ほらほら！\nもっと近くで着物を\n見ていいんですよ☆", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4701, 1, 8, 111801, 102, 2, 0, "あなたはどんな\n初夢を見ましたか？\nわたしは…内緒です☆", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4702, 1, 1, 111801, 1, 2, 0, "年明けの朝は\n心なしか\n気持ちいいですね☆", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4703, 1, 1, 111801, 2, 2, 0, "お雑煮のあとは\nおしるこで\nおやつタイムです！", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4704, 1, 1, 111801, 3, 2, 0, "あたたかくして\n幸せな初夢を\n見ましょうね", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4706, 1, 1, 111801, 2, 3, 0, "はぁ～屋台巡りを\n満喫しました☆\n一休み一休み", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4707, 1, 1, 111801, 3, 3, 0, "縁起のいい\nぬいぐるみを並べて\n福を呼びましょう！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4708, 1, 1, 111801, 4, 3, 0, "おしるこのアロマって\nないんでしょうか？\nそれなら作るしか…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4709, 1, 1, 111801, 5, 3, 0, "ここでお餅を\n焼いたらおいしく\nなりそうですね～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4711, 1, 1, 111801, 7, 3, 0, "おいしすぎて\n食べる手が\n止まりません～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4712, 1, 1, 111801, 8, 3, 0, "新しいおせち料理の\nヒントは…なるほど…\nふむふむ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4714, 1, 12, 100601, 103, 1, 0, "お兄ちゃんとあんな\n特訓やこんな特訓が\nしたいなぁ、えへへ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4715, 1, 12, 100601, 103, 2, 0, "お兄ちゃんに褒めて\nもらうと、身体が\nじぃーんと\n熱くなっちゃいます♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4716, 1, 1, 115001, 1, 1, 0, "見目麗しい衣装に\n身を包むのも\n悪い気分では\nありませんね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4717, 1, 1, 115001, 2, 1, 0, "縁起物で\n験を担ぐというのも\nたまにはよいでしょう", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4718, 1, 1, 115001, 3, 1, 0, "非効率なので\nあまり行いませんが\n私も手料理くらいは\n作れるのですよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4719, 1, 1, 115001, 4, 1, 0, "研究のためなら\n時に遠路を超え\n実地へ赴くことも\n必要なのです", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4720, 1, 1, 115001, 5, 1, 0, "トゥンヌスの\n突然変異…\n大いに興味を\nそそられますね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4721, 1, 4, 115001, 101, 1, 0, "漁師の技法も\n再現できたことで\nまた一つ、仮説が\n実証できました", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4722, 1, 8, 115001, 102, 1, 0, "正月くらいは\n私ものんびりしたい\n気持ちなのですよ\n意外ですか？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4723, 1, 1, 115001, 1, 2, 0, "模倣に頼らずとも\n衣装に合わせた\n所作程度は\nできて当然です", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4724, 1, 1, 115001, 2, 2, 0, "お年玉の使い道は\n思いつきましたか\nゆっくり考えても\nいいのですよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4725, 1, 1, 115001, 3, 2, 0, "トランプでも花札でも\n相手をしましょう\n手心が必要なら\nいつでも言いなさい", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4726, 1, 1, 115001, 4, 2, 0, "初詣でお祈りする\n内容ですか\n…ヒミツ、として\nおきましょう", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4727, 1, 4, 115001, 101, 2, 0, "私たちでオーマ中の\nトゥンヌスを\n釣り上げましょうか\n…冗談ですよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4728, 1, 8, 115001, 102, 2, 0, "見返りを求めない願い\nそれは祈りに通じるの\nかもしれませんね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4729, 1, 1, 115001, 1, 2, 0, "…貴方がその眠たげな\n顔を洗ってくる間に、\n温かいお茶でも\n淹れておきましょう", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4730, 1, 1, 115001, 2, 2, 0, "さて、本格的に\n活動を再開します\n貴方も同行しなさい\n昼食は済みましたか？", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4731, 1, 1, 115001, 3, 2, 0, "私はもう少し\n作業を片付けてから\n寝ることにします\n早く眠りなさい", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4733, 1, 1, 115001, 2, 3, 0, "少し休みます…\n時間が来たら\n起こすように", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4734, 1, 1, 115001, 3, 3, 0, "贈り物を模倣して\n潜入…試してみる\n価値はありそうですね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4735, 1, 1, 115001, 4, 3, 0, "嗅覚から得られる\n情報量は侮れません\n動物の模倣において\n注意すべき点ですよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4736, 1, 1, 115001, 5, 3, 0, "穏やかな炎による\nリラックス効果は\n本能によるものかも\nしれませんね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4738, 1, 1, 115001, 7, 3, 0, "いかなる強者でも\n兵站、補給は\n必要なものです", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4739, 1, 1, 115001, 8, 3, 0, "あらゆる学問は\n完璧な模倣のための\n大きな礎となります", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4741, 1, 1, 115501, 1, 1, 0, "新たに得たこの力で\n仲間と目指す未来を\n切り拓いてみせます", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4742, 1, 1, 115501, 2, 1, 0, "ここは、みなさまと…\nわたくしの\n帰る場所でございます", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4743, 1, 1, 115501, 3, 1, 0, "主さまも精霊の召喚を\n試してみますか？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4744, 1, 1, 115501, 4, 1, 0, "主さま、おこづかいは\n足りていますか？\n遠慮は無用ですよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4745, 1, 1, 115501, 5, 1, 0, "この装束をまとうと\n自然と背筋が伸びる\n思いがいたします", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4746, 1, 4, 115501, 101, 1, 0, "進化した精霊に\n負けぬよう\n修行に励みます", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4747, 1, 8, 115501, 102, 1, 0, "仲間たちといられる\nこの幸せが、末永く\n続きますように…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4748, 1, 1, 115501, 1, 2, 0, "主さまのお手手は\n大きくて温かいですね\nぎゅっぎゅっ…ふふ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4749, 1, 1, 115501, 2, 2, 0, "今度、主さまにも\n里に伝わる昔話を\nお話しいたしましょう", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4750, 1, 1, 115501, 3, 2, 0, "里で採れた果物を\n持って参りました\n召し上がってください", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4751, 1, 1, 115501, 4, 2, 0, "里のお土産を\nここのみなさまに\nお配りいたしましょう", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4752, 1, 4, 115501, 101, 2, 0, "仲間と囲む食卓…この\n温もりは、みなさまの\n心の温かさなのですね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4753, 1, 8, 115501, 102, 2, 0, "主さまに名前を\n呼んでいただくと\n心が温かくなります♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4754, 1, 1, 115501, 1, 2, 0, "ふふっ…主さま\n寝癖がついて\nおりますよ？", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4755, 1, 1, 115501, 2, 2, 0, "今日の昼食は\n里の伝統料理です\nお口に合うでしょうか", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4756, 1, 1, 115501, 3, 2, 0, "主さまが眠れるよう\n安らぎの風を…\nおやすみなさいませ", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4758, 1, 1, 115501, 2, 3, 0, "この癒し…\n主さまにも味わって\nいただきたい…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4759, 1, 1, 115501, 3, 3, 0, "この愛らしさ…\n精霊たちと似たものを\n感じます…♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4760, 1, 1, 115501, 4, 3, 0, "精霊よ…この安らぐ\n香りを、隅々まで\n届けてくださいまし", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4761, 1, 1, 115501, 5, 3, 0, "瞑想するのに\nちょうどよい暖かさで\nございます", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4763, 1, 1, 115501, 7, 3, 0, "進化した精霊を\n使役するためにも\nよく食べて体力を…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4764, 1, 1, 115501, 8, 3, 0, "風の精霊について\nもっと詳しく知って\nおかなければ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4766, 1, 1, 115601, 1, 1, 0, "儀式の時を思い出して\n精霊に呼びかける…\n出ておいで～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4767, 1, 1, 115601, 2, 1, 0, "ひゃうっ！　もう…\n急に背中を触るなんて\nいたずら好きな精霊に\n育てた覚えはないのに", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4768, 1, 1, 115601, 3, 1, 0, "儀式用の衣装…\nあんまり激しい動きは\nできないよね…でも\n戦う時は気にしない！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4769, 1, 1, 115601, 4, 1, 0, "そうだ…精霊に\n騎士クンのそばで\n見守っていてって\n頼んでみようかな…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4770, 1, 1, 115601, 5, 1, 0, "ふふっ、こうやって、\n楽しく精霊とお話し\nしていられる、平穏な\n日々が続くといいな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4771, 1, 4, 115601, 101, 1, 0, "き、きき、騎士クン！\nわたしと、あの…！\nうう…練習ですら\n上手く誘えないよぉ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4772, 1, 8, 115601, 102, 1, 0, "あっ、精霊が…\nふふ、応援ありがとう\nわたしの想い…いつか\n必ず伝えるね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4773, 1, 1, 115601, 1, 2, 0, "新しく覚えた料理\n作ってみたんだけど\n騎士クンも食べない？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4774, 1, 1, 115601, 2, 2, 0, "騎士クン、\n疲れてない？\nわたしと精霊で\n治療するよ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4775, 1, 1, 115601, 3, 2, 0, "もう、いたずらしない\n…騎士クン！？\nご、ごめんなさい！\n精霊だと思って！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4776, 1, 1, 115601, 4, 2, 0, "騎士クン！\n時間が合ったら、\n精霊と一緒に\nお話しない？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4777, 1, 4, 115601, 101, 2, 0, "…こういう服着るの\n初めてなんだけど…\nえっ、かわいい…？\nあ、ありがとう…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4778, 1, 8, 115601, 102, 2, 0, "困ったことがあったら\n相談してね。わたし、\n騎士クンのためなら…\n色々できるよ、色々！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4779, 1, 1, 115601, 1, 2, 0, "おはよう騎士クン！\n今からお外で\n瞑想するんだけど…\n騎士クンもどうかな？", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4780, 1, 1, 115601, 2, 2, 0, "お昼の時間だ\nご飯ならわたしが\n用意するから\nゆっくりしてて", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4781, 1, 1, 115601, 3, 2, 0, "もうこんな時間…\n騎士クン、はいどうぞ\n寝る前の温かいミルク\nぐっすり眠れるよ♪", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4783, 1, 1, 115601, 2, 3, 0, "…うん、落ち着く\n精霊との魔法で\nこんな風に誰かを\n安心させたいな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4784, 1, 1, 115601, 3, 3, 0, "かわいい…\n精霊も気に入ってる…\nそうだ、同じ物\n作ってあげようかな？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4785, 1, 1, 115601, 4, 3, 0, "いい香り…これ、\nうちに置いたら、\nお母さん喜ぶかも！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4786, 1, 1, 115601, 5, 3, 0, "あったかい…\nまるで誰かが\n抱きしめてくれた\nみたい…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4788, 1, 1, 115601, 7, 3, 0, "自然の恵みに\n感謝して…\nいただきます♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4789, 1, 1, 115601, 8, 3, 0, "もっと精霊について\n勉強して、みんなを\n守れるように\nなりたいな…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4791, 1, 12, 102201, 103, 1, 0, "アイツといると胸が\nぎゅーっとなる…\nこれってもしかして…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4792, 1, 12, 102201, 103, 2, 0, "アンタとならきっと\nどんなゲームでも…\nそ、その、たた、\n楽しいって思えるわ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4793, 1, 1, 106401, 1, 1, 0, "色んな人と話したら\n記憶探しの手がかりが\n見つかるかも…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4794, 1, 1, 106401, 2, 1, 0, "アイスクリームでも\n作ろうかしら？\nバニラにミントに…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4795, 1, 1, 106401, 3, 1, 0, "ネビアに話しかけると\n逃げられるのよね…\nもう、襲わないのに…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4796, 1, 1, 106401, 4, 1, 0, "「コッコロさん」、\n「コッコロさん」…\n…間違えないように…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4797, 1, 1, 106401, 5, 1, 0, "最近少し太ったかも？\nきっとおいしいもの、\n食べ過ぎね…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4798, 1, 4, 106401, 101, 1, 0, "ここにいると、\nとても落ち着くわ…\n自分の家、だものね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4799, 1, 8, 106401, 102, 1, 0, "みんなと一緒の時間、\nずっと記憶に残したい\n…そう思っているのよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4800, 1, 1, 106401, 1, 2, 0, "ドラゴン族って、\nそんなに珍しいの？\nあまり実感ないけど…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4801, 1, 1, 106401, 2, 2, 0, "料理、練習してるのよ\n…味見してもらえる？\n期待はしないでね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4802, 1, 1, 106401, 3, 2, 0, "頭を叩いたら、\n記憶が戻ったり\nしないかしら…？\nやらなくていいわよ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4803, 1, 1, 106401, 4, 2, 0, "赤ちゃんみたいだった\n時のことは、忘れて…\nお願いだから…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4804, 1, 4, 106401, 101, 2, 0, "ひゃんっ！？\nし、しっぽを掴むのは\nダメよ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4805, 1, 8, 106401, 102, 2, 0, "記憶が戻っても…\n私は、あなたたちと\n一緒にいたいわ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4806, 1, 1, 106401, 1, 2, 0, "おはよう。\n寝ぐせついてるわよ？\n…ふふっ、これでよし", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4807, 1, 1, 106401, 2, 2, 0, "そろそろお昼かしら？\nおいしいごはんを、\nみんなで一緒に…ね", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4808, 1, 1, 106401, 3, 2, 0, "もうこんな時間ね。\nそろそろ休みましょ？\n…ふわぁ…", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4810, 1, 1, 106401, 2, 3, 0, "ふかふか、ぎゅう～…\n…あっ、これはその…\nストレッチしててっ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4811, 1, 1, 106401, 3, 3, 0, "あまり、興味ないわ。\nけど…けど…\n…か、かわいい…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4812, 1, 1, 106401, 4, 3, 0, "いい香りね。\nとっても落ち着くわ…\n…っくしゅっ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4813, 1, 1, 106401, 5, 3, 0, "冷やす方が得意だけど\nあったかいのも、\nキライじゃないのよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4815, 1, 1, 106401, 7, 3, 0, "別に、食いしん坊\nじゃないのよ？\nでも、もう一口だけ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4816, 1, 1, 106401, 8, 3, 0, "ん…なるほどね。\n勉強になるわ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4818, 1, 1, 115701, 1, 1, 0, "水着の探偵か…\nちょっと恥ずかしい\n気がしてきたよ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4819, 1, 1, 115701, 2, 1, 0, "依頼があれば\n海でも山でも\n赴くさ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4820, 1, 1, 115701, 3, 1, 0, "今のうちに\n助手くんの秘密を…\nわ…私はなにをっ！？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4821, 1, 1, 115701, 4, 1, 0, "休暇も時には必要さ\n海でゆっくりと…\nむっ？　事件の気配！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4822, 1, 1, 115701, 5, 1, 0, "探偵の秘密道具…\n水着だと隠す\n場所がない…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4823, 1, 4, 115701, 101, 1, 0, "そろそろ助手くんが\n現れる時間かな？\nふふっ、楽しみだね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4824, 1, 8, 115701, 102, 1, 0, "真実は私と助手くん\n二人だからこそ\n解き明かせるのさ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4825, 1, 1, 115701, 1, 2, 0, "ひゃああっ！？\nわ、私の不意を突く\nとは…やるね助手くん", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4826, 1, 1, 115701, 2, 2, 0, "さて、なにか気づいた\nことはあるかな？\n言ってごらん♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4827, 1, 1, 115701, 3, 2, 0, "この感触…分かった！\n助手くんだね！\nふふっ、やっぱり♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4828, 1, 1, 115701, 4, 2, 0, "水着…どうかな？\n探偵らしさは\n残してるつもりだけど", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4829, 1, 4, 115701, 101, 2, 0, "ん？　依頼かな？\n助手くんの頼みなら\nなんでもお安い御用さ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4830, 1, 8, 115701, 102, 2, 0, "助手くん、私と海に\n行かないかな\n…事件じゃないよ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4831, 1, 1, 115701, 1, 2, 0, "珈琲でも淹れて\n目を覚ますとしよう\n一緒に飲むかい？", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4832, 1, 1, 115701, 2, 2, 0, "昼食は済ませた？\nなにを食べたか\n当ててあげよう！", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4833, 1, 1, 115701, 3, 2, 0, "夜は謎が深まる時間…\n闇に覆い隠された真実\n必ず暴いてみせる！", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4835, 1, 1, 115701, 2, 3, 0, "さて、推理の時間だ\n…サボってないよ？\n考えてるんだからね？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4836, 1, 1, 115701, 3, 3, 0, "これはもしや、中に\n秘密のメッセージが？\n…かわいいだけだね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4837, 1, 1, 115701, 4, 3, 0, "ふむ、なんだか\n頭がスッキリするね\n…閃いた！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4838, 1, 1, 115701, 5, 3, 0, "ポカポカするね…\n助手くん\n君もおいでよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4840, 1, 1, 115701, 7, 3, 0, "はむ、はむ…んぐっ\nた、食べてるところを\n見つめられると～…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4841, 1, 1, 115701, 8, 3, 0, "秘密の依頼とか\nこういう場所に\n隠したりするよね…♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4843, 1, 1, 115701, 10, 3, 0, "これはいい乗り心地だ\n事件現場にこれで登場\n…は、やめておこう", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4844, 1, 1, 115701, 11, 3, 0, "不思議な感触だね…\nでも、いい発想が\n生まれてきそうだ…♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4845, 1, 1, 180601, 1, 1, 0, "これはあたしが\n前へ進むために\nあいつがくれた\n大切な力…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4846, 1, 1, 180601, 2, 1, 0, "チェンジ・\nプリンセスフォーム！\n練習練習、っと", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4847, 1, 1, 180601, 3, 1, 0, "もうあいつに助けて\nもらいっぱなしの\nあたしは…\n卒業しなきゃね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4848, 1, 1, 180601, 4, 1, 0, "気ままに、お気楽に\nあいつと冒険する…\nそれって幸せよね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4849, 1, 1, 180601, 5, 1, 0, "プリンセスフォーム…\nお姫様みたいね\nあたしがお姫様か…\nそっかぁ…ふふ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4850, 1, 4, 180601, 101, 1, 0, "あいつと繋がって\n生まれたこの絆は\nあたしの宝物だわ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4851, 1, 8, 180601, 102, 1, 0, "あたしの命の使い方…\n全て受け止めてくれた\nあいつにだったら…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4852, 1, 1, 180601, 1, 2, 0, "ふふ～ん♪\nプリンセスフォームの\n天才魔法使い\nキャル様に用かしら？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4853, 1, 1, 180601, 2, 2, 0, "これからは\nあたしの力で\nなんでもパパっと\n解決してあげるわね！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4854, 1, 1, 180601, 3, 2, 0, "魔力がみなぎってる…\nねえ、もしかして\n今のあたしって本気で\nすごいんじゃない？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4855, 1, 1, 180601, 4, 2, 0, "変身の反動で\nお腹が空いても\n魔物料理だけは\nいやだからね！？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4856, 1, 4, 180601, 101, 2, 0, "ねぇ、あんたになら\nあたしの心…\n全部預けていいかも", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4857, 1, 8, 180601, 102, 2, 0, "あいつと一緒に\nいられたら、他には\n何も…って、あああ\nあんたいつの間に！？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4858, 1, 1, 180601, 1, 2, 0, "早起きも悪くないわね\n読みかけの魔導書\n今のうちに\n読んじゃおうかしら", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4859, 1, 1, 180601, 2, 2, 0, "ねぇ～お腹すいたー\nあんたなんか\n作りなさいよ\n魔物料理以外でね！", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4860, 1, 1, 180601, 3, 2, 0, "ふぁぁ…眠すぎて\n今なら変身しても\nお腹が減らない\n自信があるわ…", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4862, 1, 1, 180601, 2, 3, 0, "あー♪　最っ高！\n休んで魔力を高めて…\nあいつの役に\n立ってあげなきゃね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4863, 1, 1, 180601, 3, 3, 0, "なにこれかわいい…！\nコレクションにしたく\nなっちゃうじゃない", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4864, 1, 1, 180601, 4, 3, 0, "この香り…すごく\nリラックスできるわ…\nあ、いい感じの魔法\n思いついちゃったかも", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4865, 1, 1, 180601, 5, 3, 0, "あったかい…\n落ち着くわ…\nあいつの手の\nぬくもりみたいね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4867, 1, 1, 180601, 7, 3, 0, "プリンセスフォーム…\nこ、こんなに\nお腹が減るのね\n甘く見てたわ…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4868, 1, 1, 180601, 8, 3, 0, "ま、魔物料理の本！？\n誰よこんな本\n買ったの！　って…\n一人しかいないか", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4870, 1, 1, 115801, 1, 1, 0, "憧れの舞踏会…\n私、きちんと\n踊れるかしら", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4871, 1, 1, 115801, 2, 1, 0, "アン・ドゥ・トロワ\nアン・ドゥ・トロワ\n練習はばっちりね♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4872, 1, 1, 115801, 3, 1, 0, "こんなにすてきな\nドレスを着られて\n本当に夢みたいだわ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4873, 1, 1, 115801, 4, 1, 0, "魔法はいつか解ける…\nでも、その時間は心に\n残り続けるわ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4874, 1, 1, 115801, 5, 1, 0, "ガラスの靴は夢へ\n踏み出す魔法の道具…\n私、がんばっちゃう！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4875, 1, 4, 115801, 101, 1, 0, "舞踏会であなたと\n一緒に踊れたら\nいいのにな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4876, 1, 8, 115801, 102, 1, 0, "舞踏会に行く前に\n運命の王子様には\n出会ってるのよね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4877, 1, 1, 115801, 1, 2, 0, "どうかしら？\n私、きちんとドレス\n着られてる？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4878, 1, 1, 115801, 2, 2, 0, "あなたも一緒に\n踊ってみる？\nしゃなりしゃなり♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4879, 1, 1, 115801, 3, 2, 0, "あなたにエスコート\nされてきれいなお城を\n歩いてみたいわ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4880, 1, 1, 115801, 4, 2, 0, "どう？　レッスンの\n成果はきちんと\n出てるかしら？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4881, 1, 4, 115801, 101, 2, 0, "あなたが隣にいれば\n私も立派なお姫様に\n見えるかしら♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4882, 1, 8, 115801, 102, 2, 0, "私にとって\n一番の魔法はあなたが\nくれる勇気よ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4883, 1, 1, 115801, 1, 2, 0, "さぁ！　今日も一日\n立派な淑女を目指して\nいきましょう♪", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4884, 1, 1, 115801, 2, 2, 0, "お昼ごはん代わりに\nメタモルアップルを\n食べておきましょう", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4885, 1, 1, 115801, 3, 2, 0, "そろそろ魔法が解ける\n時間…でも淑女の\n心得は忘れないわ♪", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4887, 1, 1, 115801, 2, 3, 0, "淑女にも休息は\n必要よね…少しだけ\n休ませてもらおっと", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4888, 1, 1, 115801, 3, 3, 0, "かわいいものに\n囲まれた部屋も\n女の子の夢よね…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4889, 1, 1, 115801, 4, 3, 0, "すてきな香り…\nお花かしら？\n森で探してみようかな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4890, 1, 1, 115801, 5, 3, 0, "暖かい…暖炉のそばで\nおとぎ話を聞くのも\nいいわよね♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4892, 1, 1, 115801, 7, 3, 0, "レッスンのために\n栄養補給して\nおかないと！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4893, 1, 1, 115801, 8, 3, 0, "教養を身に着ける\nことも大切よね！\nなになに…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4895, 1, 1, 115901, 1, 1, 0, "や、やっぱスカートは\n落ち着かねえな…\nなんか、\nスースーするしよ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4896, 1, 1, 115901, 2, 1, 0, "せっかくこんな格好\nしてるし、いつもとは\n違う剣術の訓練を\nしてみるかな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4897, 1, 1, 115901, 3, 1, 0, "さて、他の連中が\n帰ってくる前に、\nなんか料理でも\n作っといてやるか", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4898, 1, 1, 115901, 4, 1, 0, "こ、こんな服着てる\nわけだし、紅茶とか\n楽しんじゃっても\n変じゃないよな！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4899, 1, 1, 115901, 5, 1, 0, "暇だし運動でも…\nあ、あぶねえ！\nスカートなの忘れてた\n…誰も見てねえよな？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4900, 1, 4, 115901, 101, 1, 0, "紅茶が切れてるな…\nよし、あいつと一緒に\n買い物にでも行くか♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4901, 1, 8, 115901, 102, 1, 0, "…あいつがいねえと、\nつまんねえな…ったく\n飯用意してやるから、\n早く帰ってこいよな…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4902, 1, 1, 115901, 1, 2, 0, "あ、あんまり\nジロジロ見んなよ\n…結構、\n恥ずかしいんだからな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4903, 1, 1, 115901, 2, 2, 0, "ダンスの相手は\n無理だが、\n剣の訓練なら\nいつでも付き合うぜ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4904, 1, 1, 115901, 3, 2, 0, "この服に\n触りたいのか？\nすごい作りだし、\n気持ちは分かるぜ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4905, 1, 1, 115901, 4, 2, 0, "お、いいところに！\nクッキーを焼く\nところだったんだが、\nおまえも食うか？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4906, 1, 4, 115901, 101, 2, 0, "おまえにもこういう服\n似合うんじゃねえか？\n今度あたしが\n選んでやるよ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4907, 1, 8, 115901, 102, 2, 0, "…おまえの前でなら、\nこういう格好を\nすんのも…\nな、なんでもねえよ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4908, 1, 1, 115901, 1, 2, 0, "朝の訓練を始めるか\n時間あるなら、\nおまえも付き合えよな", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4909, 1, 1, 115901, 2, 2, 0, "もうこんな時間か…\n昼飯作るけど、\nなんか\n食いたいものあるか？", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4910, 1, 1, 115901, 3, 2, 0, "眠れないなら、\nホットミルク飲むか？\n体あったまって、\n眠くなると思うぜ", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4912, 1, 1, 115901, 2, 3, 0, "ちょっと休憩して…\nおっと、服にしわが\nできないように\nしねえとな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4913, 1, 1, 115901, 3, 3, 0, "だ、誰も\n見てねえよな？\n…よ～しよしよし、\nかわいいなおまえー♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4914, 1, 1, 115901, 4, 3, 0, "すげぇいい香りだな\nうちの居酒屋にも…\nいや、合わねえか", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4915, 1, 1, 115901, 5, 3, 0, "あったけぇ…\nちょっとだけなら、\n寝ちまっても\nいいよな…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4917, 1, 1, 115901, 7, 3, 0, "これうめえな！\nたぶん材料は…よし！\nあたしも作ってみるか", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4918, 1, 1, 115901, 8, 3, 0, "この本は…\n誰でも分かる作法…か\n最低限このぐらいは\n勉強しとくか…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4920, 1, 12, 102001, 103, 1, 0, "ミミがおとなに\nなっても\nおにいちゃんとは\nずーっといっしょ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4921, 1, 12, 102001, 103, 2, 0, "ミミとくべつなだっこ\nしてほしいな…うん！\nおひめさまだっこ～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4922, 1, 12, 100401, 103, 1, 0, "次はにいちゃんに\nどんなイタズラしよう\n早く会いたいな～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4923, 1, 12, 100401, 103, 2, 0, "ミソギの特別な\nイタズラ見てくれる？\nいいよねっ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4924, 1, 12, 103601, 103, 1, 0, "大人になったら、\nあの人と何しようかな\n…楽しみだなぁ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4925, 1, 12, 103601, 103, 2, 0, "頭を撫でないで\nください！　もう、\n少しだけ、ですからね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4926, 1, 1, 116001, 1, 1, 0, "くるくる〜♪\nうちのドレス\nかわええなぁ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4927, 1, 1, 116001, 2, 1, 0, "日差しは苦手やけど\nたまにはひなたの下で\nぽかぽかしたいわぁ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4928, 1, 1, 116001, 3, 1, 0, "外はお花が咲いてる\n久しぶりにお花を\n活けたいなぁ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4929, 1, 1, 116001, 4, 1, 0, "お花をたくさん集めて\nギルドハウスに飾れば\n喜んでくれはるかな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4930, 1, 1, 116001, 5, 1, 0, "あたたかい日は\n紅茶を飲みながら\nゆっくりしたいなぁ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4931, 1, 4, 116001, 101, 1, 0, "お稽古事を始めるなら\nうちに言ってな\nどんなことも教えたる", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4932, 1, 8, 116001, 102, 1, 0, "うちにいろんな夢を\n見せてくれはって\nほんま、おおきにな♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4933, 1, 1, 116001, 1, 2, 0, "王子はん\n一緒におとぎの国へ\nお散歩にいきまひょ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4934, 1, 1, 116001, 2, 2, 0, "ふんふんふ〜ん♪\n今な、王子はんに\n花冠を作ってるんよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4935, 1, 1, 116001, 3, 2, 0, "王子はんは\nお菓子好きどす？\nうち、作りますわ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4936, 1, 1, 116001, 4, 2, 0, "うーん。ドレスの柄は\nお花とお星さま\nどっちにしまひょか", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4937, 1, 4, 116001, 101, 2, 0, "王子はんとの\n思い出が増えるたび\nうちの宝物も増えます", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4938, 1, 8, 116001, 102, 2, 0, "王子はんと一緒なら\nどんな場所でも\n夢の国になりますわ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4939, 1, 1, 116001, 1, 2, 0, "夢の中におりたいけど\n王子はんに会いたくて\n起きてもうたわ♪", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4940, 1, 1, 116001, 2, 2, 0, "王子はん一緒に\nアフタヌーンティー\nいかがやろか？", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4941, 1, 1, 116001, 3, 2, 0, "夜の舞踏会…\n夢が広がるわぁ\nもちろんお相手は…♪", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4943, 1, 1, 116001, 2, 3, 0, "ふわふわや〜\nなんや心地よぉて\n眠ってしまいそうや…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4944, 1, 1, 116001, 3, 3, 0, "かいらしいなぁ\n新しいお洋服でも\n作ってあげまひょか♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4945, 1, 1, 116001, 4, 3, 0, "お花やろうか…\nリラックスできて\nええ香りやねぇ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4946, 1, 1, 116001, 5, 3, 0, "こういう場所で\n絵本を読むと\n気分が出てええなぁ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4948, 1, 1, 116001, 7, 3, 0, "舞踏会を楽しむなら\nたくさん食べて\nたくさん踊りまひょ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4949, 1, 1, 116001, 8, 3, 0, "うち、この本好きや\n王子さまと結ばれる\nお姫さまのお話なんよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4951, 1, 1, 116301, 1, 1, 0, "はぁ、宿題めんどい…\nこれは楽しくない…\nこんな青春いらない…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4952, 1, 1, 116301, 2, 1, 0, "奨学金の道は遠いなぁ\nメルクリちゃんたちが\nお金くれればいいのに", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4953, 1, 1, 116301, 3, 1, 0, "アオイちゃん元気かな\nチエルに会いたくて\n泣いてるんだろうな…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4954, 1, 1, 116301, 4, 1, 0, "暇だし鏡でも見るか…\nうわ、顔良っ！\nえ誰この超絶美少女！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4955, 1, 1, 116301, 5, 1, 0, "ワンツーワンツー♪\nうーん…カルミナの\n振りコピ難しいにゃー", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4956, 1, 4, 116301, 101, 1, 0, "先輩も運動してます？\nチエルが一緒に走って\nあげてもいいですよ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4957, 1, 8, 116301, 102, 1, 0, "来年のテレフェスも\n今から楽しみだなあ…\nまた一緒にお化け屋敷\n入ってくださいね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4958, 1, 1, 116301, 1, 2, 0, "ちぇる～ん☆\nフェス仕様でも\nかわいいちえるん！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4959, 1, 1, 116301, 2, 2, 0, "いま話しかけないで！\n来年の聖学祭ライブの\nイメトレ中なんです！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4960, 1, 1, 116301, 3, 2, 0, "クロエ先輩のウェア姿\nめっちゃカッコよ！\n思わずおへそ触ったら\nぶたれましたけど", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4961, 1, 1, 116301, 4, 2, 0, "ユニ先輩の体操着姿…\nあれはあれで実に良き\n一生見てられますよね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4962, 1, 4, 116301, 101, 2, 0, "先輩って体操着持って\nないんですか？　ハ？\nチエルのを貸せと…？\nえっ…変態かな？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4963, 1, 8, 116301, 102, 2, 0, "テレフェスでは先輩も\n地味に頑張ってました\nね。まあ格好よかった\nんじゃないですか…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4964, 1, 1, 116301, 1, 2, 0, "おはちぇる～ん☆\nさあ今日も楽しいを\n探しに行きますよー！", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4965, 1, 1, 116301, 2, 2, 0, "先輩ビッグニュース！\n今からお昼ごはん\nおごらせてあげます！", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4966, 1, 1, 116301, 3, 2, 0, "こんばんちぇるるん☆\nさあ朝まで楽しいを\n探しに行きますよー！", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4968, 1, 1, 116301, 2, 3, 0, "はぁ…ふかふか～…\n運動の後は…眠たく…\nなり…ま…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4969, 1, 1, 116301, 3, 3, 0, "ユニ先輩に似合いそ！\nいやクロエ先輩かな？\nチエルは当然すぎて…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4970, 1, 1, 116301, 4, 3, 0, "やん、いい香り～！\nまるでチエルのために\n存在するみたい！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4971, 1, 1, 116301, 5, 3, 0, "この火を使って\n聖火リレーしましょう\n先輩、一本抜いて！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4973, 1, 1, 116301, 7, 3, 0, "あむあむ…ちゃんと\n食べないとユニ先輩\nみたいになっちゃう！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4974, 1, 1, 116301, 8, 3, 0, "ふむふむ…ちゃんと\n読まないとクロエ先輩\nみたいになっちゃう！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4976, 1, 12, 102901, 103, 1, 0, "今なら書ける気がする\n私の本当の\n想いを込めた\nラブソングを！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4977, 1, 12, 102901, 103, 2, 0, "輝きを増した私を\nいっぱい見てね！\n…ちょ、ちょっと\n見つめすぎだよ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4978, 1, 1, 116201, 1, 1, 0, "テレフェスかぁー…\nあんまいい記憶ないな\nよし、忘れとこ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4979, 1, 1, 116201, 2, 1, 0, "暇だし鏡でも見るか…\nふふ～ん…いいじゃん\nあ？　何見てんだコラ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4980, 1, 1, 116201, 3, 1, 0, "ワンツー、ワンツー♪\nああクソ、難しいな…\nあ？　何見てんだコラ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4981, 1, 1, 116201, 4, 1, 0, "アオイ元気してるかな\nまた絶叫したり白目\n剥いたりしてんのかな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4982, 1, 1, 116201, 5, 1, 0, "ミフユねえさんが\nバイトリーダーしてる\n工場…うちも行くか…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4983, 1, 4, 116201, 101, 1, 0, "うーん…今日の晩御飯\n何にすっかなァ…\nあんたも食べてく？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4984, 1, 8, 116201, 102, 1, 0, "来年のテレフェスは\nどーなンだかねえ？\nま、一緒にまた弟の\n相手してやってよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4985, 1, 1, 116201, 1, 2, 0, "体操着なんてフツーに\n着てもつまんないし？\nその辺センス出るよね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4986, 1, 1, 116201, 2, 2, 0, "奨学金の道、遠くね？\nアキノ先輩と仲良く\nなった方が早いかも…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4987, 1, 1, 116201, 3, 2, 0, "あんた体操着は持って\nないの。ハ？　うちの\n貸せって？　いいけど\n…いや、よくねえよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4988, 1, 1, 116201, 4, 2, 0, "にゃんっ！\nや、だからさ…急に\n話しかけんなっての", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4989, 1, 4, 116201, 101, 2, 0, "え、なに？　この服？\nハ？　似合ってる？\n…え、聞こえなかった\nちょ、もっかい言って", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4990, 1, 8, 116201, 102, 2, 0, "あんたもテレフェスで\n一応は頑張ってたよね\nま格好よかったんじゃ\nないの。知んないけど", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4991, 1, 1, 116201, 1, 2, 0, "ふぁ～あ…おはーす\n昨日も寝たの遅くて\nまだ目がショボだよ…", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4992, 1, 1, 116201, 2, 2, 0, "なに。あんた昼御飯\nまだなの。じゃ、うち\n適当になんか作ろうか", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4993, 1, 1, 116201, 3, 2, 0, "夜っていいよね。色々\n見えすぎない感じとか\n…この話したっけ？", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4995, 1, 1, 116201, 2, 3, 0, "あ、ヤバ…運動の後に\nこのフカついたのは…\n秒でスヤれる…すやぁ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4996, 1, 1, 116201, 3, 3, 0, "へーこんなんチエルに\n似合いそうじゃん。\n…いやユニ先輩かな？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4997, 1, 1, 116201, 4, 3, 0, "お、なんか良さげな\n香りじゃん。これは\n何の…いや分かんね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (4998, 1, 1, 116201, 5, 3, 0, "熱ッ！　この野郎なに\n爆ぜてンだコラァ！\n濡らすぞ木、てめえ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5000, 1, 1, 116201, 7, 3, 0, "何コレよく分かんない\nけど、今日モヤシしか\n食ってないから助かる", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5001, 1, 1, 116201, 8, 3, 0, "今日、体操着だしね。\n読書には逆アドかな。\nうん帰ろ。いやあ残念", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5003, 1, 1, 116501, 1, 1, 0, "ビッグボス・イノリ様\nふっふっふ…\nしびれる響きです！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5004, 1, 1, 116501, 2, 1, 0, "いつかお兄さんを\nあたしの側近に\nしてやるですよ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5005, 1, 1, 116501, 3, 1, 0, "ビッグボスになったら\nみんなからたくさん\nお菓子をもらうです！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5006, 1, 1, 116501, 4, 1, 0, "ふふん！\nこの服はあたしにしか\n着こなせませんよ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5007, 1, 1, 116501, 5, 1, 0, "ばーんと立って\nどーんと腕を組む！\nこれがボスのポーズ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5008, 1, 4, 116501, 101, 1, 0, "イノリ様の側近に\n選ばれるなんて\n名誉なことですよ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5009, 1, 8, 116501, 102, 1, 0, "未来の側近として\nあたしをしっかり\n甘やかすですよ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5010, 1, 1, 116501, 1, 2, 0, "あたしがお兄さんを\n立派なギャングに\nしてあげますからね！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5011, 1, 1, 116501, 2, 2, 0, "側近たるもの\nボスを甘やかなさいと\nだめですよ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5012, 1, 1, 116501, 3, 2, 0, "なに見てやがるです…\nくぅ、分かりました！\nお菓子は半分こです！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5013, 1, 1, 116501, 4, 2, 0, "お兄さんも\nあたしがボスなら\nうれしいですよね！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5014, 1, 4, 116501, 101, 2, 0, "お兄さんが側近なら\nあたしも安心して\n夜眠れるですよ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5015, 1, 8, 116501, 102, 2, 0, "ビッグボスになったら\nお兄さんにいい思い\nさせてあげますから！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5016, 1, 1, 116501, 1, 2, 0, "ボスたるもの\n早起きは鉄則ですよ！\nふぁあ…", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5017, 1, 1, 116501, 2, 2, 0, "お腹が空きました…\nご飯食べにいきますよ", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5018, 1, 1, 116501, 3, 2, 0, "ボスたるものぉ〜…\n夜更かしぐらい当然…\nぐー…", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5020, 1, 1, 116501, 2, 3, 0, "は〜これは人をだめに\nしやがるやつですね\nあたし専用です", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5021, 1, 1, 116501, 3, 3, 0, "これはけしからん\nかわいらしさですね\nあたしにぴったりです", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5022, 1, 1, 116501, 4, 3, 0, "高級な香りがするです\nボスになったら\n部屋に置いてやるです", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5023, 1, 1, 116501, 5, 3, 0, "あったかいですねぇ\nちょっとこのまま\n一休憩です…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5025, 1, 1, 116501, 7, 3, 0, "はむはむ♪\nボスたるもの食べねば\n戦はできませんよ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5026, 1, 1, 116501, 8, 3, 0, "ボスなら経済書ぐらい\n…う。難しくて\n読めねーです…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5028, 1, 12, 102501, 103, 1, 0, "あなたはドジな私を\n受け入れてくれる…\nだから、頑張れます！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5029, 1, 12, 102501, 103, 2, 0, "何でも仰ってください\nちょっぴり大胆な\nことでも…な～んて♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5030, 1, 1, 116601, 1, 1, 0, "あーあー、よし\nのどの調子もいいし\nはりきってガイド\nしてくるか！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5031, 1, 1, 116601, 2, 1, 0, "さあ、そろそろ行くか\nガイドが\n遅刻したんじゃ\n話になんないからな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5032, 1, 1, 116601, 3, 1, 0, "しれっとドラゴン族が\n旅行に来たり\nしてくれないかな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5033, 1, 1, 116601, 4, 1, 0, "たぎるガイド先…\n火山、魔物の巣…\nダメだ、そんなとこ\nオレしか行けないや", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5034, 1, 1, 116601, 5, 1, 0, "この前行った絶景\nスポットよかったな\nキレイな景色を見る\nのもいいもんだぜ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5035, 1, 4, 116601, 101, 1, 0, "お、今日はカレと\n一緒か。なんだか\nやる気出てきたぜ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5036, 1, 8, 116601, 102, 1, 0, "カレにガイドして\nもらったら\nどこに行っても\n楽しいだろうな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5037, 1, 1, 116601, 1, 2, 0, "よっしゃあ！\nどっちが上手く\nガイドできるか\n勝負しようぜ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5038, 1, 1, 116601, 2, 2, 0, "飯でも食いがてら\nおススメの店の\n下調べでもしようぜ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5039, 1, 1, 116601, 3, 2, 0, "なかなかいいだろ？\nこの格好。キミも\n作ってもらったら\nどうだ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5040, 1, 1, 116601, 4, 2, 0, "普通は行けない\n場所とか行きたいよな\nキミならどんな\nとこがいい？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5041, 1, 4, 116601, 101, 2, 0, "ガイドの仕事は\nキミの方が得意そうだ\n参考にさせてもらうぜ\nよろしくな！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5042, 1, 8, 116601, 102, 2, 0, "キミの探してきた店\n結構よさそうじゃん\nそうだ、今度二人で\n下見に行こうぜ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5043, 1, 1, 116601, 1, 2, 0, "いい朝だな。絶好の\n仕事日和だぜ！\n今日もよろしくな！", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5044, 1, 1, 116601, 2, 2, 0, "よし、昼飯にしよう\nこの後も激務だし\n飯抜きじゃあ\nぶっ倒れちまうからな", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5045, 1, 1, 116601, 3, 2, 0, "お疲れさん！　さーて\n次はオレと遊んで\nくれよ。まだまだ\nいけるだろ？", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5047, 1, 1, 116601, 2, 3, 0, "ガイドは一日\n歩きっぱなしだからな\nゆっくり休ませて\nもらうぜ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5048, 1, 1, 116601, 3, 3, 0, "これ、どこで\n売ってるんだろ？\n土産の情報も\n仕入れとかなきゃな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5049, 1, 1, 116601, 4, 3, 0, "アロマとかよく\n分かんねえけど\n土産にはいいかもな\n覚えておくぜ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5050, 1, 1, 116601, 5, 3, 0, "体が温まると心も\n熱くなるんだよな\nいい仕事が\nできそうだぜ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5052, 1, 1, 116601, 7, 3, 0, "食べ放題か\nこれはお客さんも\n喜んでくれそうだな！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5053, 1, 1, 116601, 8, 3, 0, "へえ、こんなスポット\nあったんだ。やっぱ\nたまには勉強も\nしとくもんだな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5055, 1, 1, 116701, 1, 1, 0, "バイト先になじめる\nように笑顔の練習を！\nに、にへぇ〜…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5056, 1, 1, 116701, 2, 1, 0, "働いて働いて働いて\n…いつかその先に\nぼっちの出口が\n見えるのかも…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5057, 1, 1, 116701, 3, 1, 0, "バイトのイメトレを\n始めます。ポーズを\n決めて…よし！\n角度を変えて…よし！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5058, 1, 1, 116701, 4, 1, 0, "バイト仲間に入る\nためには厳しい修業が\n必須と聞きました…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5059, 1, 1, 116701, 5, 1, 0, "どんどん技術を磨いて\nバイト仲間として\n認めてもらうんです！\n目指せ職人技…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5060, 1, 4, 116701, 101, 1, 0, "あの人が応援して\nくれる…だから必ず\nバイト仲間の輪に\n入ってみせます…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5061, 1, 8, 116701, 102, 1, 0, "あの人がいてくれると\n勇気が湧いてきます…\nぼっち卒業を夢見ても\nいいんだ、って…♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5062, 1, 1, 116701, 1, 2, 0, "緊張するけど\n思い切って声をかけて\nみます…バイト仲間に\n入るために…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5063, 1, 1, 116701, 2, 2, 0, "ぼっちの私が\nおもちゃ作りで\n人と共同作業…！\nか、感動です…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5064, 1, 1, 116701, 3, 2, 0, "好きなおもちゃって\nありますか？　私は\nもちろんマイフレンド\nくんです！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5065, 1, 1, 116701, 4, 2, 0, "私が作ったおもちゃも\n誰かの友達に\nなってたら\nいいなぁ…♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5066, 1, 4, 116701, 101, 2, 0, "今日も明日もバイト\nですね！　バイト\n仲間ができる日が\n楽しみです…♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5067, 1, 8, 116701, 102, 2, 0, "脱ぼっちを目指して\nこれからも一緒に\n頑張りましょう！\n目指せ、バイト仲間！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5068, 1, 1, 116701, 1, 2, 0, "おはようございます\n今日も気合いを入れて\nおもちゃ作りします！", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5069, 1, 1, 116701, 2, 2, 0, "ランチの時間です…♪\nもちろん工場の隅で\nぼっち飯です…！", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5070, 1, 1, 116701, 3, 2, 0, "ふわぁ…今日も\nお疲れさまでした\n明日も働きます～！", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5072, 1, 1, 116701, 2, 3, 0, "バイトで頑張った日は\nふわふわの誘惑に\n勝てません…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5073, 1, 1, 116701, 3, 3, 0, "こういうおもちゃの\nアイデアも\n素敵かも…♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5074, 1, 1, 116701, 4, 3, 0, "いい香りがする\nおもちゃはどうかな…\n森の薬草を使って…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5075, 1, 1, 116701, 5, 3, 0, "ぽかぽか…\n幸せなひとときですね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5077, 1, 1, 116701, 7, 3, 0, "もぐもぐ…たくさん\n食べて厳しいバイトを\n乗り切ります…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5078, 1, 1, 116701, 8, 3, 0, "ふむふむ…おもちゃの\n設計図を見るのも\n勉強になりますね！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5080, 1, 1, 116801, 1, 1, 0, "う～ん\n次はどんなたい焼きを\n作ろうかにゃぁ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5081, 1, 1, 116801, 2, 1, 0, "あたしが売る\nたい焼きには\n夢が詰まってるにゃ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5082, 1, 1, 116801, 3, 1, 0, "いつか絶対に\nたい焼き工場を\n建て直してみせるにゃ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5083, 1, 1, 116801, 4, 1, 0, "たまにたい焼き以外の\nおやつも食べるにゃ\nたとえば煮干しとか！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5084, 1, 1, 116801, 5, 1, 0, "悪い商売人がいたら\nファントムキャッツに\n知らせるにゃ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5085, 1, 4, 116801, 101, 1, 0, "キミと一緒にいると\nたい焼きのアイデアが\n次々出てくるにゃ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5086, 1, 8, 116801, 102, 1, 0, "出来ないことも\nキミと一緒にいれば\n出来る気がするにゃ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5087, 1, 1, 116801, 1, 2, 0, "なんにゃ～？\nお姉さんにかまって\nほしいのかにゃ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5088, 1, 1, 116801, 2, 2, 0, "キミ、いいところに！\nあたしとライバル店へ\n視察にいくにゃ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5089, 1, 1, 116801, 3, 2, 0, "くんくん…この匂い！\nたい焼き持ってるなら\nちょうだいにゃ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5090, 1, 1, 116801, 4, 2, 0, "次はどんなたい焼きを\n売ろうかにゃ～\nキミの案も欲しいにゃ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5091, 1, 4, 116801, 101, 2, 0, "ファントムキャッツに\n相棒が必要にゃ\nたとえばキミとか…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5092, 1, 8, 116801, 102, 2, 0, "相棒！\n…ってキミのこと\n呼んでいいかにゃ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5093, 1, 1, 116801, 1, 2, 0, "早起きは三文の徳にゃ\n今日もたい焼き\n売れますよーににゃ！", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5094, 1, 1, 116801, 2, 2, 0, "お腹空いたにゃ…\n賄いのたい焼きを\nいただくにゃ！", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5095, 1, 1, 116801, 3, 2, 0, "猫は夜が本番にゃ\nでもあたしは…\nふわぁ～眠いにゃ…", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5097, 1, 1, 116801, 2, 3, 0, "ふかふかにゃ～\nここで体を丸めて\n一眠りしたいにゃ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5098, 1, 1, 116801, 3, 3, 0, "これは…！\n猫にも負けない\nかわいさだにゃ…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5099, 1, 1, 116801, 4, 3, 0, "いい香りにゃ～\nゴロゴロのどが\n鳴りそうだにゃ～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5100, 1, 1, 116801, 5, 3, 0, "ふにゃ～…\nこのまま働かずに\nまるまってたいにゃ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5102, 1, 1, 116801, 7, 3, 0, "商売は戦いにゃ\nたくさん食べて\nたくさん働くにゃ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5103, 1, 1, 116801, 8, 3, 0, "ふむふむ。たい焼きの\nアレンジレシピ集…\nためになるにゃ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5105, 1, 12, 102101, 103, 1, 0, "お芝居の前に\nお兄ちゃんの顔を\n見ると、とっても\n落ち着きますぅ…♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5106, 1, 12, 102101, 103, 2, 0, "お兄ちゃんにもっと\n見てもらいたい…\nだから私…\n強くなりたいです…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5107, 1, 1, 116901, 1, 1, 0, "さらなる効率化で\nみんなといい仕事を\nしたいわね！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5108, 1, 1, 116901, 2, 1, 0, "他のバイトとの\nスケジュールを\n調整しないと…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5109, 1, 1, 116901, 3, 1, 0, "時給がアップしたわ！\n働きが認められると\nやる気が出るわね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5110, 1, 1, 116901, 4, 1, 0, "効率的に動ければ\n業務を追加できる…\n燃えてきたわ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5111, 1, 1, 116901, 5, 1, 0, "帳簿付けのコツ…\nユカリさんに教わって\nみようかしら", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5112, 1, 4, 116901, 101, 1, 0, "バイトリーダーとして\nみんなをビシバシ\n鍛えていくわ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5113, 1, 8, 116901, 102, 1, 0, "戦いでもバイトでも\nキミなら安心して\n背中を預けられるわね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5114, 1, 1, 116901, 1, 2, 0, "どうしたの？\n仕事のことなら\nなんでも相談してね！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5115, 1, 1, 116901, 2, 2, 0, "さすがは私の弟子ね\nこれなら安心して\n仕事を任せられるわ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5116, 1, 1, 116901, 3, 2, 0, "みんなのお手本になる\n働きぶりを見せないと\nいけないわね！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5117, 1, 1, 116901, 4, 2, 0, "キミのこと…もっと\n頼りにしちゃっても\nいいのかしら…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5118, 1, 4, 116901, 101, 2, 0, "ふふ、キミも一人前の\nバイト戦士として\n立派に成長したわね！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5119, 1, 8, 116901, 102, 2, 0, "新しいおもちゃの\n企画書を作ってみたの\nちょっと見てくれる？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5120, 1, 1, 116901, 1, 2, 0, "朝は一番効率がいい\n時間帯よ！\nバリバリ働くわよ！", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5121, 1, 1, 116901, 2, 2, 0, "さ、お昼に行きましょ\n今日の日替わり定食は\n何かしら…楽しみね♪", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5122, 1, 1, 116901, 3, 2, 0, "もうこんな時間…\nちゃんと寝て明日に\n備えなくちゃね", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5124, 1, 1, 116901, 2, 3, 0, "適度な労働と休息の\nサイクルが最大効率を\n生み出すのよ…ふぅ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5125, 1, 1, 116901, 3, 3, 0, "はぁ～なんて素敵な\n抱き心地なのかしら…\n最高効率の癒やしね♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5126, 1, 1, 116901, 4, 3, 0, "落ち着く香りね…\nなんだか集中力が\nアップした気がするわ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5127, 1, 1, 116901, 5, 3, 0, "ふぅ…癒されるわね…\n仕事のオンオフの\n切り替えは重要よね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5129, 1, 1, 116901, 7, 3, 0, "働くためには\nエネルギーをちゃんと\n摂っておかないと", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5130, 1, 1, 116901, 8, 3, 0, "効率を上げるため\n生産管理の勉強も\nしておきたいわね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5132, 1, 1, 180301, 1, 1, 0, "変身している間に\n色々と試しておこう\nまずは…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5133, 1, 1, 180301, 2, 1, 0, "飛べるというのは\n便利だな。料理が\n暖かいうちに\n持ち帰れる", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5134, 1, 1, 180301, 3, 1, 0, "プリンセスか…\n柄ではないけれど\n嫌な気はしないな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5135, 1, 1, 180301, 4, 1, 0, "この力で\n何を成すべきなのか…\n剣に聞いてみよう", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5136, 1, 1, 180301, 5, 1, 0, "変身したとて私は私\n一時的に強くなろうと\n元が弱くてはだめだ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5137, 1, 4, 180301, 101, 1, 0, "変身していない時でも\n彼がそばにいると\n力が湧いてくる…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5138, 1, 8, 180301, 102, 1, 0, "私の思い…\nいつか伝えてみせるよ\n…きっといつか！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5139, 1, 1, 180301, 1, 2, 0, "何者かと戦うの？\nそれとも修行？\nどちらだとしても\n付き合うよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5140, 1, 1, 180301, 2, 2, 0, "変身し続けたら\nどれだけ空腹に…\n私は試さないからね？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5141, 1, 1, 180301, 3, 2, 0, "本物のプリンセスに\nなるのは御免だが\nナイトがいて\nくれるのは頼もしいよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5142, 1, 1, 180301, 4, 2, 0, "いつかこの力を\n誰かを救うために…\n戦い以外に使える時が\nくることを願うよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5143, 1, 4, 180301, 101, 2, 0, "キミに感じてほしいな\n私に流れてくる\nキミからの力を…\nその…私の、思いも…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5144, 1, 8, 180301, 102, 2, 0, "思いを告げる…\nそれがこんなにも\n難しいなんて…\nでも、いつか必ず…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5145, 1, 1, 180301, 1, 2, 0, "おはよう！\nいい変身日和だ！\n…ふふっ、冗談だよ", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5146, 1, 1, 180301, 2, 2, 0, "いつもより多く\n昼食を用意しよう\n変身が解けたら大変だ", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5147, 1, 1, 180301, 3, 2, 0, "一日お疲れ様\n私もそろそろ変身を\n解いて休むとするよ", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5149, 1, 1, 180301, 2, 3, 0, "変身してしまうと\n動かずにいても\n空腹になるのかな…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5150, 1, 1, 180301, 3, 3, 0, "私もキミたちくらい\n可愛げがある姿に\nなっているかな？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5151, 1, 1, 180301, 4, 3, 0, "香りに対する感覚も\nいつもより鋭く\nなっているみたいだ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5152, 1, 1, 180301, 5, 3, 0, "ありがたいな\n少し足元が\n冷えていたところだ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5154, 1, 1, 180301, 7, 3, 0, "これは…！\n持ち運べるように\n改良できないかな？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5155, 1, 1, 180301, 8, 3, 0, "祖父に貰った\n技法書を読んで\n初心に帰ろう", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5157, 1, 1, 117001, 1, 1, 0, "最強の惚れ薬を作って\nあのおかたの心を\n私の思いのままに…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5158, 1, 1, 117001, 2, 1, 0, "夏といえば海にスイカ\n花火に水着…\nそして惚れ薬ですわ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5159, 1, 1, 117001, 3, 1, 0, "あのおかたの心を\n手に入れるための\n準備を進めましょう\nこの夏こそ確実に…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5160, 1, 1, 117001, 4, 1, 0, "あのおかたに近づく\n虫どもを、どうやって\n片付けようかしら…\nまったく忌々しいこと", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5161, 1, 1, 117001, 5, 1, 0, "…クスクス\n薬の調合を考えるのは\n至福の時間ですわ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5162, 1, 4, 117001, 101, 1, 0, "あのおかたの全てが\n早く私のものに\nなりますように…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5163, 1, 8, 117001, 102, 1, 0, "かき氷も花火も海も…\nあなた様がいてこそ…\nそう、あなた様は\n私の全てなのですわ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5164, 1, 1, 117001, 1, 2, 0, "二人だけの夏を\n楽しみましょう…？\n…クスクス", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5165, 1, 1, 117001, 2, 2, 0, "あら、あなた様\n私に秘密でどこに\n行こうとして\nいますの…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5166, 1, 1, 117001, 3, 2, 0, "海に行きませんか？\nあなた様と一緒に\n過ごすには\n最高の場所ですから…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5167, 1, 1, 117001, 4, 2, 0, "ちょうどいいところに\nいらっしゃいました\nできたての薬を…\nさぁどうぞ…クスクス", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5168, 1, 4, 117001, 101, 2, 0, "夏の海のきらめきも\nあなた様の存在が\nなければ色あせて\nしまいますわ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5169, 1, 8, 117001, 102, 2, 0, "私とあなた様は\n結ばれる運命に\nあるのです…\n逃がしませんわ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5170, 1, 1, 117001, 1, 2, 0, "起きてすぐに会いに\n来てくださるなんて\nようやく私の伴侶たる\n自覚が…クスクス", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5171, 1, 1, 117001, 2, 2, 0, "お食事はまだですか？\n特製の薬草ランチを\n召し上がれ…クスクス", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5172, 1, 1, 117001, 3, 2, 0, "私の夢を見られる薬を\n差し上げますわ…\nおやすみなさいませ\nあなた様…クスクス", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5174, 1, 1, 117001, 2, 3, 0, "いい肌触りですわ…\n考え事にもぴったり\n眠くなるのが\n玉に瑕ですが…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5175, 1, 1, 117001, 3, 3, 0, "こういったものに\n揮発性の薬を仕込む\nのも一つの手ですわね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5176, 1, 1, 117001, 4, 3, 0, "この香り…開発中の\n薬に使えそうな気が\nしますわ…クスクス", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5177, 1, 1, 117001, 5, 3, 0, "あのおかたの\nぬくもりを\n思い出しますわ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5179, 1, 1, 117001, 7, 3, 0, "美味しい…この味なら\n薬を混ぜても違和感は\nなさそうですわ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5180, 1, 1, 117001, 8, 3, 0, "この薬の調合法…\n今度試してみましょう\nもちろんあのおかたに\n飲んでいただいて…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5182, 1, 1, 117001, 10, 3, 0, "二人乗りができると\nいいですわね…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5183, 1, 1, 117001, 11, 3, 0, "ふかふかですわ…\nあのおかたの分も\n欲しくなります…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5184, 1, 12, 101701, 103, 1, 0, "今日はキミと\nいつまでも歌って\n踊りたい気分さ～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5185, 1, 12, 101701, 103, 2, 0, "あは～♪\nキミの隣は、故郷より\n落ち着くかも～…♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5186, 1, 1, 117101, 1, 1, 0, "この水着…\n一番に弟くんに\n見せたいな♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5187, 1, 1, 117101, 2, 1, 0, "弟くんをこっそり\n海に運んで…\nサプライズしちゃお♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5188, 1, 1, 117101, 3, 1, 0, "いつもと違う髪型に\nしてみたけど…\n気に入ってくれるかな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5189, 1, 1, 117101, 4, 1, 0, "海は楽しみだけど\n日焼けしないように\n気を付けなきゃね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5190, 1, 1, 117101, 5, 1, 0, "まだかな、弟くん\n水着のお姉ちゃんが\nお出迎えだよっ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5191, 1, 4, 117101, 101, 1, 0, "海が見えるおうちで\n弟くんと一緒に\n暮らしたいなぁ～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5192, 1, 8, 117101, 102, 1, 0, "弟くんの水着…\nどんな感じかなぁ\n…あ、想像してたら\nドキドキして…えへへ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5193, 1, 1, 117101, 1, 2, 0, "こーら、水着の\nお姉ちゃんは\nおさわり禁止♪\n…なんてね？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5194, 1, 1, 117101, 2, 2, 0, "なぁに？\nふふっ、何回でも\n呼んでいいんだよ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5195, 1, 1, 117101, 3, 2, 0, "海で迷子に\nならないように\n手を繋ごうね♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5196, 1, 1, 117101, 4, 2, 0, "お姉ちゃんが\n海の楽しみ方を\n教えてあげるねっ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5197, 1, 4, 117101, 101, 2, 0, "わっ、弟くん\nどこ触ってるの？\nも～驚いちゃったぞ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5198, 1, 8, 117101, 102, 2, 0, "お姉ちゃん、海でも\nずっとキミを見てるよ\n…安心してね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5199, 1, 1, 117101, 1, 2, 0, "まだ眠そうだね？\nほら、海が待ってるよ\n早く起きて起きて♪", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5200, 1, 1, 117101, 2, 2, 0, "大丈夫、疲れてない？\n海で遊び疲れた時は\n素直に休憩しようね", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5201, 1, 1, 117101, 3, 2, 0, "ほら、早く寝ないと\n明日もお姉ちゃんと\n遊べないぞ♪", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5203, 1, 1, 117101, 2, 3, 0, "だらけたお姉ちゃんは\n弟くんには\n見せられないな～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5204, 1, 1, 117101, 3, 3, 0, "ふふっ、かわいいね♪\n弟くんが持つと\nもっとかわいいよっ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5205, 1, 1, 117101, 4, 3, 0, "いい香りだね…\n一味違うお姉ちゃんに\nなれそう…♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5206, 1, 1, 117101, 5, 3, 0, "あったか～い♪\nほら、弟くんも\nおいでよ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5208, 1, 1, 117101, 7, 3, 0, "よく食べて、いつでも\n弟くんの呼び声に\n応える準備を…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5209, 1, 1, 117101, 8, 3, 0, "弟くんの水着の写真\nこの辺りに隠して\nおこうかな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5211, 1, 1, 117101, 10, 3, 0, "この子の背に乗って\nお姉ちゃんと弟くんの\n楽園へ…な～んて♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5212, 1, 1, 117101, 11, 3, 0, "海を感じる感触だね\n弟くんとの楽しい夏を\n思い出しちゃうな…♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5213, 1, 1, 117201, 1, 1, 0, "この夏、誰よりも\nキラキラしてみせる！\nだって私は\nアイドルだから！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5214, 1, 1, 117201, 2, 1, 0, "日傘にアームカバー\n帽子にサングラス…\n紫外線対策は完璧！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5215, 1, 1, 117201, 3, 1, 0, "とっておきの\nサマーソングを\nみんなに届けるよ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5216, 1, 1, 117201, 4, 1, 0, "なんにもしないのって\n苦手かも…\n彼、早く来ないかな？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5217, 1, 1, 117201, 5, 1, 0, "ラムネって\n夏って感じするよね\n後で彼と飲もっと♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5218, 1, 4, 117201, 101, 1, 0, "夏は恋の季節！\n燃えるような恋…\nいつかしたいな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5219, 1, 8, 117201, 102, 1, 0, "今年の夏の目標は\n…彼との思い出を\nいっぱい作ること！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5220, 1, 1, 117201, 1, 2, 0, "私の水着写真集\n見てくれたんだ！\nどう、かわいかった？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5221, 1, 1, 117201, 2, 2, 0, "スイカ割りしたいの？\nいいね、楽しそう！\nみんなも誘おうよ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5222, 1, 1, 117201, 3, 2, 0, "ねぇ、見て見て！\n水着の映える\nポーズ考えたんだ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5223, 1, 1, 117201, 4, 2, 0, "ライブ中に\n花火を打ち上げたら\n絶対盛り上がるよね！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5224, 1, 4, 117201, 101, 2, 0, "最近、忙しいけど…\nキミの顔見たら\n疲れなんて\n消えちゃった！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5225, 1, 8, 117201, 102, 2, 0, "この夏もいーっぱい\nドキドキさせるから！\n覚悟しててよね♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5226, 1, 1, 117201, 1, 2, 0, "外は朝から暑いね\nけど、負けないから！\nお仕事、頑張るぞー！", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5227, 1, 1, 117201, 2, 2, 0, "自分へのご褒美に、\nアイス食べちゃお♪\nキミも一緒に食べよ！", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5228, 1, 1, 117201, 3, 2, 0, "夏の夜って\nワクワクするね♪\nホタル見に行こうよ！", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5230, 1, 1, 117201, 2, 3, 0, "ふわぁ…眠い…\n仕事とかの疲れ、\nたまってるのかな…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5231, 1, 1, 117201, 3, 3, 0, "私のぬいぐるみが\nグッズになったら…\n彼にかわいがって\nもらえるかな？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5232, 1, 1, 117201, 4, 3, 0, "この香り…\n夏限定のやつでしょ？\nいいセンスしてるね♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5233, 1, 1, 117201, 5, 3, 0, "暑～い！\nけど…汗かいて\n気持ちいいかも！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5235, 1, 1, 117201, 7, 3, 0, "レッスンの後のご飯は\n特別おいしい♪\n…もうひとつだけ\n食べちゃおっかな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5236, 1, 1, 117201, 8, 3, 0, "あ、この前撮影した\n水着グラビアだ\nうんうん、いい感じ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5238, 1, 1, 117201, 10, 3, 0, "この子、もふもふだね\n触るのクセになりそう\nもふもふもふもふ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5239, 1, 1, 117201, 11, 3, 0, "わ、かわいい♪\n今度のグラビア撮影で\nこれ使ってもいい？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5240, 1, 1, 117401, 1, 1, 0, "水着っていいですよね\n海に浮かぶ一輪の花\nって感じがして！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5241, 1, 1, 117401, 2, 1, 0, "自分の水着を作るのも\n誰かの水着を作るのも\n楽しいですね♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5242, 1, 1, 117401, 3, 1, 0, "水着の最終チェック！\n伸縮性や動きやすさは\n…いい感じですね！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5243, 1, 1, 117401, 4, 1, 0, "振り付けや衣装作りに\n歌や踊りの練習…\n…やることがいっぱい", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5244, 1, 1, 117401, 5, 1, 0, "はぁ…レイ様の水着…\n私に作らせて\nもらえないかなぁ～…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5245, 1, 4, 117401, 101, 1, 0, "騎士さんのことは\n気に入らないですけど\n別に嫌いという\nわけじゃなくて…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5246, 1, 8, 117401, 102, 1, 0, "騎士さん暇そうですね\nに、荷物持ちとして\n誘ってみようかな…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5247, 1, 1, 117401, 1, 2, 0, "騎士さんいいところに\nこの衣装\nどう思います？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5248, 1, 1, 117401, 2, 2, 0, "何見てるんですか？\nもしかしてこの水着の\n良さが分かります！？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5249, 1, 1, 117401, 3, 2, 0, "真夏でも涼しい…\nそれも水着の良さの\n一つですね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5250, 1, 1, 117401, 4, 2, 0, "仕立て屋も\nアイドルも\n諦めません！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5251, 1, 4, 117401, 101, 2, 0, "夏と言えば\nバカンスですけど…\n予定、あるんですか？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5252, 1, 8, 117401, 102, 2, 0, "う～ん…騎士さんを\n海へ誘うには…って\nなんでもないですよ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5253, 1, 1, 117401, 1, 2, 0, "ふー…夏はやっぱり\n朝が一番過ごしやすい\nですね～！", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5254, 1, 1, 117401, 2, 2, 0, "暑い時こそ海です！\n体力づくりのためにも\n泳ぎにいきますよ！", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5255, 1, 1, 117401, 3, 2, 0, "外で虫が鳴いてる…\n夏って感じがして\n風流ですねぇ…", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5257, 1, 1, 117401, 2, 3, 0, "少し休憩です…\nこのまま寝ないよう\n気をつけないと…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5258, 1, 1, 117401, 3, 3, 0, "か、かわいい…！\nこの感動を次の\nステージ衣装にも…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5259, 1, 1, 117401, 4, 3, 0, "いい香りですね～\n波の音が聞こえて\nきそうです…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5260, 1, 1, 117401, 5, 3, 0, "泳いだ後は\n少し寒いので\nちょうどいいですね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5262, 1, 1, 117401, 7, 3, 0, "夏バテしないように\n栄養はしっかり\n取らないと！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5263, 1, 1, 117401, 8, 3, 0, "次のトレンドは…\nふむふむ、やっぱり\nこう来ましたか…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5265, 1, 1, 117401, 10, 3, 0, "デザインはもちろん\n触り心地も\n最高ですね♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5266, 1, 1, 117401, 11, 3, 0, "夏の撮影スポットに\n活用できそうですね\nさっそく衣装考えて…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5267, 1, 12, 104001, 103, 1, 0, "えへへ…♪\n団長さんが初めての\n友達で本当に良かった", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5268, 1, 12, 104001, 103, 2, 0, "あっ、団長さん！\n今日のＢＢ団の活動\nなんですけど…二人で\n大縄跳びしませんか！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5269, 1, 1, 117301, 1, 1, 0, "この衣装で\nライブするのは\n恥ずかしいな…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5270, 1, 1, 117301, 2, 1, 0, "せっかくの夏だし\n肝試しに\n行ってみたいかも…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5271, 1, 1, 117301, 3, 1, 0, "歌もダンスも\nより上を目指すために\n夏合宿をしよう…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5272, 1, 1, 117301, 4, 1, 0, "夏の暑さにも負けない\n熱いライブに\nしてみせる…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5273, 1, 1, 117301, 5, 1, 0, "新曲のアイデア\nまとめたいな…\n少し海辺を散歩しよう", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5274, 1, 4, 117301, 101, 1, 0, "かき氷、食べたいな\n…あの人は何味が\n好きなんだろう？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5275, 1, 8, 117301, 102, 1, 0, "水着、似合ってるって\n言ってもらえるかな…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5276, 1, 1, 117301, 1, 2, 0, "サインして、ですか？\nいいですけど…\n少し照れますね…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5277, 1, 1, 117301, 2, 2, 0, "少し待ってください\n今、新曲の楽譜を\n書いているので…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5278, 1, 1, 117301, 3, 2, 0, "なんでしょう？\nもしかして…\nボイトレに興味が？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5279, 1, 1, 117301, 4, 2, 0, "今度のライブでは\n夏らしい爽やかな歌を\nファンにお届けします", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5280, 1, 4, 117301, 101, 2, 0, "…水鉄砲で遊ぼう？\nふふ。楽しそうですね\nお手柔らかに…♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5281, 1, 8, 117301, 102, 2, 0, "あなたの声を\n聴いていると、\n何だか落ち着きます…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5282, 1, 1, 117301, 1, 2, 0, "今日も暑くなりそう…\nお互いしっかりと\n水分補給しましょうね", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5283, 1, 1, 117301, 2, 2, 0, "お腹空きましたよね\n海の家で、\nなにか買ってきます", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5284, 1, 1, 117301, 3, 2, 0, "夜の海で月明かりと\n波の音を楽しもうかと\n…一緒に行きますか？", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5286, 1, 1, 117301, 2, 3, 0, "レッスンだけでなく\nリラックスすることも\n大事、ですよね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5287, 1, 1, 117301, 3, 3, 0, "ふふ、かわいい\n精霊も負けないくらい\nかわいいですけどね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5288, 1, 1, 117301, 4, 3, 0, "夏らしい爽やかで\nいい匂い…あなたに\nピッタリな香りですね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5289, 1, 1, 117301, 5, 3, 0, "海に入った後は、\n体が冷えてしまいます\n一緒に温まりましょう", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5291, 1, 1, 117301, 7, 3, 0, "アイドルとして、\n食べすぎには\n気をつけないと…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5292, 1, 1, 117301, 8, 3, 0, "夏は恋の季節…\nガンガン攻めるべし…\nなるほど…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5294, 1, 1, 117301, 10, 3, 0, "この子に乗りながら\nライブをする…\nというのはどうですか", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5295, 1, 1, 117301, 11, 3, 0, "まるで人魚になった\n気分…なんて、ふふ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5296, 1, 1, 118301, 1, 1, 0, "お姉ちゃんとして\nシオリンにかっこいい\nとこ見せなくっちゃ☆", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5297, 1, 1, 118301, 2, 1, 0, "いつかシオリンと\n一緒に空を飛んで\n空中散歩してみたいな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5298, 1, 1, 118301, 3, 1, 0, "すこし眠くなってきた\nかも…シオリンと\nお昼寝しようかな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5299, 1, 1, 118301, 4, 1, 0, "お散歩してこよっと！\nシオリンとお出かけ\nするのにいい場所\n探さなくっちゃ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5300, 1, 1, 118301, 5, 1, 0, "シオリンに過保護って\n言われちゃった…♪\nそれでも心配しちゃう\nのが姉心…だよね？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5301, 1, 4, 118301, 101, 1, 0, "気持ちいい日差し！\nシオリンと彼と、\nお昼寝したら\n気持ちよさそ～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5302, 1, 8, 118301, 102, 1, 0, "今度天気がいい日には\nシオリンと彼を誘って\n一緒にお出かけ\nしたいな～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5303, 1, 1, 118301, 1, 2, 0, "今日はご機嫌なんだ♪\nだって、シオリンの\n調子がいいんだもん☆", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5304, 1, 1, 118301, 2, 2, 0, "今からシオリンと\nピクニックに\n行くんだ♪　そうだ！\nキミも一緒にどう？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5305, 1, 1, 118301, 3, 2, 0, "わっ、びっくりした！\nシオリンから借りた本\n読むのに集中\nしてたから…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5306, 1, 1, 118301, 4, 2, 0, "うん？　どうしたの？\nあっ、もしかして\nこの服、気になる？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5307, 1, 4, 118301, 101, 2, 0, "シオリンもキミも元気\nいっぱいだと、私も\n元気になっちゃうよ☆", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5308, 1, 8, 118301, 102, 2, 0, "シオリンの前だけ\nじゃなくて、キミにも\nかっこいい私の姿を\n見てほしいな☆", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5309, 1, 1, 118301, 1, 2, 0, "おはよう！　えへへっ\n今日は早起きなんだ♪\nシオリンにかっこ悪い\nとこ見せたくないから", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5310, 1, 1, 118301, 2, 2, 0, "おなかすいたなぁ～…\nシオリンと一緒に\nおいしいもの食べに\n行こうかな！", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5311, 1, 1, 118301, 3, 2, 0, "もうこんな時間！？\nシオリンと一緒だと\n時間が早く\n過ぎていっちゃうね！", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5313, 1, 1, 118301, 2, 3, 0, "この快適な寝心地…！\nシオリンにもおすすめ\nしなきゃ…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5314, 1, 1, 118301, 3, 3, 0, "わあ、かわいい！\nそれにさわり心地も\nいいし、抱き枕に\nちょうどいいかも！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5315, 1, 1, 118301, 4, 3, 0, "いいにおい…♪\nなんだかリラックス\nできて…すやぁ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5316, 1, 1, 118301, 5, 3, 0, "ぽかぽか～☆\nシオリンと一緒に\nぬくぬくしたら\n気持ちよさそう♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5318, 1, 1, 118301, 7, 3, 0, "仲のいい人が一緒だと\nつい食が進んじゃうね\nえへへ…♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5319, 1, 1, 118301, 8, 3, 0, "ふむふむ…この本…\nシオリンが好きかも！\n今度おすすめしよっと", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5322, 1, 1, 118401, 1, 1, 0, "お姉ちゃん、\n私のために無理は\nしてないかな…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5323, 1, 1, 118401, 2, 1, 0, "新しく買った絵本…\nこういうのなら\nお姉ちゃんと楽しく\n読めるよね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5324, 1, 1, 118401, 3, 1, 0, "ニャットちゃんを\nここに連れてきて\nみんなで遊べないかな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5325, 1, 1, 118401, 4, 1, 0, "本屋さんに行こうかな\nみんなで読める\n面白い本が\nあるといいなぁ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5326, 1, 1, 118401, 5, 1, 0, "たまには…読むだけ\nじゃなくて、自分で\nお話を書いて\nみようかな…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5327, 1, 4, 118401, 101, 1, 0, "時々…お姉ちゃんと\nあの人が似てるって\n思うことがあります…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5328, 1, 8, 118401, 102, 1, 0, "お姉ちゃんとあの人…\n優しくて頼もしい\n二人に囲まれて…\n私、幸せ者だなぁ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5329, 1, 1, 118401, 1, 2, 0, "どうかしましたか？\nもしかして…あなたも\n一緒にこの本を\n読みたい…とか？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5330, 1, 1, 118401, 2, 2, 0, "けほっ…けほっ…\nあっ、心配いりません\n飲み物を飲んで\nむせただけですから", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5331, 1, 1, 118401, 3, 2, 0, "この格好…\n似合ってますか…？\nえへへ、照れますね…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5332, 1, 1, 118401, 4, 2, 0, "なぁに、お姉ちゃん？\n…あっ、すみません\n読書に集中していて…\n間違えちゃいました…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5333, 1, 4, 118401, 101, 2, 0, "お姉ちゃん、時々無理\nしちゃうから…\nあなたも一緒だと\n安心できます", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5334, 1, 8, 118401, 102, 2, 0, "お姉ちゃんとあなたと\n頼れる人に囲まれて\n私は幸せものですね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5335, 1, 1, 118401, 1, 2, 0, "なんだか朝から\n調子がいい…今日は\nいい日になりそうです", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5336, 1, 1, 118401, 2, 2, 0, "せっかくだから、\nお姉ちゃんを誘って\nお外でランチも\nいいかも", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5337, 1, 1, 118401, 3, 2, 0, "お姉ちゃんに心配\nかけないように…もう\nちょっと本を読んだら\n寝なくちゃね", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5339, 1, 1, 118401, 2, 3, 0, "ふんわり柔らかくて\n気持ちいい手触り…\nお姉ちゃんが寝るのに\nちょうどよさそう", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5340, 1, 1, 118401, 3, 3, 0, "ふふっ、かわいい♪\n絵本の物語みたいに\nおしゃべりできない\nかな…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5341, 1, 1, 118401, 4, 3, 0, "素敵な落ち着く香り♪\nなんだかお姉ちゃん\nみたい、ふふっ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5342, 1, 1, 118401, 5, 3, 0, "あったかくてほっと\nするなぁ\nここでゆっくり\n読書しようかな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5344, 1, 1, 118401, 7, 3, 0, "この味…\nお姉ちゃんが好きそう\n少しもらって、\n持って行こうかな…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5345, 1, 1, 118401, 8, 3, 0, "この本みたいな、\n心躍る大冒険も\nお姉ちゃんと一緒なら\nできちゃいそう", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5348, 1, 1, 117501, 1, 1, 0, "いい部屋ね\nここを私の分院に\nするのもいいかも\nしれないわね…♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5349, 1, 1, 117501, 2, 1, 0, "珍しいカンポーが\n手に入ったから\n色々試してみたいわ\n色々と、ね♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5350, 1, 1, 117501, 3, 1, 0, "この服を着ると\n自分が医者だって\nより実感するわね\nまあ、闇医者だけどね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5351, 1, 1, 117501, 4, 1, 0, "新薬開発のために\n薬草を採取して\nおこうかしら", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5352, 1, 1, 117501, 5, 1, 0, "いつ急患が来ても\n対応できるように\nしておきましょうか", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5353, 1, 4, 117501, 101, 1, 0, "あの子の研究資料も\nだいぶ増えてきたわね\nちゃんとまとめて\nおかないと♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5354, 1, 8, 117501, 102, 1, 0, "あの子は大事な\n研究対象。だから\nいつまでも健康で\nいてもらわないとね♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5355, 1, 1, 117501, 1, 2, 0, "薬に興味があるの？\nなら、今度ゆっくりと\n教えてあげるわね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5356, 1, 1, 117501, 2, 2, 0, "少し時間があいたから\n一緒に新薬の実験を\nしましょうか♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5357, 1, 1, 117501, 3, 2, 0, "どうしたの？\nまさか急患…！？\nあら、違うのね\nならよかったわ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5358, 1, 1, 117501, 4, 2, 0, "具合が悪い時はすぐに\n言うのよ。君には\nいつでも健康でいて\nほしいから", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5359, 1, 4, 117501, 101, 2, 0, "少し仮眠したいわね\n肩…いいえ、膝を\n貸してもらえる？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5360, 1, 8, 117501, 102, 2, 0, "たまには…君の隣なら\n闇医者も悪い研究者も\nお休みにするのも\n悪くないわね♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5361, 1, 1, 117501, 1, 2, 0, "たまには朝の陽ざしを\n浴びるのも…\n悪くないわね", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5362, 1, 1, 117501, 2, 2, 0, "天気がいい日は\nいい薬草が採れるの\n君も集めるの、\n手伝ってくれる？", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5363, 1, 1, 117501, 3, 2, 0, "いけない、もうこんな\n時間なのね\n診察に没頭すると\n時間感覚がなくなるわ", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5365, 1, 1, 117501, 2, 3, 0, "もう少し固い方が\n私好みだけど…\n悪くない寝心地ね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5366, 1, 1, 117501, 3, 3, 0, "このぬいぐるみ…\n闇医者として\n興味深いわね…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5367, 1, 1, 117501, 4, 3, 0, "落ち着く香りね\n私も診察室で\n使ってみようかしら", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5368, 1, 1, 117501, 5, 3, 0, "冷えは病につながるわ\nしっかり温めて\n体をいたわりましょ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5370, 1, 1, 117501, 7, 3, 0, "おいしい♪\n研究のお供に\nもってこいの\n味だわ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5371, 1, 1, 117501, 8, 3, 0, "医学は日進月歩…\n闇医者とはいえ\n最新の論文には\n目を通しておかないと", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5400, 1, 1, 117601, 1, 1, 0, "白衣の天使かあ\nまさにボクのための\n呼び方だね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5401, 1, 1, 117601, 2, 1, 0, "美しさとは\n日々の努力が\n作るものなんだよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5402, 1, 1, 117601, 3, 1, 0, "大変だ…\nボクは美しく\nなりすぎたかも", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5403, 1, 1, 117601, 4, 1, 0, "ボクのかわいさで\n癒やされて\nみんな健康に\nなっちゃうね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5404, 1, 1, 117601, 5, 1, 0, "どんな服装をしても\nボクの美しさは\n変わらないね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5405, 1, 4, 117601, 101, 1, 0, "これからはボクも\n美の伝道師として\nがんばらないとね♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5406, 1, 8, 117601, 102, 1, 0, "ボクのかわいい笑顔を\n近くで見る以上の\n癒やしはないよね♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5407, 1, 1, 117601, 1, 2, 0, "どう？　看護師姿の\nボクもかわいいでしょ\nもっと近くで見る？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5408, 1, 1, 117601, 2, 2, 0, "ほら！　美しいボクの\n看護師姿を見たら\n元気でちゃうでしょ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5409, 1, 1, 117601, 3, 2, 0, "白衣の天使のボクも\n最高に美しいね\n存分に癒されてよ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5410, 1, 1, 117601, 4, 2, 0, "具合が悪いの？\nならボクの美しい顔を\n処方してあげる♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5411, 1, 4, 117601, 101, 2, 0, "さあ今日もボクと\n一緒に美の追求に\n付き合ってもらうよ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5412, 1, 8, 117601, 102, 2, 0, "かわいい天使のボクが\nいつもそばにいるんだ\nキミって幸せ者だね♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5413, 1, 1, 117601, 1, 2, 0, "さすがボク\n目が覚めるような\n美しさでしょ？", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5414, 1, 1, 117601, 2, 2, 0, "美は食で作られる\n美容にいいものを\n食べようよ！", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5415, 1, 1, 117601, 3, 2, 0, "ふああ…早く寝よ？\n夜更かしは美容の\n大敵だからね", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5417, 1, 1, 117601, 2, 3, 0, "ふう～…\nリラックスする\nボクも美しい", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5418, 1, 1, 117601, 3, 3, 0, "ボクが抱きしめると\n普段よりかわいく\n見えちゃうね♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5419, 1, 1, 117601, 4, 3, 0, "美しいボクは\n常にいい香りを\nしていなくちゃね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5420, 1, 1, 117601, 5, 3, 0, "光がボクを照らしてる\nんじゃなくて、ボクが\n光を輝かせてるのさ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5422, 1, 1, 117601, 7, 3, 0, "バランスのいい食事が\n美しいボクを\n作るのさ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5423, 1, 1, 117601, 8, 3, 0, "ふむふむ…なるほど\nこうすればより美しく\nなれるかもしれない", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5425, 1, 12, 105701, 103, 1, 0, "仲間のためなら\nきっと…どこまでも\n強くなれるよね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5426, 1, 12, 105701, 103, 2, 0, "プリンセスフォーム？\n私と同じような力を\n持ってる人がいるの？\n会ってみたいな～！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5427, 1, 1, 118001, 1, 1, 0, "ここの空気は、\nどうもゆるすぎて…\n落ち着きませんわね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5428, 1, 1, 118001, 2, 1, 0, "陛下のお越しになる\n別宅としては、\n少し警備が\n甘すぎでは？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5429, 1, 1, 118001, 3, 1, 0, "あらあら？\nサッシにホコリが！\nちょっとネコ助！\n掃除は念入りに！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5430, 1, 1, 118001, 4, 1, 0, "いつまで入り浸って\nいるのかと、\n秘書に叱られて\nしまいましたわ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5431, 1, 1, 118001, 5, 1, 0, "もういっそ、\nここに間借りして\n仕事しようかしら…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5432, 1, 4, 118001, 101, 1, 0, "邪魔している身ですし\n家事くらい\nお手伝いさせなさいな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5433, 1, 8, 118001, 102, 1, 0, "本当は、意外と\n居心地が良いのかも…\n陛下の気持ち、\n少し分かりましたわ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5434, 1, 1, 118001, 1, 2, 0, "ちょっ…わたくしを\n叩いても、ホコリなど\nあまり出ませんわよ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5435, 1, 1, 118001, 2, 2, 0, "陛下っ？\nなんだ…貴方でしたの\n用件は手短にお願いね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5436, 1, 1, 118001, 3, 2, 0, "別に、好き嫌いは\nありませんけれど…\n魔物料理って、意外と\n美味なものですわね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5437, 1, 1, 118001, 4, 2, 0, "言っておきますけれど\nわたくしは商売人です\n荒事は専門外ですわよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5438, 1, 4, 118001, 101, 2, 0, "プリンセスナイトの\n能力…なにか儲け話に\n使えませんかしらね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5439, 1, 8, 118001, 102, 2, 0, "貴方がたのことも、\n少しくらいは\n信用してないことも\nありませんでしてよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5440, 1, 1, 118001, 1, 2, 0, "おはようございます\nさ、今日もバリバリ\n働きますわよ！", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5441, 1, 1, 118001, 2, 2, 0, "お昼ですわね\n栄養補給をしつつ\n働きますわよ！", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5442, 1, 1, 118001, 3, 2, 0, "定時ですって？\n関係ありません！\nわたくし、まだまだ\n働きますわよ～っ！", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5444, 1, 1, 118001, 2, 3, 0, "うっ…いけませんわ\n日頃の疲れが…\nふわぁ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5445, 1, 1, 118001, 3, 3, 0, "もふもふ…ふふっ♪\n…なんですのっ、\nなにかおかしくて！？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5446, 1, 1, 118001, 4, 3, 0, "いい香りですわね\nわたくしの執務室にも\n置きたいくらいですわ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5447, 1, 1, 118001, 5, 3, 0, "いけませんわ…\nこんなに暖かいと\n日頃の疲れが…ふわぁ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5449, 1, 1, 118001, 7, 3, 0, "もう一つ食べたら\n仕事に戻りますわよ？\nあと一つだけ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5450, 1, 1, 118001, 8, 3, 0, "商人にとって\n知識は武器ですわ\n陛下のため、貴方も\nもっと勉強なさい？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5452, 1, 1, 117701, 1, 1, 0, "故郷のお菓子を\nいろいろ作ったんだ～\nみんなに\n配ってくるさ～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5453, 1, 1, 117701, 2, 1, 0, "彼ってどんな仮装が\n似合うかな～？\nシーサーとか？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5454, 1, 1, 117701, 3, 1, 0, "カボチャチャンプルー\n作ってみよっと！\nきっとおいしいさ～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5455, 1, 1, 117701, 4, 1, 0, "視線を感じるさ～\nもしかして幽霊？\nおしゃべりするさ～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5456, 1, 1, 117701, 5, 1, 0, "さっき作った\nサーターアンダギー、\n早く彼に\n食べてほしいさ～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5457, 1, 4, 117701, 101, 1, 0, "彼のために、\n特別なイタズラを\n考えとくさ～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5458, 1, 8, 117701, 102, 1, 0, "いつか彼と、\n故郷のハロウィンを\n楽しみたいさ～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5459, 1, 1, 117701, 1, 2, 0, "ハロウィンは\n私たちが守るさ～♪\nさ、見回りに行こ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5460, 1, 1, 117701, 2, 2, 0, "この制服、\nハロウィンらしくて\n大好きさ～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5461, 1, 1, 117701, 3, 2, 0, "イタズラする気ね～？\nどんなことされるのか\n楽しみさ～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5462, 1, 1, 117701, 4, 2, 0, "カボチャのランタン、\n一緒に作ろ！\n絶対楽しいよ～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5463, 1, 4, 117701, 101, 2, 0, "お菓子くれるの？\nあは～、ありがとー♪\nもぐもぐ…\n優しい味がするさ～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5464, 1, 8, 117701, 102, 2, 0, "キミと一緒に過ごす\nハロウィンは、\nドキドキとワクワクが\n止まらないさ～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5465, 1, 1, 117701, 1, 2, 0, "ハロウィンは\n朝から大忙しさ～！\nお菓子作り、\n手伝ってほしいさ～", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5466, 1, 1, 117701, 2, 2, 0, "お化け屋敷に行こ！\n一緒にいっぱい\nドキドキしようね～♪", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5467, 1, 1, 117701, 3, 2, 0, "ここからが\nハロウィン本番さ～！\n幽霊たちと\nいっぱい遊ぶさ～♪", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5469, 1, 1, 117701, 2, 3, 0, "ちょっと休んだら、\nお菓子作りを\n再開するさ～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5470, 1, 1, 117701, 3, 3, 0, "でーじかわいいね～♪\nこの子に仮装させたら\nきっと似合うさ～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5471, 1, 1, 117701, 4, 3, 0, "お菓子みたいに\n甘い匂い…\nハロウィンらしい\nいい香りさ～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5472, 1, 1, 117701, 5, 3, 0, "ランドソルの秋って\n冷えるけど…\nこれがあれば\n体も心もぽかぽかさ～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5474, 1, 1, 117701, 7, 3, 0, "故郷のご飯も\nおいしいけど、\nこっちのご飯も\nでーじおいしいさ～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5475, 1, 1, 117701, 8, 3, 0, "パンプキンパイの\nレシピは…なるほど～\n勉強になるさ～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5477, 1, 1, 117701, 12, 3, 0, "全部おいしそうで\nどれから食べるか\n迷っちゃうさ～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5479, 1, 1, 117801, 1, 1, 0, "ムっ！　幽霊の気配を\n感じるデス！\nさあ姿を現すデス！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5480, 1, 1, 117801, 2, 1, 0, "ムムム…\n妖魔の力を忍法に\n使えないでショウか…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5481, 1, 1, 117801, 3, 1, 0, "怪しい妖魔を\n成敗するデス！\nハロウィンの平和は\nワタシが守りマスよ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5482, 1, 1, 117801, 4, 1, 0, "ワタシの祭囃子が\n聞こえマスかー？\nさあ集まるデース！\n陽気に踊りまショウ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5483, 1, 1, 117801, 5, 1, 0, "お祭りは\n楽しんだ者勝ちデス！\n踊らにゃ\nソンソンデス！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5484, 1, 4, 117801, 101, 1, 0, "幽霊になる前に\n天下統一を\n果たしたいデスね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5485, 1, 8, 117801, 102, 1, 0, "あの世に行っても\nショーグンは\nワタシの大事な\nショーグンデス！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5486, 1, 1, 117801, 1, 2, 0, "ショーグン！\n悪霊退治に\n出かけるデース！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5487, 1, 1, 117801, 2, 2, 0, "妖魔を家来に\nしてみるのは\nどうデス？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5488, 1, 1, 117801, 3, 2, 0, "マントを使って\nウツセミの術の精度を\n高めるデス！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5489, 1, 1, 117801, 4, 2, 0, "ハロウィンにちなんだ\n新しい忍法…\nすなわち妖忍法の\n開発を急ぐデス！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5490, 1, 4, 117801, 101, 2, 0, "あの世でも\nショーグンに\nお仕えしマスよー！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5491, 1, 8, 117801, 102, 2, 0, "ショーグンもワタシと\nハロウィンパレードを\n踊り明かしまセンか？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5492, 1, 1, 117801, 1, 2, 0, "おはようございマス！\n天下統一に向けて\n急いで準備するデス！", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5493, 1, 1, 117801, 2, 2, 0, "お腹減ってまセンか？\nハロウィンスイーツで\n休憩するデ〜ス", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5494, 1, 1, 117801, 3, 2, 0, "夜更かしすると幽霊が\nでるかもしれまセン…\nヒュ〜ドロロロ〜…", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5496, 1, 1, 117801, 2, 3, 0, "もふもふなところが\nわたあめちゃんに\n似ていマス。わんっ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5497, 1, 1, 117801, 3, 3, 0, "ぬいぐるみや人形には\n魂が宿るという\n言い伝えがありマス…\nマカフシギデ〜ス", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5498, 1, 1, 117801, 4, 3, 0, "ウ〜ム…\nなんとも怪しい\n香りがしマスね！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5499, 1, 1, 117801, 5, 3, 0, "パレードの熱気に\n負けないくらい\n熱いデス！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5501, 1, 1, 117801, 7, 3, 0, "ついつい食べすぎて\nしまうのは\n妖魔のせいデスか！？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5502, 1, 1, 117801, 8, 3, 0, "トーゴク妖怪の\n図鑑デス！　個性的な\n妖怪ばかりデスね！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5504, 1, 1, 117801, 12, 3, 0, "ハロウィンの甘味を\n集めまシタよ！\nショーグンへの\n貢ぎ物デス！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5531, 1, 12, 101301, 103, 1, 0, "彼と二人で\n買い物に…って…\nこれってデートに\nなっちゃうのかな！？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5532, 1, 12, 101301, 103, 2, 0, "とうっ！　どうどう？\n今の決めポーズ！\nかっこいいよね♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5533, 1, 1, 117901, 1, 1, 0, "ヒデサイとおそろの\nハロウィンコスとか\nしてみたいな～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5534, 1, 1, 117901, 2, 1, 0, "カボチャ料理かぁ…\n作ったらヒデサイ\n食べてくれるかな～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5535, 1, 1, 117901, 3, 1, 0, "ハロウィンスイーツ…\nどれもおいしすぎて\n激ヤバなんですけど！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5536, 1, 1, 117901, 4, 1, 0, "霊界ってどんなとこ\nなんだろ～\nわりと近所だったり？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5537, 1, 1, 117901, 5, 1, 0, "服をハロウィンっぽく\nアレンジするのも\nたのしーよね♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5538, 1, 4, 117901, 101, 1, 0, "ヒデサイと一緒に\nハロウィンパーティー\n…えへへ、楽しみ～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5539, 1, 8, 117901, 102, 1, 0, "ヒデサイといると\nドキドキする…\nなんでだろ～？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5540, 1, 1, 117901, 1, 2, 0, "ねねね、うちらも\nハロウィンパレード\n見に行こー？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5541, 1, 1, 117901, 2, 2, 0, "ハロウィンセイバーズ\nしゅつどーう！\nヒデサイも行こ～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5542, 1, 1, 117901, 3, 2, 0, "マントのロゴ\nかっこいいっしょ～？\nカボきちってゆーんだ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5543, 1, 1, 117901, 4, 2, 0, "トリックオア\nトリート！\nねえねえヒデサイ\nお菓子ちょうだい♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5544, 1, 4, 117901, 101, 2, 0, "ヒデサイも\nメイクしてみる？\nハロウィンぽいやつ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5545, 1, 8, 117901, 102, 2, 0, "ヒデサイのこと\nうちがいつでも\nあっためたげるね！\n…あ！　手、手の話！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5546, 1, 1, 117901, 1, 2, 0, "ハロウィンのイベント\n朝からでも\nやってるかな～？", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5547, 1, 1, 117901, 2, 2, 0, "カボチャでおやつ\n作ってみたんだ～\nヒデサイ食べてみて！", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5548, 1, 1, 117901, 3, 2, 0, "ハロウィンの夜って\nやっぱテンション\n上がるよね～♪", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5550, 1, 1, 117901, 2, 3, 0, "超ふかふかしてる～\nちょっと休憩させて\nもらっちゃお", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5551, 1, 1, 117901, 3, 3, 0, "はぁ…\nもにカワすぎて\nつらいんですけど～！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5552, 1, 1, 117901, 4, 3, 0, "ハロウィン限定の\nフレグランスじゃん！\nいい匂いする～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5553, 1, 1, 117901, 5, 3, 0, "やばばば…\nあったかくて\n眠くなっちゃう～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5555, 1, 1, 117901, 7, 3, 0, "これ食べて\nもうひとがんばり\nしよーっと！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5556, 1, 1, 117901, 8, 3, 0, "ハロウィンっぽい\nファッションとか\n載ってないかなぁ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5558, 1, 1, 117901, 12, 3, 0, "お菓子いっぱいだ～！\nあとでヒデサイにも\n持ってってあげよ～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5560, 1, 1, 118501, 1, 1, 0, "お掃除が終わったら\n次は書類を片付けて…\nその次は…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5561, 1, 1, 118501, 2, 1, 0, "皆さんが冒険している\n間の留守は私に\nお任せください♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5562, 1, 1, 118501, 3, 1, 0, "美食殿の皆さんが\n持ってきてくれる\nお土産が楽しみです♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5563, 1, 1, 118501, 4, 1, 0, "ネビアさんとは\nときどき一緒にお茶\nする仲なんです", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5564, 1, 1, 118501, 5, 1, 0, "ギルドハウスの乱れは\nギルドの乱れ\n整理整頓が大切です", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5565, 1, 4, 118501, 101, 1, 0, "素敵なギルドハウスの\n管理人ができて\nとっても幸せです♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5566, 1, 8, 118501, 102, 1, 0, "皆さんをほんの少し\n特別扱いしちゃう\n私がいます", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5567, 1, 1, 118501, 1, 2, 0, "何かわからないことが\nあるときは私に\n聞いてくださいね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5568, 1, 1, 118501, 2, 2, 0, "書類を書いていると\n落ち着きます…\n一緒にどうですか？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5569, 1, 1, 118501, 3, 2, 0, "あなたのまわりは\nいつも賑やかで\n楽しいですね♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5570, 1, 1, 118501, 4, 2, 0, "あっ！　お頼みしたい\nことがあって\n探してたんです♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5571, 1, 4, 118501, 101, 2, 0, "ギブアンドテイクは\n大切です。たまには\nお礼させてください♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5572, 1, 8, 118501, 102, 2, 0, "陰に日向にあなたを\n支えていきます♪\n期待していてください", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5573, 1, 1, 118501, 1, 2, 0, "今日も一日\n管理人として\nがんばります♪", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5574, 1, 1, 118501, 2, 2, 0, "お腹がすきました…\nそろそろお昼に\nしませんか？", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5575, 1, 1, 118501, 3, 2, 0, "もうこんな時間…\n皆さんといると一日が\nあっという間です", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5577, 1, 1, 118501, 2, 3, 0, "良質なお仕事は\n良質な休憩から\nです♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5578, 1, 1, 118501, 3, 3, 0, "かわいいものに\n囲まれてお仕事できる\nのは幸せですね♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5579, 1, 1, 118501, 4, 3, 0, "お仕事に集中できる\nアロマを設置するのも\nよさそうですね♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5580, 1, 1, 118501, 5, 3, 0, "ポカポカ温まりながら\n書類仕事をするのも\nいいものですね…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5582, 1, 1, 118501, 7, 3, 0, "栄養補給しないと\n頭が回りませんからね\nきちんと食べましょう", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5583, 1, 1, 118501, 8, 3, 0, "新条例はこのように\n活用されている、と…\nふむふむ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5585, 1, 1, 106701, 1, 1, 0, "会合用の資料と\n連載のネーム…\nどっちを片付けよう？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5586, 1, 1, 106701, 2, 1, 0, "イノリちゃん、また\nお金くすねてる…\n帰ってきたら\nお尻ペンペンだ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5587, 1, 1, 106701, 3, 1, 0, "カヤちゃんの装備\n傷むの早いなぁ〜\nメンテしてあげないと", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5588, 1, 1, 106701, 4, 1, 0, "そうだ、ラーゴのご飯\n買いに行かないと\n今日は何がいいかな？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5589, 1, 1, 106701, 5, 1, 0, "ここの人たちは見てて\n飽きない人ばっかり♪\n漫画の参考にしよ〜", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5590, 1, 4, 106701, 101, 1, 0, "また一緒に買い物にも\n行きたいし、冒険も\nしたいな〜…♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5591, 1, 8, 106701, 102, 1, 0, "…君は見てるだけで\n満足なの？ ちゃんと\n触って欲しいな…♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5592, 1, 1, 106701, 1, 2, 0, "私の目が見たいの？\nいいけど…魅了されて\n魂が壊れちゃうよ…？\n…冗談で〜す☆", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5593, 1, 1, 106701, 2, 2, 0, "ラーゴがね、君に\n遊んで欲しいって☆\n仲良くしてあげてね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5594, 1, 1, 106701, 3, 2, 0, "アシスタントの仕事\n気に入ってくれたなら\nまたお願いしていい？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5595, 1, 1, 106701, 4, 2, 0, "私が覚醒できるか？\n見せてあげたいけど…\nすごいことになるから\nまた今度で♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5596, 1, 4, 106701, 101, 2, 0, "楽しいトラブルが\nあったら、とりあえず\n声はかけて欲しいな♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5597, 1, 8, 106701, 102, 2, 0, "マッサージお願い♪\n変なところは…\n少しだけなら☆", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5598, 1, 1, 106701, 1, 2, 0, "おはよう〜♪\n今日も一日、一緒に\nがんばっていこうね", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5599, 1, 1, 106701, 2, 2, 0, "お腹空いちゃった…\n一緒にお昼行かない？\nいいお店があるんだ〜", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5600, 1, 1, 106701, 3, 2, 0, "君、眠れないの？\n誰かに見られてる？\n…きっと気のせいだよ", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5602, 1, 1, 106701, 2, 3, 0, "ちょっと休憩〜…\nこのまま原稿すれば\n疲れなくなるのかな？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5603, 1, 1, 106701, 3, 3, 0, "かわいい〜♪\nラーゴのお友達に\nなってくれる？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5604, 1, 1, 106701, 4, 3, 0, "いい香り〜♪\nこの目でどう見えるか\n伝えられたらいいのに", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5605, 1, 1, 106701, 5, 3, 0, "ほどほどなのが\nちょうどいい時も\nあるよね…♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5607, 1, 1, 106701, 7, 3, 0, "この味付け…\nあっちでも再現\nできるかな？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5608, 1, 1, 106701, 8, 3, 0, "ふふっ、面白い♪\nイノリちゃんの買う\n漫画にハズレなし☆", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5610, 1, 1, 119001, 1, 1, 0, "私…23歳だもん\n魔法少女だなんて\n名乗れないわ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5611, 1, 1, 119001, 2, 1, 0, "あん、どぅ、とろわ〜\nみんなイイ子に\nな〜る♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5612, 1, 1, 119001, 3, 1, 0, "あっ！　イケナイ子の\n気配がするわ！\n風紀の乱れは\n先生が許しませんっ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5613, 1, 1, 119001, 4, 1, 0, "ここで闇の力が\n発動したら大変だわ！\nやだっ…そう思ったら\n緊張して…ダメぇ〜！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5614, 1, 1, 119001, 5, 1, 0, "オイタした子はみんな\nどうしてうれしそうに\n私の愛のムチを\n欲しがるのかしら…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5615, 1, 4, 119001, 101, 1, 0, "あの子に次は何を\n教えてあげようかしら\n愛？　希望？\nそれとも…♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5616, 1, 8, 119001, 102, 1, 0, "あの子のことを\n考えると、愛と希望で\n胸がいっぱいになるの", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5617, 1, 1, 119001, 1, 2, 0, "先生なのに闇の力を\n持ってるのは秘密よ？\n他の子には\n知られたくないから…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5618, 1, 1, 119001, 2, 2, 0, "秘密の授業の内容？\nそれは…あんなことや\nこんなことよ♪\nそれから、それから…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5619, 1, 1, 119001, 3, 2, 0, "しーっ！　私の正体が\nルーセント学院の\n先生だって、ここでは\n秘密にしててね？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5620, 1, 1, 119001, 4, 2, 0, "あら？　補習して\nほしいの？　いいわよ\n君のお願いなら\nなんでも聞いちゃう♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5621, 1, 4, 119001, 101, 2, 0, "イイ子にはごほうびを\nあげなくちゃ！\n君はなにが欲しい？\n遠慮しちゃダメよ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5622, 1, 8, 119001, 102, 2, 0, "君との秘密が\nまた一つ\n増えちゃったね♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5623, 1, 1, 119001, 1, 2, 0, "今日も一日\n元気に過ごしましょう\n先生が指導してあげる", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5624, 1, 1, 119001, 2, 2, 0, "夜まで頑張れるように\nお昼ご飯はしっかり\n食べましょう〜！", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5625, 1, 1, 119001, 3, 2, 0, "夜更かしはダメよ？\nイケナイ子が\nすることです！", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5627, 1, 1, 119001, 2, 3, 0, "闇の力に振り回されて\nもうヘトヘトよ〜\n心も体も\n癒されたいわ〜", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5628, 1, 1, 119001, 3, 3, 0, "『秘密の学び舎』に\nこっそり連れて\n行っちゃおうかしら♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5629, 1, 1, 119001, 4, 3, 0, "イケナイ甘い香り…\nなんだか胸の奥が\n熱くなってきたわ…♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5630, 1, 1, 119001, 5, 3, 0, "やんっ♪\n燃え上がる愛みたい♪\n火照ってきちゃった♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5632, 1, 1, 119001, 7, 3, 0, "生徒の見本に\nなれるように\n好き嫌いはしないの", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5633, 1, 1, 119001, 8, 3, 0, "『学び舎に咲く禁断の\n愛』？　気になるわ…\nううん、ダメよっ！\nでも…ちょっとだけ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5635, 1, 12, 106301, 103, 1, 0, "次に会う時は、\nまたあそこに一緒に…\nえへへ…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5636, 1, 12, 106301, 103, 2, 0, "横顔見てるの、\nバレてないよね…？\n…ちらっ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5637, 1, 12, 105301, 103, 1, 0, "背が少し伸びたか！？\nあ、あいつのところへ\n報告に行かねばっ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5638, 1, 12, 105301, 103, 2, 0, "私も貴公の隣に\n立っていられるよう\n日々精進していくぞ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5639, 1, 1, 119101, 1, 1, 0, "ああ～\nドＭミストが\nあふれてしまいます～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5640, 1, 1, 119101, 2, 1, 0, "魔法少女として\nクウカ頑張ります～！\n愛と希望の妄想を…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5641, 1, 1, 119101, 3, 1, 0, "決めポーズの練習より\nやられポーズの練習を\nしておきます…♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5642, 1, 1, 119101, 4, 1, 0, "快感を溜めに溜めて\n放つドＭビームは\n最高ですよ～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5643, 1, 1, 119101, 5, 1, 0, "はっ！\nこれは闇の気配！\n…クウカ自身でした", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5644, 1, 4, 119101, 101, 1, 0, "ドＳさん…ぐふふ…\nクウカを縛りあげ…\nじゅるりぃ～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5645, 1, 8, 119101, 102, 1, 0, "ドＳさんに攻められる\nことで、クウカは進化\nしてゆくのです～！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5646, 1, 1, 119101, 1, 2, 0, "はぅっ！\nもっと激しく…！\n情熱的に～～～！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5647, 1, 1, 119101, 2, 2, 0, "クウカの変身シーンを\nじっくり眺めたい！？\nは、恥ずかしいです～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5648, 1, 1, 119101, 3, 2, 0, "「本当に魔法少女か\n確かめてやるよ…！」\nドＳさんはそう言って\nクウカを…ぐふふ…♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5649, 1, 1, 119101, 4, 2, 0, "愛と希望の力が\nクウカの中に…\nこの胸の高鳴りが…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5650, 1, 4, 119101, 101, 2, 0, "ひゃあんっ！\nそこです～っ！\nもっと、もっと～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5651, 1, 8, 119101, 102, 2, 0, "クウカをこんなに\n悦ばせるのは\nドＳさんだけです…♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5652, 1, 1, 119101, 1, 2, 0, "明るい時間に出歩くと\n目立ちますね\n魔法少女の衣装…", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5653, 1, 1, 119101, 2, 2, 0, "お昼の見回りです～♪\nああ～うっかりドＭを\n増やしてしまいました", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5654, 1, 1, 119101, 3, 2, 0, "夜！　それはクウカが\n活性化する時間です！\n闇の活動開始です～♪", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5656, 1, 1, 119101, 2, 3, 0, "気の緩んだクウカを\nドＳさんの鋭い牙が\n狙っています～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5657, 1, 1, 119101, 3, 3, 0, "魔法少女のマスコット\nにいいかもしれません\nもっと鎖とか飾りを…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5658, 1, 1, 119101, 4, 3, 0, "魅惑的な香りです～\nドＭミストの香りに\n似て…いや違いますね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5659, 1, 1, 119101, 5, 3, 0, "火照る体…紅潮した\nクウカの肢体を熱く\n見つめるドＳさん…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5661, 1, 1, 119101, 7, 3, 0, "食欲を満たした次は\n何を満たせば\nいいですかねぇ～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5662, 1, 1, 119101, 8, 3, 0, "魔法少女がひどい目に\n遭う物語は…\nなさそうですね…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5664, 1, 1, 119901, 1, 1, 0, "ケーキも悪くないけど\nプリンが一番なの\nでもせっかくだから\n両方もらってやるの！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5665, 1, 1, 119901, 2, 1, 0, "クリスマスって聞くと\nなんだかあったかい\n気分になるの～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5666, 1, 1, 119901, 3, 1, 0, "煙突がなくても\nミヤコはどこからでも\nおうちに入れるの♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5667, 1, 1, 119901, 4, 1, 0, "イブもプリン！\nクリスマスもプリン！\nプレゼントもプリン！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5668, 1, 1, 119901, 5, 1, 0, "クリスマスツリーの\n高いところの\n飾りつけは、ミヤコに\nまかせろなの～！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5669, 1, 4, 119901, 101, 1, 0, "アイツのプレゼント\n楽しみなの～\nきっと最っ高に特別な\nプリンなの～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5670, 1, 8, 119901, 102, 1, 0, "ミヤコがサンタなら\nアイツはトナカイなの\nつまりお似合いって\nことなの～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5671, 1, 1, 119901, 1, 2, 0, "ミヤコ、プレゼントを\n配りにいくの～♪\nオマエも手伝えなの！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5672, 1, 1, 119901, 2, 2, 0, "オマエにも飾りつけ\nしてやるの！\n頭にプリンを乗せると\nかわいいの♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5673, 1, 1, 119901, 3, 2, 0, "幽霊のミヤコが\nいるなら、サンタも\nいて当たり前なの～！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5674, 1, 1, 119901, 4, 2, 0, "靴下にプリンを\n入れたらつぶれるの\nだからプリンは直接\nミヤコによこすの～！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5675, 1, 4, 119901, 101, 2, 0, "今日はプリンだけじゃ\n満足してやらないの！\nミヤコをたくさん\n甘やかせなの！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5676, 1, 8, 119901, 102, 2, 0, "ミヤコのプリン\nオマエになら一個…\nうう～、一口だけ\nあげてもいいの！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5677, 1, 1, 119901, 1, 2, 0, "サンタの朝は早いの～\nミヤコも早起きして\nプレゼント準備したの\nとってもえらいの♪", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5678, 1, 1, 119901, 2, 2, 0, "今日は一日中\nみんなと遊ぶの～！\nオマエも来いなの！", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5679, 1, 1, 119901, 3, 2, 0, "みんなが寝たら\nチャンスなの～\nこっそりプレゼント\n配りにいくの♪", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5681, 1, 1, 119901, 2, 3, 0, "むふー、気持ちいいの\nこういうプレゼントも\nいいかもしれないの", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5682, 1, 1, 119901, 3, 3, 0, "これ気に入ったの～\nプリンとアイツの\n次くらいなの♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5683, 1, 1, 119901, 4, 3, 0, "いいにおいなの～\nうっかり成仏\nしちゃいそうなの…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5684, 1, 1, 119901, 5, 3, 0, "ここに飾っといた\n雪だるま\nなくなっちゃったの\nきっと成仏したの～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5686, 1, 1, 119901, 7, 3, 0, "これはオマエがくれる\nプリンとは\n別腹なの～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5687, 1, 1, 119901, 8, 3, 0, "プリンケーキ…\nこれならミヤコにも\n作れそうなの！\nさっそく作るの！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5689, 1, 1, 119901, 14, 3, 0, "びっくりしたの～！\n幽霊のミヤコを\n驚かすなんて…\nなかなかやるの！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5690, 1, 1, 119901, 15, 3, 0, "コイツの中…\nあったかいの～♪\n昼寝にぴったりなの～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5691, 1, 1, 119201, 1, 1, 0, "メリークリスマス！\n聖夜に笑顔を\nお届けするべ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5692, 1, 1, 119201, 2, 1, 0, "クリスマス前は\n乳製品がよく売れて\nありがてぇけんど…\n目が回る忙しさだ～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5693, 1, 1, 119201, 3, 1, 0, "牧場で売る\nクリスマスリースを\n作っておかねぇと…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5694, 1, 1, 119201, 4, 1, 0, "うちの動物たちにも\nクリスマスプレゼント\n用意しなきゃなぁ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5695, 1, 1, 119201, 5, 1, 0, "クリスマスに向けた\n限定商品とか\n作ってみるべか？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5696, 1, 4, 119201, 101, 1, 0, "あんちゃんへの\nプレゼント選び…\nここでボケるか…？\nやめておくべか…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5697, 1, 8, 119201, 102, 1, 0, "オラ、あんちゃんから\n本当にたくさんの物を\nもらってんなぁ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5698, 1, 1, 119201, 1, 2, 0, "雪遊びがしたいべか？\nへへっ、あんちゃん\nには負けねぇど！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5699, 1, 1, 119201, 2, 2, 0, "ディナーと一緒に\n聖夜のお笑いライブを\nすんのはどうだべか？\nツッコミは任せたべ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5700, 1, 1, 119201, 3, 2, 0, "寒くなってくると\n鍋が食べたくなるべや\nあんちゃんはどうだ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5701, 1, 1, 119201, 4, 2, 0, "あんちゃんは\nサンタさんのこと\n信じてるだか？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5702, 1, 4, 119201, 101, 2, 0, "どでかいクリスマス\nケーキをこさえるべ\nあんちゃんも作るの\n手伝ってくんろ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5703, 1, 8, 119201, 102, 2, 0, "あんちゃんが\nオラの旦那だったら…\nって、あんちゃん！\n今の聞いてただか！？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5704, 1, 1, 119201, 1, 2, 0, "牧場の朝は早いど！\nさぁてと、まずは\n雪かきしに行くべや", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5705, 1, 1, 119201, 2, 2, 0, "あんちゃんも一緒に\nお昼食べてくか？\n採れたての冬野菜は\nうんまいど～♪", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5706, 1, 1, 119201, 3, 2, 0, "よい子は早く寝ねぇと\nサンタさんが\n来てくれねぇべよ？", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5708, 1, 1, 119201, 2, 3, 0, "こういうのリンリンが\n好きそうだな…\nプレゼント候補に\n入れておくべ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5709, 1, 1, 119201, 3, 3, 0, "もふもふしてて\nなまらめんこくて…\nあぁ…癒されるべや…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5710, 1, 1, 119201, 4, 3, 0, "この匂い…\n気持ちが和むべや\n夢心地になっちまうな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5711, 1, 1, 119201, 5, 3, 0, "あったけぇところは\n落ち着くなぁ…\nつい眠くなっちまう", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5713, 1, 1, 119201, 7, 3, 0, "こ、この味は…！\n…うーん、もっと\nリアクション芸も\n練習しねぇとな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5714, 1, 1, 119201, 8, 3, 0, "シオシオって\nどんな本読んでんだ？\n…難しい字が\nぎっしりだべや…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5716, 1, 1, 119201, 14, 3, 0, "この仕掛け…\nオラのコントに\n活かせるかも\nしんねぇな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5717, 1, 1, 119201, 15, 3, 0, "この着ぐるみで\nリマリマのモノマネを\n練習してみるべか…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5718, 1, 12, 105001, 103, 1, 0, "もっとキレイになって\nアイツにぎゃふんと\n言わせてやるんだから", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5719, 1, 12, 105001, 103, 2, 0, "アタシのこと\nどう思う？\nかわいいって…そこは\nキレイって言ってよね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5720, 1, 1, 119301, 1, 1, 0, "ハートマークの刺繍…\nは、やりすぎですかね\nいやちょっとくらい\nアピールしてみても…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5721, 1, 1, 119301, 2, 1, 0, "雪降らないかな～\nそしたらお兄ちゃんと\n雪だるま作ったり\nできるのに～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5722, 1, 1, 119301, 3, 1, 0, "プレゼントを入れる\n靴下…いくつ編んでも\n足りません～！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5723, 1, 1, 119301, 4, 1, 0, "お兄ちゃんとの\nクリスマスデートは\n早い者勝ちですからね\n負けませんよ～！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5724, 1, 1, 119301, 5, 1, 0, "クリスマスだし…\n受験勉強は今日だけ\nお休みです♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5725, 1, 4, 119301, 101, 1, 0, "お兄ちゃんと\nおそろいのコーデで\nクリスマスデート…\nしたいなぁ～！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5726, 1, 8, 119301, 102, 1, 0, "お兄ちゃんに\nプレゼントする編み物\nこれで３着目～と♪\nあれ、作りすぎ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5727, 1, 1, 119301, 1, 2, 0, "えへへ、もっと近くで\n見たくありませんか？\n私のとっておきの\nクリスマスコーデ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5728, 1, 1, 119301, 2, 2, 0, "お兄ちゃんが風邪を\n引いたら一大事です！\n私の編み物で暖かく\nしてあげます♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5729, 1, 1, 119301, 3, 2, 0, "お兄ちゃん、ほっぺに\nケーキのクリームが\nついちゃってますよ♪\n私がとってあげます！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5730, 1, 1, 119301, 4, 2, 0, "お兄ちゃん！\nケーキ、あーんって\nしてあげます！\nほら、あーん♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5731, 1, 4, 119301, 101, 2, 0, "クリスマスは大切な\n人と過ごすものっ！\nお兄ちゃん…妹の予定\n空いてますよっ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5732, 1, 8, 119301, 102, 2, 0, "クリスマスはずっと、\nお兄ちゃんと一緒に\nいたい…って言ったら\n欲張り…すぎですか？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5733, 1, 1, 119301, 1, 2, 0, "早起きは３本お得！\n…クリスマスなら\n追加で10本ぐらい\nもらえちゃうかも！？", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5734, 1, 1, 119301, 2, 2, 0, "お昼はケーキ？\nそれとも七面鳥？\nごちそういっぱいで\n迷っちゃいますね～♪", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5735, 1, 1, 119301, 3, 2, 0, "お兄ちゃんと\nクリスマスに夜更かし\n…えへへ、ちょっぴり\nドキドキしちゃう♪", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5737, 1, 1, 119301, 2, 3, 0, "編み物をするには\n絶好のくつろぎ空間\nですね～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5738, 1, 1, 119301, 3, 3, 0, "なんというかわいさ…\nクリスマス\nプレゼントに\nもってこいですね！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5739, 1, 1, 119301, 4, 3, 0, "これがお兄ちゃんの\nお部屋の香り…？\nんん～！　ずっと\nかいでいたいです～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5740, 1, 1, 119301, 5, 3, 0, "ふぁ、あったか～い…\n冬場はこういうのが\nありがたいですね～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5742, 1, 1, 119301, 7, 3, 0, "ごちそうだぁ～♪\nほら、お兄ちゃんも\n食べて食べてっ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5743, 1, 1, 119301, 8, 3, 0, "クリスマスデートの\n必勝法…こ、これは\n読むしかないです！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5745, 1, 1, 119301, 14, 3, 0, "わっ、すごいです～！\nこれを飾っておけば\nクリスマスが一層\n賑やかになりますね♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5746, 1, 1, 119301, 15, 3, 0, "さぷらーいずっ！\nリマちゃんじゃなくて\nリノちゃんでした～！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5747, 1, 1, 120601, 1, 1, 0, "ミミにもミソギにも\nあの人にも頼りに\nしてもらえるように\nしっかりしなきゃ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5748, 1, 1, 120601, 2, 1, 0, "ミミはにんじんを\n食べられてすごいな…\n私も見習わなきゃ\nがんばろう…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5749, 1, 1, 120601, 3, 1, 0, "ミソギから怖い話\n聞いちゃった…\nうぅ…誰か一緒に\nいてくれないかな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5750, 1, 1, 120601, 4, 1, 0, "立派な大人に\nなれるように\nいっぱい勉強\nしておかなくっちゃ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5751, 1, 1, 120601, 5, 1, 0, "なんだか…あの人の\nあやしい視線を\n感じるような…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5752, 1, 4, 120601, 101, 1, 0, "あの人に勉強を\n教えられるように\nしっかり予習を\nしておこうっと…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5753, 1, 8, 120601, 102, 1, 0, "今日はあの人と\n一緒に冒険できる…\nなんだかワクワク\nしてきちゃった…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5754, 1, 1, 120601, 1, 2, 0, "あ、いいとこに来て\nくれましたね！\nあなたに頼みたい\nことがあるんです！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5755, 1, 1, 120601, 2, 2, 0, "きゃあっ！？\nへ、変なところを\n触らないでください！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5756, 1, 1, 120601, 3, 2, 0, "どうですかこの格好？\nかわいい、ですか…？\nそ、そうですか…\nありがとうございます", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5757, 1, 1, 120601, 4, 2, 0, "ひゃあっ…！\nすみません、勉強に\n集中してたので\n驚いちゃいました", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5758, 1, 4, 120601, 101, 2, 0, "一緒に勉強して立派な\n大人になりましょうね\n私がそばで見張ってて\nあげます", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5759, 1, 8, 120601, 102, 2, 0, "まだ立派ではないかも\nですけど、私よりも\n大人なあなたのことは\n頼りにしてますからね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5760, 1, 1, 120601, 1, 2, 0, "立派な大人は\n朝の時間の使い方が\n上手だと聞きました\nあなたはどうですか？", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5761, 1, 1, 120601, 2, 2, 0, "ミミとミソギと\nあなたと…大事な人と\n一緒のお昼ごはんは\nやさしい味がしますね", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5762, 1, 1, 120601, 3, 2, 0, "うぅ…怖い話を\n思い出しちゃいました\n寝るまで一緒に\nいてくれますか…？", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5764, 1, 1, 120601, 2, 3, 0, "すごい座り心地…！\nとっても勉強が\nはかどりそう…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5765, 1, 1, 120601, 3, 3, 0, "この子…なんだか\nミミに似てるような\n気がする…ふふっ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5766, 1, 1, 120601, 4, 3, 0, "いい香り…♪\n勉強で疲れた\n頭がリフレッシュ\nできそうです", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5767, 1, 1, 120601, 5, 3, 0, "ぽかぽかだ…♪\nミミとミソギと\nあの人と一緒に\nいるときみたい", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5769, 1, 1, 120601, 7, 3, 0, "いっぱい食べたら\n早く大人になれたり\nしないかな…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5770, 1, 1, 120601, 8, 3, 0, "あう…\n知らない単語が…\nちゃんと調べて\n覚えなくっちゃ…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5773, 1, 1, 120401, 1, 1, 0, "うさぎさんが\nぴょ～んぴょん♪\nみんなえがおで\nるんたった～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5774, 1, 1, 120401, 2, 1, 0, "キョウカちゃんも\nいつかにんじんさんを\n好きになってくれたら\nうれしいな～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5775, 1, 1, 120401, 3, 1, 0, "今日のミソギちゃんの\nイタズラ、びっくり\nしちゃったけど\nおもしろかったな～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5776, 1, 1, 120401, 4, 1, 0, "このおうち、いつも\nいろんな人がいて\nドキドキぴょんぴょん\nって楽しいな～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5777, 1, 1, 120401, 5, 1, 0, "いいお天気～♪\nお外でみんなと\nいっぱい遊びたいな♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5778, 1, 4, 120401, 101, 1, 0, "ミソギちゃんも\nキョウカちゃんも\nおにいちゃんがいると\nずっと笑顔だね～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5779, 1, 8, 120401, 102, 1, 0, "おにいちゃんにはやく\nあいたいな～♪\nまだかな、まだかな♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5780, 1, 1, 120401, 1, 2, 0, "えへへ♪\nおにいちゃんに\nなでなでされると\nぽかぽかするね♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5781, 1, 1, 120401, 2, 2, 0, "聞いて、おにいちゃん\nさっきね、かわいい\nちょうちょさんに\n会ったんだよ～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5782, 1, 1, 120401, 3, 2, 0, "ふわっ…！\nおにいちゃんの顔を\nお絵かきしてたから\nびっくりしちゃった♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5783, 1, 1, 120401, 4, 2, 0, "おにいちゃんも\n遊びたいの？\nそれじゃあ二人も\n呼んで遊ぼう♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5784, 1, 4, 120401, 101, 2, 0, "おにいちゃんも\nみんなも、優しい人\nばっかりで\nミミ幸せ～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5785, 1, 8, 120401, 102, 2, 0, "おにいちゃん♪\n高い高いしてほしいな\nすっごくぴょんぴょん\nできて、好きなんだ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5786, 1, 1, 120401, 1, 2, 0, "おはよう～♪\nおにいちゃん、今日は\nおひさまみたいに\nきらきら元気だね～♪", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5787, 1, 1, 120401, 2, 2, 0, "おひるごはんだね～♪\nにんじんさんを\nいっぱい食べて\n元気いっぱい♪", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5788, 1, 1, 120401, 3, 2, 0, "ふわあ…\n眠くなってきちゃった\nおにいちゃん、\n一緒に寝よう～？", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5790, 1, 1, 120401, 2, 3, 0, "ふわぁ、気持ちいい♪\nずっとこうやって\n座ってたくなるね～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5791, 1, 1, 120401, 3, 3, 0, "わあ、かわいい～\nぎゅ～っ、ぎゅ～っ♪\nこれで仲よしだね\nえへへ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5792, 1, 1, 120401, 4, 3, 0, "気持ちいいにおい～♪\nぴょんぴょんしたく\nなってきちゃった♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5793, 1, 1, 120401, 5, 3, 0, "あったかぽかぽか♪\nおにいちゃんも\nきてくれたら、もっと\nあったかくなりそう♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5795, 1, 1, 120401, 7, 3, 0, "もぐもぐ…ごっくん♪\nおっきくなりたいから\nいっぱい食べるよ～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5796, 1, 1, 120401, 8, 3, 0, "えへへ、おもしろ～い\nこの絵本ね、\nキョウカちゃんから\n教えてもらったんだ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5799, 1, 1, 120501, 1, 1, 0, "この部屋…探索の\nしがいがありそう！\nついでにイタズラも\nしかけちゃお♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5800, 1, 1, 120501, 2, 1, 0, "ミミってああ見えて\n実は大胆で度胸も\nあるよね…ちょっと\nかっこいいな…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5801, 1, 1, 120501, 3, 1, 0, "怖い話を聞いた時の\nキョウカって\n面白いんだよね～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5802, 1, 1, 120501, 4, 1, 0, "にいちゃんが\n慣れちゃわないように\nまた新しいイタズラを\n考えなきゃ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5803, 1, 1, 120501, 5, 1, 0, "イタズラもいいけど\nミミとキョウカと\nにいちゃんと、普通に\n遊ぶのも楽しいなぁ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5804, 1, 4, 120501, 101, 1, 0, "ミミにキョウカ、\nそれににいちゃん！\nみんなそろえば愉快な\n冒険が始まりそう！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5805, 1, 8, 120501, 102, 1, 0, "次はどんなイタズラを\nしたら、にいちゃんは\n喜んでくれるかな？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5806, 1, 1, 120501, 1, 2, 0, "今ミソギに触ったね？\nヤバいよにいちゃん…\n逃げないとトラップが\n発動しちゃうよ…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5807, 1, 1, 120501, 2, 2, 0, "わわっ！？　びっくり\nしたあ…！　新しい\nトラップを作ってて\n気づかなかったよ～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5808, 1, 1, 120501, 3, 2, 0, "お？　にいちゃん、\nイタズラする側にも\n興味があるの？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5809, 1, 1, 120501, 4, 2, 0, "にいちゃんなにか用？\nあ、もしかして…\nミソギと遊びたく\nなっちゃった？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5810, 1, 4, 120501, 101, 2, 0, "ベストタイミングだよ\nにいちゃん！\n今からみんなで探検に\n行くところなんだ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5811, 1, 8, 120501, 102, 2, 0, "にいちゃんってさ\nホントに優しいよね\nなにしても怒らないし\n…ありがとね♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5812, 1, 1, 120501, 1, 2, 0, "にいちゃんおはよー！\n景気づけに本日\n一発目のイタズラ、\nやっちゃうよ～♪", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5813, 1, 1, 120501, 2, 2, 0, "お昼ごはんはロシアン\nサンドイッチだよ！\nさあ、にいちゃん！\n好きなの選んでよ！", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5814, 1, 1, 120501, 3, 2, 0, "夜ってなんだか\nワクワクしてなかなか\n眠れないんだよね\nにいちゃんはどう？", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5816, 1, 1, 120501, 2, 3, 0, "この座り心地のよさ…\nなにかのトラップに\nうまく使えないかな？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5817, 1, 1, 120501, 3, 3, 0, "こういうのに細工して\nミミやキョウカを\n驚かせると、いい反応\nするんだよね♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5818, 1, 1, 120501, 4, 3, 0, "においをうまく使った\nイタズラって…\nなにかないかな？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5819, 1, 1, 120501, 5, 3, 0, "あったまりながら\n新しいイタズラを\n考えると、いい案が\n出て来たりするんだ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5821, 1, 1, 120501, 7, 3, 0, "おいしくてついつい\n食べ過ぎちゃうなぁ…\nはっ…！？　これって\nまさかトラップ…！？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5822, 1, 1, 120501, 8, 3, 0, "このキョウカから\n借りた冒険物語\nすっごく\nおもしろいんだよね！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5825, 1, 1, 120801, 1, 1, 0, "晴れ着を着ると\nなんだか心が躍るね\n足取りも軽く\nなるってもんさ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5826, 1, 1, 120801, 2, 1, 0, "正月の食べ物ってのは\n美味いもんだね\nいくらでも\n食べれちまうよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5827, 1, 1, 120801, 3, 1, 0, "普段は好き勝手してる\nアタシらも\n正月くらいは\n大人しくするさ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5828, 1, 1, 120801, 4, 1, 0, "年越しそばに雑煮\nこの季節は\n温かいものが\n体に沁みるね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5829, 1, 1, 120801, 5, 1, 0, "正月だからって\nのんびりしすぎたね\nちょっと釣りにでも\n行こうかね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5830, 1, 4, 120801, 101, 1, 0, "いい正月だね\nいつかあの子と\n一杯やりたいもんだよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5831, 1, 8, 120801, 102, 1, 0, "大事な人と過ごす\n正月ってのがこんなに\n楽しいもんだとはね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5832, 1, 1, 120801, 1, 2, 0, "部屋があったかくて\n顔が火照っちまったよ\nちょいと外に涼みに\n行かないか？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5833, 1, 1, 120801, 2, 2, 0, "餅ってのは\n何をつけてもうまいね\nお前さんは\nどの味が好きだい？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5834, 1, 1, 120801, 3, 2, 0, "お祝いの鯛が\n足りないって？\nそれじゃあアタシが\n釣ってきてやるよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5835, 1, 1, 120801, 4, 2, 0, "おせちが美味いのは\n分かるけど\n食べ過ぎには\n気を付けるんだよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5836, 1, 4, 120801, 101, 2, 0, "お前さんのおかげで\nいい正月を\n迎えられたよ\nありがとさん！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5837, 1, 8, 120801, 102, 2, 0, "慣れない晴れ着も\nお前さんに見て\nもらえたなら\n着たかいがあったね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5838, 1, 1, 120801, 1, 2, 0, "おはようさん\n寒いのは分かるけど\nいい加減布団から\n出て顔洗ってきな", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5839, 1, 1, 120801, 2, 2, 0, "今日の昼飯は雑煮だ\nアタシの郷土の味\nしっかり味わって\nおくれよ", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5840, 1, 1, 120801, 3, 2, 0, "おや、お前さんも\n夜ふかしかい？\n退屈してたところさ\n付き合っておくれよ", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5842, 1, 1, 120801, 2, 3, 0, "晴れ着だと座り方にも\n気を遣うね\nしわになったら大変さ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5843, 1, 1, 120801, 3, 3, 0, "せっかくだし\nこの子にも晴れ着を\n着せてあげたいね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5844, 1, 1, 120801, 4, 3, 0, "これは冬の花の香り…\n風情があって\nいいじゃないか", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5845, 1, 1, 120801, 5, 3, 0, "あったかいねぇ…\nこんな寒い日には\nまさに天国だよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5847, 1, 1, 120801, 7, 3, 0, "いつも食べてる\nものでも正月に\n食べると\n新鮮な気がするね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5848, 1, 1, 120801, 8, 3, 0, "せっかくの正月休みだ\nたまにはゆっくりと\n本でも読もうかね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5850, 1, 1, 120701, 1, 1, 0, "新年のあいさつ…\n私も街のみんなに\nしてみようかしら…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5851, 1, 1, 120701, 2, 1, 0, "正月はゆっくり過ごす\nのよね…ゆっくりって\n何したらいいのかしら", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5852, 1, 1, 120701, 3, 1, 0, "この格好も\n慣れてくれば\n動きやすいわ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5853, 1, 1, 120701, 4, 1, 0, "う～ん、今年の抱負…\nやりたいことが\nたくさんあるのよね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5854, 1, 1, 120701, 5, 1, 0, "おせち料理\nネビアの分も\n用意しなくちゃね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5855, 1, 4, 120701, 101, 1, 0, "ふふっ\n正月っていいわね\nみんな楽しそうだもの", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5856, 1, 8, 120701, 102, 1, 0, "新年の思い出…\nこれからもっと作るわ\nそれが私の抱負よ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5857, 1, 1, 120701, 1, 2, 0, "今日は家で過ごすの？\nあなたと一緒なら\nきっと楽しいわね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5858, 1, 1, 120701, 2, 2, 0, "どう？　この格好？\nコッコロさんは\n褒めてくれたけど…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5859, 1, 1, 120701, 3, 2, 0, "ちょうど良かったわ\nあなたも大掃除\n手伝ってくれる？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5860, 1, 1, 120701, 4, 2, 0, "これから料理するけど\nお雑煮と焼き餅\nどっちが好きかしら？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5861, 1, 4, 120701, 101, 2, 0, "『今の私』にとって\n初めての正月だもの\nあなたと楽しまないと", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5862, 1, 8, 120701, 102, 2, 0, "去年よりも今年…\nもっと成長するからね\n…お兄ちゃん♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5863, 1, 1, 120701, 1, 2, 0, "おはよう\nせっかくの正月だもの\n朝から楽しまないとね", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5864, 1, 1, 120701, 2, 2, 0, "ねぇ…\n良かったら正月の遊び\n教えてくれるかしら？", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5865, 1, 1, 120701, 3, 2, 0, "ふわぁ～…今日が\n終わっちゃうのが…\nもったいないわね…", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5867, 1, 1, 120701, 2, 3, 0, "ふぅ…お参り後だから\n心地よすぎて眠って\nしまいそうだわ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5868, 1, 1, 120701, 3, 3, 0, "ふふっ\nかわいいものって\n癒されるわね…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5869, 1, 1, 120701, 4, 3, 0, "…ん\nこれが梅の香り？\n…いい匂いね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5870, 1, 1, 120701, 5, 3, 0, "キャルじゃないけど…\nこのまま丸く\nなりたいところね…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5872, 1, 1, 120701, 7, 3, 0, "はむはむっ…♪\nせっかくの正月だもの\n堪能しなくちゃね♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5873, 1, 1, 120701, 8, 3, 0, "…ふんふん\n帯の結び方って\nこんなにあるのね…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5875, 1, 12, 104201, 103, 1, 0, "今の私が作れる\n最高の一曲…\nあの人に届くといいな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5876, 1, 12, 104201, 103, 2, 0, "あの、歌を…\n聞いてくれませんか？\nあなたに贈る\n特別な歌を", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5877, 1, 1, 120901, 1, 1, 0, "ひい、ふう…\nむむ、みなに配る\nお年玉のポチ袋が\n残りわずかなのじゃ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5878, 1, 1, 120901, 2, 1, 0, "宴の準備はわらわに\n任せておくのじゃ！\n支配者の手腕、\n見せつけてくれる！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5879, 1, 1, 120901, 3, 1, 0, "新年の願掛けなら\nわらわにすればよい\n支配者に頼むほうが\n確実じゃろう？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5880, 1, 1, 120901, 4, 1, 0, "みなからもらった\nこの年賀状とやら…\nわらわも\n書いてみようかの♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5881, 1, 1, 120901, 5, 1, 0, "正月飾りにもいろいろ\n種類があるんじゃの～\nどれも華々しくて\nわらわ好みじゃ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5882, 1, 4, 120901, 101, 1, 0, "あやつへのお年玉は\nちと多めにしておくか\n…日ごろの感謝も\n込めてのう", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5883, 1, 8, 120901, 102, 1, 0, "あやつのことを想うと\nそれだけで行く年\n来る年が楽しみになる\nふふ、愉快なやつじゃ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5884, 1, 1, 120901, 1, 2, 0, "みなに晴れ着を\n褒められて、とても\nよい気分なのじゃ♪\n次はお主の番じゃぞ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5885, 1, 1, 120901, 2, 2, 0, "お主へのお年玉は\nいちばん最後じゃ！\n残り物には福が宿る\nそうじゃからのう", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5886, 1, 1, 120901, 3, 2, 0, "この季節は手が\nかじかむのう…ほれ、\nお主！　わらわの手を\nあたためるのじゃ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5887, 1, 1, 120901, 4, 2, 0, "お主のために雑煮を\n手作りしてみたのじゃ\n食べていくがよい♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5888, 1, 4, 120901, 101, 2, 0, "眷属のお主に\n正月休みはまだ早い！\nずっとわらわのそばに\n控えておくのじゃ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5889, 1, 8, 120901, 102, 2, 0, "ふふ、これほど楽しい\n正月は初めてじゃ…\nお主と過ごす時間は\nいつだって特別じゃな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5890, 1, 1, 120901, 1, 2, 0, "早起きしてわらわを\n待っておったのか？\nさすがはわらわの眷属\n殊勝な心がけじゃな♪", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5891, 1, 1, 120901, 2, 2, 0, "食事どきじゃな\n正月の宴会料理が\nわらわたちを\n待っておるぞ♪", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5892, 1, 1, 120901, 3, 2, 0, "夜も更けてきたのう\n冬の夜は長い…\n心ゆくまでお主と\n過ごせるわけじゃな♪", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5894, 1, 1, 120901, 2, 3, 0, "実に心地がよい♪\nこのまま寝正月も\n悪くないかも\nしれぬのう", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5895, 1, 1, 120901, 3, 3, 0, "愛いやつじゃのう\nわらわのそばに置いて\nやってもよいぞ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5896, 1, 1, 120901, 4, 3, 0, "品のある香りじゃのう\n寝室に置いておけば\nなにかご利益に\nあずかれそうじゃな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5897, 1, 1, 120901, 5, 3, 0, "心地よい…\nわらわはしばらく\nここから動かぬぞ\nお主も付き合うがよい", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5899, 1, 1, 120901, 7, 3, 0, "舌鼓が鳴るのう♪\n正月の宴には\n持ってこいの品じゃ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5900, 1, 1, 120901, 8, 3, 0, "ほう、新春にはそんな\n習わしが…正月を\n制するには、まだまだ\n勉強が必要じゃのう", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5902, 1, 1, 121001, 1, 1, 0, "久しぶりの我が家って\n感じです！　やっぱり\nここが一番ですね〜！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5903, 1, 1, 121001, 2, 1, 0, "さあ食べましょう！\nみんなで囲める食卓を\n心から味わいますよ☆", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5904, 1, 1, 121001, 3, 1, 0, "ランドソルに\n帰ってきた途端に\nお腹が空きました〜！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5905, 1, 1, 121001, 4, 1, 0, "ただいま☆\n幸せ特盛りの\nわたしの日常！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5906, 1, 1, 121001, 5, 1, 0, "ようやくランドソルに\n帰ってきたことですし\nやっぱり魔物料理を\nいただきましょう☆", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5907, 1, 4, 121001, 101, 1, 0, "あの人との絆を\n守るために\nもっともっと食べて\n強くならなくちゃ☆", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5908, 1, 8, 121001, 102, 1, 0, "いつもありがとうって\nあの人を思いっきり\n抱きしめたいです☆", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5909, 1, 1, 121001, 1, 2, 0, "あなたの想いは\nこの身をかけて\n全力で受け止めます！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5910, 1, 1, 121001, 2, 2, 0, "おかえりなさい☆\nここにいるみんなも\nあなたの帰りをずっと\n待っていましたよ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5911, 1, 1, 121001, 3, 2, 0, "このギルドハウスで\n冒険の疲れを\n癒しましょう〜！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5912, 1, 1, 121001, 4, 2, 0, "あなたの想いが\n全力全開を超える\n力をくれました！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5913, 1, 4, 121001, 101, 2, 0, "日常が続く幸せを\nあなたと一緒に\nかみしめたいです☆", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5914, 1, 8, 121001, 102, 2, 0, "ぎゅ〜っ！\nわたしたちは\nどんなことがあっても\nずっと一緒ですよ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5915, 1, 1, 121001, 1, 2, 0, "おはようございます！\nあなたと過ごせる\n幸せを、今日も\n味わいますよ〜！", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5916, 1, 1, 121001, 2, 2, 0, "ご飯の時間ですよー！\nあなたも一緒に\n食べましょうね！", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5917, 1, 1, 121001, 3, 2, 0, "明日もあなたが隣に\nいてくれることを\n心から願ってますよ\nおやすみなさい☆", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5919, 1, 1, 121001, 2, 3, 0, "ふい〜…\n安心できる場所で\nくつろげるって\n幸せですね〜…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5920, 1, 1, 121001, 3, 3, 0, "じゅるり…\n食べたくなっちゃう\nくらい、かわいい☆", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5921, 1, 1, 121001, 4, 3, 0, "くんくん…おおー！\nステーキの香り！\nやばいですね☆", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5922, 1, 1, 121001, 5, 3, 0, "体がぽかぽかしてきて\nわたしの食欲も\n燃えてきましたよー！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5924, 1, 1, 121001, 7, 3, 0, "わたしの食欲に\nついてこられますか？\nまだまだ食べますよ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5925, 1, 1, 121001, 8, 3, 0, "これがキャルちゃんの\n言っていた、キスで\n目覚める王子様の\nおとぎ話ですか〜☆", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5927, 1, 1, 121201, 1, 1, 0, "自分で自分を\n分析するのも、これは\nこれで面白い…♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5928, 1, 1, 121201, 2, 1, 0, "強過ぎる力の扱いには\n注意しないと…\n七冠がいい例だね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5929, 1, 1, 121201, 3, 1, 0, "少年の交友関係は\n計り知れないな…\nアタシも含むのか！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5930, 1, 1, 121201, 4, 1, 0, "原理を徹底解明して\n再発を防ぐぞー！\n…誰か手伝って～！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5931, 1, 1, 121201, 5, 1, 0, "これは祝福にも\n呪いにもなる…\n忘れずにいよう", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5932, 1, 4, 121201, 101, 1, 0, "少年が\n目を覚ましてくれて\n本当によかった…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5933, 1, 8, 121201, 102, 1, 0, "彼以外を選んでいたら\n今回の事態は\n乗り切れなかったな…\nさすがだよ、少年", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5934, 1, 1, 121201, 1, 2, 0, "少年！　調子はどう？\nクレープ食べられる？\nっていうか食べて？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5935, 1, 1, 121201, 2, 2, 0, "元気そうで何よりだ\nまた店を手伝いに\nきてよ。二人もキミに\n会いたがってたしさ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5936, 1, 1, 121201, 3, 2, 0, "キミの診察もあるし\n今はなるべくそばに\nいるよ。迷惑じゃ\nなければだけど", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5937, 1, 1, 121201, 4, 2, 0, "やっぱり少年は\nあちこちで人を\n助けてる時が\n一番輝いてるよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5938, 1, 4, 121201, 101, 2, 0, "アタシは大したこと\nしてないけど\nみんなのことは\n褒めてあげてね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5939, 1, 8, 121201, 102, 2, 0, "みんなを笑顔に\nできるのは\nやっぱり少年だね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5940, 1, 1, 121201, 1, 2, 0, "やあ少年\n朝寝坊はしてない？\nそれじゃ、よい１日を", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5941, 1, 1, 121201, 2, 2, 0, "おや、少年！\nまかないのクレープ\n作ったけど、食べる？", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5942, 1, 1, 121201, 3, 2, 0, "ふわぁ…もう夜か…\n研究してたら\nあっという間だね…\nおやすみ、少年", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5944, 1, 1, 121201, 2, 3, 0, "体がなまってたのか\n前線に出ると疲れる…\nまさか…年のせい！？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5945, 1, 1, 121201, 3, 3, 0, "キミたちも今の\nアタシと同じ感じに\nアレンジしてあげよう", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5946, 1, 1, 121201, 4, 3, 0, "誰が置いたんだろう？\nいい香りだねぇ…\nうん、気に入った♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5947, 1, 1, 121201, 5, 3, 0, "何も考えてない時間も\n実は思考にとって\n大事なんだよね～…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5949, 1, 1, 121201, 7, 3, 0, "食の大切さは\nいつの食事からも\n感じられるね♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5950, 1, 1, 121201, 8, 3, 0, "ふむ…面白い！\n今度全巻\n借りにこようっと", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5952, 1, 1, 116401, 1, 1, 0, "聖学祭か…\n良い記憶はないな\nよし忘れよう", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5953, 1, 1, 116401, 2, 1, 0, "健全なる精神は\n健全なる肉体に宿る…\nはっ、知ったことかよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5954, 1, 1, 116401, 3, 1, 0, "暇だし鏡でも見るか…\nえへへ、ユニだよ♪\nよし子供料金いけるな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5955, 1, 1, 116401, 4, 1, 0, "アオイ君は元気に\nしているだろうか…\n彼女は人一倍ぼくに\n懐いていたからな…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5956, 1, 1, 116401, 5, 1, 0, "そろそろシズル君は\n姉という幻想から\n目覚めただろうか…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5957, 1, 4, 116401, 101, 1, 0, "ぼくは来年も聖学祭に\n行くさ…卒業しても\n君らとは永遠だ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5958, 1, 8, 116401, 102, 1, 0, "最近同志の登校率が\n減少傾向だな…\nまあ仕方ないのだが…\nないのだが…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5959, 1, 1, 116401, 1, 2, 0, "ほわあああ！\nおい急に話しかけるな\n学びがこぼれる", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5960, 1, 1, 116401, 2, 2, 0, "この体操着、周囲から\n大層評判が良いのだよ\nたいそうだけにな\nどやっ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5961, 1, 1, 116401, 3, 2, 0, "何を着ていようと\nぼくはぼくさ\n外見など所詮は\n魂の識別符に過ぎん", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5962, 1, 1, 116401, 4, 2, 0, "救護院のアヤネ君が\n最近ぼくの自称年齢に\n疑念を持ち始めてな…\nあの熊…殺るか…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5963, 1, 4, 116401, 101, 2, 0, "うん？　なんだね？\nなに、用は無い？\n用も無いのに呼ぶとは\nもしや…これが友達！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5964, 1, 8, 116401, 102, 2, 0, "この服が似合ってる？\nな、なんだね突然…\nやめたまえよでゅふふ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5965, 1, 1, 116401, 1, 2, 0, "ふぁ～あ…\n朝も更けてきたな…\nそろそろ寝るか…", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5966, 1, 1, 116401, 2, 2, 0, "ふぁ～あ…もう昼だ…\n人間の起きている\n時間ではないな…\n早く寝なくては…", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5967, 1, 1, 116401, 3, 2, 0, "うーん、よく寝た！\n気持ちのいい夜だな\n今日も一日がんばるぞ", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5969, 1, 1, 116401, 2, 3, 0, "ほわぁ～…\nもしやこれは都で噂の\n人を駄目にするやつ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5970, 1, 1, 116401, 3, 3, 0, "ふむ、愛くるしいな\nだが似合い過ぎる…\nぼくが持つと我ながら\n子供にしか見えん", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5971, 1, 1, 116401, 4, 3, 0, "アロマか、興味深いな\nミツキ印ポーションも\n気化させて鼻腔から\n吸入してみるか…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5972, 1, 1, 116401, 5, 3, 0, "んふ、んふふ…\n火ってイイよね…\nずっと見てられるね…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5974, 1, 1, 116401, 7, 3, 0, "や、やめろ…\nとめどなく固形食物を\n生産するのはやめろ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5975, 1, 1, 116401, 8, 3, 0, "この可愛げのない筆致\nやはり作者は理系か！\nぐああ目が理系になる", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5977, 1, 1, 121101, 1, 1, 0, "…あいつのためなら\n無理くらいいくらでも\nしてやるわよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5978, 1, 1, 121101, 2, 1, 0, "この姿、\n魔力の制御ムズ過ぎ…\nま、あたしは優秀だし\nどうとでもなるけど！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5979, 1, 1, 121101, 3, 1, 0, "あいつのこと、\n全部受け入れるって\n決めたから！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5980, 1, 1, 121101, 4, 1, 0, "今なら誰だろうと\n負ける気がしないわ\nドラゴンでもなんでも\nかかってこいっての！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5981, 1, 1, 121101, 5, 1, 0, "散々助けられた分、\n今度はあたしが\nあいつのこと助けるわ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5982, 1, 4, 121101, 101, 1, 0, "あいつとの\nこれまでの冒険は\nどれも、あたしの\n大切な思い出…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5983, 1, 8, 121101, 102, 1, 0, "もしまたあいつが\n寝たままになったら\nなんだってするわ\nキ…キスでも…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5984, 1, 1, 121101, 1, 2, 0, "なに、お腹空いたの？\nなら、おいしいご飯を\n食べに行きましょ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5985, 1, 1, 121101, 2, 2, 0, "やっぱ、\nあんたのアホ面見ると\n安心するわ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5986, 1, 1, 121101, 3, 2, 0, "【美食殿】はあんたと\nぺコリーヌがいなきゃ\n始まんないっての", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5987, 1, 1, 121101, 4, 2, 0, "ひゃあっ！？\n急に声かけないで！？\n魔力が暴発しても\n知らないわよ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5988, 1, 4, 121101, 101, 2, 0, "…あんたとこうして\nぼーっと過ごす時間、\nまあ…嫌いじゃないわ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5989, 1, 8, 121101, 102, 2, 0, "あんたが信じて\n見守っててくれれば…\nあたし、\nなんだってできるわ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5990, 1, 1, 121101, 1, 2, 0, "おはよ\n早く顔洗いなさいよ、\nこのねぼすけ♪", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5991, 1, 1, 121101, 2, 2, 0, "今日のお昼は\nどこで食べる？\nあんたが\n決めていいわよ", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5992, 1, 1, 121101, 3, 2, 0, "もしあんたがまた\n夢に囚われても\nあたしが助けるわ\n安心して寝なさいよ", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5994, 1, 1, 121101, 2, 3, 0, "気持ちいい～…♪\nあいつもたまには\nこうして休ませなきゃ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5995, 1, 1, 121101, 3, 3, 0, "一目惚れしたかも…！\nこの子、\n部屋に飾りたいわー♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5996, 1, 1, 121101, 4, 3, 0, "へぇ、あいつって\nこういう匂いが\n好きなのかしら…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5997, 1, 1, 121101, 5, 3, 0, "暖かくて最高…♪\n丸くなっちゃいそう…\nうにゃあ…♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (5999, 1, 1, 121101, 7, 3, 0, "イケるわね、これ！\nやっぱり食べるって\n大事よねー♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6000, 1, 1, 121101, 8, 3, 0, "ファーストキスは\nどんな味、ね…\nふ、ふ～ん…？\nなるほど…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6002, 1, 1, 118101, 1, 1, 0, "お友だち…\nできるかしら…？\nこんなわたしでも…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6003, 1, 1, 118101, 2, 1, 0, "あまり、きょろきょろ\nしちゃダメよね…\n変な子だと、\n思われてしまうわ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6004, 1, 1, 118101, 3, 1, 0, "広い場所、\n落ち着かない…\nはぅ…どこにいれば…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6005, 1, 1, 118101, 4, 1, 0, "～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6006, 1, 1, 118101, 5, 1, 0, "【レイジ・レギオン】\n…またいつか、\nみんなと…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6007, 1, 4, 118101, 101, 1, 0, "…一度でいいから、\nわたしもママって…\nあっ、その…\n…な、なんでもないわ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6008, 1, 8, 118101, 102, 1, 0, "ここが、今のわたしに\nとっての帰る場所…\n…そう思っても、\nいいのかしら…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6009, 1, 1, 118101, 1, 2, 0, "ええと…\nお腹、空いてる？\nおねむじゃない？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6010, 1, 1, 118101, 2, 2, 0, "ここにいると\n邪魔かしら…？\nならもっと隅っこに…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6011, 1, 1, 118101, 3, 2, 0, "お喋り…？\nわたしでいいの…？\nうれしい…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6012, 1, 1, 118101, 4, 2, 0, "あなたの好きな歌…\n教えて…？\n練習、するから…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6013, 1, 4, 118101, 101, 2, 0, "ここは賑やかだけど…\nうるさいとは感じない\nむしろ、心地いい…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6014, 1, 8, 118101, 102, 2, 0, "安心、して…\nわたしの歌で、\nあなたを守るから…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6015, 1, 1, 118101, 1, 2, 0, "ちょっとだけ…\n朝は、苦手…\n…ふぁ…", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6016, 1, 1, 118101, 2, 2, 0, "お昼は、食べた？\n良かったら、一緒に…\n少し…焦げてるけど…", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6017, 1, 1, 118101, 3, 2, 0, "眠れないのかしら…\nじゃ、じゃあ…\n子守歌…聴く？", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6019, 1, 1, 118101, 2, 3, 0, "こんな特等席…\n恐縮、です…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6020, 1, 1, 118101, 3, 3, 0, "い、今のうちに、\nこの子でお世話の\n練習を…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6021, 1, 1, 118101, 4, 3, 0, "いい香り…\n…つらい気持ちも\n癒してくれる", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6022, 1, 1, 118101, 5, 3, 0, "あったかい…\nいつまでも、\nこうしていられそう…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6024, 1, 1, 118101, 7, 3, 0, "おいしく出来てる…\nすごいわ…\n見習わなくっちゃ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6025, 1, 1, 118101, 8, 3, 0, "『赤ちゃんとママの\nスキンシップ入門』…\n…べ、勉強になるわ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6027, 1, 1, 121301, 1, 1, 0, "きれいなお部屋…！\n立派な舞台の\nセットみたい…♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6028, 1, 1, 121301, 2, 1, 0, "お芝居では私が先輩\nなんだから、しっかり\n私がミサキちゃんを\nエスコートしなきゃ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6029, 1, 1, 121301, 3, 1, 0, "～～♪\n歌うお芝居も多いから\nもっと上手く\nなりたいな…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6030, 1, 1, 121301, 4, 1, 0, "ここにいる人たちと\n仲良くなりたいな…\nそしたらきっと演技の\n幅も広げられるよね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6031, 1, 1, 121301, 5, 1, 0, "機会があったら\nアヤネちゃんとも\nお芝居してみたいな♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6032, 1, 4, 121301, 101, 1, 0, "お兄ちゃんに褒めて\nもらえるように\nお芝居の練習\nいっぱいしなきゃ…♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6033, 1, 8, 121301, 102, 1, 0, "あう…お兄ちゃんを\n意識したら、台詞が\n飛んじゃったよぅ…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6034, 1, 1, 121301, 1, 2, 0, "あっ、お兄ちゃん…♪\nお芝居の練習を\n見てくれませんか…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6035, 1, 1, 121301, 2, 2, 0, "いまの発声練習\nどうでしたか？\nひゃん…！？\n頭は撫でないでぇ～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6036, 1, 1, 121301, 3, 2, 0, "ミサキちゃんも\nお芝居、とっても\n上達してるんですよ\n私も負けられません♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6037, 1, 1, 121301, 4, 2, 0, "また物語を聞かせて\nほしい、ですか…？\n私なんかでよければ\nもちろんよろこんで♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6038, 1, 4, 121301, 101, 2, 0, "新しいお話を\n考えてみました\n聞いてくれますか…？\nよかった…♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6039, 1, 8, 121301, 102, 2, 0, "いつか…舞台上じゃ\nなくても、照れずに\nお兄ちゃんに気持ちを\n伝えてみせますね…♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6040, 1, 1, 121301, 1, 2, 0, "おはようございます♪\nマヒルさんにもらった\nミルク、一緒に\n飲みませんか？", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6041, 1, 1, 121301, 2, 2, 0, "牧場のみなさんに\nドングリパンを\nいただきました～\nおひとつどうぞ♪", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6042, 1, 1, 121301, 3, 2, 0, "寝る前にまたココアを\n飲みながら、お話を\n聞いてもらえますか？\nえへへ、うれしいな♪", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6044, 1, 1, 121301, 2, 3, 0, "お芝居の練習の\nあとは、ここで\nお兄ちゃんと\nゆっくりしたいな♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6045, 1, 1, 121301, 3, 3, 0, "今日も演技の\n練習に付き合ってね♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6046, 1, 1, 121301, 4, 3, 0, "素敵な香り…\nこのイメージも\nお芝居に\n活かせないかな…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6047, 1, 1, 121301, 5, 3, 0, "私もお芝居で\nみんなの心を\nぽかぽか温められる\nようになりたいな…♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6049, 1, 1, 121301, 7, 3, 0, "舞台に立つんだから\n食べ過ぎないように\n気をつけないと。\nでも…おいしいなぁ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6050, 1, 1, 121301, 8, 3, 0, "このお話、面白い…\n脚本を書く\n参考にもなりそう…♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6052, 1, 12, 107501, 103, 1, 0, "えへへ…♪\n二人で見た夕日…\nキレイでしたね…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6053, 1, 12, 107501, 103, 2, 0, "これから泳ぎの\n練習ですか？\n付き合いますよ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6054, 1, 12, 107601, 103, 1, 0, "主さまを導くことで\nわたくしも未知なる\n領域へと辿り着けます", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6055, 1, 12, 107601, 103, 2, 0, "主さまとなら\n海よりも深く\n空よりも高い場所へも\n目指せそうです", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6056, 1, 12, 107801, 103, 1, 0, "あいつと二人で\n夏の遠い海に…って\nあたしってば\nなに考えてるのよ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6057, 1, 12, 107801, 103, 2, 0, "あたしが楽しく\n過ごせるのはあんたが\nいてくれたからよ\nか、感謝してるわ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6058, 1, 1, 121401, 1, 1, 0, "はぁ…アタシってば\nこんなにかわいくって\nキレイなんて罪よね～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6059, 1, 1, 121401, 2, 1, 0, "舞台の衣装を\nクリーニング\nしとかなくちゃ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6060, 1, 1, 121401, 3, 1, 0, "舞台映えするメイク…\nスズナなら\n知ってるかしら？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6061, 1, 1, 121401, 4, 1, 0, "物産展の会計係と\n山盛りの算数の宿題…\nどっちが簡単かしら…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6062, 1, 1, 121401, 5, 1, 0, "牧場と温泉の仕事に\nお芝居の練習に…\nちょ、ちょっと休憩～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6063, 1, 4, 121401, 101, 1, 0, "お客さんの視線も\nうれしいけど…\nあいつも見てて\nくれてるかしら？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6064, 1, 8, 121401, 102, 1, 0, "もっともっと\nセクシーになって\nあいつをビックリ\nさせてやるわ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6065, 1, 1, 121401, 1, 2, 0, "アンタも休憩？\nしっかり働くために\n休むのも大事よね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6066, 1, 1, 121401, 2, 2, 0, "ふふん♪　アタシも\n料理ができるように\nなったんだから！\n簡単なのだけだけど…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6067, 1, 1, 121401, 3, 2, 0, "温泉ってお肌に\nいいのよね？\nもっとキレイに\nなったらどうしよう〜", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6068, 1, 1, 121401, 4, 2, 0, "物産展の荷物は\nこれでよし…っと\nそれじゃアンタも\n運ぶの手伝ってよね♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6069, 1, 4, 121401, 101, 2, 0, "いつもと違うアタシに\n見とれちゃった？\n仕方ないわね～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6070, 1, 8, 121401, 102, 2, 0, "お花見って風流で\n大人って感じ♪\n…でもお団子も\n忘れないでよね！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6071, 1, 1, 121401, 1, 2, 0, "みんなに朝ごはんを\n作ってみたら\n喜んでくれるかしら？", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6072, 1, 1, 121401, 2, 2, 0, "お客さんがいっぱい\n来る時間帯ね！\n元気出していくわ！", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6073, 1, 1, 121401, 3, 2, 0, "そろそろ寝ないと…\n睡眠不足はお肌の\n大敵なんだから", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6075, 1, 1, 121401, 2, 3, 0, "ふかふかしてて\n気持ちいい～♪\n疲れが癒されるぅ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6076, 1, 1, 121401, 3, 3, 0, "この子もアタシと\nおそろいの衣装に\nしてあげようかしら♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6077, 1, 1, 121401, 4, 3, 0, "素敵な衣装で\nいい香りを\n身にまとう…ふふ、\n大人のたしなみよね♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6078, 1, 1, 121401, 5, 3, 0, "あったか～い♪\nここでお昼寝したら\n気持ちよさそう…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6080, 1, 1, 121401, 7, 3, 0, "お仕事がんばると\nいつもよりお腹\n空いちゃう～！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6081, 1, 1, 121401, 8, 3, 0, "最新のトレンド\n載ってないかしら？\n物産展の売り物に\n取り入れなくちゃ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6083, 1, 1, 121501, 1, 1, 0, "怪盗のこと…\nお父さんお母さんに\nもし知られたら\nなんて説明しよう…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6084, 1, 1, 121501, 2, 1, 0, "たとえ怪盗でも、人の\n家にお邪魔するときは\n身だしなみをしっかり\nしないと…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6085, 1, 1, 121501, 3, 1, 0, "鍵開けって難しい…\nうーん、もっと\n練習しておかなくちゃ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6086, 1, 1, 121501, 4, 1, 0, "姿が見えない\n怪盗の噂…？\nわ、私のことじゃ\nありませんように…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6087, 1, 1, 121501, 5, 1, 0, "予告状って…\n切手はどこに貼れば\nいいんだろう…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6088, 1, 4, 121501, 101, 1, 0, "怪盗活動にも\n慣れてきたけど…\nやっぱり先輩が\nいないと心細いなぁ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6089, 1, 8, 121501, 102, 1, 0, "せ、先輩の心も\nいつか盗めるかな…？\n…わわわ、私ってば\nなに言ってるんだろ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6090, 1, 1, 121501, 1, 2, 0, "怪盗が大活躍って\n新聞で読んだ…？\nだっ、誰のこと\nでしょうね…あはは", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6091, 1, 1, 121501, 2, 2, 0, "尾行に潜入…\n存在感がないのって\n意外と役立つことも\nあるんですね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6092, 1, 1, 121501, 3, 2, 0, "せせせ、先輩っ！\n帽子をぎゅーって\nかぶったら、抜けなく\nなっちゃいました…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6093, 1, 1, 121501, 4, 2, 0, "ぬ、盗み聞きなんて\nしてませんよ？\nみんなが私に気づかず\n話し始めるだけで…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6094, 1, 4, 121501, 101, 2, 0, "秘密道具が見たい？\nそ、それは内緒です！\n秘密道具ですから…\nで、でも先輩になら…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6095, 1, 8, 121501, 102, 2, 0, "えへへ\n見つかっちゃいました\n先輩はいつも私を\n見つけてくれますね…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6096, 1, 1, 121501, 1, 2, 0, "おはようございまふ…\n秘密道具のお手入れで\n夜更かししちゃって…\nちょっと眠たいです～", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6097, 1, 1, 121501, 2, 2, 0, "怪盗が昼に活動する…\n案外そのほうが\n意外性が\nあるのかも…？", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6098, 1, 1, 121501, 3, 2, 0, "外が暗くなりましたね\n…そろそろ怪盗の活動\n始めましょうか？", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6100, 1, 1, 121501, 2, 3, 0, "ここでくつろぐと\n疲れが取れます～\n…今日も怪盗\nがんばろっと", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6101, 1, 1, 121501, 3, 3, 0, "わぁ、かわいい…！\n心を盗まれるって\nこういうことかな…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6102, 1, 1, 121501, 4, 3, 0, "素敵な香り…でも\n服に香りがついたら\n隠れてるときに\n見つかっちゃうかな…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6103, 1, 1, 121501, 5, 3, 0, "ぽかぽかで\nいい気持ち…\n夜道を歩く前に\n温まっておこうっと…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6105, 1, 1, 121501, 7, 3, 0, "怪盗活動に備えて\n今のうちにしっかり\n食べておかなきゃ…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6106, 1, 1, 121501, 8, 3, 0, "女の子は秘密が\n多いほうが魅力的…？\nそ、そうなんだ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6108, 1, 12, 104501, 103, 1, 0, "ドＳさんはいつも\nクウカの妄想の中に…\nぐふふ、じゅるり～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6109, 1, 12, 104501, 103, 2, 0, "やさしい触り方も\n嫌いじゃありません…\nは…恥ずかしいですが", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6110, 1, 1, 121601, 1, 1, 0, "この大怪盗さまに\n盗めないものなんて\nないです！　たぶん！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6111, 1, 1, 121601, 2, 1, 0, "あたしにふさわしい\nビッグなお宝は\nどこにあるですかね～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6112, 1, 1, 121601, 3, 1, 0, "そろーりそろーり…\n気配を消せばお菓子も\n食べ放題…にやり", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6113, 1, 1, 121601, 4, 1, 0, "またサボってたのが\nバレたです…\nボスの地獄耳～！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6114, 1, 1, 121601, 5, 1, 0, "ギャングで大怪盗…！\nこの肩書の多さ！\nまさに有能の証です！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6115, 1, 4, 121601, 101, 1, 0, "お兄さんだったら…\nお宝を山分けして\nあげてもいいですよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6116, 1, 8, 121601, 102, 1, 0, "お兄さんはアレですよ\n大怪盗イノリさまの\n相棒みたいなものです", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6117, 1, 1, 121601, 1, 2, 0, "何事も用心深さが\n大切ですよ。ビ…\nビビりじゃないです！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6118, 1, 1, 121601, 2, 2, 0, "ぬ、盗み聞き？\nしてないですよっ？\nぴゅーぴゅーぴゅー", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6119, 1, 1, 121601, 3, 2, 0, "情報が欲しければ\nお菓子と交換ですよ\nさぁ、交換交換っ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6120, 1, 1, 121601, 4, 2, 0, "しーっ！\n今、尾行中なんです\n話はあとで…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6121, 1, 4, 121601, 101, 2, 0, "お兄さんと話してると\n時間が速いような…？\nこの時間泥棒！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6122, 1, 8, 121601, 102, 2, 0, "ちょうどよかった！\nいい話があるんです\n一口乗りませんか？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6123, 1, 1, 121601, 1, 2, 0, "おてんとうさまが\n出てやがるですね…\n悪事がやりにくいです", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6124, 1, 1, 121601, 2, 2, 0, "もうこんな時間ですか\nご飯にお昼寝…\nいやー忙しいです♪", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6125, 1, 1, 121601, 3, 2, 0, "ふっふっふ！　夜こそ\n大怪盗の時間です！\n……ふぁあ", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6127, 1, 1, 121601, 2, 3, 0, "うーん…\n警備の目がないと\n気楽でいいですねぇ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6128, 1, 1, 121601, 3, 3, 0, "くう…！\nこのかわいさは\n盗めないです…♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6129, 1, 1, 121601, 4, 3, 0, "犯行現場に残ったのは\n香水の匂いだけ…\nふふ、おしゃれ大怪盗", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6130, 1, 1, 121601, 5, 3, 0, "意外とこういう場所に\n隠し扉があったり…\nしないです？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6132, 1, 1, 121601, 7, 3, 0, "もぐもぐ…\nこの美味しさ…\nとんでもない罠ですね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6133, 1, 1, 121601, 8, 3, 0, "むむむ…ちっとも\n分からないです…\nほとんど暗号ですよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6135, 1, 1, 121701, 1, 1, 0, "あら、サレンさんは\nどこに行かれた\nのかしら？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6136, 1, 1, 121701, 2, 1, 0, "ふんふんふーん♪\nなんだか華やいだ\n気分ですわ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6137, 1, 1, 121701, 3, 1, 0, "さて…次の事業は\nどうしましょう\n何か面白いことは…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6138, 1, 1, 121701, 4, 1, 0, "おーっほっほっほ！\nあら…楽しくて、つい\n笑ってしまいましたわ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6139, 1, 1, 121701, 5, 1, 0, "センスがあって\nいいお部屋ですわね\n私も見習いたいですわ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6140, 1, 4, 121701, 101, 1, 0, "サレンさんとあのお方\n仲がいいですわね\nも、もちろん仲がいい\nのはよいことですわ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6141, 1, 8, 121701, 102, 1, 0, "どんなときでも\n正々堂々！\nそれが私の\n誇りですわ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6142, 1, 1, 121701, 1, 2, 0, "ふふ、ごきげんよう\n私、絶好調でしてよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6143, 1, 1, 121701, 2, 2, 0, "どうかされましたか？\n何かお困りごと\nでしたら\n是非力になりますわよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6144, 1, 1, 121701, 3, 2, 0, "たまにはこうして\nゆっくりと\nお話しするのも\nいいものですわね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6145, 1, 1, 121701, 4, 2, 0, "次の事業に関して\n考え事をしてましたの\nよければ相談に乗って\nくださるかしら？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6146, 1, 4, 121701, 101, 2, 0, "よろしければ\nお話しませんこと？\nサレンさんのお話を\n聞かせてほしいですわ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6147, 1, 8, 121701, 102, 2, 0, "ふふっ…\n先ほどサレンさんと\nあなたさまのお話を\nしていたところですわ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6148, 1, 1, 121701, 1, 2, 0, "さあ！\n今日も一日\n全力で参りますわ！", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6149, 1, 1, 121701, 2, 2, 0, "お昼はどうしましょう\nサレンさんをお誘い\nしてみようかしら", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6150, 1, 1, 121701, 3, 2, 0, "もう少しだけ…\n明日の準備をして\nおかないと…", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6152, 1, 1, 121701, 2, 3, 0, "はぁ～…いいですわね\nはっ、私としたことが\nはしたないですわね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6153, 1, 1, 121701, 3, 3, 0, "かわいらしいですわ\nいくつになっても\nぎゅっと\nしたくなりますわね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6154, 1, 1, 121701, 4, 3, 0, "落ち着きますわね\nサレンさんはどんな\n香りが好きなのかしら", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6155, 1, 1, 121701, 5, 3, 0, "ふふっ、燃え盛る\n情熱の炎…\nいいですわね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6157, 1, 1, 121701, 7, 3, 0, "このおいしさで\n元手がゼロ…\nすばらしい\n商品ですわ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6158, 1, 1, 121701, 8, 3, 0, "これは…ふむふむ\n新しい事業のヒントに\nなりそうですわ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6161, 1, 1, 121801, 1, 1, 0, "さて、と…\n今日は何をしなくちゃ\nいけないんだっけ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6162, 1, 1, 121801, 2, 1, 0, "やることは山積み\nだけど、手を動かす\nのは嫌いじゃないわ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6163, 1, 1, 121801, 3, 1, 0, "あら？　アキノさんの\n声が聞こえたような\n気がしたんだけど…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6164, 1, 1, 121801, 4, 1, 0, "アキノさんといると\nついこっちまで笑顔に\nなっちゃうのよね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6165, 1, 1, 121801, 5, 1, 0, "弱音なんか\nはいてられないわ\nアキノさんに\n笑われちゃうもの", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6166, 1, 4, 121801, 101, 1, 0, "気が付けばアキノさん\nのペースだけど…\n不思議と嫌では\nないのよね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6167, 1, 8, 121801, 102, 1, 0, "あたしを好敵手って\n言ってくれる\nアキノさんのためにも\nしっかりしなくちゃ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6168, 1, 1, 121801, 1, 2, 0, "どうしたの？\nアキノさんなら\nさっきそこに\nいなかった？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6169, 1, 1, 121801, 2, 2, 0, "あたしなら大丈夫\n救護院だって\nみんなが助けて\nくれるからね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6170, 1, 1, 121801, 3, 2, 0, "さっきアキノさんが\nあんたを探してたわよ\nなにかしたの？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6171, 1, 1, 121801, 4, 2, 0, "こう見えてヒマじゃ\nないんだけど…\n少しくらいなら\n付き合ってあげるわ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6172, 1, 4, 121801, 101, 2, 0, "ねえ、あんたと\nアキノさんって…\nううん、やっぱり\nなんでもないわ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6173, 1, 8, 121801, 102, 2, 0, "アキノさんも誘って\nお茶でもしない？\n話したいことが\nたくさんあるの♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6174, 1, 1, 121801, 1, 2, 0, "早起きは三文の得よ\nいい一日を迎えるため\n目を覚まさなきゃ", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6175, 1, 1, 121801, 2, 2, 0, "今日のお昼は…\nアキノさんと同席する\n予定だったわね\nあんたもどう？", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6176, 1, 1, 121801, 3, 2, 0, "ふぁ…っと、だめね\nたるんだところを\nアキノさんに\n見せるわけには", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6178, 1, 1, 121801, 2, 3, 0, "ん～…\n落ち着くわね\nこのまま\n寝ちゃいそう…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6179, 1, 1, 121801, 3, 3, 0, "救護院の皆も\nぬいぐるみが\n大好きなのよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6180, 1, 1, 121801, 4, 3, 0, "いい香りね…\nこれはアキノさんの\n趣味かしら？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6181, 1, 1, 121801, 5, 3, 0, "こんなに激しく\n燃えているのに\nあったかくて落ち着く\nまるでアキノさんね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6183, 1, 1, 121801, 7, 3, 0, "どんどん出てくる…\nこれ、救護院にも\nほしいわね…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6184, 1, 1, 121801, 8, 3, 0, "ふむふむ…\n前にアキノさんが\n言ってたのは\nこのことかしら？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6187, 1, 1, 122001, 1, 1, 0, "この衣装、私には\nちょっとかわい過ぎて\n恥ずかしいな…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6188, 1, 1, 122001, 2, 1, 0, "一部の人からは\n海賊より海賊らしいと\n評判の父です", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6189, 1, 1, 122001, 3, 1, 0, "潮風で錆びないよう\n武器の手入れを\nしておきましょうか", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6190, 1, 1, 122001, 4, 1, 0, "宝探しのお手伝いなら\n力になれると思います\n父のやる気次第ですが", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6191, 1, 1, 122001, 5, 1, 0, "除霊は気を遣います\n一歩間違えると父まで\n成仏させてしまうので", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6192, 1, 4, 122001, 101, 1, 0, "あの人と見た\n海の景色を…ずっと\n忘れずにいたいな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6193, 1, 8, 122001, 102, 1, 0, "幽霊になっても\n一目会えるなら…\n私はきっとあの人に…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6194, 1, 1, 122001, 1, 2, 0, "ボートを漕ぐの、少し\n上手になったんですよ\n今度お見せしますね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6195, 1, 1, 122001, 2, 2, 0, "航海に向けた\n買い出しが必要ですね\n予定は空いていますか", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6196, 1, 1, 122001, 3, 2, 0, "呪われた財宝の\n解呪依頼が多くて…\nふぅ…えっと次は…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6197, 1, 1, 122001, 4, 2, 0, "夜の航海では\n父が明かり代わりに\nなってくれるんです", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6198, 1, 4, 122001, 101, 2, 0, "船の上では食材が\n限られるので料理の\nいい練習になります", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6199, 1, 8, 122001, 102, 2, 0, "この先もずっと\nあなたと一緒に\nたくさんの思い出を\n作っていきたいです", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6200, 1, 1, 122001, 1, 2, 0, "あの人の寝顔…\nもう少しだけ\n眺めていたいな…", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6201, 1, 1, 122001, 2, 2, 0, "船の上では\n規則正しい生活が\n重要ですから", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6202, 1, 1, 122001, 3, 2, 0, "夜の海は幽霊が\n見分けやすいんです\n青白く光るので", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6204, 1, 1, 122001, 2, 3, 0, "海に揺られるような\n心地よさですね\n気が休まります…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6205, 1, 1, 122001, 3, 3, 0, "あなたも一緒に\n海を見に行きますか？\n…なんて。ふふっ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6206, 1, 1, 122001, 4, 3, 0, "爽やかな香りですね…\n一面の青い海が\n目に浮かぶようです", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6207, 1, 1, 122001, 5, 3, 0, "温かい…\n潮風で冷えた体に\n沁みますね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6209, 1, 1, 122001, 7, 3, 0, "あ、おいしい…\n日持ちするなら船旅に\n持っていこうかな…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6210, 1, 1, 122001, 8, 3, 0, "アンナさんに教えて\nもらった小説は…\nあ、これですね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6212, 1, 12, 102601, 103, 1, 0, "なにもしないを\nしてるんだよ\nあ～いそがしいそがし", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6213, 1, 12, 102601, 103, 2, 0, "はぁ～あんたといると\nどこまでも気持ちよく\nダラダラできるね～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6214, 1, 1, 121901, 1, 1, 0, "暗黒騎士にして、\n伝説の海賊でもある\n私に不可能はないっ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6215, 1, 1, 121901, 2, 1, 0, "海賊団の船員、\n随時募集中です…と\nこれで新たな盟友が\n増えるはずだ…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6216, 1, 1, 121901, 3, 1, 0, "肩に乗せるペットが\nほしいな。今のうちに\nペット用品を買いに\n行かなくては…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6217, 1, 1, 121901, 4, 1, 0, "財宝の地図はどこで\n売っているのだろう…\n古本屋？　古物商？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6218, 1, 1, 121901, 5, 1, 0, "この海賊衣装に\n身を包んでいると\n前世の新たな記憶が\nよみがえってくるな！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6219, 1, 4, 121901, 101, 1, 0, "いずれはシグルドと\n海原への冒険に\n繰り出したいな…\nきっと楽しいぞ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6220, 1, 8, 121901, 102, 1, 0, "待ちわびたぞ盟友よ！\n私と貴様あっての\n海賊団…だからその…\n今日はそばにいてくれ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6221, 1, 1, 121901, 1, 2, 0, "シグルド！　港町で\nお魚のおすそ分けを\nもらったんだ！\n一緒に食べよう！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6222, 1, 1, 121901, 2, 2, 0, "シグルドもこのコート\n羽織ってみないか？\nふふっ、きっとすごく\n似合うと思うぞ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6223, 1, 1, 121901, 3, 2, 0, "海賊船の新たな\n秘密兵器を作ったぞ！\nシグルド、これから\n見に来ないか！？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6224, 1, 1, 121901, 4, 2, 0, "私は伝説の海賊だから\n泳ぐのだって得意だ！\nふ、深いところは\n少し怖いが…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6225, 1, 4, 121901, 101, 2, 0, "ふふ、いつ無人島に\n漂着してもいいように\n非常食は準備済みだ\nシグルドの分もある！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6226, 1, 8, 121901, 102, 2, 0, "冥風戦記の新章が\n完成したぞ…！\nシグルド、まずは\n貴様に読んでほしい！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6227, 1, 1, 121901, 1, 2, 0, "ふっ、天気明朗\n絶好の航海日和だ！\n早速海に繰り出そう！", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6228, 1, 1, 121901, 2, 2, 0, "昼食用に魚を\n釣っておいたぞ！\nふふ、いかにも\n海賊っぽいだろう？", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6229, 1, 1, 121901, 3, 2, 0, "夜の海は真っ黒で\n少しだけ怖いな…\nシグルド、私の\nそばを離れるなよ…！", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6231, 1, 1, 121901, 2, 3, 0, "大海賊がくつろぐのに\n相応しい逸品だ…！\nえっと…これ、どこの\nお店で買えますか？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6232, 1, 1, 121901, 3, 3, 0, "わわ、かわいい…！\n船室のベッドに\n置いておきたいな…\n連れてってはダメか？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6233, 1, 1, 121901, 4, 3, 0, "おぉ、この香りは…！\n集中力が上がりそうだ\n海賊船の船長室に\nぜひ置きたいな！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6234, 1, 1, 121901, 5, 3, 0, "これが船乗りの\n伝承に聞く、海原を\nさまよう神秘の火か！\n…暖まるな！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6236, 1, 1, 121901, 7, 3, 0, "腹が減っては\n海賊はできぬ！\n…小説も書けぬ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6237, 1, 1, 121901, 8, 3, 0, "女海賊と騎士はその後\n恋に落ち…？　恋愛\nモノではないかっ！？\nき、興味深くはあるが", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6239, 1, 1, 122101, 1, 1, 0, "いつもの山籠もりが\nブームになるなんて…\n人生、なにが起きるか\n分からないなぁ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6240, 1, 1, 122101, 2, 1, 0, "キャンプ道具…\nいえ、キャンプギア！\nカッコいい響き…♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6241, 1, 1, 122101, 3, 1, 0, "レッツ、ＢＢキャン！\nこのフレーズ\n流行るかも…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6242, 1, 1, 122101, 4, 1, 0, "キャンプ仲間…\n募集してみようかな？\n友達も兼任で…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6243, 1, 1, 122101, 5, 1, 0, "ユカリさんと\nジュンさん…元気かな\nまた一緒に\nキャンプしたいなぁ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6244, 1, 4, 122101, 101, 1, 0, "私の取材が載った本…\n【フォレスティエ】に\n送らなくちゃ…♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6245, 1, 8, 122101, 102, 1, 0, "もし、あの人が\n落ち込むことが\nあったら…その時は\n私が力になりたいな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6246, 1, 1, 122101, 1, 2, 0, "ブッシュクラフトは\nマイフレンドくんの\nカスタムにも\n活かせる技術です！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6247, 1, 1, 122101, 2, 2, 0, "会話に花が咲く…\nって言いますよね\n私の場合、会話相手が\nお花そのものですけど", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6248, 1, 1, 122101, 3, 2, 0, "この服、実は…\n昔ミサト先生に\nこしらえてもらった\n物なんです！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6249, 1, 1, 122101, 4, 2, 0, "昔はやまびこで会話の\n練習をしてたんです\nほとんどオウム返しで\n気まずかったなぁ～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6250, 1, 4, 122101, 101, 2, 0, "どうもマイスターです\n…あ、すみません\nみなさんの期待に\n応えようと練習中で…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6251, 1, 8, 122101, 102, 2, 0, "たとえぼっちを\n卒業しても…\n私は一生ＢＢ団です！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6252, 1, 1, 122101, 1, 2, 0, "小鳥さんのさえずりで\n目が覚める…\n心地いいものですね", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6253, 1, 1, 122101, 2, 2, 0, "キャンプといえば\nおいしいご飯です！\n腕を振るいますよ！", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6254, 1, 1, 122101, 3, 2, 0, "すっかり夜ですね…\nおやすみの前に\n星でも見ませんか？", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6256, 1, 1, 122101, 2, 3, 0, "都会でくつろぎ…\n自然の中でくつろぐ…\nくつろぎ無限ループ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6257, 1, 1, 122101, 3, 3, 0, "しっかり観察すれば\nブッシュクラフトで\n再現できるかも…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6258, 1, 1, 122101, 4, 3, 0, "はぁ～…やっぱり\n木の香りを嗅ぐと\n肩の力が抜けますねぇ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6259, 1, 1, 122101, 5, 3, 0, "ああ…あったまります\nなんだか心に\nゆとりがでますね…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6261, 1, 1, 122101, 7, 3, 0, "このおいしさ…！\n今度はキャンプに\n持っていこうかな…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6262, 1, 1, 122101, 8, 3, 0, "ふむふむ…\nいろんな知識があると\nキャンプはもっと\n楽しくなりますよね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6264, 1, 1, 122201, 1, 1, 0, "部屋がキャンプ用品で\n埋まっていく…\nキャンプ…なんて\n恐ろしい趣味なの…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6265, 1, 1, 122201, 2, 1, 0, "仕事でも\nアウトドアでも\n頼れるお姉さんに\nなれたらいいなぁ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6266, 1, 1, 122201, 3, 1, 0, "この服、かわいい上に\n本当に快適ね～♪\nおしゃれにキャンプを\n楽しめて最高だわ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6267, 1, 1, 122201, 4, 1, 0, "ん〜次はどんな\nキャンプ飯を\n作ろうかしら？\n彼の好物は、っと…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6268, 1, 1, 122201, 5, 1, 0, "早く週末が\n来ないかしら…\n仕事を忘れてパーっと\nキャンプ飲みを…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6269, 1, 4, 122201, 101, 1, 0, "新しいキャンプ飯の\nレシピを覚えたわ！\n彼も喜んで\nくれるはず…よね♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6270, 1, 8, 122201, 102, 1, 0, "彼の分の食器も\nこっそり買い揃えちゃ\nおうかな…そ、それは\nさすがにまだ早いか…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6271, 1, 1, 122201, 1, 2, 0, "今度、財団主催で\n大規模なキャンプを\nやってみるのも\nいいかもしれないわね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6272, 1, 1, 122201, 2, 2, 0, "ソロキャンプも\n悪くはないけど\n私はやっぱりキミと…\nな、なんでもない！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6273, 1, 1, 122201, 3, 2, 0, "キャンプのことなら\n私に聞いて。なんでも\n教えてあげるから！\n…調べた上でね！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6274, 1, 1, 122201, 4, 2, 0, "キャンプ場で飲む\n最高の一杯は\n他じゃ味わえないわ！\nさあ、乾杯っ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6275, 1, 4, 122201, 101, 2, 0, "うん、決めた！　次は\n禁しゅわキャンプ！\nだってキミとゆっくり\n話したいんだもん♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6276, 1, 8, 122201, 102, 2, 0, "キミのおかげで\n本当の最高の一杯が\n味わえたわ…\nありがとう♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6277, 1, 1, 122201, 1, 2, 0, "んー、気持ちいい！\nキャンプが趣味に\nなってから、寝覚めが\nよくなったわ♪", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6278, 1, 1, 122201, 2, 2, 0, "お昼は私がごちそう\nするわ。キャンプ飯で\n鍛えた料理の腕前を\n披露しちゃうわよ～！", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6279, 1, 1, 122201, 3, 2, 0, "そろそろ寝ましょ？\n明日も朝日を眺める\nためにね。もちろん\nコーヒー付きで♪", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6281, 1, 1, 122201, 2, 3, 0, "くつろぐのに\n持ってこいね～\nテントの中に置いても\nよさそうだわ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6282, 1, 1, 122201, 3, 3, 0, "ふふ、かわいいわね～\n…今度キャンプ場に\n連れていこうかしら？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6283, 1, 1, 122201, 4, 3, 0, "うん、いい匂い…♪\nキャンプ場を思い出す\n自然の香りだわ〜", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6284, 1, 1, 122201, 5, 3, 0, "はぁ、温まるぅ～\n冬のキャンプで\n使ってみたいわね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6286, 1, 1, 122201, 7, 3, 0, "おいしい～♪\nキャンプ飲みの\nおつまみに\nぴったりね！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6287, 1, 1, 122201, 8, 3, 0, "へえ…こんなキャンプ\n飯もあるんだ？\n彼、好きだったり\nするかな…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6289, 1, 12, 100701, 103, 1, 0, "一生ついててやるの\n幽霊の一生は\nなっが～～～いの！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6290, 1, 12, 100701, 103, 2, 0, "む～…あんまり触ると\nすり抜けてやるの！\nウソなの。むふふ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6291, 1, 1, 122301, 1, 1, 0, "んー…\n今日はベスちゃんと\nなにして遊ぼーかなー", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6292, 1, 1, 122301, 2, 1, 0, "ヴァンピィちゃんも\nけんぞくの友だちと\n仲良くなりたいな♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6293, 1, 1, 122301, 3, 1, 0, "けんぞくのお部屋は\nヴァンピィちゃんが\n守ってあげなくちゃ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6294, 1, 1, 122301, 4, 1, 0, "お掃除するなら\nヴァンピィちゃんも\n手伝ってあげよー♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6295, 1, 1, 122301, 5, 1, 0, "ふーん、ふーん♪\nけんぞくってこーゆー\nお部屋が好きなんだー", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6296, 1, 4, 122301, 101, 1, 0, "ひまだなー\nけんぞくをさそって\nお散歩いこっかな♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6297, 1, 8, 122301, 102, 1, 0, "今日のスープに\nけんぞくの大好物を\n入れてあげよっと♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6298, 1, 1, 122301, 1, 2, 0, "けんぞくー！\nヴァンピィちゃんと\nお出かけしよ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6299, 1, 1, 122301, 2, 2, 0, "よしよし！\n今日のけんぞくも\nいい子だね♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6300, 1, 1, 122301, 3, 2, 0, "んんー…\nやっぱりけんぞくと\nベスちゃんは似てる？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6301, 1, 1, 122301, 4, 2, 0, "いーい？\nヴァンピィちゃんの\nいうこと聞くのよ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6302, 1, 4, 122301, 101, 2, 0, "ヴァンピィちゃんの\nけんぞくだもん！\n大切にするからね！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6303, 1, 8, 122301, 102, 2, 0, "えへへっ！\nやっぱりけんぞくって\nやさしー匂いがする♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6304, 1, 1, 122301, 1, 2, 0, "おはよー…\nヴァンピィちゃんは\nもうちょっと眠るねー", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6305, 1, 1, 122301, 2, 2, 0, "ヴァンピィちゃん\nひなたぼっこ\nだーいすき！", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6306, 1, 1, 122301, 3, 2, 0, "んー！　夜だ♪\nヴァンピィちゃん\n絶好調だよ！", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6308, 1, 1, 122301, 2, 3, 0, "わー！\nふかふかだ！\nきもちぃー♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6309, 1, 1, 122301, 3, 3, 0, "かわいー♪\nベスちゃんに負けない\n抱き心地！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6310, 1, 1, 122301, 4, 3, 0, "この香り…なんだろ？\nヴァンピィちゃん\n気に入っちゃった♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6311, 1, 1, 122301, 5, 3, 0, "あったかいねー…\nヴァンピィちゃん\nずっとここにいるー…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6313, 1, 1, 122301, 7, 3, 0, "はむはむ…\nんー！\nおいしー♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6314, 1, 1, 122301, 8, 3, 0, "ヴァンピィちゃん\nこの本\n好きなんだよねー♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6316, 1, 1, 122501, 1, 1, 0, "夏といえば\nかき氷だよね\n近くにお店は…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6317, 1, 1, 122501, 2, 1, 0, "この格好でいるのも\nなじんできたし\nちょっと鍛錬でも…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6318, 1, 1, 122501, 3, 1, 0, "動きやすいけれど…\nいつもと違う服装は\n少し落ち着かないな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6319, 1, 1, 122501, 4, 1, 0, "夏の空気に\n当てられると思わず\n気が緩んでしまうね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6320, 1, 1, 122501, 5, 1, 0, "砂浜で走ると\nいい具合に体幹を\n鍛えられそうだ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6321, 1, 4, 122501, 101, 1, 0, "終わらない夏は\nないけれど…\nつい願ってしまうな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6322, 1, 8, 122501, 102, 1, 0, "胸を張って彼と\n並び立てるよう\n私も、もっと強く…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6323, 1, 1, 122501, 1, 2, 0, "どうしたの？\n行きたい場所が\nあるなら付き合おうか", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6324, 1, 1, 122501, 2, 2, 0, "快晴で風は穏やか…\n絶好の釣り日和だね\nさあ、出かけようか", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6325, 1, 1, 122501, 3, 2, 0, "今日の予定を\n立ててるんだけど…\nキミの希望も\n聞きたいな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6326, 1, 1, 122501, 4, 2, 0, "ビーチスポーツか…\nキミとなら\nいい勝負ができそうだ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6327, 1, 4, 122501, 101, 2, 0, "私たちの夏に\n遠慮は無用だよ\n思いきり楽しもう！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6328, 1, 8, 122501, 102, 2, 0, "いつかまた一緒に\n花火を見に行こう\n…約束だよ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6329, 1, 1, 122501, 1, 2, 0, "潮騒の音で\n目覚めるのも\nいいものだね", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6330, 1, 1, 122501, 2, 2, 0, "今朝釣ってきた魚が\nあるんだ。これで\nお昼ご飯にしようか", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6331, 1, 1, 122501, 3, 2, 0, "夜にもイベントが\nあるみたいなんだ\n一緒に見に行かない？", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6333, 1, 1, 122501, 2, 3, 0, "気持ちが安らぐね…\nうっかり眠って…\nしまい…そうだ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6334, 1, 1, 122501, 3, 3, 0, "肌触りのよさが\nこの格好だと\nよく分かるよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6335, 1, 1, 122501, 4, 3, 0, "海をイメージさせる\n香りだね…\n爽やかで落ち着くよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6336, 1, 1, 122501, 5, 3, 0, "濡れたままだと\n風邪を引いてしまうよ\nほら、キミもおいで", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6338, 1, 1, 122501, 7, 3, 0, "夏を楽しみきるには\nエネルギーが\n必要だからね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6339, 1, 1, 122501, 8, 3, 0, "大切な人と過ごせる\n観光スポット紹介…\n…こ、このページは\nもういいかな！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6341, 1, 1, 122501, 10, 3, 0, "濡らしてもいいなら\n海に持っていって\n乗るのもよさそうだ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6342, 1, 1, 122501, 11, 3, 0, "こういう名画を\n見た気がするな…\nこんな視点なんだね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6343, 1, 1, 122401, 1, 1, 0, "水着って\n動きやすくて\nいいよねー♪\nいっちにーさんしー♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6344, 1, 1, 122401, 2, 1, 0, "ユイちゃんにもらった\n日焼け止め\n塗っておいて\nよかった～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6345, 1, 1, 122401, 3, 1, 0, "なんだか暑いなあ…\nせっかくだし\nプールで泳いで\nこようかな？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6346, 1, 1, 122401, 4, 1, 0, "この格好で\n出かけようとしたら\nレイさんに\n怒られちゃった…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6347, 1, 1, 122401, 5, 1, 0, "たとえ水着姿でも\n困ってる人を見たら\n助けに行くからね！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6348, 1, 4, 122401, 101, 1, 0, "バカンス楽しかった～\nまた騎士くんと\n一緒に行きたいな♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6349, 1, 8, 122401, 102, 1, 0, "この水着、あたしに\n似合ってるかなあ？\n騎士くんはどう\n思ってるんだろ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6350, 1, 1, 122401, 1, 2, 0, "騎士くん！\n海に行くなら\n準備運動\n忘れずにね！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6351, 1, 1, 122401, 2, 2, 0, "泳ぐ練習がしたいの？\nいいよ！　水着だから\nなにかあったらすぐに\n助けてあげるね！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6352, 1, 1, 122401, 3, 2, 0, "騎士くんの水着姿も\nかっこよかったよ！\nまた遊びに行こ！\n約束ね！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6353, 1, 1, 122401, 4, 2, 0, "このグローブの向日葵\nかわいいでしょ？\nユイちゃんが\n選んでくれたんだ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6354, 1, 4, 122401, 101, 2, 0, "実はこの水着\nいつも着てる\n服とおなじなんだ…\nって、冗談だよ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6355, 1, 8, 122401, 102, 2, 0, "変な日焼けしてないか\n見てもらってもいい？\n…なし！　今のなし！\nやっぱりなしー！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6356, 1, 1, 122401, 1, 2, 0, "夏のお日様に\n負けないよう\n今日も元気いっぱい\nがんばるぞー！", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6357, 1, 1, 122401, 2, 2, 0, "お腹減ってきちゃった\n騎士くんはお腹\n空いてない？\n…聞こえちゃった♪", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6358, 1, 1, 122401, 3, 2, 0, "今日も一日\n楽しかったー！\n明日もまた\n楽しもうね！", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6360, 1, 1, 122401, 2, 3, 0, "ふわあ…\nふわふわなのが\nいつもより\nよくわかるよー…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6361, 1, 1, 122401, 3, 3, 0, "そうだ！　水着なら\n濡れてもいいし\n今度洗ってあげるね♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6362, 1, 1, 122401, 4, 3, 0, "んー…いい匂い♪\nなんだか\n身体がぽかぽか\nしてきちゃった！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6363, 1, 1, 122401, 5, 3, 0, "水着だから、えっと…\n暖か涼しい…！\n不思議な感じー", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6365, 1, 1, 122401, 7, 3, 0, "思いっきり身体を\n動かした後だから\nいくらでも\n食べちゃえるよー！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6366, 1, 1, 122401, 8, 3, 0, "ふむふむ…\nなるほどー\n水着の種類って\nそんなにあるんだぁ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6368, 1, 1, 122401, 10, 3, 0, "乗り心地バッチリだね\nしゅっぱつしんこー！\nなんちゃって♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6369, 1, 1, 122401, 11, 3, 0, "わっ、ぽよんぽよん！\nトランポリンみたいで\nた～のし～！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6370, 1, 12, 101501, 103, 1, 0, "彼のことを考えてると\n幸せで胸が温かくなる\n…私ったら、もう", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6371, 1, 12, 101501, 103, 2, 0, "うふふふふ\nくすぐったいわ～♪\nもう、イタズラっ子ね\n次は先生の番よ～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6372, 1, 1, 122601, 1, 1, 0, "騎士クンの汗\n拭いてあげたいな…\n余計なお世話かな…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6373, 1, 1, 122601, 2, 1, 0, "夏バテ気味かも…\nそうだ、今夜は\nかば焼きにしよう♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6374, 1, 1, 122601, 3, 1, 0, "青い空、白い雲\n眩しい日差し…！\n夏って好きだな…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6375, 1, 1, 122601, 4, 1, 0, "ヒリヒリする…？\n日に焼けちゃったかな\nふー…ふー…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6376, 1, 1, 122601, 5, 1, 0, "花火大会があるんだ…\n夜、浜辺…2人きり…\nむ、無理だよ〜！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6377, 1, 4, 122601, 101, 1, 0, "水着って不思議…\n引っ込み思案が少し\nおさまる…気がする", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6378, 1, 8, 122601, 102, 1, 0, "騎士クンとまた\n海に行きたいな…\nまた、一緒に…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6379, 1, 1, 122601, 1, 2, 0, "ダ、ダイエット！？\nしてないよ！？\n元々これ位だよ…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6380, 1, 1, 122601, 2, 2, 0, "スイカ割りのコツ？\nわたしは、騎士クンに\n教わりたいな…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6381, 1, 1, 122601, 3, 2, 0, "騎士クン。うちわで\n扇いであげるね\nどこに置いたかな…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6382, 1, 1, 122601, 4, 2, 0, "うぅ…暑いね…\n騎士クン、温度計は\n見ない方がいいよ～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6383, 1, 4, 122601, 101, 2, 0, "じ、じっと見ないで…\nええっと…ちょっと！\nちょっとならいいよ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6384, 1, 8, 122601, 102, 2, 0, "え、肝試し？\nう〜ん、うう〜ん…\n…騎士クンとなら！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6385, 1, 1, 122601, 1, 2, 0, "おはよう騎士クン\n見て、あっちの空\nもう入道雲が出てる", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6386, 1, 1, 122601, 2, 2, 0, "夕立、来るかなぁ…\nもし雷が鳴ったら\nそばにいてくれる…？", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6387, 1, 1, 122601, 3, 2, 0, "しー。…ほら、虫の声\n…わ、わたしの心臓の\n音は気にしないで…！", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6389, 1, 1, 122601, 2, 3, 0, "ふふ、水着でゴロゴロ\nするのってなんだか…\nいけないことみたい", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6390, 1, 1, 122601, 3, 3, 0, "かわいい～♪\n日光浴させて\nあげたいな♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6391, 1, 1, 122601, 4, 3, 0, "ほんのり潮の香りが…\n香ばしくて塩辛くて…\nあ…おなか鳴っちゃう", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6392, 1, 1, 122601, 5, 3, 0, "水着、乾かせるかも…\nそうだ、みんなの分も\n持ってこよう！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6394, 1, 1, 122601, 7, 3, 0, "夏バテなんてしてたら\nみんなの足を\n引っ張っちゃうよね\nいただきます！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6395, 1, 1, 122601, 8, 3, 0, "騎士クンの、どうして\n海はしょっぱいの？\nって質問の答えは…\n分かんないよぉ～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6397, 1, 1, 122601, 10, 3, 0, "顔は怒ってるのに\nなんだかかわいい…\n誰かに似てる…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6398, 1, 1, 122601, 11, 3, 0, "これを枕に、ビーチで\n騎士クンと…なんて…\nなんて…なんて…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6399, 1, 1, 122901, 1, 1, 0, "夏休み、\n毎日たのしいな～♪\nつぎはなにしよう？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6400, 1, 1, 122901, 2, 1, 0, "自由研究、\nなににしよっかな～？\nプチグリフォンさんの\nかんさつにっきとか？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6401, 1, 1, 122901, 3, 1, 0, "夏休みって、\n給食ないから…\nそれはちょっぴり\nさみしいかも…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6402, 1, 1, 122901, 4, 1, 0, "砂浜をはだしで歩くの\nきもちいいよね♪\nくすぐったいけど\nミミ、好きなんだ～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6403, 1, 1, 122901, 5, 1, 0, "きれいな貝がら\n見つけちゃった♪\nミミのたからもの！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6404, 1, 4, 122901, 101, 1, 0, "ミミ、浴衣を\n買ってもらったんだ♪\n今度、おにいちゃんに\n見せてあげたいな！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6405, 1, 8, 122901, 102, 1, 0, "ミミ、おにいちゃんと\nいっぱい夏休みの\n思い出つくりたいな♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6406, 1, 1, 122901, 1, 2, 0, "ねえ、おにいちゃん…\n夏休みの宿題、\nわかんないとこ\nおしえてくれる…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6407, 1, 1, 122901, 2, 2, 0, "おかあさんにね、\n麦茶つくるの上手って\nほめられたんだ～♪\n飲んでみて♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6408, 1, 1, 122901, 3, 2, 0, "夏休みの宿題で\nポスターかくんだ～♪\nおにいちゃんを\nかいてもいい？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6409, 1, 1, 122901, 4, 2, 0, "線香花火やりたいな♪\nミミ、長持ちさせるの\n上手なんだよ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6410, 1, 4, 122901, 101, 2, 0, "おにいちゃんの\n好きな本ってなあに？\nその本で読書感想文\n書こうかな♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6411, 1, 8, 122901, 102, 2, 0, "海であそんだ後ね、\nおにいちゃんに\n髪を拭いてもらうの\nすきなんだ～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6412, 1, 1, 122901, 1, 2, 0, "夏休みって、\nワクワクして\n早起きしちゃうよね♪", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6413, 1, 1, 122901, 2, 2, 0, "わぁ♪　お昼は、\n流しそうめんなんだ？\nミミ、流してみたい♪", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6414, 1, 1, 122901, 3, 2, 0, "こわい話聞いて、\nねむれないの…\nねえ、おにいちゃん、\nいっしょに寝ていい？", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6416, 1, 1, 122901, 2, 3, 0, "ちょっぴりやすんだら\nまた、いっしょに\n海であそぼうね♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6417, 1, 1, 122901, 3, 3, 0, "すっごくかわいいね♪\nこの子のこと、\n絵日記にかこうっと♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6418, 1, 1, 122901, 4, 3, 0, "くんくん…\nスイカのにおいだね♪\nこのにおいだいすき！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6419, 1, 1, 122901, 5, 3, 0, "およいだあとは、\nちゃんと体を\nあっためなくちゃね！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6421, 1, 1, 122901, 7, 3, 0, "夏休みのご飯って\nなんだかとくべつ\nおいしい気がするね♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6422, 1, 1, 122901, 8, 3, 0, "夏の星座って\nすっごくきれいだね♪\nいつかみんなと\nいっしょに見たいな♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6424, 1, 1, 122901, 10, 3, 0, "わぁ♪　この子、\nとってもかわいいね！\nおともだちに\nなりたいな♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6425, 1, 1, 122901, 11, 3, 0, "ミミ、人魚姫に\nなったみたい！\nおにいちゃん、\nとなりにすわって♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6426, 1, 1, 122801, 1, 1, 0, "水着姿になると\nバカンス気分を\n味わえちゃうね！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6427, 1, 1, 122801, 2, 1, 0, "海に行くの待ちきれ\nなくて、もう水着に\n着替えちゃった！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6428, 1, 1, 122801, 3, 1, 0, "海に行ったら\nずぶぬれ系のイタズラ\nたくさんしかけよう！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6429, 1, 1, 122801, 4, 1, 0, "部屋の中で泳ぎの練習\nしよう！　にいちゃん\nも水着に着替えて！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6430, 1, 1, 122801, 5, 1, 0, "お日さまとお揃いの\n水着なんだ～♪\n似合ってるかなぁ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6431, 1, 4, 122801, 101, 1, 0, "海といえば冒険！\n海底に眠る財宝を\n見つけちゃおう！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6432, 1, 8, 122801, 102, 1, 0, "にいちゃんと一緒だと\n部屋にいても海気分♪\nスイカわりしよっか！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6433, 1, 1, 122801, 1, 2, 0, "にいちゃん！\n早く海に行く準備\nしなよ～！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6434, 1, 1, 122801, 2, 2, 0, "ねーにいちゃん\n何秒息止められるか\n勝負しよーよ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6435, 1, 1, 122801, 3, 2, 0, "にいちゃんはアイスと\nかき氷どっちが好き？\nミソギは…どっちも！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6436, 1, 1, 122801, 4, 2, 0, "日焼け止めクリーム\nキョウカに\nもらったんだ～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6437, 1, 4, 122801, 101, 2, 0, "水中メガネがあれば\n水鉄砲くらっても\n平気だね！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6438, 1, 8, 122801, 102, 2, 0, "水着なら、毎日夏休み\n気分！　にいちゃんも\n着てみなよ～！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6439, 1, 1, 122801, 1, 2, 0, "にいちゃんおはよー！\n早く顔洗って、\nいっぱい遊んじゃお！", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6440, 1, 1, 122801, 2, 2, 0, "にいちゃんデザートは\nイチゴのかき氷だよ！\n…激辛ってバレた？", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6441, 1, 1, 122801, 3, 2, 0, "今日は楽しかった～！\n明日もイタズラ日和に\nなるといいな！", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6443, 1, 1, 122801, 2, 3, 0, "落とし穴の下に\nやわらかいの置けば\nケガしないよね！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6444, 1, 1, 122801, 3, 3, 0, "かわいいね～！\nこの子も海に\n連れてっちゃおうよ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6445, 1, 1, 122801, 4, 3, 0, "このにおいを利用して\nイタズラするには…\nにいちゃんどう思う？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6446, 1, 1, 122801, 5, 3, 0, "水着を着て暖炉で\nあったまるのって、\n変でおもしろいね～！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6448, 1, 1, 122801, 7, 3, 0, "海辺で食べる\nお昼ごはんって最高♪\nってことで海行こう！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6449, 1, 1, 122801, 8, 3, 0, "いつか海を越えて、\n地図にも載ってない\n場所を見つけたいな！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6451, 1, 1, 122801, 10, 3, 0, "この口から水が出たら\nみんな驚くよね！\nぷぷ…細工しちゃお！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6452, 1, 1, 122801, 11, 3, 0, "変な音がするイタズラ\nを仕込んどいて…\n誰か座らないかな～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6453, 1, 12, 102701, 103, 1, 0, "あなた様と私の愛を\n周りに見せつけて\n差し上げましょう♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6454, 1, 12, 102701, 103, 2, 0, "呼びましたか？\n呼びましたね？\nでは愛を語りましょう\nそうしましょう", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6455, 1, 1, 122701, 1, 1, 0, "水着で歩き回るのは\n恥ずかしいな…\n誰も見てないよね？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6456, 1, 1, 122701, 2, 1, 0, "夏はやっぱり\nかき氷ですよね\n何色がいいかなぁ…♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6457, 1, 1, 122701, 3, 1, 0, "あの人、泳げないって\n聞きました！\n私が教えてあげないと", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6458, 1, 1, 122701, 4, 1, 0, "夏休みの宿題は\n計画的に進めましょう\n当たり前のことです！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6459, 1, 1, 122701, 5, 1, 0, "防犯魔石、どこに\n着けておこうかなぁ\n…ここにしようかな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6460, 1, 4, 122701, 101, 1, 0, "海の楽しみ方は\n予習済みです！\nあとは実践ですね♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6461, 1, 8, 122701, 102, 1, 0, "水着…きっとあの人に\n見られちゃいますよね\n…褒めてくれるかな…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6462, 1, 1, 122701, 1, 2, 0, "触りたいんですか？\n…いいですよ\n帽子くらいなら", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6463, 1, 1, 122701, 2, 2, 0, "夏は体調管理に\n気をつけましょう\n私が管理してあげます", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6464, 1, 1, 122701, 3, 2, 0, "海に行くのが楽しみで\nたまらないんですか？\n私はそこまで…嘘です", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6465, 1, 1, 122701, 4, 2, 0, "いつもより薄着だから\nなんだか恥ずかしい…\nみ、見ないでください", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6466, 1, 4, 122701, 101, 2, 0, "へんたいふしんしゃ～\nえっ？　ああ、これは\n海に叫ぶ練習です", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6467, 1, 8, 122701, 102, 2, 0, "あなたと一緒に\n海に行くなんて…\nなんだか大人…えへへ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6468, 1, 1, 122701, 1, 2, 0, "おはようございます\nさぁ、海に行って\n私と運動しましょう", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6469, 1, 1, 122701, 2, 2, 0, "あっつ～い…\nはっ！？　ダメですよ\nシャキッとしないと！", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6470, 1, 1, 122701, 3, 2, 0, "明日も海で\n遊ぶために、しっかり\nお休みしましょうね", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6472, 1, 1, 122701, 2, 3, 0, "夏を楽しむときは\n休憩も大事ですよね\n計画的に、計画的に！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6473, 1, 1, 122701, 3, 3, 0, "かわいいなぁ～\n海のお土産屋さんに\n売ってるかなぁ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6474, 1, 1, 122701, 4, 3, 0, "大人な香りがします\n私も違いが分かる\n素敵な女性になります", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6475, 1, 1, 122701, 5, 3, 0, "濡れた体を\n乾かしましょう\nタオル、タオル…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6477, 1, 1, 122701, 7, 3, 0, "泳いだ後は\n栄養補給…だけど\n…おなか膨らんじゃう", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6478, 1, 1, 122701, 8, 3, 0, "遊ぶだけじゃなくて\nお勉強もしてますよ\nあなたもどうですか？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6480, 1, 1, 122701, 10, 3, 0, "ミミが喜びそう…♪\nわ、私ははしゃいだり\nしませんけど…っ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6481, 1, 1, 122701, 11, 3, 0, "ミソギがいたずらに\n使いそう…しっかりと\n試しておかないと！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6482, 1, 1, 123001, 1, 1, 0, "あたしがまさか\nここにいるなんて…\n自分でも不思議だわ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6483, 1, 1, 123001, 2, 1, 0, "昔みたいにあちこち\n飛び回れないのって\nちょっと不便かも", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6484, 1, 1, 123001, 3, 1, 0, "こうして誰かと一緒に\n暮らせるのもあいつや\nみんなのおかげね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6485, 1, 1, 123001, 4, 1, 0, "ここで厄介になるなら\nあたしも家事とか\n手伝わないと…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6486, 1, 1, 123001, 5, 1, 0, "…だめ！　誰かの\n世話をせずには\nいられないわ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6487, 1, 4, 123001, 101, 1, 0, "あいつたちが\n帰ってくるところだし\n綺麗にしないと…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6488, 1, 8, 123001, 102, 1, 0, "せっかくあたしが\nいるっていうのに\n少しは構いなさいよ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6489, 1, 1, 123001, 1, 2, 0, "おいしい店とか\n知らないの？\n食べに行きましょ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6490, 1, 1, 123001, 2, 2, 0, "あんた暇でしょ？\nあたしのやること\n手伝いなさい", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6491, 1, 1, 123001, 3, 2, 0, "コッコロたんと\n遊んでくるから\n留守番よろしく♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6492, 1, 1, 123001, 4, 2, 0, "あらおかえり…って\nボロボロじゃない！\nまた人助けしてたの？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6493, 1, 4, 123001, 101, 2, 0, "やっぱりあたしは\nあんたのそばが\n一番落ち着くみたい", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6494, 1, 8, 123001, 102, 2, 0, "どこまでだって\nあんたと一緒に\nいてやるんだから", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6495, 1, 1, 123001, 1, 2, 0, "ふわぁ…おはよう\n…大きくなったせいか\n朝は特に体が重いわ", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6496, 1, 1, 123001, 2, 2, 0, "お昼ご飯が食べたい？\nしょうがないわね…\nなんか作って\nあげるから", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6497, 1, 1, 123001, 3, 2, 0, "あんたまだ成長期だし\nさっさと寝なさい\n大きくなれないわよ？", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6499, 1, 1, 123001, 2, 3, 0, "たまにはあたしも\nゆっくりしないと\nもたないのよ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6500, 1, 1, 123001, 3, 3, 0, "かわいいじゃな～い♪\nコッコロたんに\nプレゼントしたいわ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6501, 1, 1, 123001, 4, 3, 0, "いい香りね\n塔にもこういうの\n置いておこうかしら", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6502, 1, 1, 123001, 5, 3, 0, "炎か…前は\n羽が焼けそうで\n怖かったっけ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6504, 1, 1, 123001, 7, 3, 0, "あむ…妖精に\n体重増減の設定って\nあったっけ…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6505, 1, 1, 123001, 8, 3, 0, "記憶のデータと違って\n物語はいいわね\n結末が読めなくて", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6507, 1, 1, 123101, 1, 1, 0, "冒険のお土産\nみんなにおすそわけ\nせなあきまへんな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6508, 1, 1, 123101, 2, 1, 0, "おとぎ話よりも\n不思議なことが\nあるもんやねぇ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6509, 1, 1, 123101, 3, 1, 0, "冒険しに出かけるなら\n動きやすい服装に\nせななぁ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6510, 1, 1, 123101, 4, 1, 0, "地図に水筒に…\n他に忘れもんは\nあらへんやろか？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6511, 1, 1, 123101, 5, 1, 0, "これまでの冒険のこと\nノートに書いて\nまとめてますんよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6512, 1, 4, 123101, 101, 1, 0, "王子はんとの冒険は\nどれもマホマホ王国の\n大事な歴史や♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6513, 1, 8, 123101, 102, 1, 0, "どこへ冒険するときも\nうちと王子はんは\n一緒どす♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6514, 1, 1, 123101, 1, 2, 0, "魔法で火ぃつける\n練習してますんよ～\nよう爆発しますけど…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6515, 1, 1, 123101, 2, 2, 0, "うちと王子はんで\n冒険道具をお揃いに\nするんもええなぁ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6516, 1, 1, 123101, 3, 2, 0, "テント張りも随分\n上達したんよ\n王子はんのおかげやね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6517, 1, 1, 123101, 4, 2, 0, "ランスロットたちの\n新しい冒険衣装\n思いついたんよ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6518, 1, 4, 123101, 101, 2, 0, "迷子になっても\n王子はんと一緒なら\nきっと大丈夫やね♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6519, 1, 8, 123101, 102, 2, 0, "王子はんのこと、\nきっちり守れるように\nなってみせますえ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6520, 1, 1, 123101, 1, 2, 0, "お寝坊さんは\n布団めくって\n起こしにいきますえ～", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6521, 1, 1, 123101, 2, 2, 0, "支度が終わったら\n今日の冒険に\n出かけまひょか", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6522, 1, 1, 123101, 3, 2, 0, "もうちょっと\nおしゃべりしたいん？\nあと少しだけやで♪", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6524, 1, 1, 123101, 2, 3, 0, "冒険で疲れた体に\nこの癒しぱわーは\nあきまへん…すや…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6525, 1, 1, 123101, 3, 3, 0, "ふふ、あんさんも\nうちと一緒に\n冒険します？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6526, 1, 1, 123101, 4, 3, 0, "虫除けのアロマは\n冒険の必需品どす\n忘れんようにせんと", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6527, 1, 1, 123101, 5, 3, 0, "うちも魔法使わんでも\n火ぃ起こせるよう\n練習せんとあかんなぁ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6529, 1, 1, 123101, 7, 3, 0, "この料理…\n冒険するときに\n作ってみまひょか", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6530, 1, 1, 123101, 8, 3, 0, "王子はんとの\n愛と冒険のおとぎ話…\nはぁ…素敵やわぁ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6532, 1, 12, 103101, 103, 1, 0, "あの人との\n最良の未来のために\n占いも霊能力ももっと\n精進しなきゃ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6533, 1, 12, 103101, 103, 2, 0, "あなたともっと深く\nつながりたいです\n絆も、そして魂までも", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6534, 1, 12, 104401, 103, 1, 0, "絆の契約…\nあやつが望むのなら\n考えぬでもないがのう", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6535, 1, 12, 104401, 103, 2, 0, "なんじゃ？\nわらわの姿に\n見惚れたかの？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6536, 1, 1, 123201, 1, 1, 0, "旅の荷物って多いな…\nねぇぷうきち～\nすこしは持ってよぉ～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6537, 1, 1, 123201, 2, 1, 0, "傘はなくてもいっか\nぷうきちがいるし♪\n『おいなんだと…？』", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6538, 1, 1, 123201, 3, 1, 0, "旅先で人見知りなんか\nしてちゃだめだよね…\nまずはあいさつだ…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6539, 1, 1, 123201, 4, 1, 0, "おみやげはやっぱり\nご当地ぬいぐるみ\nだよね♪　ワクワク…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6540, 1, 1, 123201, 5, 1, 0, "知らない場所かぁ…\nうぅ…考えただけで\nすでにホームシック…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6541, 1, 4, 123201, 101, 1, 0, "野宿するってことは…\nお兄ちゃんのマントで\n寝るチャンスかも…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6542, 1, 8, 123201, 102, 1, 0, "どんなに危険な冒険も\nお兄ちゃんと一緒なら\n大丈夫！　…だよね？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6543, 1, 1, 123201, 1, 2, 0, "ぷうきちのアイマスク\nも腹巻も持ってくの！\n旅の必需品なの～！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6544, 1, 1, 123201, 2, 2, 0, "これ全部背負うのって\nけっこうむずかしいの\n順番まちがえると大変", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6545, 1, 1, 123201, 3, 2, 0, "えへへ～\nなでなでして？　帽子\nの上からでもいいよ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6546, 1, 1, 123201, 4, 2, 0, "『悪いが絆創膏の部分\nはアンタッチャブルだ\n綿が飛び出ちまう』", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6547, 1, 4, 123201, 101, 2, 0, "お兄ちゃんと一緒に\nもっともっといろんな\n景色が見たいな！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6548, 1, 8, 123201, 102, 2, 0, "旅って、終わる時は\nきっとさびしいよね…\nお兄ちゃん、帰り道は\n手をつないでもいい？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6549, 1, 1, 123201, 1, 2, 0, "出発が楽しみすぎて\n寝られなかった…\n『旅行のお約束だな』", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6550, 1, 1, 123201, 2, 2, 0, "わぁ　いいお天気！\n旅の間もずーっと\nぴーかんだといいな♪", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6551, 1, 1, 123201, 3, 2, 0, "知らない場所で寝るの\n怖いな…寝る時だけ\n帰ってこようかな…", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6553, 1, 1, 123201, 2, 3, 0, "旅先にはクッションも\nソファもないよね…\n『俺でいいだろ』", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6554, 1, 1, 123201, 3, 3, 0, "ぬいぐるみのみんな\n旅の無事を\n祈ってて…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6555, 1, 1, 123201, 4, 3, 0, "旅先の香りも\n思い出に持って\n帰れたらいいのにな～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6556, 1, 1, 123201, 5, 3, 0, "旅行用に携帯用暖炉\nがあるといいかも…！\n『まだ荷物増やす？』", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6558, 1, 1, 123201, 7, 3, 0, "『もう出発だぜ？』\nえーまってまって！\n腹ごしらえがまだ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6559, 1, 1, 123201, 8, 3, 0, "世界ってこんなに広い\nんだ…！　はやく自分\nの目で見てみたいな～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6561, 1, 1, 123301, 1, 1, 0, "はちゃめちゃヒマ～\nなーんか楽し～コト\nないかなー", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6562, 1, 1, 123301, 2, 1, 0, "ここ、いートコだねー\nこんどカリザきゅんも\nつれてこよー", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6563, 1, 1, 123301, 3, 1, 0, "かわゆいショタが\n見た～い♪\n触りた～～～い♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6564, 1, 1, 123301, 4, 1, 0, "なんか服きっついなー\nちょい太ったかー？\nん～\nま・気のせーっしょ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6565, 1, 1, 123301, 5, 1, 0, "あ～やべ\nおねえちゃんに\nメッセすんの忘れたー\nまいっかー", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6566, 1, 4, 123301, 101, 1, 0, "パンケーキ食いてー\n騎士きゅん\n誘ってみよーっと♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6567, 1, 8, 123301, 102, 1, 0, "きょーも～\n好きぴに囲まれて～\nシ・ア・ワ・セ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6568, 1, 1, 123301, 1, 2, 0, "アタシと遊ばな～い？\nってなんか\nナンパみスゴー\nウケる～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6569, 1, 1, 123301, 2, 2, 0, "んー？\n今はだるいからパース\nあとでねー", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6570, 1, 1, 123301, 3, 2, 0, "カリザきゅん\n見なかった～？\nおねえちゃんから\n逃げるとかオシオキー", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6571, 1, 1, 123301, 4, 2, 0, "ネイルしたいけど\n無理なんよねー\n爪の間にめっちゃ\nスライム入っからさー", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6572, 1, 4, 123301, 101, 2, 0, "おねえちゃんに\nかまってほし～の？\nかわゆ～い♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6573, 1, 8, 123301, 102, 2, 0, "な～に騎士きゅん♪\nここでイチャイチャ～\nし・ちゃ・う～？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6574, 1, 1, 123301, 1, 2, 0, "ねんむ～\nけど外から\nショタの声したんで～\nつい起きちった～", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6575, 1, 1, 123301, 2, 2, 0, "ランチの後はやっぱ\nデザートっしょー\nベツバラってヤツ～♪", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6576, 1, 1, 123301, 3, 2, 0, "こんな時間まで\n起きてるコは～\n食べちゃうぞ～♪", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6578, 1, 1, 123301, 2, 3, 0, "あーラクー\nいっしょーここで\nキューケーできるわー", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6579, 1, 1, 123301, 3, 3, 0, "なにこれ～\nかわいいじゃ～ん\nショタの次くらいに～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6580, 1, 1, 123301, 4, 3, 0, "いーニオイする～\nなんのニオイとかー\nよくわかんないけどー", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6581, 1, 1, 123301, 5, 3, 0, "あったか～♪\n溶けてスライム\n戻りそ～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6583, 1, 1, 123301, 7, 3, 0, "ン・ン・ン～♪\nお・い・し～♪\nも～っと食べるぅ～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6584, 1, 1, 123301, 8, 3, 0, "わ～なんかムズー\n読めなー\nウケるー", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6586, 1, 1, 123501, 1, 1, 0, "あんパンって\nお菓子に入るのかな？\nハロウィンで\nもらえたりしない？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6587, 1, 1, 123501, 2, 1, 0, "お菓子をくれなきゃ\nぐうたらするぞ～！\nハロウィン以外でも！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6588, 1, 1, 123501, 3, 1, 0, "お化けになったら\n仕事しないで\nゴロゴロし放題\nなのかな？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6589, 1, 1, 123501, 4, 1, 0, "おっと…この仮装\n袖がすごく長いからさ\n引っ掛けないように\n気を付けないとね～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6590, 1, 1, 123501, 5, 1, 0, "イタズラするのも\n体力がいるよね～\n楽にできそうなの\n何かないかな？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6591, 1, 4, 123501, 101, 1, 0, "あいつって\n仮装とかするのかな？\n似合いそうなのは\nどんなお化けだろ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6592, 1, 8, 123501, 102, 1, 0, "ずっとハロウィンなら\nあいつと遊び放題\nなんだけどな～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6593, 1, 1, 123501, 1, 2, 0, "今の街はハロウィン\n一色だからね～\n人混みが大変だよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6594, 1, 1, 123501, 2, 2, 0, "この仮装、どうかな？\n気に入ったなら\n来年もしてあげるよ～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6595, 1, 1, 123501, 3, 2, 0, "実はこの長い袖\nドングリとかを\nため込めるんだよね…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6596, 1, 1, 123501, 4, 2, 0, "カボチャランタンが\nあるんなら\nドングリランタンも\nあっていいよね～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6597, 1, 4, 123501, 101, 2, 0, "ハロウィンでも\nやる気があれば\nぐうたらできる…\nうん、いいこと言った", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6598, 1, 8, 123501, 102, 2, 0, "一緒にお菓子食べよ？\n寝転びながら堪能する\nハロウィンスイーツは\n最高だからね～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6599, 1, 1, 123501, 1, 2, 0, "お化けは寝る時間…\n今のあたしはお化け…\nだから、おやすみ～…", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6600, 1, 1, 123501, 2, 2, 0, "お昼か～…\n食後のデザートは\nハロウィンのお菓子で\n決まりだね～♪", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6601, 1, 1, 123501, 3, 2, 0, "ふぁ～、寝よ…\nお化けは夜に\n出るものだけど\nあたしはリスだからね", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6603, 1, 1, 123501, 2, 3, 0, "ハロウィンを楽しむ\nためにも、ぐっすり\n寝とかないとね～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6604, 1, 1, 123501, 3, 3, 0, "…袖から手が\n出せないから\nこのまま撫でよう\nよしよし", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6605, 1, 1, 123501, 4, 3, 0, "リラックスできる\n素敵な香りだね～\n成仏しそうだよ～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6606, 1, 1, 123501, 5, 3, 0, "あったかいね～\nここで寝てもいい？\n…着替えないとダメか", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6608, 1, 1, 123501, 7, 3, 0, "もぐもぐ…\nお化けもリスも\nお腹は空くんだよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6609, 1, 1, 123501, 8, 3, 0, "キョンシーって\nどんなお化けなのか\n本で調べようかな？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6611, 1, 1, 123501, 12, 3, 0, "より取り見取りだね～\nとりあえず、これと\nこのお菓子はキープで", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6613, 1, 1, 123601, 1, 1, 0, "よし、衣装もばっちり\nハロウィンパレードへ\n出かけよう♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6614, 1, 1, 123601, 2, 1, 0, "仮装してても\n剣は手放せないね\nほとんど体の一部だよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6615, 1, 1, 123601, 3, 1, 0, "見回りに誘うふりして\n二人でパレードに\n参加しちゃおうかな♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6616, 1, 1, 123601, 4, 1, 0, "…あれ？　手錠の鍵\n落としちゃった…？\nやば、出かける前に\n見つけないと…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6617, 1, 1, 123601, 5, 1, 0, "お家でコスプレも\nこれはこれで楽しいな\n決めポーズの練習\nたくさんしておこう！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6618, 1, 4, 123601, 101, 1, 0, "他にも、お姫様に\nメイドさんに学生服…\nやりたいコスプレが\n多すぎるよ～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6619, 1, 8, 123601, 102, 1, 0, "漫画もゲームも好き！\n仮装するのも大好き！\n…好きを言葉にすると\nもっと好きになる♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6620, 1, 1, 123601, 1, 2, 0, "ずっと目が合ってるね\n見つめてくれるのは\nいいんだけど、今日は\n衣装を見てほしいな♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6621, 1, 1, 123601, 2, 2, 0, "トリックオアトリート\nお菓子をくれなきゃ\n逮捕しちゃうぞ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6622, 1, 1, 123601, 3, 2, 0, "あなたにだけはこの姿\nいつでも見せてあげる\nふふ、なんちゃって♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6623, 1, 1, 123601, 4, 2, 0, "どうしたの？\nイタズラするなら\n隙を突かなきゃ\nこんなふうに、ね♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6624, 1, 4, 123601, 101, 2, 0, "コスプレしてても\n剣の稽古は休まないよ\nちょっと相手になって\nくれるかな？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6625, 1, 8, 123601, 102, 2, 0, "ひゃっ、びっくりした\n急に触らないでよ～\nハロウィンだからって\nイタズラしないでね？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6626, 1, 1, 123601, 1, 2, 0, "おはよう！\n今日も朝から\nパトロールに行こう！", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6627, 1, 1, 123601, 2, 2, 0, "カボチャのケーキを\n用意してるよ！\nデザートを食べて\n午後も頑張ろう！", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6628, 1, 1, 123601, 3, 2, 0, "暗くなってきたね\nカボチャのランタンに\n火を灯そうよ♪", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6630, 1, 1, 123601, 2, 3, 0, "ふかふか気持ちいい～\nあっでも衣装がシワに\nなっちゃうかも…？\n…でも気持ちいい～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6631, 1, 1, 123601, 3, 3, 0, "…かわいい\nこの子もパレードに\n連れて行きたいな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6632, 1, 1, 123601, 4, 3, 0, "はぁ～癒される♪\nハロウィンの見回りが\n終わった後は、\nまったりくつろごう", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6633, 1, 1, 123601, 5, 3, 0, "ここで二人寄り添って\nカボチャスープ飲もう\n…なんて想像したら\nポカポカしてきた…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6635, 1, 1, 123601, 7, 3, 0, "パレードに参加すると\n体力を使うから、\nいっぱい食べておこう", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6636, 1, 1, 123601, 8, 3, 0, "この絵本のお姫様\nかわいいね\nこの仮装もいいな♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6638, 1, 1, 123601, 12, 3, 0, "わあ、おいしそう！\n見回りに行く前に\n少しつまんじゃおう♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6640, 1, 12, 103701, 103, 1, 0, "今日こそは、あの人に\n気持ちを打ち明けて…\nそのあとは…うう\nど、どうしよう？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6641, 1, 12, 103701, 103, 2, 0, "私が剣なら、あなたは\nさしずめ私の鞘…\nふふ、私の帰る\n場所ってことだよ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6642, 1, 1, 123701, 1, 1, 0, "やっぱり\nコスプレしてると\nテンションあがる～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6643, 1, 1, 123701, 2, 1, 0, "キャラコスにも\n興味あるけど…\nマジカル、ラブリー\nどっちにしよっかな～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6644, 1, 1, 123701, 3, 1, 0, "キャラコスといえば\nミスティーシャイニー\nもいいんだよねえ…\nうはー！　悩むー！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6645, 1, 1, 123701, 4, 1, 0, "ギルドハウスで\n宅コスしてる人\n実は結構\nいるんじゃないかな～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6646, 1, 1, 123701, 5, 1, 0, "コスプレだけど普段着\nって言いはれそう…\nって、ダメダメ！\nオタクは節度が大事！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6647, 1, 4, 123701, 101, 1, 0, "フェスで買ってきた\n薄い本～♪\nギルドハウスに持って\nきちゃおっかな～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6648, 1, 8, 123701, 102, 1, 0, "ようこそ\nコスプレ沼へ～\nなんちゃって♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6649, 1, 1, 123701, 1, 2, 0, "どうどう？\nナナカちゃんの\nハロウィンコスプレ\n似合ってる？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6650, 1, 1, 123701, 2, 2, 0, "ハロウィン魔法少女\n参上カボ～！\n…やっぱ今の語尾\nナシで！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6651, 1, 1, 123701, 3, 2, 0, "ハロウィンといえば\nトリックオア～…\nコスプレ！\nなんちゃって", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6652, 1, 1, 123701, 4, 2, 0, "ランフェスの\n戦利品～♪\nねえねえ\n見たい？　見たい？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6653, 1, 4, 123701, 101, 2, 0, "私にしてほしい\nコスプレがあったら\n教えて♪　君のために\nや～ってやるぜぇ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6654, 1, 8, 123701, 102, 2, 0, "一緒にコスプレしたい\nならナナカちゃんは\nいつでも大歓迎だよ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6655, 1, 1, 123701, 1, 2, 0, "ふぁあ…ねむ\nって、ありゃりゃ　\n昨日コスプレしたまま\n寝ちゃってたかあ…", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6656, 1, 1, 123701, 2, 2, 0, "今日のお昼は\nなんにしよ？\nカボチャ料理に\nしよっかな～", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6657, 1, 1, 123701, 3, 2, 0, "もうこんな時間かあ\nそういえばオタクって\n夜行性の人が多い\n気がするぜぇ…", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6659, 1, 1, 123701, 2, 3, 0, "ふっふっふ\nこのパニエは\n座るときはちゃんと\nふにゃってなるのさ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6660, 1, 1, 123701, 3, 3, 0, "いや～\nかわいいものをみると\n癒されますな～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6661, 1, 1, 123701, 4, 3, 0, "新しい薄い本の匂いの\nアロマとか…ダメ？\n私はあの匂い、なんか\n好きなんだよね～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6662, 1, 1, 123701, 5, 3, 0, "暖か～い…\nけど近づきすぎて\n衣装が燃えないように\n気を付けなきゃ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6664, 1, 1, 123701, 7, 3, 0, "コス衣装のために\n体型維持は必須だけど\nついつい\n食べちゃうんだよね～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6665, 1, 1, 123701, 8, 3, 0, "おっ、次はこの本に\nのってるキャラのコス\nしてみよっかな？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6667, 1, 1, 123701, 12, 3, 0, "おおっ！\nお菓子がいっぱい！\nベルルちゃんチョコ\n入ってないかな～？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6669, 1, 1, 123801, 1, 1, 0, "むっ…\nこの耳の手触り…\n驚嘆に値する…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6670, 1, 1, 123801, 2, 1, 0, "獲物を狩りまくる…\nそんなウサギがいても\nいいだろう？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6671, 1, 1, 123801, 3, 1, 0, "ワナなど怖くないぞ？\nむしろ積極的に\n踏んでいく性分だ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6672, 1, 1, 123801, 4, 1, 0, "一匹狼ならぬ\n一匹ウサギか\nどっちでもかまわん♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6673, 1, 1, 123801, 5, 1, 0, "人間とて本を正せば獣\n本能に従えばよいのだ\n遠慮は無用さ☆", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6674, 1, 4, 123801, 101, 1, 0, "脱兎のごとく…\n逃げるものか\n坊やを追い回すのさ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6675, 1, 8, 123801, 102, 1, 0, "獣の道は険しいが…\n坊やめ、音を上げずに\nちゃんとついてきたな\n褒めてやるとしよう♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6676, 1, 1, 123801, 1, 2, 0, "ニンジンはいらん\n好物は好敵手だ\nさぁどんどんおくれ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6677, 1, 1, 123801, 2, 2, 0, "尻尾に触りたいだと？\nワタシの乱数聖域を\n崩せたらな…☆", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6678, 1, 1, 123801, 3, 2, 0, "頭で考えるな\n体に率直になれ\n己を野放しにするのさ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6679, 1, 1, 123801, 4, 2, 0, "そうさモコフワさ♪\nなで心地は最高だが\n意外にどう猛だぞ…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6680, 1, 4, 123801, 101, 2, 0, "ワタシにピョンピョン\n跳ねてほしいのか？\nあとでじっくり、な♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6681, 1, 8, 123801, 102, 2, 0, "ワタシと番になるか？\nオマエさえよければ\nいつでもかまわんぞ☆", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6682, 1, 1, 123801, 1, 2, 0, "太陽と共に目覚め\n活動する\nそれが獣の生き方さ☆", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6683, 1, 1, 123801, 2, 2, 0, "昼寝をすればいい\nワタシが周囲を\n警戒していてやる", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6684, 1, 1, 123801, 3, 2, 0, "暗くなれば眠るだけさ\nそうすれば余計な\nストレスなどたまらん", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6686, 1, 1, 123801, 2, 3, 0, "むむっ！？\n尻尾がつっかえて\nうまく座れんな…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6687, 1, 1, 123801, 3, 3, 0, "愛らしさでワタシに\n張り合おうというのか\n５億年後に出直せ☆", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6688, 1, 1, 123801, 4, 3, 0, "野生の芳香か…\n本能が呼び覚まされる\nいいセンスだ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6689, 1, 1, 123801, 5, 3, 0, "寒さも野生のうちだ\n…とはいえ\n温まっていくか♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6691, 1, 1, 123801, 7, 3, 0, "悪くはないが物足りん\nこう見えて肉食なのだ\n肉はないのか肉は☆", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6692, 1, 1, 123801, 8, 3, 0, "ウサギ族は地獄耳？\nワタシとの共通点だな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6694, 1, 12, 106401, 103, 1, 0, "ここが私の家…\nそれはこれからも\nずっと変わらないわ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6695, 1, 12, 106401, 103, 2, 0, "ええと、その…今から\nデート…じゃなくって\n散歩でもどうかしら？\n…ふ、二人きりで…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6696, 1, 1, 123901, 1, 1, 0, "ギルドハウスに\nヒーロー参上！\nみんな仲良くッスよ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6697, 1, 1, 123901, 2, 1, 0, "料理もできて\n鎧も作れる！\nお母さんはすごいッス", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6698, 1, 1, 123901, 3, 1, 0, "ううう…\nまたマントをドアに\n挟んじゃったッス…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6699, 1, 1, 123901, 4, 1, 0, "ずっと鎧を着ていれば\nジュンさんに近づける\nかも？　燃えるッス！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6700, 1, 1, 123901, 5, 1, 0, "サインの練習…\nもっとしておいた\nほうがいいッスかね～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6701, 1, 4, 123901, 101, 1, 0, "半人前でも\n胸を張るッス！\nそのうち似合う日が\nくるッスから！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6702, 1, 8, 123901, 102, 1, 0, "種族の違いも\n強い魂があれば\n超えられるッス！\n信じてるッスよ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6703, 1, 1, 123901, 1, 2, 0, "困ったときは\n自分を呼ぶッス！\nあと楽しいときにも\n呼んでほしいッス！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6704, 1, 1, 123901, 2, 2, 0, "特訓でも行くッスか？\n望むところッス！\n目指せ、一人前！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6705, 1, 1, 123901, 3, 2, 0, "新しいショーの台本を\n読みこんでるッス！\nところで…この言葉の\n意味、分かるッスか？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6706, 1, 1, 123901, 4, 2, 0, "ヒミツがなくなると\nなんか恥ずかしいッス\nあんたはどうッスか？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6707, 1, 4, 123901, 101, 2, 0, "ヒーローは遅れて\nやってくる…\nあんたもそうッスね♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6708, 1, 8, 123901, 102, 2, 0, "誰よりも早く\nあんたに見せたいッス\n一人前になった自分！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6709, 1, 1, 123901, 1, 2, 0, "うーん、今日も\nよく眠れたッス～！\n絶好調、間違いなし！", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6710, 1, 1, 123901, 2, 2, 0, "おお、もうお昼ッスか\nどうりでお腹が空くと\n思ったッス～えへへ", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6711, 1, 1, 123901, 3, 2, 0, "パトロールのあとは\n歯みがきに宿題に…\n大忙しッスね、夜！", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6713, 1, 1, 123901, 2, 3, 0, "こんなにリラックス\nできるなんて…\n平和な証拠ッスね～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6714, 1, 1, 123901, 3, 3, 0, "か、かわいい…♪\nさあ、自分と握手…\nううん、抱っこッス♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6715, 1, 1, 123901, 4, 3, 0, "優しい匂いッスね…♪\nこれくらいみんなを\n包み込めるように\nなりたいッス～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6716, 1, 1, 123901, 5, 3, 0, "ひっくちゅ！　うー…\nちょっと冷えるッスね\nぬくぬくするッス～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6718, 1, 1, 123901, 7, 3, 0, "特訓した分、\nいっぱい食べるッス！\nでっかくなるッス！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6719, 1, 1, 123901, 8, 3, 0, "いつか自分の\nヒミツ図鑑が出たり\nして…いやいやいや", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6721, 1, 1, 106901, 1, 1, 0, "牢獄よりは\nマシな環境ね\n我慢してあげるわ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6722, 1, 1, 106901, 2, 1, 0, "退屈ね…\nキャルでもいじって\n暇をつぶそうかしら", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6723, 1, 1, 106901, 3, 1, 0, "ここにいる連中\n私を天気予報士と\n勘違いしてない？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6724, 1, 1, 106901, 4, 1, 0, "音波情報が多いわね…\nここ、もう少し静かに\nならないの…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6725, 1, 1, 106901, 5, 1, 0, "共同生活なんて\n久しぶりだわ\n…懐かしいわね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6726, 1, 4, 106901, 101, 1, 0, "あの子たちのギルド\n経営が雑すぎるわ…！\n教育が必要みたいね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6727, 1, 8, 106901, 102, 1, 0, "とどめを刺される\n準備はできているのに\nまだなのかしら…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6728, 1, 1, 106901, 1, 2, 0, "私と戦う気になった？\nあら、まだなのね…\nその時を待ってるわよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6729, 1, 1, 106901, 2, 2, 0, "私というリソースは\n有効活用しなさい\n子守なんてごめんだわ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6730, 1, 1, 106901, 3, 2, 0, "どうしてそんなに\n頭がいいか？\n生まれつきよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6731, 1, 1, 106901, 4, 2, 0, "視力は権能に\n影響しないわよ\n当たり前じゃない", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6732, 1, 4, 106901, 101, 2, 0, "あなたまで天気を\n教えろですって…？\nぶっ殺すわよ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6733, 1, 8, 106901, 102, 2, 0, "暇なら付き合いなさい\n好きなゲームでいいわ\nどうせ私が勝つもの", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6734, 1, 1, 106901, 1, 2, 0, "人生朝露…\n悔いのない一日を\n過ごしなさい", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6735, 1, 1, 106901, 2, 2, 0, "午睡はいいわよ…\nふわぁ…眠りは思考に\n必要不可欠なの…", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6736, 1, 1, 106901, 3, 2, 0, "夙興夜寝なんて\nやめておきなさい\n休息は大切よ", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6738, 1, 1, 106901, 2, 3, 0, "私を満足させるなんて\n大したものね…晶に\n大量生産させるわ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6739, 1, 1, 106901, 3, 3, 0, "キャルの代わりに\nズタズタにする物が\n欲しかったの…♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6740, 1, 1, 106901, 4, 3, 0, "いい香りじゃない\n見たことのない調合ね\n誰が用意したの？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6741, 1, 1, 106901, 5, 3, 0, "パワーが足りないわね\nもっとよ、もっと私を\n寛がせなさい", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6743, 1, 1, 106901, 7, 3, 0, "栄養価はともかく\n味のバランスは\n悪くないわね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6744, 1, 1, 106901, 8, 3, 0, "つまらない本は\n私が書き直すわ\nこれはどうかしら？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6746, 1, 1, 124001, 1, 1, 0, "今年はアカリと\nお姉ちゃんでサンタが\n二人…つまり幸せも\n二倍ですね♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6747, 1, 1, 124001, 2, 1, 0, "クリスマスに向けての\nサックス練習…\nお兄ちゃんと一緒に\n猛特訓です！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6748, 1, 1, 124001, 3, 1, 0, "クリスマスツリーの\n飾りつけ、完璧です♪\nお兄ちゃんお姉ちゃん\n喜んでくれるかな？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6749, 1, 1, 124001, 4, 1, 0, "わあ、お外が真っ白！\n生クリームが\nいーっぱいの\nケーキみたいです♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6750, 1, 1, 124001, 5, 1, 0, "煙突から入ると\nお洋服が汚れちゃう…\n玄関からじゃ\nダメなのかなぁ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6751, 1, 4, 124001, 101, 1, 0, "お兄ちゃんが来たら\nサンタのお洋服\nいっぱい褒めて\nもらおっと♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6752, 1, 8, 124001, 102, 1, 0, "聖夜をお兄ちゃんと\n過ごせたら…\nアカリもちょっぴり\n大人になれるかも♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6753, 1, 1, 124001, 1, 2, 0, "今のアカリは\nサンタさんですから♪\nなんでもお願いして\nくださいね？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6754, 1, 1, 124001, 2, 2, 0, "お外は寒いですから\nアカリとくっついて\nお出かけしましょ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6755, 1, 1, 124001, 3, 2, 0, "お兄ちゃんは\nアカリのトナカイさん\nですから、ずーっと\nそばにいてください♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6756, 1, 1, 124001, 4, 2, 0, "お兄ちゃん、助けて！\nアカリのカラダで\nラッピング練習したら\n絡まっちゃいました…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6757, 1, 4, 124001, 101, 2, 0, "プレゼント、いっぱい\n準備できました♪\nお兄ちゃんの分は…\nまだヒミツでーす♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6758, 1, 8, 124001, 102, 2, 0, "アカリ、プレゼントは\n大人っぽいのがいいな\n早くお兄ちゃんに\n追いつきたいから♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6759, 1, 1, 124001, 1, 2, 0, "おはようございます♪\nふふ、アカリサンタは\nいい子のお手本なので\n早寝早起きなんです♪", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6760, 1, 1, 124001, 2, 2, 0, "あれ、もうお昼？\nクリスマスナンバーの\n練習してたら\nもうこんな時間…", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6761, 1, 1, 124001, 3, 2, 0, "冬の夜空は星が\nキレイですね～\nお兄ちゃん、一緒に\n見に行きませんか？", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6763, 1, 1, 124001, 2, 3, 0, "はぁ、快適すぎて\nとろけちゃいそう…\nこういうプレゼントも\nいいかもです…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6764, 1, 1, 124001, 3, 3, 0, "やん、かわいいです♪\nえへへ、お兄ちゃんは\nどんなプレゼントが\n欲しいですか～？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6765, 1, 1, 124001, 4, 3, 0, "わわ、大人な香り…\nロマンチックな\nクリスマスが\n過ごせそうです♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6766, 1, 1, 124001, 5, 3, 0, "これでカラダを温めて\nアカリの温もり…\nお兄ちゃんに\nおすそ分けしよっと♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6768, 1, 1, 124001, 7, 3, 0, "お腹がパンパンです…\nでもこれで\nちょっとサンタさんに\n近づけたかも…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6769, 1, 1, 124001, 8, 3, 0, "クリスマスイブの\n恋人との過ごし方…？\nはわわ、アカリには\nまだ早いかもです…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6771, 1, 1, 124001, 14, 3, 0, "わっ、すごいすごい！\n不思議な絵本ですね♪\nこの本でお兄ちゃんに\nイタズラしちゃお♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6772, 1, 1, 124001, 15, 3, 0, "アカリ、ホカホカに\nなっちゃいました～♪\n…ぎゅってしても\nいいですよ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6773, 1, 1, 124101, 1, 1, 0, "またエンジェルになる\nかもとは思ってたけど\n…サンタさん要素の\n追加は想定外だったわ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6774, 1, 1, 124101, 2, 1, 0, "ラフィたちがいない\nときは私がなんとか\nしないとね\n一応…エンジェルだし", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6775, 1, 1, 124101, 3, 1, 0, "部屋の飾りつけとか\nするのも…あり？\nふふ、浮かれすぎかな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6776, 1, 1, 124101, 4, 1, 0, "パーティーだったら\nみんなで大人数向けの\nボードゲームもできる\nかも…やったっ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6777, 1, 1, 124101, 5, 1, 0, "この服のモコモコ…\n気づいたら触っちゃう\n…やるわね、ラフィ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6778, 1, 4, 124101, 101, 1, 0, "アイツと一緒にいると\n人見知りとは違う\n緊張があるのよね…\nって今の聞いてたっ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6779, 1, 8, 124101, 102, 1, 0, "ぬいぐるみ…\nいじめてないわよね？\n…大事にしてるって？\nそれはそれで…あうぅ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6780, 1, 1, 124101, 1, 2, 0, "いい子と悪い子って\nどう見分けるのかしら\n…まさか素直さとか？\nだったら困る！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6781, 1, 1, 124101, 2, 2, 0, "プレゼントといえば\nやっぱりゲーム！\n年末商戦サイコー♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6782, 1, 1, 124101, 3, 2, 0, "サンタさんはお仕事を\n見られちゃ\nいけないのよね？\n…天職？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6783, 1, 1, 124101, 4, 2, 0, "この服は汚れ対策も\nバッチリなのよね\n煙突に入れるように\n…いや私はNGよ！？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6784, 1, 4, 124101, 101, 2, 0, "アンタのお願い…\n今なら一つくらい\n聞いてあげるわよ？\nほら、サンタ衣装だし", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6785, 1, 8, 124101, 102, 2, 0, "私たちって\nなぜかぬいぐるみと\n縁があるわよね\n…ふふ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6786, 1, 1, 124101, 1, 2, 0, "お、おはよっ\n…ふぅ、寝ぐせを\n直すのが間に合って\nよかったぁ", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6787, 1, 1, 124101, 2, 2, 0, "え…一緒にごはん？\nそ、そう…うん。OK\nまあ、せっかくだし？", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6788, 1, 1, 124101, 3, 2, 0, "この時間のゲームって\n妙に楽しいのよね\n…ということで、\nちょっと遊ばない？", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6790, 1, 1, 124101, 2, 3, 0, "ああ～ダメになるぅ…\nもうここでずっと\nゲームしてた～い", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6791, 1, 1, 124101, 3, 3, 0, "プレゼントのために\n研究は必要よね、うん\n…はぁ、かわいい～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6792, 1, 1, 124101, 4, 3, 0, "集中力を高める香りも\nあるのよね…\n勝負をするときには\n持ち歩こうかしら", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6793, 1, 1, 124101, 5, 3, 0, "なんだかまったり\nしちゃうわね…\n時間が溶けるわ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6795, 1, 1, 124101, 7, 3, 0, "お腹いっぱい\n食べられるのも\n幸せなことよね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6796, 1, 1, 124101, 8, 3, 0, "ちっちゃいときは\nアカリと一緒に絵本を\n読んだなぁ…懐かしい", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6798, 1, 1, 124101, 14, 3, 0, "飛び出す絵本…\nこれでゲームブックを\n作ったらおもしろく\nなる気がするわ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6799, 1, 1, 124101, 15, 3, 0, "あったまる…\nモフモフ…ふわふわ…\nちょっと休憩しても…\nいいよね…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6800, 1, 12, 105101, 103, 1, 0, "あら、どうしたの？\nもしかして、私と\n新しい実験を\n始めたくなった？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6801, 1, 12, 105101, 103, 2, 0, "調子が悪いときは\n診療所に来て\n君は特別に、じっくり\n診察してあげるわ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6802, 1, 1, 124201, 1, 1, 0, "特別じゃなくても\nまったり過ごす\nクリスマスも\nいいものだね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6803, 1, 1, 124201, 2, 1, 0, "ニットの帽子に\nマフラーに耳当て…\n素顔用の防寒対策を\n整えなければ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6804, 1, 1, 124201, 3, 1, 0, "靴下が\n見当たらなかったから\n枕元にはかわりに\n鎧を置いておこう", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6805, 1, 1, 124201, 4, 1, 0, "聖女なんて言われる程\n素晴らしくはないけど\n心は聖くいたいものだ\n日々精進だね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6806, 1, 1, 124201, 5, 1, 0, "兜がないから\nイルミネーションが\nいつもよりまぶしく\n感じるよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6807, 1, 4, 124201, 101, 1, 0, "皆の幸福を\n祈願して\nクリスマスリースを\n飾ろうか", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6808, 1, 8, 124201, 102, 1, 0, "私にはかわいすぎる\n衣装だが…開き直って\nクリスマス気分を\n楽しんでしまおう", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6809, 1, 1, 124201, 1, 2, 0, "ツリーの飾りつけ\n手伝ってくれるかい？\nセンスは少年に\nお任せするよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6810, 1, 1, 124201, 2, 2, 0, "パーティーは\n用意してる時も楽しい\nねえ少年、この気持ち\n分かってくれるかい？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6811, 1, 1, 124201, 3, 2, 0, "なんでトナカイの鼻は\n赤いのかって？\n無灯ソリ運転は違反で\n取り締まられるからだ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6812, 1, 1, 124201, 4, 2, 0, "トモちゃんや\nマツリちゃんは\nいい子だから、きっと\nサンタさんに会えるね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6813, 1, 4, 124201, 101, 2, 0, "ヒイラギの髪飾り\n実は気にいってるんだ\n少年にも\n褒めてもらいたいな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6814, 1, 8, 124201, 102, 2, 0, "ねえ少年…そろそろ\n見慣れてほしい\nそんなにまじまじ\n見つめられると照れる", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6815, 1, 1, 124201, 1, 2, 0, "朝日に照らされて\n街がキラキラ白銀に\n輝いてるよ", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6816, 1, 1, 124201, 2, 2, 0, "お昼ご飯を食べて\nちょっと散歩しようか\n祝祭に沸く街は\nとてもきれいだよ", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6817, 1, 1, 124201, 3, 2, 0, "やっぱりクリスマスは\n夜が本番って感じだね\nキャンドルを灯して\n厳かに過ごそうか", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6819, 1, 1, 124201, 2, 3, 0, "鎧姿では無縁だったが\nふかふかしていて\n気持ちいいな…\nふかふか…ふかふか…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6820, 1, 1, 124201, 3, 3, 0, "ふふ、かわいいね\n抱きしめると\nあの雪の日に産まれた\n赤ちゃんを思い出す", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6821, 1, 1, 124201, 4, 3, 0, "兜を脱いだおかげで\nこの香りに出会えた\n…クリスマスの魔法も\n悪くないかもね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6822, 1, 1, 124201, 5, 3, 0, "指先から体の芯まで\n暖まるね\nこれなら雪の日でも\n安心だよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6824, 1, 1, 124201, 7, 3, 0, "もぐ、もぐ…\nあまり食べるところ\n見ないでほしい", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6825, 1, 1, 124201, 8, 3, 0, "素敵なレストランの\n特集記事が載ってる\n聖夜に予約が取れたら\n最高に幸せだろうね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6827, 1, 1, 124201, 14, 3, 0, "わっ！\n…………いや\n驚いてなどいない", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6828, 1, 1, 124201, 15, 3, 0, "ふふ…頬に直接\nふかふかを感じられる\n温かくて気持ちいいよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6829, 1, 1, 124301, 1, 1, 0, "ピアノに読書…\nグレアと一緒に\nなにをしようかな～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6830, 1, 1, 124301, 2, 1, 0, "元の世界へ戻った時\n授業に遅れないよう\nグレアと勉強しとこう", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6831, 1, 1, 124301, 3, 1, 0, "街に魔物料理屋さんが\nあるんだ！　グレアと\n食べに行ってみよう♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6832, 1, 1, 124301, 4, 1, 0, "せっかく新しい魔法を\n覚えたし、グレアにも\n見てもらいたいな！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6833, 1, 1, 124301, 5, 1, 0, "グレアも誘って\nランドソルをもっと\n探索したい！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6834, 1, 4, 124301, 101, 1, 0, "私たちも\nあの人を見習って\nバイトとかしよう！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6835, 1, 8, 124301, 102, 1, 0, "いつかあの人を\n私たちの学院に\n招待したいな！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6836, 1, 1, 124301, 1, 2, 0, "あのね！\nこれからグレアと\n魔法愛好会に\n行くんだ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6837, 1, 1, 124301, 2, 2, 0, "これからなにするの？\n私たちもついて行って\nいいかな！？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6838, 1, 1, 124301, 3, 2, 0, "グレアと一緒に\nあなたも魔法の練習\nしてみない？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6839, 1, 1, 124301, 4, 2, 0, "グレアとよく話すの\nマナリアの制服\nあなたに着せたいって", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6840, 1, 4, 124301, 101, 2, 0, "グレアのそばって\n温かいんだ～！\nぬくぬくだよ～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6841, 1, 8, 124301, 102, 2, 0, "あなたは\nグレアと私の友達！\nこれからもずっと！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6842, 1, 1, 124301, 1, 2, 0, "おはよう！\n今朝はグレアと一緒に\n外を散歩してきたよ！", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6843, 1, 1, 124301, 2, 2, 0, "お腹空いたな…\nよし、グレアと一緒に\n魔物料理を作ろう！", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6844, 1, 1, 124301, 3, 2, 0, "もうこんな時間だね\n明日のために\nそろそろ寝ようかな！", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6846, 1, 1, 124301, 2, 3, 0, "はぁ～この感触…\nグレアの尻尾みたいで\n安らぐ～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6847, 1, 1, 124301, 3, 3, 0, "ぬいぐるみって\nいいよね…私もよく\nグレアに抱き付くんだ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6848, 1, 1, 124301, 4, 3, 0, "いい香り～…\nグレアもこういうの\n好きなんだよ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6849, 1, 1, 124301, 5, 3, 0, "あったかいね～…\nグレアにくっついてる\n時を思い出すよ～…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6851, 1, 1, 124301, 7, 3, 0, "この世界にしかない\nおいしいものが\nたくさんあるよね！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6852, 1, 1, 124301, 8, 3, 0, "世界を繋ぐ魔法の\nヒント…本に書いて\nないかなぁ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6855, 1, 1, 124401, 1, 1, 0, "ピアノに読書…\n今日のアンは\nなにをするのかな…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6856, 1, 1, 124401, 2, 1, 0, "私はアンほど\n勉強はできないから\nちゃんと自習しないと", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6857, 1, 1, 124401, 3, 1, 0, "一度始めたら\nとことん追求するのが\nアンなんだよね…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6858, 1, 1, 124401, 4, 1, 0, "ふふ…夢中になってる\nアンを眺めてるだけで\n一日があっという間…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6859, 1, 1, 124401, 5, 1, 0, "ランドソルは広いね…\nアンと一緒にいろんな\n場所へ行ってみたい", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6860, 1, 4, 124401, 101, 1, 0, "アンやあの人の笑顔を\n見てると、すごく\n安心するんだ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6861, 1, 8, 124401, 102, 1, 0, "もしあの人が私たちの\n学院に編入したら…\nふふ、楽しそうだね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6862, 1, 1, 124401, 1, 2, 0, "アンもあなたも\nすごいな…\nみんなとすぐに\n仲良くなっちゃうから", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6863, 1, 1, 124401, 2, 2, 0, "あなたはこれから\nなにをするの？\nその…アンと一緒に\nついていってもいい？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6864, 1, 1, 124401, 3, 2, 0, "アンから魔法の練習に\n誘われた？　実は私も\n…一緒にどうかな？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6865, 1, 1, 124401, 4, 2, 0, "あなたがマナリアに\n編入したら\n楽しいのになって\nアンとよく話すんだ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6866, 1, 4, 124401, 101, 2, 0, "こっちの世界にも\nアンのように\n私を受け入れてくれる\n人たちがいるんだね…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6867, 1, 8, 124401, 102, 2, 0, "元の世界へ帰る日が\n来たとしても私たちは\n「さよなら」より\n「またね」がいいよね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6868, 1, 1, 124401, 1, 2, 0, "おはよう。さっき\nランドソルの朝日を\nアンと見てきたんだ", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6869, 1, 1, 124401, 2, 2, 0, "私たちは\nこれからなんだけど\nあなたはお昼ごはん\n何を食べたの？", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6870, 1, 1, 124401, 3, 2, 0, "そろそろ\nアンが眠る時間だ…\nあなたもおやすみ", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6872, 1, 1, 124401, 2, 3, 0, "はぁ、疲れが取れる…\nこの尻尾…\nちょっと重いんだよね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6873, 1, 1, 124401, 3, 3, 0, "これ…\nアンに見せたら\n喜ぶだろうな…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6874, 1, 1, 124401, 4, 3, 0, "いい香り…\nアンの感想も\n聞いてみたいな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6875, 1, 1, 124401, 5, 3, 0, "あったかい…\nアンがここにいたら\n本を読みながら\n寝ちゃうんだろうな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6877, 1, 1, 124401, 7, 3, 0, "おいしいからたくさん\n食べれちゃうけど\nまた尻尾…\n大きくなっちゃうかな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6878, 1, 1, 124401, 8, 3, 0, "アンがおススメ\nしてくれた本は\nこれかな…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6881, 1, 1, 124701, 1, 1, 0, "凶、凶、凶…大凶？\nなによこのおみくじ\n自宅用だからって\n排出率が適当すぎよ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6882, 1, 1, 124701, 2, 1, 0, "今年こそ最高の新薬が\nもはや偶然に近い\n最小限の手間で\n作れますように", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6883, 1, 1, 124701, 3, 1, 0, "喉に詰まる前に\n溶けてなくなるお餅…\n最優先の研究課題だわ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6884, 1, 1, 124701, 4, 1, 0, "よし、完成♪\nミツキ特製…\n「新解釈おせち料理」\n医学的根拠が満載よ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6885, 1, 1, 124701, 5, 1, 0, "お年玉を彼に…\nあげるべき…？\n色んな意味で微妙ね…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6886, 1, 4, 124701, 101, 1, 0, "ふふ、今年もまた一年\n彼のバイタルを\n記録できるのね♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6887, 1, 8, 124701, 102, 1, 0, "今年こそ\n私の薬で彼を魔改造…\nううん、大人の男に！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6888, 1, 1, 124701, 1, 2, 0, "コマ回しのコマを\n見てたら目が回った？\nそう…手当は不要よ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6889, 1, 1, 124701, 2, 2, 0, "私の顔で\n福笑いを作った？\nあら…隻眼まで\n再現度が高いわね♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6890, 1, 1, 124701, 3, 2, 0, "門松よし、しめ縄よし\nこんなギルドでもね\n一応縁起を担ぐのよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6891, 1, 1, 124701, 4, 2, 0, "本気の薬草を煮込んだ\n本気の七草粥よ\n色は気にしちゃダメ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6892, 1, 4, 124701, 101, 2, 0, "君が今年も健康で\nつつがなく…実験台を\n務められますように", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6893, 1, 8, 124701, 102, 2, 0, "いくつ新年を迎えても\n俗世の垢にまみれない\nそんな君こそが…\n私には最高のお薬よ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6894, 1, 1, 124701, 1, 2, 0, "お正月なんだから\nいつまでも寝てないで\nさぁ、実験開始よ♪", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6895, 1, 1, 124701, 2, 2, 0, "カルタも双六もしたし\nあとは実験くらいしか\nやることがないわね", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6896, 1, 1, 124701, 3, 2, 0, "日頃からたいがい\n意味深な夢を見るけど\n初夢はまた別かしら…", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6898, 1, 1, 124701, 2, 3, 0, "さすがにこの格好で\n寝正月を敢行するのは\nためらわれるわね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6899, 1, 1, 124701, 3, 3, 0, "や、闇医者たる者\nこんな愛くるしい物に\n心奪われるわけには…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6900, 1, 1, 124701, 4, 3, 0, "お雑煮のいい香り…\n食欲が増進されるわ\nアロマも立派なお薬ね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6901, 1, 1, 124701, 5, 3, 0, "胸元は閉じないわよ？\nでも時々ここで\nこっそり暖まるの…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6903, 1, 1, 124701, 7, 3, 0, "無から有が生まれてる\n新年早々物理の法則を\n無視してくるのね…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6904, 1, 1, 124701, 8, 3, 0, "どれもこれも読んだわ\n新しい婚活本の\n入荷はまだかしら…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6906, 1, 1, 124601, 1, 1, 0, "彼が一生懸命\n書いてくれた年賀状\n気持ちがこもっていて\nすごくうれしいわ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6907, 1, 1, 124601, 2, 1, 0, "しっかり丁寧に\n大掃除したから\nとってもいい気持ちで\n新年を迎えられたわ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6908, 1, 1, 124601, 3, 1, 0, "彼の着物姿\nきっと凛々しいわよね\n想像しただけで\n笑顔になるわ～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6909, 1, 1, 124601, 4, 1, 0, "お雑煮は地域によって\n違いがあるけれど\n愛情が隠し味なのは\nどこも一緒よね♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6910, 1, 1, 124601, 5, 1, 0, "彼と凧揚げして\n遊びたいけど\n森では引っかかって\nできないかしら…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6911, 1, 4, 124601, 101, 1, 0, "今年も彼が素直に\n成長できるように\nたくさんいい子いい子\nしてあげましょう…♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6912, 1, 8, 124601, 102, 1, 0, "絵馬に彼と\n書いた通り\n今年もみんなが笑って\n過ごせますように", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6913, 1, 1, 124601, 1, 2, 0, "先生と初詣に\n行きましょう〜♪\n迷子にならないように\n手を繋いで、ね？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6914, 1, 1, 124601, 2, 2, 0, "初売りセールいいわね\nでも気を付けないと\nあれこれ欲しくなって\n買い過ぎちゃうかも…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6915, 1, 1, 124601, 3, 2, 0, "風邪をひかないように\n温かくするんですよ～\nえ？　先生、温めて？\nふふ、甘えん坊さんね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6916, 1, 1, 124601, 4, 2, 0, "あなたのために\nちょっと豪華なおせち\n用意したのよ\nお口に合うかしら？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6917, 1, 4, 124601, 101, 2, 0, "あなたも着物着たい？\nふふ、貫禄出ちゃって\n先生ドキドキ\nしちゃうかも…♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6918, 1, 8, 124601, 102, 2, 0, "あなたの成長は\nとってもうれしいけど\n今年もまだ、あなたの\n先生でいたいな…♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6919, 1, 1, 124601, 1, 2, 0, "あら、起きたの？\nお雑煮できてるから\nちゃんとお顔\n洗ってらっしゃいね", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6920, 1, 1, 124601, 2, 2, 0, "お昼ご飯を\nしっかり食べて\nみんなでかるた大会\nしましょう〜♪", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6921, 1, 1, 124601, 3, 2, 0, "は～い、良い子は\nもう寝る時間ですよ\n素敵な初夢が\n見られますように♪", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6923, 1, 1, 124601, 2, 3, 0, "このフカフカちゃんも\n天日干ししたいけど…\nその前にちょっと\n休憩しちゃおうかしら", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6924, 1, 1, 124601, 3, 3, 0, "うふふ、かわいい～\nアオイちゃんや\nハツネちゃんも\nきっと気に入るわね♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6925, 1, 1, 124601, 4, 3, 0, "お正月は空気も\n透き通ってるから\nいつも以上に\n素敵な香りに感じるわ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6926, 1, 1, 124601, 5, 3, 0, "火のぬくもりに\n抱かれていると\n不思議と眠気が…\nふああ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6928, 1, 1, 124601, 7, 3, 0, "愛情のこもった料理は\nおいしいだけじゃなく\n心も満たされちゃう♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6929, 1, 1, 124601, 8, 3, 0, "お正月のお作法も\nたくさんあるのね～\n先生も日々勉強だわ～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6931, 1, 12, 104701, 103, 1, 0, "少年への感謝をどんな\n形で伝えるべきか…\n言葉よりも物の方が\n良いのだろうか？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6932, 1, 12, 104701, 103, 2, 0, "話し相手になって\nもらってもいいかな？\n少年と一緒にいると\nとても楽しいんだ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6933, 1, 1, 124501, 1, 1, 0, "お正月だからって\nギャングのボスは\n油断できないよ～？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6934, 1, 1, 124501, 2, 1, 0, "イノリちゃんがお年玉\n欲しいんだって♪\n悪い子だったし\n10ルピでいいかな☆", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6935, 1, 1, 124501, 3, 1, 0, "お正月だからお雑煮を\n作ったんだけど\nカヤちゃんには\n大好評だったよ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6936, 1, 1, 124501, 4, 1, 0, "ラーゴがお餅を\n食べたがってるけど…\nこの子にあげるのは\nちょっと心配かな～？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6937, 1, 1, 124501, 5, 1, 0, "お正月は晴れ着の子が\nやっぱり多いよね～\n今度、私の漫画でも\n出してみようかな？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6938, 1, 4, 124501, 101, 1, 0, "君と二人っきりで\n神殿に初詣…\n行ってみたいな～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6939, 1, 8, 124501, 102, 1, 0, "君とお正月を\n過ごしていると\n面倒なことも\n全部忘れられそう…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6940, 1, 1, 124501, 1, 2, 0, "この着物、どう思う？\n似合ってたら\nうれしいけどな～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6941, 1, 1, 124501, 2, 2, 0, "袖を引っ張ったり\nしないでね？\n着崩れしちゃうから♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6942, 1, 1, 124501, 3, 2, 0, "お正月ならではの\n漫画ネタってある？\n参考にしたいんだよね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6943, 1, 1, 124501, 4, 2, 0, "昔は凧あげとか\n駒回しとかしてたけど\n大人になるとそういう\n機会も減るよね…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6944, 1, 4, 124501, 101, 2, 0, "君にもお年玉を\nあげようか？\n好きな額を言ってね☆", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6945, 1, 8, 124501, 102, 2, 0, "来年も、再来年も…\nこうしてみんなで\n新年がお祝いできると\n素敵だね～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6946, 1, 1, 124501, 1, 2, 0, "おはよ～♪\n朝ごはんは\n焼いたお餅とか\nどうかな？", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6947, 1, 1, 124501, 2, 2, 0, "お昼だね～\nおせちを用意したから\nみんなで食べよっか", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6948, 1, 1, 124501, 3, 2, 0, "もうこんな時間だね～\n一日中着物だったから\n肩が凝っちゃったよ", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6950, 1, 1, 124501, 2, 3, 0, "このまま寝たら\nしわになるかな？\n着替えた方がいい？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6951, 1, 1, 124501, 3, 3, 0, "こういうのって\nラーゴのおもちゃに\nちょうどいいかも♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6952, 1, 1, 124501, 4, 3, 0, "いい香りだけど\n着物に移らないように\n気をつけないとね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6953, 1, 1, 124501, 5, 3, 0, "ちょうどいい\n火加減だね♪\nぽかぽかするよ～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6955, 1, 1, 124501, 7, 3, 0, "着物の時は\nほどほどにしないと…\n欲張ったら、お腹が\n苦しくなるからね～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6956, 1, 1, 124501, 8, 3, 0, "ふーん\nイノリちゃんは最近\nこんな漫画を\n読んでるんだ～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6958, 1, 1, 124801, 1, 1, 0, "楽園の気配は…ナシ！\nみんな前を向いてるね\nそうこなくっちゃ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6959, 1, 1, 124801, 2, 1, 0, "レッスンの成果も\nこの格好くらい\n目に見えたら\n分かりやすいのになぁ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6960, 1, 1, 124801, 3, 1, 0, "次の曲はワイルドで\n悪い感じもいいなぁ…\nカヤとムイミちゃん\nみたいなイメージで！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6961, 1, 1, 124801, 4, 1, 0, "私…服、着てるよね？\nうんうん、着てる！\nあんまり体が軽いと\n急に焦る瞬間あるな～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6962, 1, 1, 124801, 5, 1, 0, "最近ファンの年齢層が\n幅広くなってきたかも\nこれって私…国民的\nアイドルに近づいた？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6963, 1, 4, 124801, 101, 1, 0, "パティスリーとコラボ\nしたらどんな感じかな\n…ノゾミンマカロン♪\nとか？　…いけるかも", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6964, 1, 8, 124801, 102, 1, 0, "彼と一緒にいると…\nうれしくって苦しいな\nでも両方諦められない\n…アイドルらしいや", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6965, 1, 1, 124801, 1, 2, 0, "この格好、チカと\nツムギも似合うと\n思うんだけどな～\nキミもそう思うよね？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6966, 1, 1, 124801, 2, 2, 0, "街を歩いてたらね？\nライブとか写真集の\n告知イベントだって\n思われちゃった！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6967, 1, 1, 124801, 3, 2, 0, "私が持ってた剣に\n結んであったリボン…\n何か気づいてくれた？\nヒントは…色だよ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6968, 1, 1, 124801, 4, 2, 0, "キミの気配ってすごく\n分かりやすいんだよ\n近くにいたらすぐに\nピンとくるんだ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6969, 1, 4, 124801, 101, 2, 0, "あ、それ…はじめて\nキミにあげた音響盤！\nね、一緒に聴こうよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6970, 1, 8, 124801, 102, 2, 0, "ぼうっとしてたら\nイタズラしちゃうよ♪\nもっともっと私のこと\n見てないとダーメ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6971, 1, 1, 124801, 1, 2, 0, "朝は今日のはじまり！\n昨日よりももっと\nいい一日にしようね♪", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6972, 1, 1, 124801, 2, 2, 0, "レッスンがんばったし\nまだお昼だし…\nランチ大盛りにしても\nセーフだよね！", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6973, 1, 1, 124801, 3, 2, 0, "寝る前に明日の予定を\n確認しておかないと！\nえっと、集合場所は…", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6975, 1, 1, 124801, 2, 3, 0, "一休みのつもりが\nなが～い休憩になるの\nあるあるだよね～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6976, 1, 1, 124801, 3, 3, 0, "ノゾミンの熱烈ハグ♪\n…ここだけ聞いたら\nスクープになっちゃう\nかも？　なーんて！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6977, 1, 1, 124801, 4, 3, 0, "香りってなんだか\n記憶に残るよねー\n…こういうの私だけ？\nみんなも同じだよね？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6978, 1, 1, 124801, 5, 3, 0, "なんだかライブの\n熱気を思い出して\nきちゃった…！\nレッスンしよっと！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6980, 1, 1, 124801, 7, 3, 0, "お腹が減ってたら\nいいパフォーマンスは\nできないもんね！\n…んー、おいし～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6981, 1, 1, 124801, 8, 3, 0, "いい歌詞を書くには\nしっくりくる言葉を\n知っておかないと！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6983, 1, 1, 124901, 1, 1, 0, "うだうだ悩んでる\nヤツがいたら\nオレのとこ連れてこい\n背中押してやるからさ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6984, 1, 1, 124901, 2, 1, 0, "最近どことなく\nファミリーの連中が\n浮き足立ってんだよな\n…なんでだ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6985, 1, 1, 124901, 3, 1, 0, "イノリのヤツ\nやたらと脇腹\nつついてきやがって…\nくすぐったいっての", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6986, 1, 1, 124901, 4, 1, 0, "身軽になったのは\nいいが…くそっ\nどこもかしこも\nスースーする…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6987, 1, 1, 124901, 5, 1, 0, "思い出に浸るのも\n悪くはないが\nやっぱ人間\n未来に生きないとな！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6988, 1, 4, 124901, 101, 1, 0, "またムイミやノゾミと\nツルむのもいいな\nそこにカレもいたら\nなおいいぜ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6989, 1, 8, 124901, 102, 1, 0, "未来のことを考えると\nなぜかいつもカレが\n頭に浮かんじまう…\nええい、やめだやめ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6990, 1, 1, 124901, 1, 2, 0, "ちょっくらオレと\n腕相撲しねえか？\nこの新しい力\n試させてくれ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6991, 1, 1, 124901, 2, 2, 0, "なあ、悪いが上着\n貸してくれないか？\nこのカッコ、割と\n肌寒いんだよ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6992, 1, 1, 124901, 3, 2, 0, "おっ、生まれ変わった\n喧嘩屋にご用命か？\nいいぜ、キミの\n依頼なら二つ返事だ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6993, 1, 1, 124901, 4, 2, 0, "なんか悩みごとが\nあるなら、なんでも\n話してくれよな\nオレとキミの仲だろ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6994, 1, 4, 124901, 101, 2, 0, "このカッコが\nきれいだって？\nや、やめろよ…\n照れくさいだろ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6995, 1, 8, 124901, 102, 2, 0, "キミがいると、なにを\nするにも退屈しないぜ\nだから今日も\n付き合ってくれよな！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6996, 1, 1, 124901, 1, 2, 0, "おはよう！　新しい\n未来がやってきたぜ！\n一秒だって時間は\n無駄にするなよ？", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6997, 1, 1, 124901, 2, 2, 0, "そろそろ昼飯に\nしないか？\n穴場のうまい店に\n案内してやるからさ", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (6998, 1, 1, 124901, 3, 2, 0, "夜更かしは\n未来の前借りだ\n体壊さないように\nほどほどにしとけよ？", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7000, 1, 1, 124901, 2, 3, 0, "お、いいなこれ\nファミリーの\nアジトにも\n置いときたいぜ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7001, 1, 1, 124901, 3, 3, 0, "べ、別にかわいいもん\nなんて、そんなに\n好きじゃないし…\n嫌いでもないけどさ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7002, 1, 1, 124901, 4, 3, 0, "嗅いだことない\n香りだな\nなかなか気に入った！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7003, 1, 1, 124901, 5, 3, 0, "薄着で暖を取るのって\n最高にぜいたくだよな\nへへっ、堪能させて\nもらうとするか", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7005, 1, 1, 124901, 7, 3, 0, "こいつはいいや\nこのカッコだと\n消耗が激しくて\nやたら腹が減るからな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7006, 1, 1, 124901, 8, 3, 0, "甘辛コーデ…\nへぇ、そんなのが\nあるのか…\nわ、悪くねえかもな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7008, 1, 12, 105501, 103, 1, 0, "はぁ…\nもっともっと長い時間\n先輩と過ごせたらなぁ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7009, 1, 12, 105501, 103, 2, 0, "えへへ…♪\n先輩が、すぐそばに\nいてくれるなんて…\n幸せだなぁ…♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7010, 1, 1, 125001, 1, 1, 0, "はーっはっは！\n強くてカッコいい\n大悪党、ここに参上！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7011, 1, 1, 125001, 2, 1, 0, "みんなおしゃれで\nカッコいいけど\n今のアタシも\n負けてないぞ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7012, 1, 1, 125001, 3, 1, 0, "ギャングのアジトも\nよかったけど\nここも賑やかで\n悪くないな！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7013, 1, 1, 125001, 4, 1, 0, "悪党にも休息は必要だ\nここならゆっくり\n休めそうだ！\n邪魔するぞ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7014, 1, 1, 125001, 5, 1, 0, "いっぱい人がいるな…\nアタシの悪党っぷりを\n広めるチャンスだ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7015, 1, 4, 125001, 101, 1, 0, "ここにいれば\nいい思い出がたくさん\n作れそうだな！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7016, 1, 8, 125001, 102, 1, 0, "いつかアタシも\nオクトーと一緒に\nこんなアジトを手に\n入れてやるからな！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7017, 1, 1, 125001, 1, 2, 0, "ん？　アタシの格好が\n気になるのか？\nならもっと近くで\n見てもいいぞ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7018, 1, 1, 125001, 2, 2, 0, "おい、暇だから\nアタシと遊べ！\nカヤとノゾミも呼んで\n大騒ぎするぞ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7019, 1, 1, 125001, 3, 2, 0, "オマエ、前より強く\nなってないか！？\nくっそー！　アタシも\n負けてらんないぞー！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7020, 1, 1, 125001, 4, 2, 0, "今日は特別にアタシの\n思い出話を聞かせて\nやる！　さーて、どの\n悪事から話そうか…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7021, 1, 4, 125001, 101, 2, 0, "オマエとの付き合いも\n結構長くなったよな？\nこれからも\nよろしく頼むぞ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7022, 1, 8, 125001, 102, 2, 0, "オマエまでいなく\nなったらアタシが困る\n勝手にどっか\n行くんじゃないぞ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7023, 1, 1, 125001, 1, 2, 0, "起きろ、起きろー！\n大悪党の朝は早いんだ\nさっさと\n顔洗ってこい！", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7024, 1, 1, 125001, 2, 2, 0, "昼飯どうしようか…\nそうだ！　オマエの\nおススメに連れてけ！\nいい店期待してるぞ！", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7025, 1, 1, 125001, 3, 2, 0, "明日を思い切り\n楽しむためにも\n早く寝とけよ！\nまた明日な！", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7027, 1, 1, 125001, 2, 3, 0, "こうやって座ると…\nおお！　すっごく悪党\nっぽいぞ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7028, 1, 1, 125001, 3, 3, 0, "オマエ、仲間に\nなりたそうな目を\nしてるな！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7029, 1, 1, 125001, 4, 3, 0, "うん、いい匂いだ！\n何の匂いかは全然\n分かんないけどな！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7030, 1, 1, 125001, 5, 3, 0, "炎ってなんだか\nカッコいいよな！\n見てるだけで心が\n燃えてくるぞ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7032, 1, 1, 125001, 7, 3, 0, "いっぱい食べれば\nちょっとは\n背が伸びるかな…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7033, 1, 1, 125001, 8, 3, 0, "ぐぬぅ…たまには\n本でもと思ったけど\nやっぱりアタシは\nバトル担当だな！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7035, 1, 1, 118201, 1, 1, 0, "お邪魔しまーす★\nなーんてもう\nすっかりくつろいで\nますけどー♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7036, 1, 1, 118201, 2, 1, 0, "騎士さんの\nギルドハウス…★\nなんだかドキドキ\nしちゃいます♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7037, 1, 1, 118201, 3, 1, 0, "ぺんぽこりんの\n置き場所はー…\nま、このへんで★", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7038, 1, 1, 118201, 4, 1, 0, "うーん、これは\n壊しがいがありそ…\nウソウソ、冗談です★", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7039, 1, 1, 118201, 5, 1, 0, "オトモダチでも\n作ってみますかー\nくすっ★", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7040, 1, 4, 118201, 101, 1, 0, "退屈ですねー\n騎士さん、構って\nくれないかなー", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7041, 1, 8, 118201, 102, 1, 0, "もっともっと早く\n騎士さんに逢えてたら\n…なんて、わたし\nらしくないですね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7042, 1, 1, 118201, 1, 2, 0, "わたし一人にするの\n不安だったりします？\nちゃーんとイイコに\nしてますってー★", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7043, 1, 1, 118201, 2, 2, 0, "お出かけします？\nそれともおうちで\nまったりしますー？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7044, 1, 1, 118201, 3, 2, 0, "女の子をすぐ家に\nあげちゃうなんて…\nふふ、やっぱり隅に\n置けないですねー★", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7045, 1, 1, 118201, 4, 2, 0, "騎士さん騎士さーん★\nウザ絡みしても\nいいですかー？\n騎士さんってばー★", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7046, 1, 4, 118201, 101, 2, 0, "一緒にゴロゴロ\nしませんかー？\nコイビトっぽくて\n楽しそうですし♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7047, 1, 8, 118201, 102, 2, 0, "うふふ★\nこーやってあなたに\n優しくされると…\nときめいちゃいます♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7048, 1, 1, 118201, 1, 2, 0, "おはようございます★\nあれれー？\nまだおねむですかー？", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7049, 1, 1, 118201, 2, 2, 0, "騎士さん、お昼は\nもう食べましたー？\nわたし、ラーメンが\n食べたいでーす★", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7050, 1, 1, 118201, 3, 2, 0, "あ、夜更かししてちゃ\nダメですよー？\nほらほら、お布団に\nごーごー★", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7052, 1, 1, 118201, 2, 3, 0, "ふかふかのふわふわー\nついついまーったり\nしちゃいます★", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7053, 1, 1, 118201, 3, 3, 0, "かっわいー★\n名前はー、んー…\nぱんぱかぷんで★", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7054, 1, 1, 118201, 4, 3, 0, "くんくん★\nうーん…\nたぶん素敵な香り♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7055, 1, 1, 118201, 5, 3, 0, "あったかーい★\n心も体も\nぽっかぽかですねー★", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7057, 1, 1, 118201, 7, 3, 0, "もぐもぐ★\nんー、おいしくって\n止まらないですねー★", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7058, 1, 1, 118201, 8, 3, 0, "へー★\nほほー★\nふんふーん★", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7060, 1, 1, 125101, 1, 1, 0, "さ～て時間もあるし\nラテアートの練習でも\nするかにゃ～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7061, 1, 1, 125101, 2, 1, 0, "カフェの猫たちも\n元気に働いてくれて\nお客さんからの評判も\n上々にゃ～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7062, 1, 1, 125101, 3, 1, 0, "カフェのカウンターに\nファントムキャッツの\n目安箱を置こうかにゃ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7063, 1, 1, 125101, 4, 1, 0, "あんこもコーヒーも\n原料は豆にゃ～\nそりゃ相性いいに\n決まってるにゃ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7064, 1, 1, 125101, 5, 1, 0, "あいつの為に\nタマキ特製ブレンドを\n開発したいにゃ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7065, 1, 4, 125101, 101, 1, 0, "あいつと働いてると\nなぜかあっという間に\n閉店時間が\n来ちゃうのにゃ～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7066, 1, 8, 125101, 102, 1, 0, "あいつといると\n新しいアイデアが\n溢れるほど\n出てきちゃうのにゃ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7067, 1, 1, 125101, 1, 2, 0, "キミがコーヒーを\n淹れてくれたのにゃ？\nにゃはは、常温で\n猫舌にも優しいにゃ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7068, 1, 1, 125101, 2, 2, 0, "料理が上手くなったら\nカフェご飯メニューも\n充実させたいにゃ～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7069, 1, 1, 125101, 3, 2, 0, "お店の経営は大変\nだけど、キミとなら\n乗り越えられるって\n確信してるにゃ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7070, 1, 1, 125101, 4, 2, 0, "ん？　この制服\n似合ってるにゃ？\nにゃはは！　褒めても\n１ルピも出ないにゃ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7071, 1, 4, 125101, 101, 2, 0, "あたしが好きなのは\n猫に好かれる人にゃ♪\n…ん、キミ猫に懐かれ\nまくってるにゃ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7072, 1, 8, 125101, 102, 2, 0, "キミの制服姿\nけっこーイケてるにゃ\nあたしがお客さんなら\n通いつめちゃうにゃ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7073, 1, 1, 125101, 1, 2, 0, "朝は一杯の\nコーヒーから始まる…\nにゃふふん♪\nシャレた朝だにゃ～♪", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7074, 1, 1, 125101, 2, 2, 0, "お昼はたい焼きと\nコーヒーのセットで\nまったり休憩\nしようにゃ～♪", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7075, 1, 1, 125101, 3, 2, 0, "コーヒー淹れたにゃ\n星空を見ながら\n二人で飲もうにゃ…♪", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7077, 1, 1, 125101, 2, 3, 0, "ふにゃ～ん…ゴロゴロ\nコーヒーと同じくらい\n癒されるにゃ～…♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7078, 1, 1, 125101, 3, 3, 0, "こいつかわいいにゃ～\nうちの猫たちとも\nいい勝負なのにゃ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7079, 1, 1, 125101, 4, 3, 0, "ん～いい香りだにゃ～\nコーヒーもそうだけど\n香りの癒し効果は\n抜群にゃ～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7080, 1, 1, 125101, 5, 3, 0, "猫に暖かいものは\nまるまれと言ってる\nようなもんにゃ…♪\nまるまる…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7082, 1, 1, 125101, 7, 3, 0, "にゃは～うまいにゃ♪\nカフェの新メニューに\n採用しちゃおう\nかにゃ～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7083, 1, 1, 125101, 8, 3, 0, "にゃるほど…\n調べれば調べるほど\nコーヒーは\n奥が深いのにゃ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7085, 1, 12, 107101, 103, 1, 0, "ワタシの手料理は\n坊やの口に合うらしい\n胃袋は討ち取ったぞ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7086, 1, 12, 107101, 103, 2, 0, "胸に再度触れると\n誓約は無効になるのか\n…だと？　鋭い質問だ\n安心しろ。ならん☆", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7087, 1, 1, 125201, 1, 1, 0, "ようやくこの服にも\n慣れてきたかな？\nふふっ、たまには\nこういう服も悪くない", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7088, 1, 1, 125201, 2, 1, 0, "今日も店は好調だが、\n私は軍人だからな\n当初の目的も\n忘れないようにしよう", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7089, 1, 1, 125201, 3, 1, 0, "猫が近くに来ると\nついつい見てしまうな\n仕事が終わったら\n遊んでやるかな♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7090, 1, 1, 125201, 4, 1, 0, "コーヒーの淹れ方が\nうまくなってきたな♪\nどうせならお菓子も\n研究してみるか！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7091, 1, 1, 125201, 5, 1, 0, "利益を考えると\n価格をあげるべきか？\n…いや、利益よりも\n皆の笑顔の方が大事だ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7092, 1, 4, 125201, 101, 1, 0, "またあいつを\n呼んでしまったな…\n今度、コーヒーでも\n淹れて労ってやるか♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7093, 1, 8, 125201, 102, 1, 0, "次に入荷するお菓子は\nあいつの意見を聞くか\nどんな味が好きか\n気になるしな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7094, 1, 1, 125201, 1, 2, 0, "なに、コーヒーを\n淹れてほしい？\nふふっ♪\n仕方のない奴だな！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7095, 1, 1, 125201, 2, 2, 0, "…そうだ。\nコーヒーと乾パンは\n合うのではないか？\n今度試してみよう！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7096, 1, 1, 125201, 3, 2, 0, "コーヒーもいいが、\nココアも悪くないぞ♪\n貴公も一緒にどうだ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7097, 1, 1, 125201, 4, 2, 0, "魔法少女に比べれば\n大人しい服だろう？\nだが、カフェには\nこの服でバッチリだ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7098, 1, 4, 125201, 101, 2, 0, "貴公はコーヒーを\n飲めるのかっ？それは\nうらやましいな…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7099, 1, 8, 125201, 102, 2, 0, "貴公の働きぶりには\n目を見張るものがある\nその姿は、その…\n結構かっこいいぞっ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7100, 1, 1, 125201, 1, 2, 0, "さぁ、今日も一日、\n頑張るぞっ！\n気合いを入れて\n開店準備だっ！", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7101, 1, 1, 125201, 2, 2, 0, "昼は稼ぎ時だからな！\n荒波を乗り切るため\n貴公も一緒に\n頑張ってくれるか？", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7102, 1, 1, 125201, 3, 2, 0, "ふあぁ…もう夜か\n今日は頑張ったからな\nはやめに休むとしよう", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7104, 1, 1, 125201, 2, 3, 0, "はふぅ～…\nたまにはこうして\nゆったりするのも\n悪くないな…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7105, 1, 1, 125201, 3, 3, 0, "猫もかわいいが、\nこいつもなかなか…\n見ていると、\n撫でたくなるな♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7106, 1, 1, 125201, 4, 3, 0, "これは良い香りだな\nふむ、コーヒーも\nより香り高いものが\n作れるよう試すか！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7107, 1, 1, 125201, 5, 3, 0, "ふぅ～…\n体を温めなければ、\nすぐに動けないからな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7109, 1, 1, 125201, 7, 3, 0, "これもうまいが、\n私はお菓子も…\nいや、食べすぎは\n体に良くないな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7110, 1, 1, 125201, 8, 3, 0, "…書物は参考になるな\n次はこのやり方を\n試してみるかな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7112, 1, 1, 120001, 1, 1, 0, "愛と希望の魔法少女！\n弟くんの平和は\nお姉ちゃんが守るよ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7113, 1, 1, 120001, 2, 1, 0, "魔法少女の戦い方を\n見せてあげる！\nお姉ちゃんチョップ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7114, 1, 1, 120001, 3, 1, 0, "闇の力が高まってる…\n早く弟くんを\nギュ～ってしないと！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7115, 1, 1, 120001, 4, 1, 0, "ミニ弟くんを\n部屋中に呼び出して\n弟くんハウスに～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7116, 1, 1, 120001, 5, 1, 0, "決めポーズの練習でも\nしよっかな…\nはい、ポーズ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7117, 1, 4, 120001, 101, 1, 0, "魔法少女の力で\n弟くんのそばに\n瞬間移動したいなぁ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7118, 1, 8, 120001, 102, 1, 0, "禁じられた必殺技\nその名も──\n闇のお姉ちゃん頭突き", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7119, 1, 1, 120001, 1, 2, 0, "魔法少女な\nお姉ちゃんに\n興味津々だねっ？\n全部教えてあげるね♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7120, 1, 1, 120001, 2, 2, 0, "闇のお姉ちゃんとして\n世界を弟くんのものに\n…な～んて冗談冗談♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7121, 1, 1, 120001, 3, 2, 0, "弟くんって魔法少女に\nなりたがってたよね？\n…うん。応援するね！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7122, 1, 1, 120001, 4, 2, 0, "サインが欲しいの？\nふふっ、任せて！\n愛する弟くんへ、と♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7123, 1, 4, 120001, 101, 2, 0, "ふんふふ～ん♪\n魔法少女お姉ちゃんの\nテーマソング完成っ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7124, 1, 8, 120001, 102, 2, 0, "えへへ、弟くんからの\n愛と希望を感じるよ…\n更に変身しちゃうぞ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7125, 1, 1, 120001, 1, 2, 0, "弟くんの朝を魔法で\n守りに来たよ！\n希望の朝だねっ♪", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7126, 1, 1, 120001, 2, 2, 0, "弟くんのお昼を魔法で\n守りに来たよ！\n愛のお弁当をどうぞ♪", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7127, 1, 1, 120001, 3, 2, 0, "弟くんの夜を魔法で\n守りに来たよ！\nここからは闇の時間♪", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7129, 1, 1, 120001, 2, 3, 0, "魔法少女の一休み♪\n愛と希望と…闇の力が\n回復していくね～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7130, 1, 1, 120001, 3, 3, 0, "かわいいかわいい♪\nお姉ちゃんの魔法で\n増やしちゃうぞ…♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7131, 1, 1, 120001, 4, 3, 0, "この香り…\n弟くんと同じ…\nくんくん、くんくん…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7132, 1, 1, 120001, 5, 3, 0, "体を温めておいて…\nこの熱を、ぜ～んぶ\n弟くんに伝えるよ…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7134, 1, 1, 120001, 7, 3, 0, "ミニ弟くんを呼ぶには\nいっぱい食べないと…\n魔法ってお腹減るから", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7135, 1, 1, 120001, 8, 3, 0, "弟くんの平和を守る…\nそのためには\nチェックチェック…♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7137, 1, 1, 125301, 1, 1, 0, "この服も、最初は\n慣れなかったのですが\n今ではすっかり\n体に馴染んでおります", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7138, 1, 1, 125301, 2, 1, 0, "自然を守るためにも\nお勉強は欠かせません\n日々精進でございます", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7139, 1, 1, 125301, 3, 1, 0, "採取した薬草を\n調合しておきましょう\nいつ必要になるか\n分かりませんからね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7140, 1, 1, 125301, 4, 1, 0, "合宿は過酷でしたが\nおかげでちょっと\n力持ちになったような\n気がいたします…♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7141, 1, 1, 125301, 5, 1, 0, "わんわんのほうの\n主さまに、ごはんを\nあげる時間ですね…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7142, 1, 4, 125301, 101, 1, 0, "もっともっと訓練や\nお勉強を重ねて\n主さまのお役に\n立たなくては…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7143, 1, 8, 125301, 102, 1, 0, "おや、そろそろ\nわんわんのほうの\n主さまをお散歩に\n連れていく時間です", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7144, 1, 1, 125301, 1, 2, 0, "主さま、よろしければ\nお散歩に行きませんか\n今日の森は穏やかで\n過ごしやすいかと", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7145, 1, 1, 125301, 2, 2, 0, "わんわんのほうの\n主さまも、今では\n【美食殿】の大切な\n仲間でございます", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7146, 1, 1, 125301, 3, 2, 0, "森には危険が\nいっぱいでございます\nケガをなさらないよう\nお気をつけください", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7147, 1, 1, 125301, 4, 2, 0, "もし森や動物のことで\n気になることがあれば\n気軽にお尋ねください", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7148, 1, 4, 125301, 101, 2, 0, "森へ行く前には\n必ず装備の点検を\nお願いいたします\n約束ですよ、主さま？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7149, 1, 8, 125301, 102, 2, 0, "森の中で主さまたちと\nゆったり過ごす時間は\nわたくしにとって\n大切なひとときです", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7150, 1, 1, 125301, 1, 2, 0, "おはようございます\n朝ごはんをしっかり\n食べて、お勤めに\n備えましょう", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7151, 1, 1, 125301, 2, 2, 0, "わんわんのほうの\n主さまにおやつを\nあげる時間です\nどうぞ、召し上がれ", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7152, 1, 1, 125301, 3, 2, 0, "どれだけ野営に\n慣れたとしても\nベッドで眠れるなら\nそうしたいところです", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7154, 1, 1, 125301, 2, 3, 0, "こちらなのですが\nわんわんのほうの\n主さまもお気に入りの\nようです", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7155, 1, 1, 125301, 3, 3, 0, "わんわんのほうの\n主さまにも、こうした\n玩具を与えるべき\nでしょうか…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7156, 1, 1, 125301, 4, 3, 0, "心地よい香りです\n次は、自然由来の\n香料も試しましょう", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7157, 1, 1, 125301, 5, 3, 0, "暖をとるのは\n野営の時などに\nとても大事なことで\nございますね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7159, 1, 1, 125301, 7, 3, 0, "食べられる時に食べて\nおくのも、大自然では\n重要でございます", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7160, 1, 1, 125301, 8, 3, 0, "より知識を蓄え\n自然の力を活用できる\nようにいたしましょう", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7162, 1, 12, 100901, 103, 1, 0, "今回の「冥風戦記」も\nなかなかよく書けたぞ\nふふっ、シグルドも\n喜んでくれるかな？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7163, 1, 12, 100901, 103, 2, 0, "貴様のためならば\n我が真なる魔力\nいつでも\n解き放とうぞ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7164, 1, 1, 125401, 1, 1, 0, "ニャットちゃんに\nブラッシングを\nしてあげようかな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7165, 1, 1, 125401, 2, 1, 0, "ふふ…\nこれがレンジャーの\n制服ですか", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7166, 1, 1, 125401, 3, 1, 0, "そろそろ\nパトロールの\n時間だ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7167, 1, 1, 125401, 4, 1, 0, "魔物について\nもっと知りたいな\n本を探しに行こう", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7168, 1, 1, 125401, 5, 1, 0, "レンジャーになって\n体調を崩す日が\n減った気がします…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7169, 1, 4, 125401, 101, 1, 0, "本だけじゃわからない\n世界のことを、\nもっと知りたいです…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7170, 1, 8, 125401, 102, 1, 0, "こんな私にも\nできることはある…\nそう信じています", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7171, 1, 1, 125401, 1, 2, 0, "あの…良かったら\n一緒に見回りへ\n行きませんか？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7172, 1, 1, 125401, 2, 2, 0, "またあなたと一緒に\nレンジャーの仕事が\nできたらうれしいです", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7173, 1, 1, 125401, 3, 2, 0, "けほっこほっ\n…だ、大丈夫です\nたまに出るだけなので", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7174, 1, 1, 125401, 4, 2, 0, "ニャットちゃん\nあなたにも\n懐いてきましたね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7175, 1, 4, 125401, 101, 2, 0, "ニャットちゃんと\n散歩に出かけようかな\n…あの人も誘って", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7176, 1, 8, 125401, 102, 2, 0, "あなたと肩を並べて\nレンジャーの仕事が\nできてうれしいです…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7177, 1, 1, 125401, 1, 2, 0, "おはようございます\nレンジャー・シオリ\n元気に出勤します", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7178, 1, 1, 125401, 2, 2, 0, "ニャットちゃんにも\nお昼ごはんを\n用意しなくちゃ", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7179, 1, 1, 125401, 3, 2, 0, "明日は仕事だから\n早起きしなくちゃ\nおやすみなさい", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7181, 1, 1, 125401, 2, 3, 0, "ふぅ…仕事で\n走り回ったので\n少し休憩します", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7182, 1, 1, 125401, 3, 3, 0, "仕事が終わった後に\nこうして触れていると\n癒されますね…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7183, 1, 1, 125401, 4, 3, 0, "森の香りがします…\n今日はよく\n眠れそうです…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7184, 1, 1, 125401, 5, 3, 0, "ニャットちゃんと\n同じぐらい\n暖かいです…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7186, 1, 1, 125401, 7, 3, 0, "レンジャーの仕事を\nしていると\nお腹が空きます", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7187, 1, 1, 125401, 8, 3, 0, "どんなに忙しくても\n読書だけは\nやめられませんね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7189, 1, 1, 125801, 1, 1, 0, "兄様のお留守の間\nわたくしが、兄様の\n御家を守ります！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7190, 1, 1, 125801, 2, 1, 0, "に、に、兄様の御家…\n招待いただいた以上は\n失礼のないよう\n振る舞わなくては…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7191, 1, 1, 125801, 3, 1, 0, "ふふっ…兄様の素敵な\nお人柄が、家中から\nにじみ出ています…♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7192, 1, 1, 125801, 4, 1, 0, "靴を脱がずに\n御家に上がるのに\n慣れていなくて…あ！\n飛べばいいのでは…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7193, 1, 1, 125801, 5, 1, 0, "ここに花を生けたら\n華やぎそうですね\n兄様はお花…\n好きでしょうか…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7194, 1, 4, 125801, 101, 1, 0, "ずっとここにいたい…\nなどと言ったら\n兄様を困らせて\nしまいますよね…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7195, 1, 8, 125801, 102, 1, 0, "お料理などを作って\n兄様を出迎えられたら\nいいのに…料理の腕も\n磨かなくては…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7196, 1, 1, 125801, 1, 2, 0, "このような姿で\n失礼しております…！\nネ、ネズミがいたので\nつい本気を…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7197, 1, 1, 125801, 2, 2, 0, "おかえりなさいませ\nお忙しい御身をどうぞ\n癒してくださいね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7198, 1, 1, 125801, 3, 2, 0, "兄様の話し相手…！？\nど、どうしましょう…\nわたくしには面白い\n話なんて…はうぅ…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7199, 1, 1, 125801, 4, 2, 0, "なぜか、動物などに\n好かれる体質で…\n迷い猫捜しのバイトの\nお役に立てますか？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7200, 1, 4, 125801, 101, 2, 0, "に、兄様の家に来たら\n『添い寝』をするのが\n礼儀とリアさんが…\n本当…でしょうか？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7201, 1, 8, 125801, 102, 2, 0, "わたくしはここに\nいて…よいのですね？\n『仲間』として…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7202, 1, 1, 125801, 1, 2, 0, "朝は得意なんです\nもし兄様が苦手なら\nわたくしが起こして\n差し上げますよ", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7203, 1, 1, 125801, 2, 2, 0, "兄様…昼餉を作って\nみたのですが、失敗\nしてしまいました…", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7204, 1, 1, 125801, 3, 2, 0, "ふあぁ…\nはっ！　お…\nお恥ずかしい所を…！", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7206, 1, 1, 125801, 2, 3, 0, "こ、これは…！\n人様の御家なのに\nつい…駄目になって…\nしまいますぅ…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7207, 1, 1, 125801, 3, 3, 0, "と、飛びかかっては\nこない…ですよね…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7208, 1, 1, 125801, 4, 3, 0, "すぅ…はぁ…\nこのかぐわしさ\n心が洗われます…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7209, 1, 1, 125801, 5, 3, 0, "なんて暖かい…\n兄様の優しくお広い\n御心のようです…♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7211, 1, 1, 125801, 7, 3, 0, "こんなに食べていたら\nはしたない…\nでしょうか？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7212, 1, 1, 125801, 8, 3, 0, "兄様の本棚はまるで\n知識の宝庫ですね\n己の世間知らずぶりを\n実感してしまいます", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7214, 1, 1, 125501, 1, 1, 0, "次の冒険に向けて\n騎空士弁当を作ろっと\nまずはおにぎりから！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7215, 1, 1, 125501, 2, 1, 0, "魔法の修行しなくちゃ\nもっともっと頼れる\n団長になりたいから！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7216, 1, 1, 125501, 3, 1, 0, "ビカラちゃんを\n遊びに誘いたいけど…\nどういう場所が\n好きなんだろう？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7217, 1, 1, 125501, 4, 1, 0, "仕事を真面目に\nしっかりこなすことが\n騎空艇への\n一番の近道だよね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7218, 1, 1, 125501, 5, 1, 0, "ここにいる人の中で\n騎空団に興味ある人\nいたりしないかな…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7219, 1, 4, 125501, 101, 1, 0, "これまでも想い出は\nいっぱいできたけど…\nこれからもたくさん\n想い出を作りたいな！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7220, 1, 8, 125501, 102, 1, 0, "仲間がいるから\n騎空士は限界だって\n超えられるんだね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7221, 1, 1, 125501, 1, 2, 0, "この衣装\n魔法使いらしいでしょ\n服に着られないよう\n頑張らなくちゃね！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7222, 1, 1, 125501, 2, 2, 0, "あなたとなら\nどんな相手だって\n怖くなんてないよ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7223, 1, 1, 125501, 3, 2, 0, "カリンさんから\n新しい依頼が来たよ！\n一緒に頑張ろ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7224, 1, 1, 125501, 4, 2, 0, "操舵士になるのは\nずいぶん難しそう…\nいつか頼れる仲間が\n見つかるといいな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7225, 1, 4, 125501, 101, 2, 0, "手合わせしたいの？\nいいよ、たまには\n団長らしいところ\n見せてあげるね！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7226, 1, 8, 125501, 102, 2, 0, "まだ二人きりだけど…\nこの騎空団は\n私の宝物だよ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7227, 1, 1, 125501, 1, 2, 0, "おはよう～…\n昨日は遅くまで\n魔法の研究してて…\nふわぁ…眠いなぁ…", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7228, 1, 1, 125501, 2, 2, 0, "さてと、お昼休み！\n紅茶淹れてくるから\n一緒に飲もうよ！", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7229, 1, 1, 125501, 3, 2, 0, "遅くまでお疲れさま！\n無理しない程度に\nお互い頑張ろう！", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7231, 1, 1, 125501, 2, 3, 0, "魔法の研究は\n一旦、休憩！\n頭を休めなくちゃ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7232, 1, 1, 125501, 3, 3, 0, "魔法みたいに\nかわいい！\n騎空団に入る？\nなんてね！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7233, 1, 1, 125501, 4, 3, 0, "いい匂い…\nそれになんだか\n集中力が増したかも", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7234, 1, 1, 125501, 5, 3, 0, "体だけじゃなくて\n心までポカポカする…\nなんだか魔法みたい", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7236, 1, 1, 125501, 7, 3, 0, "いろいろ考え事したら\nお腹が空いちゃった！\nいただきまーす！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7237, 1, 1, 125501, 8, 3, 0, "『摂理の彼方へ\n至るには…』\n…この魔導書\n勉強になるなあ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7239, 1, 1, 125701, 1, 1, 0, "こちらの服を着ると\n冒険に出かけたく\nなっちゃいますね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7240, 1, 1, 125701, 2, 1, 0, "冒険者の心得その一\n準備はできる限り\nしっかりとこなすべし\nなんてどうでしょう？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7241, 1, 1, 125701, 3, 1, 0, "調合に必要な素材は\nあれとこれとそれと…\n時間があるとついつい\n考えちゃいますね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7242, 1, 1, 125701, 4, 1, 0, "せっかくですから\nより良い素材を\n自分で集めたいですね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7243, 1, 1, 125701, 5, 1, 0, "このポーションが\nみなさんの役に立つと\nうれしいですね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7244, 1, 4, 125701, 101, 1, 0, "冒険者の心得その二\n危なくなった時は\nまず逃げるべし！\n大事なことですね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7245, 1, 8, 125701, 102, 1, 0, "あの人の冒険は\nいつも楽しそうで\n元気がもらえます", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7246, 1, 1, 125701, 1, 2, 0, "ぜひおすすめしたい\n依頼があるんです\nこちらをどうぞ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7247, 1, 1, 125701, 2, 2, 0, "ほしいお薬があれば\n錬金術で作って\nさしあげますよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7248, 1, 1, 125701, 3, 2, 0, "依頼で気になる\nところがあれば\n何でもご確認ください", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7249, 1, 1, 125701, 4, 2, 0, "このギルドハウスに\n錬金術の実験設備を…\nえ？　だめですか？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7250, 1, 4, 125701, 101, 2, 0, "ポーションをどうぞ\n大丈夫、効果は\n確認済みですので♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7251, 1, 8, 125701, 102, 2, 0, "次はどんな冒険に\n行かれるんですか？\nいつでもお供しますよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7252, 1, 1, 125701, 1, 2, 0, "今日もいい一日に\nなりそうですね\n冒険日和といった\nところでしょうか", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7253, 1, 1, 125701, 2, 2, 0, "いい時間ですし\nランチにしませんか？\n次の冒険の\n作戦会議も兼ねて♪", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7254, 1, 1, 125701, 3, 2, 0, "夜も遅いですし\n明日の冒険のため\n今日は早く寝ましょう", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7256, 1, 1, 125701, 2, 3, 0, "ふう…やはり冒険は\n事務仕事と違いますね\n少し休憩を…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7257, 1, 1, 125701, 3, 3, 0, "冒険は緊張の連続です\nかわいい物で\n心を癒しましょう", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7258, 1, 1, 125701, 4, 3, 0, "ふむ、この香りは…\n素晴らしいバランスで\n調合されていますね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7259, 1, 1, 125701, 5, 3, 0, "この火力…\n錬金術の調合に\n使えそうですね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7261, 1, 1, 125701, 7, 3, 0, "冒険の際は食べられる\n時に食べておく！\n…とペコリーヌさんも\nおっしゃってましたね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7262, 1, 1, 125701, 8, 3, 0, "ふむふむ…なるほど\nこんな調合も\nあるのですね…\n奥が深いです", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7264, 1, 12, 106101, 103, 1, 0, "アイツどこ行った…？\n戦うならアタシも\n連れてけよなー！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7265, 1, 12, 106101, 103, 2, 0, "今日も悪いヤツ退治…\nの前に腹ごしらえだ！\nなんか食べに行くぞ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7266, 1, 1, 125601, 1, 1, 0, "みんなー！\nギルドハウスで\nぼくと握手！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7267, 1, 1, 125601, 2, 1, 0, "種族も年齢も違うけど\nみんな楽しそうだね！\nやっぱりここはぼくの\nゆ～とぴあさ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7268, 1, 1, 125601, 3, 1, 0, "ハハッ！　まるで\nパーティー会場だね！\nこれは楽しませがいが\nあるってものだよ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7269, 1, 1, 125601, 4, 1, 0, "やぁみんな！\nご利益満点のネズ耳は\nいかがかな？\nひとつ３ルピだよ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7270, 1, 1, 125601, 5, 1, 0, "ここにいれば\n遊び相手に困らないね\nかしこみかしこみ～！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7271, 1, 4, 125601, 101, 1, 0, "どこか遊びに\n行こうかな♪\nそうだ、あの人にも\n声をかけてみよう！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7272, 1, 8, 125601, 102, 1, 0, "あの人といると\nぼくまで楽しくなって\nくるんだよね！\n毎日パレード気分さ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7273, 1, 1, 125601, 1, 2, 0, "ネズ耳の在庫が\n切れたんだ！\n作るのを\n手伝ってくれるかい？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7274, 1, 1, 125601, 2, 2, 0, "ん？　握手かい？\nいいとも！\nいつでも何度でも\nしてあげるよ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7275, 1, 1, 125601, 3, 2, 0, "この衣装に興味が\nあるのかい？\nきみ、なかなか\n見る目があるね！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7276, 1, 1, 125601, 4, 2, 0, "しばらくここに厄介に\nなるつもりなんだ\nあとで街を案内\nしてくれるかい？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7277, 1, 4, 125601, 101, 2, 0, "とっておきのチーズ\nきみと半分こだ！\nみんなには内緒だよ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7278, 1, 8, 125601, 102, 2, 0, "きみはいてほしい時に\nいつもそばに\nいてくれるね\n本当にかしこみだよ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7279, 1, 1, 125601, 1, 2, 0, "やぁ、おはよう！\n気持ちのいい朝だね\n今日も楽しくネズ耳\n売りにはげもうか！", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7280, 1, 1, 125601, 2, 2, 0, "お昼だね！\nおいしいチーズを\n持ってきたから\nぼくと半分こだ！", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7281, 1, 1, 125601, 3, 2, 0, "今日も一日\n楽しかったね！\nいい夢が見られること\n間違いなしさ！", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7283, 1, 1, 125601, 2, 3, 0, "ぼくにも休息は\n必要さ！\nリラックス〜\nリラックス〜♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7284, 1, 1, 125601, 3, 3, 0, "ぼくのぬいぐるみも\n作って売ろうかな…\n修繕費も一気に\n集まるかもね！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7285, 1, 1, 125601, 4, 3, 0, "いい香りだね！\nトーゴクのお香に\nよく似てるよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7286, 1, 1, 125601, 5, 3, 0, "はぁ…温まるねぇ…\nそうだ、この火で\nチーズでも炙って\nみようかな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7288, 1, 1, 125601, 7, 3, 0, "うーん、この味…\nトーゴクを思い出すよ\nどれ、もう一つ\nいただこうか！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7289, 1, 1, 125601, 8, 3, 0, "これはランドソルの\nガイド本だね！\nありがたく参考に\nさせてもらうよ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7291, 1, 1, 126201, 1, 1, 0, "まさか、わらわが\nこのような装束を\n纏う日がくるとはな…\n感慨深いものじゃ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7292, 1, 1, 126201, 2, 1, 0, "わらわの美貌で\nこのギルドハウスも\nより華やかに\nなったようじゃな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7293, 1, 1, 126201, 3, 1, 0, "つまらぬ戦いで\n晴れ着を汚しとうない\n今日は一日\nゆっくりするかの", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7294, 1, 1, 126201, 4, 1, 0, "今日はわらわが食事の\n準備をして、あやつの\n帰りを待ってやろう\n献立は何がよいかの…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7295, 1, 1, 126201, 5, 1, 0, "ユキといったか…\n掴みどころのない\nやつじゃが、根性は\n認めてやるかの", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7296, 1, 4, 126201, 101, 1, 0, "こうして、仲間と\nのんびり過ごす時間も\n悪くないのう♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7297, 1, 8, 126201, 102, 1, 0, "あやつを誘って\n月を見に行こうかの\n夜が楽しみじゃ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7298, 1, 1, 126201, 1, 2, 0, "いかなる衣装で\nあろうとも、わらわは\n完璧に着こなして\nしまうのじゃ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7299, 1, 1, 126201, 2, 2, 0, "なんじゃ？\nわらわの姿に\n見惚れておったとな？\nす、素直でよいのじゃ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7300, 1, 1, 126201, 3, 2, 0, "吸血鬼であるわらわに\n相応しい装束じゃろう\nあとは真っ赤な血を\n添えたいのう…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7301, 1, 1, 126201, 4, 2, 0, "お主、疲れておるな？\nわらわが癒してやろう\nほれ、近う寄るのじゃ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7302, 1, 4, 126201, 101, 2, 0, "お主がおるだけで\nギルドハウスが\n賑やかになるのう\nある種の才能じゃな♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7303, 1, 8, 126201, 102, 2, 0, "眷属よ、風呂にするか\nご飯にするか\nそれとも、わらわに…\nいや、忘れるのじゃ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7304, 1, 1, 126201, 1, 2, 0, "朝一番、わらわに\n会いたかったとな…\n眷属よ\n褒めてつかわすぞ♪", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7305, 1, 1, 126201, 2, 2, 0, "眷属よ、わらわが\n食事を用意したぞ\n忌々しい太陽に負けず\n頑張るのじゃ", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7306, 1, 1, 126201, 3, 2, 0, "眠るには惜しい夜じゃ\n眷属よ、もうしばらく\nわらわの傍に\nおるがよい", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7308, 1, 1, 126201, 2, 3, 0, "ふかふかじゃの♪\n今度あやつにも\n座らせてやろう", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7309, 1, 1, 126201, 3, 3, 0, "愛いやつじゃ\n少しばかり、わらわが\n遊んでやろうかの", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7310, 1, 1, 126201, 4, 3, 0, "気品ある香りじゃのう\nわらわの古城にも\n一つ欲しいところじゃ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7311, 1, 1, 126201, 5, 3, 0, "よいぬくもりじゃ…\n忌々しい太陽とは\n大違いじゃの", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7313, 1, 1, 126201, 7, 3, 0, "味付けの参考に\nあと、もう一つだけ…\n止まらんのじゃ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7314, 1, 1, 126201, 8, 3, 0, "吸血鬼の弱点について\n記されておるのか…\nふむ、眠気が\nさめてしもうたぞ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7316, 1, 12, 100801, 103, 1, 0, "このままボクが美しく\nなり続けても、彼なら\nきっと隣にいてくれる\nはずだよね！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7317, 1, 12, 100801, 103, 2, 0, "もっとそばに来なよ\nボクの隣は\nキミのための\n特等席なんだから", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7318, 1, 1, 126301, 1, 1, 0, "ドレスの影響かな…\n前より黒や赤が\n好きになったかも", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7319, 1, 1, 126301, 2, 1, 0, "ボクがいるだけで\nここがぐんっと\n華やかになったね♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7320, 1, 1, 126301, 3, 1, 0, "ボクを描いた絵画\n今度持ってきて\n飾ってあげようかな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7321, 1, 1, 126301, 4, 1, 0, "ボクはボクに\n魅了されっぱなしさ♪\nまさに魔性の存在だよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7322, 1, 1, 126301, 5, 1, 0, "美のアドバイスを\n聞きたい人いるかい？\nボクがもっともっと\n輝かせてあげるよ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7323, 1, 4, 126301, 101, 1, 0, "あの人、今度はボクを\nどう褒めるだろう？\nもし語彙が尽きても\n許してあげよっと♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7324, 1, 8, 126301, 102, 1, 0, "あの人に似合うコーデ\n考えてあげよっと\n案外、なんでも\n着こなしそうだよね♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7325, 1, 1, 126301, 1, 2, 0, "吸血鬼になるなんて\nやっぱりゴメンだね！\n永遠の美よりも\n大事なことがあるのさ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7326, 1, 1, 126301, 2, 2, 0, "お肌にいいものは\n大体好きだよ\nトマトジュースとかね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7327, 1, 1, 126301, 3, 2, 0, "銀のアクセサリーが\nボクは大好きでね\nいつでもプレゼント\n待ってるよ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7328, 1, 1, 126301, 4, 2, 0, "イリヤみたいにお城に\n住んでみたいんだ♪\n美を統べる者に\n相応しいでしょ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7329, 1, 4, 126301, 101, 2, 0, "今のボクがカップを\n触ると壊しちゃうんだ\nだからほら、キミが\nボクに紅茶を飲ませて", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7330, 1, 8, 126301, 102, 2, 0, "キミも美を磨けば\nもっと輝けるはずさ！\nボクがメイクとか\nしてあげようか？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7331, 1, 1, 126301, 1, 2, 0, "んん、まだ眠い…\n美のためにも、\nもうひと眠りするよ…", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7332, 1, 1, 126301, 2, 2, 0, "ボクにぴったりな\n日傘を買ったんだ♪\n散歩にでも行こうよ！", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7333, 1, 1, 126301, 3, 2, 0, "夜こそボクの時間！\nさぁ、あまねく人々へ\nボクの美を広めよう！", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7335, 1, 1, 126301, 2, 3, 0, "リラックスは大事だよ\n心の余裕こそが\n美の秘訣だからね♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7336, 1, 1, 126301, 3, 3, 0, "キミもボクの\n美の虜にしてあげる♪\nうれしいでしょ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7337, 1, 1, 126301, 4, 3, 0, "人々を魅了するほどの\n魔性の香り…\nボクにふさわしいね♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7338, 1, 1, 126301, 5, 3, 0, "冷えは美の大敵さ\nボクのように\n美しくなりたいなら\n体を温めるべきだよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7340, 1, 1, 126301, 7, 3, 0, "食事をするボクの\nなまめかしさ…♪\n吸血鬼すらも\nドキッとするだろうね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7341, 1, 1, 126301, 8, 3, 0, "吸血鬼って\n美しい存在だよね…\nボクほどではないけど", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7343, 1, 1, 126401, 1, 1, 0, "ここがナイトさんの\nお家なんですねーっ！\n雰囲気そっくりっ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7344, 1, 1, 126401, 2, 1, 0, "ドアも壁もお菓子じゃ\nないんですよねっ？\n不思議ですねーっ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7345, 1, 1, 126401, 3, 1, 0, "失礼のないように\n気をつけないとっ！\nライラエルさまとも\n約束してますからっ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7346, 1, 1, 126401, 4, 1, 0, "向こうに帰るころには\nお土産話がいっぱい\nできてるといいな～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7347, 1, 1, 126401, 5, 1, 0, "戦闘用のアームの他に\n観光用のアームも\nあったらいいのにな～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7348, 1, 4, 126401, 101, 1, 0, "ナイトさんの充填と\nライラエルさまの充填\nどっちも違って\nどっちも大好きです♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7349, 1, 8, 126401, 102, 1, 0, "…ナイトさんが\nマスターになって\nくれたら…えへへっ\nなんでもないですっ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7350, 1, 1, 126401, 1, 2, 0, "お手伝いだったら\n任せてくださいっ！\nお家の移転とか\nしちゃいますかっ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7351, 1, 1, 126401, 2, 2, 0, "こっちに来るときは\nポポおじいさんが\nしっかりメンテして\nくれるんですよっ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7352, 1, 1, 126401, 3, 2, 0, "お家の中にいるのも\nいいですけど、お外も\n気になります～っ！\nお出かけしましょう♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7353, 1, 1, 126401, 4, 2, 0, "ヴォルガーノさんに\nそっくりの子猫さんを\n見かけたんですっ！\nご親戚ですかねっ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7354, 1, 4, 126401, 101, 2, 0, "ここだけの話ですよ？\n…実はケーブルって\n触られると、ちょっと\nくすぐったいんですっ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7355, 1, 8, 126401, 102, 2, 0, "このお家の持ち主は\nナイトさんですよね？\nそれじゃここでは\nご主人さまですねっ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7356, 1, 1, 126401, 1, 2, 0, "おはようございますっ\n今日もフルパワーで\nがんばりましょうっ♪", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7357, 1, 1, 126401, 2, 2, 0, "お昼の時間ですねっ！\nランチの充填、\nお願いしまーすっ♪", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7358, 1, 1, 126401, 3, 2, 0, "そろそろ眠らなくて\n大丈夫ですかっ？\n夜ふかしさんなら\nお付き合いしますよ♪", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7360, 1, 1, 126401, 2, 3, 0, "エネルギーの消費を\n抑えるのも大切\nですよね～っ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7361, 1, 1, 126401, 3, 3, 0, "わたしが触っても\n平気なんて…！\nかわいくて頑丈っ！\n好きですっ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7362, 1, 1, 126401, 4, 3, 0, "センサーによると…\nこの香りはいい香りに\n該当するみたいですっ\nなるほどーっ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7363, 1, 1, 126401, 5, 3, 0, "あったかいのって\nいいですよねっ！\nみなさんうれしそう\nですしっ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7365, 1, 1, 126401, 7, 3, 0, "これがこっちの世界の\nごはんなんですねっ\nおいしい気がします！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7366, 1, 1, 126401, 8, 3, 0, "そーっとそーっと…\n本を破かないように…\n書いてある内容より\n難しいです～っ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7368, 1, 1, 126701, 1, 1, 0, "ウィスタリア家の\nプライベートビーチも\nご案内しますわね♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7369, 1, 1, 126701, 2, 1, 0, "世界を巡る\nリゾートクルーズも\nご用意できますわよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7370, 1, 1, 126701, 3, 1, 0, "避暑地に専用の\n別荘がありますの\n今度ご招待しますわね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7371, 1, 1, 126701, 4, 1, 0, "日焼けは乙女の\n天敵ですわね\n備えはしっかりと…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7372, 1, 1, 126701, 5, 1, 0, "線香花火だけでは\n物足りませんわね\nもっとドカーンと…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7373, 1, 4, 126701, 101, 1, 0, "ギルド活動も全力で！\nバカンスも全力で！\nそれが私ですわ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7374, 1, 8, 126701, 102, 1, 0, "私のセンスで選んだ\n特注の水着ですもの\n似合って当然ですわ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7375, 1, 1, 126701, 1, 2, 0, "ご希望ならすぐにでも\n夏の飛空艇遊覧ツアー\nへご招待しますわ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7376, 1, 1, 126701, 2, 2, 0, "夏の賑やかな\n雰囲気は好きですが…\n暑さには参りますわね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7377, 1, 1, 126701, 3, 2, 0, "日の光を浴びるほどに\n輝きを増す夏の私！\n今日も絶好調ですわ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7378, 1, 1, 126701, 4, 2, 0, "夏祭りに協力するなら\n資金と人材、どちらも\n用意しないとですわね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7379, 1, 4, 126701, 101, 2, 0, "あなたさま…その…\nそうも見つめられると\n照れてしまいます…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7380, 1, 8, 126701, 102, 2, 0, "タマキさんの働く\n海の家も訪れて\nみたいですわね♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7381, 1, 1, 126701, 1, 2, 0, "港の市場が\n賑わう頃ですわね！\n私たちも参りますわよ", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7382, 1, 1, 126701, 2, 2, 0, "冷たい飲み物でも\nご一緒にいかが？\nうちのシェフに用意\nさせてありますの", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7383, 1, 1, 126701, 3, 2, 0, "まだもう少し…\nこの夏の夜を\nあなたさまと…", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7385, 1, 1, 126701, 2, 3, 0, "あなたさまも\n良ければこちらへ\nいらっしゃいな♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7386, 1, 1, 126701, 3, 3, 0, "夏に合わせた装いに\n替えてみるのも\n楽しそうですわね♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7387, 1, 1, 126701, 4, 3, 0, "この夏めいた爽やかな\n香り…期間限定で\n売り出したいですわ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7388, 1, 1, 126701, 5, 3, 0, "く…我慢です…\nこの暑さに耐えてこそ\n美しさにも磨きが…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7390, 1, 1, 126701, 7, 3, 0, "暑い季節に働く\nみなさまへ差し入れも\n必要ですわね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7391, 1, 1, 126701, 8, 3, 0, "夏のトレンドを\n押さえておくのも\n大事ですわね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7393, 1, 1, 126701, 10, 3, 0, "上手くいけば\nヒット商品になる\nポテンシャルを\n感じますわね…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7394, 1, 1, 126701, 11, 3, 0, "この極上の肌触り…\n言い値で買いますわ\n一体おいくらですの？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7395, 1, 1, 126601, 1, 1, 0, "せっかくのバカンス…\n自由に羽を\n伸ばすとしましょう", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7396, 1, 1, 126601, 2, 1, 0, "たとえどのような\n秘密を持とうとも\n私には筒抜けですよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7397, 1, 1, 126601, 3, 1, 0, "子供だましの怪談に\n関心はありませんが…\n霊には興味があります\n観察対象として、ね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7398, 1, 1, 126601, 4, 1, 0, "彼の絵日記…\nあとでゆっくりと\n読ませてもらいます", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7399, 1, 1, 126601, 5, 1, 0, "セミの鳴き声に入道雲\nそしてクリームソーダ\nこれでこそ夏ですね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7400, 1, 4, 126601, 101, 1, 0, "夏休みの宿題でも\n作るとしましょうか\n彼の限界を少し超えた\n負荷を与えるには…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7401, 1, 8, 126601, 102, 1, 0, "彼と過ごす夏を\n特別なものにしたい…\nそれが私の望みです", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7402, 1, 1, 126601, 1, 2, 0, "夏だからといって\n浮かれすぎないように\n節度を持って\n楽しみなさい？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7403, 1, 1, 126601, 2, 2, 0, "ああ見えてもマサキは\n私のプリンセスナイト\nあまり侮られては\n困りますよ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7404, 1, 1, 126601, 3, 2, 0, "私の秘密ですか？\nまんじゅうが怖い…\nというのはどうです？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7405, 1, 1, 126601, 4, 2, 0, "ここは誰かの秘密を\n盗み聞くのに\n最適な場所ですね\n…ジョークですよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7406, 1, 4, 126601, 101, 2, 0, "一緒に風鈴の音を\nのんびり楽しもう…？\n…別に構いませんよ\n貴方とならば", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7407, 1, 8, 126601, 102, 2, 0, "貴方に約束しましょう\n決して忘れられない\n夏にしてあげますよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7408, 1, 1, 126601, 1, 2, 0, "早起きとは感心ですね\nでは一緒に\n体操でもしましょうか", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7409, 1, 1, 126601, 2, 2, 0, "昼食は流しそうめん？\n非効率的ですね\n…食べないとは\n一言も言っていません", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7410, 1, 1, 126601, 3, 2, 0, "…貴方が望むならば\nいくらでも怖い話を\nしてあげますが…\nいかがですか？", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7412, 1, 1, 126601, 2, 3, 0, "七冠にも休息は\n必要なのです\nしばらくここで\n休ませてもらいますよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7413, 1, 1, 126601, 3, 3, 0, "なかなか気に入った…\nというのは\n誰にも秘密です", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7414, 1, 1, 126601, 4, 3, 0, "薫風を思わせる\nこのさわやかな香り…\n悪くないセンスですね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7415, 1, 1, 126601, 5, 3, 0, "夏だからこそ\n冷え対策は\n重要なのです\n…とはいえ暑いですね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7417, 1, 1, 126601, 7, 3, 0, "私がどのような味が\n好きなのかは…\n当然秘密です", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7418, 1, 1, 126601, 8, 3, 0, "夏を舞台にした\n小説は数多くあります\n彼が好きそうな本を\n何冊か見繕いましょう", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7420, 1, 1, 126601, 10, 3, 0, "見た目も手触りも\n完璧に覚えました\nいつでも模倣できます", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7421, 1, 1, 126601, 11, 3, 0, "ふむ…夏らしい\nクッションですね\nナイトプールに\n映えそうです", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7422, 1, 12, 105401, 103, 1, 0, "レイ様も格好良いけど\n騎士さんもなかなか…\nって、今の聞こえて\nませんよねっ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7423, 1, 12, 105401, 103, 2, 0, "騎士さんはシンプルな\n服が似合いそうです！\n今度、一緒に\n見に行きましょう♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7424, 1, 1, 126801, 1, 1, 0, "最初はどうかな～って\n思ったけど…\n水着姿でも案外\n問題ないのよね…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7425, 1, 1, 126801, 2, 1, 0, "水着で飲みすぎると\nお腹が冷える、って\n言うけど…麦しゅわ\nなら問題ないわね！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7426, 1, 1, 126801, 3, 1, 0, "暑ければ暑いほど\nその後の麦しゅわは\nおいしくなるのよ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7427, 1, 1, 126801, 4, 1, 0, "しっかりと仕事を\nこなしてから、\nしっかりと麦しゅわ！\nやっぱりこれね！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7428, 1, 1, 126801, 5, 1, 0, "ふふっ、デザインも\n着心地も最高ね\n奮発して用意して\n正解だったわ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7429, 1, 4, 126801, 101, 1, 0, "伝説の麦しゅわ、\n飲めるものなら\nまた飲みたいわね…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7430, 1, 8, 126801, 102, 1, 0, "二つ名に恥じない\n聖騎士として\n私…飲むわ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7431, 1, 1, 126801, 1, 2, 0, "この格好なら水に\n濡れても大丈夫！\nなんだけど、さすがに\n書類は濡らせないわね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7432, 1, 1, 126801, 2, 2, 0, "そうやってじっと\n見られると…ちょっと\n恥ずかしいわ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7433, 1, 1, 126801, 3, 2, 0, "今日もいい天気ね\nふふっ、あとで\nビーチに遊びに\n行きましょ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7434, 1, 1, 126801, 4, 2, 0, "水着姿も慣れると\n案外快適ね\nそうだ、キミも\n水着になっちゃう？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7435, 1, 4, 126801, 101, 2, 0, "例え水着姿であろうと\n聖騎士としての誇りは\n忘れてないわ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7436, 1, 8, 126801, 102, 2, 0, "キミとだったら\n麦しゅわなしでも\n最高の夏のバカンスに\nなると思うわ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7437, 1, 1, 126801, 1, 2, 0, "仕事終わりの\n麦しゅわのために\n今日も一日\n頑張るわよ♪", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7438, 1, 1, 126801, 2, 2, 0, "今日のお昼は\nどうしようかしら…\nさ、さすがに昼からは\n自重してるわよ？", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7439, 1, 1, 126801, 3, 2, 0, "さ、一日の疲れを\n洗い流すために…\n軽～く飲むわよ！", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7441, 1, 1, 126801, 2, 3, 0, "こうやって\n直に触れると\n肌触りの良さが\nよく分かるわ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7442, 1, 1, 126801, 3, 3, 0, "今日もかわいいわね～\nどう？　今度\nお姉さんの一人飲みに\n付き合わない？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7443, 1, 1, 126801, 4, 3, 0, "いい香りね～…はっ！\n麦しゅわの匂いの\nアロマポットなんて\nあったらよくない？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7444, 1, 1, 126801, 5, 3, 0, "こんな格好で火に\n当たってると焙られる\nスルメの気分が\nちょっと分かるわ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7446, 1, 1, 126801, 7, 3, 0, "もぐもぐ…うん、\n結構おつまみにも\nむいてそうね～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7447, 1, 1, 126801, 8, 3, 0, "試験に使った参考書、\nたまには読み返して\nおかないとね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7449, 1, 1, 126801, 10, 3, 0, "ふふ♪\nいい手触りね～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7450, 1, 1, 126801, 11, 3, 0, "…はふう～…\nゴロゴロするのって\n最高ね～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7451, 1, 1, 126901, 1, 1, 0, "ん…やっぱり\nこの水着…\n大胆、なんじゃ…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7452, 1, 1, 126901, 2, 1, 0, "海でもお世話って、\nできるのかしら…？\n浮き輪代わりになる、\nとか…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7453, 1, 1, 126901, 3, 1, 0, "～♪\nふふ…海に行く\n準備も、楽しい…かも", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7454, 1, 1, 126901, 4, 1, 0, "あうぅ…水着で\n歩いてると…お尻…\nすぐ食い込んじゃう…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7455, 1, 1, 126901, 5, 1, 0, "海では、はしゃいだ\nほうがいいのよね…\nわ、わー、い…♪\n…こうかしら？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7456, 1, 4, 126901, 101, 1, 0, "あなたとなら…\n楽しい夏の思い出、\n作れる…気がするの…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7457, 1, 8, 126901, 102, 1, 0, "また来年も…\n再来年も…\nあなたと海へ\n行けますように…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7458, 1, 1, 126901, 1, 2, 0, "水着の着方を\n練習してるの…\n後ろ、変じゃない…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7459, 1, 1, 126901, 2, 2, 0, "海に行く支度…\n進んでるかしら…？\nお手伝い、する…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7460, 1, 1, 126901, 3, 2, 0, "ひ、日除けの準備は\n万全よ…\nずっと太陽の下に\nいたら…溶けちゃう…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7461, 1, 1, 126901, 4, 2, 0, "泳ぐ練習…\nこ、ここでするのは\n…さすがに変？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7462, 1, 4, 126901, 101, 2, 0, "人混みに入るのは\nまだ不安で…\nでも、あなたといれば\nきっと…大丈夫…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7463, 1, 8, 126901, 102, 2, 0, "あなたのおかげで\n海の楽しいところを\nたくさん知ったわ…\nありがとう…♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7464, 1, 1, 126901, 1, 2, 0, "お出かけの日の朝は\n早い、のよね…？\nだから、ずっと\n起きていたわ…", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7465, 1, 1, 126901, 2, 2, 0, "今頃ビーチの日差しは\n強いんでしょうね…\nそ、想像しただけで…\nめまいが…", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7466, 1, 1, 126901, 3, 2, 0, "ビーチの夜といえば\n花火って、聞いたわ…\nバン、って言わない\n花火…ある…？", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7468, 1, 1, 126901, 2, 3, 0, "素肌にふわふわが…\n気持ちいい…♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7469, 1, 1, 126901, 3, 3, 0, "うふふ…\n一緒に海…行く？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7470, 1, 1, 126901, 4, 3, 0, "ふぅ…♪\n気分まで…\n晴れやかになるわ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7471, 1, 1, 126901, 5, 3, 0, "暑いような…\n寒いような…\n不思議な気持ちに\nなるわ…ね？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7473, 1, 1, 126901, 7, 3, 0, "泳いだ後には\nいっぱい食べたほうが\n体によさそう、ね…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7474, 1, 1, 126901, 8, 3, 0, "ビーチの日陰で\n読書するのも…\n気持ちよさそう…♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7476, 1, 1, 126901, 10, 3, 0, "これで合ってる…？\n遊び方わからなくて…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7477, 1, 1, 126901, 11, 3, 0, "ふわふわ…♪\n海の底にいるみたい…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7478, 1, 1, 127001, 1, 1, 0, "恥ずかしい格好で\n室内を歩くこの快感\nクセになりそうです…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7479, 1, 1, 127001, 2, 1, 0, "この水着…\nどうしてかポーズを\n決めたくなりますね…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7480, 1, 1, 127001, 3, 1, 0, "海にはどんな生き物が\nいるのでしょう～\n胸が高鳴ります～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7481, 1, 1, 127001, 4, 1, 0, "夏は暑いですね…\nクウカの体が熱いのも\n夏のせい…ぐふふ…♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7482, 1, 1, 127001, 5, 1, 0, "ドＳさんと無人島に\n取り残されて…という\n妄想はかなりイイです", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7483, 1, 4, 127001, 101, 1, 0, "ドＳさんの\n欲望に満ちた眼差し…\nクウカ、受け止めます", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7484, 1, 8, 127001, 102, 1, 0, "海に行くのが\n楽しみです…\nドＳさんと…ぐふふ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7485, 1, 1, 127001, 1, 2, 0, "「もっと過激な水着を\n見せてみろよ」なんて\nいけませんドＳさん…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7486, 1, 1, 127001, 2, 2, 0, "水着のままで\n踊ればいいのですね…\nドＳさんの望みなら！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7487, 1, 1, 127001, 3, 2, 0, "ドＳさんの熱い視線を\n感じます…じゅるり…\nもっと見てください…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7488, 1, 1, 127001, 4, 2, 0, "こんな格好のクウカを\n放っておくなんて…\nさすがですドＳさん！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7489, 1, 4, 127001, 101, 2, 0, "海で水着といえば\n何かが起きる予感…！\nドＳさんの新たな攻め\n期待していますね…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7490, 1, 8, 127001, 102, 2, 0, "恥ずかしいですけど\n頑張っちゃいました\nドＳさん…こういう\n水着、好きですか？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7491, 1, 1, 127001, 1, 2, 0, "こんな朝からクウカを\n水着に着替えさせて…\n本当にドＳさんは\n欲望に正直ですね！", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7492, 1, 1, 127001, 2, 2, 0, "こんな昼間から\nドＳさんがヌルヌルの\n液体をクウカに～！\nえっ…日焼け止め？", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7493, 1, 1, 127001, 3, 2, 0, "ドＳさん…これから\nクウカと夜の海を\n見に行きませんか？\nきっと綺麗ですよ…♪", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7495, 1, 1, 127001, 2, 3, 0, "ここなら妄想に\n集中できます～♪\nじゅるり～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7496, 1, 1, 127001, 3, 3, 0, "こうもかわいいと\n妄想に登場させるのも\n気が引けます…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7497, 1, 1, 127001, 4, 3, 0, "クウカを昂らせる\nこの香り…！\nは！　ドＳさんの罠！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7498, 1, 1, 127001, 5, 3, 0, "体を熱くさせて\n水着を脱がそうと！？\nテクニカルです…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7500, 1, 1, 127001, 7, 3, 0, "クウカの口の中に\nたくさん…♪\n止まりません…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7501, 1, 1, 127001, 8, 3, 0, "読書は心が穏やかに\nなりますね…！\nぐふふ…穏やかに…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7503, 1, 1, 127001, 10, 3, 0, "いい乗り心地です…♪\nあっ…普通に楽しんで\nしまいました…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7504, 1, 1, 127001, 11, 3, 0, "これは…\n妄想に役立ちそうな\n感触です…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7505, 1, 12, 100501, 103, 1, 0, "成長した自分に\nあわせて、もっと\nかっこいいポーズを\n研究するッス！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7506, 1, 12, 100501, 103, 2, 0, "これからも一緒に\n最高のヒーローを\n目指していくッスよ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7507, 1, 1, 127101, 1, 1, 0, "お父さん、娘の\n水着姿がそんなに\nうれしいの？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7508, 1, 1, 127101, 2, 1, 0, "怖い話を子どもたちに\n聞かせたら泣かれて\nしまいました…\n加減が難しいです…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7509, 1, 1, 127101, 3, 1, 0, "海に行くと父が\n水着の女性ばかり\n追いかけて困ります…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7510, 1, 1, 127101, 4, 1, 0, "拾った貝殻で\n新しい占いでも\nはじめてみようかな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7511, 1, 1, 127101, 5, 1, 0, "貝殻を耳に当てると\n波の音が…あっ\nこれはダメです\nよくない声がします", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7512, 1, 4, 127101, 101, 1, 0, "この飲み物、スーッと\n体に沁み込んでくる…\nあの人にも\n教えてあげたいです", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7513, 1, 8, 127101, 102, 1, 0, "強い日差しも騒がしい\n海も苦手なのに\nあの人となら不思議と\n楽しく思えるんです", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7514, 1, 1, 127101, 1, 2, 0, "夏限定、水と氷を\n使った特別な占いは\nいかがですか？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7515, 1, 1, 127101, 2, 2, 0, "暑い日々にはやはり\n怪談ですね\n厳選フルコースで\nお聞かせします", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7516, 1, 1, 127101, 3, 2, 0, "寒気がする？　それは\n霊ではなくかき氷の\n食べ過ぎですよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7517, 1, 1, 127101, 4, 2, 0, "今日の占い、あなたの\nラッキーパーソンは…\n白い水着の女性です", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7518, 1, 4, 127101, 101, 2, 0, "あの、よければ\n日焼け止めを塗って\nもらえませんか？\n背中は届かなくて…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7519, 1, 8, 127101, 102, 2, 0, "海に行きませんか？\n二人きりで静かな\n砂浜を散歩したいです", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7520, 1, 1, 127101, 1, 2, 0, "潮騒だけが響く朝の海\n心が落ち着きます\nこれから一緒に散歩に\n行ってみませんか？", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7521, 1, 1, 127101, 2, 2, 0, "不思議です。どうして\n普通のご飯でも、海で\n食べると特別おいしく\n感じるのでしょう…", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7522, 1, 1, 127101, 3, 2, 0, "夜の海に行きませんか\n今なら夜光虫が\n見られるかも\nしれませんよ", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7524, 1, 1, 127101, 2, 3, 0, "夏は知らぬ間に体力を\n消耗しています\n適度に休息して\nいきましょう", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7525, 1, 1, 127101, 3, 3, 0, "このぬいぐるみ…\n大丈夫です\n何も憑いては\nいませんよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7526, 1, 1, 127101, 4, 3, 0, "ほのかにお線香\nみたいな香り…\nなんだか\n落ち着きます", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7527, 1, 1, 127101, 5, 3, 0, "人魂、鬼火\nセントエルモの火…\n炎を見ていると色々と\n連想してしまいます", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7529, 1, 1, 127101, 7, 3, 0, "お腹を壊さぬよう\n食い合わせには\nご注意ください", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7530, 1, 1, 127101, 8, 3, 0, "これはいい\n怪談集ですね…\nつい読みふけって\nしまいます", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7532, 1, 1, 127101, 10, 3, 0, "ふふ、こういうのに\n乗るとなんだか\n子どもに戻った\nみたいですね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7533, 1, 1, 127101, 11, 3, 0, "こ、これは…\nちょっとメルヘン\n過ぎて恥ずかしい\nですね…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7534, 1, 1, 126501, 1, 1, 0, "本当に素敵なお家…♪\n騎士たちの人柄を\n感じられるようで…\n不思議と落ち着きます", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7535, 1, 1, 126501, 2, 1, 0, "調度品をつい間食…\nという習慣がこちらの\n世界にはないのですね\n…気をつけないと", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7536, 1, 1, 126501, 3, 1, 0, "手土産のお菓子は\nどちらに置けば\nよいでしょうか？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7537, 1, 1, 126501, 4, 1, 0, "まるでおとぎ話の中に\n入ってしまったかの\nよう…別世界とは\nよく言ったものです", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7538, 1, 1, 126501, 5, 1, 0, "お忍び…ふふっ♪\nときめく響きです\nなんだかお茶目で…\n密やかな印象が", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7539, 1, 4, 126501, 101, 1, 0, "ふわ…\nああ、ついあくびが…\n居心地がよすぎるのも\n困り物ですね…ふふ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7540, 1, 8, 126501, 102, 1, 0, "騎士には不思議な力が\nあるだけでなく\n何者かの加護が\nあるようですね…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7541, 1, 1, 126501, 1, 2, 0, "この『王家の装備』は\n少し気難しいのです\nわたし以外が触れると\nその…ビリビリっと", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7542, 1, 1, 126501, 2, 2, 0, "子どもたちに新しい\n遊びを教わりました♪\nにーらめっこしましょ\nあっぷっ…ぷ～っ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7543, 1, 1, 126501, 3, 2, 0, "実は憧れていたのです\n友人の家を訪ねると\nいう経験に…ふふっ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7544, 1, 1, 126501, 4, 2, 0, "ぎるどはうす…\nぎるど活動の拠点だと\n管理協会？　の方に\nうかがいました", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7545, 1, 4, 126501, 101, 2, 0, "あなたは運命を\n信じますか？\nわたしは…信じます\nよい運命だけですが♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7546, 1, 8, 126501, 102, 2, 0, "もしわたしが本当に\n天使だったとしたら…\nどうされますか？\n…ふふ、冗談です♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7547, 1, 1, 126501, 1, 2, 0, "パンケーキの香りで\n目覚める朝は\nとても良いものですよ\nぜひともお試しを♪", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7548, 1, 1, 126501, 2, 2, 0, "ひと休みの時間ですね\n食事にお昼寝…\nすべきことを行って\n英気を養いましょう", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7549, 1, 1, 126501, 3, 2, 0, "そろそろ休まれては？\n眠れないようであれば\n寝物語か子守歌でも\nふふ、遠慮は無用です", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7551, 1, 1, 126501, 2, 3, 0, "すう………はっ！\n眠ってはいませんよ？\nいくら居心地が良い\nからといって…ふわぁ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7552, 1, 1, 126501, 3, 3, 0, "まあ、あいらしい…♪\nぜひわたしの寝室に\n招待させてください", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7553, 1, 1, 126501, 4, 3, 0, "なんて、かぐわしい…\nオーブンからただよう\n焼き菓子の香りとは\nまた異なる魅力が…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7554, 1, 1, 126501, 5, 3, 0, "みなで暖をとりながら\n互いに語り笑い合う\n…心休まる時間ですね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7556, 1, 1, 126501, 7, 3, 0, "食は心と体に\nゆとりを生みます\n…辛い食べ物の場合？\nそれは、その…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7557, 1, 1, 126501, 8, 3, 0, "こちらの世界の知識を\n蓄えておかなければ\nテーブルマナーに\nついてのページは…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7559, 1, 1, 127201, 1, 1, 0, "新しい色の蛍光ペン\n買ったのよね\nこれで勉強が\n楽しくなるわ～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7560, 1, 1, 127201, 2, 1, 0, "学食のランチ、結構\nおいしいのよね～\n今度ペコリーヌに\n教えてやろうかしら？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7561, 1, 1, 127201, 3, 1, 0, "ふふふ…扉に黒板消し\n仕掛けてるの…あいつ\n引っかかるかしら？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7562, 1, 1, 127201, 4, 1, 0, "部活するなら運動部も\n悪くないかもね～\n夕日に向かって\n突っ走ってやるわ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7563, 1, 1, 127201, 5, 1, 0, "毎日窮屈な制服着て\n学生も大変ね～\n…ま、かわいいけど", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7564, 1, 4, 127201, 101, 1, 0, "あいつに貸したノート\n変な落書きとか\nしてなかったよね…？\n…その、相合傘とか…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7565, 1, 8, 127201, 102, 1, 0, "昨日の家庭科で\n作ったクッキー…\nあいつにどうやって\n渡そうかな…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7566, 1, 1, 127201, 1, 2, 0, "宿題教えてって？\nしょうがないわね～\nじゃあ一緒に\nやりましょ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7567, 1, 1, 127201, 2, 2, 0, "あ、明日あたしたち\n日直当番よね？\nめんどい仕事は\nあんたに任せるわ～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7568, 1, 1, 127201, 3, 2, 0, "寮生活は楽しいけど\n就寝時間が早すぎよね\nいきなり消灯されると\nビックリするわ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7569, 1, 1, 127201, 4, 2, 0, "校則で禁止だけど、\nにけつでほうき通学\nとか青春っぽくない？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7570, 1, 4, 127201, 101, 2, 0, "制服って好きな人とも\n自然にお揃いになれて\nいいわよね…って別に\nあんたとって訳じゃ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7571, 1, 8, 127201, 102, 2, 0, "あんたネクタイ\n曲がってるわよ～？\nほら直したげるから\nこっち向いて？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7572, 1, 1, 127201, 1, 2, 0, "おはよ～！\n今日も元気に登校よ！\n忘れ物ないようにね！", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7573, 1, 1, 127201, 2, 2, 0, "午前の授業疲れたわ～\nしっかりお昼を食べて\n午後も頑張るわよ！", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7574, 1, 1, 127201, 3, 2, 0, "うう…学校の怪談\n思い出しちゃった…\n…別に怖くないけど！", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7576, 1, 1, 127201, 2, 3, 0, "ふかふか～\n…気持ち良すぎて\n制服のまま\n寝ちゃいそうね…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7577, 1, 1, 127201, 3, 3, 0, "かわいくて癒されるわ\n寮に持ち込んでも\n大丈夫かしら…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7578, 1, 1, 127201, 4, 3, 0, "集中力の高まる香りで\n勉強の効率爆上がりね\n次のテストで\nいい点取るわよ～！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7579, 1, 1, 127201, 5, 3, 0, "うう、ちょっと冷えて\n来たわね…\nスカートの下に\nジャージ穿こうかしら", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7581, 1, 1, 127201, 7, 3, 0, "もぐっ…もぐっ…\n食べとかないと教室で\nお腹なっちゃうわ～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7582, 1, 1, 127201, 8, 3, 0, "図書室で借りた本\n明日返却日なのよね…\n今日中に読むわよ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7584, 1, 12, 104301, 103, 1, 0, "あいつに背中を\n預けるのもいい気分だ\nいつかは、全部…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7585, 1, 12, 104301, 103, 2, 0, "な、なんだよ？\nじっと見るんじゃねぇ\n尻尾が揺れちまうだろ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7586, 1, 1, 127301, 1, 1, 0, "魔法の練習！\nむむむ～…\nそれっ…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7587, 1, 1, 127301, 2, 1, 0, "そだ！　これ着て\nルー学行こっかな？　\n制服自慢しちゃお♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7588, 1, 1, 127301, 3, 1, 0, "赤点回避の魔法とか\nあったら最高じゃね？\n誰かうちに教えて～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7589, 1, 1, 127301, 4, 1, 0, "制服が一番映える\n場所ってどこだろ？\n教室？　遊園地？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7590, 1, 1, 127301, 5, 1, 0, "ベンキョーつらたん…\n今度キャルっちに\n助けてもらお", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7591, 1, 4, 127301, 101, 1, 0, "部活か…\nヒデサイと一緒に\nやったら楽し～かも♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7592, 1, 8, 127301, 102, 1, 0, "男子の制服\nもにカワだった…\n今度ヒデサイに\n借りよっかな？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7593, 1, 1, 127301, 1, 2, 0, "ちょすー、うちの\nさいきょー魔法で\nテンアゲしてあげる～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7594, 1, 1, 127301, 2, 2, 0, "九九って呪文ぽくね？\nほら、例えばヒチシ…\nヒチシ、なんだっけ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7595, 1, 1, 127301, 3, 2, 0, "ヒデサイセンセー\nうちにベンキョー\n教えて！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7596, 1, 1, 127301, 4, 2, 0, "学割マジ助かるー\nヒデサイ、一緒に\nどんどん使ってこー♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7597, 1, 4, 127301, 101, 2, 0, "今度ヒデサイに\nお弁当作ったげる\n屋上で一緒に食べよ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7598, 1, 8, 127301, 102, 2, 0, "ヒデサイのバイブス\n上がる言葉教えてよ\nつらたんな時、うちが\nいっぱい言ったげるよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7599, 1, 1, 127301, 1, 2, 0, "ちょすちょす～\n今日も朝から\nバイブス十分♪", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7600, 1, 1, 127301, 2, 2, 0, "お昼どうしよっかな\nたまにはガッツリ\nいっちゃう系？", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7601, 1, 1, 127301, 3, 2, 0, "やべ、宿題まだ\nできてないし…\nヒデサイ助けて～", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7603, 1, 1, 127301, 2, 3, 0, "充電ちう～\n…ガッコーのイスも\nこれにして～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7604, 1, 1, 127301, 3, 3, 0, "かわいいし、\n癒されるし、キュン死\nしちゃうじゃん…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7605, 1, 1, 127301, 4, 3, 0, "リフレッシュの\n魔法発見！\nいい香り～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7606, 1, 1, 127301, 5, 3, 0, "ぽかぽかサイコー♪\n今日はもーずーっと\nここにいたいかも～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7608, 1, 1, 127301, 7, 3, 0, "超おいし～♪\n学食にあったら\n毎日リピする～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7609, 1, 1, 127301, 8, 3, 0, "ふむふむ…\nねむみパない…\n続きはまた今度～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7611, 1, 1, 127501, 1, 1, 0, "今度は子どもたちも\nサラサリアに連れて\n行ってあげたいわ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7612, 1, 1, 127501, 2, 1, 0, "子どもたちから\nお姫さまみたいって\n言われちゃった♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7613, 1, 1, 127501, 3, 1, 0, "この剣…火を点けたり\nするのに便利なのよ\nコントロールも効くし", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7614, 1, 1, 127501, 4, 1, 0, "あのサラサリアの\nカラっとした暑さが\nちょっと恋しいわ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7615, 1, 1, 127501, 5, 1, 0, "買ってきたお土産、\nお世話になってる\nみんなに配りましょ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7616, 1, 4, 127501, 101, 1, 0, "あんたと一緒なら\nどこの国でだって\n暮らしていけそうね♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7617, 1, 8, 127501, 102, 1, 0, "今の事業を大きくして\nあんたと世界中の国を\n飛び回ったり…なんて\nすてきじゃない？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7618, 1, 1, 127501, 1, 2, 0, "サラサリアで食べた\n料理が恋しい…？\nもう…また今度\n作ってあげるわよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7619, 1, 1, 127501, 2, 2, 0, "今度旅行するなら\nどこに行こうかしら？\n希望があれば教えてね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7620, 1, 1, 127501, 3, 2, 0, "サラサリアの香辛料、\nランドソルでも\n人気が出そうよね\nいいビジネスの予感！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7621, 1, 1, 127501, 4, 2, 0, "そんなにじっと\n見られるとさすがに\n恥ずかしいってば…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7622, 1, 4, 127501, 101, 2, 0, "お嫁さんは憧れるけど\nお婿さんになって\nもらうのもありよね…\nあんたはどう思う？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7623, 1, 8, 127501, 102, 2, 0, "やっぱり、あんたには\nいつもあたしの隣に\n居てほしいな…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7624, 1, 1, 127501, 1, 2, 0, "陽射しが強くなる前に\n雑多な用事は\n片付けちゃいましょ", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7625, 1, 1, 127501, 2, 2, 0, "サラサリアで食べた\n料理を再現してみたの\n味見してくれる？", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7626, 1, 1, 127501, 3, 2, 0, "暑さに負けないように\n睡眠はちゃんと\n取っておかないと", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7628, 1, 1, 127501, 2, 3, 0, "こっちでもあっちでも\n忙しいのは変わらない\nわね…ちょっと休憩～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7629, 1, 1, 127501, 3, 3, 0, "サラサリアで見た\nぬいぐるみも\nかわいかったわね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7630, 1, 1, 127501, 4, 3, 0, "お香って地域ごとの\n特色が出るわよね\nこの香りは…\nちょっと神秘的？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7631, 1, 1, 127501, 5, 3, 0, "砂漠地方も夜は冷える\nのよね。温かいものが\nあると安心するわ…♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7633, 1, 1, 127501, 7, 3, 0, "食べ慣れた味って\nどうしてほっと\nするのかしら…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7634, 1, 1, 127501, 8, 3, 0, "今後の取引相手に\nなるかもしれないし\n向こうの文化も\n勉強しておかないと…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7636, 1, 12, 101401, 103, 1, 0, "犯人はこの中にいる！\n探偵として一度は\n言ってみたいセリフさ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7637, 1, 12, 101401, 103, 2, 0, "助手くんの情報を\n仕入れておかないと…\n理想の私を、ずっと\n見ていてほしいからね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7638, 1, 1, 127601, 1, 1, 0, "久々の長旅で\n里心がついちまったね\nしばらくランドソルで\nのんびりしようか", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7639, 1, 1, 127601, 2, 1, 0, "たまには違う服装\n違う得物を使うのも\n悪くないね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7640, 1, 1, 127601, 3, 1, 0, "サラサリアにいた時は\n砂まみれで大丈夫かと\n思ってたけど、今じゃ\n懐かしく感じるよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7641, 1, 1, 127601, 4, 1, 0, "魔法の絨毯、あれは\n楽だし速いし便利\nだったねえ…土産に\n一枚欲しかったな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7642, 1, 1, 127601, 5, 1, 0, "砂上での戦いは\nなかなかいい鍛錬に\nなったね…今度は\n修行しに行くとしよう", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7643, 1, 4, 127601, 101, 1, 0, "サラサリアの人たちも\n気のいい連中だったが\n元気にやってるかねえ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7644, 1, 8, 127601, 102, 1, 0, "旅の恥はかき捨てって\nいうけど、悪い旅じゃ\nなかったね。次は\nアイツとどこに行こう", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7645, 1, 1, 127601, 1, 2, 0, "この格好を冷やかしに\n来たのかい？\n生憎、もうすっかり\n慣れちまったよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7646, 1, 1, 127601, 2, 2, 0, "ギルドの連中に\n土産をわたしに\n行くところなんだ\n付き合っておくれよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7647, 1, 1, 127601, 3, 2, 0, "久々に水場で\n釣りがしたいねえ…\nもちろんお前さんも\nくるんだろう？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7648, 1, 1, 127601, 4, 2, 0, "おっと\nサラサリアの連中から\n便りが来てるよ\nさ、一緒に読もうか", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7649, 1, 4, 127601, 101, 2, 0, "最近サラサリアの踊り\nを練習してるんだ\nあとで振付けを\n見ておくれよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7650, 1, 8, 127601, 102, 2, 0, "なんだいお前さん？\n…なんだか若奥さん\nっぽいって？　まあ、\nそりゃあ、ねえ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7651, 1, 1, 127601, 1, 2, 0, "いい時間だね\n日が昇りきる前に\nやることをすませ\nとかないと", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7652, 1, 1, 127601, 2, 2, 0, "昼はどうしようかね\nサラサリア風の\n食事でもつくろうか", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7653, 1, 1, 127601, 3, 2, 0, "さて、早めに寝ようか\n砂漠の夜は冷えるから\n…っと、まだ旅行中の\n気分でいたよ", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7655, 1, 1, 127601, 2, 3, 0, "良い座り心地だ\nサラサリア宮殿の\n応接間を\n思い出すねえ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7656, 1, 1, 127601, 3, 3, 0, "ランプの魔神に\nあげたら喜ぶかねえ？\n次に会う時までに\n見繕ってみようか", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7657, 1, 1, 127601, 4, 3, 0, "この香り…\nサラサリア土産の\n香木かい？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7658, 1, 1, 127601, 5, 3, 0, "旅先での焚火を\n思い出すよ\nなんだかほっとするね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7660, 1, 1, 127601, 7, 3, 0, "ふふ、慣れてる味も\nいいけど、むこうの\n味付けが懐かしく\nなっちまうね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7661, 1, 1, 127601, 8, 3, 0, "ふむふむ…\n千夜一夜物語か\nおもしろいもんだねえ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7663, 1, 1, 126001, 1, 1, 0, "立派なギルドハウス♪\n帰る場所が素敵だと\n冒険に出るのも\n楽しくなりそうです♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7664, 1, 1, 126001, 2, 1, 0, "お掃除でも…と\n思いましたけど、\nさすがお兄さんのお家\nきれいにしてますね♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7665, 1, 1, 126001, 3, 1, 0, "わたしたちもいつか\n立派なギルドハウスが\n持てる冒険者に\nなりたいです…♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7666, 1, 1, 126001, 4, 1, 0, "わたし冒険者ですけど\n家を守ってお兄さんの\n帰りを待つのも\n悪くない…かも？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7667, 1, 1, 126001, 5, 1, 0, "お菓子焼こうかな…♪\nちょっと多めに作って\nお兄さんにも\n食べてもらおうっと♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7668, 1, 4, 126001, 101, 1, 0, "このお家、とっても\n大好きになりました♪\nだからその…もう少し\nいてもいいですか…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7669, 1, 8, 126001, 102, 1, 0, "お兄さんに\n感謝の手紙を書いて\n枕元に隠して\nおこうかな…♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7670, 1, 1, 126001, 1, 2, 0, "…お兄さん\nお暇なんですか？\nじゃあ一緒に冒険へ\n出かけましょう♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7671, 1, 1, 126001, 2, 2, 0, "お兄さんは苦手な食材\nとかありますか？\nあの、もしよければ\n今から手料理を…♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7672, 1, 1, 126001, 3, 2, 0, "まったり過ごす\nお家時間もいいですね\n冒険者には羽休めも\n大事ですから♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7673, 1, 1, 126001, 4, 2, 0, "いつでもお兄さんを\n守れるように\nこの姿でいることに\n慣れておきますね…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7674, 1, 4, 126001, 101, 2, 0, "お兄さん眠そうです…\nちょっとお昼寝\nしちゃいましょうか♪\nはい、膝枕…♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7675, 1, 8, 126001, 102, 2, 0, "お兄さん怪我を…！？\nわたしが治療を…\nふー…ふー…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7676, 1, 1, 126001, 1, 2, 0, "おはようございます♪\n朝、元気に挨拶すれば\n幸せな一日が過ごせる\nそうですよ♪", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7677, 1, 1, 126001, 2, 2, 0, "お昼ごはんの時間です\n…手がふさがってる？\nわかりました、じゃあ\nあ～んしますね♪", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7678, 1, 1, 126001, 3, 2, 0, "ふわぁ…眠くなって\nきちゃいました…\nお兄さん…お泊りして\nいいですか…？", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7680, 1, 1, 126001, 2, 3, 0, "ふにゃ～～…♪\nこんな姿、他の人には\n見せられません…♪\nもふもふ～～…♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7681, 1, 1, 126001, 3, 3, 0, "お兄さんこういうの\n好きなのかな～…\nふふっ、わたしも♪\nぎゅ～～～っ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7682, 1, 1, 126001, 4, 3, 0, "アロマの癒し効果…\n上手くアレンジして\nみなさんの治療に\n活かせないかな～？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7683, 1, 1, 126001, 5, 3, 0, "は～気持ちいい～♪\n身体が暖まると\n心もぽかぽかです♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7685, 1, 1, 126001, 7, 3, 0, "うふふ、おいしい♪\nプレシアちゃんなら\n無限にぱくぱく～って\nしちゃいそうです…♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7686, 1, 1, 126001, 8, 3, 0, "本はわたしを\n知らない世界に\n連れていってくれる…\nまるでお兄さんです♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7688, 1, 12, 103801, 103, 1, 0, "話題の新刊…\nあの人も読んでるかな\n今度会う時、感想\n聞いてみたいな…♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7689, 1, 12, 103801, 103, 2, 0, "うにゃっ…あ、熱を\n測ってくれたんですか\nありがとうございます\nバッチリ元気ですよ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7690, 1, 1, 126101, 1, 1, 0, "お兄の、けはい…\nん、みつけた\nぎゅ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7691, 1, 1, 126101, 2, 1, 0, "きょろきょろ\nここも\nぱとろる、するよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7692, 1, 1, 126101, 3, 1, 0, "おうち\nにぎやか\nさみしく、ない", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7693, 1, 1, 126101, 4, 1, 0, "きょだい、おにぎり\nきょだい、肉まん\nきょだい…お兄？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7694, 1, 1, 126101, 5, 1, 0, "はっぴーちゃん\nどこ？\nお兄、たべた？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7695, 1, 4, 126101, 101, 1, 0, "くんくん…この、へや\nお兄の、におい\nすんでる\nすめる？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7696, 1, 8, 126101, 102, 1, 0, "おそうじ、にがて\nでも\nれんしゅう\nきゅっきゅ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7697, 1, 1, 126101, 1, 2, 0, "みみ、さわり、たい？\nんー…だめ…\nむずむず\nなので", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7698, 1, 1, 126101, 2, 2, 0, "くろぷれしあ\nかっこいい？\nん…あざます", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7699, 1, 1, 126101, 3, 2, 0, "お兄の、おうち\nきょうみ、ましまし\nたんけん\nするね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7700, 1, 1, 126101, 4, 2, 0, "なでなで、される…と\nねむ…く…\nぶぅ…ぶぅ…ぶぅ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7701, 1, 4, 126101, 101, 2, 0, "かみ、りぼん\nおてつだい\nよろ\nです", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7702, 1, 8, 126101, 102, 2, 0, "ここ\nすむ\nね？\nまいにち、いっしょ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7703, 1, 1, 126101, 1, 2, 0, "ぐっもにん\nあさごはん\nあさごはん", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7704, 1, 1, 126101, 2, 2, 0, "お兄\nおつかれ？\nれっつ、まいぺーす", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7705, 1, 1, 126101, 3, 2, 0, "ひとりで、ねれる\nでも\nひとりじゃ、なくても\nねれる、よ…？", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7707, 1, 1, 126101, 2, 3, 0, "ふかふか\nパパの、おなか\nみたい", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7708, 1, 1, 126101, 3, 3, 0, "こんにちは\nぷれしあ、です\nおともだち、なろ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7709, 1, 1, 126101, 4, 3, 0, "いい、におい\nでも、お兄\nもっと、いい、におい", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7710, 1, 1, 126101, 5, 3, 0, "とりま\nお肉、やこ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7712, 1, 1, 126101, 7, 3, 0, "もぐもぐ…\nむげん肉まん、も\nきぼう", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7713, 1, 1, 126101, 8, 3, 0, "ぜんぶ、よんだ\nもっと、ぶあついほん\nでも、いーよ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7715, 1, 1, 127901, 1, 1, 0, "幸せなクリスマスを\n過ごすため\nギルドハウスを\nきれいにしなくちゃ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7716, 1, 1, 127901, 2, 1, 0, "えへへ♪\n彼へのプレゼント…\n枕元にそっと\nおいておきましょう☆", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7717, 1, 1, 127901, 3, 1, 0, "クリスマスといえば\n七面鳥の丸焼き…☆\nよだれを拭いて\nいざ準備です♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7718, 1, 1, 127901, 4, 1, 0, "彼に用意した\nプレゼント…\n大きすぎて\nくつ下に入りません…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7719, 1, 1, 127901, 5, 1, 0, "プレゼントは\n貰う方も、贈る方も\nどちらも幸せを\n感じますね…☆", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7720, 1, 4, 127901, 101, 1, 0, "夜景が見える\nレストランであの人と\nクリスマスデート…\n憧れちゃいますね☆", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7721, 1, 8, 127901, 102, 1, 0, "プレゼントはわたし…\n恥ずかしいですけど\nこの贈り物、彼に\n喜んでほしいです…☆", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7722, 1, 1, 127901, 1, 2, 0, "オーナメントを\nたくさん用意したので\n二人でツリーに\n飾りつけましょう☆", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7723, 1, 1, 127901, 2, 2, 0, "あはは、お腹\n空いちゃいました？\nパーティーまで\n我慢してください♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7724, 1, 1, 127901, 3, 2, 0, "ジングルベ～ル♪\nあなたも一緒に\nクリスマスソング\n歌っちゃいましょう♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7725, 1, 1, 127901, 4, 2, 0, "サプライズプレゼント\nの用意をして…って\nあなた！？　い、今の\n聞いてませんよね…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7726, 1, 4, 127901, 101, 2, 0, "クリスマスの夜は\nパーティーしましょう\nできれば…その…\n二人っきりで…☆", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7727, 1, 8, 127901, 102, 2, 0, "あなたと過ごした\n最高のクリスマス…\nずっとずっと心の奥に\nしまっておきますね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7728, 1, 1, 127901, 1, 2, 0, "おはようございます！\n皆の願いが叶うような\n素敵な一日に\nしましょうね☆", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7729, 1, 1, 127901, 2, 2, 0, "お昼ですね！\nディナーの買い出しが\n済んだら、少しお昼寝\nしちゃいましょうか☆", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7730, 1, 1, 127901, 3, 2, 0, "そろそろ就寝の\n時間ですね～\n枕元にくつ下の準備を\nお忘れなく～☆", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7732, 1, 1, 127901, 2, 3, 0, "ふ～気持ちいい…☆\nお家でまったり過ごす\nクリスマスも\nいいですよね…♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7733, 1, 1, 127901, 3, 3, 0, "かわいいですね～♪\nサンタさんからの\n贈り物でしょうか～？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7734, 1, 1, 127901, 4, 3, 0, "すう～…はあ～…\nすごく素敵な香り…\n炊き立てのご飯と\nいい勝負です…♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7735, 1, 1, 127901, 5, 3, 0, "暖かいですけど…\nあの日の山小屋を\n思い出して\n恥ずかしいですね…☆", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7737, 1, 1, 127901, 7, 3, 0, "むしゃむしゃもぐもぐ\n止まりませんよ\nクリスマスですから\nむしゃむしゃもぐもぐ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7738, 1, 1, 127901, 8, 3, 0, "物語に出てくる\nサンタさんみたいに\nわたしも幸せを\n運びたいです…☆", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7740, 1, 1, 127901, 14, 3, 0, "わっ！\nこ…これは素敵な\nサプライズプレゼント\nですね…☆", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7741, 1, 1, 127901, 15, 3, 0, "これを着ればわたしも\n立派なサンタさんに\nなれますかね…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7742, 1, 12, 106601, 103, 1, 0, "ここが今のあたしの\n居場所ってやつです\nふふっ、なかなか\n悪くねー気分ですよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7743, 1, 12, 106601, 103, 2, 0, "これからはボス代行\nとしてブイブイ\nいわせてやるです\nついてくるですよ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7744, 1, 1, 128001, 1, 1, 0, "陛下～♪\n貴女のクレジッタが\nおめかしして\nはせ参じましたわ～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7745, 1, 1, 128001, 2, 1, 0, "パーティーとは景気の\n良さを見せつける場…\n倹約などは\nもっての外でしてよ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7746, 1, 1, 128001, 3, 1, 0, "この天井の高さなら\n搬入できそうですわね\n陛下用の特注ケーキ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7747, 1, 1, 128001, 4, 1, 0, "…ぶぇっくしゅ！\nふう…少し背中を\n開けすぎたかしら…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7748, 1, 1, 128001, 5, 1, 0, "ここは秘書の目も\n届きませんから\nチクりメモの心配も\nありませんのよね～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7749, 1, 4, 128001, 101, 1, 0, "いくらでも二度見して\nよろしくてよ？\n本当にわたくしだと\n心から納得できるまで", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7750, 1, 8, 128001, 102, 1, 0, "袖の下以外の贈り物も\nなかなかどうして…\n悪くないものですのね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7751, 1, 1, 128001, 1, 2, 0, "こう見えてダンスの\nたしなみはありますの\nお相手してあげても\nよろしくってよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7752, 1, 1, 128001, 2, 2, 0, "ドレスはもちろん\n特注品ですわ♪\nもしや貸衣装だと\n思いまして？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7753, 1, 1, 128001, 3, 2, 0, "バッグの中身？\n仕事に必要なものと…\n心を潤わせるための\n宝石がいくつか♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7754, 1, 1, 128001, 4, 2, 0, "やる気というものは\n仕事に必要な条件です\nいわばドレスコードの\nようなものですわね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7755, 1, 4, 128001, 101, 2, 0, "全ての仕事は自分の\n血となり肉となり\nお金にもなるもの\nなのです。経験談です", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7756, 1, 8, 128001, 102, 2, 0, "今年の教訓を活かして\n来年は更に素晴らしい\nクリスマスにして\n差し上げますわ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7757, 1, 1, 128001, 1, 2, 0, "おはようございます\n今日という稼ぎ時の\nはじまりですわよ！", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7758, 1, 1, 128001, 2, 2, 0, "安くて早くておいしい\nそれがランチに必須の\n栄養素ですわ！", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7759, 1, 1, 128001, 3, 2, 0, "宵越しの仕事は\n安眠の敵！\n熟睡したいのであれば\n今が勝負時ですわよ！", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7761, 1, 1, 128001, 2, 3, 0, "たまにはこうして\n休息を取ることも…\n…はっ！？\nドレスにシワが～っ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7762, 1, 1, 128001, 3, 3, 0, "…わたくしの勘が\n言っていますわ\nこの愛らしさ！\n爆売れ確・定！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7763, 1, 1, 128001, 4, 3, 0, "いい香りですわね…\nお金の匂いには\n敵いませんけれども♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7764, 1, 1, 128001, 5, 3, 0, "……ハッ！？\n完全にボーッとして\nしまいましたわ…！\nぬくもり…恐ろしい！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7766, 1, 1, 128001, 7, 3, 0, "考えませんわ…\n考えませんわよ！\nカロリーのことはっ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7767, 1, 1, 128001, 8, 3, 0, "ビジネス本以外の本を\n読むのも、たまには\n悪くありませんわね…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7769, 1, 1, 128001, 14, 3, 0, "よくできていますわね\nこの絵本！\n子ども向けだと甘く\n見ていましたわ…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7770, 1, 1, 128001, 15, 3, 0, "この視界の狭さ…\n思い出しますわね\nティッシュ配りの\nアルバイト時代を…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7771, 1, 1, 128101, 1, 1, 0, "ふふふ…こっそり\nスイーツを食べる\nリノちゃん見～っけ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7772, 1, 1, 128101, 2, 1, 0, "お姉ちゃんの抜き打ち\nお部屋チェック～♪\n…ケーキの食べこぼし\nこれはリノちゃんだね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7773, 1, 1, 128101, 3, 1, 0, "この衣装は目立つから\nどこにいても弟くんに\n気付いてもらえるね！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7774, 1, 1, 128101, 4, 1, 0, "弟くん、協力して！\nリノちゃんにお勉強\nしなさいって圧を\nかけるんだよっ…♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7775, 1, 1, 128101, 5, 1, 0, "絆の力を感じるよ\nこれならもっと万全な\n弟くんのお世話が\nできそうだねっ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7776, 1, 4, 128101, 101, 1, 0, "お姉ちゃんもたまには\nリノちゃんみたいに\n甘えてみようかな…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7777, 1, 8, 128101, 102, 1, 0, "弟くんとリノちゃん\nどっちも大切な\n私のきょうだいだよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7778, 1, 1, 128101, 1, 2, 0, "分かってるよ♪\nお姉ちゃんの膝枕を\nご所望だねっ？　え？\n違う？　違わないよ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7779, 1, 1, 128101, 2, 2, 0, "リノちゃんがね\nお姉ちゃんチョップを\n防ぐ妹白刃取りを\n訓練してるんだよね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7780, 1, 1, 128101, 3, 2, 0, "お姉ちゃん頭突きは\n封印しないとね…\nだって、今使うと\n頭飾りで威力が…ね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7781, 1, 1, 128101, 4, 2, 0, "ふふっ♪\n目をつぶってても\n弟くんは見えてるよ\n…そこでしょ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7782, 1, 4, 128101, 101, 2, 0, "お姉ちゃんも\nリノちゃんみたいに\nかわいさいっぱいに\n振舞っちゃうぞ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7783, 1, 8, 128101, 102, 2, 0, "弟くんっ♪\nいつもお姉ちゃんの\n声が届く範囲にいてね\n…絶対だよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7784, 1, 1, 128101, 1, 2, 0, "おはよう、弟くん！\nリノちゃんが起こして\nくれたんだね♪\n…先を越されちゃった", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7785, 1, 1, 128101, 2, 2, 0, "お姉ちゃんランチの\n時間だぞ♪\nさ、食べさせてあげる\nお口を開けて…♪", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7786, 1, 1, 128101, 3, 2, 0, "おやすみの時間だよ\n…弟くんは真ん中ね\n左右には私たち姉妹が\nいるから安心だよ♪", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7788, 1, 1, 128101, 2, 3, 0, "お姉ちゃんは全力で\nリラックスしてても\n弟くんの声一つで\nすぐにでも動けるよ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7789, 1, 1, 128101, 3, 3, 0, "リノちゃんこういうの\n好きそうだよね\n今度プレゼントして\nみようかな？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7790, 1, 1, 128101, 4, 3, 0, "いい匂いがするね～\nあれ？\nリノちゃんのケーキの\n甘い匂いも混ざってる", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7791, 1, 1, 128101, 5, 3, 0, "お姉ちゃんの\n弟くんへの愛も\n熱く燃えあがるね…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7793, 1, 1, 128101, 7, 3, 0, "たくさん食べて\n栄養を補給して\nお姉ちゃん力に\n全て変換するよっ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7794, 1, 1, 128101, 8, 3, 0, "ことわざ辞典…\nリノちゃんにこれを\n読ませようかな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7797, 1, 1, 128201, 1, 1, 0, "そろ～り、そろり…\nお姉ちゃんが居ぬ間に\nスイーツを～…♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7798, 1, 1, 128201, 2, 1, 0, "いつか\nお姉ちゃんに似合う服\n作ってみたいですね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7799, 1, 1, 128201, 3, 1, 0, "あいたっ！？\nひらひらしたとこ\n踏んづけちゃいました", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7800, 1, 1, 128201, 4, 1, 0, "お姉ちゃんと\nお兄ちゃんの二人から\n勉強しろって圧力が～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7801, 1, 1, 128201, 5, 1, 0, "私の中にも秘めた力が\n眠っているはず…\nそう、妹パワーが", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7802, 1, 4, 128201, 101, 1, 0, "私もお兄ちゃんを\n甘やかしてみたいです\n…お姉ちゃんみたいに", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7803, 1, 8, 128201, 102, 1, 0, "お兄ちゃんの部屋を\nこっそり拝見～♪\nはっ！？　これは\nお姉ちゃんの視線…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7804, 1, 1, 128201, 1, 2, 0, "お姉ちゃん頭突きは\n大地を揺らす威力です\n私とお兄ちゃんは\n挨拶感覚で喰らいます", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7805, 1, 1, 128201, 2, 2, 0, "お姉ちゃんチョップは\n雲を裂く切れ味です\n早く妹白刃取りを\n完成させないと…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7806, 1, 1, 128201, 3, 2, 0, "お姉ちゃんみたいに\n大人っぽく着こなす\nには一体どうしたら…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7807, 1, 1, 128201, 4, 2, 0, "弓のデザインも\nかわいいんですよっ♪\n常に持ち歩きたいです\nけど…危ないですよね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7808, 1, 4, 128201, 101, 2, 0, "きれいな服を着てると\nお姫さま気分に\nなれますね…あとは\n王子さまがいれば…♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7809, 1, 8, 128201, 102, 2, 0, "お兄ちゃんっ！\n私は褒められて伸びる\nタイプなのでもっと\nナデナデしてください", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7810, 1, 1, 128201, 1, 2, 0, "妹モーニングコール♪\nお姉ちゃんより先に\n起こしに来れました！", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7811, 1, 1, 128201, 2, 2, 0, "ああっ、お姉ちゃん！\nお兄ちゃんにアーン\nするなんてずるいです\n私もしたいですっ！", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7812, 1, 1, 128201, 3, 2, 0, "おやすみの時間ですね\nえへへ、きょうだいで\n並んで寝るのって\n楽しいですよね！", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7814, 1, 1, 128201, 2, 3, 0, "スイーツを食べた後は\nお腹を休めましょう～\n平気平気…ちゃぁんと\n運動もしますからぁ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7815, 1, 1, 128201, 3, 3, 0, "お姉ちゃんも\nこういうかわいいもの\n好きなんですかね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7816, 1, 1, 128201, 4, 3, 0, "くんくん…\nなんの匂いでしょうか\n新しいスイーツ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7817, 1, 1, 128201, 5, 3, 0, "きょうだい３人\n肩を寄せて暖まるとか\nよくないですかっ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7819, 1, 1, 128201, 7, 3, 0, "いつも元気な妹で\nいるためには\nよく食べるのが大事！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7820, 1, 1, 128201, 8, 3, 0, "なんか姉に関する本が\nちらほらと混ざって…\nこれお姉ちゃんの\n仕業ですね！？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7823, 1, 1, 128301, 1, 1, 0, "ふんふんふ～ん\nお正月で世間は\n浮かれてるですねえ\nま、いいですけど♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7824, 1, 1, 128301, 2, 1, 0, "は～、お正月は\nだらだらできて\n最高ですね\n…いつも通りですけど", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7825, 1, 1, 128301, 3, 1, 0, "ビッグな幹部は\nビッグなお年玉が\nもらえるはずです\n楽しみですね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7826, 1, 1, 128301, 4, 1, 0, "今度ドラちゃんに\nお年玉を持っていって\nやりたいですね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7827, 1, 1, 128301, 5, 1, 0, "そういえばアユアユと\n約束してたですよ…\nそろそろ出かける\nですかね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7828, 1, 4, 128301, 101, 1, 0, "お年玉がもらえたら\n何に使うか…\n今のうちに\n考えとくです", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7829, 1, 8, 128301, 102, 1, 0, "年神の報酬の駄菓子が\nたまってきたですね…\nガキどもにでも\n配っちまうですよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7830, 1, 1, 128301, 1, 2, 0, "あたしのレアな\n晴れ着姿、今のうちに\nしっかり目に焼き付け\nとくがいいですよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7831, 1, 1, 128301, 2, 2, 0, "お兄さんは今日も\nヒマそうですねぇ…\nしょうがないから\nかまってやるですよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7832, 1, 1, 128301, 3, 2, 0, "そういえばカヤぴぃ\n見なかったですか？\n喧嘩屋初売りセール\nとか言ってたですけど", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7833, 1, 1, 128301, 4, 2, 0, "お正月ならではの\n大きなシノギ…\nなにかないですかね？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7834, 1, 4, 128301, 101, 2, 0, "いいとこに来たですよ\nさっき走ったら帯が\n曲がっちゃったです\n直すの手伝うです！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7835, 1, 8, 128301, 102, 2, 0, "年神の仕事ですか？\nこれからいくとこです\nお兄さんも一緒に\n来やがるですよ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7836, 1, 1, 128301, 1, 2, 0, "ふぁあ…\nどうしたですか？\nお正月に早起きなんて\n無粋ってヤツですよ…", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7837, 1, 1, 128301, 2, 2, 0, "小腹が減ったですね\n一緒におせちでも\nつつきにいくですよ！", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7838, 1, 1, 128301, 3, 2, 0, "夜はあたしの時間\nと言いたいとこですが\n初夢のために早く\n寝とくですかね…", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7840, 1, 1, 128301, 2, 3, 0, "正月は食っちゃ寝\nするのが仕事…\nって聞いたこと\nあるですよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7841, 1, 1, 128301, 3, 3, 0, "ふふっ、晴れ着の\nあたしの次くらいには\nかわいいですね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7842, 1, 1, 128301, 4, 3, 0, "なんかいい匂い…？\nよく知らんですけど\n晴れ着からも同じ\n匂いがしてるですね…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7843, 1, 1, 128301, 5, 3, 0, "ふ～あったまるです…\nお正月の唯一の弱点は\n寒いことですからね…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7845, 1, 1, 128301, 7, 3, 0, "もぐもぐ…正月太り？\nあたしには関係ない\n言葉ですね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7846, 1, 1, 128301, 8, 3, 0, "あったあった…\nトーゴクには\nドラゴンの神宮も\nあるんですね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7848, 1, 1, 128401, 1, 1, 0, "お正月のトラブルも\nハツネちゃんが\nめでたく解決\nしてみせるのだ～！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7849, 1, 1, 128401, 2, 1, 0, "そうだ、新年会に\nサプライズ大作戦を\nしかけちゃおう！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7850, 1, 1, 128401, 3, 1, 0, "イノリちゃんも\nカスミンも振袖姿が\nかわいかったな～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7851, 1, 1, 128401, 4, 1, 0, "シオリンに\nお年玉あげたいし\n彼にバイトを紹介\nしてもらおうかな？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7852, 1, 1, 128401, 5, 1, 0, "今度はどんな凧を\n飛ばそうかな？\nみんなが驚くような\n凧がいいよね～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7853, 1, 4, 128401, 101, 1, 0, "ひらめいた！\n彼への年賀状は\nきらーんっ☆って\n感じにしよう！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7854, 1, 8, 128401, 102, 1, 0, "来年も彼の隣で\n初日の出が\n見られるといいな～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7855, 1, 1, 128401, 1, 2, 0, "今日はキミが\nギルドハウスに来る\n予知夢を見たんだよ～\nふふ、正夢になったね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7856, 1, 1, 128401, 2, 2, 0, "この振袖どうかな？\nすっごくかわいいよね\nえへへ、ありがとう～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7857, 1, 1, 128401, 3, 2, 0, "ハツネちゃんおみくじ\n開始！　きらーんっ☆\nキミは大吉なのだ～\nどう元気出たかな？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7858, 1, 1, 128401, 4, 2, 0, "今度エルフの森にある\n薬草を使って七草粥を\n作ろうと思うんだ～\nぜひ食べてほしいな♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7859, 1, 4, 128401, 101, 2, 0, "お正月っぽい遊びを\n色々と用意したのだ～\nたまにはギルドハウス\nでゆっくり過ごそうよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7860, 1, 8, 128401, 102, 2, 0, "もしかして眠たいの？\n…じゃあ、また膝枕\nしてあげてもいいよ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7861, 1, 1, 128401, 1, 2, 0, "いっぱい寝たから\n元気いっぱいだよ～\n今日もがんばろう！", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7862, 1, 1, 128401, 2, 2, 0, "お昼だね～\nおせちがあるから\nキミも食べるでしょ？", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7863, 1, 1, 128401, 3, 2, 0, "そろそろ寝る時間だね\n今日はどんな夢を\n見られるかな～", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7865, 1, 1, 128401, 2, 3, 0, "いい座り心地～\n寝正月で過ごすのも\n最高なのだ～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7866, 1, 1, 128401, 3, 3, 0, "かわいいな～\n癒されてぐっすり\n眠れそうだよ～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7867, 1, 1, 128401, 4, 3, 0, "いい香り～\nリラックスすると\n眠たくなっちゃうよ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7868, 1, 1, 128401, 5, 3, 0, "ポカポカいい気持ち～\n初日の出の温もりを\n思い出しちゃうな～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7870, 1, 1, 128401, 7, 3, 0, "もぐもぐ、おいし～\nでもいっぱい食べたら\n眠くなっちゃった…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7871, 1, 1, 128401, 8, 3, 0, "ふむふむ\nお正月料理も\n色々あるんだね～\n今度作ってみようかな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7873, 1, 12, 106501, 103, 1, 0, "最近カレを見てたら\n何故かおいしそうに\n思えてくるんだよな…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7874, 1, 12, 106501, 103, 2, 0, "手握っててくれるか？\nじゃないとオレ…\n力が抑えきれない…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7875, 1, 1, 128501, 1, 1, 0, "つかの間の平和を享受\nしながらぬくぬくと\n読書に耽る…\nふふ、最高だね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7876, 1, 1, 128501, 2, 1, 0, "お正月飾りなんて\n置いたらはしゃいでる\nように見えるかな…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7877, 1, 1, 128501, 3, 1, 0, "ま、まずい…\n一人遊びしすぎて\nお正月遊びをほぼ\n極めつつある…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7878, 1, 1, 128501, 4, 1, 0, "ミステリーは\n時と場所を選ばない\nつまり…新年でも\n探偵は休まないよ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7879, 1, 1, 128501, 5, 1, 0, "お世話になった人に\n年賀状でも書こうかな\n助手くん、喜んで\nくれるかな…えへへっ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7880, 1, 4, 128501, 101, 1, 0, "絵馬に願い事を書こう\n助手くんと一緒に\nいられますように…♪\n…は、恥ずかしいな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7881, 1, 8, 128501, 102, 1, 0, "助手くんはもっと\n派手な着物のほうが\n好みだったかな…？\nき、気になる…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7882, 1, 1, 128501, 1, 2, 0, "やあ。助手くんも\nお正月休みを\n持て余してるのかい？\nふふ、似た者同士だね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7883, 1, 1, 128501, 2, 2, 0, "助手くん助手くん！\n凧あげしにいこう！\n…そ、捜査の一環で", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7884, 1, 1, 128501, 3, 2, 0, "着物は転びそうで\n怖いんだよなあ…\nお、押さないでね？\nフリじゃないよ！？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7885, 1, 1, 128501, 4, 2, 0, "ひ、人目を憚る\n本を読むなら\nみんなが帰省中の今！\nはっ！　助手くん！？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7886, 1, 4, 128501, 101, 2, 0, "ね、ねえ…助手くん…\n一緒に初詣いかない？\nあ、いや！　深い\n意味はないんだけど！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7887, 1, 8, 128501, 102, 2, 0, "あはは…助手くんと\n一緒だと家でごろごろ\nしてるだけでも\n楽しいや…♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7888, 1, 1, 128501, 1, 2, 0, "ふぁ～…\nお正月休みのおかげで\n昼夜逆転中だよ…", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7889, 1, 1, 128501, 2, 2, 0, "お正月も依頼が\nひっきりなしだね\nさあ、街へ出よう！\nもちろん君も一緒にね", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7890, 1, 1, 128501, 3, 2, 0, "も、もっと\n近くにきてもいいよ？\n冬の夜は…寒いからね", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7892, 1, 1, 128501, 2, 3, 0, "ううぅ～…！\nこのままじゃ\n寝正月だぁ…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7893, 1, 1, 128501, 3, 3, 0, "みんなが帰省中でも\nこれがあれば\n寂しくない…かな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7894, 1, 1, 128501, 4, 3, 0, "うーん…♪\nお正月休みの\nリフレッシュに\nちょうどいいや♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7895, 1, 1, 128501, 5, 3, 0, "ふぅ…あったまるな\n冬は寒いから\n助かるよ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7897, 1, 1, 128501, 7, 3, 0, "うーん、おいしい♪\n読書のお供に\nちょうどいい", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7898, 1, 1, 128501, 8, 3, 0, "時間に余裕があると\n読書が進むよ\nいざ、本棚読破だ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7900, 1, 1, 128801, 1, 1, 0, "この新しい力…\nさらに使いこなせる\nようになるには…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7901, 1, 1, 128801, 2, 1, 0, "私に眠る属性の力…\n風の力以外には\nどんな力があるのかな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7902, 1, 1, 128801, 3, 1, 0, "今の私なら\nどこまでいけるのか…\nやれるだけ\nやってみよう", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7903, 1, 1, 128801, 4, 1, 0, "風の力の制御…\n段々とコツが\n掴めてきたな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7904, 1, 1, 128801, 5, 1, 0, "風の力か…\n応用の幅は広そうだ\n何ができそうか\n色々試してみよう", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7905, 1, 4, 128801, 101, 1, 0, "今度、私とキミで\n料理対決でもする？\n簡単には負けないよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7906, 1, 8, 128801, 102, 1, 0, "この胸に溢れる\n温かな気持ちは…\nきっと彼への…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7907, 1, 1, 128801, 1, 2, 0, "一段と剣技が\n冴えてきたような…\nいや…油断禁物だ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7908, 1, 1, 128801, 2, 2, 0, "かっこいいって？\nありがとう…そんなに\n真っすぐ褒められると\n照れてしまうな…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7909, 1, 1, 128801, 3, 2, 0, "どれほど厚い壁が\n立ち塞がろうと\n打ち破ってみせる", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7910, 1, 1, 128801, 4, 2, 0, "手が空いてるなら\n剣の稽古に少し\n付き合ってくれるかな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7911, 1, 4, 128801, 101, 2, 0, "試練を乗り越えて…\n私の中にあるものが\nはっきりと分かったよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7912, 1, 8, 128801, 102, 2, 0, "キミから流れてくる\n力の温かさ…これが\n私たちの絆なんだ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7913, 1, 1, 128801, 1, 2, 0, "朝だよ、そろそろ\n起きる時間だ\nそれとも私に起こして\nほしいのかな？", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7914, 1, 1, 128801, 2, 2, 0, "昼食は私が作るよ\nリクエストはある？\nできる範囲で応えるよ", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7915, 1, 1, 128801, 3, 2, 0, "おやすみ。また明日も\n一緒に冒険しよう\n約束だよ？", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7917, 1, 1, 128801, 2, 3, 0, "休める時は休む…\n戦い続けるには\n必要なことだからね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7918, 1, 1, 128801, 3, 3, 0, "心の安らぎの大切さ…\nそれを知れたのは\nキミたちのおかげだね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7919, 1, 1, 128801, 4, 3, 0, "気持ちが落ち着くな…\nこの香り、風の力で\n纏えたりしないかな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7920, 1, 1, 128801, 5, 3, 0, "暖まった空気の流れ…\n以前より風の変化に\n気づくようになったな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7922, 1, 1, 128801, 7, 3, 0, "この味付け…\n今度試してみようか\n味見は任せたからね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7923, 1, 1, 128801, 8, 3, 0, "心技体、すべてを\n鍛えるには座学も\n必要だからね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7925, 1, 1, 128901, 1, 1, 0, "瞑想するのに\nいいところはあるかな\n集中できて…お昼寝は\n我慢できるところ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7926, 1, 1, 128901, 2, 1, 0, "ここにはあったかい\n空気が流れてるなぁ…\nみんなが受け入れて\nくれるおかげだね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7927, 1, 1, 128901, 3, 1, 0, "まるで心の中に\n光が満ちてるみたい…\nこの気持ち、みんなに\nおすそ分けしたいな♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7928, 1, 1, 128901, 4, 1, 0, "この衣装は属性の力を\n反映しやすいんだよね\nキラキラしてるのも\n納得だよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7929, 1, 1, 128901, 5, 1, 0, "エルフの里のみなさん\n元気かなぁ…\nまた会いに行きたいな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7930, 1, 4, 128901, 101, 1, 0, "まだ変身できてる…\nわたしと騎士クンの\n修行の成果\n出てるなぁ…ふふっ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7931, 1, 8, 128901, 102, 1, 0, "この服を脱いだら\nどうなるのかな…\nた、試す勇気は\nないけど…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7932, 1, 1, 128901, 1, 2, 0, "ふぅ…あ、騎士クン\nちょうど瞑想してたの\n一緒に…する？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7933, 1, 1, 128901, 2, 2, 0, "不思議だけど\nいつの間にか\n出たり消えたりするの\n…背中の輪っか！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7934, 1, 1, 128901, 3, 2, 0, "この服って肌触りが\nとってもいいんだよ\nさ、触ってみる？\nスベスベだよ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7935, 1, 1, 128901, 4, 2, 0, "アストラルの力…\nすごく持続できてるね\n修行の成果が出てるよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7936, 1, 4, 128901, 101, 2, 0, "この力があれば\n暗いところで\n落とし物をしても\n見つけやすいかも…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7937, 1, 8, 128901, 102, 2, 0, "冒険の準備なら\nできてるよ\nいつでも声をかけてね\n…楽しみにしてる！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7938, 1, 1, 128901, 1, 2, 0, "おはよう騎士クン\n一緒にお散歩とか…\nどうかな？", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7939, 1, 1, 128901, 2, 2, 0, "お昼ご飯のあとの\n瞑想って難しいんだよ\n眠くなっちゃって…", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7940, 1, 1, 128901, 3, 2, 0, "夜ふかしさんは\nほどほどにね？\n一日頑張った自分を\n休ませてあげないと", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7942, 1, 1, 128901, 2, 3, 0, "のんびりしてたら\nケーキが食べたく\nなっちゃうなぁ…\nでも太る…よね…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7943, 1, 1, 128901, 3, 3, 0, "わあ、かわいい…！\n見てるだけで\nにやけちゃう…♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7944, 1, 1, 128901, 4, 3, 0, "これは…花の香りかな\n知ってる花かも…？\n当ててみようっ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7945, 1, 1, 128901, 5, 3, 0, "あったかいと\nどうしてこんなに\n落ち着くんだろう…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7947, 1, 1, 128901, 7, 3, 0, "食べ過ぎたらお腹が\n目立っちゃうかもって\n分かってるのに…！\n止められないよ～っ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7948, 1, 1, 128901, 8, 3, 0, "こ、恋の精霊とか\nいたりするのかな…？\nはっ！　ダメダメっ！\n実力で頑張らないと", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7950, 1, 1, 128701, 1, 1, 0, "お部屋の中でも\n修行、修行っ♪\nでも燃やさないように\n注意しなきゃだね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7951, 1, 1, 128701, 2, 1, 0, "うーん…この衣装\n厚着か薄着か\nどっちなんだろう…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7952, 1, 1, 128701, 3, 1, 0, "いつもの肉球がない分\nお片付けしやすいな♪\n騎士くんのお部屋\nピカピカにしとこー！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7953, 1, 1, 128701, 4, 1, 0, "お休みは騎士くんと\nＢＢＱに行きたいなー\nあたしと一緒なら\n火の心配はいらないよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7954, 1, 1, 128701, 5, 1, 0, "この姿ならもっと\n人を助けられる…！\nえへへっ！\n燃えてきたーーっ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7955, 1, 4, 128701, 101, 1, 0, "騎士くんが帰ったら\nアイラみたいな笑顔で\nお疲れさまさまって\nお出迎えしよっと…♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7956, 1, 8, 128701, 102, 1, 0, "この姿の時、いつも\n胸が熱く燃えてるのは\nずっと騎士くんのこと\n考えてるからかな…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7957, 1, 1, 128701, 1, 2, 0, "騎士くん騎士くん！\nあたしに触れると\nやけどしちゃうよ…？\nなんちゃってー♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7958, 1, 1, 128701, 2, 2, 0, "おっ、騎士くん\n修行に行きたいの？\nえへへっ、いいねー♪\n二人で燃えちゃおー！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7959, 1, 1, 128701, 3, 2, 0, "そうだ騎士くん\nじゃんけんしよっか？\nグローブ外してるから\nチョキも出せるよっ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7960, 1, 1, 128701, 4, 2, 0, "この姿でいられる時間\nすごく増えてきた…！\nえへへ！　あたしたち\n修行の成果出てるね！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7961, 1, 4, 128701, 101, 2, 0, "じっと見つめないでよ\n顔が真っ赤に…やば！\nほんとに顔から\n火噴いちゃいそう…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7962, 1, 8, 128701, 102, 2, 0, "火には強くなれても\n騎士くんに触られた\nほっぺはずーっと\n熱くてたまんないや…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7963, 1, 1, 128701, 1, 2, 0, "おはよー騎士くん！\n今日も一日\n燃え滾っていくよー！\nファイヤーッ！！", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7964, 1, 1, 128701, 2, 2, 0, "騎士くんお昼だよ！\nちゃんとご飯食べて\n午後からの修行も\n乗り切ろうねー！", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7965, 1, 1, 128701, 3, 2, 0, "今日もお疲れさま！\nそうだ、寝る前に\nキャンプファイヤー\n囲んじゃおっか♪", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7967, 1, 1, 128701, 2, 3, 0, "ぼふーんっ！\nえへへ、気持ちいい…\n修行の前に\nちょっと休憩ー♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7968, 1, 1, 128701, 3, 3, 0, "かわいいな～♪\nなでなでなでなでなで\nなでなでなでなでっ！\nあははっ、摩擦熱！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7969, 1, 1, 128701, 4, 3, 0, "ん～いい香り♪\n少し熱したほうが\n香りが広がるね…って\n蒸発しちゃった！！！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7970, 1, 1, 128701, 5, 3, 0, "外でも自分の火で\nあったまれるけど\nお部屋でぬくぬく\nするのが一番だよね♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7972, 1, 1, 128701, 7, 3, 0, "もぐもぐっ！\nお腹が減っては\n人助けはできずっ！\nもぐもぐもぐっ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7973, 1, 1, 128701, 8, 3, 0, "ふむふむ…\nなるほどなるほど…\n…これが上手な\nＢＢＱのやり方か…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7975, 1, 1, 129001, 1, 1, 0, "…壁も扉も柱も\n全て記録で見たまま\n本当に…来ているのね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7976, 1, 1, 129001, 2, 1, 0, "…騒がしい場所\n騎士クンが受け入れて\nくれるのをいいことに", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7977, 1, 1, 129001, 3, 1, 0, "ここが…騎士クンの\n守りたかった場所\n騎士クンの思い出が\n詰まった場所…ね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7978, 1, 1, 129001, 4, 1, 0, "視線を感じる…\nこちらの様子を\n探るような…心の動き\n取るに足らないけれど", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7979, 1, 1, 129001, 5, 1, 0, "…そこかしこで…\n言葉にもしたくない\n相手が過ごしていた\n記録を思い出す…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7980, 1, 4, 129001, 101, 1, 0, "人は多いけれど…\n雪深い山中よりは\n過ごしやすい…か", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7981, 1, 8, 129001, 102, 1, 0, "自覚したことは\nあまりなかったけれど\nひどく…諦めの悪い女\nなのかもしれない", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7982, 1, 1, 129001, 1, 2, 0, "お招きありがとう\nそれで…何をして\nいればいい…かな？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7983, 1, 1, 129001, 2, 2, 0, "ここは…明るいね\nわたしの城と比べると\nなんだか眩しいくらい", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7984, 1, 1, 129001, 3, 2, 0, "仮面はもう必要ないよ\nだって…わたしの顔を\n騎士クンに見て\nもらえなくなるから", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7985, 1, 1, 129001, 4, 2, 0, "騎士クンさえよければ\nわたしの城に専用の\n部屋を用意するよ\n…いつでも", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7986, 1, 4, 129001, 101, 2, 0, "…騎士クンのマント\nまた貸してほしいな\nきれいに使うから\n…だめ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7987, 1, 8, 129001, 102, 2, 0, "いつか二人の願いが\n重なり合えるって…\n信じているね\n…いつまでだって…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7988, 1, 1, 129001, 1, 2, 0, "おはよう、騎士クン\n…昨日は夢の中で\nお話ししすぎちゃって\nごめんね。ふふっ", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7989, 1, 1, 129001, 2, 2, 0, "お腹は…空いてない？\n食べ物はないけれど\n空腹を忘れる魔法なら\nかけてあげられるよ", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7990, 1, 1, 129001, 3, 2, 0, "騎士クン…\nまだ、眠らないの？\n…そう。それなら…\nわたしも起きているね", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7992, 1, 1, 129001, 2, 3, 0, "肩の力を抜くには…\nまだ時間がかかりそう\nきっと…騎士クンと\n二人きりなら別なのに", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7993, 1, 1, 129001, 3, 3, 0, "ぺんぽこりん…\nそう、確かあれも\nこんな感じだった…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7994, 1, 1, 129001, 4, 3, 0, "花の香りは…好き\nつかの間\n心を穏やかに\nしてくれるから", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7995, 1, 1, 129001, 5, 3, 0, "あたたかい…けれど\n騎士クンの優しさに\n比べれば、まだまだ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7997, 1, 1, 129001, 7, 3, 0, "おい、しい…？\nこの感覚が、それ？\nなぜか…納得できる", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (7998, 1, 1, 129001, 8, 3, 0, "情報を入手するには\nわざわざ本を開く\n必要があるだなんて…\nプレイヤーは面倒ね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8000, 1, 1, 129301, 1, 1, 0, "メルクリゾートか…\n良い記憶はないな\nよし忘れよう", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8001, 1, 1, 129301, 2, 1, 0, "知性の戦士ユニ！\nその恐るべき筋力！\nふおお！　うにゃー！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8002, 1, 1, 129301, 3, 1, 0, "ぽーん！\n石用の水着ください！\nセクシーロゼッタが、\n渚の視線を独占です！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8003, 1, 1, 129301, 4, 1, 0, "学院から奨学金を\n勝ち取るより某財団を\nだまくらかした方が\n早い気がしてきたな…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8004, 1, 1, 129301, 5, 1, 0, "わんつぅ、わんつぅ…\nんぎゃあ足攣ったー！\nおのれカルミナとやら\n絶対に許さん！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8005, 1, 4, 129301, 101, 1, 0, "ところで…なぜぼくは\nプールでもないのに\n水着のままなんだ…\nおのれ世界め！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8006, 1, 8, 129301, 102, 1, 0, "彼と二人で撮った動画\nうう、凝視に耐えん…\nなんだこのムズ痒さは", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8007, 1, 1, 129301, 1, 2, 0, "なに？　水着姿で\n寒くないのかだと？\n普段からもっと薄着の\n娘が沢山いるだろう", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8008, 1, 1, 129301, 2, 2, 0, "シノブ君にメリゾの\n招待状を送ったのだが\n御尊父の暴走を\n心配していたな…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8009, 1, 1, 129301, 3, 2, 0, "ミミ君らにメリゾの\n招待状を送ったのだよ\n迎える準備をせねば…\nおしゃぶり持ってる？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8010, 1, 1, 129301, 4, 2, 0, "本質の賢者…？\nいや元気にしているが\nなぜ君がぼくの身内を\n知っているんだ…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8011, 1, 4, 129301, 101, 2, 0, "水着が似合ってる？\nな、なんだね突然…\nやめたまえよふひひ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8012, 1, 8, 129301, 102, 2, 0, "今度は夏にメリゾへ\n行きたいものだ\nむろん君も一緒だぞ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8013, 1, 1, 129301, 1, 2, 0, "ぐああっ、朝陽が…！\n朝陽が昇っている…！\n夜行性ユニ死んじゃう", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8014, 1, 1, 129301, 2, 2, 0, "はぁ…もう昼か…\nそろそろ寝ないとな\n規則正しい生活を\nしないと不健康だ…", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8015, 1, 1, 129301, 3, 2, 0, "ひゃっはー、夜だー！\nオレたちの時間だぜ！\nふおおブチ上がり～！", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8017, 1, 1, 129301, 2, 3, 0, "これは…ぼくにここで\n寝ろということか？\n一向に構わんが…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8018, 1, 1, 129301, 3, 3, 0, "きゃっきゃっ！\nこれかわい～！\n…よし、今日も\n子供の擬態完璧だな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8019, 1, 1, 129301, 4, 3, 0, "ふむ、良い香りだ\nだがそれだけか…\nとくに精神高揚効果は\nないのか…ちぇー", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8020, 1, 1, 129301, 5, 3, 0, "火を見ていると…\n魔導罐を思いだすな…\nかに座、元気かな…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8022, 1, 1, 129301, 7, 3, 0, "ええい、\n固形食物はいらん！　\nポーションを出せ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8023, 1, 1, 129301, 8, 3, 0, "やはり書は文系に限る\nなんと心地よい筆致…\nぐああ突然理系文が！\nひぃ目が理系になる！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8025, 1, 1, 129301, 10, 3, 0, "ほああ～！\n知性の海上騎士ユニ！\nこれは新たな可能性！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8026, 1, 1, 129301, 11, 3, 0, "これは…なんだ？\n何が面白いんだ？\n難しいな…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8027, 1, 12, 108901, 103, 1, 0, "大切な人と過ごす\n正月の思い出、か…\n昔の私だったら\n考えられなかったな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8028, 1, 12, 108901, 103, 2, 0, "叶えたい願いなら\nたくさんあるよ\nそれでも一番を選ぶ\nなら、やはりキミと…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8029, 1, 12, 108801, 103, 1, 0, "神殿にあった\n恋愛おみくじ…\n引いてみようかな…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8030, 1, 12, 108801, 103, 2, 0, "騎士クン。来年も\nまた一緒におみくじ\n結ぼうね。約束だよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8031, 1, 12, 108701, 103, 1, 0, "今度は騎士くんと\n一緒にうつった写真、\nおばあちゃんに\n送ろうっと♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8032, 1, 12, 108701, 103, 2, 0, "あ、騎士くん！\n今年も…ううん\nこれからも、よろしく\nおねがいします♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8033, 1, 1, 129401, 1, 1, 0, "旧校舎の地下牢…\n真冬のサマー計画…\n等身大のＰＲ…\nうッ、頭が…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8034, 1, 1, 129401, 2, 1, 0, "暇だし鏡でも見るか。\nお、いい腹筋育って…\nあ？　何見てんだコラ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8035, 1, 1, 129401, 3, 1, 0, "オレは金色の疾風！\nんん…なんか違うな。\n我が名は金色の…\nあ？　何見てんだコラ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8036, 1, 1, 129401, 4, 1, 0, "モニカとユニ先輩って\n口調と身長似てるけど\n子供扱いに対する\n反応は真逆だよね…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8037, 1, 1, 129401, 5, 1, 0, "シオリン元気かな…\n昔くちゃくちゃに\n撫でくり回して\n嫌がられたっけなァ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8038, 1, 4, 129401, 101, 1, 0, "べつにいんだけどさ…\nあんたの知り合いって\n普段から水着みたいな\nカッコの女多くない？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8039, 1, 8, 129401, 102, 1, 0, "そだ、弟らがあんたに\n会いたがってたンだ。\n暇だったらいつでも\nウチ来てやってよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8040, 1, 1, 129401, 1, 2, 0, "特別講座は色々あった\nけど…メリゾ自体は\nまあ悪くなかったよね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8041, 1, 1, 129401, 2, 2, 0, "水着と合わせる小物に\nセンス出るよね、\nって話をミィコ先輩が\nしててマジわかりみ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8042, 1, 1, 129401, 3, 2, 0, "水着のミフユねえさん\nやっぱ仕上がってたね\nあれが竜殺しの体か…\nバリかっけーな…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8043, 1, 1, 129401, 4, 2, 0, "はにょんっ！？\nや、だからさ…\n後ろから声かけんな、\nつってンじゃん…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8044, 1, 4, 129401, 101, 2, 0, "メリゾの特別講座が\n上手くいったのも\n敏腕カメラマンがいた\nお陰だよ。ありがとね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8045, 1, 8, 129401, 102, 2, 0, "あ？　この水着が？\n似合っ…え、なんて？\nごめ、聞こえんくて。\nもっかい言ってみて", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8046, 1, 1, 129401, 1, 2, 0, "もう朝とかマジないわ\n昨日二人に付き合って\nオールでカラオケだよ", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8047, 1, 1, 129401, 2, 2, 0, "はぁ授業行かなきゃ…\n真っ昼間から勉強とか\nマジつらたん過ぎ…", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8048, 1, 1, 129401, 3, 2, 0, "今日これから夜勤で…\nいやバイトちゃーくて\nボランティアの話よ？\n校則違反よくない", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8050, 1, 1, 129401, 2, 3, 0, "うわ、ふっかふか…\nオニほど安らぐわ…\nやば、ユニってくる…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8051, 1, 1, 129401, 3, 3, 0, "うちも妹いりゃ\nこーゆーのもなァ…\nま自分用でもいいか", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8052, 1, 1, 129401, 4, 3, 0, "ふーん…\nいいじゃんこの香り\n抜群に嗅げるわ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8053, 1, 1, 129401, 5, 3, 0, "うちン家のルーツの森\n大火災でなくなった\nらしンだよね…\n生まれる前の話だけど", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8055, 1, 1, 129401, 7, 3, 0, "何コレありがてぇー\n食料ほくほく過ぎて草\n米ガチで尊いわァ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8056, 1, 1, 129401, 8, 3, 0, "いいけど、なんでうち\n水着で本読んでンだよ\nどーゆープレイだよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8058, 1, 1, 129401, 10, 3, 0, "金色の海上騎士クロエ\n推して参る！　覇ァ！\nあ？　何見てんだコラ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8059, 1, 1, 129401, 11, 3, 0, "何コレ、貝の椅子？\nどーやって遊ぶんだ…\nいや座って終わりか…\nまァそりゃそーか…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8060, 1, 1, 129801, 1, 1, 0, "さてと…\n明日の訓練メニュー\n考えねえとな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8061, 1, 1, 129801, 2, 1, 0, "また新入りの女子に\n弁当もらっちまった…\nありがたく食うけどよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8062, 1, 1, 129801, 3, 1, 0, "種族なんて関係なく\nみんなが力を合わせて\nランドソルを守る…\nいい時代になったよな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8063, 1, 1, 129801, 4, 1, 0, "誰にだって長所はある\n新入りのいいところを\n見つけてやるのも\n教官の仕事だ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8064, 1, 1, 129801, 5, 1, 0, "厳し過ぎても\n甘過ぎてもダメ…\n教官ってのは難しいぜ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8065, 1, 4, 129801, 101, 1, 0, "戦いが苦手な新入りを\nどうフォローするか…\n後であいつと\n相談してみるか", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8066, 1, 8, 129801, 102, 1, 0, "あいつは優し過ぎて\n戦いに向いてねえ\n…そういう甘さ\n嫌いじゃねえけどな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8067, 1, 1, 129801, 1, 2, 0, "強くなる近道はねえ\n地道な鍛錬あるのみだ\nさ、訓練始めるぞ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8068, 1, 1, 129801, 2, 2, 0, "新入りにも\nいつも言ってるが…\n装備の手入れは\n念入りにしろよな？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8069, 1, 1, 129801, 3, 2, 0, "座学も受けてえのか？\nいい心がけだな！\nなんでも教えてやるよ\n…魔法以外ならな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8070, 1, 1, 129801, 4, 2, 0, "新入りたちがよく\n恋バナしててな…\nお、おまえは…誰か\n気になるやついるか？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8071, 1, 4, 129801, 101, 2, 0, "なあ、ストレッチ\n手伝ってくれねえか？\n訓練前にしっかり\n筋肉ほぐさねえと…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8072, 1, 8, 129801, 102, 2, 0, "ずいぶん頑張って\n鍛えたよな、おまえ\nよし！　ご褒美に\nお菓子作ってやるよ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8073, 1, 1, 129801, 1, 2, 0, "訓練日和だな！\nさぁて…まずは\n走り込みといくか！", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8074, 1, 1, 129801, 2, 2, 0, "昼飯はしっかり食えよ\nでなきゃ、\nこの後もたねえぜ？", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8075, 1, 1, 129801, 3, 2, 0, "とっくに消灯時間だ\nさっさと寝な？\n明日も早いんだからよ", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8077, 1, 1, 129801, 2, 3, 0, "休むのも訓練のうちだ\n今はのんびり\nさせてもらうぜ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8078, 1, 1, 129801, 3, 3, 0, "いい面構えだ\n気に入ったぜ！\nあたしの班に入るか？\n…なんてな♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8079, 1, 1, 129801, 4, 3, 0, "…いやされる匂いだな\n新入りたちにも\nすすめてみるか…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8080, 1, 1, 129801, 5, 3, 0, "あったけえからって\n寝ないようにしねえと\n教官としての\n威厳に関わるからな…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8082, 1, 1, 129801, 7, 3, 0, "食事も訓練の一環だ\nとはいえ食い過ぎには\n気をつけねえと…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8083, 1, 1, 129801, 8, 3, 0, "トーゴクの剣術…か\nなにか訓練に\n活かせるかもしれねえ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8085, 1, 1, 129901, 1, 1, 0, "あのおかたに近付く\n不埒な輩には、特別な\n訓練で地獄を見て\nいただきましょう", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8086, 1, 1, 129901, 2, 1, 0, "これだけ広ければ\n多少派手に\n動き回っても\n問題なさそうですね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8087, 1, 1, 129901, 3, 1, 0, "…今誰か私を\n「鬼」教官と\n呼びませんでしたか？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8088, 1, 1, 129901, 4, 1, 0, "新しく開発したこの薬\n誰か試してくれる人は\nいませんか？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8089, 1, 1, 129901, 5, 1, 0, "厳しくすれば投げ出し\n優しくすればサボる\n人というのはどこまで\n度し難いのでしょう", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8090, 1, 4, 129901, 101, 1, 0, "はじめましょう\nあのおかたの心を\n射貫くための\nシミュレーションを", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8091, 1, 8, 129901, 102, 1, 0, "あらゆる苦難と\n忌々しい女から守る\nためにも、私自身も\n鍛えておかねば", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8092, 1, 1, 129901, 1, 2, 0, "あなた様、そんなに\n見つめられると…え？\nエリコはカッコいい？\nええ、当然ですとも", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8093, 1, 1, 129901, 2, 2, 0, "不調があればすぐ\n報告してください\n体のケアも教官の\n大事な役目ですので", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8094, 1, 1, 129901, 3, 2, 0, "新しい訓練器具の\n使い心地はどう…え？\n重すぎる？　これでも\n軽くしたのですが…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8095, 1, 1, 129901, 4, 2, 0, "運動後には特製の\n栄養ドリンクをどうぞ\nクスクス、何も\n入れたりしてませんわ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8096, 1, 4, 129901, 101, 2, 0, "よくがんばりました\nあなた様。あぁ…\nお疲れになったお顔も\n素敵ですわ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8097, 1, 8, 129901, 102, 2, 0, "あなた様、二人だけの\n遠征訓練へ\nまいりましょう。\n既に準備万端ですわ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8098, 1, 1, 129901, 1, 2, 0, "まだ眠いですか？\nならばストレッチで\n体を痛めつ…ではなく\nほぐしていきましょう", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8099, 1, 1, 129901, 2, 2, 0, "お昼はお弁当をご用意\nいたしました。量も\nスタミナも増し増し\n残さず食べてください", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8100, 1, 1, 129901, 3, 2, 0, "よい睡眠こそ健やかな\n体に不可欠。ですので\n私が寝ずの番をさせて\nいただきますわ", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8102, 1, 1, 129901, 2, 3, 0, "休憩用に使おうと思い\nましたが、心地よくて\n立てなくなって\nしまいますわ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8103, 1, 1, 129901, 3, 3, 0, "中に重しを詰めれば\n愛でながら運動が\n可能ですわ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8104, 1, 1, 129901, 4, 3, 0, "香りでリラックスさせ\n同時に暗示を\nかけてみましょうか", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8105, 1, 1, 129901, 5, 3, 0, "…くっ、眠気を誘う\nこの暖かさ…\n油断も隙もありません", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8107, 1, 1, 129901, 7, 3, 0, "主食はこちらで十分\nまかなえますわね\nそれでは私はおかずを\n作りましょう", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8108, 1, 1, 129901, 8, 3, 0, "まあ、実用書の類も\n揃っているのですね\nとても助かりますわ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8110, 1, 1, 129601, 1, 1, 0, "……（そわそわ）", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8111, 1, 1, 129601, 2, 1, 0, "ここに寄らずの花陣…\n作ったらダメですよね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8112, 1, 1, 129601, 3, 1, 0, "部屋の隅が\n落ち着きます\nそれか、陽の当たる所", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8113, 1, 1, 129601, 4, 1, 0, "身を隠せる場所が\nあると…\n助かるんですけど", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8114, 1, 1, 129601, 5, 1, 0, "複雑な造りの家ですね\n触って壊れたり─\nしませんか？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8115, 1, 4, 129601, 101, 1, 0, "出入りするひと\nみんな、笑顔…\n…変わった家ですね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8116, 1, 8, 129601, 102, 1, 0, "この家なら安全─\nかもしれません\n魔物が出ないので", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8117, 1, 1, 129601, 1, 2, 0, "これも、視察の一環\n遊びに来たわけじゃ\nないです", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8118, 1, 1, 129601, 2, 2, 0, "長話─苦手です\n疲れるので…\nふぅ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8119, 1, 1, 129601, 3, 2, 0, "ここで友達を作れと\nネアさんが…\nそう、言われても…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8120, 1, 1, 129601, 4, 2, 0, "世話をするのは\n得意です\n…花の、ですよ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8121, 1, 4, 129601, 101, 2, 0, "媛へのお土産を\n探してるんですけど…\n…貴方のおすすめ、\n聞いておきます", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8122, 1, 8, 129601, 102, 2, 0, "平和でのんきで…\n貴方らしい家です\n一応、褒めてます", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8123, 1, 1, 129601, 1, 2, 0, "花に、お水─？\n…いりません", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8124, 1, 1, 129601, 2, 2, 0, "日が高いうちは\nむやみに外へ出ません\n面倒事、多いので…", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8125, 1, 1, 129601, 3, 2, 0, "夜番。私がします\nあまり眠らない\n性質なので…", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8127, 1, 1, 129601, 2, 3, 0, "ここなら根を張れる\nかもしれません\n…張りませんけど", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8128, 1, 1, 129601, 3, 3, 0, "こんなにやわな体じゃ\n身を守れませんよ─\n…あ。生き物じゃ\nないんですね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8129, 1, 1, 129601, 4, 3, 0, "花みたいに─\nいい匂い、です", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8130, 1, 1, 129601, 5, 3, 0, "火を見てると、\nジオ・ゲヘナを\n思い出します…ふぅ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8132, 1, 1, 129601, 7, 3, 0, "食事も立派な戦支度\n媛が言ってました", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8133, 1, 1, 129601, 8, 3, 0, "知識も武器の一つだと\n媛が…でも、\nこれでどうやって…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8135, 1, 1, 130701, 1, 1, 0, "天気もいいしお花畑に\nピクニックでも\n行こうかな♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8136, 1, 1, 130701, 2, 1, 0, "あ、どこからか\nお花の匂いが…\nいい香りですね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8137, 1, 1, 130701, 3, 1, 0, "新しいシャベルとか\nじょうろもほしいなぁ\nお小遣いしっかり\nためないとね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8138, 1, 1, 130701, 4, 1, 0, "お花飾りたいけど\n花瓶はどこかな…\nあとであの人に\n聞いてみよっと", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8139, 1, 1, 130701, 5, 1, 0, "お花の観察日記\n書かなきゃ\n日々勉強は\n欠かせませんよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8140, 1, 4, 130701, 101, 1, 0, "衣装にお花を足して\nみたけど、あの人\n気付いてくれるかな？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8141, 1, 8, 130701, 102, 1, 0, "この花冠、上手に\n作れたかも…\n日頃のお礼にあの人に\nあげようかな…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8142, 1, 1, 130701, 1, 2, 0, "あなたは何のお花が\n好きですか？　ほら、\n一応知っておいたら\n役に立つかもですし", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8143, 1, 1, 130701, 2, 2, 0, "そのお花、乱暴に\n扱っちゃダメですよ\n花粉が服について\nとれなくなりますから", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8144, 1, 1, 130701, 3, 2, 0, "土いじりしたから\n服が汚れてますよ\nはたいてあげますから\nじっとしててください", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8145, 1, 1, 130701, 4, 2, 0, "そんなにすぐに芽は\n出ませんよ\nまあ、楽しみなのは\n私も同じですけど…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8146, 1, 4, 130701, 101, 2, 0, "この衣装、そんなに\n気になります？　なら\nこうしてお花を\n着ければおそろいです", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8147, 1, 8, 130701, 102, 2, 0, "お花の種を私に？\nありがとうございます\nせっかくなので一緒に\nお世話しませんか？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8148, 1, 1, 130701, 1, 2, 0, "お花にお水をあげるの\n手伝ってもらえます？\nお水は朝にあげるのが\nいいんですよ", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8149, 1, 1, 130701, 2, 2, 0, "とっておきのお花見\nスポットに\nご案内しますよ\nお弁当も忘れずに！", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8150, 1, 1, 130701, 3, 2, 0, "もうこんな時間\n早く寝ましょう\n草木も眠るなんとか\nって言いますからね", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8152, 1, 1, 130701, 2, 3, 0, "お花のお手入れって\nけっこう疲れますね…\nここでちょっと一息\nしちゃおっと", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8153, 1, 1, 130701, 3, 3, 0, "耳にお花を着ければ…\nえへへ、すっごく\nかわいくなったね！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8154, 1, 1, 130701, 4, 3, 0, "これはラベンダー\nこっちはミモザ\nフローラル系の香り\nっていいですねぇ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8155, 1, 1, 130701, 5, 3, 0, "ポカポカしますね～\nまるで春の陽気\nみたいです～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8157, 1, 1, 130701, 7, 3, 0, "お花を一輪さすだけで\nご飯の時間がとても\n華やかになりますね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8158, 1, 1, 130701, 8, 3, 0, "お花の本が\nいっぱいある！\n今日はこれで\nお勉強です！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8160, 1, 1, 130801, 1, 1, 0, "ふんふ～ん♪\n華やかな服を着ると\n心がウキウキします♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8161, 1, 1, 130801, 2, 1, 0, "花祭りで出会った\nみなさん…\nお元気でしょうか？\nまたお会いしたいです", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8162, 1, 1, 130801, 3, 1, 0, "はっ！　そうでした\nこの服の時は\nメイドのお仕事は\nお休みでした…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8163, 1, 1, 130801, 4, 1, 0, "この服は替えが\nありませんから\n大きなドジには\n気を付けないと…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8164, 1, 1, 130801, 5, 1, 0, "目立ったほつれもなし\n…うん、いい感じに\n補修できました♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8165, 1, 4, 130801, 101, 1, 0, "メイド姿でなくても\n心はいつでも\nお嬢さまの\nメイドです♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8166, 1, 8, 130801, 102, 1, 0, "この服を着ると心が\n浮き立つのはあのかた\nと一緒にいる気持ちに\nなるからでしょうか？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8167, 1, 1, 130801, 1, 2, 0, "はい、今日は一日\nこの服で過ごそうと\n思います\n変じゃないですよね？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8168, 1, 1, 130801, 2, 2, 0, "花祭りのおかげで\n少しだけお花に\n詳しくなった…\nような気がします！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8169, 1, 1, 130801, 3, 2, 0, "花祭り、今度は\nお嬢さまと救護院の\nみんなと一緒に\n行ってみたいですね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8170, 1, 1, 130801, 4, 2, 0, "今度キョウカちゃんと\n花祭りの服でお花を\n見にお出かけ\nするんです", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8171, 1, 4, 130801, 101, 2, 0, "メイド服を全部洗濯\nしちゃうなんて…\nドジっちゃいましたぁ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8172, 1, 8, 130801, 102, 2, 0, "花祭りの服は私たちの\n思い出の服ですから…\nこれからも\n大切にします♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8173, 1, 1, 130801, 1, 2, 0, "はう、そうでした\nお花にお水を\nあげる時間です！", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8174, 1, 1, 130801, 2, 2, 0, "お昼ご飯は\nバスケットを持って\nお花畑に食べ\nに行きませんか？", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8175, 1, 1, 130801, 3, 2, 0, "ふぁあ…繕い物を\nしていたらこんな時間\nになってしまいました\n早く寝ないと…", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8177, 1, 1, 130801, 2, 3, 0, "ふわふわしてて\nきもちいいです～\nどんな生地を\nつかってるんだろ～？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8178, 1, 1, 130801, 3, 3, 0, "かわいいですね～\nぬいぐるみさんにも\n服をつくって\nあげたいです♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8179, 1, 1, 130801, 4, 3, 0, "いい香りです～\nあれ？　もしかして\n私の服についてる花と\n同じ香りが…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8180, 1, 1, 130801, 5, 3, 0, "暖かいですけど\nあまり近づいちゃうと\n頭の花飾りが乾燥\nしちゃいそうですね…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8182, 1, 1, 130801, 7, 3, 0, "もぐもぐ…\nいい花を咲かせるには\nしっかりとした栄養が\n必要だそうです！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8183, 1, 1, 130801, 8, 3, 0, "なるほどなるほど…\nお花のお手入れは\n奥が深いですね…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8185, 1, 1, 131001, 1, 1, 0, "マホマホ王国にも\nサーカス団設立や\n夢と希望を届けますえ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8186, 1, 1, 131001, 2, 1, 0, "せっかくの晴れ舞台や\nかっこいいポーズも\nできるようにしたいわ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8187, 1, 1, 131001, 3, 1, 0, "むむむむ～っ。うーん\nクルルはんみたいに\n胸から宝石を出すんは\n難しいみたいやわ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8188, 1, 1, 131001, 4, 1, 0, "あむっ…んん～♪\nやっぱり練習のあとは\n甘いものに限るわぁ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8189, 1, 1, 131001, 5, 1, 0, "アーサーたちの衣装も\n今のうちに考えんと…\n生地、足りるやろか", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8190, 1, 4, 131001, 101, 1, 0, "王子はんとなら\nどんなアイデアも\n見つけられます♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8191, 1, 8, 131001, 102, 1, 0, "うちの晴れ姿が\n王子はんの目に一等\n輝いて見えますように", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8192, 1, 1, 131001, 1, 2, 0, "キリっとしたうちも\nかっこいい？\nもう、上手やなぁ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8193, 1, 1, 131001, 2, 2, 0, "新しい演目考えたんよ\nなぁ王子はん、意見\n聞かせてもらえます？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8194, 1, 1, 131001, 3, 2, 0, "聞いてや王子はん\n動物さんたちとさらに\n息が合うてきたんよ\nみんな頑張り屋さんや", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8195, 1, 1, 131001, 4, 2, 0, "演技の感想なら\n大歓迎どすえ♪\nたんと聞かせてや～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8196, 1, 4, 131001, 101, 2, 0, "王子はん、本番当日は\n最前席で見たってな\nそしたらうち、もっと\n頑張れますさかい♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8197, 1, 8, 131001, 102, 2, 0, "この技をあなたに\n捧げます…なんて♪\n驚いたやろ？\nどっきり作戦成功や♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8198, 1, 1, 131001, 1, 2, 0, "素敵なショーのためや\n今日も焦らず一歩ずつ\n成長していきまひょ\nな、王子はん♪", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8199, 1, 1, 131001, 2, 2, 0, "お弁当、王子はんも\n一緒にいかがどす？\nみんなで食べて団結や", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8200, 1, 1, 131001, 3, 2, 0, "夜のサーカスも\n幻想的でとっても\n魅力的なんよ\n一緒にいきまひょか♪", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8202, 1, 1, 131001, 2, 3, 0, "練習が終わったあとは\nの～んびりして\n体力を回復せな…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8203, 1, 1, 131001, 3, 3, 0, "この子たちとなら\n空中ブランコや\n玉乗りもええなぁ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8204, 1, 1, 131001, 4, 3, 0, "いい香りやなぁ\n本番前の緊張も\n和らげてくれそうやわ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8205, 1, 1, 131001, 5, 3, 0, "このあったかい感じ…\nスポットライトの熱を\n思い出すわぁ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8207, 1, 1, 131001, 7, 3, 0, "ちゃんと食べへんと\nいい演技はできまへん\n団長はんも言うてたわ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8208, 1, 1, 131001, 8, 3, 0, "動物さんたちのことも\nもっと勉強せな\n大事な仲間やさかい", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8210, 1, 12, 105601, 103, 1, 0, "器用じゃないが、\n情には厚いよ\nこういう女を「一途」\nってそう言うんだろ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8211, 1, 12, 105601, 103, 2, 0, "斬れないものはないと\n思ってきたさ。だけど\n…この気持ちだけは\n斬れないね、絶対に", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8212, 1, 1, 130901, 1, 1, 0, "んしょ、んしょっ…！\n日頃の感謝を込めて\nヒーローさんのお家を\nきれいにしますです♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8213, 1, 1, 130901, 2, 1, 0, "ふむふむ…\n新アトラクションは\nこのギルドハウスを\nモチーフに…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8214, 1, 1, 130901, 3, 1, 0, "ごそごそ、こそこそ…\nヒーローさんに内緒で\nどこかにお礼の宝石を\n隠しておきましょう…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8215, 1, 1, 130901, 4, 1, 0, "パークの運営が\n落ち着いたら\nわたしもギルド活動？\nしてみたいですます♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8216, 1, 1, 130901, 5, 1, 0, "こんなすてきな\nおうちに住めたら…\nいえ！　うさぎ小屋で\n十分です～っ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8217, 1, 4, 130901, 101, 1, 0, "ヒーローさんのおうち\nだって意識したら\nアトラクションよりも\nドキドキしてきます…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8218, 1, 8, 130901, 102, 1, 0, "ヒーローさんのことを\n考えると顔が赤く…\nこれってもしかして\nひょっとして…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8219, 1, 1, 130901, 1, 2, 0, "あっ、ヒーローさん！\n話しかけてくれた記念\nということで、宝石\n受け取ってください♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8220, 1, 1, 130901, 2, 2, 0, "元気がない時は\n語尾にぴょんですっ！\nさあヒーローさんも\nやってみるぴょん♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8221, 1, 1, 130901, 3, 2, 0, "ヒーローさんへのご恩\nおばあちゃんうさぎに\nなるまでに、お返し\nできるでしょうか…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8222, 1, 1, 130901, 4, 2, 0, "ヒーローさん！\n二人で一緒に\nパレードの練習\nしませんか～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8223, 1, 4, 130901, 101, 2, 0, "あはは♪\nヒーローさんは\n人を笑顔にする力が\nありますです♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8224, 1, 8, 130901, 102, 2, 0, "ヒーローさんに\n一日一恩返しですっ！\n返し終わるまで\nやめませんですます♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8225, 1, 1, 130901, 1, 2, 0, "おはようございます♪\nんしょ、んしょっ…！\n気持ちよく起きられた\n記念に宝石どうぞ♪", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8226, 1, 1, 130901, 2, 2, 0, "お昼にフードコートで\nかばばん君バーガー\n買ってきましたよ！\n一緒に食べますです♪", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8227, 1, 1, 130901, 3, 2, 0, "今日も一日\nお疲れさまですっ♪\nマッサージしますから\n寝そべってください♪", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8229, 1, 1, 130901, 2, 3, 0, "はふぅ～…♪\nお仕事の疲れも\nぴょんぴょ～んって\n飛んでっちゃいます♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8230, 1, 1, 130901, 3, 3, 0, "わぁっ、とっても\nかわいいですますっ！\nギルドハウスの\nマスコットですね♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8231, 1, 1, 130901, 4, 3, 0, "ヒーローさんの好きな\n香り…これを宝石に\nつければ、受け取って\nもらえるでしょうか？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8232, 1, 1, 130901, 5, 3, 0, "はあ、気持ちいい♪\nヒーローさんの\n好きな温度に\n調整しときますです♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8234, 1, 1, 130901, 7, 3, 0, "支配人は体が資本っ！\nたくさん食べ…すぎて\n動けなくなっちゃい\nましたぁ～…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8235, 1, 1, 130901, 8, 3, 0, "パークの\nガイドブックとか\nあったらいいですね♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8237, 1, 1, 131101, 1, 1, 0, "外あつすぎ溶けるわ～\nやっぱ部屋ん中で\n水着がラクだねー", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8238, 1, 1, 131101, 2, 1, 0, "ン～\nカリザきゅんどこー？\nもしかして逃げた？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8239, 1, 1, 131101, 3, 1, 0, "ミソラちゃんと\nリリちゃん誘って\nそろそろイイコト\nしよっかなー？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8240, 1, 1, 131101, 4, 1, 0, "おねえちゃんに\n頼まれたシゴトは\nまた今度にすっかー\n夏休みだしー♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8241, 1, 1, 131101, 5, 1, 0, "騎士きゅんの\nプリンセスフォームも\n見てみたいんだよなー\nンフ♡", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8242, 1, 4, 131101, 101, 1, 0, "スライムを\nこーしてあーしたら\n騎士きゅんの泳ぐ\n練習に使えるっぽ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8243, 1, 8, 131101, 102, 1, 0, "おかえり～♡\nって水着で迎えたら\n騎士きゅん喜んで\nくれるかなー？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8244, 1, 1, 131101, 1, 2, 0, "騎士きゅん\nこのサングラス\nかけてみる？\nぜってー似合うよー", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8245, 1, 1, 131101, 2, 2, 0, "日焼け痛かったら\nいつでも言ってねー\nスライムでひんやり\nしてあげるよ～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8246, 1, 1, 131101, 3, 2, 0, "今から外で\n焼いてこっかなー\n騎士きゅんもどー？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8247, 1, 1, 131101, 4, 2, 0, "スライムマッサージ\nしてあげよっかー\nほーれ、もみもみ～♡", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8248, 1, 4, 131101, 101, 2, 0, "騎士きゅんが選んだ\n水着も着てみたーい\nネアおねえちゃんに\nどんなの着てほしい？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8249, 1, 8, 131101, 102, 2, 0, "騎士きゅ～ん\n尻相撲しよっかー♪\nおねえちゃんに\n勝てたらご褒美だぞ～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8250, 1, 1, 131101, 1, 2, 0, "暑すぎてムダに\n早起きしちった～\n二度寝すっか～\n…ふぁあ…", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8251, 1, 1, 131101, 2, 2, 0, "夏バテしても\nスイカとかかき氷なら\n全然ウェルカム\nなんだよなー", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8252, 1, 1, 131101, 3, 2, 0, "今日はこのまま\n水着で寝るかー\n夢ん中で泳ごー", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8254, 1, 1, 131101, 2, 3, 0, "いったんキューケイ…\nンー…よし\n充電カンリョ～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8255, 1, 1, 131101, 3, 3, 0, "仲間になりたそうに\nこっち見てんねー\nきゃわいいしおっけ～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8256, 1, 1, 131101, 4, 3, 0, "ン～いいニオーイ\nお日様あたった\n騎士きゅんの\n次くらいかな～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8257, 1, 1, 131101, 5, 3, 0, "カラダ冷えてきたから\nたすかるーおかげで\nまだまだ水着イケるわ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8259, 1, 1, 131101, 7, 3, 0, "うめ～\nとまんねぇ～\n夏バテどこいった～？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8260, 1, 1, 131101, 8, 3, 0, "ふむふむ…\nこれは意外と\nぬるっと読めるっぽー", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8262, 1, 1, 131101, 10, 3, 0, "ンフフ～\n海の戦士ってカンジ？\nアリよりのアリだね～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8263, 1, 1, 131101, 11, 3, 0, "あーやべ\n一瞬で寝そー\nヤバだわ～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8264, 1, 1, 129701, 1, 1, 0, "服や紙が易々と\n燃えぬとは\nランドソルとはまこと\n住み易き国で候", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8265, 1, 1, 129701, 2, 1, 0, "どの世界にあっても\n民は活力に溢れ\n街は賑わうもの…\n善哉、善哉", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8266, 1, 1, 129701, 3, 1, 0, "食べ歩きに世界征服…\nギルドとはかくも\n自由気儘な徒党に\nありんすなぁ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8267, 1, 1, 129701, 4, 1, 0, "連絡は…今日もなし\n我が妹子は相変わらず\n放埓なことよ喃", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8268, 1, 1, 129701, 5, 1, 0, "モネは相変わらず\n警戒しきりなれど…\nそのようなところも\n愛らしくあり申す", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8269, 1, 4, 129701, 101, 1, 0, "なんと長閑な世にや\nありけむ…この平和は\n騎士殿たちの努力の\n賜物にありんす喃", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8270, 1, 8, 129701, 102, 1, 0, "『王家の装備』は\n使わぬのかと？\n…フフ。すでに\n用いており申す", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8271, 1, 1, 129701, 1, 2, 0, "今ばかりは媛という\n立場は置いて\nこの地の者らと\n歓談したく候", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8272, 1, 1, 129701, 2, 2, 0, "強さの秘密？　…フフ\n女子の秘密を暴くのは\n野暮にござんす\nご寛恕いただきたく候", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8273, 1, 1, 129701, 3, 2, 0, "此方の家屋、\n見目以上に\n堅牢であり申す\n何ぞ仕掛けがありや？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8274, 1, 1, 129701, 4, 2, 0, "心地よい時間とは\n疾く過ぎ去りゆくもの\n此処で過ごすと改めて\n実感できる喃", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8275, 1, 4, 129701, 101, 2, 0, "視線が気になる？\nこれはすみませぬ\nなにぶん、お前さまは\n見ていて飽きませぬ故", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8276, 1, 8, 129701, 102, 2, 0, "お前さまの前では\n誰もが心を開く…\nなんとまぁ罪作りな\n色男にありんすな？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8277, 1, 1, 129701, 1, 2, 0, "よく眠れましたかの？\n寝る子は良く育つ、と\n古くより申します由", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8278, 1, 1, 129701, 2, 2, 0, "美食殿の作法に倣い\n皆で共に昼餉を頂くと\n致しんしょう", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8279, 1, 1, 129701, 3, 2, 0, "逢魔ヶ時は望まぬ客も\n参るもの…お前さまも\n気をつけなんし", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8281, 1, 1, 129701, 2, 3, 0, "わっちの蛇に\n勝るとも劣らぬ\n座り心地で候", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8282, 1, 1, 129701, 3, 3, 0, "魂、万物に宿る\nとの云いあらば\n情愛を注いでやるのが\nよかろうの", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8283, 1, 1, 129701, 4, 3, 0, "いと香しき哉…\n騎士殿は香の道にも\n詳しくあられるか", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8284, 1, 1, 129701, 5, 3, 0, "ジオ・ゲヘナの\n灼熱と比ぶれば\nそよ風と変わり\nござんせん", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8286, 1, 1, 129701, 7, 3, 0, "美味なる哉…この味\nぺコリーヌ姫が好むも\n道理にありんす", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8287, 1, 1, 129701, 8, 3, 0, "トーゴクに、オーマ…\n書で見るだけでも\n心惹かれる土地よの", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8289, 1, 1, 131201, 1, 1, 0, "暑い日には\n冷たいものですよねー\nかき氷とか食べて\nすっきりしたいです★", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8290, 1, 1, 131201, 2, 1, 0, "海への行き来は\nテレポーテーションに\nお任せです★", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8291, 1, 1, 131201, 3, 1, 0, "トロピカルジュースが\n飲みたいでーす♪\n海の家で見たんですよ\nカップル用ジュース★", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8292, 1, 1, 131201, 4, 1, 0, "ご飯どうしようかなー\nあ、海鮮ラーメンとか\n美味しそうかも★", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8293, 1, 1, 131201, 5, 1, 0, "水鉄砲、普通に撃つと\n少し物足りないかも…\nどるるるる～ん★って\n感じが欲しいですよね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8294, 1, 4, 131201, 101, 1, 0, "コイビトっぽいこと\nアオハルっぽいこと\nいっぱいしたいです★\n修羅場とかも♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8295, 1, 8, 131201, 102, 1, 0, "騎士さんが溺れたら\n絶対助けに行きますね\n人工呼吸でもなんでも\nしてあげますから★", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8296, 1, 1, 131201, 1, 2, 0, "騎士さーん\n日焼け止めの塗り直し\nお願いします★\n優しくしてくださいね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8297, 1, 1, 131201, 2, 2, 0, "海で食べたフルーツ？\nはい、まだありますよ\nこっちでもあーんして\n食べさせましょうか★", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8298, 1, 1, 131201, 3, 2, 0, "ぺんぽこりんは\nどうしたのかって？\nどうしたんでしょうね\nどーでもいいです★", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8299, 1, 1, 131201, 4, 2, 0, "今日はどんな\nイイコトしましょうか\n思い出作り？\n人助けもいいですね★", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8300, 1, 4, 131201, 101, 2, 0, "ヒモを引っ張ったら…\n「セ・キ・ニ・ン」\n取ってもらいますよ？\nなーんてウソですけど", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8301, 1, 8, 131201, 102, 2, 0, "みんなでイイコトも\n楽しいですけど～\n…二人でイイコト\nしちゃいますか？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8302, 1, 1, 131201, 1, 2, 0, "朝ですよー！\n今日も一日、\nイイコト日和ですね♪", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8303, 1, 1, 131201, 2, 2, 0, "日差しにご注意ですね\n日焼け止め…誰かに\n塗ってほしいなー？\n…ちらっ★", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8304, 1, 1, 131201, 3, 2, 0, "夜の海って\n惹かれちゃいますよね\n…ロマンチックです★", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8306, 1, 1, 131201, 2, 3, 0, "う～ん、まったり～\nこれぞ新ジャンル\nおうちバカンス★", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8307, 1, 1, 131201, 3, 3, 0, "よーしよーし★\nわたしとイイコト\n一緒にしましょうねー", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8308, 1, 1, 131201, 4, 3, 0, "いい香りですねー\nリラックスタイムに\nちょうどよさそう★", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8309, 1, 1, 131201, 5, 3, 0, "水遊びのあとは\nしっかり体を\n温めないとですよね★", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8311, 1, 1, 131201, 7, 3, 0, "栄養補給もバッチリ★\n遊びもイイコトも\nお腹が減ってたら\nできませんからねー？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8312, 1, 1, 131201, 8, 3, 0, "今だと海関連の本とか\n興味あるんですよねー\n魚とか海難救助とか★", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8314, 1, 1, 131201, 10, 3, 0, "かっこいー★\n名前はー…\nぷんぷくぽんとか？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8315, 1, 1, 131201, 11, 3, 0, "映えますねー\n写真撮ったら\n素敵な思い出に\nなりそうです★", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8316, 1, 1, 131601, 1, 1, 0, "海でもないのに\nこの姿でいるのは…\nやはり、場違いでは\nありませんか…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8317, 1, 1, 131601, 2, 1, 0, "スイカとは割ることと\n見つけたり！\n…ですが目隠しすれば\n見当たらないもの…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8318, 1, 1, 131601, 3, 1, 0, "まぁ、膝に砂が…\n気がつきませんでした\nまるでわんぱくな\nお子様のよう…ふふっ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8319, 1, 1, 131601, 4, 1, 0, "家の中でスイカを割る\nわけにはまいりません\nここは潔く包丁で\n切りましょう", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8320, 1, 1, 131601, 5, 1, 0, "ミソラ様やネア様の\n大胆さを見習って…\nき、騎士きゅーん…★\n難しいですね…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8321, 1, 4, 131601, 101, 1, 0, "きゅ、急に暗く…！？\n…失礼しました\n鉢巻きが下がってきた\nだけでした…ううぅ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8322, 1, 8, 131601, 102, 1, 0, "わたくしに剣の才は\nあるのでしょうか…\nいえ、何事もまずは\nやってみなくては", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8323, 1, 1, 131601, 1, 2, 0, "なにか羽織った方が\nよろしいですよね…？\nそのままでいい？\nそれはそれで…その…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8324, 1, 1, 131601, 2, 2, 0, "あの、兄様…？\nスイカの種を飛ばす\n遊びもあるというのは\n…本当なのでしょうか", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8325, 1, 1, 131601, 3, 2, 0, "強くなるための修行は\nまだ始まったばかり\nまずは己の目指すべき\n道を見定めなくては", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8326, 1, 1, 131601, 4, 2, 0, "同じ薄着であっても\n黒い天使の姿より…\n気が楽です", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8327, 1, 4, 131601, 101, 2, 0, "はい、ただいまー！\n…あっ！\n海の家でのお手伝いの\nくせが、つい…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8328, 1, 8, 131601, 102, 2, 0, "兄様さえよろしければ\nまた一緒に海へ…\nあ…申し訳ありません\n浮ついてしまいました", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8329, 1, 1, 131601, 1, 2, 0, "本日もイイコトをして\n清々しい一日を\n過ごしたいものです\nまさに一日一善ですね", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8330, 1, 1, 131601, 2, 2, 0, "お昼時になると\n海の家の料理を\n思い出してしまいます\n焼きそばにかき氷…♪", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8331, 1, 1, 131601, 3, 2, 0, "兄様…よろしければ\n浜辺を歩きませんか？\nきっと、月が\nきれいでしょうから", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8333, 1, 1, 131601, 2, 3, 0, "ふわふわ心地よすぎて\n眠くなってしまいます\n水着姿では\nいけません…ふぁ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8334, 1, 1, 131601, 3, 3, 0, "なんてよい手触り…\n癖になってしまいます\nふふ、よしよし…♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8335, 1, 1, 131601, 4, 3, 0, "爽やかな香り…♪\n日焼け止めクリームを\n思い出してしまいます", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8336, 1, 1, 131601, 5, 3, 0, "冷たい水で冷えた体が\n温まります…♪\nまるで兄様のようです", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8338, 1, 1, 131601, 7, 3, 0, "お腹が空いては\n修行ができません…！\n動きに障りない程度に\n栄養の補給を…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8339, 1, 1, 131601, 8, 3, 0, "剣術とスイカ割りが\n上達する指南書…\n見つかりませんね…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8341, 1, 1, 131601, 10, 3, 0, "さあ、ともに大海原を\n参りましょう！\nなんて…ふふっ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8342, 1, 1, 131601, 11, 3, 0, "まるでおとぎ話の\n登場人物になった\nような心持ちです…♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8343, 1, 1, 131701, 1, 1, 0, "あたしはなんで水着で\n室内をうろついてるの\nかしら…不思議だわ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8344, 1, 1, 131701, 2, 1, 0, "あ、天井が汚れてる\n仕方ないわね\nひとっ飛びしますか", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8345, 1, 1, 131701, 3, 1, 0, "はい、お疲れさま♪\n出迎えの言葉は\nやっぱこれよね♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8346, 1, 1, 131701, 4, 1, 0, "そういえばあたしって\n羽があるけど\n泳げるのかしら…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8347, 1, 1, 131701, 5, 1, 0, "あいつのために\n浮き輪も用意したし\n抜かりはないわよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8348, 1, 4, 131701, 101, 1, 0, "海までのガイド計画も\nまとまったわね\nふふっ…楽しみだわ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8349, 1, 8, 131701, 102, 1, 0, "この水着、あいつが\n選んでくれたのよね\n…大事にしないと", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8350, 1, 1, 131701, 1, 2, 0, "ネビアは最近どう？\n迷惑かけてない？\n…かけてるのね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8351, 1, 1, 131701, 2, 2, 0, "あんた…部屋の片付け\nくらい自分でしなさい\n散らかってたわよ？\n…片付けておいたけど", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8352, 1, 1, 131701, 3, 2, 0, "もしかして暇なの？\nだったら記憶でも\n見せましょうか？\n凄く恥ずかしいやつ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8353, 1, 1, 131701, 4, 2, 0, "夏ってこんなに\n暑いのね…\n海にばかり行く\n気持ちが分かったわ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8354, 1, 4, 131701, 101, 2, 0, "あんたの水着って\n新しいの買わないの？\nなんならあたしが\n選んであげるわよ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8355, 1, 8, 131701, 102, 2, 0, "気軽に触れ合える\n距離にいる…\nあたしにとって\nとても大事なことよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8356, 1, 1, 131701, 1, 2, 0, "あらおはよう\n夏の朝は暑いわね\nシャワーでも浴びて\nさっぱりしなさい", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8357, 1, 1, 131701, 2, 2, 0, "お昼は何を食べるの？\nガイド妖精として\n提案しましょうか？", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8358, 1, 1, 131701, 3, 2, 0, "夜更かしは厳禁よ\n無理やり眠らせて\nあげましょうか？", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8360, 1, 1, 131701, 2, 3, 0, "アメスさまだって\nゴロゴロするときは\nあるのよ～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8361, 1, 1, 131701, 3, 3, 0, "…ふふっ♪\n何よ、あたしが\nかわいいものを\n愛でちゃだめなの？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8362, 1, 1, 131701, 4, 3, 0, "こういういい香り…\n塔の中じゃなかなか\n楽しめないのよね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8363, 1, 1, 131701, 5, 3, 0, "水着を着てるのに\n温まるのって\n矛盾してない？\n気にしすぎかしら", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8365, 1, 1, 131701, 7, 3, 0, "あんたと行動するなら\n沢山食べられるように\nならなきゃね…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8366, 1, 1, 131701, 8, 3, 0, "文字を読むのと\nデータを読むのは\nまったく違うわ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8368, 1, 1, 131701, 10, 3, 0, "へぇ、面白いじゃない\nこういう経験、昔じゃ\nできなかったわ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8369, 1, 1, 131701, 11, 3, 0, "面白いクッションね\nところであんた…\nあたしで遊んでない？\n気のせいならいいけど", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8370, 1, 1, 118601, 1, 1, 0, "また新しい悪戯\n思いついちゃった♪\n誰に仕掛けようかしら", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8371, 1, 1, 118601, 2, 1, 0, "あ～あ、退屈…\nフィオでもからかって\nこようかしら♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8372, 1, 1, 118601, 3, 1, 0, "ミソギの悪戯って\nレベル高いのよね…\n負けてらんないわ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8373, 1, 1, 118601, 4, 1, 0, "家事ニガテなのよね～\nコッコロたちには\n感謝しないとね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8374, 1, 1, 118601, 5, 1, 0, "私も魔物料理に\n慣れてきちゃった…\n毒されてるわね…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8375, 1, 4, 118601, 101, 1, 0, "あいつに仕掛ける\n次の悪戯は～…♪\nこれ…ううん…\nやっぱこっちかしら？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8376, 1, 8, 118601, 102, 1, 0, "結局ギルドハウスが\n一番落ち着くわね\n…私たちの家だもの", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8377, 1, 1, 118601, 1, 2, 0, "悪戯のコツでも\n教えてあげよっか？\n…え～興味ないの？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8378, 1, 1, 118601, 2, 2, 0, "これからもずーっと\n退屈しない毎日に\nしてあげる♪\n私の悪戯でね♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8379, 1, 1, 118601, 3, 2, 0, "ちょうど退屈してたの\nたーっぷりと\n構ってもらうわよ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8380, 1, 1, 118601, 4, 2, 0, "案内が必要なら\n頼ってくれていいわよ\n一応は私だって\nガイド妖精だし！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8381, 1, 4, 118601, 101, 2, 0, "冒険のガイドなら\n私に任せなさい♪\nある意味忘れられない\n旅にしてあげる♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8382, 1, 8, 118601, 102, 2, 0, "妖精がいる家は\n栄えるのよ♪　だから\nずっといてあげる\nあんたのそばにね♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8383, 1, 1, 118601, 1, 2, 0, "きゃははっ！\n寝ぐせついてるわよ？\nこのままにしましょ！\nだって面白いし！", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8384, 1, 1, 118601, 2, 2, 0, "自由に外へ行けるって\nすっごく幸せなことよ\nさ、散歩行きましょ♪", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8385, 1, 1, 118601, 3, 2, 0, "安心していいわ\n寝てるあんたに\n悪戯しないから\n…あんたにはね♪", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8387, 1, 1, 118601, 2, 3, 0, "悪くはないけど…\nあいつの頭の方が\n座り心地いいかも", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8388, 1, 1, 118601, 3, 3, 0, "この子を使った\n悪戯は…\n…止めとこっと\nこんなにかわいいし♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8389, 1, 1, 118601, 4, 3, 0, "このギルドハウスに\nピッタリな匂いね♪\n一言で言えば…\nカオスって感じ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8390, 1, 1, 118601, 5, 3, 0, "なんていうか…\n心があたたかいわ\n…変な感じ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8392, 1, 1, 118601, 7, 3, 0, "ガイド妖精にも\n食事の機能があって\nよかったのかも\nしれないわね…もぐ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8393, 1, 1, 118601, 8, 3, 0, "じれったい物語ね…\nこんなの霧を使えば\n一瞬で終わるのに！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8395, 1, 1, 131801, 1, 1, 0, "わる～い子は\nトゥンヌス漁船に\n乗ってもらおうかな～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8396, 1, 1, 131801, 2, 1, 0, "たまにはファミリーの\nみんなにもお休みを\nあげないとね～☆", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8397, 1, 1, 131801, 3, 1, 0, "海辺に素敵な\nバーがあるんだ～\n彼が大人になったら\n一緒に行きたいな♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8398, 1, 1, 131801, 4, 1, 0, "夏の風を肌で\n感じるの好きかも～\nラーゴはどう？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8399, 1, 1, 131801, 5, 1, 0, "海岸線を\nのんびり散歩もいいね\n砂や貝殻に紛れた\nお宝探しの旅だよ～☆", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8400, 1, 4, 131801, 101, 1, 0, "みんなで集まって\nビーチパーティーでも\n開いちゃおっかな～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8401, 1, 8, 131801, 102, 1, 0, "さて、この水着…\n彼は気に入って\nくれてるかな～？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8402, 1, 1, 131801, 1, 2, 0, "心ゆくまで夏を\n楽しんじゃお～☆\nほら君も♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8403, 1, 1, 131801, 2, 2, 0, "水着似合ってるって？\nふふ♪　うれしいこと\n言ってくれるね～☆", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8404, 1, 1, 131801, 3, 2, 0, "熱中症には\n気を付けないと\nだめだよ～？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8405, 1, 1, 131801, 4, 2, 0, "海の家ならうちの\nファミリーでやってる\nとこにしよっか☆\n豪遊しちゃお～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8406, 1, 4, 131801, 101, 2, 0, "君と一緒なら\nどんな夏になるのか\n楽しみだね～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8407, 1, 8, 131801, 102, 2, 0, "君といると子供心を\nくすぐられちゃうな～\n大人だって思いっきり\n遊んでいいんだよね♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8408, 1, 1, 131801, 1, 2, 0, "さ、今日は何をして\n過ごそっかな～…\nねえ、君は何したい？", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8409, 1, 1, 131801, 2, 2, 0, "ふふ♪\n波音が心地良い\n時間だね～☆", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8410, 1, 1, 131801, 3, 2, 0, "今夜は星空の下で\n乾杯しよっか～☆\n飲み物取ってくるね♪", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8412, 1, 1, 131801, 2, 3, 0, "ここ涼しい～♪\nちょっと一休み\nしていこっか☆", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8413, 1, 1, 131801, 3, 3, 0, "こういうのも良いけど\nやっぱりラーゴの方が\nかわいいかな～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8414, 1, 1, 131801, 4, 3, 0, "爽やかな香りだね～\nこういう感覚も絵で\n表現してみたいな～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8415, 1, 1, 131801, 5, 3, 0, "続きを描く前に\n原稿を乾かさないとね\nインクがにじむと\n直すの大変なんだよ～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8417, 1, 1, 131801, 7, 3, 0, "今日は水着だし…\n食べるのは控えめに\nしておこっかな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8418, 1, 1, 131801, 8, 3, 0, "話題の作品は\nチェックしないとね～\nあっ、この作家さん\n新刊出すんだ～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8420, 1, 1, 131801, 10, 3, 0, "うちの出店の客引きに\nこういう乗り物系を\n使うのもアリかな～☆", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8421, 1, 1, 131801, 11, 3, 0, "これ、背景用の\n参考資料に\n一つ欲しいかも♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8422, 1, 1, 181201, 1, 1, 0, "ここに帰るのも\n久しぶり…あれから\n変わったところは\nあるかしら？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8423, 1, 1, 181201, 2, 1, 0, "バイトも復帰しなきゃ\nうちのギルドにとって\n食費は重要だもの…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8424, 1, 1, 181201, 3, 1, 0, "コッコロマ…っ\n…うぅ…\nこの癖は\n直らないのね…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8425, 1, 1, 181201, 4, 1, 0, "この世界はまだまだ\n知らないことだらけね\n『裏世界』とか…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8426, 1, 1, 181201, 5, 1, 0, "必ずみんなを\n助け出してみせるわ\n…もちろん、兄さんも", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8427, 1, 4, 181201, 101, 1, 0, "またみんなと\n暮らせるなんて…\n夢じゃ、ないのよね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8428, 1, 8, 181201, 102, 1, 0, "たくさんの人が\n私に優しくしてくれる\n…だからこそ、私も\n頼れる人になりたいわ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8429, 1, 1, 181201, 1, 2, 0, "次はランドソルの\n街の外にも行きたいわ\nあの…よかったらまた\n一緒に回ってくれる？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8430, 1, 1, 181201, 2, 2, 0, "独りぼっちで眠るのは\nやっぱり寂しかったわ\n…みんなで雑魚寝？\nそんなの…絶対やるわ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8431, 1, 1, 181201, 3, 2, 0, "ネビアってば大泣きで\n私を出迎えてくれたの\n…すごく嬉しかったわ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8432, 1, 1, 181201, 4, 2, 0, "この翼、陽に当たると\n本当に綺麗よね…\n自分のだけれど、つい\nずっと見ちゃうもの", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8433, 1, 4, 181201, 101, 2, 0, "おにーたんっ♪\n…呼びたくなっただけ\n本当にそれだけよっ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8434, 1, 8, 181201, 102, 2, 0, "どうしたの？\n…ふふっ、安心して\nこれからもずっと\n私はここにいるわ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8435, 1, 1, 181201, 1, 2, 0, "みんな起きだす時間ね\n街が活気づいてく感じ\n嫌いじゃないわ", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8436, 1, 1, 181201, 2, 2, 0, "【美食殿】のお昼は\nいつも美味しいから\nまた太っちゃいそうね", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8437, 1, 1, 181201, 3, 2, 0, "おやすみなさい\n明日も、その先も\n一緒に冒険しましょう", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8439, 1, 1, 181201, 2, 3, 0, "休憩も大事…よね\nじゃあ遠慮なく…\nふぅ～…♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8440, 1, 1, 181201, 3, 3, 0, "はぁ…ふわふわ…♪\nこのかわいさには\nもう抗えないわ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8441, 1, 1, 181201, 4, 3, 0, "落ち着く香り…\nこの匂いを嗅いでると\n帰ってきたなって\n思うわね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8442, 1, 1, 181201, 5, 3, 0, "みんなで野宿した時も\n焚火の明かりを見て\nすごく安心したのよね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8444, 1, 1, 181201, 7, 3, 0, "ペコリーヌさんには\n負けるけど…\n美味しくって手が\n止まらないわ…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8445, 1, 1, 181201, 8, 3, 0, "プリンセスフォーム…\n一体どういう仕組みで\n発動するのか、文献に\n書いてないかしら…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8447, 1, 1, 131901, 1, 1, 0, "すごーく暑いけど、\nこの暑さをどうやって\n乗り越えるかって\n考えるのも楽しいかも", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8448, 1, 1, 131901, 2, 1, 0, "氷で彫刻を作るの？\nそれは楽しそうだけど\nすぐに溶けちゃわ\nないかしら？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8449, 1, 1, 131901, 3, 1, 0, "お日様の下で遊ぶのも\n部屋の中で涼むのも\nどっちも夏ならではの\n楽しみ方なのね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8450, 1, 1, 131901, 4, 1, 0, "生き物の観察日記、\n私も書いてみたいな\nパックのお昼寝日記\nなんてどうかしら？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8451, 1, 1, 131901, 5, 1, 0, "さまぁばけぇしょんは\n計画を立てるもの\nなんだって\n私も挑戦してみるわね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8452, 1, 4, 131901, 101, 1, 0, "この世界のことを\nもっと知って\nアーラム村のみんなに\nお話ししてあげるの", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8453, 1, 8, 131901, 102, 1, 0, "いつルグニカに帰るか\nわからないからこそ\n毎日頑張らなきゃね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8454, 1, 1, 131901, 1, 2, 0, "今日はどこに行くの？\n私は、えーと…\nかき氷が食べたいわ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8455, 1, 1, 131901, 2, 2, 0, "ここにも精霊が\n住んでたりするの？\n…精霊じゃなくて\nがいど妖精？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8456, 1, 1, 131901, 3, 2, 0, "パックったら、\n近所の子にいっぱい\nなでなでされてたわ\nすごーく人気者ね！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8457, 1, 1, 131901, 4, 2, 0, "実は…線香花火の\n特訓をしてたの！\n次は負けないんだから", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8458, 1, 4, 131901, 101, 2, 0, "昨日、みんなの\n似顔絵も絵日記に\n描いてみたんだけど…\n笑っちゃダメよ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8459, 1, 8, 131901, 102, 2, 0, "何気ない時間だって\n後で思い返せば\nずっと忘れない\n素敵な思い出になるの", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8460, 1, 1, 131901, 1, 2, 0, "気持ちよく起きて、\nいい一日を過ごしたい\nそんなときには\nラジーオタイソーね！", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8461, 1, 1, 131901, 2, 2, 0, "お昼ごはんの時間ね\n暑さに負けないように\nしっかり食べましょう", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8462, 1, 1, 131901, 3, 2, 0, "外は涼しいみたいだし\n夜の散歩に行ってみる\nのはどうかしら？", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8464, 1, 1, 131901, 2, 3, 0, "遊び疲れたら\nここでひとやすみ…\nふわぁ…\n眠くなっちゃった…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8465, 1, 1, 131901, 3, 3, 0, "細かいところまで\nすごーく丁寧に\n作られてる…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8466, 1, 1, 131901, 4, 3, 0, "わっ、なんだか\nすーっとする香りね\nちょっぴり涼しく\nなったみたい", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8467, 1, 1, 131901, 5, 3, 0, "海で遊ぶと\n体が冷えるから\nあったかいのも\nすごーく嬉しい…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8469, 1, 1, 131901, 7, 3, 0, "これも美味しい…！\nペコリーヌが\nどんどん食べちゃう\n気持ちが分かるかも", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8470, 1, 1, 131901, 8, 3, 0, "このお話も面白い…！\nあっという間に時間が\n過ぎちゃったわ…\n…ね、寝てません！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8472, 1, 1, 131901, 10, 3, 0, "この子と一緒に海に\nいったら楽しそうね！\nどこに行けば\n会えるのかしら？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8473, 1, 1, 131901, 11, 3, 0, "ふかふかね！\nこうしてると波の音が\n聞こえてきそう…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8474, 1, 1, 132101, 1, 1, 0, "こちらでは水着姿で\n過ごすのも\n珍しくないようですね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8475, 1, 1, 132101, 2, 1, 0, "メイド服ではなく\n姉様と色違いの水着…\nレムは嬉しいです", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8476, 1, 1, 132101, 3, 1, 0, "少し室温が高いかも\nしれませんね…\nいくつか氷を配置して\nおきましょうか", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8477, 1, 1, 132101, 4, 1, 0, "清涼感があるとはいえ\n鉄球をビーチバレーの\nボールに代用するのは\n危ないでしょうか…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8478, 1, 1, 132101, 5, 1, 0, "ふぅー…ふぅー…\nようやく浮輪が\n膨らみました\n姉様にお渡ししないと", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8479, 1, 4, 132101, 101, 1, 0, "海のことをもっと\n教えていただけますか\n…スバルくんと海の\n話をしたいんです", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8480, 1, 8, 132101, 102, 1, 0, "もし海に行く機会が\nあればレムにも…\nお声がけくださいね\nぜひ、お願いします", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8481, 1, 1, 132101, 1, 2, 0, "何を着ていても\nメイドはメイド\n掃除も料理も\nなんでもござれです", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8482, 1, 1, 132101, 2, 2, 0, "スバルくんとあなたは\n兄弟のように\n仲がいいですね\n姉様とレムと一緒です", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8483, 1, 1, 132101, 3, 2, 0, "この姿で子どもたちと\n遊ぶのは…さすがに\nはばかられますね\nやはり着替えてきます", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8484, 1, 1, 132101, 4, 2, 0, "ナンパ…というものが\n海にはあるそうです\nスバルくんがとても\n警戒していました", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8485, 1, 4, 132101, 101, 2, 0, "魚釣りでしたら\nレムもご一緒します\n氷漬けの魚の備蓄を\n補充したかったので", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8486, 1, 8, 132101, 102, 2, 0, "子どもたちと砂遊びの\n約束をしているんです\nあなたもご一緒に\nいかがですか？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8487, 1, 1, 132101, 1, 2, 0, "ラジーオタイソーは\nもうされましたか？\n体がほぐれますよ", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8488, 1, 1, 132101, 2, 2, 0, "お昼ですね。では\n姉様に焼きそばを…\nもちろん皆様の分も\n用意してありますよ", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8489, 1, 1, 132101, 3, 2, 0, "熱帯夜であっても\nお休みの際はお腹を\n冷やさないように\n気をつけてくださいね", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8491, 1, 1, 132101, 2, 3, 0, "とても気持ちよさそう\nです…！\nさっそく姉様に\nお教えしないと", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8492, 1, 1, 132101, 3, 3, 0, "季節に合わせた\n着せ替えができれば\n子どもたちも\n楽しめそうです", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8493, 1, 1, 132101, 4, 3, 0, "ふむふむ…この香りは\n疲れを癒やしてくれ\nますね。エミリア様に\nお持ちします", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8494, 1, 1, 132101, 5, 3, 0, "身も心もほぐれます\n思っていたよりも\n体が冷えていたみたい\nですね…ふう…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8496, 1, 1, 132101, 7, 3, 0, "もぐもぐ…んうっ！？\nこのすっぱさは…\n梅干し、ですか…\nくせになりますね…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8497, 1, 1, 132101, 8, 3, 0, "海に関する詩文なども\nあるのでしょうか…\n気になります", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8499, 1, 1, 132101, 10, 3, 0, "地竜とは違った\n乗り心地です…\n本物にも乗ってみたい\nものですね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8500, 1, 1, 132101, 11, 3, 0, "この大きさで\n本物の貝であれば…\nどんな料理にするか\n想像が膨らみます", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8501, 1, 1, 132001, 1, 1, 0, "これがこの世界の\n居住環境か…\n面白い構造だね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8502, 1, 1, 132001, 2, 1, 0, "暇を持て余していてね\n誰か話し相手に\nなってくれないかな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8503, 1, 1, 132001, 3, 1, 0, "目新しいものが\nたくさんあるね\n胸が躍るよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8504, 1, 1, 132001, 4, 1, 0, "頑丈そうな建物だ\nここなら彼女たちが\n暴れても大丈夫かな？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8505, 1, 1, 132001, 5, 1, 0, "力が制限されていて\n良かった面もある\n君も吐かずにすんだし", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8506, 1, 4, 132001, 101, 1, 0, "悪さなんてするわけ\nないじゃないか\n傷つくなぁ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8507, 1, 8, 132001, 102, 1, 0, "普段と違う格好で\n歩き回るのも\n悪くない経験だ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8508, 1, 1, 132001, 1, 2, 0, "ボクに聞きたいことが\nあるなら遠慮なく\n質問するといい", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8509, 1, 1, 132001, 2, 2, 0, "気軽に魔女に\n話しかけるなんて\n君は物好きだね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8510, 1, 1, 132001, 3, 2, 0, "この世界においては\nボクは異物だからね\n大人しくしているさ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8511, 1, 1, 132001, 4, 2, 0, "ボクの反応を\n見たいのかい？\n君の好奇心には\n恐れ入るよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8512, 1, 4, 132001, 101, 2, 0, "ボクの手を借りたい？\nもちろん構わないさ\n対価はいただくけどね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8513, 1, 8, 132001, 102, 2, 0, "ボクと海を見たい？\nふふ、魅力的な\nお誘いだね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8514, 1, 1, 132001, 1, 2, 0, "お目覚めかな？\nさて、君の見た夢を\n教えてくれないかな？", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8515, 1, 1, 132001, 2, 2, 0, "食後のお茶を\n一緒にどうだろうか？\nさあ、席に座ってよ", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8516, 1, 1, 132001, 3, 2, 0, "おや、お休みかな？\n魔女の前で無防備だね\n…無論、何もしないさ", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8518, 1, 1, 132001, 2, 3, 0, "こういう椅子は\n茶会には向かないね\nつい気が緩んでしまう", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8519, 1, 1, 132001, 3, 3, 0, "こういうものを好きな\n女の子がいいのかな？\n君の嗜好を知りたいな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8520, 1, 1, 132001, 4, 3, 0, "いい香りだ\nこれにはどんな\n効能があるんだろう？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8521, 1, 1, 132001, 5, 3, 0, "火の点け方に\nボクのいた世界との\n違いはないのかな？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8523, 1, 1, 132001, 7, 3, 0, "これもまた未知の味だ\n存分に味わわせて\nもらうとしよう", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8524, 1, 1, 132001, 8, 3, 0, "素晴らしいね\nここには叡智が\n詰まっている", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8526, 1, 1, 132001, 10, 3, 0, "魔物を模倣した\nぬいぐるみか…\n実物を見たくなるね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8527, 1, 1, 132001, 11, 3, 0, "いい気持ちだ…ボクと\nしたことが、何も考え\nられなくなりそうだ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8528, 1, 12, 109701, 103, 1, 0, "願わずにいられません\nどうかこちらの\n世界には…悲劇が\n生まれませんようにと", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8529, 1, 12, 109701, 103, 2, 0, "食事のご希望があれば\nなんなりと。お腹が\nいっぱいになるまで\n沢山ご用意しますね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8530, 1, 12, 109801, 103, 1, 0, "あの男、これからも\n観察が必要ね\nラムの目の届く範囲に\n置く必要があるわ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8531, 1, 12, 109801, 103, 2, 0, "素直なところは\nあの男の美点ね\n癪だけど、偶然にも\nラムと同じだわ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8532, 1, 12, 109901, 103, 1, 0, "迷惑かけちゃったけど\n雪遊びは楽しかったわ\nまたやりたい…なんて\n言ったら怒られるかも", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8533, 1, 12, 109901, 103, 2, 0, "数えきれないくらいの\n思い出を、みんなで\n作りましょう！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8534, 1, 1, 132201, 1, 1, 0, "わわっ、今すごく\nいい香りがしたけど…\n誰の香水かな\n教えてほしいな…♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8535, 1, 1, 132201, 2, 1, 0, "錬金術用の釜、\n置くならどこかな？\n自分専用の錬金釜、\n憧れちゃうよね♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8536, 1, 1, 132201, 3, 1, 0, "この匂い…誰か\n唐揚げ食べたみたい\nうぅ…お腹空いて\nきちゃうよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8537, 1, 1, 132201, 4, 1, 0, "次の新曲のタイトルは\n『恋の錬金術』！\nうーん、ちょっと\nベタ過ぎるかな…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8538, 1, 1, 132201, 5, 1, 0, "あ、錬金の素材\n切らしてたんだ…\n彼を誘って採集に\n行ってこようかな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8539, 1, 4, 132201, 101, 1, 0, "瞬時に着替えができる\n薬とかできないかな？\n舞台で衣装チェンジ！\nなーんて、楽しそう♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8540, 1, 8, 132201, 102, 1, 0, "彼が遊びに来た後は\n部屋の空気がほんのり\n彼の匂いなんだ…\nなんだか幸せだな♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8541, 1, 1, 132201, 1, 2, 0, "今日もバイト？\nそれならこれ飲んで\n私のお手製ポーション\nがんばってきてね！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8542, 1, 1, 132201, 2, 2, 0, "新しくできたカレー屋\n行ってみない？\n時々スパイスの香りが\n流れてくるんだよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8543, 1, 1, 132201, 3, 2, 0, "私のフィギュア\nそっくりでしょ！\nルイちゃんが作って\nくれたんだ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8544, 1, 1, 132201, 4, 2, 0, "あれ、シャンプー\n変えた？　私と同じの\nだよ！　どうしよう\nドキドキしちゃう…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8545, 1, 4, 132201, 101, 2, 0, "いらっしゃいませ♪\n何を作りますか？\nなーんてね。錬金堂で\nまた一日店長したいな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8546, 1, 8, 132201, 102, 2, 0, "クンクン…彼の\nハンカチ…いい匂い♪\n私だけの秘密のお守り\nにしてるんだ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8547, 1, 1, 132201, 1, 2, 0, "ねえねえ、天気も\nいいし一緒に錬金の\n素材集めに行こうよ！", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8548, 1, 1, 132201, 2, 2, 0, "お昼ご飯、用意したよ\nあはは、さすがに\nランチは錬成しないよ\n私の手作りだよ♪", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8549, 1, 1, 132201, 3, 2, 0, "今日も一日お疲れさま\nこれ飲んでゆっくり\n休んで。薬じゃないよ\nホットミルクだよ？", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8551, 1, 1, 132201, 2, 3, 0, "昨日、遅くまで\n錬金術の勉強してた\nから眠くって…\nちょっと一休みっと", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8552, 1, 1, 132201, 3, 3, 0, "この子にちょっとだけ\n香水かけて、と…\nんー！　だっこした時\nすっごくいい匂い♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8553, 1, 1, 132201, 4, 3, 0, "花みたいな、\nお菓子みたいな\nいい香り。気持ちが\n穏やかになるね♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8554, 1, 1, 132201, 5, 3, 0, "外でもこの暖かさを\n感じられる道具、\n作れたりしないかな？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8556, 1, 1, 132201, 7, 3, 0, "素材集めに行くと\nお腹が空くんだよね\nイロハちゃんも\nこの味好きそう", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8557, 1, 1, 132201, 8, 3, 0, "錬金術にはたくさんの\n知識が必要だからね\nさあ、今日も調合書を\n読まなくちゃ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8559, 1, 1, 127701, 1, 1, 0, "ここが騎士さんたちの\n住んでる場所…！\n粗相がないように\n気をつけなきゃ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8560, 1, 1, 127701, 2, 1, 0, "ジオ・テオゴニアの\nみんな、私がいない間\nちゃんとお仕事\nしてればいいけど…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8561, 1, 1, 127701, 3, 1, 0, "コッコロさんも\n従者として長いって\n聞いたような…\nあの年ですごい…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8562, 1, 1, 127701, 4, 1, 0, "自分がもてなされる\n側になるのって\nやっぱりちょっと\n落ち着かないかも…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8563, 1, 1, 127701, 5, 1, 0, "今度のお休みの日は\n何しよう…？\nあっ、最近できたお店\nヴルムと行こうかな？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8564, 1, 4, 127701, 101, 1, 0, "お買い物したら\nかわいいからって\nたくさんおまけして\nもらっちゃった…♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8565, 1, 8, 127701, 102, 1, 0, "ここにいると、つい\nぼんやりしちゃう…\nこれも騎士さんの力…\nなのかな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8566, 1, 1, 127701, 1, 2, 0, "お手伝いできることは\nありませんか？\nお世話でもなんでも\n任せてくださいっ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8567, 1, 1, 127701, 2, 2, 0, "ランドソルに初めて\n来たときは驚きました\n砂糖の山と思ったら…\nあんなに寒いなんて！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8568, 1, 1, 127701, 3, 2, 0, "あの、ヴルムを\n見ませんでしたか？\nもう…あの子ってば\nまた仕事をサボって…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8569, 1, 1, 127701, 4, 2, 0, "家具や家が壊れても\n食べられないなんて\nなんだかもったいない\nですね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8570, 1, 4, 127701, 101, 2, 0, "とっておきの紅茶を\n手に入れたんです\n一杯いかがですか？\nその、日頃のお礼です", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8571, 1, 8, 127701, 102, 2, 0, "頑張ってて偉いって…\nほ、褒めてもなんにも\n出ませんからね！？\n…えへへ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8572, 1, 1, 127701, 1, 2, 0, "おはようございます！\nモーニングティーは\nいかがですか？\n私、準備しますねっ", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8573, 1, 1, 127701, 2, 2, 0, "ライラエルさまも\nお昼寝はお好きです\nけど…だからって\n寝すぎはダメですよ！", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8574, 1, 1, 127701, 3, 2, 0, "おやすみなさい\nくれぐれも寝不足には\n気をつけてくださいね", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8576, 1, 1, 127701, 2, 3, 0, "しっかりしないと…\nメイドの私が\n気を抜くなんて…\nそんな…ふぅ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8577, 1, 1, 127701, 3, 3, 0, "わぁ、かわいい…♪\nってダメダメ！\nメイドなんだから\n落ち着かないと…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8578, 1, 1, 127701, 4, 3, 0, "いい匂い…♪\nこの香り、\nライラエルさまも\n好きそうです！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8579, 1, 1, 127701, 5, 3, 0, "ランドソルの環境に\n触れると、この光の\nありがたみが\n身に沁みますね…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8581, 1, 1, 127701, 7, 3, 0, "甘くない食べ物が\n一般的なのも\n慣れてきました\nあむ…おいひぃ…♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8582, 1, 1, 127701, 8, 3, 0, "ランドソルの文字、\n私たちのところと\nちょっと違いますね…\n覚えられるかなぁ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8584, 1, 1, 127801, 1, 1, 0, "甘い匂いがしない…\nジオ・テオゴニアとは\n別の世界なんですね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8585, 1, 1, 127801, 2, 1, 0, "掃除めんど…\nん…このくらいで\nまー十分でしょ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8586, 1, 1, 127801, 3, 1, 0, "そろそろ食事の\n用意もしなきゃ…\nあーやる事多い…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8587, 1, 1, 127801, 4, 1, 0, "秘密の隠れ場所、\nまた姉さんにバレた…\n次のを探さないと", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8588, 1, 1, 127801, 5, 1, 0, "知らない間に仕事が\n終わったり…しないか\nしてくれたらいいのに", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8589, 1, 4, 127801, 101, 1, 0, "きちんともてなすには\n騎士さんの好みも\n覚えてかないと…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8590, 1, 8, 127801, 102, 1, 0, "ご主人サマに\nご奉仕します～♪\n…キャラじゃないか\nやめやめ、恥ずかし…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8591, 1, 1, 127801, 1, 2, 0, "居心地いいっすね…\n今日からここに\n住んでもいいですか", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8592, 1, 1, 127801, 2, 2, 0, "あの、ちょっと姉さん\n怒らせちゃったんで\n匿ってくれません？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8593, 1, 1, 127801, 3, 2, 0, "羽と尻尾が格好いい？\n意外と邪魔ですよ\n仰向けで寝にくいし", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8594, 1, 1, 127801, 4, 2, 0, "ワークライフバランス\nっていい言葉ですね\nぜひライフ多めで", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8595, 1, 4, 127801, 101, 2, 0, "騎士さんって\n働き者ですね\nそのままあたしの\n仕事も…いえなんでも", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8596, 1, 8, 127801, 102, 2, 0, "熱心に見てても\n尻尾は触らせて\nあげませんってば\n角？　角なら…まぁ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8597, 1, 1, 127801, 1, 2, 0, "寝ても寝ても\n寝足りない…ふぁ…\nあー…眠たい…", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8598, 1, 1, 127801, 2, 2, 0, "アフタヌーンティーの\n準備は得意ですよ\nつまみ食いとか\n…つまみ食いとか", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8599, 1, 1, 127801, 3, 2, 0, "よく休んでください\n布団は干しといたんで\n…姉さんが", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8601, 1, 1, 127801, 2, 3, 0, "ふー…\nこのままずーっと\nここに居たい…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8602, 1, 1, 127801, 3, 3, 0, "あんたも一緒に\nサボっちゃうか～\n…なんて", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8603, 1, 1, 127801, 4, 3, 0, "落ち着く匂い…\n騎士さんの好み\nなんですかね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8604, 1, 1, 127801, 5, 3, 0, "こういうとこの\n掃除って面倒だし…\n今度でいっか", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8606, 1, 1, 127801, 7, 3, 0, "ご飯の前に\nこんな食べたら\n姉さんきっと怒るな…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8607, 1, 1, 127801, 8, 3, 0, "食に関する本が\nやけに多い…\nお国柄ですかね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8609, 1, 1, 130201, 1, 1, 0, "すんすん…\nお兄さんの匂いが\nぷんぷんする～♡\nザコくっさぁ♡", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8610, 1, 1, 130201, 2, 1, 0, "ぴこーん！\nいひひっ、新作メカの\n面白いアイデア、\n思いついちった～♡", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8611, 1, 1, 130201, 3, 1, 0, "メカもいじりたいし\n服もアクセもほしー…\nあ～、おこづかい\n足んないよ～！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8612, 1, 1, 130201, 4, 1, 0, "お勉強でもしよっかな\nザコお兄さんには\n絶対わかんない\nよーなやつ♡", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8613, 1, 1, 130201, 5, 1, 0, "お兄さんの部屋って\nどこどこぉ？\nメカの実験室に\n使えそうだよね～♡", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8614, 1, 4, 130201, 101, 1, 0, "次のデートコースは\nどんなのかなー？\n…いや、むしろ次は\nぼくが決めちゃうっ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8615, 1, 8, 130201, 102, 1, 0, "強奪計画は念入りに♡\nお兄さんのハートも\n全力でザコザコにする\n計画、立てないとね♡", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8616, 1, 1, 130201, 1, 2, 0, "ねー、ヒマヒマヒマ～\nかまってかまってー♡\nどっか連れてけー♡", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8617, 1, 1, 130201, 2, 2, 0, "今日はこっちで\nご飯食べたいなー♡\n…帰っても５日連続の\nバカナ姉カレーだし…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8618, 1, 1, 130201, 3, 2, 0, "依頼行くならぼくも\n連れてってよー♡\nお兄さんのザコっぷり\n見ててあげる～♡", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8619, 1, 1, 130201, 4, 2, 0, "お兄さんはぼくより\nオトナだもんね～♡\nじゃあオモテナシ、\n期待してるから～♡", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8620, 1, 4, 130201, 101, 2, 0, "ヤマ姉になんかお土産\n持って帰ろうかなー？\nザコお兄さん、選ぶの\n付きあって～♡", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8621, 1, 8, 130201, 102, 2, 0, "遊びにいくときは\nザコお兄さんも\n誘ってあげるよ♡\nどうせヒマでしょ～？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8622, 1, 1, 130201, 1, 2, 0, "ふぁ～、ねむぅ～…\n夜更かしぶん寝るから\nあとで起こして～♡\nあ、変なこと禁止～♡", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8623, 1, 1, 130201, 2, 2, 0, "おなかすいたぁ～\nなんか食べ行こ♡\n甘いのおごって～♡", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8624, 1, 1, 130201, 3, 2, 0, "えー、もう寝る時間？\nじゃあ…添い寝して\nあげよっか？\nなーんて、ウッソー♡", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8626, 1, 1, 130201, 2, 3, 0, "おお～ふわっふわ～♪\nバンディシャーク号の\n座席に使いたーい！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8627, 1, 1, 130201, 3, 3, 0, "カワイイ～♡\n改造したらもっと\nカワイくなるかな～？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8628, 1, 1, 130201, 4, 3, 0, "くんくん…いい匂い♡\nザコお兄さんって\nこーゆー香りが\n好きなのかなぁ～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8629, 1, 1, 130201, 5, 3, 0, "はー、あったかーい♡\nバンディシャーク号\nにも欲しいかもー♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8631, 1, 1, 130201, 7, 3, 0, "食べれるときに\n食べるのが大事って\nヤマ姉言ってたしね～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8632, 1, 1, 130201, 8, 3, 0, "あ、この本読んだよ♡\n参考文献リストが\nしっかりしてて\n勉強になったなー♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8634, 1, 1, 130001, 1, 1, 0, "部屋は多少ゴチャつい\nてる方が落ち着くのさ\nバンディシャーク号\nみたいにね♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8635, 1, 1, 130001, 2, 1, 0, "呆けてるのもいいが\nちいと物足りないねぇ\n麦しゅわもブドウ朱も\n無いなんてさ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8636, 1, 1, 130001, 3, 1, 0, "この家セキュリティが\nなってないねぇ…\n盗賊目線でちょっくら\n指南でもしてやるか", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8637, 1, 1, 130001, 4, 1, 0, "不思議だねぇ\nここで飲むと、酔いが\n回るのが早い気が\nするよ…ヒック", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8638, 1, 1, 130001, 5, 1, 0, "ふむ…\nクルマが壊れた時は\nアジト代わりに\n使えそうだ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8639, 1, 4, 130001, 101, 1, 0, "カカッ、いい部屋だ！\n妹を迎える準備は\nできてるみたいだねぇ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8640, 1, 8, 130001, 102, 1, 0, "…っと、油断したら\n落ち着いちまうね\nボーヤの甘っちょろい\n雰囲気のせいだよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8641, 1, 1, 130001, 1, 2, 0, "さすがにこの家から\n盗みはしないさ\nいずれは妹たちの\n共有財産になるからね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8642, 1, 1, 130001, 2, 2, 0, "ボーヤ、暇なら\n博打でもやるかい？\nアタシが負けたら\n掃除でもしてやるよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8643, 1, 1, 130001, 3, 2, 0, "立派な家じゃないか！\n一国一城の主とは\nちょっとボーヤのこと\n見くびってたかもねぇ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8644, 1, 1, 130001, 4, 2, 0, "かわいい義弟のためだ\nアタシが盗ってきた\nお宝をくれてやるよ\n好きな場所に飾りな！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8645, 1, 4, 130001, 101, 2, 0, "妹と結ばれる前に\n他の女との関係は\n清算しとくように\nわかったね、ボーヤ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8646, 1, 8, 130001, 102, 2, 0, "お、ヤシオーリかい？\nフフン、義弟からの\nお酌とあっちゃあ\n断れないねぇ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8647, 1, 1, 130001, 1, 2, 0, "うう…清々しい朝だが\n頭がいてぇ…\nちっと昨日は\n飲みすぎたか…？", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8648, 1, 1, 130001, 2, 2, 0, "ほらこれ、ワカナに\n作らせた弁当だ\nこれで昼からも\nバリバリやりな！", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8649, 1, 1, 130001, 3, 2, 0, "まだまだ夜はこれから\n…と言いたいとこだが\n早寝して立派に育ちな\nそれが妹たちの為だよ", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8651, 1, 1, 130001, 2, 3, 0, "中々いい座り心地だが\n盗賊が腑抜ける訳には\nいかないね…が…\nもう少しだけ座るか…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8652, 1, 1, 130001, 3, 3, 0, "こういうのの中に\n実はレアなブツが\n隠されてるとか\nありがちだねぇ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8653, 1, 1, 130001, 4, 3, 0, "ふうん、ボーヤは\nこういう匂いが\n好きなのかい？\n妹たちに教えてやろう", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8654, 1, 1, 130001, 5, 3, 0, "体を温めんにゃ\n酵露をカッとやるのが\n一番だが、これも存外\n悪くないねぇ…♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8656, 1, 1, 130001, 7, 3, 0, "ガツガツ…\nん、悪くねェ味だ\nこれにヤシオーリでも\nありゃ文句はないね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8657, 1, 1, 130001, 8, 3, 0, "本にも載ってない\nレアもんのお宝か…\nフッ、想像しただけで\n血沸き肉躍るねぇ…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8659, 1, 1, 130101, 1, 1, 0, "今日の買い物は\nいつものお野菜と…\nあっ、確か特売が\nあったはず…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8660, 1, 1, 130101, 2, 1, 0, "お掃除、お掃除…♪\nきゅっきゅっ…\nよいしょ、っと…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8661, 1, 1, 130101, 3, 1, 0, "洗濯ものも\nたまらないうちに\nやっちゃわないと…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8662, 1, 1, 130101, 4, 1, 0, "夕飯のメニュー…\n何にしよう？\n期限の早い食材は…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8663, 1, 1, 130101, 5, 1, 0, "色々お菓子も\n作ってみたいけど…\n予算…足りるかなぁ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8664, 1, 4, 130101, 101, 1, 0, "騎士さん…どんな\n料理が好きなんだろ？\n前に作ったコロッケは\n評判よかったけど…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8665, 1, 8, 130101, 102, 1, 0, "騎士さんと\nおしゃべりすると、\n胸があたたかくなる…\nなんでだろ…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8666, 1, 1, 130101, 1, 2, 0, "あっ、騎士さん\nどうかしましたか？\n御用ならいつでも…♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8667, 1, 1, 130101, 2, 2, 0, "削っても削っても\n家計簿は赤字…\n騎士さ～ん…\n助けてください～！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8668, 1, 1, 130101, 3, 2, 0, "手が空いてたら\n日用品の買い出し、\n手伝ってもらっても\nいいですか…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8669, 1, 1, 130101, 4, 2, 0, "やっと一息つけそう…\nあっ、わたし\nお茶を淹れてきますね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8670, 1, 4, 130101, 101, 2, 0, "困りごとがあったら\n言ってくださいね\nわたし…騎士さんの\n力になりたいです", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8671, 1, 8, 130101, 102, 2, 0, "助けてもらった分は\n必ず返しますから…\nそれまでわたしのこと\n見ててくださいね…♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8672, 1, 1, 130101, 1, 2, 0, "忘れ物はないですか？\n今日のお仕事も\n頑張りましょうね…！", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8673, 1, 1, 130101, 2, 2, 0, "お昼ご飯できてますよ\n大したものじゃなくて\n恐縮ですけど…", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8674, 1, 1, 130101, 3, 2, 0, "夜更かしはだめですよ\nわたしも家事を\n終わらせたら寝ないと", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8676, 1, 1, 130101, 2, 3, 0, "これ…割れ物防止の\nクッションに\n使えるかも…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8677, 1, 1, 130101, 3, 3, 0, "こういうのいいなぁ…\nけど…フブキちゃんに\n絶対改造されちゃう…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8678, 1, 1, 130101, 4, 3, 0, "いい匂い…でも\n切れかけてるかも？\nえっと補充するには…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8679, 1, 1, 130101, 5, 3, 0, "あったかい…\nちょっとだけなら\n休んでも…いいよね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8681, 1, 1, 130101, 7, 3, 0, "これがあったら\nもうお料理を\n作らなくて済むかも？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8682, 1, 1, 130101, 8, 3, 0, "新しい節約レシピは…\nあ、この草って\n食べられるんだ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8684, 1, 1, 132801, 1, 1, 0, "愛溢れるランドソルの\nクリスマス…ですか\nいけませんね、つい\n胸が躍ってしまいます", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8685, 1, 1, 132801, 2, 1, 0, "ツリーの飾りと\nわたしの髪…\nくるくるしていて\n似ているかも…くすっ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8686, 1, 1, 132801, 3, 1, 0, "この姿で傲慢な\nわたしになったら…\nいつもより怖がらせて\nしまうでしょうね…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8687, 1, 1, 132801, 4, 1, 0, "悪魔として、人を\nドキドキさせなくては\nい、イオから教わった\n…投げキスの練習を…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8688, 1, 1, 132801, 5, 1, 0, "ジオ・テオゴニアの\nお菓子は、ツリーの\n飾りに向いていそう\nですね…ふふっ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8689, 1, 4, 132801, 101, 1, 0, "騎士のギルドハウス…\nクリスマスの時期も\n暖かいのですね…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8690, 1, 8, 132801, 102, 1, 0, "城の皆と過ごす宴も\n楽しいですが…\n騎士と二人の宴も\n幸せです…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8691, 1, 1, 132801, 1, 2, 0, "騎士、あなたの\nもてなしに感謝します\nクリスマスの何たるか\n教えてくださいね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8692, 1, 1, 132801, 2, 2, 0, "この黒い羽は\nどう手入れすれば？\nイオなら知っている\nでしょうか…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8693, 1, 1, 132801, 3, 2, 0, "城の皆も連れてきたい\nほど、クリスマスは\n楽しい行事ですね\nお土産も迷います", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8694, 1, 1, 132801, 4, 2, 0, "クリスマスの晩餐に\n招待されたのですが…\nその…辛いものは\nありませんよね…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8695, 1, 4, 132801, 101, 2, 0, "クリスマスと言わず…\nいつでも星に祈って\nいますよ？　騎士達に\n幸せがあるように、と", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8696, 1, 8, 132801, 102, 2, 0, "悪魔として騎士を\nドキドキさせる…？\nそ、そのようなこと\nわたしには…うぅ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8697, 1, 1, 132801, 1, 2, 0, "朝は寒いですね…\n雪…積もっている\nでしょうか？", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8698, 1, 1, 132801, 2, 2, 0, "ふあぁ…いけません\n公務の滞在先で\nお昼寝など…", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8699, 1, 1, 132801, 3, 2, 0, "夜は悪魔として\n夢を見せましょう\n騎士はどんな夢が\n見たいですか？", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8701, 1, 1, 132801, 2, 3, 0, "ふぅ…落ち着きます…\n家でゆっくりと過ごす\nクリスマスも\n良いものですね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8702, 1, 1, 132801, 3, 3, 0, "リンドのプレゼントに\nぬいぐるみは…？\n…子ども扱いすると\n落ち込むでしょうか…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8703, 1, 1, 132801, 4, 3, 0, "暖かいお部屋に\nこの香りを漂わせたら\nふふっ…素敵な時間を\n過ごせそうです", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8704, 1, 1, 132801, 5, 3, 0, "これがあれば、\n寒い冬も安心ですね\n暖かくて…このまま\n眠ってしまいそうです", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8706, 1, 1, 132801, 7, 3, 0, "悪魔の力を使ったら\nお腹が空いてきて\nしまって…\n恥ずかしいです…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8707, 1, 1, 132801, 8, 3, 0, "クリスマスには多くの\n伝承があるのですね…\n天使の伝承まで\nありましたし…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8709, 1, 1, 132803, 14, 3, 0, "まぁ…！\nよくできていますね\nサプライズプレゼント\n…でしょうか。ふふ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8710, 1, 1, 132804, 15, 3, 0, "ど、どうでしょう…？\n親善交流の催しに\nなりそうでしょうか…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8711, 1, 1, 132901, 1, 1, 0, "ギルドハウスって\nあったかぁ～い♪\n心がぽかぽかするの！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8712, 1, 1, 132901, 2, 1, 0, "クリスマスの悪魔の\nお仕事はないわね！\nだってここのみんな\nいい子なんですもの♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8713, 1, 1, 132901, 3, 1, 0, "みんなちゃんと\n寝られてるかしら？\n夜更かしせずに、\nいい夢見てね♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8714, 1, 1, 132901, 4, 1, 0, "毎回、来年が\n楽しみになるような\n素敵なクリスマスを\n過ごしたいわね♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8715, 1, 1, 132901, 5, 1, 0, "訳ありケーキを見ると\n刑務作業を思い出すわ\n…あれも貴重な体験\nだったのかしら…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8716, 1, 4, 132901, 101, 1, 0, "ロマンチックな\nクリスマスに備えて\n今のうちに少女漫画で\n予習しておかないと！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8717, 1, 8, 132901, 102, 1, 0, "ほっぺにチューなら\nもう一回しても…\nや、やっぱり無理！\n恥ずかしいわ～！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8718, 1, 1, 132901, 1, 2, 0, "またみんなで\nセッションしたいわね\nカラーベルの練習した\n成果を見せたいの♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8719, 1, 1, 132901, 2, 2, 0, "君はサンタさんに\nどんなプレゼントを\n頼んでいたのかしら？\n気になるわ～！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8720, 1, 1, 132901, 3, 2, 0, "愛が壊れても、\n何度でも治してあげる\n私たちの間に、\n愛の尽きる日はないわ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8721, 1, 1, 132901, 4, 2, 0, "冬休みって短いようで\n長く感じちゃうわよね\n学校のみんなと\n早く会いたいな～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8722, 1, 4, 132901, 101, 2, 0, "愛をテーマにした\n冬休みの作文の宿題、\n君の分を読むのが\n今から楽しみね♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8723, 1, 8, 132901, 102, 2, 0, "よかったらまた\n私をハニーって呼んで\nほしいの。ダーリン♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8724, 1, 1, 132901, 1, 2, 0, "おはよう！\n冬の朝は冷えるから\n風邪を引かないように\n対策しましょうね", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8725, 1, 1, 132901, 2, 2, 0, "お昼ご飯は一緒に\n食べに行きましょう！\nクリスマス限定の\nランチコースよ♪", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8726, 1, 1, 132901, 3, 2, 0, "ねえねえ、キャンドル\nイルミネーションを\n見に行きましょうよ！\nロマンチックよ～♪", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8728, 1, 1, 132901, 2, 3, 0, "暖かいクリスマスを\n過ごすのに必須よね♪\nふふ、柔らかぁい！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8729, 1, 1, 132901, 3, 3, 0, "どの子もかわいい～♪\nクリスマスツリーの\n下で、一緒にお祝い\nしてもらいたいな♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8730, 1, 1, 132901, 4, 3, 0, "なんだか胸の奥が\n温かくなる匂いね～\nリラックスしちゃう♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8731, 1, 1, 132901, 5, 3, 0, "あったかぁい♪\nあ、でも夜になったら\nサンタさんが入れる\nように、火を消すのよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8733, 1, 1, 132901, 7, 3, 0, "寒い冬だからこそ\nしっかり食べて\n備えておかなきゃ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8734, 1, 1, 132901, 8, 3, 0, "『聖夜の６時間』…？\n気になる小説だけど…\n勝手に読んじゃ悪い\nわよね…ガマンよ…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8736, 1, 1, 132901, 14, 3, 0, "きゃっ…！\nこんな不思議な本が\nあったのね～\nちょっと面白いかも♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8737, 1, 1, 132901, 15, 3, 0, "もふもふ、ふわふわで\nあったかくて\n気持ちいい～…♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8738, 1, 1, 129501, 1, 1, 0, "冬といえばサマー！\n…って言うけど夏は\n何をＰＲするんだろ\nメルクリゾート…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8739, 1, 1, 129501, 2, 1, 0, "奨学金の道は遠いな…\nあまりに遠すぎて\n最近忘れてたまである", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8740, 1, 1, 129501, 3, 1, 0, "暇だし鏡でも見よっと\nはー、今日も顔が良…\nげ、若干むくんでる？\nはー、今日終わった", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8741, 1, 1, 129501, 4, 1, 0, "水着…お姉ちゃんと\n貸し借りするんだけど\nこれって少数派姉妹？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8742, 1, 1, 129501, 5, 1, 0, "ちゅむ、ツムギちゃん\n今日も可愛いねデュフ\n…なんで推し語りの声\nキモくなるんだろ私…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8743, 1, 4, 129501, 101, 1, 0, "あれ…なんで私\n水着のまま部屋の中\nうろついてるんだっけ\n…こわ！　てか寒っ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8744, 1, 8, 129501, 102, 1, 0, "先輩よく友達と海に\n行くって言ってたけど\n…え、誰とだろ？\nまあ誰でもいいけどさ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8745, 1, 1, 129501, 1, 2, 0, "始まる…\nチエルの夏が始まる…\nあ冬でしたっけ？\nまどっちでもいいです", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8746, 1, 1, 129501, 2, 2, 0, "こないだトミヱ先輩\n壁に喧嘩売ってました\n相変わらず元気ですね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8747, 1, 1, 129501, 3, 2, 0, "ユニ先輩にお芝居の\n出来を聞かれたんで\n可愛いって答えました\n嘘は言ってません", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8748, 1, 1, 129501, 4, 2, 0, "クロエ先輩の腹筋\nかっちょいーですよね\nさりげ触りたいけど\nあいつ隙ないんですよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8749, 1, 4, 129501, 101, 2, 0, "先輩もうカメラ係は\nやらないんです？\nチエルはやってますよ\n最高の被写体を常に", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8750, 1, 8, 129501, 102, 2, 0, "メリゾでカップル割の\n企画あるらしいんです\nちなみに先輩は…\nあ、いえ、何でも…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8751, 1, 1, 129501, 1, 2, 0, "朝ごはんは抜いても\n朝ちぇるだけは\n抜いちゃ駄目ですよ", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8752, 1, 1, 129501, 2, 2, 0, "お昼なに食べるか\n悩んでます？\nおごってくれるなら\nいい店教えますけど", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8753, 1, 1, 129501, 3, 2, 0, "この時間帯になると\nユニ先輩がごそごそ\n蠢きだすんですよね…", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8755, 1, 1, 129501, 2, 3, 0, "世界の美少女が…\nこんな格好で\nくつろいでたら…\nまずい…事案になる", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8756, 1, 1, 129501, 3, 3, 0, "え、この子って\nウォータープルーフ？\nメリゾにも連れて\nいけちゃうカンジ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8757, 1, 1, 129501, 4, 3, 0, "はぁ～、いい香り…\nそういえばメリゾの\nスタッフルームも\n『贅』な匂いしてた…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8758, 1, 1, 129501, 5, 3, 0, "薄着で暖まるとか…\n冷静になっちゃ\n駄目なやつですよね\nこれ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8760, 1, 1, 129501, 7, 3, 0, "おにぎり１個は\nちぇるに換算すると…\n２５ちぇるくらい？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8761, 1, 1, 129501, 8, 3, 0, "あれー？　この辞書\nまだちぇるの項目\n載ってないんだ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8763, 1, 1, 129501, 10, 3, 0, "何これ…地味に楽しい\nあとたぶん\nクロエ先輩が好きそう", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8764, 1, 1, 129501, 11, 3, 0, "これは良きですねー\n自宅でメリゾ気分とか\nユニ先輩もにっこり", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8765, 1, 1, 133001, 1, 1, 0, "ここが騎士氏の\nお住まいですか\nふむ…地縛霊は\nいなさそうですね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8766, 1, 1, 133001, 2, 1, 0, "ここは土足厳禁では\nないみたいですね\nま、私には関係ないか\n足、透っけ透けなので", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8767, 1, 1, 133001, 3, 1, 0, "ランドソルの裏路地\nあそこは最高ですね\n暗くてじめじめしてて\n癒されるー", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8768, 1, 1, 133001, 4, 1, 0, "っ！\n今ラップ音が…？\nまさか幽霊？\nやだ、怖い", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8769, 1, 1, 133001, 5, 1, 0, "獣人族にエルフ族…\nなるほど騎士氏が\n幽霊程度では驚かない\n訳です…ちぇ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8770, 1, 4, 133001, 101, 1, 0, "今日の深夜\n騎士氏が起きるまで\n枕元でずーっと\n立っててみようかな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8771, 1, 8, 133001, 102, 1, 0, "騎士氏の驚く顔を\n見ると胸が高鳴る…\n変ですね、心臓なんて\n動いてないのに", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8772, 1, 1, 133001, 1, 2, 0, "安心して下さい\n勝手にすり抜けて\n騎士氏の部屋は\n覗きませんよ…多分", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8773, 1, 1, 133001, 2, 2, 0, "なんですか騎士氏\n怖い話でも聞きます？\nじゃあいきますね…\n今貴方の前に幽霊がっ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8774, 1, 1, 133001, 3, 2, 0, "この家…幽霊が\n寄ってくる間取りに\nなってますね\nまあ、嘘ですけど", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8775, 1, 1, 133001, 4, 2, 0, "料理はしませんよ\n指きったら痛いので\nえ…透ければいい？\n……天才ですか？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8776, 1, 4, 133001, 101, 2, 0, "お散歩ですか？\n深夜なら付き合います\nあ、やっぱり断ります\n幽霊出たら怖いので", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8777, 1, 8, 133001, 102, 2, 0, "騎士氏の部屋のセンス\n好きですよ…もっと\nじめじめしていれば\nなおよし、ですが", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8778, 1, 1, 133001, 1, 2, 0, "レイスになってから\nあまり言わなくなった\n挨拶第一位はこちら…\nおはようございます", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8779, 1, 1, 133001, 2, 2, 0, "騎士氏。午後からも\n頑張って下さいね\n私は日陰で休みます\nでは、おつです", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8780, 1, 1, 133001, 3, 2, 0, "夜も更けてきましたね\nここからがレイスの\n本領発揮…では早速\nう　ら　め　し　や～", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8782, 1, 1, 133001, 2, 3, 0, "中々座り心地が\nよろしいですね\nブロッケンに跨る方が\n安心はしますが", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8783, 1, 1, 133001, 3, 3, 0, "知ってますよ\nどうせ貴方も\n夜中になったら\n動き回るんでしょ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8784, 1, 1, 133001, 4, 3, 0, "すう…はあ…\nすごく落ち着く香り\n…落ち着き過ぎて\n成仏しそうでした", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8785, 1, 1, 133001, 5, 3, 0, "温度は感じませんが\nなぜかほっこり\n温かい気持ちです\nなんででしょうね？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8787, 1, 1, 133001, 7, 3, 0, "もぐもぐ…\n食べ物は基本好きです\n消化の方法は…\n企業秘密で", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8788, 1, 1, 133001, 8, 3, 0, "苦手なんですよね\nホラー小説\nだって幽霊とか\n怖いじゃないですか", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8790, 1, 1, 133201, 1, 1, 0, "この得も言われぬ\n高揚感…やはり\n正月はいいものだな！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8791, 1, 1, 133201, 2, 1, 0, "む、いかんな\n気付くと黒豆と\n栗きんとんばかり\n食べてしまう…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8792, 1, 1, 133201, 3, 1, 0, "ギルドの連中にも\nお年玉を渡してみるか\n少しは士気が上がる\nかもしれないな！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8793, 1, 1, 133201, 4, 1, 0, "正月くらいはのんびり\n羽を伸ばすとしよう\n適切な休暇も軍人の\n大事な責務だからな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8794, 1, 1, 133201, 5, 1, 0, "正月で浮かれすぎた\n者も多いはずだ\n念のため街を見回りに\n行くとしよう", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8795, 1, 4, 133201, 101, 1, 0, "む、このおせち料理は\n絶品だな！\nあいつの分も\nとっておいてやろう", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8796, 1, 8, 133201, 102, 1, 0, "今年の抱負は…\nそうだな、あいつと\nさらに高みを目指す\nこれでいこう", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8797, 1, 1, 133201, 1, 2, 0, "えっ、お年玉！\n…い、いや、私は\n子どもではないからな\nえ、遠慮させてもらう", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8798, 1, 1, 133201, 2, 2, 0, "凧あげすごろく\nこま回し、たとえ遊び\nでも手は抜かんぞ\nさあ、勝負だ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8799, 1, 1, 133201, 3, 2, 0, "そのきな粉餅も\nうまそうだな…\n私のあんこと交換\nしないか？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8800, 1, 1, 133201, 4, 2, 0, "貴公、正月料理を\n食べ過ぎてないか？\nじ、実は私も少々、な", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8801, 1, 4, 133201, 101, 2, 0, "貴公、お参りに行くぞ\n何？　外が寒いだと？\n仕方ない、手ぐらいは\nつないでやろう", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8802, 1, 8, 133201, 102, 2, 0, "貴公と新たな年を\n迎えられてうれしいぞ\n今年も一つ\nよろしくたのむ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8803, 1, 1, 133201, 1, 2, 0, "起きろ貴公。早朝なら\n参拝客も少ないはず\n今のうちにお参りを\n済ませよう", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8804, 1, 1, 133201, 2, 2, 0, "昼食に餅を焼いたぞ\nのどに詰まらせない\nようにゆっくり噛んで\n食べるんだ", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8805, 1, 1, 133201, 3, 2, 0, "夜ふかしはあまり\n褒められたものでは\nないが…正月くらいは\n目をつむろう", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8807, 1, 1, 133201, 2, 3, 0, "これに座って飲む\n甘酒は格別だな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8808, 1, 1, 133201, 3, 3, 0, "せっかくの正月だし\nこいつにも着物を\n着せてやりたいな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8809, 1, 1, 133201, 4, 3, 0, "これは梅の香り…？\nふふ、なかなか\n風流ではないか", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8810, 1, 1, 133201, 5, 3, 0, "冬の寒さが堪えるな…\nここでしっかりと\n暖をとるとしよう", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8812, 1, 1, 133201, 7, 3, 0, "正月はのんびり\nしたいからな、勝手に\n食事が出てくるのは\n本当に助かる", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8813, 1, 1, 133201, 8, 3, 0, "ふむ、これが世界の\n新年のお祝いか\nなかなか興味深いな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8815, 1, 1, 133501, 1, 1, 0, "一年の計は元旦にあり\n計画を立てて、\n有意義な一年を\n過ごしましょ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8816, 1, 1, 133501, 2, 1, 0, "年末年始はどうしても\n出費が増えちゃうし\nしばらくはきっちり\n節約しなきゃ…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8817, 1, 1, 133501, 3, 1, 0, "タタミにフトン、\nトーゴク文化の内装…\nランドソルでももっと\n流行らないかしら", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8818, 1, 1, 133501, 4, 1, 0, "子どもたちが凧を\n手作りして遊んでたの\n偉いわよね、みんな…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8819, 1, 1, 133501, 5, 1, 0, "お年玉をあげる側に\nなって、分かる苦労…\n大人になるって\nこういうことかしら", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8820, 1, 4, 133501, 101, 1, 0, "一応実家にも\n顔を出さないと…\nよかったらだけど、\nあんたも来る？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8821, 1, 8, 133501, 102, 1, 0, "毎日一緒にいて、\nささやかな幸せを\n同じように感じて…\nそれだけで十分ね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8822, 1, 1, 133501, 1, 2, 0, "着物姿、もっと\nじっくり見たいの？\nさすがに照れるわよ、\nもう…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8823, 1, 1, 133501, 2, 2, 0, "整理整頓、できてる？\nもし散らかってるなら\nお正月休みのうちに\n片づけちゃいましょ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8824, 1, 1, 133501, 3, 2, 0, "今年もみんなが無事に\n過ごせますように…\nほら、一緒に\nお祈りしましょ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8825, 1, 1, 133501, 4, 2, 0, "みんな、あんたと\n遊ぶのが楽しみだって\n子どもに好かれる\nタイプなのよね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8826, 1, 4, 133501, 101, 2, 0, "あんたの運勢は大吉！\nラッキーパーソンは\n金髪の幼なじみよ\n…なんてねっ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8827, 1, 8, 133501, 102, 2, 0, "初夢は実現するのよね\nそれじゃ、あたしは\nあんたと…んんっ\nなんでもないわ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8828, 1, 1, 133501, 1, 2, 0, "外が寒いと、その分\n目も冴えるわよね\nこのまま朝の散歩に\n行こうかしら", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8829, 1, 1, 133501, 2, 2, 0, "お正月休みでも、\n食べてばっかじゃダメ\nしっかり動いて\n体を引き締めましょ", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8830, 1, 1, 133501, 3, 2, 0, "ちゃんとあったかく\nして寝るのよ\nお正月から\n風邪ひかないようにね", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8832, 1, 1, 133501, 2, 3, 0, "着物、しわに\nなっちゃいそう…\nでもそれ以上に、\n心地よくって…はぁ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8833, 1, 1, 133501, 3, 3, 0, "門松みたいに\nこの子も飾ってみる？\n運勢がよくなるかも♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8834, 1, 1, 133501, 4, 3, 0, "この匂い、もしかして\nトーゴクのお香？\nワビサビを感じるわ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8835, 1, 1, 133501, 5, 3, 0, "ふぅ、あったかい…\n冷えた体に\n染み渡ってくるわ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8837, 1, 1, 133501, 7, 3, 0, "んっ、おいしい！\nおせちばっかじゃ\nやっぱり飽きちゃう\nものね…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8838, 1, 1, 133501, 8, 3, 0, "験担ぎとか縁起物とか\n調べてみると\nいろいろあって\n面白いのよね～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8840, 1, 1, 133601, 1, 1, 0, "ふーん、いい家ね\nアタシの巣に\nしちゃおうかしら？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8841, 1, 1, 133601, 2, 1, 0, "お姉ちゃん…\n早く会いたいなぁ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8842, 1, 1, 133601, 3, 1, 0, "メドゥシアナも\n気に入るかしら？\n今度連れてこよっと！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8843, 1, 1, 133601, 4, 1, 0, "お邪魔してるだけ\nなのも悪いわよね…\nお返しはしないとね\n…ネズミ退治とか？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8844, 1, 1, 133601, 5, 1, 0, "何か平和よね\nランドソルって…\nアタシ的にはもう少し\n荒っぽくてもいいわ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8845, 1, 4, 133601, 101, 1, 0, "ランドソルも\n楽しい街よね…\n案内してくれる\nアイツのおかげ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8846, 1, 8, 133601, 102, 1, 0, "アタシ、アイツに\n頼りすぎてるかも…？\nで、でも！　断らない\nアイツも問題だわ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8847, 1, 1, 133601, 1, 2, 0, "ねぇ、つまんなーい！\nもっとアタシを\n楽しませなさいよねー", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8848, 1, 1, 133601, 2, 2, 0, "この家悪くないけど…\nもっとムシムシ\nジメジメにできない？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8849, 1, 1, 133601, 3, 2, 0, "何よ？\n用もないのに声掛けた\n…とか言ったら\n容赦しないわよ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8850, 1, 1, 133601, 4, 2, 0, "少し部屋が寂しいわね\nそうだ、アンタを\n石にして家具に…\n冗談よ、ふふんっ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8851, 1, 4, 133601, 101, 2, 0, "ねぇ、もう少し\n話していかない？\nアンタの話なら…\n特別に聞いてあげるわ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8852, 1, 8, 133601, 102, 2, 0, "い、いつも\nあ、あ、ありが…\nうぅ、何でもないー！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8853, 1, 1, 133601, 1, 2, 0, "やっと起きたわね？\n寝ぼすけなんだから！\n尻尾でひっぱたいて\n起こそうかと思ったわ", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8854, 1, 1, 133601, 2, 2, 0, "人捜しは日の高い\nうちに限るわ！\nさぁ、メドゥシアナ！\n全速力で出発進行よ!", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8855, 1, 1, 133601, 3, 2, 0, "アタシ…\n子どもじゃないもん…\n眠くなんか…\nないんだからぁ…", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8857, 1, 1, 133601, 2, 3, 0, "ふわふわであったかい\nつい、とぐろを\n巻きたくなるわね…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8858, 1, 1, 133601, 3, 3, 0, "アンタ、こんな\nヤワな体じゃ駄目よ\nウロコくらい\n生やしたらどうなの？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8859, 1, 1, 133601, 4, 3, 0, "くんくん…\n…ふん\n悪くないじゃない\n…くんくんくんくん…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8860, 1, 1, 133601, 5, 3, 0, "あったかいの大好き！\nジオ・ゲヘナでも\nよく日向ぼっこ\nしたもんだわ…ふわぁ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8862, 1, 1, 133601, 7, 3, 0, "お姉ちゃんを捜すなら\n…もぐ\n体力つけないとね\n…もぐもぐ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8863, 1, 1, 133601, 8, 3, 0, "何よ？　たまには\n勉強くらいするわよ\nお姉ちゃんに\n笑われちゃうもの", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8865, 1, 1, 133701, 1, 1, 0, "この装いと力…\n今の世界との\n整合性を上手いこと\n合わせておかないとだ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8866, 1, 1, 133701, 2, 1, 0, "この格好…開発してた\n当時のまんまだ\nサイズもそのまま♪\n体型維持も完璧っ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8867, 1, 1, 133701, 3, 1, 0, "あとで装備の手入れも\nやっとかないと\n…剣なら研ぐだけで\n終わるんだけどなぁ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8868, 1, 1, 133701, 4, 1, 0, "自分の原点を\n振り返ってみるのも\nたまには必要だね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8869, 1, 1, 133701, 5, 1, 0, "完璧じゃなくても\n今できる全力を尽くす\nその事に意味がある…\nそうだよね、少年", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8870, 1, 4, 133701, 101, 1, 0, "始まりのあの日、\nそして向かうべき道…\nいつも忘れずにいよう", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8871, 1, 8, 133701, 102, 1, 0, "誰かを救うために\n許容される犠牲なんて\nあっちゃいけない…\n全部、助けてやる", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8872, 1, 1, 133701, 1, 2, 0, "おっと少年、\n何か御用かな？\nキミに関わる事なら\n最優先で対応するよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8873, 1, 1, 133701, 2, 2, 0, "困った事があれば\n頼ってもらって\n構わないからね！\nむしろもっと頼って！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8874, 1, 1, 133701, 3, 2, 0, "そうだ、時間あるなら\n武器のメンテを\n手伝ってくれないかな\n数が多くてさ～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8875, 1, 1, 133701, 4, 2, 0, "遠距離戦の心得、\n少年にもレクチャー\nしてあげよっか？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8876, 1, 4, 133701, 101, 2, 0, "無茶しないで、って\n言ってもするだろうし\nせめてアタシの手が\n届く範囲で頼むよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8877, 1, 8, 133701, 102, 2, 0, "虚実入り混じる\nこの世界でキミが…\n進むべきルートを\n照らしてくれたんだ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8878, 1, 1, 133701, 1, 2, 0, "グッモーニン！\n朝だよ少年、今日も\n張り切っていこう！", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8879, 1, 1, 133701, 2, 2, 0, "クレープの代わりに\nガレットを焼いたんだ\nこの味で少年を\n唸らせてみせよう！", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8880, 1, 1, 133701, 3, 2, 0, "もうこんな時間だ\n遅くならないうちに\n作業を切り上げようか\n少年も早く寝なよ？", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8882, 1, 1, 133701, 2, 3, 0, "あえて緊張を解くのも\n戦いには必要だし…\nそうそう…大事大事…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8883, 1, 1, 133701, 3, 3, 0, "愛着を抱かせる事を\n意図したデザインだね\n心理的効果の観点から\n分析してみようか…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8884, 1, 1, 133701, 4, 3, 0, "香りは記憶と密接に\n結びつく事がある…\n懐かしい匂いだね…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8885, 1, 1, 133701, 5, 3, 0, "癒されるな…\n何がこの感情変化を\n引き起こすんだろう？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8887, 1, 1, 133701, 7, 3, 0, "食の偉大さを感じるな\n戦闘糧食ってやっぱり\n味気ないしなぁ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8888, 1, 1, 133701, 8, 3, 0, "実用ばかりじゃなく\n詩や物語ってのも\n人には必要だよね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8890, 1, 1, 133901, 1, 1, 0, "この場所の開発版も\n探せばあるかもな\nさて、どんなデザイン\nだったのやら…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8891, 1, 1, 133901, 2, 1, 0, "今まで数々の波乱に\n身を投じたが…やはり\n波乱にはこの姿が一番\n似合いだよ☆", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8892, 1, 1, 133901, 3, 1, 0, "むっ…この角に羽と\n尻尾は家具とぶつかり\nやすいな。兎の時は\n気にならなかったが…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8893, 1, 1, 133901, 4, 1, 0, "いつもより闘争を\n求める気分になるな\n初心に帰るとは\nこのことか…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8894, 1, 1, 133901, 5, 1, 0, "未調整上等☆\nそれを上手く使い\nこなしてこその\nワタシだろう？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8895, 1, 4, 133901, 101, 1, 0, "坊やとできそうな\n愉しいことは…\nふむ、多すぎて迷うな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8896, 1, 8, 133901, 102, 1, 0, "時折、坊やの居心地の\nよさに絆されそうに\nなってしまうな\nワタシらしくもない…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8897, 1, 1, 133901, 1, 2, 0, "どうだ、おろした\n髪型も艶やかだろう？\nそんじょそこらの\n賞賛では満足せんぞ☆", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8898, 1, 1, 133901, 2, 2, 0, "外に出るぞ！\n共に退屈を殺しに\n行こうじゃないか☆", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8899, 1, 1, 133901, 3, 2, 0, "角と翼も触りたい？\nワタシの虚数魔境に\n挑むというなら\n止めはせんよ☆", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8900, 1, 1, 133901, 4, 2, 0, "なんだ、困りごとか？\nクリスティーナさんが\n何でも聞いてやろう☆\n解決するかは別だがな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8901, 1, 4, 133901, 101, 2, 0, "力が欲しいか？\nならば呼ぶがいい！\nワタシの名を☆", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8902, 1, 8, 133901, 102, 2, 0, "悪魔との契約は絶対\nなのだそうだが…\nどうだ、坊や\n一つ契ってみるか？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8903, 1, 1, 133901, 1, 2, 0, "まだ眠い？　甘えるな\n寝首を掻かれたく\nなければ飛び起きて\nこい…☆", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8904, 1, 1, 133901, 2, 2, 0, "昼食の準備を\nしてやろう。\n毒見はいらんぞ？\n安心するといい！", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8905, 1, 1, 133901, 3, 2, 0, "坊やは寝る時間だ\n寝ないのならば…\n大人の時間に\n付き合ってもらうか☆", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8906, 1, 1, 133901, 1, 3, 0, "角と翼、尻尾の\nコンディションまで\n完璧だな☆", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8907, 1, 1, 133901, 2, 3, 0, "なかなかのフワモコ\n具合だが…ううむ…\n翼と尻尾が邪魔だな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8908, 1, 1, 133901, 3, 3, 0, "うーむ、角と尻尾を\n追加して…さらに槍を\n持たせた方が見栄えが\nよさそうだ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8909, 1, 1, 133901, 4, 3, 0, "いい香りだな\n血と砂塵の臭いで\n上書きするまでの\n間に合わせに最適だ☆", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8910, 1, 1, 133901, 5, 3, 0, "生温いな\nもっと焦がれるほどの\n熱量が欲しいところだ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8911, 1, 1, 133901, 6, 3, 0, "普段はないものが\n生えているのだから、\n運動でしっかり感覚を\n掴んでおかなければな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8912, 1, 1, 133901, 7, 3, 0, "腹が減っては戦は…\nと言うからな\nしっかり食べて\nおくとしようか☆", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8913, 1, 1, 133901, 8, 3, 0, "『家庭を築くための\n絶対条件』…？\nふむ…興味がある\nタイトルだ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8915, 1, 1, 133801, 1, 1, 0, "奇妙な感覚です\nまるであの頃の自分に\n変装しているかのよう", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8916, 1, 1, 133801, 2, 1, 0, "グレゴール？\nおっと、そうでした\n今は昔の姿になって\n離れていましたね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8917, 1, 1, 133801, 3, 1, 0, "このアバターでいると\n妙に子どもたちからの\n視線を感じますね…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8918, 1, 1, 133801, 4, 1, 0, "リソースの割り振りが\n変わっただけなのに\n随分と身体が軽く\n感じますね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8919, 1, 1, 133801, 5, 1, 0, "普段グレゴールに\n任せている分身の管理\n自分でやるには\nいささか煩雑ですね…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8920, 1, 4, 133801, 101, 1, 0, "長老に頼まれて描いた\n妖精の絵…いったい\n何に使ったのでしょう", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8921, 1, 8, 133801, 102, 1, 0, "私自身をモデルにした\n完全なるNPCたち\nそれが私の分身です", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8922, 1, 1, 133801, 1, 2, 0, "このアバターが\n気になるのですか？\n貴方はつくづく\n妖精に縁がありますね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8923, 1, 1, 133801, 2, 2, 0, "ちょうどよかった\nグレゴールを見かけ\nませんでしたか？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8924, 1, 1, 133801, 3, 2, 0, "あの翅は飾りでは\nありません。ただ…\nグレゴールに乗って\n飛ぶ方が楽なだけです", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8925, 1, 1, 133801, 4, 2, 0, "妖精とは本来神話に\nおけるトリックスター\n運命をかき回すことが\n存在意義なのです", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8926, 1, 4, 133801, 101, 2, 0, "変貌大妃の権能との\n相性を考えなければ\nこの姿こそが私たちの\n最強の姿です", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8927, 1, 8, 133801, 102, 2, 0, "変化とは一種の喪失\nだからこそ得られる物\nがある…貴方のそれも\n大切にするのですよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8928, 1, 1, 133801, 1, 2, 0, "マサキからの定時連絡\nが来ませんね…心配は\n不要ですが、様子を\n見に行きましょうか", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8929, 1, 1, 133801, 2, 2, 0, "昼食の時間ですね\nグレゴールのために\nカロリーの高い物を\n食べに行きましょう", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8930, 1, 1, 133801, 3, 2, 0, "夜更かしはダメですよ\n悪いスパイが暗躍して\nいる時間帯ですからね", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8932, 1, 1, 133801, 2, 3, 0, "ふむ、座り心地は中々\nですが…私にはやはり\n玉座の方があいますね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8933, 1, 1, 133801, 3, 3, 0, "ふふ、いいでしょう\n我が配下に加えます\n存分に働きなさい", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8934, 1, 1, 133801, 4, 3, 0, "時折晶からしていた\nあの甘い匂いは何の\nアロマだったのか…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8935, 1, 1, 133801, 5, 3, 0, "燃え盛る炎もこうして\n暖炉の中なら安全です\nクリスもあの衝動を\n管理さえできれば…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8937, 1, 1, 133801, 7, 3, 0, "そういえば真那が\n獄中で手持無沙汰に\nひたすらおにぎりを\n食べていましたね…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8938, 1, 1, 133801, 8, 3, 0, "これは、妖精学の本…\n長老の蔵書から借りて\n読みふけったものです", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8940, 1, 1, 134301, 1, 1, 0, "どんな詩を\n作ろうかしら…？\n…大体、暗く\nなっちゃうけど…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8941, 1, 1, 134301, 2, 1, 0, "唱喚術の練習…\nここでやったら\n迷惑よね…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8942, 1, 1, 134301, 3, 1, 0, "チカちゃんとの\nデュエット…\n足をひっぱってない…\nかしら…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8943, 1, 1, 134301, 4, 1, 0, "唱喚士の修行…\n滝に打たれるとか…\n崖を登るとか…\nなくて、よかった…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8944, 1, 1, 134301, 5, 1, 0, "精霊さん、肉じゃが\n食べる、かしら…？\n魔力だけだと…\n飽きちゃいそうだし…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8945, 1, 4, 134301, 101, 1, 0, "精霊さんのお世話に\n慣れたぶん…\nあのひとのお世話も\n上手にできる…はず？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8946, 1, 8, 134301, 102, 1, 0, "これからも、歌うわ…\nこの声が、彼の力に\nなる、限り──", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8947, 1, 1, 134301, 1, 2, 0, "この服…踊り子さんに\n間違われちゃって…\nあ…お、お触りしちゃ\nだめよ…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8948, 1, 1, 134301, 2, 2, 0, "あなたはどんな\n精霊が…好き…？\n唱喚…してみたいわ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8949, 1, 1, 134301, 3, 2, 0, "あんまり白い服って…\n着ないの…その…\nこ、ころんだりして…\n汚すから…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8950, 1, 1, 134301, 4, 2, 0, "じ、実はね…\nこの体の模様…筆で\n書いてもらうたび…\nくすぐったい…の…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8951, 1, 4, 134301, 101, 2, 0, "精霊さん…あなたと\n遊びたいみたい…\nや、やっぱり…\nパパ…だから…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8952, 1, 8, 134301, 102, 2, 0, "あなたを歌にしたら…\n…ふふっ、だめね\n一曲じゃ…あなたを\n表現できない、わ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8953, 1, 1, 134301, 1, 2, 0, "朝の修行は…\nうとうと…し…ちゃ…\n……（かくん）\n……あぅっ？", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8954, 1, 1, 134301, 2, 2, 0, "せ、精霊さんと…\nかくれんぼ中なの…\nうぅ…見つからない…\n日が暮れちゃいそう…", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8955, 1, 1, 134301, 3, 2, 0, "精霊さん、寝たから…\nふふっ…ここからは\nパパとふたりの時間…\n…ね？", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8957, 1, 1, 134301, 2, 3, 0, "ふぅ…修行と育児は…\nちょっとだけ…\n休憩…ふぁ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8958, 1, 1, 134301, 3, 3, 0, "あなたとも契約…\n…できる、かしら？\nふふっ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8959, 1, 1, 134301, 4, 3, 0, "いい香りに包まれたら\n精神統一も\nはかどりそう…♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8960, 1, 1, 134301, 5, 3, 0, "あたたかい場所…\nここで作詞したら…\n穏やかな詩が\n書ける、かも…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8962, 1, 1, 134301, 7, 3, 0, "精霊さんのお世話を\nしていると…\nお腹、空いちゃって…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8963, 1, 1, 134301, 8, 3, 0, "ふふっ…♪\n色んな楽譜…\n集めるの、楽しい…♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8965, 1, 12, 122301, 103, 1, 0, "けんぞくまだかな？\nはやく会いたいなー\nヴァンピィちゃん\nずっと待ってるからね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8966, 1, 12, 122301, 103, 2, 0, "けんぞくのこと\nずっとずっと守るよ！\nだからいつまでも\n一緒にいてね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8967, 1, 1, 134401, 1, 1, 0, "私の歌や想いを\n誰かに託せる…\nこれほどうれしい\nことはありません", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8968, 1, 1, 134401, 2, 1, 0, "コッコロさんの\n包容力はすごいです\nあんなふうに私も\n人を包み込めたら…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8969, 1, 1, 134401, 3, 1, 0, "最近のボイトレは\nランファさんと一緒に\nやるんです。お互いに\n切磋琢磨しますよ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8970, 1, 1, 134401, 4, 1, 0, "先輩として相応しい\n立ち振る舞いを\n心がけたいですね\nしっかりしなきゃ…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8971, 1, 1, 134401, 5, 1, 0, "ランファさんが\n踊ったら映えるかも…\nアイドルにも興味を\n持ってもらえないかな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8972, 1, 4, 134401, 101, 1, 0, "支え導くために\n寄り添って考える…\nあの人からたくさん\n教わったことでしたね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8973, 1, 8, 134401, 102, 1, 0, "いつだってあなたに\n恥じることのない\n姿をお見せしたいです", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8974, 1, 1, 134401, 1, 2, 0, "あなたの手は温かくて\n触れていると\n落ち着きますね…♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8975, 1, 1, 134401, 2, 2, 0, "里のみんなから修行の\n差し入れを頂きました\nよろしければ、一緒に\n食べましょう♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8976, 1, 1, 134401, 3, 2, 0, "ノゾミにもお土産を\n用意したいですね\n里のお土産選び、\n付き合ってください♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8977, 1, 1, 134401, 4, 2, 0, "精霊たちも、あなたが\n大好きみたいです♪\n優しさが伝わって\nいるんでしょうね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8978, 1, 4, 134401, 101, 2, 0, "特訓が終わったら、\n二人でどこかに\n出かけましょうか♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8979, 1, 8, 134401, 102, 2, 0, "ひゃ…す、すみません\n肌の模様を\nなぞられると、\nくすぐったくて…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8980, 1, 1, 134401, 1, 2, 0, "おはようございます\n精霊たちと一緒に、\n今日も頑張ります\n応援してくださいね♪", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8981, 1, 1, 134401, 2, 2, 0, "お昼の時間です\n今日も精いっぱい\n修行を頑張りましょう", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8982, 1, 1, 134401, 3, 2, 0, "もうすっかり夜ですね\n眠るまで子守唄を\n歌ってあげますよ？\nふふ♪", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8983, 1, 1, 134401, 2, 3, 0, "無理せず休憩するのも\n大切ですよね\nふかふか～…ふふっ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8984, 1, 1, 134401, 3, 3, 0, "今日はこの子に\n歌の特訓を聞いて\nもらいましょう♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8985, 1, 1, 134401, 4, 3, 0, "素敵な香り…\nふふ、精霊たちも\n気に入ったみたいです", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8986, 1, 1, 134401, 5, 3, 0, "あったかい…\nこの衣装、やっぱり\n少し冷えやすいものね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8987, 1, 1, 134401, 7, 3, 0, "特訓の後のご飯は\n美味しいですね♪\nこれでまた頑張れます", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8988, 1, 1, 134401, 8, 3, 0, "唱喚士についての\n書籍はないみたい\nいつか私がここに\n楽譜を入れたいな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8992, 1, 1, 134601, 1, 1, 0, "ギルドハウスの平和も\n任せてください\n何が来たって\n守ってみせますよ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8993, 1, 1, 134601, 2, 1, 0, "このギルドハウスも\nいつの間にか出撃台に\n改造されてたり\nするんでしょうか…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8994, 1, 1, 134601, 3, 1, 0, "戦いを経験してこそ\n実感できますね\n平和はいいもの、\n友達は素晴らしいもの", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8995, 1, 1, 134601, 4, 1, 0, "次はランドソルで\n巨大ロボットが\nブームになったり？\n…さすがにないか", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8996, 1, 1, 134601, 5, 1, 0, "改めて見るとこの服…\nか、体の線とか\n結構目立ってるかも…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8997, 1, 4, 134601, 101, 1, 0, "団長さんって\n知らない人とも\n話せる系ですよね\nコツはいったい…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8998, 1, 8, 134601, 102, 1, 0, "ＢＢ団、世界を救う！\nまだ信じられないけど\n本当にあった出来事\nなんですよね…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (8999, 1, 1, 134601, 1, 2, 0, "パイロットも\n視力が大事なんです\n弓使いの経験が\n生きました！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9000, 1, 1, 134601, 2, 2, 0, "ギガンティック\nマイフレンドくん、\n出撃！\nあ、ただの妄想です", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9001, 1, 1, 134601, 3, 2, 0, "コクピットから\n降りたあとに残る\nふわふわ感…ちょっと\n癖になりませんか？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9002, 1, 1, 134601, 4, 2, 0, "ひえっ、敵襲！？\nあっすみません、\nちょっと\n寝ぼけてたかも…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9003, 1, 4, 134601, 101, 2, 0, "共に戦場を駆け抜け\nＢＢ団の絆もより強く\nこれからも結束を\n強めましょうね！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9004, 1, 8, 134601, 102, 2, 0, "同じコクピットに\n乗ったのに比べれば、\nスキンシップくらい…\nや、やっぱ緊張します", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9005, 1, 1, 134601, 1, 2, 0, "おはようございます！\n今日もがんばりますっ\n…クローチェさんを\n見習ってみました…", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9006, 1, 1, 134601, 2, 2, 0, "お昼ご飯の時間ですね\nがんばって他の人\n誘ってみようかな…", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9007, 1, 1, 134601, 3, 2, 0, "夜の見張りは\n任せてください\n一人きりの夜も\n慣れてますから！", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9009, 1, 1, 134601, 2, 3, 0, "休めるときに\n休んでおかなくちゃ\nいつ何が起こるか\n分かりませんからね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9010, 1, 1, 134601, 3, 3, 0, "師匠もこういうの\n好きだったのかな…\nなんだかしんみり\nしちゃいます", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9011, 1, 1, 134601, 4, 3, 0, "香りでリラックス…\n余裕のない時でも\nこういう安らぎは\n大切ですよね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9012, 1, 1, 134601, 5, 3, 0, "あったかい…\nこれはきっと友情と\n同じぬくもり…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9014, 1, 1, 134601, 7, 3, 0, "新鮮なご飯、感動…！\n師匠たちの世界だと\nご飯は保存食ばかり\nでしたからね…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9015, 1, 1, 134601, 8, 3, 0, "操縦マニュアル、\nしっかり読んで\n性能を引き出さなきゃ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9017, 1, 1, 134501, 1, 1, 0, "屋内で飛べなくても\nここなら平気ですっ\n助けてくれる人が\nたくさんいますからっ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9018, 1, 1, 134501, 2, 1, 0, "新ボディになってから\n人や物が前より身近に\n感じられて、わたし、\n幸せですっ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9019, 1, 1, 134501, 3, 1, 0, "配達以外にも何か\nお仕事したいですっ！\nハグで遊覧飛行とか、\n喜んでもらえますか？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9020, 1, 1, 134501, 4, 1, 0, "むむむ～…\n文字を書くのって\n難しいですね～っ\n練習しなくちゃっ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9021, 1, 1, 134501, 5, 1, 0, "簡易点検、よしっ！\nポポおじいさんに\n点検の仕方を教わって\n今日も絶好調ですっ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9022, 1, 4, 134501, 101, 1, 0, "明日は北の空、\n明後日は南の空っ♪\nえへへっ、見せたい\n景色がいっぱいですっ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9023, 1, 8, 134501, 102, 1, 0, "ついナイトさんを\n探しちゃいます…\nわたし、寂しんぼさん\nだったんでしょうかっ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9024, 1, 1, 134501, 1, 2, 0, "どんなところでも\nひとっ飛びっ♪\nわたしに任せて\nくださいねっ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9025, 1, 1, 134501, 2, 2, 0, "まだ知らない機能が\nエアリアルには\n隠されてるかもっ…\nワクワクしますねっ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9026, 1, 1, 134501, 3, 2, 0, "ひゃんっ！　…あはは\nエアリアルになっても\nケーブルに触られると\nくすぐったいですねっ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9027, 1, 1, 134501, 4, 2, 0, "ヴォルガーノさんに\nそっくりの子猫さんに\n最近触れたんですっ！\nかわいかったな～っ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9028, 1, 4, 134501, 101, 2, 0, "ハグしてくれるん\nですかっ？\nえへへっ、ぎゅーっ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9029, 1, 8, 134501, 102, 2, 0, "ナイトさんと一緒に\nいるだけで、胸が\nぽかぽか～って\nあったかいですっ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9030, 1, 1, 134501, 1, 2, 0, "おはようございますっ\n目覚まし飛行は\nいかがでしょうかっ？", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9031, 1, 1, 134501, 2, 2, 0, "お昼ですよっ！\nいっぱい補給して\nまたフルパワーで\nがんばりましょうっ♪", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9032, 1, 1, 134501, 3, 2, 0, "眠れない夜はわたしに\n任せてくださいっ！\n充填さえあればずっと\n一緒にいられますよっ", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9034, 1, 1, 134501, 2, 3, 0, "歩きづらいので\nこういう休憩ポイント\nって助かります～っ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9035, 1, 1, 134501, 3, 3, 0, "スラスターを付けたら\nこの子たちも空を\n自由に飛べたり\nするんでしょうかっ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9036, 1, 1, 134501, 4, 3, 0, "あっ、これに似た\n香りを近くのお店で\n検知したことが\nありますっ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9037, 1, 1, 134501, 5, 3, 0, "高熱源反応を検知っ\nわたしのスラスターも\n熱量の高さなら\nかなりものですよっ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9039, 1, 1, 134501, 7, 3, 0, "もぐもぐもぐ…っ\n食べたぶんはあとで\n真空パックにして\nお返ししますねっ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9040, 1, 1, 134501, 8, 3, 0, "本を読むのって\n楽しいですねっ♪\n前はびりびりーって\nしちゃってたのでっ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9042, 1, 1, 134701, 1, 1, 0, "なかなかの住処ですね\nこの私の別邸にして\nあげてもいいですよ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9043, 1, 1, 134701, 2, 1, 0, "はぁ…いつになったら\nすべての神力が\n戻るのでしょう？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9044, 1, 1, 134701, 3, 1, 0, "ここも悪くないですが\n昔私が作った庭園は、\nもっと良い場所でした\n焼けちゃいましたが…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9045, 1, 1, 134701, 4, 1, 0, "うう…\nおなかが空きました…\nお米が食べたいです…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9046, 1, 1, 134701, 5, 1, 0, "ここは平和ですねえ…\nジオ・ゲヘナも\nこのくらい穏やかなら\n住みやすいのですが", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9047, 1, 4, 134701, 101, 1, 0, "騎士さんのおかげで\n神力が戻ってきました\n良い働きですよ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9048, 1, 8, 134701, 102, 1, 0, "騎士さんのいる場所が\n今の私がいるべき場所\nなのかもしれませんね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9049, 1, 1, 134701, 1, 2, 0, "頭がたかーい！\n…ですよっ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9050, 1, 1, 134701, 2, 2, 0, "ほーちゃんと呼ぶ権利\n安売りはしませんよ？\n特別ですから！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9051, 1, 1, 134701, 3, 2, 0, "私とおしゃべりを？\nまあ、いいでしょう♪\n何を話しましょうか？\nこの羽の秘密とか？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9052, 1, 1, 134701, 4, 2, 0, "小鳥扱いは\nやめなさーい！\nこれでも神様ですよ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9053, 1, 4, 134701, 101, 2, 0, "き、気安く触るのは…\n…い、いえ、\n騎士さんなら…まぁ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9054, 1, 8, 134701, 102, 2, 0, "絢爛豪華な絆！\nこの私と結べるなんて\n光栄でしょう？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9055, 1, 1, 134701, 1, 2, 0, "ふわぁぁ～\nおはようごじゃいます\n…んむむ", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9056, 1, 1, 134701, 2, 2, 0, "お昼ご飯は何を…\nえっ？　私？\nそそんな゛あ゛！", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9057, 1, 1, 134701, 3, 2, 0, "あの…騎士さん？\n添い寝する権利とか…\nほしくないですか？", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9059, 1, 1, 134701, 2, 3, 0, "はぁ～ふかふか…\n…へ？\n鳥の巣にいるみたい？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9060, 1, 1, 134701, 3, 3, 0, "ふむ…\nそばに置いてあげても\nいいでしょう", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9061, 1, 1, 134701, 4, 3, 0, "ほわぁ～\nいいかおり～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9062, 1, 1, 134701, 5, 3, 0, "あたたかい…\nなんだか元気が\nでてきますねっ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9064, 1, 1, 134701, 7, 3, 0, "たくさん食べて力を\n取り戻さなくてはっ\n…んがぐぐっ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9065, 1, 1, 134701, 8, 3, 0, "ふむふむ…\n人の子にしては\nよく書けていますね♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9067, 1, 1, 133101, 1, 1, 0, "素敵なお家ですね\n温かみがあって\nどこか騎士様に\n似ております。ふふ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9068, 1, 1, 133101, 2, 1, 0, "賑やかな街ですね\nこれほど活気豊かだと\nつい外に出てみたく\nなってしまいます", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9069, 1, 1, 133101, 3, 1, 0, "別世界の存在は知って\nおりましたが、いざ\n目の当たりにすると\n驚きの連続ですわ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9070, 1, 1, 133101, 4, 1, 0, "この街は様々な種族が\nなにはばかることなく\n混在しているのですね\n…我々も、いつかは…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9071, 1, 1, 133101, 5, 1, 0, "ランドソルにも香水の\nお店があるのですね\nどんな香りが揃って\nいるのでしょうか…♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9072, 1, 4, 133101, 101, 1, 0, "昨日、素敵な花園を\n見つけたのです\n今日のお茶会は\nそちらでしましょうか", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9073, 1, 8, 133101, 102, 1, 0, "気を抜いてはいけない\nとわかっていても、\nここだとつい安心して\nしまいます…ふう…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9074, 1, 1, 133101, 1, 2, 0, "ごきげんよう\n街で見つけた茶葉を\n試しているんです\n騎士様も一杯どうぞ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9075, 1, 1, 133101, 2, 2, 0, "親切な方ばかりですね\n素敵なお友だちが\nできそうな予感で、\nワクワクしてます…♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9076, 1, 1, 133101, 3, 2, 0, "黄泉の食卓の皆様用に\nお土産を用意しないと\n騎士様のお知恵も\n頼りにしておりますわ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9077, 1, 1, 133101, 4, 2, 0, "ジオ・ニヴルヘルと\n比べると、この世界は\n本当に明るいですね\n少し眩しいくらいです", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9078, 1, 4, 133101, 101, 2, 0, "あまり、血の巡りが\n良くなくて…\n騎士様の手は\nとても温かいですね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9079, 1, 8, 133101, 102, 2, 0, "今度はわたくしに\nジオ・ニヴルヘルを\n案内させてくださいね\n観光案内のお返しです", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9080, 1, 1, 133101, 1, 2, 0, "おはようございます\n朝の眩しい太陽には\nなかなか慣れません…", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9081, 1, 1, 133101, 2, 2, 0, "騎士様、お茶は\nいかがでしょうか？\n一度一休みして、\n英気を養いましょう", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9082, 1, 1, 133101, 3, 2, 0, "眠れないのでしょうか\nまぶたが重くなるまで\nわたくしをお話し\n相手になさって下さい", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9084, 1, 1, 133101, 2, 3, 0, "思わず気が抜けそうに\nなってしまいます…\nふふ、ふわふわ…♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9085, 1, 1, 133101, 3, 3, 0, "まあ、かわいらしい\nグレイスもきっと\n気に入りそうな\nデザインですわ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9086, 1, 1, 133101, 4, 3, 0, "まあ、いい香り…\n心安らぐようですわ\n素敵なセンスを\nお持ちなのですね♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9087, 1, 1, 133101, 5, 3, 0, "燃え移りや乾燥がある\nから近づかないほうが\nいいとわかりつつも…\nああ、温かい…♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9089, 1, 1, 133101, 7, 3, 0, "食が細いというよりは\n消化があまりうまく\nできなくて…本当は\nまだ頂きたいのですが", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9090, 1, 1, 133101, 8, 3, 0, "『薔薇の下の契り』？\nまあ、男性同士で…！\nそんな…こ、これ以上\nここで読むわけには！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9092, 1, 1, 132301, 1, 1, 0, "ダーリンが帰ってくる\n前に部屋の掃除しよ♪\n…ふふ、これってもう\n新婚生活だよね…♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9093, 1, 1, 132301, 2, 1, 0, "家具のセンスまで\n私と同じだなんて\n運命って怖いわ…♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9094, 1, 1, 132301, 3, 1, 0, "あの人はベッドの下に\n何か隠してるのかな…\nな、なんて全っ然\n気にならないし…っ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9095, 1, 1, 132301, 4, 1, 0, "うっかりして\n角引っかけないように\nしないとね…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9096, 1, 1, 132301, 5, 1, 0, "錬金術を使わなくても\nあなたの心を鷲掴む\n私は恋も錬金術も\n失敗しないんだから！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9097, 1, 4, 132301, 101, 1, 0, "あの人って破廉恥なの\n興味あるのかしら…？\n基本破廉恥禁止だけど\nあの人だけは…許す…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9098, 1, 8, 132301, 102, 1, 0, "手狭になれば錬金術で\n新しい部屋作るから\nいつ家族が増えても\n安心だよね…♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9099, 1, 1, 132301, 1, 2, 0, "錬金術でカレーライス\n作っちゃおっかな♪\n具材を釜でグツグツ…\nま、料理とも言うわね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9100, 1, 1, 132301, 2, 2, 0, "錬金術に興味がある？\nじゃあさっそく私が\n手取り足取り教えるわ\nって…やだ破廉恥っ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9101, 1, 1, 132301, 3, 2, 0, "イロハちゃんとの\n虫捕りは命懸けよ！\n一万匹捕れるまで\n帰れないんだから…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9102, 1, 1, 132301, 4, 2, 0, "ソノさんって\n体は小さいけど\nとっても頼れる私の\nお姉さん的存在なの♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9103, 1, 4, 132301, 101, 2, 0, "はあ…部屋中に広がる\nダーリンの香り…♪\n…ってノゾミみたいに\nなっちゃってる！？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9104, 1, 8, 132301, 102, 2, 0, "鹿の獣人族の特徴は\nこのエッチな角…\n違っ！　叡智よ叡智！\n噛んだだけだからっ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9105, 1, 1, 132301, 1, 2, 0, "あ、おはよう！\n寝る前に飲んだ\n目覚めすっきり秘薬の\n効果はバッチリかな？", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9106, 1, 1, 132301, 2, 2, 0, "午後からも頑張る\nあなたのために\n愛妻弁当作ったの♪\n一緒に食べよ？", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9107, 1, 1, 132301, 3, 2, 0, "今日も一日お疲れさま\n眠れないなら隣で…\nじゃなかった早寝して\n明日も頑張ろうね！", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9109, 1, 1, 132301, 2, 3, 0, "いい錬成をするには\n適度な休憩も必要よね\n…気持ち良くてたまに\n寝落ちしちゃうけど…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9110, 1, 1, 132301, 3, 3, 0, "かわいいぬいぐるみ\n錬成じゃなくて\n手編みで作ったら\n驚いてくれるかも♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9111, 1, 1, 132301, 4, 3, 0, "くん、くん…\nん～いい匂い♪\nノゾミの香水と\nいい勝負だわ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9112, 1, 1, 132301, 5, 3, 0, "ああ、暖かい\nでもソノさんの作った\nミートパイの方が\nもっと温かいわ…♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9114, 1, 1, 132301, 7, 3, 0, "もぐもぐ…私も\n食べても減らない秘薬\n錬成して他の食べ物も\n作れないかな？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9115, 1, 1, 132301, 8, 3, 0, "ふむふむ…\nこのレシピ面白いわね\n今度錬成してみよっと", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9117, 1, 1, 132501, 1, 1, 0, "おー、体が軽い\n腰痛も胃もたれも\nないし、最高かよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9118, 1, 1, 132501, 2, 1, 0, "いっつも力入れてたら\nいざって時困るし\nやっぱ省エネだな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9119, 1, 1, 132501, 3, 1, 0, "やば…なんか急に\nブドウ朱飲みたい…\nルイちゃんに頼むか？\nいや、でもなあ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9120, 1, 1, 132501, 4, 1, 0, "あれ、あの材料の在庫\nまだあったっけ…\nルイちゃんに確認しよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9121, 1, 1, 132501, 5, 1, 0, "イロハのやつ変な\nイタズラしてない\nといいけど…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9122, 1, 4, 132501, 101, 1, 0, "今日のシフトは～…\nお、彼と一緒じゃん\nふふっ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9123, 1, 8, 132501, 102, 1, 0, "イロハのやつ\n不用意に私の体で\n彼に抱きつくなって\nこっちが恥ずかしい…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9124, 1, 1, 132501, 1, 2, 0, "今日も元気だねえ\n若いってうらやま…\nっていや、今は私の\n方が若いじゃん", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9125, 1, 1, 132501, 2, 2, 0, "ん～？　どしたん\nヒマならうちの店で\nバイトしてく？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9126, 1, 1, 132501, 3, 2, 0, "イロハ見なかった？\n今日は手伝うって約束\nしてたのにさあ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9127, 1, 1, 132501, 4, 2, 0, "ルイちゃん特製\nエナドリ（子ども用）\nこれ飲んで今日も\nお仕事頑張るかー", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9128, 1, 4, 132501, 101, 2, 0, "元の体なら\n君と目線、合うのにね\n…ってドラマかよ\nごめん、今の忘れて", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9129, 1, 8, 132501, 102, 2, 0, "なんかヒマそうじゃん\n用事ないなら\nお姉さんがコーヒー\n淹れたげよっか", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9130, 1, 1, 132501, 1, 2, 0, "ふわあ…おはよ\n今日もお仕事\nがんばりますかー", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9131, 1, 1, 132501, 2, 2, 0, "ちょうどいいや\n一緒にお昼どうよ\nお姉さんが\n奢ってあげよう", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9132, 1, 1, 132501, 3, 2, 0, "ふぁぁ…ふぅ\nここからは大人の時間\nなんだけどさぁ\n眠くてかなわん…", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9134, 1, 1, 132501, 2, 3, 0, "おお～なんか前より\nでっかく感じるわ～\nてそりゃそうか", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9135, 1, 1, 132501, 3, 3, 0, "そいえば、イロハって\nあんまこういうの\n欲しがらなかったな～\nクワガタばっかで", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9136, 1, 1, 132501, 4, 3, 0, "はあ…いい匂いだね\nさすがルイちゃん特製\n錬金アロマ\n次の目玉商品だな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9137, 1, 1, 132501, 5, 3, 0, "ユカリっち焚火の前で\nキメる麦しゅわに\nハマってるらしいけど\n興味あるわぁ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9139, 1, 1, 132501, 7, 3, 0, "うわこれ…\n無限にいけちゃうわ\n子どもの胃袋\n恐るべし…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9140, 1, 1, 132501, 8, 3, 0, "ふむ、こんな需要が…\n次の新商品として\nルイちゃんに試作\n頼んでみるか～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9142, 1, 1, 134801, 1, 1, 0, "服はその人の個性です\n仕立て屋は\nその手伝いをする\nプロなんです♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9143, 1, 1, 134801, 2, 1, 0, "糸を紡いで紡いで\n巻き巻き～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9144, 1, 1, 134801, 3, 1, 0, "ふむ…あの人の衣装\nなかなか斬新ですね\n後でどうなってるのか\n見せてもらおう♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9145, 1, 1, 134801, 4, 1, 0, "動きやすさと\nファッション性\n両立させてみせますよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9146, 1, 1, 134801, 5, 1, 0, "今度のコレクション\n新作にワイルドな\nルームウェアは\nどうかな…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9147, 1, 4, 134801, 101, 1, 0, "騎士さんもたまには\n衣装の感想とか言って\nくれてもいいのにな～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9148, 1, 8, 134801, 102, 1, 0, "ノゾミさんとチカさん\nにも、おそろいの服を\nプレゼントしてみよう\n二人とも似合いそう♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9149, 1, 1, 134801, 1, 2, 0, "ジオ・ゲヘナでまた\n素材収集したいんです\n手伝ってくれますか？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9150, 1, 1, 134801, 2, 2, 0, "えっ？　薄着すぎ？\nおしゃれのためなら\nこのくらいへっちゃら\nですよ…っくしゅん！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9151, 1, 1, 134801, 3, 2, 0, "穴が開いてる？\nこれはそーいう\nデザインですー！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9152, 1, 1, 134801, 4, 2, 0, "ちょっとどこ\n触ってるんですかー！\n…あ、糸くず\n取ってくれたんですね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9153, 1, 4, 134801, 101, 2, 0, "騎士さんに\n新しい生地の服を…\nいや、シャレじゃ\nないですよ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9154, 1, 8, 134801, 102, 2, 0, "素敵な着こなしには\n健康な体も大切！\n騎士さんに見られても\n恥ずかしくないように", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9155, 1, 1, 134801, 1, 2, 0, "おはようございます！\nさあ、着替えて！\n私が見繕ってあげます", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9156, 1, 1, 134801, 2, 2, 0, "お昼は激辛料理です\nジオ・ゲヘナで\nハマッちゃって…\n一緒にどうですか？", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9157, 1, 1, 134801, 3, 2, 0, "睡眠不足は美容の大敵\nていうかシンプルに…\nね、眠い…", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9159, 1, 1, 134801, 2, 3, 0, "くつろげますね～\nでももうちょっと\n生地の手触りが…\nって、職業病？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9160, 1, 1, 134801, 3, 3, 0, "かわいいものって\n心を癒してくれますね\n服もそうありたいです", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9161, 1, 1, 134801, 4, 3, 0, "いい香り～♪\nこの匂いにはどんな服\nが合うかなあ…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9162, 1, 1, 134801, 5, 3, 0, "この服露出が多いから\n温かいとほっとする～\nずっとこうして\nいたいかも…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9164, 1, 1, 134801, 7, 3, 0, "いくらでも\n食べられちゃうから、\n自分でストップ\nかけないと…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9165, 1, 1, 134801, 8, 3, 0, "しっかり勉強して\nジオ・ゲヘナに\nお洒落を伝えたいです", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9167, 1, 1, 134901, 1, 1, 0, "あたくしは高貴なる\nマンティコア！\nサソリでもコウモリ\nでもありませんわよ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9168, 1, 1, 134901, 2, 1, 0, "なかなか居心地がいい\nお部屋ですわね～\n…さてどうやって\nあたくしのものに…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9169, 1, 1, 134901, 3, 1, 0, "高貴なるお審美眼で\nあの家具の価値を\n測ってやりますわ\n…ギリ合格ですわ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9170, 1, 1, 134901, 4, 1, 0, "高貴なる笑い声は\n一日にしてならず\nお子様の頃から練習を\n重ねてきたのですわ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9171, 1, 1, 134901, 5, 1, 0, "お美しいあたくし様と\n同じ空間にいられる…\nどうぞその喜びを\n噛みしめなさいな♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9172, 1, 4, 134901, 101, 1, 0, "アナタがお土下座して\n頼むなら、あたくしも\nもう少しここに残って\n構いませんわよ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9173, 1, 8, 134901, 102, 1, 0, "馬ボーンをあたくしに\n振り向かせるには\nどうしたら…って、\nこの言い方は語弊が！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9174, 1, 1, 134901, 1, 2, 0, "少し寒いですわね…\n…この服装のせい？\nこっちだって好きで\n着てねぇですわよ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9175, 1, 1, 134901, 2, 2, 0, "あのラミアが来たら\nあたくし様はお用事で\nお留守って言って\n追い返すんですのよ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9176, 1, 1, 134901, 3, 2, 0, "こっちの世界にも\n闘技場がありますの？\nそこもお頂点を極めて\nしまおうかしら～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9177, 1, 1, 134901, 4, 2, 0, "馬ボーンって\n人畜無害そうなお面の\nわりに、時々やること\nえげつねぇですわね…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9178, 1, 4, 134901, 101, 2, 0, "ねぇ馬ボーン…\nアナタあの魔札、\nまだ持ってまして？\nとっとと捨てなさい！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9179, 1, 8, 134901, 102, 2, 0, "絶対にあたくし様の\nお尻尾に触るんじゃ\nねぇですわよ…？\nいえ、振りではなく！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9180, 1, 1, 134901, 1, 2, 0, "ふわぁ…\n打倒ネフィのお作戦を\n夜通し考え過ぎて、\n寝不足ですわぁ…", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9181, 1, 1, 134901, 2, 2, 0, "もうお昼ですの？\nあたくし様に相応しい\n優雅なおランチと\n参りますわよ～♪", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9182, 1, 1, 134901, 3, 2, 0, "あら馬ボーンったら\nまだ起きてますの？\n…ぐぬぬ、夜の間に\n仕掛ける作戦が…", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9184, 1, 1, 134901, 2, 3, 0, "このおフカフカ感…♪\n剣闘士宿舎の\nゴワゴワなお寝床とは\n大違いですわね！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9185, 1, 1, 134901, 3, 3, 0, "なんですの、この\n弱っちそうなお顔は…\nおかわいそうですし、\nお僕にしてあげますわ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9186, 1, 1, 134901, 4, 3, 0, "馬ボーンはこういう\n匂いが好みですの？\nふーん…ほーう…\nなるほどですわ…♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9187, 1, 1, 134901, 5, 3, 0, "あぁ～～…\nそうそう、これくらい\n暑い方が快適ですわ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9189, 1, 1, 134901, 7, 3, 0, "もぐもぐ、もぐっ…\nまぁそこそこ？\n悪くないお味では\nありますわねっ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9190, 1, 1, 134901, 8, 3, 0, "こんな本よりも、\n華麗なるカノン様の\n活躍録でも置いた方が\n世のためになりますわ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9192, 1, 1, 132401, 1, 1, 0, "さっきねー、外で猫と\n木登り競争したんだよ\nなかなか手ごわい\n相手だった！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9193, 1, 1, 132401, 2, 1, 0, "いっぱい食べて\n大きくなるんだ～\nあっでもこれ\nソノちゃんの体か", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9194, 1, 1, 132401, 3, 1, 0, "強いおねーさんの\n気配を感じる…\nどこから？\n…そこら中から！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9195, 1, 1, 132401, 4, 1, 0, "虫捕り網の手入れは\nいつもバッチリ！\nいつ何が出るか\nわからないもんね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9196, 1, 1, 132401, 5, 1, 0, "うちの中で走り回ると\nソノちゃんに\n怒られちゃうけど…\nここならいける？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9197, 1, 4, 132401, 101, 1, 0, "おじちゃんも\nお部屋にいろいろ\n隠してるのかな？\nルイちゃんみたいに", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9198, 1, 8, 132401, 102, 1, 0, "世界一になったら\nおじちゃんにも\nいっぱい褒めて\nほしいな…約束っ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9199, 1, 1, 132401, 1, 2, 0, "高いとこのものが\n取りたいときは\nイロハに言ってね\nイロハ得意だから！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9200, 1, 1, 132401, 2, 2, 0, "どうも、イロハです\n剣士たるもの、\nあいさつは大事って\n教わったんだ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9201, 1, 1, 132401, 3, 2, 0, "イロハと勝負だね！\n受けて立つよ～！\n…あれ、違った？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9202, 1, 1, 132401, 4, 2, 0, "あっ！　ルイちゃんに\n素材集め頼まれてたの\n忘れてた…！　やばい\nおじちゃん手伝って！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9203, 1, 4, 132401, 101, 2, 0, "実はイロハって\n褒めたら伸びる\nタイプなんだ\nだからよろしくね♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9204, 1, 8, 132401, 102, 2, 0, "このお部屋、すごく\n居心地がいいんだ～\nやっぱりおじちゃんが\nいるからかな？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9205, 1, 1, 132401, 1, 2, 0, "昨日仕掛けた罠で\n虫捕れてるかな？\nあとで一緒に\n見に行こうね♪", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9206, 1, 1, 132401, 2, 2, 0, "もうお昼だよね？\nイロハのお腹が\n空いてきたから\n間違いないよ！", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9207, 1, 1, 132401, 3, 2, 0, "ふわぁ～…\nあ、あくびじゃないよ\nイロハ大人なんだから\n眠くなんか…ふわぁ…", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9209, 1, 1, 132401, 2, 3, 0, "ふわふわしたものって\nつい飛び込みたく\nなっちゃうよね\n…うずうずっ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9210, 1, 1, 132401, 3, 3, 0, "かわいいのもいいけど\nカッコよさも欲しいな\nそーだ、角とかもっと\nいっぱい付けてみる？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9211, 1, 1, 132401, 4, 3, 0, "おー、いい匂い♪\nこれもしかして\nルイちゃんの新作？\n爆売れ間違いなしだよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9212, 1, 1, 132401, 5, 3, 0, "ぬくぬくぽかぽか、\n気持ちいいー…\nでもちょっと\n暑くなってきたな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9214, 1, 1, 132401, 7, 3, 0, "もぐもぐもぐ…\nこれはいい食材を\n使っておりますなぁ\nうん、三ツ星！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9215, 1, 1, 132401, 8, 3, 0, "ふむふむなるほど…\nこっ、これは…！\n…ごめん、適当に\n言ってみた", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9217, 1, 1, 135001, 1, 1, 0, "また海に行く時に\n備えて…次の計画を\n練る必要がある…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9218, 1, 1, 135001, 2, 1, 0, "騒がしいのも…\n以前ほど気にならない\n慣れてしまった…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9219, 1, 1, 135001, 3, 1, 0, "眩暈がする暑さも\n騎士クンと同じ気温を\n感じられるなら…いい", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9220, 1, 1, 135001, 4, 1, 0, "騎士クンに\n好意を抱く者は\nみんな有罪\n…わたしは例外", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9221, 1, 1, 135001, 5, 1, 0, "体も心も…外の世界に\n馴染んできた気がする\n少しずつ、だけど…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9222, 1, 4, 135001, 101, 1, 0, "騎士クンの手…\n大きかった…\n…また、つなぎたいな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9223, 1, 8, 135001, 102, 1, 0, "夏が終わっても…\nその先の季節も\n…その次の夏も\n騎士クンと一緒に…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9224, 1, 1, 135001, 1, 2, 0, "騎士クンといるとね\n渇いてた心が…\n潤される、気がするの", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9225, 1, 1, 135001, 2, 2, 0, "騎士クンは…\nしたいこと、ある？\n夏にだけ…できること\n一緒に、しようね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9226, 1, 1, 135001, 3, 2, 0, "仮想領域の夢でも\n現実の海でも…\n騎士クンと一緒なら\nそこが本物の夏、だね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9227, 1, 1, 135001, 4, 2, 0, "肝試しがしたい…？\n…うまく驚けるかな\n練習しなくちゃ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9228, 1, 4, 135001, 101, 2, 0, "騎士クンとなら\nどんな夏でも…\nきっと楽しい…♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9229, 1, 8, 135001, 102, 2, 0, "水着…どうかしら…？\n…よかった\nシミュレーションの\n成果ね。ふふ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9230, 1, 1, 135001, 1, 2, 0, "騎士クン…眠たそう\nもうちょっと…寝る？\n後で起こしてあげるね", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9231, 1, 1, 135001, 2, 2, 0, "お出かけするなら\n水筒を用意するね…\n夏の水分補給は大事…", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9232, 1, 1, 135001, 3, 2, 0, "また…夜の海を\n見に行きたいな\n騎士クンと一緒に\n連れていってくれる？", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9234, 1, 1, 135001, 2, 3, 0, "騎士クンを待つには\nちょうどいい…\n…落ち着く…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9235, 1, 1, 135001, 3, 3, 0, "こういう物を\n城に置いた方が\nかわいいって思って\nもらえる…かな…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9236, 1, 1, 135001, 4, 3, 0, "夏に合わせた香りね…\n涼やかな風が胸を\n吹き抜けるような…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9237, 1, 1, 135001, 5, 3, 0, "火は落ち着く…\n身体感覚を味わえる\n作り物の…この体でも", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9239, 1, 1, 135001, 7, 3, 0, "『食』…騎士クンと\n話す共通の話題には\n悪くなさそう…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9240, 1, 1, 135001, 8, 3, 0, "『好きな人と楽しめる\n夏デート特集』…？\n……ふむ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9242, 1, 1, 135001, 10, 3, 0, "わたしの趣味では\nないけれど…\n騎士クンが望むなら\n城に置いても…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9243, 1, 1, 135001, 11, 3, 0, "む…悪くない\nこれなら\n騎士クンと一緒に…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9244, 1, 1, 135101, 1, 1, 0, "一日中水着でいるって\n浮かれているみたいね\n夏は短いから、沢山\n楽しまなくちゃね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9245, 1, 1, 135101, 2, 1, 0, "ちょっと暑いし\n氷の上を滑って\n涼もうかしら", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9246, 1, 1, 135101, 3, 1, 0, "そろそろ運送ギルドの\nアルバイトの時間だわ\n準備しなきゃ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9247, 1, 1, 135101, 4, 1, 0, "エリスさん、水着が\nすごく似合ってた…\nどこで買ったのかしら", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9248, 1, 1, 135101, 5, 1, 0, "未練が残らないように\nこの夏休みでやりたい\nことはやらなくちゃ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9249, 1, 4, 135101, 101, 1, 0, "かき氷を作ったら\n彼も食べるかしら？\nアイスクリームの\nほうがいいかな…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9250, 1, 8, 135101, 102, 1, 0, "水着を着ていれば\nどこにいても\nリゾート気分だわ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9251, 1, 1, 135101, 1, 2, 0, "夏はおいしいものが\nたくさんあるし\n【美食殿】の活動も\n忙しくなりそうね♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9252, 1, 1, 135101, 2, 2, 0, "わ、私の尻尾…\nそんなに気になる？\nつついたらダメよ\nいいわね…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9253, 1, 1, 135101, 3, 2, 0, "夏は日が長いし\nまだまだたくさん\n遊べそうね♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9254, 1, 1, 135101, 4, 2, 0, "今日は一緒に海に\n行かない？\nあなたも水着\n持ってきてるでしょ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9255, 1, 4, 135101, 101, 2, 0, "そろそろ日焼け止めを\n塗り直さなきゃ…あの\nお願いしてもいい…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9256, 1, 8, 135101, 102, 2, 0, "私たちで最高の\n夏休みにしましょう\nね、おにーたん♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9257, 1, 1, 135101, 1, 2, 0, "夏の朝は早いわね\nもうセミも鳴いてるし\nあなたも早起きだわ…\n見習わなくちゃ", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9258, 1, 1, 135101, 2, 2, 0, "日中は暑いだろうし\nゆっくりしましょう\nお昼寝タイムはどう？", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9259, 1, 1, 135101, 3, 2, 0, "この時間のアイス\nクリームは格別よね♪\n何味にしようかしら", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9261, 1, 1, 135101, 2, 3, 0, "暑い夏はしっかり\n休むことが大切\nふぅ…いい気持ち♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9262, 1, 1, 135101, 3, 3, 0, "ふわふわの毛並み\n暑くないのかしら\nかわいいだけじゃなく\n忍耐強くもあるのね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9263, 1, 1, 135101, 4, 3, 0, "爽やかでいい匂い…♪\nジトジトしがちの夏に\nピッタリなアロマね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9264, 1, 1, 135101, 5, 3, 0, "ずっと水着でいると\n流石に冷えるわね\nうん、いい心地だわ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9266, 1, 1, 135101, 7, 3, 0, "はむっはむっ…\nしっかり食べて\n夏バテ対策しないと", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9267, 1, 1, 135101, 8, 3, 0, "ふむふむ…面白いわね\n今度プレシアちゃんと\n一緒に読もうかしら", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9269, 1, 1, 135101, 10, 3, 0, "私のフロートと少し\n似ている気がする\nサイズ的にはこっちが\nお兄さんかしら…ふふ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9270, 1, 1, 135101, 11, 3, 0, "ふふ、きれい♪\nまるで\n海のステージね！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9271, 1, 1, 135201, 1, 1, 0, "じゅんび、おっけー\nうみでも\nおふろでも\nおんせんでも", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9272, 1, 1, 135201, 2, 1, 0, "ひまわり。すき\nおっきくて\nたね、うまし", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9273, 1, 1, 135201, 3, 1, 0, "ハッピーちゃん\nいい、におい\nひやけ？\nやきぶた？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9274, 1, 1, 135201, 4, 1, 0, "じーわ、じーわ…\nせみの、なきごえ\nきいてる、だけで\nあつい。かも…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9275, 1, 1, 135201, 5, 1, 0, "アイス、かきごおり…\nあつい、なつ\nだから\nひえひえ、ひっす", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9276, 1, 4, 135201, 101, 1, 0, "おっきくなるまほー\nきわめたい\nお兄を\nだっこ。するよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9277, 1, 8, 135201, 102, 1, 0, "「あなた」\n…ちょっと、ちがう？\nシェフィたち\nみたいに、ならない…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9278, 1, 1, 135201, 1, 2, 0, "じゃりじゃり\nべたべた\n…シャワー\nいって、きます", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9279, 1, 1, 135201, 2, 2, 0, "（すんすん）\nお兄。ほんのり\nうみの、におい\nおいしそう", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9280, 1, 1, 135201, 3, 2, 0, "お兄。みて\nべろ、まっさお\nぶるーはわい\nカラー", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9281, 1, 1, 135201, 4, 2, 0, "あつくても\nあついもの\nおいしい\n…バーベキュー…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9282, 1, 4, 135201, 101, 2, 0, "ついんて\nバランス、むずかし\nお兄\nリボン、よろ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9283, 1, 8, 135201, 102, 2, 0, "みずぎ\nにあってる。って\nお兄。いった\nもっと。みて？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9284, 1, 1, 135201, 1, 2, 0, "おはよ\nあさごはん\nやきざかな", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9285, 1, 1, 135201, 2, 2, 0, "おひる\nおひるね\nいっしょに、しよ？", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9286, 1, 1, 135201, 3, 2, 0, "おそと\nまっくら\nはなび\nしよ？", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9288, 1, 1, 135201, 2, 3, 0, "ふわぁ…\nあそびすぎ\nおつかれ\nねむねむ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9289, 1, 1, 135201, 3, 3, 0, "よしよし\nおるすばん\nおねがい。ね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9290, 1, 1, 135201, 4, 3, 0, "（くんくん）\nれもんの、におい\nじゅる", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9291, 1, 1, 135201, 5, 3, 0, "さかな、くしやき\nすたんばい、おっけー\nいつでも、やける。よ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9293, 1, 1, 135201, 7, 3, 0, "いっぱい、あそびたい\nいっぱい、つよくなる\nいっぱい、たべる", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9294, 1, 1, 135201, 8, 3, 0, "おさかなの、ほん\nおもしろかった\nでも\nおなかが…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9296, 1, 1, 135201, 10, 3, 0, "れっつ、ごー\nすいへーせん\nせかい\nいっしゅー", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9297, 1, 1, 135201, 11, 3, 0, "きぶんは、かいばしら\nバターやき\nおすすめ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9298, 1, 1, 135301, 1, 1, 0, "成長して着れなく\nなっちゃう前に\nこの水着でできること\n全部しちゃいたいな♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9299, 1, 1, 135301, 2, 1, 0, "お家で水着だと\n開放的な気分かも♪\nふふ、お兄さんにも\n教えてあげなきゃ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9300, 1, 1, 135301, 3, 1, 0, "泳ぎの練習しちゃお♪\nすいすい、すいすいー\n…ちょっと、\n子供っぽかったかも…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9301, 1, 1, 135301, 4, 1, 0, "インドア派だけど\n夏の屋外は好き…や…\nお兄さんと一緒なら\nどこでも…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9302, 1, 1, 135301, 5, 1, 0, "夏だからってちょっと\n羽目外しすぎかな…\nわたし…やっぱり\n真面目じゃない…かも", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9303, 1, 4, 135301, 101, 1, 0, "わたしの水着姿…\n少しは大人っぽいって\n思ってくれたかな…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9304, 1, 8, 135301, 102, 1, 0, "家の中は涼しいのに\nお兄さんのこと\n考えると暑くなって…\n夏バテしちゃいそう…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9305, 1, 1, 135301, 1, 2, 0, "お兄さんと一緒なら\n夏休みの日記\n書くことが多すぎて\nページが足りません♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9306, 1, 1, 135301, 2, 2, 0, "かき氷食べましょ♪\n…ふふ、お兄さん\n舌の色すごいですよ♪\nわたしも…べ～っ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9307, 1, 1, 135301, 3, 2, 0, "今日は海に行きます？\nそれともお家で\nのんびりですか？\nわたしはどっちでも♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9308, 1, 1, 135301, 4, 2, 0, "夏バテしないように\nスタミナ料理のレシピ\nお勉強しました♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9309, 1, 4, 135301, 101, 2, 0, "夏が終わっちゃっても\nこの水着を着てたら\n夏の思い出、忘れずに\nいてくれますか…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9310, 1, 8, 135301, 102, 2, 0, "…この日焼け跡\nお兄さんと\nわたしだけの\n秘密…ですよ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9311, 1, 1, 135301, 1, 2, 0, "おはようございます！\n朝の体操１、２、３♪\nしゃきっと目覚めて\nいい一日にしましょ♪", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9312, 1, 1, 135301, 2, 2, 0, "お昼休憩ですね♪\n午後からも頑張れる様\nお兄さんのこと\n癒しますね…♪", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9313, 1, 1, 135301, 3, 2, 0, "そろそろ寝る時間…\nお兄さんが眠るまで\n一緒にいますね…\n…ふあぁ…", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9315, 1, 1, 135301, 2, 3, 0, "気持ちいいけど、汗…\n染みちゃわないように\n気をつけないと…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9316, 1, 1, 135301, 3, 3, 0, "ぎゅう～♪\nなんて、ちょっと\nあざといかな…♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9317, 1, 1, 135301, 4, 3, 0, "夏にぴったりの\nフルーティーな香り♪\nお兄さん、好きかな？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9318, 1, 1, 135301, 5, 3, 0, "水着で暖まるのって\nなんだか贅沢な気分♪\nこたつでアイス的な♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9320, 1, 1, 135301, 7, 3, 0, "お腹ぽっこりに\nならないよう\n気をつけないと…\nでも止まらない…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9321, 1, 1, 135301, 8, 3, 0, "弾ける水着でビーチの\n視線を独り占め…かぁ\nわたしはお兄さんの\n視線だけで…なんて♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9323, 1, 1, 135301, 10, 3, 0, "これで夏の大冒険に\nしゅっぱ～つ！\n…なんちゃって♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9324, 1, 1, 135301, 11, 3, 0, "なんだか女神さま\nみたい…？\nふふ、わたしには\n似合わないかもです♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9325, 1, 1, 135401, 1, 1, 0, "真っ白い日差し─\nジオ・ゲヘナには\nなかったものですね\n─悪くはないです", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9326, 1, 1, 135401, 2, 1, 0, "しばらくここで\n休んでます\n人の多い街中よりは\n落ち着くので─", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9327, 1, 1, 135401, 3, 1, 0, "ここにも─\nあるんでしょうか\n海で使う遊び道具…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9328, 1, 1, 135401, 4, 1, 0, "見張りは─\nここならあまり\n必要なさそう…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9329, 1, 1, 135401, 5, 1, 0, "夏のバカンス─\n媛が楽しんでるなら\n私は十分です", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9330, 1, 4, 135401, 101, 1, 0, "知らない相手は、\n苦手だけど─\n…少しだけ、\n話してみても…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9331, 1, 8, 135401, 102, 1, 0, "ここにいると\n安心する、ような…\n土壌が─良い…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9332, 1, 1, 135401, 1, 2, 0, "夏の正装、水着─\n着方、間違えて\nないですよね…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9333, 1, 1, 135401, 2, 2, 0, "相変わらず、\nおしゃべりが\n好きなんですね\n私は─普通です", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9334, 1, 1, 135401, 3, 2, 0, "─喉が渇きました\n…いえ、花にお水が\n欲しいわけじゃなくて", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9335, 1, 1, 135401, 4, 2, 0, "─いつもより\n花がつやつやしてる？\n…あ、褒めて\nくれたんですか", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9336, 1, 4, 135401, 101, 2, 0, "この部屋─\n貴方が飾りつけを？\nわかります、何となく", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9337, 1, 8, 135401, 102, 2, 0, "すぐ傍にいると、\n暑くなりませんか？\n─私は別に\nかまいませんが", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9338, 1, 1, 135401, 1, 2, 0, "朝から元気ですね─\nおかげですっかり\n目が覚めました", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9339, 1, 1, 135401, 2, 2, 0, "外─暑そうです\n出かけるなら\n気を付けてください\n倒れたりしないよう…", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9340, 1, 1, 135401, 3, 2, 0, "─貴方も\n夜更かしですか？\n…少しだけなら\n付き合いますよ", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9342, 1, 1, 135401, 2, 3, 0, "貴方もここで\n休みたいですか？\n─ダメです\n今は私が使うので", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9343, 1, 1, 135401, 3, 3, 0, "ゆるい顔つきですね\nジャングルでは\n生き残れそうに\nないです─", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9344, 1, 1, 135401, 4, 3, 0, "─ん…\nこれ、海の香り？\n─いい匂い…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9345, 1, 1, 135401, 5, 3, 0, "…暑さには\n慣れてますよ\n寒いのよりは、ずっと", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9347, 1, 1, 135401, 7, 3, 0, "栄養は、大事です\n味は─あまり\n気にしたこと、\nありませんでした", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9348, 1, 1, 135401, 8, 3, 0, "適当に本を読んでいる\nだけです─\n…別に、遊びについて\n調べたりしてません", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9350, 1, 1, 135401, 10, 3, 0, "魔物を模した\n人形─ですね\n戦闘訓練用に？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9351, 1, 1, 135401, 11, 3, 0, "貝のクッションですか\n─噛みついてきたり\nしないですよね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9352, 1, 1, 137101, 1, 1, 0, "シントーメッキャク\n火もまた涼し…\nむむむ…やっぱり夏は\n暑いデース！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9353, 1, 1, 137101, 2, 1, 0, "海にも新しい忍法の\nヒントがあるはずデス\nショーグンと海底散歩\nも楽しそうデス", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9354, 1, 1, 137101, 3, 1, 0, "釣りの心得は\nメイキョーシスイ…\n忍者の修行に\nうってつけデス♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9355, 1, 1, 137101, 4, 1, 0, "天下統一の後のことも\n考えねばなりまセン\n今度はテンジョー統一\nデスかね…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9356, 1, 1, 137101, 5, 1, 0, "デケデケデン♪\nテルテル坊主完成デス\n晴れてないとお外で\n遊べまセンからね！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9357, 1, 4, 137101, 101, 1, 0, "次のオモテナシも\nショーグンをワクワク\nさせマス♪　今から\n用意しておかねばデス", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9358, 1, 8, 137101, 102, 1, 0, "新しい忍法の開発に\n休みはないのデス！\nショーグンの天下統一\nを思えばなんのその！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9359, 1, 1, 137101, 1, 2, 0, "海の前に準備運動デス\nまずは手を後ろに、\n素早く忍者走り！\nシュバババッ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9360, 1, 1, 137101, 2, 2, 0, "夏にお祭りは\n欠かせないデスよね♪\n地域のイベント情報は\n見逃しまセン！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9361, 1, 1, 137101, 3, 2, 0, "ショーグン、団扇で\nあおいであげマス！\n忍法・扇風の術ー！\nうりゃりゃりゃー！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9362, 1, 1, 137101, 4, 2, 0, "ワタシが必要な時は\n風鈴を鳴らすデス！\n風よりも早く\n駆けつけマスよ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9363, 1, 4, 137101, 101, 2, 0, "じっと見つめられると\n顔が熱くなってきて…\nはっ、ショーグンの\n新しい忍法デスか！？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9364, 1, 8, 137101, 102, 2, 0, "ショーグン、日焼けは\nノン、デスよ！\nワタシがクリームぬり\nぬりしてあげマース！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9365, 1, 1, 137101, 1, 2, 0, "おはようデス！\n朝ごはんはワタシに\nお任せデス。\n毎日みそ汁作るデス！", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9366, 1, 1, 137101, 2, 2, 0, "日差しに注意デスよ\nネッチューショーに\n日焼け、どっちも\n大変デスからね！", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9367, 1, 1, 137101, 3, 2, 0, "ショーグン、\n外で花火しまショウ！\nぱちぱちキレイで\n楽しいデスよ！", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9369, 1, 1, 137101, 2, 3, 0, "遊びに備えて\n今は休む時…\nガシンショータンデス", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9370, 1, 1, 137101, 3, 3, 0, "この子たちにも\n手伝ってもらって\n新しい忍法を開発\nしてみまショウ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9371, 1, 1, 137101, 4, 3, 0, "むむっ、ここまで\n潮の香りが漂って\nきた気がしマス！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9372, 1, 1, 137101, 5, 3, 0, "海に入った後は\n体を温めねば\nやはりサウナも\n必要デスね！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9374, 1, 1, 137101, 7, 3, 0, "いっぱい食べて\nいっぱい遊ぶデス！\n夏バテなんかに\n負けてられまセン！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9375, 1, 1, 137101, 8, 3, 0, "『BUSHIDO』の\n最新刊が待ち遠しい\nデス…マイゾーキン\n特集、読みたいデス", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9377, 1, 1, 137101, 10, 3, 0, "ショーグンとこの子に\n乗って、天下統一まで\nシュババババッ！\nなんてどうでショウ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9378, 1, 1, 137101, 11, 3, 0, "ぽよんぽよ～ん♪\nショーグンと一緒に\n遊んでみたいデス♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9379, 1, 1, 135501, 1, 1, 0, "今度はシナツちゃんの\n世界へ行ってみたいな\nエリスちゃんも誘って\n大人女子旅とか最高♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9380, 1, 1, 135501, 2, 1, 0, "あっはっはー！\nよし、今日も絶好調！\nま、いつも笑ってたら\n不調なんてないけど♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9381, 1, 1, 135501, 3, 1, 0, "ん～あっちの世界と\nほとんど一緒だね！\n違ってるところ探しに\n出かけてみよっかな？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9382, 1, 1, 135501, 4, 1, 0, "ううーん、のんびり\nするのもいいけど\nどこか行ってみたいな\n人助けとか、ね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9383, 1, 1, 135501, 5, 1, 0, "あっ、そーだ\n向こうへ帰る前に\nこっちのお土産とか\n買っとかなくちゃ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9384, 1, 4, 135501, 101, 1, 0, "ふ～ん、騎士くんって\nこういうのが好みか～\n後でヒヨリちゃんに\n教えてあげよ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9385, 1, 8, 135501, 102, 1, 0, "次はあたしが恩を返す\n番だね。騎士くんに\n助けが必要になったら\n絶対駆けつけるよ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9386, 1, 1, 135501, 1, 2, 0, "あっ騎士くん！\n困ってることない？\nお姉さんがなんでも\n力になっちゃうよ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9387, 1, 1, 135501, 2, 2, 0, "また闘技場に行こうよ\nちょっと運動不足でさ\nバーンと暴れたい気分\nなんだよね♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9388, 1, 1, 135501, 3, 2, 0, "いつか大人になったら\n一緒に飲もうよ！\nあははっ、騎士くん\n指切りげんまん♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9389, 1, 1, 135501, 4, 2, 0, "一緒に作った麦しゅわ\nカップ、最高だね♪\n騎士くんの分もどうぞ\nちゃんとジュースだよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9390, 1, 4, 135501, 101, 2, 0, "ねえ…ぶっちゃけ\nヒヨリちゃんのこと\nどう思ってるのかな？\nお姉さんに聞かせなよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9391, 1, 8, 135501, 102, 2, 0, "騎士くん、うっかり\n飲みすぎたあの日の\nコトは誰にも内緒で\nお願いね。約束だよ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9392, 1, 1, 135501, 1, 2, 0, "あはは、騎士くん\n新しい朝だよー！\n今日はどんな一日か\nワクワクするね～♪", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9393, 1, 1, 135501, 2, 2, 0, "午後からも笑顔で\n元気に乗り切ろうね！\n外に行くもよし、\n部屋で麦しゅわもよし", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9394, 1, 1, 135501, 3, 2, 0, "ここからは大人の時間\n騎士くんにはまだ少し\n早いかな～？\nだから、おやすみ♪", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9396, 1, 1, 135501, 2, 3, 0, "麦しゅわ飲み過ぎた\n夜はここにダーイブ\n…なんて大人に\nなっちゃダメだよ～？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9397, 1, 1, 135501, 3, 3, 0, "あたしの世界の子たち\nこういうの好きかな？\nお土産に探してみよう\n気に入るかも♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9398, 1, 1, 135501, 4, 3, 0, "ん～いいね♪\nあとでお気に入りの\nフレーバーも\n試してみよっかな？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9399, 1, 1, 135501, 5, 3, 0, "ほうほう…熱い！\nサラサリアは\nいつも暖かいから\nこういうの新鮮！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9401, 1, 1, 135501, 7, 3, 0, "ん～おいしい！\nけどやっぱご飯は\nみんなで一緒に\n食べたいな～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9402, 1, 1, 135501, 8, 3, 0, "文字ばっかの本は\nちょーっと苦手かも…\n必要なら読むけどさ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9406, 1, 1, 135601, 1, 1, 0, "皆思い思いに過ごして\nいるのに、不思議と\n落ち着く。いつまでも\nここにいられたら…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9407, 1, 1, 135601, 2, 1, 0, "皆に挨拶をせねば\n…いや、今は、まだ\nタイミングが悪い…\nまた今度にしよう…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9408, 1, 1, 135601, 3, 1, 0, "私の服装…この場で\n浮いてないだろうか\nまずい、心配に\nなってきた…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9409, 1, 1, 135601, 4, 1, 0, "アイラたちとの旅行も\n楽しかったな…\nまた３人で温泉に\nいきたいものだ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9410, 1, 1, 135601, 5, 1, 0, "ここで剣の稽古は\nさすがによろしくない\n代わりに今日は瞑想を\nすることにしよう", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9411, 1, 4, 135601, 101, 1, 0, "人とのつながりは\n大切にしなければ\n今日こそは誰かに\n話しかけるぞ…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9412, 1, 8, 135601, 102, 1, 0, "できることが増えて、\n迷うばかりの毎日…\nそれが楽しく尊いと\n今実感している…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9413, 1, 1, 135601, 1, 2, 0, "ええと…奇遇だな\nこんなところで\n会うなんて。いや違う\n…その、こんにちは…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9415, 1, 1, 135601, 3, 2, 0, "弟子たちへの土産も\n探しておかないとな\nよければキミからも\nアドバイスを頼む…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9416, 1, 1, 135601, 4, 2, 0, "剣の稽古をしたい？\nふっ…いいとも\nキミとならいくらでも\n準備をしてすぐ戻る", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9417, 1, 4, 135601, 101, 2, 0, "よければまた、\n街を案内してくれ\n名所旧跡もだがキミと\n歩くのは楽しいんだ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9418, 1, 8, 135601, 102, 2, 0, "感謝の心を\n忘れたことはないよ\nキミたちの優しさに\n救われた一人として…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9419, 1, 1, 135601, 1, 2, 0, "うむ、いい顔だな\n良い一日は、\n良い目覚めから始まる\n心掛けておくといい", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9420, 1, 1, 135601, 2, 2, 0, "もう昼時か\n誰かを誘って食事に…\n…いや無理だ、\nハードルが高い…！", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9421, 1, 1, 135601, 3, 2, 0, "夜はいい…\n誰とも話さず\n一人きりでいても、\n不自然ではないからな", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9423, 1, 1, 135601, 2, 3, 0, "…いかんな、これは\n気を抜くとこのまま\n離れられなくなる…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9424, 1, 1, 135601, 3, 3, 0, "シナツだ、よろしく\n……ふぅ\n人形相手の練習なら\n緊張しないのだがな…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9425, 1, 1, 135601, 4, 3, 0, "ふむ…集中力が\n高まりそうな香りだな\n稽古にも役立つだろう\n皆にも教えてやろうか", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9426, 1, 1, 135601, 5, 3, 0, "凍える人に寄り添い\n温もりを与える…\n私もそんな存在に\nなりたいものだ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9428, 1, 1, 135601, 7, 3, 0, "食の楽しみ…\nあまり意識して\nいなかったが、\nやはりいいものだな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9429, 1, 1, 135601, 8, 3, 0, "…っと、いかん\n読みふけっていたら\nいくらでも時間を\n過ごせてしまうぞ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9433, 1, 1, 135701, 1, 1, 0, "また空を飛びたいな～\nふわふわ～ってなる\nあの感じが気持ちいい\nんだよね～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9434, 1, 1, 135701, 2, 1, 0, "またサラサリアに\n行きたいなー\n…串焼きの味が\n忘れられなくって♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9435, 1, 1, 135701, 3, 1, 0, "悩んでる人がいたら\n背中を押してあげたい\nあたしがそれで、勇気\nづけられたみたいに", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9436, 1, 1, 135701, 4, 1, 0, "気持ちを言葉にするの\n大事って分かってても\nいざとなると緊張して\n上手くできないなぁ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9437, 1, 1, 135701, 5, 1, 0, "あっちの世界でも\nギルドハウスは\nあるのかな…？\n今度探してみよっと♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9438, 1, 4, 135701, 101, 1, 0, "アイラに会って少し\n成長できたかも…！\n素敵な女の人目指して\nもっと頑張るぞーっ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9439, 1, 8, 135701, 102, 1, 0, "あっちのあたしは\n騎士くんに選ばれた…\nよし、あたしも\n頑張らなくっちゃ…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9440, 1, 1, 135701, 1, 2, 0, "部屋の模様替えする？\nサラサリア風の家具も\nあったら素敵だよね！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9441, 1, 1, 135701, 2, 2, 0, "なんだかお疲れ気味？\nそうだっ、サラサリア\nで教わったマッサージ\nをしてあげるっ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9442, 1, 1, 135701, 3, 2, 0, "騎士くんっ！\n今からあたしとデート\nしませんかっ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9443, 1, 1, 135701, 4, 2, 0, "今ちょうど、踊りの\n練習してたとこなの\n騎士くんも\n一緒にどう？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9444, 1, 4, 135701, 101, 2, 0, "き、騎士くん！\nこの前の続き…\n手、繋いでいい…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9445, 1, 8, 135701, 102, 2, 0, "えへへへ、騎士くんが\nあたしの背中を押して\nくれると、勇気も\n100万倍だよっ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9446, 1, 1, 135701, 1, 2, 0, "おはよ、騎士くん！\n今日もいい朝だよ！\nサラサリアみたいに\n太陽が輝いてるもん！", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9447, 1, 1, 135701, 2, 2, 0, "フレフレ、騎士くん！\n午後もファイトー！\nあたしは日陰でお昼寝\nタイム…なんてねっ♪", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9448, 1, 1, 135701, 3, 2, 0, "夜に騎士くんといると\n空飛ぶ絨毯から見た\nサラサリアの夜景を\n思い出しちゃうな♪", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9450, 1, 1, 135701, 2, 3, 0, "休憩きゅうけ～い♪\n強い人と戦った後は\nしっかりと体を\n休ませないとね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9451, 1, 1, 135701, 3, 3, 0, "にゃ～、\nかわいいにゃ～♪\nアンブラを\n思い出しちゃうよ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9452, 1, 1, 135701, 4, 3, 0, "わっ…すごくいい\n匂い～サラサリアの\n砂漠の匂いにちょっと\n似てるかも", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9453, 1, 1, 135701, 5, 3, 0, "サラサリアの太陽\nみたいに暖かい♪\n日向ぼっこしてる\nみたいでしあわせ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9455, 1, 1, 135701, 7, 3, 0, "ん～おいしいっ！\nアイラもきっと\n好きな味だね…！\n今度食べてもらおっ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9456, 1, 1, 135701, 8, 3, 0, "これは恋愛指南本…！\nふむ…意中の男性と\nキスするには…って\nこれはまだ早いよ～！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9460, 1, 1, 135801, 1, 1, 0, "あてがここにいたら、\nみんな寒い思いを\nしちまうかな…\nご、ごめんな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9461, 1, 1, 135801, 2, 1, 0, "あて、みんなより\n体でっけぇし…\nもうちょっと隅っこ\n行っとこうかな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9462, 1, 1, 135801, 3, 1, 0, "さ、騒がしいとこは\n苦手だども…\nここはそんなに\n気になんね…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9463, 1, 1, 135801, 4, 1, 0, "いろいろ勉強してぇな\nイナカもんだから、\nみんなに迷惑とか\nかけねぇように…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9464, 1, 1, 135801, 5, 1, 0, "家具とか、飾りとか、\n壊しちまったら大変…\nき、気ぃつけねぇとっ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9465, 1, 4, 135801, 101, 1, 0, "あ、あての三味線、\n聴きたいの…？\nこ、心の準備が\nできてからで、い…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9466, 1, 8, 135801, 102, 1, 0, "っ、ぁぶね…\nあてったらまた、\n騎士さのこと見て、\n溶けそうになってた…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9467, 1, 1, 135801, 1, 2, 0, "お、おめさ、その…\nおやつさん、いらね？\nかき氷とかなら、\nあて、作れるよ…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9468, 1, 1, 135801, 2, 2, 0, "あ、あてに…何か用？\n…声、聴きたかった？\nな、なら…えっと…\nおしゃべり…する？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9469, 1, 1, 135801, 3, 2, 0, "こっちさ来てから…\n向こうの世界の\n何年分も、おしゃべり\nしたかもしんね…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9470, 1, 1, 135801, 4, 2, 0, "こっちの世界には、\n氷滑りって遊びが\nあるって…？\nあてにもできっかな…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9471, 1, 4, 135801, 101, 2, 0, "ん…あては元気だよ\n悩みがあっても、\nおめさの声もらうと\n吹っ飛んじまうし…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9472, 1, 8, 135801, 102, 2, 0, "騎士さが近くに\nいてくれる…\n今は、それだけで\nいいんだ…うん…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9473, 1, 1, 135801, 1, 2, 0, "お、おはよ…\nおめさのおかげで、\n昨夜もよく眠れたよ…", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9474, 1, 1, 135801, 2, 2, 0, "…あっ、もう昼飯の\n時間かぁ…\n考えごとしてたら\nあっという間だよ", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9475, 1, 1, 135801, 3, 2, 0, "景色は似てるけど…\nこの世界の夜は、\nざわざわ音がして\n賑やかだな…", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9477, 1, 1, 135801, 2, 3, 0, "あんま縮こまってると\n体硬くなっちまうし…\nたまにはのんびり…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9478, 1, 1, 135801, 3, 3, 0, "えへへ、めんこいな…\nそだ、仁太坊たちも\n並んでたら\nもっとめんこいかも…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9479, 1, 1, 135801, 4, 3, 0, "この匂い…なんか、\nは、はいから…？\nあて、イナカもん\nだから…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9480, 1, 1, 135801, 5, 3, 0, "あ、暑くて溶けそう…\nだども、我慢できたら\n修行になる…かも…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9482, 1, 1, 135801, 7, 3, 0, "ん…♪　うめぇ…\nこんなうめぇもん\n毎日食べられるの、\n幸せだなぁ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9483, 1, 1, 135801, 8, 3, 0, "こっちの世界にも…\n雪女が出る物語、\nあるみてぇだな…\nか、悲しい話だども…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9487, 1, 1, 135901, 1, 1, 0, "なかなか上等な\n家じゃねェか\nちっとばかし\n軒を借りるぜ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9488, 1, 1, 135901, 2, 1, 0, "俺の棒っきれの\n置き場所は…\nま、玄関にでも\n立てかけときゃいいか", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9489, 1, 1, 135901, 3, 1, 0, "こっちの世界は\n眩しくって敵わねェぜ\n目がチカチカしやがる", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9490, 1, 1, 135901, 4, 1, 0, "建物の様式が同じでも\n世界と住人が違えば\n印象も変わるモンだな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9491, 1, 1, 135901, 5, 1, 0, "街の連中は\n戦いなんて知らねェ\nのんきなツラしてんな\n…いいトコじゃねェか", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9492, 1, 4, 135901, 101, 1, 0, "騎士公のヤツ、\nまたぞろ厄介事に\n巻き込まれや\nしてねェだろうな…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9493, 1, 8, 135901, 102, 1, 0, "ふわぁ…\nこうも居心地がいいと\n眠くなっちまうぜ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9494, 1, 1, 135901, 1, 2, 0, "俺ばっかり\n見てんじゃねェよ\n赤鬼になっちまわぁ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9495, 1, 1, 135901, 2, 2, 0, "注いでくれんのか？\nあんがとよ\nんじゃ俺からも\nお返しだぜ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9496, 1, 1, 135901, 3, 2, 0, "おう、暇してるなら\n鍛錬に付き合えよ\nじっとしてっと\n体がなまって仕方ねェ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9497, 1, 1, 135901, 4, 2, 0, "退屈してきたな…\n散歩にでも行くか？\n騎士公なら退屈しねェ\n場所知ってるだろ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9498, 1, 4, 135901, 101, 2, 0, "女との関係は\nきちっとしとけよ\n俺だって助けらんねぇ\nもんはあるからな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9499, 1, 8, 135901, 102, 2, 0, "角に触りたいだぁ？\n…まあ、騎士公の\n頼みなら…\nあんま手荒にすんなよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9500, 1, 1, 135901, 1, 2, 0, "起きろ\n朝の鍛錬の時間だぜ\nほれ、さっさと\n身支度しちまいな", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9501, 1, 1, 135901, 2, 2, 0, "昼飯ィ？\n俺の分はいらねぇよ\n気ィつかわねぇで\nくんな", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9502, 1, 1, 135901, 3, 2, 0, "まだ起きてんのか？\nさっさと寝ねェと\nこうやって鬼が来るぜ", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9504, 1, 1, 135901, 2, 3, 0, "悪くない座り心地だな\n気ィ抜くと、ダメに\nされちまいそうだぜ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9505, 1, 1, 135901, 3, 3, 0, "…今こいつ動いたか？\nグレ公あたりが\n悪さしてんじゃ\nねェだろうな", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9506, 1, 1, 135901, 4, 3, 0, "騎士公はこういう\n香りが好みか？\nお姫とユキノに\n教えてやるとすっか", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9507, 1, 1, 135901, 5, 3, 0, "地獄の釜よりかは\nちとぬるいが…\nなかなか\nいい火力してやがるぜ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9510, 1, 1, 135901, 8, 3, 0, "こっちの世界の本にも\n鬼が出てくるのか", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9514, 1, 1, 136101, 1, 1, 0, "フランシュシュ１号、\nサクラです！　サガの\n心を、歌でみんなに\n届けにきました！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9515, 1, 1, 136101, 2, 1, 0, "ランドソルはドキドキ\nいっぱいの街やけん！\nサガとどっちが\n暮らしやすかろうねっ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9516, 1, 1, 136101, 3, 1, 0, "ソロ活動がんばって、\n憧れの子にもも～っと\n近づくと！　成長した\n私、早く見せたか～！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9517, 1, 1, 136101, 4, 1, 0, "部屋におるとは、伝説\nのメンバーばかり…！\n私もこのランドソルで\n伝説ば刻むとよ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9518, 1, 1, 136101, 5, 1, 0, "あそこの修繕が気に\nなると…。…あのっ！\nあ、ついあの子を\n呼びそうに…えへへっ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9519, 1, 4, 136101, 101, 1, 0, "アイドルのレッスン、\n気になると？　まず、\n脚をぐい～っと広げて\n…あわわ、戻らん！？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9520, 1, 8, 136101, 102, 1, 0, "騎士さんのコネが\nあれば、いつか堂々と\nゾンビアイドルで\n売り出せるかもっ…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9521, 1, 1, 136101, 1, 2, 0, "おはようござい\nまーす！　挨拶は基本\nやけん、元気よくっ！\nさ、一緒にやろう！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9522, 1, 1, 136101, 2, 2, 0, "身だしなみチェック、\nよしっ。今日もいい\n天気っ♪　さあ、\nいってきまーーす！！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9523, 1, 1, 136101, 3, 2, 0, "あんまり引っ張ると、\nう、腕が～…！　あ、\n気にせんで。伸びても\nすぐ元に戻るけんっ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9524, 1, 1, 136101, 4, 2, 0, "私、持っとらんって\n思ってきたけど、それ\nが何ね！　友達ば、\nいっぱい持っとるけん", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9525, 1, 4, 136101, 101, 2, 0, "嚙めば噛むほど味が\n出る…騎士さんは\nサガのアタリメ\nみたいな人やねっ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9526, 1, 8, 136101, 102, 2, 0, "騎士さんにだけ教え\nますね。私の秘密…\n私の死因を。え？\nもう分かる気もする？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9527, 1, 1, 136101, 1, 2, 0, "ぼ～っとしとかれんね\n「ぶっ殺すぞ」って\n気合入れてくれる子が\n両方の街におるけんっ", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9528, 1, 1, 136101, 2, 2, 0, "あのすごか食べっぷり\nよう思い出すとよ\n元気かな…変なもん\n食べてないかな…", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9529, 1, 1, 136101, 3, 2, 0, "夜、出歩くと、怖～い\nアンデッドに襲われる\nかもしれんよ！\n気をつけんと…！", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9531, 1, 1, 136101, 2, 3, 0, "リラックス！　もっと\nがんばってリラックス\nせんといかんね！\nむむむ～っ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9532, 1, 1, 136101, 3, 3, 0, "が～ばや～らしか～♪\nあの子のお土産に\nしたいけど、同じの\n売っとらんかな…？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9533, 1, 1, 136101, 4, 3, 0, "ん～、いい香りっ♪\nいつもこやん素敵な\n香りのする人が、私の\n友達におってね…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9534, 1, 1, 136101, 5, 3, 0, "え～と…熱いとその…\n傷んじゃいそうで、\nちょっと心配やけん…\nそ…その…私の体が", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9536, 1, 1, 136101, 7, 3, 0, "わあ、美味しか～っ！\nランドソルのグルメも\nなかなか手強いっ…！\nサガも負けられんね～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9537, 1, 1, 136101, 8, 3, 0, "アイドル雑誌はあると\nですかね？　フラン\nシュシュのランキング\nは…しょぼぼぼーん…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9541, 1, 1, 137401, 1, 1, 0, "名探偵から聖職者へ、\n華麗なる変装だ！\nいや、心の中まで\n敬虔に…変貌、かな♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9542, 1, 1, 137401, 2, 1, 0, "真実の鏡で、誰の謎も\n解いてみせるよ。\n恋の謎は…私が解いて\nほしいくらいだ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9543, 1, 1, 137401, 3, 1, 0, "歩き方も…厳かに…\n気品と高潔さが\n感じられるように\n練習しなくてはね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9544, 1, 1, 137401, 4, 1, 0, "私の行いはきっと、\nどこかで見られている\nかつてお別れをした\n大切なあの子にも…ね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9545, 1, 1, 137401, 5, 1, 0, "異議あり！　…う～ん\nもっと威厳をもって…\n異議あり！　はっ、\n聞いていたのかいっ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9546, 1, 4, 137401, 101, 1, 0, "静かに心を見つめれば\n大切なものが分かる\n助手くんとの時間の\nかけがえなさも、ね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9547, 1, 8, 137401, 102, 1, 0, "私も、想い合う二人の\n神聖な誓約の儀と\nいうものに憧れるよ\nつまり結婚式、にね…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9548, 1, 1, 137401, 1, 2, 0, "珈琲を一杯お願い\nするよ、助手くん\nこれがないと私の\n推理が冴えなくてね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9549, 1, 1, 137401, 2, 2, 0, "懺悔があるなら聞き\n届けよう。…え～っ！\n棚にある私のチョコを\n食べてしまったって！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9550, 1, 1, 137401, 3, 2, 0, "助手くん、今日の\n依頼は何件かな\nもし誰も来ないなら\n一緒にお出かけしよう", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9551, 1, 1, 137401, 4, 2, 0, "さあ、出かけようか\n君と私で巡礼の旅だ\n鞄の中身はただ一つ\n真実を愛する心、さ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9552, 1, 4, 137401, 101, 2, 0, "一緒に祈ってくれる\nかい？　ありがとう\n一人では真実が重荷に\nなるときもあってね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9553, 1, 8, 137401, 102, 2, 0, "何でも話してほしい\n分かち合いたいんだ\n喜びも悲しみも、君が\n背負う、なにもかも", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9554, 1, 1, 137401, 1, 2, 0, "おはよう！　聖職者は\n早起きなのさ♪\nふわぁ…はっ、今の\nあくびは見てないね？", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9555, 1, 1, 137401, 2, 2, 0, "今日のお昼は…\n一緒に台所で\n作らないかい？\n新婚さんみたいに♪", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9556, 1, 1, 137401, 3, 2, 0, "夜のお祈りをしよう\n凶悪な事件なんて、\nもう起きないように", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9558, 1, 1, 137401, 2, 3, 0, "はあ…ふかふか～…\n堕落してしまいそう…\nなんとも罪なアイテム\nだねえ、これは…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9559, 1, 1, 137401, 3, 3, 0, "かわいいな…♪\n探偵事務所の机に\n置いたら場を和ませて\nくれそうだね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9560, 1, 1, 137401, 4, 3, 0, "はあ…心が落ち着く\nけして、その、小説の\n謎解きにイライラして\nいたわけではないよ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9561, 1, 1, 137401, 5, 3, 0, "…暖かいね。こんな\n暖かさをあまねく\n世に届けるのが、\n今の私の役目だよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9563, 1, 1, 137401, 7, 3, 0, "こんなにおいしい\nなんて、きっと重大な\n秘密が隠されているに\n違いないよね！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9564, 1, 1, 137401, 8, 3, 0, "時には推理小説でなく\n教えを説く本もいいね\nちょっとだけ…眠く\nなっちゃうけど…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9568, 1, 1, 136201, 1, 1, 0, "すんすん…\nなんか甘い匂いが\nするよーな？\nどこかなどこかな～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9569, 1, 1, 136201, 2, 1, 0, "お部屋の中、\nいっぱい歩いたよ～\nよーし、今日の訓練\nお～しまいっ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9570, 1, 1, 136201, 3, 1, 0, "どうしてドーナツには\n穴が開いてるんだろ？\nカスミちゃーん、\nおしえてーっ？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9571, 1, 1, 136201, 4, 1, 0, "いち、に、さん、し、\nごー、ろく…ナーナ！\nえへへへへ～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9572, 1, 1, 136201, 5, 1, 0, "訓練しなきゃだけど…\nおやつとかゲームとか\nいろいろありすぎて…\nで、でもぉ～っ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9573, 1, 4, 136201, 101, 1, 0, "ここ、騎士ちゃんの\nお部屋って感じする！\nだってすっごく\n安心するもん", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9574, 1, 8, 136201, 102, 1, 0, "お耳を澄ませたら～…\n騎士ちゃんの声、\nどこからだって\n聞こえちゃうんだ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9575, 1, 1, 136201, 1, 2, 0, "はい、ナーナだよっ\n騎士ちゃんに\nあいさつしてもらうと\n元気が出てくるねっ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9576, 1, 1, 136201, 2, 2, 0, "ナーナの頭の角、\n気になっちゃった？\nさわってもいいよ～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9577, 1, 1, 136201, 3, 2, 0, "ナーナ、ずーっと\nだめだめだったけど…\nいつまでもそれじゃ\nだめだってわかったよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9578, 1, 1, 136201, 4, 2, 0, "るんるる～、らら～♪\nあっ、一緒に歌う？\nせーの、らんらら～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9579, 1, 4, 136201, 101, 2, 0, "ねぇ騎士ちゃんっ\n今日も一緒に\nおでかけしようよ！\nどこへ行こっかな～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9580, 1, 8, 136201, 102, 2, 0, "ランドソルのごはん、\nどれもおいしいけど…\n一番はやっぱり\n騎士ちゃんのごはん！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9581, 1, 1, 136201, 1, 2, 0, "おはよ～っ！\nおっきな声で\nあいさつすると\n気持ちいいよね♪", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9582, 1, 1, 136201, 2, 2, 0, "お昼ごはんの時間だ！\nサクサクドーナツと\nもちもちドーナツ、\nどっちにしようかな～", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9583, 1, 1, 136201, 3, 2, 0, "うーん、眠くなって\nきちゃった…\nみんなはまだまだ\n夜ふかしするの～？", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9585, 1, 1, 136201, 2, 3, 0, "訓練の後はちゃんと\n休むのが大事だよ\n…ナーナはいつも\n休んでるけどっ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9586, 1, 1, 136201, 3, 3, 0, "あなたもナーナの\nお友達になっちゃお？\n「ウン、イイヨ！」\nやったぁ～♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9587, 1, 1, 136201, 4, 3, 0, "いい匂～い♪\nこの匂いつけたら\nナーナもお洒落さんに\nなれるかな～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9588, 1, 1, 136201, 5, 3, 0, "ん～、あったかーい♪\nあっでも、お耳が\n焦げないように\n気をつけなきゃ…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9590, 1, 1, 136201, 7, 3, 0, "いくら食べても\nなくならないなんて…\nすごすぎるよぉ～っ！\n絶対ふとっちゃう～！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9591, 1, 1, 136201, 8, 3, 0, "むずかしそーな本だ！\nふむふむどれどれ…\nうん、むずかしい！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9595, 1, 1, 136401, 1, 1, 0, "クリスマスの時期は\nやりたいことが\nたくさんあって…\n忙しいけど楽しいね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9596, 1, 1, 136401, 2, 1, 0, "みんながウキウキ\nしてるの、\n伝わってくるな♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9597, 1, 1, 136401, 3, 1, 0, "思い切っておしゃれ\nしてみたけど…\nちょっと照れちゃう", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9598, 1, 1, 136401, 4, 1, 0, "誘惑に負けて\n限定のスイーツセット\n買っちゃった…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9599, 1, 1, 136401, 5, 1, 0, "帽子だけでも、\n印象が変わるんだよね\n新しい自分に\nなれたかな♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9600, 1, 4, 136401, 101, 1, 0, "魔物のお料理…\nわたしも騎士クンに\n教えてもらおうかな…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9601, 1, 8, 136401, 102, 1, 0, "騎士クンの温もりが\n恋しくなっちゃうのも\n寒い冬だから…だよね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9602, 1, 1, 136401, 1, 2, 0, "大人っぽいコーデで、\nちょっと背伸びした\n気分…なんてねっ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9603, 1, 1, 136401, 2, 2, 0, "今度一緒に\nお出かけしようよ\n騎士クンのお洋服\n選んであげるから", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9604, 1, 1, 136401, 3, 2, 0, "今度はここで\nパーティーしようよ\nきっと楽しいから", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9605, 1, 1, 136401, 4, 2, 0, "風邪には気をつけてね\nわたしが温めてあげ…\nな、なんでもない！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9606, 1, 4, 136401, 101, 2, 0, "一緒にゆっくり\nおしゃべりしよう？\nいくらでも付き合うよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9607, 1, 8, 136401, 102, 2, 0, "騎士クン、\nぎゅーってさせて？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9608, 1, 1, 136401, 1, 2, 0, "おはよう♪\n昨夜はわたし、\n騎士クンと夢の中でも\nデートしちゃった", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9609, 1, 1, 136401, 2, 2, 0, "お昼ごはんの代わりに\nケーキは\nさ、さすがに\nいけないよね？", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9610, 1, 1, 136401, 3, 2, 0, "あのね、一緒に\n夜更かし、しない？\n今夜だけ…ね？", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9612, 1, 1, 136401, 2, 3, 0, "ん～、気持ちいい\nよかったら騎士クンも\n一緒に…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9613, 1, 1, 136401, 3, 3, 0, "ぬいぐるみも\n楽しそうっ\nふふっ、かわいい♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9614, 1, 1, 136401, 4, 3, 0, "アロマオイルとかも\nプレゼントの定番\nだよね…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9615, 1, 1, 136401, 5, 3, 0, "冷えた体が\nあったまって…\nね、眠くなっちゃう…\nふわぁ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9617, 1, 1, 136401, 7, 3, 0, "愛情のこもった\nお料理って、\nいくらでも\n食べられちゃう…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9618, 1, 1, 136401, 8, 3, 0, "夢占いかぁ…\n昨夜の夢は、\nどんな意味なんだろ？\n…えぇっ、そんな…！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9622, 1, 1, 136401, 14, 3, 0, "次のプレゼントは\nこういう本もいいかも\n騎士クンも\n喜んでくれそう…♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9623, 1, 1, 136401, 15, 3, 0, "ふわもこだけど、\n動くのは大変っ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9624, 1, 1, 136301, 1, 1, 0, "今からギルドハウスを\nクリスマス風に改造\nしちゃいます♪\nイイコトですよね★", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9625, 1, 1, 136301, 2, 1, 0, "ええっと…飾りつけを\nするには、まず\nテレポーテーションで\nツリーをもってきて…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9626, 1, 1, 136301, 3, 1, 0, "クリスマスとはずっと\n無縁な人生でしたし\n普通にそれっぽいコト\nしてみたいですねー", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9627, 1, 1, 136301, 4, 1, 0, "おうちでまったりも\nイイカンジです★\nあまーいココアでも\n淹れてみましょうか", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9628, 1, 1, 136301, 5, 1, 0, "ケーキを食べ過ぎると\nいつかのユイさん\nみたいになっちゃう\nので要注意ですね★", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9629, 1, 4, 136301, 101, 1, 0, "騎士さんは他の子と\nクリスマスデート中\nなんですかねー？\n邪魔しにいこうかなー", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9630, 1, 8, 136301, 102, 1, 0, "騎士さんと\nクリスマスデート…\nふふっ、考えると\nニヤニヤしちゃいます", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9631, 1, 1, 136301, 1, 2, 0, "あ、騎士さん\nお邪魔してまーす★\nクリスマスも一緒に\nイイコトしましょうか", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9632, 1, 1, 136301, 2, 2, 0, "外が寒すぎなので\n今日はイイコトクラブ\nも休部中ですよ★\nゆっくりしましょう♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9633, 1, 1, 136301, 3, 2, 0, "この衣装ふわふわして\nついつい触りたく\nなっちゃいますよね？\n好きなだけイイですよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9634, 1, 1, 136301, 4, 2, 0, "この白い衣装を血で\n真っ赤に染め上げる\n…なーんて事件は\n起きませんよ、多分", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9635, 1, 4, 136301, 101, 2, 0, "クリスマスプレゼント\nのお届けでーす★\nプレゼントはもちろん\nわ・た・し、ですよ♥", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9636, 1, 8, 136301, 102, 2, 0, "このギルドハウスは\nキスをしないと出られ\nないみたいですねー\n騎士さんどうしましょ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9637, 1, 1, 136301, 1, 2, 0, "うぅ～、寒さで\n目が覚めちゃいました\n風邪引きたくないので\nぎゅっ★　しますね♪", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9638, 1, 1, 136301, 2, 2, 0, "ちょっと気分転換に\n散歩でもいきますか\n雪が積もっていたら\n雪だるま作りましょう", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9639, 1, 1, 136301, 3, 2, 0, "サンタさんがそろそろ\n来る時間ですかね？\nやることやってベッド\nにいきましょうか★", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9641, 1, 1, 136301, 2, 3, 0, "ふぅ…\n聖夜騎姫★\nチャーミングスカイも\n充電が必要なんです", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9642, 1, 1, 136301, 3, 3, 0, "ぺんぽこりんはクビで\nこの子を戦わせるのも\nアリですね★\n壊れちゃいますけど♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9643, 1, 1, 136301, 4, 3, 0, "はぁ…イイ香り♪\nクリスマスに\nピッタリですね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9644, 1, 1, 136301, 5, 3, 0, "あったかーい♪\nもうここから\n動けないですー", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9646, 1, 1, 136301, 7, 3, 0, "これだけ食べたら\n冬眠もできそうです\n春になったら\n起こしてくださーい♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9647, 1, 1, 136301, 8, 3, 0, "へー、クリスマス\nケーキの作り方…\nランファさんにも後で\nシェアしてみますか", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9651, 1, 1, 136301, 14, 3, 0, "あはっ、イイですねー\nバイオレンスな展開\nはなくてもちゃんと\n面白くなるんですね★", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9652, 1, 1, 136301, 15, 3, 0, "こうして遊んでいると\nぺんぽこりんの\n着ぐるみもほしく\nなっちゃいます♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9653, 1, 1, 137601, 1, 1, 0, "お父さまのような\n威厳のある佇まいを\n心がけたいものです", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9654, 1, 1, 137601, 2, 1, 0, "見慣れた部屋が\n少し小さく感じます\nこの体の感覚にも\n早く慣れなければ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9655, 1, 1, 137601, 3, 1, 0, "…おや？\n以前は見えなかった\n高いところの埃が…\nすぐに対処いたします", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9656, 1, 1, 137601, 4, 1, 0, "わんわんの主さまも\n驚いたご様子でした\n人の成長は早いものと\n思われたでしょうか…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9657, 1, 1, 137601, 5, 1, 0, "すらりとした長い足…\n自分のものと\n分かっていても、つい\n見つめてしまいます…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9658, 1, 4, 137601, 101, 1, 0, "主さまのために\nできることも\n増えました…\nふふ、胸が躍ります", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9659, 1, 8, 137601, 102, 1, 0, "わたくしの願いは、\n主さまと共にあること\nいわば、生涯を\n添い遂げるように…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9660, 1, 1, 137601, 1, 2, 0, "新たな姿でも変わらず\nおはようから\nおやすみまで\nお供いたしますよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9661, 1, 1, 137601, 2, 2, 0, "装いも、大人の体に\n相応しいものに…\nエレガントさを\n心がけてみました", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9662, 1, 1, 137601, 3, 2, 0, "昨日の買い出しでは\n少々買いすぎましたが\n大荷物でも平気でした\nすばらしいです…♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9663, 1, 1, 137601, 4, 2, 0, "主さまのための\n大事な体ですからね\n長い髪の手入れも\n念入りにいたします", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9664, 1, 4, 137601, 101, 2, 0, "本日の添い寝は\n大人と子ども、\nどちらの姿に\nいたしましょうか", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9665, 1, 8, 137601, 102, 2, 0, "あなたさま…\nあぁいえ、その…\n少し違う呼び名を\n試してみようかと", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9666, 1, 1, 137601, 1, 2, 0, "おはようございます\n主さまとのご挨拶で\n始まる一日…\nワクワクいたします", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9667, 1, 1, 137601, 2, 2, 0, "おや、もうお昼時…\nお腹の空き具合が\n違うと、少々感覚が\n鈍ってしまいますね", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9668, 1, 1, 137601, 3, 2, 0, "おやすみの際には\nわたくしの子守唄を…\n安眠の力も以前より\n増しておりますよ♪", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9670, 1, 1, 137601, 2, 3, 0, "いつでも主さまに\n尽くせるよう、\n体を休めることも\n大事です…ふぅ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9671, 1, 1, 137601, 3, 3, 0, "愛らしいものを\n慈しむ心は、\n大人になっても\n変わりません…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9672, 1, 1, 137601, 4, 3, 0, "大人の体になると、\n匂いの感じかたも\n少し変わるような…？\n不思議なものです", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9673, 1, 1, 137601, 5, 3, 0, "手足が冷えないよう\nしっかり温めて…\n冷え性は大人の悩みと\n伺いましたので", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9675, 1, 1, 137601, 7, 3, 0, "胃のほうも、前より\n大きくなった模様です\n手が止まりません…\nもぐもぐ…", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9676, 1, 1, 137601, 8, 3, 0, "久々に、主さまに\n本の読み聞かせを\nしましょうか\nふふ、楽しみです", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9682, 1, 1, 136501, 1, 1, 0, "弟くんは何が好きかな\n縁起物がいっぱいの\nおせち料理を\n食べてほしいんだよね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9683, 1, 1, 136501, 2, 1, 0, "ちょっと…帯がきつい\nかも…おせち料理の\n食べすぎかな？\n気を付けなくちゃ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9684, 1, 1, 136501, 3, 1, 0, "初日の出は見なくちゃ\n新しい一年の始まりを\n告げるお日さまに\n挨拶しておきたいし", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9685, 1, 1, 136501, 4, 1, 0, "甘酒って不思議…\nすっごく寒い日に\n飲むと、もっと\nおいしくなるんだもん", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9686, 1, 1, 136501, 5, 1, 0, "着物のお姉ちゃん♪\nいつもとは違う格好で\n弟くんをドキドキ\nさせちゃおっと♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9687, 1, 4, 136501, 101, 1, 0, "うふふ…弟くんの寝顔\nかわいかったなぁ♪\nもはや国宝級だよ！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9688, 1, 8, 136501, 102, 1, 0, "弟くんの着物姿、\nかっこよかった～♪\nはぁぁ…思い出したら\nドキドキしてきた…♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9689, 1, 1, 136501, 1, 2, 0, "私の着物が気になる？\n触ってもいいよ\n肌触りがよくって、\n着心地も最高なんだ～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9690, 1, 1, 136501, 2, 2, 0, "初詣に行くときは手を\n繋ごうね。人混みの\n中ではぐれたりしたら\n大変だもんっ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9691, 1, 1, 136501, 3, 2, 0, "叶えたいことが\nあったら言ってね？\nお姉ちゃんが全力で\n協力するから！", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9692, 1, 1, 136501, 4, 2, 0, "お正月は一緒に\n過ごしてお買い物も\nして…ラブラブな\n時間を楽しもうねっ♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9693, 1, 4, 136501, 101, 2, 0, "…大好きだよ、弟くん\nふふっ、急にごめんね\nどうしても今、伝えた\nいって思っちゃったの", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9694, 1, 8, 136501, 102, 2, 0, "弟くんにもらった指輪\n宝物だよ♪　だけど\nいつか本当の新婚さん\nになりたいな…♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9695, 1, 1, 136501, 1, 2, 0, "お正月の朝は空気が\n冷たいよね。こんな\nときは、太陽の光を\n浴びながらお散歩しよ", 1);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9696, 1, 1, 136501, 2, 2, 0, "お昼はお雑煮！\n出汁も具材も\nこだわったから、\n楽しみにしててね♪", 2);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9697, 1, 1, 136501, 3, 2, 0, "そろそろ寝よっか？\nそうだ、添い寝をして\nあげる！　寒いとなか\nなか寝付けないでしょ", 3);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9699, 1, 1, 136501, 2, 3, 0, "座り方には気を付け\nないと。きれいな\n着物に、しわは作り\nたくないもんね", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9700, 1, 1, 136501, 3, 3, 0, "ふわふわでかわいいね\nこの子たちも、\n弟くんとの生活、\nお祝いしてくれてる？", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9701, 1, 1, 136501, 4, 3, 0, "くんくん…神社のお香\nみたい♪　いいこと\nありそうな気が\nしてきたよ", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9702, 1, 1, 136501, 5, 3, 0, "寒い日に暖炉はありが\nたいな～♪　ここで\nお餅を焼けば、お腹も\n膨れて一石二鳥かも♪", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9704, 1, 1, 136501, 7, 3, 0, "お腹いっぱい…でも\nまだ食べたい！　う～\nこれじゃ、正月太りへ\nまっしぐらだよ～", 0);
INSERT INTO `v1_f5931e9c7363d02962f945e54867c3d8855203a4edf720f9ddef78e44d8c560a` VALUES (9705, 1, 1, 136501, 8, 3, 0, "ラブラブだけじゃなく\nもくもくと一緒に本を\n読むのもいいよね♪\nお正月は時間もあるし", 0);
